trsuperobject.biblemap.put("b62_1_10", new trsuperobject(62,1,10,"Günah işlemedik dersek, O'nu yalancı durumuna düşürmüş oluruz; O'nun sözü içimizde olmaz."));
trsuperobject.biblemap.put("b62_2_1", new trsuperobject(62,2,1,"Yavrularım, bunları size günah işlemeyesiniz diye yazıyorum. Ama içimizden biri günah işlerse, adil olan İsa Mesih bizi Babanın önünde savunur."));
trsuperobject.biblemap.put("b62_2_2", new trsuperobject(62,2,2,"O günahlarımızı, yalnız bizim günahlarımızı değil, bütün dünyanın günahlarını da bağışlatan kurbandır."));
