trsuperobject.biblemap.put("b62_2_5", new trsuperobject(62,2,5,"Ama Onun sözüne uyan kişinin Tanrıya olan sevgisi gerçekten yetkinleşmiştir. Tanrıda olduğumuzu bununla anlarız."));
trsuperobject.biblemap.put("b62_2_6", new trsuperobject(62,2,6,"‹‹Tanrıda yaşıyorum›› diyen, Mesihin yürüdüğü yolda yürümelidir."));
trsuperobject.biblemap.put("b62_2_7", new trsuperobject(62,2,7,"Sevgili kardeşlerim, size yeni bir buyruk değil, başlangıçtan beri kabul ettiğiniz eski buyruğu yazıyorum. Eski buyruk, işitmiş olduğunuz Tanrı sözüdür."));
trsuperobject.biblemap.put("b62_2_8", new trsuperobject(62,2,8,"Yine de size yeni bir buyruk yazıyorum. Bunun gerçek olduğu, Mesihte ve sizde görülüyor. Çünkü karanlık geçiyor, gerçek ışık şimdiden parlıyor."));
trsuperobject.biblemap.put("b62_2_9", new trsuperobject(62,2,9,"Işıkta olduğunu söyleyip de kardeşinden nefret eden hâlâ karanlıktadır."));
trsuperobject.biblemap.put("b62_2_10", new trsuperobject(62,2,10,"Kardeşini seven ışıkta yaşar ve başkasının tökezlemesine neden olmaz."));
trsuperobject.biblemap.put("b62_2_11", new trsuperobject(62,2,11,"Ama kardeşinden nefret eden karanlıktadır, karanlıkta yürür ve nereye gittiğini bilmez. Çünkü karanlık gözlerini kör etmiştir."));
trsuperobject.biblemap.put("b62_2_12", new trsuperobject(62,2,12,"Yavrularım, size yazıyorum,  Çünkü Mesihin adı uğruna günahlarınız bağışlandı."));
trsuperobject.biblemap.put("b62_2_13", new trsuperobject(62,2,13,"Babalar, size yazıyorum,  Çünkü başlangıçtan beri var Olanı tanıyorsunuz.  Gençler, size yazıyorum,  Çünkü kötü olanı yendiniz.  Çocuklar, size yazdım,  Çünkü Babayı tanıyorsunuz."));
trsuperobject.biblemap.put("b62_2_14", new trsuperobject(62,2,14,"Babalar, size yazdım,  Çünkü başlangıçtan beri var Olanı tanıyorsunuz.  Gençler, size yazdım,  Çünkü güçlüsünüz,  Tanrının sözü içinizde yaşıyor,  Kötü olanı yendiniz."));
trsuperobject.biblemap.put("b62_2_15", new trsuperobject(62,2,15,"Dünyayı da dünyaya ait şeyleri de sevmeyin. Dünyayı sevenin Babaya sevgisi yoktur."));
trsuperobject.biblemap.put("b62_2_16", new trsuperobject(62,2,16,"Çünkü dünyaya ait olan her şey -benliğin tutkuları, gözün tutkuları, maddi yaşamın verdiği gurur- Babadan değil, dünyadandır."));
trsuperobject.biblemap.put("b62_2_17", new trsuperobject(62,2,17,"Dünya da dünyasal tutkular da geçer, ama Tanrının isteğini yerine getiren sonsuza dek yaşar."));
trsuperobject.biblemap.put("b62_2_18", new trsuperobject(62,2,18,"Çocuklar, bu son saattir. Mesih Karşıtının geleceğini duydunuz. Nitekim şimdiden çok sayıda Mesih karşıtı türemiş bulunuyor. Son saat olduğunu bundan biliyoruz."));
trsuperobject.biblemap.put("b62_2_19", new trsuperobject(62,2,19,"Bunlar aramızdan çıktılar, ama bizden değildiler. Bizden olsalardı, bizimle kalırlardı. Ayrılmaları hiçbirinin bizden olmadığını ortaya çıkardı."));
trsuperobject.biblemap.put("b62_2_20", new trsuperobject(62,2,20,"Sizlerse Kutsal Olan tarafından meshedildiniz; hepiniz bilgilisiniz."));
trsuperobject.biblemap.put("b62_2_21", new trsuperobject(62,2,21,"Gerçeği bilmediğiniz için değil, gerçeği ve hiçbir yalanın gerçekle ilgisi olmadığını bildiğiniz için size yazıyorum."));
trsuperobject.biblemap.put("b62_2_22", new trsuperobject(62,2,22,"İsanın Mesih olduğunu yadsıyan yalancı değilse, kim yalancıdır? Babayı ve Oğulu yadsıyan Mesih karşıtıdır."));
trsuperobject.biblemap.put("b62_2_23", new trsuperobject(62,2,23,"Oğulu yadsıyanda Baba da yoktur; Oğulu açıkça kabul edende Baba da vardır."));
trsuperobject.biblemap.put("b62_2_24", new trsuperobject(62,2,24,"Başlangıçtan beri işittiğiniz söz içinizde yaşasın. Başlangıçtan beri işittiğiniz söz içinizde yaşarsa, siz de Oğulda ve Babada yaşarsınız."));
trsuperobject.biblemap.put("b62_2_25", new trsuperobject(62,2,25,"Mesihin bize vaat ettiği budur, yani sonsuz yaşamdır."));
trsuperobject.biblemap.put("b62_2_26", new trsuperobject(62,2,26,"Bunları sizi saptırmak isteyenlerle ilgili olarak yazıyorum."));
trsuperobject.biblemap.put("b62_2_27", new trsuperobject(62,2,27,"Size gelince, Ondan aldığınız mesh sizde kalır. Kimsenin size bir şey öğretmesine gerek yoktur. Onun size her şeyi öğreten meshi gerçektir, sahte değildir. Size öğrettiği gibi, Mesihte yaşayın."));
trsuperobject.biblemap.put("b62_2_28", new trsuperobject(62,2,28,"Evet, yavrularım, şimdi Mesihte yaşayın ki, O göründüğünde cesaretimiz olsun, geldiğinde Onun önünde utanmayalım."));
trsuperobject.biblemap.put("b62_2_29", new trsuperobject(62,2,29,"O'nun doğru olduğunu bilirseniz, doğru olanı yapan herkesin O'ndan doğduğunu da bilirsiniz."));
trsuperobject.biblemap.put("b62_3_1", new trsuperobject(62,3,1,"Bakın, Baba bizi o kadar çok seviyor ki, bize ‹‹Tanrının çocukları›› deniyor! Gerçekten de öyleyiz. Dünya Babayı tanımadığı için bizi de tanımıyor."));
trsuperobject.biblemap.put("b62_3_2", new trsuperobject(62,3,2,"Sevgili kardeşlerim, daha şimdiden Tanrının çocuklarıyız, ama ne olacağımız henüz bize gösterilmedi. Ancak, Mesih göründüğü zaman Ona benzer olacağımızı biliyoruz. Çünkü Onu olduğu gibi göreceğiz."));
trsuperobject.biblemap.put("b62_3_3", new trsuperobject(62,3,3,"Mesihte bu umuda sahip olan, Mesih pak olduğu gibi kendini pak kılar."));
trsuperobject.biblemap.put("b62_3_4", new trsuperobject(62,3,4,"Günah işleyen, yasaya karşı gelmiş olur. Çünkü günah demek, yasaya karşı gelmek demektir."));
trsuperobject.biblemap.put("b62_3_5", new trsuperobject(62,3,5,"Mesihin, günahları kaldırmak için ortaya çıktığını ve kendisinde günah olmadığını bilirsiniz."));
trsuperobject.biblemap.put("b62_3_6", new trsuperobject(62,3,6,"Mesihte yaşayan, günah işlemez. Günah işleyen Onu ne görmüştür, ne de tanımıştır."));
trsuperobject.biblemap.put("b62_3_7", new trsuperobject(62,3,7,"Yavrularım, kimse sizi aldatmasın. Mesih doğru olduğu gibi, doğru olanı yapan da doğru kişidir."));
trsuperobject.biblemap.put("b62_3_8", new trsuperobject(62,3,8,"Günah işleyen, İblistendir. Çünkü İblis başlangıçtan beri günah işlemektedir. Tanrının Oğlu, İblisin yaptıklarına son vermek için ortaya çıktı."));
trsuperobject.biblemap.put("b62_3_9", new trsuperobject(62,3,9,"Tanrıdan doğmuş olan, günah işlemez. Çünkü Tanrının tohumu onda yaşar. Tanrıdan doğmuş olduğu için günah işleyemez."));
trsuperobject.biblemap.put("b62_3_10", new trsuperobject(62,3,10,"Doğru olanı yapmayan ve kardeşini sevmeyen kişi Tanrıdan değildir. İşte Tanrının çocuklarıyla İblisin çocukları böyle ayırt edilir."));
trsuperobject.biblemap.put("b62_3_11", new trsuperobject(62,3,11,"Başlangıçtan beri işittiğiniz buyruk şudur: Birbirimizi sevelim."));
trsuperobject.biblemap.put("b62_3_12", new trsuperobject(62,3,12,"Şeytana ait olup kardeşini öldüren Kayin gibi olmayalım. Kayin kardeşini neden öldürdü? Kendi yaptıkları kötü, kardeşinin yaptıkları doğru olduğu için öldürdü."));
trsuperobject.biblemap.put("b62_3_13", new trsuperobject(62,3,13,"Kardeşler, dünya sizden nefret ederse şaşmayın."));
trsuperobject.biblemap.put("b62_3_14", new trsuperobject(62,3,14,"Biz kardeşleri sevdiğimiz için ölümden yaşama geçtiğimizi biliyoruz. Sevmeyen ölümde kalır."));
trsuperobject.biblemap.put("b62_3_15", new trsuperobject(62,3,15,"Kardeşinden nefret eden katildir. Hiçbir katilin sonsuz yaşama sahip olmadığını bilirsiniz."));
trsuperobject.biblemap.put("b62_3_16", new trsuperobject(62,3,16,"Sevginin ne olduğunu Mesihin bizim için canını vermesinden anlıyoruz. Bizim de kardeşlerimiz için canımızı vermemiz gerekir."));
trsuperobject.biblemap.put("b62_3_17", new trsuperobject(62,3,17,"Dünya malına sahip olup da kardeşini ihtiyaç içinde gördüğü halde ondan şefkatini esirgeyen kişide Tanrının sevgisi olabilir mi?"));
trsuperobject.biblemap.put("b62_3_18", new trsuperobject(62,3,18,"Yavrularım, sözle ve dille değil, eylemle ve içtenlikle sevelim."));
trsuperobject.biblemap.put("b62_3_19-20", new trsuperobject(62,3,19-20,"Böylelikle gerçeğe ait olduğumuzu bileceğiz ve yüreğimiz bizi ne zaman suçlarsa, Tanrının önünde onu yatıştıracağız. Çünkü Tanrı yüreğimizden daha büyüktür ve her şeyi bilir."));
trsuperobject.biblemap.put("b62_3_21-22", new trsuperobject(62,3,21-22,"Sevgili kardeşlerim, yüreğimiz bizi suçlamazsa, Tanrının önünde cesaretimiz olur, Ondan ne dilersek alırız. Çünkü Onun buyruklarını yerine getiriyor, Onu hoşnut eden şeyleri yapıyoruz."));
trsuperobject.biblemap.put("b62_3_23", new trsuperobject(62,3,23,"Onun buyruğu Oğlu İsa Mesihin adına inanmamız ve İsanın buyurduğu gibi birbirimizi sevmemizdir."));
trsuperobject.biblemap.put("b62_3_24", new trsuperobject(62,3,24,"Tanrı'nın buyruklarını yerine getiren Tanrı'da yaşar, Tanrı da o kişide yaşar. İçimizde yaşadığını bize verdiği Ruh sayesinde biliriz."));
trsuperobject.biblemap.put("b62_4_1", new trsuperobject(62,4,1,"Sevgili kardeşlerim, her ruha inanmayın. Tanrıdan olup olmadıklarını anlamak için ruhları sınayın. Çünkü birçok sahte peygamber dünyanın her yanına yayılmış bulunuyor."));
trsuperobject.biblemap.put("b62_4_2", new trsuperobject(62,4,2,"İsa Mesihin beden alıp dünyaya geldiğini kabul eden her ruh Tanrıdandır. Tanrının Ruhunu bununla tanıyacaksınız."));
trsuperobject.biblemap.put("b62_4_3", new trsuperobject(62,4,3,"İsayı kabul etmeyen hiçbir ruh Tanrıdan değildir. Böylesi, Mesih Karşıtının ruhudur. Onun geleceğini duydunuz. Zaten o şimdiden dünyadadır."));
trsuperobject.biblemap.put("b62_4_4", new trsuperobject(62,4,4,"Yavrularım, siz Tanrıdansınız ve sahte peygamberleri yendiniz. Çünkü sizde olan, dünyadakinden üstündür."));
trsuperobject.biblemap.put("b62_4_5", new trsuperobject(62,4,5,"Sahte peygamberler dünyadandır. Bu nedenle söyledikleri sözler de dünyadandır ve dünya onları dinler."));
trsuperobject.biblemap.put("b62_4_6", new trsuperobject(62,4,6,"Bizse Tanrıdanız; Tanrıyı tanıyan bizi dinler, Tanrıdan olmayan dinlemez. Gerçeğin Ruhuyla yalan ruhunu böyle ayırt ederiz."));
trsuperobject.biblemap.put("b62_4_7", new trsuperobject(62,4,7,"Sevgili kardeşlerim, birbirimizi sevelim. Çünkü sevgi Tanrıdandır. Seven herkes Tanrıdan doğmuştur ve Tanrıyı tanır."));
trsuperobject.biblemap.put("b62_4_8", new trsuperobject(62,4,8,"Sevmeyen kişi Tanrıyı tanımaz. Çünkü Tanrı sevgidir."));
trsuperobject.biblemap.put("b62_4_9", new trsuperobject(62,4,9,"Tanrı biricik Oğlu aracılığıyla yaşayalım diye Onu dünyaya gönderdi, böylece bizi sevdiğini gösterdi."));
trsuperobject.biblemap.put("b62_4_10", new trsuperobject(62,4,10,"Tanrıyı biz sevmiş değildik, ama O bizi sevdi ve Oğlunu günahlarımızı bağışlatan kurban olarak dünyaya gönderdi. İşte sevgi budur."));
trsuperobject.biblemap.put("b62_4_11", new trsuperobject(62,4,11,"Sevgili kardeşlerim, Tanrı bizi bu kadar çok sevdiğine göre biz de birbirimizi sevmeye borçluyuz."));
trsuperobject.biblemap.put("b62_4_12", new trsuperobject(62,4,12,"Hiç kimse hiçbir zaman Tanrıyı görmüş değildir. Ama birbirimizi seversek, Tanrı içimizde yaşar ve sevgisi içimizde yetkinleşmiş olur."));
trsuperobject.biblemap.put("b62_4_13", new trsuperobject(62,4,13,"Tanrıda yaşadığımızı ve Onun bizde yaşadığını bize kendi Ruhundan vermiş olmasından anlıyoruz."));
trsuperobject.biblemap.put("b62_4_14", new trsuperobject(62,4,14,"Babanın Oğlunu dünyanın Kurtarıcısı olarak gönderdiğini gördük, şimdi buna tanıklık ediyoruz."));
trsuperobject.biblemap.put("b62_4_15", new trsuperobject(62,4,15,"Kim İsanın Tanrının Oğlu olduğunu açıkça kabul ederse, Tanrı onda yaşar, o da Tanrıda yaşar."));
trsuperobject.biblemap.put("b62_4_16", new trsuperobject(62,4,16,"Tanrının bize olan sevgisini tanıdık ve buna inandık. Tanrı sevgidir. Sevgide yaşayan Tanrıda yaşar, Tanrı da onda yaşar."));
trsuperobject.biblemap.put("b62_4_17", new trsuperobject(62,4,17,"Yargı gününde cesaretimiz olsun diye sevgi böylelikle içimizde yetkin kılınmıştır. Çünkü Mesih nasılsa, biz de bu dünyada öyleyiz."));
trsuperobject.biblemap.put("b62_4_18", new trsuperobject(62,4,18,"Sevgide korku yoktur. Tersine, yetkin sevgi korkuyu siler atar. Çünkü korku işkencedir. Korkan kişi sevgide yetkin kılınmamıştır."));
trsuperobject.biblemap.put("b62_4_19", new trsuperobject(62,4,19,"Bizse seviyoruz, çünkü önce O bizi sevdi."));
trsuperobject.biblemap.put("b62_4_20", new trsuperobject(62,4,20,"‹‹Tanrıyı seviyorum›› deyip de kardeşinden nefret eden yalancıdır. Çünkü gördüğü kardeşini sevmeyen, görmediği Tanrıyı sevemez."));
trsuperobject.biblemap.put("b62_4_21", new trsuperobject(62,4,21,"‹‹Tanrı'yı seven kardeşini de sevsin›› diyen buyruğu Mesih'ten aldık."));
trsuperobject.biblemap.put("b62_5_1", new trsuperobject(62,5,1,"İsanın Mesih olduğuna inanan herkes Tanrıdan doğmuştur. Babayı seven Ondan doğmuş olanı da sever."));
trsuperobject.biblemap.put("b62_5_2", new trsuperobject(62,5,2,"Tanrıyı sevip buyruklarını yerine getirmekle, Tanrının çocuklarını sevdiğimizi anlarız."));
trsuperobject.biblemap.put("b62_5_3", new trsuperobject(62,5,3,"Tanrıyı sevmek Onun buyruklarını yerine getirmek demektir. Onun buyrukları da ağır değildir."));
trsuperobject.biblemap.put("b62_5_4", new trsuperobject(62,5,4,"Çünkü Tanrıdan doğmuş olan herkes dünyayı yener. Bize dünyaya karşı zafer kazandıran imanımızdır."));
trsuperobject.biblemap.put("b62_5_5", new trsuperobject(62,5,5,"İsanın Tanrı Oğlu olduğuna iman edenden başka dünyayı yenen kim?"));
trsuperobject.biblemap.put("b62_5_6", new trsuperobject(62,5,6,"Suyla ve kanla gelen İsa Mesihtir. O yalnız suyla değil, suyla ve kanla gelmiştir. Buna tanıklık eden Ruhtur. Çünkü Ruh gerçektir."));
trsuperobject.biblemap.put("b62_5_7-8", new trsuperobject(62,5,7-8,"Şöyle ki, tanıklık edenler üçtür: Ruh, su ve kan. Bunların üçü de uyum içindedir."));
trsuperobject.biblemap.put("b62_5_9", new trsuperobject(62,5,9,"İnsanların tanıklığını kabul ediyoruz, oysa Tanrının tanıklığı daha üstündür. Çünkü bu, Tanrının tanıklığıdır, kendi Oğluyla ilgili olarak yaptığı tanıklıktır."));
trsuperobject.biblemap.put("b62_5_10", new trsuperobject(62,5,10,"Tanrının Oğluna inanan, yüreğinde Tanrının tanıklığına sahiptir. Tanrıya inanmayansa Onu yalancı durumuna düşürmüş olur. Çünkü Tanrının Oğluyla ilgili tanıklığına inanmamıştır."));
trsuperobject.biblemap.put("b62_5_11", new trsuperobject(62,5,11,"Tanıklık da şudur: Tanrı bize sonsuz yaşam verdi, bu yaşam Onun Oğlundadır."));
trsuperobject.biblemap.put("b62_5_12", new trsuperobject(62,5,12,"Kendisinde Tanrı Oğlu bulunanda yaşam vardır, kendisinde Tanrı Oğlu bulunmayanda yaşam yoktur."));
trsuperobject.biblemap.put("b62_5_13", new trsuperobject(62,5,13,"Tanrı Oğlunun adına iman eden sizlere, sonsuz yaşama sahip olduğunuzu bilesiniz diye bunları yazdım."));
trsuperobject.biblemap.put("b62_5_14", new trsuperobject(62,5,14,"Tanrının önünde güvenimiz şu ki, Onun isteğine uygun ne dilersek bizi işitir."));
trsuperobject.biblemap.put("b62_5_15", new trsuperobject(62,5,15,"Her ne dilersek bizi işittiğini bildiğimize göre, Ondan dilediklerimizi aldığımızı da biliriz."));
trsuperobject.biblemap.put("b62_5_16", new trsuperobject(62,5,16,"Kardeşinin ölümcül olmayan bir günah işlediğini gören, onun için dua etsin. Duasıyla kardeşine yaşam verecektir. Bu, ölümcül olmayan günah işleyenler için geçerlidir. Ölümcül günah da vardır, bunun için dua etsin demiyorum."));
trsuperobject.biblemap.put("b62_5_17", new trsuperobject(62,5,17,"Her kötülük günahtır, ama ölümcül olmayan günah da vardır."));
trsuperobject.biblemap.put("b62_5_18", new trsuperobject(62,5,18,"Tanrıdan doğmuş olanın günah işlemediğini biliriz. Tanrıdan doğmuş olan İsa Mesih onu korur ve kötü olan ona dokunamaz."));
trsuperobject.biblemap.put("b62_5_19", new trsuperobject(62,5,19,"Biliyoruz ki, biz Tanrıdanız, bütün dünya ise kötü olanın denetimindedir."));
trsuperobject.biblemap.put("b62_5_20", new trsuperobject(62,5,20,"Yine biliyoruz ki, Tanrının Oğlu gelmiş ve gerçek Olanı tanımamız için bize anlama gücü vermiştir. Biz gerçek Olandayız, Onun Oğlu İsa Mesihteyiz. O gerçek Tanrı ve sonsuz yaşamdır."));
trsuperobject.biblemap.put("b62_5_21", new trsuperobject(62,5,21,"Yavrularım, kendinizi putlardan koruyun."));

