trsuperobject.biblemap.put("b7_4_14", new trsuperobject(7,4,14,"Debora Baraka, ‹‹Haydi kalk! Çünkü RABbin Siserayı senin eline teslim ettiği gün bugündür›› dedi, ‹‹RAB senin önünden gidiyor.›› Bunun üzerine Barak ardında on bin kişiyle Tavor Dağından indi."));
trsuperobject.biblemap.put("b7_4_15", new trsuperobject(7,4,15,"RAB, Siserayı, savaş arabalarını sürenleri ve ordusunu Barakın önünde şaşkına çevirerek bozguna uğrattı. Sisera savaş arabasından indi ve yaya olarak kaçtı."));
trsuperobject.biblemap.put("b7_4_16", new trsuperobject(7,4,16,"Barak savaş arabalarını ve orduyu Haroşet-Goyime kadar kovaladı. Siseranın bütün ordusu kılıçtan geçirildi, tek kişi bile kurtulamadı."));
trsuperobject.biblemap.put("b7_4_17", new trsuperobject(7,4,17,"Yaya olarak kaçan Sisera ise Kenlilerden Heverin karısı Yaelin çadırına sığındı. Çünkü Hasor Kralı Yavinle Kenlilerden Heverin arası iyiydi."));
trsuperobject.biblemap.put("b7_4_18", new trsuperobject(7,4,18,"Yael Siserayı karşılamaya çıktı. Ona, ‹‹Korkma, efendim, gel çadırıma sığın›› dedi. Çadırına sığınan Siseranın üzerine bir yorgan örttü."));
trsuperobject.biblemap.put("b7_4_19", new trsuperobject(7,4,19,"Sisera, ‹‹Susadım, lütfen biraz su ver de içeyim›› dedi. Yael süt tulumunu açıp ona içirdikten sonra üzerini yine örttü."));
trsuperobject.biblemap.put("b7_4_20", new trsuperobject(7,4,20,"Sisera kadına, ‹‹Çadırın kapısında dur›› dedi, ‹‹Biri gelir de çadırda kimse var mı diye sorarsa, yok de.››"));
trsuperobject.biblemap.put("b7_4_21", new trsuperobject(7,4,21,"Heverin karısı Yael eline bir çadır kazığı ile tokmak aldı. Yorgunluktan derin bir uykuya dalmış olan Siseraya sessizce yaklaşarak kazığı şakağına dayadı ve yere saplanıncaya dek çaktı. Sisera hemen öldü."));
trsuperobject.biblemap.put("b7_4_22", new trsuperobject(7,4,22,"Yael Siserayı kovalayan Barakı karşılamaya çıktı. ‹‹Gel, aradığın adamı sana göstereyim›› dedi. Barak kadını izledi ve şakağına kazık çakılmış Siserayı ölü buldu."));
trsuperobject.biblemap.put("b7_4_23", new trsuperobject(7,4,23,"Böylece Tanrı o gün Kenanlı kral Yavini İsraillilerin önünde bozguna uğrattı."));
trsuperobject.biblemap.put("b7_4_24", new trsuperobject(7,4,24,"Giderek güçlenen İsrailliler sonunda Kenanlı kral Yavin'i ortadan kaldırdılar."));
trsuperobject.biblemap.put("b7_5_1", new trsuperobject(7,5,1,"Debora ile Avinoam oğlu Barak o gün şu ezgiyi söylediler:"));
trsuperobject.biblemap.put("b7_5_2", new trsuperobject(7,5,2,"‹‹İsrailin önderleri başı çekince,Halk gönüllü olarak savaşıncaRABbe övgüler sunun."));
trsuperobject.biblemap.put("b7_5_3", new trsuperobject(7,5,3,"Dinleyin, ey krallar!Ey yönetenler, kulak verin!RABbe ezgiler söyleyipİsrailin Tanrısı RABbi ilahilerle öveceğim."));
trsuperobject.biblemap.put("b7_5_4", new trsuperobject(7,5,4,"Seirden çıktığında, ya RAB,Edom kırlarından geçtiğinde,Yer sarsıldı, göklerden yağmur boşandı,Evet, bulutlar yağmur yağdırdı."));
trsuperobject.biblemap.put("b7_5_5", new trsuperobject(7,5,5,"Sina Dağında olan RABbin,İsrailin Tanrısı RABbin önündeDağlar sarsıldı."));
trsuperobject.biblemap.put("b7_5_6", new trsuperobject(7,5,6,"Anat oğlu Şamgar zamanında,Yael zamanında kervanların ardı kesildi.Yolcular sapa yollardan gider oldu."));
trsuperobject.biblemap.put("b7_5_7", new trsuperobject(7,5,7,"Bomboştu İsrailin köyleri,Ben İsrailde ana olarak ortaya çıkıncaya dek,Ben Debora ortaya çıkıncaya dekİsrailin köyleri bomboştu."));
trsuperobject.biblemap.put("b7_5_8", new trsuperobject(7,5,8,"Yeni ilahlar seçtikleri zamanSavaş kentin kapılarına dayandı.İsraildeki kırk bin askerin elindeNe kalkan ne de mızrak vardı."));
trsuperobject.biblemap.put("b7_5_9", new trsuperobject(7,5,9,"Yüreğim İsraili yönetenlerleVe halkın arasındaki gönüllülerledir.RABbe övgüler sunun!"));
trsuperobject.biblemap.put("b7_5_10", new trsuperobject(7,5,10,"Ey semerleri pahalı boz eşeklere binenler,Ey yoldan yaya gidenler, dinleyin!"));
trsuperobject.biblemap.put("b7_5_11", new trsuperobject(7,5,11,"Kuyu başındaki kalabalıklarRABbin zaferlerini,İsrail savaşçılarının zaferlerini anlatıyorlar.Ardından RABbin halkı kent kapılarınaAkın etmeye başladı."));
trsuperobject.biblemap.put("b7_5_12", new trsuperobject(7,5,12,"Uyan, uyan Debora, uyan uyan!Söyle, ezgiler söyle!Ey Avinoam oğlu Barak,Kalk, götür tutsaklarını."));
trsuperobject.biblemap.put("b7_5_13", new trsuperobject(7,5,13,"Geriye kalanlar soyluların yanına geldi,RABbin halkı yiğitleriyle bana geldi."));
trsuperobject.biblemap.put("b7_5_14", new trsuperobject(7,5,14,"Amalek kökünden olanlar Efrayimden geldi,Benyaminliler de seni izleyenlerin arasındaydı.Yöneticiler Makirden,Başbuğ asasını taşıyanlar Zevulundan geldi."));
trsuperobject.biblemap.put("b7_5_15", new trsuperobject(7,5,15,"Deborayla birlikteydi İssakarın beyleri.Evet, İssakaroğulları da Barakın ardındanHızla ovaya indi.Ama Ruben oymağının bölükleriBüyük bir kararsızlık içindeydi."));
trsuperobject.biblemap.put("b7_5_16", new trsuperobject(7,5,16,"Sürülerine kaval çalan çobanlarıDinlemek için neden ağıllarda kaldılar?Evet, Ruben oymağının bölükleriBüyük bir kararsızlık içindeydi."));
trsuperobject.biblemap.put("b7_5_17", new trsuperobject(7,5,17,"Gilatlılar Şeria Irmağının ötesinde kaldı,Dan oymağıysa gemilerde oyalandı.Aşer oymağı deniz kıyısında dinlendi,Koylarda yan gelip oturdu."));
trsuperobject.biblemap.put("b7_5_18", new trsuperobject(7,5,18,"Ama Zevulun ve Naftali halklarıTehlikeye attılar canlarını savaş alanında."));
trsuperobject.biblemap.put("b7_5_19", new trsuperobject(7,5,19,"Taanakta ve Megiddo sularının kıyısındaKrallar gelip savaştılar.Kenan kralları da savaştı.Ancak ne gümüş ne ganimet aldılar."));
trsuperobject.biblemap.put("b7_5_20", new trsuperobject(7,5,20,"Yıldızlar göklerden savaşa katıldı.Göğü bir baştan öbür başa geçerken,Siseraya karşı savaştı."));
trsuperobject.biblemap.put("b7_5_21", new trsuperobject(7,5,21,"Kişon Irmağı, o eski ırmak,Süpürüp götürdü onları.Yürü, ey ruhum, üzerlerine güçle yürü!"));
trsuperobject.biblemap.put("b7_5_22", new trsuperobject(7,5,22,"O zaman atlar dörtnala koştu.Güçlü atların toynaklarıYerde izler bıraktı."));
trsuperobject.biblemap.put("b7_5_23", new trsuperobject(7,5,23,"RABbin meleği, ‹Meroz Kentini lanetleyin› dedi,‹Halkına lanetler yağdırın.Çünkü RABbin yardımına,Zorbalara karşı RABbin yardımına koşmadılar.›"));
trsuperobject.biblemap.put("b7_5_24", new trsuperobject(7,5,24,"Kenlilerden Heverin karısı YaelKadınlar arasında alabildiğine kutsansın.Çadırlarda yaşayan kadınlar arasındaAlabildiğine kutsansın."));
trsuperobject.biblemap.put("b7_5_25", new trsuperobject(7,5,25,"Sisera su istedi, Yael ona süt verdi.Soylulara yaraşır bir çanakla ayran sundu."));
trsuperobject.biblemap.put("b7_5_26", new trsuperobject(7,5,26,"Sol eline çadır kazığını,Sağ eline işçi tokmağını aldı.Vurdu, Siseranın başını ezdi.Şakağına çaktı kazığı, deldi geçirdi."));
trsuperobject.biblemap.put("b7_5_27", new trsuperobject(7,5,27,"Ayaklarının dibine çöktü,Yere serildi Sisera.Düşüp yığıldı Yaelin ayakları dibine,Yığıldığı yerde cansız kaldı."));
trsuperobject.biblemap.put("b7_5_28", new trsuperobject(7,5,28,"Siseranın annesi parmaklıkların ardından,Pencereden bakıp feryat etti:‹Oğlumun savaş arabasıNeden bu kadar gecikti,Nal sesleri neden duyulmuyor?›"));
trsuperobject.biblemap.put("b7_5_29", new trsuperobject(7,5,29,"Bilge kadınlar onu yanıtladılar.O da şöyle düşündü:"));
trsuperobject.biblemap.put("b7_5_30", new trsuperobject(7,5,30,"‹Ganimeti bulmuş, paylaşıyor olmalılar.Her yiğide bir ya da iki kız,Siseraya ganimet olarak rengarenk giysiler,Evet, işlemeli, rengarenk giysiler.Yağmacıların boyunları içinİki yanı işlemeli renkli giysiler,Hepsi ganimet.›"));
trsuperobject.biblemap.put("b7_5_31", new trsuperobject(7,5,31,"Ya RAB, bütün düşmanların böyle yok olsun.Seni sevenlerse,Bütün gücüyle doğan güneş gibi olsunlar.››"));
trsuperobject.biblemap.put("b7_6_1", new trsuperobject(7,6,1,"İsrailliler yine RABbin gözünde kötü olanı yaptılar. RAB de onları yedi yıl süreyle Midyanlıların eline teslim etti."));
trsuperobject.biblemap.put("b7_6_2", new trsuperobject(7,6,2,"Midyan boyunduruğu İsraillilere öyle ağır geldi ki, dağlarda kendilerine sığınaklar, mağaralar, kaleler yaptılar."));
trsuperobject.biblemap.put("b7_6_3", new trsuperobject(7,6,3,"Ekin ektikleri vakit, Midyanlılar, Amalekliler ve öbür doğulu halklar topraklarına girip"));
trsuperobject.biblemap.put("b7_6_4", new trsuperobject(7,6,4,"ordugah kurarlardı. Gazzeye dek ekinleri yok eder, koyun, sığır, eşek gibi geçim kaynağı olan her şeyi alırlardı."));
trsuperobject.biblemap.put("b7_6_5", new trsuperobject(7,6,5,"Hayvanları ve çadırlarıyla birlikte çekirge sürüsü gibi gelirlerdi. Adamları, develeri saymak olanaksızdı. Yakıp yıkmak amacıyla toprakları işgal ederlerdi."));
trsuperobject.biblemap.put("b7_6_6", new trsuperobject(7,6,6,"Midyanlılar İsraili öyle yoksul düşürdüler ki, İsrailliler RABbe yakarmaya başladılar."));
trsuperobject.biblemap.put("b7_6_7", new trsuperobject(7,6,7,"İsrailliler Midyanlılardan ötürü RABbe yakarınca,"));
trsuperobject.biblemap.put("b7_6_9", new trsuperobject(7,6,9,"Mısırlıların elinden, size baskı yapanların hepsinin elinden sizi ben kurtardım. Onları önünüzden kovdum, topraklarını size verdim."));
trsuperobject.biblemap.put("b7_6_10", new trsuperobject(7,6,10,"Size dedim ki, Ben Tanrınız RABbim. Topraklarında yaşadığınız Amorluların ilahlarına tapmayın. Ama sözümü dinlemediniz.››"));
trsuperobject.biblemap.put("b7_6_11", new trsuperobject(7,6,11,"RABbin meleği gelip Aviezerli Yoaşın Ofra Kentindeki yabanıl fıstık ağacının altında oturdu. Yoaşın oğlu Gidyon, buğdayı Midyanlılardan kurtarmak için üzüm sıkma çukurunda dövüyordu."));
trsuperobject.biblemap.put("b7_6_12", new trsuperobject(7,6,12,"RABbin meleği ona görünerek, ‹‹Ey yiğit savaşçı, RAB seninledir›› dedi."));
trsuperobject.biblemap.put("b7_6_13", new trsuperobject(7,6,13,"Gidyon, ‹‹Ey Efendim, eğer RAB bizimleyse bütün bunlar neden başımıza geldi?›› diye karşılık verdi, ‹‹Atalarımız RABbin bizi Mısırdan çıkardığını söylemediler mi? Bize anlattıkları RABbin bütün o harikaları nerede? RAB bizi terk etti, Midyanlıların eline teslim etti.››"));
trsuperobject.biblemap.put("b7_6_14", new trsuperobject(7,6,14,"RAB Gidyona dönüp, ‹‹Kendi gücünle git, İsraili Midyanlıların elinden kurtar›› dedi, ‹‹Seni ben gönderiyorum.››"));
trsuperobject.biblemap.put("b7_6_15", new trsuperobject(7,6,15,"Gidyon, ‹‹Ey Efendim, ben İsraili nasıl kurtarabilirim?›› diye karşılık verdi, ‹‹Ait olduğum boy Manaşşe oymağının en zayıf boyudur. Ben de ailemin en genç adamıyım.››"));
trsuperobject.biblemap.put("b7_6_16", new trsuperobject(7,6,16,"RAB, ‹‹Ben seninle olacağım›› dedi, ‹‹Midyanlıları tek bir adamı yener gibi bozguna uğratacaksın.››"));
trsuperobject.biblemap.put("b7_6_17", new trsuperobject(7,6,17,"Gidyon, ‹‹Benden hoşnutsan, benimle konuşanın sen olduğuna dair bana bir belirti göster›› dedi,"));
trsuperobject.biblemap.put("b7_6_18", new trsuperobject(7,6,18,"‹‹Lütfen gelip sana adağımı sununcaya, önüne koyuncaya dek buradan ayrılma.›› RAB, ‹‹Sen dönünceye dek kalırım›› diye yanıtladı."));
trsuperobject.biblemap.put("b7_6_19", new trsuperobject(7,6,19,"Gidyon eve gidip bir oğlak kesti, bir efa undan mayasız pide yaptı. Eti sepete, et suyunu tencereye koydu; bunları getirip yabanıl fıstık ağacının altında meleğe sundu."));
trsuperobject.biblemap.put("b7_6_20", new trsuperobject(7,6,20,"Tanrının meleği, ‹‹Eti ve mayasız pideleri al, şu kayanın üzerine koy. Et suyunu ise dök›› dedi. Gidyon söyleneni yaptı."));
trsuperobject.biblemap.put("b7_6_21", new trsuperobject(7,6,21,"RABbin meleği elindeki değneğin ucuyla ete ve mayasız pidelere dokununca kayadan ateş fışkırdı. Ateş eti ve mayasız pideleri yakıp kül etti. Sonra RABbin meleği gözden kayboldu."));
trsuperobject.biblemap.put("b7_6_22", new trsuperobject(7,6,22,"Gidyon, gördüğü kişinin RABbin meleği olduğunu anlayınca, ‹‹Eyvah, Egemen RAB! Meleğinin yüzünü gördüm›› dedi."));
trsuperobject.biblemap.put("b7_6_23", new trsuperobject(7,6,23,"RAB ona, ‹‹Sana esenlik olsun. Korkma, ölmeyeceksin›› dedi."));
trsuperobject.biblemap.put("b7_6_24", new trsuperobject(7,6,24,"Gidyon orada RAB için bir sunak yaptı. Sunağa ‹‹Yahve şalom›› adını verdi. Sunak bugün de Aviezerlilerin Ofra Kentinde duruyor."));
trsuperobject.biblemap.put("b7_6_25", new trsuperobject(7,6,25,"Aynı gece RAB, Gidyona, ‹‹Babanın boğasını, yedi yaşındaki ikinci boğayı al›› dedi, ‹‹Sonra babanın Baal için yaptırdığı sunağı yık. Sunağın yanındaki Aşera putunu kes."));
trsuperobject.biblemap.put("b7_6_26", new trsuperobject(7,6,26,"Tanrın RAB için bu höyüğün üstünde uygun bir sunak yap. İkinci boğayı al, keseceğin Aşera putunun odunlarıyla yakmalık sunu olarak sun.››"));
trsuperobject.biblemap.put("b7_6_27", new trsuperobject(7,6,27,"Gidyon adamlarından onunu yanına alarak RABbin kendisine buyurduklarını yerine getirdi. Ne var ki, ailesinden ve kent halkından korktuğu için bunu gündüz yerine gece yaptı."));
trsuperobject.biblemap.put("b7_6_28", new trsuperobject(7,6,28,"Sabah erkenden kalkan kent halkı, Baala ait sunağın yıkıldığını, yanındaki Aşera putunun kesildiğini, ikinci boğanın yeni yapılan sunak üzerinde sunulduğunu gördü."));
trsuperobject.biblemap.put("b7_6_29", new trsuperobject(7,6,29,"Birbirlerine, ‹‹Bu işi kim yaptı?›› diye sordular. Araştırıp soruşturduktan sonra, bu işi Yoaş oğlu Gidyonun yaptığını anladılar."));
trsuperobject.biblemap.put("b7_6_30", new trsuperobject(7,6,30,"Bunun üzerine Yoaşa, ‹‹Oğlunu dışarı çıkar›› dediler, ‹‹Ölmesi gerek. Çünkü Baalın sunağını yıktı, yanındaki Aşera putunu kesti.››"));
trsuperobject.biblemap.put("b7_6_31", new trsuperobject(7,6,31,"Yoaş çevresindeki öfkeli kalabalığa, ‹‹Baalı savunmak size mi düştü?›› dedi, ‹‹Siz mi onu kurtaracaksınız? Onu savunan şafak sökmeden ölecek. Baal tanrıysa, bırakın kendini savunsun. Yıkılan sunak onun!››"));
trsuperobject.biblemap.put("b7_6_32", new trsuperobject(7,6,32,"O gün Yoaş, ‹‹Baal kendini savunsun, yıkılan sunak onun sunağıdır›› diyerek Gidyona Yerubbaal adını verdi."));
trsuperobject.biblemap.put("b7_6_33", new trsuperobject(7,6,33,"Bu arada Midyanlılar, Amalekliler ve öbür doğulu halklar birleşerek Şeria Irmağını geçtiler, gidip Yizreel Vadisinde ordugah kurdular."));
trsuperobject.biblemap.put("b7_6_34", new trsuperobject(7,6,34,"RABbin Ruhu Gidyonu yönlendirmeye başladı. Gidyon borusunu çalınca Aviezerliler onun çevresinde toplandı."));
trsuperobject.biblemap.put("b7_6_35", new trsuperobject(7,6,35,"Gidyon bütün Manaşşeye ulaklar göndererek oranın halkını da topladı. Aşer, Zevulun ve Naftaliye de ulaklar gönderdi. Onlar da onu karşılamaya çıktılar."));
trsuperobject.biblemap.put("b7_6_36", new trsuperobject(7,6,36,"Gidyon Tanrıya şöyle seslendi: ‹‹Söz verdiğin gibi İsraili benim aracılığımla kurtaracağın doğruysa,"));
trsuperobject.biblemap.put("b7_6_37", new trsuperobject(7,6,37,"çiy yalnızca harman yerine koyduğum yün yapağının üzerine düşsün, topraksa kuru kalsın. Böylece, söylediğin gibi İsraili benim aracılığımla kurtaracağını bileceğim.››"));
trsuperobject.biblemap.put("b7_6_38", new trsuperobject(7,6,38,"Ve öyle oldu. Ertesi gün erkenden kalkan Gidyon yapağıyı alıp sıktı. Yapağıdan bir tas dolusu çiy süzüldü."));
trsuperobject.biblemap.put("b7_6_39", new trsuperobject(7,6,39,"Bunun üzerine Gidyon Tanrıya şöyle seslendi: ‹‹Bana kızma, bir istekte daha bulunmak istiyorum. Yapağıyla bir deneme daha yapmama izin ver. Lütfen bu kez yalnızca yapağı kuru kalsın, topraksa çiyle ıslansın.››"));
trsuperobject.biblemap.put("b7_6_40", new trsuperobject(7,6,40,"Tanrı o gece Gidyon'un dediğini yaptı. Yapağı kuru kaldı, toprağın her yanıysa çiyle kaplandı."));
trsuperobject.biblemap.put("b7_7_1", new trsuperobject(7,7,1,"Yerubbaal -Gidyon- ile yanındaki halk erkenden kalkıp Harot Pınarının başında ordugah kurdular. Midyanlıların ordugahıysa onların kuzeyinde, More Tepesinin yanındaki vadideydi."));
trsuperobject.biblemap.put("b7_7_3", new trsuperobject(7,7,3,"Şimdi halka şunu söyle: ‹Korkudan titreyen dönsün, Gilat Dağından geri gitsin.›› Bunun üzerine halktan yirmi iki bin kişi döndü, on bin kişi orada kaldı."));
trsuperobject.biblemap.put("b7_7_5", new trsuperobject(7,7,5,"Gidyon halkı suyun başına götürdü. RAB Gidyona, ‹‹Köpek gibi diliyle su içenleri bir yana, su içmek için dizleri üzerine çökenleri öbür yana ayır›› dedi."));
trsuperobject.biblemap.put("b7_7_6", new trsuperobject(7,7,6,"Ellerini ağızlarına götürerek dilleriyle su içenlerin sayısı üç yüzü buldu. Geri kalanların hepsi su içmek için dizleri üzerine çöktüler."));
trsuperobject.biblemap.put("b7_7_8", new trsuperobject(7,7,8,"Gidyon yalnız üç yüz kişiyi alıkoyarak geri kalan İsraillileri çadırlarına gönderdi. Bu üç yüz kişi, gidenlerin kumanyalarıyla borularını da aldılar. Midyanlıların ordugahı Gidyonun aşağısında, vadideydi."));
trsuperobject.biblemap.put("b7_7_10", new trsuperobject(7,7,10,"Ordugaha yalnız gitmekten korkuyorsan, uşağın Purayı da yanına al."));
trsuperobject.biblemap.put("b7_7_11", new trsuperobject(7,7,11,"Midyanlıların söylediklerine kulak kabart. O zaman ordugahlarına saldırmaya cesaret bulursun.›› Böylece Gidyon uşağı Pura ile ordugahın yanına kadar sokuldu."));
trsuperobject.biblemap.put("b7_7_12", new trsuperobject(7,7,12,"Midyanlılar, Amalekliler ve öbür doğulu halklar çekirge sürüsü gibi vadiye yayılmışlardı. Kıyıların kumu kadar çok, sayısız develeri vardı."));
trsuperobject.biblemap.put("b7_7_13", new trsuperobject(7,7,13,"Gidyon ordugahın yanına vardığında, adamlardan biri arkadaşına gördüğü düşü anlatıyordu. ‹‹Bir düş gördüm›› diyordu, ‹‹Arpa unundan yapılmış bir somun ekmek, Midyan ordugahına doğru yuvarlanarak çadıra kadar geldi, çadıra çarpıp onu devirdi, altüst etti. Çadır yerle bir oldu.››"));
trsuperobject.biblemap.put("b7_7_14", new trsuperobject(7,7,14,"Adamın arkadaşı şöyle karşılık verdi: ‹‹Bu, İsrailli Yoaş oğlu Gidyonun kılıcından başka bir şey değildir. Tanrı Midyanı ve bütün ordugahı onun eline teslim edecek.››"));
trsuperobject.biblemap.put("b7_7_15", new trsuperobject(7,7,15,"Gidyon düşü ve yorumunu duyunca Tanrıya tapındı. İsrail ordugahına döndü ve adamlarına, ‹‹Kalkın! RAB Midyan ordugahını elinize teslim etti›› dedi."));
trsuperobject.biblemap.put("b7_7_16", new trsuperobject(7,7,16,"Sonra üç yüz adamını üç bölüğe ayırdı. Hepsine borular, boş testiler ve testilerin içinde yakılmak üzere çıralar verdi."));
trsuperobject.biblemap.put("b7_7_17", new trsuperobject(7,7,17,"Onlara, ‹‹Gözünüz bende olsun›› dedi, ‹‹Ben ne yaparsam siz de onu yapın. Ordugahın yanına vardığımda ne yaparsam siz de aynısını yapın."));
trsuperobject.biblemap.put("b7_7_18", new trsuperobject(7,7,18,"Ben ve yanımdakiler borularımızı çalınca, siz de ordugahın çevresinde durup borularınızı çalın ve, ‹RAB için ve Gidyon için!› diye bağırın.››"));
trsuperobject.biblemap.put("b7_7_19", new trsuperobject(7,7,19,"Gidyon ile yanındaki yüz kişi gece yarısından az önce, nöbetçi değişiminden hemen sonra ordugahın yanına vardılar; borularını çalmaya başlayıp ellerindeki testileri kırdılar."));
trsuperobject.biblemap.put("b7_7_20", new trsuperobject(7,7,20,"Üç bölük de borularını çalıp testileri kırdı. Çalacakları boruları sağ ellerinde, çıralarıysa sol ellerinde tutuyorlardı. ‹‹Yaşasın RABbin ve Gidyonun kılıcı!›› diye bağırdılar."));
trsuperobject.biblemap.put("b7_7_21", new trsuperobject(7,7,21,"Onlar ordugahın çevresinde dururken, ordugahtakilerin hepsi koşuşmaya, bağırıp kaçışmaya başladı."));
trsuperobject.biblemap.put("b7_7_22", new trsuperobject(7,7,22,"Üç yüz boru birden çalınca RAB ordugahtakilerin hepsini kılıçla birbirlerine saldırttı. Midyan ordusu Sereraya doğru, Beytşittaya, Tabbat yakınındaki Avel-Mehola sınırına dek kaçtı."));
trsuperobject.biblemap.put("b7_7_23", new trsuperobject(7,7,23,"Naftali, Aşer ve bütün Manaşşeden çağrılan İsrailliler Midyanlıları kovalamaya başladılar."));
trsuperobject.biblemap.put("b7_7_24", new trsuperobject(7,7,24,"Gidyon, Efrayimin dağlık bölgesine gönderdiği ulaklar aracılığıyla, ‹‹İnip Midyanlılara saldırın›› dedi, ‹‹Önlerini kesmek için Şeria Irmağının Beytbaraya kadar uzanan bölümünü tutun.›› Efrayimoğulları Şeria Irmağının Beytbaraya kadarki bölümünü ele geçirdiler."));
trsuperobject.biblemap.put("b7_7_25", new trsuperobject(7,7,25,"Midyanlı iki önderi, Orev ile Zeev'i tutsak aldılar. Orev'i Orev Kayası'nda, Zeev'i ise Zeev'in üzüm sıkma çukurunda öldürerek Midyanlılar'ı kovalamaya devam ettiler. Orev'le Zeev'in kesik başlarını Şeria Irmağı'nın karşı yakasından Gidyon'a getirdiler."));
trsuperobject.biblemap.put("b7_8_1", new trsuperobject(7,8,1,"Efrayimoğulları Gidyona, ‹‹Midyanlılarla savaşmaya gittiğinde bizi çağırmadın; bize neden böyle davrandın?›› diyerek onu sert bir dille eleştirdiler."));
trsuperobject.biblemap.put("b7_8_2", new trsuperobject(7,8,2,"Gidyon, ‹‹Sizin yaptığınızın yanında benim yaptığım ne ki?›› diye karşılık verdi, ‹‹Efrayimin bağbozumundan artakalan üzümler, Aviezerin bütün bağbozumu ürününden daha iyi değil mi?"));
trsuperobject.biblemap.put("b7_8_3", new trsuperobject(7,8,3,"Tanrı Midyan önderlerini, Orevi ve Zeevi elinize teslim etti. Sizin yaptıklarınıza kıyasla ben ne yapabildim ki?›› Gidyonun bu sözleri onların öfkesini yatıştırdı."));
trsuperobject.biblemap.put("b7_8_4", new trsuperobject(7,8,4,"Gidyon bitkin olmalarına karşın Midyanlıları kovalamayı sürdüren üç yüz adamıyla Şeria Irmağına ulaşıp karşıya geçti."));
trsuperobject.biblemap.put("b7_8_5", new trsuperobject(7,8,5,"Sukkota vardıklarında kent halkına, ‹‹Lütfen ardımdaki adamlara ekmek verin, bitkin haldeler›› dedi, ‹‹Ben Midyan kralları Zevah ve Salmunnayı kovalıyorum.››"));
trsuperobject.biblemap.put("b7_8_6", new trsuperobject(7,8,6,"Sukkot önderleri, ‹‹Zevah ile Salmunnayı tutsak aldın mı ki, orduna ekmek verelim?›› dediler."));
trsuperobject.biblemap.put("b7_8_7", new trsuperobject(7,8,7,"Gidyon, ‹‹Öyle olsun!›› diye karşılık verdi, ‹‹RAB Zevah ile Salmunnayı elime teslim edince, bedenlerinizi çöl dikenleriyle, çalılarla yaracağım.››"));
trsuperobject.biblemap.put("b7_8_8", new trsuperobject(7,8,8,"Gidyon oradan Penuele gitti ve oranın halkından da aynı şeyi istedi. Penuel halkı da Sukkot halkının verdiği yanıtın aynısını verdi."));
trsuperobject.biblemap.put("b7_8_9", new trsuperobject(7,8,9,"Gidyon onlara, ‹‹Esenlik içinde döndüğüm zaman bu kuleyi yıkacağım›› dedi."));
trsuperobject.biblemap.put("b7_8_10", new trsuperobject(7,8,10,"Zevah ile Salmunna doğulu halkların ordularından artakalan yaklaşık on beş bin kişilik bir orduyla birlikte Karkordaydılar. Eli kılıç tutan yüz yirmi bin savaşçı ölmüştü."));
trsuperobject.biblemap.put("b7_8_11", new trsuperobject(7,8,11,"Gidyon Novah ve Yogbohanın doğusundan, göçebelerin yolundan geçerek düşman ordugahına saldırdı. Adamlar hazırlıksız yakalandılar."));
trsuperobject.biblemap.put("b7_8_12", new trsuperobject(7,8,12,"Zevah ile Salmunna kaçtıysa da Gidyon peşlerine düştü. Bu iki Midyan kralını, Zevah ile Salmunnayı yakalayıp bütün ordularını bozguna uğrattı."));
trsuperobject.biblemap.put("b7_8_13", new trsuperobject(7,8,13,"Yoaş oğlu Gidyon Heres Geçidi yoluyla savaştan döndü."));
trsuperobject.biblemap.put("b7_8_14", new trsuperobject(7,8,14,"Yolda Sukkottan genç bir adamı yakalayıp sorguya çekti. Adam Sukkot önderleriyle ileri gelenlerinin adlarını, toplam yetmiş yedi kişinin adını yazıp Gidyona verdi."));
trsuperobject.biblemap.put("b7_8_15", new trsuperobject(7,8,15,"Gidyon Sukkota gidip halka şöyle dedi: ‹‹Zevah ile Salmunnayı tutsak aldın mı ki bitkin adamlarına ekmek verelim› diyerek beni aşağıladınız. İşte Zevah ile Salmunna!››"));
trsuperobject.biblemap.put("b7_8_16", new trsuperobject(7,8,16,"Sonra kentin ileri gelenlerini topladı; Sukkot halkını çöl dikenleriyle, çalılarla döverek cezalandırdı."));
trsuperobject.biblemap.put("b7_8_17", new trsuperobject(7,8,17,"Ardından Penuel Kulesini yıkıp kent halkını kılıçtan geçirdi."));
trsuperobject.biblemap.put("b7_8_18", new trsuperobject(7,8,18,"Sonra Zevah ile Salmunnaya, ‹‹Tavorda öldürdükleriniz nasıl adamlardı?›› diye sordu. ‹‹Tıpkı senin gibiydiler, hepsi kral oğullarına benziyordu›› yanıtını verdiler."));
trsuperobject.biblemap.put("b7_8_19", new trsuperobject(7,8,19,"Gidyon, ‹‹Onlar kardeşlerimdi, öz annemin oğullarıydı›› dedi, ‹‹Yaşayan RABbin adıyla ant içerim ki, onları sağ bıraksaydınız sizi öldürmezdim.››"));
trsuperobject.biblemap.put("b7_8_20", new trsuperobject(7,8,20,"Sonra büyük oğlu Yetere, ‹‹Haydi, öldür onları›› dedi. Ne var ki, henüz genç olan Yeter korktu, kılıcını çekmedi."));
trsuperobject.biblemap.put("b7_8_21", new trsuperobject(7,8,21,"Bunun üzerine Zevah ile Salmunna Gidyona, ‹‹Sen öldür bizi›› dediler, ‹‹Erkeğin işini ancak erkek yapar.›› Böylece Gidyon varıp Zevah ile Salmunnayı öldürdü. Develerinin boyunlarındaki hilal biçimi süsleri de aldı."));
trsuperobject.biblemap.put("b7_8_22", new trsuperobject(7,8,22,"İsrailliler Gidyona, ‹‹Sen, oğlun ve torunun bize önderlik edin›› dediler. ‹‹Çünkü bizi Midyanlıların elinden sen kurtardın.››"));
trsuperobject.biblemap.put("b7_8_23", new trsuperobject(7,8,23,"Ama Gidyon, ‹‹Ben size önderlik etmem, oğlum da etmez›› diye karşılık verdi, ‹‹Size RAB önderlik edecek.››"));
trsuperobject.biblemap.put("b7_8_24", new trsuperobject(7,8,24,"Sonra, ‹‹Yalnız sizden bir dileğim var›› diye sözünü sürdürdü, ‹‹Ele geçirdiğiniz ganimetin içindeki küpeleri bana verin.›› -İsmaililer altın küpeler takarlardı.-"));
trsuperobject.biblemap.put("b7_8_25", new trsuperobject(7,8,25,"İsrailliler, ‹‹Seve seve veririz›› diyerek yere bir üstlük serdiler. Herkes ele geçirdiği küpeleri üstlüğün üzerine attı."));
trsuperobject.biblemap.put("b7_8_26", new trsuperobject(7,8,26,"Hilaller, kolyeler, Midyan krallarının giydiği mor giysiler ve develerin boyunlarından alınan zincirler dışında, Gidyonun aldığı altın küpelerin ağırlığı bin yedi yüz şekelfı tuttu."));
trsuperobject.biblemap.put("b7_8_27", new trsuperobject(7,8,27,"Gidyon bu altından bir efod yaparak onu kendi kenti olan Ofraya yerleştirdi. Bütün İsrailliler bu put yüzünden RABbe vefasızlık ettiler. Böylece efod Gidyon ile ailesi için bir tuzak oldu."));
trsuperobject.biblemap.put("b7_8_28", new trsuperobject(7,8,28,"İsraillilere yenilen Midyanlılar bir daha toparlanamadılar. Ülke Gidyon zamanında kırk yıl barış içinde yaşadı."));
trsuperobject.biblemap.put("b7_8_29", new trsuperobject(7,8,29,"Yoaş oğlu Yerubbaal dönüp kendi evinde yaşamını sürdürdü."));
trsuperobject.biblemap.put("b7_8_30", new trsuperobject(7,8,30,"Çok sayıda kadınla evlendi ve yetmiş oğlu oldu."));
trsuperobject.biblemap.put("b7_8_31", new trsuperobject(7,8,31,"Ayrıca Şekemde bir cariyesi vardı. Bundan da bir oğlu oldu, adını Avimelek koydu."));
trsuperobject.biblemap.put("b7_8_32", new trsuperobject(7,8,32,"Yoaş oğlu Gidyon iyice yaşlanıp öldü. Aviezerlilere ait Ofra Kentinde, babası Yoaşın mezarına gömüldü."));
trsuperobject.biblemap.put("b7_8_33", new trsuperobject(7,8,33,"Gidyon ölünce İsrailliler yine RABbe vefasızlık ettiler. Baallara taptılar. Baal-Beriti ilah edinerek"));
trsuperobject.biblemap.put("b7_8_34", new trsuperobject(7,8,34,"kendilerini çevrelerindeki düşmanlarının elinden kurtaran Tanrıları RABbi unuttular."));
trsuperobject.biblemap.put("b7_8_35", new trsuperobject(7,8,35,"İsrail'e büyük iyilikler yapan Yerubbaal'ın -Gidyon'un- ev halkına vefasızlık ettiler."));
trsuperobject.biblemap.put("b7_9_1", new trsuperobject(7,9,1,"Yerubbaalın oğlu Avimelek, dayılarının bulunduğu Şekem Kentine giderek onlara ve annesinin boyundan gelen herkese şöyle dedi:"));
trsuperobject.biblemap.put("b7_9_2", new trsuperobject(7,9,2,"‹‹Şekem halkına şunu duyurun: ‹Sizin için hangisi daha iyi? Gidyonun yetmiş oğlu tarafından yönetilmek mi, yoksa bir kişi tarafından yönetilmek mi?› Unutmayın ki ben sizinle aynı etten, aynı kandanım.››"));
trsuperobject.biblemap.put("b7_9_3", new trsuperobject(7,9,3,"Dayıları Avimelekin söylediklerini Şekem halkına ilettiler. Halkın yüreği Avimelekten yanaydı. ‹‹O bizim kardeşimizdir›› dediler."));
trsuperobject.biblemap.put("b7_9_4", new trsuperobject(7,9,4,"Ona Baal-Berit Tapınağından yetmiş parça gümüş verdiler. Avimelek bu parayla kiraladığı belalı serserileri peşine taktı."));
trsuperobject.biblemap.put("b7_9_5", new trsuperobject(7,9,5,"Sonra Ofraya, babasının evine dönüp kardeşlerini, Yerubbaalın yetmiş oğlunu bir taşın üzerinde kesip öldürdü. Yalnız Yerubbaalın küçük oğlu Yotam kaçıp gizlendiği için sağ kaldı."));
trsuperobject.biblemap.put("b7_9_6", new trsuperobject(7,9,6,"Şekem ve Beytmillo halkları toplanarak hep birlikte Şekemde dikili taş meşesinin olduğu yere gittiler; Avimeleki orada kral ilan ettiler."));
trsuperobject.biblemap.put("b7_9_7", new trsuperobject(7,9,7,"Olup biteni Yotama bildirdiklerinde Yotam Gerizim Dağının tepesine çıkıp yüksek sesle halka şöyle dedi: ‹‹Ey Şekem halkı, beni dinleyin, Tanrı da sizi dinleyecek."));
trsuperobject.biblemap.put("b7_9_8", new trsuperobject(7,9,8,"Bir gün ağaçlar kendilerine bir kral meshetmek istediler; zeytin ağacına gidip, ‹Gel kralımız ol› dediler."));
trsuperobject.biblemap.put("b7_9_9", new trsuperobject(7,9,9,"‹‹Zeytin ağacı, ‹İlahları ve insanları onurlandırmak için kullanılan yağımı bırakıp ağaçlar üzerinde sallanmaya mı gideyim?› diye yanıtladı."));
trsuperobject.biblemap.put("b7_9_10", new trsuperobject(7,9,10,"‹‹Bunun üzerine ağaçlar incir ağacına, ‹Gel sen kralımız ol› dediler."));
trsuperobject.biblemap.put("b7_9_11", new trsuperobject(7,9,11,"‹‹İncir ağacı, ‹Tatlılığımı ve güzel meyvemi bırakıp ağaçlar üzerinde sallanmaya mı gideyim?› diye yanıtladı."));
trsuperobject.biblemap.put("b7_9_12", new trsuperobject(7,9,12,"‹‹Sonra ağaçlar asmaya, ‹Gel sen bizim kralımız ol› dediler."));
trsuperobject.biblemap.put("b7_9_13", new trsuperobject(7,9,13,"Asma, ‹İlahlarla insanlara zevk veren yeni şarabımı bırakıp ağaçlar üzerinde sallanmaya mı gideyim?› dedi."));
trsuperobject.biblemap.put("b7_9_14", new trsuperobject(7,9,14,"‹‹Sonunda ağaçlar karaçalıya, ‹Gel sen kralımız ol› dediler."));
trsuperobject.biblemap.put("b7_9_15", new trsuperobject(7,9,15,"‹‹Karaçalı, ‹Eğer gerçekten beni kendinize kral meshetmek istiyorsanız, gelin gölgeme sığının› diye karşılık verdi, ‹Eğer sığınmazsanız, karaçalıdan çıkan ateş Lübnanın bütün sedir ağaçlarını yakıp kül edecektir.›"));
trsuperobject.biblemap.put("b7_9_16", new trsuperobject(7,9,16,"‹‹Şimdi siz Avimeleki kral yapmakla içten ve dürüst davrandığınızı mı sanıyorsunuz? Yerubbaalla ailesine iyilik mi ettiniz? Ona hak ettiği gibi mi davrandınız?"));
trsuperobject.biblemap.put("b7_9_17", new trsuperobject(7,9,17,"Oysa babam sizi Midyanlıların elinden kurtarmak için canını tehlikeye atarak sizin için savaştı."));
trsuperobject.biblemap.put("b7_9_18", new trsuperobject(7,9,18,"Ama bugün siz babamın ailesine karşı ayaklandınız, yetmiş oğlunu bir taşın üzerinde kesip öldürdünüz. Cariyesinden doğan Avimelek kardeşiniz olduğu için onu Şekeme kral yaptınız."));
trsuperobject.biblemap.put("b7_9_19", new trsuperobject(7,9,19,"Eğer bugün Yerubbaalla ailesine içten ve dürüst davrandığınıza inanıyorsanız, Avimelekle sevinin, o da sizinle sevinsin!"));
trsuperobject.biblemap.put("b7_9_20", new trsuperobject(7,9,20,"Ama öyle değilse, dilerim, Avimelek ateş olsun, Şekem ve Beytmillo halkını yakıp kül etsin. Ya da Şekem ve Beytmillo halkı ateş olsun, Avimeleki yakıp kül etsin.››"));
trsuperobject.biblemap.put("b7_9_21", new trsuperobject(7,9,21,"Ardından Yotam kardeşi Avimelekten korktuğu için kaçtı, gidip Beere yerleşti."));
trsuperobject.biblemap.put("b7_9_22", new trsuperobject(7,9,22,"Avimelek İsraili üç yıl yönetti."));
trsuperobject.biblemap.put("b7_9_23", new trsuperobject(7,9,23,"Sonra Tanrı Avimelekle Şekem halkını birbirine düşürdü; halk Avimeleke başkaldırdı."));
trsuperobject.biblemap.put("b7_9_24", new trsuperobject(7,9,24,"Tanrı bunu Avimeleki Yerubbaalın yetmiş oğluna yapılan zorbalığın aynısına uğratmak, kardeşlerini öldüren Avimelekten ve onu bu kırıma isteklendiren Şekem halkından akıttıkları kanın öcünü almak için yaptı."));
trsuperobject.biblemap.put("b7_9_25", new trsuperobject(7,9,25,"Şekem halkı dağ başlarında Avimeleke pusu kurdu. Oradan geçen herkesi soyuyorlardı. Bu durum Avimeleke bildirildi."));
trsuperobject.biblemap.put("b7_9_26", new trsuperobject(7,9,26,"Ebet oğlu Gaal kardeşleriyle birlikte gelip Şekeme yerleşti. Şekem halkı ona güvendi."));
trsuperobject.biblemap.put("b7_9_27", new trsuperobject(7,9,27,"Bağlara çıkıp üzümleri topladıktan, ezip şarap yaptıktan sonra bir şenlik düzenlediler. İlahlarının tapınağına gittiler; orada yiyip içerken Avimeleke lanetler yağdırdılar."));
trsuperobject.biblemap.put("b7_9_28", new trsuperobject(7,9,28,"Ebet oğlu Gaal kalkıp şöyle dedi: ‹‹Avimelek kim ki, biz Şekem halkı ona hizmet edelim? Yerubbaalın oğlu değil mi o? Zevul da onun yardımcısı değil mi? Şekemlilerin babası Hamorun soyundan gelenlere hizmet edin. Neden Avimeleke hizmet edelim?"));
trsuperobject.biblemap.put("b7_9_29", new trsuperobject(7,9,29,"Keşke bu halkı ben yönetseydim! Avimeleki uzaklaştırır ve, ‹Ordunu güçlendir de öyle ortaya çık!› derdim.››"));
trsuperobject.biblemap.put("b7_9_30", new trsuperobject(7,9,30,"Kentin yöneticisi olan Zevul, Ebet oğlu Gaalın sözlerini duyunca öfkelendi."));
trsuperobject.biblemap.put("b7_9_31", new trsuperobject(7,9,31,"Avimeleke gizlice gönderdiği ulaklar aracılığıyla şöyle dedi: ‹‹Ebet oğlu Gaal ve kardeşleri Şekeme geldiler. Kenti sana karşı ayaklandırıyorlar."));
trsuperobject.biblemap.put("b7_9_32", new trsuperobject(7,9,32,"Gel, adamlarınla birlikte gece kırda pusuya yat."));
trsuperobject.biblemap.put("b7_9_33", new trsuperobject(7,9,33,"Sabah güneş doğar doğmaz kalk, kenti bas. Gaal ile adamları sana saldırdığında onlara yapacağını yap.››"));
trsuperobject.biblemap.put("b7_9_34", new trsuperobject(7,9,34,"Böylece Avimelekle adamları gece kalkıp dört bölük halinde Şekem yakınında pusuya yattılar."));
trsuperobject.biblemap.put("b7_9_35", new trsuperobject(7,9,35,"Ebet oğlu Gaal çıkıp kentin giriş kapısında durunca, Avimelekle yanındakiler pusu yerinden fırladılar."));
trsuperobject.biblemap.put("b7_9_36", new trsuperobject(7,9,36,"Gelenleri gören Gaal, Zevula, ‹‹Dağların tepesinden inip gelenlere bak!›› dedi. Zevul, ‹‹Adam sandığın aslında dağların gölgesidir›› diye karşılık verdi."));
trsuperobject.biblemap.put("b7_9_37", new trsuperobject(7,9,37,"Ama Gaal ısrar etti: ‹‹Bak, topraklarımızın ortasında ilerleyenler var. Bir kısmı da Falcılar Meşesi yolundan geliyor.››"));
trsuperobject.biblemap.put("b7_9_38", new trsuperobject(7,9,38,"Bunun üzerine Zevul, ‹‹Avimelek kim ki, ona hizmet edelim› diye övünen sen değil miydin?›› dedi, ‹‹Küçümsediğin halk bu değil mi? Haydi şimdi git, onlarla savaş!››"));
trsuperobject.biblemap.put("b7_9_39", new trsuperobject(7,9,39,"Şekem halkına öncülük eden Gaal, Avimelekle savaşa tutuştu."));
trsuperobject.biblemap.put("b7_9_40", new trsuperobject(7,9,40,"Ama tutunamayıp kaçmaya başladı. Avimelek ardına düştü. Kentin giriş kapısına dek çok sayıda ölü yerde yatıyordu."));
trsuperobject.biblemap.put("b7_9_41", new trsuperobject(7,9,41,"Avimelek Arumada kaldı. Zevul ise Gaalı ve kardeşlerini Şekemden kovdu, kentte yaşamalarına izin vermedi."));
trsuperobject.biblemap.put("b7_9_42", new trsuperobject(7,9,42,"Savaşın ertesi günü Avimelek Şekemlilerin tarlalarına gittiklerini haber aldı."));
trsuperobject.biblemap.put("b7_9_43", new trsuperobject(7,9,43,"Adamlarını üç bölüğe ayırıp kırda pusuya yattı. Halkın kentten çıktığını görünce saldırıp onları öldürdü."));
trsuperobject.biblemap.put("b7_9_44", new trsuperobject(7,9,44,"Sonra yanındaki bölükle hızla ilerleyerek kentin giriş kapısına dayandı. Öbür iki bölükse tarlalardakilere saldırıp onları öldürdü."));
trsuperobject.biblemap.put("b7_9_45", new trsuperobject(7,9,45,"Avimelek gün boyu kente karşı savaştı; kenti ele geçirdikten sonra halkını kılıçtan geçirdi. Kenti yıkıp üstüne tuz serpti."));
trsuperobject.biblemap.put("b7_9_46", new trsuperobject(7,9,46,"Şekem Kulesindeki halk olup biteni duyunca, El-Berit Tapınağının kalesine sığındı."));
trsuperobject.biblemap.put("b7_9_47", new trsuperobject(7,9,47,"Onların Şekem Kulesinde toplandığını haber alan Avimelek,"));
trsuperobject.biblemap.put("b7_9_48", new trsuperobject(7,9,48,"yanındaki halkla birlikte Salmon Dağına çıktı. Eline bir balta alıp ağaçtan bir dal kesti, dalı omuzuna atarak yanındakilere, ‹‹Ne yaptığımı gördünüz›› dedi, ‹‹Çabuk olun, siz de benim gibi yapın.››"));
trsuperobject.biblemap.put("b7_9_49", new trsuperobject(7,9,49,"Böylece hepsi birer dal kesip Avimeleki izledi. Dalları kalenin dibinde yığıp ateşe verdiler. Şekem Kulesindeki bin kadar kadın, erkek yanarak öldü."));
trsuperobject.biblemap.put("b7_9_50", new trsuperobject(7,9,50,"Bundan sonra Avimelek Teves üzerine yürüdü, kenti kuşatıp ele geçirdi."));
trsuperobject.biblemap.put("b7_9_51", new trsuperobject(7,9,51,"Kentin ortasında sağlam bir kule vardı. Kadın erkek bütün kent halkı oraya sığındı. Kapıları kapayıp kulenin damına çıktılar."));
trsuperobject.biblemap.put("b7_9_52", new trsuperobject(7,9,52,"Avimelek gelip kuleyi kuşattı. Ateşe vermek için kapısına yaklaştığında,"));
trsuperobject.biblemap.put("b7_9_53", new trsuperobject(7,9,53,"bir kadın değirmenin üst taşını Avimelekin üzerine atıp başını yardı."));
trsuperobject.biblemap.put("b7_9_54", new trsuperobject(7,9,54,"Avimelek hemen silahlarını taşıyan uşağını çağırdı ve, ‹‹Kılıcını çek, beni öldür›› dedi, ‹‹Hiç kimse, ‹Avimeleki bir kadın öldürdü› demesin.›› Uşak kılıcını Avimeleke saplayıp onu öldürdü."));
trsuperobject.biblemap.put("b7_9_55", new trsuperobject(7,9,55,"Avimelekin öldüğünü görünce İsrailliler evlerine döndüler."));
trsuperobject.biblemap.put("b7_9_56", new trsuperobject(7,9,56,"Böylece Tanrı yetmiş kardeşini öldürerek babasına büyük kötülük eden Avimeleki cezalandırdı."));
trsuperobject.biblemap.put("b7_9_57", new trsuperobject(7,9,57,"Tanrı Şekem halkını da yaptıkları kötülüklerden ötürü cezalandırdı. Yerubbaal'ın oğlu Yotam'ın lanetine uğradılar."));
trsuperobject.biblemap.put("b7_10_1", new trsuperobject(7,10,1,"Avimelekin ölümünden sonra İsraili kurtarmak için İssakar oymağından Dodo oğlu Pua oğlu Tola adında bir adam ortaya çıktı. Tola Efrayimin dağlık bölgesindeki Şamirde yaşardı."));
trsuperobject.biblemap.put("b7_10_2", new trsuperobject(7,10,2,"İsraili yirmi üç yıl yönettikten sonra öldü, Şamirde gömüldü."));
trsuperobject.biblemap.put("b7_10_3", new trsuperobject(7,10,3,"Ondan sonra Gilatlı Yair başa geçti. Yair İsraili yirmi iki yıl yönetti."));
trsuperobject.biblemap.put("b7_10_4", new trsuperobject(7,10,4,"Otuz oğlu vardı. Bunlar otuz eşeğe biner, otuz kenti yönetirlerdi. Gilat yöresindeki bu kentler bugün de Havvot-Yair diye anılıyor."));
trsuperobject.biblemap.put("b7_10_5", new trsuperobject(7,10,5,"Yair ölünce Kamonda gömüldü. gelir."));
trsuperobject.biblemap.put("b7_10_6", new trsuperobject(7,10,6,"İsrailliler yine RABbin gözünde kötü olanı yaptılar; Baallara, Aştoretlere, Aram, Sayda, Moav, Ammon ve Filist ilahlarına kulluk ettiler. RABbi terk ettiler, Ona kulluk etmediler."));
trsuperobject.biblemap.put("b7_10_7", new trsuperobject(7,10,7,"Bu yüzden İsraillilere öfkelenen RAB, onları Filistlilere ve Ammonlulara tutsak etti."));
trsuperobject.biblemap.put("b7_10_8", new trsuperobject(7,10,8,"Bunlar o yıldan başlayarak İsraillileri baskı altında ezdiler; Şeria Irmağının ötesinde, Gilattaki Amorlular ülkesinde yaşayan bütün İsraillileri on sekiz yıl baskı altında tuttular."));
trsuperobject.biblemap.put("b7_10_9", new trsuperobject(7,10,9,"Ammonlular Yahuda, Benyamin ve Efrayim oymaklarıyla savaşmak için Şeria Irmağının ötesine geçtiler. İsrail büyük sıkıntı içindeydi."));
trsuperobject.biblemap.put("b7_10_10", new trsuperobject(7,10,10,"İsrailliler RABbe, ‹‹Sana karşı günah işledik›› diye seslendiler, ‹‹Seni, Tanrımızı terk edip Baallara kulluk ettik.››"));
trsuperobject.biblemap.put("b7_10_11", new trsuperobject(7,10,11,"RAB, ‹‹Sizi Mısırlılardan, Amorlulardan, Ammonlulardan, Filistlilerden kurtaran ben değil miyim?›› diye karşılık verdi,"));
trsuperobject.biblemap.put("b7_10_12", new trsuperobject(7,10,12,"‹‹Saydalılar, Amalekliler, Maonlular size baskı yaptıklarında bana yakardınız, ben de sizi onların elinden kurtardım."));
trsuperobject.biblemap.put("b7_10_13", new trsuperobject(7,10,13,"Sizse beni terk ettiniz, başka ilahlara kulluk ettiniz. Bu yüzden sizi bir daha kurtarmayacağım."));
trsuperobject.biblemap.put("b7_10_14", new trsuperobject(7,10,14,"Gidin, seçtiğiniz ilahlara yakarın; sıkıntıya düştüğünüzde sizi onlar kurtarsın.››"));
trsuperobject.biblemap.put("b7_10_15", new trsuperobject(7,10,15,"İsrailliler, ‹‹Günah işledik›› dediler, ‹‹Bize ne istersen yap. Yalnız bugün bizi kurtar.››"));
trsuperobject.biblemap.put("b7_10_16", new trsuperobject(7,10,16,"Sonra aralarındaki yabancı putları atıp RABbe tapındılar. RAB de onların daha fazla acı çekmesine dayanamadı."));
trsuperobject.biblemap.put("b7_10_17", new trsuperobject(7,10,17,"Ammonlular toplanıp Gilatta ordugah kurunca İsrailliler de toplanarak Mispada ordugah kurdular."));
trsuperobject.biblemap.put("b7_10_18", new trsuperobject(7,10,18,"Gilat halkının önderleri birbirlerine, ‹‹Ammonlular'a karşı ilk saldırıyı başlatan kişi, bütün Gilat halkının önderi olacak›› dediler."));
trsuperobject.biblemap.put("b7_11_1", new trsuperobject(7,11,1,"Yiftah adında yiğit bir savaşçı vardı. Bir fahişenin oğlu olan Yiftahın babasının adı Gilattı."));
trsuperobject.biblemap.put("b7_11_2", new trsuperobject(7,11,2,"Gilatın karısı da ona erkek çocuklar doğurmuştu. Bu çocuklar büyüyünce Yiftahı kovmuşlardı. Ona, ‹‹Babamızın evinden miras almayacaksın. Çünkü sen başka bir kadının oğlusun›› demişlerdi."));
trsuperobject.biblemap.put("b7_11_3", new trsuperobject(7,11,3,"Yiftah kardeşlerinden kaçıp Tov yöresine yerleşti. Çevresinde toplanan serserilere önderlik etmeye başladı."));
trsuperobject.biblemap.put("b7_11_4", new trsuperobject(7,11,4,"Bir süre sonra Ammonlular İsraillilere savaş açtı."));
trsuperobject.biblemap.put("b7_11_5", new trsuperobject(7,11,5,"Savaş patlak verince Gilat ileri gelenleri Yiftahı almak için Tov yöresine gittiler."));
trsuperobject.biblemap.put("b7_11_6", new trsuperobject(7,11,6,"Ona, ‹‹Gel, komutanımız ol, Ammonlularla savaşalım›› dediler."));
trsuperobject.biblemap.put("b7_11_7", new trsuperobject(7,11,7,"Yiftah, ‹‹Benden nefret eden, beni babamın evinden kovan siz değil miydiniz?›› diye yanıtladı, ‹‹Sıkıntıya düşünce neden bana geldiniz?››"));
trsuperobject.biblemap.put("b7_11_8", new trsuperobject(7,11,8,"Gilat ileri gelenleri, ‹‹Sana başvuruyoruz; çünkü bizimle gelip Ammonlularla savaşmanı, bize, Gilat halkına önderlik etmeni istiyoruz›› dediler."));
trsuperobject.biblemap.put("b7_11_9", new trsuperobject(7,11,9,"Yiftah, ‹‹Ammonlularla savaşmak için beni götürürseniz, RAB de onları elime teslim ederse, sizin önderiniz olacak mıyım?›› diye sordu."));
trsuperobject.biblemap.put("b7_11_10", new trsuperobject(7,11,10,"Gilat ileri gelenleri, ‹‹RAB aramızda tanık olsun, kesinlikle dediğin gibi yapacağız›› dediler."));
trsuperobject.biblemap.put("b7_11_11", new trsuperobject(7,11,11,"Böylece Yiftah Gilat ileri gelenleriyle birlikte gitti. Halk onu kendine önder ve komutan yaptı. Yiftah bütün söylediklerini Mispada, RABbin önünde yineledi."));
trsuperobject.biblemap.put("b7_11_12", new trsuperobject(7,11,12,"Sonra Ammon Kralına ulaklar göndererek, ‹‹Aramızda ne var ki, ülkeme saldırmaya kalkıyorsun?›› dedi."));
trsuperobject.biblemap.put("b7_11_13", new trsuperobject(7,11,13,"Ammon Kralı, Yiftahın ulaklarına şu karşılığı verdi: ‹‹İsrailliler Mısırdan çıktıktan sonra Arnon Vadisinden Yabbuk ve Şeria ırmaklarına kadar uzanan topraklarımı aldılar. Şimdi buraları bana savaşsız geri ver.››"));
trsuperobject.biblemap.put("b7_11_14", new trsuperobject(7,11,14,"Yiftah yine Ammon Kralına ulaklar göndererek"));
trsuperobject.biblemap.put("b7_11_15", new trsuperobject(7,11,15,"şöyle dedi: ‹‹Yiftah diyor ki, İsrailliler ne Moav ülkesini, ne de Ammon topraklarını aldı."));
trsuperobject.biblemap.put("b7_11_16", new trsuperobject(7,11,16,"Mısırdan çıktıkları zaman Kızıldenize kadar çölde yürüyerek Kadeşe ulaştılar."));
trsuperobject.biblemap.put("b7_11_17", new trsuperobject(7,11,17,"Sonra Edom Kralına ulaklar göndererek, ‹Lütfen topraklarından geçmemize izin ver› dediler. Edom Kralı kulak asmadı. İsrailliler Moav Kralına da ulaklar gönderdi, ama o da izin vermedi. Bunun üzerine Kadeşte kaldılar."));
trsuperobject.biblemap.put("b7_11_18", new trsuperobject(7,11,18,"‹‹Çölü izleyerek Edom ile Moav topraklarının çevresinden geçtiler; Moav bölgesinin doğusunda, Arnon Vadisinin öbür yakasında konakladılar. Moav sınırından içeri girmediler. Çünkü Arnon Vadisi sınırdı."));
trsuperobject.biblemap.put("b7_11_19", new trsuperobject(7,11,19,"‹‹Sonra Heşbonda egemenlik süren Amorluların Kralı Sihona ulaklar göndererek, ‹Ülkenden geçip topraklarımıza ulaşmamıza izin ver› diye rica ettiler."));
trsuperobject.biblemap.put("b7_11_20", new trsuperobject(7,11,20,"Ama Sihon İsraillilerin topraklarından geçip gideceklerine inanmadı. Bu nedenle bütün halkını toplayıp Yahesada ordugah kurdu ve İsraillilerle savaşa tutuştu."));
trsuperobject.biblemap.put("b7_11_21", new trsuperobject(7,11,21,"‹‹İsrailin Tanrısı RAB, Sihonu ve bütün halkını İsraillilerin eline teslim etti. İsrailliler Amorluları yenip o yöredeki halkın bütün topraklarını ele geçirdiler."));
trsuperobject.biblemap.put("b7_11_22", new trsuperobject(7,11,22,"Arnon Vadisinden Yabbuk Irmağına, çölden Şeria Irmağına kadar uzanan bütün Amor topraklarını ele geçirdiler."));
trsuperobject.biblemap.put("b7_11_23", new trsuperobject(7,11,23,"‹‹İsrailin Tanrısı RAB Amorluları kendi halkı İsrailin önünden kovduktan sonra, sen hangi hakla buraları geri istiyorsun?"));
trsuperobject.biblemap.put("b7_11_24", new trsuperobject(7,11,24,"İlahın Kemoş sana bir yer verse oraya sahip çıkmaz mısın? Biz de Tanrımız RABbin önümüzden kovduğu halkın topraklarını sahipleneceğiz."));
trsuperobject.biblemap.put("b7_11_25", new trsuperobject(7,11,25,"Sen Moav Kralı Sippor oğlu Balaktan üstün müsün? O hiç İsraillilerle çekişti mi, hiç onlarla savaşmaya kalkıştı mı?"));
trsuperobject.biblemap.put("b7_11_26", new trsuperobject(7,11,26,"İsrailliler üç yüz yıldır Heşbonda, Aroerde, bunların çevre köylerinde ve Arnon kıyısındaki bütün kentlerde yaşarken neden buraları geri almaya çalışmadınız?"));
trsuperobject.biblemap.put("b7_11_27", new trsuperobject(7,11,27,"Ben sana karşı suç işlemedim. Ama sen benimle savaşmaya kalkışmakla bana haksızlık ediyorsun. Hakim olan RAB, İsraillilerle Ammonlular arasında bugün hakemlik yapsın.››"));
trsuperobject.biblemap.put("b7_11_28", new trsuperobject(7,11,28,"Ne var ki Ammon Kralı, Yiftahın kendisine ilettiği bu sözlere kulak asmadı."));
trsuperobject.biblemap.put("b7_11_29", new trsuperobject(7,11,29,"RABbin Ruhu Yiftahın üzerine indi. Yiftah, Gilat ve Manaşşeden geçti, Gilattaki Mispadan geçerek Ammonlulara doğru ilerledi."));
trsuperobject.biblemap.put("b7_11_30", new trsuperobject(7,11,30,"RABbin önünde ant içerek şöyle dedi: ‹‹Gerçekten Ammonluları elime teslim edersen,"));
trsuperobject.biblemap.put("b7_11_31", new trsuperobject(7,11,31,"onları yenip sağ salim döndüğümde beni karşılamak için evimin kapısından ilk çıkan, RABbe adanacaktır. Onu yakmalık sunu olarak sunacağım.››"));
trsuperobject.biblemap.put("b7_11_32", new trsuperobject(7,11,32,"Yiftah bundan sonra Ammonlularla savaşmaya gitti. RAB onları Yiftahın eline teslim etti."));
trsuperobject.biblemap.put("b7_11_33", new trsuperobject(7,11,33,"Yiftah, başta Avel-Keramim olmak üzere, Aroerden Minnite kadar yirmi kenti yakıp yıkarak Ammonlulara çok büyük kayıplar verdirdi. Böylece Ammonlular İsraillilerin boyunduruğuna girdi."));
trsuperobject.biblemap.put("b7_11_34", new trsuperobject(7,11,34,"Yiftah Mispaya, kendi evine döndüğünde, kızı tef çalıp dans ederek onu karşılamaya çıktı. Tek çocuğu oydu, ondan başka ne oğlu ne de kızı vardı."));
trsuperobject.biblemap.put("b7_11_35", new trsuperobject(7,11,35,"Yiftah, kızını görünce giysilerini yırtarak, ‹‹Eyvahlar olsun, kızım!›› dedi, ‹‹Beni perişan ettin, umarsız bıraktın! Çünkü RABbe verdiğim sözden dönemem.››"));
trsuperobject.biblemap.put("b7_11_36", new trsuperobject(7,11,36,"Kız, ‹‹Baba, RABbe ant içtin›› dedi, ‹‹Madem RAB düşmanların olan Ammonlulardan senin öcünü aldı, ağzından ne çıktıysa bana öyle yap.››"));
trsuperobject.biblemap.put("b7_11_37", new trsuperobject(7,11,37,"Sonra ekledi: ‹‹Yalnız bir dileğim var: Beni iki ay serbest bırak, gidip arkadaşlarımla kırlarda gezineyim, kızlığıma ağlayayım.››"));
trsuperobject.biblemap.put("b7_11_38", new trsuperobject(7,11,38,"Babası, ‹‹Gidebilirsin›› diyerek onu iki ay serbest bıraktı. Kız arkadaşlarıyla birlikte kırlara çıkıp erdenliğine ağladı."));
trsuperobject.biblemap.put("b7_11_39", new trsuperobject(7,11,39,"İki ay sonra babasının yanına döndü. Babası da içtiği andı yerine getirdi. Kıza erkek eli değmemişti. Bundan sonra İsrailde bir gelenek oluştu."));
trsuperobject.biblemap.put("b7_11_40", new trsuperobject(7,11,40,"İsrail kızları her yıl kırlara çıkıp Gilatlı Yiftah'ın kızı için dört gün yas tutar oldular."));
trsuperobject.biblemap.put("b7_12_1", new trsuperobject(7,12,1,"Efrayimli erkekler toplanıp Safona geçtiler. Yiftaha, ‹‹Ammonlularla savaşmaya gittiğinde bizi neden çağırmadın?›› dediler, ‹‹Seni de evini de yakacağız.››"));
trsuperobject.biblemap.put("b7_12_2", new trsuperobject(7,12,2,"Yiftah, ‹‹Halkımla ben Ammonlulara karşı amansız bir savaşa tutuşmuştuk›› diye yanıtladı, ‹‹Sizi çağırdım, ama gelip beni onların elinden kurtarmadınız."));
trsuperobject.biblemap.put("b7_12_3", new trsuperobject(7,12,3,"Beni kurtarmak istemediğinizi görünce canımı dişime takıp Ammonlulara karşı harekete geçtim. Sonunda RAB onları elime teslim etti. Neden bugün benimle savaşmaya kalkışıyorsunuz?››"));
trsuperobject.biblemap.put("b7_12_4", new trsuperobject(7,12,4,"Bundan sonra Yiftah Gilat erkeklerini toplayarak Efrayimoğullarıyla savaşa girdi. Gilatlılar Efrayimoğullarına saldırdılar. Çünkü Efrayimoğulları onlara, ‹‹Ey Efrayim ve Manaşşe halkları arasında yaşayan Gilatlılar, siz Efrayimden kaçan döneklersiniz!›› demişlerdi."));
trsuperobject.biblemap.put("b7_12_5", new trsuperobject(7,12,5,"Şeria Irmağının Efrayime yol veren geçitlerini tutan Gilatlılar, geçmek isteyen Efrayimli kaçaklara, ‹‹Efrayimli misin?›› diye sorarlardı. Adamlar, ‹‹Hayır›› derlerse,"));
trsuperobject.biblemap.put("b7_12_6", new trsuperobject(7,12,6,"o zaman onlara, ‹‹Şibbolet› deyin bakalım›› derlerdi. Adamlar ‹‹Sibbolet›› derdi. Çünkü ‹‹Şibbolet›› sözcüğünü doğru söyleyemezlerdi. Bunun üzerine onları yakalayıp Şeria Irmağının geçitlerinde öldürürlerdi. O gün Efrayimlilerden kırk iki bin kişi öldürüldü."));
trsuperobject.biblemap.put("b7_12_7", new trsuperobject(7,12,7,"Gilatlı Yiftah İsraili altı yıl yönetti. Ölünce Gilat kentlerinden birinde gömüldü."));
trsuperobject.biblemap.put("b7_12_8", new trsuperobject(7,12,8,"Ondan sonra İsrailin başına Beytlehemli İvsan geçti."));
trsuperobject.biblemap.put("b7_12_9", new trsuperobject(7,12,9,"İvsanın otuz oğlu, otuz kızı vardı. İvsan kızlarını başka boylara verdi, oğullarına da başka boylardan kızlar aldı. İsraili yedi yıl yönetti."));
trsuperobject.biblemap.put("b7_12_10", new trsuperobject(7,12,10,"Ölünce Beytlehemde gömüldü."));
trsuperobject.biblemap.put("b7_12_11", new trsuperobject(7,12,11,"Ondan sonra İsrailin başına Zevulun oymağından Elon geçti. Elon İsraili on yıl yönetti."));
trsuperobject.biblemap.put("b7_12_12", new trsuperobject(7,12,12,"Ölünce Zevulun topraklarında, Ayalonda gömüldü."));
trsuperobject.biblemap.put("b7_12_13", new trsuperobject(7,12,13,"Onun ardından İsrailin başına Piratonlu Hillel oğlu Avdon geçti."));
trsuperobject.biblemap.put("b7_12_14", new trsuperobject(7,12,14,"Avdonun kırk oğlu, otuz torunu ve bunların bindiği yetmiş eşeği vardı. İsraili sekiz yıl yönetti."));
trsuperobject.biblemap.put("b7_12_15", new trsuperobject(7,12,15,"Piratonlu Hillel oğlu Avdon ölünce Amalekliler'e ait dağlık bölgenin Efrayim yöresindeki Piraton'da gömüldü."));
trsuperobject.biblemap.put("b7_13_1", new trsuperobject(7,13,1,"İsrailliler yine RABbin gözünde kötü olanı yaptılar. RAB de onları kırk yıl süreyle Filistlilerin boyunduruğuna terk etti."));
trsuperobject.biblemap.put("b7_13_2", new trsuperobject(7,13,2,"Dan oymağından Soralı bir adam vardı. Adı Manoahtı. Karısı kısırdı ve hiç çocuğu olmamıştı."));
trsuperobject.biblemap.put("b7_13_4", new trsuperobject(7,13,4,"‹‹Bundan böyle şarap ya da içki içmemeye dikkat et, murdar bir şey yeme."));
trsuperobject.biblemap.put("b7_13_5", new trsuperobject(7,13,5,"Çünkü gebe kalıp bir oğul doğuracaksın. Onun başına ustura değmeyecek. Çünkü o daha rahmindeyken Tanrıya adanmış olacak. İsraili Filistlilerin elinden kurtarmaya başlayacak olan odur.››"));
trsuperobject.biblemap.put("b7_13_6", new trsuperobject(7,13,6,"Kadın kocasına gidip, ‹‹Yanıma bir Tanrı adamı geldi›› dedi, ‹‹Tanrının meleğine benzer görkemli bir görünüşü vardı. Nereden geldiğini sormadım. Bana adını da söylemedi."));
trsuperobject.biblemap.put("b7_13_7", new trsuperobject(7,13,7,"Ama, ‹Gebe kalıp bir oğul doğuracaksın› dedi, ‹Bundan böyle şarap ve içki içme, murdar bir şey yeme. Çünkü çocuk ana rahmine düştüğü andan öleceği güne dek Tanrının adanmışı olacak.››"));
trsuperobject.biblemap.put("b7_13_8", new trsuperobject(7,13,8,"Manoah RABbe şöyle yakardı: ‹‹Ya Rab, gönderdiğin Tanrı adamının yine gelmesini, doğacak çocuk için ne yapmamız gerektiğini bize öğretmesini dilerim.››"));
trsuperobject.biblemap.put("b7_13_9", new trsuperobject(7,13,9,"Tanrı Manoahın yakarışını duydu. Kadın tarladayken Tanrının meleği yine ona göründü. Ne var ki, Manoah karısının yanında değildi."));
trsuperobject.biblemap.put("b7_13_10", new trsuperobject(7,13,10,"Kadın haber vermek için koşa koşa kocasına gitti. ‹‹İşte geçen gün yanıma gelen adam yine bana göründü!›› dedi."));
trsuperobject.biblemap.put("b7_13_11", new trsuperobject(7,13,11,"Manoah kalkıp karısının ardısıra gitti. Adamın yanına varınca, ‹‹Karımla konuşan adam sen misin?›› diye sordu. Adam, ‹‹Evet, benim›› dedi."));
trsuperobject.biblemap.put("b7_13_12", new trsuperobject(7,13,12,"Manoah, ‹‹Söylediklerin yerine geldiğinde, çocuğun yaşamı ve göreviyle ilgili yargı ne olacak?›› diye sordu."));
trsuperobject.biblemap.put("b7_13_13", new trsuperobject(7,13,13,"RABbin meleği, ‹‹Karın kendisine söylediğim her şeyden sakınsın›› diye karşılık verdi,"));
trsuperobject.biblemap.put("b7_13_14", new trsuperobject(7,13,14,"‹‹Asmanın ürününden üretilen hiçbir şey yemesin, şarap ve içki içmesin. Murdar bir şey yemesin. Buyurduklarımın hepsini yerine getirsin.››"));
trsuperobject.biblemap.put("b7_13_15", new trsuperobject(7,13,15,"Manoah, ‹‹Seni alıkoymak, onuruna bir oğlak kesmek istiyoruz›› dedi."));
trsuperobject.biblemap.put("b7_13_16", new trsuperobject(7,13,16,"RABbin meleği, ‹‹Beni alıkoysan da hazırlayacağın yemeği yemem›› dedi, ‹‹Yakmalık bir sunu sunacaksan, RABbe sunmalısın.›› Manoah onun RABbin meleği olduğunu anlamamıştı."));
trsuperobject.biblemap.put("b7_13_17", new trsuperobject(7,13,17,"RABbin meleğine, ‹‹Adın ne?›› diye sordu, ‹‹Bilelim ki, söylediklerin yerine geldiğinde seni onurlandıralım.››"));
trsuperobject.biblemap.put("b7_13_18", new trsuperobject(7,13,18,"RABbin meleği, ‹‹Adımı niçin soruyorsun?›› dedi, ‹‹Adım tanımlanamaz.››"));
trsuperobject.biblemap.put("b7_13_19", new trsuperobject(7,13,19,"Manoah bir oğlakla tahıl sunusunu aldı, bir kayanın üzerinde RABbe sundu. O anda Manoahla karısının gözü önünde şaşılacak şeyler oldu:"));
trsuperobject.biblemap.put("b7_13_20", new trsuperobject(7,13,20,"RABbin meleği sunaktan yükselen alevle birlikte göğe yükseldi. Bunu gören Manoahla karısı yüzüstü yere kapandılar."));
trsuperobject.biblemap.put("b7_13_21", new trsuperobject(7,13,21,"RABbin meleği Manoahla karısına bir daha görünmeyince, Manoah onun RABbin meleği olduğunu anladı."));
trsuperobject.biblemap.put("b7_13_22", new trsuperobject(7,13,22,"Karısına, ‹‹Kesinlikle öleceğiz›› dedi, ‹‹Çünkü Tanrıyı gördük.››"));
trsuperobject.biblemap.put("b7_13_23", new trsuperobject(7,13,23,"Karısı, ‹‹RAB bizi öldürmek isteseydi, yakmalık sunuyu ve tahıl sunusunu kabul etmezdi›› diye karşılık verdi, ‹‹Bütün bunları bize göstermezdi. Bugün söylediklerini de işitmezdik.››"));
trsuperobject.biblemap.put("b7_13_24", new trsuperobject(7,13,24,"Ve kadın bir erkek çocuk doğurdu. Adını Şimşon koydu. Çocuk büyüyüp gelişti. RAB de onu kutsadı."));
trsuperobject.biblemap.put("b7_13_25", new trsuperobject(7,13,25,"RAB'bin Ruhu Sora ile Eştaol arasında, Mahane-Dan'da bulunan Şimşon'u yönlendirmeye başladı."));
trsuperobject.biblemap.put("b7_14_1", new trsuperobject(7,14,1,"Şimşon bir gün Timnaya gitti. Orada Filistli bir kadın gördü."));
trsuperobject.biblemap.put("b7_14_2", new trsuperobject(7,14,2,"Geri dönünce annesiyle babasına, ‹‹Timnada Filistli bir kadın gördüm›› dedi, ‹‹Onu hemen bana eş olarak alın.››"));
trsuperobject.biblemap.put("b7_14_3", new trsuperobject(7,14,3,"Annesiyle babası, ‹‹Akrabalarının ya da halkımızın kızları arasında kimse yok mu ki, sünnetsiz Filistlilerden kız almaya kalkıyorsun?›› diye karşılık verdiler. Ama Şimşon babasına, ‹‹Bana o kadını al, ondan hoşlanıyorum›› dedi."));
trsuperobject.biblemap.put("b7_14_4", new trsuperobject(7,14,4,"Şimşonun annesiyle babası bunu isteyenin RAB olduğunu anlamadılar. Çünkü RAB o sırada İsraillilere egemen olan Filistlilere karşı fırsat kolluyordu."));
trsuperobject.biblemap.put("b7_14_5", new trsuperobject(7,14,5,"Böylece Şimşon annesi ve babasıyla Timnaya doğru yola koyuldu. Timna bağlarına vardıklarında, genç bir aslan kükreyerek Şimşonun karşısına çıktı."));
trsuperobject.biblemap.put("b7_14_6", new trsuperobject(7,14,6,"Şimşon üzerine inen RABbin Ruhuyla güçlendi ve aslanı bir oğlak parçalar gibi çıplak elle parçaladı. Ama yaptığını ne annesine ne de babasına bildirdi."));
trsuperobject.biblemap.put("b7_14_7", new trsuperobject(7,14,7,"Sonra gidip kadınla konuştu ve ondan çok hoşlandı."));
trsuperobject.biblemap.put("b7_14_8", new trsuperobject(7,14,8,"Bir süre sonra kadınla evlenmek üzere yine Timnaya giderken, aslanın leşini görmek için yoldan saptı. Bir arı sürüsünün aslanın leşini kovana çevirdiğini gördü."));
trsuperobject.biblemap.put("b7_14_9", new trsuperobject(7,14,9,"Kovandaki balı avuçlarına doldurdu, yiye yiye oradan uzaklaştı. Annesiyle babasının yanına varınca baldan onlara da verdi, onlar da yedi. Ama balı aslanın leşinden aldığını söylemedi."));
trsuperobject.biblemap.put("b7_14_10", new trsuperobject(7,14,10,"Babası kadını görmeye gidince, Şimşon da damat geleneğine uyarak orada bir şölen düzenledi."));
trsuperobject.biblemap.put("b7_14_11", new trsuperobject(7,14,11,"Filistliler onu görünce ona eşlik etmek üzere otuz genç getirdiler."));
trsuperobject.biblemap.put("b7_14_12", new trsuperobject(7,14,12,"Şimşon onlara, ‹‹Size bir bilmece sorayım›› dedi, ‹‹Şölenin yedi günü içinde kesin yanıtı bulup bana bildirirseniz, size otuz keten mintan, otuz takım da üst giysi vereceğim."));
trsuperobject.biblemap.put("b7_14_13", new trsuperobject(7,14,13,"Ama bilmeceyi çözemezseniz, o zaman da siz bana otuz keten mintanla otuz takım üst giysi vereceksiniz.›› Ona, ‹‹Seni dinliyoruz›› dediler, ‹‹Söyle bakalım bilmeceni.››"));
trsuperobject.biblemap.put("b7_14_14", new trsuperobject(7,14,14,"Şimşon,  ‹‹Yiyenden yiyecek,Güçlüden tatlı çıktı››"));
trsuperobject.biblemap.put("b7_14_15", new trsuperobject(7,14,15,"Dördüncü gün gençler Şimşonun karısına, ‹‹Kocanı kandır da bize bilmecenin yanıtını versin›› dediler, ‹‹Yoksa, seni de babanın evini de yakarız. Bizi soymak için mi buraya çağırdınız?›› Masoretik metin ‹‹Yedinci››."));
trsuperobject.biblemap.put("b7_14_16", new trsuperobject(7,14,16,"Şimşonun karısı ağlayarak ona, ‹‹Benden nefret ediyorsun›› dedi, ‹‹Beni sevmiyorsun. Soydaşlarıma bir bilmece sordun, yanıtını bana söylemedin.›› Şimşon karısına, ‹‹Bak›› dedi, ‹‹Anneme babama bile söylemedim, sana mı söyleyeceğim?››"));
trsuperobject.biblemap.put("b7_14_17", new trsuperobject(7,14,17,"Kadın şölen boyunca yedi gün ağlayıp durdu. Kadının sürekli sıkıştırması üzerine Şimşon yedinci gün bilmecenin yanıtını ona söyledi. Kadın da yanıtı soydaşlarına iletti."));
trsuperobject.biblemap.put("b7_14_18", new trsuperobject(7,14,18,"Yedinci gün, gün batmadan kentli gençler Şimşona geldiler.  ‹‹Baldan tatlı,Aslandan güçlü ne var?›› Şimşon, ‹‹Düvemle çift sürmüş olmasaydınız, bilmecemi çözemezdiniz›› diye karşılık verdi."));
trsuperobject.biblemap.put("b7_14_19", new trsuperobject(7,14,19,"RABbin Ruhu üzerine inince güçlenen Şimşon Aşkelona gitti; otuz kişi vurup mallarını yağmaladı, giysilerini de bilmeceyi çözenlere verdi. Öfkeden kudurmuş bir halde babasının evine döndü."));
trsuperobject.biblemap.put("b7_14_20", new trsuperobject(7,14,20,"Şimşon'un karısı ise Şimşon'a eşlik eden sağdıca verildi."));
trsuperobject.biblemap.put("b7_15_1", new trsuperobject(7,15,1,"Bir süre sonra, buğday biçimi sırasında Şimşon bir oğlak alıp karısını ziyarete gitti. ‹‹Karımın odasına girmek istiyorum›› dedi. Ama kızın babası Şimşonun girmesine izin vermedi."));
trsuperobject.biblemap.put("b7_15_2", new trsuperobject(7,15,2,"‹‹Ondan gerçekten nefret ettiğini sanıyordum›› dedi, ‹‹Bu nedenle onu senin sağdıcına verdim. Küçük kızkardeşi ondan daha güzel değil mi? Ablasının yerine onu al.››"));
trsuperobject.biblemap.put("b7_15_3", new trsuperobject(7,15,3,"Şimşon, ‹‹Bu kez Filistlilere kötülük etsem de buna hakkım var›› dedi."));
trsuperobject.biblemap.put("b7_15_4", new trsuperobject(7,15,4,"Kıra çıkıp üç yüz çakal yakaladı. Sonra çakalları çifter çifter kuyruk kuyruğa bağladı. Kuyruklarının arasına da birer çıra sıkıştırdı."));
trsuperobject.biblemap.put("b7_15_5", new trsuperobject(7,15,5,"Çıraları tutuşturup çakalları Filistlilerin ekinlerinin arasına salıverdi. Böylece demetleri, ekinleri, bağları, zeytinlikleri yaktı."));
trsuperobject.biblemap.put("b7_15_6", new trsuperobject(7,15,6,"Filistliler, ‹‹Bunu kim yaptı?›› dediler, ‹‹Yapsa yapsa, Timnalının damadı Şimşon yapmıştır. Çünkü Timnalı karısını elinden alıp sağdıcına verdi.›› Sonra gidip kadınla babasını yaktılar."));
trsuperobject.biblemap.put("b7_15_7", new trsuperobject(7,15,7,"Şimşon onlara, ‹‹Madem böyle yaptınız, sizden öcümü almadan duramam›› dedi."));
trsuperobject.biblemap.put("b7_15_8", new trsuperobject(7,15,8,"Onlara acımasızca saldırarak çoğunu öldürdü, sonra Etam Kayalığına çekilip bir mağaraya sığındı."));
trsuperobject.biblemap.put("b7_15_9", new trsuperobject(7,15,9,"Filistliler de gidip Yahudada ordugah kurdular, Lehi yöresine yayıldılar."));
trsuperobject.biblemap.put("b7_15_10", new trsuperobject(7,15,10,"Yahudalılar, ‹‹Neden bizimle savaşmaya geldiniz?›› diye sorunca, Filistliler, ‹‹Şimşonu yakalamaya geldik, bize yaptığının aynısını ona yapmak için buradayız›› diye karşılık verdiler."));
trsuperobject.biblemap.put("b7_15_11", new trsuperobject(7,15,11,"Yahudalılardan üç bin kişi, Etam Kayalığındaki mağaraya giderek Şimşona, ‹‹Filistlilerin bize egemen olduklarını bilmiyor musun? Nedir bu bize yaptığın?›› dediler. Şimşon, ‹‹Onlar bana ne yaptılarsa ben de onlara öyle yaptım›› diye karşılık verdi."));
trsuperobject.biblemap.put("b7_15_12", new trsuperobject(7,15,12,"‹‹Seni yakalayıp Filistlilere teslim etmek için geldik›› dediler. Şimşon, ‹‹Beni öldürmeyeceğinize ant için›› dedi."));
trsuperobject.biblemap.put("b7_15_13", new trsuperobject(7,15,13,"Onlar da, ‹‹Olur, ama seni sıkıca bağlayıp onlara teslim edeceğiz›› dediler, ‹‹Söz veriyoruz, seni öldürmeyeceğiz.›› Sonra onu iki yeni urganla bağlayıp mağaradan çıkardılar."));
trsuperobject.biblemap.put("b7_15_14", new trsuperobject(7,15,14,"Şimşon Lehiye yaklaşınca, Filistliler bağırarak ona yöneldiler. RABbin Ruhu büyük bir güçle Şimşonun üzerine indi. Şimşonun kollarını saran urganlar yanan keten gibi dağıldı, elindeki bağlar çözüldü."));
trsuperobject.biblemap.put("b7_15_15", new trsuperobject(7,15,15,"Şimşon yeni ölmüş bir eşeğin çene kemiğini eline alıp bununla bin kişiyi öldürdü."));
trsuperobject.biblemap.put("b7_15_16", new trsuperobject(7,15,16,"Sonra şöyle dedi:  ‹‹Bir eşeğin çene kemiğiyle,İki eşek yığını yaptım,Eşeğin çene kemiğiyle bin kişiyi öldürdüm.››"));
trsuperobject.biblemap.put("b7_15_17", new trsuperobject(7,15,17,"Bunları söyledikten sonra çene kemiğini elinden attı. Oraya Ramat-Lehi adı verildi."));
trsuperobject.biblemap.put("b7_15_18", new trsuperobject(7,15,18,"Şimşon ölesiye susamıştı. RABbe şöyle yakardı: ‹‹Kulunun eliyle büyük bir kurtuluş sağladın. Ama şimdi susuzluktan ölüp sünnetsizlerin eline mi düşeceğim?››"));
trsuperobject.biblemap.put("b7_15_19", new trsuperobject(7,15,19,"Bunun üzerine Tanrı Lehideki çukuru yardı. Çukurdan su fışkırdı. Şimşon suyu içince canlanıp güçlendi. Suyun çıktığı yere Eyn-Hakkore adını verdi. Pınar bugün de Lehide duruyor."));
trsuperobject.biblemap.put("b7_15_20", new trsuperobject(7,15,20,"Şimşon Filistliler'in egemenliği sırasında İsrailliler'e yirmi yıl önderlik yaptı."));
trsuperobject.biblemap.put("b7_16_1", new trsuperobject(7,16,1,"Şimşon bir gün Gazzeye gitti. Orada gördüğü bir fahişenin evine girdi."));
trsuperobject.biblemap.put("b7_16_2", new trsuperobject(7,16,2,"Gazzelilere, ‹‹Şimşon buraya geldi›› diye haber verilince çevreyi kuşattılar. Bütün gece kentin kapısında pusuya yattılar. ‹‹Gün ağarınca onu öldürürüz›› diyerek gece boyunca yerlerinden kımıldamadılar."));
trsuperobject.biblemap.put("b7_16_3", new trsuperobject(7,16,3,"Şimşon gece yarısına dek yattı. Gece yarısı kalktı, kent kapısının iki kanadıyla iki direğini tutup sürgüyle birlikte yerlerinden söktü. Hepsini omuzlayıp Hevronun karşısındaki tepeye çıkardı."));
trsuperobject.biblemap.put("b7_16_4", new trsuperobject(7,16,4,"Bir süre sonra Şimşon Sorek Vadisinde yaşayan Delila adında bir kadına aşık oldu."));
trsuperobject.biblemap.put("b7_16_5", new trsuperobject(7,16,5,"Filist beyleri kadına gelip, ‹‹Şimşonun üstün gücünün kaynağı nedir, onu kandırıp öğrenmeye bak›› dediler, ‹‹Böylece belki onu bağlar, etkisiz hale getirip yenebiliriz. Her birimiz sana bin yüzer parça gümüş vereceğiz.››"));
trsuperobject.biblemap.put("b7_16_6", new trsuperobject(7,16,6,"Bunun üzerine Delila Şimşona, ‹‹Lütfen, söyle bana, bu üstün gücü nereden alıyorsun?›› diye sordu, ‹‹Seni bağlayıp yenmek olası mı?››"));
trsuperobject.biblemap.put("b7_16_7", new trsuperobject(7,16,7,"Şimşon, ‹‹Beni kurumamış yedi taze sırımla bağlarlarsa sıradan bir adam gibi güçsüz olurum›› dedi."));
trsuperobject.biblemap.put("b7_16_8", new trsuperobject(7,16,8,"Bunun üzerine Filist beyleri Delilaya kurumamış yedi taze sırım getirdiler. Delila bunlarla Şimşonu bağladı."));
trsuperobject.biblemap.put("b7_16_9", new trsuperobject(7,16,9,"Adamları bitişik odada pusuya yatmıştı. Delila, ‹‹Şimşon, Filistliler geldi!›› dedi. Şimşon sırımları ateş değdiğinde dağılıveren kendir lifleri gibi koparıp attı. Gücünün sırrını vermemişti."));
trsuperobject.biblemap.put("b7_16_10", new trsuperobject(7,16,10,"Delila, ‹‹Beni kandırdın, bana yalan söyledin›› dedi, ‹‹Lütfen söyle bana, seni neyle bağlamalı?››"));
trsuperobject.biblemap.put("b7_16_11", new trsuperobject(7,16,11,"Şimşon, ‹‹Beni hiç kullanılmamış yeni urganla sımsıkı bağlarlarsa sıradan bir adam gibi güçsüz olurum›› dedi."));
trsuperobject.biblemap.put("b7_16_12", new trsuperobject(7,16,12,"Böylece Delila yeni urgan alıp Şimşonu bağladı. Sonra, ‹‹Şimşon, Filistliler geldi!›› dedi. Adamlar hâlâ bitişik odada pusu kurmuş bekliyorlardı. Şimşon urganları iplik koparır gibi koparıp kollarından sıyırdı."));
trsuperobject.biblemap.put("b7_16_13", new trsuperobject(7,16,13,"Delila ona, ‹‹Şimdiye kadar beni hep kandırdın, bana yalan söyledin›› dedi, ‹‹Söyle bana, seni neyle bağlamalı?›› Şimşon, ‹‹Başımdaki yedi örgüyü dokuma tezgahındaki kumaşla birlikte dokuyup kazıkla burarsan sıradan bir adam gibi güçsüz olurum›› dedi."));
trsuperobject.biblemap.put("b7_16_14", new trsuperobject(7,16,14,"Şimşon uyurken Delila onun başındaki yedi örgüyü dokuma tezgahındaki kumaşla birlikte dokuyup kazıkla burdu. Sonra, ‹‹Şimşon, Filistliler geldi!›› dedi. Şimşon uykusundan uyandı, saçını tezgah kazığından ve kumaştan çekip kurtardı. tezgahındaki kumaşla birlikte dokuyup kazıkla burarsan sıradan bir adam gibi güçsüz olurum› dedi. Şimşon uyurken Delila onun başındaki yedi örgüyü dokuma tezgahındaki kumaşla birlikte dokuyup kazıkla burdu››, Masoretik metin ‹‹Şimşon kadına, ‹Başımdaki yedi örgüyü dokuma tezgahındaki kumaşla birlikte dokursan› dedi. Kadın dokuyup kazıkla burdu››."));
trsuperobject.biblemap.put("b7_16_15", new trsuperobject(7,16,15,"Delila, ‹‹Bana güvenmiyorsan nasıl olur da, ‹Seni seviyorum› diyorsun?›› dedi, ‹‹Üç kezdir beni kandırıyorsun, üstün gücünün nereden geldiğini söylemiyorsun.››"));
trsuperobject.biblemap.put("b7_16_16", new trsuperobject(7,16,16,"Bu sözlerle Şimşonu sıkıştırıp günlerce başını ağrıttı. Sonunda Şimşon dayanamayıp"));
trsuperobject.biblemap.put("b7_16_17", new trsuperobject(7,16,17,"yüreğini kadına tümüyle açtı. ‹‹Başıma hiç ustura değmedi›› dedi, ‹‹Çünkü ben ana rahmindeyken Tanrıya adanmışım. Tıraş olursam gücümü yitiririm. Sıradan bir adam gibi güçsüz olurum.››"));
trsuperobject.biblemap.put("b7_16_18", new trsuperobject(7,16,18,"Delila Şimşonun gerçeği söylediğini anlayınca haber gönderip Filist beylerini çağırttı. ‹‹Bir kez daha gelin›› dedi, ‹‹Şimşon bana gerçeği söyledi.›› Kadının yanına gelen Filist beyleri gümüşü de birlikte getirdiler."));
trsuperobject.biblemap.put("b7_16_19", new trsuperobject(7,16,19,"Delila Şimşonu dizleri üzerinde uyuttuktan sonra adamlardan birini çağırtıp başındaki yedi örgüyü kestirdi. Sonra alay ederek onu dürtüklemeye başladı. Çünkü Şimşon gücünü yitirmişti."));
trsuperobject.biblemap.put("b7_16_20", new trsuperobject(7,16,20,"Delila, ‹‹Şimşon, Filistliler geldi!›› dedi. Şimşon uyandı ve, ‹‹Her zamanki gibi kalkıp silkinirim›› diye düşündü. RABbin kendisinden ayrıldığını bilmiyordu."));
trsuperobject.biblemap.put("b7_16_21", new trsuperobject(7,16,21,"Filistliler onu yakalayıp gözlerini oydular. Gazzeye götürüp tunç zincirlerle bağladılar, cezaevinde değirmen taşına koştular."));
trsuperobject.biblemap.put("b7_16_22", new trsuperobject(7,16,22,"Bu arada Şimşonun kesilen saçları uzamaya başladı."));
trsuperobject.biblemap.put("b7_16_23", new trsuperobject(7,16,23,"Filist beyleri ilahları Dagonun onuruna çok sayıda kurban kesip eğlenmek için toplandılar. ‹‹İlahımız, düşmanımız Şimşonu elimize teslim etti›› dediler."));
trsuperobject.biblemap.put("b7_16_24", new trsuperobject(7,16,24,"Halk Şimşonu görünce kendi ilahlarını övmeye başladı.  ‹‹İlahımız ülkemizi yakıp yıkan,Birçoğumuzu öldürenDüşmanımızı elimize teslim etti››"));
trsuperobject.biblemap.put("b7_16_25", new trsuperobject(7,16,25,"İyice coşunca, ‹‹Şimşonu getirin, bizi eğlendirsin›› dediler. Şimşonu cezaevinden getirip oynatmaya başladılar, sonra sütunların arasında durdurdular."));
trsuperobject.biblemap.put("b7_16_26", new trsuperobject(7,16,26,"Şimşon, elinden tutan gence, ‹‹Beni tapınağın damını taşıyan sütunların yanına götür de onlara yaslanayım›› dedi."));
trsuperobject.biblemap.put("b7_16_27", new trsuperobject(7,16,27,"Tapınak erkeklerle, kadınlarla doluydu. Bütün Filist beyleri de oradaydı. Üç bin kadar kadın erkek Şimşonun oynayışını damdan seyrediyordu."));
trsuperobject.biblemap.put("b7_16_28", new trsuperobject(7,16,28,"Şimşon RABbe yakarmaya başladı: ‹‹Ey Egemen RAB, lütfen beni anımsa. Ey Tanrı, bir kez daha beni güçlendir; Filistlilerden bir vuruşta iki gözümün öcünü alayım.››"));
trsuperobject.biblemap.put("b7_16_29", new trsuperobject(7,16,29,"Sonra tapınağın damını taşıyan iki ana sütunun ortasında durup sağ eliyle birini, sol eliyle ötekini kavradı."));
trsuperobject.biblemap.put("b7_16_30", new trsuperobject(7,16,30,"‹‹Filistlilerle birlikte öleyim›› diyerek bütün gücüyle sütunlara yüklendi. Tapınak Filist beylerinin ve bütün içindekilerin üzerine çöktü. Böylece Şimşon ölürken, yaşamı boyunca öldürdüğünden daha çok insan öldürdü."));
trsuperobject.biblemap.put("b7_16_31", new trsuperobject(7,16,31,"Şimşon'un kardeşleriyle babası Manoah'ın bütün ailesi onun ölüsünü almaya geldiler. Şimşon'u götürüp babasının Sora ile Eştaol arasındaki mezarına gömdüler. Şimşon İsrail'i yirmi yıl süreyle yönetmişti."));
trsuperobject.biblemap.put("b7_17_1", new trsuperobject(7,17,1,"Efrayimin dağlık bölgesinde Mika adında bir adam vardı."));
trsuperobject.biblemap.put("b7_17_2", new trsuperobject(7,17,2,"Mika annesine, ‹‹Senden çalınan, lanetlediğini duyduğum bin yüz parça gümüş var ya, işte o gümüşler bende, onları ben çaldım›› dedi. Annesi, ‹‹RAB seni kutsasın, oğlum!›› dedi."));
trsuperobject.biblemap.put("b7_17_3", new trsuperobject(7,17,3,"Mika bin yüz parça gümüşü annesine geri verdi. Annesi, ‹‹Oğlumun bir oyma put, bir de dökme put yaptırabilmesi için gümüşün tamamını RABbe adıyorum›› dedi, ‹‹Gümüşü sana geri veriyorum.››"));
trsuperobject.biblemap.put("b7_17_4", new trsuperobject(7,17,4,"Gümüşü Mikadan geri alan kadın, iki yüz parçasını ayırıp kuyumcuya verdi. Kuyumcu bundan bir oyma, bir de dökme put yaptı. Putlar Mikanın evine götürüldü."));
trsuperobject.biblemap.put("b7_17_5", new trsuperobject(7,17,5,"Mikanın bir tapınma yeri vardı. Özel aile putları ve bir efod yaptırmış, oğullarından birini de kâhinliğe atamıştı."));
trsuperobject.biblemap.put("b7_17_6", new trsuperobject(7,17,6,"O dönemde İsrailde kral yoktu. Herkes dilediğini yapıyordu."));
trsuperobject.biblemap.put("b7_17_7", new trsuperobject(7,17,7,"Yahudanın Beytlehem Kentinde, Yahudalı bir ailenin yanında geçici olarak yaşayan genç bir Levili vardı."));
trsuperobject.biblemap.put("b7_17_8", new trsuperobject(7,17,8,"Adam yerleşecek başka bir yer bulmak üzere Yahudanın Beytlehem Kentinden ayrıldı. Efrayimin dağlık bölgesinden geçerken Mikanın evine geldi."));
trsuperobject.biblemap.put("b7_17_9", new trsuperobject(7,17,9,"Mika, ‹‹Nereden geliyorsun?›› diye sorunca adam, ‹‹Yahudanın Beytlehem Kentinden geliyorum, Leviliyim, yerleşecek yer arıyorum›› dedi."));
trsuperobject.biblemap.put("b7_17_10", new trsuperobject(7,17,10,"Mika, ‹‹Benimle kal›› dedi, ‹‹Bana danışmanlık ve kâhinlik yap. Seni doyurur, yılda bir takım giysi, on parça da gümüş veririm.›› Levili kabul etti."));
trsuperobject.biblemap.put("b7_17_11", new trsuperobject(7,17,11,"Mika ile kalmaya razı oldu. Mika da ona oğlu gibi davrandı."));
trsuperobject.biblemap.put("b7_17_12", new trsuperobject(7,17,12,"Genç Leviliyi kâhinliğe atayarak evine aldı."));
trsuperobject.biblemap.put("b7_17_13", new trsuperobject(7,17,13,"Mika, ‹‹Şimdi biliyorum ki, RAB bana iyi davranacak›› dedi, ‹‹Çünkü bir Levili kâhinim var.››"));
trsuperobject.biblemap.put("b7_18_1", new trsuperobject(7,18,1,"O dönemde İsrailde kral yoktu ve Dan oymağından olanlar yerleşecek yer arıyorlardı. Çünkü İsrail oymakları arasında kendilerine düşen payı henüz almamışlardı."));
trsuperobject.biblemap.put("b7_18_2", new trsuperobject(7,18,2,"Böylece kendi boylarından, Sora ve Eştaol kentlerinden beş cesur savaşçıyı toprakları araştırıp bilgi toplamak üzere yola çıkardılar. Onlara, ‹‹Gidin, toprakları araştırın›› dediler. Adamlar Efrayimin dağlık bölgesinde bulunan Mikanın evine gelip geceyi orada geçirdiler."));
trsuperobject.biblemap.put("b7_18_3", new trsuperobject(7,18,3,"Mikanın evinin yanındayken genç Levilinin sesini tanıdılar. Eve yaklaşarak ona, ‹‹Seni buraya kim getirdi? Burada ne yapıyorsun? Burada ne işin var?›› diye sordular."));
trsuperobject.biblemap.put("b7_18_4", new trsuperobject(7,18,4,"Levili Mikanın kendisi için yaptıklarını anlattı. ‹‹Bana verdiği ücrete karşılık ona kâhinlik ediyorum›› dedi."));
trsuperobject.biblemap.put("b7_18_5", new trsuperobject(7,18,5,"Adamlar, ‹‹Lütfen Tanrıya danış, bu yolculuğumuz başarılı olacak mı, bilelim›› dediler."));
trsuperobject.biblemap.put("b7_18_6", new trsuperobject(7,18,6,"Kâhin, ‹‹Esenlikle gidin, Tanrı yolculuğunuzu onaylıyor›› diye yanıtladı."));
trsuperobject.biblemap.put("b7_18_7", new trsuperobject(7,18,7,"Böylece beş adam yola çıkıp Layişe vardılar. Kent halkının Saydalılar gibi kaygıdan uzak, esenlik ve güvenlik içinde yaşadığını gördüler. Yörede onlara egemen olan, baskı yapan kimse yoktu. Saydalılardan uzaktaydılar, başka kimseyle de ilişkileri yoktu."));
trsuperobject.biblemap.put("b7_18_8", new trsuperobject(7,18,8,"Sonra adamlar Sora ve Eştaola, soydaşlarının yanına döndüler. Soydaşları, ‹‹Ne öğrendiniz?›› diye sordular."));
trsuperobject.biblemap.put("b7_18_9", new trsuperobject(7,18,9,"Adamlar, ‹‹Haydi, onlara saldıralım›› dediler, ‹‹Ülkeyi gördük, toprağı çok güzel. Ne duruyorsunuz? Gecikmeden gidip ülkeyi sahiplenin."));
trsuperobject.biblemap.put("b7_18_10", new trsuperobject(7,18,10,"Oraya vardığınızda halkın her şeyden habersiz olduğunu göreceksiniz. Tanrının elinize teslim ettiği bu ülke çok geniş; öyle bir yer ki, hiçbir eksiği yok.››"));
trsuperobject.biblemap.put("b7_18_11", new trsuperobject(7,18,11,"Bunun üzerine Dan oymağından altı yüz kişi silahlarını kuşanıp Sora ve Eştaoldan yola çıktı."));
trsuperobject.biblemap.put("b7_18_12", new trsuperobject(7,18,12,"Gidip Yahudanın Kiryat-Yearim Kenti yakınında ordugah kurdular. Bu nedenle Kiryat-Yearimin batısındaki bu yer bugün de Mahane-Dan diye anılıyor."));
trsuperobject.biblemap.put("b7_18_13", new trsuperobject(7,18,13,"Buradan Efrayimin dağlık bölgesine geçip Mikanın evine gittiler."));
trsuperobject.biblemap.put("b7_18_14", new trsuperobject(7,18,14,"Layiş yöresini araştırmaya gitmiş olan beş adam soydaşlarına, ‹‹Bu evlerden birinde bir efod, özel aile putları, bir oyma, bir de dökme put olduğunu biliyor musunuz?›› dediler, ‹‹Ne yapacağınıza siz karar verin.››"));
trsuperobject.biblemap.put("b7_18_15", new trsuperobject(7,18,15,"Bunun üzerine halk genç Levilinin kaldığı Mikanın evine yöneldi. Eve girip Leviliye hal hatır sordular."));
trsuperobject.biblemap.put("b7_18_16", new trsuperobject(7,18,16,"Silahlarını kuşanmış altı yüz Danlı dış kapının önüne yığılmıştı."));
trsuperobject.biblemap.put("b7_18_17", new trsuperobject(7,18,17,"Yöreyi araştırmış olan beş adam içeri girip efodu, özel putları, oyma ve dökme putları aldılar. Kâhinle silah kuşanmış altı yüz kişiyse dış kapının önünde duruyordu."));
trsuperobject.biblemap.put("b7_18_18", new trsuperobject(7,18,18,"Adamların Mikanın evine girip efodu, özel putları, oyma ve dökme putları aldığını gören kâhin, ‹‹Ne yapıyorsunuz?›› diye sordu."));
trsuperobject.biblemap.put("b7_18_19", new trsuperobject(7,18,19,"Adamlar, ‹‹Sus, sesini çıkarma›› dediler, ‹‹Bizimle gel. Bize danışmanlık ve kâhinlik yap. Bir adamın evinde kâhinlik etmek mi iyi, yoksa İsrailin bir boyuna, bir oymağına kâhinlik etmek mi?››"));
trsuperobject.biblemap.put("b7_18_20", new trsuperobject(7,18,20,"Kâhinin yüreği sevinçle doldu. Efodu, özel putları, oyma putu alıp topluluğun ortasında yürümeye başladı."));
trsuperobject.biblemap.put("b7_18_21", new trsuperobject(7,18,21,"Topluluk çocuklarını, hayvanlarını, değerli eşyalarını alıp yola çıktı."));
trsuperobject.biblemap.put("b7_18_22", new trsuperobject(7,18,22,"Danoğulları Mikanın evinden biraz uzaklaştıktan sonra, Mikanın komşuları toplanıp onlara yetiştiler."));
trsuperobject.biblemap.put("b7_18_23", new trsuperobject(7,18,23,"Bağırıp çağırmaya başladılar. Danoğulları dönüp Mikaya, ‹‹Ne oldu, neden adamlarını toplayıp geldin?›› dediler."));
trsuperobject.biblemap.put("b7_18_24", new trsuperobject(7,18,24,"Mika, ‹‹Kâhinimi, yaptırdığım putları alıp gittiniz›› dedi, ‹‹Bana ne kaldı ki? Bir de, ‹Ne oldu?› diye soruyorsunuz.››"));
trsuperobject.biblemap.put("b7_18_25", new trsuperobject(7,18,25,"‹‹Kes sesini!›› dediler, ‹‹Yoksa öfkeli adamlarımız saldırıp seni de, aileni de öldürür.››"));
trsuperobject.biblemap.put("b7_18_26", new trsuperobject(7,18,26,"Sonra yollarına devam ettiler. Mika onların kendisinden daha güçlü olduğunu görünce dönüp evine gitti."));
trsuperobject.biblemap.put("b7_18_27", new trsuperobject(7,18,27,"Danoğulları Mikanın yaptırdığı putları ve kâhini yanlarına alarak Layiş üzerine yürüdüler. Barışçıl ve her şeyden habersiz olan kent halkını kılıçtan geçirip kenti ateşe verdiler."));
trsuperobject.biblemap.put("b7_18_28", new trsuperobject(7,18,28,"Beytrehov yakınındaki vadide bulunan Layiş Kentinin yardımına gelen olmadı. Çünkü kent Saydadan uzaktı, başka bir kentle de ilişkisi yoktu. Danoğulları kenti yeniden inşa ederek oraya yerleştiler."));
trsuperobject.biblemap.put("b7_18_29", new trsuperobject(7,18,29,"Yakupun oğlu olan ataları Danın anısına kente Dan adını verdiler. Kentin eski adı Layişti."));
trsuperobject.biblemap.put("b7_18_30", new trsuperobject(7,18,30,"Oyma putu oraya diktiler. Musa oğlufö Gerşom oğlu Yonatan ile oğulları sürgüne kadar onlara kâhinlik ettiler."));
trsuperobject.biblemap.put("b7_18_31", new trsuperobject(7,18,31,"Tanrı'nın Tapınağı Şilo'da olduğu sürece Mika'nın yaptırdığı puta taptılar. İbrani din bilginlerine göre ‹‹Musa oğlu››, Masoretik metin ‹‹Manaşşe oğlu››."));
trsuperobject.biblemap.put("b7_19_1", new trsuperobject(7,19,1,"İsrailin kralsız olduğu o dönemde Efrayimin dağlık bölgesinin ücra yerinde yaşayan bir Levili vardı. Adam Yahudanın Beytlehem Kentinden kendisine bir cariye almıştı."));
trsuperobject.biblemap.put("b7_19_2-3", new trsuperobject(7,19,2-3,"Ama kadın onu başka erkeklerle aldattı. Sonra adamı bırakıp Yahudaya, babasının Beytlehemdeki evine döndü. Kadın dört ay orada kaldıktan sonra kocası kalkıp onun yanına gitti. Gönlünü hoş edip onu geri getirmek istiyordu. Yanında uşağı ve iki de eşek vardı. Kadın onu babasının evine götürdü. Kayınbaba damadını görünce onu sevinçle karşıladı."));
trsuperobject.biblemap.put("b7_19_4", new trsuperobject(7,19,4,"Yanında alıkoydu. Adam onların evinde üç gün kaldı, onlarla birlikte yedi, içti ve orada geceledi."));
trsuperobject.biblemap.put("b7_19_5", new trsuperobject(7,19,5,"Dördüncü günün sabahı erkenden kalktılar. Kızın babası gitmeye hazırlanan damadına, ‹‹Rahatına bak, bir lokma ekmek ye, sonra gidersiniz›› dedi."));
trsuperobject.biblemap.put("b7_19_6", new trsuperobject(7,19,6,"İkisi oturup birlikte yiyip içtiler. Kayınbaba, ‹‹Lütfen bu gece de kal, keyfine bak›› dedi."));
trsuperobject.biblemap.put("b7_19_7", new trsuperobject(7,19,7,"Damat gitmek üzere ayağa kalkınca kayınbabası ısrarla kalmasını istedi; damat da geceyi orada geçirdi."));
trsuperobject.biblemap.put("b7_19_8", new trsuperobject(7,19,8,"Beşinci gün gitmek üzere erkenden kalktı. Kayınbaba, ‹‹Rahatına bak, bir şeyler ye; öğleden sonra gidersiniz›› dedi. İkisi birlikte yemek yediler."));
trsuperobject.biblemap.put("b7_19_9", new trsuperobject(7,19,9,"Damat, cariyesi ve uşağıyla birlikte gitmek için ayağa kalkınca, kayınbaba, ‹‹Bak, akşam oluyor, lütfen geceyi burada geçirin›› dedi, ‹‹Gün batmak üzere. Geceyi burada geçirin, keyfinize bakın. Yarın erkenden kalkıp yola çıkar, evine gidersin.››"));
trsuperobject.biblemap.put("b7_19_10", new trsuperobject(7,19,10,"Ama adam orada gecelemek istemedi. Cariyesini alıp palan vurulmuş iki eşekle yola çıktı. Yevusun -Yeruşalimin- karşısında bir yere geldiler."));
trsuperobject.biblemap.put("b7_19_11", new trsuperobject(7,19,11,"Yevusa yaklaştıklarında gün batmak üzereydi. Uşak efendisine, ‹‹Yevusluların bu kentine girip geceyi orada geçirelim›› dedi."));
trsuperobject.biblemap.put("b7_19_12", new trsuperobject(7,19,12,"Efendisi, ‹‹İsraillilere ait olmayan yabancı bir kente girmeyeceğiz›› dedi, ‹‹Givaya gideceğiz.››"));
trsuperobject.biblemap.put("b7_19_13", new trsuperobject(7,19,13,"Sonra ekledi: ‹‹Haydi Givaya ya da Ramaya ulaşmaya çalışalım. Bunlardan birinde geceleriz.››"));
trsuperobject.biblemap.put("b7_19_14", new trsuperobject(7,19,14,"Böylece yollarına devam ettiler. Benyaminlilerin Giva Kentine yaklaştıklarında güneş batmıştı."));
trsuperobject.biblemap.put("b7_19_15", new trsuperobject(7,19,15,"Geceyi geçirmek için Givaya giden yola saptılar. Varıp kentin meydanında konakladılar. Çünkü hiç kimse onları evine almadı."));
trsuperobject.biblemap.put("b7_19_16", new trsuperobject(7,19,16,"Akşam saatlerinde yaşlı bir adam tarladaki işinden dönüyordu. Efrayimin dağlık bölgesindendi. Givada oturuyordu. Kent halkı ise Benyaminliydi."));
trsuperobject.biblemap.put("b7_19_17", new trsuperobject(7,19,17,"Yaşlı adam kent meydanındaki yolcuları görünce Leviliye, ‹‹Nereden geliyor, nereye gidiyorsunuz?›› diye sordu."));
trsuperobject.biblemap.put("b7_19_18", new trsuperobject(7,19,18,"Levili, ‹‹Yahudanın Beytlehem Kentinden geliyor, Efrayimin dağlık bölgesinde uzak bir yere gidiyoruz›› dedi, ‹‹Ben oralıyım. Beytleheme gitmiştim. Şimdi RABbin evine dönüyorum. Ama kimse bizi evine almadı."));
trsuperobject.biblemap.put("b7_19_19", new trsuperobject(7,19,19,"Eşeklerimiz için yem ve saman, kendim, cariyem ve uşağım için ekmek ve şarap var. Hepimiz sana hizmet etmeye hazırız. Hiçbir eksiğimiz yok.››"));
trsuperobject.biblemap.put("b7_19_20", new trsuperobject(7,19,20,"Yaşlı adam, ‹‹Gönlün rahat olsun›› dedi, ‹‹Her ihtiyacını ben karşılayacağım. Geceyi meydanda geçirmeyin.››"));
trsuperobject.biblemap.put("b7_19_21", new trsuperobject(7,19,21,"Onları evine götürdü, eşeklerine yem verdi. Konuklar ayaklarını yıkadıktan sonra yiyip içtiler."));
trsuperobject.biblemap.put("b7_19_22", new trsuperobject(7,19,22,"Onlar dinlenirken kentin serserileri evi kuşattı. Kapıya var güçleriyle vurarak yaşlı ev sahibine, ‹‹Evine gelen o adamı dışarı çıkar, onunla yatalım›› diye bağırdılar."));
trsuperobject.biblemap.put("b7_19_23", new trsuperobject(7,19,23,"Ev sahibi dışarıya çıkıp onların yanına gitti. ‹‹Hayır, kardeşlerim, rica ediyorum böyle bir kötülük yapmayın›› dedi, ‹‹Madem adam evime gelip konuğum oldu, böyle bir alçaklık yapmayın."));
trsuperobject.biblemap.put("b7_19_24", new trsuperobject(7,19,24,"Bakın, daha erkek eli değmemiş kızımla adamın cariyesi içerde. Onları dışarı çıkarayım, onlarla yatın, onlara dilediğinizi yapın. Ama adama bu kötülüğü yapmayın.››"));
trsuperobject.biblemap.put("b7_19_25", new trsuperobject(7,19,25,"Ne var ki, adamlar onu dinlemediler. Bunun üzerine Levili cariyesini zorla dışarı çıkarıp onlara teslim etti. Adamlar bütün gece, sabaha dek kadınla yattılar, onun ırzına geçtiler. Şafak sökerken onu salıverdiler."));
trsuperobject.biblemap.put("b7_19_26", new trsuperobject(7,19,26,"Kadın gün ağarırken efendisinin kaldığı evin kapısına geldi, düşüp yere yığıldı. Ortalık aydınlanıncaya dek öylece kaldı."));
trsuperobject.biblemap.put("b7_19_27", new trsuperobject(7,19,27,"Sabahleyin kalkan adam, yoluna devam etmek üzere kapıyı açtı. Elleri eşiğin üzerinde, yerde boylu boyunca yatan cariyesini görünce,"));
trsuperobject.biblemap.put("b7_19_28", new trsuperobject(7,19,28,"kadına, ‹‹Kalk, gidelim›› dedi. Kadın yanıt vermedi. Bunun üzerine adam onu eşeğe bindirip evine doğru yola çıktı."));
trsuperobject.biblemap.put("b7_19_29", new trsuperobject(7,19,29,"Eve varınca eline bir bıçak aldı, cariyesinin cesedini on iki parçaya bölüp İsrailin on iki oymağına dağıttı."));
trsuperobject.biblemap.put("b7_19_30", new trsuperobject(7,19,30,"Bunu her gören, ‹‹İsrailliler Mısır'dan çıktığından beri böyle bir şey olmamış, görülmemiştir›› dedi, ‹‹Düşünün taşının, ne yapmamız gerek, söyleyin.››"));
trsuperobject.biblemap.put("b7_20_1", new trsuperobject(7,20,1,"Gilat başta olmak üzere Dandan Beer-Şevaya kadar, bütün İsrail halkı yola çıkıp Mispada, RABbin önünde tek beden gibi toplandı."));
trsuperobject.biblemap.put("b7_20_2", new trsuperobject(7,20,2,"Tanrı halkı İsrailin bütün oymak önderleri bu toplantıda hazır bulundular. Eli kılıç tutan dört yüz bin yayaydılar."));
trsuperobject.biblemap.put("b7_20_3", new trsuperobject(7,20,3,"Bu arada Benyaminoğulları İsraillilerin Mispada toplandığını duydular.- İsrailliler, ‹‹Anlatın bize, bu korkunç olay nasıl oldu?›› diye sordular."));
trsuperobject.biblemap.put("b7_20_4", new trsuperobject(7,20,4,"Öldürülen kadının Levili kocası şöyle yanıtladı: ‹‹Cariyemle birlikte geceyi geçirmek üzere Benyamin bölgesinin Giva Kentine girdik."));
trsuperobject.biblemap.put("b7_20_5", new trsuperobject(7,20,5,"Givadan bazı adamlar gece beni öldürmeyi tasarlayarak gelip evi kuşattılar. Cariyemin ırzına geçtiler, ölümüne neden oldular."));
trsuperobject.biblemap.put("b7_20_6", new trsuperobject(7,20,6,"Onun ölüsünü alıp parçaladım, her bir parçasını İsrailin mülk aldığı bir bölgeye gönderdim. Çünkü bu alçakça rezalet İsrailde işlendi."));
trsuperobject.biblemap.put("b7_20_7", new trsuperobject(7,20,7,"Ey İsrailliler! İşte hepiniz buradasınız. Düşünceniz, kararınız nedir, söyleyin.››"));
trsuperobject.biblemap.put("b7_20_8", new trsuperobject(7,20,8,"Oradakilerin hepsi ağız birliği etmişçesine, ‹‹Bizden hiç kimse çadırına gitmeyecek, evine dönmeyecek›› dediler,"));
trsuperobject.biblemap.put("b7_20_9", new trsuperobject(7,20,9,"‹‹Yapacağımız şu: Givaya kura ile saldıracağız."));
trsuperobject.biblemap.put("b7_20_10", new trsuperobject(7,20,10,"Halka yiyecek sağlamak için bütün İsrail oymaklarından nüfuslarına göre, her yüz kişiden on, bin kişiden yüz, on bin kişiden bin kişi seçeceğiz. Bunlar Benyaminin Giva Kentine geldiklerinde kentlilerden İsrailde yaptıkları bu alçaklığın öcünü alsınlar.››"));
trsuperobject.biblemap.put("b7_20_11", new trsuperobject(7,20,11,"Givaya karşı toplanmış olan İsrailliler tam bir birlik içindeydi."));
trsuperobject.biblemap.put("b7_20_12", new trsuperobject(7,20,12,"İsrail oymakları, Benyamin oymağına adamlar göndererek, ‹‹Aranızda yapılan bu alçaklık nedir?›› diye sordular,"));
trsuperobject.biblemap.put("b7_20_13", new trsuperobject(7,20,13,"‹‹Givadaki o serserileri bize hemen teslim edin. Onları öldürüp İsraildeki kötülüğün kökünü kazıyalım.›› Ama Benyaminoğulları İsrailli kardeşlerini dinlemediler."));
trsuperobject.biblemap.put("b7_20_14", new trsuperobject(7,20,14,"İsraillilerle savaşmak üzere öbür kentlerden akın akın Givaya geldiler."));
trsuperobject.biblemap.put("b7_20_15", new trsuperobject(7,20,15,"Giva halkından olan yedi yüz seçme adam dışında, öbür kentlerden gelen ve eli kılıç tutan Benyaminoğullarının sayısı o gün yirmi altı bini buldu."));
trsuperobject.biblemap.put("b7_20_16", new trsuperobject(7,20,16,"Solak olan yedi yüz seçme adam da bunların arasındaydı. Hepsi de bir kılı sapanla vuracak kadar iyi nişancıydı."));
trsuperobject.biblemap.put("b7_20_17", new trsuperobject(7,20,17,"Benyaminoğullarının yanısıra İsrailliler de sayıldı. Eli kılıç tutan dört yüz bin askerleri vardı. Hepsi de yaman savaşçılardı."));
trsuperobject.biblemap.put("b7_20_18", new trsuperobject(7,20,18,"Beytele çıkan İsrailliler Tanrıya, ‹‹Benyaminoğullarına karşı önce hangimiz savaşacak?›› diye sordular. RAB, ‹‹Önce Yahudaoğulları savaşacak›› dedi."));
trsuperobject.biblemap.put("b7_20_19", new trsuperobject(7,20,19,"İsrailliler sabah kalkıp Givanın karşısında ordugah kurdular."));
trsuperobject.biblemap.put("b7_20_20", new trsuperobject(7,20,20,"Benyaminoğullarıyla savaşmak üzere ilerleyip Givada savaş düzenine girdiler."));
trsuperobject.biblemap.put("b7_20_21", new trsuperobject(7,20,21,"Givadan çıkan Benyaminoğulları, o gün İsraillilerden yirmi iki bin kişiyi yere serdiler."));
trsuperobject.biblemap.put("b7_20_22", new trsuperobject(7,20,22,"Ama İsrailliler birbirlerini yüreklendirerek önceki gün savaş düzenine girdikleri yerde mevzilendiler."));
trsuperobject.biblemap.put("b7_20_23", new trsuperobject(7,20,23,"Sonra Beytelde RABbin önünde akşama dek ağladılar. RABbe, ‹‹Kardeşlerimiz olan Benyaminoğullarıyla yine savaşmaya çıkalım mı?›› diye sordular. RAB, ‹‹Evet, onlarla savaşın›› dedi."));
trsuperobject.biblemap.put("b7_20_24", new trsuperobject(7,20,24,"Bunun üzerine İsrailliler ikinci gün yine Benyaminoğullarına yaklaştılar."));
trsuperobject.biblemap.put("b7_20_25", new trsuperobject(7,20,25,"Benyaminoğulları da aynı gün Givadan onların üzerine yürüyerek on sekiz bin kişiyi daha yere serdiler. Ölenlerin hepsi eli kılıç tutan savaşçılardı."));
trsuperobject.biblemap.put("b7_20_26", new trsuperobject(7,20,26,"Bütün İsrailliler, bütün halk çekilip Beytele döndü. Orada, RABbin önünde durup ağladılar, o gün akşama dek oruç tuttular. RABbe yakmalık sunular ve esenlik sunuları sundular."));
trsuperobject.biblemap.put("b7_20_27-28", new trsuperobject(7,20,27-28,"Tanrının Antlaşma Sandığı o sırada Beyteldeydi. Harun oğlu Elazar oğlu Pinehas o sırada sandığın önünde görev yapıyordu. İsrailliler RABbe, ‹‹Kardeşimiz Benyaminoğullarıyla savaşmaya devam edelim mi, yoksa vaz mı geçelim?›› diye sordular. RAB, ‹‹Savaşın›› dedi, ‹‹Çünkü onları yarın elinize teslim edeceğim.››"));
trsuperobject.biblemap.put("b7_20_29", new trsuperobject(7,20,29,"İsrailliler dört bir yandan Givanın çevresinde pusuya yattılar."));
trsuperobject.biblemap.put("b7_20_30", new trsuperobject(7,20,30,"Üçüncü gün Benyaminoğullarına karşı harekete geçerek önceki gibi kentin karşısında savaş düzenine girdiler."));
trsuperobject.biblemap.put("b7_20_31", new trsuperobject(7,20,31,"Saldırıya geçen Benyaminoğulları kentten epey uzaklaştılar. Beytele ve Givaya giden ana yollarda, kırlarda önceki çarpışmalarda olduğu gibi İsraillilere kayıplar verdirmeye başladılar; otuz kadarını öldürdüler."));
trsuperobject.biblemap.put("b7_20_32-33", new trsuperobject(7,20,32-33,"‹‹Geçen seferki gibi onları yine bozguna uğratıyoruz›› dediler. İsrailliler ise birbirlerine, ‹‹Kaçalım da onları kentten uzağa, ana yollara çekelim›› diyerek bulundukları yerden çıkıp Baal-Tamarda savaş düzenine girdiler. Givanın batısında pusuya yatanlar da birden yerlerinden fırladı."));
trsuperobject.biblemap.put("b7_20_34", new trsuperobject(7,20,34,"Böylece bütün İsrailden seçme on bin kişi Givaya cepheden saldırdı. Savaş iyice kızışmıştı. Benyaminoğulları başlarına gelecek felaketten habersizdi."));
trsuperobject.biblemap.put("b7_20_35", new trsuperobject(7,20,35,"RAB onları İsrailin önünde bozguna uğrattı. İsrailliler o gün Benyaminoğullarından eli kılıç tutan yirmi beş bin yüz kişiyi öldürdüler. Masoretik metin ‹‹Gevanın açıklığında››."));
trsuperobject.biblemap.put("b7_20_36", new trsuperobject(7,20,36,"Benyaminoğulları yenildiklerini anladılar. İsrailliler onların geçmesine izin verdiler; çünkü Giva çevresinde pusuda yatanlara güveniyorlardı."));
trsuperobject.biblemap.put("b7_20_37", new trsuperobject(7,20,37,"Pusudakiler ansızın Givaya saldırdılar. Bütün kente dağılarak halkı kılıçtan geçirdiler."));
trsuperobject.biblemap.put("b7_20_38", new trsuperobject(7,20,38,"Pusuya yatanlarla öbür İsrailliler arasında bir işaret kararlaştırılmıştı: Kenti ateşe verip büyük bir duman bulutu oluşturacaklardı."));
trsuperobject.biblemap.put("b7_20_39", new trsuperobject(7,20,39,"O zaman savaş alanındaki İsrailliler birden geri dönecekti. Bu arada Benyaminoğulları İsraillilere kayıplar verdirmeye başlamış, otuz kadarını vurmuşlardı. Daha önceki savaşta olduğu gibi, İsraillileri kesin bir bozguna uğrattıklarını sandılar."));
trsuperobject.biblemap.put("b7_20_40", new trsuperobject(7,20,40,"Ama dönüp kente baktıklarında orada hortum gibi göğe yükselen duman bulutunu gördüler. Yanan kentin dumanı göğü kaplamıştı."));
trsuperobject.biblemap.put("b7_20_41", new trsuperobject(7,20,41,"İsraillilerin döndüğünü gören Benyaminoğulları paniğe kapıldı. Çünkü başlarına gelecek felaketi sezmişlerdi."));
trsuperobject.biblemap.put("b7_20_42", new trsuperobject(7,20,42,"İsraillilerin önüsıra kırlara doğru yöneldilerse de savaştan kaçamadılar. Çeşitli kentlerden çıkagelen İsrailliler onları kuşatıp yok etti."));
trsuperobject.biblemap.put("b7_20_43", new trsuperobject(7,20,43,"Geri kalan Benyaminoğullarını kovaladılar. Givanın doğusunda konakladıkları yere dek onları yol boyunca vurup yere serdiler."));
trsuperobject.biblemap.put("b7_20_44", new trsuperobject(7,20,44,"Benyaminoğullarından on sekiz bin kişi vuruldu. Hepsi de yiğit savaşçılardı."));
trsuperobject.biblemap.put("b7_20_45", new trsuperobject(7,20,45,"Sağ kalanlar dönüp kırlara, Rimmon Kayalığına doğru kaçmaya başladı. İsrailliler yol boyunca bunlardan beş bin kişi daha öldürdü. Gidoma kadar onları adım adım izleyerek iki binini daha vurup yere serdiler."));
trsuperobject.biblemap.put("b7_20_46", new trsuperobject(7,20,46,"O gün Benyaminoğullarından öldürülenlerin toplam sayısı yirmi beş bin kişiyi buldu. Hepsi de eli kılıç tutan yiğit savaşçılardı."));
trsuperobject.biblemap.put("b7_20_47", new trsuperobject(7,20,47,"Kırlara kaçıp Rimmon Kayalığına sığınanların sayısı altı yüzdü. Kayalıkta dört ay kaldılar."));
trsuperobject.biblemap.put("b7_20_48", new trsuperobject(7,20,48,"İsrailliler Benyamin kentlerine döndüler; insanları, hayvanları ve oradaki bütün canlıları kılıçtan geçirdiler, rastladıkları bütün kentleri ateşe verdiler."));
trsuperobject.biblemap.put("b7_21_1", new trsuperobject(7,21,1,"İsrailliler Mispada, ‹‹Bizden hiç kimse Benyaminoğullarına kız vermeyecek›› diye ant içmişlerdi."));
trsuperobject.biblemap.put("b7_21_2", new trsuperobject(7,21,2,"Halk Beytele geldi. Akşama dek orada, Tanrının önünde oturup hıçkıra hıçkıra ağladılar."));
trsuperobject.biblemap.put("b7_21_3", new trsuperobject(7,21,3,"‹‹Ey İsrailin Tanrısı RAB!›› dediler, ‹‹Bugün İsrailden bir oymağın eksilmesine yol açan böyle bir şey neden oldu?››"));
trsuperobject.biblemap.put("b7_21_4", new trsuperobject(7,21,4,"Ertesi gün erkenden kalkıp bir sunak yaptılar, orada yakmalık sunular ve esenlik sunuları sundular."));
trsuperobject.biblemap.put("b7_21_5", new trsuperobject(7,21,5,"İsrailliler, ‹‹RABbin önüne çıkmak üzere toplandığımızda İsrail oymaklarından bize kimler katılmadı?›› diye sordular. Çünkü Mispada, RABbin önünde toplandıklarında kendilerine katılmayanların kesinlikle öldürüleceğine dair ant içmişlerdi."));
trsuperobject.biblemap.put("b7_21_6", new trsuperobject(7,21,6,"İsrailliler Benyaminli kardeşleri için çok üzülüyorlardı. ‹‹İsrail bugün bir oymağını yitirdi›› dediler,"));
trsuperobject.biblemap.put("b7_21_7", new trsuperobject(7,21,7,"‹‹Sağ kalanlara eş olacak kızları bulmak için ne yapsak? Çünkü kızlarımızdan hiçbirini onlara eş olarak vermeyeceğimize RABbin adına ant içtik.››"));
trsuperobject.biblemap.put("b7_21_8", new trsuperobject(7,21,8,"Sonra, ‹‹Mispaya, RABbin önüne İsrail oymaklarından kim çıkmadı?›› diye sordular. Böylece Yaveş-Gilattan toplantıya, ordugaha kimsenin gelmediği ortaya çıktı."));
trsuperobject.biblemap.put("b7_21_9", new trsuperobject(7,21,9,"Çünkü gelenler sayıldığında Yaveş-Gilattan kimsenin olmadığı anlaşılmıştı."));
trsuperobject.biblemap.put("b7_21_10", new trsuperobject(7,21,10,"Bunun üzerine topluluk Yaveş-Gilat halkının üzerine on iki bin yiğit savaşçı gönderdi. ‹‹Gidin, Yaveş-Gilat halkını, kadın, çoluk çocuk demeden kılıçtan geçirin›› dediler,"));
trsuperobject.biblemap.put("b7_21_11", new trsuperobject(7,21,11,"‹‹Yapacağınız şu: Her erkeği ve erkek eli değmiş her kadını öldüreceksiniz.››"));
trsuperobject.biblemap.put("b7_21_12", new trsuperobject(7,21,12,"Yaveş-Gilat halkı arasında erkek eli değmemiş dört yüz kız bulup Kenan topraklarında bulunan Şilodaki ordugaha getirdiler."));
trsuperobject.biblemap.put("b7_21_13", new trsuperobject(7,21,13,"Ardından bütün topluluk Rimmon Kayalığındaki Benyaminoğullarına aracılar göndererek barış yapmayı önerdi."));
trsuperobject.biblemap.put("b7_21_14", new trsuperobject(7,21,14,"Bunun üzerine Benyaminoğulları döndü. Topluluk Yaveş-Gilat halkından sağ bırakılan kızları onlara eş olarak verdi. Ama kızların sayısı Benyaminoğulları için yine de yeterli değildi."));
trsuperobject.biblemap.put("b7_21_15", new trsuperobject(7,21,15,"İsrail halkı Benyaminoğullarının durumuna çok üzülüyordu. Çünkü RAB İsrail oymakları arasında birliği bozmuştu."));
trsuperobject.biblemap.put("b7_21_16", new trsuperobject(7,21,16,"Topluluğun ileri gelenleri, ‹‹Benyaminoğullarının kadınları öldürüldüğüne göre, kalan erkeklere eş bulmak için ne yapsak?›› diyorlardı,"));
trsuperobject.biblemap.put("b7_21_17", new trsuperobject(7,21,17,"‹‹İsrailden bir oymağın yok olup gitmemesi için sağ kalan Benyaminoğullarının mirasçıları olmalı."));
trsuperobject.biblemap.put("b7_21_18", new trsuperobject(7,21,18,"Biz onlara kızlarımızdan eş veremeyiz. Çünkü Benyaminoğullarına kız veren her İsrailli lanetlenecek diye ant içtik.››"));
trsuperobject.biblemap.put("b7_21_19", new trsuperobject(7,21,19,"Sonra, ‹‹Bakın, Şiloda her yıl RAB adına bir şölen düzenleniyor›› diye eklediler. Şilo Beytelin kuzeyinde, Beytelden Şekeme giden yolun doğusunda, Levonanın güneyindedir."));
trsuperobject.biblemap.put("b7_21_20", new trsuperobject(7,21,20,"Böylece Benyaminoğullarına, ‹‹Gidip bağlarda gizlenin›› diye öğüt verdiler,"));
trsuperobject.biblemap.put("b7_21_21", new trsuperobject(7,21,21,"‹‹Gözünüzü açık tutun. Şilolu kızlar dans etmeye kalkınca bağlardan fırlayıp onlardan kendinize birer eş kapın ve Benyamin topraklarına götürün."));
trsuperobject.biblemap.put("b7_21_22", new trsuperobject(7,21,22,"Kızların babaları ya da erkek kardeşleri bize yakınmaya gelirse, ‹Benyaminoğullarını hatırımız için bağışlayın› diyeceğiz, ‹Savaşarak aldığımız kızlar hepsine yetmedi. Siz de kendi kızlarınızı isteyerek vermediğinize göre suçlu sayılmazsınız.››"));
trsuperobject.biblemap.put("b7_21_23", new trsuperobject(7,21,23,"Benyaminoğulları da böyle yaptılar. Kızlar dans ederken her erkek bir kız kapıp götürdü. Kendi topraklarına gittiler, kentlerini onarıp yerleştiler."));
trsuperobject.biblemap.put("b7_21_24", new trsuperobject(7,21,24,"Ardından İsrailliler de oradan ayrılıp kendi topraklarına, oymaklarına, ailelerine döndüler."));
trsuperobject.biblemap.put("b7_21_25", new trsuperobject(7,21,25,"O dönemde İsrail'de kral yoktu. Herkes dilediğini yapıyordu."));

