trsuperobject.biblemap.put("b9_4_3", new trsuperobject(9,4,3,"Askerler ordugaha dönünce, İsrailin ileri gelenleri, ‹‹Neden bugün RAB bizi Filistlilerin önünde bozguna uğrattı?›› diye sordular, ‹‹RABbin Antlaşma Sandığını Şilodan buraya getirelim ki, aramıza geldiğinde bizi düşmanlarımızın elinden kurtarsın.››"));
trsuperobject.biblemap.put("b9_4_4", new trsuperobject(9,4,4,"Halk Şiloya adamlar gönderdi. Keruvlar arasında taht kurmuş, Her Şeye Egemen RABbin Antlaşma Sandığını oradan getirdiler. Elinin iki oğlu, Hofni ile Pinehas da Tanrının Antlaşma Sandığının yanındaydılar."));
trsuperobject.biblemap.put("b9_4_6", new trsuperobject(9,4,6,"Filistliler bağrışmaları duyunca, ‹‹İbranilerin ordugahındaki bu yüksek bağrışmaların anlamı ne?›› diye sordular. RABbin Sandığının ordugaha getirildiğini öğrenince,"));
trsuperobject.biblemap.put("b9_4_7", new trsuperobject(9,4,7,"korkarak, ‹‹Tanrılar ordugaha gelmiş›› dediler, ‹‹Vay başımıza! Daha önce buna benzer bir olay olmamıştı."));
trsuperobject.biblemap.put("b9_4_8", new trsuperobject(9,4,8,"Vay başımıza! Bu güçlü tanrıların elinden bizi kim kurtarabilir? Çölde Mısırlıları her tür belaya çarptıran tanrılar bunlar."));
trsuperobject.biblemap.put("b9_4_9", new trsuperobject(9,4,9,"Güçlü olun, ey Filistliler! Yiğitçe davranın! Yoksa, İbraniler size nasıl boyun eğdiyse, siz de onlara öyle boyun eğeceksiniz. Bu yüzden yiğitçe davranın ve savaşın!››"));
trsuperobject.biblemap.put("b9_4_10", new trsuperobject(9,4,10,"Böylece Filistliler savaşıp İsraillileri bozguna uğrattılar. İsraillilerin hepsi evlerine kaçtı. Yenilgi öyle büyüktü ki, İsrailliler otuz bin yaya asker yitirdi,"));
trsuperobject.biblemap.put("b9_4_11", new trsuperobject(9,4,11,"Tanrının Sandığı alındı, Elinin iki oğlu, Hofni ile Pinehas öldü."));
trsuperobject.biblemap.put("b9_4_12", new trsuperobject(9,4,12,"Benyaminli bir adam savaş alanından koşarak aynı gün Şiloya ulaştı. Giysileri yırtılmış, başı toz toprak içindeydi."));
trsuperobject.biblemap.put("b9_4_13", new trsuperobject(9,4,13,"Adam Şiloya vardığında, Tanrının Sandığı için yüreği titreyen Eli, yol kenarında bir sandalyeye oturmuş, kaygıyla bekliyordu. Adam kente girip olup bitenleri anlatınca, kenttekilerin tümü haykırdı."));
trsuperobject.biblemap.put("b9_4_14", new trsuperobject(9,4,14,"Eli haykırışları duyunca, ‹‹Bu gürültünün anlamı ne?›› diye sordu. Adam olanları Eliye bildirmek için hemen onun yanına geldi."));
trsuperobject.biblemap.put("b9_4_15", new trsuperobject(9,4,15,"O sırada Eli doksan sekiz yaşındaydı. Gözleri zayıflamış, göremiyordu."));
trsuperobject.biblemap.put("b9_4_16", new trsuperobject(9,4,16,"Adam Eliye, ‹‹Ben savaş alanından geliyorum›› dedi, ‹‹Savaş alanından bugün kaçtım.›› Eli, ‹‹Ne oldu, oğlum?›› diye sordu."));
trsuperobject.biblemap.put("b9_4_17", new trsuperobject(9,4,17,"Haber getiren adam şöyle yanıtladı: ‹‹İsrailliler Filistlilerin önünden kaçtı. Askerler büyük bir yenilgiye uğradı. İki oğlun, Hofniyle Pinehas öldü. Tanrının Sandığı da ele geçirildi.››"));
trsuperobject.biblemap.put("b9_4_18", new trsuperobject(9,4,18,"Adam Tanrının Sandığından söz edince, Eli sandalyeden geriye, kapının yanına düştü. Yaşlı ve şişman olduğundan boynu kırılıp öldü. İsrail halkını kırk yıl süreyle yönetmişti."));
trsuperobject.biblemap.put("b9_4_19", new trsuperobject(9,4,19,"Elinin gelini -Pinehasın karısı- gebeydi, doğurmak üzereydi. Tanrının Sandığının ele geçirildiğini, kayınbabasıyla kocasının öldüğünü duyunca birden sancıları tuttu, yere çömelip doğurdu."));
trsuperobject.biblemap.put("b9_4_20", new trsuperobject(9,4,20,"Ölmek üzereyken ona yardım eden kadınlar, ‹‹Korkma, bir oğlun oldu›› dediler. Ama o aldırmadı, karşılık da vermedi."));
trsuperobject.biblemap.put("b9_4_21", new trsuperobject(9,4,21,"Tanrının Sandığı ele geçirilmiş, kayınbabasıyla kocası ölmüştü. Bu yüzden, ‹‹Yücelik İsrailden ayrıldı!›› diyerek çocuğa İkavotfı adını verdi."));
trsuperobject.biblemap.put("b9_4_22", new trsuperobject(9,4,22,"‹‹Yücelik İsrail'den ayrıldı!›› dedi, ‹‹Çünkü Tanrı'nın Sandığı ele geçirildi.››"));
trsuperobject.biblemap.put("b9_5_1", new trsuperobject(9,5,1,"Filistliler, Tanrının Sandığını ele geçirdikten sonra, onu Even-Ezerden Aşdota götürdüler."));
trsuperobject.biblemap.put("b9_5_2", new trsuperobject(9,5,2,"Tanrının Sandığını Dagon Tapınağına taşıyıp Dagon heykelinin yanına yerleştirdiler."));
trsuperobject.biblemap.put("b9_5_3", new trsuperobject(9,5,3,"Ertesi gün erkenden kalkan Aşdotlular, Dagonu RABbin Sandığının önünde yüzüstü yere düşmüş buldular. Dagonu alıp yerine koydular."));
trsuperobject.biblemap.put("b9_5_4", new trsuperobject(9,5,4,"Ama ertesi sabah erkenden kalktıklarında, Dagonu yine RABbin Sandığının önünde yüzüstü yere düşmüş buldular. Bu kez Dagonun başıyla iki eli kırılmış, eşiğin üzerinde duruyordu; yalnızca gövdesi kalmıştı."));
trsuperobject.biblemap.put("b9_5_5", new trsuperobject(9,5,5,"Dagon kâhinleri de, Aşdottaki Dagon Tapınağına bütün gelenler de bu yüzden bugün de tapınağın eşiğine basmıyorlar."));
trsuperobject.biblemap.put("b9_5_7", new trsuperobject(9,5,7,"Aşdotlular olup bitenleri görünce, ‹‹İsrail Tanrısının Sandığı yanımızda kalmamalı; çünkü O bizi de, ilahımız Dagonu da ağır bir biçimde cezalandırıyor›› dediler."));
trsuperobject.biblemap.put("b9_5_8", new trsuperobject(9,5,8,"Bunun üzerine ulaklar gönderip bütün Filist beylerini çağırttılar ve, ‹‹İsrail Tanrısının Sandığını ne yapalım?›› diye sordular. Filist beyleri, ‹‹İsrail Tanrısının Sandığı Gata götürülsün›› dediler. Böylece İsrail Tanrısının Sandığını Gata götürdüler."));
trsuperobject.biblemap.put("b9_5_9", new trsuperobject(9,5,9,"Ama sandık oraya götürüldükten sonra, RAB o kenti de cezalandırdı. Kenti çok büyük bir korku sardı. RAB kent halkını, büyük küçük herkesi urlarla cezalandırdı."));
trsuperobject.biblemap.put("b9_5_10", new trsuperobject(9,5,10,"Bu yüzden Tanrının Sandığını Ekrona gönderdiler. Tanrının Sandığı kente girer girmez Ekronlular, ‹‹Bizi ve halkımızı yok etmek için İsrail Tanrısının Sandığını bize getirdiler!›› diye bağırdılar."));
trsuperobject.biblemap.put("b9_5_11", new trsuperobject(9,5,11,"Bütün Filist beylerini toplayarak, ‹‹İsrail Tanrısının Sandığını buradan uzaklaştırın›› dediler, ‹‹Sandık yerine geri gönderilsin; öyle ki, bizi de halkımızı da yok etmesin.›› Çünkü kentin her yanını ölüm korkusu sarmıştı. Tanrının onlara verdiği ceza çok ağırdı."));
trsuperobject.biblemap.put("b9_5_12", new trsuperobject(9,5,12,"Sağ kalanlarda urlar çıktı. Kent halkının haykırışı göklere yükseldi."));
trsuperobject.biblemap.put("b9_6_2", new trsuperobject(9,6,2,"Filistliler kâhinlerle falcıları çağırtıp, ‹‹RABbin Sandığını ne yapalım? Onu nasıl yerine göndereceğimizi bize bildirin›› dediler."));
trsuperobject.biblemap.put("b9_6_3", new trsuperobject(9,6,3,"Kâhinlerle falcılar, ‹‹İsrail Tanrısının Sandığını geri gönderecekseniz, boş göndermeyin›› diye yanıtladılar, ‹‹Ona bir suç sunusu sunmalısınız. O zaman iyileşecek ve Onun sizi neden sürekli cezalandırdığını anlayacaksınız.››"));
trsuperobject.biblemap.put("b9_6_4", new trsuperobject(9,6,4,"Filistliler, ‹‹Ona suç sunusu olarak ne göndermeliyiz?›› diye sordular. Kâhinlerle falcılar, ‹‹Suç sununuz Filist beylerinin sayısına göre beş altın ur ve beş altın fare olsun›› diye yanıtladılar, ‹‹Çünkü aynı bela hepinizin de, beylerinizin de üzerindedir."));
trsuperobject.biblemap.put("b9_6_5", new trsuperobject(9,6,5,"Onun için, urların ve ülkeyi yıkan farelerin benzerlerini yapın. Böylelikle İsrailin Tanrısını onurlandırın. Belki sizin, ilahlarınızın ve ülkenizin üzerindeki cezayı hafifletir."));
trsuperobject.biblemap.put("b9_6_6", new trsuperobject(9,6,6,"Neden Mısırlıların ve firavunun yaptığı gibi inat ediyorsunuz? Tanrı Mısırlıları alaya aldıktan sonra, İsrail halkının Mısırdan çıkması için onları serbest bırakmadılar mı?"));
trsuperobject.biblemap.put("b9_6_7", new trsuperobject(9,6,7,"‹‹Şimdi yeni bir arabayla boyunduruk vurulmamış, süt veren iki inek hazırlayın. İnekleri arabaya koşun; buzağılarını artlarından ayırıp ahıra götürün."));
trsuperobject.biblemap.put("b9_6_9", new trsuperobject(9,6,9,"Ama ardından gözetleyin. Eğer kendi ülkesine, Beytşemeşe giden yoldan ilerlerse, demek ki, üzerimize bu büyük yıkımı getiren Odur. Yoksa bu yıkımın Ondan gelmediğini, bize bir rastlantı olduğunu anlayacağız.››"));
trsuperobject.biblemap.put("b9_6_10", new trsuperobject(9,6,10,"Adamlar denileni yaptılar. Süt veren iki inek getirip arabaya koştular, buzağılarını da ahıra kapadılar."));
trsuperobject.biblemap.put("b9_6_11", new trsuperobject(9,6,11,"İçinde farelerle urların altın benzerlerinin bulunduğu kutuyu RABbin Sandığıyla birlikte arabaya koydular."));
trsuperobject.biblemap.put("b9_6_12", new trsuperobject(9,6,12,"İnekler dosdoğru Beytşemeş yolundan gittiler. Sağa sola sapmadan, böğüre böğüre ana yoldan ilerlediler. Filist beyleri onları Beytşemeş sınırına dek izledi."));
trsuperobject.biblemap.put("b9_6_13", new trsuperobject(9,6,13,"O sırada Beytşemeşliler vadide buğday biçiyorlardı. Gözlerini kaldırıp sandığı görünce sevindiler."));
trsuperobject.biblemap.put("b9_6_14", new trsuperobject(9,6,14,"Beytşemeşli Yeşunun tarlasına giren araba oradaki büyük bir taşın yanında durdu. Beytşemeşliler arabanın odununu yardılar, inekleri de RABbe yakmalık sunu olarak sundular."));
trsuperobject.biblemap.put("b9_6_15", new trsuperobject(9,6,15,"Levililer RABbin Sandığını ve içinde altın eşyaların bulunduğu yanındaki kutuyu indirip büyük taşın üzerine koymuşlardı. O gün Beytşemeşliler RABbe yakmalık sunular sunup kurbanlar kestiler."));
trsuperobject.biblemap.put("b9_6_16", new trsuperobject(9,6,16,"Filistlilerin beş beyi olup bitenleri gördükten sonra aynı gün Ekrona döndüler."));
trsuperobject.biblemap.put("b9_6_17", new trsuperobject(9,6,17,"Filistliler Aşdot, Gazze, Aşkelon, Gat ve Ekron kentleri için RABbe suç sunusu olarak ur biçiminde birer altın gönderdiler."));
trsuperobject.biblemap.put("b9_6_18", new trsuperobject(9,6,18,"Altın farelerse, surlu kentlerle çevre köyler dahil beş Filistli beye ait kentlerin sayısı kadardı. Beytşemeşli Yeşunun tarlasında RABbin Antlaşma Sandığının üzerine konduğu büyük taş tanık olarak bugün de duruyor."));
trsuperobject.biblemap.put("b9_6_19", new trsuperobject(9,6,19,"RABbin Antlaşma Sandığının içine baktıkları için, RAB Beytşemeşlilerden bazılarını cezalandırıp yetmiş kişiyi yok etti. Halk RABbin başlarına getirdiği bu büyük yıkımdan dolayı yas tuttu."));
trsuperobject.biblemap.put("b9_6_20", new trsuperobject(9,6,20,"Beytşemeşliler, ‹‹Bu kutsal Tanrının, RABbin önünde kim durabilir? Bizden sonra kime gidecek?›› diyorlardı."));
trsuperobject.biblemap.put("b9_6_21", new trsuperobject(9,6,21,"Sonunda Kiryat-Yearim'de oturanlara ulaklar göndererek, ‹‹Filistliler RAB'bin Sandığı'nı geri getirdiler; gelin, onu alıp götürün›› dediler. elyazmaları ‹‹Yetmiş kişi, elli bin kişi››."));
trsuperobject.biblemap.put("b9_7_1", new trsuperobject(9,7,1,"Bunun üzerine Kiryat-Yearim halkı varıp RABbin Sandığını aldı. Onu Avinadavın tepedeki evine götürdüler. RABbin Antlaşma Sandığına bakması için Avinadav oğlu Elazarı görevlendirdiler."));
trsuperobject.biblemap.put("b9_7_2", new trsuperobject(9,7,2,"Sandık uzun bir süre, yirmi yıl boyunca Kiryat-Yearimde kaldı. Bu arada bütün İsrail halkı RABbin özlemini çekti."));
trsuperobject.biblemap.put("b9_7_3", new trsuperobject(9,7,3,"Samuel İsrail halkına şöyle dedi: ‹‹Eğer bütün yüreğinizle RABbe dönmeye istekliyseniz, yabancı ilahları ve Aştoretin putlarını aranızdan kaldırın. Kendinizi RABbe adayıp yalnız Ona kulluk edin. RAB de sizi Filistlilerin elinden kurtaracaktır.››"));
trsuperobject.biblemap.put("b9_7_4", new trsuperobject(9,7,4,"Bunun üzerine İsrailliler Baalın ve Aştoretin putlarını atıp yalnızca RABbe kulluk etmeye başladılar."));
trsuperobject.biblemap.put("b9_7_5", new trsuperobject(9,7,5,"O zaman Samuel, ‹‹Bütün İsrail halkını Mispada toplayın, ben de sizin için RABbe yakaracağım›› dedi."));
trsuperobject.biblemap.put("b9_7_6", new trsuperobject(9,7,6,"Mispada toplanan İsrailliler kuyudan su çekip RABbin önüne döktüler. O gün oruç tuttular ve, ‹‹RABbe karşı günah işledik›› dediler. Samuel Mispada İsrail halkına önderlik etti."));
trsuperobject.biblemap.put("b9_7_7", new trsuperobject(9,7,7,"Filistliler İsrail halkının Mispada toplandığını duydular. Filist beyleri İsraillilere karşı savaşmaya çıktılar. İsrailliler bunu duyunca Filistlilerden korktular."));
trsuperobject.biblemap.put("b9_7_8", new trsuperobject(9,7,8,"Samuele, ‹‹Bizi Filistlilerin elinden kurtarması için Tanrımız RABbe yakarmayı bırakma›› dediler."));
trsuperobject.biblemap.put("b9_7_9", new trsuperobject(9,7,9,"Bunun üzerine Samuel bir süt kuzusu alıp RABbe tümüyle yakmalık sunu olarak sundu ve İsrailliler adına RABbe yakardı. RAB de ona karşılık verdi."));
trsuperobject.biblemap.put("b9_7_10", new trsuperobject(9,7,10,"Samuel yakmalık sunuyu sunarken, Filistliler, İsraillilere saldırmak üzere yaklaşmışlardı. Ama RAB o an korkunç bir sesle gürleyerek Filistlileri öyle şaşkına çevirdi ki, İsraillilerin önünde bozguna uğradılar."));
trsuperobject.biblemap.put("b9_7_11", new trsuperobject(9,7,11,"Mispadan çıkan İsrailliler Filistlileri Beytkarın altına kadar kovalayıp öldürdüler."));
trsuperobject.biblemap.put("b9_7_12", new trsuperobject(9,7,12,"Samuel bir taş alıp Mispa ile Şen arasına dikti. ‹‹RAB buraya kadar bize yardım etmiştir›› diyerek taşa Even-Ezer adını verdi."));
trsuperobject.biblemap.put("b9_7_13", new trsuperobject(9,7,13,"Yenilgiye uğrayan Filistliler bir daha İsrail topraklarına saldırmadılar. Samuel yaşadığı sürece RAB Filistlilerin saldırmasını engelledi."));
trsuperobject.biblemap.put("b9_7_14", new trsuperobject(9,7,14,"Ekrondan Gata kadar Filistlilerin ele geçirdiği kentler İsraile geri verildi. Bunun yanısıra İsrailin sınır toprakları da Filistlilerin elinden kurtarıldı. İsraillilerle Amorlular arasında ise barış vardı."));
trsuperobject.biblemap.put("b9_7_15", new trsuperobject(9,7,15,"Samuel yaşadığı sürece İsraile önderlik yaptı."));
trsuperobject.biblemap.put("b9_7_16", new trsuperobject(9,7,16,"Her yıl gidip Beyteli, Gilgalı, Mispayı dolaşır, bu kentlerden İsraili yönetirdi."));
trsuperobject.biblemap.put("b9_7_17", new trsuperobject(9,7,17,"Sonra Rama'daki evine döner, İsrail'i oradan yönetirdi. Orada RAB'be bir sunak yaptı."));
trsuperobject.biblemap.put("b9_8_1", new trsuperobject(9,8,1,"Samuel yaşlanınca oğullarını İsraile önder atadı."));
trsuperobject.biblemap.put("b9_8_2", new trsuperobject(9,8,2,"Beer-Şevada görev yapan ilk oğlunun adı Yoel, ikinci oğlunun adıysa Aviyaydı."));
trsuperobject.biblemap.put("b9_8_3", new trsuperobject(9,8,3,"Ama oğulları onun yolunda yürümediler. Tersine, haksız kazanca yönelip rüşvet alır, yargıda yan tutarlardı."));
trsuperobject.biblemap.put("b9_8_4", new trsuperobject(9,8,4,"Bu yüzden İsrailin bütün ileri gelenleri toplanıp Ramaya, Samuelin yanına vardılar."));
trsuperobject.biblemap.put("b9_8_5", new trsuperobject(9,8,5,"Ona, ‹‹Bak, sen yaşlandın›› dediler, ‹‹Oğulların da senin yolunda yürümüyor. Şimdi, öteki uluslarda olduğu gibi, bizi yönetecek bir kral ata.››"));
trsuperobject.biblemap.put("b9_8_6", new trsuperobject(9,8,6,"Ne var ki, ‹‹Bizi yönetecek bir kral ata›› demeleri Samuelin hoşuna gitmedi. Samuel RABbe yakardı."));
trsuperobject.biblemap.put("b9_8_8", new trsuperobject(9,8,8,"Onları Mısırdan çıkardığım günden bu yana bütün yaptıklarının aynısını sana da yapıyorlar. Beni bırakıp başka ilahlara kulluk ettiler."));
trsuperobject.biblemap.put("b9_8_9", new trsuperobject(9,8,9,"Şimdi onları dinle. Ancak onları açıkça uyar ve kendilerine krallık yapacak kişinin onları nasıl yöneteceğini söyle.››"));
trsuperobject.biblemap.put("b9_8_10", new trsuperobject(9,8,10,"Samuel kendisinden kral isteyen halka RABbin bütün söylediklerini bildirdi:"));
trsuperobject.biblemap.put("b9_8_11", new trsuperobject(9,8,11,"‹‹Size krallık yapacak kişinin yönetimi şöyle olacak: Oğullarınızı alıp savaş arabalarında ve atlı birliklerinde görevlendirecek. Onun savaş arabalarının önünde koşacaklar."));
trsuperobject.biblemap.put("b9_8_12", new trsuperobject(9,8,12,"Bazılarını biner, bazılarını ellişer kişilik birliklere komutan atayacak. Kimisini toprağını sürüp ekinini biçmek, kimisini de silahların ve savaş arabalarının donatımını yapmak için görevlendirecek."));
trsuperobject.biblemap.put("b9_8_13", new trsuperobject(9,8,13,"Kızlarınızı ıtriyatçı, aşçı, fırıncı olmak üzere alacak."));
trsuperobject.biblemap.put("b9_8_14", new trsuperobject(9,8,14,"Seçkin tarlalarınızı, bağlarınızı, zeytinliklerinizi alıp hizmetkârlarına verecek."));
trsuperobject.biblemap.put("b9_8_15", new trsuperobject(9,8,15,"Tahıllarınızın, üzümlerinizin ondalığını alıp saray görevlileriyle öbür hizmetkârlarına dağıtacak."));
trsuperobject.biblemap.put("b9_8_16", new trsuperobject(9,8,16,"Kadın erkek kölelerinizi, seçkin boğalarınızı, eşeklerinizi alıp kendi işinde çalıştıracak."));
trsuperobject.biblemap.put("b9_8_17", new trsuperobject(9,8,17,"Sürülerinizin de ondalığını alacak. Sizler ise onun köleleri olacaksınız."));
trsuperobject.biblemap.put("b9_8_18", new trsuperobject(9,8,18,"Bunlar gerçekleştiğinde, seçtiğiniz kral yüzünden feryat edeceksiniz. Ama RAB o gün size karşılık vermeyecek.›› ‹‹Gençlerinizi››."));
trsuperobject.biblemap.put("b9_8_19", new trsuperobject(9,8,19,"Ne var ki, halk Samuelin sözünü dinlemek istemedi. ‹‹Hayır, bizi yönetecek bir kral olsun›› dediler,"));
