trsuperobject.biblemap.put("b9_9_1", new trsuperobject(9,9,1,"Benyamin oymağından Afiyah oğlu Bekorat oğlu Seror oğlu Aviel oğlu Kiş adında bir adam vardı. Benyaminli Kiş sözü geçen biriydi."));
trsuperobject.biblemap.put("b9_9_2", new trsuperobject(9,9,2,"Saul adında genç, yakışıklı bir oğlu vardı. İsrail halkı arasında ondan daha yakışıklısı yoktu. Boyu herkesten bir baş daha uzundu."));
trsuperobject.biblemap.put("b9_9_3", new trsuperobject(9,9,3,"Bir gün Saulun babası Kişin eşekleri kayboldu. Kiş, oğlu Saula, ‹‹Hizmetkârlardan birini yanına al da git, eşekleri ara›› dedi."));
trsuperobject.biblemap.put("b9_9_4", new trsuperobject(9,9,4,"Saul Efrayim dağlık bölgesinden geçip Şalişa topraklarını dolaştı. Ama eşekleri bulamadılar. Şaalim bölgesine geçtiler. Eşekler orada da yoktu. Sonra Benyamin bölgesinden geçtilerse de, hayvanları bulamadılar."));
trsuperobject.biblemap.put("b9_9_5", new trsuperobject(9,9,5,"Suf bölgesine varınca, Saul yanındaki hizmetkârına, ‹‹Haydi dönelim! Yoksa babam eşekleri düşünmekten vazgeçip bizim için kaygılanmaya başlar›› dedi."));
trsuperobject.biblemap.put("b9_9_6", new trsuperobject(9,9,6,"Hizmetkâr, ‹‹Bak, bu kentte saygın bir Tanrı adamı vardır›› diye karşılık verdi, ‹‹Bütün söyledikleri bir bir yerine geliyor. Şimdi ona gidelim. Belki gideceğimiz yolu o bize gösterir.››"));
trsuperobject.biblemap.put("b9_9_7", new trsuperobject(9,9,7,"Saul, ‹‹Gidersek, adama ne götüreceğiz?›› dedi, ‹‹Torbalarımızdaki ekmek tükendi. Tanrı adamına götürecek bir armağanımız yok. Neyimiz kaldı ki?››"));
trsuperobject.biblemap.put("b9_9_8", new trsuperobject(9,9,8,"Hizmetkâr, ‹‹Bak, bende çeyrek şekel gümüş var›› diye karşılık verdi, ‹‹Gideceğimiz yolu bize göstermesi için bunu Tanrı adamına vereceğim.››"));
trsuperobject.biblemap.put("b9_9_9", new trsuperobject(9,9,9,"Eskiden İsrailde biri Tanrıya bir şey sormak istediğinde, ‹‹Haydi, biliciye gidelim›› derdi. Çünkü bugün peygamber denilene o zaman bilici denirdi.-"));
trsuperobject.biblemap.put("b9_9_10", new trsuperobject(9,9,10,"Saul hizmetkârına, ‹‹İyi, haydi gidelim›› dedi. Böylece Tanrı adamının yaşadığı kente gittiler."));
trsuperobject.biblemap.put("b9_9_11", new trsuperobject(9,9,11,"Yokuştan kente doğru çıkarlarken, kuyudan su çekmeye giden kızlarla karşılaştılar. Onlara, ‹‹Bilici burada mı?›› diye sordular."));
trsuperobject.biblemap.put("b9_9_12", new trsuperobject(9,9,12,"Kızlar, ‹‹Evet, ilerde›› diye karşılık verdiler, ‹‹Şimdi çabuk davranın. Kentimize bugün geldi. Çünkü halk bugün tapınma yerinde bir kurban sunacak."));
trsuperobject.biblemap.put("b9_9_13", new trsuperobject(9,9,13,"Kente girer girmez, yemek için tapınma yerine çıkmadan önce onu bulacaksınız. Kurbanı o kutsayacağı için, kendisi gelmeden halk yemek yemez. Çağrılı olanlar o geldikten sonra yemeye başlar. Şimdi gidin, onu hemen bulursunuz.››"));
trsuperobject.biblemap.put("b9_9_14", new trsuperobject(9,9,14,"Saulla hizmetkârı kente gittiler. Kente girdiklerinde, tapınma yerine çıkmaya hazırlanan Samuel onlara doğru ilerliyordu."));
trsuperobject.biblemap.put("b9_9_15", new trsuperobject(9,9,15,"Saul gelmeden bir gün önce RAB Samuele şunu açıklamıştı:"));
trsuperobject.biblemap.put("b9_9_16", new trsuperobject(9,9,16,"‹‹Yarın bu saatlerde sana Benyamin bölgesinden birini göndereceğim. Onu halkım İsrailin önderi olarak meshedeceksin. Halkımı Filistlilerin elinden o kurtaracak. Halkımın durumuna baktım; çünkü haykırışları bana ulaştı.››"));
trsuperobject.biblemap.put("b9_9_17", new trsuperobject(9,9,17,"Samuel Saulu görünce, RAB, ‹‹İşte sana sözünü ettiğim adam!›› dedi, ‹‹Halkıma o önderlik edecek.››"));
trsuperobject.biblemap.put("b9_9_18", new trsuperobject(9,9,18,"Saul kent kapısında duran Samuele yaklaştı. ‹‹Bilicinin evi nerede, lütfen söyler misin?›› dedi."));
trsuperobject.biblemap.put("b9_9_19", new trsuperobject(9,9,19,"Samuel, ‹‹Bilici benim›› diye yanıtladı, ‹‹Önümden tapınma yerine çıkın. Bugün benimle birlikte yemek yiyeceksiniz. Yarın sabah düşündüğün her şeyi sana bildirip seni geri gönderirim."));
trsuperobject.biblemap.put("b9_9_20", new trsuperobject(9,9,20,"Üç gün önce kaybolan eşeklerin için kaygılanma. Onlar bulundu. İsrailin özlemi kime yönelik? Sana ve babanın ailesine değil mi?››"));
trsuperobject.biblemap.put("b9_9_21", new trsuperobject(9,9,21,"Saul şu karşılığı verdi: ‹‹Ben İsrail oymaklarının en küçüğü olan Benyamin oymağından değil miyim? Ait olduğum boy da Benyamin oymağına bağlı bütün boyların en küçüğü değil mi? Bana neden böyle şeyler söylüyorsun?››"));
trsuperobject.biblemap.put("b9_9_22", new trsuperobject(9,9,22,"Samuel Saul ile hizmetkârını alıp yemek odasına götürdü; yaklaşık otuz çağrılı arasında ilk sırayı onlara verdi."));
trsuperobject.biblemap.put("b9_9_23", new trsuperobject(9,9,23,"Sonra aşçıya, ‹‹Sana verdiğim ve bir kenara ayırmanı söylediğim payı getir›› dedi."));
trsuperobject.biblemap.put("b9_9_24", new trsuperobject(9,9,24,"Aşçı budu getirip Saulun önüne koydu. Samuel, ‹‹İşte senin için ayrılan parça, buyur ye!›› dedi, ‹‹Çünkü bunu belirtilen gün çağırdığım halkla birlikte yemen için sakladım.›› O gün Saul Samuelle yemek yedi."));
trsuperobject.biblemap.put("b9_9_25", new trsuperobject(9,9,25,"Tapınma yerinden kente indikten sonra Samuel evinin damında Saulla konuştu."));
trsuperobject.biblemap.put("b9_9_26", new trsuperobject(9,9,26,"Sabah erkenden, şafak sökerken kalktılar. Samuel, damdan Saulu çağırıp, ‹‹Hazırlan, seni göndereceğim›› dedi. Saul kalktı. Samuelle birlikte dışarı çıktılar."));
trsuperobject.biblemap.put("b9_9_27", new trsuperobject(9,9,27,"Kentin sınırına yaklaşırken Samuel Saul'a, ‹‹Hizmetkâra önümüzden gitmesini söyle›› dedi. Hizmetkâr öne geçince, Samuel, ‹‹Ama sen dur›› diye ekledi, ‹‹Sana Tanrı'nın sözünü bildireceğim.›› konuştu››, Septuaginta ‹‹Saul için damda bir döşek serildi, o da orada yattı.››"));
trsuperobject.biblemap.put("b9_10_1", new trsuperobject(9,10,1,"Sonra Samuel yağ kabını alıp yağı Saulun başına döktü. Onu öpüp şöyle dedi: ‹‹RAB seni kendi halkına önder olarak meshetti."));
trsuperobject.biblemap.put("b9_10_2", new trsuperobject(9,10,2,"Bugün benden ayrıldıktan sonra Benyamin sınırında, Selsahtaki Rahelin mezarı yanında iki kişiyle karşılaşacaksın. Sana, ‹Aramaya çıktığın eşekler bulundu› diyecekler, ‹Baban eşekleri düşünmekten vazgeçti, oğlum için ne yapsam diye sizin için kaygılanmaya başladı.›"));
trsuperobject.biblemap.put("b9_10_3", new trsuperobject(9,10,3,"Oradan daha ilerleyip Tavordaki meşe ağacına varacaksın. Orada biri üç oğlak, biri üç somun ekmek, öbürü de bir tulum şarapla Tanrının huzuruna, Beytele çıkan üç adamla karşılaşacaksın."));
trsuperobject.biblemap.put("b9_10_4", new trsuperobject(9,10,4,"Seni selamlayıp iki somun ekmek verecekler. Sen de kabul edeceksin."));
trsuperobject.biblemap.put("b9_10_5", new trsuperobject(9,10,5,"Sonra Filist ordugahının bulunduğu Givat-Elohime varacaksın. Kente girince, önlerinde çenk, tef, kaval ve lir çalanlarla birlikte peygamberlik ederek tapınma yerinden inen bir peygamber topluluğuyla karşılaşacaksın."));
trsuperobject.biblemap.put("b9_10_7", new trsuperobject(9,10,7,"Bu belirtiler gerçekleştiğinde, duruma göre gerekeni yap. Çünkü Tanrı seninledir."));
trsuperobject.biblemap.put("b9_10_8", new trsuperobject(9,10,8,"Şimdi benden önce Gilgala git. Yakmalık sunuları sunmak ve esenlik kurbanlarını kesmek için ben de yanına geleceğim. Ancak, ben yanına gelip ne yapacağını bildirene dek yedi gün beklemen gerekecek.››"));
trsuperobject.biblemap.put("b9_10_9", new trsuperobject(9,10,9,"Saul, Samuelin yanından ayrılmak üzere ona sırtını döner dönmez, Tanrı ona başka bir kişilik verdi. O gün bütün bu belirtiler gerçekleşti."));
trsuperobject.biblemap.put("b9_10_10", new trsuperobject(9,10,10,"Givaya varınca, Saulu bir peygamber topluluğu karşıladı. Tanrının Ruhu güçlü bir biçimde üzerine indi ve Saul onlarla birlikte peygamberlikte bulunmaya başladı."));
trsuperobject.biblemap.put("b9_10_11", new trsuperobject(9,10,11,"Onu önceden tanıyanların hepsi, peygamberlerle birlikte peygamberlikte bulunduğunu görünce, birbirlerine, ‹‹Ne oldu Kiş oğluna? Saul da mı peygamber oldu?›› diye sordular."));
trsuperobject.biblemap.put("b9_10_12", new trsuperobject(9,10,12,"Orada oturanlardan biri, ‹‹Ya onların babası kim?›› dedi. İşte, ‹‹Saul da mı peygamber oldu?›› sözü buradan gelir."));
trsuperobject.biblemap.put("b9_10_13", new trsuperobject(9,10,13,"Saul peygamberlikte bulunduktan sonra tapınma yerine çıktı."));
trsuperobject.biblemap.put("b9_10_14", new trsuperobject(9,10,14,"Amcası, Saul ile hizmetkârına, ‹‹Nerede kaldınız?›› diye sordu. Saul, ‹‹Eşekleri arıyorduk›› diye karşılık verdi, ‹‹Onları bulamayınca, Samuele gittik.››"));
trsuperobject.biblemap.put("b9_10_15", new trsuperobject(9,10,15,"Amcası, ‹‹Samuel sana neler söyledi, lütfen bana da anlat›› dedi."));
trsuperobject.biblemap.put("b9_10_16", new trsuperobject(9,10,16,"Saul, ‹‹Eşeklerin bulunduğunu bize açıkça bildirdi›› diye yanıtladı. Ama Samuelin krallıkla ilgili sözlerini amcasına açıklamadı."));
trsuperobject.biblemap.put("b9_10_17-18", new trsuperobject(9,10,17-18,"Sonra Samuel, İsrail halkını Mispada RAB için bir araya getirip şöyle dedi: ‹‹İsrailin Tanrısı RAB diyor ki, ‹Ben İsraillileri Mısırdan çıkardım. Mısırlıların ve size baskı yapan bütün krallıkların elinden sizi kurtardım.›"));
trsuperobject.biblemap.put("b9_10_19", new trsuperobject(9,10,19,"Ama siz bugün bütün zorluk ve sıkıntılarınızdan sizi kurtaran Tanrınıza sırt çevirdiniz ve, ‹Hayır, bize bir kral ata› dediniz. Şimdi RABbin önünde oymak oymak, boy boy dizilin.››"));
trsuperobject.biblemap.put("b9_10_20", new trsuperobject(9,10,20,"Samuel bütün İsrail oymaklarını bir bir öne çıkardı. Bunlardan Benyamin oymağı kurayla seçildi."));
trsuperobject.biblemap.put("b9_10_21", new trsuperobject(9,10,21,"Sonra Benyamin oymağını boy boy öne çağırdı. Matrinin boyu seçildi. En sonunda da Matri boyundan Kiş oğlu Saul seçildi. Onu aradılarsa da bulamadılar."));
trsuperobject.biblemap.put("b9_10_22", new trsuperobject(9,10,22,"Yine RABbe, ‹‹O daha buraya gelmedi mi?›› diye sordular. RAB de, ‹‹O burada, eşyaların arasında saklanıyor›› dedi."));
trsuperobject.biblemap.put("b9_10_23", new trsuperobject(9,10,23,"Bunun üzerine koşup Saulu oradan getirdiler. Saul halkın arasına geldi. Boyu hepsinden bir baş uzundu."));
trsuperobject.biblemap.put("b9_10_24", new trsuperobject(9,10,24,"Samuel halka, ‹‹RABbin seçtiği adamı görüyor musunuz?›› dedi, ‹‹Bütün halkın arasında bir benzeri yok.›› Bunun üzerine halk, ‹‹Yaşasın kral!›› diye bağırdı."));
trsuperobject.biblemap.put("b9_10_25", new trsuperobject(9,10,25,"Samuel krallığın ilkelerini halka açıkladı. Bunları kitap haline getirip RABbin önüne koydu. Sonra herkesi evine gönderdi."));
trsuperobject.biblemap.put("b9_10_26", new trsuperobject(9,10,26,"Saul da Givaya, kendi evine döndü. Tanrının isteklendirdiği yiğitler ona eşlik ettiler."));
trsuperobject.biblemap.put("b9_10_27", new trsuperobject(9,10,27,"Ama bazı kötü kişiler, ‹‹O bizi nasıl kurtarabilir?›› diyerek Saul'u küçümsediler ve ona armağan vermediler. Saul ise buna aldırmadı."));
trsuperobject.biblemap.put("b9_11_1", new trsuperobject(9,11,1,"Ammon Kralı Nahaş Yaveş-Gilat üzerine yürüyüp kenti kuşattı. Bütün Yaveşliler, Nahaşa, ‹‹Bizimle bir antlaşma yap, sana kulluk ederiz›› dediler."));
trsuperobject.biblemap.put("b9_11_2", new trsuperobject(9,11,2,"Ama Ammonlu Nahaş, ‹‹Ancak bir koşulla sizinle antlaşma yaparım›› diye karşılık verdi, ‹‹Bütün İsrail halkını küçük düşürmek için her birinizin sağ gözünü oyup çıkaracağım.››"));
trsuperobject.biblemap.put("b9_11_3", new trsuperobject(9,11,3,"Yaveş Kentinin ileri gelenleri ona, ‹‹İsrailin her bölgesine ulaklar göndermemiz için bize yedi günlük bir süre tanı›› dediler, ‹‹Eğer bizi kurtaracak kimse çıkmazsa o zaman sana teslim oluruz.››"));
trsuperobject.biblemap.put("b9_11_4", new trsuperobject(9,11,4,"Ulaklar Saulun yaşadığı Giva Kentine gelip olanları halka bildirince, herkes hıçkıra hıçkıra ağlamaya başladı."));
trsuperobject.biblemap.put("b9_11_5", new trsuperobject(9,11,5,"Tam o sırada Saul, öküzlerinin ardında, tarladan dönüyordu. ‹‹Halka ne oldu? Neden böyle ağlıyorlar?›› diye sordu. Yaveşlilerin söylediklerini ona anlattılar."));
trsuperobject.biblemap.put("b9_11_6", new trsuperobject(9,11,6,"Saul bu sözleri duyunca, Tanrının Ruhu güçlü bir biçimde onun üzerine indi. Saul çok öfkelendi."));
trsuperobject.biblemap.put("b9_11_7", new trsuperobject(9,11,7,"Bir çift öküz alıp parçaladı. Ulaklar aracılığıyla İsrailin her bölgesine bu parçaları gönderip şöyle dedi: ‹‹Saul ile Samuelin ardınca gelmeyen herkesin öküzlerine de aynı şey yapılacaktır.›› Halk RAB korkusuyla sarsıldı ve tek beden halinde yola çıktı."));
trsuperobject.biblemap.put("b9_11_8", new trsuperobject(9,11,8,"Saul onları Bezekte topladı. İsrail halkı üç yüz bin, Yahudalılar ise otuz bin kişiydi."));
trsuperobject.biblemap.put("b9_11_9", new trsuperobject(9,11,9,"Oraya gelen Yaveşli ulaklara şöyle dediler: ‹‹Yaveş-Gilat halkına, ‹Yarın öğleye doğru kurtarılacaksınız› deyin.›› Ulaklar gidip bu haberi iletince Yaveşliler sevindi."));
trsuperobject.biblemap.put("b9_11_10", new trsuperobject(9,11,10,"Ammonlulara, ‹‹Yarın size teslim olacağız›› dediler, ‹‹Bize ne dilerseniz yapın.››"));
trsuperobject.biblemap.put("b9_11_11", new trsuperobject(9,11,11,"Ertesi gün Saul adamlarını üç bölüğe ayırdı. Adamlar sabah nöbetinde Ammonluların ordugahına girdi. Kırım günün en sıcak zamanına dek sürdü. Sağ kalanlar dağıldı; iki kişi bile bir arada kalmadı."));
trsuperobject.biblemap.put("b9_11_12", new trsuperobject(9,11,12,"Bundan sonra halk Samuele, ‹‹Saul mu bize krallık yapacak?› diyenler kimdi? Getirin onları, öldürelim›› dedi."));
trsuperobject.biblemap.put("b9_11_13", new trsuperobject(9,11,13,"Ama Saul, ‹‹Bugün hiç kimse öldürülmeyecek›› diye yanıtladı, ‹‹Çünkü RAB bugün İsrail halkına kurtuluş verdi.››"));
trsuperobject.biblemap.put("b9_11_14", new trsuperobject(9,11,14,"Samuel halka, ‹‹Haydi, Gilgala gidip orada krallığı yeniden onaylayalım›› dedi."));
trsuperobject.biblemap.put("b9_11_15", new trsuperobject(9,11,15,"Böylece bütün halk Gilgal'a gidip RAB'bin önünde Saul'un kral olduğunu onayladı. Orada, RAB'bin önünde esenlik kurbanları kestiler; Saul da bütün İsrailliler de büyük bir sevinç yaşadılar."));
trsuperobject.biblemap.put("b9_12_1", new trsuperobject(9,12,1,"Bundan sonra Samuel İsrail halkına şöyle dedi: ‹‹Bana söylediğiniz her şeye kulak verdim: Size bir kral atadım."));
trsuperobject.biblemap.put("b9_12_2", new trsuperobject(9,12,2,"Şimdi size önderlik yapan bir kralınız var. Bense yaşlandım, saçım ağardı. Oğullarım da sizlerle birlikte. Gençliğimden bu güne dek size önderlik yaptım."));
trsuperobject.biblemap.put("b9_12_3", new trsuperobject(9,12,3,"İşte karşınızda duruyorum. Hanginizin öküzünü aldım? Kimin eşeğine el koydum? Kimi dolandırdım? Kime baskı yaptım? Göz yummak için kimden rüşvet aldım? RABbin ve Onun meshettiğinin önünde bana karşı tanıklık edin de size karşılığını vereyim.››"));
trsuperobject.biblemap.put("b9_12_4", new trsuperobject(9,12,4,"Halk, ‹‹Bizi dolandırmadın›› diye karşılık verdi, ‹‹Bize baskı da yapmadın. Kimsenin elinden hiçbir şey almadın.››"));
trsuperobject.biblemap.put("b9_12_5", new trsuperobject(9,12,5,"Samuel, ‹‹Bana karşı bir şey bulamadığınıza bugün hem RAB, hem de Onun meshettiği kral tanıktır›› dedi. ‹‹Evet, tanıktır›› dediler."));
trsuperobject.biblemap.put("b9_12_6", new trsuperobject(9,12,6,"Samuel konuşmasını şöyle sürdürdü: ‹‹Musa ile Harunu görevlendiren, atalarınızı Mısırdan çıkaran RABdir."));
