trsuperobject.biblemap.put("b9_14_39", new trsuperobject(9,14,39,"‹‹İsraili kurtaran yaşayan RABbin adıyla derim ki, bu günaha yol açan oğlum Yonatan bile olsa kesinlikle öldürülecektir.›› Ama kimse bir şey söylemedi."));
trsuperobject.biblemap.put("b9_14_40", new trsuperobject(9,14,40,"Bunun üzerine Saul halka, ‹‹Siz bir yanda durun, oğlum Yonatanla ben öbür yanda duracağız›› dedi. Halk, ‹‹Sence uygun olan neyse onu yap›› diye karşılık verdi."));
trsuperobject.biblemap.put("b9_14_41", new trsuperobject(9,14,41,"Saul İsrailin Tanrısı RABbe, ‹‹Bana doğru yanıtı ver›› dedi. Kura Yonatanla Saula düştü, halk aklandı."));
trsuperobject.biblemap.put("b9_14_42", new trsuperobject(9,14,42,"Saul bu kez, ‹‹Benimle oğlum Yonatan arasında kura çekin›› dedi. Kura Yonatana düştü."));
trsuperobject.biblemap.put("b9_14_43", new trsuperobject(9,14,43,"Bunun üzerine Saul Yonatana, ‹‹Söyle bana, ne yaptın?›› diye sordu. doğru yanıtı ver› dedi.››, Septuaginta ‹‹Saul, ‹Ey İsrailin Tanrısı RAB, bugün neden kuluna yanıt vermedin? Suç bende ya da oğlum Yonatandaysa, ey İsrail Tanrısı RAB, Urimi ver. Yok eğer suç halkın İsraildeyse Tummimi ver› dedi.›› Yonatan, ‹‹Ben yalnızca elimdeki değneğin ucuyla biraz bal alıp tattım. Şimdi ölmem mi gerek?›› diye karşılık verdi."));
trsuperobject.biblemap.put("b9_14_44", new trsuperobject(9,14,44,"Saul, ‹‹Yonatan, eğer seni öldürtmezsem, Tanrı bana aynısını, hatta daha kötüsünü yapsın!›› dedi."));
trsuperobject.biblemap.put("b9_14_45", new trsuperobject(9,14,45,"Ama halk Saula, ‹‹İsraili bu büyük zafere ulaştıran Yonatanı mı öldürteceksin?›› dedi, ‹‹Asla! Yaşayan RABbin adıyla deriz ki, saçının bir teline bile zarar gelmeyecektir. Çünkü bugün o ne yaptıysa Tanrının yardımıyla yapmıştır.›› Böylece halk Yonatanı öldürülmekten kurtardı."));
trsuperobject.biblemap.put("b9_14_46", new trsuperobject(9,14,46,"Bundan sonra Saul Filistlileri kovalamaktan vazgeçti. Filistliler de yerlerine döndüler."));
trsuperobject.biblemap.put("b9_14_47", new trsuperobject(9,14,47,"Saul İsraile kral atandıktan sonra, her yandaki düşmanlarına -Moav, Ammon, Edom halkları, Sova kralları ve Filistlilere- karşı savaştı. Gittiği her yerde zafer kazandı."));
trsuperobject.biblemap.put("b9_14_48", new trsuperobject(9,14,48,"Yiğitçe savaşarak Amaleklileri yenilgiye uğrattı, İsraillileri düşmanın yağmasından kurtardı. verdi››."));
trsuperobject.biblemap.put("b9_14_49", new trsuperobject(9,14,49,"Saulun oğulları Yonatan, Yişvi ve Malkişua idi. İki kızından büyüğünün adı Merav, küçüğünün adı Mikaldı."));
trsuperobject.biblemap.put("b9_14_50", new trsuperobject(9,14,50,"Karısı, Ahimaasın kızı Ahinoamdı. Ordusunun başkomutanı amcası Ner oğlu Avnerdi."));
trsuperobject.biblemap.put("b9_14_51", new trsuperobject(9,14,51,"Saulun babası Kişle Avnerin babası Ner, Avielin oğullarıydı."));
trsuperobject.biblemap.put("b9_14_52", new trsuperobject(9,14,52,"Saul yaşamı boyunca Filistliler'le kıyasıya savaştı. Nerede yiğit, güçlü birini görse kendi ordusuna kattı."));
trsuperobject.biblemap.put("b9_15_1", new trsuperobject(9,15,1,"Samuel Saula şöyle dedi: ‹‹RAB seni kendi halkı İsrailin Kralı olarak meshetmek için beni gönderdi. Şimdi RABbin sözlerine kulak ver."));
trsuperobject.biblemap.put("b9_15_2", new trsuperobject(9,15,2,"Her Şeye Egemen RAB diyor ki, ‹İsraillilere yaptıkları kötülükten ötürü Amaleklileri cezalandıracağım. Çünkü Mısırdan çıkan İsraillilere karşı koydular."));
trsuperobject.biblemap.put("b9_15_3", new trsuperobject(9,15,3,"Şimdi git, Amaleklilere saldır. Onlara ait her şeyi tümüyle yok et, hiçbir şeyi esirgeme. Kadın erkek, çoluk çocuk, öküz, koyun, deve, eşek hepsini öldür.›› bir daha alınmamak üzere RABbe adamak anlamını içerir. İbranicede aynı ifade 8. ve 21. ayetlerde de geçer."));
trsuperobject.biblemap.put("b9_15_4", new trsuperobject(9,15,4,"Bunun üzerine Saul askerlerini toplayıp Telaim Kentinde saydı. İki yüz bin yaya askerin yanısıra Yahudalılardan da on bin kişi vardı."));
trsuperobject.biblemap.put("b9_15_5", new trsuperobject(9,15,5,"Saul Amalek Kentine varıp vadide pusu kurdu."));
trsuperobject.biblemap.put("b9_15_6", new trsuperobject(9,15,6,"Sonra Kenlilere şu uyarıyı gönderdi: ‹‹Haydi gidin, Amaleklileri bırakın; öyle ki, sizi de onlarla birlikte yok etmeyeyim. Çünkü siz Mısırdan çıkan İsrail halkına iyilik ettiniz.›› Bunun üzerine Kenliler Amaleklilerden ayrıldılar."));
trsuperobject.biblemap.put("b9_15_7", new trsuperobject(9,15,7,"Saul Haviladan Mısırın doğusundaki Şura dek Amaleklileri yenilgiye uğrattı."));
trsuperobject.biblemap.put("b9_15_8", new trsuperobject(9,15,8,"Amalek Kralı Agakı sağ olarak yakaladı. Halkının tümünü de kılıçtan geçirdi."));
trsuperobject.biblemap.put("b9_15_9", new trsuperobject(9,15,9,"Ne var ki, Saul ile adamları Agakı ve en iyi koyunları, sığırları, besili danaları, kuzuları -iyi olan ne varsa hepsini- esirgediler. Bunları tümüyle yok etmek istemediler. Ancak değersiz ve zayıf ne varsa hepsini yok ettiler."));
trsuperobject.biblemap.put("b9_15_10", new trsuperobject(9,15,10,"RAB Samuele şöyle seslendi:"));
trsuperobject.biblemap.put("b9_15_11", new trsuperobject(9,15,11,"‹‹Saulu kral yaptığıma pişmanım. Beni izlemekten vazgeçti. Buyruklarımı yerine getirmedi.›› Samuel öfkelendi ve bütün geceyi RABbe yakarmakla geçirdi."));
trsuperobject.biblemap.put("b9_15_12", new trsuperobject(9,15,12,"Ertesi sabah Samuel Saulla görüşmek için erkenden kalktı. Saulun Karmel Kentine gittiğini, orada kendisine bir anıt diktikten sonra aşağı inip Gilgala döndüğünü öğrendi."));
trsuperobject.biblemap.put("b9_15_13", new trsuperobject(9,15,13,"Saul kendisine gelen Samuele, ‹‹RAB seni kutsasın! Ben RABbin buyruğunu yerine getirdim›› dedi."));
trsuperobject.biblemap.put("b9_15_14", new trsuperobject(9,15,14,"Samuel, ‹‹Öyleyse nedir kulağıma gelen bu koyun melemesi? Nedir bu duyduğum sığır böğürmesi?›› diye sordu."));
trsuperobject.biblemap.put("b9_15_15", new trsuperobject(9,15,15,"Saul şöyle yanıtladı: ‹‹Halk bunları Amaleklilerden getirdi. Tanrın RABbe kurban sunmak üzere davarların, sığırların en iyilerini esirgediler. Ama geri kalanları tümüyle yok ettik.››"));
trsuperobject.biblemap.put("b9_15_16", new trsuperobject(9,15,16,"Samuel, ‹‹Dur da bu gece RABbin bana neler söylediğini sana bildireyim›› dedi. Saul, ‹‹Söyle›› diye karşılık verdi."));
trsuperobject.biblemap.put("b9_15_17", new trsuperobject(9,15,17,"Samuel konuşmasını şöyle sürdürdü: ‹‹Kendini önemsiz saydığın halde, sen İsrail oymaklarının önderi olmadın mı? RAB seni İsraile kral meshetti."));
trsuperobject.biblemap.put("b9_15_18", new trsuperobject(9,15,18,"RAB seni bir göreve gönderip, ‹Git, o günahlı Amaleklileri tümüyle yok et; hepsini ortadan kaldırıncaya dek onlarla savaş› dedi."));
trsuperobject.biblemap.put("b9_15_19", new trsuperobject(9,15,19,"Öyleyse neden RABbin sözüne kulak asmadın? Neden yağmalanan mallara saldırarak RABbin gözünde kötü olanı yaptın?››"));
trsuperobject.biblemap.put("b9_15_20", new trsuperobject(9,15,20,"Saul, ‹‹Ama ben RABbin sözüne kulak verdim!›› diye yanıtladı, ‹‹RABbin beni gönderdiği yere gittim. Amaleklileri tümüyle yok ettim, Amalek Kralı Agakı da buraya getirdim."));
trsuperobject.biblemap.put("b9_15_21", new trsuperobject(9,15,21,"Ne var ki askerler, Gilgalda Tanrın RABbe kurban sunmak üzere yağmalanmış bazı malları, yok edilmeye adanmış en iyi davarlarla sığırları aldılar.››"));
trsuperobject.biblemap.put("b9_15_22", new trsuperobject(9,15,22,"Samuel şöyle karşılık verdi:  ‹‹RAB kendi sözünün dinlenmesinden hoşlandığı kadarYakmalık sunulardan, kurbanlardan hoşlanır mı?İşte söz dinlemek kurbandan,Sözü önemsemek de koçların yağlarından daha iyidir."));
trsuperobject.biblemap.put("b9_15_23", new trsuperobject(9,15,23,"Çünkü başkaldırma, falcılık kadar günahtırVe dikbaşlılık, putperestlikfç kadar kötüdür.Sen RABbin buyruğunu reddettiğin için,RAB de senin kral olmanı reddetti.›› danışmak››."));
trsuperobject.biblemap.put("b9_15_24", new trsuperobject(9,15,24,"Bunun üzerine Saul, ‹‹Günah işledim! Evet, RABbin buyruğunu da, senin sözlerini de çiğnedim›› dedi, ‹‹Halktan korktuğum için onların sözünü dinledim."));
trsuperobject.biblemap.put("b9_15_25", new trsuperobject(9,15,25,"Ama şimdi yalvarırım, günahımı bağışla ve benimle birlikte dön ki, RABbe tapınayım.››"));
trsuperobject.biblemap.put("b9_15_26", new trsuperobject(9,15,26,"Samuel, ‹‹Seninle dönmem›› dedi, ‹‹Çünkü sen RABbin buyruğunu reddettin, RAB de İsrail Kralı olmanı reddetti!››"));
trsuperobject.biblemap.put("b9_15_27", new trsuperobject(9,15,27,"Samuel dönüp gitmeye davranınca, Saul onun cüppesinin eteğini tuttu. Cüppe yırtıldı."));
trsuperobject.biblemap.put("b9_15_28", new trsuperobject(9,15,28,"Samuel, ‹‹Bugün RAB İsrail Krallığını elinden aldı ve senden daha iyi birine verdi›› dedi,"));
trsuperobject.biblemap.put("b9_15_29", new trsuperobject(9,15,29,"‹‹İsrailin yüce Tanrısı yalan söylemez, düşüncesini de değiştirmez. Çünkü O insan değil ki, düşüncesini değiştirsin.››"));
trsuperobject.biblemap.put("b9_15_30", new trsuperobject(9,15,30,"Saul, ‹‹Günah işledim!›› dedi, ‹‹Ama ne olur halkımın ileri gelenleri ve İsrailliler karşısında beni onurlandır. Tanrın RABbe tapınmam için benimle dön.››"));
trsuperobject.biblemap.put("b9_15_31", new trsuperobject(9,15,31,"Böylece Samuel Saulla birlikte geri döndü ve Saul RABbe tapındı."));
trsuperobject.biblemap.put("b9_15_32", new trsuperobject(9,15,32,"Samuel, ‹‹Amalek Kralı Agakı bana getirin›› diye buyurdu. Agak güvenle geldi. Çünkü, ‹‹Ölüm tehlikesi kesinlikle geçti›› diye düşünüyordu."));
trsuperobject.biblemap.put("b9_15_33", new trsuperobject(9,15,33,"Ama Samuel,  ‹‹Kılıcın kadınları nasıl çocuksuz bıraktıysaSenin annen de kadınlar arasındaÇocuksuz bırakılacak››"));
trsuperobject.biblemap.put("b9_15_34", new trsuperobject(9,15,34,"Samuel Ramaya, Saul da Givadaki evine gitti."));
trsuperobject.biblemap.put("b9_15_35", new trsuperobject(9,15,35,"Samuel ölümüne dek Saul'u bir daha görmediyse de, onun için üzüldü. RAB de Saul'u İsrail Kralı yaptığına pişmandı. Givası'ndaki››."));
trsuperobject.biblemap.put("b9_16_2", new trsuperobject(9,16,2,"Samuel, ‹‹Nasıl gidebilirim? Saul bunu duyarsa beni öldürür!›› dedi. RAB şöyle yanıtladı: ‹‹Yanına bir düve al ve, ‹RABbe kurban sunmak için geldim› de."));
trsuperobject.biblemap.put("b9_16_3", new trsuperobject(9,16,3,"İşayı kurban törenine çağır. O zaman ne yapman gerektiğini ben sana bildireceğim. Sana belirteceğim kişiyi benim adıma kral olarak meshedeceksin.››"));
trsuperobject.biblemap.put("b9_16_4", new trsuperobject(9,16,4,"Samuel RABbin sözüne uyarak Beytlehem Kentine gitti. Kentin ileri gelenleri onu titreyerek karşıladılar ve, ‹‹Barış için mi geldin?›› diye sordular."));
trsuperobject.biblemap.put("b9_16_5", new trsuperobject(9,16,5,"Samuel, ‹‹Evet, barış için›› diye yanıtladı, ‹‹RABbe kurban sunmaya geldim. Kendinizi kutsayıp benimle birlikte kurban törenine gelin.›› Sonra İşay ile oğullarını kutsayıp kurban törenine çağırdı."));
trsuperobject.biblemap.put("b9_16_6", new trsuperobject(9,16,6,"İşay ile oğulları gelince Samuel Eliavı gördü ve, ‹‹Gerçekten RABbin önünde duran bu adam Onun meshettiği kişidir›› diye düşündü."));
trsuperobject.biblemap.put("b9_16_7", new trsuperobject(9,16,7,"Ama RAB Samuele, ‹‹Onun yakışıklı ve uzun boylu olduğuna bakma›› dedi, ‹‹Ben onu reddettim. Çünkü RAB insanın gördüğü gibi görmez; insan dış görünüşe, RAB ise yüreğe bakar.››"));
trsuperobject.biblemap.put("b9_16_8", new trsuperobject(9,16,8,"İşay, oğlu Avinadavı çağırıp Samuelin önünden geçirdi. Ama Samuel, ‹‹RAB bunu da seçmedi›› dedi."));
trsuperobject.biblemap.put("b9_16_9", new trsuperobject(9,16,9,"Bunun üzerine İşay Şammayı da geçirdi. Samuel yine, ‹‹RAB bunu da seçmedi›› dedi."));
trsuperobject.biblemap.put("b9_16_10", new trsuperobject(9,16,10,"Böylece İşay yedi oğlunu da Samuelin önünden geçirdi. Ama Samuel, ‹‹RAB bunlardan hiçbirini seçmedi›› dedi."));
trsuperobject.biblemap.put("b9_16_11", new trsuperobject(9,16,11,"Sonra İşaya, ‹‹Oğullarının hepsi bunlar mı?›› diye sordu. İşay, ‹‹Bir de en küçüğü var›› dedi, ‹‹Sürüyü güdüyor.›› Samuel, ‹‹Birini gönder de onu getirsin›› dedi, ‹‹O buraya gelmeden yemeğe oturmayacağız.››"));
trsuperobject.biblemap.put("b9_16_12", new trsuperobject(9,16,12,"İşay birini gönderip oğlunu getirtti. Çocuk kızıl saçlı, yakışıklı, gözleri pırıl pırıl bir delikanlıydı. RAB Samuele, ‹‹Kalk, onu meshet. Seçtiğim kişi odur›› dedi."));
trsuperobject.biblemap.put("b9_16_13", new trsuperobject(9,16,13,"Samuel yağ boynuzunu alıp kardeşlerinin önünde çocuğu meshetti. O günden başlayarak RABbin Ruhu Davutun üzerine güçlü bir biçimde indi. Bundan sonra Samuel kalkıp Ramaya döndü."));
trsuperobject.biblemap.put("b9_16_14", new trsuperobject(9,16,14,"Bu sıralarda RABbin Ruhu Sauldan ayrılmıştı. RABbin gönderdiği kötü bir ruh ona sıkıntı çektiriyordu."));
trsuperobject.biblemap.put("b9_16_15", new trsuperobject(9,16,15,"Hizmetkârları Saula, ‹‹Bak, Tanrının gönderdiği kötü bir ruh sana sıkıntı çektiriyor›› dediler,"));
trsuperobject.biblemap.put("b9_16_16", new trsuperobject(9,16,16,"‹‹Efendimiz, biz hizmetkârlarına buyruk ver, iyi lir çalan birini bulalım. Öyle ki, Tanrının gönderdiği kötü ruh üzerine gelince, o lir çalar, sen de rahatlarsın.››"));
trsuperobject.biblemap.put("b9_16_17", new trsuperobject(9,16,17,"Saul hizmetkârlarına, ‹‹İyi lir çalan birini bulup bana getirin›› diye buyurdu."));
trsuperobject.biblemap.put("b9_16_18", new trsuperobject(9,16,18,"Hizmetkârlardan biri, ‹‹Beytlehemli İşayın oğullarından birini gördüm›› dedi, ‹‹İyi lir çalar. Üstelik yürekli, güçlü bir savaşçıdır; akıllıca konuşur, yakışıklıdır. RAB de onunladır.››"));
trsuperobject.biblemap.put("b9_16_19", new trsuperobject(9,16,19,"Bunun üzerine Saul İşaya ulaklar göndererek, ‹‹Sürüyü güden oğlun Davutu bana gönder›› dedi."));
trsuperobject.biblemap.put("b9_16_20", new trsuperobject(9,16,20,"İşay ekmek yüklü bir eşek, bir tulum şarap, bir de oğlak alıp oğlu Davutla birlikte Saula gönderdi."));
trsuperobject.biblemap.put("b9_16_21", new trsuperobject(9,16,21,"Davut Saulun yanına varıp onun hizmetine girdi. Saul Davutu çok sevdi ve ona silahlarını taşıma görevini verdi."));
trsuperobject.biblemap.put("b9_16_22", new trsuperobject(9,16,22,"Saul İşaya şu haberi gönderdi: ‹‹İzin ver de Davut hizmetimde kalsın; ondan hoşnudum.››"));
trsuperobject.biblemap.put("b9_16_23", new trsuperobject(9,16,23,"O günden sonra, Tanrı'nın gönderdiği kötü ruh ne zaman Saul'un üzerine gelse, Davut liri alıp çalar, Saul rahatlayıp kendine gelirdi. Kötü ruh da ondan uzaklaşırdı."));
trsuperobject.biblemap.put("b9_17_1", new trsuperobject(9,17,1,"Savaşmak üzere ordularını bir araya getiren Filistliler, Yahudanın Soko Kentinde toplandılar. Soko ile Azeka Kenti arasındaki Efes-Dammimde ordugah kurdular."));
trsuperobject.biblemap.put("b9_17_2", new trsuperobject(9,17,2,"Saul ile İsrailliler de toplandılar. Ela Vadisinde ordugah kurup Filistlilere karşı savaş düzeni aldılar."));
trsuperobject.biblemap.put("b9_17_3", new trsuperobject(9,17,3,"Filistliler tepenin bir yanında, İsrailliler de karşı tepede yerlerini aldı. Aralarında vadi vardı."));
trsuperobject.biblemap.put("b9_17_4", new trsuperobject(9,17,4,"Filist ordugahından Gatlı Golyat adında usta bir dövüşçü ortaya çıktı. Boyu altı arşın bir karıştı."));
trsuperobject.biblemap.put("b9_17_5", new trsuperobject(9,17,5,"Başına tunç miğfer takmış, pullu bir zırh kuşanmıştı. Tunç zırhın ağırlığı beş bin şekeldi."));
trsuperobject.biblemap.put("b9_17_6", new trsuperobject(9,17,6,"Baldırları zırhlarla korunmuştu. Omuzları arasında tunç bir pala asılıydı."));
trsuperobject.biblemap.put("b9_17_7", new trsuperobject(9,17,7,"Mızrağının sapı dokumacı tezgahının sırığı gibiydi. Mızrağın demir başının ağırlığı altı yüz şekeldi. Golyatın önüsıra kalkanını taşıyan bir adam yürüyordu."));
trsuperobject.biblemap.put("b9_17_8", new trsuperobject(9,17,8,"Golyat durup İsrail ordusuna, ‹‹Neden savaş düzeni aldınız?›› diye haykırdı, ‹‹Ben Filistliyim, sizse Saulun kölelerisiniz. Aranızdan karşıma çıkacak birini seçin."));
trsuperobject.biblemap.put("b9_17_9", new trsuperobject(9,17,9,"Dövüşte beni yenip öldürebilirse, biz sizin köleniz oluruz. Ama ben üstün gelip onu yok edebilirsem, siz bizim kölemiz olur, bize kulluk edersiniz.››"));
trsuperobject.biblemap.put("b9_17_10", new trsuperobject(9,17,10,"Filistli Golyat konuşmasını şöyle sürdürdü: ‹‹Bugün İsrail ordusuna meydan okuyorum! Benimle dövüşecek birini çıkarın karşıma!››"));
trsuperobject.biblemap.put("b9_17_11", new trsuperobject(9,17,11,"Filistlinin bu sözlerini duyunca, Saul da İsrailliler de çok korkup dehşet içinde kaldılar."));
trsuperobject.biblemap.put("b9_17_12", new trsuperobject(9,17,12,"Davut Yahudanın Beytlehem Kentinden Efratlı İşay adında bir adamın oğluydu. İşayın sekiz oğlu vardı. Saulun krallığı döneminde İşayın yaşı oldukça ilerlemişti."));
trsuperobject.biblemap.put("b9_17_13", new trsuperobject(9,17,13,"İşayın üç büyük oğlu Saulla birlikte savaşa katılmıştı. Savaşa giden en büyük oğlunun adı Eliav, ikincisinin adı Avinadav, üçüncüsünün adıysa Şammaydı."));
trsuperobject.biblemap.put("b9_17_14", new trsuperobject(9,17,14,"Davut en küçükleriydi. Üç büyük oğul Saulun yanındaydı."));
trsuperobject.biblemap.put("b9_17_15", new trsuperobject(9,17,15,"Davut ise babasının sürüsüne bakmak için Saulun yanından ayrılıp Beytleheme gider gelirdi."));
trsuperobject.biblemap.put("b9_17_16", new trsuperobject(9,17,16,"Filistli Golyat kırk gün boyunca sabah akşam ortaya çıkıp meydan okudu."));
trsuperobject.biblemap.put("b9_17_17", new trsuperobject(9,17,17,"Bir gün İşay, oğlu Davuta şöyle dedi: ‹‹Kardeşlerin için şu kavrulmuş bir efa buğdayla on somun ekmeği al, çabucak ordugaha, kardeşlerinin yanına git."));
trsuperobject.biblemap.put("b9_17_18", new trsuperobject(9,17,18,"Şu on parça peyniri de birlik komutanına götür. Kardeşlerinin ne durumda olduğunu öğren ve iyi olduklarına ilişkin bir belirti getir."));
trsuperobject.biblemap.put("b9_17_19", new trsuperobject(9,17,19,"Kardeşlerin Saul ve öbür İsraillilerle birlikte Ela Vadisinde Filistlilere karşı savaşıyorlar.››"));
trsuperobject.biblemap.put("b9_17_20", new trsuperobject(9,17,20,"Ertesi sabah Davut erkenden kalktı. Sürüyü bir çobana bıraktı. İşayın buyurduğu gibi erzağı alıp yola koyuldu. Ordugaha vardığı sırada askerler savaş naraları atarak savaş düzenine giriyorlardı."));
trsuperobject.biblemap.put("b9_17_21", new trsuperobject(9,17,21,"İsraillilerle Filistliler karşı karşıya savaş düzeni almışlardı."));
trsuperobject.biblemap.put("b9_17_22", new trsuperobject(9,17,22,"Davut getirdiklerini levazım görevlisine bırakıp cepheye koştu; kardeşlerinin yanına varıp onları selamladı."));
trsuperobject.biblemap.put("b9_17_23", new trsuperobject(9,17,23,"Davut onlarla konuşurken, Gatlı Filistli, Golyat adındaki dövüşçü Filist cephesinden ileri çıkarak daha önce yaptığı gibi meydan okudu. Davut bunu duydu."));
trsuperobject.biblemap.put("b9_17_24", new trsuperobject(9,17,24,"İsrailliler Golyatı görünce büyük korkuyla önünden kaçıştılar."));
trsuperobject.biblemap.put("b9_17_25", new trsuperobject(9,17,25,"Birbirlerine, ‹‹İsraile meydan okumak için ortaya çıkan şu adamı görüyorsunuz ya!›› diyorlardı, ‹‹Kral onu öldürene büyük bir armağanın yanısıra kızını da verecek. Babasının ailesini de İsraile vergi ödemekten muaf tutacak.››"));
trsuperobject.biblemap.put("b9_17_26", new trsuperobject(9,17,26,"Davut yanındakilere, ‹‹Bu Filistliyi öldürüp İsrailden bu utancı kaldıracak kişiye ne verilecek?›› diye sordu, ‹‹Bu sünnetsiz Filistli kim oluyor da yaşayan Tanrının ordusuna meydan okuyor?››"));
trsuperobject.biblemap.put("b9_17_27", new trsuperobject(9,17,27,"Adamlar daha önce verilmiş olan söze göre Golyatı öldürecek kişiye neler verileceğini anlattılar."));
trsuperobject.biblemap.put("b9_17_28", new trsuperobject(9,17,28,"Ağabeyi Eliav Davutun adamlarla konuştuğunu duyunca öfkelendi. ‹‹Ne işin var burada?›› dedi, ‹‹Çöldeki üç beş koyunu kime bıraktın? Ne kadar kendini beğenmiş ve ne kadar kötü yürekli olduğunu biliyorum. Sadece savaşı görmeye geldin.››"));
trsuperobject.biblemap.put("b9_17_29", new trsuperobject(9,17,29,"Davut, ‹‹Ne yaptım ki?›› dedi, ‹‹Bir soru sordum, o kadar.››"));
trsuperobject.biblemap.put("b9_17_30", new trsuperobject(9,17,30,"Sonra başka birine dönüp aynı soruyu sordu. Adamlar öncekine benzer bir yanıt verdiler."));
trsuperobject.biblemap.put("b9_17_31", new trsuperobject(9,17,31,"Davutun söylediklerini duyanlar Saula ilettiler. Saul onu çağırttı."));
trsuperobject.biblemap.put("b9_17_32", new trsuperobject(9,17,32,"Davut Saula, ‹‹Bu Filistli yüzünden kimse yılmasın! Ben kulun gidip onunla dövüşeceğim!›› dedi."));
trsuperobject.biblemap.put("b9_17_33", new trsuperobject(9,17,33,"Saul, ‹‹Sen bu Filistliyle dövüşemezsin›› dedi, ‹‹Çünkü daha gençsin, o ise gençliğinden beri savaşçıdır.››"));
trsuperobject.biblemap.put("b9_17_34", new trsuperobject(9,17,34,"Ama Davut, ‹‹Kulun babasının sürüsünü güder›› diye karşılık verdi, ‹‹Bir aslan ya da ayı gelip sürüden bir kuzu kaçırınca,"));
trsuperobject.biblemap.put("b9_17_35", new trsuperobject(9,17,35,"peşinden gidip ona saldırır, kuzuyu ağzından kurtarırım. Eğer aslan ya da ayı üzerime gelirse, boğazından tuttuğum gibi vurur öldürürüm."));
trsuperobject.biblemap.put("b9_17_36", new trsuperobject(9,17,36,"Kulun, aslan da ayı da öldürmüştür. Bu sünnetsiz Filistli de onlar gibi olacak. Çünkü yaşayan Tanrının ordusuna meydan okudu."));
trsuperobject.biblemap.put("b9_17_37", new trsuperobject(9,17,37,"Beni aslanın, ayının pençesinden kurtaran RAB, bu Filistlinin elinden de kurtaracaktır.›› Saul, ‹‹Öyleyse git, RAB seninle birlikte olsun›› dedi."));
trsuperobject.biblemap.put("b9_17_38", new trsuperobject(9,17,38,"Sonra kendi giysilerini Davuta verdi; başına tunç miğfer taktı, ona bir zırh giydirdi."));
trsuperobject.biblemap.put("b9_17_39", new trsuperobject(9,17,39,"Davut giysilerinin üzerine kılıcını kuşanıp yürümeye çalıştı. Çünkü bu giysilere alışık değildi. Saula, ‹‹Bunlarla yürüyemiyorum›› dedi, ‹‹Çünkü alışık değilim.›› Sonra giysileri üzerinden çıkardı."));
trsuperobject.biblemap.put("b9_17_40", new trsuperobject(9,17,40,"Değneğini alıp dereden beş çakıl taşı seçti. Bunları çoban dağarcığının cebine koyduktan sonra sapanını alıp Filistli Golyata doğru ilerledi."));
trsuperobject.biblemap.put("b9_17_41", new trsuperobject(9,17,41,"Filistli de, önünde kalkan taşıyıcısı, Davuta doğru ilerliyordu."));
trsuperobject.biblemap.put("b9_17_42", new trsuperobject(9,17,42,"Davutu tepeden tırnağa süzdü. Kızıl saçlı, yakışıklı bir genç olduğu için onu küçümsedi."));
trsuperobject.biblemap.put("b9_17_43", new trsuperobject(9,17,43,"‹‹Ben köpek miyim ki, üzerime değnekle geliyorsun?›› diyerek kendi ilahlarının adıyla Davutu lanetledi."));
trsuperobject.biblemap.put("b9_17_44", new trsuperobject(9,17,44,"‹‹Bana gelsene! Bedenini gökteki kuşlara ve kırdaki hayvanlara yem edeceğim!›› dedi."));
trsuperobject.biblemap.put("b9_17_45", new trsuperobject(9,17,45,"Davut, ‹‹Sen kılıçla, mızrakla, palayla üzerime geliyorsun›› diye karşılık verdi, ‹‹Bense meydan okuduğun İsrail ordusunun Tanrısı, Her Şeye Egemen RABbin adıyla senin üzerine geliyorum."));
trsuperobject.biblemap.put("b9_17_46", new trsuperobject(9,17,46,"Bugün RAB seni elime teslim edecek. Seni vurup başını gövdenden ayıracağım. Bugün Filistli askerlerin leşlerini gökteki kuşlarla yerdeki hayvanlara yem edeceğim. Böylece bütün dünya İsrailde Tanrının var olduğunu anlayacak."));
trsuperobject.biblemap.put("b9_17_47", new trsuperobject(9,17,47,"Bütün bu topluluk RABbin kılıçla, mızrakla kurtarmadığını anlayacak. Çünkü savaş zaten RABbindir! O sizi elimize teslim edecek.››"));
trsuperobject.biblemap.put("b9_17_48", new trsuperobject(9,17,48,"Golyat saldırmak amacıyla Davuta doğru ilerledi. Davut da onunla dövüşmek üzere hemen Filist cephesine doğru koştu."));
trsuperobject.biblemap.put("b9_17_49", new trsuperobject(9,17,49,"Elini dağarcığına sokup bir taş çıkardı, sapanla fırlattı. Taş Filistlinin alnına çarpıp saplandı. Filistli yüzükoyun yere düştü."));
trsuperobject.biblemap.put("b9_17_50", new trsuperobject(9,17,50,"Böylece Davut Filistli Golyatı sapan ve taşla yendi. Elinde kılıç olmaksızın onu yere serdi."));
trsuperobject.biblemap.put("b9_17_51", new trsuperobject(9,17,51,"Sonra koşup üzerine çıktı. Golyatın kılıcını tutup kınından çektiği gibi onu öldürdü ve başını kesti. Kahraman Golyatın öldüğünü gören Filistliler kaçtılar."));
trsuperobject.biblemap.put("b9_17_52", new trsuperobject(9,17,52,"İsraillilerle Yahudalılar kalkıp Gatınfı girişine ve Ekron kapılarına kadar nara atarak onları kovaladılar. Filistlilerin ölüleri Gata, Ekrona kadar Şaarayim yolunda yerlere serildi."));
trsuperobject.biblemap.put("b9_17_53", new trsuperobject(9,17,53,"Filistlileri kovaladıktan sonra geri dönen İsrailliler Filist ordugahını yağmaladılar."));
trsuperobject.biblemap.put("b9_17_54", new trsuperobject(9,17,54,"Davut Filistli Golyatın başını alıp Yeruşalime götürdü, silahlarını da kendi çadırına koydu."));
trsuperobject.biblemap.put("b9_17_55", new trsuperobject(9,17,55,"Saul, Davutun Golyatla dövüşmeye çıktığını görünce, ordu komutanı Avnere, ‹‹Ey Avner, kimin oğlu bu genç?›› diye sormuştu. Avner de, ‹‹Yaşamın hakkı için, ey kral, bilmiyorum›› diye yanıtlamıştı."));
trsuperobject.biblemap.put("b9_17_56", new trsuperobject(9,17,56,"Kral Saul, ‹‹Bu gencin kimin oğlu olduğunu öğren›› diye buyurmuştu."));
trsuperobject.biblemap.put("b9_17_57", new trsuperobject(9,17,57,"Davut Golyatı öldürüp ordugaha döner dönmez, Avner onu alıp Saula götürdü. Golyatın kesik başı Davutun elindeydi."));
trsuperobject.biblemap.put("b9_17_58", new trsuperobject(9,17,58,"Saul, ‹‹Kimin oğlusun, delikanlı?›› diye sordu. Davut, ‹‹Kulun Beytlehemli İşay'ın oğluyum›› diye karşılık verdi."));
trsuperobject.biblemap.put("b9_18_1", new trsuperobject(9,18,1,"Saulla Davutun konuşması sona erdiğinde, Saul oğlu Yonatanın yüreği Davuta bağlandı. Yonatan onu canı gibi sevdi."));
trsuperobject.biblemap.put("b9_18_2", new trsuperobject(9,18,2,"O günden sonra Saul Davutu yanında tuttu ve babasının evine dönmesine izin vermedi."));
trsuperobject.biblemap.put("b9_18_3", new trsuperobject(9,18,3,"Yonatan, Davuta beslediği derin sevgiden ötürü, onunla bir dostluk antlaşması yaptı."));
trsuperobject.biblemap.put("b9_18_4", new trsuperobject(9,18,4,"Üzerinden kaftanını çıkarıp zırhı, kılıcı, yayı ve kuşağıyla birlikte Davuta verdi."));
trsuperobject.biblemap.put("b9_18_5", new trsuperobject(9,18,5,"Davut Saulun kendisini gönderdiği her yere gitti ve başarılı oldu. Bu yüzden Saul ona ordusunda üstün bir rütbe verdi. Bu olay bütün halkı, Saulun görevlilerini bile hoşnut etti."));
trsuperobject.biblemap.put("b9_18_6", new trsuperobject(9,18,6,"Davutun Filistli Golyatı öldürmesinden sonra, askerler geri dönerken, İsrailin bütün kentlerinden gelen kadınlar, tef ve çeşitli çalgılar çalarak, sevinçli ezgiler söyleyip oynayarak Kral Saulu karşılamaya çıktılar."));
trsuperobject.biblemap.put("b9_18_7", new trsuperobject(9,18,7,"Bir yandan oynuyor, bir yandan da şu ezgiyi söylüyorlardı:  ‹‹Saul binlercesini öldürdü,Davutsa on binlercesini.››"));
trsuperobject.biblemap.put("b9_18_8", new trsuperobject(9,18,8,"Bu sözlere gücenen Saul çok öfkelendi. ‹‹Davuta on binlercesini, banaysa ancak binlercesini verdiler. Artık kral olmaktan başka onun ne eksiği kaldı ki?›› diye düşündü."));
trsuperobject.biblemap.put("b9_18_9", new trsuperobject(9,18,9,"Böylece o günden sonra Saul Davutu kıskanmaya başladı."));
trsuperobject.biblemap.put("b9_18_10", new trsuperobject(9,18,10,"Ertesi gün Tanrının gönderdiği kötü bir ruh Saulun üzerine güçlü bir biçimde indi. Saul evinde sayıklamaya başladı. Davut her zamanki gibi yine lir çalıyordu. Saulun elinde bir mızrak vardı."));
trsuperobject.biblemap.put("b9_18_11", new trsuperobject(9,18,11,"‹‹Davutu vurup duvara çakacağım›› diye düşünerek mızrağı ona fırlattı. Ama Davut iki kez ondan kurtuldu."));
trsuperobject.biblemap.put("b9_18_12", new trsuperobject(9,18,12,"Saul Davuttan korkuyordu. Çünkü RAB Davutlaydı, oysa kendisinden ayrılmıştı."));
trsuperobject.biblemap.put("b9_18_13", new trsuperobject(9,18,13,"Bu yüzden Saul Davutu yanından uzaklaştırdı. Onu bin kişilik birliğe komutan atadı. Davut askerlere öncülük yapıyordu."));
trsuperobject.biblemap.put("b9_18_14", new trsuperobject(9,18,14,"RAB onunla birlikte olduğundan, yaptığı her işte başarılıydı."));
trsuperobject.biblemap.put("b9_18_15", new trsuperobject(9,18,15,"Davutun büyük başarısını gördükçe Saulun korkusu daha da artıyordu."));
trsuperobject.biblemap.put("b9_18_16", new trsuperobject(9,18,16,"Ne var ki, bütün İsrail ve Yahuda halkı Davutu seviyordu; çünkü Davut onlara öncülük ediyordu."));
trsuperobject.biblemap.put("b9_18_17", new trsuperobject(9,18,17,"Saul Davuta, ‹‹İşte büyük kızım Merav›› dedi, ‹‹Onu sana eş olarak vereceğim. Yalnız hatırım için yiğitçe davran ve RABbin savaşlarını sürdür.›› Çünkü, ‹‹Davutun ölümü benim elimden değil, Filistlilerin elinden olsun›› diye düşünüyordu."));
trsuperobject.biblemap.put("b9_18_18", new trsuperobject(9,18,18,"Davut, ‹‹Ben kim oluyorum, İsrailde ailem ve babamın oymağı ne ki, krala damat olayım?›› diye karşılık verdi."));
trsuperobject.biblemap.put("b9_18_19", new trsuperobject(9,18,19,"Ne var ki, Saulun kızı Meravın Davuta verileceği zaman geldiğinde, kız Davut yerine Meholalı Adriele eş olarak verildi."));
trsuperobject.biblemap.put("b9_18_20", new trsuperobject(9,18,20,"Bu arada Saulun öbür kızı Mikal Davuta gönül vermişti. Bunu duyan Saul sevindi."));
trsuperobject.biblemap.put("b9_18_21", new trsuperobject(9,18,21,"‹‹Davuta Mikalı veririm›› diye düşündü, ‹‹Öyle ki, Mikal Davutu tuzağa düşürür; Filistliler de onu öldürür.›› Davuta, ‹‹Bugün damadım olmak için yine fırsatın var›› dedi."));
trsuperobject.biblemap.put("b9_18_22", new trsuperobject(9,18,22,"Sonra görevlilerine, Davuta gizlice şunları söylemelerini buyurdu: ‹‹Bak, kral senden hoşnut, bütün görevlileri de seni seviyor. Kralın damadı olmanın zamanı geldi.››"));
trsuperobject.biblemap.put("b9_18_23", new trsuperobject(9,18,23,"Saulun görevlileri bu sözleri Davuta ilettiler. Davut, ‹‹Yoksul ve önemsiz biriyken kralın damadı olmak sizce küçük bir şey mi?›› diye karşılık verdi."));
trsuperobject.biblemap.put("b9_18_24", new trsuperobject(9,18,24,"Görevliler Davutun dediklerini Saula bildirdiler."));
trsuperobject.biblemap.put("b9_18_25", new trsuperobject(9,18,25,"Saul şöyle buyurdu: ‹‹Davuta deyin ki, ‹Kral düşmanlarından öç almak için başlık parası olarak yüz Filistlinin sünnet derisinden başka bir şey istemiyor.›› Davutun Filistlilerin eline düşüp öleceğini tasarlıyordu."));
trsuperobject.biblemap.put("b9_18_26", new trsuperobject(9,18,26,"Görevliler Saulun söylediklerini Davuta ilettiler. Davut, kralın damadı olacağına sevindi. Tanınan süre dolmadan"));
trsuperobject.biblemap.put("b9_18_27", new trsuperobject(9,18,27,"Davutla adamları gidip iki yüz Filistli öldürdüler. Kralın damadı olabilmek için Davut, öldürülen Filistlilerin sünnet derilerini tam tamına getirip krala sundu. Saul da buna karşılık kızı Mikalı eş olarak ona verdi."));
trsuperobject.biblemap.put("b9_18_28", new trsuperobject(9,18,28,"Saul, RABbin Davutla birlikte olduğunu ve kızı Mikalın onu sevdiğini apaçık gördü."));
trsuperobject.biblemap.put("b9_18_29", new trsuperobject(9,18,29,"Bu yüzden Davuttan daha çok korktu ve yaşamı boyunca ona düşmanlık besledi."));
trsuperobject.biblemap.put("b9_18_30", new trsuperobject(9,18,30,"Filistli komutanlar saldırdıkça Davut Saul'un öbür komutanlarından daha başarılı oluyordu. Bu yüzden büyük bir üne kavuştu."));
trsuperobject.biblemap.put("b9_19_1-2", new trsuperobject(9,19,1-2,"Saul, oğlu Yonatana ve bütün görevlilerine Davutu öldürmeleri için buyruk verdi. Ama Davutu çok seven Yonatan ona, ‹‹Babam Saul seni öldürmek için fırsat kolluyor›› diye haber verdi, ‹‹Lütfen yarın sabah dikkatli ol; gizlenebileceğin bir yere gidip saklan."));
trsuperobject.biblemap.put("b9_19_3", new trsuperobject(9,19,3,"Ben de saklandığın tarlaya gidip babamın yanında duracağım ve onunla senin hakkında konuşacağım. Bir şey öğrenirsem, sana bildiririm.››"));
trsuperobject.biblemap.put("b9_19_4", new trsuperobject(9,19,4,"Yonatan babası Saula Davutu överek şunları söyledi: ‹‹Kral kulu Davuta haksızlık etmesin. Çünkü o sana hiç haksızlık etmedi ve yaptığı her şeyde sana büyük yararı dokundu."));
trsuperobject.biblemap.put("b9_19_5", new trsuperobject(9,19,5,"Yaşamını tehlikeye atarak Filistliyi öldürdü. RAB de bütün İsraili büyük bir zafere ulaştırdı. Sen de bunu görüp sevindin. Öyleyse neden Davutu yok yere öldürerek suçsuz birine haksızlık edesin?››"));
trsuperobject.biblemap.put("b9_19_6", new trsuperobject(9,19,6,"Saul Yonatanın söylediklerinden etkilenerek ant içti: ‹‹Yaşayan RABbin adıyla derim ki, Davut öldürülmeyecektir.››"));
trsuperobject.biblemap.put("b9_19_7", new trsuperobject(9,19,7,"Bunun üzerine Yonatan Davutu çağırıp ona her şeyi anlattı. Sonra Davutu Saulun yanına getirdi. Davut da önceden olduğu gibi kralın hizmetine girdi."));
trsuperobject.biblemap.put("b9_19_8", new trsuperobject(9,19,8,"Savaş yine patlak verdi. Davut gidip Filistlilere karşı savaştı. Onları öyle büyük bir bozguna uğrattı ki, önünden kaçtılar."));
trsuperobject.biblemap.put("b9_19_9", new trsuperobject(9,19,9,"Bir gün Saul, mızrağı elinde evinde oturuyor, Davut da lir çalıyordu. Derken RABbin gönderdiği kötü bir ruh Saulu yakaladı."));
trsuperobject.biblemap.put("b9_19_10", new trsuperobject(9,19,10,"Saul mızrağıyla Davutu duvara çakmaya çalıştı. Ancak Davut yana kaçınca Saulun mızrağı duvara saplandı. O gece Davut kaçıp kurtuldu."));
trsuperobject.biblemap.put("b9_19_11", new trsuperobject(9,19,11,"Saul, Davutu gözetlemeleri, ertesi sabah da öldürmeleri için evine ulaklar gönderdi. Ama karısı Mikal Davuta, ‹‹Bu gece kaçıp kurtulamazsan, yarın öldürüleceksin›› dedi."));
trsuperobject.biblemap.put("b9_19_12", new trsuperobject(9,19,12,"Sonra Davutu pencereden aşağıya indirdi. Böylece Davut kaçıp kurtuldu."));
trsuperobject.biblemap.put("b9_19_13", new trsuperobject(9,19,13,"Mikal aile putunu alıp yatağa koydu, üstüne yorganı örttü, baş tarafına da keçi kılından bir yastık yerleştirdi."));
trsuperobject.biblemap.put("b9_19_14", new trsuperobject(9,19,14,"Saulun gönderdiği ulaklar Davutu yakalamaya geldiğinde, Mikal, ‹‹Davut hasta›› dedi."));
trsuperobject.biblemap.put("b9_19_15", new trsuperobject(9,19,15,"Saul Davutu görmeleri için ulakları yeniden göndererek, ‹‹Onu yatağıyla buraya getirin de öldüreyim›› diye buyurdu."));
trsuperobject.biblemap.put("b9_19_16", new trsuperobject(9,19,16,"Ulaklar eve girince, yatakta başında keçi kılından yastık olan putu gördüler."));
trsuperobject.biblemap.put("b9_19_17", new trsuperobject(9,19,17,"Saul Mikala ‹‹Neden beni böyle kandırıp düşmanımın kaçmasını sağladın?›› diye sordu. Mikal, ‹‹Davut bana, ‹Bırak beni gideyim, yoksa seni öldürürüm› dedi›› diye yanıtladı."));
trsuperobject.biblemap.put("b9_19_18", new trsuperobject(9,19,18,"Kaçıp kurtulan Davut, Ramada yaşayan Samuelin yanına gitti. Saulun kendisine bütün yaptıklarını ona anlattı. Sonra Samuelle birlikte Nayot Mahallesine gidip orada kaldı."));
trsuperobject.biblemap.put("b9_19_19", new trsuperobject(9,19,19,"Davutun Ramanın Nayot Mahallesinde olduğu haberi Saula ulaştırıldı."));
trsuperobject.biblemap.put("b9_19_20", new trsuperobject(9,19,20,"Bunun üzerine Saul Davutu yakalamaları için ulaklarını oraya gönderdi. Ulaklar Samuelin önderliğinde bir peygamber topluluğunun oynayıp coştuğunu gördüler. İşte o zaman Tanrının Ruhu Saulun ulaklarının üzerine indi. Onlar da oynayıp coşmaya başladılar."));
trsuperobject.biblemap.put("b9_19_21", new trsuperobject(9,19,21,"Saul olup bitenleri duyunca, başka ulaklar gönderdi. Onlar da oynayıp coştular. Saulun üçüncü kez gönderdiği ulaklar da öncekiler gibi yaptı."));
trsuperobject.biblemap.put("b9_19_22", new trsuperobject(9,19,22,"Sonunda Saul kendisi Ramaya doğru yola çıktı. Sekudaki büyük sarnıca varınca, ‹‹Samuelle Davut neredeler?›› diye sordu. Biri, ‹‹Ramanın Nayot Mahallesinde›› dedi."));
trsuperobject.biblemap.put("b9_19_23", new trsuperobject(9,19,23,"Saul Ramadaki Nayota doğru ilerlerken, Tanrının Ruhu onun üzerine de indi. Nayota varıncaya dek yol boyunca oynayıp coştu."));
trsuperobject.biblemap.put("b9_19_24", new trsuperobject(9,19,24,"Giysilerini de çıkarıp Samuel'in önünde oynayıp coştu. Bütün gün ve gece çıplak yattı. Halkın, ‹‹Saul da mı peygamber oldu?›› demesi bundandır. ‹‹Peygamberlik etmek›› sözcüğünden türemiştir."));
trsuperobject.biblemap.put("b9_20_1", new trsuperobject(9,20,1,"Davut Ramanın Nayot Mahallesinden kaçtıktan sonra Yonatana gitti. Ona, ‹‹Ne yaptım? Suçum ne?›› diye sordu, ‹‹Babana karşı ne günah işledim ki, beni öldürmek istiyor?››"));
trsuperobject.biblemap.put("b9_20_2", new trsuperobject(9,20,2,"Yonatan, ‹‹Bu senden uzak olsun, ölmeyeceksin!›› diye yanıtladı, ‹‹Babam bana bildirmeden ister büyük, ister küçük olsun hiçbir iş yapmaz. Neden bunu benden gizlesin? Olmaz öyle şey!››"));
trsuperobject.biblemap.put("b9_20_3", new trsuperobject(9,20,3,"Ancak Davut ant içerek, ‹‹Senin beni sevdiğini baban çok iyi biliyor›› diye yanıtladı, ‹‹Yonatan ne yapacağımı bilmemeli, yoksa üzülür› diye düşünmüştür. RABbin ve senin yaşamın hakkı için derim ki, ölüm ile aramda yalnız bir adım var.››"));
trsuperobject.biblemap.put("b9_20_4", new trsuperobject(9,20,4,"Yonatan Davuta, ‹‹Ne dilersen dile, senin için yaparım›› diye karşılık verdi."));
trsuperobject.biblemap.put("b9_20_5", new trsuperobject(9,20,5,"Davut Yonatana, ‹‹Bak, yarın Yeni Ay Töreni›› dedi, ‹‹Kralla birlikte yemeğe oturmam gerekir. Ama izin ver, ertesi günün akşamına dek tarlada gizleneyim."));
trsuperobject.biblemap.put("b9_20_6", new trsuperobject(9,20,6,"Eğer baban yokluğumu sezerse ona, ‹Davut aceleyle kendi kenti Beytleheme gitmek için benden ısrarla izin istedi; orada bütün ailenin yıllık kurban töreni var› dersin."));
trsuperobject.biblemap.put("b9_20_7", new trsuperobject(9,20,7,"Baban, ‹İyi› derse, kulun güvenlikte demektir. Ama öfkelenirse, bil ki, bana kötülük yapmaya karar vermiştir."));
trsuperobject.biblemap.put("b9_20_8", new trsuperobject(9,20,8,"Sana gelince, bana yardım et; çünkü RABbin önünde benimle antlaşma yaptın. Suçluysam, beni sen öldür! Neden beni babana teslim edesin?››"));
trsuperobject.biblemap.put("b9_20_9", new trsuperobject(9,20,9,"Yonatan, ‹‹Olmaz öyle şey!›› diye yanıtladı, ‹‹Babamın sana kötülük yapmaya karar verdiğini bilsem, sana söylemez miydim?››"));
trsuperobject.biblemap.put("b9_20_10", new trsuperobject(9,20,10,"Davut, ‹‹Baban sana sert bir karşılık verirse, kim bana bildirecek?›› diye sordu."));
trsuperobject.biblemap.put("b9_20_11", new trsuperobject(9,20,11,"Yonatan, ‹‹Gel, tarlaya gidelim›› dedi. Böylece ikisi tarlaya gittiler."));
trsuperobject.biblemap.put("b9_20_12", new trsuperobject(9,20,12,"Yonatan Davutla konuşmasını sürdürdü: ‹‹İsrailin Tanrısı RAB tanık olsun! Yarın ya da öbür gün bu saate kadar babamın ne düşündüğünü araştıracağım. Babamın sana karşı tutumu olumluysa, sana haber göndereceğim."));
trsuperobject.biblemap.put("b9_20_13", new trsuperobject(9,20,13,"Ama babam seni öldürmeyi tasarlıyorsa, bunu sana bildirip güvenlik içinde gitmeni sağlamazsam, RAB bana aynısını, hatta daha kötüsünü yapsın! RAB önceden babamla olduğu gibi seninle de birlikte olsun!"));
trsuperobject.biblemap.put("b9_20_14", new trsuperobject(9,20,14,"Ama sen yaşamım boyunca RABbin iyiliğini bana göster ki ölmeyeyim."));
trsuperobject.biblemap.put("b9_20_15", new trsuperobject(9,20,15,"RAB Davutun bütün düşmanlarını yeryüzünden yok edeceği zaman bile, sen soyuma iyiliklerini sonsuza dek esirgeme.››"));
trsuperobject.biblemap.put("b9_20_16", new trsuperobject(9,20,16,"Böylece Yonatan Davut soyuyla bir antlaşma yaptı ve, ‹‹RAB Davutun düşmanlarını cezalandırsın›› dedi."));
trsuperobject.biblemap.put("b9_20_17", new trsuperobject(9,20,17,"Davuta beslediği sevgiden ötürü Yonatan ona bir daha ant içirtti. Çünkü onu canı kadar seviyordu."));
trsuperobject.biblemap.put("b9_20_18", new trsuperobject(9,20,18,"Yonatan Davuta, ‹‹Yarın Yeni Ay Töreni›› dedi, ‹‹Yerin boş kalacağından, yokluğun anlaşılacak."));
trsuperobject.biblemap.put("b9_20_19", new trsuperobject(9,20,19,"Öbür gün, geçen sefer gizlendiğin yere çabucak git. Ezel Taşının yanında bekle."));
trsuperobject.biblemap.put("b9_20_20", new trsuperobject(9,20,20,"Ben hedefe atar gibi taşın bir yanına üç ok atacağım."));
trsuperobject.biblemap.put("b9_20_21", new trsuperobject(9,20,21,"Sonra hizmetkârımı gönderip, ‹Git okları bul› diye buyruk vereceğim. Eğer özellikle ona, ‹Bak, oklar senin bu yanında, onları alıp buraya getir› dersem, gel. Yaşayan RABbin adıyla derim ki, güvenliktesin, tehlike yok."));
trsuperobject.biblemap.put("b9_20_22", new trsuperobject(9,20,22,"Ama hizmetkâra, ‹Bak, oklar ötende› dersem, git; çünkü RAB seni uzaklaştırmıştır."));
trsuperobject.biblemap.put("b9_20_23", new trsuperobject(9,20,23,"Birbirimizle yaptığımız antlaşmaya gelince, RAB sonsuza dek seninle benim aramda tanık olsun.››"));
trsuperobject.biblemap.put("b9_20_24", new trsuperobject(9,20,24,"Böylece Davut tarlada gizlendi. Yeni Ay Töreni başlayınca, Kral Saul gelip yemeğe oturdu."));
trsuperobject.biblemap.put("b9_20_25", new trsuperobject(9,20,25,"Her zamanki gibi duvarın yanındaki yerine oturmuştu. Yonatan karşısında, Avner de yanında yerlerini aldılar. Davutun yeriyse boş kaldı."));
trsuperobject.biblemap.put("b9_20_26", new trsuperobject(9,20,26,"Ama Saul o gün bir şey söylemedi. ‹‹Davutun başına birşey gelmiş olmalı. Dinsel açıdan kirli olsa gerek, evet dinsel açıdan temiz değildir›› diye düşündü."));
trsuperobject.biblemap.put("b9_20_27", new trsuperobject(9,20,27,"Ertesi gün, ayın ikinci günü, Davutun yeri yine boştu. Bunun üzerine Saul, oğlu Yonatana, ‹‹İşayın oğlu neden dün de, bugün de yemeğe gelmedi?›› diye sordu. kalktı››."));
trsuperobject.biblemap.put("b9_20_28", new trsuperobject(9,20,28,"Yonatan, ‹‹Davut Beytleheme gitmek için benden ısrarla izin istedi›› diye karşılık verdi,"));
trsuperobject.biblemap.put("b9_20_29", new trsuperobject(9,20,29,"‹‹Lütfen izin ver. Çünkü ailemizin kentte bir kurbanı var, ağabeyim orada bulunmamı buyurdu. Gözünde lütuf bulduysam gidip kardeşlerimi göreyim› dedi. İşte bu yüzden kralın sofrasına gelemedi.››"));
trsuperobject.biblemap.put("b9_20_30", new trsuperobject(9,20,30,"Saul Yonatana öfkelenerek, ‹‹Seni sapık ve dikbaşlı kadının oğlu!›› diye bağırdı, ‹‹İşayın oğlunu desteklediğini bilmiyor muyum? Bu kendin için de, seni doğuran annen için de utanç verici."));
trsuperobject.biblemap.put("b9_20_31", new trsuperobject(9,20,31,"Çünkü İşayın oğlu yeryüzünde yaşadıkça ne sen güvenlikte olabilirsin, ne de krallığın. Şimdi adam gönder, onu bana getir. O ölmeli!››"));
trsuperobject.biblemap.put("b9_20_32", new trsuperobject(9,20,32,"Yonatan babası Saula, ‹‹Neden ölmeli? Ne yaptı ki?›› diye karşılık verdi."));
trsuperobject.biblemap.put("b9_20_33", new trsuperobject(9,20,33,"Ama Saul Yonatanı öldürmek amacıyla mızrağını ona fırlattı. Böylece Yonatan babasının Davutu öldürmeye kararlı olduğunu anladı."));
trsuperobject.biblemap.put("b9_20_34", new trsuperobject(9,20,34,"Büyük bir öfkeyle sofradan kalktı ve ayın ikinci günü hiç yemek yemedi. Babasının Davutu böyle aşağılamasına üzüldü."));
trsuperobject.biblemap.put("b9_20_35", new trsuperobject(9,20,35,"Sabahleyin Yonatan Davutla buluşmak üzere tarlaya gitti. Yanına bir uşak almıştı."));
trsuperobject.biblemap.put("b9_20_36", new trsuperobject(9,20,36,"Uşağa, ‹‹Haydi koş, atacağım okları bul›› dedi. Uşak koşarken, Yonatan onun ötesine bir ok attı."));
trsuperobject.biblemap.put("b9_20_37", new trsuperobject(9,20,37,"Uşak Yonatanın attığı okun düştüğü yere varınca, Yonatan, ‹‹Ok ötende!›› diye seslendi,"));
trsuperobject.biblemap.put("b9_20_38", new trsuperobject(9,20,38,"‹‹Çabuk ol! Koş, yerinde durma!›› Yonatanın uşağı oku alıp efendisine getirdi."));
trsuperobject.biblemap.put("b9_20_39", new trsuperobject(9,20,39,"Olup bitenden habersizdi. Olanları yalnız Yonatanla Davut biliyordu."));
trsuperobject.biblemap.put("b9_20_40", new trsuperobject(9,20,40,"Yonatan, silahlarını yanındaki uşağa vererek, ‹‹Al bunları kente götür›› dedi."));
trsuperobject.biblemap.put("b9_20_41", new trsuperobject(9,20,41,"Uşak gider gitmez, Davut taşın güney yanından ayağa kalktı ve yüzüstü yere kapanarak üç kez eğildi. İki arkadaş birbirlerini öpüp ağladılar; ancak Davut daha çok ağladı."));
trsuperobject.biblemap.put("b9_20_42", new trsuperobject(9,20,42,"Yonatan, ‹‹Esenlikle yoluna git›› dedi, ‹‹İkimiz RAB'bin adıyla ant içmiştik. RAB seninle benim aramda ve soylarımız arasında sonsuza dek tanık olsun.›› Bundan sonra Davut yoluna gitti. Yonatan da kente döndü."));
trsuperobject.biblemap.put("b9_21_1", new trsuperobject(9,21,1,"Davut Nov Kentine, Kâhin Ahimelekin yanına gitti. Ahimelek titreyerek Davutu karşılamaya çıktı. ‹‹Neden yalnızsın? Neden yanında kimse yok?›› diye sordu."));
trsuperobject.biblemap.put("b9_21_2", new trsuperobject(9,21,2,"Davut şöyle yanıtladı: ‹‹Kral bana bir görev verdi. ‹Sana verdiğim görevden ve buyruklardan kimsenin haberi olmasın› dedi. Adamlarıma gelince, belli bir yere gitmelerini söyledim."));
trsuperobject.biblemap.put("b9_21_3", new trsuperobject(9,21,3,"Şu an elinde ne var? Bana beş somun ekmek ya da başka ne varsa ver.››"));
trsuperobject.biblemap.put("b9_21_4", new trsuperobject(9,21,4,"Kâhin, ‹‹Taze ekmeğim yok›› diye karşılık verdi, ‹‹Ama adamların kadından uzak kaldılarsa kutsanmış ekmek var.››"));
trsuperobject.biblemap.put("b9_21_5", new trsuperobject(9,21,5,"Davut, ‹‹Yola çıktığımızdan her zaman olduğu gibi, kadından uzak kaldık›› dedi, ‹‹Sıradan bir yolculuğa çıktığımızda bile adamlarım kendilerini temiz tutarlar; özellikle bugün ne kadar daha çok temiz olacaklar.››"));
trsuperobject.biblemap.put("b9_21_6", new trsuperobject(9,21,6,"Bunun üzerine kâhin ona kutsanmış ekmek verdi; çünkü orada huzura konan ekmekten başka ekmek yoktu. Bu ekmek RABbin huzurundan alındığı gün yerine sıcak ekmek konurdu. huzuruna konan ekmek diye de bilinir."));
trsuperobject.biblemap.put("b9_21_7", new trsuperobject(9,21,7,"O gün Saulun görevlilerinden Edomlu Doek adındaki baş çoban RABbin önünde dinsel görevini yerine getirmek üzere orada bulunuyordu."));
trsuperobject.biblemap.put("b9_21_8", new trsuperobject(9,21,8,"Davut Ahimeleke, ‹‹Yanında mızrak ya da kılıç yok mu?›› diye sordu, ‹‹Kralın işi acele olduğundan, yanıma ne kılıcımı aldım, ne de başka bir silah.›› ‹‹Alıkonulmuş››."));
trsuperobject.biblemap.put("b9_21_9", new trsuperobject(9,21,9,"Kâhin, ‹‹Ela Vadisinde öldürdüğün Filistli Golyatın kılıcı var›› diye karşılık verdi, ‹‹Efodun arkasında beze sarılı duruyor. Burada başka silah yok. İstersen onu alabilirsin.›› Davut, ‹‹Onun gibisi yoktur, onu bana ver›› dedi."));
trsuperobject.biblemap.put("b9_21_10", new trsuperobject(9,21,10,"Sauldan kaçan Davut o gün Gat Kralı Akişe gitti."));
trsuperobject.biblemap.put("b9_21_11", new trsuperobject(9,21,11,"Akişin görevlileri, ‹‹Bu İsrail Kralı Davut değil mi?›› dediler,  ‹‹Çalıp oynarken,  ‹Saul binlercesini öldürdü,  Davutsa on binlercesini›"));
trsuperobject.biblemap.put("b9_21_12-13", new trsuperobject(9,21,12-13,"Bu sözler Davutu derin derin düşündürdü. Gat Kralı Akişten çok korkan Davut, onların önünde tutumunu değiştirerek deli gibi davrandı. Kentin kapılarını tırmaladı, salyasını sakalına akıttı."));
trsuperobject.biblemap.put("b9_21_14", new trsuperobject(9,21,14,"Akiş görevlilerine, ‹‹Şu adama bakın!›› dedi, ‹‹Delinin biri! Onu neden bana getirdiniz?"));
trsuperobject.biblemap.put("b9_21_15", new trsuperobject(9,21,15,"Bizde deliler eksik mi ki, önümde delilik yapsın diye bu adamı getirdiniz? Bu adamın sarayıma girmesi şart mı?››"));
trsuperobject.biblemap.put("b9_22_1", new trsuperobject(9,22,1,"Davut Gattan ayrılıp Adullam Mağarasına kaçtı. Bunu duyan kardeşleri ve ailesinin öteki bireyleri yanına gittiler."));
trsuperobject.biblemap.put("b9_22_2", new trsuperobject(9,22,2,"Sıkıntısı, borcu, hoşnutsuzluğu olan herkes Davutun çevresinde toplandı. Davut sayısı dört yüze varan bu adamlara önderlik yaptı."));
trsuperobject.biblemap.put("b9_22_3", new trsuperobject(9,22,3,"Davut oradan Moavdaki Mispa Kentine gitti. Moav Kralından, ‹‹Tanrının bana ne yapacağı belli oluncaya dek annemle babamın gelip yanınızda kalmasına izin verir misin?›› diye bir istekte bulundu."));
trsuperobject.biblemap.put("b9_22_4", new trsuperobject(9,22,4,"Böylece Davut annesiyle babasını Moav Kralının yanına bıraktı. Davut sığınakta kaldığı sürece onlar da Moav Kralının yanında kaldılar."));
trsuperobject.biblemap.put("b9_22_5", new trsuperobject(9,22,5,"Ne var ki, Peygamber Gad Davuta, ‹‹Sığınakta kalma. Yahuda ülkesine git›› dedi. Bunun üzerine Davut oradan ayrılıp Heret Ormanına gitti."));
trsuperobject.biblemap.put("b9_22_6", new trsuperobject(9,22,6,"Bu sırada Saul Davutla yanındakilerin nerede olduklarını öğrendi. Saul elinde mızrağıyla Givada bir tepedeki ılgın ağacının altında oturuyordu. Askerleri de çevresinde duruyordu."));
trsuperobject.biblemap.put("b9_22_7", new trsuperobject(9,22,7,"Saul onlara şöyle dedi: ‹‹Ey Benyaminliler, şimdi dinleyin! İşayın oğlu her birinize tarlalar, bağlar mı verecek? Her birinizi binbaşı, yüzbaşı mı yapacak?"));
trsuperobject.biblemap.put("b9_22_8", new trsuperobject(9,22,8,"Hepiniz bana karşı düzen kurdunuz. Çünkü oğlum İşayın oğluyla antlaşma yaptığında bana haber veren olmadı. İçinizden bana acıyan tek kişi çıkmadı. Bugün olduğu gibi, bana pusu kurması için oğlumun kulum Davutu kışkırttığını bana bildiren olmadı.››"));
trsuperobject.biblemap.put("b9_22_9", new trsuperobject(9,22,9,"Bunun üzerine Saulun askerlerinin yanında duran Edomlu Doek, ‹‹İşay oğlu Davutun Nov Kentine, Ahituv oğlu Kâhin Ahimelekin yanına geldiğini gördüm›› dedi,"));
trsuperobject.biblemap.put("b9_22_10", new trsuperobject(9,22,10,"‹‹Ahimelek Davut için RABbe danıştı. Ona hem yiyecek sağladı, hem de Filistli Golyatın kılıcını verdi.››"));
trsuperobject.biblemap.put("b9_22_11", new trsuperobject(9,22,11,"Kral Saul, Ahituv oğlu Kâhin Ahimeleki ve babasının ailesinden Novda yaşayan bütün kâhinleri çağırmak için ulaklar gönderdi. Hepsi kralın yanına geldi."));
trsuperobject.biblemap.put("b9_22_12", new trsuperobject(9,22,12,"Saul Ahimeleke, ‹‹Ey Ahituv oğlu, beni dinle!›› dedi. Ahimelek, ‹‹Buyur, efendim›› diye yanıtladı."));
trsuperobject.biblemap.put("b9_22_13", new trsuperobject(9,22,13,"Saul, ‹‹Neden sen ve İşay oğlu bana karşı düzen kurdunuz?›› dedi, ‹‹Çünkü ona ekmek, kılıç verdin ve onun için Tanrıya danıştın. O da bana karşı ayaklandı ve bugün yaptığı gibi pusu kurdu.››"));
trsuperobject.biblemap.put("b9_22_14", new trsuperobject(9,22,14,"Ahimelek, ‹‹Bütün görevlilerin arasında Davut kadar sana bağlı biri var mı?›› diye karşılık verdi, ‹‹Davut senin damadın, muhafız birliği komutanın ve ailende saygın biridir."));
trsuperobject.biblemap.put("b9_22_15", new trsuperobject(9,22,15,"Ben Davut için Tanrıya danışmaya o gün mü başladım? Kesinlikle hayır! Kral ben kulunu ve babasının ailesini suçlamasın. Çünkü kulun bu konuda hiçbir şey bilmiyor.››"));
trsuperobject.biblemap.put("b9_22_16", new trsuperobject(9,22,16,"Ama Saul, ‹‹Ey Ahimelek, sen de bütün ailen de kesinlikle öleceksiniz›› dedi."));
trsuperobject.biblemap.put("b9_22_17", new trsuperobject(9,22,17,"Sonra yanında duran nöbetçi askerlere, ‹‹Gidin ve Davutu destekleyen RABbin kâhinlerini öldürün!›› dedi, ‹‹Çünkü onun kaçtığını bildikleri halde bana haber vermediler.›› Ne var ki, kralın görevlileri el kaldırıp RABbin kâhinlerini öldürmek istemediler."));
trsuperobject.biblemap.put("b9_22_18", new trsuperobject(9,22,18,"Bunun üzerine kral, Doeke, ‹‹Sen git, kâhinleri öldür›› diye buyurdu. Edomlu Doek de gidip kâhinleri öldürdü. O gün Doek keten efod giymiş seksen beş kişi öldürdü."));
trsuperobject.biblemap.put("b9_22_19", new trsuperobject(9,22,19,"Kadın erkek, çoluk çocuk demeden kâhinler kenti Novun halkını kılıçtan geçirdi. Sığırları, eşekleri, koyunları da öldürdü."));
trsuperobject.biblemap.put("b9_22_20", new trsuperobject(9,22,20,"Yalnız Ahituv oğlu Kâhin Ahimelekin oğullarından Aviyatar adında biri kurtulup Davuta kaçtı."));
trsuperobject.biblemap.put("b9_22_21", new trsuperobject(9,22,21,"Aviyatar Saulun RABbin kâhinlerini öldürttüğünü Davuta söyledi."));
trsuperobject.biblemap.put("b9_22_22", new trsuperobject(9,22,22,"Davut Aviyatara, ‹‹O gün orada bulunan Edomlu Doekin olup biteni Saula bildireceğini anlamıştım zaten›› dedi, ‹‹Babanın bütün aile bireylerinin ölümüne ben neden oldum."));
trsuperobject.biblemap.put("b9_22_23", new trsuperobject(9,22,23,"Yanımda kal ve korkma! Seni öldürmek isteyen beni de öldürmek istiyor. Yanımda güvenlikte olursun.››"));
trsuperobject.biblemap.put("b9_23_1", new trsuperobject(9,23,1,"Davuta, ‹‹Filistliler Keila Kentine saldırıp harmanları yağmalıyorlar›› diye haber verdiler."));
trsuperobject.biblemap.put("b9_23_2", new trsuperobject(9,23,2,"Davut RABbe, ‹‹Gidip şu Filistlilere saldırayım mı?›› diye danıştı. RAB, ‹‹Git, Filistlilere saldır ve Keila Kentini kurtar›› diye yanıtladı."));
trsuperobject.biblemap.put("b9_23_3", new trsuperobject(9,23,3,"Ama adamları Davuta, ‹‹Bak, biz burada Yahudadayken korkuyoruz›› dediler, ‹‹Keilaya Filist ordusuna karşı savaşmaya gidersek büsbütün korkarız.››"));
trsuperobject.biblemap.put("b9_23_4", new trsuperobject(9,23,4,"Bunun üzerine Davut RABbe bir kez daha danıştı. RAB ona yine, ‹‹Kalk, Keilaya git! Çünkü Filistlileri senin eline ben teslim edeceğim›› dedi."));
trsuperobject.biblemap.put("b9_23_5", new trsuperobject(9,23,5,"Böylece Davutla adamları Keilaya gidip Filistlilere karşı savaştılar. Davut onların hayvanlarını ele geçirdi. Filistlileri ağır bir yenilgiye uğratarak Keila halkını kurtardı."));
trsuperobject.biblemap.put("b9_23_6", new trsuperobject(9,23,6,"Ahimelekin oğlu Aviyatar kaçıp Keilada bulunan Davuta gittiğinde, efodu da birlikte götürmüştü."));
trsuperobject.biblemap.put("b9_23_7", new trsuperobject(9,23,7,"Saul, Davutun Keila Kentine gittiğini duyunca, ‹‹Tanrı Davutu elime teslim etti›› dedi, ‹‹Davut sürgülü kapıları olan bir kente girmekle kendini hapsetmiş oldu.››"));
trsuperobject.biblemap.put("b9_23_8", new trsuperobject(9,23,8,"Böylece Saul, Keilaya yürüyüp Davutla adamlarını kuşatmak amacıyla bütün halkı savaşa çağırdı."));
trsuperobject.biblemap.put("b9_23_9", new trsuperobject(9,23,9,"Davut, Saulun kendisine bir düzen kurduğunu duyunca, Kâhin Aviyatara, ‹‹Efodu getir›› dedi."));
trsuperobject.biblemap.put("b9_23_10", new trsuperobject(9,23,10,"Sonra şöyle yakardı: ‹‹Ey İsrailin Tanrısı RAB! Ben kulun yüzünden Saulun gelip Keilayı yıkmayı tasarladığına dair kesin haber aldım."));
trsuperobject.biblemap.put("b9_23_11", new trsuperobject(9,23,11,"Keila halkı beni onun eline teslim eder mi? Kulunun duymuş olduğu gibi Saul gelecek mi? Ey İsrailin Tanrısı RAB, yalvarırım, kuluna bildir!›› RAB, ‹‹Saul gelecek›› yanıtını verdi."));
trsuperobject.biblemap.put("b9_23_12", new trsuperobject(9,23,12,"Davut RABbe, ‹‹Keila halkı beni ve adamlarımı Saulun eline teslim edecek mi?›› diye sordu. RAB, ‹‹Teslim edecek›› dedi."));
trsuperobject.biblemap.put("b9_23_13", new trsuperobject(9,23,13,"Bunun üzerine Davut ile yanındaki altı yüz kadar kişi Keiladan ayrılıp oradan oraya yer değiştirmeye başladılar. Davutun Keiladan kaçtığını öğrenen Saul oraya gitmekten vazgeçti."));
trsuperobject.biblemap.put("b9_23_14", new trsuperobject(9,23,14,"Davut kırsal bölgedeki sığınaklarda ve Zif Çölünün dağlık kesiminde kaldı. Saul her gün Davutu aradığı halde, Tanrı onu Saulun eline teslim etmedi."));
trsuperobject.biblemap.put("b9_23_15", new trsuperobject(9,23,15,"Davut Zif Çölünde, Horeşteyken, Saulun kendisini öldürmek için yola çıktığını öğrendi."));
trsuperobject.biblemap.put("b9_23_16", new trsuperobject(9,23,16,"Bu arada Saul oğlu Yonatan kalkıp Horeşe, Davutun yanına gitti ve onu Tanrının adıyla yüreklendirdi."));
trsuperobject.biblemap.put("b9_23_17", new trsuperobject(9,23,17,"‹‹Korkma!›› dedi, ‹‹Babam Saul sana dokunmayacak. Sen İsrail Kralı olacaksın, ben de senin yardımcın olacağım. Babam Saul da bunu biliyor.››"));
trsuperobject.biblemap.put("b9_23_18", new trsuperobject(9,23,18,"İkisi de RABbin önünde aralarındaki antlaşmayı yenilediler. Sonra Yonatan evine döndü, Davut ise Horeşte kaldı."));
trsuperobject.biblemap.put("b9_23_19", new trsuperobject(9,23,19,"Zifliler Givaya gidip Saula, ‹‹Davut aramızda›› dediler, ‹‹Yeşimonun güneyinde, Hakila Tepesindeki Horeş sığınaklarında gizleniyor."));
trsuperobject.biblemap.put("b9_23_20", new trsuperobject(9,23,20,"Ey kral, ne zaman gelmek istersen gel! Davutu kralın eline teslim etmeyi ise bize bırak.››"));
trsuperobject.biblemap.put("b9_23_21", new trsuperobject(9,23,21,"Saul, ‹‹RAB sizi kutsasın! Bana acıdınız›› dedi,"));
trsuperobject.biblemap.put("b9_23_22", new trsuperobject(9,23,22,"‹‹Gidin ve bir daha araştırın; Davutun genellikle nerelerde gizlendiğini, orada onu kimin gördüğünü iyice öğrenin. Çünkü onun çok kurnaz olduğunu söylüyorlar."));
trsuperobject.biblemap.put("b9_23_23", new trsuperobject(9,23,23,"Gizlendiği yerlerin hepsini öğrenip bana kesin bir haber getirin. O zaman ben de sizinle gelirim. Eğer Davut o bölgedeyse, bütün Yahuda boyları içinde onu arayıp bulacağım.››"));
trsuperobject.biblemap.put("b9_23_24", new trsuperobject(9,23,24,"Böylece Zifliler kalkıp Sauldan önce Zife gittiler. O sırada Davutla adamları Yeşimonun güneyindeki Aravada, Maon Çölündeydiler."));
trsuperobject.biblemap.put("b9_23_25", new trsuperobject(9,23,25,"Saul ile adamlarının kendisini aramaya geldiklerini öğrenince Davut aşağıya inip Maon Çölündeki kayalığa sığındı. Saul bunu duyunca Davutun ardından Maon Çölüne gitti."));
trsuperobject.biblemap.put("b9_23_26", new trsuperobject(9,23,26,"Saul dağın bir yanından, Davutla adamları ise öbür yanından ilerliyordu. Davut Sauldan kaçıp kurtulmaya çalışıyordu. Saulla askerleri Davutla adamlarını yakalamak üzere yaklaşırken,"));
trsuperobject.biblemap.put("b9_23_27", new trsuperobject(9,23,27,"bir ulak gelip Saula şöyle dedi: ‹‹Çabuk gel! Filistliler ülkeye saldırıyor.››"));
trsuperobject.biblemap.put("b9_23_28", new trsuperobject(9,23,28,"Bunun üzerine Saul Davutu kovalamayı bırakıp Filistlilerle savaşmaya gitti. Bu yüzden oraya Sela-Hammahlekot adı verildi."));
trsuperobject.biblemap.put("b9_23_29", new trsuperobject(9,23,29,"Davut oradan ayrılıp Eyn-Gedi bölgesindeki sığınaklara gizlendi."));
trsuperobject.biblemap.put("b9_24_1", new trsuperobject(9,24,1,"Saul Filistlileri kovalamaktan dönünce, Davutun Eyn-Gedi Çölünde olduğu haberini aldı."));
trsuperobject.biblemap.put("b9_24_2", new trsuperobject(9,24,2,"Saul da Davutla adamlarını Dağ Keçisi Kayalığı dolaylarında arayıp bulmak için, bütün İsrailden üç bin seçme asker alıp yola çıktı."));
trsuperobject.biblemap.put("b9_24_3", new trsuperobject(9,24,3,"Yolda koyun ağıllarına rastladı. Yakında bir de mağara vardı. Saul ihtiyacını gidermek için mağaraya girdi. Davutla adamları mağaranın en iç bölümünde kalıyorlardı."));
trsuperobject.biblemap.put("b9_24_4", new trsuperobject(9,24,4,"Adamları, Davuta, ‹‹İşte RABbin sana, ‹Dilediğini yapabilmen için düşmanını eline teslim edeceğim› dediği gün bugündür›› dediler. Davut kalkıp Saulun cüppesinin eteğinden gizlice bir parça kesti."));
trsuperobject.biblemap.put("b9_24_5", new trsuperobject(9,24,5,"Ama sonradan Saulun eteğinden bir parça kestiği için kendini suçlu buldu."));
trsuperobject.biblemap.put("b9_24_6", new trsuperobject(9,24,6,"Adamlarına, ‹‹Efendime, RABbin meshettiği kişiye karşı böyle bir şey yapmaktan, el kaldırmaktan RAB beni uzak tutsun›› dedi, ‹‹Çünkü o RABbin meshettiği kişidir.››"));
trsuperobject.biblemap.put("b9_24_7", new trsuperobject(9,24,7,"Davut bu sözlerle adamlarını engelledi ve Saula saldırmalarına izin vermedi. Saul mağaradan çıkıp yoluna koyuldu."));
trsuperobject.biblemap.put("b9_24_8", new trsuperobject(9,24,8,"O zaman Davut da mağaradan çıktı. Saula, ‹‹Efendim kral!›› diye seslendi. Saul arkasına bakınca, Davut eğilip yüzüstü yere kapandı."));
trsuperobject.biblemap.put("b9_24_9", new trsuperobject(9,24,9,"‹‹Davut sana kötülük yapmak istiyor› diyenlerin sözlerini neden önemsiyorsun?›› dedi,"));
trsuperobject.biblemap.put("b9_24_10", new trsuperobject(9,24,10,"‹‹Bugün RABbin mağarada seni elime nasıl teslim ettiğini gözünle görüyorsun. Bazıları seni öldürmemi istedi. Ama ben seni esirgeyip, ‹Efendime el kaldırmayacağım, çünkü o RABbin meshettiği kişidir› dedim."));
trsuperobject.biblemap.put("b9_24_11", new trsuperobject(9,24,11,"Ey baba, cüppenin eteğinden kesilmiş, elimdeki şu parçaya bak; evet, bak! Cüppenden bir parça kestim, ama seni öldürmedim. Bundan ötürü içimde kötülük ve başkaldırma düşüncesi olmadığını iyice bilesin. Sana kötülük yapmadığım halde sen beni öldürmeye çalışıyorsun."));
trsuperobject.biblemap.put("b9_24_12", new trsuperobject(9,24,12,"RAB aramızda yargıç olsun ve benim öcümü senden O alsın. Ama ben elimi sana karşı kaldırmayacağım."));
trsuperobject.biblemap.put("b9_24_13", new trsuperobject(9,24,13,"Eskilerin şu, ‹Kötülük kötü kişilerden gelir› deyişi uyarınca elim sana karşı kalkmayacaktır."));
trsuperobject.biblemap.put("b9_24_14", new trsuperobject(9,24,14,"İsrail Kralı kime karşı çıkmış? Sen kimi kovalıyorsun? Ölü bir köpek mi? Bir pire mi?"));
trsuperobject.biblemap.put("b9_24_15", new trsuperobject(9,24,15,"RAB yargıç olsun ve hangimizin haklı olduğuna O karar versin. RAB davama baksın ve beni savunup senin elinden kurtarsın.››"));
trsuperobject.biblemap.put("b9_24_16", new trsuperobject(9,24,16,"Davut söylediklerini bitirince, Saul, ‹‹Davut oğlum, bu senin sesin mi?›› diye sordu ve hıçkıra hıçkıra ağlamaya başladı."));
trsuperobject.biblemap.put("b9_24_17", new trsuperobject(9,24,17,"Sonra, ‹‹Sen benden daha doğru bir adamsın›› dedi, ‹‹Sana kötülük yaptığım halde sen bana iyilikle karşılık verdin."));
trsuperobject.biblemap.put("b9_24_18", new trsuperobject(9,24,18,"Bugün bana iyi davrandığını kanıtladın: RAB beni eline teslim ettiği halde beni öldürmedin."));
trsuperobject.biblemap.put("b9_24_19", new trsuperobject(9,24,19,"Düşmanını yakalayan biri onu güvenlik içinde salıverir mi? Bugün bana yaptığın iyiliğe karşılık RAB de seni iyilikle ödüllendirsin."));
trsuperobject.biblemap.put("b9_24_20", new trsuperobject(9,24,20,"Şimdi anladım ki, sen gerçekten kral olacaksın ve İsrail Krallığı senin egemenliğin altında sürecek."));
trsuperobject.biblemap.put("b9_24_21", new trsuperobject(9,24,21,"Benden sonra soyumu ortadan kaldırmayacağına, babamın ailesinden adımı silmeyeceğine dair RABbin önünde ant iç.››"));
trsuperobject.biblemap.put("b9_24_22", new trsuperobject(9,24,22,"Davut Saul'un istediği gibi ant içti. Sonra Saul evine döndü. Davut'la adamları da sığınağa gittiler."));
trsuperobject.biblemap.put("b9_25_1", new trsuperobject(9,25,1,"Bu sırada Samuel öldü. Bütün İsrailliler toplanıp onun için yas tuttular. Onu Ramadaki evine gömdüler. Bundan sonra Davut Maon Çölüne gitti."));
trsuperobject.biblemap.put("b9_25_2", new trsuperobject(9,25,2,"Maonda çok varlıklı bir adam vardı; işi Karmeldeydi. Üç bin koyunu, bin keçisi vardı. O sırada Karmelde koyunlarını kırkmaktaydı."));
trsuperobject.biblemap.put("b9_25_3", new trsuperobject(9,25,3,"Adamın adı Naval, karısının adı da Avigayildi. Kadın sağgörülü ve güzeldi. Ama Kalev soyundan gelen kocası kaba, kötü huylu biriydi."));
trsuperobject.biblemap.put("b9_25_4", new trsuperobject(9,25,4,"Davut kırdayken, Navalın koyunlarını kırktığını duydu."));
trsuperobject.biblemap.put("b9_25_5", new trsuperobject(9,25,5,"On uşağı şu buyrukla ona gönderdi: ‹‹Karmelde Navalın yanına gidin. Benden ona selam söyleyip"));
trsuperobject.biblemap.put("b9_25_6", new trsuperobject(9,25,6,"şöyle deyin: ‹Ömrün uzun olsun! Sana, ailene ve sana bağlı olan herkese esenlik olsun!"));
trsuperobject.biblemap.put("b9_25_7", new trsuperobject(9,25,7,"Şimdi koyunların kırkma zamanı olduğunu duydum. Çobanların bizimle birlikteyken, onları incitmedik. Karmelde kaldıkları sürece hiçbir kayıpları olmadı."));
trsuperobject.biblemap.put("b9_25_8", new trsuperobject(9,25,8,"Uşaklarına sor, sana söyleyecekler. Bunun için adamlarıma yakınlık göster. Çünkü sana şenlik zamanında geldik. Lütfen kullarına ve oğlun Davuta elinden geleni ver.››"));
trsuperobject.biblemap.put("b9_25_9", new trsuperobject(9,25,9,"Davutun adamları varıp Davut adına bu sözleri Navala ilettiler ve beklemeye başladılar."));
trsuperobject.biblemap.put("b9_25_10", new trsuperobject(9,25,10,"Ne var ki, Naval Davutun adamlarına şu karşılığı verdi: ‹‹Bu Davut da kim? İşayın oğlu da kim oluyor? Bu günlerde birçok köle efendilerini bırakıp kaçıyor."));
trsuperobject.biblemap.put("b9_25_11", new trsuperobject(9,25,11,"Ekmeğimi, suyumu, kırkıcılarım için kestiğim hayvanların etini alıp nereden geldiklerini bilmediğim kişilere mi vereyim?››"));
trsuperobject.biblemap.put("b9_25_12", new trsuperobject(9,25,12,"Davutun adamları geldikleri yoldan döndüler ve Navalın bütün söylediklerini Davuta bildirdiler."));
trsuperobject.biblemap.put("b9_25_13", new trsuperobject(9,25,13,"Davut adamlarına, ‹‹Herkes kılıcını kuşansın!›› diye buyruk verdi. Davut da, adamları da kılıçlarını kuşandılar. Yaklaşık dört yüz adam Davutla birlikte gitti; iki yüz kişi de erzağın yanında kaldı."));
trsuperobject.biblemap.put("b9_25_14", new trsuperobject(9,25,14,"Navalın uşaklarından biri, Navalın karısı Avigayile, ‹‹Davut efendimiz Navala esenlik dilemek için kırdan ulaklar gönderdi›› dedi, ‹‹Ama Naval onları tersledi."));
trsuperobject.biblemap.put("b9_25_15", new trsuperobject(9,25,15,"Oysa adamlar bize çok iyi davrandılar. Bizi incitmediler. Kırda onlarla birlikte kaldığımız sürece hiçbir şeyimiz kaybolmadı."));
trsuperobject.biblemap.put("b9_25_16", new trsuperobject(9,25,16,"Koyunlarımızı güderken, yanlarında kaldığımız sürece gece gündüz bizi korudular."));
trsuperobject.biblemap.put("b9_25_17", new trsuperobject(9,25,17,"Şimdi ne yapman gerektiğini iyi düşün. Çünkü efendimize ve bütün ailesine kötülük yapmayı tasarlıyorlar. Üstelik efendimiz o kadar kötü ki, kimse ona bir şey söyleyemiyor.››"));
trsuperobject.biblemap.put("b9_25_18", new trsuperobject(9,25,18,"Bunun üzerine Avigayil, hiç zaman yitirmeden, iki yüz ekmek, iki tulum şarap, hazırlanmış beş koyun, beş sea kavrulmuş buğdayfö, yüz salkım kuru üzüm ve iki yüz parça incir pestili alıp eşeklere yükledi."));
trsuperobject.biblemap.put("b9_25_19", new trsuperobject(9,25,19,"Sonra uşaklarına, ‹‹Önümden gidin, ben arkanızdan geliyorum›› dedi. Kocası Navala hiçbir şey söylemedi."));
trsuperobject.biblemap.put("b9_25_20", new trsuperobject(9,25,20,"Avigayil eşeğe binmiş, dağın öbür yolundan inerken, Davutla adamları da ona doğru ilerliyorlardı. Avigayil onlarla karşılaştı."));
trsuperobject.biblemap.put("b9_25_21", new trsuperobject(9,25,21,"Davut, ‹‹Bu adamın kırdaki malını doğrusu boş yere korudum›› demişti, ‹‹Onun mallarından hiçbir şey eksilmedi. Öyleyken bana iyilik yapacağına kötülükle karşılık verdi."));
trsuperobject.biblemap.put("b9_25_22", new trsuperobject(9,25,22,"Eğer sabaha dek adamlarından tek birini bile sağ bırakırsam, Tanrı bana aynısını, hatta daha kötüsünü yapsın!›› düşmanlarına››."));
trsuperobject.biblemap.put("b9_25_23", new trsuperobject(9,25,23,"Avigayil Davutu görünce hemen eşekten indi; Davutun önünde eğilip yüzüstü yere kapandı."));
trsuperobject.biblemap.put("b9_25_24", new trsuperobject(9,25,24,"Onun ayaklarına kapanarak şöyle yalvardı: ‹‹Efendim, suçu ben, yalnız ben üstüme alıyorum. İzin ver, ben kölen seninle konuşsun, onun söyleyeceklerini dinle."));
trsuperobject.biblemap.put("b9_25_25", new trsuperobject(9,25,25,"Yalvarırım, efendim, o kötü adam Navala aldırma. Çünkü kişiliği tıpkı adı gibidir. Adı akılsız anlamına gelir; kendisi de akılsızın biridir. Ben kulun, efendim Davutun gönderdiği ulakları görmedim."));
trsuperobject.biblemap.put("b9_25_26", new trsuperobject(9,25,26,"‹‹Ama şimdi, ey efendim, RAB senin kan dökmene ve kendi elinle öç almana engel oldu. Yaşayan RABbin adı ve senin yaşamın hakkı için yalvarırım, düşmanların ve efendime kötülük tasarlayanların tümü Naval gibi olsun."));
trsuperobject.biblemap.put("b9_25_27", new trsuperobject(9,25,27,"Ben kölenin efendime getirdiği bu armağan, seni izleyen adamlarına verilsin."));
trsuperobject.biblemap.put("b9_25_28", new trsuperobject(9,25,28,"Lütfen kölenin suçunu bağışla. RAB kesinlikle efendimin soyunu sürdürecektir; çünkü efendim RABbin savaşlarını sürdürüyor. Yaşadığın sürece sende hiçbir haksızlık bulunmasın."));
trsuperobject.biblemap.put("b9_25_29", new trsuperobject(9,25,29,"Biri kalkıp seni öldürmek amacıyla ardına düşerse, yaşamını Tanrın RAB güven altında tutacaktır; düşmanlarını sapanla taş atar gibi fırlatıp atacaktır."));
trsuperobject.biblemap.put("b9_25_30", new trsuperobject(9,25,30,"RAB, efendime söz verdiği bütün iyilikleri yerine getirip onu İsraile önder atadığında,"));
trsuperobject.biblemap.put("b9_25_31", new trsuperobject(9,25,31,"kendi öcünü almak uğruna boş yere kan dökmediğin için pişmanlık ve üzüntü duymayacaksın. RAB efendimi başarıya ulaştırdığında köleni anımsa.››"));
trsuperobject.biblemap.put("b9_25_32", new trsuperobject(9,25,32,"Davut, ‹‹Bugün seni karşıma çıkaran İsrailin Tanrısı RABbe övgüler olsun!›› diye karşılık verdi,"));
trsuperobject.biblemap.put("b9_25_33", new trsuperobject(9,25,33,"‹‹Anlayışını kutlarım! Bugün kan dökmemi ve öcümü elimle almamı engellediğin için seni kutlarım."));
trsuperobject.biblemap.put("b9_25_34", new trsuperobject(9,25,34,"Doğrusu sana kötülük etmemi önleyen İsrailin Tanrısı yaşayan RABbin adıyla derim ki, beni karşılamak için hemen gelmemiş olsaydın, gün doğuncaya dek Navalın adamlarından hiçbiri sağ kalmayacaktı.››"));
trsuperobject.biblemap.put("b9_25_35", new trsuperobject(9,25,35,"Avigayilin kendisine getirdiklerini kabul eden Davut, ‹‹Esenlikle evine dön. Sözlerine kulak verip dileğini kabul ettim›› dedi."));
trsuperobject.biblemap.put("b9_25_36", new trsuperobject(9,25,36,"Avigayil Navalın yanına döndü. Naval evinde krallara yaraşır bir şölen düzenlemişti. Çok sarhoş olduğundan neşeliydi. Bu yüzden Avigayil sabaha dek ona bir şey söylemedi."));
trsuperobject.biblemap.put("b9_25_37", new trsuperobject(9,25,37,"Ama ertesi sabah Naval ayılınca karısı ona olup bitenleri anlattı. İşte o an Navalın kalbi sıkıştı ve felç oldu."));
trsuperobject.biblemap.put("b9_25_38", new trsuperobject(9,25,38,"Yaklaşık on gün sonra da RAB Navalı cezalandırıp öldürdü."));
trsuperobject.biblemap.put("b9_25_39", new trsuperobject(9,25,39,"Davut, Navalın öldüğünü duyunca, ‹‹Beni küçümseyen Navala karşı davama bakan, kulunu kötülük etmekten alıkoyan RABbe övgüler olsun!›› dedi, ‹‹RAB Navalın kötülüğünü onun başına döndürdü.›› Sonra Davut Avigayile evlenme teklifinde bulunmak için ulaklar gönderdi."));
trsuperobject.biblemap.put("b9_25_40", new trsuperobject(9,25,40,"Davutun ulakları Karmele, Avigayilin yanına varıp, ‹‹Davut sana evlenme teklifinde bulunmak için bizi gönderdi›› dediler."));
trsuperobject.biblemap.put("b9_25_41", new trsuperobject(9,25,41,"Avigayil yüzüstü yere kapanarak, ‹‹Ben kölen sana hizmet etmeye ve efendimin ulaklarının ayaklarını yıkamaya hazırım›› diye yanıtladı."));
trsuperobject.biblemap.put("b9_25_42", new trsuperobject(9,25,42,"Hemen kalkıp eşeğe bindi. Yanına beş hizmetçisini alıp Davutun ulaklarını izleyerek yola koyuldu. Sonra Davutun karısı oldu."));
trsuperobject.biblemap.put("b9_25_43", new trsuperobject(9,25,43,"Davut Yizreelli Ahinoamı da eş olarak almıştı. Böylece ikisi de onun karısı oldular."));
trsuperobject.biblemap.put("b9_25_44", new trsuperobject(9,25,44,"Bu arada Saul, Davut'un karısı olan kızı Mikal'ı Gallimli Layiş oğlu Palti'ye vermişti."));
trsuperobject.biblemap.put("b9_26_1", new trsuperobject(9,26,1,"Zifliler Givaya, Saulun yanına gidip, ‹‹Davut Yeşimona bakan Hakila Tepesinde gizleniyor›› dediler."));
trsuperobject.biblemap.put("b9_26_2", new trsuperobject(9,26,2,"Bunun üzerine Saul üç bin seçme İsrailli askerle Zif Çölünde Davutu aramaya çıktı."));
trsuperobject.biblemap.put("b9_26_3", new trsuperobject(9,26,3,"Yeşimona bakan Hakila Tepesinde, yol kenarında ordugah kurdu. Kırda bulunan Davut, Saulun peşine düştüğünü anlayınca,"));
trsuperobject.biblemap.put("b9_26_4", new trsuperobject(9,26,4,"gözcü gönderdi. Böylece Saulun oraya geldiğini saptadı."));
trsuperobject.biblemap.put("b9_26_5", new trsuperobject(9,26,5,"Bunun üzerine Davut, Saulun ordugah kurduğu yere gitti ve Saulla ordusunun başkomutanı Ner oğlu Avnerin nerede yattıklarını gördü. Saul ordugahın ortasında, askerler de çevresinde yatıyorlardı."));
trsuperobject.biblemap.put("b9_26_6", new trsuperobject(9,26,6,"O zaman Davut, Hititli Ahimelek ile Yoavın kardeşi, Seruya oğlu Avişaya, ‹‹Kim benimle ordugaha, Saulun yanına gelecek?›› diye sordu. Avişay, ‹‹Ben seninle geleceğim›› diye karşılık verdi."));
trsuperobject.biblemap.put("b9_26_7", new trsuperobject(9,26,7,"Davutla Avişay o gece ordugaha girdiler. Saul, mızrağı başucunda yere saplanmış, ordugahın ortasında uyuyordu. Avnerle askerler de çevresinde uyuyorlardı."));
trsuperobject.biblemap.put("b9_26_8", new trsuperobject(9,26,8,"Avişay Davuta, ‹‹Bugün Tanrı düşmanını senin eline teslim etti›› dedi, ‹‹Şimdi bırak da, onu kendi mızrağıyla bir atışta yere çakayım. İkinci kez vurmama gerek kalmayacak.››"));
trsuperobject.biblemap.put("b9_26_9", new trsuperobject(9,26,9,"Ne var ki Davut, ‹‹Onu öldürme!›› dedi, ‹‹RABbin meshettiği kişiye kim el uzatırsa, suçlu çıkar."));
trsuperobject.biblemap.put("b9_26_10", new trsuperobject(9,26,10,"Yaşayan RABbin adıyla derim ki, RAB kendisi onu öldürecektir; ya günü gelince ölecek, ya da savaşta vurulup yok olacak."));
trsuperobject.biblemap.put("b9_26_11", new trsuperobject(9,26,11,"Ama RABbin meshettiği kişiye el uzatmaktan RAB beni uzak tutsun! Haydi, Saulun başucundaki mızrakla su matarasını al da gidelim.››"));
trsuperobject.biblemap.put("b9_26_12", new trsuperobject(9,26,12,"Böylece Davut Saulun başucundan mızrağını ve su matarasını aldı. Sonra oradan uzaklaştılar. Onları gören olmadı. Kimse olup bitenin farkına varmadı, uyanan da olmadı. Hepsi uyuyorlardı, çünkü RAB onlara derin bir uyku vermişti."));
trsuperobject.biblemap.put("b9_26_13", new trsuperobject(9,26,13,"Davut karşı yakaya geçip tepenin üstünde, onlardan uzak bir yerde durdu. Aralarında epeyce mesafe vardı."));
trsuperobject.biblemap.put("b9_26_14", new trsuperobject(9,26,14,"Davut askerlere ve Ner oğlu Avnere, ‹‹Ey Avner, bana yanıt vermeyecek misin?›› diye seslendi. Avner, ‹‹Sen kimsin ki krala sesleniyorsun?›› diye karşılık verdi."));
trsuperobject.biblemap.put("b9_26_15", new trsuperobject(9,26,15,"Davut, ‹‹Sen yiğit biri değil misin?›› dedi, ‹‹İsrailde senin gibisi var mı? Öyleyse neden efendin kralı korumadın? Çünkü biri onu öldürmek için ordugaha girdi."));
trsuperobject.biblemap.put("b9_26_16", new trsuperobject(9,26,16,"Görevini iyi yapmadın. Yaşayan RABbin adıyla derim ki, hepiniz ölümü hak ettiniz; çünkü efendinizi, RABbin meshettiği kişiyi korumadınız. Bak bakalım, kralın başucundaki mızrağıyla su matarası nerede?››"));
trsuperobject.biblemap.put("b9_26_17", new trsuperobject(9,26,17,"Davutun sesini tanıyan Saul, ‹‹Davut, oğlum, bu senin sesin mi?›› diye sordu. Davut, ‹‹Evet, efendim kral, benim sesim›› diye karşılık verdi,"));
trsuperobject.biblemap.put("b9_26_18", new trsuperobject(9,26,18,"‹‹Efendim, ben kulunu neden kovalıyorsun? Ne yaptım? Ne suç işledim?"));
trsuperobject.biblemap.put("b9_26_19", new trsuperobject(9,26,19,"Lütfen, efendim kral, kulunun sözlerine kulak ver. Eğer seni bana karşı kışkırtan RAB ise, bir sunu Onu yatıştırır. Ama bunu yapan insanlarsa, RABbin önünde lanetli olsunlar! Çünkü, ‹Git, başka ilahlara kulluk et› diyerek, RABbin mirasından bana düşen paydan bugün beni uzaklaştırdılar."));
trsuperobject.biblemap.put("b9_26_20", new trsuperobject(9,26,20,"Ne olur, kanım RABden uzak topraklara dökülmesin. İsrail Kralı, dağlarda keklik avlayan avcı gibi, bir pireyi avlamaya çıkmış!››"));
trsuperobject.biblemap.put("b9_26_21", new trsuperobject(9,26,21,"Bunun üzerine Saul, ‹‹Günah işledim›› diye karşılık verdi, ‹‹Davut, oğlum, geri dön. Bugün yaşamıma değer verdiğin için sana bir daha kötülük yapmayacağım. Gerçekten akılsızca davrandım, çok büyük yanlışlık yaptım.››"));
trsuperobject.biblemap.put("b9_26_22", new trsuperobject(9,26,22,"Davut, ‹‹İşte kralın mızrağı!›› dedi, ‹‹Adamlarından biri gelip alsın."));
trsuperobject.biblemap.put("b9_26_23", new trsuperobject(9,26,23,"RAB herkesi doğruluğuna ve bağlılığına göre ödüllendirir. Bugün RAB seni elime teslim ettiği halde, ben RABbin meshettiği kişiye elimi uzatmak istemedim."));
trsuperobject.biblemap.put("b9_26_24", new trsuperobject(9,26,24,"Bugün ben senin yaşamına nasıl değer verdiysem, RAB de benim yaşamıma öyle değer versin ve beni her sıkıntıdan kurtarsın.››"));
trsuperobject.biblemap.put("b9_26_25", new trsuperobject(9,26,25,"Saul, ‹‹Davut, oğlum, RAB seni kutsasın!›› dedi, ‹‹Sen kesinlikle büyük işler yapacak, başarılı olacaksın!›› Bundan sonra Davut yoluna koyuldu, Saul da evine döndü."));
trsuperobject.biblemap.put("b9_27_1", new trsuperobject(9,27,1,"Davut, ‹‹Bir gün Saulun eliyle yok olacağım›› diye düşündü, ‹‹Benim için en iyisi hemen Filist topraklarına kaçmak. O zaman Saul İsrailin her yanında beni aramaktan vazgeçer; ben de onun elinden kurtulmuş olurum.››"));
trsuperobject.biblemap.put("b9_27_2", new trsuperobject(9,27,2,"Böylece Davutla yanındaki altı yüz kişi kalkıp Gat Kralı Maok oğlu Akişin tarafına geçtiler."));
trsuperobject.biblemap.put("b9_27_3", new trsuperobject(9,27,3,"Aileleriyle birlikte Gatta Akişin yanına yerleştiler. İki karısı Yizreelli Ahinoamla Karmelli Navalın dul karısı Avigayil de Davutun yanındaydı."));
trsuperobject.biblemap.put("b9_27_4", new trsuperobject(9,27,4,"Saul Davutun Gata kaçtığını duyunca, artık onu aramaktan vazgeçti."));
trsuperobject.biblemap.put("b9_27_5", new trsuperobject(9,27,5,"Davut Akişe, ‹‹Benden hoşnut kaldıysan, çevre kentlerden birinde bana bir yer versinler de orada oturayım›› dedi, ‹‹Çünkü ben kulunun seninle birlikte kral kentinde yaşamasına gerek yok.››"));
trsuperobject.biblemap.put("b9_27_6", new trsuperobject(9,27,6,"Akiş o gün ona Ziklak Kentini verdi. Bundan ötürü Ziklak bugün de Yahuda krallarına aittir."));
trsuperobject.biblemap.put("b9_27_7", new trsuperobject(9,27,7,"Davut Filist topraklarında bir yıl dört ay yaşadı."));
trsuperobject.biblemap.put("b9_27_8", new trsuperobject(9,27,8,"Bu süre içinde Davutla adamları gidip Geşurlulara, Girizlilere ve Amaleklilere baskınlar yaptılar. Bunlar uzun zamandan beri Şura, hatta Mısıra dek uzanan topraklarda yaşıyorlardı."));
trsuperobject.biblemap.put("b9_27_9", new trsuperobject(9,27,9,"Davut bir bölgeye saldırdığında kadın erkek demez, kimseyi sağ bırakmazdı; yalnız davarları, sığırları, eşekleri, develeri ve giysileri alıp Akişe dönerdi."));
trsuperobject.biblemap.put("b9_27_10", new trsuperobject(9,27,10,"Akiş, ‹‹Bugün nerelere baskın düzenlediniz?›› diye sorardı. Davut da, ‹‹Yahudanın güneyine, Yerahmeellilerin ve Kenlilerin güney bölgesine saldırdık›› derdi."));
trsuperobject.biblemap.put("b9_27_11", new trsuperobject(9,27,11,"Davut, kendisiyle Gata kimseyi götürmemek için kadın erkek kimseyi sağ bırakmazdı. Çünkü, ‹‹Gata gidip, ‹Davut şöyle yaptı, böyle yaptı› diyerek bize karşı bilgi aktarmasınlar›› diye düşünürdü. Davut, Filist topraklarında yaşadığı sürece bu yöntemi uyguladı."));
trsuperobject.biblemap.put("b9_27_12", new trsuperobject(9,27,12,"Akiş Davut'a güven duymaya başladı. ‹‹Davut kendi halkı olan İsrailliler'in nefretine uğradı. Bundan böyle benim hizmetimde kalacak›› diye düşünüyordu."));
trsuperobject.biblemap.put("b9_28_1", new trsuperobject(9,28,1,"O sırada Filistliler İsraille savaşmak için askeri birliklerini topladılar. Akiş Davuta, ‹‹Adamlarınla birlikte benim yanımda savaşacağını bilmelisin›› dedi."));
trsuperobject.biblemap.put("b9_28_2", new trsuperobject(9,28,2,"Davut, ‹‹O zaman sen de kulunun neler yapabileceğini göreceksin!›› diye karşılık verdi. Akiş, ‹‹İyi!›› dedi, ‹‹Yaşadığın sürece seni kendime koruma görevlisi atayacağım.››"));
trsuperobject.biblemap.put("b9_28_3", new trsuperobject(9,28,3,"Samuel ölmüş, bütün İsrail halkı onun için yas tutmuştu. Onu kendi kenti Ramada gömmüşlerdi. Saul da cincilerle ruhlara danışanları ülkeden kovmuştu."));
trsuperobject.biblemap.put("b9_28_4", new trsuperobject(9,28,4,"Filistliler toplanıp Şuneme gittiler ve orada ordugah kurdular. Saul da bütün İsraillileri toplayıp Gilboa Dağında ordugah kurdu."));
trsuperobject.biblemap.put("b9_28_5", new trsuperobject(9,28,5,"Saul Filist ordusunu görünce korkup büyük dehşete kapıldı."));
trsuperobject.biblemap.put("b9_28_7", new trsuperobject(9,28,7,"Bunun üzerine Saul görevlilerine, ‹‹Bana bir cinci kadın bulun da varıp ona danışayım›› diye buyruk verdi. Görevliler, ‹‹Eyn-Dorda bir cinci kadın var›› dediler."));
trsuperobject.biblemap.put("b9_28_8", new trsuperobject(9,28,8,"Böylece Saul başka giysilere bürünüp kılığını değiştirdi. Geceleyin yanına iki kişi alıp kadının yaşadığı yere gitti. Kadına, ‹‹Lütfen benim için ruhlara danış ve sana söyleyeceğim kişiyi çağır›› dedi."));
trsuperobject.biblemap.put("b9_28_9", new trsuperobject(9,28,9,"Ama kadın ona şu karşılığı verdi: ‹‹Saulun neler yaptığını, cincilerle ruhlara danışanları ülkeden kovduğunu biliyorsun. Öyleyse neden beni öldürmek için tuzak kuruyorsun?››"));
trsuperobject.biblemap.put("b9_28_10", new trsuperobject(9,28,10,"Saul, ‹‹Yaşayan RABbin adıyla derim ki, bundan sana bir kötülük gelmeyecek›› diye ant içti."));
trsuperobject.biblemap.put("b9_28_11", new trsuperobject(9,28,11,"Bunun üzerine kadın, ‹‹Sana kimi çağırayım?›› diye sordu. Saul, ‹‹Bana Samueli çağır›› dedi."));
trsuperobject.biblemap.put("b9_28_12", new trsuperobject(9,28,12,"Kadın, Samueli görünce çığlık atarak, ‹‹Sen Saulsun! Neden beni kandırdın?›› dedi."));
trsuperobject.biblemap.put("b9_28_13", new trsuperobject(9,28,13,"Kral ona, ‹‹Korkma!›› dedi, ‹‹Ne görüyorsun?›› Kadın, ‹‹Yerin altından çıkan bir ilah görüyorum›› diye karşılık verdi."));
trsuperobject.biblemap.put("b9_28_14", new trsuperobject(9,28,14,"Saul, ‹‹Neye benziyor?›› diye sordu. Kadın, ‹‹Cüppe giymiş yaşlı bir adam yukarıya çıkıyor›› dedi. O zaman Saul onun Samuel olduğunu anladı; eğilip yüzüstü yere kapandı."));
trsuperobject.biblemap.put("b9_28_15", new trsuperobject(9,28,15,"Samuel Saula, ‹‹Neden beni çağırtıp rahatsız ettin?›› dedi. Saul, ‹‹Büyük sıkıntı içindeyim›› diye yanıtladı, ‹‹Filistliler bana karşı savaşıyor ve Tanrı da beni terk etti. Artık bana ne peygamberler aracılığıyla, ne de düşlerle yanıt veriyor. Bu yüzden, ne yapmam gerektiğini bana bildirmen için seni çağırttım.››"));
trsuperobject.biblemap.put("b9_28_16", new trsuperobject(9,28,16,"Samuel, ‹‹RAB seni terk edip sana düşman olduğuna göre, neden bana danışıyorsun?›› dedi,"));
trsuperobject.biblemap.put("b9_28_17", new trsuperobject(9,28,17,"‹‹RAB benim aracılığımla söylediğini yaptı, krallığı senden alıp soydaşın Davuta verdi."));
trsuperobject.biblemap.put("b9_28_18", new trsuperobject(9,28,18,"Çünkü sen RABbin buyruğuna uymadın, Onun alevlenen öfkesini Amaleklilere uygulamadın. RAB bugün bunları bu yüzden başına getirdi."));
trsuperobject.biblemap.put("b9_28_19", new trsuperobject(9,28,19,"RAB seni de, İsrail halkını da Filistlilerin eline teslim edecek. Yarın sen ve oğulların bana katılacaksınız. RAB İsrail ordusunu da Filistlilerin eline teslim edecek.››"));
trsuperobject.biblemap.put("b9_28_20", new trsuperobject(9,28,20,"Saul birden boylu boyunca yere düştü. Samuelin sözlerinden ötürü büyük korkuya kapıldı. Gücü de kalmamıştı; çünkü bütün gün, bütün gece yemek yememişti."));
trsuperobject.biblemap.put("b9_28_21", new trsuperobject(9,28,21,"Kadın Saula yaklaştı. Onun büyük şaşkınlık içinde olduğunu görünce, ‹‹Bak, kölen sözünü dinledi›› dedi, ‹‹Canımı tehlikeye atarak benden istediğini yaptım."));
trsuperobject.biblemap.put("b9_28_22", new trsuperobject(9,28,22,"Şimdi lütfen kölenin söyleyeceğini dinle. İzin ver de, önüne biraz yemek koyayım. Yoluna devam edecek gücün olması için yemek yemelisin.››"));
trsuperobject.biblemap.put("b9_28_23", new trsuperobject(9,28,23,"Ama Saul, ‹‹Yemem›› diyerek reddetti. Ancak hizmetkârlarıyla kadın zorlayınca, onların dediğini yaptı. Yerden kalkıp yatağın üzerine oturdu."));
trsuperobject.biblemap.put("b9_28_24", new trsuperobject(9,28,24,"Kadının evinde besili bir dana vardı. Kadın onu hemen kesti. Un alıp yoğurdu ve mayasız ekmek pişirdi."));
trsuperobject.biblemap.put("b9_28_25", new trsuperobject(9,28,25,"Sonra Saul'la görevlilerinin önüne koydu. Onlar da yediler. Sonra o gece kalkıp gittiler."));
trsuperobject.biblemap.put("b9_29_1", new trsuperobject(9,29,1,"Filistliler bütün ordularını Afekte topladılar. İsrailliler ise Yizreeldeki pınarın yanına kurdukları ordugahta kalıyorlardı."));
trsuperobject.biblemap.put("b9_29_2", new trsuperobject(9,29,2,"Filist beyleri yüzer ve biner kişilik birliklerle ilerliyordu. Davutla adamlarıysa Akişle birlikte geriden geliyorlardı."));
trsuperobject.biblemap.put("b9_29_3", new trsuperobject(9,29,3,"Filistli komutanlar, ‹‹Bu İbranilerin burada ne işi var?›› diye sorunca, Akiş şu karşılığı verdi: ‹‹Bu, İsrail Kralı Saulun görevlisi Davuttur. Bir yıldan uzun süredir yanımda kalıyor. Bana geldiğinden beri kendisinde hiçbir kötülük bulamadım.››"));
trsuperobject.biblemap.put("b9_29_4", new trsuperobject(9,29,4,"Ama Filistli komutanlar Akişe öfkelendiler. ‹‹Adamı geri gönder, kendisine verdiğin yere dönsün›› dediler, ‹‹Bizimle birlikte savaşa gelmesin; yoksa savaş sırasında bize karşı çıkar. Efendisinin beğenisini nasıl kazanabilir? Adamlarımızın başını ona vermekten daha iyi bir yol bulabilir mi?"));
trsuperobject.biblemap.put("b9_29_5", new trsuperobject(9,29,5,"Çalıp oynarken,  ‹Saul binlercesini öldürdü,Davutsa on binlercesini›"));
trsuperobject.biblemap.put("b9_29_6", new trsuperobject(9,29,6,"Bunun üzerine Akiş, Davutu çağırıp, ‹‹Yaşayan RABbin adıyla derim ki, sen dürüst bir kişisin›› dedi, ‹‹Benimle birlikte savaşa katılmanı isterdim. Yanıma geldiğin günden bu yana ters bir davranışını görmedim. Ama Filist beyleri seni uygun görmedi."));
trsuperobject.biblemap.put("b9_29_7", new trsuperobject(9,29,7,"Şimdi geri dön ve esenlikle git. Filist beylerinin gözünde ters bir davranışta bulunma.››"));
trsuperobject.biblemap.put("b9_29_8", new trsuperobject(9,29,8,"Davut, ‹‹Ama ben ne yaptım?›› diye sordu, ‹‹Yanına geldiğimden bu yana bende ne buldun ki, gidip efendim kralın düşmanlarına karşı savaşmayayım?››"));
trsuperobject.biblemap.put("b9_29_9", new trsuperobject(9,29,9,"Akiş, ‹‹Biliyorum, sen benim gözümde Tanrının bir meleği gibi iyisin›› diye yanıtladı, ‹‹Ne var ki Filistli komutanlar, ‹Bizimle savaşa gelmesin› diyorlar."));
trsuperobject.biblemap.put("b9_29_10", new trsuperobject(9,29,10,"Seninle gelmiş olan efendin Saulun kullarıyla birlikte sabah erkenden kalkın ve tan ağarır ağarmaz gidin.››"));
trsuperobject.biblemap.put("b9_29_11", new trsuperobject(9,29,11,"Böylece Davut'la adamları Filist ülkesine dönmek üzere sabah erkenden kalktılar. Filistliler ise Yizreel'e gittiler."));
trsuperobject.biblemap.put("b9_30_1", new trsuperobject(9,30,1,"Davutla adamları üçüncü gün Ziklak Kentine vardılar. Bu arada Amalekliler Negev bölgesiyle Ziklaka baskın yapmış, Ziklak Kentini yakıp yıkmışlardı."));
trsuperobject.biblemap.put("b9_30_2", new trsuperobject(9,30,2,"Kimseyi öldürmemişlerdi, ama kadınlarla orada yaşayan genç, yaşlı herkesi tutsak etmişlerdi. Sonra onları da yanlarına alıp yollarına gitmişlerdi."));
trsuperobject.biblemap.put("b9_30_3", new trsuperobject(9,30,3,"Davutla adamları oraya varınca kentin ateşe verildiğini, karılarının, oğullarının, kızlarının tutsak alındığını anladılar."));
trsuperobject.biblemap.put("b9_30_4", new trsuperobject(9,30,4,"Güçleri tükeninceye dek hıçkıra hıçkıra ağladılar."));
trsuperobject.biblemap.put("b9_30_5", new trsuperobject(9,30,5,"Davutun iki karısı, Yizreelli Ahinoam ile Karmelli Navalın dulu Avigayil de tutsak edilmişti."));
trsuperobject.biblemap.put("b9_30_6", new trsuperobject(9,30,6,"Davut büyük sıkıntı içindeydi. Çünkü herkes oğulları, kızları için acı çekiyor ve, ‹‹Davutu taşlayalım›› diyordu. Ama Davut, Tanrısı RABde güç bularak,"));
trsuperobject.biblemap.put("b9_30_7", new trsuperobject(9,30,7,"Ahimelek oğlu Kâhin Aviyatara, ‹‹Bana efodu getir›› dedi. Aviyatar efodu getirdi."));
trsuperobject.biblemap.put("b9_30_8", new trsuperobject(9,30,8,"Davut RABbe danışarak, ‹‹Bu akıncıların ardına düşersem, onlara yetişir miyim?›› diye sordu. RAB, ‹‹Artlarına düş, kesinlikle onlara yetişip tutsakları kurtaracaksın›› diye yanıtladı."));
trsuperobject.biblemap.put("b9_30_9-10", new trsuperobject(9,30,9-10,"Bunun üzerine Davut yanındaki altı yüz kişiyle yola çıktı. Besor Vadisine geldiler. Vadiyi geçemeyecek kadar bitkin düşen iki yüz kişi orada kaldı. Davut dört yüz kişiyle akıncıları kovalamayı sürdürdü."));
trsuperobject.biblemap.put("b9_30_11", new trsuperobject(9,30,11,"Kırda bir Mısırlı bulup Davuta getirdiler. Yiyip içmesi için ona yiyecek, içecek verdiler."));
trsuperobject.biblemap.put("b9_30_12", new trsuperobject(9,30,12,"Bir parça incir pestili ile iki salkım kuru üzüm de verdiler. Adam yiyince canlandı. Üç gün üç gecedir yiyip içmemişti."));
trsuperobject.biblemap.put("b9_30_13", new trsuperobject(9,30,13,"Davut ona, ‹‹Kime bağlısın? Nerelisin?›› diye sordu. Genç adam, ‹‹Mısırlıyım, bir Amaleklinin kölesiyim›› diye yanıtladı, ‹‹Üç gün önce hastalanınca, efendim beni bıraktı."));
trsuperobject.biblemap.put("b9_30_14", new trsuperobject(9,30,14,"Keretlilerin güney sınırlarına, Yahuda topraklarına, Kalevin güneyine baskınlar düzenlemiş, Ziklak Kentini de ateşe vermiştik.››"));
trsuperobject.biblemap.put("b9_30_15", new trsuperobject(9,30,15,"Davut, ‹‹Beni bu akıncılara götürebilir misin?›› diye sordu. Mısırlı genç, ‹‹Beni öldürmeyeceğine ya da efendimin eline teslim etmeyeceğine dair Tanrının önünde ant içersen, seni akıncıların olduğu yere götürürüm›› diye karşılık verdi."));
trsuperobject.biblemap.put("b9_30_16", new trsuperobject(9,30,16,"Böylece Mısırlı Davutu götürdü. Akıncılar dört bir yana dağılmışlardı. Filist ve Yahuda topraklarından topladıkları büyük yağmadan yiyip içiyor, eğlenip oynuyorlardı."));
trsuperobject.biblemap.put("b9_30_17", new trsuperobject(9,30,17,"Davut ertesi gün tan vaktinden akşama dek onları öldürdü. Develere binip kaçan dört yüz genç dışında içlerinden kurtulan olmadı."));
trsuperobject.biblemap.put("b9_30_18", new trsuperobject(9,30,18,"Davut Amaleklilerin ele geçirdiği her şeyi, bu arada da iki karısını kurtardı."));
trsuperobject.biblemap.put("b9_30_19", new trsuperobject(9,30,19,"Gençler, yaşlılar, oğullar, kızlar, yağmalanan mallar, kısacası Amaleklilerin aldıklarından hiçbir şey eksik kalmadı. Davut tümünü geri aldı."));
trsuperobject.biblemap.put("b9_30_20", new trsuperobject(9,30,20,"Bütün koyunlarla sığırları da aldı. Adamları, bunları öbür hayvanların önünden sürerek, ‹‹Bunlar Davutun yağmaladıkları›› diyorlardı."));
trsuperobject.biblemap.put("b9_30_21", new trsuperobject(9,30,21,"Bundan sonra Davut, daha ileriye gidemeyecek kadar bitkin düşüp Besor Vadisinde kalan iki yüz kişinin bulunduğu yere vardı. Onlar da Davutla yanındakileri karşılamaya çıktılar. Davut yaklaşınca onlara esenlik diledi."));
trsuperobject.biblemap.put("b9_30_22", new trsuperobject(9,30,22,"Ama Davutla giden adamlardan kötü ve değersiz olanların tümü, ‹‹Madem bizimle birlikte gitmediler, geri aldığımız yağmadan onlara hiçbir pay vermeyeceğiz›› dediler, ‹‹Her biri yalnız karısıyla çocuklarını alıp gitsin.››"));
trsuperobject.biblemap.put("b9_30_23", new trsuperobject(9,30,23,"Ama Davut, ‹‹Hayır, kardeşlerim!›› dedi, ‹‹RABbin bize verdikleri konusunda böyle davranamayız! O bizi korudu ve bize saldıran akıncıları elimize teslim etti."));
trsuperobject.biblemap.put("b9_30_24", new trsuperobject(9,30,24,"Sizin bu söylediklerinizi kim kabul eder? Savaşa gidenle eşyanın yanında kalanın payı aynıdır. Her şey eşit paylaşılacak!››"));
trsuperobject.biblemap.put("b9_30_25", new trsuperobject(9,30,25,"O günden sonra Davut bunu İsrail için bugüne dek geçerli bir kural ve ilke haline getirdi."));
trsuperobject.biblemap.put("b9_30_26", new trsuperobject(9,30,26,"Davut Ziklaka dönünce, dostları olan Yahuda ileri gelenlerine yağma mallardan göndererek, ‹‹İşte RABbin düşmanlarından yağmalanan mallardan size bir armağan›› dedi."));
trsuperobject.biblemap.put("b9_30_27", new trsuperobject(9,30,27,"Sonra Beytel, Negevdeki Ramot, Yattir,"));
trsuperobject.biblemap.put("b9_30_28", new trsuperobject(9,30,28,"Aroer, Sifmot, Eştemoa,"));
trsuperobject.biblemap.put("b9_30_29", new trsuperobject(9,30,29,"Rakal, Yerahmeellilerin, Kenlilerin kentlerinde,"));
trsuperobject.biblemap.put("b9_30_30", new trsuperobject(9,30,30,"Horma, Bor-Aşan, Atak,"));
trsuperobject.biblemap.put("b9_30_31", new trsuperobject(9,30,31,"Hevron'da oturanlara ve adamlarıyla birlikte sık sık uğradığı yerlerin tümüne yağmalanan mallardan gönderdi."));
trsuperobject.biblemap.put("b9_31_1", new trsuperobject(9,31,1,"Filistliler İsraillilerle savaşa tutuştu. İsrailliler Filistlilerin önünden kaçtı. Birçoğu Gilboa Dağında ölüp yere serildi."));
trsuperobject.biblemap.put("b9_31_2", new trsuperobject(9,31,2,"Filistliler Saulla oğullarının ardına düştüler. Saulun oğulları Yonatanı, Avinadavı ve Malkişuayı yakalayıp öldürdüler."));
trsuperobject.biblemap.put("b9_31_3", new trsuperobject(9,31,3,"Saulun çevresinde savaş kızıştı. Derken Saul Filistli okçular tarafından vuruldu ve ağır yaralandı."));
trsuperobject.biblemap.put("b9_31_4", new trsuperobject(9,31,4,"Saul, silahını taşıyan adama, ‹‹Kılıcını çek de bana sapla›› dedi, ‹‹Yoksa bu sünnetsizler gelip bana kılıç saplayacak ve benimle alay edecekler.›› Ama silah taşıyıcısı büyük bir korkuya kapılarak bunu yapmak istemedi. Bunun üzerine Saul kılıcını çekip kendini üzerine attı."));
trsuperobject.biblemap.put("b9_31_5", new trsuperobject(9,31,5,"Saulun öldüğünü görünce, silah taşıyıcısı da kendini kılıcının üzerine attı ve Saulla birlikte öldü."));
trsuperobject.biblemap.put("b9_31_6", new trsuperobject(9,31,6,"Böylece Saul, üç oğlu, silah taşıyıcısı ve bütün adamları aynı gün öldüler."));
trsuperobject.biblemap.put("b9_31_7", new trsuperobject(9,31,7,"Vadinin öbür tarafında ve Şeria Irmağının karşı yakasında oturan İsrailliler, İsrail ordusunun kaçtığını, Saulla oğullarının öldüğünü anlayınca, kentlerini terk edip kaçmaya başladılar. Filistliler gelip bu kentlere yerleştiler."));
trsuperobject.biblemap.put("b9_31_8", new trsuperobject(9,31,8,"Ertesi gün Filistliler, öldürülenleri soymak için geldiklerinde, Saulla üç oğlunun Gilboa Dağında öldüğünü gördüler."));
trsuperobject.biblemap.put("b9_31_9", new trsuperobject(9,31,9,"Saulun başını kesip silahlarını aldılar. Sonra bu iyi haberin putlarının tapınağında ve halk arasında duyurulması için Filist ülkesinin her yanına ulaklar gönderdiler."));
trsuperobject.biblemap.put("b9_31_10", new trsuperobject(9,31,10,"Saulun silahlarını Aştoretin tapınağına koyup cesedini Beytşean Kentinin suruna çaktılar."));
trsuperobject.biblemap.put("b9_31_11", new trsuperobject(9,31,11,"Yaveş-Gilat halkı Filistlilerin Saula yaptıklarını duydu."));
trsuperobject.biblemap.put("b9_31_12", new trsuperobject(9,31,12,"Bütün yiğitler geceleyin yola koyularak Beytşeana gittiler. Saulla oğullarının cesetlerini Beytşean surundan indirip Yaveşe götürdüler, orada yaktılar."));
trsuperobject.biblemap.put("b9_31_13", new trsuperobject(9,31,13,"Sonra kemiklerini toplayıp Yaveş'teki ılgın ağacının altına gömdüler ve yedi gün oruç tuttular."));

