visuperobject.biblemap.put("b1_1_1", new visuperobject(1,1,1,"Ban đầu Ðức Chúa Trời dựng nên trời đất."));
visuperobject.biblemap.put("b1_1_2", new visuperobject(1,1,2,"Vả, đất là vô hình và trống không, sự mờ tối ở trên mặt vực; Thần Ðức Chúa Trời vận hành trên mặt nước."));
visuperobject.biblemap.put("b1_1_3", new visuperobject(1,1,3,"Ðức Chúa Trời phán rằng: Phải có sự sáng; thì có sự sáng."));
visuperobject.biblemap.put("b1_1_4", new visuperobject(1,1,4,"Ðức Chúa Trời thấy sự sáng là tốt lành, bèn phân sáng ra cùng tối."));
visuperobject.biblemap.put("b1_1_5", new visuperobject(1,1,5,"Ðức Chúa Trời đặt tên sự sáng là ngày; sự tối là đêm. Vậy, có buổi chiều và buổi mai; ấy là ngày thứ nhứt."));
visuperobject.biblemap.put("b1_1_6", new visuperobject(1,1,6,"Ðức Chúa Trời lại phán rằng: Phải có một khoảng không ở giữa nước đặng phân rẽ nước cách với nước."));
visuperobject.biblemap.put("b1_1_7", new visuperobject(1,1,7,"Ngài làm nên khoảng không, phân rẽ nước ở dưới khoảng không cách với nước ở trên khoảng không; thì có như vậy."));
visuperobject.biblemap.put("b1_1_8", new visuperobject(1,1,8,"Ðức Chúa Trời đặt tên khoảng không là trời. Vậy, có buổi chiều và buổi mai; ấy là ngày thứ nhì."));
visuperobject.biblemap.put("b1_1_9", new visuperobject(1,1,9,"Ðức Chúa Trời lại phán rằng: Những nước ở dưới trời phải tụ lại một nơi, và phải có chỗ khô cạn bày ra; thì có như vậy."));
visuperobject.biblemap.put("b1_1_10", new visuperobject(1,1,10,"Ðức Chúa Trời đặt tên chỗ khô cạn là đất, còn nơi nước tụ lại là biển. Ðức Chúa Trời thấy điều đó là tốt lành."));
visuperobject.biblemap.put("b1_1_11", new visuperobject(1,1,11,"Ðức Chúa Trời lại phán rằng: Ðất phải sanh cây cỏ; cỏ kết hột giống, cây trái kết quả, tùy theo loại mà có hột giống trong mình trên đất; thì có như vậy."));
visuperobject.biblemap.put("b1_1_12", new visuperobject(1,1,12,"Ðất sanh cây cỏ: cỏ kết hột tùy theo loại, cây kết quả có hột trong mình, tùy theo loại. Ðức Chúa Trời thấy điều đó là tốt lành."));
visuperobject.biblemap.put("b1_1_13", new visuperobject(1,1,13,"Vậy, có buổi chiều và buổi mai; ấy là ngày thứ ba."));
visuperobject.biblemap.put("b1_1_14", new visuperobject(1,1,14,"Ðức Chúa Trời lại phán rằng: Phải có các vì sáng trong khoảng không trên trời, đặng phân ra ngày với đêm, và dùng làm dấu để định thì tiết, ngày và năm;"));
visuperobject.biblemap.put("b1_1_15", new visuperobject(1,1,15,"lại dùng làm vì sáng trong khoảng không trên trời để soi xuống đất; thì có như vậy."));
visuperobject.biblemap.put("b1_1_16", new visuperobject(1,1,16,"Ðức Chúa Trời làm nên hai vì sáng lớn; vì lớn hơn để cai trị ban ngày, vì nhỏ hơn để cai trị ban đêm; Ngài cũng làm các ngôi sao."));
visuperobject.biblemap.put("b1_1_17", new visuperobject(1,1,17,"Ðức Chúa Trời đặt các vì đó trong khoảng không trên trời, đặng soi sáng đất,"));
visuperobject.biblemap.put("b1_1_18", new visuperobject(1,1,18,"đặng cai trị ban ngày và ban đêm, đặng phân ra sự sáng với sự tối. Ðức Chúa Trời thấy điều đó là tốt lành."));
visuperobject.biblemap.put("b1_1_19", new visuperobject(1,1,19,"Vậy, có buổi chiều và buổi mai; ấy là ngày thứ tư."));
visuperobject.biblemap.put("b1_1_20", new visuperobject(1,1,20,"Ðức Chúa Trời lại phán rằng: Nước phải sanh các vật sống cho nhiều, và các loài chim phải bay trên mặt đất trong khoảng không trên trời."));
visuperobject.biblemap.put("b1_1_21", new visuperobject(1,1,21,"Ðức Chúa Trời dựng nên các loài cá lớn, các vật sống hay động nhờ nước mà sanh nhiều ra, tùy theo loại, và các loài chim hay bay, tùy theo loại. Ðức Chúa Trời thấy điều đó là tốt lành."));
visuperobject.biblemap.put("b1_1_22", new visuperobject(1,1,22,"Ðức Chúa Trời ban phước cho các loài đó mà phán rằng: Hãy sanh sản, thêm nhiều, làm cho đầy dẫy dưới biển; còn các loài chim hãy sanh sản trên đất cho nhiều."));
visuperobject.biblemap.put("b1_1_23", new visuperobject(1,1,23,"Vậy, có buổi chiều và buổi mai; ấy là ngày thứ năm."));
visuperobject.biblemap.put("b1_1_24", new visuperobject(1,1,24,"Ðức Chúa Trời lại phán rằng: Ðất phải sanh các vật sống tùy theo loại, tức súc vật, côn trùng, và thú rừng, đều tùy theo loại; thì có như vậy."));
visuperobject.biblemap.put("b1_1_25", new visuperobject(1,1,25,"Ðức Chúa Trời làm nên các loài thú rừng tùy theo loại, súc vật tùy theo loại, và các côn trùng trên đất tùy theo loại, Ðức Chúa Trời thấy điều đó là tốt lành."));
visuperobject.biblemap.put("b1_1_26", new visuperobject(1,1,26,"Ðức Chúa Trời phán rằng: Chúng ta hãy làm nên loài người như hình ta và theo tượng ta, đặng quản trị loài cá biển, loài chim trời, loài súc vật, loài côn trùng bò trên mặt đất, và khắp cả đất."));
visuperobject.biblemap.put("b1_1_27", new visuperobject(1,1,27,"Ðức Chúa Trời dựng nên loài người như hình Ngài; Ngài dựng nên loài người giống như hình Ðức Chúa Trời; Ngài dựng nên người nam cùng người nữ."));
visuperobject.biblemap.put("b1_1_28", new visuperobject(1,1,28,"Ðức Chúa Trời ban phước cho loài người và phán rằng: Hãy sanh sản, thêm nhiều, làm cho đầy dẫy đất; hãy làm cho đất phục tùng, hãy quản trị loài cá dưới biển, loài chim trên trời cùng các vật sống hành động trên mặt đất."));
visuperobject.biblemap.put("b1_1_29", new visuperobject(1,1,29,"Ðức Chúa Trời lại phán rằng: Nầy, ta sẽ ban cho các ngươi mọi thứ cỏ kết hột mọc khắp mặt đất, và các loài cây sanh quả có hột giống; ấy sẽ là đồ ăn cho các ngươi."));
visuperobject.biblemap.put("b1_1_30", new visuperobject(1,1,30,"Còn các loài thú ngoài đồng, các loài chim trên trời, và các động vật khác trên mặt đất, phàm giống nào có sự sống thì ta ban cho mọi thứ cỏ xanh đặng dùng làm đồ ăn; thì có như vậy."));
visuperobject.biblemap.put("b1_1_31", new visuperobject(1,1,31,"Ðức Chúa Trời thấy các việc Ngài đã làm thật rất tốt lành. Vậy, có buổi chiều và buổi mai; ấy là ngày thứ sáu."));
visuperobject.biblemap.put("b1_2_1", new visuperobject(1,2,1,"Ấy vậy, trời đất và muôn vật đã dựng nên xong rồi."));
visuperobject.biblemap.put("b1_2_2", new visuperobject(1,2,2,"Ngày thứ bảy, Ðức Chúa Trời làm xong các công việc Ngài đã làm, và ngày thứ bảy, Ngài nghỉ các công việc Ngài đã làm."));
visuperobject.biblemap.put("b1_2_3", new visuperobject(1,2,3,"Rồi, Ngài ban phước cho ngày thứ bảy, đặt là ngày thánh; vì trong ngày đó, Ngài nghỉ các công việc đã dựng nên và đã làm xong rồi."));
visuperobject.biblemap.put("b1_2_4", new visuperobject(1,2,4,"Ấy là gốc tích trời và đất khi đã dựng nên, trong lúc Giê-hô-va Ðức Chúa Trời dựng nên trời và đất."));
visuperobject.biblemap.put("b1_2_5", new visuperobject(1,2,5,"Vả, lúc đó, chưa có một cây nhỏ nào mọc ngoài đồng, và cũng chưa có một ngọn cỏ nào mọc ngoài ruộng, vì Giê-hô-va Ðức Chúa Trời chưa có cho mưa xuống trên đất, và cũng chẳng có một người nào cày cấy đất nữa."));
visuperobject.biblemap.put("b1_2_6", new visuperobject(1,2,6,"Song có hơi nước dưới đất bay lên tưới khắp cùng mặt đất,"));
visuperobject.biblemap.put("b1_2_7", new visuperobject(1,2,7,"Giê-hô-va Ðức Chúa Trời bèn lấy bụi đất nắn nên hình người, hà sanh khí vào lỗ mũi; thì người trở nên một loài sanh linh."));
visuperobject.biblemap.put("b1_2_8", new visuperobject(1,2,8,"Ðoạn, Giê-hô-va Ðức Chúa Trời lập một cảnh vườn tại Ê-đen, ở về hướng Ðông, và đặt người mà Ngài vừa dựng nên ở đó."));
visuperobject.biblemap.put("b1_2_9", new visuperobject(1,2,9,"Giê-hô-va Ðức Chúa Trời khiến đất mọc lên các thứ cây đẹp mắt, và trái thì ăn ngon; giữa vườn lại có cây sự sống cùng cây biết điều thiện và điều ác."));
visuperobject.biblemap.put("b1_2_10", new visuperobject(1,2,10,"Một con sông từ Ê-đen chảy ra đặng tưới vườn; rồi từ đó chia ra làm bốn ngả."));
visuperobject.biblemap.put("b1_2_11", new visuperobject(1,2,11,"Tên ngả thứ nhứt là Bi-sôn; ngả đó chảy quanh xứ Ha-vi-la, là nơi có vàng."));
visuperobject.biblemap.put("b1_2_12", new visuperobject(1,2,12,"Vàng xứ nầy rất cao; đó lại có nhũ hương và bính ngọc."));
visuperobject.biblemap.put("b1_2_13", new visuperobject(1,2,13,"Tên sông thứ nhì là Ghi-hôn, chảy quanh xứ Cu-sơ."));
visuperobject.biblemap.put("b1_2_14", new visuperobject(1,2,14,"Tên sông thứ ba là Hi-đê-ke, chảy về phía đông bờ cõi A-si-ri. Còn sông thứ tư là sông Ơ-phơ-rát."));
visuperobject.biblemap.put("b1_2_15", new visuperobject(1,2,15,"Giê-hô-va Ðức Chúa Trời đem người ở vào cảnh vườn Ê-đen để trồng và giữ vườn."));
visuperobject.biblemap.put("b1_2_16", new visuperobject(1,2,16,"Rồi, Giê-hô-va Ðức Chúa Trời phán dạy rằng: Ngươi được tự do ăn hoa quả các thứ cây trong vườn;"));
visuperobject.biblemap.put("b1_2_17", new visuperobject(1,2,17,"nhưng về cây biết điều thiện và điều ác thì chớ hề ăn đến; vì một mai ngươi ăn chắc sẽ chết."));
visuperobject.biblemap.put("b1_2_18", new visuperobject(1,2,18,"Giê-hô-va Ðức Chúa Trời phán rằng: Loài người ở một mình thì không tốt; ta sẽ làm nên một kẻ giúp đỡ giống như nó."));
visuperobject.biblemap.put("b1_2_19", new visuperobject(1,2,19,"Giê-hô-va Ðức Chúa Trời lấy đất nắn nên các loài thú đồng, các loài chim trời, rồi dẫn đến trước mặt A-đam đặng thử xem người đặt tên chúng nó làm sao, hầu cho tên nào A-đam đặt cho mỗi vật sống, đều thành tên riêng cho nó."));
visuperobject.biblemap.put("b1_2_20", new visuperobject(1,2,20,"A-đam đặt tên các loài súc vật, các loài chim trời cùng các loài thú đồng; nhưng về phần A-đam, thì chẳng tìm được một ai giúp đỡ giống như mình hết."));
visuperobject.biblemap.put("b1_2_21", new visuperobject(1,2,21,"Giê-hô-va Ðức Chúa Trời làm cho A-đam ngủ mê, bèn lấy một xương sường, rồi lấp thịt thế vào."));
visuperobject.biblemap.put("b1_2_22", new visuperobject(1,2,22,"Giê-hô-va Ðức Chúa Trời dùng xương sường đã lấy nơi A-đam làm nên một người nữ, đưa đến cùng A-đam."));
visuperobject.biblemap.put("b1_2_23", new visuperobject(1,2,23,"A-đam nói rằng: Người nầy là xương bởi xương tôi, thịt bởi thịt tôi mà ra. Người nầy sẽ được gọi là người nữ, vì nó do nơi người nam mà có."));
visuperobject.biblemap.put("b1_2_24", new visuperobject(1,2,24,"Bởi vậy cho nên người nam sẽ lìa cha mẹ mà dính díu cùng vợ mình, và cả hai sẽ trở nên một thịt."));
visuperobject.biblemap.put("b1_2_25", new visuperobject(1,2,25,"Vả, A-đam và vợ, cả hai đều trần truồng, mà chẳng hổ thẹn."));
visuperobject.biblemap.put("b1_3_1", new visuperobject(1,3,1,"Vả, trong các loài thú đồng mà Giê-hô-va Ðức Chúa Trời đã làm nên, có con rắn là giống quỉ quyệt hơn hết. Rắn nói cùng người nữ rằng: Mà chi! Ðức Chúa Trời há có phán dặn các ngươi không được phép ăn trái các cây trong vườn sao?"));
visuperobject.biblemap.put("b1_3_2", new visuperobject(1,3,2,"Người nữ đáp rằng: Chúng ta được ăn trái các cây trong vườn,"));
visuperobject.biblemap.put("b1_3_3", new visuperobject(1,3,3,"song về phần trái của cây mọc giữa vườn, Ðức Chúa Trời có phán rằng: Hai ngươi chẳng nên ăn đến và cũng chẳng nên đá-động đến, e khi hai ngươi phải chết chăng."));
visuperobject.biblemap.put("b1_3_4", new visuperobject(1,3,4,"Rắn bèn nói với người nữ rằng: Hai ngươi chẳng chết đâu;"));
visuperobject.biblemap.put("b1_3_5", new visuperobject(1,3,5,"nhưng Ðức Chúa Trời biết rằng hễ ngày nào hai ngươi ăn trái cây đó, mắt mình mở ra, sẽ như Ðức Chúa Trời, biết điều thiện và điều ác."));
visuperobject.biblemap.put("b1_3_6", new visuperobject(1,3,6,"Người nữ thấy trái của cây đó bộ ăn ngon, lại đẹp mắt và quí vì để mở trí khôn, bèn hái ăn, rồi trao cho chồng đứng gần mình, chồng cũng ăn nữa."));
visuperobject.biblemap.put("b1_3_7", new visuperobject(1,3,7,"Ðoạn, mắt hai người đều mỡ ra, biết rằng mình lỏa lồ, bèn lấy lá cây vả đóng khố che thân."));
visuperobject.biblemap.put("b1_3_8", new visuperobject(1,3,8,"Lối chiều, nghe tiếng Giê-hô-va Ðức Chúa Trời đi ngang qua vườn, A-đam và vợ ẩn mình giữa bụi cây, để tránh mặt Giê-hô-va Ðức Chúa Trời."));
visuperobject.biblemap.put("b1_3_9", new visuperobject(1,3,9,"Giê-hô-va Ðức Chúa Trời kêu A-đam mà phán hỏi rằng: Ngươi ở đâu?"));
visuperobject.biblemap.put("b1_3_10", new visuperobject(1,3,10,"A-đam thưa rằng: Tôi có nghe tiếng Chúa trong vườn, bèn sợ, bởi vì tôi lỏa lồ, nên đi ẩn mình."));
visuperobject.biblemap.put("b1_3_11", new visuperobject(1,3,11,"Ðức Chúa Trời phán hỏi: Ai đã chỉ cho ngươi biết rằng mình lỏa lồ? Ngươi có ăn trái cây ta đã dặn không nên ăn đó chăng?"));
visuperobject.biblemap.put("b1_3_12", new visuperobject(1,3,12,"Thưa rằng: Người nữ mà Chúa đã để gần bên tôi cho tôi trái cây đó và tôi đã ăn rồi."));
visuperobject.biblemap.put("b1_3_13", new visuperobject(1,3,13,"Giê-hô-va Ðức Chúa Trời phán hỏi người nữ rằng: Người có làm điều chi vậy? Người nữ thưa rằng: Con rắn dỗ dành tôi và tôi đã ăn rồi."));
visuperobject.biblemap.put("b1_3_14", new visuperobject(1,3,14,"Giê-hô-va Ðức Chúa Trời bèn phán cùng rắn rằng: Vì mầy đã làm điều như vậy, mầy sẽ bị rủa sả trong vòng các loài súc vật, các loài thú đồng, mầy sẽ bò bằng bụng và ăn bụi đất trọn cả đời."));
visuperobject.biblemap.put("b1_3_15", new visuperobject(1,3,15,"Ta sẽ làm cho mầy cùng người nữ, dòng dõi mầy cùng dòng dõi người nữ nghịch thù nhau. Người sẽ giày đạp đầu mầy, còn mầy sẽ cắn gót chân người."));
visuperobject.biblemap.put("b1_3_16", new visuperobject(1,3,16,"Ngài phán cùng người nữ rằng: Ta sẽ thêm điều cực khổ bội phần trong cơn thai nghén; ngươi sẽ chịu đau đớn mỗi khi sanh con; sự dục vọng ngươi phải xu hướng về chồng, và chồng sẽ cai trị ngươi."));
visuperobject.biblemap.put("b1_3_17", new visuperobject(1,3,17,"Ngài lại phán cùng A-đam rằng: Vì ngươi nghe theo lời vợ mà ăn trái cây ta đã dặn không nên ăn, vậy, đất sẽ bị rủa sả vì ngươi; trọn đời ngươi phải chịu khó nhọc mới có vật đất sanh ra mà ăn."));
visuperobject.biblemap.put("b1_3_18", new visuperobject(1,3,18,"Ðất sẽ sanh chông gai và cây tật lê, và ngươi sẽ ăn rau của đồng ruộng;"));
visuperobject.biblemap.put("b1_3_19", new visuperobject(1,3,19,"ngươi sẽ làm đổ mồ hôi trán mới có mà ăn, cho đến ngày nào ngươi trở về đất, là nơi mà có ngươi ra; vì ngươi là bụi, ngươi sẽ trở về bụi."));
visuperobject.biblemap.put("b1_3_20", new visuperobject(1,3,20,"A-đam gọi vợ là Ê-va, vì là mẹ của cả loài người."));
visuperobject.biblemap.put("b1_3_21", new visuperobject(1,3,21,"Giê-hô-va Ðức Chúa Trời lấy da thú kết thành áo dài cho vợ chồng A-đam, và mặc lấy cho."));
visuperobject.biblemap.put("b1_3_22", new visuperobject(1,3,22,"Giê-hô-va Ðức Chúa Trời phán rằng: Nầy, về sự phân biệt điều thiện và điều ác, loài người đã thành một bực như chúng ta; vậy bây giờ, ta hãy coi chừng, e loài người giơ tay khiến cũng hái trái cây sự sống mà ăn và được sống đời đời chăng."));
visuperobject.biblemap.put("b1_3_23", new visuperobject(1,3,23,"Giê-hô-va Ðức Chúa Trời bèn đuổi loài người ra khỏi vườn Ê-đen đặng cày cấy đất, là nơi có người ra."));
visuperobject.biblemap.put("b1_3_24", new visuperobject(1,3,24,"Vậy, Ngài đuổi loài người ra khỏi vườn, rồi đặt tại phía đông vườn Ê-đen các thần chê-ru-bin với gươm lưỡi chói lòa, để giữ con đường đi đến cây sự sống."));
visuperobject.biblemap.put("b1_4_1", new visuperobject(1,4,1,"A-đam ăn ở với Ê-va, là vợ mình; người thọ thai sanh Ca-in và nói rằng: Nhờ Ðức Giê-hô-va giúp đỡ, tôi mới sanh được một người."));
visuperobject.biblemap.put("b1_4_2", new visuperobject(1,4,2,"Ê-va lại sanh em Ca-in, là A-bên; A-bên làm nghề chăn chiên, còn Ca-in thì nghề làm ruộng."));
visuperobject.biblemap.put("b1_4_3", new visuperobject(1,4,3,"Vả, cách ít lâu, Ca-in dùng thổ sản làm của lễ dâng cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b1_4_4", new visuperobject(1,4,4,"A-bên cũng dâng chiên đầu lòng trong bầy mình cùng mỡ nó. Ðức Giê-hô-va đoái xem A-bên và nhận lễ vật của người;"));
visuperobject.biblemap.put("b1_4_5", new visuperobject(1,4,5,"nhưng chẳng đoái đến Ca-in và cũng chẳng nhận lễ vật của người; cho nên Ca-in giận lắm mà gằm nét mặt."));
visuperobject.biblemap.put("b1_4_6", new visuperobject(1,4,6,"Ðức Giê-hô-va phán hỏi Ca-in rằng: Cớ sao ngươi giận, và cớ sao nét mặt ngươi gằm xuống?"));
visuperobject.biblemap.put("b1_4_7", new visuperobject(1,4,7,"Nếu ngươi làm lành, há chẳng ngước mặt lên sao? Còn như chẳng làm lành, thì tội lỗi rình đợi trước cửa, thèm ngươi lắm; nhưng ngươi phải quản trị nó."));
visuperobject.biblemap.put("b1_4_8", new visuperobject(1,4,8,"Ca-in thuật lại cùng A-bên là em mình. Vả, khi hai người đương ở ngoài đồng, thì Ca-in xông đến A-bên là em mình, và giết đi."));
visuperobject.biblemap.put("b1_4_9", new visuperobject(1,4,9,"Ðức Giê-hô-va hỏi Ca-in rằng: A-bên, em ngươi, ở đâu? Thưa rằng: Tôi không biết; tôi là người giữ em tôi sao?"));
visuperobject.biblemap.put("b1_4_10", new visuperobject(1,4,10,"Ðức Giê-hô-va hỏi: Ngươi đã làm điều chi vậy? Tiếng của máu em ngươi từ dưới đất kêu thấu đến ta."));
visuperobject.biblemap.put("b1_4_11", new visuperobject(1,4,11,"Bây giờ, ngươi sẽ bị đất rủa sả, là đất đã hả miệng chịu hút máu của em ngươi bởi chính tay ngươi làm đổ ra."));
visuperobject.biblemap.put("b1_4_12", new visuperobject(1,4,12,"Khi ngươi trồng tỉa, đất chẳng sanh hoa lợi cho ngươi nữa; ngươi sẽ lưu lạc và trốn tránh, trên mặt đất."));
visuperobject.biblemap.put("b1_4_13", new visuperobject(1,4,13,"Ca-in thưa cùng Ðức Giê-hô-va rằng: Sự hình phạt tôi nặng quá mang không nổi."));
visuperobject.biblemap.put("b1_4_14", new visuperobject(1,4,14,"Nầy, ngày nay, Chúa đã đuổi tôi ra khỏi đất nầy, tôi sẽ lánh mặt Chúa, sẽ đi lưu lạc trốn tránh trên đất; rồi, xảy có ai gặp tôi, họ sẽ giết đi."));
visuperobject.biblemap.put("b1_4_15", new visuperobject(1,4,15,"Ðức Giê-hô-va phán rằng: Bởi cớ ấy, nếu ai giết Ca-in, thì sẽ bị báo thù bảy lần. Ðức Giê-hô-va bèn đánh đấu trên mình Ca-in, hầu cho ai gặp Ca-in thì chẳng giết."));
visuperobject.biblemap.put("b1_4_16", new visuperobject(1,4,16,"Ca-in bèn lui ra khỏi mặt Ðức Giê-hô-va, và ở tại xứ Nốt, về phía đông của Ê-đen."));
visuperobject.biblemap.put("b1_4_17", new visuperobject(1,4,17,"Ðoạn, Ca-in ăn ở cùng vợ mình, nàng thọ thai và sanh được Hê-nóc; Ca-in xây một cái thành đặt tên là Hê-nóc, tùy theo tên con trai mình."));
visuperobject.biblemap.put("b1_4_18", new visuperobject(1,4,18,"Rồi, Hê-nóc sanh Y-rát; Y-rát sanh Nê-hu-đa-ên; Nê-hu-đa-ên sanh Mê-tu-sa-ên; Mê-tu-sa-ên sanh Lê-méc."));
visuperobject.biblemap.put("b1_4_19", new visuperobject(1,4,19,"Lê-méc cưới hai vợ; một người tên là A-đa, một người tên là Si-la."));
visuperobject.biblemap.put("b1_4_20", new visuperobject(1,4,20,"A-đa sanh Gia-banh; Gia-banh là tổ phụ của các dân ở trại và nuôi bầy súc vật."));
visuperobject.biblemap.put("b1_4_21", new visuperobject(1,4,21,"Em người là Giu-banh, tổ phụ của những kẻ đánh đờn và thổi sáo."));
visuperobject.biblemap.put("b1_4_22", new visuperobject(1,4,22,"Còn Si-la cũng sanh Tu-banh-Ca-in là người rèn đủ thứ khí giới bén bằng đồng và bằng sắt. Em gái của Tu-banh-Ca-in là Na-a-ma."));
visuperobject.biblemap.put("b1_4_23", new visuperobject(1,4,23,"Lê-méc bèn nói với hai vợ mình rằng: Hỡi A-đa và Si-la! hãy nghe tiếng ta; Nầy, vợ Lê-méc hãy lắng tai nghe lời ta: Ừ! Ta đã giết một người, vì làm thương ta, Và một người trẻ, vì đánh sưng bầm ta."));
visuperobject.biblemap.put("b1_4_24", new visuperobject(1,4,24,"Nếu Ca-in được bảy lần báo thù, Lê-méc sẽ được bảy mươi bảy lần báo oán."));
visuperobject.biblemap.put("b1_4_25", new visuperobject(1,4,25,"A-đam còn ăn ở với vợ mình; người sanh được một con trai đặt tên là Sết; vì vợ rằng: Ðức Chúa Trời đã cho tôi một con trai khác thế cho A-bên mà đã bị Ca-in giết rồi."));
visuperobject.biblemap.put("b1_4_26", new visuperobject(1,4,26,"Sết cũng sanh được một con trai, đặt tên là Ê-nót. Từ đây, người ta bắt đầu cầu khẩn danh Ðức Giê-hô-va."));
visuperobject.biblemap.put("b1_5_1", new visuperobject(1,5,1,"Ðây là sách chép dòng dõi của A-đam. Ngày mà Ðức Chúa Trời dựng nên loài người, thì người làm nên loài người giống như Ðức Chúa Trời;"));
visuperobject.biblemap.put("b1_5_2", new visuperobject(1,5,2,"Ngài dựng nên người nam cùng người nữ, ban phước cho họ, và trong ngày đã dựng nên, đặt tên là người."));
visuperobject.biblemap.put("b1_5_3", new visuperobject(1,5,3,"Vả, A-đam được một trăm ba mươi tuổi, sanh một con trai giống như hình tượng mình, đặt tên là Sết."));
visuperobject.biblemap.put("b1_5_4", new visuperobject(1,5,4,"Sau khi A-đam sanh Sết rồi, còn sống được tám trăm năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_5_5", new visuperobject(1,5,5,"Vậy, A-đam hưởng thọ được chín trăm ba mươi tuổi, rồi qua đời."));
visuperobject.biblemap.put("b1_5_6", new visuperobject(1,5,6,"Sết được một trăm năm tuổi, sanh Ê-nót."));
visuperobject.biblemap.put("b1_5_7", new visuperobject(1,5,7,"Sau khi Sết sanh Ê-nót rồi, còn sống được tám trăm bảy năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_5_8", new visuperobject(1,5,8,"Vậy, Sết hưởng thọ được chín trăm mười hai tuổi, rồi qua đời."));
visuperobject.biblemap.put("b1_5_9", new visuperobject(1,5,9,"Ê-nót được chín mươi tuổi, sanh Kê-nan."));
visuperobject.biblemap.put("b1_5_10", new visuperobject(1,5,10,"Sau khi sanh Kê-nan rồi, còn sống được tám trăm mười lăm năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_5_11", new visuperobject(1,5,11,"Vậy, Ê-nót hưởng thọ được chín trăm năm tuổi, rồi qua đời."));
visuperobject.biblemap.put("b1_5_12", new visuperobject(1,5,12,"Kê-nan được bảy mươi tuổi, sanh Ma-ha-la-le."));
visuperobject.biblemap.put("b1_5_13", new visuperobject(1,5,13,"Sau khi Kê-nan sanh Ma-ha-la-le rồi, còn sống được tám trăm bốn mươi năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_5_14", new visuperobject(1,5,14,"Vậy, Kê-nan hưởng thọ được chín trăm mười tuổi, rồi qua đời."));
visuperobject.biblemap.put("b1_5_15", new visuperobject(1,5,15,"Ma-ha-la-le được sáu mươi lăm tuổi, sanh Giê-rệt."));
visuperobject.biblemap.put("b1_5_16", new visuperobject(1,5,16,"Sau khi Ma-ha-la-le sanh Giê-rệt rồi, còn sống được tám trăm ba mươi năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_5_17", new visuperobject(1,5,17,"Vậy, Ma-ha-la-le hưởng thọ được tám trăm chín mươi lăm tuổi, rồi qua đời."));
visuperobject.biblemap.put("b1_5_18", new visuperobject(1,5,18,"Giê-rệt được một trăm sáu mươi hai tuổi, sanh Hê-nóc."));
visuperobject.biblemap.put("b1_5_19", new visuperobject(1,5,19,"Sau khi Giê-rệt sanh Hê-nót rồi, còn sống được tám trăm năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_5_20", new visuperobject(1,5,20,"Vậy, Giê-rệt hưởng thọ được chín trăm sáu mươi hai tuổi, rồi qua đời."));
visuperobject.biblemap.put("b1_5_21", new visuperobject(1,5,21,"Hê-nóc được sáu mươi lăm tuổi, sanh Mê-tu-sê-la."));
visuperobject.biblemap.put("b1_5_22", new visuperobject(1,5,22,"Sau khi Hê-nóc sanh Mê-tu-sê-la rồi, đồng đi cùng Ðức Chúa Trời trong ba trăm năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_5_23", new visuperobject(1,5,23,"Vậy Hê-nóc hưởng thọ được ban trăm sáu mươi lăm tuổi."));
visuperobject.biblemap.put("b1_5_24", new visuperobject(1,5,24,"Hê-nóc đồng đi cùng Ðức Chúa Trời, rồi mất biệt, bởi vì Ðức Chúa Trời tiếp người đi."));
visuperobject.biblemap.put("b1_5_25", new visuperobject(1,5,25,"Mê-tu-sê-la được một trăm tám mươi bảy tuổi, sanh Lê-méc."));
visuperobject.biblemap.put("b1_5_26", new visuperobject(1,5,26,"Sau khi Mê-tu-sê-la sanh Lê-méc rồi, còn sống được bảy trăm tám mươi hai năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_5_27", new visuperobject(1,5,27,"Vậy, Mê-tu-sê-la hưởng thọ được chín trăm sáu mươi chín tuổi, rồi qua đời."));
visuperobject.biblemap.put("b1_5_28", new visuperobject(1,5,28,"Lê-méc được một trăm tám mươi hai tuổi, sanh một trai,"));
visuperobject.biblemap.put("b1_5_29", new visuperobject(1,5,29,"Ðặt tên là Nô-ê, mà nói rằng: Ðứa nầy sẽ an ủy lòng ta về công việc và về sự nhọc nhằn mà đất bắt tay ta phải làm, là đất Ðức Giê-hô-va đã rủa sả."));
visuperobject.biblemap.put("b1_5_30", new visuperobject(1,5,30,"Sau khi Lê-méc sanh Nô-ê rồi, còn sống được năm trăm chín mươi lăm năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_5_31", new visuperobject(1,5,31,"Vậy, Lê-méc hưởng thọ được bảy trăm bảy mươi bảy tuổi, rồi qua đời."));
visuperobject.biblemap.put("b1_5_32", new visuperobject(1,5,32,"Còn Nô-ê, khi đến năm trăm tuổi, sanh Sem Cham và Gia-phết."));
visuperobject.biblemap.put("b1_6_1", new visuperobject(1,6,1,"Vả, khi loài người khởi thêm nhiều trên mặt đất, và khi loài người đã sanh được con gái rồi,"));
visuperobject.biblemap.put("b1_6_2", new visuperobject(1,6,2,"các con trai của Ðức Chúa Trời thấy con gái loài người tốt đẹp, bèn cưới người nào vừa lòng mình mà làm vợ."));
visuperobject.biblemap.put("b1_6_3", new visuperobject(1,6,3,"Ðức Giê-hô-va phán rằng: Thần ta sẽ chẳng hằng ở trong loài người luôn; trong điều lầm lạc, loài người chỉ là xác thịt; đời người sẽ là một trăm hai mươi năm mà thôi."));
visuperobject.biblemap.put("b1_6_4", new visuperobject(1,6,4,"Ðời đó và đời sau, có người cao lớn trên mặt đất, vì con trai Ðức Chúa Trời ăn ở cùng con gái loài người mà sanh con cái; ấy những người mạnh dạn ngày xưa là tay anh hùng có danh."));
visuperobject.biblemap.put("b1_6_5", new visuperobject(1,6,5,"Ðức Giê-hô-va thấy sự hung ác của loài người trên mặt đất rất nhiều, và các ý tưởng của lòng họ chỉ là xấu luôn;"));
visuperobject.biblemap.put("b1_6_6", new visuperobject(1,6,6,"thì tự trách đã dựng nên loài người trên mặt đất, và buồn rầu trong lòng."));
visuperobject.biblemap.put("b1_6_7", new visuperobject(1,6,7,"Ðức Giê-hô-va phán rằng: Ta sẽ hủy diệt khỏi mặt đất loài người mà ta đã dựng nên, từ loài người cho đến loài súc vật, loài côn trùng, loài chim trời; vì ta tự trách đã dựng nên các loài đó."));
visuperobject.biblemap.put("b1_6_8", new visuperobject(1,6,8,"Nhưng Nô-ê được ơn trước mặt Ðức Giê-hô-va."));
visuperobject.biblemap.put("b1_6_9", new visuperobject(1,6,9,"Nầy là dòng dõi của Nô-ê. Nô-ê trong đời mình là một người công bình và toàn vẹn, đồng đi cùng Ðức Chúa Trời."));
visuperobject.biblemap.put("b1_6_10", new visuperobject(1,6,10,"Nô-ê sanh ba con trai là Sem, Cham và Gia-phết."));
visuperobject.biblemap.put("b1_6_11", new visuperobject(1,6,11,"Thế gian bấy giờ đều bại hoại trước mặt Ðức Chúa Trời và đầy dẫy sự hung ác."));
visuperobject.biblemap.put("b1_6_12", new visuperobject(1,6,12,"Nầy, Ðức Chúa Trời nhìn xem thế gian, thấy điều bại hoại, vì hết thảy xác thịt làm cho đường mình trên đất phải bại hoại."));
visuperobject.biblemap.put("b1_6_13", new visuperobject(1,6,13,"Ðức Chúa Trời bèn phán cùng Nô-ê rằng: Kỳ cuối cùng của mọi xác thịt đã đưa đến trước mặt ta; vì cớ loài người mà đất phải đầy dẫy điều hung hăng; vậy, ta sẽ diệt-trừ họ cùng đất."));
visuperobject.biblemap.put("b1_6_14", new visuperobject(1,6,14,"Ngươi hãy đóng một chiếc tàu bằng cây gô-phe, đóng có từng phòng, rồi trét chai bề trong cùng bề ngoài."));
visuperobject.biblemap.put("b1_6_15", new visuperobject(1,6,15,"Vậy, hãy làm theo thế nầy: Bề dài tàu ba trăm thước, bề ngang năm mươi thước, bề cao ba mươi thước."));
visuperobject.biblemap.put("b1_6_16", new visuperobject(1,6,16,"Trên tàu ngươi sẽ làm một cửa sổ, bề cao một thước, và chừa một cửa bên hông; ngươi sẽ làm một từng dưới, một từng giữa và một từng trên."));
visuperobject.biblemap.put("b1_6_17", new visuperobject(1,6,17,"Còn ta đây, ta sẽ dẫn nước lụt khắp trên mặt đất, đặng diệt tuyệt các xác thịt có sanh khí ở dưới trời; hết thảy vật chi ở trên mặt đất đều sẽ chết hết."));
visuperobject.biblemap.put("b1_6_18", new visuperobject(1,6,18,"Nhưng ta sẽ lập giao ước cùng ngươi, rồi ngươi và vợ, các con và các dâu của ngươi, đều hãy vào tàu."));
visuperobject.biblemap.put("b1_6_19", new visuperobject(1,6,19,"Về các loài sanh vật, ngươi hãy dẫn xuống tàu mỗi loài một cặp, có đực có cái, có trống có mái, hầu cho ở cùng ngươi đặng giữ tròn sự sống;"));
visuperobject.biblemap.put("b1_6_20", new visuperobject(1,6,20,"chim tùy theo loại, súc vật tùy theo loại, côn trùng tùy theo loại, mỗi thứ hai con, sẽ đến cùng ngươi, để ngươi giữ tròn sự sống cho."));
visuperobject.biblemap.put("b1_6_21", new visuperobject(1,6,21,"Lại, ngươi hãy lấy các thứ đồ ăn đem theo, đặng để dàng làm lương thực cho ngươi và các loài đó."));
visuperobject.biblemap.put("b1_6_22", new visuperobject(1,6,22,"Nô-ê làm các điều nầy y như lời Ðức Chúa Trời đã phán dặn."));
visuperobject.biblemap.put("b1_7_1", new visuperobject(1,7,1,"Ðức Giê-hô-va phán cùng Nô-ê rằng: Ngươi và cả nhà ngươi hãy vào tàu, vì về đời nầy ta thấy ngươi là công bình ở trước mặt ta."));
visuperobject.biblemap.put("b1_7_2", new visuperobject(1,7,2,"Trong các loài vật thanh sạch, hãy đem theo mỗi loài bảy cặp, đực và cái; còn loài vật không thanh sạch mỗi loài một cặp, đực và cái."));
visuperobject.biblemap.put("b1_7_3", new visuperobject(1,7,3,"Cũng hãy đem theo những chim trời, mỗi thứ bảy cặp, trống và mái, để giữ giống ở trên khắp mặt đất."));
visuperobject.biblemap.put("b1_7_4", new visuperobject(1,7,4,"Vì còn bảy ngày nữa, ta sẽ làm mưa xuống mặt đất, trong bốn mươi ngày và bốn mươi đêm; ta sẽ tuyệt diệt khỏi đất hết các loài của ta đã dựng nên."));
visuperobject.biblemap.put("b1_7_5", new visuperobject(1,7,5,"Ðoạn, Nô-ê làm theo mọi điều Ðức Giê-hô-va đã phán dặn mình."));
visuperobject.biblemap.put("b1_7_6", new visuperobject(1,7,6,"Vả, khi lụt xảy ra, và nước tràn trên mặt đất, thì Nô-ê đã được sáu trăm tuổi."));
visuperobject.biblemap.put("b1_7_7", new visuperobject(1,7,7,"Vì cớ nước lụt, nên Nô-ê vào tàu cùng vợ, các con trai và các dâu mình;"));
visuperobject.biblemap.put("b1_7_8", new visuperobject(1,7,8,"loài vật thanh sạch và loài vật không thanh sạch, loài chim, loài côn trùng trên mặt đất,"));
visuperobject.biblemap.put("b1_7_9", new visuperobject(1,7,9,"từng cặp, đực và cái, trống và mái, đều đến cùng Nô-ê mà vào tàu, y như lời Ðức Chúa Trời đã phán dặn người."));
visuperobject.biblemap.put("b1_7_10", new visuperobject(1,7,10,"Sau bảy ngày, nước lụt xảy có trên mặt đất."));
visuperobject.biblemap.put("b1_7_11", new visuperobject(1,7,11,"Nhằm năm sáu trăm của đời Nô-ê, tháng hai, ngày mười bảy, chánh ngày đó, các nguồn của vực lớn nổ ra, và các đập trên trời mở xuống;"));
visuperobject.biblemap.put("b1_7_12", new visuperobject(1,7,12,"mưa sa trên mặt đất trọn bốn mươi ngày và bốn mươi đêm."));
visuperobject.biblemap.put("b1_7_13", new visuperobject(1,7,13,"Lại cũng trong một ngày đó, Nô-ê với vợ, ba con trai: Sem, Cham và Gia-phết, cùng ba dâu mình đồng vào tàu."));
visuperobject.biblemap.put("b1_7_14", new visuperobject(1,7,14,"Họ và các loài, thú rừng tùy theo loại, súc vật tùy theo loại, côn trùng trên mặt đất tùy theo loài, chim tùy theo loại, và hết thảy vật nào có cánh,"));
visuperobject.biblemap.put("b1_7_15", new visuperobject(1,7,15,"đều từng cặp theo Nô-ê vào tàu; nghĩa là mọi xác thịt nào có sanh khí,"));
visuperobject.biblemap.put("b1_7_16", new visuperobject(1,7,16,"một đực một cái, một trống một mái, đều đến vào tàu, y như lời Ðức Chúa Trời đã phán dặn; đoạn, Ðức Giê-hô-va đóng cửa tàu lại."));
visuperobject.biblemap.put("b1_7_17", new visuperobject(1,7,17,"Nước lụt phủ mặt đất bốn mươi ngày; nước dưng thêm nâng hỏng tàu lên khỏi mặt đất."));
visuperobject.biblemap.put("b1_7_18", new visuperobject(1,7,18,"Trên mặt đất nước lớn và dưng thêm nhiều lắm; chiếc tàu nổi trên mặt nước."));
visuperobject.biblemap.put("b1_7_19", new visuperobject(1,7,19,"Nước càng dưng lên bội phần trên mặt đất, hết thảy những ngọn núi cao ở dưới trời đều bị ngập."));
visuperobject.biblemap.put("b1_7_20", new visuperobject(1,7,20,"Nước dưng lên mười lăm thước cao hơn, mấy ngọn núi đều ngập."));
visuperobject.biblemap.put("b1_7_21", new visuperobject(1,7,21,"Các xác thịt hành động trên mặt đất đều chết ngột, nào chim, nào súc vật, nào thú rừng, nào côn trùng, và mọi người."));
visuperobject.biblemap.put("b1_7_22", new visuperobject(1,7,22,"Các vật có sanh khí trong lỗ mũi, các vật ở trên đất liền đều chết hết."));
visuperobject.biblemap.put("b1_7_23", new visuperobject(1,7,23,"Mọi loài ở trên mặt đất đều bị hủy diệt, từ loài người cho đến loài thú, loài côn trùng, cùng loài chim trời, chỉ còn Nô-ê và các loài ở với người trong tàu mà thôi."));
visuperobject.biblemap.put("b1_7_24", new visuperobject(1,7,24,"Nước dưng lên trên mặt đất trọn một trăm năm mươi ngày."));
visuperobject.biblemap.put("b1_8_1", new visuperobject(1,8,1,"Vả, Ðức Chúa Trời nhớ lại Nô-ê cùng các loài thú và súc vật ở trong tàu với người, bèn khiến một trận gió thổi ngang qua trên đất, thì nước dừng lại."));
visuperobject.biblemap.put("b1_8_2", new visuperobject(1,8,2,"Các nguồn của vực lớn và các đập trên trời lấp ngăn lại; mưa trên trời không sa xuống nữa."));
visuperobject.biblemap.put("b1_8_3", new visuperobject(1,8,3,"Nước giựt khỏi mặt đất, lần lần vừa hạ vừa giựt; trong một trăm năm mươi ngày nước mới bớt xuống."));
visuperobject.biblemap.put("b1_8_4", new visuperobject(1,8,4,"Ðến tháng bảy, ngày mười bảy, chiếc tàu tấp trên núi A-ra-rát."));
visuperobject.biblemap.put("b1_8_5", new visuperobject(1,8,5,"Nước cứ lần lần hạ cho đến tháng mười; ngày mồng một tháng đó, mấy đỉnh núi mới lộ ra."));
visuperobject.biblemap.put("b1_8_6", new visuperobject(1,8,6,"Ðược bốn mươi ngày, Nô-ê mở cửa sổ mình đã làm trên tàu,"));
visuperobject.biblemap.put("b1_8_7", new visuperobject(1,8,7,"thả một con quạ ra; quạ liệng đi liệng lại cho đến khi nước giựt khô trên mặt đất."));
visuperobject.biblemap.put("b1_8_8", new visuperobject(1,8,8,"Người cũng thả một con bò câu ra, đặng xem thử nước hạ bớt trên mặt đất chưa."));
visuperobject.biblemap.put("b1_8_9", new visuperobject(1,8,9,"Nhưng bò câu chẳng tìm được nơi nào đáp chơn xuống, bèn bay trở về trong tàu cùng người, vì nước còn khắp cả trên mặt đất. Nô-ê giơ tay bắt lấy bò câu, đem vào tàu lại với mình."));
visuperobject.biblemap.put("b1_8_10", new visuperobject(1,8,10,"Ðoạn, người đợi bảy ngày nữa, lại thả bò câu ra khỏi tàu;"));
visuperobject.biblemap.put("b1_8_11", new visuperobject(1,8,11,"đến chiều bò câu về cùng người, và nầy, trong mỏ tha một lá Ô-li-ve tươi; Nô-ê hiểu rằng nước đã giảm bớt trên mặt đất."));
visuperobject.biblemap.put("b1_8_12", new visuperobject(1,8,12,"Người đợi bảy ngày nữa, thả bò câu ra; nhưng chuyến nầy bò câu chẳng trở về cùng người nữa."));
visuperobject.biblemap.put("b1_8_13", new visuperobject(1,8,13,"Nhằm năm sáu trăm một của đời Nô-ê, ngày mồng một, tháng giêng, nước đã giựt bày mặt đất khô; Nô-ê bèn giở mui tàu mà nhìn; nầy, mặt đất đã se."));
visuperobject.biblemap.put("b1_8_14", new visuperobject(1,8,14,"Ðến tháng hai, ngày hai mươi bảy, đất đã khô rồi."));
visuperobject.biblemap.put("b1_8_15", new visuperobject(1,8,15,"Ðức Chúa Trời bèn phán cùng Nô-ê rằng:"));
visuperobject.biblemap.put("b1_8_16", new visuperobject(1,8,16,"Hãy ra khỏi tàu, ngươi, vợ các con và các dâu ngươi."));
visuperobject.biblemap.put("b1_8_17", new visuperobject(1,8,17,"Hãy thả ra với ngươi mọi vật sống của các xác thịt đã ở cùng ngươi: nào chim, nào thú, nào côn trùng bò trên đất, hầu cho chúng nó sanh sản, và thêm nhiều trên mặt đất."));
visuperobject.biblemap.put("b1_8_18", new visuperobject(1,8,18,"Vậy, Nô-ê cùng vợ người, các con trai và các dâu người ra khỏi tàu."));
visuperobject.biblemap.put("b1_8_19", new visuperobject(1,8,19,"Các thú, rắn, chim cùng mọi vật hành động trên mặt đất tùy theo giống đều ra khỏi tàu."));
visuperobject.biblemap.put("b1_8_20", new visuperobject(1,8,20,"Nô-ê lập một bàn thờ cho Ðức Giê-hô-va. Người bắt các súc vật thanh sạch, các loài chim thanh sạch, bày của lễ thiêu dâng lên bàn thờ."));
visuperobject.biblemap.put("b1_8_21", new visuperobject(1,8,21,"Ðức Giê-hô-va hưởng lấy mùi thơm và nghĩ thầm rằng: Ta chẳng vì loài người mà rủa sả đất nữa, vì tâm tánh loài người vẫn xấu xa từ khi còn tuổi trẻ; ta cũng sẽ chẳng hành các vật sống như ta đã làm."));
visuperobject.biblemap.put("b1_8_22", new visuperobject(1,8,22,"Hễ đất còn, thì mùa gieo giống cùng mùa gặt hái, lạnh và nóng, mùa hạ cùng mùa đông, ngày và đêm, chẳng bao giờ tuyệt được."));
visuperobject.biblemap.put("b1_9_1", new visuperobject(1,9,1,"Ðức Chúa Trời ban phước cho Nô-ê cùng các con trai người, mà phán rằng: Hãy sanh sản, thêm nhiều, làm cho đầy dẫy trên mặt đất."));
visuperobject.biblemap.put("b1_9_2", new visuperobject(1,9,2,"Các loài vật ở trên đất, các loài chim trời, và các vật hành động trên đất, cùng các cá biển, đều sẽ kinh khủng ngươi và bị phú vào tay ngươi."));
visuperobject.biblemap.put("b1_9_3", new visuperobject(1,9,3,"Phàm vật chi hành động và có sự sống thì dùng làm đồ ăn cho các ngươi. Ta cho mọi vật đó như ta đã cho thứ cỏ xanh."));
visuperobject.biblemap.put("b1_9_4", new visuperobject(1,9,4,"Song các ngươi không nên ăn thịt còn hồn sống, nghĩa là có máu."));
visuperobject.biblemap.put("b1_9_5", new visuperobject(1,9,5,"Quả thật, ta sẽ đòi máu của sự sống ngươi lại, hoặc nơi các loài thú vật, hoặc nơi tay người, hoặc nơi tay của anh em người."));
visuperobject.biblemap.put("b1_9_6", new visuperobject(1,9,6,"Hễ kẻ nào làm đổ máu người, thì sẽ bị người khác làm đổ máu lại; vì Ðức Chúa Trời làm nên người như hình của Ngài."));
visuperobject.biblemap.put("b1_9_7", new visuperobject(1,9,7,"Vậy, các ngươi hãy sanh sản, thêm nhiều, và làm cho đầy dẫy trên mặt đất."));
visuperobject.biblemap.put("b1_9_8", new visuperobject(1,9,8,"Ðức Chúa Trời cũng phán cùng Nô-ê và các con trai người rằng:"));
visuperobject.biblemap.put("b1_9_9", new visuperobject(1,9,9,"Còn phần ta đây, ta lập giao ước cùng các ngươi, cùng dòng dõi các ngươi,"));
visuperobject.biblemap.put("b1_9_10", new visuperobject(1,9,10,"và cùng mọi vật sống ở với ngươi, nào loài chim, nào súc vật, nào loài thú ở trên đất, tức là các loài ở trong tàu ra, cho đến các loài vật ở trên đất."));
visuperobject.biblemap.put("b1_9_11", new visuperobject(1,9,11,"Vậy, ta lập giao ước cùng các ngươi, và các loài xác thịt chẳng bao giờ lại bị nước lụt hủy diệt, và cũng chẳng có nước lụt để hủy hoại đất nữa."));
visuperobject.biblemap.put("b1_9_12", new visuperobject(1,9,12,"Ðức Chúa Trời lại phán rằng: Ðây là dấu chỉ về sự giao ước mà ta lập cùng các ngươi, cùng hết thảy vật sống ở với các ngươi, trải qua các đời mãi mãi."));
visuperobject.biblemap.put("b1_9_13", new visuperobject(1,9,13,"Ta đặt mống của ta trên từng mây, dùng làm dấu chỉ sự giao ước của ta với đất."));
visuperobject.biblemap.put("b1_9_14", new visuperobject(1,9,14,"Phàm lúc nào ta góp các đám mây trên mặt đất và phàm mống mọc trên từng mây,"));
visuperobject.biblemap.put("b1_9_15", new visuperobject(1,9,15,"thì ta sẽ nhớ lại sự giao ước của ta đã lập cùng các ngươi, và cùng các loài xác thịt có sự sống, thì nước chẳng bao giờ lại trở nên lụt mà hủy diệt các loài xác thịt nữa."));
visuperobject.biblemap.put("b1_9_16", new visuperobject(1,9,16,"Vậy, cái mống sẽ ở trên mây, ta nhìn xem nó đặng nhớ lại sự giao ước đời đời của Ðức Chúa Trời cùng các loài xác thịt có sự sống ở trên đất."));
visuperobject.biblemap.put("b1_9_17", new visuperobject(1,9,17,"Ðức Chúa Trời lại phán cùng Nô-ê rằng: Ðó là dấu chỉ sự giao ước mà ta đã lập giữa ta và các xác thịt ở trên mặt đất."));
visuperobject.biblemap.put("b1_9_18", new visuperobject(1,9,18,"Các con trai của Nô-ê ở trong tàu ra là Sem, Cham và Gia-phết. Vả, Cham là cha của Ca-na-an."));
visuperobject.biblemap.put("b1_9_19", new visuperobject(1,9,19,"Ấy đó, là ba con trai của Nô-ê, và cũng do nơi họ mà có loài người ở khắp trên mặt đất."));
visuperobject.biblemap.put("b1_9_20", new visuperobject(1,9,20,"Vả, Nô-ê khởi cày đất và trồng nho."));
visuperobject.biblemap.put("b1_9_21", new visuperobject(1,9,21,"Người uống rượu say, rồi lõa thể ở giữa trại mình."));
visuperobject.biblemap.put("b1_9_22", new visuperobject(1,9,22,"Cham là cha Ca-na-an, thấy sự trần truồng của cha, thì ra ngoài thuật lại cùng hai anh em mình."));
visuperobject.biblemap.put("b1_9_23", new visuperobject(1,9,23,"Nhưng Sem và Gia-phết đều lấy áo choàng vắt trên vai mình, đi thùi lui đến đắp khuất thân cho cha; và bởi họ xây mặt qua phía khác, nên chẳng thấy sự trần truồng của cha chút nào."));
visuperobject.biblemap.put("b1_9_24", new visuperobject(1,9,24,"Khi Nô-ê tỉnh rượu rồi, hay được điều con thứ hai đã làm cho mình,"));
visuperobject.biblemap.put("b1_9_25", new visuperobject(1,9,25,"bèn nói rằng: Ca-na-an đáng rủa sả! Nó sẽ làm mọi cho các tôi tớ của anh em nó."));
visuperobject.biblemap.put("b1_9_26", new visuperobject(1,9,26,"Người lại nói rằng: Ðáng ngợi khen Giê-hô-va, là Ðức Chúa Trời của Sem thay; Ca-na-an phải làm tôi cho họ!"));
visuperobject.biblemap.put("b1_9_27", new visuperobject(1,9,27,"Cầu xin Ðức Chúa Trời mở rộng đất cho Gia-phết, cho người ở nơi trại của Sem; còn Ca-na-an phải làm tôi của họ."));
visuperobject.biblemap.put("b1_9_28", new visuperobject(1,9,28,"Sau khi lụt, Nô-ê còn sống ba trăm năm mươi năm."));
visuperobject.biblemap.put("b1_9_29", new visuperobject(1,9,29,"Vậy, Nô-ê hưởng thọ được chín trăm năm mươi tuổi, rồi qua đời."));
visuperobject.biblemap.put("b1_10_1", new visuperobject(1,10,1,"Ðây là dòng dõi của Sem, Cham và Gia-phết, ba con trai của Nô-ê; sau khi lụt, họ sanh con cái."));
visuperobject.biblemap.put("b1_10_2", new visuperobject(1,10,2,"Con trai của Gia-phết là Gô-me, Ma-gốc, Ma-đai, Gia-van, Tu-banh, Mê-siếc, và Ti-ra."));
visuperobject.biblemap.put("b1_10_3", new visuperobject(1,10,3,"Con trai của Gô-me là Ách-kê-na, Ri-phát, và Tô-ga-ma."));
visuperobject.biblemap.put("b1_10_4", new visuperobject(1,10,4,"Con trai của Gia-van là Ê-li-sa và Ta-rê-si, Kít-sim và Ðô-đa-nim."));
visuperobject.biblemap.put("b1_10_5", new visuperobject(1,10,5,"Do họ mà có người ta ở tràn ra các cù lao của các dân, tùy theo xứ, tiếng nói, chi phái và dân tộc của họ mà chia ra."));
visuperobject.biblemap.put("b1_10_6", new visuperobject(1,10,6,"Con trai của Cham là Cúc, Mích-ra-im, Phút và Ca-na-an."));
visuperobject.biblemap.put("b1_10_7", new visuperobject(1,10,7,"Con trai của Cúc là Sê-ba, Ha-vi-la, Sáp-ta, Ra-ê-ma và Sáp-tê-ca; con trai của Ra-ê-ma là Sê-la và Ðê-đan."));
visuperobject.biblemap.put("b1_10_8", new visuperobject(1,10,8,"Cúc sanh Nim-rốt, ấy là người bắt đầu làm anh hùng trên mặt đất."));
visuperobject.biblemap.put("b1_10_9", new visuperobject(1,10,9,"Người là một tay thợ săn can đảm trước mặt Ðức Giê-hô-va. Bởi cớ đó, có tục ngữ rằng: Hệt như Nim-rốt, một tay thợ săn can đảm trước mặt Ðức Giê-hô-va!"));
visuperobject.biblemap.put("b1_10_10", new visuperobject(1,10,10,"Nước người sơ-lập là Ba-bên, Ê-rết, A-cát và Ca-ne ở tại xứ Si-nê-a."));
visuperobject.biblemap.put("b1_10_11", new visuperobject(1,10,11,"Từ xứ đó người đến xứ A-si-ri, rồi lập thành Ni-ni-ve, Rê-hô-bô-ti, Ca-lách,"));
visuperobject.biblemap.put("b1_10_12", new visuperobject(1,10,12,"Và thành Rê-sen, là thành rất lớn, ở giữa khoảng thành Ni-ni-ve và Ca-lách."));
visuperobject.biblemap.put("b1_10_13", new visuperobject(1,10,13,"Mích-ra-im sanh họ Lu-đim, họ A-na-mim, họ Lê-ha-bim, họ Náp-tu-him,"));
visuperobject.biblemap.put("b1_10_14", new visuperobject(1,10,14,"họ Bát-ru-sim, họ Cách-lu-him (bởi đó sanh họ Phi-li-tin), và họ Cáp-tô-rim."));
visuperobject.biblemap.put("b1_10_15", new visuperobject(1,10,15,"Ca-na-an sanh Si-đôn, là con trưởng nam, và Hếch,"));
visuperobject.biblemap.put("b1_10_16", new visuperobject(1,10,16,"cùng họ Giê-bu-sít, họ A-mô-rít, họ Ghi-rê-ga-sít,"));
visuperobject.biblemap.put("b1_10_17", new visuperobject(1,10,17,"họ Hê-vít, họ A-rê-kít, họ Si-nít,"));
visuperobject.biblemap.put("b1_10_18", new visuperobject(1,10,18,"họ A-va-đít, họ Xê-ma-rít, họ Ha-ma-tít. Kế đó, chi tộc Ca-na-an đi tản lạc."));
visuperobject.biblemap.put("b1_10_19", new visuperobject(1,10,19,"Còn địa-phận Ca-na-an, về hướng Ghê-ra, chạy từ Si-đôn tới Ga-xa; về hướng Sô-đôm, Gô-mô-rơ, Át-ma và Sê-bô-im, chạy đến Lê-sa."));
visuperobject.biblemap.put("b1_10_20", new visuperobject(1,10,20,"Ðó là con cháu của Cham, tùy theo họ hàng, tiếng nói, xứ và dân tộc của họ."));
visuperobject.biblemap.put("b1_10_21", new visuperobject(1,10,21,"Sem, tổ phụ của họ Hê-be và anh cả của Gia-phết, cũng có sanh con trai."));
visuperobject.biblemap.put("b1_10_22", new visuperobject(1,10,22,"Con trai của Sem là Ê-lam, A-sự-rơ, A-bác-sát, Lút và A-ram."));
visuperobject.biblemap.put("b1_10_23", new visuperobject(1,10,23,"Con trai của A-ram là U-xơ, Hu-lơ, Ghê-te và Mách."));
visuperobject.biblemap.put("b1_10_24", new visuperobject(1,10,24,"Còn A-bác-sát sanh Sê-lách; Sê-lách sanh Hê-be,"));
visuperobject.biblemap.put("b1_10_25", new visuperobject(1,10,25,"Hê-be sanh được hai con trai; tên của một người là Bê-léc, vì đất trong đời người đó đã chia ra; trên của người em là Giốc-tan."));
visuperobject.biblemap.put("b1_10_26", new visuperobject(1,10,26,"Giốc-tan sanh A-mô-đát, Sê-lép, Ha-sa-ma-vết và Giê-rách,"));
visuperobject.biblemap.put("b1_10_27", new visuperobject(1,10,27,"Ha-đô-ram, U-xa, Ðiếc-la,"));
visuperobject.biblemap.put("b1_10_28", new visuperobject(1,10,28,"Ô-banh, A-bi-ma-ên, Sê-ba,"));
visuperobject.biblemap.put("b1_10_29", new visuperobject(1,10,29,"Ô-phia, Ha-vi-la và Giô-báp. Hết thảy các người đó là con trai của Giốc-tan."));
visuperobject.biblemap.put("b1_10_30", new visuperobject(1,10,30,"Cõi đất của các người đó ở về phía Sê-pha, chạy từ Mê-sa cho đến núi Ðông phương."));
visuperobject.biblemap.put("b1_10_31", new visuperobject(1,10,31,"Ðó là con cháu của Sem, tùy theo họ hàng, tiếng nói, xứ và dân tộc của họ."));
visuperobject.biblemap.put("b1_10_32", new visuperobject(1,10,32,"Ấy là các họ hàng của ba con trai Nô-ê tùy theo đời và dân tộc của họ; lại cũng do nơi họ mà các dân chia ra trên mặt đất sau cơn nước lụt."));
visuperobject.biblemap.put("b1_11_1", new visuperobject(1,11,1,"Vả, cả thiên hạ đều có một giọng nói và một thứ tiếng."));
visuperobject.biblemap.put("b1_11_2", new visuperobject(1,11,2,"Nhưng khi ở Ðông phương dời đi, người ta gặp một đồng bằng trong xứ Si-nê-a, rồi ở tại đó."));
visuperobject.biblemap.put("b1_11_3", new visuperobject(1,11,3,"Người nầy nói với người kia rằng: Hè! chúng ta hãy làm gạch và hầm trong lửa. Lúc đó, gạch thế cho đá, còn chai thế cho hồ."));
visuperobject.biblemap.put("b1_11_4", new visuperobject(1,11,4,"Lại nói rằng: Nào! chúng ta hãy xây một cái thành và dựng lên một cái tháp, chót cao đến tận trời; ta hãy lo làm cho rạng danh, e khi phải tản lạc khắp trên mặt đất."));
visuperobject.biblemap.put("b1_11_5", new visuperobject(1,11,5,"Ðức Giê-hô-va bèn ngự xuống đặng xem cái thành và tháp của con cái loài người xây nên."));
visuperobject.biblemap.put("b1_11_6", new visuperobject(1,11,6,"Ðức Giê-hô-va phán rằng: Nầy, chỉ có một thứ dân, cùng đồng một thứ tiếng; và kia kìa công việc chúng nó đương khởi làm; bây giờ chẳng còn chi ngăn chúng nó làm các điều đã quyết định được."));
visuperobject.biblemap.put("b1_11_7", new visuperobject(1,11,7,"Thôi! chúng ta, hãy xuống, làm lộn xộn tiếng nói của chúng nó, cho họ nghe không được tiếng nói của người nầy với người kia."));
visuperobject.biblemap.put("b1_11_8", new visuperobject(1,11,8,"Rồi, từ đó Ðức Giê-hô-va làm cho loài người tản ra khắp trên mặt đất, và họ thôi công việc xây cất thành."));
visuperobject.biblemap.put("b1_11_9", new visuperobject(1,11,9,"Bởi cớ đó đặt tên thành là Ba-bên, vì nơi đó Ðức Giê-hô-va làm lộn xộn tiếng nói của cả thế gian, và từ đây Ngài làm cho loài người tản ra khắp trên mặt đất."));
visuperobject.biblemap.put("b1_11_10", new visuperobject(1,11,10,"Ðây là dòng dõi của Sem: Cách hai năm, sau cơn nước lụt, Sem được một trăm tuổi, sanh A-bác-sát."));
visuperobject.biblemap.put("b1_11_11", new visuperobject(1,11,11,"Sau khi Sem sanh A-bác-sát rồi, còn sống được năm trăm năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_11_12", new visuperobject(1,11,12,"A-bác-sát được ba mươi lăm tuổi, sanh Sê-lách."));
visuperobject.biblemap.put("b1_11_13", new visuperobject(1,11,13,"Sau khi A-bác-sát sanh Sê-lách rồi, còn sống được bốn trăm ba năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_11_14", new visuperobject(1,11,14,"Sê-lách được ba mươi tuổi, sanh Hê-be."));
visuperobject.biblemap.put("b1_11_15", new visuperobject(1,11,15,"Sau khi Sê-lách sanh Hê-be rồi, còn sống được bốn trăm ba năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_11_16", new visuperobject(1,11,16,"Hê-be được ba mươi bốn tuổi, sanh Bê-léc."));
visuperobject.biblemap.put("b1_11_17", new visuperobject(1,11,17,"Sau khi Hê-be sanh Bê-léc rồi, còn sống được bốn trăm ba mươi năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_11_18", new visuperobject(1,11,18,"Bê-léc được ba mươi tuổi, sanh Rê-hu"));
visuperobject.biblemap.put("b1_11_19", new visuperobject(1,11,19,"Sau khi Bê-léc sanh Rê-hu rồi, còn sống được hai trăm chín năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_11_20", new visuperobject(1,11,20,"Rê-hu được ba mươi hai tuổi, sanh Sê-rúc."));
visuperobject.biblemap.put("b1_11_21", new visuperobject(1,11,21,"Sau khi Rê-hu sanh Sê-rúc rồi, còn sống được hai trăm bảy năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_11_22", new visuperobject(1,11,22,"Sê-rúc được ba mươi tuổi, sanh Na-cô."));
visuperobject.biblemap.put("b1_11_23", new visuperobject(1,11,23,"Sau khi Sê-rúc sanh Na-cô rồi, còn sống được hai trăm năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_11_24", new visuperobject(1,11,24,"Na-cô được hai mươi chín tuổi, sanh Tha-rê."));
visuperobject.biblemap.put("b1_11_25", new visuperobject(1,11,25,"Sau khi Na-cô sanh Tha-rê rồi, còn sống được một trăm mười chín năm, sanh con trai con gái."));
visuperobject.biblemap.put("b1_11_26", new visuperobject(1,11,26,"Còn Tha-rê được bảy mươi tuổi, sanh Áp-ram, Na-cô và Ha-ran."));
visuperobject.biblemap.put("b1_11_27", new visuperobject(1,11,27,"Ðây là dòng dõi của Tha-rê: Tha-rê sanh Áp-ram, Na-cô và Ha-ran; Ha-ran sanh Lót."));
visuperobject.biblemap.put("b1_11_28", new visuperobject(1,11,28,"Ha-ran qua đời tại quê hương mình, tức là U-ru, thuộc về xứ Canh-đê, khi cha người là Tha-rê hãy còn sống."));
visuperobject.biblemap.put("b1_11_29", new visuperobject(1,11,29,"Áp-ram và Na-cô cưới vợ; vợ Áp-ram tên là Sa-rai, còn vợ Na-cô tên là Minh-ca; Minh-ca và Dích-ca tức là con gái của Ha-ran."));
visuperobject.biblemap.put("b1_11_30", new visuperobject(1,11,30,"Vả, Sa-rai son sẻ, nên người không có con."));
visuperobject.biblemap.put("b1_11_31", new visuperobject(1,11,31,"Tha-rê dẫn Áp-ram, con trai mình, Lót, con trai Ha-ran, cháu mình, và Sa-rai, vợ Áp-ram, tức dâu mình, đồng ra khỏi U-rơ, thuộc về xứ Canh-đê, đặng qua xứ Ca-na-an. Khi đến Cha-ran thì lập gia cư tại đó."));
visuperobject.biblemap.put("b1_11_32", new visuperobject(1,11,32,"Tha-rê hưởng thọ được hai trăm năm tuổi, rồi qua đời tại Cha-ran."));
visuperobject.biblemap.put("b1_12_1", new visuperobject(1,12,1,"Vả, Ðức Giê-hô-va có phán cùng Áp-ram rằng: Ngươi hãy ra khỏi quê hương, vòng bà con và nhà cha ngươi, mà đi đến xứ ta sẽ chỉ cho."));
visuperobject.biblemap.put("b1_12_2", new visuperobject(1,12,2,"Ta sẽ làm cho ngươi nên một dân lớn; ta sẽ ban phước cho ngươi, cùng làm nổi danh ngươi, và ngươi sẽ thành một nguồn phước."));
visuperobject.biblemap.put("b1_12_3", new visuperobject(1,12,3,"Ta sẽ ban phước cho người nào chúc phước cho ngươi, rủa sả kẻ nào rủa sả ngươi; và các chi tộc nơi thế gian sẽ nhờ ngươi mà được phước."));
visuperobject.biblemap.put("b1_12_4", new visuperobject(1,12,4,"Rồi Áp-ram đi, theo như lời Ðức Giê-hô-va đã phán dạy; Lót đồng đi với người. Khi Áp-ram ra khỏi Cha-ran, tuổi người được bảy mươi lăm."));
visuperobject.biblemap.put("b1_12_5", new visuperobject(1,12,5,"Áp-ram dẫn Sa-rai, vợ mình, Lót, cháu mình, cả gia tài đã thâu góp, và các đầy tớ đã được tại Cha-ran, từ đó ra, để đi đến xứ Ca-na-an; rồi, chúng đều đến xứ Ca-na-an."));
visuperobject.biblemap.put("b1_12_6", new visuperobject(1,12,6,"Áp-ram trải qua xứ nầy, đến cây dẻ bộp của Mô-rê, tại Si-chem. Vả, lúc đó, dân Ca-na-an ở tại xứ."));
visuperobject.biblemap.put("b1_12_7", new visuperobject(1,12,7,"Ðức Giê-hô-va hiện ra cùng Áp-ram mà phán rằng: Ta sẽ ban cho dòng dõi ngươi đất nầy! Rồi tại đó Áp-ram lập một bàn thờ cho Ðức Giê-hô-va, là Ðấng đã hiện đến cùng người."));
visuperobject.biblemap.put("b1_12_8", new visuperobject(1,12,8,"Từ đó, người đi qua núi ở về phía đông Bê-tên, rồi đóng trại; phía tây có Bê-tên, phía đông có A-hi. Ðoạn, người lập tại đó một bàn thờ cho Ðức Giê-hô-va và cầu khẩn danh Ngài."));
visuperobject.biblemap.put("b1_12_9", new visuperobject(1,12,9,"Kế sau, Áp-ram vừa đi vừa đóng trại lần lần đến Nam phương."));
visuperobject.biblemap.put("b1_12_10", new visuperobject(1,12,10,"Bấy giờ, trong xứ bị cơn đói kém; sự đói kém ấy lớn, nên Áp-ram xuống xứ Ê-díp-tô mà kiều ngụ."));
visuperobject.biblemap.put("b1_12_11", new visuperobject(1,12,11,"Khi hầu vào đất Ê-díp-tô, Áp-ram bèn nói cùng Sa-rai, vợ mình, rằng: Nầy, ta biết ngươi là một người đờn bà đẹp."));
visuperobject.biblemap.put("b1_12_12", new visuperobject(1,12,12,"Gặp khi nào dân Ê-díp-tô thấy ngươi, họ sẽ nói rằng: Ấy là vợ hắn đó; họ sẽ giết ta, nhưng để cho ngươi sống."));
visuperobject.biblemap.put("b1_12_13", new visuperobject(1,12,13,"Ta xin hãy xưng ngươi là em gái ta, hầu cho sẽ vì ngươi mà ta được trọng đãi và giữ toàn mạng ta."));
visuperobject.biblemap.put("b1_12_14", new visuperobject(1,12,14,"Áp-ram vừa đến xứ Ê-díp-tô, dân Ê-díp-tô nhìn thấy người đờn bà đó đẹp lắm."));
visuperobject.biblemap.put("b1_12_15", new visuperobject(1,12,15,"Các triều thần của Pha-ra-ôn cùng thấy người và trằm-trồ trước mặt vua; đoạn người đờn bà bị dẫn vào cung Pha-ra-ôn."));
visuperobject.biblemap.put("b1_12_16", new visuperobject(1,12,16,"Vì cớ người, nên Pha-ra-ôn hậu đãi Áp-ram, và Áp-ram được nhiều chiên, bò, lừa đực, lừa cái, lạc đà, tôi trai và tớ gái."));
visuperobject.biblemap.put("b1_12_17", new visuperobject(1,12,17,"Song vì Sa-rai, vợ Áp-ram, nên Ðức Giê-hô-va hành phạt Pha-ra-ôn cùng nhà người bị tai họa lớn."));
visuperobject.biblemap.put("b1_12_18", new visuperobject(1,12,18,"Pha-ra-ôn bèn đòi Áp-ram hỏi rằng: Ngươi đã làm chi cho ta vậy? Sao không tâu với ta rằng là vợ ngươi?"));
visuperobject.biblemap.put("b1_12_19", new visuperobject(1,12,19,"Sao đã nói rằng: người đó là em gái tôi? nên nỗi ta đã lấy nàng làm vợ. Bây giờ, vợ ngươi đây; hãy nhận lấy và đi đi."));
visuperobject.biblemap.put("b1_12_20", new visuperobject(1,12,20,"Ðoạn, Pha-ra-ôn hạ lịnh cho quan quân đưa vợ chồng Áp-ram cùng hết thảy tài vật của người đi."));
visuperobject.biblemap.put("b1_13_1", new visuperobject(1,13,1,"Áp-ram ở Ê-díp-tô dẫn vợ cùng các tài vật mình và Lót đồng trở lên Nam phương."));
visuperobject.biblemap.put("b1_13_2", new visuperobject(1,13,2,"Vả, Áp-ram rất giàu có súc vật, vàng và bạc."));
visuperobject.biblemap.put("b1_13_3", new visuperobject(1,13,3,"người vừa đi vừa đóng trại, từ Nam phương trở về Bê-tên và A-hi,"));
visuperobject.biblemap.put("b1_13_4", new visuperobject(1,13,4,"là nơi đã lập một bàn thờ lúc trước. Ở đó Áp-ram cầu khẩn danh Ðức Giê-hô-va."));
visuperobject.biblemap.put("b1_13_5", new visuperobject(1,13,5,"Vả, Lót cùng đi với Áp-ram, cũng có chiên, bò, và trại."));
visuperobject.biblemap.put("b1_13_6", new visuperobject(1,13,6,"Xứ đó không đủ chỗ cho hai người ở chung, vì tài vật rất nhiều cho đến đỗi không ở chung nhau được."));
visuperobject.biblemap.put("b1_13_7", new visuperobject(1,13,7,"Trong khi dân Ca-na-an và dân Phê-rê-sít ở trong xứ, xảy có chuyện tranh giành của bọn chăn chiên Áp-ram cùng bọn chăn chiên Lót."));
visuperobject.biblemap.put("b1_13_8", new visuperobject(1,13,8,"Áp-ram nói cùng Lót rằng: Chúng ta là cốt nhục, xin ngươi cùng ta chẳng nên cãi lẫy nhau và bọn chăn chiên ta cùng bọn chăn chiên ngươi cũng đừng tranh giành nhau nữa."));
visuperobject.biblemap.put("b1_13_9", new visuperobject(1,13,9,"Toàn xứ há chẳng ở trước mặt ngươi sao? Vậy, hãy lìa khỏi ta; nếu ngươi lấy bên hữu, ta sẽ qua bên tả."));
visuperobject.biblemap.put("b1_13_10", new visuperobject(1,13,10,"Lót bèn ngước mắt lên, thấy khắp cánh đồng bằng bên sông Giô-đanh, là nơi (trước khi Ðức Giê-hô-va chưa phá hủy thành Sô-đôm và Gô-mô-rơ) thảy đều có nước chảy tưới khắp đến Xoa; đồng ó cũng như vườn của Ðức Giê-hô-va và như xứ Ê-díp-tô vậy."));
visuperobject.biblemap.put("b1_13_11", new visuperobject(1,13,11,"Lót bèn chọn lấy cho mình hết cánh đồng bằng bên sông Giô-đanh và đi qua phía Ðông. Vậy, hai người chia rẽ nhau."));
visuperobject.biblemap.put("b1_13_12", new visuperobject(1,13,12,"Áp-ram ở trong xứ Ca-na-an, còn Lót ở trong thành của đồng bằng và dời trại mình đến Sô-đôm."));
visuperobject.biblemap.put("b1_13_13", new visuperobject(1,13,13,"Vả, dân Sô-đôm là độc ác và kẻ phạm tội trọng cùng Ðức Giê-hô-va."));
visuperobject.biblemap.put("b1_13_14", new visuperobject(1,13,14,"Sau khi Lót lìa khỏi Áp-ram rồi, Ðức Giê-hô-va phán cùng Áp-ram rằng: Hãy nhướng mắt lên, nhìn từ chỗ ngươi cho đến phương bắc, phương nam, phương đông và phương tây:"));
visuperobject.biblemap.put("b1_13_15", new visuperobject(1,13,15,"Vì cả xứ nào ngươi thấy, ta sẽ ban cho ngươi và cho dòng dõi ngươi đời đời."));
visuperobject.biblemap.put("b1_13_16", new visuperobject(1,13,16,"Ta sẽ làm cho dòng dõi ngươi như bụi trên đất; thế thì, nếu kẻ nào đếm đặng dòng dõi ngươi vậy."));
visuperobject.biblemap.put("b1_13_17", new visuperobject(1,13,17,"Hãy đứng dậy đi khắp trong xứ, bề dài và bề ngang; vì ta sẽ ban cho ngươi xứ nầy."));
visuperobject.biblemap.put("b1_13_18", new visuperobject(1,13,18,"Ðoạn áp-ram đời trại mình đến ở nơi lùm cây dẻ bộp tại Mam-rê, thuộc về Hếp-rôn, và lập tại đó một bàn thờ cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b1_14_1", new visuperobject(1,14,1,"Trong đời Am-ra-phên, vua Si-nê-a; A-ri-óc, vua Ê-la-sa; Kết-rô-Lao me, vua Ê-lam, và Ti-đanh, vua Gô-im,"));
visuperobject.biblemap.put("b1_14_2", new visuperobject(1,14,2,"bốn vua hiệp lại tranh chiến cùng Bê-ra, vua Sô-đôm; Bi-rê-sa, vua Gô-mô-rơ; Si-nê-áp, vua Át-ma; Sê-mê-bê, vua Xê-bô-im, và vua Bê-la, tức là Xoa."));
visuperobject.biblemap.put("b1_14_3", new visuperobject(1,14,3,"Các vua nầy hiệp lại tại trũng Si-điêm, bây giờ là biển muối."));
visuperobject.biblemap.put("b1_14_4", new visuperobject(1,14,4,"Trong mười hai năm các vua nầy đều là chư-hầu của vua Kết-rô-Lao me; nhưng qua năm thứ mười ba, thì họ dấy loạn."));
visuperobject.biblemap.put("b1_14_5", new visuperobject(1,14,5,"Nhằm năm thứ mười bốn, Kết-rô-Lao-me cùng các vua đồng minh kéo quân đến dẹp dân Rê-pha-im ở đất Ách-tê-rót-Ca-na-im, dân Xu-xin tại đất Ham, dân Ê-mim ở trong đồng bằng Ki-ri-a-ta-im,"));
visuperobject.biblemap.put("b1_14_6", new visuperobject(1,14,6,"và dân Hô-rít tại núi Sê -i-rơ, cho đến nơi Eân-Ba-ran, ở gần đồng vắng."));
visuperobject.biblemap.put("b1_14_7", new visuperobject(1,14,7,"Ðoạn, các vua thâu binh trở về, đến Suối Xử đoán, tức Ca-đe, hãm đánh khắp miền dân A-ma-léc, và dân A-mô-rít ở tại Hát-sát sôn-Tha-ma."));
visuperobject.biblemap.put("b1_14_8", new visuperobject(1,14,8,"Các vua của Sô-đôm, Gô-mô-rơ, Át-ma, Xê-bô-im và vua Bê-la, tức là Xoa, bèn xuất trận và dàn binh đóng tại trũng Si-điêm,"));
visuperobject.biblemap.put("b1_14_9", new visuperobject(1,14,9,"đặng chống cự cùng Kết-rô-Lao-me, vua Ê-lam; Ti-đanh, vua Gô-im; Am-ra-phên, vua Si-nê-a, và A-ri-óc, vua Ê-la-sa; bốn vị đương địch cùng năm."));
visuperobject.biblemap.put("b1_14_10", new visuperobject(1,14,10,"Vả, trong trũng Si-điêm có nhiều hố nhựa chai; vua Sô-đôm và vua Gô-mô-rơ thua chạy, sa xuống hố, còn kẻ nào thoát được chạy trốn trên núi."));
visuperobject.biblemap.put("b1_14_11", new visuperobject(1,14,11,"Bên thắng trận bèn cướp lấy hết của cải và lương thực của Sô-đôm và Gô-mô-rơ, rồi đi."));
visuperobject.biblemap.put("b1_14_12", new visuperobject(1,14,12,"Giặc cũng bắt Lót, là cháu của Áp-ram, ở tại Sô-đôm, và hết thảy gia tài người, rồi đem đi."));
visuperobject.biblemap.put("b1_14_13", new visuperobject(1,14,13,"Có một người thoát được chạy đến báo điều đó cùng Áp-ram, là người Hê-bơ-rơ. Áp-ram ở tại lùm cây dẻ bộp của Mam-rê, là người A-mô-rít, anh của Ếch-côn và A-ne; ba người nầy đã có kết-ước cùng Áp-ram."));
visuperobject.biblemap.put("b1_14_14", new visuperobject(1,14,14,"Khi Áp-ram hay được cháu mình bị quân giặc bắt, bèn chiêu tập ba trăm mười tám gia nhân đã tập luyện, sanh đẻ nơi nhà mình, mà đuổi theo các vua đó đến đất Ðan."));
visuperobject.biblemap.put("b1_14_15", new visuperobject(1,14,15,"Ðoạn Áp-ram chia bọn đầy tớ mình ra, thừa ban đêm xông hãm quân nghịch, đánh đuổi theo đến đất Hô-ba ở về phía tả Ða-mách."));
visuperobject.biblemap.put("b1_14_16", new visuperobject(1,14,16,"Người thâu về đủ hết các tài vật mà quân giặc đã cướp lấy; lại cũng dẫn cháu mình, là Lót cùng gia tài người, đờn bà và dân chúng trở về."));
visuperobject.biblemap.put("b1_14_17", new visuperobject(1,14,17,"Sau khi Áp-ram đánh bại Kết-rô-Lao-me và các vua đồng minh, thắng trận trở về, thì vua Sô-đôm ra đón rước người tại trũng Sa-ve, tức là trũng Vua."));
visuperobject.biblemap.put("b1_14_18", new visuperobject(1,14,18,"Mên-chi-xê-đéc, vua Sa-lem, sai đem bánh và rượu ra. Vả, vua nầy là thầy tế lễ của Ðức Chúa Trời Chí cao,"));
visuperobject.biblemap.put("b1_14_19", new visuperobject(1,14,19,"chúc phước cho Áp-ram và nói rằng: Nguyện Ðức Chúa Trời Chí cao, là Ðấng dựng nên trời và đất, ban phước cho Áp-ram!"));
visuperobject.biblemap.put("b1_14_20", new visuperobject(1,14,20,"Ðáng ngợi khen thay Ðức Chúa Trời Chí cao đã phó kẻ thù nghịch vào tay ngươi! Ðoạn, Áp-ram lấy một phần mười về cả của giặc mà dâng cho vua đó."));
visuperobject.biblemap.put("b1_14_21", new visuperobject(1,14,21,"Vua Sô-đôm bèn nói cùng Áp-ram rằng: Hãy giao người cho ta, còn của cải, thì ngươi thâu lấy."));
visuperobject.biblemap.put("b1_14_22", new visuperobject(1,14,22,"Áp-ram đáp lại rằng: Tôi giơ tay lên trước mặt Giê-hô-va Ðức Chúa Trời Chí cao, Chúa Tể của trời và đất, mà thề rằng:"));
visuperobject.biblemap.put("b1_14_23", new visuperobject(1,14,23,"Hễ của chi thuộc về vua, dầu đến một sợi chỉ, hay là một sợi dây giày đi nữa, tôi cũng chẳng hề lấy; e vua nói được rằng: Nhờ ta làm cho Áp-ram giàu có,"));
visuperobject.biblemap.put("b1_14_24", new visuperobject(1,14,24,"chỉ món chi của những người trẻ đã ăn, và phần của các người cùng đi với tôi, là A-ne, Ếch-côn và Mam-rê; về phần họ, họ hãy lấy phần của họ đi."));
visuperobject.biblemap.put("b1_15_1", new visuperobject(1,15,1,"Sau các việc đó, trong sự hiện thấy có lời Ðức Giê-hô-va phán cùng Áp-ram rằng: Hỡi Áp-ram! ngươi chớ sợ chi; ta đây là một cái thuẫn đỡ cho ngươi; phần thưởng của ngươi sẽ rất lớn."));
visuperobject.biblemap.put("b1_15_2", new visuperobject(1,15,2,"Áp-ram thưa rằng: Lạy Chúa Giê-hô-va, Chúa sẽ cho tôi chi? Tôi sẽ chết không con, kẻ nối nghiệp nhà tôi là Ê-li-ê-se, người Ða-mách."));
visuperobject.biblemap.put("b1_15_3", new visuperobject(1,15,3,"Áp-ram lại nói rằng: Nầy, Chúa làm cho tôi tuyệt-tự; một kẻ tôi tớ sanh đẻ tại nhà tôi sẽ làm người kế nghiệp tôi."));
visuperobject.biblemap.put("b1_15_4", new visuperobject(1,15,4,"Ðức Giê-hô-va bèn phán cùng Áp-ram rằng: Kẻ đó chẳng phải là kẻ kế nghiệp ngươi đâu, nhưng ai ở trong gan ruột ngươi ra, sẽ là người kế nghiệp ngươi."));
visuperobject.biblemap.put("b1_15_5", new visuperobject(1,15,5,"Ðoạn, Ngài dẫn người ra ngoài và phán rằng: Ngươi hãy ngó lên trời, và nếu ngươi đếm được các ngôi sao thì hãy đếm đi. Ngài lại phán rằng: Dòng dõi ngươi cũng sẽ như vậy."));
visuperobject.biblemap.put("b1_15_6", new visuperobject(1,15,6,"Áp-ram tin Ðức Giê-hô-va, thì Ngài kể sự đó là công bình cho người."));
visuperobject.biblemap.put("b1_15_7", new visuperobject(1,15,7,"Ðức Giê-hô-va lại phán cùng Áp-ram rằng: Ta là Ðức Giê-hô-va, Ðấng đã dẫn ngươi ra khỏi U-rơ, thuộc về xứ Canh-đê, để ban cho ngươi xứ nầy làm sản nghiệp."));
visuperobject.biblemap.put("b1_15_8", new visuperobject(1,15,8,"Áp-ram thưa rằng: Lạy Chúa Giê-hô-va, bởi cớ chi tôi biết rằng tôi sẽ được xứ nầy làm sản nghiệp?"));
visuperobject.biblemap.put("b1_15_9", new visuperobject(1,15,9,"Ðức Giê-hô-va đáp rằng: Ngươi hãy bắt đem cho ta một con bò cái ba tuổi, một con dê cái ba tuổi, một con chiên đực ba tuổi, một con cu rừng và một con bò câu con."));
visuperobject.biblemap.put("b1_15_10", new visuperobject(1,15,10,"Áp-ram bắt đủ các loài vật đó, mổ làm hai, để mỗi nửa con mỗi bên đối với nhau, nhưng không mổ các loài chim ra làm hai."));
visuperobject.biblemap.put("b1_15_11", new visuperobject(1,15,11,"Có những chim ăn mồi bay đáp trên mấy con thú chết đó, song Áp-ram đuổi nó đi."));
visuperobject.biblemap.put("b1_15_12", new visuperobject(1,15,12,"Vả, khi mặt trời vừa lặn, thì Áp-ram ngủ mê; nầy một cơn kinh hãi, tối tăm nhập vào mình người."));
visuperobject.biblemap.put("b1_15_13", new visuperobject(1,15,13,"Ðức Giê-hô-va phán cùng Áp-ram rằng: Phải biết rằng, dòng dõi ngươi sẽ ngụ trong một xứ chẳng thuộc về chúng nó, làm tôi mọi cho dân xứ đó và bị họ hà hiếp bốn trăm năm."));
visuperobject.biblemap.put("b1_15_14", new visuperobject(1,15,14,"Nhưng, ta sẽ đoán phạt dân mà dòng dõi ngươi sẽ làm tôi mọi đó; rồi khi ra khỏi xứ, thì sẽ được của cải rất nhiều."));
visuperobject.biblemap.put("b1_15_15", new visuperobject(1,15,15,"Còn ngươi sẽ bình yên về nơi tổ phụ, hưởng lộc già sung sướng, rồi qua đời."));
visuperobject.biblemap.put("b1_15_16", new visuperobject(1,15,16,"Ðến đời thứ tư, dòng dõi ngươi sẽ trở lại đây, vì tội lỗi của dân A-mô-rít chưa được đầy dẫy."));
visuperobject.biblemap.put("b1_15_17", new visuperobject(1,15,17,"Khi mặt trời đã lặn, thình lình sự tối mịt giáng xuống; kìa có một lò lớn khói lên, và một ngọn lửa lòe ngang qua các xác thịt đã mổ,"));
visuperobject.biblemap.put("b1_15_18", new visuperobject(1,15,18,"Ngày đó, Ðức Giê-hô-va lập giao ước cùng Áp-ram, mà phán rằng: Ta cho dòng dõi ngươi xứ nầy, từ sông Ê-díp-tô cho đến sông lớn kia, tức sông Ơ-phơ-rát,"));
visuperobject.biblemap.put("b1_15_19", new visuperobject(1,15,19,"là xứ của các dân Kê-nít, Kê-nê-sít, Cát-mô-nít"));
visuperobject.biblemap.put("b1_15_20", new visuperobject(1,15,20,"Hê-tít, Phê-rê-sít, Rê-pha-im,"));
visuperobject.biblemap.put("b1_15_21", new visuperobject(1,15,21,"A-mô-nít, Ca-na-an, Ghi-ê-ga-sít và Giê-bu-sít."));
visuperobject.biblemap.put("b1_16_1", new visuperobject(1,16,1,"Vả, Sa-rai, vợ của Áp-ram, vẫn không sanh con; nàng có một con đòi Ê-díp-tô, tên là A-ga."));
visuperobject.biblemap.put("b1_16_2", new visuperobject(1,16,2,"Sa-rai nói cùng Áp-ram rằng: Nầy, Ðức Giê-hô-va đã làm cho tôi son sẻ, vậy xin ông hãy lại ăn ở cùng con đòi tôi, có lẽ tôi sẽ nhờ nó mà có con chăng. Áp-ram bèn nghe theo lời của Sa-rai."));
visuperobject.biblemap.put("b1_16_3", new visuperobject(1,16,3,"Sau khi Áp-ram đã trú ngụ mười năm tại xứ Ca-na-an, Sa-rai, vợ người, bắt A-ga, là con đòi Ê-díp-tô mình, đưa cho chồng làm hầu."));
visuperobject.biblemap.put("b1_16_4", new visuperobject(1,16,4,"Người lại cùng con đòi, thì nàng thọ thai. Khi con đòi thấy mình thọ thai, thì khinh bỉ bà chủ mình."));
visuperobject.biblemap.put("b1_16_5", new visuperobject(1,16,5,"Sa-rai nói cùng Áp-ram rằng: Ðiều sỉ nhục mà tôi bị đây đổ lại trên ông. Tôi đã phú con đòi tôi vào lòng ông, mà từ khi nó thấy mình thọ thai, thì lại khinh tôi. Cầu Ðức Giê-hô-va xét đoán giữa tôi với ông."));
visuperobject.biblemap.put("b1_16_6", new visuperobject(1,16,6,"Áp-ram đáp cùng Sa-rai rằng: Nầy, con đòi đó ở trong tay ngươi, phân xử thể nào, mặc ý ngươi cho vừa dạ. Ðoạn Sa-rai hành hạ A-ga, thì nàng trốn đi khỏi mặt người."));
visuperobject.biblemap.put("b1_16_7", new visuperobject(1,16,7,"Nhưng thiên sứ của Ðức Giê-hô-va thấy nàng ở trong đồng vắng gần bên suối nước, nơi mé đường đi và Su-rơ,"));
visuperobject.biblemap.put("b1_16_8", new visuperobject(1,16,8,"thì hỏi rằng: Hỡi A-ga, đòi của Sa-rai, ngươi ở đâu đến, và sẽ đi đâu? Nàng thưa rằng: Tôi lánh xa mặt Sa-rai, chủ tôi."));
visuperobject.biblemap.put("b1_16_9", new visuperobject(1,16,9,"Thiên sứ của Ðức Giê-hô-va dạy nàng rằng: Ngươi hãy trở về chủ ngươi, và chịu lụy dưới tay người."));
visuperobject.biblemap.put("b1_16_10", new visuperobject(1,16,10,"Thiên sứ của Ðức Giê-hô-va lại phán rằng: Ta sẽ thêm dòng dõi ngươi nhiều, đông đảo đến đỗi người ta đếm không đặng nữa."));
visuperobject.biblemap.put("b1_16_11", new visuperobject(1,16,11,"Lại phán rằng: Nầy, ngươi đương có thai, sẽ sanh một trai, đặt tên là Ích-ma-ên; vì Ðức Giê-hô-va có nghe sự sầu khổ của ngươi."));
visuperobject.biblemap.put("b1_16_12", new visuperobject(1,16,12,"Ðứa trẻ đó sẽ như một con lừa rừng; tay nó sẽ địch cùng mọi người, và tay mọi người sẽ địch lại nó. Nó sẽ ở về phía đông đối mặt cùng hết thảy anh em mình."));
visuperobject.biblemap.put("b1_16_13", new visuperobject(1,16,13,"Nàng gọi Ðức Giê-hô-va mà đã phán cùng mình, danh là Ðức Chúa Trời hay đoái xem, vì nàng nói rằng: Chính tại đây, tôi há chẳng có thấy được Ðắng đoái xem tôi sao?"));
visuperobject.biblemap.put("b1_16_14", new visuperobject(1,16,14,"Bởi cớ ấy, người ta gọi cái giếng nầy ở về giữa khoảng của Ca-đe và Bê-re, là giếng La-chai-Roi."));
visuperobject.biblemap.put("b1_16_15", new visuperobject(1,16,15,"Rồi nàng A-ga sanh được một con trai; Áp-ram đặt tên đứa trai đó là Ích-ma-ên."));
visuperobject.biblemap.put("b1_16_16", new visuperobject(1,16,16,"Vả lại, khi A-ga sanh Ích-ma-ên cho Áp-ram, thì Áp-ram đã được tám mươi sáu tuổi."));
visuperobject.biblemap.put("b1_17_1", new visuperobject(1,17,1,"Khi Áp-ram được chín mươi chín tuổi, thì Ðức Giê-hô-va hiện đến cùng người và phán rằng: Ta là Ðức Chúa Trời toàn năng; ngươi hãy đi ở trước mặt ta làm một người trọn vẹn."));
visuperobject.biblemap.put("b1_17_2", new visuperobject(1,17,2,"Ta sẽ lập giao ước cùng ngươi, làm cho dòng dõi ngươi thêm nhiều quá-bội."));
visuperobject.biblemap.put("b1_17_3", new visuperobject(1,17,3,"Áp-ram bèn sấp mình xuống đất; Ðức Chúa Trời phán cùng người rằng:"));
visuperobject.biblemap.put("b1_17_4", new visuperobject(1,17,4,"Nầy, phần ta đây, ta đã lập giao ước cùng ngươi; vậy ngươi sẽ trở nên tổ phụ của nhiều dân tộc."));
visuperobject.biblemap.put("b1_17_5", new visuperobject(1,17,5,"Thiên hạ chẳng còn gọi ngươi là Áp-ram nữa, nhưng tên ngươi là Áp-ra-ham, vì ta đặt ngươi làm tổ phụ của nhiều dân tộc."));
visuperobject.biblemap.put("b1_17_6", new visuperobject(1,17,6,"Ta sẽ làm cho ngươi sanh sản rất nhiều, làm cho ngươi thành nhiều nước; và các vua sẽ do nơi ngươi mà ra."));
visuperobject.biblemap.put("b1_17_7", new visuperobject(1,17,7,"Ta sẽ lập giao ước cùng ngươi, và cùng hậu tự ngươi trải qua các đời; ấy là giao ước đời đời, hầu cho ta làm Ðức Chúa Trời của ngươi và của dòng dõi ngươi."));
visuperobject.biblemap.put("b1_17_8", new visuperobject(1,17,8,"Ta sẽ cho ngươi cùng dòng dõi ngươi xứ mà ngươi đương kiều ngụ, tức toàn xứ Ca-na-an, làm cơ nghiệp đời đời. Vậy, ta sẽ làm Ðức Chúa Trời của họ."));
visuperobject.biblemap.put("b1_17_9", new visuperobject(1,17,9,"Ðoạn, Ðức Chúa Trời phán cùng Áp-ra-ham rằng: Phần ngươi cùng dòng dõi ngươi, từ đời nọ sang đời kia, sẽ giữ sự giao ước của ta."));
visuperobject.biblemap.put("b1_17_10", new visuperobject(1,17,10,"Mỗi người nam trong vòng các ngươi phải chịu phép cắt bì; ấy là giao ước mà các ngươi phải giữ, tức giao ước lập giữa ta và các ngươi, cùng dòng dõi sau ngươi."));
visuperobject.biblemap.put("b1_17_11", new visuperobject(1,17,11,"Các ngươi phải chịu cắt bì; phép đó sẽ là dấu hiệu của sự giao ước giữa ta cùng các ngươi."));
visuperobject.biblemap.put("b1_17_12", new visuperobject(1,17,12,"Trải qua các đời, mỗi người nam trong vòng các ngươi, hoặc sanh đẻ tại nhà, hoặc đem tiền ra mua nơi người ngoại bang, chẳng thuộc về dòng giống ngươi, hễ lên được tám ngày rồi, đều phải chịu phép cắt bì."));
visuperobject.biblemap.put("b1_17_13", new visuperobject(1,17,13,"Chớ khá bỏ làm phép cắt bì cho ai sanh tại trong nhà ngươi, hay đem tiền ra mua về; sự giao ước của ta sẽ lập đời đời trong xác thịt của các ngươi vậy."));
visuperobject.biblemap.put("b1_17_14", new visuperobject(1,17,14,"Một người nam nào không chịu phép cắt bì nơi xác thịt mình, sẽ bị truất ra khỏi ngoài dân sự mình; người đó là kẻ bội lời giao ước ta."));
visuperobject.biblemap.put("b1_17_15", new visuperobject(1,17,15,"Ðức Chúa Trời phán cùng Áp-ra-ham rằng: còn Sa-rai, vợ ngươi, chớ gọi là Sa-rai nữa; nhưng Sa-ra là tên người đó."));
visuperobject.biblemap.put("b1_17_16", new visuperobject(1,17,16,"Ta sẽ ban phước cho nàng, lại do nơi nàng ta sẽ cho ngươi một con trai, Ta sẽ ban phước cho nàng, nàng sẽ làm mẹ các dân tộc; những vua của các dân tộc sẽ do nơi nàng mà ra."));
visuperobject.biblemap.put("b1_17_17", new visuperobject(1,17,17,"Áp-ra-ham bèn sấp mình xuống đất, cười và nói thầm rằng: Hồ dễ người đã trăm tuổi rồi, mà sanh con được chăng? Còn Sa-ra, tuổi đã chín mươi, sẽ sanh sản được sao?"));
visuperobject.biblemap.put("b1_17_18", new visuperobject(1,17,18,"Áp-ra-ham thưa cùng Ðức Chúa Tr»»i rằng: Chớ chi Ích-ma-ên vẫn được sống trước mặt Ngài!"));
visuperobject.biblemap.put("b1_17_19", new visuperobject(1,17,19,"Ðức Chúa Trời bèn phán rằng: Thật vậy, Sa-ra vợ ngươi, sẽ sanh một con trai, rồi ngươi đặt tên là Y-sác. Ta sẽ lập giao ước cùng nó, để làm giao ước đời đời cho dòng dõi của nó."));
visuperobject.biblemap.put("b1_17_20", new visuperobject(1,17,20,"Ta cũng nhậm lời ngươi xin cho Ích-ma-ên. Nầy, ta ban phước cho người, sẽ làm cho người sanh sản và thêm nhiều quá-bội; người sẽ là tổ phụ của mười hai vị công-hầu, và ta làm cho người thành một dân lớn."));
visuperobject.biblemap.put("b1_17_21", new visuperobject(1,17,21,"Nhưng ta sẽ lập giao ước ta cùng Y-sác, độ khoảng nầy năm tới Sa-ra phải sanh cho ngươi."));
visuperobject.biblemap.put("b1_17_22", new visuperobject(1,17,22,"Khi Ðức Chúa Trời đã phán xong, thì Ngài từ Áp-ra-ham ngự lên."));
visuperobject.biblemap.put("b1_17_23", new visuperobject(1,17,23,"Chánh ngày đó, Áp-ra-ham bắt Ích-ma-ên, con trai mình, và hết thảy các người nam trong nhà, hoặc sanh tại đó, hoặc đem tiền ra mua về, mà làm phép cắt bì cho, y như lời Ðức Chúa Trời đã phải dặn."));
visuperobject.biblemap.put("b1_17_24", new visuperobject(1,17,24,"Vả, khi Áp-ra-ham chịu phép cắt bì, thì đã được chín mươi chín tuổi;"));
visuperobject.biblemap.put("b1_17_25", new visuperobject(1,17,25,"còn Ích-ma-ên, con người, khi chịu phép cắt bì, thì được mười ba tuổi."));
visuperobject.biblemap.put("b1_17_26", new visuperobject(1,17,26,"Cũng trong một ngày đó, Áp-ra-ham và Ích-ma-ên chịu phép cắt bì,"));
visuperobject.biblemap.put("b1_17_27", new visuperobject(1,17,27,"và mọi người nam trong nhà, hoặc sanh tại đó, hoặc đem tiền ra mua nơi người ngoại bang, đồng chịu phép cắt bì với người."));
visuperobject.biblemap.put("b1_18_1", new visuperobject(1,18,1,"Ðức Giê-hô-va hiện ra cùng Áp-ra-ham nơi lùm cây dẻ bộp của Mam-rê, đương khi ngồi nơi cửa trại lúc trời nắng ban ngày."));
visuperobject.biblemap.put("b1_18_2", new visuperobject(1,18,2,"Áp-ra-ham nhướng mắt lên, thấy ba người đứng trước mặt. Vừa khi thấy, bèn bắt từ cửa trại chạy đến trước mặt ba người đó, sấp mình xuống đất,"));
visuperobject.biblemap.put("b1_18_3", new visuperobject(1,18,3,"và thưa rằng: Lạy Chúa, nếu tôi được ơn trước mặt Chúa, xin hãy ghé lại nhà kẻ tôi tớ Chúa, đừng bỏ đi luôn."));
visuperobject.biblemap.put("b1_18_4", new visuperobject(1,18,4,"Xin các đấng hãy cho phép người ta lấy chút nước rửa chơn các đấng, và xin hãy nằm nghỉ mát dưới cội cây nầy."));
visuperobject.biblemap.put("b1_18_5", new visuperobject(1,18,5,"Tôi sẽ đi đem một miếng bánh cho các đấng ăn vững lòng, rồi sẽ dời gót lên đường; vì cớ ấy, nên mới quá bộ lại nhà kẻ tôi tớ các đấng vậy. Các đấng phán rằng: Hãy cứ việc làm như ngươi đã nói."));
visuperobject.biblemap.put("b1_18_6", new visuperobject(1,18,6,"Ðoạn, Áp-ra-ham lật đật vào trại đến cùng Sa-ra mà rằng: Hãy mau mau lấy ba đấu bột lọc nhồi đi, rồi làm bánh nhỏ."));
visuperobject.biblemap.put("b1_18_7", new visuperobject(1,18,7,"Áp-ra-ham bèn chạy lại bầy, bắt một con bò con ngon, giao cho đầy tớ mau mau nấu dọn;"));
visuperobject.biblemap.put("b1_18_8", new visuperobject(1,18,8,"rồi lấy mỡ-sữa và sữa cùng con bò con đã nấu xong, dọn ngay trước mặt các đấng; còn người thì đứng hầu trước mặt, dưới cội cây. Vậy các đấng đó bèn ăn."));
visuperobject.biblemap.put("b1_18_9", new visuperobject(1,18,9,"Các đấng hỏi Áp-ra-ham rằng: Sa-ra, vợ ngươi, ở đâu? Ðáp rằng: Kìa, nàng ở trong trại kia."));
visuperobject.biblemap.put("b1_18_10", new visuperobject(1,18,10,"Một đấng nói rằng: Trong độ một năm nữa, ta sẽ trở lại đây với ngươi không sai, và chừng đó, Sa-ra, vợ ngươi, sẽ có một con trai. Sa-ra ở nơi cửa trại sau lưng đấng đó, nghe các lời nầy."));
visuperobject.biblemap.put("b1_18_11", new visuperobject(1,18,11,"Vả, Áp-ra-ham cùng Sa-ra đã già, tuổi tác đã cao; Sa-ra không còn chi nữa như thế thường người đờn bà."));
visuperobject.biblemap.put("b1_18_12", new visuperobject(1,18,12,"Sa-ra cười thầm mà rằng: Già như tôi thế nầy, dễ còn được điều vui đó sao? Còn chúa tôi cũng đã già rồi!"));
visuperobject.biblemap.put("b1_18_13", new visuperobject(1,18,13,"Ðức Giê-hô-va phán hỏi Áp-ra-ham rằng: Cớ sao Sa-ra cười như vậy mà rằng: Có quả thật rằng tôi già đến thế nầy lại còn sanh sản chăng?"));
visuperobject.biblemap.put("b1_18_14", new visuperobject(1,18,14,"Há điều chi Ðức Giê-hô-va làm không được chăng? Ðến kỳ đã định, trong một năm nữa, ta sẽ trở lại cùng ngươi, và Sa-ra sẽ có một con trai."));
visuperobject.biblemap.put("b1_18_15", new visuperobject(1,18,15,"Vì Sa-ra sợ, nên chối mà thưa rằng: Tôi có cười đâu! Nhưng Ngài phán rằng: Thật ngươi có cười đó!"));
visuperobject.biblemap.put("b1_18_16", new visuperobject(1,18,16,"Các đấng đều đứng dậy mà đi, ngó về phía, Sô-đôm. Áp-ra-ham cũng theo để tiễn bước các đấng."));
visuperobject.biblemap.put("b1_18_17", new visuperobject(1,18,17,"Ðức Giê-hô-va phán rằng: Lẽ nào ta giấu Áp-ra-ham điều chi ta sẽ làm sao?"));
visuperobject.biblemap.put("b1_18_18", new visuperobject(1,18,18,"vì Áp-ra-ham chắc sẽ được trở nên một dân lớn và cường thạnh; các dân tộc trên thế gian đều sẽ nhờ người mà được phước."));
visuperobject.biblemap.put("b1_18_19", new visuperobject(1,18,19,"Ta đã chọn người đặng người khiến dạy các con cùng nội nhà người giữ theo đạo Ðức Giê-hô-va, làm các điều công bình và ngay thẳng; thế thì, Ðức Giê-hô-va sẽ làm cho ứng nghiệm lời Ngài đã hứa cùng Áp-ra-ham."));
visuperobject.biblemap.put("b1_18_20", new visuperobject(1,18,20,"Ðức Giê-hô-va phán rằng: Tiếng kêu oan về Sô-đôm và Gô-mô-rơ thật là quá, tội lỗi các thành đó thật là trọng."));
visuperobject.biblemap.put("b1_18_21", new visuperobject(1,18,21,"Ta muốn ngự xuống, để xem xét chúng nó ăn ở có thật như tiếng đã kêu thấu đến ta chăng; nếu chẳng thật, thì ta sẽ biết."));
visuperobject.biblemap.put("b1_18_22", new visuperobject(1,18,22,"Vậy, thì đấng bèn từ đó đi qua hướng Sô-đôm; nhưng Áp-ra-ham hãy còn đứng chầu trước mặt Ðức Giê-hô-va."));
visuperobject.biblemap.put("b1_18_23", new visuperobject(1,18,23,"Áp-ra-ham lại gần và thưa rằng: Chúa sẽ diệt người công bình luôn với người độc ác sao?"));
visuperobject.biblemap.put("b1_18_24", new visuperobject(1,18,24,"Ngộ trong thành có năm mươi người công bình, Chúa cũng sẽ diệt họ hết sao? Há chẳng tha thứ cho thành đó vì cớ năm mươi người công bình ở trong sao?"));
visuperobject.biblemap.put("b1_18_25", new visuperobject(1,18,25,"Không lẽ nào Chúa làm điều như vậy, diệt người công bình luôn với kẻ độc ác; đến đỗi kể người công bình cũng như người độc ác. Không, Chúa chẳng làm điều như vậy bao giờ! Ðấng đoán xét toàn thế gian, há lại không làm sự công bình sao?"));
visuperobject.biblemap.put("b1_18_26", new visuperobject(1,18,26,"Ðức Giê-hô-va phán rằng: Nếu ta tìm được trong Sô-đôm năm mươi người công bình, vì tình thương bấy nhiêu người đó ta sẽ tha hết cả thành."));
visuperobject.biblemap.put("b1_18_27", new visuperobject(1,18,27,"Áp-ra-ham lại thưa rằng: mặc dầu tôi đây vốn là tro bụi, song tôi cũng dám cả gan thưa lại cùng Chúa."));
visuperobject.biblemap.put("b1_18_28", new visuperobject(1,18,28,"Hãy trong năm mươi người công bình rủi thiếu hết năm; vì cớ năm người thiếu Chúa sẽ diệt hết cả thành chăng? Ngài trả lời rằng: Nếu ta tìm được có bốn mươi lăm người, ta chẳng diệt thành đâu."));
visuperobject.biblemap.put("b1_18_29", new visuperobject(1,18,29,"Áp-ra-ham cứ thưa rằng: Ngộ trong thành chỉ có bốn mươi người công bình, thì sẽ ra sao? Ngài phán rằng: Ta sẽ chẳng diệt đâu, vì tình thương bốn mươi nầy."));
visuperobject.biblemap.put("b1_18_30", new visuperobject(1,18,30,"Áp-ra-ham cứ tiếp: Tôi xin Chúa đừng giận, thì tôi sẽ thưa: Ngộ trong đó chỉ có ba mươi người, thì sẽ ra sao? Ngài phán: Nếu ta tìm trong đó có ba mươi người, thì ta chẳng diệt đâu."));
visuperobject.biblemap.put("b1_18_31", new visuperobject(1,18,31,"Áp-ra-ham thưa rằng: Tôi đây cũng cả gan thưa cùng Chúa: Nếu chỉ có hai mươi người, thì lại làm sao? Ngài rằng: Vì tình thương hai mươi người đó, ta sẽ chẳng diệt thành đâu."));
visuperobject.biblemap.put("b1_18_32", new visuperobject(1,18,32,"Áp-ra-ham lại thưa: Xin Chúa đừng giận, để cho tôi thưa chỉ một lần nầy nữa: Ngộ chỉ có mười người, thì nghĩ làm sao? Ngài phán rằng: Ta cũng sẽ chẳng diệt thành đâu, vì tình thương mười người đó."));
visuperobject.biblemap.put("b1_18_33", new visuperobject(1,18,33,"Khi Ðức Giê-hô-va phán xong cùng Áp-ra-ham, thì Ngài ngự đi; còn Áp-ra-ham trở về trại mình."));
visuperobject.biblemap.put("b1_19_1", new visuperobject(1,19,1,"Lối chiều, hai thiên sứ đến Sô-đôm; lúc đó, Lót đương ngồi tại cửa thành. Khi Lót thấy hai thiên sứ đến, đứng dậy mà đón rước và sấp mình xuống đất."));
visuperobject.biblemap.put("b1_19_2", new visuperobject(1,19,2,"Người thưa rằng: Nầy, lạy hai chúa, xin hãy đến ở nhà của kẻ tôi tớ, và hãy nghỉ đêm tại đó. Hai chúa hãy rửa chơn, rồi sáng mai thức dậy lên đường. Hai thiên sứ phán rằng: Không, đêm nầy ta sẽ ở ngoài đường."));
visuperobject.biblemap.put("b1_19_3", new visuperobject(1,19,3,"Nhưng Lót cố mời cho đến đỗi hai thiên sứ phải đi lại vào nhà mình. Người dâng một bữa tiệc, làm bánh không men, và hai thiên sứ bèn dùng tiệc."));
visuperobject.biblemap.put("b1_19_4", new visuperobject(1,19,4,"Hai thiên sứ chưa đi nằm, mà các người nam ở Sô-đôm, từ trẻ đến già, tức cả dân, đều chạy đến bao chung quanh nhà."));
visuperobject.biblemap.put("b1_19_5", new visuperobject(1,19,5,"Bọn đó gọi Lót mà hỏi rằng: Những khác đã vào nhà ngươi buổi chiều tối nay ở đâu? Hãy dẫn họ ra đây, hầu cho chúng ta được biết."));
visuperobject.biblemap.put("b1_19_6", new visuperobject(1,19,6,"Lót bèn ra đến cùng dân chúng ở ngoài cửa, rồi đóng cửa lại,"));
visuperobject.biblemap.put("b1_19_7", new visuperobject(1,19,7,"và nói cùng họ rằng: Nầy, tôi xin anh em đừng làm điều ác đó!"));
visuperobject.biblemap.put("b1_19_8", new visuperobject(1,19,8,"Ðây, tôi sẵn có hai con gái chưa chồng, tôi sẽ đưa chúng nó cho anh em, rồi mặc tình anh em tính làm sao tùy ý; miễn đừng làm cho hại cho hai người kia; vì cớ đó, nên họ đến núp bóng mái nhà tôi."));
visuperobject.biblemap.put("b1_19_9", new visuperobject(1,19,9,"Bọn dân chúng nói rằng: Ngươi hãy tránh chỗ khác! Lại tiếp rằng: Người nầy đến đây như kẻ kiều ngụ, lại muốn đoán xét nữa sao! Vậy, thôi! chúng ta sẽ đãi ngươi bạc-tệ hơn hai khách kia. Ðoạn, họ lấn ép Lót mạnh quá, và tràn đến đặng phá cửa."));
visuperobject.biblemap.put("b1_19_10", new visuperobject(1,19,10,"Nhưng hai thiên sứ giơ tay ra, đem Lót vào nhà, và đóng cửa lại,"));
visuperobject.biblemap.put("b1_19_11", new visuperobject(1,19,11,"đoạn, hành phạt bọn dân chúng ở ngoài cửa, từ trẻ đến già, đều quáng lòa mắt, cho đến đỗi tìm cửa mệt mà không được."));
visuperobject.biblemap.put("b1_19_12", new visuperobject(1,19,12,"Hai thiên sứ bèn hỏi Lót rằng: Ngươi còn có ai tại đây nữa chăng? Rể, con trai, con gái và ai trong thành thuộc về ngươi, hãy đem ra khỏi hết đi!"));
visuperobject.biblemap.put("b1_19_13", new visuperobject(1,19,13,"Chúng ta sẽ hủy diệt chỗ nầy, vì tiếng kêu oan về dân thành nầy đã thấu lên đến Ðức Giê-hô-va, nên Ngài sai chúng ta xuống mà hủy diệt."));
visuperobject.biblemap.put("b1_19_14", new visuperobject(1,19,14,"Lót bèn đi ra và nói cùng rể đã cưới con gái mình rằng: Hãy chổi dậy, đi ra khỏi chốn nầy, vì Ðức Giê-hô-va sẽ hủy diệt thành. Nhưng các chàng rể tưởng người nói chơi."));
visuperobject.biblemap.put("b1_19_15", new visuperobject(1,19,15,"Ðến sáng, hai thiên sứ hối Lót và phán rằng: Hãy thức dậy, dẫn vợ và hai con gái ngươi đương ở đây ra, e khi ngươi cũng chết lây về việc hình phạt của thành nữa chăng."));
visuperobject.biblemap.put("b1_19_16", new visuperobject(1,19,16,"Nhưng Lót lần lữa; vì cớ Ðức Giê-hô-va thương xót Lót, nên hai thiên sứ nắm lấy tai kéo người, vợ cùng hai con gái người, và dẫn ra khỏi thành."));
visuperobject.biblemap.put("b1_19_17", new visuperobject(1,19,17,"Vả, khi hai thiên sứ dẫn họ ra khỏi rồi, một trong hai vì nói rằng: Hãy chạy trốn cứu lấy mạng, đừng ngó lại sau và cũng đừng dừng bước lại nơi nào ngoài đồng bằng; hãy chạy trốn lên núi, kẻo phải bỏ mình chăng."));
visuperobject.biblemap.put("b1_19_18", new visuperobject(1,19,18,"Lót đáp lại rằng: Lạy Chúa, không được!"));
visuperobject.biblemap.put("b1_19_19", new visuperobject(1,19,19,"Nầy, tôi tớ đã được ơn trước mặt Chúa; Chúa đã tỏ lòng nhơn từ rất lớn cùng tôi mà cứu tròn sự sống tôi. Nhưng tôi chạy trốn lên núi không kịp trước khi tai nạn đến, thì tôi phải chết."));
visuperobject.biblemap.put("b1_19_20", new visuperobject(1,19,20,"Kìa, thành kia đã nhỏ, lại cũng gần đặng tôi có thế ẩn mình. Ôi! chớ chi Chúa cho tôi ẩn đó đặng cứu tròn sự sống tôi. Thành nầy há chẳng phải nhỏ sao?"));
visuperobject.biblemap.put("b1_19_21", new visuperobject(1,19,21,"Thiên sứ phán rằng: Ðây, ta ban ơn nầy cho ngươi nữa, sẽ không hủy diệt thành của ngươi đã nói đó đâu."));
visuperobject.biblemap.put("b1_19_22", new visuperobject(1,19,22,"Mau mau hãy lại ẩn đó, vì ta không làm chi được khi ngươi chưa vào đến nơi. Bởi cớ ấy, nên người ta gọi tên thành nầy là Xoa."));
visuperobject.biblemap.put("b1_19_23", new visuperobject(1,19,23,"Khi mặt trời mọc lên khỏi đất, thì Lót vào đến thành Xoa."));
visuperobject.biblemap.put("b1_19_24", new visuperobject(1,19,24,"Ðoạn, Ðức Giê-hô-va giáng mưa diêm sanh và lửa từ nơi Ngài trên trời sa xuống Sô-đôm và Gô-mô-rơ,"));
visuperobject.biblemap.put("b1_19_25", new visuperobject(1,19,25,"hủy diệt hai thành nầy, cả đồng bằng, hết thảy dân sự cùng các cây cỏ ở nơi đất đó."));
visuperobject.biblemap.put("b1_19_26", new visuperobject(1,19,26,"Nhưng vợ của Lót quay ngó lại đặng sau mình, nên hóa ra một tượng muối."));
visuperobject.biblemap.put("b1_19_27", new visuperobject(1,19,27,"Áp-ra-ham dậy sớm, đi đến nơi mà người đã đứng chầu Ðức Giê-hô-va,"));
visuperobject.biblemap.put("b1_19_28", new visuperobject(1,19,28,"ngó về hướng Sô-đôm và Gô-mô-rơ, cùng khắp xứ ở đồng bằng, thì thấy từ dưới đất bay lên một luồng khói, như khói của một lò lửa lớn."));
visuperobject.biblemap.put("b1_19_29", new visuperobject(1,19,29,"Vả, khi Ðức Chúa Trời hủy diệt các thành nơi đồng bằng, tức là thành Lót ở, thì nhớ đến Áp-ra-ham, cứu Lót ra khỏi chốn phá tan đó."));
visuperobject.biblemap.put("b1_19_30", new visuperobject(1,19,30,"Lót ở Xoa thì sợ hãi, nên cùng hai con gái mình bỏ đó mà lên núi, ở trong một hang đá kia."));
visuperobject.biblemap.put("b1_19_31", new visuperobject(1,19,31,"Cô lớn nói cùng em mình rằng: Cha ta đã già, mà không còn ai trên mặt đất đến sánh duyên cùng ta theo như thế thường thiên hạ."));
visuperobject.biblemap.put("b1_19_32", new visuperobject(1,19,32,"Hè! chúng ta hãy phục rượu cho cha, và lại nằm cùng người, để lưu truyền dòng giống cha lại."));
visuperobject.biblemap.put("b1_19_33", new visuperobject(1,19,33,"Ðêm đó, hai nàng phục rượu cho cha mình; nàng lớn đến nằm cùng cha; nhưng người chẳng hay lúc nào nàng nằm, lúc nào nàng dậy hết."));
visuperobject.biblemap.put("b1_19_34", new visuperobject(1,19,34,"Qua ngày mai, chị nói cùng em rằng: Nầy, đêm hôm qua ta đã nằm cùng cha rồi; hôm nay chúng ta hãy phục rượu cho cha nữa, rồi em hãy lại nằm cùng người, để lưu truyền dòng giống cha lại."));
visuperobject.biblemap.put("b1_19_35", new visuperobject(1,19,35,"Ðêm đó, hai nàng lại phục rượu cho cha mình nữa, rồi nàng nhỏ thức dậy lại nằm cùng cha; nhưng người chẳng hay lúc nào nàng nằm, lúc nào nàng dậy hết."));
visuperobject.biblemap.put("b1_19_36", new visuperobject(1,19,36,"Vậy, hai con gái của Lót do nơi cha mình mà thọ thai."));
visuperobject.biblemap.put("b1_19_37", new visuperobject(1,19,37,"Nàng lớn sanh được một con trai, đặt tên là Mô-áp; ấy là tổ phụ của dân Mô-áp đến bây giờ."));
visuperobject.biblemap.put("b1_19_38", new visuperobject(1,19,38,"Người em cũng sanh đặng một con trai, đặt trên là Bên-Am-mi; ấy là tổ phụ của dân Am-môn đến bây giờ."));
visuperobject.biblemap.put("b1_20_1", new visuperobject(1,20,1,"Áp-ra-ham từ đó đi đến miền Nam, kiều ngụ tại Ghê-ra, ở về giữa khoảng Ca-đe và Su-rơ."));
visuperobject.biblemap.put("b1_20_2", new visuperobject(1,20,2,"Áp-ra-ham nói về Sa-ra, vợ mình rằng: Nó là em gái tôi. A-bi-mê-léc, vua Ghê-ra sai người bắt Sa-ra."));
visuperobject.biblemap.put("b1_20_3", new visuperobject(1,20,3,"Nhưng trong cơn chiêm bao ban đêm, Ðức Chúa Trời hiện đến cùng vua A-bi-mê-léc mà phán rằng: Nầy, ngươi sẽ chết bởi cớ người đờn bà mà ngươi đã bắt đến; vì nàng có chồng rồi."));
visuperobject.biblemap.put("b1_20_4", new visuperobject(1,20,4,"Vả, vua A-bi-mê-léc chưa đến gần người đó, nên thưa rằng: Lạy Chúa, Chúa há sẽ hủy diệt cả một dân công bình chăng?"));
visuperobject.biblemap.put("b1_20_5", new visuperobject(1,20,5,"Người đó há chẳng nói với tôi rằng: ấy là em gái tôi chăng? và chánh người nữ há chẳng nói rằng: ấy là anh tôi sao? Tôi làm sự nầy bởi lòng ngay thẳng và tay thanh khiết của tôi."));
visuperobject.biblemap.put("b1_20_6", new visuperobject(1,20,6,"Trong cơn chiêm bao, Ðức Chúa Trời phán nữa rằng: Ta cũng biết ngươi vì lòng ngay thẳng mà làm điều đó; bởi cớ ấy, ta mới ngăn trở ngươi phạm tội cùng ta, và không cho động đến người đó."));
visuperobject.biblemap.put("b1_20_7", new visuperobject(1,20,7,"Bây giờ, hãy giao đờn bà đó lại cho chồng nó, vì chồng nó là một đấng tiên tri, sẽ cầu nguyện cho ngươi, thì ngươi mới được sống. Còn như không giao lại, thì phải biết rằng ngươi và hết thảy ai thuộc về ngươi quả hẳn sẽ chết."));
visuperobject.biblemap.put("b1_20_8", new visuperobject(1,20,8,"Vua A-bi-mê-léc dậy sớm, đòi các tôi tớ mình đến, thuật lại hết mọi lời, thì họ lấy làm kinh ngạc."));
visuperobject.biblemap.put("b1_20_9", new visuperobject(1,20,9,"Rồi, A-bi-mê-léc đòi Áp-ra-ham mà nói rằng: Ngươi đã làm gì cho ta vậy? Ta có làm điều chi mất lòng chăng mà ngươi làm cho ta và cả nước phải bị một việc phạm tội lớn dường nầy? Ðối cùng ta, ngươi đã làm những việc không nên làm đó."));
visuperobject.biblemap.put("b1_20_10", new visuperobject(1,20,10,"Vua A-bi-mê-léc lại nói cùng Áp-ra-ham rằng: Ngươi có ý gì mà làm như vậy?"));
visuperobject.biblemap.put("b1_20_11", new visuperobject(1,20,11,"Áp-ra-ham đáp: Tôi tự nghĩ rằng: Trong xứ nầy thật không có ai kính sợ Ðức Chúa Trời, thì họ sẽ vì cớ vợ tôi mà giết tôi chăng."));
visuperobject.biblemap.put("b1_20_12", new visuperobject(1,20,12,"Nhưng nó cũng thật là em gái tôi, em một cha khác mẹ; và tôi cưới nó làm vợ."));
visuperobject.biblemap.put("b1_20_13", new visuperobject(1,20,13,"Khi Ðức Chúa Trời làm cho tôi phải lưu lạc xa nhà cha, thì tôi có nói với nàng rằng: Nầy là ơn của ngươi sẽ làm cho ta: Hễ chỗ nào chúng ta sẽ đi đến, hãy nói về ta: Ấy là anh tôi."));
visuperobject.biblemap.put("b1_20_14", new visuperobject(1,20,14,"Ðoạn, vua A-bi-mê-léc đem chiên và bò, tôi trai cùng tớ gái cho Áp-ra-ham, và trả Sa-ra vợ người lại, mà phán rằng:"));
visuperobject.biblemap.put("b1_20_15", new visuperobject(1,20,15,"Nầy, xứ ta sẵn dành cho ngươi; ngươi thích đâu thì ở đó."));
visuperobject.biblemap.put("b1_20_16", new visuperobject(1,20,16,"Rồi vua phán cùng Sa-ra rằng: Ðây, ta ban cho anh ngươi một ngàn miếng bạc; số tiền đó dùng cho ngươi như một bức màn che trước mắt về mọi việc đã xảy ra cùng ngươi; và mọi người đều sẽ cho ngươi là công bình."));
visuperobject.biblemap.put("b1_20_17", new visuperobject(1,20,17,"Áp-ra-ham cầu xin Ðức Chúa Trời, thì Ngài chữa bịnh cho vua A-bi-mê-léc, vợ cùng các con đòi người; vậy, họ đều có con."));
visuperobject.biblemap.put("b1_20_18", new visuperobject(1,20,18,"Vả, lúc trước, vì vụ Sa-ra, vợ Áp-ra-ham, nên Ðức Giê-hô-va làm cho cả nhà A-bi-mê-léc đều son sẻ."));
visuperobject.biblemap.put("b1_21_1", new visuperobject(1,21,1,"Ðức Giê-hô-va đến viếng Sa-ra, theo như lời Ngài đã phán, và làm cho nàng như lời Ngài đã nói."));
visuperobject.biblemap.put("b1_21_2", new visuperobject(1,21,2,"Sa-ra thọ thai, sanh một con trai cho Áp-ra-ham trong khi tuổi đã già, đúng kỳ Ðức Chúa Trời đã định."));
visuperobject.biblemap.put("b1_21_3", new visuperobject(1,21,3,"Áp-ra-ham đặt tên đứa trai mà Sa-ra đã sanh cho mình là Y-sác."));
visuperobject.biblemap.put("b1_21_4", new visuperobject(1,21,4,"Ðúng tám ngày, Áp-ra-ham làm phép cắt bì cho Y-sác theo như lời Ðức Chúa Trời đã phán dặn."));
visuperobject.biblemap.put("b1_21_5", new visuperobject(1,21,5,"Vả, khi Y-sác ra đời, thì Áp-ra-ham đã được một trăm tuổi."));
visuperobject.biblemap.put("b1_21_6", new visuperobject(1,21,6,"Sa-ra nói rằng: Ðức Chúa Trời làm cho tôi một việc vui cười; hết thảy ai hay được cũng sẽ vui cười về sự của tôi."));
visuperobject.biblemap.put("b1_21_7", new visuperobject(1,21,7,"Lại nói rằng: Há ai dám nói với Áp-ra-ham rằng Sa-ra sẽ cho con bú ư? vì tôi đã sanh một đứa trai trong lúc người già yếu rồi."));
visuperobject.biblemap.put("b1_21_8", new visuperobject(1,21,8,"Ðứa trẻ lớn lên, thì thôi bú. Chánh ngày Y-sác thôi bú, Áp-ra-ham bày một tiệc lớn ăn mừng."));
visuperobject.biblemap.put("b1_21_9", new visuperobject(1,21,9,"Sa-ra thấy đứa trai của A-ga, người Ê-díp-tô, đã sanh cho Áp-ra-ham, cười cợt,"));
visuperobject.biblemap.put("b1_21_10", new visuperobject(1,21,10,"thì người nói với Áp-ra-ham rằng: Hãy đuổi con đòi với con nó đi đi, vì đứa trai của con đòi nầy sẽ chẳng được kế nghiệp cùng con trai tôi là Y-sác đâu."));
visuperobject.biblemap.put("b1_21_11", new visuperobject(1,21,11,"Lời nầy lấy làm buồn lòng Áp-ra-ham lắm, vì cớ con trai mình."));
visuperobject.biblemap.put("b1_21_12", new visuperobject(1,21,12,"Nhưng Ðức Chúa Trời phán cùng Áp-ra-ham rằng: Ngươi chớ buồn bực vì con trai và con đòi ngươi. Sa-ra nói thể nào, hãy nghe theo tiếng người nói; vì do nơi Y-sác sẽ sanh ra dòng dõi lưu danh ngươi."));
visuperobject.biblemap.put("b1_21_13", new visuperobject(1,21,13,"Ta cũng sẽ làm cho đứa trai của con đòi trở nên một dân, vì nó cũng do một nơi ngươi mà ra."));
visuperobject.biblemap.put("b1_21_14", new visuperobject(1,21,14,"Áp-ra-ham dậy sớm, lấy bánh và một bầu nước, đưa cho A-ga; để các món đó trên vai nàng, và giao đứa trai cho nàng, rồi đuổi đi. Nàng ra đi, đi dông dài trong đồng vắng Bê -e-Sê-ba."));
visuperobject.biblemap.put("b1_21_15", new visuperobject(1,21,15,"Khi nước trong bầu đã hết, nàng để đứa trẻ dưới một cội cây nhỏ kia,"));
visuperobject.biblemap.put("b1_21_16", new visuperobject(1,21,16,"đi ngồi đối diện cách xa xa dài chừng một khoảng tên bắn; vì nói rằng: Ôi! tôi nỡ nào thấy đứa trẻ phải chết! Nàng ngồi đối diện đó, cất tiếng la khóc."));
visuperobject.biblemap.put("b1_21_17", new visuperobject(1,21,17,"Ðức Chúa Trời nghe tiếng đứa trẻ khóc, thì thiên sứ của Ðức Chúa Trời từ trên trời kêu nàng A-ga mà phán rằng: Hỡi A-ga! Ngươi có điều gì vậy? Chớ sợ chi, vì Ðức Chúa Trời đã nghe tiếng đứa trẻ ở đâu đó rồi."));
visuperobject.biblemap.put("b1_21_18", new visuperobject(1,21,18,"Hãy đứng dậy đỡ lấy đứa trẻ và giơ tay nắm nó, vì ta sẽ làm cho nó nên một dân lớn."));
visuperobject.biblemap.put("b1_21_19", new visuperobject(1,21,19,"Ðoạn, Ðức Chúa Trời mở mắt nàng ra; nàng bèn thấy một cái giếng nước, và đi lại múc đầy bầu cho đứa trẻ uống."));
visuperobject.biblemap.put("b1_21_20", new visuperobject(1,21,20,"Ðức Chúa Trời vùa giúp đứa trẻ; nó lớn lên, ở trong đồng vắng, có tài bắn cung."));
visuperobject.biblemap.put("b1_21_21", new visuperobject(1,21,21,"Nó ở tại trong đồng vắng Pha-ran; mẹ cưới cho nó một người vợ quê ở xứ Ê-díp-tô."));
visuperobject.biblemap.put("b1_21_22", new visuperobject(1,21,22,"Về thuở đó, vua A-bi-mê-léc và Phi-côn, quan tổng binh mình, nói cùng Áp-ra-ham rằng: Ðức Chúa Trời vùa giúp ngươi trong mọi việc ngươi làm."));
visuperobject.biblemap.put("b1_21_23", new visuperobject(1,21,23,"Vậy bây giờ, hãy chỉ danh Ðức Chúa Trời mà thề rằng: Ngươi sẽ chẳng gạt ta, con ta cùng dòng giống ta. Nhưng ngươi sẽ đãi ta và xứ ngươi đương trú ngụ, một lòng tử tế như ta đã đãi ngươi vậy."));
visuperobject.biblemap.put("b1_21_24", new visuperobject(1,21,24,"Áp-ra-ham đáp rằng: Tôi xin thề."));
visuperobject.biblemap.put("b1_21_25", new visuperobject(1,21,25,"Áp-ra-ham phàn nàn cùng vua A-bi-mê-léc về vụ một giếng kia bị đầy tớ người chiếm đoạt."));
visuperobject.biblemap.put("b1_21_26", new visuperobject(1,21,26,"Vua A-bi-mê-léc bèn nói rằng: Ta chẳng hay ai đã làm nên nông nổi đó; chính ngươi chẳng cho ta hay trước; ngày nay ta mới rõ đây mà thôi."));
visuperobject.biblemap.put("b1_21_27", new visuperobject(1,21,27,"Ðoạn, Áp-ra-ham bắt chiên và bò, dâng cho vua A-bi-mê-léc; rồi hai người kết ước cùng nhau."));
visuperobject.biblemap.put("b1_21_28", new visuperobject(1,21,28,"Áp-ra-ham lựa để riêng ra bảy con chiên tơ trong bầy;"));
visuperobject.biblemap.put("b1_21_29", new visuperobject(1,21,29,"thì vua A-bi-mê-léc hỏi rằng: Làm chi để bảy con chiên tơ đó riêng ra vậy?"));
visuperobject.biblemap.put("b1_21_30", new visuperobject(1,21,30,"Ðáp rằng: Xin vua hãy nhận lấy bảy con chiên tơ nầy mà chánh tay tôi dâng cho, đặng làm chứng rằng tôi đã đào cái giếng nầy."));
visuperobject.biblemap.put("b1_21_31", new visuperobject(1,21,31,"Bởi cớ ấy, nên họ đặt tên chỗ nầy là Bê -e-Sê-ba; vì tại đó hai người đều đã thề nguyện cùng nhau."));
visuperobject.biblemap.put("b1_21_32", new visuperobject(1,21,32,"Vậy, hai người kết ước cùng nhau tại Bê -e-Sê-ba. Ðoạn vua A-bi-mê-léc cùng quan tổng binh Phi-côn đứng dậy, trở về xứ Phi-li-tin."));
visuperobject.biblemap.put("b1_21_33", new visuperobject(1,21,33,"Áp-ra-ham trồng một cây me tại Bê -e-Sê-ba, và ở đó người cầu khẩn danh Ðức Giê-hô-va, là Ðức Chúa Trời hằng-hữu."));
visuperobject.biblemap.put("b1_21_34", new visuperobject(1,21,34,"Áp-ra-ham trú ngụ lâu ngày tại xứ Phi-li-tin."));
visuperobject.biblemap.put("b1_22_1", new visuperobject(1,22,1,"Khi mọi việc kia đã xong, thì Ðức Chúa Trời thử Áp-ra-ham; Ngài phán rằng: Hỡi Áp-ra-ham! Người thưa rằng: Có tôi đây."));
visuperobject.biblemap.put("b1_22_2", new visuperobject(1,22,2,"Ðức Chúa Trời phán rằng: Hãy bắt đứa con một ngươi yêu dấu, là Y-sác, và đi đến xứ Mô-ri-a, nơi đó dâng đứa con làm của lễ thiêu ở trên một hòn núi kia mà ta sẽ chỉ cho."));
visuperobject.biblemap.put("b1_22_3", new visuperobject(1,22,3,"Áp-ra-ham dậy sớm, thắng lừa, đem hai đầy tớ và con mình, là Y-sác, cùng đi; người cũng chặt củi để dùng về của lễ thiêu, rồi đi đến nơi mà Ðức Chúa Trời đã truyền dạy."));
visuperobject.biblemap.put("b1_22_4", new visuperobject(1,22,4,"Qua đến ngày thứ ba, Áp-ra-ham nhướng mắt lên thấy nơi đó ở lối đằng xa,"));
visuperobject.biblemap.put("b1_22_5", new visuperobject(1,22,5,"thì nói cùng hai kẻ đầy tớ rằng: Hãy ở lại đây với con lừa; ta cùng đứa trẻ sẽ đi đến chốn kia đặng thờ phượng, rồi sẽ trở lại với hai ngươi."));
visuperobject.biblemap.put("b1_22_6", new visuperobject(1,22,6,"Áp-ra-ham lấy củi về của lễ thiêu, chất trên Y-sác, con mình; rồi người cầm lửa và dao trong tay, và cả hai cha con đồng đi."));
visuperobject.biblemap.put("b1_22_7", new visuperobject(1,22,7,"Y-sác bèn nói cùng Áp-ra-ham, cha mình rằng: Hỡi Cha! Người đáp: Con ơi! cha đây. Y-sác nói: Củi đây, lửa đây, nhưng chiên con đây có đặng làm của lễ thiêu?"));
visuperobject.biblemap.put("b1_22_8", new visuperobject(1,22,8,"Áp-ra-ham rằng: Con ơi! chính Ðức Chúa Trời sẽ sắm sẵn lấy chiên con đặng dùng làm của lễ thiêu; rồi cả hai cha con cứ đồng đi."));
visuperobject.biblemap.put("b1_22_9", new visuperobject(1,22,9,"Họ đến chốn Ðức Chúa Trời đã phán dạy; tại đó, Áp-ra-ham lập bàn thờ, chất củi lên, trói Y-sác con mình lại, để lên đống củi trên bàn thờ."));
visuperobject.biblemap.put("b1_22_10", new visuperobject(1,22,10,"Áp-ra-ham bèn giơ tay ra cầm lấy dao đặng giết con mình."));
visuperobject.biblemap.put("b1_22_11", new visuperobject(1,22,11,"Thiên sứ của Ðức Giê-hô-va từ trên trời kêu xuống mà rằng: Hỡi Áp-ra-ham, Áp-ra-ham! Người thưa rằng: Có tôi đây."));
visuperobject.biblemap.put("b1_22_12", new visuperobject(1,22,12,"Thiên sứ phán rằng: Ðừng tra tay vào mình con trẻ và chớ làm chi hại đến nó; vì bây giờ ta biết rằng ngươi thật kính sợ Ðức Chúa Trời, bởi cớ không tiếc với ta con ngươi, tức con một ngươi."));
visuperobject.biblemap.put("b1_22_13", new visuperobject(1,22,13,"Áp-ra-ham nhướng mắt lên, xem thấy sau lưng một con chiên đực, sừng mắc trong bụi cây, bèn bắt con chiên đực đó dâng làm của lễ thiêu thay cho con mình."));
visuperobject.biblemap.put("b1_22_14", new visuperobject(1,22,14,"Áp-ra-ham gọi chỗ đó là Giê-hô-va Di-rê. Bởi cớ ấy, ngày nay có tục ngữ rằng: Trên núi của Ðức Giê-hô-va sẽ có sắm sẵn."));
visuperobject.biblemap.put("b1_22_15", new visuperobject(1,22,15,"Thiên sứ của Ðức Giê-hô-va từ trên trời kêu Áp-ra-ham lần thứ nhì mà rằng:"));
visuperobject.biblemap.put("b1_22_16", new visuperobject(1,22,16,"Ðức Giê-hô-va phán rằng: Vì ngươi đã làm điều đó, không tiếc con ngươi, tức con một ngươi, thì ta lấy chánh mình ta mà thề rằng:"));
visuperobject.biblemap.put("b1_22_17", new visuperobject(1,22,17,"sẽ ban phước cho ngươi, thêm dòng dõi ngươi nhiều như sao trên trời, đông như cát bờ biển, và dòng dõi đó sẽ chiếm được cửa thành quân nghịch."));
visuperobject.biblemap.put("b1_22_18", new visuperobject(1,22,18,"Bởi vì ngươi đã vâng theo lời dặn ta, nên các dân thế gian đều sẽ nhờ dòng dõi ngươi mà được phước."));
visuperobject.biblemap.put("b1_22_19", new visuperobject(1,22,19,"Ðoạn Áp-ra-ham trở về nơi hai người đầy tớ; họ đứng dậy, đồng nhau đi về Bê -e-Sê-ba. Áp-ra-ham cứ ở tại Bê -e-Sê-ba."));
visuperobject.biblemap.put("b1_22_20", new visuperobject(1,22,20,"Sau các việc đó, người ta thuật lại với Áp-ra-ham tin nầy rằng: Nầy, nàng Minh-ca cũng sanh con cho em ngươi, là Na-cô."));
visuperobject.biblemap.put("b1_22_21", new visuperobject(1,22,21,"Con trưởng nam là Uùt-xơ, em trai là Bu-xơ, Kê-mu-ên, cha của A-ram;"));
visuperobject.biblemap.put("b1_22_22", new visuperobject(1,22,22,"Kê-sết, Ha-xô, Phin-đát, Díp-láp và Bê-tu-ên;"));
visuperobject.biblemap.put("b1_22_23", new visuperobject(1,22,23,"Bê-tu-ên là người sanh Rê-be-ca. Minh-ca sanh tám người con trai đó cho Na-cô, em của Áp-ra-ham."));
visuperobject.biblemap.put("b1_22_24", new visuperobject(1,22,24,"Còn người vợ nhỏ, tên là Rê -u-ma, cũng sanh con, là Tê-la, Ga-ham, Ta-hách và Ma-a-ca."));
visuperobject.biblemap.put("b1_23_1", new visuperobject(1,23,1,"Sa-ra hưởng thọ được một trăm hai mươi bảy tuổi. Ấy là bao nhiêu năm của đời Sa-ra."));
visuperobject.biblemap.put("b1_23_2", new visuperobject(1,23,2,"Sa-ra qua đời tại Ki-ri-át-a-ra-ba, tức là Hếp-rôn, thuộc về xứ Ca-na-an. Áp-ra-ham đến chịu tang cho Sa-ra và than khóc người."));
visuperobject.biblemap.put("b1_23_3", new visuperobject(1,23,3,"Ðoạn, Áp-ra-ham đứng dậy trước người chết mình và nói cùng dân họ Hếch rằng:"));
visuperobject.biblemap.put("b1_23_4", new visuperobject(1,23,4,"Ta là một khách kiều ngụ trong vòng các ngươi; xin hãy cho một nơi mộ địa trong xứ các ngươi, để chôn người thác của ta."));
visuperobject.biblemap.put("b1_23_5", new visuperobject(1,23,5,"Dân họ Hếch đáp rằng:"));
visuperobject.biblemap.put("b1_23_6", new visuperobject(1,23,6,"Lạy Chúa, xin hãy nghe chúng tôi: giữa chúng tôi, chúa tức là một quân trưởng của Ðức Chúa Trời; hãy chôn người chết của chúa nơi mộ địa nào tốt hơn hết của chúng tôi. Trong bọn chúng tôi chẳng có ai tiếc mộ địa mình, đặng chôn người chết của chúa đâu."));
visuperobject.biblemap.put("b1_23_7", new visuperobject(1,23,7,"Áp-ra-ham bèn đứng dậy, sấp mình xuống trước mặt các dân của xứ, tức dân họ Hếch,"));
visuperobject.biblemap.put("b1_23_8", new visuperobject(1,23,8,"mà nói rằng: Nếu các ngươi bằng lòng cho chôn người chết ta, thì hãy nghe lời, và cầu xin Ép-rôn, con của Xô-ha giùm ta,"));
visuperobject.biblemap.put("b1_23_9", new visuperobject(1,23,9,"đặng người nhượng cho ta hang đá Mặc-bê-la, ở về tận đầu đồng người, để lại cho đúng giá, hầu cho ta được trong vòng các ngươi một nơi mộ địa."));
visuperobject.biblemap.put("b1_23_10", new visuperobject(1,23,10,"Vả, Ép-rôn, người Hê-tít, đương ngồi trong bọn dân họ Hếch, đáp lại cùng Áp-ra-ham trước mặt dân họ Hếch vẫn nghe và trước mặt mọi người đến nơi cửa thành, mà rằng:"));
visuperobject.biblemap.put("b1_23_11", new visuperobject(1,23,11,"Không, thưa chúa, hãy nghe lời tôi: Hiện trước mặt dân tôi, tôi xin dâng cho chúa cánh đồng, và cũng dâng luôn cái hang đá ở trong đó nữa; hãy chôn người chết của chúa đi."));
visuperobject.biblemap.put("b1_23_12", new visuperobject(1,23,12,"Áp-ra-ham sấp mình xuống trước mặt dân của xứ,"));
visuperobject.biblemap.put("b1_23_13", new visuperobject(1,23,13,"và nói lại cùng Ép-rôn hiện trước mặt dân của xứ đương nghe, mà rằng: Xin hãy nghe, ta trả giá tiền cái đồng, hãy nhận lấy đi, thì ta mới chôn người chết ta,"));
visuperobject.biblemap.put("b1_23_14", new visuperobject(1,23,14,"Ép-rôn đáp rằng:"));
visuperobject.biblemap.put("b1_23_15", new visuperobject(1,23,15,"Thưa Chúa, hãy nghe lời tôi: một miếng đất giá đáng bốn trăm siếc-lơ bạc, mà tôi cùng chúa, thì có giá chi đâu? Xin hãy chôn người chết của chúa đi."));
visuperobject.biblemap.put("b1_23_16", new visuperobject(1,23,16,"Áp-ra-ham nghe theo lời Ép-rôn, trước mặt dân họ Hếch cân bốn trăm siếc-lơ bạc cho người, là bạc thông dụng nơi các tay buôn bán."));
visuperobject.biblemap.put("b1_23_17", new visuperobject(1,23,17,"Vậy, cái đồng của Ép-rôn, tại Mặc-bê-la, nằm ngang Mam-rê, nghĩa là cái đồng ruộng hang đá, các cây cối ở trong và chung quanh theo giới hạn đồng,"));
visuperobject.biblemap.put("b1_23_18", new visuperobject(1,23,18,"đều trước mặt có các dân họ Hếch cùng mọi người đến cửa thành, nhận chắc cho Áp-ra-ham làm sản nghiệp."));
visuperobject.biblemap.put("b1_23_19", new visuperobject(1,23,19,"Sau các việc đó, Áp-ra-ham chôn Sa-ra, vợ mình, trong hang đá của đồng Mặc-bê-la, nằm ngang Nam-rê tại Hếp-rôn, thuộc về xứ Ca-na-an."));
visuperobject.biblemap.put("b1_23_20", new visuperobject(1,23,20,"Ðồng và hang đá đều có các người họ Hếch nhận chắc, để lại cho Áp-ra-ham dùng làm mộ địa."));
visuperobject.biblemap.put("b1_24_1", new visuperobject(1,24,1,"Vả, Áp-ra-ham đã già, tuổi đã cao; trong mọi việc Ðức Giê-hô-va đã ban phước cho người."));
visuperobject.biblemap.put("b1_24_2", new visuperobject(1,24,2,"Áp-ra-ham nói cũng người đầy tớ ở lâu hơn hết trong nhà mình, có quyền quản trị các gia tài, mà rằng: Xin hãy đặt tay lên đùi ta,"));
visuperobject.biblemap.put("b1_24_3", new visuperobject(1,24,3,"và ta sẽ biểu ngươi chỉ Ðức Giê-hô-va, là Ðức Chúa Trời của trời và đất, mà thề rằng: ngươi không cưới một cô nào trong bọn con gái của dân Ca-na-an, tức giữa dân ấy ta trú ngụ, làm vợ cho con trai ta."));
visuperobject.biblemap.put("b1_24_4", new visuperobject(1,24,4,"Nhưng hãy đi về quê hương ta, trong vòng bà con ta, cưới một người vợ cho Y-sác, con trai ta."));
visuperobject.biblemap.put("b1_24_5", new visuperobject(1,24,5,"Người đầy tớ thưa rằng: Có lẽ người con gái không khứng theo tôi về xứ nầy; vậy, có phải tôi dẫn con trai ông đến xứ mà ông đã ra đi khỏi chăng?"));
visuperobject.biblemap.put("b1_24_6", new visuperobject(1,24,6,"Áp-ra-ham nói rằng: Hãy cẩn thận, đừng dẫn con ta về xứ đó!"));
visuperobject.biblemap.put("b1_24_7", new visuperobject(1,24,7,"Giê-hô-va Ðức Chúa Trời trên trời, là Ðấng đã đem ta ra khỏi nhà cha ta và xứ sở ta, có phán và thề rằng: Ta sẽ cho dòng dõi ngươi xứ nầy! Chánh Ngài sẽ sai thiên sứ đi trước ngươi, và nơi đó ngươi hãy cưới một vợ cho con trai ta."));
visuperobject.biblemap.put("b1_24_8", new visuperobject(1,24,8,"Nếu người con gái không khứng theo, thì ngươi sẽ khỏi mắc lời của ta biểu ngươi thề; mà dẫu thế nào đi nữa, chớ đem con ta về chốn đó."));
visuperobject.biblemap.put("b1_24_9", new visuperobject(1,24,9,"Ðoạn, người đầy tớ để tay lên đùi Áp-ra-ham chủ mình, và thề sẽ làm theo như lời người đã dặn biểu."));
visuperobject.biblemap.put("b1_24_10", new visuperobject(1,24,10,"Ðoạn, người đầy tớ bắt mười con lạc đà trong bầy của chủ mình, đem theo đủ các tài vật của chủ đã giao, rồi đứng dậy đi qua xứ Mê-sô-bô-ta-mi, đến thành của Na-cô."));
visuperobject.biblemap.put("b1_24_11", new visuperobject(1,24,11,"Người cho lạc đà nằm quì gối xuống nghỉ ở ngoài thành, gần bên một giếng kia, vào buổi chiều, đến giờ của những con gái ra đi xách nước."));
visuperobject.biblemap.put("b1_24_12", new visuperobject(1,24,12,"Người nói rằng: Hỡi Giê-hô-va, Ðức Chúa Trời của chủ Áp-ra-ham tôi ơi! xin ngày nay cho tôi gặp điều mà tôi tìm kiếm, và hãy làm ơn cho chủ Áp-ra-ham tôi!"));
visuperobject.biblemap.put("b1_24_13", new visuperobject(1,24,13,"Nầy, tôi đứng gần bên giếng nầy, các con gái của dân trong thành sẽ ra đi xách nước,"));
visuperobject.biblemap.put("b1_24_14", new visuperobject(1,24,14,"xin cho người gái trẻ nào mà tôi nói như vầy: Xin nàng hãy nghiêng bình, cho tôi uống nhờ hớp nước, mà nàng trả lời rằng: Hãy uống đi, rồi tôi sẽ cho các lạc đà ngươi uống nữa, là chánh người mà Chúa đã định cho Y-sác, kẻ tôi tớ người; và nhờ đó tôi sẽ biết rằng Chúa đã làm ơn cho chủ tôi vậy."));
visuperobject.biblemap.put("b1_24_15", new visuperobject(1,24,15,"Người đầy tớ chưa dứt lời, nầy, nàng Rê-be-ca vác bình trên vai đi ra. Nàng là con gái của Bê-tu-ên, Bê-tu-ên là con trai của Minh-ca, vợ Na-cô, em trai của Áp-ra-ham."));
visuperobject.biblemap.put("b1_24_16", new visuperobject(1,24,16,"Người gái trẻ đó thật rất đẹp, còn đồng trinh, chưa gả cho ai. Nàng xuống giếng nhận đầy bình nước, rồi trở lên."));
visuperobject.biblemap.put("b1_24_17", new visuperobject(1,24,17,"Ðầy tớ bèn chạy lại trước mặt nàng và nói rằng: Xin hãy cho tôi uống một hớp nước trong bình."));
visuperobject.biblemap.put("b1_24_18", new visuperobject(1,24,18,"Nàng đáp rằng: Thưa chúa, hãy uống đi; rồi nàng lật đật đỡ bình xuống tay và cho người uống."));
visuperobject.biblemap.put("b1_24_19", new visuperobject(1,24,19,"Khi đỡ cho người uống xong rồi, nàng lại tiếp rằng: Tôi cũng sẽ xách cho mấy con lạc đà chúa uống nữa, chừng nào uống đã thì thôi."));
visuperobject.biblemap.put("b1_24_20", new visuperobject(1,24,20,"Nàng lật đật đổ nước bình ra máng, chạy xuống giếng xách nước thêm nữa cho hết thảy các con lạc đà uống."));
visuperobject.biblemap.put("b1_24_21", new visuperobject(1,24,21,"Còn người ngồi làm thinh mà nhìn nàng, để cho biết Ðức Giê-hô-va làm thành công việc mình đi đây hay chăng."));
visuperobject.biblemap.put("b1_24_22", new visuperobject(1,24,22,"Khi lạc đà vừa thôi uống, người đầy tớ bèn lấy một khoanh vàng nặng được nửa siếc-lơ và đôi xuyến nặng được mười siếc-lơ,"));
visuperobject.biblemap.put("b1_24_23", new visuperobject(1,24,23,"mà nói rằng: Nàng là con ai? xin nói cho tôi biết. Trong nhà cha nàng có chỗ nào cho tôi ở nghỉ ban đêm chăng?"));
visuperobject.biblemap.put("b1_24_24", new visuperobject(1,24,24,"Nàng đáp rằng: Tôi là con gái của Bê-tu-ên, cháu nội của Minh-ca và Na-cô."));
visuperobject.biblemap.put("b1_24_25", new visuperobject(1,24,25,"Rồi lại nói: Nhà chúng tôi có nhiều rơm và cỏ, và cũng có chỗ để cho ở nghỉ nữa."));
visuperobject.biblemap.put("b1_24_26", new visuperobject(1,24,26,"Người bèn cúi đầu và sấp mình xuống trước mặt Ðức Giê-hô-va,"));
visuperobject.biblemap.put("b1_24_27", new visuperobject(1,24,27,"mà nói rằng: Ðáng ngợi khen thay Giê-hô-va, là Ðức Chúa Trời của chủ Áp-ra-ham tôi! Ngài chẳng ngớt thương xót và thành thực đối cùng chủ tôi! Khi đương đi, Ðức Giê-hô-va dẫn dắt tôi đến nhà anh em của chủ tôi vậy."));
visuperobject.biblemap.put("b1_24_28", new visuperobject(1,24,28,"Người gái trẻ đó chạy về, thuật chuyện nầy lại cho nội nhà mẹ mình."));
visuperobject.biblemap.put("b1_24_29", new visuperobject(1,24,29,"Vả, nàng Rê-be-ca có một anh, tên là La-ban. La-ban chạy đến người đầy tớ đó ở gần bên giếng."));
visuperobject.biblemap.put("b1_24_30", new visuperobject(1,24,30,"Vừa thấy khoanh vàng và đôi xuyến nơi tay em gái mình, và đã nghe chuyện Rê-be-ca thuật lại rằng: Người nầy nói vậy! thì đi đến người, đương đứng gần bên mấy con lạc đà, ở nơi giếng nước,"));
visuperobject.biblemap.put("b1_24_31", new visuperobject(1,24,31,"mà thưa rằng: Hởi người đã được Ðức Giê-hô-va ban phước! Xin mời vào. Sao đứng ngoài nầy vậy? Tôi đã sửa soạn nhà và dọn-dẹp một nơi cho mấy con lạc đà rồi."));
visuperobject.biblemap.put("b1_24_32", new visuperobject(1,24,32,"Người đầy tớ bèn vào nhà; La-ban cổi yên cho lạc đà, đem cỏ cùng rơm cho các thú đó; rồi lấy nước đặng rửa chơn cho người đầy tớ và mấy kẻ đi theo."));
visuperobject.biblemap.put("b1_24_33", new visuperobject(1,24,33,"Ðoạn, người nhà bày bữa và mời ăn; nhưng người đầy tớ nói: Không, tôi không ăn đâu, nếu chưa nói được những điều tôi cần phải nói. La-ban đáp rằng: Hãy nói đi!"));
visuperobject.biblemap.put("b1_24_34", new visuperobject(1,24,34,"Người bèn nói rằng: Tôi là đầy tớ của Áp-ra-ham."));
visuperobject.biblemap.put("b1_24_35", new visuperobject(1,24,35,"Ðức Giê-hô-va đã ban phước cho chủ tôi bội phần, trở nên thạnh vượng; Ngài cho chủ tôi chiên, bò, bạc, vàng, tôi trai tớ gái, lạc đà và lừa."));
visuperobject.biblemap.put("b1_24_36", new visuperobject(1,24,36,"Vả, Sa-ra, vợ chủ tôi, trở về già, có sanh một con trai cho người; người đã giao gia tài mình lại hết cho con trai đó."));
visuperobject.biblemap.put("b1_24_37", new visuperobject(1,24,37,"Chủ tôi lại có bắt tôi thề rằng: Ngươi chớ cưới cho con trai ta một người vợ nào trong bọn con gái của dân Ca-na-an, là xứ ta đương trú ngụ;"));
visuperobject.biblemap.put("b1_24_38", new visuperobject(1,24,38,"nhưng hãy về nhà cha ta, trong vòng bà con ta, mà cưới cho nó một người vợ tại đó."));
visuperobject.biblemap.put("b1_24_39", new visuperobject(1,24,39,"Tôi nói cùng chủ tôi rằng: Có lẽ người con gái không khứng theo tôi về."));
visuperobject.biblemap.put("b1_24_40", new visuperobject(1,24,40,"Chủ đáp rằng: Ðức Giê-hô-va là Ðấng ta phục sự sẽ sai thiên sứ theo ngươi, làm cho thành công việc ngươi đi; và ngươi sẽ cưới cho con trai ta một người vợ trong vòng bà con ta, tại nhà cha ta."));
visuperobject.biblemap.put("b1_24_41", new visuperobject(1,24,41,"Vậy, nếu khi ngươi đi về bà con ta, mà họ không khứng gả cho, thì ngươi sẽ được gỡ lời thề nặng của ngươi đã thề cùng ta đó."));
visuperobject.biblemap.put("b1_24_42", new visuperobject(1,24,42,"Ngày nay, khi đi đến giếng kia, tôi có cầu rằng: Lạy Ðức Giê-hô-va, Ðức Chúa Trời của chủ Áp-ra-ham tôi! Xin đoái đến mà giúp công việc của tôi đi đây cho được thành!"));
visuperobject.biblemap.put("b1_24_43", new visuperobject(1,24,43,"Nầy, tôi ngồi gần bên giếng nầy: cầu xin hễ người gái trẻ nào sẽ ra xách nước mà tôi nói cùng nàng rằng: Xin cho tôi uống một hớp nước trong bình;"));
visuperobject.biblemap.put("b1_24_44", new visuperobject(1,24,44,"mà sẽ trả lời rằng: Hãy uống đi, trước tôi sẽ xách cho các lạc đà ngươi uống nữa, xin cho người gái trẻ đó là vợ mà Ðức Giê-hô-va đã định cho con trai của chủ tôi!"));
visuperobject.biblemap.put("b1_24_45", new visuperobject(1,24,45,"Chưa dứt lời thầm nguyện, bỗng đâu nàng Rê-be-ca vác bình trên vai đi ra, xuống giếng xách nước. Tôi nói với nàng rằng: Xin cho tôi uống hớp nước."));
visuperobject.biblemap.put("b1_24_46", new visuperobject(1,24,46,"Nàng lật đật hạ bình trên vai xuống mà nói rằng: Hãy uống đi, rồi tôi sẽ cho các lạc đà ngươi uống nữa; vậy tôi có uống và nàng cũng có cho các lạc đà uống nữa."));
visuperobject.biblemap.put("b1_24_47", new visuperobject(1,24,47,"Tôi bèn hỏi nàng rằng: Nàng là con gái ai? Nàng đáp: Tôi là con gái của Bê-tu-ên, cháu nội của Na-cô và Minh-ca. Tôi liền đeo cái khoanh nơi lỗ mũi và đôi xuyến vào tay nàng."));
visuperobject.biblemap.put("b1_24_48", new visuperobject(1,24,48,"Ðoạn, tôi cúi đầu và sấp mình xuống trước mặt Ðức Giê-hô-va, ngợi khen Giê-hô-va Ðức Chúa Trời của chủ Áp-ra-ham tôi, mà đã dẫn tôi vào đường chánh đáng, để chọn được cho con trai người con gái của em chủ tôi."));
visuperobject.biblemap.put("b1_24_49", new visuperobject(1,24,49,"Vậy, bây giờ, nếu các ông muốn lấy lòng tử tế và trung tín đối cùng chủ tôi, xin hãy tỏ điều đó cho, dầu không, xin cũng nói cho biết, hầu cho tôi sẽ xây về bên hữu hay là về bên tả."));
visuperobject.biblemap.put("b1_24_50", new visuperobject(1,24,50,"La-ban và Bê-tu-ên đáp rằng: Ðiều đó do nơi Ðức Giê-hô-va mà ra, chúng tôi đâu nói được cùng ngươi rằng quấy hay rằng phải."));
visuperobject.biblemap.put("b1_24_51", new visuperobject(1,24,51,"Kìa, Rê-be-ca đương ở trước mặt ngươi, hãy dẫn nó đi, để nó được làm vợ của con trai chủ ngươi, theo như lời Ðức Giê-hô-va phán định."));
visuperobject.biblemap.put("b1_24_52", new visuperobject(1,24,52,"Ðầy tớ của Áp-ra-ham vừa nghe lời hai người nói xong, liền sấp mình xuống đất trước mặt Ðức Giê-hô-va."));
visuperobject.biblemap.put("b1_24_53", new visuperobject(1,24,53,"Người lấy đồ trang sức bằng bạc, vàng, và áo xống trao cho Rê-be-ca; rồi cùng lấy các vật quí dâng cho anh và mẹ nàng."));
visuperobject.biblemap.put("b1_24_54", new visuperobject(1,24,54,"Ðoạn người đầy tớ cùng các người đi theo ngồi ăn uống; rồi ngủ tại đó. Qua ngày sau, khi họ đã thức dậy, người đầy tớ nói rằng: Xin hãy cho tôi trở về chủ."));
visuperobject.biblemap.put("b1_24_55", new visuperobject(1,24,55,"Nhưng anh và mẹ nàng rằng: Hãy cho người gái trẻ ở lại cùng chúng tôi ít ngày nữa, độ chừng mười bữa, rồi nó sẽ đi."));
visuperobject.biblemap.put("b1_24_56", new visuperobject(1,24,56,"Ðầy tớ đáp rằng: Xin chớ cầm tôi trễ lại, vì Ðức Giê-hô-va có làm việc đi đường tôi được thành. Hãy cho tôi đi, trở về nhà chủ tôi."));
visuperobject.biblemap.put("b1_24_57", new visuperobject(1,24,57,"Họ đáp rằng: Chúng ta hãy gọi người gái trẻ đó và hỏi ý nó ra sao;"));
visuperobject.biblemap.put("b1_24_58", new visuperobject(1,24,58,"bèn kêu Rê-be-ca và nói rằng: Con muốn đi cùng người nầy chăng? Nàng rằng: Tôi muốn đi."));
visuperobject.biblemap.put("b1_24_59", new visuperobject(1,24,59,"Ðoạn, họ cho Rê-be-ca, em gái mình, và người vú của nàng, đi về theo người đầy tớ của Áp-ra-ham và các kẻ đi theo."));
visuperobject.biblemap.put("b1_24_60", new visuperobject(1,24,60,"Họ chúc phước cho Rê-be-ca mà rằng: Hỡi em gái chúng ta! Chúc cho em được trở nên mẹ của ức triệu người, và cho dòng dõi em được chiếm lấy cửa thành của quân nghịch."));
visuperobject.biblemap.put("b1_24_61", new visuperobject(1,24,61,"Rê-be-ca và các đòi nàng đứng dậy, lên cỡi lạc đà, đi theo người đầy tớ đó. Vậy, người đầy tớ dẫn Rê-be-ca đi."));
visuperobject.biblemap.put("b1_24_62", new visuperobject(1,24,62,"Bấy giờ Y-sác ở tại nam phương từ giếng La-chai-Roi trở về;"));
visuperobject.biblemap.put("b1_24_63", new visuperobject(1,24,63,"lối chiều, người đi ra ngoài đồng đặng suy ngẫm; nhướng mắt lên, kìa ngó thấy lạc đà đâu đi đến."));
visuperobject.biblemap.put("b1_24_64", new visuperobject(1,24,64,"Nàng Rê-be-ca cũng nhướng mắt lên, bèn chợt thấy Y-sác; liền nhảy xuống lạc đà,"));
visuperobject.biblemap.put("b1_24_65", new visuperobject(1,24,65,"và hỏi đầy tớ rằng: Người ở đằng trước mặt tôi đi lại trong đồng đó là ai: Ðầy tớ thưa rằng: Ấy là chủ tôi đó. Nàng bèn lấy lúp che mặt lại."));
visuperobject.biblemap.put("b1_24_66", new visuperobject(1,24,66,"Rồi, đầy tớ thuật cho Y-sác nghe hết các việc mà mình đã làm;"));
visuperobject.biblemap.put("b1_24_67", new visuperobject(1,24,67,"Y-sác bèn dẫn Rê-be-ca vào trại của Sa-ra, mẹ mình, cưới làm vợ, và yêu mến nàng. Vậy, Y-sác được giải phiền sau khi mẹ mình qua đời."));
visuperobject.biblemap.put("b1_25_1", new visuperobject(1,25,1,"Áp-ra-ham cưới một người vợ khác, tên là Kê-tu-ra."));
visuperobject.biblemap.put("b1_25_2", new visuperobject(1,25,2,"Người sanh cho Áp-ra-ham Xim-ram, Giốc-chan, Mê-đan, Ma-đi-an, Dích-bác và Su-ách."));
visuperobject.biblemap.put("b1_25_3", new visuperobject(1,25,3,"Giốc-chan sanh Sê-ba và Ðê-đan; con cháu của Ðê-đan là họ A-chu-rim, họ Lê-tu-chim và họ Lê-um-mim."));
visuperobject.biblemap.put("b1_25_4", new visuperobject(1,25,4,"Con trai của Ma-đi-an là Ê-pha, Ê-phe, Ha-nóc, A-bi-đa và Eân-đa. Các người trên đây đều là dòng dõi của Kê-tu-ra."));
visuperobject.biblemap.put("b1_25_5", new visuperobject(1,25,5,"Áp-ra-ham cho Y-sác hết thảy gia tài mình; còn cho các con dòng thứ những tiền của;"));
visuperobject.biblemap.put("b1_25_6", new visuperobject(1,25,6,"rồi, khi còn sống, người sai họ qua đông phương mà ở, cách xa con dòng chánh, là Y-sác."));
visuperobject.biblemap.put("b1_25_7", new visuperobject(1,25,7,"Áp-ra-ham hưởng thọ được một trăm bảy mươi lăm tuổi;"));
visuperobject.biblemap.put("b1_25_8", new visuperobject(1,25,8,"người tuổi cao tác lớn và đã thỏa về đời mình, tắt hơi, được qui về nơi tổ tông."));
visuperobject.biblemap.put("b1_25_9", new visuperobject(1,25,9,"Hai con trai người, Y-sác và Ích-ma-ên, chôn người trong hang đá Mặc-bê-la tại nơi đồng của Ép-rôn, con trai Sô-ha, người Hê-tít, nằm ngang Mam-rê."));
visuperobject.biblemap.put("b1_25_10", new visuperobject(1,25,10,"Ấy là cái đồng mà lúc trước Áp-ra-ham mua lại của dân họ Hếch; nơi đó họ chôn Áp-ra-ham cùng Sa-ra, vợ người."));
visuperobject.biblemap.put("b1_25_11", new visuperobject(1,25,11,"Sau khi Áp-ra-ham qua đời, Ðức Giê-hô-va ban phước cho Y-sác con trai người. Y-sác ở gần bên cái giếng La-chai-Roi."));
visuperobject.biblemap.put("b1_25_12", new visuperobject(1,25,12,"Ðây là dòng dõi của Ích-ma-ên, con trai của Áp-ra-ham, do nơi nàng A-ga, người Ê-díp-tô, con đòi của Sa-ra, đã sanh."));
visuperobject.biblemap.put("b1_25_13", new visuperobject(1,25,13,"Và đây là trên các con trai của Ích-ma-ên, sắp thứ tự theo ngày ra đời: Con trưởng nam của Ích-ma-ên là Nê-ba-giốt; kế sau Kê-đa, Át-bê-ên, Mi-bô-sam,"));
visuperobject.biblemap.put("b1_25_14", new visuperobject(1,25,14,"Mích-ma, Ðu-ma, Ma-sa,"));
visuperobject.biblemap.put("b1_25_15", new visuperobject(1,25,15,"Ha-đa, Thê-ma, Giê-thu, Na-phích, và Kết-ma."));
visuperobject.biblemap.put("b1_25_16", new visuperobject(1,25,16,"Ðó là các con trai của Ích-ma-ên và tên của họ tùy theo làng và nơi đóng trại mà đặt. Ấy là mười hai vị công hầu trong dân tộc của họ."));
visuperobject.biblemap.put("b1_25_17", new visuperobject(1,25,17,"Ích-ma-ên hưởng thọ được một trăm ba mươi bảy tuổi, rồi tắt hơi mà qua đời, được qui về nơi tổ tông."));
visuperobject.biblemap.put("b1_25_18", new visuperobject(1,25,18,"Dòng dõi người ở trước mặt anh em mình, từ Ha-vi-la cho đến Xu-xơ, đối ngang Ê-díp-tô, chạy qua A-si-ri."));
visuperobject.biblemap.put("b1_25_19", new visuperobject(1,25,19,"Ðây là dòng dõi của Y-sác, con trai Áp-ra-ham. Áp-ra-ham sanh Y-sác."));
visuperobject.biblemap.put("b1_25_20", new visuperobject(1,25,20,"Vả, khi Y-sác được bốn mươi tuổi, thì cưới Rê-be-ca, con gái của Bê-tu-ên và em gái của La-ban, đều là dân A-ram, ở tại xứ Pha-đan-a-ram."));
visuperobject.biblemap.put("b1_25_21", new visuperobject(1,25,21,"Y-sác khẩn cầu Ðức Giê-hô-va cho vợ mình, vì nàng son sẻ. Ðức Giê-hô-va cảm động lời khẩn cầu đó, nên cho Rê-be-ca thọ thai."));
visuperobject.biblemap.put("b1_25_22", new visuperobject(1,25,22,"Nhưng vì thai đôi làm cho đụng nhau trong bụng, thì nàng nói rằng: Nếu quả thật vậy, cớ sao đều nầy xảy đến làm chi? Ðoạn nàng đi hỏi Ðức Giê-hô-va."));
visuperobject.biblemap.put("b1_25_23", new visuperobject(1,25,23,"Ðức Giê-hô-va phán rằng: Hai nước hiện ở trong bụng ngươi, và hai thứ dân sẽ do lòng ngươi mà ra; dân nầy mạnh hơn dân kia, và đứa lớn phải phục đứa nhỏ."));
visuperobject.biblemap.put("b1_25_24", new visuperobject(1,25,24,"Ðến ngày nàng phải sanh nở, nầy hai đứa sanh đôi trong bụng nàng ra."));
visuperobject.biblemap.put("b1_25_25", new visuperobject(1,25,25,"Ðứa ra trước đỏ hồng, lông cùng mình như một áo tơi lông; đặt tên là Ê-sau."));
visuperobject.biblemap.put("b1_25_26", new visuperobject(1,25,26,"Kế em nó lại ra sau, tay nắm lấy gót Ê-sau; nên đặt tên là Gia-cốp. Khi sanh hai đứa con nầy thì Y-sác đã được sáu mươi tuổi."));
visuperobject.biblemap.put("b1_25_27", new visuperobject(1,25,27,"Khi hai đứa trai nầy lớn lên, thì Ê-sau trở nên một thợ săn giỏi, thường giong ruổi nơi đồng ruộng; còn Gia-cốp là người hiền lành cứ ở lại trại."));
visuperobject.biblemap.put("b1_25_28", new visuperobject(1,25,28,"Y-sác yêu Ê-sau, vì người có tánh ưa ăn thịt rừng; nhưng Rê-be-ca lại yêu Gia-cốp."));
visuperobject.biblemap.put("b1_25_29", new visuperobject(1,25,29,"Một ngày kia, Gia-cốp đương nấu canh, Ê-sau ở ngoài đồng về lấy làm mệt mỏi lắm;"));
visuperobject.biblemap.put("b1_25_30", new visuperobject(1,25,30,"liền nói cùng Gia-cốp rằng: Em hãy cho anh ăn canh gì đỏ đó với, vì anh mệt mỏi lắm. Bởi cớ ấy, người ta gọi Ê-sau là Ê-đôm."));
visuperobject.biblemap.put("b1_25_31", new visuperobject(1,25,31,"Gia-cốp đáp rằng: Nay anh hãy bán quyền trưởng nam cho tôi đi."));
visuperobject.biblemap.put("b1_25_32", new visuperobject(1,25,32,"Ê-sau đáp rằng: Nầy, anh gần thác, quyền trưởng nam để cho anh dùng làm chi?"));
visuperobject.biblemap.put("b1_25_33", new visuperobject(1,25,33,"Gia-cốp đáp lại rằng: Anh hãy thề trước đi. Người bèn thề; vậy, người bán quyền trưởng nam cho Gia-cốp."));
visuperobject.biblemap.put("b1_25_34", new visuperobject(1,25,34,"Rồi, Gia-cốp cho Ê-sau ăn bánh và canh phạn đậu; ăn uống xong, người bèn đứng dậy đi. Vậy, Ê-sau khinh quyền trưởng nam là thế."));
visuperobject.biblemap.put("b1_26_1", new visuperobject(1,26,1,"Trừ cơn đói kém thứ nhứt trong đời Áp-ra-ham, bấy giờ tại xứ lại có một lần nữa. Y-sác bèn đi đến A-bi-mê-léc, vua Phi-li-tin, tại Ghê-ra."));
visuperobject.biblemap.put("b1_26_2", new visuperobject(1,26,2,"Ðức Giê-hô-va hiện đến cùng người và phán rằng: Chớ xuống xứ Ê-díp-tô; hãy ở lại xứ mà ta sẽ chỉ cho."));
visuperobject.biblemap.put("b1_26_3", new visuperobject(1,26,3,"Hãy ngụ trong xứ nầy, ta sẽ ở cùng ngươi và ban phước cho ngươi; vì ta sẽ cho ngươi cùng dòng dõi ngươi các xứ nầy và ta sẽ làm thành lời ta đã thề cùng Áp-ra-ham, cha ngươi."));
visuperobject.biblemap.put("b1_26_4", new visuperobject(1,26,4,"Ta sẽ thêm dòng dõi ngươi nhiều như sao trên trời, sẽ cho họ các xứ nầy; hết thảy dân thế gian đều sẽ nhờ dòng dõi ngươi mà được phước;"));
visuperobject.biblemap.put("b1_26_5", new visuperobject(1,26,5,"vì Áp-ra-ham đã vâng lời ta và đã giữ điều ta phán dạy, lịnh, luật và lệ của ta."));
visuperobject.biblemap.put("b1_26_6", new visuperobject(1,26,6,"Vậy, Y-sác ở tại Ghê-ra."));
visuperobject.biblemap.put("b1_26_7", new visuperobject(1,26,7,"Bởi nàng Rê-be-ca sắc-sảo dung nhan, nên khi dân xứ đó hỏi thăm vợ người, thì người đáp rằng: Ấy là em gái tôi, e khi nói: Ấy là vợ tôi, thì họ sẽ giết mình chăng."));
visuperobject.biblemap.put("b1_26_8", new visuperobject(1,26,8,"Vả, người ngụ tại xứ đó cũng đã lâu; một ngày kia A-bi-mê-léc, vua Phi-li-tin, ngó ngang qua cửa sổ, thấy Y-sác đương giỡn chơi cùng Rê-be-ca, vợ người,"));
visuperobject.biblemap.put("b1_26_9", new visuperobject(1,26,9,"bèn đòi Y-sác mà rằng: Quả thật là vợ ngươi đó; sao có nói: Ấy là em gái tôi? Y-sác đáp rằng: Vì tôi có thầm nói: Chớ khá vì nàng mà tôi phải bỏ mình."));
visuperobject.biblemap.put("b1_26_10", new visuperobject(1,26,10,"A-bi-mê-léc hỏi: Ngươi làm chi cho chúng ta vậy? Ngộ có một người dân đến nằm cùng vợ ngươi, ngươi làm cho chúng ta phải phạm tội!"));
visuperobject.biblemap.put("b1_26_11", new visuperobject(1,26,11,"A-bi-mê-léc bèn truyền lịnh cho cả dân chúng rằng: Kẻ nào động đến người nầy, hay là vợ người nầy, thì sẽ bị xử tử."));
visuperobject.biblemap.put("b1_26_12", new visuperobject(1,26,12,"Y-sác gieo hột giống trong xứ đó; năm ấy gặt được bội trăm phần; vì Ðức Giê-hô-va đã ban phước cho."));
visuperobject.biblemap.put("b1_26_13", new visuperobject(1,26,13,"Người nên thạnh vượng, của cải càng ngày càng thêm cho đến đỗi người trở nên rất lớn."));
visuperobject.biblemap.put("b1_26_14", new visuperobject(1,26,14,"Người có nhiều bầy chiên, bầy bò và tôi tớ rất đông; bởi cớ ấy, dân Phi-li-tin đem lòng ganh ghẻ."));
visuperobject.biblemap.put("b1_26_15", new visuperobject(1,26,15,"Mấy cái giếng lúc trước đầy tớ của Áp-ra-ham, cha người, đã đào, bây giờ đều bị dân Phi-li-tin lấp đất lại hết."));
visuperobject.biblemap.put("b1_26_16", new visuperobject(1,26,16,"A-bi-mê-léc nói cùng Y-sác rằng: Hãy ra khỏi nơi ta, vì ngươi thạnh vượng hơn chúng ta bội phần."));
visuperobject.biblemap.put("b1_26_17", new visuperobject(1,26,17,"Vậy, Y-sác bỏ chốn nầy đi đến đóng trại tại trũng Ghê-ra và ở đó."));
visuperobject.biblemap.put("b1_26_18", new visuperobject(1,26,18,"Y-sác bèn đào lại mấy cái giếng người ta đã đào trong đời Áp-ra-ham, cha mình, mà bị dân Phi-li-tin lấp đất lại khi Áp-ra-ham qua đời, và đặt tên mấy giếng đó như tên của cha mình đã đặt."));
visuperobject.biblemap.put("b1_26_19", new visuperobject(1,26,19,"Các đầy tớ của Y-sác còn đào thêm giếng nơi trũng, gặp được một giếng nước mạch."));
visuperobject.biblemap.put("b1_26_20", new visuperobject(1,26,20,"Nhưng bọn chăn chiên Ghê-ra tranh giành cùng bọn chăn chiên của Y-sác, mà rằng: Người đó của chúng ta; nên người đặt tên giếng nầy là Ê-sét. Vì bọn chăn chiên đó có tranh giành cùng mình."));
visuperobject.biblemap.put("b1_26_21", new visuperobject(1,26,21,"Kế ấy, các đầy tớ đào một giếng khác, thì bị tranh giành nhau nữa; người đặt tên giếng nầy là Sít-na."));
visuperobject.biblemap.put("b1_26_22", new visuperobject(1,26,22,"Ðoạn, người bỏ chỗ đó đi đào một giếng khác; về giếng nầy, họ không tranh giành nhau, nên người đặt tên là Rê-hô-bốt, mà rằng: Bây giờ Ðức Giê-hô-va đã để cho chúng ta được rộng rãi, và ta sẽ đặng thịnh vượng trong xứ."));
visuperobject.biblemap.put("b1_26_23", new visuperobject(1,26,23,"Y-sác ở đó đi, dời lên Bê -e-Sê-ba."));
visuperobject.biblemap.put("b1_26_24", new visuperobject(1,26,24,"Ðêm đó Ðức Giê-hô-va hiện đến cùng người và phán rằng: Ta là Ðức Chúa Trời của Áp-ra-ham, cha ngươi; chớ sợ chi, ta ở cùng ngươi, sẽ ban phước cho và thêm dòng dõi ngươi, vì cớ Áp-ra-ham là tôi tớ ta."));
visuperobject.biblemap.put("b1_26_25", new visuperobject(1,26,25,"Y-sác lập một bàn thờ, cầu khẩn danh Ðức Giê-hô-va và đóng trại tại đó. Ðoạn, các đầy tớ người đào một cái giếng tại nơi ấy."));
visuperobject.biblemap.put("b1_26_26", new visuperobject(1,26,26,"Vua A-bi-mê-léc ở Ghê-ra đi đến viếng Y-sác, có A-hu-sát, bạn mình, và Phi-côn, quan tổng binh mình, đi theo."));
visuperobject.biblemap.put("b1_26_27", new visuperobject(1,26,27,"Y-sác bèn hỏi rằng: Các người ghét tôi, đã đuổi tôi ra khỏi nơi mình, sao còn đi đến tôi mà chi?"));
visuperobject.biblemap.put("b1_26_28", new visuperobject(1,26,28,"Chúng đáp rằng: Chúng tôi đã thấy rõ ràng Ðức Giê-hô-va phù hộ người, nên nói với nhau rằng: Phải có một lời thề giữa chúng tôi và người, để chúng ta kết giao ước với người."));
visuperobject.biblemap.put("b1_26_29", new visuperobject(1,26,29,"Hãy thề rằng: Người chẳng bao giờ làm hại chúng tôi, như chúng tôi đã không động đến người, chỉ hậu đãi người và cho đi bình yên. Bây giờ người được Ðức Giê-hô-va ban phước cho."));
visuperobject.biblemap.put("b1_26_30", new visuperobject(1,26,30,"Y-sác bày một bữa tiệc, các người đồng ăn uống."));
visuperobject.biblemap.put("b1_26_31", new visuperobject(1,26,31,"Qua ngày sau, chúng đậy sớm, lập lời thề với nhau. Rồi, Y-sác đưa các người đó đi về bình yên."));
visuperobject.biblemap.put("b1_26_32", new visuperobject(1,26,32,"Cùng trong ngày đó, các đầy tớ của Y-sác đến đem tin cho người hay về giếng họ đã đào, rằng: Chúng tôi đã thấy có nước."));
visuperobject.biblemap.put("b1_26_33", new visuperobject(1,26,33,"Người bèn đặt tên cái giếng đó là Si-ba. Vì cớ đó nên tên thành ấy là Bê -e-Sê-ba cho đến ngày nay."));
visuperobject.biblemap.put("b1_26_34", new visuperobject(1,26,34,"Khi Ê-sau được bốn mươi tuổi, cưới Giu-đít, con gái của Bê -e-ri, người Hê-tít; và Bách-mát, con gái của Ê-lôn, cũng người Hê-tít."));
visuperobject.biblemap.put("b1_26_35", new visuperobject(1,26,35,"Hai dâu đó là một sự cay đắng lòng cho Y-sác và Rê-be-ca."));
visuperobject.biblemap.put("b1_27_1", new visuperobject(1,27,1,"Bởi Y-sác đã già, mắt làng chẳng thấy được nữa, bèn gọi Ê-sau, con trưởng nam, mà rằng: Hỡi con! Ê-sau thưa rằng: Có con đây."));
visuperobject.biblemap.put("b1_27_2", new visuperobject(1,27,2,"Người bèn nói rằng: Nầy, cha đã già rồi, chẳng biết ngày nào phải chết;"));
visuperobject.biblemap.put("b1_27_3", new visuperobject(1,27,3,"vậy bây giờ, cha xin con hãy lấy khí giới, ống tên và cung con, ra đồng săn thịt rừng cho cha,"));
visuperobject.biblemap.put("b1_27_4", new visuperobject(1,27,4,"dọn một món ngon tùy theo cha sở thích; rồi dâng lên cho cha ăn, đặng linh hồn cha chúc phước cho con trước khi chết."));
visuperobject.biblemap.put("b1_27_5", new visuperobject(1,27,5,"Vả, đương khi Y-sác nói cùng Ê-sau con trai mình, thì Rê-be-ca có nghe. Ê-sau bèn ra đồng săn thịt rừng đặng đem về cho cha."));
visuperobject.biblemap.put("b1_27_6", new visuperobject(1,27,6,"Rê-be-ca nói cùng Gia-cốp, con trai mình, rằng: Nầy, mẹ có nghe cha nói cùng Ê-sau, anh con, và dặn rằng:"));
visuperobject.biblemap.put("b1_27_7", new visuperobject(1,27,7,"Hãy đem thịt rừng về cho cha, dọn một món ngon cha ăn; đặng trước khi qua đời, cha sẽ chúc phước cho con trước mặt Ðức Giê-hô-va."));
visuperobject.biblemap.put("b1_27_8", new visuperobject(1,27,8,"Vậy, con ơi! bây giờ, hãy nghe mẹ và làm theo lời mẹ biểu:"));
visuperobject.biblemap.put("b1_27_9", new visuperobject(1,27,9,"Hãy ra ngoài bầy, bắt hai dê con tốt, rồi mẹ sẽ dọn cho cha con một món ngon, tùy theo người sở thích;"));
visuperobject.biblemap.put("b1_27_10", new visuperobject(1,27,10,"con sẽ đem cho cha ăn, đặng người chúc phước con trước khi qua đời."));
visuperobject.biblemap.put("b1_27_11", new visuperobject(1,27,11,"Gia-cốp thưa lại cùng mẹ rằng: Thưa mẹ, Ê-sau, anh con, có lông, còn con lại không."));
visuperobject.biblemap.put("b1_27_12", new visuperobject(1,27,12,"Có lẽ cha sẽ rờ mình chăng, coi con như kẻ phỉnh gạt, thì con chắc lấy cho mình sự rủa sả, chớ chẳng phải sự chúc phước đâu."));
visuperobject.biblemap.put("b1_27_13", new visuperobject(1,27,13,"Mẹ đáp rằng: Con ơi! xin sự rủa sả đó để cho mẹ chịu; hãy cứ nghe lời mẹ, ra bắt hai dê con."));
visuperobject.biblemap.put("b1_27_14", new visuperobject(1,27,14,"Vậy, Gia-cốp đi bắt hai dê con, đem lại cho mẹ, làm món ngon, tùy theo cha sở thích."));
visuperobject.biblemap.put("b1_27_15", new visuperobject(1,27,15,"Ðoạn Rê-be-ca lấy quần áo tốt nhứt của Ê-sau, con trưởng nam, sẵn để trong nhà, mà mặc vào cho Gia-cốp, con út mình;"));
visuperobject.biblemap.put("b1_27_16", new visuperobject(1,27,16,"rồi người lấy da dê con bao hai tay và cổ, vì tay và cổ Gia-cốp không có lông."));
visuperobject.biblemap.put("b1_27_17", new visuperobject(1,27,17,"Rê-be-ca bèn để món ngon và bánh của mình đã dọn vào tay con út mình."));
visuperobject.biblemap.put("b1_27_18", new visuperobject(1,27,18,"Gia-cốp bưng đến cha mình và nói rằng: Thưa cha! Y-sác đáp: Có cha đây; con là đứa nào đó?"));
visuperobject.biblemap.put("b1_27_19", new visuperobject(1,27,19,"Gia-cốp thưa: Tôi là Ê-sau, con trưởng nam của cha; con đã làm theo lời cha dặn biểu; xin cha dậy, ngồi ăn lấy thịt săn của con, đặng linh hồn cha sẽ chúc phước cho con."));
visuperobject.biblemap.put("b1_27_20", new visuperobject(1,27,20,"Y-sác hỏi rằng: Sao con đi săn được mau thế? Thưa rằng: Ấy nhờ Giê-hô-va Ðức Chúa Trời của cha xui tôi gặp mau vậy."));
visuperobject.biblemap.put("b1_27_21", new visuperobject(1,27,21,"Y-sác nói cùng Gia-cốp rằng: Hỡi con! hãy lại gần đặng cha rờ thử xem có phải là Ê-sau, con của cha chăng?"));
visuperobject.biblemap.put("b1_27_22", new visuperobject(1,27,22,"Gia-cốp bèn lại gần Y-sác; người rờ và nói rằng: Tiếng nói thì của Gia-cốp, còn hai tay lại của Ê-sau."));
visuperobject.biblemap.put("b1_27_23", new visuperobject(1,27,23,"Người chẳng nhận biết đặng vì hai tay cũng có lông như của anh, là Ê-sau. Vậy Y-sác chúc phước cho."));
visuperobject.biblemap.put("b1_27_24", new visuperobject(1,27,24,"Người lại hỏi rằng: Ấy con thật là Ê-sau, con trai ta, phải chăng? Gia-cốp thưa: Phải, con đây."));
visuperobject.biblemap.put("b1_27_25", new visuperobject(1,27,25,"Y-sác bèn nói: Hãy dọn cho cha ăn lấy thịt săn của con, đặng linh hồn cha chúc phước cho con. Gia-cốp dọn cho người ăn, cũng có đem rượu cho người uống nữa."));
visuperobject.biblemap.put("b1_27_26", new visuperobject(1,27,26,"Rồi Y-sác cha người nói rằng: Hỡi con! hãy lại gần và hôn cha."));
visuperobject.biblemap.put("b1_27_27", new visuperobject(1,27,27,"Gia-cốp bèn lại gần và hôn người. Y-sác đánh mùi thơm của áo con, liền chúc phước cho, và nói rằng: Nầy, mùi hương của con ta Khác nào mùi hương của cánh đồng mà Ðức Giê-hô-va đã ban phước cho."));
visuperobject.biblemap.put("b1_27_28", new visuperobject(1,27,28,"Cầu xin Ðức Chúa Trời cho con sương móc trên trời xuống, Ðược màu mỡ của đất, và dư dật lúa mì cùng rượu."));
visuperobject.biblemap.put("b1_27_29", new visuperobject(1,27,29,"Muôn dân phải phục con, Các nước phải quì lạy trước mặt con! Hãy được quyền chủ các anh em, Các con trai của mẹ con phải quì lạy trước mặt con! Ai rủa sả con sẽ bị rủa sả lại, Ai cầu phước cho con, sẽ được ban phước lại!"));
visuperobject.biblemap.put("b1_27_30", new visuperobject(1,27,30,"Vừa khi Y-sác chúc phước cho Gia-cốp xuống, và vừa khi Gia-cốp lùi ra khỏi nơi cha mình, thì Ê-sau, anh người, đi săn trở về."));
visuperobject.biblemap.put("b1_27_31", new visuperobject(1,27,31,"Người cũng dọn một món ngon, dâng lên cho cha và thưa rằng: Xin dậy, ngồi ăn thịt săn của con trai cha, đặng linh hồn cha chúc phước cho con."));
visuperobject.biblemap.put("b1_27_32", new visuperobject(1,27,32,"Y-sác, cha người, hỏi: Con là đứa nào? Thưa rằng: Tôi là Ê-sau, con trưởng nam của cha."));
visuperobject.biblemap.put("b1_27_33", new visuperobject(1,27,33,"Y-sác lấy làm cảm động qua đỗi mà nói rằng: Vậy, còn đứa nào đã săn thịt rừng, và đã dâng lên cho cha đó? Trước khi con về, cha đã ăn hết rồi, và chúc phước cho nó; nó lại sẽ được ban phước vậy."));
visuperobject.biblemap.put("b1_27_34", new visuperobject(1,27,34,"Vừa nghe dứt lời cha, thì Ê-sau la lên một tiếng rất lớn, và rất thảm thiết mà thưa cùng cha rằng: Cha ơi! xin hãy chúc phước cho con luôn nữa!"));
visuperobject.biblemap.put("b1_27_35", new visuperobject(1,27,35,"Nhưng Y-sác lại đáp: Em con đã dùng mưu kế đến cướp sự chúc phước của con rồi."));
visuperobject.biblemap.put("b1_27_36", new visuperobject(1,27,36,"Ê-sau liền nói rằng: Có phải vì người ta gọi nó là Gia-cốp mà đó hai lần chiếm lấy vị tôi rồi chăng? Nó đã chiếm quyền trưởng nam tôi, và lại bây giờ còn chiếm sự phước lành của tôi nữa. Tiếp rằng: Cha chẳng có dành sự chúc phước chi cho tôi sao?"));
visuperobject.biblemap.put("b1_27_37", new visuperobject(1,27,37,"Y-sác đáp rằng: Nầy, cho đã lập nó lên làm chủ con, và đã cho các anh em làm tôi nó; và đã dự bị lúa mì và rượu cho nó. Vậy, cha sẽ làm chi cho con đây?"));
visuperobject.biblemap.put("b1_27_38", new visuperobject(1,27,38,"Ê-sau thưa rằng: Cha ơi! cha chỉ có sự chúc phước đó thôi sao? Hãy chúc phước cho con với, cha! Rồi Ê-sau cất tiếng lên khóc."));
visuperobject.biblemap.put("b1_27_39", new visuperobject(1,27,39,"Y-sác cha người, đáp rằng: Nầy, nơi con ở sẽ thiếu màu mỡ của đất, Cùng sương-móc trên trời sa xuống."));
visuperobject.biblemap.put("b1_27_40", new visuperobject(1,27,40,"Con sẽ nhờ gươm mới được sống. Và làm tôi tớ cho em con. Song khi con lưu lạc rày đó mai đây, Sẽ bẻ cái ách của em trên cổ con vậy."));
visuperobject.biblemap.put("b1_27_41", new visuperobject(1,27,41,"Ê-sau trở lòng ghét Gia-cốp vì cớ cha mình chúc phước cho người, bèn nói thầm trong lòng rằng: Ngày tang của cha đã hầu gần; vậy, ta sẽ giết Gia-cốp, em ta, đi."));
visuperobject.biblemap.put("b1_27_42", new visuperobject(1,27,42,"Họ đem lời Ê-sau, con lớn, thuật lại cùng Rê-be-ca, thì người sai gọi Gia-cốp, con út mình, mà nói rằng: Nầy, Ê-sau, anh con, toan giết con để báo thù."));
visuperobject.biblemap.put("b1_27_43", new visuperobject(1,27,43,"Vậy, bây giờ, hỡi con, hãy nghe lời mẹ, đứng dậy, chạy trốn qua nhà La-ban, cậu con, tại Cha-ran,"));
visuperobject.biblemap.put("b1_27_44", new visuperobject(1,27,44,"và hãy ở cùng người ít lâu, cho đến lúc nào cơn giận của anh con qua hết."));
visuperobject.biblemap.put("b1_27_45", new visuperobject(1,27,45,"Khi cơn giận anh con bỏ qua rồi, và nó quên việc con đã gây nên với nó, thì mẹ sẽ biểu con về. Có lẽ đâu mẹ phải chịu mất cả hai đứa trong một ngày ư?"));
visuperobject.biblemap.put("b1_27_46", new visuperobject(1,27,46,"Rê-be-ca nói cùng Y-sác rằng: Tôi đã chán, không muốn sống nữa, vì cớ mấy con gái họ Hếch. Nếu Gia-cốp cưới một trong mấy con gái họ Hếch mà làm vợ, tức một người con gái trong xứ như các dân đó; thôi, tôi còn sống mà chi?"));
visuperobject.biblemap.put("b1_28_1", new visuperobject(1,28,1,"Y-sác bèn gọi Gia-cốp, chúc phước cho và dặn rằng: Con chớ chọn một người vợ nào hết trong bọn con gái Ca-na-an."));
visuperobject.biblemap.put("b1_28_2", new visuperobject(1,28,2,"Hãy đứng dậy, đi qua xứ Pha-đan-a-ram, tại nhà Bê-tu-ên, ông ngoại con, và cưới ở đó một người vợ trong các con gái của La-ban, là cậu con."));
visuperobject.biblemap.put("b1_28_3", new visuperobject(1,28,3,"Cầu xin Ðức Chúa Trời toàn năng ban phước cho con, làm con sanh sản, thêm nhiều và trở nên một đám dân đông."));
visuperobject.biblemap.put("b1_28_4", new visuperobject(1,28,4,"Cầu xin Ngài ban cho con và dòng dõi con phước lành của Áp-ra-ham, hầu cho xứ con đã kiều ngụ sẽ làm sản nghiệp cho con, tức xứ Ngài đã ban cho Áp-ra-ham!"));
visuperobject.biblemap.put("b1_28_5", new visuperobject(1,28,5,"Rồi Y-sác sai Gia-cốp đi qua xứ Pha-đan-a-ram, đến nhà La-ban, con trai Bê-tu-ên, vốn người A-ram, lại là anh của Rê-bê-ca, mẹ của Gia-cốp và Ê-sau."));
visuperobject.biblemap.put("b1_28_6", new visuperobject(1,28,6,"Ê-sau thấy Y-sác đã chúc phước cho Gia-cốp, và sai đi qua xứ Pha-đan-a-ram đặng cưới vợ; và trong lúc đương chúc phước có dặn rằng: Chớ cưới vợ trong bọn con gái Ca-na-an;"));
visuperobject.biblemap.put("b1_28_7", new visuperobject(1,28,7,"lại đã thấy Gia-cốp vâng lời cha me đi qua xứ Pha-đan-a-ram đó,"));
visuperobject.biblemap.put("b1_28_8", new visuperobject(1,28,8,"thì biết rằng bọn con gái Ca-na-an không vừa ý Y-sác, cha mình;"));
visuperobject.biblemap.put("b1_28_9", new visuperobject(1,28,9,"nên người bèn đi đến nhà Ích-ma-ên (ngoài hai người vợ đã có rồi) cưới thêm nàng Ma-ba-lát, con gái của Ích-ma-ên, cháu nội của Áp-ra-ham và em của Nê-ba-giốt."));
visuperobject.biblemap.put("b1_28_10", new visuperobject(1,28,10,"Gia-cốp từ Bê -e-Sê-ba đi đến Cha-ran,"));
visuperobject.biblemap.put("b1_28_11", new visuperobject(1,28,11,"tới một chỗ kia, mặt trời đã khuất, thì qua đêm tại đó. Người lấy một hòn đá làm gối đầu, và nằm ngủ tại đó;"));
visuperobject.biblemap.put("b1_28_12", new visuperobject(1,28,12,"bèn chiêm bao thấy một cái thang bắc từ dưới đất, đầu đến tận trời, các thiên sứ của Ðức Chúa Trời đi lên xuống trên thang đó."));
visuperobject.biblemap.put("b1_28_13", new visuperobject(1,28,13,"Nầy, Ðức Giê-hô-va ngự trên đầu thang mà phán rằng: Ta là Ðức Chúa Trời của Áp-ra-ham, tổ phụ ngươi, cùng là Ðức Chúa Trời của Y-sác. Ta sẽ cho ngươi và dòng dõi ngươi đất mà ngươi đương nằm ngủ đây."));
visuperobject.biblemap.put("b1_28_14", new visuperobject(1,28,14,"Dòng dõi ngươi sẽ đông như cát bụi trên mặt đất, tràn ra đến đông tây nam bắc, và các chi họ thế gian sẽ nhờ ngươi và dòng dõi ngươi mà được phước."));
visuperobject.biblemap.put("b1_28_15", new visuperobject(1,28,15,"Nầy, ta ở cùng ngươi, ngươi đi đâu, sẽ theo gìn giữ đó, và đem ngươi về xứ nầy; vì ta không bao giờ bỏ ngươi cho đến khi ta làm xong những điều ta đã hứa cùng ngươi."));
visuperobject.biblemap.put("b1_28_16", new visuperobject(1,28,16,"Gia-cốp thức giấc, nói rằng: Thật Ðức Giê-hô-va hiện có trong nơi đây mà tôi không biết!"));
visuperobject.biblemap.put("b1_28_17", new visuperobject(1,28,17,"Người bắt sợ và nói rằng: Chốn nầy đáng kinh khủng thay thật là đền Ðức Chúa Trời, thật là cửa của trời!"));
visuperobject.biblemap.put("b1_28_18", new visuperobject(1,28,18,"Người dậy sớm, lấy hòn đá của mình dùng gối đầu, dựng đứng lên làm cây trụ, đổ dầu lên trên chót trụ đó;"));
visuperobject.biblemap.put("b1_28_19", new visuperobject(1,28,19,"rồi đặt tên chốn nầy là Bê-tên; còn nguyên khi trước tên thành là Lu-xơ."));
visuperobject.biblemap.put("b1_28_20", new visuperobject(1,28,20,"Gia-cốp bèn khấn vái rằng: Nếu Ðức Chúa Trời ở cùng tôi, gìn giữ tôi trong lúc đi đường, cho bánh ăn, áo mặc,"));
visuperobject.biblemap.put("b1_28_21", new visuperobject(1,28,21,"và nếu tôi trở về bình an đến nhà cha tôi, thì Ðức Giê-hô-va sẽ là Ðức Chúa Trời tôi."));
visuperobject.biblemap.put("b1_28_22", new visuperobject(1,28,22,"Hòn đá đã dùng làm trụ đây sẽ là đền Ðức Chúa Trời, Và tôi sẽ nộp lại cho Ngài một phần mười mọi của cải mà Ngài sẽ cho tôi."));
visuperobject.biblemap.put("b1_29_1", new visuperobject(1,29,1,"Ðoạn, Gia-cốp lên đường, đi đến xứ của dân Ðông phương."));
visuperobject.biblemap.put("b1_29_2", new visuperobject(1,29,2,"Người nhìn xem, thấy một cái giếng trong đồng ruộng, gần đó có ba bầy chiên nằm nghỉ, vì nơi nầy là chốn người ta cho các bầy chiên uống nước. Hòn đá đậy trên miệng giếng rất lớn."));
visuperobject.biblemap.put("b1_29_3", new visuperobject(1,29,3,"Các bầy chiên đều hiệp lại đó, rồi họ lăn hòn đá trên miệng giếng ra, cho các bầy uống nước; đoạn, lăn đá lại chỗ cũ, đậy trên miệng giếng."));
visuperobject.biblemap.put("b1_29_4", new visuperobject(1,29,4,"Gia-cốp hỏi mấy tay chăn chiên rằng: Hỡi các anh! các anh ở đâu đây? Bọn đó đáp rằng: Chúng tôi ở Cha-ran đến."));
visuperobject.biblemap.put("b1_29_5", new visuperobject(1,29,5,"Người hỏi: Các anh có biết La-ban, con trai Na-cô, chăng? Ðáp rằng: Chúng tôi biết."));
visuperobject.biblemap.put("b1_29_6", new visuperobject(1,29,6,"Lại hỏi: Người đó được mạnh giỏi chăng? Ðáp rằng: Người vẫn được mạnh giỏi, và nầy, Ra-chên, con gái người, dương đi đến cùng bầy chiên kia."));
visuperobject.biblemap.put("b1_29_7", new visuperobject(1,29,7,"Ngươi nói: Nầy, trời hãy còn sớm, chưa phải là giờ nhóm hiệp các súc vật; vậy, các anh hãy cho bầy chiên uống nước, rồi thả đi ăn lại đi."));
visuperobject.biblemap.put("b1_29_8", new visuperobject(1,29,8,"Ðáp rằng: Chúng tôi làm như vậy chẳng được; phải đợi các bầy hiệp lại đủ hết rồi, bấy giờ mới lăn hòn đá trên miệng giếng ra, cho bầy uống nước được."));
visuperobject.biblemap.put("b1_29_9", new visuperobject(1,29,9,"Vả, đương lúc người còn nói chuyện cùng bọn đó, thì Ra-chên dắt bầy chiên của cha nàng đến, vi nàng vốn là người chăn chiên."));
visuperobject.biblemap.put("b1_29_10", new visuperobject(1,29,10,"Khi vừa thấy Ra-chên, con gài La-ban, cậu mình, thì người liền lại gần lăn hòn đá ra khỏi miệng giếng, cho bầy chiên của La-ban, cậu mình, uống nước."));
visuperobject.biblemap.put("b1_29_11", new visuperobject(1,29,11,"Gia-cốp hôn Ra-chên, cất tiếng lên khóc;"));
visuperobject.biblemap.put("b1_29_12", new visuperobject(1,29,12,"rồi nói cho nàng biết rằng mình là bà con với cha nàng, tức con trai của Rê-bê-ca. Nàng bèn chạy về thuật lại cho cha hay."));
visuperobject.biblemap.put("b1_29_13", new visuperobject(1,29,13,"Vừa khi nghe nói Gia-cốp, con trai của em gái mình, thì La-ban chạy đến trước mặt người, ôm choàng lấy mà hôn, rồi mới vào nhà. Gia-cốp thuật lại cho La-ban nghe các việc đã xảy qua."));
visuperobject.biblemap.put("b1_29_14", new visuperobject(1,29,14,"La-ban bèn đáp rằng: Thật vậy, cháu là cốt nhục của cậu; rồi Gia-cốp ở một tháng cùng cậu."));
visuperobject.biblemap.put("b1_29_15", new visuperobject(1,29,15,"Ðoạn, La-ban nói cùng Gia-cốp rằng: vì cớ cháu là bà con của cậu, cháu giúp công không cho cậu thôi sao? Tiền công bao nhiêu hãy nói cho cậu biết."));
visuperobject.biblemap.put("b1_29_16", new visuperobject(1,29,16,"Vả, La-ban có hai con gái, con lớn tên là Lê-a; con nhỏ tên là Ra-chên."));
visuperobject.biblemap.put("b1_29_17", new visuperobject(1,29,17,"Mắt Lê-a yếu, còn Ra-chên hình dung đẹp đẽ, vẻ mặt tốt tươi."));
visuperobject.biblemap.put("b1_29_18", new visuperobject(1,29,18,"Gia-cốp yêu Ra-chên nên nói rằng: Vì nàng Ra-chên, con út cậu, tôi sẽ giúp việc trong bảy năm."));
visuperobject.biblemap.put("b1_29_19", new visuperobject(1,29,19,"La-ban trả lời rằng: Thà cậu gả nó cho cháu hơn là gả cho một người khác; hãy ở với cậu."));
visuperobject.biblemap.put("b1_29_20", new visuperobject(1,29,20,"Vậy, Gia-cốp vì Ra-chên, phải giúp việc trong bảy năm: nhưng bởi yêu nàng, nên coi bảy năm bằng chừng đôi ba bữa."));
visuperobject.biblemap.put("b1_29_21", new visuperobject(1,29,21,"Kế đó, Gia-cốp nói cùng La-ban rằng: Hạn tôi đã mãn rồi; đâu! xin cậu hãy giao vợ tôi lại đặng tôi đến gần cùng nàng."));
visuperobject.biblemap.put("b1_29_22", new visuperobject(1,29,22,"La-ban bèn mời các người trong xóm, bày một bữa tiệc;"));
visuperobject.biblemap.put("b1_29_23", new visuperobject(1,29,23,"đến chiều tối, bắt Lê-a, con gái mình, đưa cho Gia-cốp, rồi chàng đến cùng nàng."));
visuperobject.biblemap.put("b1_29_24", new visuperobject(1,29,24,"La-ban lại cắt con đòi Xinh-ba theo hầu Lê-a, con gái mình."));
visuperobject.biblemap.put("b1_29_25", new visuperobject(1,29,25,"Sáng bữa sau, mới biết là nàng Lê-a, thì Gia-cốp hỏi La-ban rằng: Cậu đã đãi tôi cách chi vậy? Có phải vì Ra-chên mà tôi mới giúp việc cho nhà cậu chăng? Sao cậu lừa-gạt tôi?"));
visuperobject.biblemap.put("b1_29_26", new visuperobject(1,29,26,"La-ban đáp rằng: Phong tục ở đây chẳng phải được gả em út trước, rồi chị cả sau."));
visuperobject.biblemap.put("b1_29_27", new visuperobject(1,29,27,"Hãy ở với đứa nầy trọn một tuần đi, rồi ta sẽ gả luôn đứa kia cho; về đứa kia cháu phải giúp công cho cậu thêm bảy năm nữa."));
visuperobject.biblemap.put("b1_29_28", new visuperobject(1,29,28,"Gia-cốp theo lời, ở cùng Lê-a trọn một tuần, rồi La-ban gả Ra-chên con gái mình, cho chàng,"));
visuperobject.biblemap.put("b1_29_29", new visuperobject(1,29,29,"và cắt con đòi Bi-la theo hầu Ra-chên con gái mình."));
visuperobject.biblemap.put("b1_29_30", new visuperobject(1,29,30,"Gia-cốp đi lại cùng Ra-chên, thương yêu nàng hơn Lê-a. Vậy, người ở giúp việc nhà La-ban thêm bảy năm nữa."));
visuperobject.biblemap.put("b1_29_31", new visuperobject(1,29,31,"Ðức Giê-hô-va thấy Lê-a bị ghét, bèn cho nàng sanh sản; còn Ra-chên lại son sẻ."));
visuperobject.biblemap.put("b1_29_32", new visuperobject(1,29,32,"Lê-a thọ thai, sanh một con trai, đặt tên Ru-bên; vì nói rằng: Ðức Giê-hô-va đã thấy sự cực khổ tôi; bây giờ chồng sẽ yêu mến tôi."));
visuperobject.biblemap.put("b1_29_33", new visuperobject(1,29,33,"Nàng thọ thai nữa và sanh một con trai, đặt tên Si-mê-ôn; vì nói rằng. Ðức Giê-hô-va có nghe biết tôi bị ghét, nên cho thêm đứa này."));
visuperobject.biblemap.put("b1_29_34", new visuperobject(1,29,34,"Nàng thọ thai nữa và sanh một con trai; nàng rằng: Lần nầy chồng sẽ dính díu cùng tôi, vì tôi đã sanh cho người ba con trai; bởi cớ đó, đặt tên là Lê-vi."));
visuperobject.biblemap.put("b1_29_35", new visuperobject(1,29,35,"Nàng thọ thai nữa, sanh một con trai, và nói rằng: Lần nầy tôi ngợi khen Ðức Giê-hô-va; vì cớ ấy, đặt tên là Giu-da. Ðoạn, nàng thôi thai nghén."));
visuperobject.biblemap.put("b1_30_1", new visuperobject(1,30,1,"Ra-chên thấy mình chẳng sanh con cho Gia-cốp, thì đem lòng ghen ghét chị, nói cùng Gia-cốp rằng: Hãy cho tôi có con, bằng không, tôi chết."));
visuperobject.biblemap.put("b1_30_2", new visuperobject(1,30,2,"Gia-cốp nổi giận cùng Ra-chên mà rằng: Há ta được thay quyền Ðức Giê-hô-va, mà không cho ngươi sanh sản sao?"));
visuperobject.biblemap.put("b1_30_3", new visuperobject(1,30,3,"Ra-chên rằng: Nầy con đòi Bi-la của tôi, hãy lại cùng nó, hầu cho nó sanh đẻ thay cho tôi, đặng phần tôi bởi nó cũng có con nữa."));
visuperobject.biblemap.put("b1_30_4", new visuperobject(1,30,4,"Vậy, Ra-chên đưa Bi-la làm hầu Gia-cốp, và người đi lại cùng nàng."));
visuperobject.biblemap.put("b1_30_5", new visuperobject(1,30,5,"Bi-la thọ thai, sanh một con trai cho Gia-cốp."));
visuperobject.biblemap.put("b1_30_6", new visuperobject(1,30,6,"Ra-chên rằng: Ðức Chúa Trời đã xét tôi công bình, nhậm lời tôi, nên cho tôi một con trai; vì cớ ấy, nàng đặt tên đứa trai đó là Ðan."));
visuperobject.biblemap.put("b1_30_7", new visuperobject(1,30,7,"Bi-la, con đòi của Ra-chên, thọ thai nữa, và sanh một con trai thứ nhì cho Gia-cốp."));
visuperobject.biblemap.put("b1_30_8", new visuperobject(1,30,8,"Ra-chên rằng: Tôi đã hết sức chống cự với chị tôi, và tôi được thắng; vì cớ ấy, nàng đặt tên đứa trai nầy là Nép-ta-li."));
visuperobject.biblemap.put("b1_30_9", new visuperobject(1,30,9,"Lê-a thấy mình thôi sanh sản, bèn bắt Xinh-ba, con đòi mình, làm hầu cho Gia-cốp."));
visuperobject.biblemap.put("b1_30_10", new visuperobject(1,30,10,"Xinh-ba, con đòi của Lê-a, sanh một con trai cho Gia-cốp."));
visuperobject.biblemap.put("b1_30_11", new visuperobject(1,30,11,"Lê-a rằng: Phước biết dường bao! rồi đặt tên đứa trai đó là Gát."));
visuperobject.biblemap.put("b1_30_12", new visuperobject(1,30,12,"Xinh ba, con đòi của Lê-a, sanh một con trai thứ nhì cho Gia-cốp;"));
visuperobject.biblemap.put("b1_30_13", new visuperobject(1,30,13,"thì Lê-a rằng: Tôi lấy làm vui mừng dường bao! vì các con gái sẽ xưng ta được phước; nàng bèn đặt tên đứa trai đó là A-se."));
visuperobject.biblemap.put("b1_30_14", new visuperobject(1,30,14,"Nhằm mùa gặt lúa mì, Ru-bên đi ra đồng gặp được những trái phong già, đem về dâng cho Lê-a, mẹ mình. Ra-chên bèn nói cùng Lê-a rằng: Xin chị hãy cho tôi những trái phong già của con chị đó."));
visuperobject.biblemap.put("b1_30_15", new visuperobject(1,30,15,"Ðáp rằng: Cướp chồng tao há là một việc nhỏ sao, nên mầy còn muốn lấy trái phong già của con trai tao nữa? Ra-chên rằng: Ừ thôi! chồng ta hãy ở cùng chị tối đêm nay, đổi cho trái phong già của con chị đó."));
visuperobject.biblemap.put("b1_30_16", new visuperobject(1,30,16,"Ðến chiều, Gia-cốp ở ngoài đồng về, thì Lê-a đến trước mặt người mà nói rằng: Chàng hãy lại cùng tôi, vì tôi đã dùng trái phong già của con tôi chịu thuê được chàng; vậy, đêm đó người lại nằm cùng nàng."));
visuperobject.biblemap.put("b1_30_17", new visuperobject(1,30,17,"Ðức Chúa Trời nhậm lời Lê-a, nàng thọ thai và sanh cho Gia-cốp một con trai thứ năm."));
visuperobject.biblemap.put("b1_30_18", new visuperobject(1,30,18,"Nàng rằng: Ðức Chúa Trời đã đền công tôi, vì cớ tôi đã trao con đòi cho chồng; vậy, nàng đặt tên đứa con trai đó là Y-sa-ca."));
visuperobject.biblemap.put("b1_30_19", new visuperobject(1,30,19,"Lê-a thọ thai nữa, sanh một con trai thứ sáu cho Gia-cốp,"));
visuperobject.biblemap.put("b1_30_20", new visuperobject(1,30,20,"bèn nói rằng: Ðức Chúa Trời ban cho tôi một vật quí báu; lần nầy chồng sẽ ở cùng tôi, vì đã sanh cho người sáu con trai rồi. Nàng đặt tên đứa con trai nầy là Sa-bu-lôn."));
visuperobject.biblemap.put("b1_30_21", new visuperobject(1,30,21,"Kế đó, nàng sanh một con gái, đặt tên là Ði-na."));
visuperobject.biblemap.put("b1_30_22", new visuperobject(1,30,22,"Ðức Chúa Trời nhớ lại Ra-chên, bèn nhậm lời và cho nàng sanh sản."));
visuperobject.biblemap.put("b1_30_23", new visuperobject(1,30,23,"Nàng thọ thai, sanh một con trai, mà rằng: Ðức Chúa Trời đã rửa sự xấu hổ cho tôi rồi;"));
visuperobject.biblemap.put("b1_30_24", new visuperobject(1,30,24,"bèn đặt tên đứa trai đó là Giô-sép; lại nói rằng: Cầu xin Ðức Giê-hô-va thêm cho một con trai nữa!"));
visuperobject.biblemap.put("b1_30_25", new visuperobject(1,30,25,"Khi Ra-chên đã sanh Giô-sép thì Gia-cốp thưa cùng La-ban rằng: Xin cậu cho tôi thôi, đặng trở về quê hương tôi;"));
visuperobject.biblemap.put("b1_30_26", new visuperobject(1,30,26,"xin giao vợ và con lại cho tôi, mà tôi đã đặng vì cớ giúp cậu, để tôi đi; bởi cậu cũng biết rằng tôi đã giúp công việc cho cậu thể nào."));
visuperobject.biblemap.put("b1_30_27", new visuperobject(1,30,27,"La-ban đáp rằng: Ước gì cậu được nhờ ơn cháu! Cậu cũng đoán rõ ràng Ðức Giê-hô-va đã vì cháu mà ban phước cho cậu vậy."));
visuperobject.biblemap.put("b1_30_28", new visuperobject(1,30,28,"Lại nói rằng: Cháu hãy định công giá cháu đi, rồi cậu sẽ trả cho!"));
visuperobject.biblemap.put("b1_30_29", new visuperobject(1,30,29,"Gia-cốp đáp: Cậu biết tôi giúp đỡ cậu thể nào, và về tay tôi bầy súc vật cậu được ra sao."));
visuperobject.biblemap.put("b1_30_30", new visuperobject(1,30,30,"Trước khi tôi đến, súc vật của cậu thật ít, nhưng từ khi tôi đến ở, thì thêm nhiều quá bội. Ðức Giê-hô-va đã ban phước cho cậu từ khi tôi bước chơn vào nhà nầy; bây giờ biết chừng nào tôi sẽ được lo việc nhà tôi?"));
visuperobject.biblemap.put("b1_30_31", new visuperobject(1,30,31,"La-ban rằng: Cậu sẽ cho cháu cái gì bây giờ? Ðáp rằng: Sẽ chẳng cho tôi chi cả. Nếu thuận theo lời nầy, thì tôi sẽ cho ăn và chăn bầy súc vật cậu nữa:"));
visuperobject.biblemap.put("b1_30_32", new visuperobject(1,30,32,"Ngày nay tôi sẽ đi kiểm soát các bầy chiên và bầy dê của cậu; trong bầy chiên bầy dê, hễ con nào có rằn, có đốm, cùng chiên sắc đen, thì tôi sẽ để riêng ra; ấy sẽ là phần công giá của tôi đó."));
visuperobject.biblemap.put("b1_30_33", new visuperobject(1,30,33,"Một mai cậu đi xem xét công giá tôi, thì lòng ngay thẳng tôi sẽ làm chứng trước mặt cậu. Hễ ở về phần bên tôi, các con dê nào không có rằn và đốm, các chiên con nào không có sắc đen, thì sẽ cho là ăn cắp."));
visuperobject.biblemap.put("b1_30_34", new visuperobject(1,30,34,"La-ban đáp: Ừ thôi! được như lời cháu nói."));
visuperobject.biblemap.put("b1_30_35", new visuperobject(1,30,35,"Chánh ngày đó, La-ban liền để riêng các dê đực có sọc, có đốm, các dê cái đó rằn, có đốm, các vật nào có đém trắng và các chiên con có sắc đen, giao cho mấy con trai mình;"));
visuperobject.biblemap.put("b1_30_36", new visuperobject(1,30,36,"rồi để Gia-cốp cùng mình ở cách xa nhau chừng ba ngày đường; Gia-cốp bèn chăn bầy súc vật của La-ban chỉ còn lại."));
visuperobject.biblemap.put("b1_30_37", new visuperobject(1,30,37,"Ðoạn, Gia-cốp lấy nhành bạch dương, nhành bồ đào và nhành bá-tiên tươi, lột vỏ để bày lằn da trắng ở trong ra."));
visuperobject.biblemap.put("b1_30_38", new visuperobject(1,30,38,"Người để những nhành đã lột vỏ trong máng trước mặt con chiên, là nơi các thú đó đến uống; khi đến uống nước thì con chiên có giao hiệp nhau."));
visuperobject.biblemap.put("b1_30_39", new visuperobject(1,30,39,"Các con chiên ngó thấy những nhành đó mà giao hiệp nhau thì sanh chiên con có sọc, có rằn và có đốm."));
visuperobject.biblemap.put("b1_30_40", new visuperobject(1,30,40,"Gia-cốp để riêng chiên con ra, rồi để chiên cái trong bầy của La-ban trước mặt các con có sọc và có sắc đen; nhưng bầy mình thì riêng ra, không để gần bầy của La-ban."));
visuperobject.biblemap.put("b1_30_41", new visuperobject(1,30,41,"Vả, mọi lần các con chiên mạnh mập giao hiệp, thì Gia-cốp để các nhành đó dưới mắt trong máng uống nước, đặng chúng nó đối nhành mà giao hiệp cùng nhau."));
visuperobject.biblemap.put("b1_30_42", new visuperobject(1,30,42,"Nhưng khi nào con chiên gầy ốm, thì người không để nhành như vậy; thế thì, các chiên con gầy ốm để về phần La-ban, còn chiên con mạnh mập lại về phần Gia-cốp."));
visuperobject.biblemap.put("b1_30_43", new visuperobject(1,30,43,"Vậy, người trở nên rất giàu, có nhiều bầy súc vật, tôi trai, tớ gái, lạc đà và lừa."));
visuperobject.biblemap.put("b1_31_1", new visuperobject(1,31,1,"Gia-cốp được nghe lời các con La-ban nói rằng: Gia-cốp đã lấy hết gia tài cha ta, và vì nhờ của cha ta, nên mới được giàu có dường ấy."));
visuperobject.biblemap.put("b1_31_2", new visuperobject(1,31,2,"Gia-cốp cũng để ý coi nét mặt cậu, nhận biết rằng người chẳng đối ở với mình như trước nữa."));
visuperobject.biblemap.put("b1_31_3", new visuperobject(1,31,3,"Ðức Giê-hô-va phán cùng Gia-cốp rằng: Hãy trở về xứ của tổ phụ ngươi, chốn bà con ngươi, ta sẽ phù hộ ngươi."));
visuperobject.biblemap.put("b1_31_4", new visuperobject(1,31,4,"Gia-cốp bèn sai người gọi Ra-chên và Lê-a đến nơi cầm bầy súc vật của mình ngoài đồng,"));
visuperobject.biblemap.put("b1_31_5", new visuperobject(1,31,5,"và nói cùng họ rằng: Ta thấy sắc mặt cha hai ngươi đối cùng ta chẳng còn như trước nữa, nhưng nhờ Ðức Chúa Trời của cha ta phù hộ ta."));
visuperobject.biblemap.put("b1_31_6", new visuperobject(1,31,6,"Chánh hai ngươi cũng biết rằng ta đã giúp cha hai ngươi hết sức,"));
visuperobject.biblemap.put("b1_31_7", new visuperobject(1,31,7,"còn cha hai ngươi lại khinh bạc và mười lần thay đổi công giá ta; nhưng Ðức Chúa Trời không cho phép người làm hại ta chút nào."));
visuperobject.biblemap.put("b1_31_8", new visuperobject(1,31,8,"Khi người dạy rằng: Các con chiên có đốm dùng làm công giá ngươi, quả các con chiên đều sanh con ra có sọc."));
visuperobject.biblemap.put("b1_31_9", new visuperobject(1,31,9,"Thế thì, Ðức Chúa Trời đã bắt súc vật của cha hai ngươi cho ta đó!"));
visuperobject.biblemap.put("b1_31_10", new visuperobject(1,31,10,"Ðương trong lúc chiên giao hiệp nhau, ta nhướng mắt lên, chiêm bao thấy các chiên đực đương giao hiệp cùng chiên cái đều có sọc, có rằn và có đốm."));
visuperobject.biblemap.put("b1_31_11", new visuperobject(1,31,11,"Thiên sứ Ðức Chúa Trời phán cùng ta trong mộng rằng: Hỡi Gia-cốp! Ta bèn thưa: Có tôi đây."));
visuperobject.biblemap.put("b1_31_12", new visuperobject(1,31,12,"Thiên sứ rằng: Hỡi nhướng mắt lên mà nhìn: hết thảy chiên đực đương giao hiệp cùng chiên cái đều có sọc, có rằn và có đốm; vì ta đã thấy cách La-ban ăn ở cùng ngươi rồi."));
visuperobject.biblemap.put("b1_31_13", new visuperobject(1,31,13,"Ta đây là Ðức Chúa Trời của Bê-tên, tức nơi ngươi đã thoa dầu đầu cây trụ và đã khấn vái ta. Bây giờ, hãy đứng dậy, ra khỏi xứ bầy và trở về xứ của bà con ngươi."));
visuperobject.biblemap.put("b1_31_14", new visuperobject(1,31,14,"Ra-chên và Lê-a đáp cùng người mà rằng: Chúng tôi còn một phần chi hay là cơ nghiệp chi nơi nhà cha chúng tôi chăng?"));
visuperobject.biblemap.put("b1_31_15", new visuperobject(1,31,15,"Vì người đã gả bán chúng tôi và ăn xài hết tiền bạc bán nữa, há người chẳng đãi chúng tôi như người dưng ư?"));
visuperobject.biblemap.put("b1_31_16", new visuperobject(1,31,16,"Các tài vật mà Ðức Chúa Trời đoạt nơi tay cha chúng tôi tức là của chúng tôi và của con cái chúng tôi. Vậy bây giờ, chàng hãy làm theo mọi lời Ðức Chúa Trời đã phải dạy."));
visuperobject.biblemap.put("b1_31_17", new visuperobject(1,31,17,"Gia-cốp bèn đứng dậy, đỡ vợ và con lên lưng lạc đà,"));
visuperobject.biblemap.put("b1_31_18", new visuperobject(1,31,18,"dẫn hết thảy súc vật và của cải mình, tức súc vật mình đã gây dựng ở Pha-đan-a-ram, đặng trở về cùng Y-sác, cha mình, ở Ca-na-an."));
visuperobject.biblemap.put("b1_31_19", new visuperobject(1,31,19,"Trong lúc La-ban mắc đi hớt lông chiên người, thì Ra-chên ăn cắp các pho tượng thờ trong nhà của cha mình."));
visuperobject.biblemap.put("b1_31_20", new visuperobject(1,31,20,"Gia-cốp gạt La-ban, vì không nói rằng, mình muốn đi trốn."));
visuperobject.biblemap.put("b1_31_21", new visuperobject(1,31,21,"Vậy, người đứng dậy, đem theo hết thảy tài vật mình, trốn đi ngang qua sông, thẳng tuốt về núi Ga-la-át."));
visuperobject.biblemap.put("b1_31_22", new visuperobject(1,31,22,"Ðến ngày thứ ba, người ta học lại cùng La-ban rằng Gia-cốp đã trốn đi rồi."));
visuperobject.biblemap.put("b1_31_23", new visuperobject(1,31,23,"Người bèn đem các anh em mình đuổi theo Gia-cốp trong bảy ngày đường, và theo kịp tại núi Ga-la-át."));
visuperobject.biblemap.put("b1_31_24", new visuperobject(1,31,24,"Nhưng trong cơn chiêm bao lúc ban đêm, Ðức Chúa Trời đến cùng La-ban, người A-ram, mà phán rằng: Dầu lành dầu dữ, thế nào ngươi khá giữ mình đừng nói chi cùng Gia-cốp hết."));
visuperobject.biblemap.put("b1_31_25", new visuperobject(1,31,25,"Vậy, La-ban theo kịp Gia-cốp. Vả, Gia-cốp đương đóng trại trên núi; La-ban và các anh em người cũng đóng trại trên núi Ga-la-át."));
visuperobject.biblemap.put("b1_31_26", new visuperobject(1,31,26,"La-ban nói cùng Gia-cốp rằng: Cháu đã làm chi vậy? Cháu đã gạt cậu và dẫn mấy đứa gái cậu như phu tù giặc."));
visuperobject.biblemap.put("b1_31_27", new visuperobject(1,31,27,"Sao trốn nhẹm, gạt và không cho cậu hay trước? Có lẽ cậu đưa đi rất vui vẻ, tiếng hát, tiếng đờn và tiếng trống phụ đưa."));
visuperobject.biblemap.put("b1_31_28", new visuperobject(1,31,28,"Cháu làm cách dại dột vậy, không để cho cậu hôn con trai và con gái cậu."));
visuperobject.biblemap.put("b1_31_29", new visuperobject(1,31,29,"Tay cậu có đủ quyền làm hại cháu; nhưng Ðức Chúa Trời của cha cháu đã mách bảo cùng cậu tối hôm qua rằng: Dầu lành dầu dữ, ngươi khá giữ mình đừng nói chi cùng Gia-cốp hết."));
visuperobject.biblemap.put("b1_31_30", new visuperobject(1,31,30,"Bây giờ, vì lòng cháu mong mỏi về nhà cha cháu, nên đã ra đi; nhưng cớ sao cháu lại ăn cắp các pho tượng cậu?"));
visuperobject.biblemap.put("b1_31_31", new visuperobject(1,31,31,"Gia-cốp đáp rằng: Vì cớ tôi e cậu bắt hai người con gái lại chăng."));
visuperobject.biblemap.put("b1_31_32", new visuperobject(1,31,32,"Nhưng tìm nơi ai được các pho tượng của cậu, thì ai đó sẽ bị chết đi! Tại trước mặt các anh em chúng ta, cậu hãy kiểm soát các món nơi tôi đi, và hãy lấy vật chi thuộc về cậu. Vả, Gia-cốp vốn không hay rằng Ra-chên đã trộm mấy pho tượng đó."));
visuperobject.biblemap.put("b1_31_33", new visuperobject(1,31,33,"Vậy, La-ban vào trại Gia-cốp, trại Lê-a, trại hai người đòi, chẳng tìm được chi cả. Ðoạn, ở trại Lê-a bước ra đi vào trại Ra-chên."));
visuperobject.biblemap.put("b1_31_34", new visuperobject(1,31,34,"Vả, Ra-chên có lấy mấy pho tượng đó, giấu dưới bành lạc đà, rồi ngồi lên trên. La-ban soát lục khắp trại chẳng gặp pho tượng."));
visuperobject.biblemap.put("b1_31_35", new visuperobject(1,31,35,"Nàng bèn thưa cùng cha rằng: Vì trong mình con có việc riêng của đờn bà, nên đứng dậy rước chẳng được; xin chúa chớ giận con chi hết. Người kiếm, nhưng chẳng thấy pho tượng đâu hết."));
visuperobject.biblemap.put("b1_31_36", new visuperobject(1,31,36,"Gia-cốp bèn nổi giận rầy lộn cùng La-ban, mà rằng: Tôi có án gì, tội gì, mà cậu hằm hằm đuổi theo như vậy!"));
visuperobject.biblemap.put("b1_31_37", new visuperobject(1,31,37,"Cậu đã soát lục các đồ hành lý tôi, có kiếm được món chi thuộc về nhà cậu chăng? Hãy đem món đó ra trước mặt anh em tôi cùng anh em cậu, đặng họ xét đoán đôi ta."));
visuperobject.biblemap.put("b1_31_38", new visuperobject(1,31,38,"Kìa, đã hai mươi năm tôi ăn ở nơi nhà cậu, chiên cùng dê cậu nào có sảo thai, và tôi chẳng hề ăn thịt chiên đực của bầy cậu bao giờ;"));
visuperobject.biblemap.put("b1_31_39", new visuperobject(1,31,39,"cũng chẳng hề đem về cho cậu một con nào bị xé; bằng có, chính tôi chịu đền đó thôi. Cậu cớ đòi luôn những con bị ăn cắp ban ngày và ăn trộm ban đêm."));
visuperobject.biblemap.put("b1_31_40", new visuperobject(1,31,40,"Ban ngày tôi chịu nắng nồng, ban đêm chịu lạnh lùng, ngủ nào có an giấc được đâu."));
visuperobject.biblemap.put("b1_31_41", new visuperobject(1,31,41,"Ðó trong hai mươi năm tôi ở tại nhà cậu là thế; trong mười bốn năm giúp việc, để được hai con gái cậu, và sáu năm đặng lãnh lấy bầy súc vật của cậu, mà cậu lại còn thay đổi mười lần công giá tôi."));
visuperobject.biblemap.put("b1_31_42", new visuperobject(1,31,42,"Nếu Ðức Chúa Trời của cha tôi, Ðức Chúa Trời của Áp-ra-ham, là Ðấng mà Y-sác kính sợ, không phù hộ tôi, chắc bây giờ cậu đuổi tôi ra tay không, Ðức Chúa Trời đã xem thấy nỗi đau-khổ tôi cùng công việc hai tay tôi làm, nên đêm qua Ngài đã xét công bình rồi đó."));
visuperobject.biblemap.put("b1_31_43", new visuperobject(1,31,43,"La-ban đáp cùng Gia-cốp mà rằng: Các đứa gái nầy là con của cậu; các đứa trẻ nầy là trẻ của cậu; các bầy súc vật tức là bầy súc vật của cậu, và các vật chi cháu thấy được tức là của cậu hết; ngày nay cậu sẽ làm chi được cùng các con gái hai là các cháu cậu đó?"));
visuperobject.biblemap.put("b1_31_44", new visuperobject(1,31,44,"Vậy bây giờ, hè! chúng ta kết-ước cùng nhau, để dùng làm chứng cho cậu và cháu."));
visuperobject.biblemap.put("b1_31_45", new visuperobject(1,31,45,"Gia-cốp lấy một hòn đá, dựng đứng lên làm trụ;"));
visuperobject.biblemap.put("b1_31_46", new visuperobject(1,31,46,"và nói cùng các anh em mình rằng: Hãy lượm góp đá lại. Chúng bèn góp đá lại, chất thành một đống, rồi ngồi lên trên ăn bữa."));
visuperobject.biblemap.put("b1_31_47", new visuperobject(1,31,47,"La-ban đặt trên đống đá đó là Y-ê-ga Sa-ha-du-ta; còn Gia-cốp kêu là Ga-lét."));
visuperobject.biblemap.put("b1_31_48", new visuperobject(1,31,48,"La-ban nói rằng: Ðống đá nầy ngày nay làm chứng cho cậu và cháu đó. Cho nên chúng gọi là Ga-lét."));
visuperobject.biblemap.put("b1_31_49", new visuperobject(1,31,49,"Mà cũng gọi là Mích-ba, vì La-ban có nói thêm rằng: Khi chúng ta phân cách nhau, cầu xin Ðức Giê-hô-va coi sóc cậu và cháu."));
visuperobject.biblemap.put("b1_31_50", new visuperobject(1,31,50,"Nếu cháu hành hạ các con gái cậu, nếu cưới vợ khác nữa, thì hãy giữ mình! Chẳng phải người thường soi xét ta đâu, bèn là Ðức Chúa Trời làm chứng cho chúng ta vậy."));
visuperobject.biblemap.put("b1_31_51", new visuperobject(1,31,51,"La-ban lại nói cùng Gia-cốp rằng: Nầy đống đá, nầy cây trụ mình cậu đã dựng lên giữa cậu và cháu đây."));
visuperobject.biblemap.put("b1_31_52", new visuperobject(1,31,52,"Ðống đá nầy và cây trụ nầy làm chứng rằng cậu chẳng qua khỏi đây, đi đến nơi cháu, và cháu cũng chẳng vượt khỏi đây, đi đến nơi cậu, trong khi có ý muốn làm hại nhau."));
visuperobject.biblemap.put("b1_31_53", new visuperobject(1,31,53,"Cầu xin Ðức Chúa Trời của Áp-ra-ham, Ðức Chúa Trời của Na-cô, Ðức Chúa Trời của cha các người đó đoán xét cho chúng ta! Gia-cốp chỉ Ðấng của Y-sác, cha mình, kính sợ mà thề."));
visuperobject.biblemap.put("b1_31_54", new visuperobject(1,31,54,"Ðoạn, người dâng một của lễ tại trên núi, và mời các anh em mình dùng bánh. Vậy, chúng dùng bánh, rồi ở ban đêm trên núi."));
visuperobject.biblemap.put("b1_31_55", new visuperobject(1,31,55,"La-ban dậy sớm, hôn các con trai và các con gái mình, và chúc phước cho, rồi đi trở về nhà mình."));
visuperobject.biblemap.put("b1_32_1", new visuperobject(1,32,1,"Gia-cốp cứ đi đường, có thiên sứ Ðức Chúa Trời gặp người."));
visuperobject.biblemap.put("b1_32_2", new visuperobject(1,32,2,"Khi Gia-cốp thấy các vị nầy, nói rằng: Ấy là trại quân của Ðức Chúa Trời! Nên người đặt tên chỗ đó là Ma-ha-na-im."));
visuperobject.biblemap.put("b1_32_3", new visuperobject(1,32,3,"Gia-cốp sai sứ giả đến trước cùng Ê-sau, anh mình, trong xứ Sê -i-rơ thuộc về địa-phận Ê-đôm;"));
visuperobject.biblemap.put("b1_32_4", new visuperobject(1,32,4,"người bèn dặn rằng: Các ngươi hãy thưa cùng Ê-sau, chúa ta như vầy: Gia-cốp, kẻ tôi tớ chúa, có nói như vầy rằng: Tôi đã ngụ tại nhà La-ban đến ngày nay;"));
visuperobject.biblemap.put("b1_32_5", new visuperobject(1,32,5,"có bò, lừa, chiên, tôi trai, tớ gái; vậy, xin sai đi nói cho chúa hay trước, hầu cho nhờ được ơn trước mặt chúa tôi."));
visuperobject.biblemap.put("b1_32_6", new visuperobject(1,32,6,"Các sứ giả trở về nói cùng Gia-cốp rằng: Chúng tôi đã đi đến Ê-sau, anh của chủ; nầy người đương đem bốn trăm người đến để rước chủ."));
visuperobject.biblemap.put("b1_32_7", new visuperobject(1,32,7,"Gia-cốp rất sợ hãi và sầu não, bèn chia các dân đi theo, cùng các bầy chiên, bò, lạc đà ra làm hai đội, mà rằng:"));
visuperobject.biblemap.put("b1_32_8", new visuperobject(1,32,8,"Nếu Ê-sau đến hãm đánh một trong hai đội, thì đội còn lại sẽ thoát được."));
visuperobject.biblemap.put("b1_32_9", new visuperobject(1,32,9,"Ðoạn, Gia-cốp cầu rằng: Hỡi Ðức Chúa Trời của tổ phụ Áp-ra-ham tôi! Ðức Chúa Trời của cha Y-sác tôi, tức là Ðức Giê-hô-va! Ngài có phán dạy tôi rằng: Hãy trở về xứ ngươi và nơi bà con ngươi, rồi ta sẽ làm ơn cho ngươi!"));
visuperobject.biblemap.put("b1_32_10", new visuperobject(1,32,10,"Tôi lấy làm hèn mọn không đáng chịu các ân huệ và các điều thành thực mà Ngài đã làm cho kẻ tôi tớ Ngài; vì lúc trước khi qua sông Giô-đanh chỉ có cây gậy tôi, mà ngày nay tôi lại trở nên hai đội quân nầy."));
visuperobject.biblemap.put("b1_32_11", new visuperobject(1,32,11,"Vậy, cầu xin Ðức Chúa Trời giải cứu tôi khỏi tay Ê-sau, anh tôi, vì e người đến đánh tôi và đánh luôn mẹ với con nữa."));
visuperobject.biblemap.put("b1_32_12", new visuperobject(1,32,12,"Vả, Ngài có nói rằng: Quả thật vậy, ta sẽ làm ơn cho ngươi, và làm cho dòng dõi ngươi đông như cát bãi biển, người ta sẽ không biết sao đếm được, vì đông đúc quá."));
visuperobject.biblemap.put("b1_32_13", new visuperobject(1,32,13,"Người ở ban đêm tại đó, lấy các vật có hiện trong tay để làm một của lễ dâng cho Ê-sau, anh mình,"));
visuperobject.biblemap.put("b1_32_14", new visuperobject(1,32,14,"là hai trăm dê cái và hai chục dê đực; hai trăm chiên cái và hai chục chiên đực;"));
visuperobject.biblemap.put("b1_32_15", new visuperobject(1,32,15,"ba chục lạc đà cái có con đương bú, bốn chục bò cái và mười bò đực; hai chục con lừa cái và mười lừa con."));
visuperobject.biblemap.put("b1_32_16", new visuperobject(1,32,16,"Rồi, giao mỗi bầy riêng ra nơi tay đầy tớ mình và dặn rằng: Các ngươi hãy đi trước ta và dẫn bầy nầy cách một khoảng đến bầy kia."));
visuperobject.biblemap.put("b1_32_17", new visuperobject(1,32,17,"Người dặn tên dẫn bầy đi trước hết như vầy: Khi Ê-sau, sanh ta, gặp và hỏi ngươi rằng: Ngươi ở với ai, đi đâu, và bầy súc vật trước mặt ngươi đó thuộc về ai?"));
visuperobject.biblemap.put("b1_32_18", new visuperobject(1,32,18,"thì hãy thưa rằng: Của Gia-cốp, kẻ tôi tớ chúa; ấy là một lễ gởi dâng cho Ê-sau, chúa tôi; và nầy, chánh mình người đương theo sau."));
visuperobject.biblemap.put("b1_32_19", new visuperobject(1,32,19,"Gia-cốp cũng dặn y lời cho tên dẫn bầy thứ nhì, thứ ba và hết thảy người theo coi chừng các bầy, và lại nói: Khi gặp Ê-sau, các ngươi hãy nhớ lời đó,"));
visuperobject.biblemap.put("b1_32_20", new visuperobject(1,32,20,"và nói như vầy: Nầy, Gia-cốp, kẻ tôi tớ chúa, đương theo sau chúng tôi. Vì người thầm nghĩ rằng: Mình đem lễ nầy dâng trước cho Ê-sau nguôi giận đã, rồi mình sẽ giáp mặt sau; có lẽ người tiếp rước mình một cách thuận hiệp đó."));
visuperobject.biblemap.put("b1_32_21", new visuperobject(1,32,21,"Vậy, lễ nầy đem đi trước, còn người, đêm đó ở lại trại."));
visuperobject.biblemap.put("b1_32_22", new visuperobject(1,32,22,"Ban đêm, người thức dậy, dẫn hai vợ, hai tên đòi và mười một đứa con mình đi qua rạch Gia-bốc."));
visuperobject.biblemap.put("b1_32_23", new visuperobject(1,32,23,"Người đem họ qua rạch, và hết thảy của cải mình qua nữa."));
visuperobject.biblemap.put("b1_32_24", new visuperobject(1,32,24,"Vả, một mình Gia-cốp ở lại; thì có một người vật lộn với mình được rạng đông."));
visuperobject.biblemap.put("b1_32_25", new visuperobject(1,32,25,"Khi người đó thấy mình không thắng nổi, bèn đánh vào xương hông Gia-cốp; xương hông liền trặt trong khi vật lộn."));
visuperobject.biblemap.put("b1_32_26", new visuperobject(1,32,26,"Người đó bèn nói: Trời đã rạng đông rồi; thôi để cho ta đi; nhưng Gia-cốp đáp rằng: Tôi chẳng cho người đi đâu, nếu người không ban phước cho tôi."));
visuperobject.biblemap.put("b1_32_27", new visuperobject(1,32,27,"Người đó hỏi: Tên ngươi là chi? Ðáp rằng: Tên tôi là Gia-cốp."));
visuperobject.biblemap.put("b1_32_28", new visuperobject(1,32,28,"Người lại nói: Tên ngươi sẽ chẳng làm Gia-cốp nữa, nhưng tên là Y-sơ-ra-ên, vì ngươi đã có vật lộn cùng Ðức Chúa Trời và người ta; ngươi đều được thắng."));
visuperobject.biblemap.put("b1_32_29", new visuperobject(1,32,29,"Gia-cốp hỏi: Xin cho tôi biết tên người. Ðáp rằng: Làm sao ngươi hỏi tên ta? Rồi người nầy ban phước cho Gia-cốp tại đó."));
visuperobject.biblemap.put("b1_32_30", new visuperobject(1,32,30,"Gia-cốp đặt tên chỗ đó là Phê-ni-ên, và nói rằng: Tôi đã thấy Ðức Chúa Trời đối mặt cùng tôi và linh hồn tôi được giải cứu."));
visuperobject.biblemap.put("b1_32_31", new visuperobject(1,32,31,"Khi qua nơi Phi-ni-ên, thấy mặt trời mọc rồi; và người đi giẹo cẳng."));
visuperobject.biblemap.put("b1_32_32", new visuperobject(1,32,32,"Bởi cớ đó, cho đến ngày nay dân Y-sơ-ra-ên chẳng bao giờ ăn gân bắp vế của lối xương hông; vì người đó có đánh vào xương hông Gia-cốp, nơi gân của bắp vế."));
visuperobject.biblemap.put("b1_33_1", new visuperobject(1,33,1,"Gia-cốp nhướng mắt lên và nhìn, kìa, Ê-sau dẫn bốn trăm người đi đến. Người bèn chia các con cho Lê-a, Ra-chên, và cho hai tên đòi;"));
visuperobject.biblemap.put("b1_33_2", new visuperobject(1,33,2,"sắp hai tên đòi và con cái họ ở trước; kế đến Lê-a và con cái nàng; Ra-chên cùng Giô-sép ở sau chót."));
visuperobject.biblemap.put("b1_33_3", new visuperobject(1,33,3,"Còn người, thì đi trước họ và sấp mình xuống đất bảy lần cho đến khi tới gần anh mình."));
visuperobject.biblemap.put("b1_33_4", new visuperobject(1,33,4,"Nhưng Ê-sau chạy đến trước mặt người, ôm choàng cổ mà hôn, rồi hai anh em đều khóc."));
visuperobject.biblemap.put("b1_33_5", new visuperobject(1,33,5,"Ðoạn, Ê-sau nhướng mắt lên thấy mấy người đờn bà và con cái, thì hỏi rằng: Các người mà em có đó là ai? Ðáp rằng: Ấy là con cái mà Ðức Chúa Trời đã cho kẻ tôi tớ anh."));
visuperobject.biblemap.put("b1_33_6", new visuperobject(1,33,6,"Hai tên đòi và con cái họ lại gần người, sấp mình xuống."));
visuperobject.biblemap.put("b1_33_7", new visuperobject(1,33,7,"Lê-a và các con cái nàng cũng lại gần, sấp mình xuống; đoạn, Ra-chên và Giô-sép lại gần, và sấp mình xuống."));
visuperobject.biblemap.put("b1_33_8", new visuperobject(1,33,8,"Ê-sau hỏi: Toán quân anh đã gặp đi trước đó, em tính làm chi? Ðáp rằng: Ấy để nhờ được ơn trước mặt chúa tôi."));
visuperobject.biblemap.put("b1_33_9", new visuperobject(1,33,9,"Ê-sau nói: Hỡi em! anh đã được đủ rồi; vậy, hãy lấy lại vật chi của em đi."));
visuperobject.biblemap.put("b1_33_10", new visuperobject(1,33,10,"Thưa rằng: Xin anh, không. Nếu em được ơn trước mặt anh, hãy nhậm lấy lễ vật bởi tay em làm ra đi; vì em thấy được mặt anh khác nào người ta thấy được mặt Ðức Chúa Trời, và anh đã đẹp lòng tiếp rước em."));
visuperobject.biblemap.put("b1_33_11", new visuperobject(1,33,11,"Xin anh hãy nhậm lấy lễ vật em đã dâng cho anh, vì Ðức Chúa Trời cho em đầy dẫy ân huệ, và em có đủ hết. Người nài xin Ê-sau quá đến đỗi phải chịu nhậm lấy."));
visuperobject.biblemap.put("b1_33_12", new visuperobject(1,33,12,"Ê-sau nói: Hè, ta hãy lên đường! Anh sẽ đi trước em."));
visuperobject.biblemap.put("b1_33_13", new visuperobject(1,33,13,"Gia-cốp đáp: Chúa biết rằng các đứa trả yếu lắm, và em lại mắc coi chừng chiên và bò cái đương có con bú; nếu ép đi mau chỉ trong một ngày, chắc cả bầy phải chết hết."));
visuperobject.biblemap.put("b1_33_14", new visuperobject(1,33,14,"Xin chúa hãy đi trước kẻ tôi tớ chúa, còn tôi sẽ đi tới chậm chậm theo bước một của súc vật đi trước và của các trẻ, cho đến chừng nào tới nhà chúa tại xứ Sê -i-rơ."));
visuperobject.biblemap.put("b1_33_15", new visuperobject(1,33,15,"Ê-sau nói: Vậy, anh xin để lại cùng em một vài người tùy tùng của anh. Gia-cốp đáp rằng: Chi vậy? miễn em được nhờ ơn trước mặt chúa thôi!"));
visuperobject.biblemap.put("b1_33_16", new visuperobject(1,33,16,"Nội ngày đó, Ê-sau bắt đường trở về Sê -i-rơ."));
visuperobject.biblemap.put("b1_33_17", new visuperobject(1,33,17,"Gia-cốp đi đến Su-cốt; bèn cất một nhà cho mình ở, và mấy cái lều cho súc vật; cho nên họ đặt tên chốn nầy là Su-cốt."));
visuperobject.biblemap.put("b1_33_18", new visuperobject(1,33,18,"Gia-cốp ở xứ Pha-ran-a-ram đến thành Si-chem thuộc về xứ Ca-na-an, được bình an. Người đóng tại trước thành,"));
visuperobject.biblemap.put("b1_33_19", new visuperobject(1,33,19,"bèn mua miếng đất chỗ người đóng trại, giá một trăm miếng bạc của con Hê-mô, cha Si-chem."));
visuperobject.biblemap.put("b1_33_20", new visuperobject(1,33,20,"Nơi đó người lập một bàn thờ, đặt trên là En-Eân-ô-hê -Y-sơ-ra-ên."));
visuperobject.biblemap.put("b1_34_1", new visuperobject(1,34,1,"Nàng Ði-na, con gái của Lê-a sanh cho Gia-cốp, đi ra thăm bọn con gái của xứ đó."));
visuperobject.biblemap.put("b1_34_2", new visuperobject(1,34,2,"Si-chem, con trai Hê-mô, người Hê-vít, hoàng tử xứ đó, thấy nàng, thì cướp đi, nằm với nàng và làm điếm nhục nàng."));
visuperobject.biblemap.put("b1_34_3", new visuperobject(1,34,3,"Tâm hồn chàng vấn-vít cùng Ði-na, con gái Gia-cốp, thương mến con gái tơ đó, lấy lời ngon ngọt nói cùng nàng."));
visuperobject.biblemap.put("b1_34_4", new visuperobject(1,34,4,"Si-chem nói cùng Hê-mô, cha mình, rằng: Hãy cho tôi con gái nầy làm vợ."));
visuperobject.biblemap.put("b1_34_5", new visuperobject(1,34,5,"Vả, Gia-cốp hay được rằng chàng làm hư danh giá Ði-na, con gái mình, nhưng các con trai mình mắc ở ngoài đồng cùng súc vật, nên người làm thinh đến khi họ trở về."));
visuperobject.biblemap.put("b1_34_6", new visuperobject(1,34,6,"Lúc đó, Hê-mê, cha Si-chem, đến cùng Gia-cốp đặng nói chuyện cùng người."));
visuperobject.biblemap.put("b1_34_7", new visuperobject(1,34,7,"Các con trai Gia-cốp ở ngoài đồng trở về, vừa hay được việc ấy, bèn nổi nóng và giận lắm, vì việc ô danh mà Si-chem đã xui cho họ Y-sơ-ra-ên, nằm cùng con gái của Gia-cốp, là việc chẳng bao giờ nên làm."));
visuperobject.biblemap.put("b1_34_8", new visuperobject(1,34,8,"Hê-mô nói cùng họ rằng: Tâm hồn Si-chem, con trai tôi, đã dan-díu cùng con gái các ông; xin hãy gả nàng cho nó làm vợ."));
visuperobject.biblemap.put("b1_34_9", new visuperobject(1,34,9,"Hãy kết sui-gia cùng nhau; các ông gả con gái cho chúng tôi, rồi cưới các con gái chúng tôi lại."));
visuperobject.biblemap.put("b1_34_10", new visuperobject(1,34,10,"Hãy ở cùng chúng tôi; xứ sẵn dành cho các ông; hãy ở, buôn bán và dựng cơ nghiệp tại đó."));
visuperobject.biblemap.put("b1_34_11", new visuperobject(1,34,11,"Si-chem thưa cùng cha và các anh em của người con gái rằng: Xin cho tôi được ơn trước mặt các ông, và điều chi các ông nói, tôi xin nạp cho."));
visuperobject.biblemap.put("b1_34_12", new visuperobject(1,34,12,"Xin hãy đòi một lễ cưới cho lớn, sính nghi cho cao, tôi xin nạp theo y lời các ông nói; nhưng hãy gả con gái đó cho tôi làm vợ."));
visuperobject.biblemap.put("b1_34_13", new visuperobject(1,34,13,"Các con trai Gia-cốp đáp lại cùng Si-chem và Hê-mô, cha chàng, cùng dùng mưu nói chuyện với họ, vì Si-chem đã làm mất danh giá của Ði-na, em gái mình."));
visuperobject.biblemap.put("b1_34_14", new visuperobject(1,34,14,"Các người ấy nói rằng: Gả em gái chúng tôi cho ai chẳng chịu phép cắt bì, ấy là một việc chúng tôi làm không được, vì sẽ là một nỗi nhuốc nha cho chúng tôi vậy."));
visuperobject.biblemap.put("b1_34_15", new visuperobject(1,34,15,"Chúng tôi sẽ nhậm lời các ông được, là khi nào các ông trở nên giống như chúng tôi, tức là mọi người nam chịu phép cắt bì;"));
visuperobject.biblemap.put("b1_34_16", new visuperobject(1,34,16,"vậy, chúng tôi sẽ gả con gái chúng tôi cho các ông, và sẽ cưới con gái các ông lại; đoạn ta sẽ ở chung nhau, thành ra một dân mà thôi."));
visuperobject.biblemap.put("b1_34_17", new visuperobject(1,34,17,"Còn nếu không khứng nghe theo lời, và không chịu phép cắt bì, thì chúng tôi bắt con gái lại và đi ở nơi khác."));
visuperobject.biblemap.put("b1_34_18", new visuperobject(1,34,18,"Bao nhiêu lời họ nói làm cho đẹp dạ Hê-mô và Si-chem, con trai người."));
visuperobject.biblemap.put("b1_34_19", new visuperobject(1,34,19,"Chàng trẻ tuổi đó không ngần ngại các việc họ đã định, vì con gái của Gia-cốp đẹp lòng chàng. Vả, chàng đây là một người quí trọng hơn mọi người trong nhà cha mình."));
visuperobject.biblemap.put("b1_34_20", new visuperobject(1,34,20,"Vậy, Hê-mô và Si-chem, con trai người, đi đến cửa thành mình, nói cùng dân trong thành như vầy:"));
visuperobject.biblemap.put("b1_34_21", new visuperobject(1,34,21,"Các người đó ăn ở hòa thuận giữa vòng chúng ta; vậy cho họ ở trong xứ đặng buôn bán. Nầy, xứ cũng rộng cho họ ở; chúng ta sẽ cưới con gái họ làm vợ, và gả các con gái chúng ta lại."));
visuperobject.biblemap.put("b1_34_22", new visuperobject(1,34,22,"Nhưng họ bằng lòng ở cùng chúng ta đặng thành một dân, miễn khi nào các người nam trong bọn mình phải chịu phép cắt bì chính như mọi người nam của họ vậy."));
visuperobject.biblemap.put("b1_34_23", new visuperobject(1,34,23,"Thế thì, há rằng các súc vật, các tài sản và các bầy của họ sẽ chẳng thuộc về chúng ta sao? thôi, ta hãy nhận theo họ điều đó đi, để họ ở chung cùng chúng ta."));
visuperobject.biblemap.put("b1_34_24", new visuperobject(1,34,24,"Mọi người đi ra cửa thành mình đều nghe theo lời Hê-mô và Si-chem, con trai người, và hết thảy người nam đi ra cửa thành mình đều chịu phép cắt bì."));
visuperobject.biblemap.put("b1_34_25", new visuperobject(1,34,25,"Ðến ngày thứ ba, khi mọi người đương đau đớn, thì hai con trai Gia-cốp, là Si-mê-ôn và Lê-vi, anh nàng Ði-na, cầm mỗi người một thanh gươm, thừa khi ai nấy yên ổn, xông vào thành giết hết thảy người nam."));
visuperobject.biblemap.put("b1_34_26", new visuperobject(1,34,26,"Hai người lại lấy gươm giết Hê-mô và Si-chem, con trai người, đem nàng Ði-na ra khỏi nhà Si-chem, rồi đi."));
visuperobject.biblemap.put("b1_34_27", new visuperobject(1,34,27,"Vì cớ họ làm mất danh giá em gái mình, nên các con trai Gia-cốp xông vào những xác chết và phá cướp thành;"));
visuperobject.biblemap.put("b1_34_28", new visuperobject(1,34,28,"bắt các bầy chiên, bò, lừa, cùng vật chi trong thành và ngoài đồng;"));
visuperobject.biblemap.put("b1_34_29", new visuperobject(1,34,29,"cướp đem đi hết tài vật, các con trẻ và đờn bà, cùng mọi vật chi ở trong nhà vậy."));
visuperobject.biblemap.put("b1_34_30", new visuperobject(1,34,30,"Gia-cốp bèn nói cùng Si-mê-ôn và Lê-vi rằng: Bay xui cho tao bối rối, làm cho dân xứ nầy, là người Ca-na-an và người Phê-rê-sít, oán ghét tao vậy. Tao đây, chỉ có ít người; nếu họ hiệp lại đánh tao, thì chắc tao và nội nhà đều bị tàn hại."));
visuperobject.biblemap.put("b1_34_31", new visuperobject(1,34,31,"Ðáp rằng: Chúng tôi nỡ chịu người ta đãi em gái chúng tôi như con đĩ sao?"));
visuperobject.biblemap.put("b1_35_1", new visuperobject(1,35,1,"Ðức Chúa Trời phán cùng Gia-cốp rằng: Hãy chổi dậy, đi lên Bê-tên mà ở, và hãy lập nơi đó một bàn thờ cho Ðức Chúa Trời, là Ðấng hiện ra cùng ngươi đương lúc chạy trốn khỏi Ê-sau, anh ngươi."));
visuperobject.biblemap.put("b1_35_2", new visuperobject(1,35,2,"Gia-cốp bèn nói cùng vợ con và mọi kẻ đi theo mình rằng: Hãy dẹp các tượng thần ngoại bang khỏi giữa các ngươi, làm cho mình được thanh sạch và thay áo xống đi."));
visuperobject.biblemap.put("b1_35_3", new visuperobject(1,35,3,"Ðoạn, chúng ta hãy chổi dậy, đi đến Bê-tên, nơi đó ta sẽ lập một bàn thờ cho Ðức Chúa Trời, là Ðấng đã trả lời ta trong lúc nguy hiểm, và đã phù hộ ta trong khi đi đường."));
visuperobject.biblemap.put("b1_35_4", new visuperobject(1,35,4,"Họ bèn đưa cho Gia-cốp các tượng thần ngoại bang của mình, và các vòng đeo nơi tai; rồi người chôn mấy vật đó dưới gốc cây dẻ bộp, gần thành Si-chem."));
visuperobject.biblemap.put("b1_35_5", new visuperobject(1,35,5,"Ðoạn, chúng khởi hành. Ðức Chúa Trời bèn giáng sự kinh hãi cho các thành ở chung quanh đó, nên họ chẳng dám đuổi theo các con trai của Gia-cốp."));
visuperobject.biblemap.put("b1_35_6", new visuperobject(1,35,6,"Gia-cốp cùng mọi kẻ đi theo đến tại Lu-xơ (là thành Bê-tên), thuộc về xứ Ca-na-an."));
visuperobject.biblemap.put("b1_35_7", new visuperobject(1,35,7,"Người lập tại đó một bàn thờ, đặt tên chốn nầy Eân-Bê-tên, vì là nơi Ðức Chúa Trời đã hiện ra cùng người trong lúc chạy trốn khỏi anh mình."));
visuperobject.biblemap.put("b1_35_8", new visuperobject(1,35,8,"Khi đó Ðê-bô-ra, mụ vú của Rê-be-ca, qua đời, được chôn thân dưới thành Bê-tên tại gốc dẻ bộp; nhơn đó người ta gọi là cây A-lôn-Ba-cút."));
visuperobject.biblemap.put("b1_35_9", new visuperobject(1,35,9,"Khi ở xứ Pha-đan-a-ram đến, Ðức Chúa Trời lại hiện ra cùng Gia-cốp nữa, ban phước cho người,"));
visuperobject.biblemap.put("b1_35_10", new visuperobject(1,35,10,"và phán rằng: Tên ngươi là Gia-cốp, sau nầy sẽ chẳng gọi Gia-cốp nữa, nhưng sẽ đặt là Y-sơ-ra-ên. Rồi Ngài đặt tên người là Y-sơ-ra-ên."));
visuperobject.biblemap.put("b1_35_11", new visuperobject(1,35,11,"Ðức Chúa Trời lại phán rằng: Ta là Ðức Chúa Trời toàn năng; ngươi hãy sanh sản và thêm nhiều; một dân cho đến nhiều dân đều sẽ do nơi ngươi mà sanh, cùng các vua sẽ do nơi mình ngươi mà ra."));
visuperobject.biblemap.put("b1_35_12", new visuperobject(1,35,12,"Xứ ta đã cho Áp-ra-ham và Y-sác thì ta sẽ cho ngươi cùng dòng dõi ngươi."));
visuperobject.biblemap.put("b1_35_13", new visuperobject(1,35,13,"Ðức Chúa Trời bèn ngự lên khỏi nơi Ngài đã phải cùng Gia-cốp."));
visuperobject.biblemap.put("b1_35_14", new visuperobject(1,35,14,"Người dựng một cây trụ đó tại nơi Ðức Chúa Trời đã phán cùng mình, dâng lễ quán và tưới dầu vào."));
visuperobject.biblemap.put("b1_35_15", new visuperobject(1,35,15,"Ðoạn, Gia-cốp đặt tên nơi Ðức Chúa Trời đã phán cùng mình là Bê-tên."));
visuperobject.biblemap.put("b1_35_16", new visuperobject(1,35,16,"Ở từ Bê-tên đi, họ còn cách Eân-phù-hộ-rát chừng vào thôi đường, thì Ra-chên sanh nở. Việc sanh nở của người thật đau đớn."));
visuperobject.biblemap.put("b1_35_17", new visuperobject(1,35,17,"Trong khi khó sanh, bà mụ nói rằng: Ðừng sợ chi, vì nàng còn một con trai nữa."));
visuperobject.biblemap.put("b1_35_18", new visuperobject(1,35,18,"Vả, bởi Ra-chên gần trút linh hồn, vì đương cơn hấp hối, nên người đặt đứa con trai đó tên là Bê-nô-ni; còn cha nó lại đặt tên là Bên-gia-min."));
visuperobject.biblemap.put("b1_35_19", new visuperobject(1,35,19,"Vậy, Ra-chên qua đời, được chôn bên con đường đi về Ê-phơ-rát, tức là Bết-lê-hem."));
visuperobject.biblemap.put("b1_35_20", new visuperobject(1,35,20,"Gia-cốp dựng một mộ-bia; ấy là mộ-bia của Ra-chên đến ngày nay hãy còn di tích."));
visuperobject.biblemap.put("b1_35_21", new visuperobject(1,35,21,"Kế đó, Y-sơ-ra-ên đi, đóng trại ở ngoài nơi tháp Ê-để."));
visuperobject.biblemap.put("b1_35_22", new visuperobject(1,35,22,"Vả, nhầm khi Y-sơ-ra-ên ngụ tại xứ nầy, thì Ru-bên có đến nằm cùng Bi-la, vợ lẽ của cha mình; Y-sơ-ra-ên hay được việc đó."));
visuperobject.biblemap.put("b1_35_23", new visuperobject(1,35,23,"Vả, Gia-cốp có được mười hai con trai. Con của Lê-a là Ru-bên, trưởng nam của Gia-cốp; kế nữa là Si-mê-ôn, Lê-vi, Giu-đa, Y-sa-ca và Sa-bu-lôn."));
visuperobject.biblemap.put("b1_35_24", new visuperobject(1,35,24,"Con của Ra-chên và Giô-sép và Bên-gia-min."));
visuperobject.biblemap.put("b1_35_25", new visuperobject(1,35,25,"Con của Bi-la, đòi của Ra-chên là Ðan và Nép-ta-li;"));
visuperobject.biblemap.put("b1_35_26", new visuperobject(1,35,26,"và con của Xinh-ba, đòi của Lê-a, là Gát và A-se. Ấy đó là các con trai của Gia-cốp sanh tại xứ Pha-đan-a-ram."));
visuperobject.biblemap.put("b1_35_27", new visuperobject(1,35,27,"Gia-cốp đến cùng Y-sác, cha mình, ở Mam-rê, tại thành Ki-ri-át-a-ra-ba, tức là Hếp-rôn, nơi Áp-ra-ham và Y-sác đã kiều ngụ."));
visuperobject.biblemap.put("b1_35_28", new visuperobject(1,35,28,"Y-sác hưởng thọ được một trăm tám mươi tuổi."));
visuperobject.biblemap.put("b1_35_29", new visuperobject(1,35,29,"Người tuổi cao tác lớn, thỏa mãn về đời mình, tắt hơi, được về cùng tổ phụ. Ê-sau và Gia-cốp, hai con trai, chôn người."));
visuperobject.biblemap.put("b1_36_1", new visuperobject(1,36,1,"Ðây là dòng dõi của Ê-sau, tức là Ê-đôm vậy."));
visuperobject.biblemap.put("b1_36_2", new visuperobject(1,36,2,"Ê-sau cưới các vợ trong bọn con gái xứ Ca-na-an, là A-đa, con gái Ê-lôn, người Hê-tít; Ô-hô-li-ba-ma, con gái A-na, cháu ngoại của Xi-bê-ôn, người Hê-vít,"));
visuperobject.biblemap.put("b1_36_3", new visuperobject(1,36,3,"cùng Bách-mát, con gái Ích-ma-ên, và là em gái của Nê-ba-giốt."));
visuperobject.biblemap.put("b1_36_4", new visuperobject(1,36,4,"A-đa sanh Ê-li-pha cho Ê-sau; Bách-mát sanh Rê -u-ên;"));
visuperobject.biblemap.put("b1_36_5", new visuperobject(1,36,5,"còn Ô-hô-li-ba-ma sanh Giê-úc, Gia-lam và Cô-ra. Ấy đó là các con trai của Ê-sau sanh tại xứ Ca-na-an."));
visuperobject.biblemap.put("b1_36_6", new visuperobject(1,36,6,"Ê-sau dẫn các vợ, con trai, con gái mình, mọi người nhà, các bầy chiên, lục-súc, và tài vật mình đã gây dựng tại xứ Ca-na-an, dời qua xứ khác, cách xa Gia-cốp, em mình."));
visuperobject.biblemap.put("b1_36_7", new visuperobject(1,36,7,"Vả, vì cớ hai anh em của cải nhiều, bầy súc vật đông, xứ đương kiều ngụ không đủ đất dùng nữa, nên ở chung nhau chẳng được."));
visuperobject.biblemap.put("b1_36_8", new visuperobject(1,36,8,"Vậy, Ê-sau, tức là Ê-đôm, ở trên núi Sê -i-rơ."));
visuperobject.biblemap.put("b1_36_9", new visuperobject(1,36,9,"Ðây là dòng dõi Ê-sau, tổ phụ dân Ê-đôm, ở trên núi Sê -i-rơ."));
visuperobject.biblemap.put("b1_36_10", new visuperobject(1,36,10,"Ðây là tên các con trai Ê-sau: A-đa-vợ người, sanh Ê-li-pha; Bách-mát, cũng vợ người, sanh Rê -u-ên."));
visuperobject.biblemap.put("b1_36_11", new visuperobject(1,36,11,"Con trai của Ê-li-pha là Thê-ma, Ô-ma, Xê-phô, Ga-tham và Kê-na."));
visuperobject.biblemap.put("b1_36_12", new visuperobject(1,36,12,"Thim-na, vợ lẽ Ê-li-pha, con trai của Ê-sau, sanh A-ma-léc cho người. Ðó là cháu của A-đa, vợ Ê-sau."));
visuperobject.biblemap.put("b1_36_13", new visuperobject(1,36,13,"Còn đây là con trai của Rê -u-ên: Na-hát, Xê-rách, Sam-ma và Mích-xa. Ấy là các cháu của Bách-mát, cùng vợ Ê-sau."));
visuperobject.biblemap.put("b1_36_14", new visuperobject(1,36,14,"Và mấy người nầy là con trai của Ô-lô-li-ba-ma, vợ của Ê-sau, con gái A-na, cháu ngoại Xi-bê-ôn. Nàng sanh cho Ê-sau Giê-úc, Gai-lam, và Cô-ra."));
visuperobject.biblemap.put("b1_36_15", new visuperobject(1,36,15,"Ðây là các trưởng tộc trong vòng con cháu Ê-sau: Các con trai Ê-li-pha con trưởng nam của Ê-sau, là trưởng tộc Thê-man, trưởng tộc Ô-ma, trưởng tộc Xê-phô, trưởng tộc Kê-na,"));
visuperobject.biblemap.put("b1_36_16", new visuperobject(1,36,16,"trưởng tộc A-ma-léc. Ðó là các trưởng tộc do nơi Ê-li-pha sanh tại xứ Ê-đôm, và là các cháu của A-đa."));
visuperobject.biblemap.put("b1_36_17", new visuperobject(1,36,17,"Ðây là các con trai của Rê -u-ên, con Ê-sau: trưởng tộc Na-hát, trưởng tộc Xê-rách, trưởng tộc Sam-ma, và trưởng tộc Mích-xa. Ðó là các trưởng tộc do nơi Rê -u-ên sanh ra tại xứ Ê-đôm, và là các cháu của Bách-mát."));
visuperobject.biblemap.put("b1_36_18", new visuperobject(1,36,18,"Ðây là các con trai của Ô-lo-li-ba-ma, vợ Ê-sau: trưởng tộc Giê-úc, trưởng tộc Gia-lam, và trưởng tộc Cô-ra. Ðó là mấy trưởng tộc do Ô-hô-li-ba-ma, vợ Ê-sau, con gái của A-na, sanh ra."));
visuperobject.biblemap.put("b1_36_19", new visuperobject(1,36,19,"Ấy là các con trai của Ê-sau, tức là Ê-đôm, và đó là mấy trưởng tộc của họ."));
visuperobject.biblemap.put("b1_36_20", new visuperobject(1,36,20,"Ðây là các con trai của Sê -i-rơ, người Hô-rít, thuở xưa ở tại xứ đó: Lô-than, Sô-banh, Xi-bê-ôn, A-na,"));
visuperobject.biblemap.put("b1_36_21", new visuperobject(1,36,21,"Ði-sôn, Ét-xe, và Ði-san. Ðó là mấy con trai của Sê -i-rơ, trưởng tộc dân Hô-rít, ở tại xứ Ê-đôm."));
visuperobject.biblemap.put("b1_36_22", new visuperobject(1,36,22,"Các con trai của Lô-than là Hô-ri và Hê-man; còn Thim-na là em của gái Lô-than."));
visuperobject.biblemap.put("b1_36_23", new visuperobject(1,36,23,"Ðây là các con trai của Sô-banh: Anh-van, Ma-na-hát, Ê-banh, Sê-phô, và Ô-nam."));
visuperobject.biblemap.put("b1_36_24", new visuperobject(1,36,24,"Ðây là các con trai của Xi-bê-ôn: Ai-gia và A-na. Ấy là A-na có tìm đặng suối nước sôi nơi đồng vắng, trong khi chăn lừa cho Xi-bê-ôn, cha nàng."));
visuperobject.biblemap.put("b1_36_25", new visuperobject(1,36,25,"Ðây là con cái của A-na: Ði-sôn và Ô-hô-li-ba-ma, con gái người."));
visuperobject.biblemap.put("b1_36_26", new visuperobject(1,36,26,"Ðây là con trai của Ði-sôn: Hem-đan, Ếch-ban, Dít-ran và Kê-ran."));
visuperobject.biblemap.put("b1_36_27", new visuperobject(1,36,27,"Ðây là con trai của Ét-xe: Binh-han, Xa-van và A-can."));
visuperobject.biblemap.put("b1_36_28", new visuperobject(1,36,28,"Ðây là con trai của Ði-san: Uùt-sơ và A-ran."));
visuperobject.biblemap.put("b1_36_29", new visuperobject(1,36,29,"Còn đây là các trưởng tộc dân Hô-rít: trưởng tộc Lô-than, trưởng tộc Sô-banh, trưởng tộc Xi-bê-ôn, trưởng tộc A-na,"));
visuperobject.biblemap.put("b1_36_30", new visuperobject(1,36,30,"trưởng tộc Ði-sôn, trưởng tộc Ét-xe, và trưởng tộc Ði-san. Ðó là các trưởng tộc của dân Hô-rít, ở tại xứ Sê -i-rơ."));
visuperobject.biblemap.put("b1_36_31", new visuperobject(1,36,31,"Trước khi dân Y-sơ-ra-ên chưa có một vua nào cai trị, thì đây là các vua trị vì xứ Ê-đôm;"));
visuperobject.biblemap.put("b1_36_32", new visuperobject(1,36,32,"Bê-la, con trai Bê-ô, trì vì xứ Ê-đôm; tên thành người là Ðin-ha-ba."));
visuperobject.biblemap.put("b1_36_33", new visuperobject(1,36,33,"Vua Bê-la băng, Giô-báp, con trai Xê-rách, người Bốt-ra, lên kế vị."));
visuperobject.biblemap.put("b1_36_34", new visuperobject(1,36,34,"Vua Giô-báp băng, Hu-sam, người xứ Thê-man, lên kế vị."));
visuperobject.biblemap.put("b1_36_35", new visuperobject(1,36,35,"Vua Hu-sam băng, Ha-đát, con trai Bê-đát, lên kế vị. Ấy là vua nầy đánh thắng dân Ma-đi-an tại đồng Mô-áp; và tên thành người là A-vít."));
visuperobject.biblemap.put("b1_36_36", new visuperobject(1,36,36,"Vua Ha-đát băng, Sam-la, người Ma-rê-ca lên kế vị."));
visuperobject.biblemap.put("b1_36_37", new visuperobject(1,36,37,"Vua Sam-la băng, Sau-lơ, người Rê-hô-bốt ở trên mé sông, lên kế vị."));
visuperobject.biblemap.put("b1_36_38", new visuperobject(1,36,38,"Vua Sau-lơ băng, Ba-anh-Ha-man, con trai Aïc-bồ, lên kế vị."));
visuperobject.biblemap.put("b1_36_39", new visuperobject(1,36,39,"vua Ba-anh-Ha-nan băng, Ha-đa lên kế vị. Tên thành người là Ba -u; vợ người là Mê-hê-ta-bê-ên, con gái của Mát-rết, cháu ngoại Mê-xa-háp."));
visuperobject.biblemap.put("b1_36_40", new visuperobject(1,36,40,"Ðây là trưởng tộc do nơi Ê-sau sanh ra, kể theo chi tộc, địa hạt và tên mình: trưởng tộc Thim-na, trưởng tộc Anh-va, trưởng tộc Giê-hết,"));
visuperobject.biblemap.put("b1_36_41", new visuperobject(1,36,41,"trưởng tộc Ô-hô-li-ba-ma, trưởng tộc Ê-la, trưởng tộc Phi-nôn,"));
visuperobject.biblemap.put("b1_36_42", new visuperobject(1,36,42,"trưởng tộc Kê-na, trưởng tộc Thê-man, trưởng tộc Mép-xa,"));
visuperobject.biblemap.put("b1_36_43", new visuperobject(1,36,43,"trưởng tộc Mác-đi-ên à trưởng tộc Y-ram. Ðó là các trưởng tộc của Ê-đôm, tùy theo nhà ở trong xứ của họ. Tổ phụ của dân Ê-đôm là Ê-sau."));
visuperobject.biblemap.put("b1_37_1", new visuperobject(1,37,1,"Gia-cốp, tại xứ của cha mình đã kiều ngụ, là xứ Ca-na-an."));
visuperobject.biblemap.put("b1_37_2", new visuperobject(1,37,2,"Ðây là chuyện dòng dõi của Gia-cốp. Giô-sép, tuổi mười bảy, chăn chiên với các anh mình; người đi kết bạn cùng con trai Bi-la và con Xinh-ba, hai vợ cha mình. Giô-sép thuật lại với cha các chuyện xấu của họ nói."));
visuperobject.biblemap.put("b1_37_3", new visuperobject(1,37,3,"Vả, Y-sơ-ra-ên thương yêu Giô-sép hơn những con trai khác, vì là con muộn mình, nên may cho chàng một cái áo dài có nhiều sắc."));
visuperobject.biblemap.put("b1_37_4", new visuperobject(1,37,4,"Các anh thấy cha thương Giô-sép hơn bọn mình, bèn sanh lòng ganh ghét, chẳng có thế lấy lời tử tế nói cùng chàng được."));
visuperobject.biblemap.put("b1_37_5", new visuperobject(1,37,5,"Giô-sép có nằm chiêm bao, thuật lại cho các anh mình nghe; họ càng thêm ganh ghét nữa."));
visuperobject.biblemap.put("b1_37_6", new visuperobject(1,37,6,"Người nói rằng: Tôi có điềm chiêm bao, xin các anh hãy nghe tôi thuật:"));
visuperobject.biblemap.put("b1_37_7", new visuperobject(1,37,7,"Chúng ta đương ở ngoài đồng bó lúa, nầy bó lúa tôi đứng dựng lên, còn bó lúa của các anh đều đến ở chung quanh, sấp mình xuống trước bó lúa tôi."));
visuperobject.biblemap.put("b1_37_8", new visuperobject(1,37,8,"Các anh người bèn đáp rằng: Vậy, mầy sẽ cai trị chúng ta sao? mầy sẽ hành-quyền trên chúng ta sao? Họ càng ganh ghét hơn nữa, vì điềm chiêm bao và lời nói của chàng."));
visuperobject.biblemap.put("b1_37_9", new visuperobject(1,37,9,"Giô-sép lại nằm chiêm bao nữa, thuật cùng các anh mình rằng: Tôi còn một điềm chiêm bao nữa: Nầy mặt trời, mặt trăng, và mười một ngôi sao đều sấp mình xuống trước mặt tôi!"));
visuperobject.biblemap.put("b1_37_10", new visuperobject(1,37,10,"Chàng thuật chiêm bao đó lại cho cha và các anh mình nghe; nhưng cha chàng quở mà hỏi rằng: Ðiềm chiêm bao của mầy đó có nghĩa chi? Có phải tao, mẹ, và các anh mầy đều phải đến sấp mình xuống đất trước mặt mầy chăng?"));
visuperobject.biblemap.put("b1_37_11", new visuperobject(1,37,11,"Các anh lấy làm ganh ghét chàng; còn cha lại ghi nhớ lấy điều đó."));
visuperobject.biblemap.put("b1_37_12", new visuperobject(1,37,12,"Các anh chàng đi chăn chiên của cha tại Si-chem."));
visuperobject.biblemap.put("b1_37_13", new visuperobject(1,37,13,"Y-sơ-ra-ên nói cùng Giô-sép rằng: Các anh con có chăn bầy chiên tại Si-chem chăng? Lại đây, đặng cha sai con đi đến chúng nó. Giô-sép đáp rằng: Có con đây."));
visuperobject.biblemap.put("b1_37_14", new visuperobject(1,37,14,"Y-sơ-ra-ên biểu rằng: Con hãy đi, xem thử các anh con ra sao, và các bầy chiên thể nào; rồi đem tin về cho cha hay. Vậy, từ trũng Hếp-rôn, người sai Giô-sép đi đến Si-chem."));
visuperobject.biblemap.put("b1_37_15", new visuperobject(1,37,15,"Có một người gặp chàng đi lạc trong đồng ruộng, liền hỏi và nói cùng chàng rằng: Ngươi tìm chi?"));
visuperobject.biblemap.put("b1_37_16", new visuperobject(1,37,16,"Ðáp rằng: Tôi tìm các anh tôi. Xin hãy chỉ giùm tôi họ chăn bầy chiên nơi nào."));
visuperobject.biblemap.put("b1_37_17", new visuperobject(1,37,17,"Người nói: Các anh ngươi ở đây đi rồi, vì tôi có nghe họ nói rằng: Ta hãy đi đến Ðô-ta-in. Vậy, Giô-sép, đi tìm các anh mình, gặp được tại Ðô-ta-in."));
visuperobject.biblemap.put("b1_37_18", new visuperobject(1,37,18,"Các anh thấy chàng đi ở đàng xa, chưa đến gần, thì đã lập mưu để giết chàng."));
visuperobject.biblemap.put("b1_37_19", new visuperobject(1,37,19,"Chúng bèn nói nhau rằng: Kìa, thằng nằm mộng đến kia!"));
visuperobject.biblemap.put("b1_37_20", new visuperobject(1,37,20,"Bây giờ, nào! chúng ta hãy giết nó đi, quăng xuống một cái hố nước nào đó; đoạn ta sẽ nói rằng nó đã bị thú rừng xé ăn, rồi sẽ xem các điềm chiêm bao của nó ra sao."));
visuperobject.biblemap.put("b1_37_21", new visuperobject(1,37,21,"Ru-bên nghe qua lời đó, liền giải cứu chàng ra khỏi ra các anh mà rằng: Chúng ta đừng giết chết nó;"));
visuperobject.biblemap.put("b1_37_22", new visuperobject(1,37,22,"lại tiếp: Chúng ta chớ nên làm đổ máu ra; hãy liệng nó trong cái hố nước nơi đồng vắng kia, và đừng tra tay vào mình nó. Người nói vậy, có ý muốn giải cứu chàng khỏi tay họ, để trả chàng về cùng cha mình."));
visuperobject.biblemap.put("b1_37_23", new visuperobject(1,37,23,"Vừa khi Giô-sép đến gần các anh, họ bèn lột áo chàng, là áo có nhiều sắc đương mặc trong mình;"));
visuperobject.biblemap.put("b1_37_24", new visuperobject(1,37,24,"rồi, bắt chàng đem quăng trong hố nước. Vả, hố khô cạn, chẳng có nước."));
visuperobject.biblemap.put("b1_37_25", new visuperobject(1,37,25,"Ðoạn, họ ngồi lại ăn. Nhướng mắt lên chợt thấy một đoàn dân Ích-ma-ên ở phía Ga-la-át đi đến, trên lưng lạc đà họ chở đầy thuốc thơm, nhũ hương và một dược đương đem đi đến xứ Ê-díp-tô."));
visuperobject.biblemap.put("b1_37_26", new visuperobject(1,37,26,"Giu-đa bèn nói cùng các anh em rằng: Giết em ta mà giấu máu nó, có dùng cho ta được việc chi?"));
visuperobject.biblemap.put("b1_37_27", new visuperobject(1,37,27,"Hè! hãy đem bán nó cho dân Ích-ma-ên và đừng tra tay vào mình nó; vì nó là em, là cốt nhục của chúng ta. Các anh em đều nghe theo lời người nói."));
visuperobject.biblemap.put("b1_37_28", new visuperobject(1,37,28,"Vả, khi những lái-buôn Ma-đi-an đi ngang qua, các anh kéo Giô-sép lên khỏi hố nước, rồi bán chàng giá hai mươi miếng bạc cho dân Ích-ma-ên đem qua xứ Ê-díp-tô."));
visuperobject.biblemap.put("b1_37_29", new visuperobject(1,37,29,"Ru-bên trở lại hố; nầy Giô-sép đâu mất rồi; bèn xé áo mình,"));
visuperobject.biblemap.put("b1_37_30", new visuperobject(1,37,30,"trở lại nơi các em mình mà rằng: Ôi! đứa trẻ đâu mất rồi! còn tôi, sẽ đi đâu."));
visuperobject.biblemap.put("b1_37_31", new visuperobject(1,37,31,"Các anh giết một con dê đực, rồi lấy áo Giô-sép nhúng vào trong máu;"));
visuperobject.biblemap.put("b1_37_32", new visuperobject(1,37,32,"đoạn, gởi cái áo nhiều sắc đó đem về cho cha mình và dặn nói rằng: Ðây là vật chúng tôi đã kiếm được. Vậy, xin hãy nhìn coi, có phải áo của con trai cha chăng."));
visuperobject.biblemap.put("b1_37_33", new visuperobject(1,37,33,"Gia-cốp nhìn và nói rằng: Ấy là áo của con trai ta đó; một thú dữ đã xé cấu nó! Quả thật Giô-sép đã bị phân thây rồi!"));
visuperobject.biblemap.put("b1_37_34", new visuperobject(1,37,34,"Người xé quần áo mình ra, lấy bao quấn ngang hông, và để tang lâu ngày cho con trai mình."));
visuperobject.biblemap.put("b1_37_35", new visuperobject(1,37,35,"Hết thảy con trai, con gái hiệp lại an ủy người, nhưng không chịu; bèn nói rằng: Ta để tang luôn xuống chốn âm phủ cùng con ta! Ấy đó, cha Giô-sép khóc than chàng như vậy."));
visuperobject.biblemap.put("b1_37_36", new visuperobject(1,37,36,"Các lái buôn Ma-đi-an đem Giô-sép đến xứ Ê-díp-tô, bán cho Phô-ti-pha, quan thị vệ của Pha-ra-ôn."));
visuperobject.biblemap.put("b1_38_1", new visuperobject(1,38,1,"Trong lúc đó, Giu-đa, lìa xa các anh em mình, đến ở cùng một người A-đu-lam, tên là Hi-ra."));
visuperobject.biblemap.put("b1_38_2", new visuperobject(1,38,2,"Tại đó, Giu-đa thấy con gái của một người Ca-na-an, tên là Su-a, bèn cưới làm vợ, và ăn ở cùng nàng."));
visuperobject.biblemap.put("b1_38_3", new visuperobject(1,38,3,"Nàng thọ thai, sanh được một con trai, đặt tên là Ê-rơ."));
visuperobject.biblemap.put("b1_38_4", new visuperobject(1,38,4,"Nàng thọ thai nữa, sanh được một con trai đặt tên là Ô-nan;"));
visuperobject.biblemap.put("b1_38_5", new visuperobject(1,38,5,"lại sanh thêm một con trai, đặt tên là Sê-la. Vả, lúc vợ sanh sản, thì Giu-đa ngụ tại Kê-xíp."));
visuperobject.biblemap.put("b1_38_6", new visuperobject(1,38,6,"Giu-đa cưới cho Ê-rơ, con trưởng nam, một người vợ tên là Ta-ma."));
visuperobject.biblemap.put("b1_38_7", new visuperobject(1,38,7,"Nhưng Ê-rơ độc ác trước mặt Ðức Giê-hô-va, nên Ngài giết người đi."));
visuperobject.biblemap.put("b1_38_8", new visuperobject(1,38,8,"Giu-đa bèn biểu Ô-nan rằng: Con hãy lại gần vợ anh con, kết bạn cùng nàng như em chồng, đặng nối dòng dõi cho anh."));
visuperobject.biblemap.put("b1_38_9", new visuperobject(1,38,9,"Ô-nan biết rằng dòng dõi nầy sẽ chẳng thuộc về mình, nên đương khi đến cùng nàng, thì làm rơi rớt xuống đất, để đừng sanh dòng dõi cho anh."));
visuperobject.biblemap.put("b1_38_10", new visuperobject(1,38,10,"Nhưng điều người làm vậy không đẹp lòng Ðức Giê-hô-va, nên Ngài cũng giết người luôn đi."));
visuperobject.biblemap.put("b1_38_11", new visuperobject(1,38,11,"Ðoạn, Giu-đa biểu Ta-ma, dâu mình, rằng: Hãy về ở góa bên nhà cha con, cho đến chừng nào Sê-la, con trai cha, sẽ trở nên khôn lớn. Vì người nói rằng: Chúng ta hãy coi chừng, e khi nó cũng chết như hai anh nó chăng. Ta-ma bèn đi về ở nhà cha mình."));
visuperobject.biblemap.put("b1_38_12", new visuperobject(1,38,12,"Ngày qua tháng lại, con gái của Su-a, vợ Giu-đa, qua đời. Khi đã nguôi lòng, thì Giu-đa đi cùng bạn là Hi-ra, người A-đu-lam, lên đến nơi mấy thợ hớt lông chiên mình, tại Thim-na."));
visuperobject.biblemap.put("b1_38_13", new visuperobject(1,38,13,"Người ta cho nàng Ta-ma hay điều đó mà rằng: Nầy, ông gia ngươi đi khiến Thim-na đặng hớt lông chiên."));
visuperobject.biblemap.put("b1_38_14", new visuperobject(1,38,14,"Nàng bèn cổi áo góa bụa mình lại, rồi ngồi trước cửa thành Ê-na-im, bên con đường đi về Thim-na; làm vậy là vì nàng đã thấy Sê-la khôn lớn rồi, nhưng họ không gả mình cho chàng làm vợ."));
visuperobject.biblemap.put("b1_38_15", new visuperobject(1,38,15,"Giu-đa thấy nàng, cho là một kỵ nữ, vì nàng che mặt,"));
visuperobject.biblemap.put("b1_38_16", new visuperobject(1,38,16,"bèn lại gần mà nói rằng: Hãy cho ta đến cùng nàng; vì cớ ngươi không biết nàng là dâu mình. Nàng đáp rằng: Ngươi sẽ cho tôi món chi đặng đi đến cùng tôi?"));
visuperobject.biblemap.put("b1_38_17", new visuperobject(1,38,17,"Ðáp rằng: Ta sẽ gởi cho nàng một con dê con của bầy ta. Nàng hỏi: Người sẽ cho tôi một của chi làm tin, cho đến chừng nào sẽ gởi con dê?"));
visuperobject.biblemap.put("b1_38_18", new visuperobject(1,38,18,"Ðáp rằng: Ta sẽ cho nàng của chi làm tin bây giờ? Nàng đáp: Con dầu, dây và cây gậy của người đương cầm nơi tay. Người liền cho, đi lại cùng nàng; nàng thọ thai vì người vậy. Ðoạn, nàng đứng dậy mà đi,"));
visuperobject.biblemap.put("b1_38_19", new visuperobject(1,38,19,"cổi lúp ra và mặc quần áo góa bụa lại."));
visuperobject.biblemap.put("b1_38_20", new visuperobject(1,38,20,"Giu-đa cậy bạn A-đu-lam mình đem cho con dê con đặng chuộc mấy vật tin nơi tay người đờn bà nầy; nhưng tìm nàng chẳng thấy."));
visuperobject.biblemap.put("b1_38_21", new visuperobject(1,38,21,"Bạn bèn hỏi dân tại nơi nàng ở rằng: Người kỵ nữ khi trước ở ngoài đường nơi cửa thành Ê-na-im đâu rồi? Họ đáp rằng: Ở đây chẳng có một kỵ nữ nào hết."));
visuperobject.biblemap.put("b1_38_22", new visuperobject(1,38,22,"Bạn bèn trở về Giu-đa mà rằng: Tôi kiếm nàng không đặng; và dân nơi đó có nói: ở đây chẳng có một kỵ nữ nào."));
visuperobject.biblemap.put("b1_38_23", new visuperobject(1,38,23,"Giu-đa rằng: Nầy, tôi đã gởi dê con đến, mà bạn chẳng thấy nàng; vậy, để cho nàng giữ lấy của tin đó, chẳng nên gây điều nhơ nhuốc cho chúng ta."));
visuperobject.biblemap.put("b1_38_24", new visuperobject(1,38,24,"Cách chừng ba tháng sau, người ta có học lại cùng Giu-đa rằng: Ta-ma, dâu ngươi, đã làm kỵ nữ, và vì nông nổi đó, nàng đã hoang-thai. Giu-đa đáp: Hãy đem nó ra thiêu đi."));
visuperobject.biblemap.put("b1_38_25", new visuperobject(1,38,25,"Ðương khi người ta đem nàng ra, thì nàng sai đến nói cùng ông gia rằng: Xin cha hãy nhìn lại con dấu, dây và gậy nầy là của ai. Tôi thọ thai do nơi người mà có các vật nầy."));
visuperobject.biblemap.put("b1_38_26", new visuperobject(1,38,26,"Giu-đa nhìn biết mấy món đó, bèn nói rằng: Nàng phải hơn ta, vì ta không đưa Sê-la, con trai ta, cho nàng. Ðoạn, người không ăn ở cùng nàng nữa."));
visuperobject.biblemap.put("b1_38_27", new visuperobject(1,38,27,"Ðến kỳ sanh nở, nầy trong bụng nàng có thai đôi."));
visuperobject.biblemap.put("b1_38_28", new visuperobject(1,38,28,"Ðương lúc sanh nở, một trong hai đứa đưa tay ra trước; bà mụ bắt lấy tay buộc một sợi chỉ điều, và nói rằng: Ðứa nầy ra trước."));
visuperobject.biblemap.put("b1_38_29", new visuperobject(1,38,29,"Nhưng nó rút tay vào, thì đây, anh nó lại ra. Bà mụ nói rằng: Mầy, tông rách dường nầy! Họ đặt tên là Phê-rết."));
visuperobject.biblemap.put("b1_38_30", new visuperobject(1,38,30,"Kế em nó ra sau, là đứa nơi tay có sợi chỉ điều, và họ đặt tên là Sê-rách."));
visuperobject.biblemap.put("b1_39_1", new visuperobject(1,39,1,"Vả, Giô-sép bị đem qua xứ Ê-díp-tô, thì Phô-ti-pha, người bổn xứ, làm quan thị vệ của Pha-ra-ôn, mua chàng nơi thay dân Ích-ma-ên đã dẫn đến."));
visuperobject.biblemap.put("b1_39_2", new visuperobject(1,39,2,"Giô-sép ở trong nhà chủ mình, là người Ê-díp-tô, được Ðức Giê-hô-va phù hộ, nên thạnh lợi luôn."));
visuperobject.biblemap.put("b1_39_3", new visuperobject(1,39,3,"Chủ thấy Ðức Giê-hô-va phù hộ chàng và xui cho mọi việc chi chàng lãnh làm nơi tay đều được thạnh vượng."));
visuperobject.biblemap.put("b1_39_4", new visuperobject(1,39,4,"Vậy, Giô-sép được ơn trước mặt chủ mình; chủ cắt chàng hầu việc, đặt cai quản cửa nhà, và phó của cải mình cho chàng hết."));
visuperobject.biblemap.put("b1_39_5", new visuperobject(1,39,5,"Từ khi người đặt Giô-sép cai quản cửa nhà cùng hết thảy của cải mình rồi, thì Ðức Giê-hô-va vì Giô-sép ban phước cho nhà người; sự ban phước của Ðức Giê-hô-va bủa khắp mọi vật của người làm chủ, hoặc trong nhà, hoặc ngoài đồng."));
visuperobject.biblemap.put("b1_39_6", new visuperobject(1,39,6,"Người giao hết của cải mình cho Giô-sép coi sóc, chẳng lo đến việc nào nữa, trừ ra các phần vật thực của mình ăn mà thôi. Vả, Giô-sép hình dung đẹp đẽ, mặt mày tốt tươi."));
visuperobject.biblemap.put("b1_39_7", new visuperobject(1,39,7,"Sau các việc nầy, vợ chủ đưa mắt cùng Giô-sép, mà nói rằng: Hãy lại nằm cùng ta."));
visuperobject.biblemap.put("b1_39_8", new visuperobject(1,39,8,"Chàng từ chối và đáp rằng: Chủ đã giao nơi tay tôi mọi vật của người, và nầy, chủ chẳng lo biết đến việc chi trong nhà nữa;"));
visuperobject.biblemap.put("b1_39_9", new visuperobject(1,39,9,"trong nhà nầy chẳng ai lớn hơn tôi, và chủ cùng không cấm chi tôi, trừ ra một mình ngươi, vì là vợ của chủ tôi. Thế nào tôi dám làm điều đại ác dường ấy, mà phạm tội cùng Ðức Chúa Trời sao?"));
visuperobject.biblemap.put("b1_39_10", new visuperobject(1,39,10,"Thường ngày người dỗ dành mặc dầu, thì Giô-sép chẳng khấng nghe lời dụ dỗ nằm cùng hay là ở cùng người chút nào."));
visuperobject.biblemap.put("b1_39_11", new visuperobject(1,39,11,"Một ngày kia, Giô-sép vào nhà đặng làm công việc; vả, chẳng có người nhà nào ở đó,"));
visuperobject.biblemap.put("b1_39_12", new visuperobject(1,39,12,"thì người bèn nắm áo chàng mà rằng: Hãy nằm cùng ta! Nhưng chàng liền tuột áo để lại trong tay người mà chạy trốn ra ngoài."));
visuperobject.biblemap.put("b1_39_13", new visuperobject(1,39,13,"Khi người thấy áo còn lại nơi tay mình, và chàng đã chạy trốn ra ngoài như vậy,"));
visuperobject.biblemap.put("b1_39_14", new visuperobject(1,39,14,"bèn gọi người nhà mà nói rằng: Bay coi! Họ khéo đem vào nhà một thằng Hê-bơ-rơ để chọc ghẹo ta. Nó lại gần toan nằm cùng ta, nhưng ta la lớn lên."));
visuperobject.biblemap.put("b1_39_15", new visuperobject(1,39,15,"Vừa khi nó nghe ta cất tiếng la, thì tuột áo lại bên ta, chạy trốn ra ngoài."));
visuperobject.biblemap.put("b1_39_16", new visuperobject(1,39,16,"Người bèn để áo của Giô-sép bên mình đợi khi chủ về nhà,"));
visuperobject.biblemap.put("b1_39_17", new visuperobject(1,39,17,"học lại cùng chủ y như lời trước, rằng: Thằng nô lệ Hê-bơ-rơ mà ông khéo đem về nhà đã đến gần đặng chọc ghẹo tôi;"));
visuperobject.biblemap.put("b1_39_18", new visuperobject(1,39,18,"nhưng khi tôi cất tiếng la lên, nó tuột áo lại bên tôi, rồi chạy trốn ra ngoài."));
visuperobject.biblemap.put("b1_39_19", new visuperobject(1,39,19,"Vừa khi chủ của Giô-sép nghe lời vợ nói rằng: Ðó, kẻ nô lệ ông làm điều như vậy, thì nổi giận phừng phừng,"));
visuperobject.biblemap.put("b1_39_20", new visuperobject(1,39,20,"bèn bắt chàng đem bỏ vào tù, là nơi cầm các kẻ phạm tội của vua. Vậy, ch@ ng ở tù tại đó."));
visuperobject.biblemap.put("b1_39_21", new visuperobject(1,39,21,"Ðức Giê-hô-va phù hộ Giô-sép và tỏ lòng nhân từ cùng chàng, làm cho được ơn trước mặt chủ ngục."));
visuperobject.biblemap.put("b1_39_22", new visuperobject(1,39,22,"Chủ ngục giao hết các kẻ tù trong tay Giô-sép, chẳng việc chi làm qua khỏi được chàng."));
visuperobject.biblemap.put("b1_39_23", new visuperobject(1,39,23,"Chủ ngục chẳng soát lại những việc đã ở nơi tay Giô-sép, vì có Ðức Giê-hô-va phù hộ chàng, xui cho việc chi chàng làm cũng được thạnh vượng"));
visuperobject.biblemap.put("b1_40_1", new visuperobject(1,40,1,"Mấy việc nầy qua rồi, xảy có quan tửu chánh và quan thượng thiện của vua Ê-díp-tô phạm đến chúa mình."));
visuperobject.biblemap.put("b1_40_2", new visuperobject(1,40,2,"Pha-ra-ôn nổi giận cùng hai quan mình, là tửu chánh và thượng thiện,"));
visuperobject.biblemap.put("b1_40_3", new visuperobject(1,40,3,"bèn đem họ cần ngục tại dinh quan thị vệ, là nơi đương cầm Giô-sép."));
visuperobject.biblemap.put("b1_40_4", new visuperobject(1,40,4,"Quan thị vệ cắt phần Giô-sép hầu việc hai quan đó; họ bị ngồi ngục một ít lâu."));
visuperobject.biblemap.put("b1_40_5", new visuperobject(1,40,5,"Cùng trong một đêm, quan tửu chánh và quan thượng thiện của vua Ê-díp-tô đương bị cầm ngục, thấy một điềm chiêm bao, mỗi người một chiêm bao, và mỗi chiêm bao có ý nghĩa rõ ràng."));
visuperobject.biblemap.put("b1_40_6", new visuperobject(1,40,6,"Sáng mai, Giô-sép đến cùng họ, nhìn thấy sắc buồn bực,"));
visuperobject.biblemap.put("b1_40_7", new visuperobject(1,40,7,"bèn hỏi rằng: Ngày nay sao hai quan có sắc mặt âu sầu vậy?"));
visuperobject.biblemap.put("b1_40_8", new visuperobject(1,40,8,"Ðáp rằng: Chúng tôi có thấy một điềm chiêm bao mà không ai bàn giùm. Giô-sép rằng: Sự bàn chiêm bao há chẳng do nơi Ðức Chúa Trời ư? Xin hãy thuật lại điềm chiêm bao của hai quan cho tôi nghe đi."));
visuperobject.biblemap.put("b1_40_9", new visuperobject(1,40,9,"Quan tửu chánh thuật điềm chiêm bao mình lại cho Giô-sép mà rằng: Trong chiêm bao, tôi thấy một gốc nho ở trước mặt tôi;"));
visuperobject.biblemap.put("b1_40_10", new visuperobject(1,40,10,"gốc nho đó lại có ba nhành. Dường như thấy nhành nảy chồi, trổ bông, chùm có trái chín thì phải."));
visuperobject.biblemap.put("b1_40_11", new visuperobject(1,40,11,"Tay tôi đương cầm cái chén của Pha-ra-ôn; tôi hái nho đó, ép nước nho vào, rồi dâng tửu bôi vào tay Pha-ra-ôn."));
visuperobject.biblemap.put("b1_40_12", new visuperobject(1,40,12,"Giô-sép nói: Ý-nghĩa chiêm bao đó là thể nầy: Ba nhành nho tức là ba ngày."));
visuperobject.biblemap.put("b1_40_13", new visuperobject(1,40,13,"Trong ba ngày nữa, Pha-ra-ôn sẽ tha quan ra khỏi ngục, phục chức lại, quan sẽ dâng cái chén của Pha-ra-ôn vào tay người như cũ, như khi còn làm chức tửu chánh."));
visuperobject.biblemap.put("b1_40_14", new visuperobject(1,40,14,"Song khi quan được hưởng lạc lại rồi, xin nhớ đến tôi, làm ơn tâu cùng Pha-ra-ôn về nổi tôi, và đem tôi ra khỏi chốn nầy."));
visuperobject.biblemap.put("b1_40_15", new visuperobject(1,40,15,"Vì tôi bị người ta bắt đem ra khỏi xứ của người Hê-bơ-rơ, và tại đây tôi cũng chẳng có làm gì mà bị cầm nơi lao-lung nầy."));
visuperobject.biblemap.put("b1_40_16", new visuperobject(1,40,16,"Quan thượng thiện thấy Giô-sép bàn mộng được tốt, bèn nói: Còn trong điềm chiêm bao của tôi đây, tôi đội ba giỏ bánh trắng trên đầu;"));
visuperobject.biblemap.put("b1_40_17", new visuperobject(1,40,17,"trong giỏ cao hơn hết, có đủ món thực vật của thợ làm bánh hấp cho Pha-ra-ôn; chim đáp vào giỏ trên đầu tôi ăn các món đó."));
visuperobject.biblemap.put("b1_40_18", new visuperobject(1,40,18,"Giô-sép đáp rằng: Ý-nghĩa chiêm bao nầy là, ba giỏ, tức là ba ngày."));
visuperobject.biblemap.put("b1_40_19", new visuperobject(1,40,19,"Trong ba ngày nữa, Pha-ra-ôn sẽ xử trảm quan, sai đem treo lên cây cho chim chóc ăn thịt quan vậy."));
visuperobject.biblemap.put("b1_40_20", new visuperobject(1,40,20,"Ðến ngày thứ ba, là ngày sanh-nhựt của Pha-ra-ôn, người bèn bày tiệc đãi cả quần thần, và tha quan tửu chánh cùng quan thượng thiện ra khỏi ngục."));
visuperobject.biblemap.put("b1_40_21", new visuperobject(1,40,21,"Pha-ra-ôn phục chức quan tửu chánh lại như xưa, đặng quan nầy dân tửu bôi vào tay mình;"));
visuperobject.biblemap.put("b1_40_22", new visuperobject(1,40,22,"nhưng lại hạ lịnh đem treo quan thượng thiện, y như lời Giô-sép đã bàn ra."));
visuperobject.biblemap.put("b1_40_23", new visuperobject(1,40,23,"Ðoạn, quan tửu chánh chẳng còn nhớ đến Giô-sép nữa; quên người đi."));
visuperobject.biblemap.put("b1_41_1", new visuperobject(1,41,1,"Cách hai năm sau, Pha-ra-ôn nằm chiêm bao thấy mình đứng bên bờ sông."));
visuperobject.biblemap.put("b1_41_2", new visuperobject(1,41,2,"Nầy đâu dưới sông đi lên bảy con bò mập và tốt, ăn cỏ trong bung."));
visuperobject.biblemap.put("b1_41_3", new visuperobject(1,41,3,"Rồi nầy, bảy con bò khác, xấu dạng, gầy guộc, ở dưới sông đi theo lên sau, đến đứng gần các con bò trước trên bờ sông."));
visuperobject.biblemap.put("b1_41_4", new visuperobject(1,41,4,"Bảy con bò xấu dạng, gầy guộc nuốt bảy con bò mập tốt. Pha-ra-ôn bèn thức giấc."));
visuperobject.biblemap.put("b1_41_5", new visuperobject(1,41,5,"Vua nằm ngủ lại, chiêm bao lần thứ nhì, thấy bảy gié lúa chắc, tốt tươi, mọc chung trên một cộng rạ."));
visuperobject.biblemap.put("b1_41_6", new visuperobject(1,41,6,"Kế đó, bảy gié lúa khác lép và bị gió đông thổi háp, mọc theo bảy gié lúa kia."));
visuperobject.biblemap.put("b1_41_7", new visuperobject(1,41,7,"Bảy gié lúa lép nuốt bảy gié lúa chắc. Pha-ra-ôn bèn thức giấc; và nầy, thành ra một điềm chiêm bao."));
visuperobject.biblemap.put("b1_41_8", new visuperobject(1,41,8,"Sáng mai, tâm thần người bất định, truyền đòi các pháp-sư và các tay bác sĩ xứ Ê-díp-tô đến, thuật lại điềm chiêm bao mình cho họ nghe; nhưng chẳng có ai bàn được điềm chiêm bao đó cho vua hết."));
visuperobject.biblemap.put("b1_41_9", new visuperobject(1,41,9,"Quan tửu chánh bèn tâu cùng Pha-ra-ôn rằng: Bây giờ, tôi nhớ đến lỗi của tôi."));
visuperobject.biblemap.put("b1_41_10", new visuperobject(1,41,10,"Ngày trước, bệ hạ nổi giận cùng quần thần, có cầm ngục quan thượng thiện và tôi nơi dinh quan thị vệ."));
visuperobject.biblemap.put("b1_41_11", new visuperobject(1,41,11,"Trong lúc đó, cùng một đêm kia, chúng tôi có thấy điềm chiêm bao, chiêm bao mỗi người đều có ý nghĩa riêng rõ ràng."));
visuperobject.biblemap.put("b1_41_12", new visuperobject(1,41,12,"Tại đó, cùng chung ngục, có một người Hê-bơ-rơ, còn trẻ, kẻ gia-đinh của quan thị vệ; chúng tôi thuật lại cho chàng nghe điềm chiêm bao của mình; chàng bàn rõ ra cho ai mỗi chiêm bao nấy."));
visuperobject.biblemap.put("b1_41_13", new visuperobject(1,41,13,"Rồi ra, công việc xảy đến y như lời chàng bàn: bệ hạ phục chức tôi lại, và xử treo quan kia."));
visuperobject.biblemap.put("b1_41_14", new visuperobject(1,41,14,"Pha-ra-ôn bèn sai đi đòi Giô-sép; họ lập tức tha người ra khỏi ngục, cạo mặt mày cho, biểu thay đổi áo xống, rồi vào chầu Pha-ra-ôn."));
visuperobject.biblemap.put("b1_41_15", new visuperobject(1,41,15,"Pha-ra-ôn phán cùng Giô-sép rằng: Trẫm có thấy một điềm chiêm bao mà chẳng ai bàn ra. Vậy, trẫm nghe rằng khi người ta thuật điềm chiêm bao lại cho, thì ngươi bàn được."));
visuperobject.biblemap.put("b1_41_16", new visuperobject(1,41,16,"Giô-sép tâu rằng: Ðó chẳng phải tôi, nhưng ấy là Ðức Chúa Trời đem sự bình an đáp cho bệ hạ vậy."));
visuperobject.biblemap.put("b1_41_17", new visuperobject(1,41,17,"Pha-ra-ôn phán rằng: Nầy, trong điềm chiêm bao trẫm đương đứng bên bờ sông."));
visuperobject.biblemap.put("b1_41_18", new visuperobject(1,41,18,"Nầy đâu bảy con bò mập và tốt dưới sông đi lên, đến ăn cỏ trong bưng."));
visuperobject.biblemap.put("b1_41_19", new visuperobject(1,41,19,"Rồi bảy con bò khác xấu dạng gầy guộc đi theo lên sau; thật trẫm chẳng thấy trong xứ Ê-díp-tô nầy có bò xấu dạng như vậy bao giờ."));
visuperobject.biblemap.put("b1_41_20", new visuperobject(1,41,20,"Bảy con bò xấu dạng gầy guộc đó nuốt bảy con bò mập tốt trước kia,"));
visuperobject.biblemap.put("b1_41_21", new visuperobject(1,41,21,"nuốt vào bụng, mà dường như không có nuốt, vì các con bò đó vẫn xấu dạng như xưa."));
visuperobject.biblemap.put("b1_41_22", new visuperobject(1,41,22,"Trẫm bèn thức giấc; rồi lại thấy điềm chiêm bao khác, là bảy gié lúa chắc tốt tươi mọc chung trên một cộng rạ."));
visuperobject.biblemap.put("b1_41_23", new visuperobject(1,41,23,"Kế đó, bảy gié lúa khác, khô, lép, bị gió đông thổi háp, mọc theo bảy gié lúa kia;"));
visuperobject.biblemap.put("b1_41_24", new visuperobject(1,41,24,"và bảy gié lúa lép đó lại nuốt bảy gié lúa chắc. Trẫm đã thuật điềm chiêm bao nầy cho các pháp-sư, nhưng không ai giải nghĩa ra được."));
visuperobject.biblemap.put("b1_41_25", new visuperobject(1,41,25,"Giô-sép tâu cùng Pha-ra-ôn rằng: Hai điềm chiêm bao của bệ hạ thấy đó đồng một nghĩa mà thôi; Ðức Chúa Trời mách cho bệ hạ biết trước những việc Ngài sẽ làm."));
visuperobject.biblemap.put("b1_41_26", new visuperobject(1,41,26,"Bảy con bò mập tốt đó, tức là bảy năm; bảy gié lúa chắc đó cũng là bảy năm. Ấy chỉ đồng một điềm chiêm bao vậy."));
visuperobject.biblemap.put("b1_41_27", new visuperobject(1,41,27,"Bảy con bò xấu dạng gầy guộc lên theo sau bảy con bò kia, tức là bảy năm; và bảy gié lúa lép bị gió đông thổi háp đó, tức là bảy năm đói kém."));
visuperobject.biblemap.put("b1_41_28", new visuperobject(1,41,28,"Ấy là điều tôi đã tâu cùng bệ hạ rồi: Ðức Chúa Trời có cho bệ hạ thấy những việc Ngài sẽ làm."));
visuperobject.biblemap.put("b1_41_29", new visuperobject(1,41,29,"Nầy, trong khắp xứ Ê-díp-tô sẽ có bảy năm được mùa dư dật."));
visuperobject.biblemap.put("b1_41_30", new visuperobject(1,41,30,"Nhưng bảy năm đó lại liền tiếp bảy năm đói kém; dân bổn xứ đều sẽ quên sự dư dật đó, và ách đói kém sẽ làm cho toàn xứ hao mòn."));
visuperobject.biblemap.put("b1_41_31", new visuperobject(1,41,31,"Và vì sự đói kém liên tiếp nầy lớn quá, nên thiên hạ chẳng còn thấy chi về sự dư dật đó nữa."));
visuperobject.biblemap.put("b1_41_32", new visuperobject(1,41,32,"Nếu điềm chiêm bao đến hai lần cho bệ hạ, ấy bởi Ðức Chúa Trời đã quyết định điều đó rồi, và Ngài mau mau lo làm cho thành vậy."));
visuperobject.biblemap.put("b1_41_33", new visuperobject(1,41,33,"Bây giờ bệ hạ khá chọn một người thông minh trí-huệ, lập người lên làm đầu trong xứ Ê-díp-tô,"));
visuperobject.biblemap.put("b1_41_34", new visuperobject(1,41,34,"cùng lập các ủy viên trong xứ, để góp một phần năm của số thâu vào trong bảy năm được mùa dư dật đó."));
visuperobject.biblemap.put("b1_41_35", new visuperobject(1,41,35,"Họ hãy thâu góp hết thảy mùa màng trong bảy năm được mùa dư dật sẽ đến sau nầy, cùng thâu thập lúa mì sẵn dành cho Pha-ra-ôn, dùng làm lương để dành trong các thành, và họ hãy giữ gìn lấy."));
visuperobject.biblemap.put("b1_41_36", new visuperobject(1,41,36,"Các lương thực nầy phải để dành cho bảy năm đói kém sẽ đến trong xứ Ê-díp-tô, hầu cho xứ nầy khỏi bị diệt bởi sự đói kém đó."));
visuperobject.biblemap.put("b1_41_37", new visuperobject(1,41,37,"Các lời nầy đẹp ý Pha-ra-ôn và quần thần."));
visuperobject.biblemap.put("b1_41_38", new visuperobject(1,41,38,"Pha-ra-ôn phán cùng quần thần rằng: Chúng ta há dễ tìm một người như người nầy, có thần minh của Ðức Chúa Trời được sao?"));
visuperobject.biblemap.put("b1_41_39", new visuperobject(1,41,39,"Pha-ra-ôn phán cùng Giô-sép rằng: Vì Ðức Chúa Trời xui cho ngươi biết mọi việc nầy, thì chẳng còn ai được thông minh trí-huệ như ngươi nữa."));
visuperobject.biblemap.put("b1_41_40", new visuperobject(1,41,40,"Vậy, ngươi sẽ lên cai trị nhà trẫm; hết thảy dân sự của trẫm đều sẽ vâng lời ngươi. Trẫm lớn hơn ngươi chỉ vì ngự ngôi vua mà thôi."));
visuperobject.biblemap.put("b1_41_41", new visuperobject(1,41,41,"Pha-ra-ôn lại phán cùng Giô-sép rằng: Hãy xem! trẫm lập ngươi cầm quyền trên cả xứ Ê-díp-tô."));
visuperobject.biblemap.put("b1_41_42", new visuperobject(1,41,42,"Vua liền cổi chiếc nhẫn trong tay mình, đeo vào tay Giô-sép, truyền mặc áo vải gai mịn, và đeo vòng vàng vào cổ người;"));
visuperobject.biblemap.put("b1_41_43", new visuperobject(1,41,43,"rồi, truyền cho lên ngồi sau xe vua, có người đi trước hô rằng: Hãy quì xuống! Ấy, Giô-sép được lập lên cầm quyền cả xứ Ê-díp-tô là vậy."));
visuperobject.biblemap.put("b1_41_44", new visuperobject(1,41,44,"Pha-ra-ôn phán cùng Giô-sép: Ta là Pha-ra-ôn, nhưng nếu chẳng có ngươi, thì trong cả xứ Ê-díp-tô không có ai giơ tay đưa chơn lên được."));
visuperobject.biblemap.put("b1_41_45", new visuperobject(1,41,45,"Pha-ra-ôn đặt tên Giô-sép là Xa-phơ-nát-Pha-nê-ách, đưa nàng Ách-nát, con gái Phô-ti-phê-ra, thầy cả thành Ôn, cho người làm vợ. Người thường tuần hành trong xứ Ê-díp-tô."));
visuperobject.biblemap.put("b1_41_46", new visuperobject(1,41,46,"Vả, khi Giô-sép ra mắt Pha-ra-ôn, vua Ê-díp-tô, thì người đã ba mươi tuổi. Vậy, người từ tạ Pha-ra-ôn mà đi tuần khắp xứ Ê-díp-tô."));
visuperobject.biblemap.put("b1_41_47", new visuperobject(1,41,47,"Trong bảy năm được mùa dư dật, đất sanh sản ra đầy dẫy."));
visuperobject.biblemap.put("b1_41_48", new visuperobject(1,41,48,"Giô-sép bèn thâu góp hết thảy lương thực của bảy năm đó trong xứ Ê-díp-tô, và chứa lương thực nầy khắp các thành; trong mỗi thành, đều dành chứa hoa lợi của các ruộng ở chung quanh thành đó."));
visuperobject.biblemap.put("b1_41_49", new visuperobject(1,41,49,"Vậy, Giô-sép thâu góp lúa mì rất nhiều, như cát nơi bờ biển, cho đến đỗi người ta đếm không được, vì đầy dẫy vô số."));
visuperobject.biblemap.put("b1_41_50", new visuperobject(1,41,50,"Trước khi đến năm đói kém, thì Ách-nát, con gái Phô-ti-phê-ra, thầy cả thành Ôn, sanh cho Giô-sép hai con trai."));
visuperobject.biblemap.put("b1_41_51", new visuperobject(1,41,51,"Giô-sép đặt tên đứa đầu lòng là Ma-na-se, vì nói rằng: Ðức Chúa Trời đã làm cho ta quên điều cực nhọc, và cả nhà cha ta."));
visuperobject.biblemap.put("b1_41_52", new visuperobject(1,41,52,"Người đặt tên đứa thứ nhì là Ép-ra-im, vì nói rằng: Ðức Chúa Trời làm cho ta được hưng vượng trong xứ mà ta bị khốn khổ."));
visuperobject.biblemap.put("b1_41_53", new visuperobject(1,41,53,"Bảy năm được mùa dư dật trong xứ Ê-díp-tô bèn qua,"));
visuperobject.biblemap.put("b1_41_54", new visuperobject(1,41,54,"thì bảy năm đói kém khởi đến, y như lời Giô-sép đã bàn trước. Khắp các xứ khác cũng đều bị đói, nhưng trong cả xứ Ê-díp-tô lại có bánh."));
visuperobject.biblemap.put("b1_41_55", new visuperobject(1,41,55,"Ðoạn, cả xứ Ê-díp-tô đều bị đói, dân chúng đến kêu cầu Pha-ra-ôn xin lương. Pha-ra-ôn phán cùng bổn dân rằng: Hãy đi đến Giô-sép, rồi làm theo lời người sẽ chỉ bảo cho."));
visuperobject.biblemap.put("b1_41_56", new visuperobject(1,41,56,"Khi khắp xứ bị đói kém, thì Giô-sép mở mọi kho lúa mà bán cho dân Ê-díp-tô."));
visuperobject.biblemap.put("b1_41_57", new visuperobject(1,41,57,"Xứ càng đói nhiều nữa; vả, vì khắp thế gian đều bị đói quá, nên đâu đâu cũng đổ đến Ê-díp-tô mùa lúa nơi Giô-sép bán."));
visuperobject.biblemap.put("b1_42_1", new visuperobject(1,42,1,"Gia-cốp thấy xứ Ê-díp-tô có lúa bán ra, bèn nói cùng các con trai mình rằng: Sao bay ngơ ngẩn nhìn nhau vậy?"));
visuperobject.biblemap.put("b1_42_2", new visuperobject(1,42,2,"Lại rằng: Nầy, cha nghe dưới xứ Ê-díp-tô có lúa bán; bay hãy xuống đó mua lúa cho chúng ta, hầu cho chúng ta sống và khỏi chết, chớ!"));
visuperobject.biblemap.put("b1_42_3", new visuperobject(1,42,3,"Mười người anh của Giô-sép bèn xuống Ê-díp-tô đặng mua lúa."));
visuperobject.biblemap.put("b1_42_4", new visuperobject(1,42,4,"Nhưng Gia-cốp không cho Bên-gia-min, em Giô-sép, đi cùng các anh; vì nghĩ rằng: Ta nên giữ chừng, kẻo xảy ra điều rủi ro cho nó."));
visuperobject.biblemap.put("b1_42_5", new visuperobject(1,42,5,"Xứ Ca-na-an bấy giờ cũng đói, nên các con trai của Y-sơ-ra-ên cũng đổ xuống Ê-díp-tô mua lúa như các người khác."));
visuperobject.biblemap.put("b1_42_6", new visuperobject(1,42,6,"Vả, lúc nầy, chánh Giô-sép cai trị trong nước, và bán lúa cho cả dân bổn xứ. Các anh Giô-sép bèn đến, sấp mình xuống trước mặt người."));
visuperobject.biblemap.put("b1_42_7", new visuperobject(1,42,7,"Giô-sép thấy, nhìn biết các anh mình; nhưng giả làm mặt lạ cùng họ, nói một cách xẳng rằng: Các ngươi ở đâu đến? Ðáp rằng: Ở xứ Ca-na-an đến đặng mua lúa."));
visuperobject.biblemap.put("b1_42_8", new visuperobject(1,42,8,"Vậy, Giô-sép nhìn biết các anh mình; nhưng họ nhìn lại chẳng ra."));
visuperobject.biblemap.put("b1_42_9", new visuperobject(1,42,9,"Giô-sép nhớ lại điềm chiêm bao mình đã thấy về anh em mình, liền thét rằng: Các ngươi là thám tử, đến đây đặng dòm hành những nơi yếu óp của xứ ta."));
visuperobject.biblemap.put("b1_42_10", new visuperobject(1,42,10,"Ðáp rằng: Thưa chúa, chẳng phải vậy; các tôi tớ chúa đến đặng mua lúa mà thôi."));
visuperobject.biblemap.put("b1_42_11", new visuperobject(1,42,11,"Chúng tôi đây đều là con một cha, vốn nhà lương thiện, chẳng phải là thám tử đâu."));
visuperobject.biblemap.put("b1_42_12", new visuperobject(1,42,12,"Người đáp lại rằng: Chẳng phải; các ngươi đến đặng dòm hành những nơi yếu óp của xứ nầy."));
visuperobject.biblemap.put("b1_42_13", new visuperobject(1,42,13,"Ðáp rằng: Kẻ tôi tớ chúa có mười hai anh em, con của một cha, nguyên xứ Ca-na-an; nầy một người đã đi mất biệt, còn người út hiện bây giờ hãy ở tại nhà cùng cha chúng tôi."));
visuperobject.biblemap.put("b1_42_14", new visuperobject(1,42,14,"Giô-sép nói: Ấy quả thật như ta nói, các ngươi là thám tử."));
visuperobject.biblemap.put("b1_42_15", new visuperobject(1,42,15,"Ðây là điều ta thử các ngươi: Ta chỉ mạng Pha-ra-ôn mà thề rằng, hễ em út các ngươi không đến đây thì chẳng bao giờ các ngươi ra khỏi chốn nầy được."));
visuperobject.biblemap.put("b1_42_16", new visuperobject(1,42,16,"Hãy sai một người nào trong bọn các ngươi về dẫn em út xuống; còn bao nhiêu thì phải ở tù lại đây. Lời các ngươi phải thử đã, rồi ta sẽ biết các ngươi nói đó có quả thật chăng. Nếu chẳng thật, thì ta chỉ mạng Pha-ra-ôn thề rằng, các ngươi là thám tử đó."));
visuperobject.biblemap.put("b1_42_17", new visuperobject(1,42,17,"Ðoạn, Giô-sép truyền đem giam họ chung trong ngục ba ngày."));
visuperobject.biblemap.put("b1_42_18", new visuperobject(1,42,18,"Ngày thứ ba, Giô-sép nói cùng họ rằng: Ta kính sợ Ðức Chúa Trời; hãy làm như điều nầy, thì được sống."));
visuperobject.biblemap.put("b1_42_19", new visuperobject(1,42,19,"Nếu các ngươi vốn nhà lương thiện, thì hãy để một người nào ở tù lại đây, còn bao nhiêu đem lương thực về nuôi đói cho nhà các ngươi."));
visuperobject.biblemap.put("b1_42_20", new visuperobject(1,42,20,"Ðoạn, hãy dẫn em út xuống đây, thì lời các ngươi sẽ cho là thật, và các ngươi chẳng phải chết. Vậy, họ nghe theo."));
visuperobject.biblemap.put("b1_42_21", new visuperobject(1,42,21,"Họ bèn nói với nhau rằng: Quả thật chúng ta cam tội cùng em ta ra; vì lúc trước ta thấy tâm hồn nó buồn thảm khi nó xin nhờ ơn, nhưng ta không khứng cho; vậy nên tai vạ nầy mới xảy đến cho chúng ta."));
visuperobject.biblemap.put("b1_42_22", new visuperobject(1,42,22,"Ru-bên đáp rằng: Anh há không có nói cùng các em rằng: Chớ phạm tội nầy cùng đứa trẻ đó sao? Nhưng các em không nghe; nên bây giờ huyết nó đòi chúng ta thường lại."));
visuperobject.biblemap.put("b1_42_23", new visuperobject(1,42,23,"Vả, Giô-sép vẫn dùng người thông ngôn nói chuyện lại cùng các anh, nên họ tưởng Giô-sép chẳng nghe hiểu."));
visuperobject.biblemap.put("b1_42_24", new visuperobject(1,42,24,"Giô-sép xây mặt qua chỗ khác mà khóc. Ðoạn xây mặt lại nói chuyện cùng họ; bèn bắt Si-mê-ôn trong vòng anh em, truyền trói lại trước mặt họ."));
visuperobject.biblemap.put("b1_42_25", new visuperobject(1,42,25,"Giô-sép truyền đầy tớ xúc lúa đổ đầy bao và để bạc lại trong đó cho mỗi người anh em, cũng để thêm lương thực dùng dọc đường. Ðầy tớ làm y như lời."));
visuperobject.biblemap.put("b1_42_26", new visuperobject(1,42,26,"Ðoạn, anh em chất mấy bao lúa lên lưng lừa, rồi đi."));
visuperobject.biblemap.put("b1_42_27", new visuperobject(1,42,27,"Ðến quán, một người trong bọn mở bao ra cho lừa ăn thóc, thấy bạc mình ở tại miệng bao;"));
visuperobject.biblemap.put("b1_42_28", new visuperobject(1,42,28,"bèn nói cùng anh em rằng: Bạc của tôi trả lại, hiện trong bao tôi đây! Các anh em nhát gan, vừa nói với nhau, vừa run, mà rằng: Ðức Chúa Trời đã làm chi cho chúng ta đây?"));
visuperobject.biblemap.put("b1_42_29", new visuperobject(1,42,29,"Các anh em trở về cùng Gia-cốp, cha mình, tại xứ Ca-na-an, thuật lại cho người nghe mọi nỗi đã xảy đến, rằng:"));
visuperobject.biblemap.put("b1_42_30", new visuperobject(1,42,30,"người đương làm chúa tại xứ Ê-díp-tô nói với chúng tôi cách xẳng xớm, cho chúng tôi là thám tử."));
visuperobject.biblemap.put("b1_42_31", new visuperobject(1,42,31,"Song chúng tôi có thưa cùng người rằng: Chúng tôi vốn nhà lương thiện, nào có phải là thám tử đâu."));
visuperobject.biblemap.put("b1_42_32", new visuperobject(1,42,32,"Chúng tôi được mười hai anh em, con một cha; một người đã đi mất biệt, còn người út hiện bây giờ ở nhà cùng cha, tại xứ Ca-na-an."));
visuperobject.biblemap.put("b1_42_33", new visuperobject(1,42,33,"Người làm chúa xứ đó dạy rằng: Làm như vầy, ta mới cho các ngươi là lương thiện: Hãy để lại cùng ta một người nào trong bọn các ngươi, còn bao nhiêu sẽ đem lương thực về nuôi đói cho nhà các ngươi;"));
visuperobject.biblemap.put("b1_42_34", new visuperobject(1,42,34,"đoạn, hãy dẫn em út đến cho ta. Vậy, ta mới biết rằng các ngươi chẳng phải là thám tử, nhưng là người lương thiện; ta sẽ giao em lại cho, và các ngươi sẽ được buôn bán trong xứ nầy."));
visuperobject.biblemap.put("b1_42_35", new visuperobject(1,42,35,"Vả, đương khi các anh em trút lúa mì ra bao, nầy đâu, trong mỗi bao có gói bạc của mỗi người; cha và các anh em thấy những gói bạc mình, đều lấy làm sợ hãi."));
visuperobject.biblemap.put("b1_42_36", new visuperobject(1,42,36,"Gia-cốp, cha họ bèn nói rằng: Bay làm mất các con tao; Giô-sép đã mất biệt, Si-mê-ôn cũng mất biệt; mà bây giờ, lại muốn dẫn Bê-gia-min đi nữa sao! Các nông nỗi nầy đều đổ lại cho tao hết!"));
visuperobject.biblemap.put("b1_42_37", new visuperobject(1,42,37,"Ru-bên thưa lại cùng cha rằng: Cha hãy giao em nơi tôi, tôi sẽ dẫn về cho. Ví bằng chẳng dẫn em về, thì cha hãy giết hai đứa con trai tôi đi."));
visuperobject.biblemap.put("b1_42_38", new visuperobject(1,42,38,"Nhưng Gia-cốp đáp rằng: Con út tao sẽ chẳng đi xuống với bay đâu; vì anh nó đã chết rồi, chỉ còn một mình nó mà thôi. Nếu điều rủi ro chi xảy đến cho nó nơi dọc đường bay đi, tức nhiên bay làm cho kẻ tóc bạc nầy đau lòng xót dạ xuống âm phủ."));
visuperobject.biblemap.put("b1_43_1", new visuperobject(1,43,1,"Vả, sự đói kém trong xứ lớn lắm."));
visuperobject.biblemap.put("b1_43_2", new visuperobject(1,43,2,"Khi nội nhà đã ăn hết lương thực ở xứ Ê-díp-tô đem về rồi, thì cha các người ấy dạy rằng: Bay hãy trở xuống mua một ít lương thực cho chúng ta."));
visuperobject.biblemap.put("b1_43_3", new visuperobject(1,43,3,"Giu-đa thưa: Người đó có nói quyết cùng các con rằng: Ví em út không theo xuống với các ngươi, thì sẽ không bao giờ thấy mặt ta."));
visuperobject.biblemap.put("b1_43_4", new visuperobject(1,43,4,"Nếu cha cho em đi với, thì chúng tôi mới xuống Ê-díp-tô mua lương thực cho cha được."));
visuperobject.biblemap.put("b1_43_5", new visuperobject(1,43,5,"Bằng không, thì chúng tôi chẳng trở xuống đâu, vì người đó đã nói rằng: Ví em út không theo xuống với các ngươi, thì sẽ không bao giờ thấy mặt ta."));
visuperobject.biblemap.put("b1_43_6", new visuperobject(1,43,6,"Y-sơ-ra-ên quở rằng: Sao bay khéo tệ cùng tao, mà tỏ cho người ấy rằng bay còn một em nữa?"));
visuperobject.biblemap.put("b1_43_7", new visuperobject(1,43,7,"Thưa rằng: Người đó hỏi kỹ càng về chúng tôi, và về bà con chúng tôi rằng: Thân-phụ các ngươi còn sống chăn? cò có một người anh em nào nữa chăng? Chúng tôi có đáp các lời hỏi đó. Há dễ biết người đó sẽ dặn chúng tôi rằng: Hãy đem em út xuống đây, sao?"));
visuperobject.biblemap.put("b1_43_8", new visuperobject(1,43,8,"Giu-đa lại thưa cùng Y-sơ-ra-ên cha mình, rằng: Hãy cho đứa trẻ theo con, thì chúng tôi sẽ đứng dậy đi, hầu cho chúng ta, nào các con, nào cha, nào các cháu của cha đây đều được sống và khỏi chết."));
visuperobject.biblemap.put("b1_43_9", new visuperobject(1,43,9,"Con sẽ bảo lãnh em cho; cha sẽ cứ nơi con mà đòi em. Nếu không dẫn em về không để em trước mặt cha, thì con đây sẽ cam tội cùng cha mãi mãi."));
visuperobject.biblemap.put("b1_43_10", new visuperobject(1,43,10,"Vả, nếu không có điều dùng dằng, thì chúng tôi đã đi và về được hai lần rồi."));
visuperobject.biblemap.put("b1_43_11", new visuperobject(1,43,11,"Ðoạn, Y-sơ-ra-ên, cha các anh em, nói rằng: Nếu việc đã thể kia, thì bay phải làm thể nầy: Hãy lấy trong đồ hành lý những thổ sản quí nhứt của xứ ta: một ít nhũ hương và mật ong, các hương hoa và một dược, phi tử và hạnh nhân, đem dâng cho người đó làm của lễ."));
visuperobject.biblemap.put("b1_43_12", new visuperobject(1,43,12,"Hãy đem theo một số tiền bằng hai, đặng thối hồi tiền mà họ đã để lại nơi miệng bao của bay: có lẽ là một sự lộn chăng."));
visuperobject.biblemap.put("b1_43_13", new visuperobject(1,43,13,"Bay hãy đứng dậy đi, dẫn em út theo và trở xuống đến người đó."));
visuperobject.biblemap.put("b1_43_14", new visuperobject(1,43,14,"Cầu xin Ðức Chúa Trời toàn năng xui cho bay được ơn trước mặt người, và tha đứa em kia và Bên-gia-min cho bay. Còn phần cha, nếu phải mất con, thì cha chịu phận vậy!"));
visuperobject.biblemap.put("b1_43_15", new visuperobject(1,43,15,"Vậy, các anh em đem theo của lễ và một số tiền bằng hai, cùng dẫn Bên-gia-min đồng đứng dậy đi xuống Ê-díp-tô, mà ra mắt Giô-sép."));
visuperobject.biblemap.put("b1_43_16", new visuperobject(1,43,16,"Vừa khi Giô-sép thấy Bên-gia-min đi theo xuống với, bèn truyền cho quản gia rằng: Hãy đua các người nầy vào nhà trong, bắt con gì làm thịt và nấu dọn đi; vì trưa nầy họ sẽ dùng bữa cùng ta."));
visuperobject.biblemap.put("b1_43_17", new visuperobject(1,43,17,"Quản gia làm theo y như lời dặn, đưa họ vào nhà Giô-sép."));
visuperobject.biblemap.put("b1_43_18", new visuperobject(1,43,18,"Anh em thấy mình phải đưa vào nhà Giô-sép, thì sợ hãi, và nói với nhau rằng: Ấy cũng vì số tiền đã để lại vào bao chúng ta lần trước, nên họ mới đem chúng ta vào chốn nầy, đặng tìm cớ xông vào đánh chúng ta, bắt chúng ta làm tôi mọi, và chiếm đoạt mấy con lừa của chúng ta."));
visuperobject.biblemap.put("b1_43_19", new visuperobject(1,43,19,"Mấy anh em bèn đến gần quản gia của Giô-sép, thưa cùng người tại ngoài cửa"));
visuperobject.biblemap.put("b1_43_20", new visuperobject(1,43,20,"mà rằng: Xin chúa tha lỗi cho! anh em chúng tôi đã đến đây một lần rồi, đặng mua lương thực;"));
visuperobject.biblemap.put("b1_43_21", new visuperobject(1,43,21,"và khi trở về đến nhà quán, mở các bao ra, nầy số bạc của mỗi người đều ở tại miệng bao mình; cân nặng bao nhiêu đều y như bấy nhiêu. Nầy, chúng tôi có đem lại số bạc đó,"));
visuperobject.biblemap.put("b1_43_22", new visuperobject(1,43,22,"và cũng có đem thêm số bạc nữa đặng mua lương thực. Chẳng biết ai để bạc lại trong bao chúng tôi."));
visuperobject.biblemap.put("b1_43_23", new visuperobject(1,43,23,"Quản gia đáp rằng: Mọi việc đều bình yên! đừng sợ chi cả! Ấy là Ðức Chúa Trời của các ngươi, tức Ðức Chúa Trời của cha các ngươi, đã ban vật quí vào bao. Còn bạc các ngươi thì đã có giao cho ta. Ðoạn, người dẫn Si-mê-ôn đến cùng anh em;"));
visuperobject.biblemap.put("b1_43_24", new visuperobject(1,43,24,"rồi đưa anh em vào nhà Giô-sép, đem nước ra cho rửa chơn, và cũng cho những lừa ăn cỏ nữa."));
visuperobject.biblemap.put("b1_43_25", new visuperobject(1,43,25,"Các anh em bèn sửa soạn của lễ, đợi trưa Giô-sép về; vì có hay rằng mình sẽ ăn bữa tại nhà nầy."));
visuperobject.biblemap.put("b1_43_26", new visuperobject(1,43,26,"Khi Giô-sép trở về, các anh em bèn lấy của lễ đã đem theo, dâng cho Giô-sép tại nhà; rồi cùng sấp mình xuống đất trước mặt người."));
visuperobject.biblemap.put("b1_43_27", new visuperobject(1,43,27,"Người liền hỏi thăm họ mạnh giỏi thể nào, và nói rằng: Người cha già mà các ngươi đã nói cùng ta đó được mạnh khỏe chăng? còn sống chăng?"));
visuperobject.biblemap.put("b1_43_28", new visuperobject(1,43,28,"Ðáp rằng: Kẻ tôi tớ cháu, là cha chúng tôi, vẫn mạnh khỏe và còn sống. Ðoạn, họ cùi đầu và lạy."));
visuperobject.biblemap.put("b1_43_29", new visuperobject(1,43,29,"Giô-sép nhướng mắt thấy Bên-gia-min, em ruột, con trai của mẹ mình, thì hỏi rằng: Ðó có phải em út mà các ngươi đã nói cùng ta chăng? Và tiếp rằng: Con út ơi! cầu xin Ðức Chúa Trời làm ơn cho ngươi!"));
visuperobject.biblemap.put("b1_43_30", new visuperobject(1,43,30,"Vì thấy em mình, nên Giô-sép tất lòng cảm động, lật đật bước ra ngoài, tìm nơi nào đặng khóc. Người vào phòng nhà trong và khóc."));
visuperobject.biblemap.put("b1_43_31", new visuperobject(1,43,31,"Ðoạn, rửa mặt, bước ra, làm bộ chắc gan mà nói rằng: Hãy dọn bữa ăn đi."));
visuperobject.biblemap.put("b1_43_32", new visuperobject(1,43,32,"Người ta dọn cho Giô-sép ăn riêng, và cho các anh em ăn riêng. Người Ê-díp-tô ăn chung cùng người cũng dọn cho ngồi riêng nữa; vì người Ê-díp-tô có tánh gớm người Hê-bơ-rơ, nên không ăn chung được."));
visuperobject.biblemap.put("b1_43_33", new visuperobject(1,43,33,"Vậy, các anh em bèn ngồi lại trước mặt Giô-sép, lớn nhỏ tùy theo thứ tự mình, ngơ ngẩn mà nhìn nhau."));
visuperobject.biblemap.put("b1_43_34", new visuperobject(1,43,34,"Giô-sép sai đem mấy món đồ ăn trước mặt mình cho các anh em; nhưng phần Bên-gia-min lại nhiều gấp năm của các người khác. Họ uống và ăn ngon vui cùng Giô-sép."));
visuperobject.biblemap.put("b1_44_1", new visuperobject(1,44,1,"Giô-sép bèn truyền lịnh cho quản gia rằng: Hễ các người đó chở nổi bao nhiêu, thì hãy đổ lương thực cho họ đầy bao bấy nhiêu, rồi để bạc của mỗi người lại nơi miệng bao họ."));
visuperobject.biblemap.put("b1_44_2", new visuperobject(1,44,2,"Lại hãy để cái chén bằng bạc ta tại miệng bao chung với bạc mua lúa của người út nữa. Quản gia bèn làm y như lời Giô-sép dặn mình."));
visuperobject.biblemap.put("b1_44_3", new visuperobject(1,44,3,"Ngày mai, trời vừa sáng, người ta cho các người đó và lừa về."));
visuperobject.biblemap.put("b1_44_4", new visuperobject(1,44,4,"Khi đi ra khỏi thành chưa xa, Giô-sép nói cùng quản gia rằng: Ngươi hãy đứng dậy, đuổi theo mấy người đó; khi theo kịp rồi, hãy nói rằng: Sao các ngươi lấy oán trả ơn như vậy?"));
visuperobject.biblemap.put("b1_44_5", new visuperobject(1,44,5,"Có phải cái chén nầy là của chúa ta thường dùng uống rượu và bói chăng? Các ngươi đã làm một việc chẳng thiện đó."));
visuperobject.biblemap.put("b1_44_6", new visuperobject(1,44,6,"Quản gia theo kịp, lặp lại mấy lời ấy;"));
visuperobject.biblemap.put("b1_44_7", new visuperobject(1,44,7,"các anh em bèn đáp rằng: Sao chúa nói như vậy? Chẳng bao giờ kẻ tôi tớ chúa có tưởng làm đến việc thế nầy!"));
visuperobject.biblemap.put("b1_44_8", new visuperobject(1,44,8,"Ðây, từ xứ Ca-na-an chúng tôi có đem bạc đã được nơi miệng bao xuống giao lại cho người thay; dễ nào lại có ý mong ăn cắp bạc hay vàng của dinh chủ người sao?"));
visuperobject.biblemap.put("b1_44_9", new visuperobject(1,44,9,"Xin cho kẻ tôi tớ nào mà người tìm được cái chén đó phải chết đi, và chính chúng tôi sẽ làm tôi mọi cho chúa."));
visuperobject.biblemap.put("b1_44_10", new visuperobject(1,44,10,"Quản gia đáp: Ừ thôi! hãy làm như lời các ngươi đã nói: ta tìm được chén nơi ai thì người đó sẽ làm tôi mọi cho ta đó; còn các người thì vô tội."));
visuperobject.biblemap.put("b1_44_11", new visuperobject(1,44,11,"Tức thì, mỗi người lật đật hạ bao mình xuống đất và mở ra."));
visuperobject.biblemap.put("b1_44_12", new visuperobject(1,44,12,"Quản gia lục soát, bắt từ bao anh cả lần đến bao em út. Cái chén bèn tìm đặng trong bao Bê-gia-min."));
visuperobject.biblemap.put("b1_44_13", new visuperobject(1,44,13,"Mỗi người xé áo mình ra, chất bao lúa lên lưng lừa, rồi cùng trở lại thành."));
visuperobject.biblemap.put("b1_44_14", new visuperobject(1,44,14,"Giu-đa cùng anh em đồng vào đến nhà Giô-sép, người hãy còn ở nơi đó; họ bèn sấp mình xuống đất trước mặt người."));
visuperobject.biblemap.put("b1_44_15", new visuperobject(1,44,15,"Giô-sép hỏi: Các ngươi gây nên nông nỗi chi vậy? Há chẳng biết một kẻ như ta đây có tài bói sao?"));
visuperobject.biblemap.put("b1_44_16", new visuperobject(1,44,16,"Giu-đa đáp: Chúng tôi sẽ nói cùng chúa làm sao? sẽ phân lại làm sao? sẽ chữa mình chúng tôi làm sao? Ðức Chúa Trời đã thấu rõ điều gian ác của tôi tớ chúa rồi. Nầy, chúng tôi cùng kẻ đã bị bắt được chén nơi tay đều làm kẻ tôi tớ cho chúa."));
visuperobject.biblemap.put("b1_44_17", new visuperobject(1,44,17,"Nhưng Giô-sép đáp rằng: Ta chẳng hề có ý làm như vậy đâu! Người mà đã bị bắt được chén nơi tay sẽ làm tôi mọi ta; còn các ngươi hãy trở về nhà cha mình bình yên."));
visuperobject.biblemap.put("b1_44_18", new visuperobject(1,44,18,"Giu-đa bèn lại gần Giô-sép mà thưa rằng: Vì chúa ngang vai Pha-ra-ôn, xin lỗi chúa, chớ nổi giận cùng kẻ tôi tớ, để tôi nói một lời cho chúa nghe."));
visuperobject.biblemap.put("b1_44_19", new visuperobject(1,44,19,"Chúa có hỏi kẻ tôi tớ rằng: Các ngươi còn cha hay là anh em nào chăng?"));
visuperobject.biblemap.put("b1_44_20", new visuperobject(1,44,20,"Có thưa lại cùng chúa rằng: Chúng tôi còn một cha già, cùng một con trai đã sanh muộn cho người. Anh ruột đứa nầy đã chết rồi, nên trong vòng các con của mẹ nó, nó còn một mình, và cha thương nó lắm."));
visuperobject.biblemap.put("b1_44_21", new visuperobject(1,44,21,"Vả, chúa có dặn rằng: Hãy dẫn nó xuống đến ta, hầu cho ta thấy nó được tận mắt."));
visuperobject.biblemap.put("b1_44_22", new visuperobject(1,44,22,"Và chúng tôi có thưa lại cùng chúa rằng: Ðứa trẻ chẳng lìa cha được; nếu lìa ra thì cha sẽ chết."));
visuperobject.biblemap.put("b1_44_23", new visuperobject(1,44,23,"Nhưng chúa lại dạy: Nếu em út không theo các ngươi xuống đây, thì các ngươi chẳng còn thấy mặt ta nữa."));
visuperobject.biblemap.put("b1_44_24", new visuperobject(1,44,24,"Nên khi chúng tôi trở về nhà kẻ tôi tớ chúa, là cha tôi, thì chúng tôi có thuật lại cho người nghe những lời của chúa."));
visuperobject.biblemap.put("b1_44_25", new visuperobject(1,44,25,"Kế ấy, cha lại biểu chúng tôi rằng: Hãy trở xuống đặng mua một ít lương thực."));
visuperobject.biblemap.put("b1_44_26", new visuperobject(1,44,26,"Chúng tôi thưa lại rằng: Không được; nhưng nếu em út đi theo cùng, thì mới trở xuống được; bằng không, thì chúng tôi không thế ra mắt người đó."));
visuperobject.biblemap.put("b1_44_27", new visuperobject(1,44,27,"Kẻ tôi tớ chúa, là cha tôi, đáp rằng: Bây biết rằng vợ ta sanh cho ta hai đứa con trai;"));
visuperobject.biblemap.put("b1_44_28", new visuperobject(1,44,28,"một đứa đã từ ta đi mất-biệt; ta cũng đã nói: Chắc đã bị thú dữ xé rồi, vì đến bây giờ chưa thấy lại."));
visuperobject.biblemap.put("b1_44_29", new visuperobject(1,44,29,"Nếu bây còn dắt đứa nầy đi khỏi mặt ta nữa, rủi có điều tai hại chi xảy đến cho nó, tức nhiên bây sẽ làm cho kẻ tóc bạc nầy đau lòng xót dạ mà xuống âm phủ."));
visuperobject.biblemap.put("b1_44_30", new visuperobject(1,44,30,"Cha thương em út tôi lắm đến đỗi hai linh hồn khắng khít nhau; nếu bây giờ, khi tôi trở về nơi kẻ tôi tớ chúa, là cha tôi, mà không có em út đó,"));
visuperobject.biblemap.put("b1_44_31", new visuperobject(1,44,31,"thì khi vừa thấy con út không còn nữa, chắc người sẽ chết. Nếu vậy, các tôi tớ chúa sẽ làm cha già tóc bạc của chúng tôi, cũng là kẻ tôi tớ chúa, đau lòng xót dạ xuống âm phủ."));
visuperobject.biblemap.put("b1_44_32", new visuperobject(1,44,32,"Vì kẻ tôi tớ nầy có chịu bảo lãnh đứa con út đó mà thưa rằng: Nếu con không đem em về cho cha, thì sẽ cam tội cùng cha mãi mãi."));
visuperobject.biblemap.put("b1_44_33", new visuperobject(1,44,33,"Vậy bây giờ, xin cho kẻ tôi tớ chúa ở tôi mọi thế cho đứa trẻ, đặng nó theo trở lên cùng các anh mình."));
visuperobject.biblemap.put("b1_44_34", new visuperobject(1,44,34,"Vì, nếu đứa trẻ không theo về, làm sao tôi dám về cùng cha tôi? Ôi, tôi nỡ nào thấy điều tai họa của cha tôi ư!"));
visuperobject.biblemap.put("b1_45_1", new visuperobject(1,45,1,"Bây giờ, Giô-sép không còn thể nào cầm lòng cho đậu được trước mặt các người hầu chung quanh, bèn la lên rằng: Hãy đuổi họ ra hết thảy! Khi Giô-sép tỏ thật cùng các anh em mình, thì không có một người nào khác ở tại đó hết."));
visuperobject.biblemap.put("b1_45_2", new visuperobject(1,45,2,"Người cất tiếng lên khóc; dân Ê-díp-tô nghe và nhà Pha-ra-ôn nghe nữa."));
visuperobject.biblemap.put("b1_45_3", new visuperobject(1,45,3,"Giô-sép nói cùng anh em rằng: Tôi là Giô-sép. Cha tôi còn sống chăng? Nhưng trước mặt người các anh em bối rối, chẳng đáp lời được."));
visuperobject.biblemap.put("b1_45_4", new visuperobject(1,45,4,"Người lại nói rằng: Các anh em hãy lại gần tôi. Họ bèn lại gần. Người nói: Tôi là Giô-sép, em mà các anh đã bán đặng bị dẫn qua xứ Ê-díp-tô."));
visuperobject.biblemap.put("b1_45_5", new visuperobject(1,45,5,"Bây giờ, đừng sầu não, và cũng đừng tiếc chi về điều các anh đã bán tôi đặng bị dẫn đến xứ nầy; vì để giữ gìn sự sống các anh, nên Ðức Chúa Trời đã sai tôi đến đây trước các anh."));
visuperobject.biblemap.put("b1_45_6", new visuperobject(1,45,6,"Kìa, hai năm rồi trong xứ đã bị đói kém, và còn năm năm nữa sẽ không còn cày cấy chi, gặt hái chi được hết."));
visuperobject.biblemap.put("b1_45_7", new visuperobject(1,45,7,"Ðức Chúa Trời sai tôi đến đây trước, đặng làm cho các anh còn nối dòng trên mặt đất, và nương một sự giải cứu lớn đặng giữ gìn sự sống cho anh em."));
visuperobject.biblemap.put("b1_45_8", new visuperobject(1,45,8,"Không, chẳng phải các anh sai tôi đến đây đâu, ấy là Ðức Chúa Trời; Ngài dường đặt tôi làm cha Pha-ra-ôn, cai quản cả nhà người, vì trị khắp xứ Ê-díp-tô."));
visuperobject.biblemap.put("b1_45_9", new visuperobject(1,45,9,"Các anh hãy mau mau trở về cha tôi đi, và nói với người rằng: Giô-sép, con của cha, có nói như vầy: Ðức Chúa Trời đã đặt tôi làm chúa cả xứ Ê-díp-tô, cha hãy xuống với tôi; xin đừng chậm trễ,"));
visuperobject.biblemap.put("b1_45_10", new visuperobject(1,45,10,"cha, các con, các cháu, các chiên, bò cùng tài vật của cha sẽ ở tại xứ Gô-sen gần tôi đây."));
visuperobject.biblemap.put("b1_45_11", new visuperobject(1,45,11,"Ở đó tôi sẽ nuôi cha, (vì còn năm năm đói kém nữa), e khi cha, người nhà cha, và hết thảy loài vật của cha phải bị ách mà hao mòn."));
visuperobject.biblemap.put("b1_45_12", new visuperobject(1,45,12,"Này, các anh và Bên-gia-min, em tôi, đã thấy tận mắt rằng, chánh miệng tôi đã nói chuyện cùng các anh em đó."));
visuperobject.biblemap.put("b1_45_13", new visuperobject(1,45,13,"Vậy, hãy thuật lại các điều vinh hiển của tôi tại xứ Ê-díp-tô, cùng mọi việc mà anh em đã thấy cho cha nghe, và hãy mau mau dời cha xuống đây."));
visuperobject.biblemap.put("b1_45_14", new visuperobject(1,45,14,"Ðoạn, người ôm lấy cổ Bên-gia-min, em mình, mà khóc; Bên-gia-min cũng ôm cổ người mà khóc."));
visuperobject.biblemap.put("b1_45_15", new visuperobject(1,45,15,"Người cũng ôm các anh mình mà khóc. Ðoạn, anh em nói chuyện cùng người."));
visuperobject.biblemap.put("b1_45_16", new visuperobject(1,45,16,"Lập tức, tiếng đồn đến nhà Pha-ra-ôn rằng: Anh em Giô-sép đã đến. Pha-ra-ôn và quần thần nghe lấy làm đẹp dạ."));
visuperobject.biblemap.put("b1_45_17", new visuperobject(1,45,17,"Pha-ra-ôn bèn phán cùng Giô-sép rằng: Hãy dặn các anh em ngươi rằng: Hãy chỡ đồ lên lừa, đi trở về xứ Ca-na-an,"));
visuperobject.biblemap.put("b1_45_18", new visuperobject(1,45,18,"rước cha và người nhà của các ngươi xuống ở cùng ta. Ta sẽ nhượng cho vật tốt nhất trong xứ Ê-díp-tô, và các ngươi sẽ hưỡng mầu mỡ của đất."));
visuperobject.biblemap.put("b1_45_19", new visuperobject(1,45,19,"Còn ta dặn ngươi hãy nói lại cùng họ như vậy: Hãy đem xe cộ từ xứ Ê-díp-tô về cho con nhỏ và vợ mình, cùng dời cha các ngươi xuống đây."));
visuperobject.biblemap.put("b1_45_20", new visuperobject(1,45,20,"Ðừng tiếc tài vật mình, vì vật tốt nhất của xứ Ê-díp-tô sẽ về phần các ngươi."));
visuperobject.biblemap.put("b1_45_21", new visuperobject(1,45,21,"Các con trai của Y-sơ-ra-ên làm y như lời; Giô-sép vâng mạng Pha-ra-ôn, đưa những xe cộ cho anh em mình, và luôn vật thực dùng trong lúc đi đường."));
visuperobject.biblemap.put("b1_45_22", new visuperobject(1,45,22,"Người cũng đưa áo xống mặc đổi thay cho mỗi người, còn Bên-gia-min, người cho trăm miếng bạc cùng năm bộ áo xống."));
visuperobject.biblemap.put("b1_45_23", new visuperobject(1,45,23,"Người cũng sai đem về cho cha mình mười con lừa chỡ các vật quí nhất trong xứ Ê-díp-tô, mười con lừa cái chỡ lúa, bánh, và lương thực để dành dùng trong khi cha đi đường."));
visuperobject.biblemap.put("b1_45_24", new visuperobject(1,45,24,"Vậy, Giô-sép đưa anh em mình lên đường. Lại dặn họ rằng: Xin anh em đừng cải lẫy nhau dọc đường."));
visuperobject.biblemap.put("b1_45_25", new visuperobject(1,45,25,"Các anh em ở Ê-díp-tô trở lên và đến xứ Ca-na-an, nơi Gia-cốp, cha mình,"));
visuperobject.biblemap.put("b1_45_26", new visuperobject(1,45,26,"thuật lại lời nầy mà rằng: Giô-sép hãy còn sống; lại ấy là người đang cai trị cả xứ Ê-díp-tô. Nhưng lòng Gia-cốp vẫn vô tình vì người không tin lời họ nói."));
visuperobject.biblemap.put("b1_45_27", new visuperobject(1,45,27,"Anh em thuật lại cho người nghe mọi lời Giô-sép đã nói; Gia-cốp vừa thấy các xe cộ của Giô-sép sai đem về đặng rước mình, thì tâm thần người tỉnh lại,"));
visuperobject.biblemap.put("b1_45_28", new visuperobject(1,45,28,"bèn nói rằng: Thôi, biết rồi; Giô-sép, con trai ta, hãy còn sống; ta sẽ đi thăm nó trước khi ta qua đời."));
visuperobject.biblemap.put("b1_46_1", new visuperobject(1,46,1,"Y-sơ-ra-ên ra đi, đem theo các tài vật mình. Ðến Bê -e-Sê-ba, người bày của lễ dâng cho Ðức Chúa Trời của Y-sác, cha mình."));
visuperobject.biblemap.put("b1_46_2", new visuperobject(1,46,2,"Trong một sự hiện thấy ban đêm kia, Ðức Chúa Trời có phán cùng Y-sơ-ra-ên rằng: Hỡi Gia-cốp, Gia-cốp! Y-sơ-ra-ên đáp rằng: Có tôi đây."));
visuperobject.biblemap.put("b1_46_3", new visuperobject(1,46,3,"Ðức Chúa Trời phán: Ta là Ðức Chúa Trời, Ðức Chúa Trời của cha ngươi. Hãy xuống Ê-díp-tô, đừng sợ chi, vì tại đó ta sẽ làm cho ngươi thành một nước lớn."));
visuperobject.biblemap.put("b1_46_4", new visuperobject(1,46,4,"Chánh ta sẽ xuống đến đó với ngươi, và chánh ta cũng sẽ dẫn ngươi về chẳng sai. Giô-sép sẽ vuốt mắt ngươi nhắm lại."));
visuperobject.biblemap.put("b1_46_5", new visuperobject(1,46,5,"Từ Bê -e-Sê-ba, Gia-cốp khởi đi; các con trai Y-sơ-ra-ên để Gia-cốp, cha mình, cùng các vợ và con mình lên xe cộ của Pha-ra-ôn đã sai đến rước những người đó."));
visuperobject.biblemap.put("b1_46_6", new visuperobject(1,46,6,"Chúng cũng đem theo các súc vật và của cải mình đã gây dựng tại xứ Ca-na-an, mà đi đến cứ Ê-díp-tô."));
visuperobject.biblemap.put("b1_46_7", new visuperobject(1,46,7,"Vậy, Gia-cốp cùng cả nhà người, nào các con trai, nào các cháu trai, nào các con gái, nào các cháu gái, thảy đều xuống xứ Ê-díp-tô."));
visuperobject.biblemap.put("b1_46_8", new visuperobject(1,46,8,"Ðây là tên các con trai của Y-sơ-ra-ên, tức là Gia-cốp, đến xứ Ê-díp-tô: Con trưởng nam của Gia-cốp là Ru-bên."));
visuperobject.biblemap.put("b1_46_9", new visuperobject(1,46,9,"Các con trai của Ru-bên là Hê-nóc, Pha-lu, Hết-rôn, và Cạt-mi."));
visuperobject.biblemap.put("b1_46_10", new visuperobject(1,46,10,"Các con trai của Si-mê-ôn là Giê-mu-ên, Gia-min, Ô-hát, Gia-kin, Xô-ha, và Sau-lơ, tức con của người vợ xứ Ca-na-an."));
visuperobject.biblemap.put("b1_46_11", new visuperobject(1,46,11,"Các con trai của Lê-vi là Ghẹt-sôn, Kê-hát, và Mê-ra-ri."));
visuperobject.biblemap.put("b1_46_12", new visuperobject(1,46,12,"Các con trai của Giu-đa là Ê-rơ, O-nan, Sê-la, Phê-rết và Sê-rách. Nhưng Ê-rơ và O-nan đã thác tại xứ Ca-na-an; con của Phê-rết là Hết-rôn, và Ha-mun."));
visuperobject.biblemap.put("b1_46_13", new visuperobject(1,46,13,"Các con trai của Y-sa-ca là Thô-la, Phu-va, Gióp và Sim-rôn."));
visuperobject.biblemap.put("b1_46_14", new visuperobject(1,46,14,"Các con trai của Sa-bu-lôn là Sê-rết, Ê-lôn, và Gia-lê-ên."));
visuperobject.biblemap.put("b1_46_15", new visuperobject(1,46,15,"Vả, các con trai, và một con gái tên là Ði-na, mà Lê-a sanh cho Gia-cốp khi ở tại Pha-đan-a-ram, cùng các cháu đó, hết thảy là ba mươi ba người."));
visuperobject.biblemap.put("b1_46_16", new visuperobject(1,46,16,"Các con trai của Gát là Xi-phi-ôn, Ha-ghi, Su-ni, Ét-bôn, Ê-ri, A-rô-đi và A-rê-li."));
visuperobject.biblemap.put("b1_46_17", new visuperobject(1,46,17,"Các con trai của A-se là Dim-na, Dích-và, Dích-vi, và Bê-ri-a, cùng Sê-rách, em gái các người đó. Con của Bê-ri-a là Hê-be, và Manh-ki-ên."));
visuperobject.biblemap.put("b1_46_18", new visuperobject(1,46,18,"Vả, các con của nàng Xinh-ba sanh cho Gia-cốp, cùng các cháu đó, hết thảy là mười sáu người. Nàng là con đòi của của La-ban đã cho theo hầu Lê-a, con gái mình."));
visuperobject.biblemap.put("b1_46_19", new visuperobject(1,46,19,"Các con trai của Ra-chên, vợ Gia-cốp, Giô-sép, và Bên-gia-min."));
visuperobject.biblemap.put("b1_46_20", new visuperobject(1,46,20,"Giô-sép đã có con tại xứ Ê-díp-tô, là Ma-na-se, và Ép-ra-im, mà Ách-nát, con gái của Phô-ti-phê-ra, thầy cả thành Ôn, đã sanh cho người."));
visuperobject.biblemap.put("b1_46_21", new visuperobject(1,46,21,"Các con trai của Bên-gia-min là Bê-la, Bê-kê, Ách-bên, Giê-ra, Na-a-man, Ê-hi, Rô-sơ, Mốp-bim, Hốp-bim, và A-rết."));
visuperobject.biblemap.put("b1_46_22", new visuperobject(1,46,22,"Vả, các con trai của Ra-chên sanh cho Gia-cốp, cùng các cháu đó, hết thảy là mười bốn người."));
visuperobject.biblemap.put("b1_46_23", new visuperobject(1,46,23,"Con trai của Ðan là Hu-sim."));
visuperobject.biblemap.put("b1_46_24", new visuperobject(1,46,24,"Các con trai của Nép-ta-li là Giát-sê-ên, Gu-ni, Dít-se, và Si-lem."));
visuperobject.biblemap.put("b1_46_25", new visuperobject(1,46,25,"Vả, các con trai của nàng Bi-la sanh cho Gia-cốp, cùng các cháu đó, hết thảy là bảy người. Nàng là con đòi của La-ban đã cho theo hầu Ra-chên, con gái mình."));
visuperobject.biblemap.put("b1_46_26", new visuperobject(1,46,26,"Các người đi đến xứ Ê-díp-tô với Gia-cốp, tức là các người do nơi Gia-cốp sanh, nếu không kể các nàng dâu, thì hết thảy là sáu mươi sáu người."));
visuperobject.biblemap.put("b1_46_27", new visuperobject(1,46,27,"Con của Giô-sép đã sanh tại Ê-díp-tô được hai. Vậy, các người thuộc về nhà Gia-cốp đi đến Ê-díp-tô, cọng hết thảy là bảy mươi người."));
visuperobject.biblemap.put("b1_46_28", new visuperobject(1,46,28,"Gia-cốp sai Giu-đa đi đến trước đặng xin Giô-sép đưa mình vào bờ cõi Gô-sen. Vậy, họ đều vào xứ Gô-sen."));
visuperobject.biblemap.put("b1_46_29", new visuperobject(1,46,29,"Giô-sép thắng xe đi lên Gô-sen, đón Y-sơ-ra-ên, cha mình. Người ra mắt cha, ôm choàng lấy người và khóc một hồi lâu."));
visuperobject.biblemap.put("b1_46_30", new visuperobject(1,46,30,"Y-sơ-ra-ên nói cùng Giô-sép rằng: Chớ chi cho cha chết bây giờ đi! vì cha đã thấy được mặt con, và biết con vẫn còn sống."));
visuperobject.biblemap.put("b1_46_31", new visuperobject(1,46,31,"Ðoạn Giô-sép nói cùng anh em và người nhà cha mình rằng: Tôi sẽ lên trước tâu cho Pha-ra-ôn hay rằng: Anh em và cả nhà cha tôi, ở xứ Ca-na-an đã đến cùng tôi."));
visuperobject.biblemap.put("b1_46_32", new visuperobject(1,46,32,"Họ vốn làm nghề chăn chiên, nuôi bầy súc vật; có dẫn theo hết bầy chiên, bò và các tài vật của mình."));
visuperobject.biblemap.put("b1_46_33", new visuperobject(1,46,33,"Và khi Pha-ra-ôn truyền gọi anh em mà hỏi rằng: Các ngươi làm nghề chi?"));
visuperobject.biblemap.put("b1_46_34", new visuperobject(1,46,34,"Thì hãy tâu rằng: Kẻ tôi tớ chúa cùng tổ phụ chúng tôi vẫn làm nghề nuôi súc vật từ thuở còn nhỏ cho đến giờ. Ấy hầu cho anh em đặng ở lại xứ Gô-sen, vì dân Ê-díp-tô có tánh gớm ghê hết thảy kẻ chăn chiên lắm."));
visuperobject.biblemap.put("b1_47_1", new visuperobject(1,47,1,"Giô-sép đến tâu mọi điều đó cho Pha-ra-ôn hay, và nói rằng: Cha và anh em tôi đã ở xứ Ca-na-an đến, có đem theo chiên, bò, cùng các tài vật của mình. Hiện bây giờ đương ở trong xứ Gô-sen."));
visuperobject.biblemap.put("b1_47_2", new visuperobject(1,47,2,"Người bèn đưa năm người trong bọn anh em mình vào yết kiến Pha-ra-ôn."));
visuperobject.biblemap.put("b1_47_3", new visuperobject(1,47,3,"Pha-ra-ôn hỏi: Các ngươi làm nghề chi? Tâu rằng: Kẻ tôi tớ bệ hạ là kẻ chăn chiên, như tổ phụ chúng tôi khi trước."));
visuperobject.biblemap.put("b1_47_4", new visuperobject(1,47,4,"Rồi lại tâu rằng: Ấy đặng kiều ngụ trong xứ mà kẻ tôi tớ bệ hạ đã đến; vì xứ Ca-na-an đói kém lớn lắm, không còn đồng cỏ chi hết cho bầy súc vật ăn. Vậy, xin phép cho kẻ tôi tớ bệ hạ ngụ tại xứ Gô-sen."));
visuperobject.biblemap.put("b1_47_5", new visuperobject(1,47,5,"Pha-ra-ôn phán cùng Giô-sép như vầy: Cha và anh em ngươi đã đến cùng ngươi;"));
visuperobject.biblemap.put("b1_47_6", new visuperobject(1,47,6,"vậy, xứ Ê-díp-tô sẵn dành cho ngươi; hãy cho cha và anh em ở chốn nào tốt hơn hết trong xứ; hãy cho ở tại xứ Gô-sen vậy. Và nếu trong các người đó, ngươi biết ai giỏi, hãy đặt họ chăn các bầy súc vật của ta."));
visuperobject.biblemap.put("b1_47_7", new visuperobject(1,47,7,"Ðoạn, Giô-sép dẫn Gia-cốp, cha mình, đến yết-kiến Pha-ra-ôn. Gia-cốp chúc phước cho Pha-ra-ôn."));
visuperobject.biblemap.put("b1_47_8", new visuperobject(1,47,8,"Pha-ra-ôn hỏi Gia-cốp rằng: Ngươi hưởng thọ được bao nhiêu tuổi?"));
visuperobject.biblemap.put("b1_47_9", new visuperobject(1,47,9,"Gia-cốp tâu rằng: Người năm tôi sống ở đời phiêu lưu hết thảy là một trăm ba mươi năm; các năm của đời tôi lấy làm ngắn-ngủi và lại nhọc nhằn, chẳng bằng những năm bình sanh của tổ phụ tôi khi người ở phiêu lưu đó."));
visuperobject.biblemap.put("b1_47_10", new visuperobject(1,47,10,"Gia-cốp chúc phước cho Pha-ra-ôn một lần nữa, rồi lui ra khỏi mặt người."));
visuperobject.biblemap.put("b1_47_11", new visuperobject(1,47,11,"Vậy, Giô-sép vâng mạng Pha-ra-ôn, định chỗ ở cho cha và anh em mình, cho họ một sở đất tốt nhứt trong xứ Ê-díp-tô làm sản nghiệp, tại miền Ram-se."));
visuperobject.biblemap.put("b1_47_12", new visuperobject(1,47,12,"Giô-sép, tùy theo số người, cấp lương thực cho cha, anh em và cả nhà cha mình."));
visuperobject.biblemap.put("b1_47_13", new visuperobject(1,47,13,"Vả, sự đói kém lớn lắm, nên trong khắp xứ chẳng còn lương thực nữa; xứ Ê-díp-tô và xứ Ca-na-an đều bị hao mòn vì ách đói kém đó."));
visuperobject.biblemap.put("b1_47_14", new visuperobject(1,47,14,"Giô-sép thâu hết bạc tiền trong xứ Ê-díp-tô và xứ Ca-na-an, tức giá tiền của muôn dân mua lúa; rồi chứa bạc đó vào kho Pha-ra-ôn."));
visuperobject.biblemap.put("b1_47_15", new visuperobject(1,47,15,"Khi bạc tiền trong xứ Ê-díp-tô và xứ Ca-na-an đã hết, thì tất cả dân Ê-díp-tô đều đến cùng Giô-sép mà kêu rằng: Xin cho chúng tôi lương thực; lẽ nào vì cớ hết tiền mà chúng tôi phải chết trước mặt chúa sao?"));
visuperobject.biblemap.put("b1_47_16", new visuperobject(1,47,16,"Giô-sép đáp rằng: Nếu hết bạc tiền rồi, hãy giao súc vật các ngươi cho ta, ta sẽ phát lương thực đổi lại."));
visuperobject.biblemap.put("b1_47_17", new visuperobject(1,47,17,"Ðoạn, dân chúng bèn dẫn súc vật lại cho Giô-sép; Giô-sép phát lương thực đổi lấy ngựa, bầy chiên, bầy bò, và lừa. Năm đó, người thâu các bầy súc vật của họ mà đổi thế lương thực cho."));
visuperobject.biblemap.put("b1_47_18", new visuperobject(1,47,18,"Mãn năm rồi, năm sau dân chúng lại đến kêu cùng người rằng: tiền bạc sạch trơn, bầy súc vật đã giao cho chúa; bây giờ chỉ sẵn dành cho chúa bổn thân và đất ruộng."));
visuperobject.biblemap.put("b1_47_19", new visuperobject(1,47,19,"Lẽ nào chúng tôi và đất ruộng phải hao mòn trước mặt chúa sao? Hãy mua đổi lấy lương thực chúng tôi và đất ruộng luôn đi. Vậy, chúng tôi cùng đất ruộng sẽ làm tôi mọi cho Pha-ra-ôn. Hãy cho giống chi đặng gieo, hầu cho chúng tôi sống khỏi chết, và đất không phải bỏ hoang."));
visuperobject.biblemap.put("b1_47_20", new visuperobject(1,47,20,"Giô-sép bèn mua hết thảy ruộng đất trong xứ Ê-díp-tô cho Pha-ra-ôn; vì sự đói kém thúc giục nên mọi người Ê-díp-tô đều đem bán ruộng mình; vậy, ruộng đất đều thuộc về Pha-ra-ôn."));
visuperobject.biblemap.put("b1_47_21", new visuperobject(1,47,21,"Còn dân chúng, từ đầu nầy đến đầu kia, người đều dời về ở trong các thành."));
visuperobject.biblemap.put("b1_47_22", new visuperobject(1,47,22,"Song ruộng đất của những thầy cả thì Giô-sép không mua đến, vì những thầy cả có lãnh một phần lương của Pha-ra-ôn đã định; vậy, họ ăn phần lương thực của Pha-ra-ôn đã cấp cho. Thế cho nên những thầy cả chẳng đem bán đất của mình."));
visuperobject.biblemap.put("b1_47_23", new visuperobject(1,47,23,"Giô-sép nói cùng dân chúng rằng: Nầy, ta đã mua các ngươi và ruộng đất cho Pha-ra-ôn; đây, hột giống cho các ngươi đặng gieo mạ trong ruộng đó."));
visuperobject.biblemap.put("b1_47_24", new visuperobject(1,47,24,"Ðến mùa gặt, phải nộp cho Pha-ra-ôn một phần năm, còn bốn phần kia để cho các ngươi làm giống gieo mạ, dùng lương thực cho mình, cho người nhà cùng cho các con nhỏ mình."));
visuperobject.biblemap.put("b1_47_25", new visuperobject(1,47,25,"Dân chúng nói rằng: Chúa đã cứu mạng chúng tôi! Cầu xin cho chúng tôi được nhờ ơn trước mặt chúa, thì sẽ làm tôi mọi cho Pha-ra-ôn."));
visuperobject.biblemap.put("b1_47_26", new visuperobject(1,47,26,"Về việc đó, Giô-sép bèn định một luật, cho đến ngày nay hãy còn, buộc đất Ê-díp-tô phải nộp thuế cho Pha-ra-ôn một phần năm hoa lợi mình. Chỉ đất ruộng của những thầy cả chẳng thuộc về Pha-ra-ôn."));
visuperobject.biblemap.put("b1_47_27", new visuperobject(1,47,27,"Vậy, Y-sơ-ra-ên trú ngụ tại miền Gô-sen thuộc về xứ Ê-díp-tô, gây được cơ nghiệp tại đó, sanh sản và thêm lên bội phần."));
visuperobject.biblemap.put("b1_47_28", new visuperobject(1,47,28,"Gia-cốp kiều ngụ trong xứ Ê-díp-tô được mười bảy năm, hưởng thọ được một trăm bốn mươi bảy tuổi."));
visuperobject.biblemap.put("b1_47_29", new visuperobject(1,47,29,"Khi ngày gần chết, Y-sơ-ra-ên gọi Giô-sép, con trai mình, mà nói rằng: Nếu cha được nhờ ơn trước mặt con, xin hãy để tay trên đùi cha cậy hết lòng nhơn từ và thành thực ở cùng cha, xin con đừng chôn cha tại đất Ê-díp-tô."));
visuperobject.biblemap.put("b1_47_30", new visuperobject(1,47,30,"Khi cha an-giấc cùng tổ phụ rồi, hãy đem cha ra khỏi Ê-díp-tô; chôn chung cùng mồ mả của người. Giô-sép thưa rằng: Con sẽ làm y theo lời cha dặn."));
visuperobject.biblemap.put("b1_47_31", new visuperobject(1,47,31,"Giô-sép nói: Con hãy thề đi. Giô-sép bèn thề. Ðoạn, Y-sơ-ra-ên quì lạy nơi đầu giường mình."));
visuperobject.biblemap.put("b1_48_1", new visuperobject(1,48,1,"Vả, khi các việc đó qua rồi, có người nói cùng Giô-sép rằng: Nầy cha người đau; Giô-sép bèn đem Ma-na-se và Ép-ra-im, hai đứa con trai mình, cùng đi đến."));
visuperobject.biblemap.put("b1_48_2", new visuperobject(1,48,2,"Họ cho Gia-cốp hay và nói rằng: Nầy Giô-sép, con trai ông, đến thăm ông đó; Y-sơ-ra-ên cố gượng ngồi dậy trên giường."));
visuperobject.biblemap.put("b1_48_3", new visuperobject(1,48,3,"Gia-cốp nói cùng Giô-sép rằng: Ðức Chúa Trời toàn năng đã hiện ra, bà ban phước cho cha tại Lu-xơ, trong xứ Ca-na-an,"));
visuperobject.biblemap.put("b1_48_4", new visuperobject(1,48,4,"mà phán rằng: Nầy ta sẽ làm cho ngươi sanh sản và thêm nhiều, làm thành một hội dân; ta sẽ cho dòng dõi ngươi xứ nầy làm cơ nghiệp đời đời."));
visuperobject.biblemap.put("b1_48_5", new visuperobject(1,48,5,"Bây giờ, hai đứa con trai đã sanh cho con tại xứ Ê-díp-tô trước khi cha đến, là Ép-ra-im và Ma-na-se, cũng sẽ thuộc về cha như Ru-bên và Si-mê-ôn vậy."));
visuperobject.biblemap.put("b1_48_6", new visuperobject(1,48,6,"Còn mấy đứa mà con sanh kế đó, thì sẽ thuộc về con; về phần hưởng cơ nghiệp, chúng nó sẽ đồng một thể cùng anh em mình."));
visuperobject.biblemap.put("b1_48_7", new visuperobject(1,48,7,"Khi cha ở Pha-đan trở về xứ Ca-na-an, thì Ra-chên chết dọc đường có mặt cha, gần Ê-phơ-rát; cha chôn người ở bên con đường đi về Ê-phơ-rát (tức là Bết-lê-hem)."));
visuperobject.biblemap.put("b1_48_8", new visuperobject(1,48,8,"Y-sơ-ra-ên thấy các con trai Giô-sép, bèn hỏi rằng: Những đứa nầy là ai?"));
visuperobject.biblemap.put("b1_48_9", new visuperobject(1,48,9,"Giô-sép thưa rằng: Ấy là những con trai của con mà Ðức Chúa Trời đã cho tại xứ nầy. Y-sơ-ra-ên lại nói: Xin hãy đem đến đây, đặng cha chúc phước cho chúng nó."));
visuperobject.biblemap.put("b1_48_10", new visuperobject(1,48,10,"Vả, mắt của Y-sơ-ra-ên già nên làng, chẳng thấy chi nữa, bèn biểu chúng nó lại gần, ôm choàng và hôn."));
visuperobject.biblemap.put("b1_48_11", new visuperobject(1,48,11,"Y-sơ-ra-ên nói cùng Giô-sép rằng: Trước cha tưởng chẳng còn thấy được mặt con, nhưng bây giờ Ðức Chúa Trời lại làm cho cha thấy được đến dòng dõi con nữa."));
visuperobject.biblemap.put("b1_48_12", new visuperobject(1,48,12,"Giô-sép dẫn hai đứa con trai ra khỏi hai đầu gối cha mình, rồi sấp mình xuống đất."));
visuperobject.biblemap.put("b1_48_13", new visuperobject(1,48,13,"Ðoạn, người dẫn hai đứa trẻ lại gần cha; tay hữu thì dẫn Ép-ra-im sang qua phía tả của cha, còn tay tả dắt Ma-na-se sang qua phía hữu."));
visuperobject.biblemap.put("b1_48_14", new visuperobject(1,48,14,"Y-sơ-ra-ên đưa tay mặt ra, để trên đầu Ép-ra-im, là đứa nhỏ, còn tay trái lại để trên đầu Ma-na-se. Người có ý riêng để tay như vậy, vì Ma-na-se là đứa lớn."));
visuperobject.biblemap.put("b1_48_15", new visuperobject(1,48,15,"Rồi người chúc phước cho Giô-sép rằng: Cầu xin Ðức Chúa Trời mà tổ phụ tôi là Áp-ra-ham và Y-sác đã thờ phượng; là Ðức Chúa Trời đã chăn nuôi tôi từ khi mới lọt lòng cho đến ngày nay,"));
visuperobject.biblemap.put("b1_48_16", new visuperobject(1,48,16,"thiên sứ đã cứu tôi ra ngoài vòng hoạn nạn, hãy ban phước cho hai đứa trẻ nầy; nối danh tôi và tổ phụ tôi là Áp-ra-ham và Y-sác, và cho chúng nó thêm lên nhiều vô số trên mặt đất!"));
visuperobject.biblemap.put("b1_48_17", new visuperobject(1,48,17,"Nhưng Giô-sép thấy cha mình để tay hữu trên đầu Ép-ra-im, thì có ý bất bình, liền nắm lấy tay cha đã để lên đầu Ép-ra-im mà tráo đổi qua đấu Ma-na-se,"));
visuperobject.biblemap.put("b1_48_18", new visuperobject(1,48,18,"rồi thưa rằng: Chẳng phải vậy, cha. Ðứa nầy đầu lòng, để tay hữu cha trên đầu nó mới phải chớ."));
visuperobject.biblemap.put("b1_48_19", new visuperobject(1,48,19,"Nhưng cha người không chịu và cãi rằng: Cha biết, con, cha biết. Nó sẽ trở nên một dân; nó cũng sẽ lớn vậy, con; song thể nào em nó cũng sẽ lớn hơn và dòng dõi nó sẽ thành ra vô số nước."));
visuperobject.biblemap.put("b1_48_20", new visuperobject(1,48,20,"Trong ngày đó, người chúc phước cho hai đứa con trai nầy mà nói rằng: Ấy vì ngươi mà dân Y-sơ-ra-ên sẽ chúc phước nhau rằng: Cầu xin Ðức Chúa Trời làm cho ngươi được giống như Ép-ra-im và Ma-na-se. Vậy, Gia-cốp đặt Ép-ra-im trước Ma-na-se."));
visuperobject.biblemap.put("b1_48_21", new visuperobject(1,48,21,"Y-sơ-ra-ên lại nói cùng Giô-sép rằng: Nầy, cha sẽ thác, nhưng Ðức Chúa Trời sẽ phù hộ và đem các con trở về xứ tổ phụ."));
visuperobject.biblemap.put("b1_48_22", new visuperobject(1,48,22,"Còn cha sẽ cho con một phần đất trổi hơn các anh em, là phần đất của cha đã dùng cung-kiếm đoạt lấy của dân A-mô-rít đó."));
visuperobject.biblemap.put("b1_49_1", new visuperobject(1,49,1,"Gia-cốp gọi các con trai mình lại và nói rằng: Hãy hội lại đây, cha sẽ nói những điều phải xảy đến cho các con ngày sau."));
visuperobject.biblemap.put("b1_49_2", new visuperobject(1,49,2,"Hỡi các con trai Gia-cốp, hãy hội lại mà nghe; Nghe lời Y-sơ-ra-ên, cha của các con."));
visuperobject.biblemap.put("b1_49_3", new visuperobject(1,49,3,"Hỡi Ru-bên! con là trưởng nam của cha, Sức lực cha, và đầu tiên sự mạnh mẽ cha; Vốn có sự tôn trọng và quyền năng tót chúng."));
visuperobject.biblemap.put("b1_49_4", new visuperobject(1,49,4,"Con sôi trào như nước, nên sẽ chẳng phần hơn ai! Vì con đã lên giường cha. Con lên giường cha bèn làm ô làm dơ đó!"));
visuperobject.biblemap.put("b1_49_5", new visuperobject(1,49,5,"Si-mê-ôn và Lê-vi là anh em ruột. Thanh gươm chúng nó thật khí giới hung tàn."));
visuperobject.biblemap.put("b1_49_6", new visuperobject(1,49,6,"Cầu cho tâm hồn cha chớ có đồng mưu, Vinh hiển cha chớ hiệp cùng hội họ; Vì họ đã giết người trong cơn giận dữ, Cắt nhượng bò đực vì ý riêng mình."));
visuperobject.biblemap.put("b1_49_7", new visuperobject(1,49,7,"Ðáng rủa sả thay cơn giận dữ họ, vì thật là hung mạnh! Ðáng rủa sả thay khí giận họ, vì dữ dằn thay! Ta sẽ phân chia họ ra trong nhà Gia-cốp, Tan lạc họ trong dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b1_49_8", new visuperobject(1,49,8,"Hỡi Giu-đa! các anh em sẽ khen ngợi con, Tay con chận cổ quân nghịch, Các con trai cha sẽ quì lạy trước mặt con."));
visuperobject.biblemap.put("b1_49_9", new visuperobject(1,49,9,"Giu-đa là một sư tử tơ; Hỡi con! Con bắt được mồi rồi tha về. Nó sụm gối, nằm khác nào sư tử đực, Như sư tử cái; há ai dám khiến ngồi lên?"));
visuperobject.biblemap.put("b1_49_10", new visuperobject(1,49,10,"Cây phủ việt chẳng hề dời khỏi Giu-đa, Kẻ lập pháp không dứt khỏi giữa chơn nó, Cho đến chừng Ðấng Si-lô hiện tới, Và các dân vâng phục Ðấng đó."));
visuperobject.biblemap.put("b1_49_11", new visuperobject(1,49,11,"Người buộc lừa tơ mình vào gốc nho, Lừa con mình vào nhành nho tốt nhứt. Người giặt áo xống mình vào rượu nho, Cùng lấy huyết nho lau áo tơi mình."));
visuperobject.biblemap.put("b1_49_12", new visuperobject(1,49,12,"Mắt người đỏ vì cớ rượu, Răng nhiều trắng vì cớ sữa."));
visuperobject.biblemap.put("b1_49_13", new visuperobject(1,49,13,"Sa-bu-lôn sẽ ở nơi gành biển, Tức là nơi có tàu đậu; Bờ cõi người chạy về hướng Si-đôn."));
visuperobject.biblemap.put("b1_49_14", new visuperobject(1,49,14,"Y-sa-ca là một con lừa mạnh mẽ, Nằm nghỉ giữa chuồng;"));
visuperobject.biblemap.put("b1_49_15", new visuperobject(1,49,15,"Thấy rằng sự yên ổn là tốt lành, Và đất-đai đẹp lắm thay. Người đã rùn vai vác gánh nặng, Phải vâng phục những điều sưu-dịch."));
visuperobject.biblemap.put("b1_49_16", new visuperobject(1,49,16,"Ðan sẽ xử đoán dân chúng mình, Như một trong các chi phái Y-sơ-ra-ên."));
visuperobject.biblemap.put("b1_49_17", new visuperobject(1,49,17,"Ðan sẽ là một con rắn trên đường, Một con rắn lục trong chốn nẻo cùng, Cắn vó ngựa, Làm cho kẻ cỡi phải té nhào."));
visuperobject.biblemap.put("b1_49_18", new visuperobject(1,49,18,"Hỡi Giê-hô-va! tôi trông ơn chửng-cứu của Ngài!"));
visuperobject.biblemap.put("b1_49_19", new visuperobject(1,49,19,"Còn Gát sẽ bị một đạo binh xông đánh, Nhưng người xông đánh lại và đuổi theo."));
visuperobject.biblemap.put("b1_49_20", new visuperobject(1,49,20,"Do nơi A-se có thực vật ngon, Người sẽ cung cấp mỹ vị cho các vua."));
visuperobject.biblemap.put("b1_49_21", new visuperobject(1,49,21,"Nép-ta-li là nai cái thả chuồng Nói bày nhiều lời văn hoa."));
visuperobject.biblemap.put("b1_49_22", new visuperobject(1,49,22,"Giô-sép là chồi của cây tươi tốt, Mọc gần bên suối nước; Nhành nhánh phủ bao trên ngọn tường."));
visuperobject.biblemap.put("b1_49_23", new visuperobject(1,49,23,"Kẻ cầm cung đã ghẹo-chọc người, Bắn tên vào, và hãm đánh;"));
visuperobject.biblemap.put("b1_49_24", new visuperobject(1,49,24,"Nhờ tay Ðấng toàn năng của Gia-cốp, Nên cung người vẫn bền-chắc; Nhờ Ðấng Chăn chiên, là Ðá của Y-sơ-ra-ên, Nên hai tay người thêm mạnh."));
visuperobject.biblemap.put("b1_49_25", new visuperobject(1,49,25,"Ðức Chúa Trời của Cha sẽ giúp đỡ con; Ðấng toàn năng sẽ ban phước cho con, Tức là phước lành ở chốn trời cao xuống, Cùng phước lành ở nơi vực rộng thẳm lên, Phước lành của vú, và của lòng mẹ."));
visuperobject.biblemap.put("b1_49_26", new visuperobject(1,49,26,"Phước lành cha chúc cho con vượt lần lên Cao hơn các phước lành của tổ phụ cha, Cho đến các chót núi đời đời: Các phước nầy sẽ ở nơi đầu Giô-sép, Nơi trán của chúa các anh em mình."));
visuperobject.biblemap.put("b1_49_27", new visuperobject(1,49,27,"Bên-gia-min là một con chó sói hay cấu-xé; Ban mai đi đánh chết mồi, Chiều phân chia mồi đã được."));
visuperobject.biblemap.put("b1_49_28", new visuperobject(1,49,28,"Các người đó là đầu trưởng của mười hai chi phái Y-sơ-ra-ên; và đó là lời của cha họ nói đương khi chúc phước cho, chúc một lời phước riêng cho mỗi người vậy."));
visuperobject.biblemap.put("b1_49_29", new visuperobject(1,49,29,"Ðoạn, người ra lịnh cho các con trai mà rằng: Cha sẽ về nơi tổ tông, các con hãy chôn cha chung cùng tổ phụ, nơi hang đá tại đồng ruộng Ép-rôn, người Hê-tít,"));
visuperobject.biblemap.put("b1_49_30", new visuperobject(1,49,30,"tức là hang đá ở trong đồng Mặc-bê-la, ngang Mam-rê, thuộc về xứ Ca-na-an, mà Áp-ra-ham đã mua làm mộ địa luôn với đồng ruộng của Ép-rôn"));
visuperobject.biblemap.put("b1_49_31", new visuperobject(1,49,31,"Ấy nơi đó, người ta đã chôn Áp-ra-ham và Sa-ra, vợ người; Y-sác và Rê-be-ca, vợ người; mà lại nơi đó cha cũng đã chôn Lê-a nữa."));
visuperobject.biblemap.put("b1_49_32", new visuperobject(1,49,32,"Cái đồng ruộng cùng hang đá ở tại đó đã mua của dân họ Hếch vậy."));
visuperobject.biblemap.put("b1_49_33", new visuperobject(1,49,33,"Khi trối mấy lời nầy cho các con mình xong, thì Gia-cốp để chơn vào giường lại, rồi tắt hơi, được về cùng tổ tông mình."));
visuperobject.biblemap.put("b1_50_1", new visuperobject(1,50,1,"Giô-sép bèn cúi đầu xuống mặt cha mình, hôn người và khóc."));
visuperobject.biblemap.put("b1_50_2", new visuperobject(1,50,2,"Ðoạn, Giô-sép biểu mấy thầy thuốc hầu việc mình, dùng thuốc thơm xông cho xác cha; thầy thuốc bèn xông cho Y-sơ-ra-ên."));
visuperobject.biblemap.put("b1_50_3", new visuperobject(1,50,3,"Bốn mươi ngày xông thuốc hầu qua, vì ấy là hạn ngày dùng xông thuốc; dân Ê-díp-tô khóc người trong bảy mươi ngày."));
visuperobject.biblemap.put("b1_50_4", new visuperobject(1,50,4,"Khi mãn tang rồi, Giô-sép bèn nói cùng quần thần Pha-ra-ôn rằng: Nếu tôi được ơn trước mặt các ngươi, xin hãy thuật lại lời nầy cho Pha-ra-ôn nghe:"));
visuperobject.biblemap.put("b1_50_5", new visuperobject(1,50,5,"Cha tôi có biểu tôi thề mà rằng: Nầy, cha sẽ chết, con hãy chôn cha nơi mộ địa ta đã mua sẵn rồi trong xứ Ca-na-an. Vậy bây giờ, tôi phải lên đó chôn cha tôi, rồi sẽ trở xuống."));
visuperobject.biblemap.put("b1_50_6", new visuperobject(1,50,6,"Pha-ra-ôn bèn phán rằng: Hãy trở lên chôn cha ngươi, y như lời người đã bắt thề đó."));
visuperobject.biblemap.put("b1_50_7", new visuperobject(1,50,7,"Giô-sép dời xác cha mình trở lên chôn; quần thần Pha-ra-ôn, các bực trưởng lão trong đền, các bực trưởng lão trong xứ Ê-díp-tô,"));
visuperobject.biblemap.put("b1_50_8", new visuperobject(1,50,8,"hết thảy nội nhà Giô-sép, các anh em và nội nhà cha mình đều đi lên theo đưa với người; trong Gô-sen chỉ còn những đứa trẻ và chiên, cùng bò của họ mà thôi."));
visuperobject.biblemap.put("b1_50_9", new visuperobject(1,50,9,"Lại cũng có đem ngựa và xe theo lên nữa: thật là một đám xác rất đông thay!"));
visuperobject.biblemap.put("b1_50_10", new visuperobject(1,50,10,"Khi đến sân đạp lúa của A-tát, ở bên kia sông Giô-đanh, thì họ làm lễ khóc than rất nên trọng thể tại đó; đoạn, Giô-sép để tang cha trong bảy ngày."));
visuperobject.biblemap.put("b1_50_11", new visuperobject(1,50,11,"Dân Ca-na-an, tức là dân xứ ấy, thấy khóc than nơi sân đạp lúa A-tát, thì nói rằng: Ấy là một đám khóc than trọng thể của dân Ê-díp-tô đó! Bởi cớ ấy người ta gọi cái sân nầy tên là A-bên-Mích-ra-im ở bên kia sông Giô-đanh."));
visuperobject.biblemap.put("b1_50_12", new visuperobject(1,50,12,"Vậy, các con trai Gia-cốp làm theo lời cha trối lại,"));
visuperobject.biblemap.put("b1_50_13", new visuperobject(1,50,13,"dời xác người về xứ Ca-na-an, chôn trong hanh đá của đồng Mặc-bê-la ngang Mam-rê, mà Áp-ra-ham đã mua luôn với đồng ruộng Ép-rôn, người Hê-tít, để dùng làm mộ địa."));
visuperobject.biblemap.put("b1_50_14", new visuperobject(1,50,14,"Chôn cha xong rồi, Giô-sép cùng các anh em và các người đi lên theo đưa xác cha, đều trở xuống xứ Ê-díp-tô."));
visuperobject.biblemap.put("b1_50_15", new visuperobject(1,50,15,"Các anh Giô-sép thấy cha mình chết rồi, thì nói với nhau rằng: Có lẽ Giô-sép sẽ ganh ghét chúng ta, và trả thù việc ác chúng ta đã làm cho người chăng."));
visuperobject.biblemap.put("b1_50_16", new visuperobject(1,50,16,"Các anh bèn sai người đến nói cùng Giô-sép rằng: Trước khi qua đời, cha em có trối rằng:"));
visuperobject.biblemap.put("b1_50_17", new visuperobject(1,50,17,"Hãy nói lại cho Giô-sép như vầy: Ôi! xin hãy tha điều ác, tội phạm của các anh con đi, vì chúng nó đã lỗi cũng con đó; nhưng bây giờ cha xin con hãy tha tội kẻ tôi tớ của Ðức Chúa Trời cha. Nghe qua mấy lời nầy, Giô-sép bèn khóc."));
visuperobject.biblemap.put("b1_50_18", new visuperobject(1,50,18,"Chánh các anh người đến sắp mình xuống dưới chơn mà nói rằng: Các anh đây thật là kẻ tôi tớ của em đó."));
visuperobject.biblemap.put("b1_50_19", new visuperobject(1,50,19,"Giô-sép đáp rằng: Các anh đừng sợ chi, vì tôi há thay mặt Ðức Chúa Trời sao?"));
visuperobject.biblemap.put("b1_50_20", new visuperobject(1,50,20,"Các anh toan hại tôi, nhưng Ðức Chúa Trời lại toan làm điều ích cho tôi, hầu cho cứu sự sống cho nhiều người, y như đã xảy đến ngày nay, và giữ gìn sự sống của dân sự đông đảo."));
visuperobject.biblemap.put("b1_50_21", new visuperobject(1,50,21,"Vậy, đừng sợ, tôi sẽ cấp dưỡng các anh và con cái các anh. Ðoạn, Giô-sép an ủy các anh, và lấy lời êm dịu mà nói cùng họ."));
visuperobject.biblemap.put("b1_50_22", new visuperobject(1,50,22,"Giô-sép cùng nhà cha mình kiều ngụ tại xứ Ê-díp-tô; người hưởng thọ được một trăm mười tuổi."));
visuperobject.biblemap.put("b1_50_23", new visuperobject(1,50,23,"Người thấy được các con cháu Ép-ra-im đến đời thứ ba, và cũng có được nâng niu trên gối mình các con của Ma-ki, tức con trai của Ma-na-se, nữa."));
visuperobject.biblemap.put("b1_50_24", new visuperobject(1,50,24,"Kế, Giô-sép nói cùng các anh em rằng: Em sẽ chết, nhưng Ðức Chúa Trời sẽ đến viếng các anh em thật; đem các anh em về xứ mà Ngài đã thề hứa cùng Áp-ra-ham, Y-sác, và Gia-cốp."));
visuperobject.biblemap.put("b1_50_25", new visuperobject(1,50,25,"Giô-sép biểu các con trai của Y-sơ-ra-ên thề mà rằng: Quả thật, Ðức Chúa Trời sẽ đến viḀ¿ng các anh em; xin anh em hãy dời hài cốt tôi khỏi xứ nầy."));
visuperobject.biblemap.put("b1_50_26", new visuperobject(1,50,26,"Ðoạn, Giô-sép qua đời, hưởng thọ được một trăm mười tuổi. Người ta xông thuốc thơm cho xác Giô-sép, và liệm trong một quan tài tại xứ Ê-díp-tô."));

