visuperobject.biblemap.put("b2_1_3", new visuperobject(2,1,3,"Y-sa-ca, Sa-bu-lôn và Bên-gia-min;"));
visuperobject.biblemap.put("b2_1_4", new visuperobject(2,1,4,"Ðan, Nép-ta-li, Gát, và A-se."));
visuperobject.biblemap.put("b2_1_5", new visuperobject(2,1,5,"Hết thảy những người bởi Gia-cốp sanh ra, được bảy mươi người; Giô-sép đã ở tại xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_1_6", new visuperobject(2,1,6,"Vả, Giô-sép và anh em người cùng mọi kẻ đồng đời đó đều chết hết."));
visuperobject.biblemap.put("b2_1_7", new visuperobject(2,1,7,"Con cháu Y-sơ-ra-ên thêm nhiều lạ lùng, nẩy nở ra, và trở nên rất cường thạnh; cả xứ đều đầy dẫy."));
visuperobject.biblemap.put("b2_1_8", new visuperobject(2,1,8,"Nhưng bấy giờ tại nước Ê-díp-tô, có một vua mới lên ngôi, chẳng quen biết Giô-sép."));
visuperobject.biblemap.put("b2_1_9", new visuperobject(2,1,9,"Vua phán cùng dân mình rằng: Nầy, dân Y-sơ-ra-ên đông và mạnh hơn chúng ta;"));
visuperobject.biblemap.put("b2_1_10", new visuperobject(2,1,10,"hè! ta hãy dùng chước khôn ngoan đối cùng họ, kẻo họ thêm nhiều lên, một mai nếu có cơn chinh chiến xảy đến, họ sẽ hiệp cùng quân nghịch đánh lại ta, và ra khỏi xứ chăng."));
visuperobject.biblemap.put("b2_1_11", new visuperobject(2,1,11,"Vậy, người Ê-díp-tô bèn đặt các kẻ đầu xâu để bắt dân Y-sơ-ra-ên làm xâu khó nhọc; họ xây thành Phi-thom và Ram-se dùng làm kho tàng cho Pha-ra-ôn."));
visuperobject.biblemap.put("b2_1_12", new visuperobject(2,1,12,"Nhưng người Ê-díp-tô càng bắt làm khó nhọc chừng nào, dân Y-sơ-ra-ên càng thêm nhiều lên, và tràn ra chừng nấy. Người Ê-díp-tô bèn đem lòng ghen ghét dân Y-sơ-ra-ên,"));
visuperobject.biblemap.put("b2_1_13", new visuperobject(2,1,13,"bắt làm công việc nhọc nhằn,"));
visuperobject.biblemap.put("b2_1_14", new visuperobject(2,1,14,"gây cho đời dân ấy nên cay đắng, vì nỗi khổ sở nhồi đất, làm gạch và mọi việc khác ở ngoài đồng. Các công việc nầy người Ê-díp-tô bắt dân Y-sơ-ra-ên làm nhọc nhằn lắm."));
visuperobject.biblemap.put("b2_1_15", new visuperobject(2,1,15,"Vua xứ Ê-díp-tô cũng phán cùng các bà mụ của dân Hê-bơ-rơ, một người tên Siếp-ra và một người tên Phu-a,"));
visuperobject.biblemap.put("b2_1_16", new visuperobject(2,1,16,"mà rằng: Khi các ngươi đi rước thai cho người đàn bà Hê-bơ-rơ, hễ thấy sanh con trai, thì hãy làm cho chết đi; còn con gái, thì hãy để cho sống."));
visuperobject.biblemap.put("b2_1_17", new visuperobject(2,1,17,"Nhưng các bà mụ kính sợ Ðức Chúa Trời, chẳng làm theo lời vua Ê-díp-tô phán dặn, đều để cho các con trai sống hết."));
visuperobject.biblemap.put("b2_1_18", new visuperobject(2,1,18,"Vua xứ Ê-díp-tô bèn đòi những bà mụ mà phán rằng: Sao các ngươi làm như vậy, để cho những con trai sống?"));
visuperobject.biblemap.put("b2_1_19", new visuperobject(2,1,19,"Các bà mụ tâu rằng: Ấy tại người đàn bà Hê-bơ-rơ chẳng phải như người đàn bà Ê-díp-tô; vì họ mạnh khỏe hơn, đã sanh nở trước khi mụ đến."));
visuperobject.biblemap.put("b2_1_20", new visuperobject(2,1,20,"Ðức Chúa Trời ban ơn cho những bà mụ; dân sự gia thêm và trở nên đông đúc."));
visuperobject.biblemap.put("b2_1_21", new visuperobject(2,1,21,"Ấy vậy, vì bà mụ có lòng kính sợ Ðức Chúa Trời, nên Ngài làm cho nhà họ được thạnh vượng."));
visuperobject.biblemap.put("b2_1_22", new visuperobject(2,1,22,"Pha-ra-ôn bèn truyền lịnh cho cả dân mình rằng: Phàm con trai của dân Hê-bơ-rơ mới sanh, hãy liệng xuống sông; còn con gái, thì để cho sống."));
visuperobject.biblemap.put("b2_2_1", new visuperobject(2,2,1,"Vả, có một người trong họ Lê-vi đi cưới con gái Lê-vi làm vợ."));
visuperobject.biblemap.put("b2_2_2", new visuperobject(2,2,2,"Nàng thọ thai, và sanh một con trai; thấy con ngộ, nên đem đi giấu trong ba tháng."));
visuperobject.biblemap.put("b2_2_3", new visuperobject(2,2,3,"Nhưng giấu lâu hơn nữa không được, nàng bèn lấy một cái rương mây, trét chai và nhựa thông, rồi để đứa trẻ vào, đem thả trong đám sậy dựa mé sông."));
visuperobject.biblemap.put("b2_2_4", new visuperobject(2,2,4,"Người chị đứa trẻ đứng xa nơi đó đặng cho biết nó sẽ ra sao."));
visuperobject.biblemap.put("b2_2_5", new visuperobject(2,2,5,"Vả, bấy giờ, con gái Pha-ra-ôn xuống sông tắm, còn các con đòi đi dạo chơi trên mé sông; công chúa thấy cái rương mây đó giữa đám sậy, bèn sai con đòi mình đi vớt lên."));
visuperobject.biblemap.put("b2_2_6", new visuperobject(2,2,6,"Công chúa mở rương ra, thấy đứa trẻ, là một đứa con trai nhỏ đương khóc, bèn động lòng thương xót mà rằng: Ấy là một đứa con của người Hê-bơ-rơ."));
visuperobject.biblemap.put("b2_2_7", new visuperobject(2,2,7,"Người chị đứa trẻ bèn nói cùng công chúa rằng: Tôi phải đi kêu một người vú trong bọn đàn bà Hê-bơ-rơ đặng cho dứa trẻ bú chớ?"));
visuperobject.biblemap.put("b2_2_8", new visuperobject(2,2,8,"Công chúa đáp rằng: Hãy đi đi. Người gái trẻ đó kêu mẹ của đứa trẻ."));
visuperobject.biblemap.put("b2_2_9", new visuperobject(2,2,9,"Công chúa nói rằng: Hãy đem đứa trẻ nầy về nuôi bú cho ta; ta sẽ trả tiền công cho. Người đàn bà ẵm đứa trẻ mà cho bú."));
visuperobject.biblemap.put("b2_2_10", new visuperobject(2,2,10,"Khi lớn khôn rồi, người bèn dẫn nó vào cho công chúa, nàng nhận làm con, và đặt tên là Môi-se, vì nàng nói rằng: Ta đã vớt nó khỏi nước."));
visuperobject.biblemap.put("b2_2_11", new visuperobject(2,2,11,"Vả, đang lúc đó, Môi-se đã lớn khôn rồi, ra đi đến cùng anh em mình, xem thấy công việc nhọc nhằn của họ; cũng thấy một người Ê-díp-tô đánh một người Hê-bơ-rơ trong vòng anh em mình;"));
visuperobject.biblemap.put("b2_2_12", new visuperobject(2,2,12,"ngó quanh quất chẳng thấy ai, bèn giết người Ê-díp-tô đem vùi trong cát."));
visuperobject.biblemap.put("b2_2_13", new visuperobject(2,2,13,"Qua ngày sau, Môi-se đi ra nữa, thấy hai người Hê-bơ-rơ đánh lộn, bèn nói cùng người có lỗi rằng: Sao ngươi đánh người đồng loại mình?"));
visuperobject.biblemap.put("b2_2_14", new visuperobject(2,2,14,"Nhưng người đó đáp rằng: Ai đặt ngươi làm vua, làm quan án cho chúng ta? Có phải muốn giết ta như đã giết người Ê-díp-tô kia chăng? Môi-se sợ, nói rằng: Chắc thật, việc nầy phải lậu rồi."));
visuperobject.biblemap.put("b2_2_15", new visuperobject(2,2,15,"Pha-ra-ôn hay việc đó, thì tìm giết Môi-se; nhưng người trốn đi khỏi mặt Pha-ra-ôn, dừng chân tại xứ Ma-đi-an, và ngồi gần bên một cái giếng."));
visuperobject.biblemap.put("b2_2_16", new visuperobject(2,2,16,"Vả, thầy tế lễ xứ Ma-đi-an có bảy con gái; các nàng đó đến giếng xách nước đổ đầy máng đặng cho bầy chiên cha mình uống."));
visuperobject.biblemap.put("b2_2_17", new visuperobject(2,2,17,"Nhưng các kẻ chăn chiên đến đuổi đi; Môi-se bèn đứng dậy, binh vực các nàng đó và cho những bầy chiên uống nước."));
visuperobject.biblemap.put("b2_2_18", new visuperobject(2,2,18,"Khi mấy nàng trở về nhà Rê -u-ên, cha mình, thì người hỏi rằng: Sao bữa nay các con về sớm vậy?"));
visuperobject.biblemap.put("b2_2_19", new visuperobject(2,2,19,"Thưa rằng: Một người Ê-díp-tô cứu chúng tôi ra khỏi tay bọn chăn chiên, và cũng có xách nước nhiều quá cho bầy chiên uống nữa."));
visuperobject.biblemap.put("b2_2_20", new visuperobject(2,2,20,"Cha lại hỏi rằng: Người đó bây giờ ở đâu? Sao các con bỏ người đi? Hãy mời đến đặng ăn bánh."));
visuperobject.biblemap.put("b2_2_21", new visuperobject(2,2,21,"Môi-se ưng ở cùng người nầy, người bèn gả Sê-phô-ra, con gái mình, cho Môi-se."));
visuperobject.biblemap.put("b2_2_22", new visuperobject(2,2,22,"Nàng sanh một con trai; Môi-se đặt tên là Ghẹt-sôn vì nói rằng: Tôi kiều ngụ nơi ngoại bang."));
visuperobject.biblemap.put("b2_2_23", new visuperobject(2,2,23,"Sau cách lâu, vua xứ Ê-díp-tô băng; dân Y-sơ-ra-ên than thở kêu van vì phải phục dịch khổ sở; tiếng kêu van lên thấu Ðức Chúa Trời."));
visuperobject.biblemap.put("b2_2_24", new visuperobject(2,2,24,"Ngài nghe tiếng than thở chúng, nhớ đến sự giao ước mình kết lập cùng Áp-ra-ham, Y-sác và Gia-cốp."));
visuperobject.biblemap.put("b2_2_25", new visuperobject(2,2,25,"Ðức Chúa Trời đoái lại dân Y-sơ-ra-ên, nhận biết cảnh ngộ của chúng."));
visuperobject.biblemap.put("b2_3_1", new visuperobject(2,3,1,"Vả, Môi-se chăn bầy chiên cho Giê-trô, ông gia mình, là thầy tế lễ tại xứ Ma-đi-an; dẫn bầy chiên qua phía bên kia đồng vắng, đến núi của Ðức Chúa Trời, là núi Hô-rếp."));
visuperobject.biblemap.put("b2_3_2", new visuperobject(2,3,2,"Thiên sứ của Ðức Giê-hô-va hiện ra cùng người trong ngọn lửa, giữa bụi gai kia. Người nhìn thấy cả bụi gai đang cháy, nhưng không hề tàn."));
visuperobject.biblemap.put("b2_3_3", new visuperobject(2,3,3,"Môi-se bèn nói rằng: Ta hãy tẻ bước lại đặng xem sự lạ lớn nầy, vì cớ sao bụi gai chẳng tàn chút nào."));
visuperobject.biblemap.put("b2_3_4", new visuperobject(2,3,4,"Ðức Giê-hô-va thấy người tẻ bước lại xem, Ðức Chúa Trời bèn ở giữa bụi gai gọi rằng: Hỡi Môi-se, hỡi Môi-se! Người thưa rằng: Có tôi đây!"));
visuperobject.biblemap.put("b2_3_5", new visuperobject(2,3,5,"Ðức Chúa Trời phán rằng: Chớ lại gần chốn nầy, Hãy cổi giầy ngươi ra, vì chỗ ngươi đang đứng là đất thánh."));
visuperobject.biblemap.put("b2_3_6", new visuperobject(2,3,6,"Rồi Ngài lại nói: Ta là Ðức Chúa Trời của tổ phụ ngươi, Ðức Chúa Trời của Áp-ra-ham, Ðức Chúa Trời của Y-sác, và Ðức Chúa Trời của Gia-cốp. Môi-se liền che mặt, vì sợ nhìn đến Ðức Chúa Trời."));
visuperobject.biblemap.put("b2_3_7", new visuperobject(2,3,7,"Ðức Giê-hô-va phán rằng: Ta đã thấy rõ ràng sự cực khổ của dân ta tại xứ Ê-díp-tô, và có nghe thấu tiếng kêu rêu vì cớ người đốc công của nó; phải, ta biết được nỗi đau đớn của nó."));
visuperobject.biblemap.put("b2_3_8", new visuperobject(2,3,8,"Ta ngự xuống đặng cứu dân nầy khỏi tay người Ê-díp-tô, dẫn từ xứ ấy lên đến một xứ kia đẹp đẽ và rộng rãi, đượm sữa và mật, tức là nơi dân Ca-na-an, dân Hê-tít, dân A-mô-rít, dân Phê-rê-sít, dân Hê-vít và dân Giê-bu-sít ở."));
visuperobject.biblemap.put("b2_3_9", new visuperobject(2,3,9,"Nầy, tiếng kêu rêu của dân Y-sơ-ra-ên thấu đến ta, và ta đã thấy dân Ê-díp-tô hà hiếp chúng nó thể nào;"));
visuperobject.biblemap.put("b2_3_10", new visuperobject(2,3,10,"vậy bây giờ, hãy lại đây, đặng ta sai ngươi đi đến Pha-ra-ôn, để dắt dân ta, là dân Y-sơ-ra-ên, ra khỏi xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_3_11", new visuperobject(2,3,11,"Môi-se bèn thưa rằng: Tôi là ai, dám đi đến Pha-ra-ôn, đặng dắt dân Y-sơ-ra-ên ra khỏi xứ Ê-díp-tô?"));
visuperobject.biblemap.put("b2_3_12", new visuperobject(2,3,12,"Ðức Chúa Trời phán rằng: Ta sẽ ở cùng ngươi; nầy là điều làm dấu cho ngươi biết rằng ta đã sai ngươi đi: Khi ngươi dắt dân sự ra khỏi xứ Ê-díp-tô rồi, thì các ngươi sẽ phụng sự Ðức Chúa Trời tại trên núi nầy."));
visuperobject.biblemap.put("b2_3_13", new visuperobject(2,3,13,"Môi-se thưa cùng Ðức Chúa Trời rằng: Nầy, tôi sẽ đi đến dân Y-sơ-ra-ên, nói cùng họ rằng: Ðức Chúa Trời của tổ phụ các ngươi sai ta đến cùng các ngươi; nhưng nếu họ hỏi: Tên Ngài là chi? thì tôi nói với họ làm sao?"));
visuperobject.biblemap.put("b2_3_14", new visuperobject(2,3,14,"Ðức Chúa Trời phán rằng: Ta là Ðấng Tự Hữu Hằng Hữu; rồi Ngài lại rằng: Hãy nói cho dân Y-sơ-ra-ên như vầy: Ðấng Tự Hữu đã sai ta đến cùng các ngươi."));
visuperobject.biblemap.put("b2_3_15", new visuperobject(2,3,15,"Ðức Chúa Trời lại phán cùng Môi-se rằng: Ngươi sẽ nói cho dân Y-sơ-ra-ên như vầy: Giê-hô-va, Ðức Chúa Trời của tổ phụ các ngươi, Ðức Chúa Trời của Áp-ra-ham, Ðức Chúa Trời của Y-sác, Ðức Chúa Trời của Gia-cốp, sai ta đến cùng các ngươi. Ấy đó là danh đời đời của ta, ấy sẽ là kỷ niệm của ta trải qua các đời."));
visuperobject.biblemap.put("b2_3_16", new visuperobject(2,3,16,"Hãy đi, hội hiệp các trưởng lão Y-sơ-ra-ên, mà nói cùng họ rằng: Giê-hô-va, Ðức Chúa Trời của tổ phụ các ngươi, Ðức Chúa Trời của Áp-ra-ham, Ðức Chúa Trời của Y-sác, Ðức Chúa Trời của Gia-cốp, đã hiện ra cùng ta mà phán rằng: Thật vậy, ta đã thăm viếng các ngươi, thấy điều họ đãi các ngươi tại xứ Ê-díp-tô,"));
visuperobject.biblemap.put("b2_3_17", new visuperobject(2,3,17,"nên ta đã phán rằng: Ta sẽ rút các ngươi ra khỏi cảnh khổ tại xứ Ê-díp-tô, đặng đem lên xứ của dân Ca-na-an, dân Hê-tít, dân A-mô-rít, dân Phê-rê-sít, dân Hê-vít, và dân Giê-bu-sít, tức là một xứ đượm sữa và mật."));
visuperobject.biblemap.put("b2_3_18", new visuperobject(2,3,18,"Dân sự sẽ vâng theo lời ngươi; vậy, ngươi và các trưởng lão Y-sơ-ra-ên hãy đi yết-kiến vua xứ Ê-díp-tô mà tâu rằng: Giê-hô-va, Ðức Chúa Trời của dân Hê-bơ-rơ, đã hiện ra cùng chúng tôi. Vây bây giờ, xin để cho chúng tôi đi đến nơi đồng vắng, cách chừng ba ngày đường đặng dâng của lễ cho Giê-hô-va Ðức Chúa Trời chúng tôi."));
visuperobject.biblemap.put("b2_3_19", new visuperobject(2,3,19,"Vả, ta biết rằng dẫu lấy quyền lực ép buộc vua Ê-díp-tô, thì người cũng chẳng bao giờ cho các ngươi đi!"));
visuperobject.biblemap.put("b2_3_20", new visuperobject(2,3,20,"Nhưng ta sẽ giơ tay ra hành xứ Ê-díp-tô bằng các phép lạ ta làm giữa xứ đó, sau rồi họ sẽ cho các ngươi đi."));
visuperobject.biblemap.put("b2_3_21", new visuperobject(2,3,21,"Ta sẽ làm cho dân nầy được ơn trước mắt người Ê-díp-tô; vậy, khi nào các ngươi ra đi, thì sẽ chẳng ra đi tay không;"));
visuperobject.biblemap.put("b2_3_22", new visuperobject(2,3,22,"nhưng mỗi người đàn bà sẽ hỏi xin người nữ lân cận, cùng kẻ ở tạm nhà mình những đồ bằng vàng, bằng bạc và quần áo, mặc lấy cho con trai con gái mình. Các ngươi sẽ lột trần dân Ê-díp-tô là như vậy."));
visuperobject.biblemap.put("b2_4_1", new visuperobject(2,4,1,"Môi-se thưa rằng: Nhưng dân đó sẽ chẳng tin và chẳng vâng lời tôi, vì sẽ nói rằng: Ðức Giê-hô-va chẳng có hiện ra cùng ngươi đâu."));
visuperobject.biblemap.put("b2_4_2", new visuperobject(2,4,2,"Ðức Giê-hô-va phán rằng: Trong tay ngươi cầm vật chi? Thưa rằng: Một cây gậy."));
visuperobject.biblemap.put("b2_4_3", new visuperobject(2,4,3,"Phán rằng: Hãy ném xuống đất đi. Người bèn ném xuống đất, gậy hóa ra một con rắn; Môi-se chạy trốn nó."));
visuperobject.biblemap.put("b2_4_4", new visuperobject(2,4,4,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng: Hãy giơ tay ngươi ra nắm đuôi nó. Người giơ tay ra nắm, thì nó hườn lại cây gậy trong tay."));
visuperobject.biblemap.put("b2_4_5", new visuperobject(2,4,5,"Ðức Giê-hô-va phán rằng: Ấy để cho chúng nó tin rằng Giê-hô-va, Ðức Chúa Trời của tổ phụ mình, là Ðức Chúa Trời của Áp-ra-ham, Ðức Chúa Trời của Y-sác, Ðức Chúa Trời của Gia-cốp, đã hiện ra cùng ngươi."));
visuperobject.biblemap.put("b2_4_6", new visuperobject(2,4,6,"Ðức Giê-hô-va lại phán rằng: Hãy đặt tay ngươi vào lòng. Người liền đặt vào, rồi lấy ra. Nầy, tay người nổi phung trắng như tuyết."));
visuperobject.biblemap.put("b2_4_7", new visuperobject(2,4,7,"Ðoạn, Ngài phán rằng: Hãy đặt tay ngươi vào lòng lại; người bèn đặt vào, rồi lấy ra. Kìa, tay trở lại như thịt mình."));
visuperobject.biblemap.put("b2_4_8", new visuperobject(2,4,8,"Lại phán: Nếu khi nào dân đó chẳng tin ngươi và chẳng vâng theo tiếng của dấu thứ nhất, thì sẽ tin theo tiếng của dấu thứ nhì."));
visuperobject.biblemap.put("b2_4_9", new visuperobject(2,4,9,"Vả lại, nếu dân chẳng tin cả hai dấu nầy, và không vâng theo lời ngươi, thì hãy lấy nước dưới sông mà làm tràn ngập trên mặt đất; nước mà ngươi đã lấy dưới sông lên đó, sẽ thành máu trên mặt đất vậy."));
visuperobject.biblemap.put("b2_4_10", new visuperobject(2,4,10,"Môi-se thưa cùng Ðức Giê-hô-va rằng: Ôi! lạy Chúa, từ hôm qua, hôm kia, hay là từ lúc Chúa phán dạy kẻ tôi tớ Chúa, tôi vẫn chẳng phải một tay nói giỏi, vì miệng và lưỡi tôi hay ngập ngừng."));
visuperobject.biblemap.put("b2_4_11", new visuperobject(2,4,11,"Ðức Giê-hô-va bèn phán rằng: Ai tạo miệng loài người ra? hay là ai đã làm câm, làm điếc, làm sáng, làm mờ? Có phải ta là Ðức Giê-hô-va chăng?"));
visuperobject.biblemap.put("b2_4_12", new visuperobject(2,4,12,"Vậy bây giờ, hãy đi; ta sẽ ở cùng miệng ngươi và dạy ngươi những lời phải nói."));
visuperobject.biblemap.put("b2_4_13", new visuperobject(2,4,13,"Môi-se thưa rằng: Ôi! lạy Chúa, Chúa muốn sai ai đi, thì sai."));
visuperobject.biblemap.put("b2_4_14", new visuperobject(2,4,14,"Ðức Giê-hô-va bèn nổi giận cùng Môi-se mà phán rằng: Có phải A-rôn, người Lê-vi, là anh ngươi chăng? Ta biết người đó có tài nói giỏi, và kìa, người đang đi đến đón ngươi kìa; khi thấy ngươi, chắc sẽ vui mừng trong lòng."));
visuperobject.biblemap.put("b2_4_15", new visuperobject(2,4,15,"Vậy ngươi hãy nói cùng người, và sắp để những lời trong miệng người. Khi hai ngươi nói, ta sẽ ở cùng miệng ngươi và miệng anh ngươi, dạy các ngươi những điều gì phải làm."));
visuperobject.biblemap.put("b2_4_16", new visuperobject(2,4,16,"Ấy là người đó sẽ nói cùng dân sự thế cho ngươi, dùng làm miệng ngươi, còn ngươi sẽ dường như Ðức Chúa Trời cho người vậy."));
visuperobject.biblemap.put("b2_4_17", new visuperobject(2,4,17,"Ngươi hãy cầm lấy gậy nầy nơi tay, để dùng làm các dấu lạ."));
visuperobject.biblemap.put("b2_4_18", new visuperobject(2,4,18,"Môi-se bèn đi, trở về Giê-trô, ông gia mình, mà thưa rằng: Xin cha hãy cho tôi trở về nơi anh em tôi tại xứ Ê-díp-tô, đặng thăm thử họ còn sống chăng. Giê-trô bèn nói cùng Môi-se rằng: Con hãy đi bình yên."));
visuperobject.biblemap.put("b2_4_19", new visuperobject(2,4,19,"Vả, Ðức Giê-hô-va có phán cùng Môi-se tại Ma-đi-an rằng: Hãy trở về xứ Ê-díp-tô, vì mấy người tìm giết ngươi đã chết hết rồi."));
visuperobject.biblemap.put("b2_4_20", new visuperobject(2,4,20,"Môi-se bèn đỡ vợ và con mình lên lưng lừa, trở về xứ Ê-díp-tô. Người cũng cầm cây gậy của Ðức Chúa Trời theo trong tay."));
visuperobject.biblemap.put("b2_4_21", new visuperobject(2,4,21,"Ðức Giê-hô-va phán cùng Môi-se rằng: Khi trở về xứ Ê-díp-tô, hãy cẩn thận về các dấu lạ của ta đã giao nơi tay ngươi mà làm trước mặt Pha-ra-ôn; nhưng ta sẽ khiến người cứng lòng chẳng cho dân sự đi."));
visuperobject.biblemap.put("b2_4_22", new visuperobject(2,4,22,"Vậy, ngươi phải tâu cùng Pha-ra-ôn rằng: Ðức Giê-hô-va có phán như vầy: Y-sơ-ra-ên là con ta, tức trưởng nam ta,"));
visuperobject.biblemap.put("b2_4_23", new visuperobject(2,4,23,"nên ta có phán cùng ngươi rằng: Hãy cho con ta đi, để nó phụng sự ta, mà ngươi đã từ chối không cho nó đi. Nầy, ta sẽ giết con trai ngươi, là con trưởng nam của ngươi."));
visuperobject.biblemap.put("b2_4_24", new visuperobject(2,4,24,"Vả, đang khi đi đường, Ðức Giê-hô-va hiện ra trước mặt Môi-se nơi nhà quán, và kiếm thế giết người đi."));
visuperobject.biblemap.put("b2_4_25", new visuperobject(2,4,25,"Sê-phô-ra lấy con dao bằng đá, cắt dương bì của con mình, và quăng dưới chân Môi-se, mà nói rằng: Thật, chàng là huyết lang cho tôi!"));
visuperobject.biblemap.put("b2_4_26", new visuperobject(2,4,26,"Ðức Giê-hô-va tha chồng; nàng bèn nói rằng: Huyết lang! là vì cớ phép cắt bì."));
visuperobject.biblemap.put("b2_4_27", new visuperobject(2,4,27,"Ðức Giê-hô-va phán cùng A-rôn rằng: Hãy đi đến đồng vắng, đón rước Môi-se. A-rôn bèn đi, gặp Môi-se tại núi của Ðức Chúa Trời, và hôn người."));
visuperobject.biblemap.put("b2_4_28", new visuperobject(2,4,28,"Môi-se thuật lại cho A-rôn mọi lời của Ðức Giê-hô-va sai mình truyền, và các dấu lạ mà Ngài đã dạy mình làm."));
visuperobject.biblemap.put("b2_4_29", new visuperobject(2,4,29,"Vậy, Môi-se đi cùng A-rôn, nhóm lại các trưởng lão dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b2_4_30", new visuperobject(2,4,30,"A-rôn bèn thuật hết các lời của Ðức Giê-hô-va đã phán cùng Môi-se, và làm những dấu lạ trước mặt dân sự."));
visuperobject.biblemap.put("b2_4_31", new visuperobject(2,4,31,"Chúng bèn tin, hiểu rằng Ðức Giê-hô-va đã thăm viếng dân Y-sơ-ra-ên, và thấy điều cực khổ của mình; chúng bèn cúi đầu và thờ lạy."));
visuperobject.biblemap.put("b2_5_1", new visuperobject(2,5,1,"Kế sau, Môi-se và A-rôn đến tâu cùng Pha-ra-ôn rằng: Giê-hô-va, Ðức Chúa Trời của dân Y-sơ-ra-ên có phán như vầy: Hãy cho dân ta đi, đặng nó giữ một lễ cho ta tại đồng vắng."));
visuperobject.biblemap.put("b2_5_2", new visuperobject(2,5,2,"Nhưng Pha-ra-ôn đáp rằng: Giê-hô-va là ai mà trẫm phải vâng lời người, để cho dân Y-sơ-ra-ên đi? Trẫm chẳng biết Giê-hô-va nào hết; cũng chẳng cho dân Y-sơ-ra-ên đi nữa."));
visuperobject.biblemap.put("b2_5_3", new visuperobject(2,5,3,"Môi-se và A-rôn tâu rằng: Ðức Chúa Trời của dân Hê-bơ-rơ đã đến cùng chúng tôi; xin hãy cho phép chúng tôi đi vào nơi đồng vắng, cách chừng ba ngày đường, đặng dâng tế lễ cho Giê-hô-va Ðức Chúa Trời chúng tôi, kẻo Ngài khiến bịnh dịch hay việc gươm dao hành chúng tôi chăng."));
visuperobject.biblemap.put("b2_5_4", new visuperobject(2,5,4,"Vua xứ Ê-díp-tô phán rằng: Hỡi Môi-se và A-rôn, sao các ngươi xui cho dân chúng bỏ công việc như vậy? Hãy đi làm công việc mình đi!"));
visuperobject.biblemap.put("b2_5_5", new visuperobject(2,5,5,"Pha-ra-ôn lại phán rằng: Kìa, dân sự trong xứ bây giờ đông đúc quá, hai người làm cho chúng nó phải nghỉ việc ư!"));
visuperobject.biblemap.put("b2_5_6", new visuperobject(2,5,6,"Trong ngày đó, Pha-ra-ôn truyền lịnh cho các đốc dịch và phái viên rằng:"));
visuperobject.biblemap.put("b2_5_7", new visuperobject(2,5,7,"Các ngươi đừng phát rơm cho dân sự làm gạch như lúc trước nữa; chúng nó phải tự đi kiếm góp lấy."));
visuperobject.biblemap.put("b2_5_8", new visuperobject(2,5,8,"Nhưng các ngươi hãy hạn số gạch y như cũ, chớ trừ bớt chi cả; vì họ biếng nhác, lấy cớ mà đồng kêu lên rằng: Hè! đi dâng tế lễ cho Ðức Chúa Trời chúng ta!"));
visuperobject.biblemap.put("b2_5_9", new visuperobject(2,5,9,"Hãy gán công việc nặng cho chúng nó, hầu cho chúng nó mắc công việc, chẳng xiêu về lời giả dối nữa."));
visuperobject.biblemap.put("b2_5_10", new visuperobject(2,5,10,"Các đốc dịch và phái viên bèn ra, nói cùng dân chúng rằng: Pha-ra-ôn có phán như vầy: Ta chẳng cho các ngươi rơm nữa."));
visuperobject.biblemap.put("b2_5_11", new visuperobject(2,5,11,"Hãy tự đi kiếm lấy rơm ở đâu đi; song chẳng trừ bớt công việc các ngươi chút nào."));
visuperobject.biblemap.put("b2_5_12", new visuperobject(2,5,12,"Bấy giờ, dân chúng bèn đi rải khắp xứ Ê-díp-tô, đặng góp gốc rạ thế cho rơm."));
visuperobject.biblemap.put("b2_5_13", new visuperobject(2,5,13,"Các đốc dịch lại hối rằng: Hãy làm cho xong phần việc các ngươi, y cố định, ngày nào cho rồi ngày nấy, cũng như khi có phát rơm."));
visuperobject.biblemap.put("b2_5_14", new visuperobject(2,5,14,"Những phái viên trong vòng dân Y-sơ-ra-ên mà các đốc dịch của Pha-ra-ôn đã lập lên trên dân đó, bị đánh và quở rằng: Sao hôm qua và ngày nay, các ngươi chẳng làm xong phần định về việc mình làm gạch y như trước?"));
visuperobject.biblemap.put("b2_5_15", new visuperobject(2,5,15,"Các phái viên của dân Y-sơ-ra-ên bèn đến kêu cùng Pha-ra-ôn rằng: Sao bệ hạ đãi kẻ tôi tớ như vậy?"));
visuperobject.biblemap.put("b2_5_16", new visuperobject(2,5,16,"Người ta chẳng phát rơm cho, lại cứ biểu chúng tôi rằng: Hãy làm gạch đi! Nầy tôi tớ của bệ hạ đây bị đòn; lỗi tại dân của bệ hạ."));
visuperobject.biblemap.put("b2_5_17", new visuperobject(2,5,17,"Nhưng vua đáp rằng: Các ngươi là đồ biếng nhác, đồ biếng nhác! Bởi cớ đó nên nói với nhau rằng: Hè! đi tế Ðức Giê-hô-va!"));
visuperobject.biblemap.put("b2_5_18", new visuperobject(2,5,18,"Vậy bây giờ, cứ đi làm việc đi, người ta chẳng phát rơm cho đâu, nhưng các ngươi phải nộp gạch cho đủ số."));
visuperobject.biblemap.put("b2_5_19", new visuperobject(2,5,19,"Vậy, các phái viên của dân Y-sơ-ra-ên lấy làm bối rối, vì người ta có nói cùng mình rằng: Các ngươi chẳng được trừ số gạch đã định cho mình ngày nào rồi ngày nấy."));
visuperobject.biblemap.put("b2_5_20", new visuperobject(2,5,20,"Khi lui ra khỏi Pha-ra-ôn thì họ gặp Môi-se và A-rôn đợi tại đó,"));
visuperobject.biblemap.put("b2_5_21", new visuperobject(2,5,21,"bèn nói rằng: Hai ngươi đã làm cho chúng tôi ra mùi hôi hám trước mặt Pha-ra-ôn và quần thần, mà trao thanh gươm vào tay họ đặng giết chúng tôi. Cầu xin Ðức Giê-hô-va xem xét và xử đoán hai ngươi!"));
visuperobject.biblemap.put("b2_5_22", new visuperobject(2,5,22,"Môi-se bèn trở về cùng Ðức Giê-hô-va mà thưa rằng: Lạy Chúa! sao Chúa đã làm hại cho dân sự nầy? Chúa sai tôi đến mà chi?"));
visuperobject.biblemap.put("b2_5_23", new visuperobject(2,5,23,"Từ khi tôi đi yết kiến Pha-ra-ôn đặng nhân danh Chúa mà nói, thì người lại ngược đãi dân nầy, và Chúa chẳng giải cứu dân của Chúa."));
visuperobject.biblemap.put("b2_6_1", new visuperobject(2,6,1,"Ðức Giê-hô-va phán cùng Môi-se rằng: Bây giờ ngươi hãy xem những điều ta sẽ hành Pha-ra-ôn; vì nhờ tay quyền năng ép buộc vua đó sẽ tha và đuổi dân Y-sơ-ra-ên ra khỏi xứ mình."));
visuperobject.biblemap.put("b2_6_2", new visuperobject(2,6,2,"Ðức Chúa Trời lại phán cùng Môi-se rằng: Ta là Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_6_3", new visuperobject(2,6,3,"Ta đã hiện ra cùng Áp-ra-ham, cùng Y-sác, và cùng Gia-cốp, tỏ mình là Ðức Chúa Trời toàn năng; song về danh ta là Giê-hô-va, thì ta chưa hề tỏ cho họ biết."));
visuperobject.biblemap.put("b2_6_4", new visuperobject(2,6,4,"Ta cũng có lập giao ước cùng họ, để ban xứ Ca-na-an cho, là xứ họ đã kiều ngụ như khách ngoại bang."));
visuperobject.biblemap.put("b2_6_5", new visuperobject(2,6,5,"Ta cũng có nghe lời than thở của dân Y-sơ-ra-ên bị người Ê-díp-tô bắt làm tôi mọi, bèn nhớ lại sự giao ước của ta."));
visuperobject.biblemap.put("b2_6_6", new visuperobject(2,6,6,"Vậy nên, ngươi hãy nói cùng dân Y-sơ-ra-ên rằng: Ta là Ðức Giê-hô-va sẽ rút các ngươi khỏi gánh nặng mà người Ê-díp-tô đã gán cho, cùng giải thoát khỏi vòng tôi mọi; ta sẽ giơ thẳng tay ra, dùng sự đoán phạt nặng mà chuộc các ngươi."));
visuperobject.biblemap.put("b2_6_7", new visuperobject(2,6,7,"Ta sẽ nhận các ngươi làm dân ta, và ta sẽ làm Ðức Chúa Trời của các ngươi; các ngươi sẽ biết ta là Giê-hô-va Ðức Chúa Trời của các ngươi, đã rút các ngươi khỏi gánh nặng của người Ê-díp-tô."));
visuperobject.biblemap.put("b2_6_8", new visuperobject(2,6,8,"Ta sẽ dắt các ngươi vào xứ ta đã thề ban cho Áp-ra-ham, cho Y-sác, cho Gia-cốp, mà cho các ngươi xứ đó làm cơ nghiệp: Ta là Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_6_9", new visuperobject(2,6,9,"Môi-se nói lại như vậy cho dân Y-sơ-ra-ên; nhưng vì bị sầu não và việc tôi mọi nặng nề, nên chẳng nghe Môi-se chút nào."));
visuperobject.biblemap.put("b2_6_10", new visuperobject(2,6,10,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b2_6_11", new visuperobject(2,6,11,"Hãy đi tâu cùng Pha-ra-ôn, vua xứ Ê-díp-tô, phải cho dân Y-sơ-ra-ên ra khỏi xứ mình."));
visuperobject.biblemap.put("b2_6_12", new visuperobject(2,6,12,"Nhưng Môi-se thưa cùng Ðức Giê-hô-va rằng: Nầy, tôi là một kẻ vụng miệng; dân Y-sơ-ra-ên chẳng có nghe lời tôi, Pha-ra-ôn há sẽ khứng nghe lời tôi sao?"));
visuperobject.biblemap.put("b2_6_13", new visuperobject(2,6,13,"Ðức Giê-hô-va lại phán cùng Môi-se và A-rôn, bèn truyền cho hai người phải đi đến dân Y-sơ-ra-ên và Pha-ra-ôn, vua xứ Ê-díp-tô, đặng đem dân đó ra khỏi xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_6_14", new visuperobject(2,6,14,"Ðây là các trưởng tộc của những họ hàng dân Y-sơ-ra-ên. Các con trai của Ru-bên, tức là trưởng nam Y-sơ-ra-ên, là: Hê-nóc, Pha-lu, Hết-rôn và Cạt-mi. Ðó là những họ hàng của Ru-bên."));
visuperobject.biblemap.put("b2_6_15", new visuperobject(2,6,15,"Các con trai của Si-mê-ôn là: Giê-mu-ên, Gia-min, Ô-hát, Gia-kin, Xô-ha và Sau-lơ là con của vợ xứ Ca-na-an. Ðó là họ hàng của Si-mê-ôn."));
visuperobject.biblemap.put("b2_6_16", new visuperobject(2,6,16,"Ðây là tên các con trai của Lê-vi, tùy theo dòng dõi của họ: Ghẹt-sôn, Kê-hát, và Mê-ra-ri. Lê-vi hưởng thọ được một trăm ba mươi bảy tuổi."));
visuperobject.biblemap.put("b2_6_17", new visuperobject(2,6,17,"Các con trai của Ghẹt-sôn, tùy theo họ hàng mình: Líp-ni và Si-mê -y."));
visuperobject.biblemap.put("b2_6_18", new visuperobject(2,6,18,"Các con trai của Kê-hát là: Am-ram, Dít-sê-ha, Hếp-rôn, và U-xi-ên. Kê-hát hưởng thọ được một trăm ba mươi ba tuổi."));
visuperobject.biblemap.put("b2_6_19", new visuperobject(2,6,19,"Các con trai của Mê-ra-ri là: Mách-li, và Mu-si. Ðó là các họ hàng của Lê-vi, tùy theo dòng dõi của họ."));
visuperobject.biblemap.put("b2_6_20", new visuperobject(2,6,20,"Vả, Am-ram lấy Giô-kê-bết, là cô mình, làm vợ; nàng sanh cho người A-rôn và Môi-se. Am-ram hưởng thọ được một trăm ba mươi bảy tuổi."));
visuperobject.biblemap.put("b2_6_21", new visuperobject(2,6,21,"Các con trai của Dít-sê-ha là: Cô-rê, Nê-phết và Xiếc-ri."));
visuperobject.biblemap.put("b2_6_22", new visuperobject(2,6,22,"Các con trai của U-xi-ên là: Mi-sa-ên, Eân-sa-phan, và Sít-ri."));
visuperobject.biblemap.put("b2_6_23", new visuperobject(2,6,23,"A-rôn cưới Ê-li-sê-ba, con gái của A-mi-na-đáp, em của Na-ha-sôn; nàng sanh cho người Na-đáp, A-bi-hu, Ê-lê-a-sa và Y-tha-ma."));
visuperobject.biblemap.put("b2_6_24", new visuperobject(2,6,24,"Các con trai của Cô-rê là: Át-si, Eân-ca-na và A-bi-a-sáp. Ðó là các họ hàng của dân Cô-rê."));
visuperobject.biblemap.put("b2_6_25", new visuperobject(2,6,25,"Ê-lê-a-sa, con trai của A-rôn, cưới con gái của Phu-ti-ên làm vợ; nàng sanh Phi-nê-a cho người. Ðó là các trưởng tộc của nhà tổ phụ người Lê-vi, tùy theo họ hàng mình vậy."));
visuperobject.biblemap.put("b2_6_26", new visuperobject(2,6,26,"Ấy, A-rôn và Môi-se nầy, tức là người mà Ðức Giê-hô-va đã phán dạy rằng: Hãy tùy theo cơ đội, đem dân Y-sơ-ra-ên ra khỏi xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_6_27", new visuperobject(2,6,27,"Ấy là hai người tâu cùng Pha-ra-ôn, vua xứ Ê-díp-tô, đặng đem dân Y-sơ-ra-ên ra khỏi xứ đó; ấy là Môi-se và A-rôn nầy."));
visuperobject.biblemap.put("b2_6_28", new visuperobject(2,6,28,"Vậy, trong ngày Ðức Giê-hô-va phán cùng Môi-se tại xứ Ê-díp-tô,"));
visuperobject.biblemap.put("b2_6_29", new visuperobject(2,6,29,"thì Ngài có truyền rằng: Ta là Ðức Giê-hô-va, hãy tâu lại cùng Pha-ra-ôn, vua xứ Ê-díp-tô, hết thảy mọi lời ta sẽ phán cùng ngươi."));
visuperobject.biblemap.put("b2_6_30", new visuperobject(2,6,30,"Môi-se bèn thưa rằng: Nầy, tôi là người vụng miệng: Pha-ra-ôn há sẽ nghe tôi sao?"));
visuperobject.biblemap.put("b2_7_1", new visuperobject(2,7,1,"Ðức Giê-hô-va phán cùng Môi-se rằng: Hãy xem, ta lập ngươi như là Ðức Chúa Trời cho Pha-ra-ôn, còn A-rôn, anh ngươi, sẽ làm kẻ tiên tri của ngươi."));
visuperobject.biblemap.put("b2_7_2", new visuperobject(2,7,2,"Hãy nói lại mọi lời ta sẽ truyền dạy ngươi, rồi A-rôn, anh ngươi, sẽ tâu cùng Pha-ra-ôn, đặng người thả dân Y-sơ-ra-ên ra khỏi xứ mình."));
visuperobject.biblemap.put("b2_7_3", new visuperobject(2,7,3,"Ta sẽ làm cho Pha-ra-ôn cứng lòng, và thêm nhiều dấu lạ phép kỳ của ta trong xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_7_4", new visuperobject(2,7,4,"Nhưng Pha-ra-ôn sẽ chẳng nghe các ngươi đâu; ta sẽ tra tay vào đất Ê-díp-tô, dùng những sự đoán-phạt nằng-nề, đặng rút khỏi xứ đó các quân đội ta, là dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b2_7_5", new visuperobject(2,7,5,"Khi nào ta tra tay vào xứ Ê-díp-tô mà rút dân Y-sơ-ra-ên ra khỏi giữa vòng người Ê-díp-tô, thì họ sẽ biết ta là Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_7_6", new visuperobject(2,7,6,"Môi-se và A-rôn bèn làm y như lời Ðức Giê-hô-va đã phán dặn."));
visuperobject.biblemap.put("b2_7_7", new visuperobject(2,7,7,"Vả, khi hai người tâu cùng Pha-ra-ôn, thì Môi-se tuổi đã được tám mươi, còn A-rôn tám mươi ba."));
visuperobject.biblemap.put("b2_7_8", new visuperobject(2,7,8,"Ðức Giê-hô-va phán cùng Môi-se và A-rôn rằng:"));
visuperobject.biblemap.put("b2_7_9", new visuperobject(2,7,9,"Khi nào Pha-ra-ôn truyền cho các ngươi: Hãy làm phép lạ đi, thì ngươi sẽ nói cùng A-rôn rằng: Hãy lấy cây gậy của anh, liệng trước mặt Pha-ra-ôn, gậy sẽ hóa thành một con rắn."));
visuperobject.biblemap.put("b2_7_10", new visuperobject(2,7,10,"Vậy, Môi-se và Pha-ra-ôn đến cùng Pha-ra-ôn và làm như lời Ðức Giê-hô đã phán dặn. A-rôn liệng cây gậy mình trước mặt Pha-ra-ôn và quần thần, gậy liền hóa thành một con rắn."));
visuperobject.biblemap.put("b2_7_11", new visuperobject(2,7,11,"Còn Pha-ra-ôn bèn đòi các bác sĩ và thầy phù chú, là những thuật-sĩ Ê-díp-tô; phần họ, cũng cậy phép phù chú mình mà làm giống in như vậy."));
visuperobject.biblemap.put("b2_7_12", new visuperobject(2,7,12,"Mỗi người liệng gậy mình, liền hóa thành rắn; nhưng gậy của A-rôn nuốt các gậy của họ."));
visuperobject.biblemap.put("b2_7_13", new visuperobject(2,7,13,"Thế mà lòng Pha-ra-ôn vẫn cứng cỏi, chẳng nghe Môi-se và A-rôn, y như lời Ðức Giê-hô-va đã phán."));
visuperobject.biblemap.put("b2_7_14", new visuperobject(2,7,14,"Ðức Giê-hô-va phán cùng Môi-se rằng: Pha-ra-ôn đã rắn lòng, từ chối không để cho dân sự đi."));
visuperobject.biblemap.put("b2_7_15", new visuperobject(2,7,15,"Sớm mai Pha-ra-ôn sẽ ngự ra bờ sông, ngươi hãy ra mắt người và cầm theo tay cây gậy đã biến ra con rắn đó."));
visuperobject.biblemap.put("b2_7_16", new visuperobject(2,7,16,"Ngươi hãy tâu rằng: Giê-hô-va, là Ðức Chúa Trời của dân Hê-bơ-rơ, sai tôi đến gần bệ hạ, đặng tâu rằng: Hãy tha cho dân ta đi, để chúng nó hầu việc ta trong đồng vắng; mà đến bây giờ ngươi không nghe ta chút nào."));
visuperobject.biblemap.put("b2_7_17", new visuperobject(2,7,17,"Ðức Giê-hô-va có phán như vầy: Vì việc nầy, ngươi sẽ biết ta là Ðức Giê-hô-va: Nầy ta lấy gậy trong tay ta mà đập nước ở dưới sông, nước sẽ trở nên huyết."));
visuperobject.biblemap.put("b2_7_18", new visuperobject(2,7,18,"Cá dưới sông sẽ chết, và sông sẽ hôi thúi đi; người Ê-díp-tô uống nước sông lấy làm ghê gớm."));
visuperobject.biblemap.put("b2_7_19", new visuperobject(2,7,19,"Ðoạn, Ðức Giê-hô-va phán cùng Môi-se rằng: Hãy truyền cho A-rôn rằng: Cầm lấy cây gậy anh, giơ tay ra trên nước xứ Ê-díp-tô, trên rạch, sông, bàu, và khắp mọi nơi có nước trong xứ, hầu cho nước hóa thành huyết; cả xứ Ê-díp-tô, và trong những bình chứa nước bằng gỗ, bằng đá đều sẽ có huyết hết thảy."));
visuperobject.biblemap.put("b2_7_20", new visuperobject(2,7,20,"Vậy, Môi-se và A-rôn bèn làm y như lời Ðức Giê-hô-va đã dặn mình. Trước mặt Pha-ra-ôn và quần thần, A-rôn giơ gậy lên, đập nước sông, hết thảy nước sông bèn hóa thành huyết."));
visuperobject.biblemap.put("b2_7_21", new visuperobject(2,7,21,"Cá dưới sông chết, nước sông hôi-thúi, người Ê-díp-tô không thể uống được; vậy, huyết lan khắp cả xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_7_22", new visuperobject(2,7,22,"Song các thuật sĩ cậy phù chú mình cũng làm được như vậy, lòng Pha-ra-ôn cứng cỏi, không nghe Môi-se và A-rôn chút nào, y như lời Ðức Giê-hô-va đã phán."));
visuperobject.biblemap.put("b2_7_23", new visuperobject(2,7,23,"Pha-ra-ôn xây đi về đền mình, lòng chẳng để về mấy điều đó chút nào."));
visuperobject.biblemap.put("b2_7_24", new visuperobject(2,7,24,"Vả, vì hết thảy người Ê-díp-tô uống nước sông chẳng được, nên đào xung quanh sông tìm nước mà uống."));
visuperobject.biblemap.put("b2_7_25", new visuperobject(2,7,25,"Sau khi Ðức Giê-hô-va đập sông thì được bảy ngày."));
visuperobject.biblemap.put("b2_8_1", new visuperobject(2,8,1,"Ðoạn, Ðức Giê-hô-va phán cùng Môi-se rằng: Hãy đi ra mắt Pha-ra-ôn, và tâu cùng người rằng: Ðức Giê-hô-va có phán như vầy: Hãy tha cho dân ta đi, để chúng nó hầu việc ta."));
visuperobject.biblemap.put("b2_8_2", new visuperobject(2,8,2,"Nếu ngươi không tha cho đi, nầy ta sẽ giáng tai nạn ếch nhái khắp bờ cõi ngươi."));
visuperobject.biblemap.put("b2_8_3", new visuperobject(2,8,3,"Sông sẽ sanh đông đúc ếch nhái, bò lên vào cung điện, phòng ngủ, và trên giường người, vào nhà đầy tớ và dân sự ngươi, trong lò bếp, cùng nơi nhồi bột làm bánh của ngươi."));
visuperobject.biblemap.put("b2_8_4", new visuperobject(2,8,4,"Ếch nhái sẽ bò lên mình ngươi, lên mình dân sự và mọi đầy tớ ngươi."));
visuperobject.biblemap.put("b2_8_5", new visuperobject(2,8,5,"Vậy, Ðức Giê-hô-va phán cùng Môi-se rằng: Hãy truyền cho A-rôn rằng: Hãy cầm gậy giơ tay ra trên rạch, trên sông và trên bàu, khiến ếch nhái tràn lên xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_8_6", new visuperobject(2,8,6,"A-rôn giơ tay mình trên các sông rạch xứ Ê-díp-tô, ếch nhái bò lên lan khắp xứ."));
visuperobject.biblemap.put("b2_8_7", new visuperobject(2,8,7,"Các thuật sĩ cũng cậy phù chú mình làm y như vậy, đặng khiến ếch nhái bò lên xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_8_8", new visuperobject(2,8,8,"Pha-ra-ôn bèn đòi Môi-se và A-rôn mà phán rằng: Hãy cầu nguyện Ðức Giê-hô-va, để Ngài khiến ếch nhái cách xa ta và dân sự ta; thì ta sẽ tha dân Hê-bơ-rơ đi tế Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_8_9", new visuperobject(2,8,9,"Môi-se bèn tâu cùng Pha-ra-ôn rằng: Xin hãy định chừng nào tôi phải vì bệ hạ, đầy tớ cùng dân sự bệ hạ mà cầu khẩn Ðức Giê-hô-va, để trừ ếch nhái lìa khỏi bệ hạ và cung điện, chỉ còn dưới sông mà thôi."));
visuperobject.biblemap.put("b2_8_10", new visuperobject(2,8,10,"Vua đáp rằng: Ðến ngày mai. Môi-se rằng: Sẽ làm y theo lời, hầu cho bệ hạ biết rằng chẳng có ai giống như Giê-hô-va là Ðức Chúa Trời chúng tôi."));
visuperobject.biblemap.put("b2_8_11", new visuperobject(2,8,11,"Vậy, ếch nhái sẽ lìa xa bệ hạ, cung điện, tôi tớ, cùng dân sự của bệ hạ; chỉ còn lại dưới sông mà thôi."));
visuperobject.biblemap.put("b2_8_12", new visuperobject(2,8,12,"Môi-se và A-rôn bèn lui ra khỏi Pha-ra-ôn; Môi-se kêu cầu Ðức Giê-hô-va về việc ếch nhái mà Ngài đã khiến đến cho Pha-ra-ôn."));
visuperobject.biblemap.put("b2_8_13", new visuperobject(2,8,13,"Ðức Giê-hô-va làm y theo lời Môi-se; ếch nhái trong nhà, trong làng, ngoài đồng đều chết hết."));
visuperobject.biblemap.put("b2_8_14", new visuperobject(2,8,14,"Người ta dồn ếch nhái lại từ đống, và cả xứ hôi thúi lắm."));
visuperobject.biblemap.put("b2_8_15", new visuperobject(2,8,15,"Nhưng Pha-ra-ôn thấy được khỏi nạn, bèn rắn lòng, chẳng nghe Môi-se và A-rôn chút nào, y như lời Ðức Giê-hô-va đã phán."));
visuperobject.biblemap.put("b2_8_16", new visuperobject(2,8,16,"Ðức Giê-hô-va phán cùng Môi-se rằng: Hãy truyền cho A-rôn rằng: Hãy giơ gậy anh ra, đập bụi trên đất: bụi sẽ hóa muỗi khắp cả xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_8_17", new visuperobject(2,8,17,"Hai người làm y như vậy. A-rôn cầm gậy giơ tay ra, đập bụi trên đất, bụi liền hóa thành muỗi, bu người và súc vật; hết thảy bụi đều hóa thành muỗi trong xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_8_18", new visuperobject(2,8,18,"Các thuật sĩ cũng muốn cậy phù chú mình làm như vậy, để hóa sanh muỗi, nhưng làm chẳng đặng. Vậy, muỗi bu người và súc vật."));
visuperobject.biblemap.put("b2_8_19", new visuperobject(2,8,19,"Các thuật sĩ bèn tâu cùng Pha-ra-ôn rằng: Ấy là ngón tay của Ðức Chúa Trời; nhưng Pha-ra-ôn cứng lòng, chẳng nghe Môi-se và A-rôn chút nào, y như lời Ðức Giê-hô-va đã phán."));
visuperobject.biblemap.put("b2_8_20", new visuperobject(2,8,20,"Kế đó, Ðức Giê-hô-va phán cùng Môi-se rằng: Nầy Pha-ra-ôn sẽ ngự ra mé sông, ngươi hãy dậy sớm, đi ra mắt người, mà tâu rằng: Ðức Giê-hô-va có phán như vầy: Hãy tha cho dân ta đi, để chúng nó hầu việc ta."));
visuperobject.biblemap.put("b2_8_21", new visuperobject(2,8,21,"Vì nếu ngươi không cho đi, nầy, ta sẽ sai ruồi mòng bu nơi ngươi, quần thần, dân sự và cung điện ngươi; nhà của người Ê-díp-tô đều sẽ bị ruồi mòng bu đầy, và khắp trên đất nữa."));
visuperobject.biblemap.put("b2_8_22", new visuperobject(2,8,22,"Nhưng ngày đó, ta sẽ phân biệt xứ Gô-sen ra, là nơi dân ta ở, không có ruồi mòng, hầu cho ngươi biết rằng ta, Giê-hô-va, có ở giữa xứ."));
visuperobject.biblemap.put("b2_8_23", new visuperobject(2,8,23,"Ta sẽ phân biệt dân ta cùng dân ngươi. Ðến mai, dấu lạ nầy tất sẽ có."));
visuperobject.biblemap.put("b2_8_24", new visuperobject(2,8,24,"Ðức Giê-hô-va bèn làm y như lời. Vậy, trong cung điện Pha-ra-ôn, nhà cửa của quần thần người cùng cả xứ Ê-díp-tô đều có ruồi mòng vô số, đất bị ruồi mòng hủy hoại."));
visuperobject.biblemap.put("b2_8_25", new visuperobject(2,8,25,"Pha-ra-ôn bèn đòi Môi-se và A-rôn mà phán rằng: Hãy đi dâng tế lễ cho Ðức Chúa Trời các ngươi trong xứ."));
visuperobject.biblemap.put("b2_8_26", new visuperobject(2,8,26,"Nhưng Môi-se tâu rằng: Làm như vậy chẳng tiện, vì các của lễ mà chúng tôi sẽ tế Giê-hô-va Ðức Chúa Trời chúng tôi, là một điều gớm ghê cho người Ê-díp-tô. Nếu chúng tôi dâng của lễ gớm ghê trước mắt người Ê-díp-tô, họ há chẳng liệng đá chúng tôi sao?"));
visuperobject.biblemap.put("b2_8_27", new visuperobject(2,8,27,"Chúng tôi sẽ đi trong đồng vắng, chừng ba ngày đường, dâng tế lễ cho Giê-hô-va Ðức Chúa Trời chúng tôi, y như lời Ngài sẽ chỉ bảo."));
visuperobject.biblemap.put("b2_8_28", new visuperobject(2,8,28,"Pha-ra-ôn bèn phán rằng: Ta sẽ tha các ngươi đi dâng tế lễ cho Giê-hô-va Ðức Chúa Trời các ngươi, ở nơi đồng vắng, nhưng đừng đi xa hơn. Hãy cầu nguyện cho ta với!"));
visuperobject.biblemap.put("b2_8_29", new visuperobject(2,8,29,"Môi-se tâu rằng: Nầy, tôi sẽ lui ra khỏi bệ hạ, cầu nguyện Ðức Giê-hô-va cho; ngày mai ruồi mòng sẽ lìa xa Pha-ra-ôn, quần thần, và dân sự người. Nhưng xin Pha-ra-ôn chớ cứ gạt chúng tôi mà không tha dân sự đi dâng tế lễ cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_8_30", new visuperobject(2,8,30,"Môi-se bèn lui ra khỏi Pha-ra-ôn, và cầu nguyện Ðức Giê-hô-va;"));
visuperobject.biblemap.put("b2_8_31", new visuperobject(2,8,31,"Ngài làm y như lời Môi-se, ruồi mòng lìa xa Pha-ra-ôn, quần thần, và dân sự người, không còn lại một con nào."));
visuperobject.biblemap.put("b2_8_32", new visuperobject(2,8,32,"Nhưng lần nầy, Pha-ra-ôn lại cứng lòng nữa, không cho dân sự đi."));
visuperobject.biblemap.put("b2_9_1", new visuperobject(2,9,1,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng: Hãy đi ra mắt Pha-ra-ôn mà tâu rằng: Giê-hô-va, là Ðức Chúa Trời của dân Hê-bơ-rơ, có phán như vầy: Hãy tha dân ta đi, đặng chúng nó hầu việc ta;"));
visuperobject.biblemap.put("b2_9_2", new visuperobject(2,9,2,"vì nếu ngươi từ chối không tha đi, còn cầm lại nữa,"));
visuperobject.biblemap.put("b2_9_3", new visuperobject(2,9,3,"nầy tay của Ðức Giê-hô-va sẽ tra vào các súc vật của người ngoài đồng, vào ngựa, lừa, lạc đà, bò và chiên: sẽ có dịch lệ rất lớn."));
visuperobject.biblemap.put("b2_9_4", new visuperobject(2,9,4,"Ðức Giê-hô-va sẽ phân biệt súc vật của dân Y-sơ-ra-ên cùng súc vật của người Ê-díp-tô, sẽ chẳng có một con nào của dân Y-sơ-ra-ên bị chết."));
visuperobject.biblemap.put("b2_9_5", new visuperobject(2,9,5,"Ðức Giê-hô-va định kỳ mà rằng: Ðến mai, Ðức Giê-hô-va sẽ làm sự đó tại trong xứ."));
visuperobject.biblemap.put("b2_9_6", new visuperobject(2,9,6,"Vừa đến mai, Ðức Giê-hô-va làm sự đó, hết thảy súc vật của người Ê-díp-tô đều chết; nhưng của dân Y-sơ-ra-ên chẳng chết một con nào."));
visuperobject.biblemap.put("b2_9_7", new visuperobject(2,9,7,"Pha-ra-ôn sai người đi xét, thấy chẳng có một con súc vật nào của dân Y-sơ-ra-ên chết hết. Nhưng Pha-ra-ôn rắn lòng, chẳng cho dân sự đi."));
visuperobject.biblemap.put("b2_9_8", new visuperobject(2,9,8,"Ðức Giê-hô-va bèn phán cùng Môi-se và A-rôn rằng: Hãy hốt trong trong lò đầy tay các ngươi, rồi tại trước mặt Pha-ra-ôn, Môi-se sẽ vải tro đó lên trời."));
visuperobject.biblemap.put("b2_9_9", new visuperobject(2,9,9,"Tro sẽ hóa thành bụi khắp cả xứ Ê-díp-tô, và sanh nhiều ghẻ chốc cương mủ trên mình người và súc vật trong khắp cả xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_9_10", new visuperobject(2,9,10,"Vậy, hai người hốt tro trong lò, đứng trước mặt Pha-ra-ôn, rồi Môi-se vải tro lên trời, thì sanh ra ghẻ chốc cương mủ trên mình người ta và súc vật."));
visuperobject.biblemap.put("b2_9_11", new visuperobject(2,9,11,"Các thuật sĩ đứng trước mặt Môi-se không được, vì cớ ghẻ chốc đã sanh trên mình các thuật sĩ như trên mình của hết thảy người Ê-díp-tô."));
visuperobject.biblemap.put("b2_9_12", new visuperobject(2,9,12,"Ðức Giê-hô-va làm cho Pha-ra-ôn cứng lòng, không nghe Môi-se và A-rôn chút nào, y như lời Ðức Giê-hô-va đã phán cùng Môi-se."));
visuperobject.biblemap.put("b2_9_13", new visuperobject(2,9,13,"Ðoạn, Ðức Giê-hô-va phán cùng Môi-se rằng: Ngươi hãy thức dậy sớm, ra mắt Pha-ra-ôn mà tâu rằng: Giê-hô-va, là Ðức Chúa Trời của dân Hê-bơ-rơ, có phán như vầy: Hãy tha dân ta đi, để chúng nó hầu việc ta;"));
visuperobject.biblemap.put("b2_9_14", new visuperobject(2,9,14,"vì lần nầy ta sẽ giáng các tai nạn cho lòng ngươi, quần thần, và dân sự ngươi, hầu cho ngươi biết rằng khắp thế gian chẳng có ai bằng ta."));
visuperobject.biblemap.put("b2_9_15", new visuperobject(2,9,15,"Nếu bây giờ, ta đã giơ tay ra hành hại ngươi bịnh dịch, thì ngươi cùng dân ngươi đều đã bị diệt khỏi đất rồi."));
visuperobject.biblemap.put("b2_9_16", new visuperobject(2,9,16,"Nhưng vì cớ nầy ta để ngươi còn sống, là cho ngươi thấy quyền năng của ta, hầu cho danh ta đồn khắp cả thiên hạ."));
visuperobject.biblemap.put("b2_9_17", new visuperobject(2,9,17,"Nếu ngươi còn cản trở, không để cho dân ta đi,"));
visuperobject.biblemap.put("b2_9_18", new visuperobject(2,9,18,"thì mai, tại giờ nầy, ta sẽ làm một đám mưa đá lớn, đến đỗi tại xứ Ê-díp-tô từ khi khai sáng cho đến ngày nay chẳng có đám mưa nào giống như vậy."));
visuperobject.biblemap.put("b2_9_19", new visuperobject(2,9,19,"Thế thì, bây giờ hãy sai đem lục súc và mọi vật thuộc về ngươi ở ngoài đồng vào, cho khỏi hiểm nghèo. Mưa đá sẽ rớt xuống trên mọi người và vật đang ở ngoài đồng không vào nhà, thì sẽ chết hết."));
visuperobject.biblemap.put("b2_9_20", new visuperobject(2,9,20,"Những kẻ nào trong quần thần Pha-ra-ôn có lòng kính sợ lời Ðức Giê-hô-va, lật đật rút đầy tớ và súc vật mình về nhà;"));
visuperobject.biblemap.put("b2_9_21", new visuperobject(2,9,21,"còn kẻ nào chẳng chú ý về lời Ðức Giê-hô-va, thì để đầy tớ và súc vật mình ở ngoài đồng."));
visuperobject.biblemap.put("b2_9_22", new visuperobject(2,9,22,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng: Hãy giơ tay ngươi lên trời, làm mưa đá rớt xuống trên cả xứ Ê-díp-tô, trên mình người, súc vật, và khắp rau cỏ ngoài đồng."));
visuperobject.biblemap.put("b2_9_23", new visuperobject(2,9,23,"Môi-se bèn giơ gậy mình lên trời, Ðức Giê-hô-va làm cho sấm động, mưa đá sa, lửa chạy trên mặt đất. Vậy, Ðức Giê-hô-va khiến mưa đá sa trên xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_9_24", new visuperobject(2,9,24,"Vả, có mưa đá sa xuống và lửa cháy rất mạnh pha lộn với, đến nỗi từ khi xứ Ê-díp-tô lập quốc đến giờ, chẳng có giống như vậy."));
visuperobject.biblemap.put("b2_9_25", new visuperobject(2,9,25,"Thế thì, trong cả xứ Ê-díp-tô, mưa đá làm hại mọi vật ở ngoài đồng, nào người ta, nào súc vật, nào rau-cỏ, và làm gãy cây cối nữa."));
visuperobject.biblemap.put("b2_9_26", new visuperobject(2,9,26,"Chỉ đất Gô-sen, là nơi dân Y-sơ-ra-ên ở, chẳng có mưa đá mà thôi."));
visuperobject.biblemap.put("b2_9_27", new visuperobject(2,9,27,"Pha-ra-ôn bèn truyền đòi Môi-se và A-rôn mà phán rằng: Lần nầy trẫm đã phạm tội; Ðức Giê-hô-va là công bình; trẫm cùng dân sự trẫm là kẻ phạm tội."));
visuperobject.biblemap.put("b2_9_28", new visuperobject(2,9,28,"Hãy cầu nguyện Ðức Giê-hô-va, hầu cho tan sấm sét và mưa đá; rồi trẫm sẽ tha các ngươi đi, không đợi lâu nữa đâu."));
visuperobject.biblemap.put("b2_9_29", new visuperobject(2,9,29,"Môi-se bèn đáp rằng: Vừa khi tôi ra khỏi thành, tôi sẽ giơ tay lên Ðức Giê-hô-va, sấm sét sẽ lặng, mưa đá sẽ tan, hầu cho bệ hạ biết rằng trái đất thuộc về Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_9_30", new visuperobject(2,9,30,"Nhưng tôi biết rằng bệ hạ và quần thần chưa kính sợ Giê-hô-va Ðức Chúa Trời."));
visuperobject.biblemap.put("b2_9_31", new visuperobject(2,9,31,"Vả, trong lúc đó, lúa mạch trổ bông, cây gai có hoa, nên lúa mạch và gai bị đập nát;"));
visuperobject.biblemap.put("b2_9_32", new visuperobject(2,9,32,"còn lúa mì và tiểu mạch trổ muộn, nên không bị đập."));
visuperobject.biblemap.put("b2_9_33", new visuperobject(2,9,33,"Vậy, Môi-se lui khỏi Pha-ra-ôn, đi ra thành, giơ tay lên Ðức Giê-hô-va, sấm sét và mưa đá tạnh, mưa bèn ngớt hột trên mặt đất."));
visuperobject.biblemap.put("b2_9_34", new visuperobject(2,9,34,"Khi Pha-ra-ôn thấy mưa, mưa đá và sấm sét đã tạnh rồi, người và quần thần lại rắn lòng, cứ phạm tội nữa."));
visuperobject.biblemap.put("b2_9_35", new visuperobject(2,9,35,"Vậy Pha-ra-ôn cứng lòng, không tha dân Y-sơ-ra-ên đi, y như lời Ðức Giê-hô-va đã cậy Môi-se mà phán."));
visuperobject.biblemap.put("b2_10_1", new visuperobject(2,10,1,"Ðức Giê-hô-va phán cùng Môi-se rằng: Hãy đi đến Pha-ra-ôn, vì ta đã làm rắn lòng người và lòng quần thần, để làm cho các dấu lạ của ta tỏ ra giữa họ,"));
visuperobject.biblemap.put("b2_10_2", new visuperobject(2,10,2,"hầu cho ngươi thuật cùng con và cháu mình những công việc to tát ta đã làm trên dân Ê-díp-tô, các dấu lạ ta đã làm giữa vòng họ, đặng các ngươi biết rằng ta là Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_10_3", new visuperobject(2,10,3,"Môi-se và A-rôn bèn đi đến Pha-ra-ôn mà tâu rằng: Giê-hô-va là Ðức Chúa Trời dân Hê-bơ-rơ, có phán như vầy: Ngươi không chịu hạ mình trước mặt ta cho đến chừng nào? Hãy tha dân ta đi, để chúng nó hầu việc ta."));
visuperobject.biblemap.put("b2_10_4", new visuperobject(2,10,4,"Nếu từ chối, không cho dân ta đi, nầy, ngày mai ta sẽ sai cào cào đến địa phận ngươi;"));
visuperobject.biblemap.put("b2_10_5", new visuperobject(2,10,5,"nó sẽ phủ rợp trên mặt đất, người ta chẳng còn thấy đất nữa; nó sẽ cắn xả những vật chi còn lại, tức vật nào trận mưa đá còn chừa lại; nó cũng cắn xả những cây mọc ngoài đồng ruộng các ngươi,"));
visuperobject.biblemap.put("b2_10_6", new visuperobject(2,10,6,"vào đầy cung điện ngươi, dinh thất của quần thần ngươi, và nhà cửa của người Ê-díp-tô: ấy là một điều tổ phụ ngươi, đến nỗi thỉ tổ ngươi, cũng chẳng bao giờ thấy từ khi họ đã có trên mặt đất cho đến ngày nay. Ðoạn, Môi-se xây mặt lui ra khỏi Pha-ra-ôn."));
visuperobject.biblemap.put("b2_10_7", new visuperobject(2,10,7,"Quần thần Pha-ra-ôn tâu rằng: Ðến chừng nào người nầy mới thôi gài bẫy ta? Hãy tha dân ấy đi, để chúng nó hầu việc Giê-hô-va Ðức Chúa Trời chúng nó! Bệ hạ chưa biết rằng nước Ê-díp-tô đã bị nguy vong sao?"));
visuperobject.biblemap.put("b2_10_8", new visuperobject(2,10,8,"Họ bèn truyền đòi Môi-se và A-rôn trở lại cùng Pha-ra-ôn; người nói rằng: Hãy đi hầu việc Giê-hô-va Ðức Chúa Trời các ngươi. Hết thảy những ai là người phải đi?"));
visuperobject.biblemap.put("b2_10_9", new visuperobject(2,10,9,"Môi-se tâu rằng: Chúng tôi đi, sẽ đem nam phụ lão ấu và chiên bò theo, vì về phần chúng tôi, ấy là một lễ của Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_10_10", new visuperobject(2,10,10,"Pha-ra-ôn nói rằng: Cầu xin Ðức Giê-hô-va ở cùng các ngươi! Hồ dễ ta cho các ngươi cùng con cái đều đi sao! Không! quả thật không cho đâu, vì các ngươi có mưu ác!"));
visuperobject.biblemap.put("b2_10_11", new visuperobject(2,10,11,"Không được vậy đâu, chỉ các ngươi, là đàn ông, phải đi hầu việc Ðức Giê-hô-va, vì là điều các ngươi đã xin. Ðoạn, họ bèn đuổi Môi-se và A-rôn khỏi trước mặt Pha-ra-ôn."));
visuperobject.biblemap.put("b2_10_12", new visuperobject(2,10,12,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng: Hãy giơ tay ngươi ra trên xứ Ê-díp-tô, đặng khiến cào cào tràn lên xứ đó, cắn xả rau cỏ và mọi vật chi trên đất mưa đá còn chừa lại."));
visuperobject.biblemap.put("b2_10_13", new visuperobject(2,10,13,"Vậy, Môi-se bèn giơ gậy mình ra trên xứ Ê-díp-tô, thì Ðức Giê-hô-va dẫn ngọn gió đông thổi trên xứ sòng ngày và đêm đó; qua sáng mai gió đông đã dẫn cào cào đến."));
visuperobject.biblemap.put("b2_10_14", new visuperobject(2,10,14,"Cào cào tràn lên khắp cả xứ Ê-díp-tô, và sa xuống trong địa hạt xứ ấy nhiều vô số. Trước kia chẳng bao giờ có bằng số đó, sau nầy cũng chẳng hề sẽ có bằng như vậy."));
visuperobject.biblemap.put("b2_10_15", new visuperobject(2,10,15,"Cào cào bao phủ khắp mặt đất của cả xứ, và xứ thành ra tối tăm, cắn xả các rau cỏ ngoài đồng cùng trái cây mà mưa đá còn chừa lại; trong khắp xứ Ê-díp-tô chẳng còn chút xanh tươi chi cho cây cối hay là cho cỏ rau ở ngoài đồng ruộng nữa."));
visuperobject.biblemap.put("b2_10_16", new visuperobject(2,10,16,"Pha-ra-ôn vội đòi Môi-se và A-rôn đến mà phán rằng: Trẫm đã phạm tội cùng Giê-hô-va Ðức Chúa Trời các ngươi, và cùng các ngươi nữa."));
visuperobject.biblemap.put("b2_10_17", new visuperobject(2,10,17,"Nhưng bây giờ, xin xá tội cho trẫm lần nầy; và hãy cầu giùm Giê-hô-va Ðức Chúa Trời các ngươi, hầu cho ít nữa Ngài khiến xa trẫm cái vạ chết nầy."));
visuperobject.biblemap.put("b2_10_18", new visuperobject(2,10,18,"Môi-se bèn lui ra khỏi Pha-ra-ôn, và khẩn nguyện Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_10_19", new visuperobject(2,10,19,"Ðức Giê-hô-va dẫn ngọn gió tây đến rất mạnh, đem cào cào đi đuổi xuống Biển đỏ; khắp địa hạt Ê-díp-tô chẳng còn một con cào cào nào."));
visuperobject.biblemap.put("b2_10_20", new visuperobject(2,10,20,"Nhưng Ðức Giê-hô-va làm cho Pha-ra-ôn cứng lòng: người chẳng tha dân Y-sơ-ra-ên đi."));
visuperobject.biblemap.put("b2_10_21", new visuperobject(2,10,21,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng: Hãy giơ tay ngươi lên trời, hầu cho trên xứ Ê-díp-tô có sự tối tăm dày đến nỗi người ta rờ đụng đến được."));
visuperobject.biblemap.put("b2_10_22", new visuperobject(2,10,22,"Vậy, Môi-se giơ tay mình lên trời, bèn có sự tối tăm rất dày trong ba ngày tại xứ Ê-díp-tô;"));
visuperobject.biblemap.put("b2_10_23", new visuperobject(2,10,23,"trong ba ngày đó người ta không thấy nhau được, không ai nhớm khỏi chỗ mình được. Nhưng trong chốn dân Y-sơ-ra-ên ở thì có ánh sáng cho họ."));
visuperobject.biblemap.put("b2_10_24", new visuperobject(2,10,24,"Pha-ra-ôn bèn đòi Môi-se mà phán rằng: Hãy đi hầu việc Ðức Giê-hô-va; con trẻ các ngươi đi theo nữa; chỉ bầy chiên và bò ở lại thôi."));
visuperobject.biblemap.put("b2_10_25", new visuperobject(2,10,25,"Nhưng Môi-se tâu rằng: Chính bệ hạ hãy cấp cho chúng tôi các con sinh và vật chi dùng làm của lễ thiêu để dâng cho Giê-hô-va Ðức Chúa Trời chúng tôi."));
visuperobject.biblemap.put("b2_10_26", new visuperobject(2,10,26,"Các bầy lục súc sẽ theo chúng tôi nữa, không còn để lại một móng chân nào; vì chúng tôi sẽ dùng mấy con thú đó mà hầu việc Giê-hô-va Ðức Chúa Trời chúng tôi. Chúng tôi biết vật chi dùng để thờ phượng Ðức Giê-hô-va, chỉ khi nào sẽ đến nơi đó."));
visuperobject.biblemap.put("b2_10_27", new visuperobject(2,10,27,"Nhưng Ðức Giê-hô-va làm cho Pha-ra-ôn cứng lòng, nên người không khứng cho dân Y-sơ-ra-ên đi."));
visuperobject.biblemap.put("b2_10_28", new visuperobject(2,10,28,"Pha-ra-ôn nói rằng: Hãy lui ra khỏi ta cho rảnh! Khá giữ mình đừng thấy mặt ta nữa, vì ngày nào ngươi thấy mặt ta thì sẽ chết!"));
visuperobject.biblemap.put("b2_10_29", new visuperobject(2,10,29,"Môi-se bèn nói rằng: Bệ hạ nói phải lắm; tôi chẳng hề thấy mặt bệ hạ nữa đâu."));
visuperobject.biblemap.put("b2_11_1", new visuperobject(2,11,1,"Vả, Ðức Giê-hô-va có phán cùng Môi-se rằng: Ta sẽ giáng cho Pha-ra-ôn và xứ Ê-díp-tô một tai vạ nữa; đoạn, người sẽ tha các ngươi đi khỏi đây. Khi người định tha đi, thì sẽ đuổi các ngươi ra khỏi đây."));
visuperobject.biblemap.put("b2_11_2", new visuperobject(2,11,2,"Vậy, hãy nói cùng dân sự và dặn rằng mỗi người bất luận nam hay nữ phải xin kẻ lân cận mình những đồ bằng bạc và bằng vàng."));
visuperobject.biblemap.put("b2_11_3", new visuperobject(2,11,3,"Ðức Giê-hô-va làm cho dân sự được ơn trước mặt người Ê-díp-tô; Môi-se cũng là một người rất tôn trọng trong xứ Ê-díp-tô, trước mắt quần thần của Pha-ra-ôn và trước mắt dân ấy."));
visuperobject.biblemap.put("b2_11_4", new visuperobject(2,11,4,"Môi-se nói: Ðức Giê-hô-va có phán như vầy: Chừng giữa đêm ta sẽ ra tuần hành xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_11_5", new visuperobject(2,11,5,"Hết thảy con trưởng nam trong xứ Ê-díp-tô sẽ chết, từ thái tử của Pha-ra-ôn ngồi trên ngai mình, cho đến con cả của người đòi ở sau cối, và luôn mọi con đầu lòng của súc vật nữa."));
visuperobject.biblemap.put("b2_11_6", new visuperobject(2,11,6,"Trong cả xứ Ê-díp-tô sẽ có tiếng kêu la inh ỏi, cho đến đỗi chưa hề có, và cũng sẽ chẳng bao giờ có giống như vậy nữa."));
visuperobject.biblemap.put("b2_11_7", new visuperobject(2,11,7,"Nhưng, trong cả dân Y-sơ-ra-ên dầu đến một con chó cũng sẽ chẳng sủa hoặc người, hoặc vật; hầu cho các ngươi biết rằng Ðức Giê-hô-va phân biệt dân Y-sơ-ra-ên cùng người Ê-díp-tô là dường nào."));
visuperobject.biblemap.put("b2_11_8", new visuperobject(2,11,8,"Nầy, quần thần của bệ hạ sẽ xuống cùng tôi, sấp mình trước mặt tôi mà rằng: Ngươi và cả dân sự theo ngươi hãy đi đi! Ðoạn, tôi sẽ đi ra. Môi-se bèn lui ra khỏi Pha-ra-ôn lấy làm giận lắm."));
visuperobject.biblemap.put("b2_11_9", new visuperobject(2,11,9,"Vả, Ðức Giê-hô-va có phán cùng Môi-se rằng: Pha-ra-ôn chẳng khứng nghe ngươi đâu, hầu cho các dấu lạ ta thêm lên trong xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_11_10", new visuperobject(2,11,10,"Môi-se và A-rôn bèn làm các dấu lạ trước mặt Pha-ra-ôn; nhưng Ðức Giê-hô-va làm cho Pha-ra-ôn cứng lòng, nên người chẳng tha dân Y-sơ-ra-ên đi khỏi xứ mình."));
visuperobject.biblemap.put("b2_12_1", new visuperobject(2,12,1,"Tại xứ Ê-díp-tô, Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b2_12_2", new visuperobject(2,12,2,"Tháng nầy định làm tháng đầu cho các ngươi, tức là tháng giêng trong quanh năm."));
visuperobject.biblemap.put("b2_12_3", new visuperobject(2,12,3,"Hãy nói cho toàn hội chúng Y-sơ-ra-ên rằng: Ðến ngày mồng mười tháng nầy, mỗi gia trưởng tùy theo nhà mình phải bắt một con chiên con."));
visuperobject.biblemap.put("b2_12_4", new visuperobject(2,12,4,"Nếu nhà ít người quá ăn không hết một chiên con, hãy chung cùng người rất lân cận mình, tùy theo số người; và hãy tính cho mỗi chiên con tùy sức người ăn hết."));
visuperobject.biblemap.put("b2_12_5", new visuperobject(2,12,5,"Các ngươi hãy bắt hoặc trong bầy chiên, hoặc trong bầy dê, chiên con đực hay là dê con đực, tuổi giáp niên, chẳng tì vít chi,"));
visuperobject.biblemap.put("b2_12_6", new visuperobject(2,12,6,"đoạn để dành cho đến ngày mười bốn tháng nầy; rồi cả hội chúng Y-sơ-ra-ên sẽ giết nó, vào lối chiều tối."));
visuperobject.biblemap.put("b2_12_7", new visuperobject(2,12,7,"Họ sẽ lấy huyết đem bôi trên hai cây cột và mày cửa của nhà nào ăn thịt chiên con đó."));
visuperobject.biblemap.put("b2_12_8", new visuperobject(2,12,8,"Ðêm ấy họ sẽ ăn thịt chiên quay với bánh không men và rau đắng."));
visuperobject.biblemap.put("b2_12_9", new visuperobject(2,12,9,"Chớ ăn thịt nửa chín nửa sống hay là luộc trong nước, nhưng phải quay trên lửa cả và đầu, giò, với bộ lòng."));
visuperobject.biblemap.put("b2_12_10", new visuperobject(2,12,10,"Các ngươi chớ để chi còn lại đến sáng mai; nếu còn lại, hãy thiêu đi."));
visuperobject.biblemap.put("b2_12_11", new visuperobject(2,12,11,"Vậy, ăn thịt đó, phải như vầy: Dây lưng cột, chân mang giày, tay cầm gậy, ăn hối hả; ấy là lễ Vượt-qua của Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_12_12", new visuperobject(2,12,12,"Ðêm đó ta sẽ đi qua xứ Ê-díp-tô, hành hại mọi con đầu lòng xứ Ê-díp-tô, từ người ta cho đến súc vật; ta sẽ xét đoán các thần của xứ Ê-díp-tô; ta là Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_12_13", new visuperobject(2,12,13,"Huyết bôi trên nhà các ngươi ở, sẽ dùng làm dấu hiệu; khi ta hành hại xứ Ê-díp-tô, thấy huyết đó, thì sẽ vượt qua, chẳng có tai nạn hủy diệt các ngươi."));
visuperobject.biblemap.put("b2_12_14", new visuperobject(2,12,14,"Các ngươi hãy ghi ngày đó làm kỷ niệm; trải các đời hãy giữ làm một lễ cho Ðức Giê-hô-va, tức là một lễ lập ra đời đời."));
visuperobject.biblemap.put("b2_12_15", new visuperobject(2,12,15,"Trong bảy ngày phải ăn bánh không men: vừa đến ngày thứ nhất, hãy dẹp men khỏi nhà đi; vì hễ ai ăn bánh có men từ ngày thứ nhất cho đến ngày thứ bảy, thì sẽ bị truất ra khỏi Y-sơ-ra-ên."));
visuperobject.biblemap.put("b2_12_16", new visuperobject(2,12,16,"Ngày thứ nhất, sẽ có một sự nhóm hiệp thánh; qua ngày thứ bảy các ngươi cũng sẽ có một sự nhóm hiệp thánh nữa. Trong mấy ngày đó chẳng nên làm công việc chi, chỉ lo sắm đồ ăn cho mỗi người cần ăn mà thôi."));
visuperobject.biblemap.put("b2_12_17", new visuperobject(2,12,17,"Vậy, các ngươi hãy giữ lễ bánh không men, vì chánh ngày đó ta rút quân đội các ngươi ra khỏi xứ Ê-díp-tô; nên chi, trải các đời phải giữ ngày đó như một lễ lập ra đời đời."));
visuperobject.biblemap.put("b2_12_18", new visuperobject(2,12,18,"Ngày mười bốn tháng giêng, chiều lại, các ngươi sẽ ăn bánh không men cho đến chiều ngày hai mươi mốt tháng đó."));
visuperobject.biblemap.put("b2_12_19", new visuperobject(2,12,19,"Trong bảy ngày không nên còn thấy men tại nhà các ngươi; vì bất luận người ngoại bang hay là người bổn xứ, hễ ai ăn bánh có men sẽ bị truất khỏi hội chúng Y-sơ-ra-ên."));
visuperobject.biblemap.put("b2_12_20", new visuperobject(2,12,20,"Chớ nên ăn bánh có men; khắp nơi các ngươi ở đều phải ăn bánh không men."));
visuperobject.biblemap.put("b2_12_21", new visuperobject(2,12,21,"Vậy, Môi-se nhóm lại các trưởng lão Y-sơ-ra-ên mà nói rằng: Hãy đi bắt một con trong bầy cho mọi nhà các ngươi, và giết nó làm lễ Vượt-qua."));
visuperobject.biblemap.put("b2_12_22", new visuperobject(2,12,22,"Rồi hãy lấy một chùm kinh giới nhúng vào huyết trong chậu, bôi lên mây và hai cây cột cửa, đoạn, trong các ngươi chớ ai ra khỏi cửa nhà mình cho đến sáng mai."));
visuperobject.biblemap.put("b2_12_23", new visuperobject(2,12,23,"Ðức Giê-hô-va sẽ đi qua đặng hành hại xứ Ê-díp-tô; khi thấy huyết nơi mày và hai cây cột cửa, Ngài sẽ vượt qua, không cho kẻ tiêu diệt vào nhà các ngươi đặng hành hại."));
visuperobject.biblemap.put("b2_12_24", new visuperobject(2,12,24,"Hãy giữ lễ nầy như một lệ lập ra đời đời cho các ngươi và con cháu các ngươi."));
visuperobject.biblemap.put("b2_12_25", new visuperobject(2,12,25,"Khi nào các ngươi vào xứ mà Ðức Giê-hô-va sẽ ban cho, như lời Ngài đã phán, thì hãy giữ lễ nầy."));
visuperobject.biblemap.put("b2_12_26", new visuperobject(2,12,26,"Khi con cháu các ngươi hỏi rằng: Lễ nầy là nghĩa chi?"));
visuperobject.biblemap.put("b2_12_27", new visuperobject(2,12,27,"Hãy đáp rằng: Ấy là của tế lễ Vượt-qua của Ðức Giê-hô-va, vì khi Ngài hành hại xứ Ê-díp-tô thì Ngài đi vượt qua các nhà dân Y-sơ-ra-ên, và cứu nhà chúng ta đó."));
visuperobject.biblemap.put("b2_12_28", new visuperobject(2,12,28,"Dân Y-sơ-ra-ên bèn cúi đầu lạy, rồi đi, làm theo như lời Ðức Giê-hô-va đã phán dặn Môi-se và A-rôn."));
visuperobject.biblemap.put("b2_12_29", new visuperobject(2,12,29,"Vả, khi giữa đêm, Ðức Giê-hô-va hành hại mọi con đầu lòng tại xứ Ê-díp-tô, từ thái tử của Pha-ra-ôn ngồi trên ngai mình, cho đến con cả người bị tù, và hết thảy con đầu lòng của súc vật."));
visuperobject.biblemap.put("b2_12_30", new visuperobject(2,12,30,"Ðang lúc ban đêm, Pha-ra-ôn, quần thần cùng hết thảy người Ê-díp-tô đều chờ dậy; có tiếng kêu la inh ỏi trong xứ Ê-díp-tô, vì chẳng một nhà nào là không có người chết."));
visuperobject.biblemap.put("b2_12_31", new visuperobject(2,12,31,"Trong đêm đó, Pha-ra-ôn bèn đòi Môi-se và A-rôn mà phán rằng: Hai ngươi và dân Y-sơ-ra-ên hãy chờ dậy, ra khỏi giữa vòng dân ta mà đi hầu việc Ðức Giê-hô-va, như các ngươi đã nói."));
visuperobject.biblemap.put("b2_12_32", new visuperobject(2,12,32,"Cũng hãy dẫn bò và chiên đi, như lời các ngươi đã nói, và cầu phước cho ta nữa."));
visuperobject.biblemap.put("b2_12_33", new visuperobject(2,12,33,"Người Ê-díp-tô thúc giục dân Y-sơ-ra-ên ra khỏi xứ mau mau, vì nói rằng: Chúng ta đều chết hết!"));
visuperobject.biblemap.put("b2_12_34", new visuperobject(2,12,34,"Vậy, dân sự đều đem bột nhồi rồi chưa men đi, và bọc những thùng nhồi bột trong áo tơi vác lên vai mình."));
visuperobject.biblemap.put("b2_12_35", new visuperobject(2,12,35,"Vả, dân Y-sơ-ra-ên có làm y theo lời Môi-se đã dặn, xin người Ê-díp-tô những đồ bằng bạc và bằng vàng cùng áo xống."));
visuperobject.biblemap.put("b2_12_36", new visuperobject(2,12,36,"Ðức Giê-hô-va làm cho dân sự được ơn trước mắt người Ê-díp-tô, nên họ bằng lòng cho; và dân Y-sơ-ra-ên lột trần người Ê-díp-tô vậy."));
visuperobject.biblemap.put("b2_12_37", new visuperobject(2,12,37,"Dân Y-sơ-ra-ên đi từ Ram-se đến Su-cốt, số không kể con nít, được chừng sáu mươi vạn người đàn ông đi bộ."));
visuperobject.biblemap.put("b2_12_38", new visuperobject(2,12,38,"Lại có vô số người ngoại bang đi lên chung luôn với chiên, bò, súc vật rất nhiều."));
visuperobject.biblemap.put("b2_12_39", new visuperobject(2,12,39,"Chúng bèn hấp bánh không men bằng bột của mình đã đem theo từ xứ Ê-díp-tô; bột không men, vì cớ bị đuổi khỏi xứ Ê-díp-tô không thế chậm trễ, và cũng chẳng sắm kịp lương thực chi được."));
visuperobject.biblemap.put("b2_12_40", new visuperobject(2,12,40,"Vả, thì kiều ngụ của dân Y-sơ-ra-ên tại xứ Ê-díp-tô được bốn trăm ba mươi năm."));
visuperobject.biblemap.put("b2_12_41", new visuperobject(2,12,41,"Khi mãn bốn trăm ba mươi năm rồi, thì, cũng trong ngày đó, các quân đội của Ðức Giê-hô-va ra khỏi xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_12_42", new visuperobject(2,12,42,"Ấy là một đêm người ta phải giữ cho Ðức Giê-hô-va, vì Ngài rút dân Y-sơ-ra-ên khỏi xứ Ê-díp-tô. Trải các đời, cả dân Y-sơ-ra-ên phải giữ đêm đó, để tôn trọng Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_12_43", new visuperobject(2,12,43,"Ðức Giê-hô-va phán cùng Môi-se và A-rôn rằng: Ðây là luật lệ về lễ Vượt-qua: Phàm người ngoại bang chẳng được ăn lễ đó."));
visuperobject.biblemap.put("b2_12_44", new visuperobject(2,12,44,"Còn về phần kẻ tôi mọi, là người bỏ tiền ra mua, ngươi hãy làm phép cắt bì cho, thì họ mới được ăn."));
visuperobject.biblemap.put("b2_12_45", new visuperobject(2,12,45,"Khách ngoại bang và kẻ làm thuê cũng chẳng được phép ăn lễ đâu."));
visuperobject.biblemap.put("b2_12_46", new visuperobject(2,12,46,"Lễ đó chỉ ăn nội trong nhà; ngươi đừng đem thịt ra ngoài, và cũng đừng làm gãy một cái xương nào."));
visuperobject.biblemap.put("b2_12_47", new visuperobject(2,12,47,"Hết thảy hội chúng Y-sơ-ra-ên phải giữ lễ Vượt-qua."));
visuperobject.biblemap.put("b2_12_48", new visuperobject(2,12,48,"Khi một khách ngoại bang nào kiều ngụ nhà ngươi, muốn giữ lễ Vượt-qua của Ðức Giê-hô-va, thì mọi người nam của họ phải chịu phép cắt bì; đoạn, họ mới được đến gần giữ lễ nầy, và được coi như người sanh trong xứ. Nhưng ai không chịu phép cắt bì, thì chẳng được ăn đâu."));
visuperobject.biblemap.put("b2_12_49", new visuperobject(2,12,49,"Cũng đồng một luật cho người sanh trong xứ cùng khách ngoại bang nào đến kiều ngụ giữa các ngươi."));
visuperobject.biblemap.put("b2_12_50", new visuperobject(2,12,50,"Toàn dân Y-sơ-ra-ên đều làm như lời Ðức Giê-hô-va đã phán dặn Môi-se và A-rôn."));
visuperobject.biblemap.put("b2_12_51", new visuperobject(2,12,51,"Cũng trong ngày đó, Ðức Giê-hô-va rút dân Y-sơ-ra-ên, tùy theo quân đội, ra khỏi xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_13_1", new visuperobject(2,13,1,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b2_13_2", new visuperobject(2,13,2,"Trong vòng dân Y-sơ-ra-ên bất luận người hay vật, hãy vì ta biệt riêng ra thánh mọi con đầu lòng; bởi con đầu lòng thuộc về ta."));
visuperobject.biblemap.put("b2_13_3", new visuperobject(2,13,3,"Môi-se nói cùng dân sự rằng: Hãy kỷ niệm ngày nầy, vì là ngày Ðức Giê-hô-va dùng tay quyền năng rút các ngươi ra khỏi xứ Ê-díp-tô, tức là khỏi nhà nô lệ; nên chớ ai ăn bánh có men."));
visuperobject.biblemap.put("b2_13_4", new visuperobject(2,13,4,"Ngày nay, nhằm tháng lúa trỗ, các ngươi ra đi."));
visuperobject.biblemap.put("b2_13_5", new visuperobject(2,13,5,"Vậy khi nào Ðức Giê-hô-va đưa ngươi vào bờ cõi xứ dân Ca-na-an, dân Hê-tít, dân A-mô-rít, dân Hê-vít, và dân Giê-bu-sít rồi, là một xứ đượm sữa và mật, và là xứ Ngài đã thề cùng tổ phụ sẽ ban cho ngươi, thì đến trong tháng nầy hãy giữ lễ thánh đó."));
visuperobject.biblemap.put("b2_13_6", new visuperobject(2,13,6,"Trong bảy ngày hãy ăn bánh không men; đến ngày thứ bảy sẽ có lễ kính Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_13_7", new visuperobject(2,13,7,"Phải ăn bánh không men trong bảy ngày; khắp cả địa hạt ngươi sẽ không thấy bánh có men, hoặc men tại nơi ngươi nữa."));
visuperobject.biblemap.put("b2_13_8", new visuperobject(2,13,8,"Trong ngày đó hãy giải nghĩa cho con trai ngươi rằng: Ấy vì việc Ðức Giê-hô-va làm cho ta, khi ta ra khỏi xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_13_9", new visuperobject(2,13,9,"Ðiều đó sẽ làm một dấu hiệu nơi tay ngươi, làm một kỷ niệm ghi nơi trán ở giữa cặp mắt ngươi, hầu cho luật pháp của Ðức Giê-hô-va ở nơi miệng ngươi, vì Ðức Giê-hô-va đã dùng tay quyền năng rút ngươi ra khỏi xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_13_10", new visuperobject(2,13,10,"Hằng năm, đến kỳ, ngươi phải giữ lễ nầy."));
visuperobject.biblemap.put("b2_13_11", new visuperobject(2,13,11,"Khi Ðức Giê-hô-va đã đưa ngươi vào bờ cõi xứ Ca-na-an, như Ngài đã thề cùng ngươi, và tổ phụ ngươi, khi Ngài đã ban xứ đó cho rồi,"));
visuperobject.biblemap.put("b2_13_12", new visuperobject(2,13,12,"thì hãy đem dâng cho Ðức Giê-hô-va những con trưởng nam cùng cả con đầu lòng đực của súc vật ngươi, vì chúng nó đều sẽ thuộc về ÐṀ©c Giê-hô-va."));
visuperobject.biblemap.put("b2_13_13", new visuperobject(2,13,13,"Nhưng mỗi con lừa đầu lòng, ngươi phải bắt chiên con hay là dê con mà chuộc; bằng không, ngươi hãy vặn cổ nó. Ngươi cũng chuộc mọi trưởng nam trong vòng con trai ngươi."));
visuperobject.biblemap.put("b2_13_14", new visuperobject(2,13,14,"Vả, một mai con trai ngươi hỏi rằng: Ðiều đó có ý nghĩa chi? thì hãy đáp rằng: Ấy vì cớ Ðức Giê-hô-va đã dùng tay quyền năng rút chúng ta ra khỏi xứ Ê-díp-tô, tức là nhà nô lệ;"));
visuperobject.biblemap.put("b2_13_15", new visuperobject(2,13,15,"và khi Pha-ra-ôn cứng lòng không tha chúng ta đi, Ngài bèn giết hết các con đầu lòng trong xứ đó, từ con trưởng nam người ta cho đến con đầu lòng súc vật. Vì cớ đó, ta dâng tế lễ cho Ðức Giê-hô-va mọi con đực sanh đầu lòng, và ta chuộc con trưởng nam trong vòng con trai ta."));
visuperobject.biblemap.put("b2_13_16", new visuperobject(2,13,16,"Ấy sẽ làm một dấu hiệu nơi tay ngươi, và ấn chí nơi trán giữa cặp mắt ngươi, để nhắc cho nhớ rằng Ðức Giê-hô-va đã dùng tay quyền năng rút chúng ta ra khỏi xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_13_17", new visuperobject(2,13,17,"Vả, khi Pha-ra-ôn tha dân Y-sơ-ra-ên đi, Ðức Chúa Trời không dẫn dân ấy đi theo đường về xứ Phi-li-tin, là đường gần hơn hết, vì Ngài nói rằng: E khi dân thấy trận mạc, dời lòng trở về xứ Ê-díp-tô chăng."));
visuperobject.biblemap.put("b2_13_18", new visuperobject(2,13,18,"Cho nên Ðức Chúa Trời dẫn chúng đi vòng theo đường trong đồng vắng, về hướng Biển-dỏ. Dân Y-sơ-ra-ên cầm khí giới ra khỏi xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_13_19", new visuperobject(2,13,19,"Môi-se dời hài cốt Giô-sép theo mình, vì Giô-sép có bắt dân Y-sơ-ra-ên thề quyết rằng: Chắc Ðức Chúa Trời sẽ viếng các ngươi; hãy dời hài cốt ta theo các ngươi khỏi đây."));
visuperobject.biblemap.put("b2_13_20", new visuperobject(2,13,20,"Vả, chúng đi khỏi Su-cốt, đóng trại tại Ê-tam, ở cuối đầu đồng vắng."));
visuperobject.biblemap.put("b2_13_21", new visuperobject(2,13,21,"Ðức Giê-hô-va đi trước dân sự, ban ngày ở trong một trụ mây, để dẫn đường đi; ban đêm trong một trụ lửa, để soi sáng cho chúng, hầu cho được đi luôn ngày và đêm."));
visuperobject.biblemap.put("b2_13_22", new visuperobject(2,13,22,"Ban ngày trụ mây chẳng bao giờ cách xa dân sự, trụ lửa ban đêm cũng vậy."));
visuperobject.biblemap.put("b2_14_1", new visuperobject(2,14,1,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b2_14_2", new visuperobject(2,14,2,"Hãy truyền cho dân Y-sơ-ra-ên trở lại đóng trại tại trước Phi-Ha-hi-rốt, ngang Ba-anh-Sê-phôn, giữa khoảng Mít-đôn và biển. Các ngươi hãy đóng trại đối diện nơi nầy, gần biển."));
visuperobject.biblemap.put("b2_14_3", new visuperobject(2,14,3,"Chắc Pha-ra-ôn sẽ nói về dân Y-sơ-ra-ên rằng: Dân đó lạc đường bị khốn trong đồng vắng rồi."));
visuperobject.biblemap.put("b2_14_4", new visuperobject(2,14,4,"Ta sẽ làm cho Pha-ra-ôn cứng lòng, người sẽ đuổi theo dân đó; nhưng ta sẽ vì Pha-ra-ôn và cả toán binh người được rạng danh; người Ê-díp-tô sẽ biết ta là Ðức Giê-hô-va. Dân Y-sơ-ra-ên bèn làm như vậy."));
visuperobject.biblemap.put("b2_14_5", new visuperobject(2,14,5,"Vả, họ báo cùng vua Ê-díp-tô rằng dân Hê-bơ-rơ đã trốn đi rồi. Lòng Pha-ra-ôn và quần thần đối cùng dân đó bèn thay đổi, nói rằng: Chúng ta đã làm chi vậy, tha dân Y-sơ-ra-ên đi để khỏi phục dịch ta nữa sao?"));
visuperobject.biblemap.put("b2_14_6", new visuperobject(2,14,6,"Pha-ra-ôn bèn thắng xe và đem dân mình theo."));
visuperobject.biblemap.put("b2_14_7", new visuperobject(2,14,7,"Người đem sáu trăm xe thượng hạng và hết thảy xe trong xứ Ê-díp-tô, trên mỗi xe đều có chiến binh cả."));
visuperobject.biblemap.put("b2_14_8", new visuperobject(2,14,8,"Ðức Giê-hô-va làm cho Pha-ra-ôn, vua xứ Ê-díp-tô, cứng lòng, đuổi theo dân Y-sơ-ra-ên; nhưng dân nầy đã ra đi một cách dạn dĩ."));
visuperobject.biblemap.put("b2_14_9", new visuperobject(2,14,9,"Người Ê-díp-tô và cả ngựa xe, lính kỵ, cùng toán binh của Pha-ra-ôn đều đuổi theo dân Y-sơ-ra-ên, gặp đang đóng đồn nơi bờ biển, gần Phi-ha-hi-rốt, ngang Ba-anh-Sê-phôn."));
visuperobject.biblemap.put("b2_14_10", new visuperobject(2,14,10,"Vả, khi Pha-ra-ôn đến gần, dân Y-sơ-ra-ên ngước mắt lên, thấy dân Ê-díp-tô đuổi theo, bèn lấy làm hãi hùng, kêu van Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_14_11", new visuperobject(2,14,11,"Chúng lại nói cùng Môi-se rằng: Xứ Ê-díp-tô há chẳng có nơi mộ phần, nên nỗi người mới dẫn chúng tôi vào đồng vắng đặng chết sao? Người đưa chúng tôi ra xứ Ê-díp-tô để làm chi?"));
visuperobject.biblemap.put("b2_14_12", new visuperobject(2,14,12,"Chúng tôi há chẳng có nói cùng người tại xứ Ê-díp-tô rằng: Ðể mặc chúng tôi phục dịch dân Ê-díp-tô, vì thà rằng phục dịch họ còn hơn phải chết nơi đồng vắng?"));
visuperobject.biblemap.put("b2_14_13", new visuperobject(2,14,13,"Môi-se đáp cùng dân sự rằng: Chớ sợ chi, hãy ở đó, rồi ngày nay xem sự giải cứu Ðức Giê-hô-va sẽ làm cho các ngươi; vì người Ê-díp-tô mà các ngươi ngó thấy ngày nay, thì chẳng bao giờ ngó thấy nữa."));
visuperobject.biblemap.put("b2_14_14", new visuperobject(2,14,14,"Ðức Giê-hô-va sẽ chiến cự cho, còn các ngươi cứ yên lặng."));
visuperobject.biblemap.put("b2_14_15", new visuperobject(2,14,15,"Ðức Giê-hô-va phán cùng Môi-se rằng: Sao ngươi kêu van ta? Hãy bảo dân Y-sơ-ra-ên cứ đi;"));
visuperobject.biblemap.put("b2_14_16", new visuperobject(2,14,16,"còn ngươi hãy giơ gậy lên, đưa tay trên mặt biển, phân rẽ nước ra, thì dân Y-sơ-ra-ên sẽ xuống biển đi như trên đất cạn."));
visuperobject.biblemap.put("b2_14_17", new visuperobject(2,14,17,"Còn ta sẽ làm cho dân Ê-díp-tô cứng lòng theo dân Y-sơ-ra-ên xuống biển, rồi ta sẽ được rạng danh vì Pha-ra-ôn, cả đạo binh, binh xa, và lính kỵ người."));
visuperobject.biblemap.put("b2_14_18", new visuperobject(2,14,18,"Khi nào ta được rạng danh vì Pha-ra-ôn, binh xa, cùng lính kỵ người, thì người Ê-díp-tô sẽ rõ ràng ta là Ðức Giê-hô-va vậy."));
visuperobject.biblemap.put("b2_14_19", new visuperobject(2,14,19,"Thiên sứ của Ðức Chúa Trời đã đi trước trại quân của dân Y-sơ-ra-ên lộn lại đi sau; còn trụ mây vốn đi trước trại quân, lại ở về sau;"));
visuperobject.biblemap.put("b2_14_20", new visuperobject(2,14,20,"trụ mây đứng về giữa khoảng trại người Ê-díp-tô và trại dân Y-sơ-ra-ên, làm áng mây tối tăm cho đàng nầy, soi sáng ban đêm cho đàng kia; nên trọn cả đêm hai trại chẳng hề xáp gần nhau được."));
visuperobject.biblemap.put("b2_14_21", new visuperobject(2,14,21,"Vả, Môi-se giơ tay ra trên biển, Ðức Giê-hô-va dẫn trận gió đông thổi mạnh đến đùa biển lại trọn cả đêm; Ngài làm cho biển bày ra khô, nước phân rẽ."));
visuperobject.biblemap.put("b2_14_22", new visuperobject(2,14,22,"Dân Y-sơ-ra-ên xuống biển, đi như trên đất cạn; còn nước làm thành một tấm vách ngăn bên hữu và bên tả."));
visuperobject.biblemap.put("b2_14_23", new visuperobject(2,14,23,"Người Ê-díp-tô bèn đuổi theo; hết thảy ngựa, binh xa cùng lính kỵ của Pha-ra-ôn đều theo xuống giữa biển."));
visuperobject.biblemap.put("b2_14_24", new visuperobject(2,14,24,"Vả, đến canh sáng, Ðức Giê-hô-va ở trong lòng trụ mây và lửa, nhìn ra thấy trại quân Ê-díp-tô, bèn làm cho họ phải rối loạn."));
visuperobject.biblemap.put("b2_14_25", new visuperobject(2,14,25,"Ngài tháo bánh xe của họ, khiến dẫn dắt cực nhọc. Người Ê-díp-tô bèn nói nhau rằng: Ta hãy trốn khỏi dân Y-sơ-ra-ên đi, vì Ðức Giê-hô-va thế dân đó mà chiến cự cùng chúng ta."));
visuperobject.biblemap.put("b2_14_26", new visuperobject(2,14,26,"Ðức Giê-hô-va phán cùng Môi-se rằng: Hãy giơ tay ngươi ra trên biển, nước sẽ trở lấp người Ê-díp-tô, binh xa và lính kỵ của chúng nó."));
visuperobject.biblemap.put("b2_14_27", new visuperobject(2,14,27,"Môi-se bèn giơ tay mình ra trên biển, lối sáng mai, nước trở lấp phủ đáy biển lại, người Ê-díp-tô chạy trốn bị nước chận; vậy Ðức Giê-hô-va xô họ xuống giữa biển."));
visuperobject.biblemap.put("b2_14_28", new visuperobject(2,14,28,"Thế thì, nước trở lại bao phủ binh xa, lính kỵ của cả đạo binh Pha-ra-ôn đã theo dân Y-sơ-ra-ên xuống biển, chẳng còn sót lại một ai."));
visuperobject.biblemap.put("b2_14_29", new visuperobject(2,14,29,"Song dân Y-sơ-ra-ên đi giữa biển như trên đất cạn; nước làm thành vách ngăn bên hữu và bên tả."));
visuperobject.biblemap.put("b2_14_30", new visuperobject(2,14,30,"Trong ngày đó, Ðức Giê-hô-va giải cứu dân Y-sơ-ra-ên thoát khỏi tay người Ê-díp-tô; dân ấy thấy người Ê-díp-tô chết trên bãi biển."));
visuperobject.biblemap.put("b2_14_31", new visuperobject(2,14,31,"Dân Y-sơ-ra-ên thấy việc lớn lao mà Ðức Giê-hô-va đã làm cho người Ê-díp-tô, nên kính sợ Ngài, tin Ngài và Môi-se, là tôi tớ Ngài."));
visuperobject.biblemap.put("b2_15_1", new visuperobject(2,15,1,"Ðoạn, Môi-se và dân Y-sơ-ra-ên hát bài ca nầy cho Ðức Giê-hô-va, rằng: Tôi ca tụng Ðức Giê-hô-va, vì Ngài rất vinh hiển oai nghiêm. Ngài đã liệng xuống biển ngựa và người cỡi ngựa."));
visuperobject.biblemap.put("b2_15_2", new visuperobject(2,15,2,"Ðức Giê-hô-va là sức mạnh và sự ca tụng của tôi: Ngài đã trở nên Ðấng cứu tôi. Ngài là Ðức Chúa Trời tôi, tôi ngợi khen Ngài; Ðức Chúa Trời của tổ phụ tôi, tôi tôn kính Ngài."));
visuperobject.biblemap.put("b2_15_3", new visuperobject(2,15,3,"Ðức Giê-hô-va là một chiến sĩ; Danh Ngài là Giê-hô-va."));
visuperobject.biblemap.put("b2_15_4", new visuperobject(2,15,4,"Ngài đã ném xuống biển binh xa Pha-ra-ôn và cả đạo binh của người; Quan tướng kén chọn của người đã bị đắm nơi Biển đỏ,"));
visuperobject.biblemap.put("b2_15_5", new visuperobject(2,15,5,"Những lượn sóng đã vùi lấp họ rồi; Họ chìm đến đáy biển như một hòn đá vậy."));
visuperobject.biblemap.put("b2_15_6", new visuperobject(2,15,6,"Hỡi Ðức Giê-hô-va! Tay hữu Ngài có sức mạnh vinh hiển thay. Hỡi Ðức Giê-hô-va! Tay hữu Ngài hủy quân nghịch tan đi."));
visuperobject.biblemap.put("b2_15_7", new visuperobject(2,15,7,"Bởi sự cả thể của oai nghiêm Ngài, Ðánh đổ kẻ dấy nghịch cùng mình. Ngài nổi giận phừng phừng, Ðốt họ cháy như rơm ra."));
visuperobject.biblemap.put("b2_15_8", new visuperobject(2,15,8,"Lỗ mũi Ngài thở hơi ra, nước bèn dồn lại, Lượn sóng đổ dựng như một bờ đê; Vực sâu đọng lại nơi rún biển."));
visuperobject.biblemap.put("b2_15_9", new visuperobject(2,15,9,"Quân nghịch rằng: Ta đuổi theo, ta sẽ đuổi kịp Ta sẽ chia phân của giặc; Tâm hồn ta sẽ thỏa nguyện nơi họ; Ta rút gươm ra, tay ta hủy diệt họ."));
visuperobject.biblemap.put("b2_15_10", new visuperobject(2,15,10,"Ngài đã thở hơi ra Biển vùi lấp quân nghịch lại; Họ bị chìm như cục chì Nơi đáy nước sâu."));
visuperobject.biblemap.put("b2_15_11", new visuperobject(2,15,11,"Hỡi Ðức Giê-hô-va! trong vòng các thần, ai giống như Ngài? Trong sự thánh khiết, ai được vinh hiển như Ngài. Ðáng sợ, đáng khen Hay làm các phép lạ?"));
visuperobject.biblemap.put("b2_15_12", new visuperobject(2,15,12,"Ngài đã đưa tay hữu ra, Ðất đã nuốt chúng nó."));
visuperobject.biblemap.put("b2_15_13", new visuperobject(2,15,13,"Ngài lấy lòng thương xót dìu dắt Dân nầy mà Ngài đã chuộc lại; Ngài đã dùng năng lực đưa nó Về nơi ở thánh của Ngài."));
visuperobject.biblemap.put("b2_15_14", new visuperobject(2,15,14,"Các dân tộc nghe biết việc nầy đều run sợ, Cơn kinh hãi áp-hãm dân xứ Pha-lê-tin."));
visuperobject.biblemap.put("b2_15_15", new visuperobject(2,15,15,"Các quan trưởng xứ Ê-đôm bèn lấy làm bối rối; Mấy mặt anh hùng nơi Mô-áp đều kinh hồn; Cả dân Ca-na-an đều mất vía"));
visuperobject.biblemap.put("b2_15_16", new visuperobject(2,15,16,"Sự hoảng hốt kinh hãi giáng cho chúng nó; Hỡi Ðức Giê-hô-va! vì quyền cao nơi cánh tay Ngài, Chúng nó đều bị câm như đá, Cho đến chừng nào dân Ngài vượt qua; Cho đến khi dân mà Ngài đã chuộc, vượt qua rồi."));
visuperobject.biblemap.put("b2_15_17", new visuperobject(2,15,17,"Hỡi Ðức Giê-hô-va! Ngài đưa dân ấy vào, và lập nơi núi cơ nghiệp Ngài, Tức là chốn Ngài đã sắm sẵn, để làm nơi ở của Ngài, Hỡi Chúa! là đền thánh mà tay Ngài đã lập."));
visuperobject.biblemap.put("b2_15_18", new visuperobject(2,15,18,"Ðức Giê-hô-va sẽ cai trị đời đời kiếp kiếp."));
visuperobject.biblemap.put("b2_15_19", new visuperobject(2,15,19,"Vì ngựa của Pha-ra-ôn, binh xa, cùng lính kỵ người đã xuống biển; Và Ðức Giê-hô-va đã vùi dập nước biển lên trên, Nhưng dân Y-sơ-ra-ên đi giữa biển như đi trên đất cạn."));
visuperobject.biblemap.put("b2_15_20", new visuperobject(2,15,20,"Nữ tiên tri Mi-ri-am, là em gái A-rôn, tay cầm trống cơm, các đàn bà đều đi ra theo người, cầm trống cơm và múa."));
visuperobject.biblemap.put("b2_15_21", new visuperobject(2,15,21,"Mi-ri-am đáp lại rằng: Hãy ca tụng Ðức Giê-hô-va, vì Ngài rất vinh hiển oai nghiêm; Ngài đã liệng xuống biển ngựa và người cỡi ngựa."));
visuperobject.biblemap.put("b2_15_22", new visuperobject(2,15,22,"Ðoạn, Môi-se đem dân Y-sơ-ra-ên đi khỏi Biển đỏ, đến nơi đồng vắng Su-rơ; trọn ba ngày đi trong đồng vắng, kiếm chẳng được nước đâu cả."));
visuperobject.biblemap.put("b2_15_23", new visuperobject(2,15,23,"Kế đến đất Ma-ra, nhưng vì nước tại đó đắng, uống chẳng được, nên chi chỗ nầy gọi là Ma-ra."));
visuperobject.biblemap.put("b2_15_24", new visuperobject(2,15,24,"Dân sự bèn oán trách Môi-se rằng: Chúng tôi lấy chi uống?"));
visuperobject.biblemap.put("b2_15_25", new visuperobject(2,15,25,"Môi-se kêu van Ðức Giê-hô-va; Ngài bèn chỉ cho người một cây gỗ, người lấy liệng xuống nước, nước bèn hóa ra ngọt. Ấy tại đó, Ðức Giê-hô-va định luật lệ cùng lập pháp độ cho dân sự, và tại đó Ngài thử họ."));
visuperobject.biblemap.put("b2_15_26", new visuperobject(2,15,26,"Ngài phán rằng: Nếu ngươi chăm chỉ nghe lời Giê-hô-va Ðức Chúa Trời ngươi, làm sự ngay thẳng trước mặt Ngài, lắng tai nghe các điều răn và giữ mọi luật lệ Ngài, thì ta chẳng giáng cho ngươi một trong các bịnh nào mà ta đã giáng cho xứ Ê-díp-tô; vì ta là Ðức Giê-hô-va, Ðấng chữa bịnh cho ngươi."));
visuperobject.biblemap.put("b2_15_27", new visuperobject(2,15,27,"Ðoạn, dân sự đến đất Ê-lim; nơi đó có mười hai suối nước, và bảy mươi cây chà là; dân sự đóng trại tại đó, gần bên suối nước."));
visuperobject.biblemap.put("b2_16_1", new visuperobject(2,16,1,"Nhằm ngày mười lăm tháng hai, sau khi ra khỏi xứ Ê-díp-tô, cả hội chúng Y-sơ-ra-ên ở nơi Ê-lim đi đến đồng vắng Sin, nằm về giữa khoảng Ê-lim và Si-na -i."));
visuperobject.biblemap.put("b2_16_2", new visuperobject(2,16,2,"Cả hội chúng Y-sơ-ra-ên oán trách Môi-se và A-rôn tại nơi đồng vắng,"));
visuperobject.biblemap.put("b2_16_3", new visuperobject(2,16,3,"nói cùng hai người rằng: Ôi! thà rằng chúng tôi chịu chết về tay Ðức Giê-hô-va tại xứ Ê-díp-tô, khi còn ngồi kề nồi thịt và ăn bánh chán hê! Vì hai người dẫn dắt chúng tôi vào nơi đồng vắng nầy đều bị chết đói."));
visuperobject.biblemap.put("b2_16_4", new visuperobject(2,16,4,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng: Nầy, ta sẽ từ trên trời cao làm mưa bánh xuống cho các ngươi. Dân sự sẽ đi ra, mỗi ngày nào thâu bánh đủ cho ngày nấy, đặng ta thử dân coi có đi theo luật lệ của ta hay chăng."));
visuperobject.biblemap.put("b2_16_5", new visuperobject(2,16,5,"Qua ngày thứ sáu, dân sự sẽ dự bị phần đã góp, và sẽ được phần gấp bằng hai của mình thường thâu hằng ngày."));
visuperobject.biblemap.put("b2_16_6", new visuperobject(2,16,6,"Môi-se và A-rôn bèn nói cùng dân Y-sơ-ra-ên rằng: Chiều nay các ngươi sẽ nhận biết rằng ấy là Ðức Giê-hô-va đã rút các ngươi ra khỏi xứ Ê-díp-tô,"));
visuperobject.biblemap.put("b2_16_7", new visuperobject(2,16,7,"và sáng mai sẽ thấy sự vinh quang của Ðức Giê-hô-va, vì Ngài đã nghe lời các ngươi oán trách Ngài rồi. Vả lại, chúng ta là ai mà các ngươi oán trách chúng ta?"));
visuperobject.biblemap.put("b2_16_8", new visuperobject(2,16,8,"Môi-se nói: Chiều nầy Ðức Giê-hô-va sẽ phát thịt cho các ngươi ăn, rồi sáng mai bánh nhiều dư dật, vì Ngài đã nghe lời các ngươi oán trách Ngài rồi. Thế thì, chúng ta là ai? Những lời oán trách chẳng phải đổ về chúng ta đâu, nhưng về Ðức Giê-hô-va vậy."));
visuperobject.biblemap.put("b2_16_9", new visuperobject(2,16,9,"Môi-se nói cùng A-rôn rằng: Hãy nói cho cả hội chúng Y-sơ-ra-ên rằng: Các ngươi hãy đến trước mặt Ðức Giê-hô-va, vì Ngài đã nghe lời oán trách của các ngươi rồi."));
visuperobject.biblemap.put("b2_16_10", new visuperobject(2,16,10,"Vả, khi A-rôn nói cùng cả hội chúng Y-sơ-ra-ên, thì họ xây mặt về phía đồng vắng, thấy sự vinh quang của Ngài hiện ra trong đám mây."));
visuperobject.biblemap.put("b2_16_11", new visuperobject(2,16,11,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b2_16_12", new visuperobject(2,16,12,"Ta đã nghe lời oán trách của dân Y-sơ-ra-ên. Vậy, ngươi hãy nói cùng chúng nó rằng: Lối chiều các ngươi sẽ ăn thịt; sáng mai sẽ ăn bánh no nê, và sẽ biết ta là Giê-hô-va Ðức Chúa Trời của các ngươi."));
visuperobject.biblemap.put("b2_16_13", new visuperobject(2,16,13,"Vậy, chiều lại, có chim cút bay lên phủ trên trại quân; đến sáng mai lại có một lớp sương ở chung quanh trại quân."));
visuperobject.biblemap.put("b2_16_14", new visuperobject(2,16,14,"Lớp sương đó tan đi, trên mặt đồng vắng thấy có vật chi nhỏ, tròn, như hột sương đóng trên mặt đất."));
visuperobject.biblemap.put("b2_16_15", new visuperobject(2,16,15,"Khi dân Y-sơ-ra-ên thấy, bèn hỏi nhau rằng: Cái chi vậy? Vì chẳng biết vật đó là gì. Môi-se bèn nói cùng dân sự rằng: Ấy là bánh mà Ðức Giê-hô-va ban cho các ngươi làm lương thực đó."));
visuperobject.biblemap.put("b2_16_16", new visuperobject(2,16,16,"Nầy là lời Ðức Giê-hô-va đã phán dặn: Hãy tùy sức ăn của mỗi người, và tùy số người trong trại mà góp cho mỗi tên một ô-me."));
visuperobject.biblemap.put("b2_16_17", new visuperobject(2,16,17,"Dân Y-sơ-ra-ên bèn làm như vậy, kẻ thì lượm nhiều, người thì lượm ít,"));
visuperobject.biblemap.put("b2_16_18", new visuperobject(2,16,18,"cứ lường từ mô-me; ai lượm nhiều chẳng được trội, ai lượm ít cũng chẳng thiếu; mỗi người lượm vừa đủ sức mình ăn."));
visuperobject.biblemap.put("b2_16_19", new visuperobject(2,16,19,"Môi-se nói cùng dân sự rằng: Ðừng ai để dư lại cho đến sáng mai."));
visuperobject.biblemap.put("b2_16_20", new visuperobject(2,16,20,"Nhưng dân sự chẳng nghe theo lời Môi-se, một vài người để dư lại đến sáng mai; thì sâu hóa ở trong, vật sanh mùi hôi hám. Môi-se bèn nổi giận cùng họ."));
visuperobject.biblemap.put("b2_16_21", new visuperobject(2,16,21,"Vậy, hằng buổi sớm mai mỗi người lượm vừa đủ sức mình ăn; khi mặt trời nắng nóng thì vật đó tan ra."));
visuperobject.biblemap.put("b2_16_22", new visuperobject(2,16,22,"Ðến ngày thứ sáu, dân sự lượm lương thực gấp hai; mỗi người hai ô-me. Các hội trưởng đến thuật lại cho Môi-se rõ."));
visuperobject.biblemap.put("b2_16_23", new visuperobject(2,16,23,"Người đáp rằng: Ấy là lời Ðức Giê-hô-va đã phán rằng: Mai là ngày nghỉ, tức ngày Sa-bát thánh cho Ðức Giê-hô-va, hãy nướng món chi các ngươi muốn nướng, hãy nấu món chi các ngươi muốn nấu; hễ còn dư, hãy để dành đến sáng mai."));
visuperobject.biblemap.put("b2_16_24", new visuperobject(2,16,24,"Dân sự bèn để dành cho đến sáng mai, y như lời Môi-se đã truyền; vật đó chẳng sanh mùi hôi hám và cũng chẳng hóa sâu chút nào."));
visuperobject.biblemap.put("b2_16_25", new visuperobject(2,16,25,"Môi-se bèn nói rằng: Bữa nay hãy ăn đồ đó đi, vì là ngày Sa-bát của Ðức Giê-hô-va; hôm nay các ngươi chẳng tìm thấy vật đó ở trong đồng đâu."));
visuperobject.biblemap.put("b2_16_26", new visuperobject(2,16,26,"Các ngươi lượm trong sáu ngày, nhưng qua ngày thứ bảy là ngày Sa-bát, sẽ chẳng có đâu."));
visuperobject.biblemap.put("b2_16_27", new visuperobject(2,16,27,"Ngày thứ bảy, một vài người trong vòng dân sự ra đặng lượm lấy, nhưng tìm chẳng thấy chi hết."));
visuperobject.biblemap.put("b2_16_28", new visuperobject(2,16,28,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng: Các ngươi chẳng chịu vâng giữ điều răn và luật pháp ta cho đến chừng nào?"));
visuperobject.biblemap.put("b2_16_29", new visuperobject(2,16,29,"Hãy suy nghĩ rằng Ðức Giê-hô-va đã cho các ngươi ngày Sa-bát; vậy nên, đến ngày thứ sáu Ngài lại cho các ngươi lương thực đủ hai ngày. Trong ngày thứ bảy, mỗi người phải ở yên chỗ mình, chớ ai ra khỏi nhà."));
visuperobject.biblemap.put("b2_16_30", new visuperobject(2,16,30,"Thế thì, ngày thứ bảy dân sự đều nghỉ ngơi."));
visuperobject.biblemap.put("b2_16_31", new visuperobject(2,16,31,"Nhà Y-sơ-ra-ên đặt tên lương thực nầy là ma-na; nó giống như hột ngò, sắc trắng, mùi như bánh ngọt pha mật ong."));
visuperobject.biblemap.put("b2_16_32", new visuperobject(2,16,32,"Môi-se nói rằng: Lời Ðức Giê-hô-va đã phán dặn như vầy: Hãy đổ đầy một ô-me ma-na, đặng lưu truyền trải các đời, hầu cho thiên hạ thấy thứ bánh ta đã cho các ngươi ăn nơi đồng vắng, khi ta rút các ngươi ra khỏi xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_16_33", new visuperobject(2,16,33,"Môi-se lại nói cùng A-rôn rằng: Hãy lấy một cái bình đựng đầy một ô-me ma-na, để trước mặt Ðức Giê-hô-va, đặng lưu truyền các đời."));
visuperobject.biblemap.put("b2_16_34", new visuperobject(2,16,34,"A-rôn bèn để bình đó trước sự chứng cớ, hầu cho được lưu truyền y như lời Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b2_16_35", new visuperobject(2,16,35,"Dân Y-sơ-ra-ên ăn ma-na trải bốn mươi năm, cho đến khi vào bờ cõi xứ Ca-na-an, là xứ có người ta ở."));
visuperobject.biblemap.put("b2_16_36", new visuperobject(2,16,36,"Vả, ô-me là một phần mười của ê-pha."));
visuperobject.biblemap.put("b2_17_1", new visuperobject(2,17,1,"Cả hội chúng Y-sơ-ra-ên đi từ đồng vắng Sin, đi từng trạm, tùy theo lịnh Ðức Giê-hô-va, đóng trại tại Rê-phi-đim; ở đó chẳng có nước cho dân sự uống."));
visuperobject.biblemap.put("b2_17_2", new visuperobject(2,17,2,"Dân sự bèn kiếm cớ cãi lộn cùng Môi-se mà rằng: Hãy cho chúng tôi nước uống. Môi-se đáp rằng: Sao các ngươi kiếm cớ cãi lộn cùng ta? Sao ướm thử Ðức Giê-hô-va vậy?"));
visuperobject.biblemap.put("b2_17_3", new visuperobject(2,17,3,"Dân sự ở đó không có nước, bị khát, nên oán trách Môi-se mà rằng: Sao người khiến chúng tôi ra xứ Ê-díp-tô, hầu cho chúng tôi, con và các bầy súc vật phải chịu chết khát thế nầy?"));
visuperobject.biblemap.put("b2_17_4", new visuperobject(2,17,4,"Môi-se bèn kêu cầu Ðức Giê-hô-va mà rằng: Tôi phải xử thế nào cùng dân nầy? Thiếu điều họ ném đá tôi!"));
visuperobject.biblemap.put("b2_17_5", new visuperobject(2,17,5,"Ðức Giê-hô-va đáp cùng Môi-se rằng: Hãy cầm gậy của ngươi đã đập dưới sông, dẫn theo những trưởng lão Y-sơ-ra-ên, rồi đi trước mặt dân sự."));
visuperobject.biblemap.put("b2_17_6", new visuperobject(2,17,6,"Nầy ta sẽ đứng đằng trước ngươi, ở trên hòn đá tại Hô-rếp kia, ngươi hãy đập hòn đá, thì nước từ đó chảy ra, dân sự sẽ uống lấy. Môi-se bèn làm như vậy trước mặt các trưởng lão Y-sơ-ra-ên."));
visuperobject.biblemap.put("b2_17_7", new visuperobject(2,17,7,"Người đặt tên nơi nầy là Ma-sa và Mê-ri-ba, vì cớ việc cãi lộn mà dân Y-sơ-ra-ên kiếm cớ gây cùng người, và vì họ đã ướm thử Ðức Giê-hô-va mà nói rằng: Có Ðức Giê-hô-va ở giữa chúng ta hay chăng?"));
visuperobject.biblemap.put("b2_17_8", new visuperobject(2,17,8,"Vả, khi đó, dân A-ma-léc đến khêu chiến cùng Y-sơ-ra-ên tại Rê-phi-đim."));
visuperobject.biblemap.put("b2_17_9", new visuperobject(2,17,9,"Môi-se bèn nói cùng Giô-suê rằng: Hãy chọn lấy tráng sĩ cho chúng ta, ra chiến đấu cùng dân A-ma-léc; ngày mai ta sẽ đứng nơi đầu nổng, cầm gậy của Ðức Chúa Trời trong tay."));
visuperobject.biblemap.put("b2_17_10", new visuperobject(2,17,10,"Giô-suê bèn làm y như lời Môi-se nói, để cự chiến dân A-ma-léc; còn Môi-se, A-rôn và Hu-rơ lên trên đầu nổng."));
visuperobject.biblemap.put("b2_17_11", new visuperobject(2,17,11,"Vả, hễ đang khi Môi-se giơ tay lên, thì dân Y-sơ-ra-ên thắng hơn; nhưng khi người xụi tay xuống, dân A-ma-léc lại thắng hơn."));
visuperobject.biblemap.put("b2_17_12", new visuperobject(2,17,12,"Tay Môi-se mỏi, A-rôn và Hu-rơ bèn lấy đá kê cho người ngồi, rồi ở hai bên đỡ tay người lên; tay người chẳng lay động cho đến khi mặt trời lặn."));
visuperobject.biblemap.put("b2_17_13", new visuperobject(2,17,13,"Giô-suê lấy lưỡi gươm đánh bại A-ma-léc và dân sự người."));
visuperobject.biblemap.put("b2_17_14", new visuperobject(2,17,14,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng: Hãy chép điều nầy trong sách làm kỷ niệm, và hãy nói cho Giô-suê biết rằng ta sẽ bôi sạch kỷ niệm về A-ma-léc trong thiên hạ."));
visuperobject.biblemap.put("b2_17_15", new visuperobject(2,17,15,"Môi-se lập lên một bàn thờ, đặt tên là Giê-hô-va cờ xí của tôi;"));
visuperobject.biblemap.put("b2_17_16", new visuperobject(2,17,16,"và nói rằng: Bởi vì A-ma-léc có giơ tay lên nghịch cùng ngôi Ðức Giê-hô-va, nên Ðức Giê-hô-va chinh chiến với A-ma-léc từ đời nầy qua đời kia."));
visuperobject.biblemap.put("b2_18_1", new visuperobject(2,18,1,"Giê-trô, thầy tế lễ xứ Ma-đi-an, ông gia của Môi-se, hay được mọi điều Ðức Chúa Trời đã làm cho Môi-se cùng Y-sơ-ra-ên, là dân sự Ngài, và nghe Ngài đã dẫn dân ấy ra khỏi xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_18_2", new visuperobject(2,18,2,"Người bèn dẫn Sê-phô-ra, vợ Môi-se, đã cho trở về nhà cha,"));
visuperobject.biblemap.put("b2_18_3", new visuperobject(2,18,3,"cùng hai con trai, một tên là Ghẹt-sôn, vì Môi-se đã nói rằng: Tôi kiều ngụ nơi ngoại bang;"));
visuperobject.biblemap.put("b2_18_4", new visuperobject(2,18,4,"và một tên là Ê-li-ê-se, vì người lại có nói: Ðức Chúa Trời của tổ phụ tôi đã vùa giúp và giải cứu tôi khỏi gươm Pha-ra-ôn."));
visuperobject.biblemap.put("b2_18_5", new visuperobject(2,18,5,"Vậy, Giê-trô, ông gia của Môi-se, đến với hai con trai và vợ Môi-se, thăm người nơi đồng vắng, chỗ đóng trại gần bên núi Ðức Chúa Trời."));
visuperobject.biblemap.put("b2_18_6", new visuperobject(2,18,6,"Người bèn sai báo cùng Môi-se rằng: Ta là Giê-trô, ông gia của ngươi, ta cùng vợ ngươi và hai con trai nàng đến thăm ngươi."));
visuperobject.biblemap.put("b2_18_7", new visuperobject(2,18,7,"Môi-se ra đón rước ông gia mình, mọp xuống và hôn người, hỏi thăm nhau, rồi vào trại."));
visuperobject.biblemap.put("b2_18_8", new visuperobject(2,18,8,"Môi-se liền thuật cho ông gia mình biết mọi điều Ðức Giê-hô-va vì Y-sơ-ra-ên đã làm cho Pha-ra-ôn và người Ê-díp-tô, mọi điều khổ nhọc xảy đến cho mình dọc đường và Ðức Giê-hô-va đã giải thoát dân Y-sơ-ra-ên thế nào."));
visuperobject.biblemap.put("b2_18_9", new visuperobject(2,18,9,"Giê-trô lấy làm vui mừng về các ơn mà Ðức Giê-hô-va đã làm cho Y-sơ-ra-ên, giải cứu dân ấy khỏi tay người Ê-díp-tô."));
visuperobject.biblemap.put("b2_18_10", new visuperobject(2,18,10,"Giê-trô nói rằng: Ðáng ngợi khen Ðức Giê-hô-va thay! là Ðấng đã giải cứu các ngươi ra khỏi tay người Ê-díp-tô và tay Pha-ra-ôn, và giải cứu dân sự ra khỏi quyền của người Ê-díp-tô."));
visuperobject.biblemap.put("b2_18_11", new visuperobject(2,18,11,"Bây giờ ta biết rằng Ðức Giê-hô-va là lớn hơn hết các thần, vì khi người Ê-díp-tô cậy lòng kiêu ngạo dấy lên, thì Ngài thắng được chúng nó."));
visuperobject.biblemap.put("b2_18_12", new visuperobject(2,18,12,"Giê-trô, ông gia của Môi-se, bèn bày một của lễ thiêu và các lễ vật mà dâng cho Ðức Chúa Trời; rồi A-rôn và các trưởng lão Y-sơ-ra-ên đến ăn bánh cùng ông gia của Môi-se, trước mặt Ðức Chúa Trời."));
visuperobject.biblemap.put("b2_18_13", new visuperobject(2,18,13,"Qua ngày sau, Môi-se ra ngồi xét đoán dân sự; dân sự đứng chực trước mặt người từ sớm mai đến chiều."));
visuperobject.biblemap.put("b2_18_14", new visuperobject(2,18,14,"Ông gia thấy mọi điều người làm cho dân sự, bèn hỏi rằng: Con làm chi cùng dân sự đó? Sao con chỉ ngồi một mình, còn cả dân sự đứng chực trước mặt từ sớm mai cho đến chiều như vậy?"));
visuperobject.biblemap.put("b2_18_15", new visuperobject(2,18,15,"Môi-se bèn thưa rằng: Ấy vì dân sự đến cùng tôi đặng hỏi ý Ðức Chúa Trời."));
visuperobject.biblemap.put("b2_18_16", new visuperobject(2,18,16,"Khi nào họ có việc chi, thì đến cùng tôi, tôi xét đoán người nầy cùng người kia, và cho họ biết những mạng lịnh và luật pháp của Ðức Chúa Trời."));
visuperobject.biblemap.put("b2_18_17", new visuperobject(2,18,17,"Nhưng ông gia lại nói rằng: Ðiều con làm đó chẳng tiện."));
visuperobject.biblemap.put("b2_18_18", new visuperobject(2,18,18,"Quả thật, con cùng dân sự ở với con sẽ bị đuối chẳng sai, vì việc đó nặng nề quá sức con, một mình gánh chẳng nổi."));
visuperobject.biblemap.put("b2_18_19", new visuperobject(2,18,19,"Bây giờ, hãy nghe cha khuyên con một lời, cầu xin Ðức Giê-hô-va phù hộ cho. Về phần con, hãy làm kẻ thay mặt cho dân sự trước mặt Ðức Chúa Trời, và đem trình mọi việc cho Ngài hay."));
visuperobject.biblemap.put("b2_18_20", new visuperobject(2,18,20,"Hãy lấy mạng lịnh và luật pháp Ngài mà dạy họ, chỉ cho biết con đường nào phải đi, và điều chi phải làm."));
visuperobject.biblemap.put("b2_18_21", new visuperobject(2,18,21,"Nhưng hãy chọn lấy trong vòng dân sự mấy người tài năng, kính sợ Ðức Chúa Trời, chân thật, ghét sự tham lợi, mà lập lên trên dân sự, làm trưởng cai trị hoặc ngàn người, hoặc trăm người, hoặc năm mươi người, hoặc mười người,"));
visuperobject.biblemap.put("b2_18_22", new visuperobject(2,18,22,"đặng xét đoán dân sự hằng ngày. Nếu có việc can hệ lớn, họ hãy giải lên cho con; còn những việc nhỏ mọn, chính họ hãy xét đoán lấy. Hãy san bớt gánh cho nhẹ; đặng họ chia gánh cùng con."));
visuperobject.biblemap.put("b2_18_23", new visuperobject(2,18,23,"Nếu con làm việc nầy, và Ðức Chúa Trời ban lịnh cho con, con chắc sẽ chịu nổi được, và cả dân sự nấy sẽ đến chỗ mình bình yên."));
visuperobject.biblemap.put("b2_18_24", new visuperobject(2,18,24,"Môi-se vâng lời ông gia mình, làm y như lọi điều người đã dạy."));
visuperobject.biblemap.put("b2_18_25", new visuperobject(2,18,25,"Vậy, Môi-se bèn chọn trong cả Y-sơ-ra-ên những người tài năng, lập lên trên dân sự, làm trưởng cai trị, hoặc ngàn người, hoặc trăm người, hoặc năm mươi người, hoặc mười người;"));
visuperobject.biblemap.put("b2_18_26", new visuperobject(2,18,26,"họ xét đoán dân sự hằng ngày. Các việc khó họ giãi lên cho Môi-se; nhưng chính họ xét lấy các việc nhỏ."));
visuperobject.biblemap.put("b2_18_27", new visuperobject(2,18,27,"Ðoạn, Môi-se để cho ông gia mình trở về xứ người."));
visuperobject.biblemap.put("b2_19_1", new visuperobject(2,19,1,"Tháng thứ ba, sau khi ra khỏi xứ Ê-díp-tô, trong ngày đó dân Y-sơ-ra-ên đến nơi đồng vắng Si-na -i."));
visuperobject.biblemap.put("b2_19_2", new visuperobject(2,19,2,"Từ Rê-phi-đim ra đi, đến nơi đồng vắng Si-na -i, đóng trại tại đó, đối diện cùng núi."));
visuperobject.biblemap.put("b2_19_3", new visuperobject(2,19,3,"Môi-se bèn lên đến Ðức Chúa Trời, Ðức Giê-hô-va ở trên núi gọi người mà phán rằng: Ngươi hãy nói như vầy cho nhà Gia-cốp, và tỏ điều nầy cho dân Y-sơ-ra-ên:"));
visuperobject.biblemap.put("b2_19_4", new visuperobject(2,19,4,"Các ngươi đã thấy điều ta làm cho người Ê-díp-tô, ta chở các ngươi trên cánh chim ưng làm sao, và dẫn các ngươi đến cùng ta thể nào."));
visuperobject.biblemap.put("b2_19_5", new visuperobject(2,19,5,"Vậy, bây giờ, nếu các ngươi vâng lời ta và giữ sự giao ước ta, thì trong muôn dân, các ngươi sẽ thuộc riêng về ta, vì cả thế gian đều thuộc về ta."));
visuperobject.biblemap.put("b2_19_6", new visuperobject(2,19,6,"Các ngươi sẽ thành một nước thầy tế lễ, cùng một dân tộc thánh cho ta. Bấy nhiêu lời đó ngươi sẽ nói lại cùng dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b2_19_7", new visuperobject(2,19,7,"Môi-se đến đòi các trưởng lão trong dân sự, thuật cùng họ mọi lời Ðức Giê-hô-va đã dặn mình nói lại."));
visuperobject.biblemap.put("b2_19_8", new visuperobject(2,19,8,"Cả dân sự đồng thinh đáp rằng: Chúng tôi xin làm mọi việc Ðức Giê-hô-va đã phán dặn. Môi-se bèn thuật lại lời của dân sự cùng Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_19_9", new visuperobject(2,19,9,"Ngài phán rằng: Nầy, ta sẽ ở trong đám mây mịt mịt đi đến ngươi; đặng khi nào ta phán cùng ngươi, thì dân sự nghe và tin cậy ngươi luôn luôn. Ðoạn, Môi-se đem lời của dân sự thưa lại cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_19_10", new visuperobject(2,19,10,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng: Hãy đi đến cùng dân sự, khiến dân giữ mình thánh sạch ngày nay và ngày mai, cùng phải giặt áo xống mình."));
visuperobject.biblemap.put("b2_19_11", new visuperobject(2,19,11,"Qua ngày thứ ba, dân khá sẵn sàng, vì ngày đó Ðức Giê-hô-va sẽ giáng lâm trước mắt dân sự tại núi Si-na -i."));
visuperobject.biblemap.put("b2_19_12", new visuperobject(2,19,12,"Vả, ngươi hãy phân định giới hạn cho dân sự ở chung quanh núi, và dặn rằng: Khá giữ mình đừng leo lên núi, hoặc đụng đến chân; hễ kẻ nào đụng đến thì sẽ bị xử tử."));
visuperobject.biblemap.put("b2_19_13", new visuperobject(2,19,13,"Chớ ai tra tay vào mình người đó, nhưng người đó phải bị liệng đá hoặc bắn tên; bất luận vật hay người, cũng chẳng để cho sống đâu. Khi kèn thổi lên, dân sự sẽ đến gần núi."));
visuperobject.biblemap.put("b2_19_14", new visuperobject(2,19,14,"Môi-se xuống núi đến cùng dân sự, khiến họ giữ mình thánh sạch, và giặt áo xống mình."));
visuperobject.biblemap.put("b2_19_15", new visuperobject(2,19,15,"Người dặn dân sự rằng: Trong ba ngày hãy sẵn sàng chớ đến gần đàn bà."));
visuperobject.biblemap.put("b2_19_16", new visuperobject(2,19,16,"Qua sáng ngày thứ ba, có sấm vang chớp nhoáng, một áng mây mịt mịt ở trên núi, và tiếng kèn thổi rất vang động; cả dân sự ở trong trại quân đều run hãi."));
visuperobject.biblemap.put("b2_19_17", new visuperobject(2,19,17,"Môi-se bèn biểu dân ra khỏi trại quân nghinh tiếp Ðức Chúa Trời; dân sự dừng lại tại chân núi."));
visuperobject.biblemap.put("b2_19_18", new visuperobject(2,19,18,"Vả, bấy giờ, khắp núi Si-na -i đều ra khói, vì Ðức Giê-hô-va ở trong lửa giáng lâm nơi đó; khói ra khác nào khói của lò lửa lớn kia, và cả hòn núi đều rung động cách kịch liệt."));
visuperobject.biblemap.put("b2_19_19", new visuperobject(2,19,19,"Tiếng kèn càng vang động; Môi-se nói, Ðức Chúa Trời đáp tiếng lại."));
visuperobject.biblemap.put("b2_19_20", new visuperobject(2,19,20,"Ðức Giê-hô-va giáng-lâm trên chót núi Si-na -i, đòi Môi-se lên, Môi-se bèn lên."));
visuperobject.biblemap.put("b2_19_21", new visuperobject(2,19,21,"Ðức Giê-hô-va phán rằng: Hãy xuống cấm dân sự đừng xông pha đến gần Ðức Giê-hô-va mà nhìn, e nhiều người trong bọn phải chết chăng."));
visuperobject.biblemap.put("b2_19_22", new visuperobject(2,19,22,"Dầu những thầy tế lễ đến gần Ðức Giê-hô-va cũng phải giữ mình thánh sạch, kẻo Ngài hại họ chăng."));
visuperobject.biblemap.put("b2_19_23", new visuperobject(2,19,23,"Môi-se thưa cùng Ðức Giê-hô-va rằng: Dân sự chẳng được lên núi Si-na -i đâu, vì Ngài có cấm chúng tôi mà phán rằng: Hãy phân định giới hạn chung quanh núi, và phân biệt nơi đó là đất thánh."));
visuperobject.biblemap.put("b2_19_24", new visuperobject(2,19,24,"Ðức Giê-hô-va phán rằng: Hãy đi xuống, rồi đem A-rôn lên cùng ngươi, song những thầy tế lễ và dân sự chớ xông pha đặng lên đến gần Ðức Giê-hô-va, e Ngài hại họ chăng."));
visuperobject.biblemap.put("b2_19_25", new visuperobject(2,19,25,"Vậy, Môi-se đi xuống, nói lại cho dân sự nghe."));
visuperobject.biblemap.put("b2_20_1", new visuperobject(2,20,1,"Bấy giờ, Ðức Chúa Trời phán mọi lời nầy, rằng:"));
visuperobject.biblemap.put("b2_20_2", new visuperobject(2,20,2,"Ta là Giê-hô-va Ðức Chúa Trời ngươi, đã rút ngươi ra khỏi xứ Ê-díp-tô, là nhà nô lệ."));
visuperobject.biblemap.put("b2_20_3", new visuperobject(2,20,3,"Trước mặt ta, ngươi chớ có các thần khác."));
visuperobject.biblemap.put("b2_20_4", new visuperobject(2,20,4,"Ngươi chớ làm tượng chạm cho mình, cũng chớ làm tượng nào giống những vật trên trời cao kia, hoặc nơi đất thấp nầy, hoặc trong nước dưới đất."));
visuperobject.biblemap.put("b2_20_5", new visuperobject(2,20,5,"Ngươi chớ quì lạy trước các hình tượng đó, và cũng đừng hầu việc chúng nó; vì ta là Giê-hô-va Ðức Chúa Trời ngươi, tức là Ðức Chúa Trời kỵ tà, hễ ai ghét ta, ta sẽ nhơn tội tổ phụ phạt lại con cháu đến ba bốn đời,"));
visuperobject.biblemap.put("b2_20_6", new visuperobject(2,20,6,"và sẽ làm ơn đến ngàn đời cho những kẻ yêu mến ta và giữ các điều răn ta."));
visuperobject.biblemap.put("b2_20_7", new visuperobject(2,20,7,"Ngươi chớ lấy danh Giê-hô-va Ðức Chúa Trời ngươi mà làm chơi, vì Ðức Giê-hô-va chẳng cầm bằng vô tội kẻ nào lấy danh Ngài mà làm chơi."));
visuperobject.biblemap.put("b2_20_8", new visuperobject(2,20,8,"Hãy nhớ ngày nghỉ đặng làm nên ngày thánh."));
visuperobject.biblemap.put("b2_20_9", new visuperobject(2,20,9,"Ngươi hãy làm hết công việc mình trong sáu ngày;"));
visuperobject.biblemap.put("b2_20_10", new visuperobject(2,20,10,"nhưng ngày thứ bảy là ngày nghỉ của Giê-hô-va Ðức Chúa Trời ngươi: trong ngày đó, ngươi, con trai, con gái tôi trai tớ gái, súc vật của ngươi, hoặc khách ngoại bang ở trong nhà ngươi, đều chớ làm công việc chi hết;"));
visuperobject.biblemap.put("b2_20_11", new visuperobject(2,20,11,"vì trong sáu ngày Ðức Giê-hô-va đã dựng nên trời, đất, biển, và muôn vật ở trong đó, qua ngày thứ bảy thì Ngài nghỉ: vậy nên Ðức Giê-hô-va đã ban phước cho ngày nghỉ và làm nên ngày thánh."));
visuperobject.biblemap.put("b2_20_12", new visuperobject(2,20,12,"Hãy hiếu kính cha mẹ ngươi, hầu cho ngươi được sống lâu trên đất mà Giê-hô-va Ðức Chúa Trời ngươi ban cho."));
visuperobject.biblemap.put("b2_20_13", new visuperobject(2,20,13,"Ngươi chớ giết người."));
visuperobject.biblemap.put("b2_20_14", new visuperobject(2,20,14,"Ngươi chớ phạm tội tà dâm."));
visuperobject.biblemap.put("b2_20_15", new visuperobject(2,20,15,"Ngươi chớ trộm cướp."));
visuperobject.biblemap.put("b2_20_16", new visuperobject(2,20,16,"Ngươi chớ nói chứng dối cho kẻ lân cận mình."));
visuperobject.biblemap.put("b2_20_17", new visuperobject(2,20,17,"Ngươi chớ tham nhà kẻ lân cận ngươi, cũng đừng tham vợ người, hoặc tôi trai tớ gái, bò, lừa, hay là vật chi thuộc về kẻ lân cận ngươi."));
visuperobject.biblemap.put("b2_20_18", new visuperobject(2,20,18,"Vả, cả dân sự nghe sấm vang, tiếng kèn thổi, thấy chớp nhoáng, núi ra khói, thì run rẩy và đứng cách tận xa."));
visuperobject.biblemap.put("b2_20_19", new visuperobject(2,20,19,"Dân sự bèn nói cùng Môi-se rằng: Chính mình người hãy nói cùng chúng tôi thì chúng tôi sẽ nghe; nhưng cầu xin Ðức Chúa Trời chớ phán cùng, e chúng tôi phải chết chăng."));
visuperobject.biblemap.put("b2_20_20", new visuperobject(2,20,20,"Môi-se đáp rằng: Ðừng sợ chi, vì Ðức Chúa Trời giáng lâm đặng thử các ngươi, hầu cho sự kính sợ Ngài ở trước mặt các ngươi, cho các ngươi không phạm tội."));
visuperobject.biblemap.put("b2_20_21", new visuperobject(2,20,21,"Vậy, dân sự đứng cách tận xa, còn Môi-se đến gần đám mây đen kịt có Ðức Chúa Trời ngự ở trong."));
visuperobject.biblemap.put("b2_20_22", new visuperobject(2,20,22,"Ðức Giê-hô-va phán cùng Môi-se rằng: Ngươi hãy nói cho dân Y-sơ-ra-ên như vầy: Các ngươi đã thấy ta từ trên trời phán xuống cùng;"));
visuperobject.biblemap.put("b2_20_23", new visuperobject(2,20,23,"vậy, chớ đúc tượng thần bằng bạc hay bằng vàng mà sánh với ta."));
visuperobject.biblemap.put("b2_20_24", new visuperobject(2,20,24,"Ngươi hãy lập cho ta một bàn thờ bằng đất, trên đó phải dâng của lễ thiêu, cùng của lễ thù ân của ngươi, hoặc chiên hay bò. Phàm nơi nào có ghi nhớ danh ta, ta sẽ đến và ban phước cho ngươi tại đó."));
visuperobject.biblemap.put("b2_20_25", new visuperobject(2,20,25,"Nếu ngươi lập cho ta một bàn thờ bằng đá, chớ dùng đá chạm, vì nếu ngươi dùng đồ để chạm thì sẽ làm đá đó ra ô uế."));
visuperobject.biblemap.put("b2_20_26", new visuperobject(2,20,26,"Ngươi chớ đi từ bực thang lên bàn thờ ta, hầu cho sự lõa lồ ngươi không tố lộ."));
visuperobject.biblemap.put("b2_21_1", new visuperobject(2,21,1,"Ðây là luật lệ ngươi phải truyền cho dân sự:"));
visuperobject.biblemap.put("b2_21_2", new visuperobject(2,21,2,"Nếu ngươi mua một người đầy tớ Hê-bơ-rơ, nó sẽ hầu việc cho sáu năm; nhưng qua năm thứ bảy nó sẽ được thả ra, không phải thối tiền."));
visuperobject.biblemap.put("b2_21_3", new visuperobject(2,21,3,"Nếu nó vào ở một mình, thì sẽ ra một mình; nếu có vợ, thì sẽ ra với vợ."));
visuperobject.biblemap.put("b2_21_4", new visuperobject(2,21,4,"Nếu người chủ cưới vợ cho, và nếu vợ sanh được con trai hay gái, thì nó sẽ ra một mình, vợ và con thuộc về chủ."));
visuperobject.biblemap.put("b2_21_5", new visuperobject(2,21,5,"Nếu kẻ tôi mọi nói rằng: Tôi thương chủ, vợ và con tôi, không muốn ra được tự do,"));
visuperobject.biblemap.put("b2_21_6", new visuperobject(2,21,6,"thì người chủ sẽ dẫn nó đến trước mặt Ðức Chúa Trời, biểu lại gần cửa hay là cột, rồi lấy mũi dùi xỏ tai; nó sẽ hầu việc người chủ đó trọn đời."));
visuperobject.biblemap.put("b2_21_7", new visuperobject(2,21,7,"Nếu ai bán con gái mình làm con đòi, con gái đó không được ra như các đầy tớ kia."));
visuperobject.biblemap.put("b2_21_8", new visuperobject(2,21,8,"Nếu chủ trước đã ưng lấy con đòi làm hầu, rồi sau lại chẳng muốn, thì chủ phải biểu chuộc nàng lại, chớ không có phép gạt, rồi đem bán nàng cho kẻ ngoại bang."));
visuperobject.biblemap.put("b2_21_9", new visuperobject(2,21,9,"Nhưng nếu chủ định để cho con trai mình, thì phải xử với nàng theo phép như con gái mình vậy."));
visuperobject.biblemap.put("b2_21_10", new visuperobject(2,21,10,"Nếu chủ có lấy người khác, thì đối với người thứ nhất chẳng được phép giảm đồ ăn, đồ mặc, và tình nghĩa vợ chồng chút nào."));
visuperobject.biblemap.put("b2_21_11", new visuperobject(2,21,11,"Nếu người chủ không làm theo ba điều nầy, nàng được phép ra không, chẳng phải thối tiền lại."));
visuperobject.biblemap.put("b2_21_12", new visuperobject(2,21,12,"Kẻ nào đánh chết một người, sẽ bị xử tử."));
visuperobject.biblemap.put("b2_21_13", new visuperobject(2,21,13,"Nhược bằng kẻ đó chẳng phải mưu giết, nhưng vì Ðức Chúa Trời đã phú người bị giết vào tay kẻ đó, thì ta sẽ lập cho ngươi một chỗ đặng kẻ giết người ẩn thân."));
visuperobject.biblemap.put("b2_21_14", new visuperobject(2,21,14,"Còn nhược bằng kẻ nào dấy lên cùng người lân cận mà lập mưu giết người, thì dẫu rằng nó núp nơi bàn thờ ta, ngươi cũng bắt mà giết đi."));
visuperobject.biblemap.put("b2_21_15", new visuperobject(2,21,15,"Kẻ nào đánh cha hay mẹ mình, sẽ bị xử tử."));
visuperobject.biblemap.put("b2_21_16", new visuperobject(2,21,16,"Kẻ nào bắt người và bán, hoặc giữ lại trong tay mình, sẽ bị xử tử."));
visuperobject.biblemap.put("b2_21_17", new visuperobject(2,21,17,"Kẻ nào mắng cha hay mẹ mình, sẽ bị xử tử."));
visuperobject.biblemap.put("b2_21_18", new visuperobject(2,21,18,"Khi hai người đánh lộn nhau, người nầy đánh người kia bằng đá hay là đấm cú, không đến đỗi phải chết, nhưng phải nằm liệt giường,"));
visuperobject.biblemap.put("b2_21_19", new visuperobject(2,21,19,"nếu đứng dậy chống gậy đi ra ngoài được, người đánh đó sẽ được tha tội. Nhưng chỉ phải đền tiền thiệt hại trong mấy ngày nghỉ và nuôi cho đến khi lành mạnh."));
visuperobject.biblemap.put("b2_21_20", new visuperobject(2,21,20,"Khi người chủ lấy gậy đánh đầy tớ trai hay gái mình và chết liền theo tay, thì chắc phải bị phạt."));
visuperobject.biblemap.put("b2_21_21", new visuperobject(2,21,21,"Song nếu đầy tớ trai hay gái còn sống một hai ngày, người chủ khỏi tội, vì nó vốn là tài sản của chủ."));
visuperobject.biblemap.put("b2_21_22", new visuperobject(2,21,22,"Nếu người ta đánh nhau, đụng nhằm một người đàn bà có thai, làm cho phải sảo, nhưng chẳng bị sự hại chi khác, thì kẻ đánh nhằm đó phải bồi thường theo lời chồng người sẽ định, và trả tiền trước mặt quan án."));
visuperobject.biblemap.put("b2_21_23", new visuperobject(2,21,23,"Còn nếu có sự hại chi, thì ngươi sẽ lấy mạng thường mạng,"));
visuperobject.biblemap.put("b2_21_24", new visuperobject(2,21,24,"lấy mắt thường mắt, lấy răng thường răng, lấy tay thường tay, lấy chân thường chân,"));
visuperobject.biblemap.put("b2_21_25", new visuperobject(2,21,25,"lấy phỏng thường phỏng, lấy bầm thường bầm, lấy thương thường thương."));
visuperobject.biblemap.put("b2_21_26", new visuperobject(2,21,26,"Nếu ai đánh nhằm con mắt đầy tớ trai hay gái mình, làm cho mất đi, thì hãy tha nó ra tự do, vì cớ mất con mắt."));
visuperobject.biblemap.put("b2_21_27", new visuperobject(2,21,27,"Nếu ai làm rụng một răng của đầy tớ trai hay gái mình, thì hãy tha nó ra tự do, vì cớ mất một răng."));
visuperobject.biblemap.put("b2_21_28", new visuperobject(2,21,28,"Ví có một con bò báng nhằm một người đàn ông hay đàn bà phải chết đi, con bò sẽ bị ném đá chết, người ta không nên ăn thịt nó; còn người chủ bò sẽ được vô tội."));
visuperobject.biblemap.put("b2_21_29", new visuperobject(2,21,29,"Nhưng ngộ từ trước con bò có tật hay báng và chủ đã bị mắng vốn mà không cầm giữ, nếu bò nầy còn giết một người đàn ông hay đàn bà, thì nó sẽ bị ném đá, và chủ sẽ bị xử tử nữa."));
visuperobject.biblemap.put("b2_21_30", new visuperobject(2,21,30,"Nhược bằng người ta định giá cho chủ bò chuộc mạng, chủ phải chuộc mạng mình y như giá đã định."));
visuperobject.biblemap.put("b2_21_31", new visuperobject(2,21,31,"Nếu bò báng nhằm một đứa con trai hay là con gái, người ta cũng sẽ chiếu theo luật nầy."));
visuperobject.biblemap.put("b2_21_32", new visuperobject(2,21,32,"Con bò báng nhằm một đứa đầy tớ trai hay gái, chủ bò phải trả ba chục siếc lơ bạc cho chủ nó; rồi bò sẽ bị ném đá chết."));
visuperobject.biblemap.put("b2_21_33", new visuperobject(2,21,33,"Nếu ai mở miệng hầm hay là đào hầm mà chẳng đậy lại, và nếu có bò hay là lừa té xuống đó,"));
visuperobject.biblemap.put("b2_21_34", new visuperobject(2,21,34,"chủ hầm sẽ bồi thường giá tiền cho chủ của súc vật, nhưng súc vật bị giết đó sẽ về phần mình."));
visuperobject.biblemap.put("b2_21_35", new visuperobject(2,21,35,"Bằng bò của ai báng nhằm bò của kẻ lân cận mình phải chết đi, hai người hãy bán bò sống đó, chia tiền và chia luôn con bò chết nữa."));
visuperobject.biblemap.put("b2_21_36", new visuperobject(2,21,36,"Nếu người chủ đã tỏ tường trước rằng bò mình có tật hay báng, mà không lo cầm giữ, chủ phải lấy bò thường bò, nhưng bò chết sẽ về phần người."));
visuperobject.biblemap.put("b2_22_1", new visuperobject(2,22,1,"Ví ai bắt trộm bò hay chiên, rồi giết hoặc bán đi, thì phải thường năm con bò cho một con, và bốn con chiên cho một con."));
visuperobject.biblemap.put("b2_22_2", new visuperobject(2,22,2,"Nếu kẻ trộm đang cạy cửa mà bị bắt và đánh chết đi, thì kẻ đánh chết chẳng phải tội sát nhân."));
visuperobject.biblemap.put("b2_22_3", new visuperobject(2,22,3,"Song nếu đánh chết trong khi mặt trời đã mọc rồi, thì bị tội sát nhân. Kẻ trộm phải bồi thường; bằng chẳng có chi, thì sẽ bị bán vì tội trộm cắp mình."));
visuperobject.biblemap.put("b2_22_4", new visuperobject(2,22,4,"Nếu vật mà kẻ trộm đã lấy, hoặc bò, lừa hay chiên, hãy còn thấy sống trong tay, thì kẻ trộm phải bồi thường gấp hai."));
visuperobject.biblemap.put("b2_22_5", new visuperobject(2,22,5,"Ngộ ai làm hư hại trong ruộng hay là vườn nho, thả súc vật mình vào ăn trong ruộng người khác, thì ai đó phải lấy hoa lợi tốt nhất của ruộng hay là vườn nho mình mà thường bồi."));
visuperobject.biblemap.put("b2_22_6", new visuperobject(2,22,6,"Nếu lửa nổi đốt bụi gai và cháy lan qua lúa đã bó, mùa màng, hay là đồng ruộng, thì kẻ nổi lửa đó phải bồi thường trọn mọi vật đã bị cháy."));
visuperobject.biblemap.put("b2_22_7", new visuperobject(2,22,7,"Khi ai giao tiền bạc bay đồ vật cho kẻ lân cận mình giữ, rủi bị trộm cắp tại nhà kẻ lân cận đó, nếu tên trộm đã bị bắt được, thì nó phải thường gấp hai."));
visuperobject.biblemap.put("b2_22_8", new visuperobject(2,22,8,"Nếu kẻ trộm không bị bắt được, thì chủ nhà cho gởi phải dẫn đến trước mặt Ðức Chúa Trời, đặng thề rằng mình chẳng hề đặt tay vào tài vật của kẻ lân cận mình."));
visuperobject.biblemap.put("b2_22_9", new visuperobject(2,22,9,"Trong mọi việc gian lận nào, hoặc về một con bò, một con lừa, một con chiên, áo xống hay là một vật chi bị mất, mà có người nói rằng: quả thật là đồ đó, thì phải đem duyên cớ hai đàng đến trước mặt Ðức Chúa Trời; kẻ nào bị Ngài xử phạt phải thường bồi cho kẻ lân cận mình gấp hai."));
visuperobject.biblemap.put("b2_22_10", new visuperobject(2,22,10,"Nhược bằng người nào giao lừa, bò, chiên, hoặc súc vật nào khác cho kẻ lân cận mình giữ và bị chết, gãy một giò hay là bị đuổi đi, không ai thấy,"));
visuperobject.biblemap.put("b2_22_11", new visuperobject(2,22,11,"thì hai đàng phải lấy danh Ðức Giê-hô-va mà thề, hầu cho biết rằng người giữ súc vật có đặt tay trên tài vật của kẻ lân cận mình chăng. Người chủ con vật phải nhận lời thề, và người kia chẳng bồi thường."));
visuperobject.biblemap.put("b2_22_12", new visuperobject(2,22,12,"Còn nếu con vật bị bắt trộm, thì người lãnh giữ phải thường bồi cho chủ nó."));
visuperobject.biblemap.put("b2_22_13", new visuperobject(2,22,13,"Nếu con bật bị thú rừng xé chết, người lãnh giữ phải đem nó ra làm chứng, sẽ không phải bồi thường vật bị xé đó."));
visuperobject.biblemap.put("b2_22_14", new visuperobject(2,22,14,"Vì ai mượn người lân cận mình một con vật mà nó gãy một giò, hoặc bị chết, không có mặt chủ, thì ai đó phải bồi thường."));
visuperobject.biblemap.put("b2_22_15", new visuperobject(2,22,15,"Nếu chủ có mặt tại đó, thì không phải bồi thường. Nếu con vật đã cho mướn, thì giá muớn thế cho tiền bồi thường."));
visuperobject.biblemap.put("b2_22_16", new visuperobject(2,22,16,"Nếu kẻ nào hòa dụ và nằm với một người gái đồng trinh chưa hứa giá, thì kẻ đó phải nộp tiền sính và cưới nàng làm vợ."));
visuperobject.biblemap.put("b2_22_17", new visuperobject(2,22,17,"Nhược bằng cha nàng quyết từ chối không gả, thì kẻ đó phải nộp tiền bằng số tiền sính của người gái đồng trinh."));
visuperobject.biblemap.put("b2_22_18", new visuperobject(2,22,18,"Ngươi chớ để các đồng cốt sống."));
visuperobject.biblemap.put("b2_22_19", new visuperobject(2,22,19,"Kẻ nào nằm cùng một con vật sẽ bị xử tử."));
visuperobject.biblemap.put("b2_22_20", new visuperobject(2,22,20,"Kẻ nào tế các thần khác hơn một mình Ðức Giê-hô-va sẽ bị diệt."));
visuperobject.biblemap.put("b2_22_21", new visuperobject(2,22,21,"Ngươi chớ nên bạc đãi khách ngoại bang, và cũng chẳng nên hà hiếp họ, vì các ngươi đã làm khách kiều ngụ tại xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_22_22", new visuperobject(2,22,22,"Các ngươi chớ ức hiếp một người góa bụa hay là một kẻ mồ côi nào."));
visuperobject.biblemap.put("b2_22_23", new visuperobject(2,22,23,"Nếu ức hiếp họ, và họ kêu van ta, chắc ta sẽ nghe tiếng kêu của họ;"));
visuperobject.biblemap.put("b2_22_24", new visuperobject(2,22,24,"cơn nóng giận ta phừng lên, sẽ lấy gươm giết các ngươi, thì vợ các ngươi sẽ trở nên góa bụa, và con các ngươi sẽ mồ côi."));
visuperobject.biblemap.put("b2_22_25", new visuperobject(2,22,25,"Trong dân ta có kẻ nghèo nàn ở cùng ngươi, nếu ngươi cho người mượn tiền, chớ xử với họ như người cho vay, và cũng chẳng nên bắt họ chịu lời."));
visuperobject.biblemap.put("b2_22_26", new visuperobject(2,22,26,"Nếu ngươi cầm áo xống của kẻ lân cận mình làm của tin, thì phải trả cho họ trước khi mặt trời lặn;"));
visuperobject.biblemap.put("b2_22_27", new visuperobject(2,22,27,"vì là đồ chỉ có che thân, là áo xống che đậy da mình; người lấy chi mà ngủ? Nếu người đến kêu van ta, tất ta sẽ nghe lời người, vì ta là Ðấng hay thương xót."));
visuperobject.biblemap.put("b2_22_28", new visuperobject(2,22,28,"Ngươi chớ nên nói lộng ngôn cùng Ðức Chúa Trời, và cũng đừng rủa sả vua chúa của dân sự ngươi."));
visuperobject.biblemap.put("b2_22_29", new visuperobject(2,22,29,"Ngươi chớ trễ nải mà dâng cho ta những hoa quả đầu mùa của ngươi chứa trong vựa và rượu ép chảy nơi bàn ép. Ngươi cũng phải dâng cho ta con trai đầu lòng ngươi."));
visuperobject.biblemap.put("b2_22_30", new visuperobject(2,22,30,"Về phần chiên và bò ngươi cũng hãy làm như vậy; trong bảy ngày đầu, con đầu lòng ở cùng mẹ nó, qua ngày thứ tám ngươi hãy dâng nó cho ta."));
visuperobject.biblemap.put("b2_22_31", new visuperobject(2,22,31,"Các ngươi sẽ làm người thánh của ta, chớ nên ăn thịt chi bị thú rừng xé ở ngoài đồng; hãy liệng cho chó ăn."));
visuperobject.biblemap.put("b2_23_1", new visuperobject(2,23,1,"Ngươi chớ đồn huyễn; chớ hùa cùng kẻ hung ác đặng làm chứng dối."));
visuperobject.biblemap.put("b2_23_2", new visuperobject(2,23,2,"Ngươi chớ hùa đảng đông đặng làm quấy; khi ngươi làm chứng trong việc kiện cáo, chớ nên trở theo bên phe đông mà phạm sự công bình."));
visuperobject.biblemap.put("b2_23_3", new visuperobject(2,23,3,"Ngươi chớ tư vị kẻ nghèo trong việc kiện cáo."));
visuperobject.biblemap.put("b2_23_4", new visuperobject(2,23,4,"Nhược bằng ngươi gặp bò hay lừa của kẻ thù nghịch mình đi lạc thì buộc phải đem về cho họ."));
visuperobject.biblemap.put("b2_23_5", new visuperobject(2,23,5,"Ngộ ngươi thấy lừa của kẻ ghét mình bị chở nặng phải quị, chớ khá bỏ ngơ, phải cứu nó cho được nhẹ."));
visuperobject.biblemap.put("b2_23_6", new visuperobject(2,23,6,"Trong cơn kiện cáo, ngươi chớ phạm quyền lợi của người nghèo ở giữa vòng ngươi."));
visuperobject.biblemap.put("b2_23_7", new visuperobject(2,23,7,"Ngươi phải tránh xa các lời giả dối; chớ giết kẻ vô tội và kẻ công bình, vì ta chẳng hề tha kẻ gian ác đâu."));
visuperobject.biblemap.put("b2_23_8", new visuperobject(2,23,8,"Ngươi chớ nhậm của hối lộ, vì của hối lộ làm mờ mắt người thượng trí, và làm mất duyên do của kẻ công bình."));
visuperobject.biblemap.put("b2_23_9", new visuperobject(2,23,9,"Ngươi chớ hiếp đáp người ngoại bang, vì các ngươi đã kiều ngụ tại xứ Ê-díp-tô, chính các ngươi hiểu biết lòng khách ngoại bang là thế nào."));
visuperobject.biblemap.put("b2_23_10", new visuperobject(2,23,10,"Trải sáu năm ngươi hãy gieo trong đất, thâu lấy hoa lợi,"));
visuperobject.biblemap.put("b2_23_11", new visuperobject(2,23,11,"nhưng qua năm thứ bảy hãy để đất hoang cho nghỉ; hầu cho kẻ nghèo khổ trong dân sự ngươi được nhờ lấy hoa quả đất đó, còn dư lại bao nhiêu, thú vật ngoài đồng ăn lấy. Cũng hãy làm như vậy về vườn nho và cây ô-li-ve."));
visuperobject.biblemap.put("b2_23_12", new visuperobject(2,23,12,"Luôn trong sáu ngày ngươi hãy làm công việc mình, nhưng qua ngày thứ bảy hãy nghỉ, hầu cho bò và lừa ngươi được nghỉ; cùng con trai của đầy tớ gái và người ngoại bang được dưỡng sức lại."));
visuperobject.biblemap.put("b2_23_13", new visuperobject(2,23,13,"Ngươi khá giữ hết mọi lời ta phán cùng ngươi. Chớ xưng đến danh các thần khác; chớ cho nghe danh đó ở nơi miệng ngươi ra."));
visuperobject.biblemap.put("b2_23_14", new visuperobject(2,23,14,"Mỗi năm ba kỳ ngươi sẽ giữ lễ kính ta."));
visuperobject.biblemap.put("b2_23_15", new visuperobject(2,23,15,"Ngươi hãy giữ lễ bánh không men; phải ăn bánh không men trong bảy ngày về mùa tháng lúa trỗ, như lời ta đã phán dặn; vì trong tháng đó ngươi ra khỏi xứ Ê-díp-tô. Lại chẳng ai nên tay không đến trước mặt ta."));
visuperobject.biblemap.put("b2_23_16", new visuperobject(2,23,16,"Ngươi hãy giữ lễ mùa màng, tức là mùa hoa quả đầu tiên của công lao ngươi về các giống ngươi đã gieo ngoài đồng; và giữ lễ mùa gặt về lúc cuối năm, khi các ngươi đã hái hoa quả của công lao mình ngoài đồng rồi."));
visuperobject.biblemap.put("b2_23_17", new visuperobject(2,23,17,"Mỗi năm ba kỳ các người nam phải đến trước mặt Chúa, tức là Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_23_18", new visuperobject(2,23,18,"Ngươi chớ dâng huyết của con sinh tế ta chung với bánh có pha men; còn mỡ của nó dâng nhằm ngày lễ kính ta, chớ để trọn đêm đến sáng mai."));
visuperobject.biblemap.put("b2_23_19", new visuperobject(2,23,19,"Những hoa quả đầu mùa của đất, ngươi phải đem đến đền thờ Giê-hô-va Ðức Chúa Trời ngươi. Ngươi chớ nên nấu dê con trong sữa mẹ nó."));
visuperobject.biblemap.put("b2_23_20", new visuperobject(2,23,20,"Ðây nầy, ta sai một thiên sứ đi trước mặt ngươi, đặng phù hộ trong lúc đi đường, và đưa ngươi vào nơi ta đã dự bị."));
visuperobject.biblemap.put("b2_23_21", new visuperobject(2,23,21,"Trước mặt người, ngươi khá giữ mình, phải nghe theo lời, chớ nên phản nghịch; người sẽ chẳng tha tội ngươi đâu, vì danh ta ngự trong mình người."));
visuperobject.biblemap.put("b2_23_22", new visuperobject(2,23,22,"Nhưng nếu ngươi chăm chỉ nghe lời người, cùng làm theo mọi lời ta sẽ phán, ta sẽ thù nghịch cùng kẻ thù nghịch ngươi, và đối địch với kẻ đối địch ngươi."));
visuperobject.biblemap.put("b2_23_23", new visuperobject(2,23,23,"Vì thiên sứ ta sẽ đi trước mắt, đưa ngươi vào xứ của dân A-mô-rít, dân Hê-tít, dân Phê-rê-sít, dân Ca-na-an, dân Hê-vít, và dân Giê-bu-sít, rồi ta sẽ diệt chúng nó."));
visuperobject.biblemap.put("b2_23_24", new visuperobject(2,23,24,"Ngươi chớ quì lạy và hầu việc các thần chúng nó; chớ bắt chước công việc họ, nhưng phải diệt hết các thần đó và đạp nát các pho tượng họ đi."));
visuperobject.biblemap.put("b2_23_25", new visuperobject(2,23,25,"Ngươi hãy hầu việc Giê-hô-va Ðức Chúa Trời ngươi; Ngài sẽ ban ơn cho vật ăn uống của ngươi và tiêu trừ các bịnh hoạn giữa vòng ngươi."));
visuperobject.biblemap.put("b2_23_26", new visuperobject(2,23,26,"Trong xứ ngươi sẽ chẳng có đàn bà sảo thai hay là đàn bà son sẻ. Ta sẽ cho ngươi được trường thọ."));
visuperobject.biblemap.put("b2_23_27", new visuperobject(2,23,27,"Ta sẽ sai sự kinh khiếp ta đi trước, hễ ngươi đến dân nào, ta sẽ làm cho dân ấy vỡ chạy lạc đường, và cho kẻ thù nghịch ngươi xây lưng trước mặt ngươi."));
visuperobject.biblemap.put("b2_23_28", new visuperobject(2,23,28,"Ta sẽ sai ong lỗ đi trước đánh đuổi dân Hê-vít, dân Ca-na-an, dân Hê-tít khỏi trước mặt ngươi."));
visuperobject.biblemap.put("b2_23_29", new visuperobject(2,23,29,"Ta sẽ chẳng đuổi chúng nó đi hết trong một năm đâu, e khi xứ sẽ trở nên đồng vắng, và loài thú rừng sanh sản ra đông, hại ngươi chăng;"));
visuperobject.biblemap.put("b2_23_30", new visuperobject(2,23,30,"nhưng ta sẽ đuổi chúng nó lần lần khỏi trước mặt ngươi, cho đến chừng nào số ngươi thêm nhiều và có thể cầm đất đó làm sản nghiệp."));
visuperobject.biblemap.put("b2_23_31", new visuperobject(2,23,31,"Ta sẽ phân định bờ cõi ngươi từ Biển đỏ chí biển Phi-li-tin, từ đồng vắng đến sông cái; vì ta sẽ giao phú dân đó vào tay ngươi, và ngươi sẽ đuổi chúng nó khỏi mặt mình."));
visuperobject.biblemap.put("b2_23_32", new visuperobject(2,23,32,"Ngươi đừng kết giao ước cùng chúng nó, hoặc cùng các thần của chúng nó."));
visuperobject.biblemap.put("b2_23_33", new visuperobject(2,23,33,"Họ sẽ chẳng ở trong xứ ngươi đâu, e khi chúng nó xui cho ngươi phạm tội cùng ta mà hầu việc các thần họ chăng; điều đó chắc sẽ thành một cái bẫy cho ngươi vậy."));
visuperobject.biblemap.put("b2_24_1", new visuperobject(2,24,1,"Ðức Chúa Trời phán cùng Môi-se rằng: Ngươi và A-rôn, Na-đáp, và A-bi-hu cùng bảy mươi trưởng lão Y-sơ-ra-ên hãy lên cùng Ðức Giê-hô-va ở đằng xa mà lạy."));
visuperobject.biblemap.put("b2_24_2", new visuperobject(2,24,2,"Chỉ một mình Môi-se sẽ đến gần Ðức Giê-hô-va mà thôi, còn họ không đến gần, và dân sự cùng không lên cùng người."));
visuperobject.biblemap.put("b2_24_3", new visuperobject(2,24,3,"Môi-se bèn đến thuật lại cho dân sự mọi lời phán của Ðức Giê-hô-va và các luật lệ, thì chúng đồng thinh đáp rằng: Chúng tôi sẽ làm mọi lời Ðức Giê-hô-va phán dạy."));
visuperobject.biblemap.put("b2_24_4", new visuperobject(2,24,4,"Môi-se chép hết mọi lời của Ðức Giê-hô-va; rồi dậy sớm, xây một bàn thờ nơi chân núi, dựng mười hai cây trụ chỉ về mười hai chi phái Y-sơ-ra-ên."));
visuperobject.biblemap.put("b2_24_5", new visuperobject(2,24,5,"Người sai kẻ trai trẻ của dân Y-sơ-ra-ên đi dâng của lễ thiêu và của lễ thù ân cho Ðức Giê-hô-va bằng con bò tơ."));
visuperobject.biblemap.put("b2_24_6", new visuperobject(2,24,6,"Môi-se lấy phân nửa huyết đựng trong các chậu, còn phân nửa thì rưới trên bàn thờ."));
visuperobject.biblemap.put("b2_24_7", new visuperobject(2,24,7,"Ðoạn, người cầm quyển sách giao ước đọc cho dân sự nghe, thì dân sự nói rằng: Chúng tôi sẽ làm và vâng theo mọi lời Ðức Giê-hô-va phán chỉ."));
visuperobject.biblemap.put("b2_24_8", new visuperobject(2,24,8,"Môi-se bèn lấy huyết rưới trên mình dân sự mà nói rằng: Ðây là huyết giao ước của Ðức Giê-hô-va đã lập cùng các ngươi y theo mọi lời nầy."));
visuperobject.biblemap.put("b2_24_9", new visuperobject(2,24,9,"Ðoạn, Môi-se và A-rôn, Na-đáp và A-bi-hu cùng bảy mươi trưởng lão dân Y-sơ-ra-ên đều lên núi,"));
visuperobject.biblemap.put("b2_24_10", new visuperobject(2,24,10,"ngó thấy Ðức Chúa Trời của Y-sơ-ra-ên, dưới chân Ngài có một vật giống bích ngọc trong ngần, khác nào như sắc trời thanh-quang."));
visuperobject.biblemap.put("b2_24_11", new visuperobject(2,24,11,"Ngài chẳng tra tay vào những người tôn trọng trong vòng dân Y-sơ-ra-ên; nhưng họ ngó thấy Ðức Chúa Trời, thì ăn và uống."));
visuperobject.biblemap.put("b2_24_12", new visuperobject(2,24,12,"Ðức Giê-hô-va phán cùng Môi-se rằng: Hãy lên núi, đến cùng ta và ở lại đó; ta sẽ ban cho ngươi bảng đá, luật pháp và các điều răn của ta đã chép đặng dạy dân sự."));
visuperobject.biblemap.put("b2_24_13", new visuperobject(2,24,13,"Môi-se bèn chờ dậy cùng Giô-suê, kẻ hầu mình, rồi Môi-se đi lên núi Ðức Chúa Trời."));
visuperobject.biblemap.put("b2_24_14", new visuperobject(2,24,14,"Người bèn nói cùng các trưởng lão rằng: Hãy đợi chúng tôi tại đây cho đến chừng nào trở về cùng các ngươi. Nầy, A-rôn và Hu-rơ ở lại cùng các ngươi, ai có việc chi hãy hỏi hai người đó."));
visuperobject.biblemap.put("b2_24_15", new visuperobject(2,24,15,"Vậy, Môi-se lên núi, mây che phủ núi."));
visuperobject.biblemap.put("b2_24_16", new visuperobject(2,24,16,"Sự vinh quang của Ðức Giê-hô-va ở tại núi Si-na -i; trong sáu ngày mây phủ núi, qua ngày thứ bảy Ðức Giê-hô-va ngự trong đám mây gọi Môi-se."));
visuperobject.biblemap.put("b2_24_17", new visuperobject(2,24,17,"Cảnh trạng của sự vinh quang Ðức Giê-hô-va nơi đỉnh núi, trước mặt dân Y-sơ-ra-ên, khác nào như đám lửa hừng."));
visuperobject.biblemap.put("b2_24_18", new visuperobject(2,24,18,"Môi-se vào giữa đám mây, lên núi, ở tại đó trong bốn mươi ngày và bốn mươi đêm."));
visuperobject.biblemap.put("b2_25_1", new visuperobject(2,25,1,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b2_25_2", new visuperobject(2,25,2,"Hãy nói cùng dân Y-sơ-ra-ên đặng họ dâng lễ vật cho ta; các ngươi hãy nhận lấy lễ vật của mọi người có lòng thành dâng cho."));
visuperobject.biblemap.put("b2_25_3", new visuperobject(2,25,3,"Nầy là lễ vật các ngươi sẽ nhận lấy của họ: vàng, bạc, và đồng;"));
visuperobject.biblemap.put("b2_25_4", new visuperobject(2,25,4,"chỉ tím, đỏ điều, đỏ sặm, vải gai mịn, lông dê, da chiên đực nhuộm đỏ,"));
visuperobject.biblemap.put("b2_25_5", new visuperobject(2,25,5,"da cá nược, cây si-tim,"));
visuperobject.biblemap.put("b2_25_6", new visuperobject(2,25,6,"dầu thắp, hương liệu đặng làm dầu xức và hương,"));
visuperobject.biblemap.put("b2_25_7", new visuperobject(2,25,7,"bích ngọc cùng các thứ ngọc khác để gắn vào ê-phót và bảng đeo ngực."));
visuperobject.biblemap.put("b2_25_8", new visuperobject(2,25,8,"Họ sẽ làm cho ta một đền thánh và ta sẽ ở giữa họ."));
visuperobject.biblemap.put("b2_25_9", new visuperobject(2,25,9,"Hãy làm điều đó y như kiểu đền tạm cùng kiểu các đồ dùng mà ta sẽ chỉ cho ngươi."));
visuperobject.biblemap.put("b2_25_10", new visuperobject(2,25,10,"Vậy, chúng hãy đóng một cái hòm bằng cây si-tim; bề dài hai thước rưỡi, bề ngang một thước rưỡi, và bề cao cũng một thước rưỡi,"));
visuperobject.biblemap.put("b2_25_11", new visuperobject(2,25,11,"lấy vàng ròng bọc trong, bọc ngoài, và chạy đường viền chung quanh hòm bằng vàng."));
visuperobject.biblemap.put("b2_25_12", new visuperobject(2,25,12,"Ngươi cũng hãy đúc bốn khoen bằng vàng để tại bốn góc hòm: hai cái bên hông nầy, hai cái bên hông kia,"));
visuperobject.biblemap.put("b2_25_13", new visuperobject(2,25,13,"cùng làm hai cây đòn bằng cây si-tim, bọc vàng;"));
visuperobject.biblemap.put("b2_25_14", new visuperobject(2,25,14,"rồi lòn đòn vào khoen hai bên hông hòm, để dùng đòn khiêng hòm."));
visuperobject.biblemap.put("b2_25_15", new visuperobject(2,25,15,"Ðòn sẽ ở trong khoen luôn, không nên rút ra."));
visuperobject.biblemap.put("b2_25_16", new visuperobject(2,25,16,"Ngươi hãy cất vào trong hòm bảng chứng mà ta sẽ ban cho."));
visuperobject.biblemap.put("b2_25_17", new visuperobject(2,25,17,"Ngươi cũng hãy làm một cái nắp thi ân bằng vàng ròng, bề dài hai thước rưỡi, bề ngang một thước rưỡi."));
visuperobject.biblemap.put("b2_25_18", new visuperobject(2,25,18,"Lại làm hai tượng chê-ru-bin bằng vàng giát mỏng, để hai đầu nắp thi ân,"));
visuperobject.biblemap.put("b2_25_19", new visuperobject(2,25,19,"ló ra ngoài, một tượng ở đầu nầy và một tượng ở đầu kia."));
visuperobject.biblemap.put("b2_25_20", new visuperobject(2,25,20,"Hai chê-ru-bin sẽ sè cánh ra, che trên nắp thi ân, đối diện nhau và xây mặt vào nắp thi ân."));
visuperobject.biblemap.put("b2_25_21", new visuperobject(2,25,21,"Ngươi hãy để nắp thi ân trên hòm, rồi để vào trong hòm bảng chứng mà ta sẽ ban cho."));
visuperobject.biblemap.put("b2_25_22", new visuperobject(2,25,22,"Ta sẽ gặp ngươi tại đó, và ở trên nắp thi ân, giữa hai tượng chê-ru-bin, trên hòm bảng chứng, ta sẽ truyền cho ngươi các mạng lịnh về dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b2_25_23", new visuperobject(2,25,23,"Ngươi cũng hãy đóng một cái bàn bằng cây si-tim; bề dài hai thước, bề ngang một thước, và bề cao một thước rưỡi,"));
visuperobject.biblemap.put("b2_25_24", new visuperobject(2,25,24,"bọc bằng vàng ròng, và chạy một đường viền chung quanh;"));
visuperobject.biblemap.put("b2_25_25", new visuperobject(2,25,25,"rồi lên be cho tứ vi bàn, cao chừng bốn ngón tay và chạy cho be một đường viền vàng."));
visuperobject.biblemap.put("b2_25_26", new visuperobject(2,25,26,"Lại đúc bốn cái khoen vàng, tra vào bốn góc nơi chân bàn."));
visuperobject.biblemap.put("b2_25_27", new visuperobject(2,25,27,"Khoen sẽ ở gần be, để xỏ đòn khiêng bàn."));
visuperobject.biblemap.put("b2_25_28", new visuperobject(2,25,28,"Ngươi hãy chuốt đòn bằng cây si-tim, bọc vàng, rồi người ta sẽ dùng khiêng bàn đó."));
visuperobject.biblemap.put("b2_25_29", new visuperobject(2,25,29,"Lại hãy lấy vàng ròng mà làm dĩa, chén, chậu, và ly đặng dùng làm lễ quán."));
visuperobject.biblemap.put("b2_25_30", new visuperobject(2,25,30,"Trên bàn ngươi sẽ để bánh trần thiết cho có luôn luôn trước mặt ta."));
visuperobject.biblemap.put("b2_25_31", new visuperobject(2,25,31,"Ngươi cũng hãy làm chân đèn bằng vàng ròng. Cái chân, cái thân, cái đài, cái bầu cùng cái hoa của đèn đều làm bằng vàng đánh giát."));
visuperobject.biblemap.put("b2_25_32", new visuperobject(2,25,32,"Hai bên thân đèn sẽ có sáu nhánh nứt ra, hạ nhánh ở bên nầy và ba nhánh ở bên kia."));
visuperobject.biblemap.put("b2_25_33", new visuperobject(2,25,33,"Trong sáu nhánh nứt ra trên chân đèn, mỗi nhánh đều sẽ có ba cái đài hình như hột hạnh nhân cùng bầu và hoa."));
visuperobject.biblemap.put("b2_25_34", new visuperobject(2,25,34,"Trên thân chân đèn, lại cũng sẽ có bốn cái đài hình hột hạnh nhân, bầu và hoa."));
visuperobject.biblemap.put("b2_25_35", new visuperobject(2,25,35,"Trong sáu nhánh từ thân chân đèn nứt ra, hễ cứ mỗi hai nhánh thì dưới có một cái bầu."));
visuperobject.biblemap.put("b2_25_36", new visuperobject(2,25,36,"Bầu cùng nhánh của chân đèn đều bằng vàng ròng nguyên miếng đánh giát."));
visuperobject.biblemap.put("b2_25_37", new visuperobject(2,25,37,"Ngươi cũng hãy làm bảy cái thếp đèn, đặng hễ khi thắp thì chiếu trước chân đèn."));
visuperobject.biblemap.put("b2_25_38", new visuperobject(2,25,38,"Kéo bắt tim cùng đồ đựng tàn đèn cũng sẽ bằng vàng ròng."));
visuperobject.biblemap.put("b2_25_39", new visuperobject(2,25,39,"Người ta sẽ dùng một ta lâng vàng ròng làm chân đèn nầy và các đồ phụ tùng của chân đèn."));
visuperobject.biblemap.put("b2_25_40", new visuperobject(2,25,40,"Vậy, ngươi hãy xem, cứ làm y như kiểu đã chỉ cho trên núi."));
visuperobject.biblemap.put("b2_26_1", new visuperobject(2,26,1,"Ngươi hãy dùng mười bức màn mà làm đền tạm, màn dệt bằng vải gai mịn, chỉ tím, đỏ điều và đỏ sặm, có thêu các hình chê-ru-bin cực xảo."));
visuperobject.biblemap.put("b2_26_2", new visuperobject(2,26,2,"Mỗi bức màn bề dài hai mươi tám thước, bề rộng bốn thước. Các bức đều đồng cỡ với nhau."));
visuperobject.biblemap.put("b2_26_3", new visuperobject(2,26,3,"Mỗi năm bức màn sẽ kết dính lại nhau."));
visuperobject.biblemap.put("b2_26_4", new visuperobject(2,26,4,"Ngươi hãy thắt vòng chỉ tím nơi triêng của bức màn thứ nhất ở đầu bức nguyên; và cũng làm như vậy cho đầu chót của bức màn cuối trong bức nguyên thứ nhì."));
visuperobject.biblemap.put("b2_26_5", new visuperobject(2,26,5,"Lại làm năm chục cái vòng cho bức màn thứ nhất, và năm chục cái vòng nơi đầu triêng bức nguyên thứ nhì; các vòng hai bên sẽ đối nhau."));
visuperobject.biblemap.put("b2_26_6", new visuperobject(2,26,6,"Cũng hãy làm năm mươi cái móc bằng vàng, rồi móc bức nguyên nầy qua bức nguyên kia, hầu cho đền tạm kết lại thành một."));
visuperobject.biblemap.put("b2_26_7", new visuperobject(2,26,7,"Ngươi cũng hãy kết mười một bức màn bằng lông dê, để dùng làm bong che trên đền tạm."));
visuperobject.biblemap.put("b2_26_8", new visuperobject(2,26,8,"Bề dài mỗi bức màn ba chục thước, bề rộng bốn thước, mười một bức đồng một cỡ với nhau."));
visuperobject.biblemap.put("b2_26_9", new visuperobject(2,26,9,"Ngươi hãy kết năm bức màn nầy riêng ra, và sáu bức màn kia riêng ra; nhưng hãy gấp đôi bức màn thứ sáu ở trước đền tạm."));
visuperobject.biblemap.put("b2_26_10", new visuperobject(2,26,10,"Lại thắt năm chục cái vòng nơi triêng màn thứ nhất, ném về cuối của bức nguyên thứ nhất, và năm chục cái vòng nơi triêng màn của bức nguyên thứ nhì;"));
visuperobject.biblemap.put("b2_26_11", new visuperobject(2,26,11,"cũng làm năm chục cái móc bằng đồng, móc vào vòng, rồi hiệp cái bong lại, để nó thành ra một."));
visuperobject.biblemap.put("b2_26_12", new visuperobject(2,26,12,"Nhưng còn dư, tức là phân nửa của bức màn dư trong bức bong nguyên, thì sẽ xủ xuống phía sau đền tạm;"));
visuperobject.biblemap.put("b2_26_13", new visuperobject(2,26,13,"phần bề dài dư trong các bức màn của bong, bên nầy một thước, bên kia một thước, thì sẽ xủ xuống hai bên hông đền tạm đặng che vậy."));
visuperobject.biblemap.put("b2_26_14", new visuperobject(2,26,14,"Ngươi hãy làm một tấm lá phủ bằng da chiên đực nhuộm đỏ để trên bong, và kết một tấm lá phủ bằng da cá nược đắp lên trên nữa."));
visuperobject.biblemap.put("b2_26_15", new visuperobject(2,26,15,"Ngươi cũng hãy làm những ván cho vách đền tạm bằng cây si-tim."));
visuperobject.biblemap.put("b2_26_16", new visuperobject(2,26,16,"Mỗi tấm trướng mười thước, hoành một thước rưỡi,"));
visuperobject.biblemap.put("b2_26_17", new visuperobject(2,26,17,"có hai cái mộng liền nhau; hết thảy các tấm ván vách đền tạm ngươi cũng sẽ làm như vậy."));
visuperobject.biblemap.put("b2_26_18", new visuperobject(2,26,18,"Về phía nam đền tạm, ngươi hãy làm hai mươi tấm ván;"));
visuperobject.biblemap.put("b2_26_19", new visuperobject(2,26,19,"dưới hai mươi tấm ván hãy đặt bốn mươi lỗ mộng bằng bạc; cứ hai lỗ mộng ở dưới, để chịu hai cái mộng của mỗi tấm ván."));
visuperobject.biblemap.put("b2_26_20", new visuperobject(2,26,20,"Lại hãy làm hai mươi tấm ván về phía bắc đền tạm,"));
visuperobject.biblemap.put("b2_26_21", new visuperobject(2,26,21,"và bốn mươi lỗ mộng bằng bạc, cứ dưới mỗi tấm có hai lỗ mộng."));
visuperobject.biblemap.put("b2_26_22", new visuperobject(2,26,22,"Ngươi cũng hãy làm sáu tấm ván về phía sau đền tạm, tức là về hướng tây."));
visuperobject.biblemap.put("b2_26_23", new visuperobject(2,26,23,"Về hai góc phía sau đền tạm, phải làm hai tấm ván,"));
visuperobject.biblemap.put("b2_26_24", new visuperobject(2,26,24,"khít nhau tự dưới chí trên bởi một cái khoen ở nơi đầu; hai cái góc đều làm như vậy."));
visuperobject.biblemap.put("b2_26_25", new visuperobject(2,26,25,"Thế thì, có tám tấm ván và mười sáu lỗ mộng bằng bạc; cứ hai lỗ mộng ở dưới mỗi một tấm ván."));
visuperobject.biblemap.put("b2_26_26", new visuperobject(2,26,26,"Lại, ngươi hãy làm năm cây xà ngang bằng gỗ si-tim cho những tấm ván về phía bên nầy,"));
visuperobject.biblemap.put("b2_26_27", new visuperobject(2,26,27,"năm cây xà ngang về phía bên kia và năm cây xà ngang khác nữa về phía sau của đền tạm, tức là hướng tây."));
visuperobject.biblemap.put("b2_26_28", new visuperobject(2,26,28,"Cây xà ngang giữa sẽ ở về thân giữa những tấm ván, chạy suốt từ đầu nầy đến đầu kia."));
visuperobject.biblemap.put("b2_26_29", new visuperobject(2,26,29,"Cũng hãy lấy vàng bọc ván cùng xà ngang, và làm những khoen bằng vàng, đặng xỏ các cây xà ngang."));
visuperobject.biblemap.put("b2_26_30", new visuperobject(2,26,30,"Ngươi phải dựng đền tạm y như kiểu đã chỉ cho ngươi trên núi vậy."));
visuperobject.biblemap.put("b2_26_31", new visuperobject(2,26,31,"Ngươi hãy làm một bức màn bằng vải gai đậu mịn, chỉ tím, đỏ điều, đỏ sặm, có thêu những hình chê-ru-bin cực xảo;"));
visuperobject.biblemap.put("b2_26_32", new visuperobject(2,26,32,"rồi xủ màn đó trên bốn trụ bằng cây si-tim, bọc vàng, dựng trên bốn lỗ trụ bằng bạc; các móc trụ đều bằng vàng."));
visuperobject.biblemap.put("b2_26_33", new visuperobject(2,26,33,"Ngươi sẽ treo màn đó vào móc dưới bức bong, rồi ở phía trong màn để hòm bảng chứng; màn nầy dùng phân biệt cho các ngươi nơi thánh và nơi chí thánh."));
visuperobject.biblemap.put("b2_26_34", new visuperobject(2,26,34,"Ðoạn, hãy để cái nắp thi ân trên hòm bảng chứng, đặt trong nơi chí thánh."));
visuperobject.biblemap.put("b2_26_35", new visuperobject(2,26,35,"Bên ngoài bức màn về phía bắc đền tạm thì để cái bàn; còn cây chân đèn thì để về phía nam đối ngang cái bàn."));
visuperobject.biblemap.put("b2_26_36", new visuperobject(2,26,36,"Về cửa Trại ngươi hãy làm một bức màn bằng chỉ tím, đỏ điều, đỏ sặm, vải gai đậu mịn, có thêu cực xảo;"));
visuperobject.biblemap.put("b2_26_37", new visuperobject(2,26,37,"rồi làm cho bức màn nầy năm cái trụ bằng cây si-tim, bọc vàng; đinh nó cũng bằng vàng, và đúc năm lỗ trụ bằng đồng."));
visuperobject.biblemap.put("b2_27_1", new visuperobject(2,27,1,"Ngươi cũng hãy đóng một bàn thờ bằng cây si-tim, hình vuông, năm thước bề dài, năm thước bề ngang, và bề cao ba thước."));
visuperobject.biblemap.put("b2_27_2", new visuperobject(2,27,2,"Nơi bốn góc bàn thờ hãy làm sừng ló ra và bọc đồng."));
visuperobject.biblemap.put("b2_27_3", new visuperobject(2,27,3,"Những đồ phụ tùng của bàn thờ thì hãy làm bằng đồng: bình đựng tro, vá, ảng, nỉa và bình hương."));
visuperobject.biblemap.put("b2_27_4", new visuperobject(2,27,4,"Lại làm cho bàn thờ một tấm rá bằng lưới đồng; và nơi bốn góc rá làm bốn cái khoen bằng đồng;"));
visuperobject.biblemap.put("b2_27_5", new visuperobject(2,27,5,"rồi để rá đó dưới thành bàn thờ, từ dưới chân lên đến nửa bề cao."));
visuperobject.biblemap.put("b2_27_6", new visuperobject(2,27,6,"Cũng hãy chuốt đòn khiêng bàn thờ bằng cây si-tim, bọc đồng,"));
visuperobject.biblemap.put("b2_27_7", new visuperobject(2,27,7,"rồi xỏ vào những khoen; khi khiêng đi, đòn sẽ ở hai bên bàn thờ."));
visuperobject.biblemap.put("b2_27_8", new visuperobject(2,27,8,"Bàn thờ sẽ đóng bằng ván, trống bộng, làm y như đã chỉ cho ngươi trên núi vậy."));
visuperobject.biblemap.put("b2_27_9", new visuperobject(2,27,9,"Ngươi cũng hãy làm cái hành lang cho đền tạm. Về phía nam của hành lang làm bố vi bằng vải gai đậu mịn, dài trăm thước,"));
visuperobject.biblemap.put("b2_27_10", new visuperobject(2,27,10,"cùng hai chục cây trụ và hai chục lỗ trụ bằng đồng; đinh và nuông trụ thì bằng bạc."));
visuperobject.biblemap.put("b2_27_11", new visuperobject(2,27,11,"Về phía bắc cũng vậy; bố vi đó có trăm thước bề dài, hai chục cây trụ và hai chục lỗ trụ bằng đồng; đinh và nuông trụ thì bằng bạc."));
visuperobject.biblemap.put("b2_27_12", new visuperobject(2,27,12,"Bố vi của hành lang về phía tây có năm chục thước bề dài, mười cây trụ và mười lỗ trụ."));
visuperobject.biblemap.put("b2_27_13", new visuperobject(2,27,13,"Bề ngang của hành lang phía trước, tức là phía đông, có năm chục thước: phía hữu"));
visuperobject.biblemap.put("b2_27_14", new visuperobject(2,27,14,"có mười lăm thước bố vi, ba cây trụ và ba lỗ trụ;"));
visuperobject.biblemap.put("b2_27_15", new visuperobject(2,27,15,"phía tả có mười lăm thước bố vi, ba cây trụ và ba lỗ trụ."));
visuperobject.biblemap.put("b2_27_16", new visuperobject(2,27,16,"Còn cửa hành lang có một bức màn dài hai chục thước bằng vải gai đậu mịn, chỉ tím, đỏ điều, đỏ sặm, có thêu, cùng vốn cây trụ và bốn lỗ trụ."));
visuperobject.biblemap.put("b2_27_17", new visuperobject(2,27,17,"Hết thảy trụ xung quanh hành lang đều sẽ có nhờ nuông bạc chắp lại với nhau; đinh trụ thì bằng bạc, lỗ trụ thì bằng đồng."));
visuperobject.biblemap.put("b2_27_18", new visuperobject(2,27,18,"Bề dài của hành lang được một trăm thước, bề ngang, mỗi phía, năm chục thước, bề cao năm thước; bố vi sẽ bằng vải gai đậu mịn, còn những lỗ trụ thì bằng đồng."));
visuperobject.biblemap.put("b2_27_19", new visuperobject(2,27,19,"Hết thảy đồ phụ tùng dùng về các việc phụng sự trong đền tạm, cùng cây nọc của đền tạm và cây nọc của hành lang, đều sẽ làm bằng đồng."));
visuperobject.biblemap.put("b2_27_20", new visuperobject(2,27,20,"Ngươi hãy truyền cho dân Y-sơ-ra-ên đem đến dầu lọc ô-li-ve ép, đặng thắp đèn cho đèn sáng luôn luôn."));
visuperobject.biblemap.put("b2_27_21", new visuperobject(2,27,21,"Trong hội mạc, ở ngoài màn, trước hòm bảng chứng, A-rôn cùng các con trai người sẽ lo sửa soạn đèn, hầu cho trước mặt Ðức Giê-hô-va đèn cháy luôn luôn từ tối cho đến sáng. Ấy là một luật đời đời cho dòng dõi dân Y-sơ-ra-ên phải giữ."));
visuperobject.biblemap.put("b2_28_1", new visuperobject(2,28,1,"Trong vòng dân Y-sơ-ra-ên, ngươi hãy chọn A-rôn, anh ngươi, cùng các con trai ngươi, là Na-đáp, A-bi-hu, Ê-lê-a-sa, và Y-tha-ma, lập lên làm chức tế lễ trước mặt ta."));
visuperobject.biblemap.put("b2_28_2", new visuperobject(2,28,2,"Ngươi hãy chế bộ áo thánh cho A-rôn, anh ngươi, để người được vinh hiển trang sức."));
visuperobject.biblemap.put("b2_28_3", new visuperobject(2,28,3,"Hãy truyền cho hết thảy kẻ thông minh, là người ta đã ban đầy tính trí huệ, chế bộ áo cho A-rôn, đặng biệt người riêng ra thánh, hầu cho người làm chức tế lễ trước mặt ta."));
visuperobject.biblemap.put("b2_28_4", new visuperobject(2,28,4,"Ðây là bộ áo mà họ sẽ chế: Bảng đeo ngực, ê-phót, áo dài, áo lá trong thêu, mũ và đai. Thế thì, họ sẽ chế bộ áo thánh cho A-rôn, anh ngươi, cùng các con trai người, để các người đó làm chức tế lễ trước mặt ta."));
visuperobject.biblemap.put("b2_28_5", new visuperobject(2,28,5,"Họ sẽ dùng kim tuyến, chỉ màu tím, đỏ điều, đỏ sặm, và vải gai mịn."));
visuperobject.biblemap.put("b2_28_6", new visuperobject(2,28,6,"Họ sẽ chế ê-phót bằng kim tuyến, chỉ màu tím, đỏ điều, đỏ sặm, và vải gai đậu mịn thêu cực xảo."));
visuperobject.biblemap.put("b2_28_7", new visuperobject(2,28,7,"Hai bên ê-phót làm hai đai vai, đâu đầu vào ê-phót."));
visuperobject.biblemap.put("b2_28_8", new visuperobject(2,28,8,"Ðai để cột ê-phót ở phía ngoài, sẽ làm nguyên miếng một cũng như công việc ê-phót, bằng kim tuyến, chỉ tím, đỏ điều, đỏ sặm, và vải gai đậu mịn."));
visuperobject.biblemap.put("b2_28_9", new visuperobject(2,28,9,"Ngươi hãy lấy hai viên bích ngọc mà khắc các tên con trai Y-sơ-ra-ên ở trên:"));
visuperobject.biblemap.put("b2_28_10", new visuperobject(2,28,10,"sáu tên trên viên bích ngọc nầy, sáu tên trên viên bích ngọc kia, tùy theo thứ tự ngày sinh của họ."));
visuperobject.biblemap.put("b2_28_11", new visuperobject(2,28,11,"Phải khắc các tên con trai Y-sơ-ra-ên trên hai viên bích ngọc đó, như cách của thợ khắc con dấu, và khảm trong khuôn bằng vàng."));
visuperobject.biblemap.put("b2_28_12", new visuperobject(2,28,12,"Ðoạn gắn hai viên bích ngọc đó trên hai đai vai ê-phót, dùng làm ngọc kỷ niệm về con cháu Y-sơ-ra-ên; A-rôn sẽ mang tên của họ trên hai vai mình làm kỷ niệm trước mặt Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_28_13", new visuperobject(2,28,13,"Ngươi cũng hãy làm móc gài bằng vàng,"));
visuperobject.biblemap.put("b2_28_14", new visuperobject(2,28,14,"đánh hai sợi dây chuyền nhỏ bằng vàng ròng như hình dây, rồi để chuyền đánh như thế vào móc gài."));
visuperobject.biblemap.put("b2_28_15", new visuperobject(2,28,15,"Bảng đeo ngực về sự xét đoán, ngươi cũng phải làm cho cực xảo, y như ê-phót; tức là bằng kim tuyến, chỉ tím, đỏ điều, đỏ sặm, và vải gai đậu mịn."));
visuperobject.biblemap.put("b2_28_16", new visuperobject(2,28,16,"Bảng đeo ngực hình vuông và may lót; bề dài một em-ban, bề ngang một em-ban."));
visuperobject.biblemap.put("b2_28_17", new visuperobject(2,28,17,"Ngươi hãy gắn bốn hàng ngọc vào bảng đeo ngực: hàng thứ nhất gắn ngọc mã não, ngọc hồng bích, và ngọc lục bửu;"));
visuperobject.biblemap.put("b2_28_18", new visuperobject(2,28,18,"hàng thứ nhì, ngọc phỉ túy, ngọc lam bửu và ngọc kim cương;"));
visuperobject.biblemap.put("b2_28_19", new visuperobject(2,28,19,"hàng thứ ba, ngọc hồng bửu, ngọc bạch mã não, và ngọc tử tinh;"));
visuperobject.biblemap.put("b2_28_20", new visuperobject(2,28,20,"hàng thứ tư, ngọc huỳnh bích, ngọc hồng mã não, và bích ngọc. Các ngọc nầy sẽ khảm vào khuôn bằng vàng."));
visuperobject.biblemap.put("b2_28_21", new visuperobject(2,28,21,"Số các ngọc đó hết thảy là mười hai viên, tùy theo tên các con trai Y-sơ-ra-ên; trên mỗi viên ngọc sẽ khắc tên của một trong mười hai chi phái Y-sơ-ra-ên, như người ta khắc con dấu vậy."));
visuperobject.biblemap.put("b2_28_22", new visuperobject(2,28,22,"Ngươi hãy làm các sợi chuyền bằng vàng ròng đánh như hình dây, chuyền trên bảng đeo ngực;"));
visuperobject.biblemap.put("b2_28_23", new visuperobject(2,28,23,"rồi làm hai khoanh bằng vàng để ở hai góc đầu trên bảng."));
visuperobject.biblemap.put("b2_28_24", new visuperobject(2,28,24,"cùng cột hai sợi chuyền vàng vào hai khoanh nơi hai đầu bảng,"));
visuperobject.biblemap.put("b2_28_25", new visuperobject(2,28,25,"và hai đầu sợi chuyền cột vào móc gài, đoạn gắn trên hai đai vai ê-phót về phía đằng trước ngực."));
visuperobject.biblemap.put("b2_28_26", new visuperobject(2,28,26,"Ngươi cũng hãy làm hai cái khoanh bằng vàng nữa, để vào hai góc đầu dưới của bảng đeo ngực, về phía trong ê-phót."));
visuperobject.biblemap.put("b2_28_27", new visuperobject(2,28,27,"Lại làm hai khoanh khác nữa bằng vàng, gắn vào hai đầu dưới đai vai ê-phót ném về phía trước, ở trên đai gần chỗ giáp mối."));
visuperobject.biblemap.put("b2_28_28", new visuperobject(2,28,28,"Rồi một sợi dây màu tím sẽ cột hai cái khoanh của bảng đeo ngực lại cùng hai cái khoanh ê-phót, hầu cho nó dính với đai, đừng rớt khỏi ê-phót."));
visuperobject.biblemap.put("b2_28_29", new visuperobject(2,28,29,"Thế thì, khi nào A-rôn vào nơi thánh, sẽ mang trên lòng mình tên các con trai Y-sơ-ra-ên nơi bảng đeo ngực về sự xét đoán, đặng giữ làm kỷ niệm luôn luôn trước mặt Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_28_30", new visuperobject(2,28,30,"Trên bảng đeo ngực của sự xét đoán, ngươi hãy gắn u-rim và thu-mim vào, đặng khi A-rôn đến trước mặt Ðức Giê-hô-va thì các món đó sẽ ở trên lòng mình. Ấy vậy, trước mặt Ðức Giê-hô-va, A-rôn sẽ mang sự xét đoán của con trai Y-sơ-ra-ên trên lòng mình luôn luôn."));
visuperobject.biblemap.put("b2_28_31", new visuperobject(2,28,31,"Ngươi cũng hãy may áo dài của ê-phót toàn bằng vải tím."));
visuperobject.biblemap.put("b2_28_32", new visuperobject(2,28,32,"Ở giữa áo có một lỗ tròng đầu vào; viền chung quanh, dệt như lỗ áo giáp mặt lưới, để cho khỏi tét."));
visuperobject.biblemap.put("b2_28_33", new visuperobject(2,28,33,"Nơi biên dưới hãy thắt trái lựu màu tím, đỏ điều, đỏ sặm, cùng chuông vàng nhỏ đan tréo nhau ở vòng theo viền,"));
visuperobject.biblemap.put("b2_28_34", new visuperobject(2,28,34,"nghĩa là một cái chuông rồi kế một trái lựu, ở vòng khắp chung quanh viền áo."));
visuperobject.biblemap.put("b2_28_35", new visuperobject(2,28,35,"Khi A-rôn hầu việc tế lễ, sẽ mặc áo đó; và khi vào hay ra nơi thánh trước mặt Ðức Giê-hô-va, người ta sẽ nghe tiếng chuông vàng nhỏ, và A-rôn khỏi chết vậy."));
visuperobject.biblemap.put("b2_28_36", new visuperobject(2,28,36,"Ngươi cũng hãy làm một cái thẻ bằng vàng ròng, trên đó khắc như người ta khắc con dấu, rằng: Thánh Cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_28_37", new visuperobject(2,28,37,"Rồi lấy một sợi dây màu tím buộc thẻ đó để trên đằng trước cái mũ."));
visuperobject.biblemap.put("b2_28_38", new visuperobject(2,28,38,"Thẻ đó sẽ ở trên trán A-rôn; A-rôn gánh tội lỗi của dân Y-sơ-ra-ên phạm trong khi họ dâng của lễ thánh; thẻ đó sẽ ở nơi trán A-rôn luôn luôn, để dân sự được đẹp ý Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_28_39", new visuperobject(2,28,39,"Ngươi hãy chế một cái áo lá mặc trong bằng vải gai mịn, một cái mũ bằng vải gai mịn và một cái đai thêu."));
visuperobject.biblemap.put("b2_28_40", new visuperobject(2,28,40,"Ngươi cũng hãy chế cho các con trai A-rôn áo lá, đai và mũ, hầu cho họ được vinh hiển và trang sức."));
visuperobject.biblemap.put("b2_28_41", new visuperobject(2,28,41,"Ðoạn, hãy lấy các bộ áo đó mặc cho A-rôn, anh ngươi, cùng các con trai người; hãy xức dầu cho, lập và biệt riêng ra thánh, để họ làm chức tế lễ trước mặt ta."));
visuperobject.biblemap.put("b2_28_42", new visuperobject(2,28,42,"Hãy chế cho họ quần lót trong bằng vải gai, đặng che sự lõa lồ, mặc từ ngang lưng quần cho đến bắp vế."));
visuperobject.biblemap.put("b2_28_43", new visuperobject(2,28,43,"A-rôn cùng các con trai người sẽ mặc quần đó trong khi vào hội mạc hay là đến gần bàn thờ đặng làm việc tế lễ trong nơi thánh, thì chẳng mắc tội và chẳng chết đâu. Ấy là một mạng lịnh đời đời cho A-rôn và dòng dõi người."));
visuperobject.biblemap.put("b2_29_1", new visuperobject(2,29,1,"Ðây là điều ngươi sẽ làm đặng biệt riêng A-rôn và con trai người riêng ra thánh, để họ làm chức tế lễ trước mặt ta."));
visuperobject.biblemap.put("b2_29_2", new visuperobject(2,29,2,"Hãy bắt một con bò tơ đực và hai con chiên đực không tì vít, dùng bột mì thiệt mịn làm bánh không men, bánh nhỏ không men nhồi với dầu, và bánh tráng không men tẩm dầu;"));
visuperobject.biblemap.put("b2_29_3", new visuperobject(2,29,3,"rồi để các bánh đó trong một giỏ dâng luôn lên cùng bò tơ đực và hai chiên đực."));
visuperobject.biblemap.put("b2_29_4", new visuperobject(2,29,4,"Ðoạn, ngươi sẽ biểu A-rôn cùng các con trai người đến gần nơi cửa hội mạc, rồi lấy nước tắm cho họ."));
visuperobject.biblemap.put("b2_29_5", new visuperobject(2,29,5,"Kế đó, lấy bộ áo mặc cho A-rôn, tức là áo lá mặc trong, áo dài của ê-phót, ê-phót, và bảng đeo ngực, rồi lấy đai của ê-phót thắt lưng cho người;"));
visuperobject.biblemap.put("b2_29_6", new visuperobject(2,29,6,"đoạn đội cái mũ lên đầu và để thẻ thánh trên mũ,"));
visuperobject.biblemap.put("b2_29_7", new visuperobject(2,29,7,"cùng lấy dầu xức đổ trên đầu và xức cho người."));
visuperobject.biblemap.put("b2_29_8", new visuperobject(2,29,8,"Lại biểu các con trai A-rôn đến gần mặc áo lá cho họ,"));
visuperobject.biblemap.put("b2_29_9", new visuperobject(2,29,9,"rồi lấy đai thắt lưng và đội mũ cho A-rôn cùng các con trai người. Thế thì, chức tế lễ sẽ bởi mạng lịnh định đời đời cho họ. Ngươi lập A-rôn và các con trai người là thế."));
visuperobject.biblemap.put("b2_29_10", new visuperobject(2,29,10,"Ðoạn, hãy dắt con bò tơ đực lại gần trước hội mạc, A-rôn cùng các con trai người sẽ nhận tay mình trên đầu con bò đó."));
visuperobject.biblemap.put("b2_29_11", new visuperobject(2,29,11,"Ngươi hãy giết bò đực trước mặt Ðức Giê-hô-va, tại nơi cửa hội mạc;"));
visuperobject.biblemap.put("b2_29_12", new visuperobject(2,29,12,"rồi lấy huyết bò đó, nhúng ngón tay vào, bôi các sừng của bàn thờ, và đổ khắp dưới chân bàn thờ."));
visuperobject.biblemap.put("b2_29_13", new visuperobject(2,29,13,"Cũng hãy lấy hết thảy những mỡ bọc ruột, tấm da mỏng bọc gan, hai trái cật và mỡ trên trái cật, mà đem xông trên bàn thờ."));
visuperobject.biblemap.put("b2_29_14", new visuperobject(2,29,14,"Nhưng ở ngoài trại quân hãy thiêu hết thịt, da, và phẩn của con bò đực đó; ấy là một của lễ chuộc tội vậy."));
visuperobject.biblemap.put("b2_29_15", new visuperobject(2,29,15,"Ngươi cũng hãy bắt một trong hai con chiên đực, A-rôn và các con trai ngươi sẽ nhận tay mình trên đầu nó;"));
visuperobject.biblemap.put("b2_29_16", new visuperobject(2,29,16,"rồi giết chiên đực đó, lấy huyết mà rưới khắp chung quanh trên bàn thờ."));
visuperobject.biblemap.put("b2_29_17", new visuperobject(2,29,17,"Kế đó, sả chiên ra từng miếng, rửa sạch bộ lòng và giò đi, rồi để lên trên các miếng đã sả và đầu nó;"));
visuperobject.biblemap.put("b2_29_18", new visuperobject(2,29,18,"đoạn xông cả con chiên đực tại trên bàn thờ; ấy là một của lễ thiêu, có mùi thơm dâng cho Ðức Giê-hô-va, tức là một của lễ dùng lửa dâng lên cho Ðức Giê-hô-va vậy."));
visuperobject.biblemap.put("b2_29_19", new visuperobject(2,29,19,"Ðoạn, ngươi hãy bắt con chiên đực thứ nhì, A-rôn cùng các con trai người sẽ nhận tay mình trên đầu nó;"));
visuperobject.biblemap.put("b2_29_20", new visuperobject(2,29,20,"rồi ngươi giết chiên đực đó, lấy huyết bôi nơi trái tai hữu của A-rôn và của các con trai người, trên ngón cái bàn tay mặt và ngón cái bàn chân mặt; huyết còn dư hãy rưới chung quanh trên bàn thờ."));
visuperobject.biblemap.put("b2_29_21", new visuperobject(2,29,21,"Ngươi hãy lấy huyết ở nơi bàn thờ và dầu xức mà rảy trên mình và quần áo A-rôn, cùng trên mình và quần áo các con trai người. Như vậy, A-rôn cùng quần áo mình, các con trai người cùng quần áo họ sẽ được biệt riêng ra thánh."));
visuperobject.biblemap.put("b2_29_22", new visuperobject(2,29,22,"Vì cớ chiên đực đó dùng làm lễ truyền chức, nên ngươi hãy lấy mỡ, đuôi, mỡ bọc ruột, tấm da mỏng bọc gan, hai trái cật, cùng mỡ trên trái cật và giò bên hữu,"));
visuperobject.biblemap.put("b2_29_23", new visuperobject(2,29,23,"lại lấy luôn một ổ bánh, một bánh nhỏ nhồi với dầu, và một cái bánh tráng ở trong giỏ bánh không men để trước mặt Ðức Giê-hô-va;"));
visuperobject.biblemap.put("b2_29_24", new visuperobject(2,29,24,"rồi ngươi để mấy món đó trong lòng bàn tay A-rôn, và trong lòng bàn tay của các con trai ngươi, mà đưa qua đưa lại trước mặt Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_29_25", new visuperobject(2,29,25,"Ðoạn, hãy lấy ra khỏi bàn tay họ đem xông trên bàn thờ, ở trên của lễ thiêu, để cho có mùi thơm trước mặt Ðức Giê-hô-va; ấy là một của lễ dùng lửa dâng lên cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_29_26", new visuperobject(2,29,26,"Cũng hãy lấy cái o chiên đực về lễ lập A-rôn mà đưa qua đưa lại trước mặt Ðức Giê-hô-va. Ấy sẽ về phần ngươi."));
visuperobject.biblemap.put("b2_29_27", new visuperobject(2,29,27,"Vậy, ngươi sẽ biệt riêng ra thánh cái o và giò của chiên đực đã dùng làm lễ lập A-rôn cùng các con trai người, tức là o đã đưa qua đưa lại, và giò đã giơ lên."));
visuperobject.biblemap.put("b2_29_28", new visuperobject(2,29,28,"Lễ vật nầy sẽ do nơi Y-sơ-ra-ên dâng cho A-rôn cùng các con trai người tùy theo mạng lịnh đời đời đã lập, vì là một điều ban cho bởi trong các của lễ thù ân của dân Y-sơ-ra-ên, tức là một của lễ giơ lên mà họ dâng cho Ðức Giê-hô-va vậy."));
visuperobject.biblemap.put("b2_29_29", new visuperobject(2,29,29,"Những áo thánh của A-rôn sẽ để lại cho các con trai nối sau người, để họ mặc áo đó, được xức dầu và lập trong chức tế lễ."));
visuperobject.biblemap.put("b2_29_30", new visuperobject(2,29,30,"Ai trong vòng con trai người được làm thầy tế lễ thế cho ngươi, mà đến hội mạc đặng hầu việc tại nơi thánh, thì sẽ mặc áo đó trong bảy ngày."));
visuperobject.biblemap.put("b2_29_31", new visuperobject(2,29,31,"Ðoạn, ngươi hãy bắt con chiên đực dùng làm lễ thiết lập mà nấu thịt nó trong một nơi thánh."));
visuperobject.biblemap.put("b2_29_32", new visuperobject(2,29,32,"A-rôn cùng các con trai người sẽ ăn thịt chiên đực đó, cùng bánh trong giỏ nơi cửa hội mạc."));
visuperobject.biblemap.put("b2_29_33", new visuperobject(2,29,33,"Vậy, họ sẽ ăn các món nầy, bởi đó được chuộc tội, để lập và biệt họ riêng ra thánh; nhưng khách ngoại bang chẳng được ăn, vì các món đó đã biệt riêng ra thánh rồi."));
visuperobject.biblemap.put("b2_29_34", new visuperobject(2,29,34,"Nếu thịt lễ thiết lập và bánh còn dư lại đến ngày mai, ngươi hãy thiêu trong lửa, chớ nên ăn, vì là của đã biệt riêng ra thánh."));
visuperobject.biblemap.put("b2_29_35", new visuperobject(2,29,35,"Vậy, theo mọi điều ta phán dặn, ngươi hãy làm cho A-rôn cùng các con trai người, và lập họ trong bảy ngày."));
visuperobject.biblemap.put("b2_29_36", new visuperobject(2,29,36,"Mỗi ngày hãy dâng một con bò tơ đực đặng chuộc tội, và dâng vì bàn thờ một của lễ chuộc tội cùng xức dầu cho để biệt bàn thờ riêng ra thánh."));
visuperobject.biblemap.put("b2_29_37", new visuperobject(2,29,37,"Trong bảy ngày ngươi hãy làm lễ chuộc tội cho bàn thờ và biệt riêng ra thánh; bàn thờ sẽ trở nên rất thánh, phàm vật chi đụng đến đều sẽ được nên thánh vậy."));
visuperobject.biblemap.put("b2_29_38", new visuperobject(2,29,38,"Ðây là các điều ngươi sẽ dâng lên bàn thờ: Hai chiên con một tuổi, mỗi ngày thường thường như vậy."));
visuperobject.biblemap.put("b2_29_39", new visuperobject(2,29,39,"Buổi sớm mai ngươi hãy dâng một trong hai chiên con; con thứ nhì hãy dâng vào buổi chiều tối."));
visuperobject.biblemap.put("b2_29_40", new visuperobject(2,29,40,"Với con chiên thứ nhất, hãy dâng một phần mười bột mì mịn nhồi với một phần tư hin dầu ép, và cùng lễ quán một phần tư hin rượu nho."));
visuperobject.biblemap.put("b2_29_41", new visuperobject(2,29,41,"Về con chiên thứ nhì hãy dâng vào buổi chiều tối với một của lễ và một lễ quán y như lễ sớm mai; ấy là một của lễ thiêu, có mùi thơm dâng cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_29_42", new visuperobject(2,29,42,"Ấy là một của lễ thiêu mà các ngươi phải dâng luôn luôn, trải qua các đời, tại cửa hội mạc trước mặt Ðức Giê-hô-va, là nơi ta sẽ giáng lâm mà phán cùng ngươi."));
visuperobject.biblemap.put("b2_29_43", new visuperobject(2,29,43,"Vậy, ta sẽ có tại đó cùng dân Y-sơ-ra-ên; chỗ nầy sẽ vì sự vinh hiển ta mà được biệt riêng ra thánh."));
visuperobject.biblemap.put("b2_29_44", new visuperobject(2,29,44,"Ta sẽ biệt hội mạc cùng bàn thờ riêng ra thánh; cùng biệt A-rôn và các con trai người riêng ra thánh nữa, đặng họ làm chức tế lễ trước mặt ta."));
visuperobject.biblemap.put("b2_29_45", new visuperobject(2,29,45,"Ta sẽ ở giữa dân Y-sơ-ra-ên, làm Ðức Chúa Trời dân ấy."));
visuperobject.biblemap.put("b2_29_46", new visuperobject(2,29,46,"Dân ấy sẽ biết ta là Giê-hô-va Ðức Chúa Trời của chúng nó, đã đem chúng nó ra khỏi xứ Ê-díp-tô đặng ở giữa vòng họ. Ta là Giê-hô-va Ðức Chúa Trời của họ."));
visuperobject.biblemap.put("b2_30_1", new visuperobject(2,30,1,"Ngươi cũng hãy đóng một cái bàn thờ bằng cây si-tim, để xông hương."));
visuperobject.biblemap.put("b2_30_2", new visuperobject(2,30,2,"Mặt bàn sẽ vuông, bề dài một thước, bề ngang một thước, và bề cao hai thước; bàn thờ sẽ có bốn sừng ló ra."));
visuperobject.biblemap.put("b2_30_3", new visuperobject(2,30,3,"Ngươi hãy bọc vàng ròng trên mặt, bốn cạnh chung quanh và các sừng; còn tứ vi chạy đường viền vàng."));
visuperobject.biblemap.put("b2_30_4", new visuperobject(2,30,4,"Dưới đường viền đó, nơi hai bên, hãy làm hai cái khoen bằng vàng, để xỏ đòn dùng đặng khiêng."));
visuperobject.biblemap.put("b2_30_5", new visuperobject(2,30,5,"Hãy chuốt đòn khiêng bằng cây si-tim, bọc vàng."));
visuperobject.biblemap.put("b2_30_6", new visuperobject(2,30,6,"Ngươi sẽ để bàn thờ đó trước bức màn che hòm bảng chứng đối cùng nắp thi ân trên hòm, là nơi ta sẽ gặp ngươi."));
visuperobject.biblemap.put("b2_30_7", new visuperobject(2,30,7,"Mỗi buổi sớm mai, khi A-rôn làm đèn, sẽ xông hương tại nơi đó."));
visuperobject.biblemap.put("b2_30_8", new visuperobject(2,30,8,"Vào lúc chiều tối, khi A-rôn thắp đèn, cũng sẽ xông hương: ấy là một thứ hương phải xông trước mặt Ðức Giê-hô-va luôn luôn, trải qua các đời."));
visuperobject.biblemap.put("b2_30_9", new visuperobject(2,30,9,"Trên bàn thờ nầy chớ xông hương lạ, hoặc dâng của lễ thiêu, của lễ chay hay là lễ quán nào hết."));
visuperobject.biblemap.put("b2_30_10", new visuperobject(2,30,10,"Nhưng trải qua các đời, mỗi năm một lần, A-rôn sẽ lấy huyết của con sinh tế chuộc tôi, bôi trên sừng bàn thờ nầy đặng chuộc tội cho nó. Ấy sẽ là một việc rất thánh cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_30_11", new visuperobject(2,30,11,"Ðức Giê-hô-va cũng phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b2_30_12", new visuperobject(2,30,12,"Khi nào ngươi điểm số dân Y-sơ-ra-ên đặng kê sổ, mỗi tên phải nộp tiền đền mạng mình cho Ðức Giê-hô-va, hầu cho khỏi mắc tai nạn chi trong khi kê sổ."));
visuperobject.biblemap.put("b2_30_13", new visuperobject(2,30,13,"Kẻ nào có tên trong sổ phải nộp nửa siếc-lơ, tùy siếc-lơ của nơi thánh, cận nặng hai mươi ghê-ra; vậy, nửa siếc-lơ, tức là của dâng cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_30_14", new visuperobject(2,30,14,"Mọi người có tên vào sổ từ hai mươi tuổi sắp lên sẽ dâng của nầy cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_30_15", new visuperobject(2,30,15,"Khi dâng của nầy cho Ðức Giê-hô-va đặng đền mạng mình, người giàu không nộp trội, người nghèo không nộp thiếu nửa siếc-lơ."));
visuperobject.biblemap.put("b2_30_16", new visuperobject(2,30,16,"Vậy, ngươi thâu tiền đền mạng của dân Y-sơ-ra-ên, rồi dùng tiền đó vào việc hội mạc; ấy là một kỷ niệm của dân Y-sơ-ra-ên trước mặt Ðức Giê-hô-va, đặng đền mạng mình."));
visuperobject.biblemap.put("b2_30_17", new visuperobject(2,30,17,"Ðức Giê-hô-va lại phán cùng Môi-se nữa rằng:"));
visuperobject.biblemap.put("b2_30_18", new visuperobject(2,30,18,"Ngươi hãy làm một cái thùng với chân thùng bằng đồng, đặng rửa mình ở trong, rồi để giữa khoảng của hội mạc và bàn thờ, và đổ nước vào."));
visuperobject.biblemap.put("b2_30_19", new visuperobject(2,30,19,"A-rôn cùng các con trai người sẽ rửa tay và chân mình ở trong."));
visuperobject.biblemap.put("b2_30_20", new visuperobject(2,30,20,"Khi họ vào hội mạc sẽ lấy nước rửa mình, hầu cho họ khỏi chết; và khi lại gần bàn thờ đặng phụng sự, tức là xông của lễ dùng lửa dâng cho Ðức Giê-hô-va, họ cũng phải giữ như vậy."));
visuperobject.biblemap.put("b2_30_21", new visuperobject(2,30,21,"Thế thì, họ hãy rửa tay và chân, hầu cho khỏi chết. Ấy là một lệ đời đời cho A-rôn, cùng dòng dõi người trải qua các đời."));
visuperobject.biblemap.put("b2_30_22", new visuperobject(2,30,22,"Ðức Giê-hô-va lại phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b2_30_23", new visuperobject(2,30,23,"Hãy góp các hương liệu tốt nhất: một dược nước năm trăm siếc-lơ; hương nhục quế phân nửa số đó, tức là hai trăm rưỡi; hương xương bồ hai trăm rưỡi;"));
visuperobject.biblemap.put("b2_30_24", new visuperobject(2,30,24,"quế-bì năm trăm, đều theo siếc-lơ nơi thánh, và một hin dầu ô-li-ve."));
visuperobject.biblemap.put("b2_30_25", new visuperobject(2,30,25,"Ngươi hãy lấy các hương liệu đó theo phép hòa hương, chế ra một thứ dầu thơm dùng làm dầu xức thánh."));
visuperobject.biblemap.put("b2_30_26", new visuperobject(2,30,26,"Ðoạn, lấy xức cho hội mạc cùng hòm bảng chứng,"));
visuperobject.biblemap.put("b2_30_27", new visuperobject(2,30,27,"bàn thờ cùng các đồ phụ tùng, chân đèn cùng các đồ phụ tùng, bàn thờ xông hương,"));
visuperobject.biblemap.put("b2_30_28", new visuperobject(2,30,28,"bàn thờ của lễ thiêu cùng các đồ phụ tùng, cái thùng và chân thùng."));
visuperobject.biblemap.put("b2_30_29", new visuperobject(2,30,29,"Ấy vậy, ngươi sẽ biệt các vật nầy riêng ra thánh, hầu cho làm rất thánh, hễ món chi đụng đến, đều sẽ được nên thánh."));
visuperobject.biblemap.put("b2_30_30", new visuperobject(2,30,30,"Ngươi cũng hãy xức dầu cho A-rôn cùng các con trai người, biệt họ riêng ra thánh, để làm chức tế lễ trước mặt ta."));
visuperobject.biblemap.put("b2_30_31", new visuperobject(2,30,31,"Lại hãy nói cùng dân Y-sơ-ra-ên rằng: Về phần ta, dầu nầy sẽ là dầu xức thánh trải qua mọi đời các ngươi."));
visuperobject.biblemap.put("b2_30_32", new visuperobject(2,30,32,"Chớ nên đổ trên xác thịt loài người, và cũng đừng làm dầu nào khác theo phép hòa hương đó; dầu nầy là thánh, cũng sẽ thánh cho các ngươi."));
visuperobject.biblemap.put("b2_30_33", new visuperobject(2,30,33,"Hễ ai chế một thứ hòa hương giống như vậy, và đem rưới trên mình người ngoại bang, sẽ bị truất khỏi vòng dân sự."));
visuperobject.biblemap.put("b2_30_34", new visuperobject(2,30,34,"Ðức Giê-hô-va phán cùng Môi-se nữa rằng: Hãy lấy các hương liệu, tức tô hiệp hương, loa yểm hương, phong chi hương, cùng thanh nhũ hương, mỗi thứ bằng nhau,"));
visuperobject.biblemap.put("b2_30_35", new visuperobject(2,30,35,"theo phép hòa hương, chế ra một thứ hương, mặn, trong và thánh."));
visuperobject.biblemap.put("b2_30_36", new visuperobject(2,30,36,"Hãy nghiền nó ra bột, rồi để trước hòm bảng chứng trong hội mạc, tức là nơi ta sẽ gặp ngươi: về phần các ngươi, hương nầy sẽ là một vật rất thánh."));
visuperobject.biblemap.put("b2_30_37", new visuperobject(2,30,37,"Còn thứ hương mà ngươi sẽ chế, chớ chế giống như phép hòa hương nầy; ấy là một vật ngươi nên biệt riêng ra thánh cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_30_38", new visuperobject(2,30,38,"Hễ kẻ nào làm giống y như vậy đặng ngửi mùi, sẽ bị truất khỏi vòng dân sự."));
visuperobject.biblemap.put("b2_31_1", new visuperobject(2,31,1,"Ðức Giê-hô-va phán cùng Môi-se nữa rằng:"));
visuperobject.biblemap.put("b2_31_2", new visuperobject(2,31,2,"Nầy, ta đã kêu tên Bết-sa-lê-ên, con trai của U-ri, cháu của Hu-rơ, trong chi phái Giu-đa."));
visuperobject.biblemap.put("b2_31_3", new visuperobject(2,31,3,"Ta đã làm cho người đầy dẫy Thần của Ðức Chúa Trời, sự khôn ngoan, sự thông sáng, và sự hiểu biết để làm mọi thứ nghề thợ,"));
visuperobject.biblemap.put("b2_31_4", new visuperobject(2,31,4,"đặng bày ra và chế tạo đồ vàng, bạc và đồng,"));
visuperobject.biblemap.put("b2_31_5", new visuperobject(2,31,5,"đặng khác và khảm ngọc, đẽo gỗ và làm được mọi thứ nghề thợ."));
visuperobject.biblemap.put("b2_31_6", new visuperobject(2,31,6,"Ðây, ta lại cho một người phụ việc, tên là Ô-hô-li-áp, con trai của A-hi-sa-mạc, trong chi phái Ðan. Ta cũng đã ban sự thông minh cho mọi người tài năng, hầu cho họ làm các việc ta đã phán dặn ngươi,"));
visuperobject.biblemap.put("b2_31_7", new visuperobject(2,31,7,"là hội mạc, hòm bảng chứng, nắp thi ân ở trên hòm, cùng đồ dùng trong hội mạc;"));
visuperobject.biblemap.put("b2_31_8", new visuperobject(2,31,8,"bàn thờ cùng đồ phụ tùng, chân đèn bằng vàng ròng và đồ phụ tùng, bàn thờ xông hương,"));
visuperobject.biblemap.put("b2_31_9", new visuperobject(2,31,9,"bàn thờ dâng của lễ thiêu cùng đồ phụ tùng, cái thùng và chân thùng;"));
visuperobject.biblemap.put("b2_31_10", new visuperobject(2,31,10,"áo lễ, áo thánh cho A-rôn thầy tế lễ cả, và bộ áo cho các con trai người, đặng làm chức tế lễ;"));
visuperobject.biblemap.put("b2_31_11", new visuperobject(2,31,11,"dầu xức, và hương thơm của nơi thánh. Các người đó phải làm hết thảy theo lời ta đã phán dặn ngươi."));
visuperobject.biblemap.put("b2_31_12", new visuperobject(2,31,12,"Ðức Giê-hô-va lại phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b2_31_13", new visuperobject(2,31,13,"Phần ngươi, hãy nói cùng dân Y-sơ-ra-ên rằng: Nhất là các ngươi hãy giữ ngày sa-bát ta, vì là một dấu giữa ta và các ngươi, trải qua mọi đời, để thiên hạ biết rằng ta, là Ðức Giê-hô-va, làm cho các ngươi nên thánh."));
visuperobject.biblemap.put("b2_31_14", new visuperobject(2,31,14,"Vậy, hãy giữ ngày sa-bát, vì là một ngày thánh cho các ngươi. Kẻ nào phạm đến ngày đó, phải bị xử tử; kẻ nào làm một việc chi trong ngày đó, cũng sẽ bị truất khỏi vòng dân sự."));
visuperobject.biblemap.put("b2_31_15", new visuperobject(2,31,15,"Người ta sẽ làm công việc trong sáu ngày, nhưng qua ngày thứ bảy là ngày sa-bát, tức là ngày nghỉ, biệt riêng ra thánh cho Ðức Giê-hô-va. Trong ngày sa-bát hễ ai làm một việc chi sẽ bị xử tử."));
visuperobject.biblemap.put("b2_31_16", new visuperobject(2,31,16,"Ấy vậy, dân Y-sơ-ra-ên sẽ giữ ngày sa-bát trải các đời của họ, như một giao ước đời đời."));
visuperobject.biblemap.put("b2_31_17", new visuperobject(2,31,17,"Ấy là một dấu đời đời cho ta cùng dân Y-sơ-ra-ên, vì Ðức Giê-hô-va đã dựng nên trời và đất trong sáu ngày, qua ngày thứ bảy Ngài nghỉ và lấy sức lại."));
visuperobject.biblemap.put("b2_31_18", new visuperobject(2,31,18,"Khi Ðức Giê-hô-va đã phán xong cùng Môi-se tại núi Si-na -i, bèn cho người hai bảng chứng bằng đá, với ngón tay Ðức Chúa Trời viết ra."));
visuperobject.biblemap.put("b2_32_1", new visuperobject(2,32,1,"Dân sự thấy Môi-se ở trên núi chậm xuống, bèn nhóm lại chung quanh A-rôn mà nói rằng: Nào! hãy làm các thần để đi trước chúng tôi đi, vì về phần Môi-se nầy, là người đã dẫn chúng tôi ra khỏi xứ Ê-díp-tô, chúng tôi chẳng biết điều chi đã xảy đến cho người rồi."));
visuperobject.biblemap.put("b2_32_2", new visuperobject(2,32,2,"A-rôn đáp rằng: Hãy một những vòng vàng đeo nơi tai vợ, con trai và con gái các ngươi đi, rồi đem lại cho ta."));
visuperobject.biblemap.put("b2_32_3", new visuperobject(2,32,3,"Hết thảy đều lột vòng vàng nơi tai mình mà đem lại cho A-rôn;"));
visuperobject.biblemap.put("b2_32_4", new visuperobject(2,32,4,"người nhận lấy nơi tay họ, và dùng đục làm thành một bò con đúc. Dân chúng nói rằng: Hỡi Y-sơ-ra-ên! nầy là các thần của ngươi đã đem ngươi ra khỏi xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_32_5", new visuperobject(2,32,5,"A-rôn thấy vậy, bèn lập một bàn thờ trước mặt tượng bò đó; đoạn, người la lên rằng: Sáng mai sẽ có lễ tôn trọng Ðức Giê-hô-va!"));
visuperobject.biblemap.put("b2_32_6", new visuperobject(2,32,6,"Sáng mai dân chúng bèn thức dậy sớm, dâng các của lễ thiêu và lễ thù ân; ngồi mà ăn uống, đoạn đứng dậy mà vui chơi."));
visuperobject.biblemap.put("b2_32_7", new visuperobject(2,32,7,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng: Hãy xuống đi, vì dân mà ngươi đưa ra khỏi xứ Ê-díp-tô đã bại hoại rồi,"));
visuperobject.biblemap.put("b2_32_8", new visuperobject(2,32,8,"vội bỏ đạo ta truyền dạy, đúc một con bò tơ, mọp trước tượng bò đó và dâng của lễ cho nó mà nói rằng: Hỡi Y-sơ-ra-ên! đây là các thần đã dẫn ngươi lên khỏi xứ Ê-díp-tô!"));
visuperobject.biblemap.put("b2_32_9", new visuperobject(2,32,9,"Ðức Giê-hô-va cũng phán cùng Môi-se rằng: Ta đã xem thấy dân nầy, kìa là một dân cứng cổ."));
visuperobject.biblemap.put("b2_32_10", new visuperobject(2,32,10,"Vả, bây giờ hãy để mặc ta làm, hầu cho cơn thạnh nộ ta nổi lên cùng chúng nó, diệt chúng nó đi; nhưng ta sẽ làm cho ngươi thành một dân lớn."));
visuperobject.biblemap.put("b2_32_11", new visuperobject(2,32,11,"Môi-se bèn nài xin Giê-hô-va Ðức Chúa Trời người rằng: Lạy Ðức Giê-hô-va, sao nổi thạnh nộ cùng dân Ngài? là dân mà Ngài đã dùng quyền lớn lao mạnh mẽ đưa ra khỏi xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_32_12", new visuperobject(2,32,12,"Sao để cho người Ê-díp-tô nói rằng: Ngài đưa chúng nó ra khỏi xứ đặng làm hại cho, giết đi tại trong núi, cùng diệt chúng nó khỏi mặt đất? Cầu xin Chúa hãy nguôi cơn giận và bỏ qua điều tai họa mà Ngài muốn giáng cho dân Ngài."));
visuperobject.biblemap.put("b2_32_13", new visuperobject(2,32,13,"Xin Chúa hãy nhớ lại Áp-ra-ham, Y-sác, Y-sơ-ra-ên, là các tôi tớ Ngài, mà Ngài có chỉ mình thề cùng họ rằng: Ta sẽ thêm dòng dõi các ngươi lên nhiều như sao trên trời, ta sẽ ban cho dòng dõi đó cả xứ mà ta chỉ phán, và họ sẽ được xứ ấy làm cơ nghiệp đời đời."));
visuperobject.biblemap.put("b2_32_14", new visuperobject(2,32,14,"Ðức Giê-hô-va bèn bỏ qua điều tai họa mà Ngài nói rằng sẽ giáng cho dân mình."));
visuperobject.biblemap.put("b2_32_15", new visuperobject(2,32,15,"Ðoạn, Môi-se ở trên núi trở xuống, tay cầm hai bảng chứng; hai bảng chứng có viết hai bên, mặt nầy và mặt kia."));
visuperobject.biblemap.put("b2_32_16", new visuperobject(2,32,16,"Hai bảng đó là việc của Ðức Chúa Trời làm ra; chữ cũng là chữ của Ðức Chúa Trời khắc trên bảng."));
visuperobject.biblemap.put("b2_32_17", new visuperobject(2,32,17,"Vả, Giô-suê nghe dân chúng la lên, bèn nói cùng Môi-se rằng: Trong trại quân có tiếng chiến đấu."));
visuperobject.biblemap.put("b2_32_18", new visuperobject(2,32,18,"Môi-se đáp rằng: Chẳng phải tiếng kêu về thắng trận, cũng chẳng phải tiếng kêu về thua trận; nhưng ta nghe tiếng kẻ hát."));
visuperobject.biblemap.put("b2_32_19", new visuperobject(2,32,19,"Khi đến gần trại quân, Môi-se thấy bò con và sự nhảy múa, bèn nổi giận, liệng hai bảng chứng khỏi tay mịnh, bể ra nơi chân núi;"));
visuperobject.biblemap.put("b2_32_20", new visuperobject(2,32,20,"đoạn lấy bò con của chúng đã đúc đem đốt trong lửa, rồi nghiền cho đến thành ra bụi, rải trên mặt nước, và cho dân Y-sơ-ra-ên uống."));
visuperobject.biblemap.put("b2_32_21", new visuperobject(2,32,21,"Môi-se bèn nói cùng A-rôn rằng: Dân nầy làm chi anh, mà anh xui cho chúng phạm tội nặng dường ấy?"));
visuperobject.biblemap.put("b2_32_22", new visuperobject(2,32,22,"A-rôn đáp rằng: Xin chúa tôi đừng nổi giận, chúa biết rằng dân nầy chuyên làm điều ác!"));
visuperobject.biblemap.put("b2_32_23", new visuperobject(2,32,23,"Họ có nói cùng tôi rằng: Hãy làm các thần đi trước chúng tôi; vì về phần Môi-se nầy, là người đã dẫn chúng tôi ra khỏi xứ Ê-díp-tô, chúng tôi chẳng biết có điều chi xảy đến cho người rồi."));
visuperobject.biblemap.put("b2_32_24", new visuperobject(2,32,24,"Tôi bèn nói cùng chúng rằng: Ai có vàng hãy lột ra! Họ bèn đưa cho tôi, tôi bỏ vào lửa, và bởi đó thành ra bò con nầy."));
visuperobject.biblemap.put("b2_32_25", new visuperobject(2,32,25,"Vả, Môi-se thấy dân sự buông lung, vì A-rôn để họ buông lung, đến đỗi bị sỉ nhục trong vòng các thù nghịch,"));
visuperobject.biblemap.put("b2_32_26", new visuperobject(2,32,26,"thì người đứng nơi cửa trại quân mà nói rằng: Ai thuộc về Ðức Giê-hô-va, hãy đến cùng ta đây! Hết thảy người Lê-vi đều nhóm lại gần bên người."));
visuperobject.biblemap.put("b2_32_27", new visuperobject(2,32,27,"Người truyền cho họ rằng: Giê-hô-va, là Ðức Chúa Trời của Y-sơ-ra-ên, có phán như vầy: Mỗi người trong các ngươi hãy đeo gươm bên mình, đi qua đi lại trong trại quân, từ cửa nầy đến cửa kia, và mỗi người hãy giết anh em, bạn hữu, và kẻ lân cận mình."));
visuperobject.biblemap.put("b2_32_28", new visuperobject(2,32,28,"Dân Lê-vi bèn làm y như lời Môi-se; trong ngày đó có chừng ba ngàn người bị chết."));
visuperobject.biblemap.put("b2_32_29", new visuperobject(2,32,29,"Vả, Môi-se đã truyền rằng: Ngày nay mỗi người trong vòng các ngươi hãy dâng tay mình cho Ðức Giê-hô-va, chẳng sá chi đến con trai hay là anh em mình, hầu cho các ngươi được ơn phước vậy."));
visuperobject.biblemap.put("b2_32_30", new visuperobject(2,32,30,"Ngày mai, Môi-se nói cùng dân sự rằng: Các ngươi đã phạm một tội rất trọng; song bây giờ ta lên đến Ðức Giê-hô-va, có lẽ ta sẽ được chuộc tội các ngươi chăng."));
visuperobject.biblemap.put("b2_32_31", new visuperobject(2,32,31,"Vậy, Môi-se trở lên đến Ðức Giê-hô-va mà thưa rằng: Ôi! dân sự nầy có phạm một tội trọng, làm cho mình các thần bằng vàng;"));
visuperobject.biblemap.put("b2_32_32", new visuperobject(2,32,32,"nhưng bây giờ xin Chúa tha tội cho họ! Bằng không, hãy xóa tên tôi khỏi sách Ngài đã chép đi."));
visuperobject.biblemap.put("b2_32_33", new visuperobject(2,32,33,"Ðức Giê-hô-va phán cùng Môi-se rằng: Kẻ nào phạm tội cùng ta, ta sẽ xóa nó khỏi sách ta."));
visuperobject.biblemap.put("b2_32_34", new visuperobject(2,32,34,"Bây giờ, hãy đi, dẫn dân sự đến nơi ta đã chỉ phán. Nầy thiên sứ ta sẽ đi trước ngươi; nhưng ngày nào ta hình phạt thì sẽ phạt tội chúng nó."));
visuperobject.biblemap.put("b2_32_35", new visuperobject(2,32,35,"Ðức Giê-hô-va hành phạt dân sự là vậy, vì dân sự là gốc phạm về tượng bò vàng mà A-rôn đã làm ra."));
visuperobject.biblemap.put("b2_33_1", new visuperobject(2,33,1,"Ðức Giê-hô-va phán cùng Môi-se rằng: Nầy, ngươi cùng dân sự mà ngươi đã dẫn ra khỏi xứ Ê-díp-tô hãy từ đây đi lên xứ ta đã thề ban cho Áp-ra-ham, Y-sác, và Gia-cốp, rằng: Ta sẽ ban xứ đó cho dòng dõi ngươi."));
visuperobject.biblemap.put("b2_33_2", new visuperobject(2,33,2,"Ta sẽ sai một thiên sứ đi trước ngươi, và sẽ đuổi dân Ca-na-an, dân A-mô-rít, dân Hê-tít, dân Phê-rê-sít, dân Hê-vít, và dân Giê-bu-sít,"));
visuperobject.biblemap.put("b2_33_3", new visuperobject(2,33,3,"đặng đưa các ngươi vào xứ đượm sữa và mật; nhưng ta không cùng lên với ngươi đâu, vì ngươi là dân cứng cổ, e ta diệt ngươi dọc đường chăng."));
visuperobject.biblemap.put("b2_33_4", new visuperobject(2,33,4,"Khi dân sự nghe lời hăm nầy, bèn đều để tang, không ai đeo đồ trang sức hết."));
visuperobject.biblemap.put("b2_33_5", new visuperobject(2,33,5,"Vì Ðức Giê-hô-va đã phán cùng Môi-se rằng: Hãy nói cùng dân Y-sơ-ra-ên: Các ngươi là dân cứng cổ, nếu ta cùng lên với các ngươi chỉ trong một lúc, thì ta sẽ diệt các ngươi! Vậy, bây giờ, hãy cất đồ trang sức trong mình ngươi đi, đặng ta biết liệu đãi ngươi cách nào."));
visuperobject.biblemap.put("b2_33_6", new visuperobject(2,33,6,"Thế thì, từ núi Hô-rếp, dân Y-sơ-ra-ên đã lột các đồ trang sức mình."));
visuperobject.biblemap.put("b2_33_7", new visuperobject(2,33,7,"Môi-se lấy Trại đem dựng xa ra ngoài trại quân, gọi là hội mạc, phàm ai muốn cầu khẩn Ðức Giê-hô-va, thì ra đến hội mạc ở ngoài trại quân."));
visuperobject.biblemap.put("b2_33_8", new visuperobject(2,33,8,"Vừa khi Môi-se ra đến Trại, thì cả dân sự chổi dậy, mỗi người đứng nơi cửa trại mình, ngó theo Môi-se cho đến khi nào người vào trong Trại rồi."));
visuperobject.biblemap.put("b2_33_9", new visuperobject(2,33,9,"Vừa khi người vào đó, thì trụ mây giáng xuống dừng tại cửa Trại, và Ðức Giê-hô-va phán cùng Môi-se."));
visuperobject.biblemap.put("b2_33_10", new visuperobject(2,33,10,"Cả dân sự thấy trụ mây dừng tại cửa Trại, bèn đứng dậy, rồi mỗi người đều sấp mình xuống nơi cửa trại mình."));
visuperobject.biblemap.put("b2_33_11", new visuperobject(2,33,11,"Ðức Giê-hô-va đối diện phán cùng Môi-se, như một người nói chuyện cùng bạn hữu mình. Ðoạn, Môi-se trở về trại quân, còn kẻ hầu trẻ của người, tên là Giô-suê, con trai của Nun, không ra khỏi Trại."));
visuperobject.biblemap.put("b2_33_12", new visuperobject(2,33,12,"Môi-se thưa cùng Ðức Giê-hô-va rằng: Nầy, Chúa phán cùng tôi rằng: Hãy đem dân sự nầy lên! Song Chúa chẳng cho tôi biết Chúa sai ai đi cùng tôi. Vả, Chúa có phán rằng: Ta biết ngươi vì danh ngươi, và ngươi được ơn trước mặt ta."));
visuperobject.biblemap.put("b2_33_13", new visuperobject(2,33,13,"Vậy bây giờ, nếu tôi được ơn trước mặt Chúa, xin cho tôi biết đường của Chúa, để cho tôi biết Chúa và được ơn trước mặt Ngài. Xin cũng hãy nghĩ rằng dân nầy là dân của Ngài!"));
visuperobject.biblemap.put("b2_33_14", new visuperobject(2,33,14,"Ðức Giê-hô-va đáp rằng: Chính mình ta sẽ đi cùng ngươi, và ta sẽ cho ngươi an nghỉ."));
visuperobject.biblemap.put("b2_33_15", new visuperobject(2,33,15,"Môi-se thưa rằng: Nếu chính mình Ngài chẳng đi, xin đừng đem chúng tôi lên khỏi đây."));
visuperobject.biblemap.put("b2_33_16", new visuperobject(2,33,16,"Lấy cớ chi mà người ta sẽ biết rằng tôi cùng dân sự Ngài được ơn trước mặt Ngài? Có phải khi nào Ngài cùng đi với chúng tôi chăng? Thế thì, tôi cùng dân sự Ngài sẽ được phân biệt với muôn dân trên mặt đất."));
visuperobject.biblemap.put("b2_33_17", new visuperobject(2,33,17,"Ðức Giê-hô-va phán cùng Môi-se rằng: Ta sẽ làm điều ngươi cầu xin ta, vì ngươi được ơn trước mặt ta, và ta biết ngươi bởi danh ngươi vậy."));
visuperobject.biblemap.put("b2_33_18", new visuperobject(2,33,18,"Môi-se thưa rằng: Tôi xin Ngài cho tôi xem sự vinh hiển của Ngài!"));
visuperobject.biblemap.put("b2_33_19", new visuperobject(2,33,19,"Ðức Giê-hô-va phán ràng: Ta sẽ làm cho các sự nhân từ ta phát ra trước mặt ngươi; ta hô danh Giê-hô-va trước mặt ngươi; làm ơn cho ai ta muốn làm ơn, và thương xót ai ta muốn thương xót."));
visuperobject.biblemap.put("b2_33_20", new visuperobject(2,33,20,"Ngài lại phán rằng: Ngươi sẽ chẳng thấy được mặt ta, vì không ai thấy mặt ta mà còn sống."));
visuperobject.biblemap.put("b2_33_21", new visuperobject(2,33,21,"Ðức Giê-hô-va lại phán: Ðây có một chỗ gần ta, ngươi hãy đứng trên hòn đá;"));
visuperobject.biblemap.put("b2_33_22", new visuperobject(2,33,22,"khi sự vinh hiển ta đi ngang qua, ta sẽ để ngươi trong bộng đá, lấy tay ta che ngươi, cho đến chừng nào ta đi qua rồi."));
visuperobject.biblemap.put("b2_33_23", new visuperobject(2,33,23,"Ta sẽ rút tay lại, và ngươi thấy phía sau ta; nhưng thấy mặt ta chẳng được."));
visuperobject.biblemap.put("b2_34_1", new visuperobject(2,34,1,"Ðức Giê-hô-va phán cùng Môi-se rằng: Hãy đục hai bảng đá như hai bảng trước; rồi ta sẽ viết trên hai bảng nầy các lời đã ở nơi hai bảng kia, mà ngươi đã làm bể."));
visuperobject.biblemap.put("b2_34_2", new visuperobject(2,34,2,"Ngày mai, hãy chực cho sẵn, vừa sáng lên trên đỉnh núi Si-na -i, đứng trước mặt ta tại đó."));
visuperobject.biblemap.put("b2_34_3", new visuperobject(2,34,3,"Không ai nên lên theo cùng ngươi hết, khắp núi không nên thấy có người, và chiên, bò cũng chẳng nên ăn cỏ nơi núi nầy nữa."));
visuperobject.biblemap.put("b2_34_4", new visuperobject(2,34,4,"Môi-se bèn đục hai bảng đá, y như hai bảng trước; dậy sớm, cầm theo tay hai bảng đó lên trên núi Si-na -i, y như lời Ðức Giê-hô-va đã phán dặn."));
visuperobject.biblemap.put("b2_34_5", new visuperobject(2,34,5,"Ðức Giê-hô-va ngự xuống trong đám mây, đứng gần bên Môi-se và hô danh Giê-hô-va."));
visuperobject.biblemap.put("b2_34_6", new visuperobject(2,34,6,"Ngài đi ngang qua mặt người, hô rằng: Giê-hô-va! Giê-hô-va! là Ðức Chúa Trời nhân từ, thương xót, chậm giận, đầy dẫy ân huệ và thành thực,"));
visuperobject.biblemap.put("b2_34_7", new visuperobject(2,34,7,"ban ơn đến ngàn đời, xá điều gian ác, tội trọng, và tội lỗi; nhưng chẳng kể kẻ có tội là vô tội, và nhơn tội tổ phụ phạt đến con cháu trải ba bốn đời."));
visuperobject.biblemap.put("b2_34_8", new visuperobject(2,34,8,"Môi-se lật đật cúi đầu xuống đất và thờ lạy, mà thưa rằng:"));
visuperobject.biblemap.put("b2_34_9", new visuperobject(2,34,9,"Lạy Chúa! Nếu tôi được ơn trước mặt Chúa, xin hãy đi giữa chúng tôi, vì dân nầy là một dân cứng cổ. Xin hãy tha sự gian ác cùng tội lỗi, và nhận chúng tôi làm cơ nghiệp Chúa."));
visuperobject.biblemap.put("b2_34_10", new visuperobject(2,34,10,"Ðức Giê-hô-va phán rằng: Nầy, ta lập một giao ước. Trước mặt các dân sự của ngươi, ta sẽ làm các phép lạ chưa hề có làm trên cả mặt đất, hay là nơi dân nào; và toàn dân sự mà trong đó có ngươi, sẽ xem thấy việc Ðức Giê-hô, vì điều ta sẽ làm cùng ngươi là một điều đáng kinh khiếp."));
visuperobject.biblemap.put("b2_34_11", new visuperobject(2,34,11,"Hãy cẩn thận về điều ta truyền cho ngươi hôm nay. Nầy, ta sẽ đuổi khỏi trước mặt ngươi dân A-mô-rít, dân Ca-na-an, dân Hê-tít, dân Phê-rê-sít, dân Hê-vít, và dân Giê-bu-sít."));
visuperobject.biblemap.put("b2_34_12", new visuperobject(2,34,12,"Hãy cẩn thận đừng lập giao ước cùng dân của xứ mà ngươi sẽ vào, e chúng nó sẽ thành cái bẫy giữa ngươi chăng."));
visuperobject.biblemap.put("b2_34_13", new visuperobject(2,34,13,"Nhưng các ngươi hãy phá hủy bàn thờ, đập bể pho tượng và đánh hạ các thần chúng nó."));
visuperobject.biblemap.put("b2_34_14", new visuperobject(2,34,14,"Vì ngươi đừng sấp mình xuống trước mặt Chúa nào khác, bởi Ðức Giê-hô-va tự xưng là Ðức Chúa Trời kỵ tà; Ngài thật là một Ðức Chúa Trời kỵ tà vậy."));
visuperobject.biblemap.put("b2_34_15", new visuperobject(2,34,15,"Hãy cẩn thận đừng lập giao ước cùng dân của xứ đó, e khi chúng nó hành dâm cùng các tà thần chúng nó và tế các tà thần của chúng nó, có kẻ mời, rồi ngươi ăn của chúng họ chăng."));
visuperobject.biblemap.put("b2_34_16", new visuperobject(2,34,16,"Lại đừng cưới con gái chúng nó cho con trai ngươi, e khi con gái chúng nó hành dâm với các tà thần chúng nó, quyến dụ con trai ngươi cũng hành dâm cùng các tà thần của chúng nó nữa chăng."));
visuperobject.biblemap.put("b2_34_17", new visuperobject(2,34,17,"Ngươi chớ đúc thần tượng."));
visuperobject.biblemap.put("b2_34_18", new visuperobject(2,34,18,"Ngươi hãy giữ lễ bánh không men. Nhằm kỳ tháng lúa trỗ, hãy ăn bánh không men trong bảy ngày, như ta đã truyền dặn ngươi; vì nhằm tháng đó ngươi ra khỏi xứ Ê-díp-tô."));
visuperobject.biblemap.put("b2_34_19", new visuperobject(2,34,19,"Các con trưởng nam đều thuộc về ta; cùng các con đực đầu lòng của bầy súc vật ngươi, hoặc bò hoặc chiên cũng vậy."));
visuperobject.biblemap.put("b2_34_20", new visuperobject(2,34,20,"Nhưng ngươi sẽ bắt một chiên con hay là dê con mà chuộc một con lừa đầu lòng. Nếu chẳng chuộc nó, ngươi hãy bẻ cổ nó. Ngươi sẽ chuộc các con trưởng nam mình; và chớ ai đi tay không đến chầu trước mặt ta."));
visuperobject.biblemap.put("b2_34_21", new visuperobject(2,34,21,"Trong sáu ngày ngươi hãy làm công việc, nhưng qua ngày thứ bảy hãy nghỉ, dẫu mùa cày hay mùa gặt ngươi cũng phải nghỉ vậy."));
visuperobject.biblemap.put("b2_34_22", new visuperobject(2,34,22,"Ðầu mùa gặt lúa mì, ngươi hãy giữ lễ của các tuần lễ; và cuối năm giữ lễ mùa màng."));
visuperobject.biblemap.put("b2_34_23", new visuperobject(2,34,23,"Thường năm ba lần, trong vòng các ngươi, mỗi người nam sẽ ra mắt Chúa Giê-hô-va, là Ðức Chúa Trời của Y-sơ-ra-ên."));
visuperobject.biblemap.put("b2_34_24", new visuperobject(2,34,24,"Vì ta sẽ đuổi các dân khỏi trước mặt ngươi, ta sẽ mở rộng bờ cõi ngươi; và trong khi ngươi lên ra mắt Giê-hô-va Ðức Chúa Trời ngươi, mỗi năm ba lần, thì sẽ chẳng ai tham muốn xứ ngươi hết."));
visuperobject.biblemap.put("b2_34_25", new visuperobject(2,34,25,"Ngươi chớ dâng huyết của con sinh tế ta cùng bánh pha men; thịt của con sinh về lễ Vượt-qua chớ nên giữ đến sáng mai."));
visuperobject.biblemap.put("b2_34_26", new visuperobject(2,34,26,"Ngươi sẽ đem dâng hoa quả đầu mùa của đất sanh sản vào đến Giê-hô-va, là Ðức Chúa Trời ngươi. Ngươi chớ nấu thịt dê con trong sữa mẹ nó."));
visuperobject.biblemap.put("b2_34_27", new visuperobject(2,34,27,"Ðức Giê-hô-va cũng phán cùng Môi-se rằng: Hãy chép các lời nầy; vì theo các lời nầy mà ta lập giao ước cùng ngươi và cùng Y-sơ-ra-ên."));
visuperobject.biblemap.put("b2_34_28", new visuperobject(2,34,28,"Môi-se ở đó cùng Ðức Giê-hô-va trong bốn mươi ngày và bốn mười đêm, không ăn bánh, cũng không uống nước; Ðức Giê-hô-va chép trên hai bảng đá các lời giao ước, tức là mười điều răn."));
visuperobject.biblemap.put("b2_34_29", new visuperobject(2,34,29,"Khi Môi-se tay cầm hai bảng chứng đi xuống núi Si-na -i, chẳng biết rằng da mặt mình sáng rực bởi vì mình hầu chuyện Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_34_30", new visuperobject(2,34,30,"Nhưng A-rôn cùng cả dân Y-sơ-ra-ên nhìn Môi-se thấy mặt người sáng rực, sợ không dám lại gần."));
visuperobject.biblemap.put("b2_34_31", new visuperobject(2,34,31,"Môi-se bèn gọi họ, A-rôn cùng các hội trưởng đến gần; rồi nói chuyện cùng họ."));
visuperobject.biblemap.put("b2_34_32", new visuperobject(2,34,32,"Kế sau, cả dân Y-sơ-ra-ên đến gần, người truyền dặn các điều của Ðức Giê-hô-va đã phán dặn mình nơi núi Si-na -i."));
visuperobject.biblemap.put("b2_34_33", new visuperobject(2,34,33,"Môi-se thôi nói chuyện cùng họ, liền lấy lúp che mặt mình lại."));
visuperobject.biblemap.put("b2_34_34", new visuperobject(2,34,34,"Khi Môi-se vào trước mặt Ðức Giê-hô-va đặng hầu chuyện Ngài, thì dở lúp lên cho đến chừng nào lui ra; đoạn ra nói lại cùng dân Y-sơ-ra-ên mọi lời Ngài đã phán dặn mình."));
visuperobject.biblemap.put("b2_34_35", new visuperobject(2,34,35,"Dân Y-sơ-ra-ên nhìn mặt Môi-se thấy da mặt người sáng rực, thì Môi-se lấy lúp che mặt mình cho đến khi nào vào hầu chuyện Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_35_1", new visuperobject(2,35,1,"Môi-se nhóm hết hội chúng Y-sơ-ra-ên lại mà nói rằng: Ðây là các việc của Ðức Giê-hô-va đã phán dạy làm:"));
visuperobject.biblemap.put("b2_35_2", new visuperobject(2,35,2,"Người ta sẽ làm công việc trong sáu ngày, nhưng đến ngày thứ bảy sẽ là một lễ thánh, tức là ngày sa-bát biệt riêng ra thánh cho Ðức Giê-hô-va. Ai làm công việc trong ngày đó sẽ bị xử tử."));
visuperobject.biblemap.put("b2_35_3", new visuperobject(2,35,3,"Nhằm ngày sa-bát chớ nổi lửa trong nhà nào của các ngươi hết."));
visuperobject.biblemap.put("b2_35_4", new visuperobject(2,35,4,"Môi-se nói cùng cả hội chúng Y-sơ-ra-ên rằng: Ðây là các lời Ðức Giê-hô-va đã phán dặn:"));
visuperobject.biblemap.put("b2_35_5", new visuperobject(2,35,5,"Hãy lấy một lễ vật chi ở nhà các ngươi mà dâng cho Ðức Giê-hô-va. Hễ người nào có lòng thành dâng cho, hãy đem lễ vật cho Ðức Giê-hô-va: vàng, bạc và đồng;"));
visuperobject.biblemap.put("b2_35_6", new visuperobject(2,35,6,"chỉ tím, đỏ điều, đỏ sặm, vải gai mịn, lông dê,"));
visuperobject.biblemap.put("b2_35_7", new visuperobject(2,35,7,"da chiên đực nhuộm đỏ, da cá nược, cây si-tim,"));
visuperobject.biblemap.put("b2_35_8", new visuperobject(2,35,8,"dầu thắp, các thứ hương liệu đặng chế dầu xức và hương thơm,"));
visuperobject.biblemap.put("b2_35_9", new visuperobject(2,35,9,"bích ngọc, và các thứ ngọc khác để dùng khảm cho ê-phót và bảng đeo ngực."));
visuperobject.biblemap.put("b2_35_10", new visuperobject(2,35,10,"Trong vòng các ngươi mấy người khôn ngoan hơn hết hãy đến làm mọi điều Ðức Giê-hô-va đã phán dặn;"));
visuperobject.biblemap.put("b2_35_11", new visuperobject(2,35,11,"đền tạm, trại và bong của đền tạm, móc, ván, xà ngang, trụ và lỗ trụ;"));
visuperobject.biblemap.put("b2_35_12", new visuperobject(2,35,12,"hòm bảng chứng và đòn khiêng, nắp thi ân cùng màn che nơi chí thánh;"));
visuperobject.biblemap.put("b2_35_13", new visuperobject(2,35,13,"cài bàn và đòn khiêng, các đồ phụ tùng của bàn cùng bánh trần thiết;"));
visuperobject.biblemap.put("b2_35_14", new visuperobject(2,35,14,"chân đèn, đồ phụ tùng, các đèn và dầu thắp đèn;"));
visuperobject.biblemap.put("b2_35_15", new visuperobject(2,35,15,"bàn thờ xông hương cùng đòn khiêng; dầu xức, hương liệu, và bức màn cửa đền tạm;"));
visuperobject.biblemap.put("b2_35_16", new visuperobject(2,35,16,"bàn thờ dâng của lễ thiêu và rá đồng; đòn khiêng và các đồ phụ tùng của bàn thờ; cái thùng và chân thùng;"));
visuperobject.biblemap.put("b2_35_17", new visuperobject(2,35,17,"bố vi che hành lang, trụ, lỗ trụ, và bức màn của cửa hành lang;"));
visuperobject.biblemap.put("b2_35_18", new visuperobject(2,35,18,"các nọc của đền tạm, cùng nọc và dây của hành lang;"));
visuperobject.biblemap.put("b2_35_19", new visuperobject(2,35,19,"bộ áo lễ dùng về công việc trong nơi thánh; bộ áo thánh cho A-rôn, thầy tế lễ cả, và bộ áo cho các con trai người đặng làm chức tế lễ."));
visuperobject.biblemap.put("b2_35_20", new visuperobject(2,35,20,"Cả hội chúng Y-sơ-ra-ên bèn lui ra khỏi mặt Môi-se."));
visuperobject.biblemap.put("b2_35_21", new visuperobject(2,35,21,"Mọi người có lòng cảm động, và mọi người có lòng thành, đều đem lễ vật đến dâng cho Ðức Giê-hô-va, để làm công việc hội mạc, các đồ phụ tùng và bộ áo thánh."));
visuperobject.biblemap.put("b2_35_22", new visuperobject(2,35,22,"Phàm người nam cùng nữ, tức mọi kẻ có lòng thành, đều đến đem những hoa tai, nhẫn, khâu, kiềng, các thứ trang sức bằng vàng và hết thảy đều dâng lễ vật bằng vàng cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b2_35_23", new visuperobject(2,35,23,"Kẻ nào ở nhà mình có chỉ tím, đỏ điều, đỏ sặm, vải gai mịn, lông dê, da chiên đực nhuộm đỏ, và da cá nược đều đem đến."));
visuperobject.biblemap.put("b2_35_24", new visuperobject(2,35,24,"Hễ ai có chi làm lễ vật bằng bạc hay bằng đồng, đều đem dâng cho Ðức Giê-hô-va, và hễ ai có cây si-tim nơi nhà mình dùng hiệp về các công việc tế lễ, đều đem đến."));
visuperobject.biblemap.put("b2_35_25", new visuperobject(2,35,25,"Phàm người đàn bà khéo thì chánh tay mình kéo chỉ lấy và đem đến món chi họ đã kéo rồi, chỉ tím, đỏ điều, đỏ sặm, và chỉ gai mịn."));
visuperobject.biblemap.put("b2_35_26", new visuperobject(2,35,26,"Còn mấy người đàn bà có cảm động và tài khéo thì kéo chỉ lông dê."));
visuperobject.biblemap.put("b2_35_27", new visuperobject(2,35,27,"Các bực tôn trưởng trong dân sự đem bích ngọc và các thứ ngọc khác để khảm cho ê-phót và bảng đeo ngực;"));
visuperobject.biblemap.put("b2_35_28", new visuperobject(2,35,28,"dầu thắp đèn, các hương liệu để chế dầu xức và dầu thơm."));
visuperobject.biblemap.put("b2_35_29", new visuperobject(2,35,29,"Cả dân Y-sơ-ra-ên, nam hay nữ, phàm ai có lòng cảm động xui cho mình tình nguyện quyên vào các công việc Ðức Giê-hô-va đã phán dặn nơi Môi-se, đều đem dâng cho Ðức Giê-hô-va các lễ tình nguyện vậy."));
visuperobject.biblemap.put("b2_35_30", new visuperobject(2,35,30,"Môi-se nói cùng dân Y-sơ-ra-ên rằng: Nầy Ðức Giê-hô-va đã kêu danh Bết-sa-lê-ên, con trai của U-ri, cháu của Hu-rơ, trong chi phái Giu-đa."));
visuperobject.biblemap.put("b2_35_31", new visuperobject(2,35,31,"Ngài phú cho người đầy dẫy Thần của Ðức Chúa Trời, sự khôn ngoan, sự thông sáng, và sự hiểu biết, để làm mọi thứ nghề thợ,"));
visuperobject.biblemap.put("b2_35_32", new visuperobject(2,35,32,"đặng bày ra và làm đồ vàng, bạc và đồng,"));
visuperobject.biblemap.put("b2_35_33", new visuperobject(2,35,33,"đặng khắc và khảm các thứ ngọc, đặng chạm cây làm các đồ khéo léo."));
visuperobject.biblemap.put("b2_35_34", new visuperobject(2,35,34,"Ngài lại phú cho người tài dạy dỗ, và cũng đồng ban cho Ô-hô-li-áp, con của A-hi-sa-mạc, trong chi phái Ðan nữa;"));
visuperobject.biblemap.put("b2_35_35", new visuperobject(2,35,35,"Ngài phú cho hai người đó đầy sự khôn ngoan đặng làm các công nghệ về thợ thêu, thợ dệt nhiều màu, tức là màu tím, đỏ điều, đỏ sặm, và vải gai mịn; để bày ra và làm các thứ công nghệ khéo."));
visuperobject.biblemap.put("b2_36_1", new visuperobject(2,36,1,"Bết-sa-lê-ên, Ô-hô-li-áp và các người khôn khéo, tức là người Ðức Giê-hô-va đã phú cho sự khôn ngoan và sự thông sáng đặng làm các công việc định dùng về việc tế lễ nơi thánh, đều làm mọi việc mà Ðức Giê-hô-va đã phán dặn."));
visuperobject.biblemap.put("b2_36_2", new visuperobject(2,36,2,"Vậy, Môi-se bèn gọi Bết-sa-lê-ên, Ô-hô-li-áp, và các người khôn khéo mà trong lòng họ Ðức Giê-hô-va phú sự thông sáng cùng các người có lòng cảm động xui mình đến gần, đặng làm công việc."));
visuperobject.biblemap.put("b2_36_3", new visuperobject(2,36,3,"Trước mặt Môi-se, họ thâu các lễ vật của dân Y-sơ-ra-ên đã đem đến, để làm các công việc định dùng và sự tế lễ nơi thánh. Nhưng mỗi buổi sớm mai, dân sự lại đem đến lễ vật tình nguyện nữa."));
visuperobject.biblemap.put("b2_36_4", new visuperobject(2,36,4,"Thế thì, các người khôn khéo làm mọi công việc của nơi thánh, đều tạm đình,"));
visuperobject.biblemap.put("b2_36_5", new visuperobject(2,36,5,"đến nỗi cùng Môi-se rằng: Dân sự đem đến dư bội phần đặng làm các công việc mà Ðức Giê-hô-va đã phán dặn."));
visuperobject.biblemap.put("b2_36_6", new visuperobject(2,36,6,"Theo lịnh truyền của Môi-se, họ bèn đi rao từ trại quân rằng: Bất kỳ người nam hay nữ, chớ làm công việc về lễ vật của nơi thánh nữa! Vậy họ cấm dân sự không cho đem đến chi thêm nữa hết."));
visuperobject.biblemap.put("b2_36_7", new visuperobject(2,36,7,"Vì đã đủ các vật liệu đặng làm hết thảy công việc, cho đến đỗi còn dư lại nữa."));
visuperobject.biblemap.put("b2_36_8", new visuperobject(2,36,8,"Các người khôn khéo trong vòng những người làm công việc, dùng mười bức màn bằng vải gai đậu mịn, chỉ tím, đỏ điều, đỏ sặm, thêu hình chê-ru-bin cực xảo, mà dựng đền tạm."));
visuperobject.biblemap.put("b2_36_9", new visuperobject(2,36,9,"Mỗi bức màn đều dài hai mươi tám thước, rộng bốn thước; các bức đều đồng cỡ nhau."));
visuperobject.biblemap.put("b2_36_10", new visuperobject(2,36,10,"Họ kết mỗi năm bức màn dính lại nhau;"));
visuperobject.biblemap.put("b2_36_11", new visuperobject(2,36,11,"thắt vòng bằng chỉ tím nơi triêng của bức màn thứ nhất, ở đầu bức nguyên; và cũng làm như vậy cho triêng của bức màn ở nơi chót trong bức nguyên thứ nhì."));
visuperobject.biblemap.put("b2_36_12", new visuperobject(2,36,12,"Họ thắt năm chục vòng theo triêng của bức màn ở đầu của bức nguyên thứ nhất, và năm chục vòng theo triêng bức màn ở chót của bức nguyên thứ nhì; các vòng đối nhau."));
visuperobject.biblemap.put("b2_36_13", new visuperobject(2,36,13,"Ðoạn chế năm chục cái móc bằng vàng, móc bức nầy với bức kia, để cho đền tạm kết lại thành một."));
visuperobject.biblemap.put("b2_36_14", new visuperobject(2,36,14,"Kế đó, họ dùng lông dê kết mười một bức màn, để làm bong trên đền tạm."));
visuperobject.biblemap.put("b2_36_15", new visuperobject(2,36,15,"Bề dài mỗi bức màn ba mươi thước, bề rộng bốn thước: mười một bức màn đều đồng cỡ nhau."));
visuperobject.biblemap.put("b2_36_16", new visuperobject(2,36,16,"Họ kết năm bức màn riêng ra; và sáu bức màn khác riêng ra;"));
visuperobject.biblemap.put("b2_36_17", new visuperobject(2,36,17,"thắt năm chục cái vòng nơi triêng bức chót của bức nguyên thứ nhất, và năm chục vòng nơi triêng bức ở đầu của bức nguyên thứ nhì;"));
visuperobject.biblemap.put("b2_36_18", new visuperobject(2,36,18,"làm năm chục cái móc bằng đồng, móc bức bong hiệp lại thành một."));
visuperobject.biblemap.put("b2_36_19", new visuperobject(2,36,19,"Họ cũng làm cho đền tạm một tấm lá phủ bằng da chiên đực nhuộm đỏ, và một tấm là phủ bằng da cá nược đắp lên trên."));
visuperobject.biblemap.put("b2_36_20", new visuperobject(2,36,20,"Họ dùng ván bằng cây si-tim làm vách cho đền tạm."));
visuperobject.biblemap.put("b2_36_21", new visuperobject(2,36,21,"Mỗi tấm ván mười thước về dài, một thước rưỡi bề ngang."));
visuperobject.biblemap.put("b2_36_22", new visuperobject(2,36,22,"Mỗi tấm có hai cái mộng liên nhau: cả ván đền tạm đều làm một cách."));
visuperobject.biblemap.put("b2_36_23", new visuperobject(2,36,23,"Vậy, họ làm ván cho đền tạm: hai chục tấm về phía nam;"));
visuperobject.biblemap.put("b2_36_24", new visuperobject(2,36,24,"dưới hai chục tấm làm bốn chục lỗ mộng bằng bạc: dưới mỗi tấm có hai lỗ mộng, để chịu hai cái mộng."));
visuperobject.biblemap.put("b2_36_25", new visuperobject(2,36,25,"Họ cũng làm hai chục tấm ván ở phía bắc đền tạm,"));
visuperobject.biblemap.put("b2_36_26", new visuperobject(2,36,26,"và bốn chục lỗ mộng bằng bạc; dưới mỗi tấm có hai lỗ mộng."));
visuperobject.biblemap.put("b2_36_27", new visuperobject(2,36,27,"Về phía đằng sau của đền tạm, tức là phía tây, họ làm sáu tấm ván,"));
visuperobject.biblemap.put("b2_36_28", new visuperobject(2,36,28,"và về hai góc sau thì làm hai tấm ván."));
visuperobject.biblemap.put("b2_36_29", new visuperobject(2,36,29,"Hai tấm ván góc ra khít với nhau từ dưới chí trên bởi một cái khoen ở nơi đầu; hai góc hai bên đều làm như vậy."));
visuperobject.biblemap.put("b2_36_30", new visuperobject(2,36,30,"Thế thì, có tám tấm ván và mười sáu lỗ mộng bằng bạc, dưới mỗi tấm có hai lỗ mộng."));
visuperobject.biblemap.put("b2_36_31", new visuperobject(2,36,31,"Họ làm năm cây xà ngang bằng gỗ si-tim cặp mấy tấm ván về phía bên nầy của đền tạm,"));
visuperobject.biblemap.put("b2_36_32", new visuperobject(2,36,32,"năm cây xà ngang khác về phía bên kia, và năm cây xà ngang khác nữa về phía sau đền tạm, tức là phía tây."));
visuperobject.biblemap.put("b2_36_33", new visuperobject(2,36,33,"Họ làm cây xà ngang giữa, đặng lòn qua thân giữa các tấm ván từ đầu nầy đến đầu kia,"));
visuperobject.biblemap.put("b2_36_34", new visuperobject(2,36,34,"và bọc vàng mấy tấm ván. Họ làm các khoen của ván bằng vàng, đặng xỏ xà ngang qua, và bọc vàng các cây xà ngang."));
visuperobject.biblemap.put("b2_36_35", new visuperobject(2,36,35,"Họ lại chế một bức màn bằng chỉ tím, đỏ điều, đỏ sặm, và vải gai đậu mịn, thêu hình chê-ru-bin cực xảo,"));
visuperobject.biblemap.put("b2_36_36", new visuperobject(2,36,36,"rồi làm bốn cây trụ bằng gỗ si-tim, bọc vàng, cùng đinh bằng vàng; đúc bốn lỗ trụ bằng bạc cho mấy cây trụ đó."));
visuperobject.biblemap.put("b2_36_37", new visuperobject(2,36,37,"Họ cũng chế cho cửa vào Trại một tấm màn bằng chỉ tím, đỏ điều, đỏ sặm, và vải gai đậu mịn: ấy là một công việc thêu thùa."));
visuperobject.biblemap.put("b2_36_38", new visuperobject(2,36,38,"Họ cũng làm năm cây trụ cùng đinh, rồi bọc vàng trên đầu trụ, và các cây nuông; còn năm lỗ trụ thì bằng đồng."));
visuperobject.biblemap.put("b2_37_1", new visuperobject(2,37,1,"Ðoạn, Bết-sa-lê-ên đóng hòm bằng cây si-tim, bề dài hai thước rưỡi, bề ngang một thước rưỡi, và bề cao một thước rưỡi."));
visuperobject.biblemap.put("b2_37_2", new visuperobject(2,37,2,"Người bọc vàng ròng bề trong và bề ngoài, cùng chạy đường viền chung quanh."));
visuperobject.biblemap.put("b2_37_3", new visuperobject(2,37,3,"Ðúc bốn cái khoen vàng đặng gắn bốn góc: hai cái bên hông nầy, hai cái bên hông kia."));
visuperobject.biblemap.put("b2_37_4", new visuperobject(2,37,4,"Người cũng chuốt các đòn bằng cây si-tim, bọc vàng;"));
visuperobject.biblemap.put("b2_37_5", new visuperobject(2,37,5,"rồi xỏ đòn đó vào khoen hai bên hông, đặng khiêng hòm."));
visuperobject.biblemap.put("b2_37_6", new visuperobject(2,37,6,"Người cũng làm một cái nắp thi ân bằng vàng ròng, bề dài hai thước rưỡi, bề ngang một thước rưỡi."));
visuperobject.biblemap.put("b2_37_7", new visuperobject(2,37,7,"Làm hai tượng chê-ru-bin bằng vàng đánh giát để nơi hai đầu nắp thi ân,"));
visuperobject.biblemap.put("b2_37_8", new visuperobject(2,37,8,"một tượng ở đầu nầy và một tượng ở đầu kia, ló ra nơi hai đầu nắp."));
visuperobject.biblemap.put("b2_37_9", new visuperobject(2,37,9,"Hai chê-ru-bin sè cánh ra che trên nắp thi ân, đối diện nhau và xây mặt vào nắp thi ân."));
visuperobject.biblemap.put("b2_37_10", new visuperobject(2,37,10,"Người cũng đóng một cái bàn bằng cây si-tim; bề dài hai thước, bề ngang một thước, bề cao một thước rưỡi;"));
visuperobject.biblemap.put("b2_37_11", new visuperobject(2,37,11,"bọc bằng vàng ròng, và chạy một đường viền chung quanh."));
visuperobject.biblemap.put("b2_37_12", new visuperobject(2,37,12,"Lại cùng lên be cho tứ vi bàn cao chừng bốn ngón tay, và chạy cho be một đường viền vàng."));
visuperobject.biblemap.put("b2_37_13", new visuperobject(2,37,13,"Người đúc bốn cái khoen vàng tra vào bốn góc, tại nơi chân bàn."));
visuperobject.biblemap.put("b2_37_14", new visuperobject(2,37,14,"Các khoen ở gần nơi be để xỏ đòn vào, đặng khiêng bàn;"));
visuperobject.biblemap.put("b2_37_15", new visuperobject(2,37,15,"người chuốt đòn bằng cây si-tim, bọc vàng, đặng khiêng bàn."));
visuperobject.biblemap.put("b2_37_16", new visuperobject(2,37,16,"Lại cũng lấy vàng ròng mà làm các đồ dùng bày trên bàn: dĩa, chén, ly, và chậu, để dùng làm lễ quán."));
visuperobject.biblemap.put("b2_37_17", new visuperobject(2,37,17,"Người cũng làm chân đèn bằng vàng ròng; cái chân, cái thân, cái đài, cái bầu, và cái hoa đều làm ra bằng vàng đánh giát."));
visuperobject.biblemap.put("b2_37_18", new visuperobject(2,37,18,"Hai bên thân đèn có sáu nhánh nứt ra, ba nhánh ở bên nầy và ba nhánh ở bên kia."));
visuperobject.biblemap.put("b2_37_19", new visuperobject(2,37,19,"Trong sáu nhánh nứt ra trên thân chân đèn, mỗi nhánh đều có ba cái đài hình như hột hạnh nhân, cùng bầu và hoa."));
visuperobject.biblemap.put("b2_37_20", new visuperobject(2,37,20,"Trên thân chân đèn lại cũng có bốn cái đài hình như hột hạnh nhân, cùng bầu và hoa."));
visuperobject.biblemap.put("b2_37_21", new visuperobject(2,37,21,"Trong sáu nhánh từ thân chân đèn nứt ra, hễ cứ mỗi hai nhánh, thì dưới có một cái bầu."));
visuperobject.biblemap.put("b2_37_22", new visuperobject(2,37,22,"Bầu và nhành của chân đèn đều bằng vàng ròng nguyên miếng đánh giát."));
visuperobject.biblemap.put("b2_37_23", new visuperobject(2,37,23,"Người cũng làm bảy cái thếp đèn, kéo bắt tim và đồ đựng tàn bằng vàng ròng."));
visuperobject.biblemap.put("b2_37_24", new visuperobject(2,37,24,"Người dùng một ta lâng vàng ròng, mà làm chân đèn và các đồ phụ tùng của chân đèn."));
visuperobject.biblemap.put("b2_37_25", new visuperobject(2,37,25,"Người cũng đóng một cái bàn thờ xông hương bằng cây si-tim, hình vuông, bề dài một thước, bề ngang một thước, và bề cao hai thước. Bàn thờ có bốn sừng ló ra."));
visuperobject.biblemap.put("b2_37_26", new visuperobject(2,37,26,"Người bọc vàng ròng trên mặt, bốn cạnh chung quanh và các sừng; tứ vi cũng chạy dường viền vàng."));
visuperobject.biblemap.put("b2_37_27", new visuperobject(2,37,27,"Dưới đường viền đó, nơi hai bên góc, người làm hai cái khoen vàng để xỏ đòn, đặng khiêng."));
visuperobject.biblemap.put("b2_37_28", new visuperobject(2,37,28,"Người chuốt đòn bằng cây si-tim và bọc vàng."));
visuperobject.biblemap.put("b2_37_29", new visuperobject(2,37,29,"Người cũng chế dầu thánh để xức, và hương thanh sạch bằng các hương liệu theo nghề thợ chế hương."));
visuperobject.biblemap.put("b2_38_1", new visuperobject(2,38,1,"Người cũng đóng bàn thờ về của lễ thiêu bằng cây si-tim, hình vuông, bề dài năm thước, bề ngang năm thước và bề cao ba thước."));
visuperobject.biblemap.put("b2_38_2", new visuperobject(2,38,2,"Người làm nơi bốn góc bàn thờ những sừng ló ra, bọc đồng."));
visuperobject.biblemap.put("b2_38_3", new visuperobject(2,38,3,"Rồi làm các đồ phụ tùng của bàn thờ bằng đồng: chảo nhỏ, vá, ảng, nỉa, và bình hương."));
visuperobject.biblemap.put("b2_38_4", new visuperobject(2,38,4,"Lại làm một tấm rá bằng lưới đồng để dưới thành bàn thờ, từ chân lên đến ngang giữa,"));
visuperobject.biblemap.put("b2_38_5", new visuperobject(2,38,5,"và đúc cái khoen cho bốn góc rá lưới đồng, đặng xỏ đòn khiêng."));
visuperobject.biblemap.put("b2_38_6", new visuperobject(2,38,6,"Ðoạn, chuốt đòn khiêng bằng cây si-tim, và bọc đồng;"));
visuperobject.biblemap.put("b2_38_7", new visuperobject(2,38,7,"xỏ đòn vào khoen nơi hai bên bàn thờ, đặng khiêng bàn đi. Người đóng bàn thờ bằng ván, trống bộng."));
visuperobject.biblemap.put("b2_38_8", new visuperobject(2,38,8,"Người dùng các tấm gương của mấy người đàn bà hầu việc nơi cửa hội mạc, chế ra một cái thùng và chân thùng bằng đồng."));
visuperobject.biblemap.put("b2_38_9", new visuperobject(2,38,9,"Người cũng làm hành lang: về phía nam mấy bức bố vi của hành lang bằng vải gai đậu mịn, dài trăm thước;"));
visuperobject.biblemap.put("b2_38_10", new visuperobject(2,38,10,"hai chục cây trụ và hai chục lỗ trụ bằng đồng, đinh và nuông trụ bằng bạc."));
visuperobject.biblemap.put("b2_38_11", new visuperobject(2,38,11,"Về phía bắc, cũng một trăm thước bố vi, hai chục cây trụ và hai chục lỗ trụ bằng đồng, đinh và nuông trụ bằng bạc."));
visuperobject.biblemap.put("b2_38_12", new visuperobject(2,38,12,"Về phía tây, năm chục thước bố vi, mười cây trụ, và mười lỗ trụ bằng đồng, đinh và nuông trụ bằng bạc."));
visuperobject.biblemap.put("b2_38_13", new visuperobject(2,38,13,"Về phía trước, là phía đông, cũng năm chục thước:"));
visuperobject.biblemap.put("b2_38_14", new visuperobject(2,38,14,"nghĩa là bên hữu của hành lang mười lăm thước bố vi ba cây trụ và ba lỗ trụ;"));
visuperobject.biblemap.put("b2_38_15", new visuperobject(2,38,15,"bên tả của hành lang cũng mười lăm thước bố vi, ba cây trụ và ba lỗ trụ."));
visuperobject.biblemap.put("b2_38_16", new visuperobject(2,38,16,"Hết thảy bố vi của hành lang ở chung quanh đều bằng vải gai đậu mịn;"));
visuperobject.biblemap.put("b2_38_17", new visuperobject(2,38,17,"các lỗ trụ bằng đồng, đinh, nuông bằng bạc, và đầu trụ bọc bạc. Hết thảy trụ của hành lang đều nhờ có nuông bạc chắp với nhau."));
visuperobject.biblemap.put("b2_38_18", new visuperobject(2,38,18,"Tấm màn nơi cửa hành lang làm thêu thùa bằng chỉ tím, đỏ điều, đỏ sặm, và vải gai đậu mịn; bề dài hai chục thước, bề cao (tùy theo bề khổ của vải) năm thước, đồng cỡ với bề cao của các bố vi chung quanh hành lang."));
visuperobject.biblemap.put("b2_38_19", new visuperobject(2,38,19,"Bốn cây trụ và bốn lỗ trụ bằng đồng, đinh, đồ bọc đầu trụ, và những nuông đều bằng bạc."));
visuperobject.biblemap.put("b2_38_20", new visuperobject(2,38,20,"Các nọc của đền tạm và của hành lang chung quanh đều bằng đồng."));
visuperobject.biblemap.put("b2_38_21", new visuperobject(2,38,21,"Ðây là sổ tổng cộng về đền tạm, là đền tạm chứng cớ theo lịnh truyền của Môi-se biểu người Lê-vi làm ra, nhờ Y-tha-ma, con trai của thầy tế lễ cả A-rôn, cai quản."));
visuperobject.biblemap.put("b2_38_22", new visuperobject(2,38,22,"Bết-sa-lê-ên, con trai U-ri, cháu Hu-rơ, thuộc về chi phái Giu-đa, làm theo mọi điều Ðức Giê-hô-va đã phán dặn Môi-se,"));
visuperobject.biblemap.put("b2_38_23", new visuperobject(2,38,23,"và kẻ giúp người là Ô-hô-li-áp, con trai A-hi-sa-mạc, thuộc về chi phái Ðan, làm thợ chạm, kẻ bày vẽ, thợ thêu thùa bằng chỉ tím, đỏ điều, đỏ sặm, và bằng chỉ gai mịn."));
visuperobject.biblemap.put("b2_38_24", new visuperobject(2,38,24,"Hết thảy vàng đã hiệp dùng vào các công việc nơi thánh (ấy là vàng dâng) được hai mươi chín ta lâng, bảy trăm ba chục siếc-lơ, theo siếc-lơ của nơi thánh."));
visuperobject.biblemap.put("b2_38_25", new visuperobject(2,38,25,"Bạc của mấy người hội chúng có kể sổ tổng cộng được một trăm ta lâng, một ngàn bảy trăm bảy mươi lăm siếc-lơ, theo siếc-lơ của nơi thánh."));
visuperobject.biblemap.put("b2_38_26", new visuperobject(2,38,26,"Hết thảy người nào có kể sổ, từ hai mươi tuổi sắp lên, nghĩa là sáu trăm ba ngàn năm trăm năm chục người, mỗi người nộp nửa siếc-lơ, theo siếc-lơ của nơi thánh."));
visuperobject.biblemap.put("b2_38_27", new visuperobject(2,38,27,"Vậy, phải một trăm ta lâng bạc đặng đúc các lỗ trụ của nơi thánh, và lỗ trụ màn; một trăm ta lâng cho một trăm lỗ trụ, tức một ta lâng vào mỗi lỗ trụ."));
visuperobject.biblemap.put("b2_38_28", new visuperobject(2,38,28,"Còn một ngàn bảy trăm bảy mươi lăm siếc-lơ dư lại, thì dùng làm đinh trụ, bọc đầu trụ và lấy nuông chắp các trụ với nhau."));
visuperobject.biblemap.put("b2_38_29", new visuperobject(2,38,29,"Ðồng đem dâng cộng được bảy chục ta lâng, hai ngàn bốn trăm siếc-lơ."));
visuperobject.biblemap.put("b2_38_30", new visuperobject(2,38,30,"Họ dùng đồng đó làm lỗ trụ của cửa hội mạc, bàn thờ bằng đồng, rá bằng đồng, các đồ phụ tùng của bàn thờ;"));
visuperobject.biblemap.put("b2_38_31", new visuperobject(2,38,31,"các lỗ trụ của hành lang chung quanh, các lỗ trụ của cửa hành lang, các nọc của đền tạm, và các nọc của hành lang chung quanh."));
visuperobject.biblemap.put("b2_39_1", new visuperobject(2,39,1,"Họ dùng chỉ màu tím, đỏ điều, đỏ sặm mà chế bộ áo định cho sự tế lễ trong nơi thánh, và bộ áo thánh cho A-rôn, y như lời Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b2_39_2", new visuperobject(2,39,2,"Vậy, họ chế ê-phót bằng kim tuyến, chỉ tím, đỏ điều, đỏ sặm, và vải gai đậu mịn."));
visuperobject.biblemap.put("b2_39_3", new visuperobject(2,39,3,"Họ căng vàng lá ra, cắt thành sợi chỉ mà xen cùng chỉ tím, đỏ điều, đỏ sặm, và chỉ gai mịn, chế thật cực xảo."));
visuperobject.biblemap.put("b2_39_4", new visuperobject(2,39,4,"Họ may hai đai vai ê-phót, đâu hai đầu dính lại."));
visuperobject.biblemap.put("b2_39_5", new visuperobject(2,39,5,"Ðai để cột ê-phót, phía ngoài, làm nguyên miếng một y như công việc của ê-phót: bằng kim tuyến, chỉ tím, đỏ điều, đỏ sặm, và bằng chỉ gai đậu mịn, y như lời Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b2_39_6", new visuperobject(2,39,6,"Họ khảm trong khuôn vàng các bích ngọc có khắc tên các chi phái Y-sơ-ra-ên, như người ta khắc con dấu."));
visuperobject.biblemap.put("b2_39_7", new visuperobject(2,39,7,"Họ để các ngọc đó trên đai vai ê-phót đặng làm ngọc kỷ niệm về dân Y-sơ-ra-ên, y như lời Ðức Giê-hô-va phán dặn Môi-se."));
visuperobject.biblemap.put("b2_39_8", new visuperobject(2,39,8,"Họ cũng chế bảng đeo ngực cực xảo, như công việc ê-phót: bằng kim tuyến, chỉ tím, đỏ điều, đỏ sặm, và chỉ gai đậu mịn."));
visuperobject.biblemap.put("b2_39_9", new visuperobject(2,39,9,"Bảng đeo ngực may lót, hình vuông, bề dài một em-ban, bề ngang một em-ban."));
visuperobject.biblemap.put("b2_39_10", new visuperobject(2,39,10,"Họ nhận bốn hàng ngọc: hàng thứ nhất, ngọc mã não, ngọc hồng bích, và ngọc lục bửu;"));
visuperobject.biblemap.put("b2_39_11", new visuperobject(2,39,11,"hàng thứ nhì, ngọc phỉ túy, ngọc lam bửu, và ngọc kim cương;"));
visuperobject.biblemap.put("b2_39_12", new visuperobject(2,39,12,"hàng thứ ba, ngọc hồng bửu, ngọc bạch mã não, và ngọc tử tinh;"));
visuperobject.biblemap.put("b2_39_13", new visuperobject(2,39,13,"hàng thứ tư, ngọc huỳnh bích, ngọc hồng mã não, và bích ngọc. Các ngọc nầy đều khảm vàng."));
visuperobject.biblemap.put("b2_39_14", new visuperobject(2,39,14,"Số ngọc là mười hai, tùy theo số danh của các con trai Y-sơ-ra-ên. Mỗi viên ngọc có khắc một tên trong mười hai chi phái Y-sơ-ra-ên như người ta khắc con dấu."));
visuperobject.biblemap.put("b2_39_15", new visuperobject(2,39,15,"Ðoạn, trên bảng đeo ngực họ chế các sợi chuyền bằng vàng ròng, đánh lại như hình dây."));
visuperobject.biblemap.put("b2_39_16", new visuperobject(2,39,16,"Cũng làm hai cái móc và hai cái khoanh bằng vàng, để hai khoanh nơi hai góc đầu bảng đeo ngực;"));
visuperobject.biblemap.put("b2_39_17", new visuperobject(2,39,17,"cùng cột hai sợi chuyền vàng vào hai khoanh đó."));
visuperobject.biblemap.put("b2_39_18", new visuperobject(2,39,18,"Rồi chuyền hai đầu của hai sợi chuyền vào móc gài nơi đai vai ê-phót, về phía trước ngực."));
visuperobject.biblemap.put("b2_39_19", new visuperobject(2,39,19,"Cũng làm hai khoanh vàng khác để vào hai góc đầu dưới của bảng đeo ngực, về phía trong ê-phót."));
visuperobject.biblemap.put("b2_39_20", new visuperobject(2,39,20,"Lại làm hai khoanh vàng khác nữa để nơi đầu dưới của hai đai vai ê-phót, về phía trước, ở trên đai, gần chỗ giáp mối."));
visuperobject.biblemap.put("b2_39_21", new visuperobject(2,39,21,"Họ lấy một sợi dây màu tím, cột hai cái khoanh của bảng đeo ngực lại cùng hai cái khoanh ê-phót, hầu cho nó dính với đai và đừng rớt khỏi ê-phót, y như lời Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b2_39_22", new visuperobject(2,39,22,"Họ cũng chế áo dài của ê-phót bằng vải thường toàn màu tím."));
visuperobject.biblemap.put("b2_39_23", new visuperobject(2,39,23,"Cổ để tròng đầu vào, ở về chính giữa áo, như lỗ áo giáp, có viền chung quanh cho khỏi tét."));
visuperobject.biblemap.put("b2_39_24", new visuperobject(2,39,24,"Nơi trôn áo, thắt những trái lựu bằng chỉ xe mịn màu tím, đỏ điều, đỏ sặm;"));
visuperobject.biblemap.put("b2_39_25", new visuperobject(2,39,25,"lại làm chuông nhỏ bằng vàng ròng, gắn giữa hai trái lựu, vòng theo chung quanh trôn áo;"));
visuperobject.biblemap.put("b2_39_26", new visuperobject(2,39,26,"cứ một trái lựu, kế một cái chuông nhỏ, vòng theo chung quanh trôn áo dùng để hầu việc, y như lời Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b2_39_27", new visuperobject(2,39,27,"Họ cũng chế áo lá mặc trong bằng vải gai mịn, dệt thường, cho A-rôn cùng các con trai người;"));
visuperobject.biblemap.put("b2_39_28", new visuperobject(2,39,28,"luôn cái mũ, đồ trang sức của mũ, và quần lót trong, đều bằng vải gai đậu mịn;"));
visuperobject.biblemap.put("b2_39_29", new visuperobject(2,39,29,"cùng cái đai bằng vải gai đậu mịn, chỉ tím, đỏ điều, đỏ sặm, có thêu, y như lời Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b2_39_30", new visuperobject(2,39,30,"Họ cũng dùng vàng lá ròng chế cái thẻ thánh, khắc trên đó như người ta khắc con dấu, rằng: Thánh Cho Ðức Giê-hô-va!"));
visuperobject.biblemap.put("b2_39_31", new visuperobject(2,39,31,"Rồi lấy một sợi dây tím đặng cột để trên mũ, y như lời Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b2_39_32", new visuperobject(2,39,32,"Các công việc của đền tạm và hội mạc làm xong là như vậy. Dân Y-sơ-ra-ên làm y như mọi lời Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b2_39_33", new visuperobject(2,39,33,"Họ đem đền tạm đến cho Môi-se: Trại và các đồ phụ tùng của Trại, nọc, ván, xà ngang, trụ và lỗ trụ;"));
visuperobject.biblemap.put("b2_39_34", new visuperobject(2,39,34,"bong da chiên đực nhuộm đỏ, bong da cá nược và cái màn;"));
visuperobject.biblemap.put("b2_39_35", new visuperobject(2,39,35,"hòm bảng chứng và đòn khiêng, cùng nắp thi ân;"));
visuperobject.biblemap.put("b2_39_36", new visuperobject(2,39,36,"bàn và đồ phụ tùng của bàn cùng bánh trần thiết;"));
visuperobject.biblemap.put("b2_39_37", new visuperobject(2,39,37,"chân đèn bằng vàng ròng, thếp đèn đã sắp sẵn, các đồ phụ tùng của chân đèn và dầu thắp,"));
visuperobject.biblemap.put("b2_39_38", new visuperobject(2,39,38,"bàn thờ bằng vàng, dầu xức, hương liệu, tấm màn của cửa Trại;"));
visuperobject.biblemap.put("b2_39_39", new visuperobject(2,39,39,"bàn thờ bằng đồng và rá đồng, đòn khiêng, các đồ phụ tùng của bàn thờ, thùng và chân thùng;"));
visuperobject.biblemap.put("b2_39_40", new visuperobject(2,39,40,"các bố vi của hành lang, trụ, lỗ trụ, màn của cửa hành lang, dây, nọc, hết thảy đồ phụ tùng về việc tế lễ của đền tạm;"));
visuperobject.biblemap.put("b2_39_41", new visuperobject(2,39,41,"bộ áo lễ đặng hầu việc trong nơi thánh, bộ áo thánh cho A-rôn, thầy tế lễ cả, và bộ áo của các con trai người để làm chức tế lễ."));
visuperobject.biblemap.put("b2_39_42", new visuperobject(2,39,42,"Dân Y-sơ-ra-ên làm mọi công việc nầy y như mạng lịnh của Ðức Giê-hô-va đã truyền cho Môi-se."));
visuperobject.biblemap.put("b2_39_43", new visuperobject(2,39,43,"Môi-se xem các công việc làm, thấy họ đều làm y như lời Ðức Giê-hô-va đã phán dặn; đoạn Môi-se chúc phước cho họ."));
visuperobject.biblemap.put("b2_40_1", new visuperobject(2,40,1,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b2_40_2", new visuperobject(2,40,2,"Ngày mồng một tháng giêng, ngươi sẽ dựng đền tạm."));
visuperobject.biblemap.put("b2_40_3", new visuperobject(2,40,3,"Hãy để trong đó cái hòm bảng chứng, rồi lấy màn phủ lại."));
visuperobject.biblemap.put("b2_40_4", new visuperobject(2,40,4,"Cũng hãy đem bàn đến, chưng bày các đồ trên bàn cho có thứ tự, cùng chân đèn và thắp các đèn;"));
visuperobject.biblemap.put("b2_40_5", new visuperobject(2,40,5,"cũng hãy để bàn thờ bằng vàng dùng xông hương trước hòm bảng chứng, rồi xủ tấm màn nơi cửa đền tạm."));
visuperobject.biblemap.put("b2_40_6", new visuperobject(2,40,6,"Cũng hãy để bàn thờ về của lễ thiêu đằng trước cửa đền tạm;"));
visuperobject.biblemap.put("b2_40_7", new visuperobject(2,40,7,"và để cái thùng ở giữa khoảng hội mạc và bàn thờ; rồi đổ nước vào;"));
visuperobject.biblemap.put("b2_40_8", new visuperobject(2,40,8,"lại chừa hành lang chung quanh và xủ tấm màn nơi cửa hành lang."));
visuperobject.biblemap.put("b2_40_9", new visuperobject(2,40,9,"Ðoạn, ngươi hãy lấy dầu xức mà xức đền tạm và các đồ để trong đó; biệt riêng đền tạm ra thánh, thì sẽ làm thánh vậy."));
visuperobject.biblemap.put("b2_40_10", new visuperobject(2,40,10,"Cũng hãy xức dầu bàn thờ về của lễ thiêu, và các đồ phụ tùng của bàn thờ; rồi biệt riêng ra thánh, thì bàn thờ sẽ làm rất thánh."));
visuperobject.biblemap.put("b2_40_11", new visuperobject(2,40,11,"Cũng hãy xức dầu cho thùng và chân thùng; biệt thùng riêng ra thánh."));
visuperobject.biblemap.put("b2_40_12", new visuperobject(2,40,12,"Ðoạn, hãy dẫn A-rôn cùng các con trai người đến gần cửa hội mạc, rồi tắm họ trong nước."));
visuperobject.biblemap.put("b2_40_13", new visuperobject(2,40,13,"Ngươi hãy mặc áo thánh cho A-rôn, xức dầu và biệt người riêng ra thánh; vậy người sẽ làm chức tế lễ trước mặt ta."));
visuperobject.biblemap.put("b2_40_14", new visuperobject(2,40,14,"Hãy dẫn các con trai người đến gần, rồi mặc áo lá cho họ;"));
visuperobject.biblemap.put("b2_40_15", new visuperobject(2,40,15,"xức dầu cho như ngươi đã xức cho cha họ, thì họ sẽ làm chức tế lễ trước mặt ta. Sự xức dầu sẽ phong cho họ chức tế lễ mãi mãi trải qua các đời."));
visuperobject.biblemap.put("b2_40_16", new visuperobject(2,40,16,"Môi-se làm y như mọi điều Ðức Giê-hô-va đã phán dặn mình."));
visuperobject.biblemap.put("b2_40_17", new visuperobject(2,40,17,"Ðến ngày mồng một tháng giêng về năm thứ hai, thì đền tạm đã dựng."));
visuperobject.biblemap.put("b2_40_18", new visuperobject(2,40,18,"Môi-se dựng đền tạm, để mấy lỗ trụ, đóng ván, thả xà ngang và dựng trụ."));
visuperobject.biblemap.put("b2_40_19", new visuperobject(2,40,19,"Người căng bong trên đền tạm, rồi trải lá phủ bong lên trên, y như lời Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b2_40_20", new visuperobject(2,40,20,"Ðoạn, người cũng lấy bảng chứng, để trong hòm, xỏ đòn khiêng vào, để nắp thi ân lên trên hòm."));
visuperobject.biblemap.put("b2_40_21", new visuperobject(2,40,21,"Người khiêng hòm vào đền tạm và treo màn để che chỗ chí thánh, phủ hòm bảng chứng lại, y như lời Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b2_40_22", new visuperobject(2,40,22,"Người cũng để cái bàn trong hội mạc, về phía bắc đền tạm ở ngoài bức màn;"));
visuperobject.biblemap.put("b2_40_23", new visuperobject(2,40,23,"rồi sắp một hàng bánh ở trên, trước mặt Ðức Giê-hô-va, y như lời Ngài đã phán dặn Môi-se."));
visuperobject.biblemap.put("b2_40_24", new visuperobject(2,40,24,"Ðể chân đèn trong hội mạc về phía nam, đối diện cùng cái bàn."));
visuperobject.biblemap.put("b2_40_25", new visuperobject(2,40,25,"Người thắp đèn trước mặt Ðức Giê-hô-va, y như lời Ngài đã phán dặn Môi-se."));
visuperobject.biblemap.put("b2_40_26", new visuperobject(2,40,26,"Ðể bàn thờ bằng vàng trước bức màn trong hội mạc;"));
visuperobject.biblemap.put("b2_40_27", new visuperobject(2,40,27,"trên đó người xông hương, y như lời Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b2_40_28", new visuperobject(2,40,28,"Người cũng xủ màn nơi cửa đền tạm."));
visuperobject.biblemap.put("b2_40_29", new visuperobject(2,40,29,"Ðoạn, người để bàn thờ về của lễ thiêu nơi cửa đền tạm, dâng trên đó của lễ thiêu và của lễ chay, y như lời Ðức Giê-hô-va đã phán dặn Môi-se."));
