package vi.bible;

public class vibible23 {

public vibible23() {

putmapbible23();

}

private void putmapbible23() {

visuperobject.biblemap.put("b23_1_1", new visuperobject(23,1,1,"Về đời Ô-xia, Giô-tham, A-cha và Ê-xê-chia, là các vua nước Giu-đa, thì Ê-sai, con trai A-mốt, có sự hiện thấy về nước Giu-đa và thành Giê-ru-sa-lem."));
visuperobject.biblemap.put("b23_1_2", new visuperobject(23,1,2,"Hỡi các từng trời, hãy nghe; hỡi đất, hãy lắng tai; vì Ðức Giê-hô-va có phán rằng: Ta đã nuôi nấng con cái, trưởng dưỡng chúng nó, song chúng nó dấy loạn nghịch cùng ta."));
visuperobject.biblemap.put("b23_1_3", new visuperobject(23,1,3,"Bò biết chủ mình, lừa biết máng của chủ; song Y-sơ-ra-ên chẳng hiểu biết, dân ta chẳng suy nghĩ."));
visuperobject.biblemap.put("b23_1_4", new visuperobject(23,1,4,"Ôi! nước mắc tội, dân mang lỗi nặng nề, tông giống độc dữ, con cái làm bậy bạ kia! Chúng nó đã lìa bỏ Ðức Giê-hô-va, khinh lờn Ðấng Thánh của Y-sơ-ra-ên, đã trở nên xa lạ và lui đi."));
visuperobject.biblemap.put("b23_1_5", new visuperobject(23,1,5,"Các ngươi sao còn cứ bạn nghịch, để lại bị đánh nữa? Ðầu đều đau đớn cả, lòng đều mòn mỏi cả."));
visuperobject.biblemap.put("b23_1_6", new visuperobject(23,1,6,"Từ bàn chơn cho đến đỉnh đầu, chẳng có chỗ nào lành: rặt những vết thương, vít sưng cùng lằn mới, chưa rịt, chưa quấn, cũng chưa bôi dầu cho êm."));
visuperobject.biblemap.put("b23_1_7", new visuperobject(23,1,7,"Xứ các ngươi là nơi hoang vu, thành các ngươi bị lửa thiêu hủy; dân ngoại nuốt đất đai các ngươi trước mặt các ngươi, hoang vu như bị dân ngoại phá tán."));
visuperobject.biblemap.put("b23_1_8", new visuperobject(23,1,8,"Con cái Si-ôn bị bỏ lại như lều vườn nho, như chòi ruộng dưa, như thành bị vây."));
visuperobject.biblemap.put("b23_1_9", new visuperobject(23,1,9,"Ví bằng Ðức Giê-hô-va vạn quân chẳng để chúng ta còn sót lại chút đỉnh, thì chúng ta sẽ giống thành Sô-đôm và như thành Gô-mô-rơ vậy!"));
visuperobject.biblemap.put("b23_1_10", new visuperobject(23,1,10,"Hỡi các quan trưởng Sô-đôm, hãy nghe lời của Ðức Giê-hô-va. Hỡi dân Gô-mô-rơ, hãy để tai vào luật pháp của Ðức Chúa Trời chúng ta!"));
visuperobject.biblemap.put("b23_1_11", new visuperobject(23,1,11,"Ðức Giê-hô-va phán: Muôn vàn của lễ các ngươi nào có can hệ gì đến ta? Ta đã chán chê của lễ thiêu bằng chiên đực và mỡ của bò con mập. Ta chẳng đẹp lòng về huyết của bò đực, chiên con và dê đực đâu."));
visuperobject.biblemap.put("b23_1_12", new visuperobject(23,1,12,"Khi các ngươi đến chầu trước mặt ta, ai khiến các ngươi giày đạp hành lang ta?"));
visuperobject.biblemap.put("b23_1_13", new visuperobject(23,1,13,"Thôi, đừng dâng của lễ chay vô ích cho ta nữa! Ta gớm ghét mùi hương, ngày trăng mới, ngày sa-bát cùng sự rao nhóm hội; ta chẳng khứng chịu tội ác hội hiệp nơi lễ trọng thể."));
visuperobject.biblemap.put("b23_1_14", new visuperobject(23,1,14,"Thật, lòng ta ghét những ngày trăng mới và kỳ lễ các ngươi, là nặng nề cho ta, ta lấy làm mệt mà gánh lấy."));
visuperobject.biblemap.put("b23_1_15", new visuperobject(23,1,15,"Vậy nên, khi các ngươi giơ tay, thì ta che mắt khỏi các ngươi. Khi các ngươi cầu nguyện rườn rà, ta chẳng thèm nghe. Tay các ngươi đầy những máu."));
visuperobject.biblemap.put("b23_1_16", new visuperobject(23,1,16,"Hãy rửa đi, hãy làm cho sạch! Hãy tránh những việc ác khỏi trước mắt ta. Ðừng làm dữ nữa."));
visuperobject.biblemap.put("b23_1_17", new visuperobject(23,1,17,"Hãy học làm lành, tìm kiếm sự công bình; hãy đỡ đần kẻ bị hà hiếp, làm công bình cho kẻ mồ côi, binh vực lẽ của người góa bụa."));
visuperobject.biblemap.put("b23_1_18", new visuperobject(23,1,18,"Ðức Giê-hô-va phán: Bây giờ hãy đến, cho chúng ta biện luận cùng nhau. Dầu tội các ngươi như hồng điều, sẽ trở nên trắng như tuyết; dầu đỏ như son, sẽ trở nên trắng như lông chiên."));
visuperobject.biblemap.put("b23_1_19", new visuperobject(23,1,19,"Nếu các ngươi sẵn lòng vâng lời, sẽ ăn được sản vật tốt nhứt của đất."));
visuperobject.biblemap.put("b23_1_20", new visuperobject(23,1,20,"Nhược bằng các ngươi chẳng khứng và bạn nghịch, sẽ bị nuốt bởi gươm; vì miệng Ðức Giê-hô-va đã phán."));
visuperobject.biblemap.put("b23_1_21", new visuperobject(23,1,21,"Ôi! thành trung nghĩa đã hóa ra kỵ nữ, nó vốn đầy sự chánh trực, giờ đầy những kẻ giết người!"));
visuperobject.biblemap.put("b23_1_22", new visuperobject(23,1,22,"Bạc ngươi biến thành cặn, rượu ngươi pha lộn nước."));
visuperobject.biblemap.put("b23_1_23", new visuperobject(23,1,23,"Các quan trưởng của dân ngươi là đồ phản nghịch, làm bạn với kẻ trộm, mỗi người trong chúng đều ưa của hối lộ, đeo đuổi theo tài lợi; chẳng làm công bình cho kẻ mồ côi, vụ kiện của người góa bụa chẳng đến cùng chúng nó."));
visuperobject.biblemap.put("b23_1_24", new visuperobject(23,1,24,"Vậy nên, Chúa, là Ðức Giê-hô-va vạn quân, Ðấng Quyền năng của Y-sơ-ra-ên, phán như vầy: Ôi! ta sẽ được thỏa lòng về kẻ đối định ta, và báo trả kẻ cừu thù ta!"));
visuperobject.biblemap.put("b23_1_25", new visuperobject(23,1,25,"Ta sẽ lại tra tay trên ngươi, làm ta sạch hết cáu cặn ngươi, và bỏ hết chất pha của ngươi."));
visuperobject.biblemap.put("b23_1_26", new visuperobject(23,1,26,"Ta sẽ lập các quan xét của ngươi như ngày trước, các mưu sĩ của ngươi như lúc đầu. Rồi sau người ta sẽ xưng ngươi là thành công bình, là ấp trung nghĩa."));
visuperobject.biblemap.put("b23_1_27", new visuperobject(23,1,27,"Si-ôn sẽ được chuộc bởi sự chánh trực, còn kẻ nào trở lại đó thì được chuộc bởi sự công bình."));
visuperobject.biblemap.put("b23_1_28", new visuperobject(23,1,28,"Song những kẻ bạn nghịch và mắc tội sẽ cùng bị hủy diệt, còn kẻ bỏ Ðức Giê-hô-va sẽ bị diệt vong."));
visuperobject.biblemap.put("b23_1_29", new visuperobject(23,1,29,"Vậy các ngươi sẽ hổ thẹn vì những cây thông mình ưa; mắc cỡ vì những vườn mình đã chọn."));
visuperobject.biblemap.put("b23_1_30", new visuperobject(23,1,30,"Vì các ngươi sẽ giống như cây thông khô lá, như vườn không nước."));
visuperobject.biblemap.put("b23_1_31", new visuperobject(23,1,31,"Người mạnh sẽ như bã gai, việc làm của nó như đóm lửa; cả hai sẽ cùng nhau cháy, chẳng có ai giập tắt."));
visuperobject.biblemap.put("b23_2_1", new visuperobject(23,2,1,"Lời mà Ê-sai, con trai A-mốt, đã thấy về Giu-đa và Giê-ru-sa-lem."));
visuperobject.biblemap.put("b23_2_2", new visuperobject(23,2,2,"Sẽ xảy ra trong những ngày sau rốt, núi của nhà Ðức Giê-hô-va sẽ được lập vững trên đỉnh các núi, cao hơn các đồi. Mọi nước sẽ đổ về đó,"));
visuperobject.biblemap.put("b23_2_3", new visuperobject(23,2,3,"và nhiều dân tộc sẽ đến mà nói rằng: Hãy đến; chúng ta hãy lên núi Ðức Giê-hô-va, nơi nhà Ðức Chúa Trời của Gia-cốp: Ngài sẽ dạy chúng ta về đường lối Ngài, chúng ta sẽ đi trong các nẻo Ngài. Vì luật pháp sẽ ra từ Si-ôn, lời Ðức Giê-hô-va sẽ ra từ Giê-ru-sa-lem."));
visuperobject.biblemap.put("b23_2_4", new visuperobject(23,2,4,"Ngài sẽ làm sự phán xét trong các nước, đoán định về nhiều dân. Bấy giờ họ sẽ lấy gươm rèn lưỡi cày, lấy giáo rèn lưỡi liềm. Nước nầy chẳng còn giá gươm lên nghịch cùng nước khác, người ta chẳng còn tập sự chiến tranh."));
visuperobject.biblemap.put("b23_2_5", new visuperobject(23,2,5,"Hỡi nhà Gia-cốp, hãy đến, chúng ta hãy bước đi trong sự sáng của Ðức Giê-hô-va."));
visuperobject.biblemap.put("b23_2_6", new visuperobject(23,2,6,"Thật Chúa đã bỏ dân Ngài, là nhà Gia-cốp, vì họ đầy dẫy tục phương đông, và bói khoa như người Phi-li-tin, lại giao ước với con cái dân ngoại."));
visuperobject.biblemap.put("b23_2_7", new visuperobject(23,2,7,"Xứ nó đầy bạc và vàng, của cải vô cùng; xứ nó đầy những ngữa, xe cộ vô số."));
visuperobject.biblemap.put("b23_2_8", new visuperobject(23,2,8,"Xứ nó chan chứa những hình tượng, họ thờ lạy đồ tay mình làm ra, đồ ngón tay mình tạo nên."));
visuperobject.biblemap.put("b23_2_9", new visuperobject(23,2,9,"Kẻ hèn sẽ bị khuất, người sang sẽ bị hạ mình; vậy Chúa đừng tha họ!"));
visuperobject.biblemap.put("b23_2_10", new visuperobject(23,2,10,"Ngươi khá vào nơi vầng đá, ẩn mình trong bụi đất, đặng tránh khỏi sự kinh khiếp Ðức Giê-hô-va và sự chói sáng của uy nghiêm Ngài."));
visuperobject.biblemap.put("b23_2_11", new visuperobject(23,2,11,"Con mắt ngó cao của loài người sẽ bị thấp xuống, sự kiêu ngạo của người ta sẽ bị hạ đi; trong ngày đó chỉ Ðức Giê-hô-va là tôn trọng."));
visuperobject.biblemap.put("b23_2_12", new visuperobject(23,2,12,"Vì sẽ có một ngày của Ðức Giê-hô-va vạn quân đến trên mọi vật kiêu căng, ngạo mạn, tự cao; nó sẽ bị hạ xuống;"));
visuperobject.biblemap.put("b23_2_13", new visuperobject(23,2,13,"lại đến trên mọi cây dẽ của Ba-san;"));
visuperobject.biblemap.put("b23_2_14", new visuperobject(23,2,14,"cùng trên mọi núi cao, mọi đồi cả,"));
visuperobject.biblemap.put("b23_2_15", new visuperobject(23,2,15,"trên mọi tháp cao và mọi vách thành vững bền,"));
visuperobject.biblemap.put("b23_2_16", new visuperobject(23,2,16,"trên mọi tàu bè của Ta-rê-si, và mọi vật chi đẹp mắt."));
visuperobject.biblemap.put("b23_2_17", new visuperobject(23,2,17,"Sự tự cao của loài người sẽ bị dằn xuống, và sự cậy mình của người ta sẽ bị hạ đi; trong ngày đó chỉ Ðức Giê-hô-va là tôn trọng."));
visuperobject.biblemap.put("b23_2_18", new visuperobject(23,2,18,"Bấy giờ các thần tượng sẽ qua hết đi."));
visuperobject.biblemap.put("b23_2_19", new visuperobject(23,2,19,"Người ta sẽ vào trong hang đá, trong hầm đất, đặng tránh khỏi sự kinh khiếp Ðức Giê-hô-va và sẽ chói sáng của uy nghiêm Ngài, khi Ngài dấy lên đặng làm rúng động đất cách mạnh lắm."));
visuperobject.biblemap.put("b23_2_20", new visuperobject(23,2,20,"Trong ngày đó, người ta sẽ ném cho chuột cho dơi những thần tượng bằng bạc bằng vàng mà họ làm ra cho mình thờ lạy;"));
visuperobject.biblemap.put("b23_2_21", new visuperobject(23,2,21,"và vào trong hang đá, trong kẽ đá, đặng tránh khỏi sự kinh khiếp Ðức Giê-hô-va và sự chói sáng của uy nghiêm Ngài, khi Ngài dấy lên đặng làm rúng động đất cách mạnh lắm."));
visuperobject.biblemap.put("b23_2_22", new visuperobject(23,2,22,"Chớ cậy ở loài người, là loài có hơi thở trong lỗ mũi; vì họ nào có đáng kể gì đâu?"));
visuperobject.biblemap.put("b23_3_1", new visuperobject(23,3,1,"Nầy, Chúa, là Ðức Giê-hô-va vạn quân, sẽ cất lấy chói và gậy khỏi Giê-ru-sa-lem và Giu-đa, tức là cả bánh nó cậy và cả nước nó nhờ."));
visuperobject.biblemap.put("b23_3_2", new visuperobject(23,3,2,"Ngài sẽ cất lấy những người mạnh mẽ, chiến sĩ, quan xét, đấng tiên tri, thầy bói, trưởng lão,"));
visuperobject.biblemap.put("b23_3_3", new visuperobject(23,3,3,"cai đội, dòng quí phái, mưu sĩ, lương công, và thuật sĩ."));
visuperobject.biblemap.put("b23_3_4", new visuperobject(23,3,4,"Ta sẽ ban cho họ những người trai trẻ làm quan trưởng, và con nít sẽ cai trị họ."));
visuperobject.biblemap.put("b23_3_5", new visuperobject(23,3,5,"Dân sự hà hiếp nhau, người với người, lân cận với lân cận; con nít lấn lướt kẻ già cả, người hèn hạ lấn lướt người tôn trọng."));
visuperobject.biblemap.put("b23_3_6", new visuperobject(23,3,6,"Khi một người sẽ bắt anh em mình tại nhà cha mà nói với rằng: Ngươi có áo choàng; hãy làm đầu chúng ta, và sự bại hoại nầy hãy thuộc về dưới tay ngươi!"));
visuperobject.biblemap.put("b23_3_7", new visuperobject(23,3,7,"Trong ngày đó người kia lên tiếng đáp rằng: Không, ta không làm thầy chữa lành, và trong nhà ta không có bánh cũng không có áo; chớ lập ta làm quan cai dân sự."));
visuperobject.biblemap.put("b23_3_8", new visuperobject(23,3,8,"Giê-ru-sa-lem phải nghiêng úp, và Giu-đa xiêu đổ; vì lời nói việc làm của họ nghịch cùng Ðức Giê-hô-va, và trêu chọc con mắt của uy nghiêm Ngài."));
visuperobject.biblemap.put("b23_3_9", new visuperobject(23,3,9,"Bộ mặt họ làm chứng nghịch cùng mình; họ bêu tội rõ ràng như thành Sô-đôm chẳng giấu chút nào. Khốn thay cho linh hồn họ! vì họ đã làm hại cho mình!"));
visuperobject.biblemap.put("b23_3_10", new visuperobject(23,3,10,"Hãy rao cho kẻ công bình được phước; vì họ sẽ ăn trái của việc làm mình."));
visuperobject.biblemap.put("b23_3_11", new visuperobject(23,3,11,"Khốn cho kẻ hung ác, ắt mang họa! Vì họ sẽ thâu lại việc tự tay mình làm ra!"));
visuperobject.biblemap.put("b23_3_12", new visuperobject(23,3,12,"Dân ta bị con nít hà hiếp, và đờn bà cai trị nó. Hỡi dân ta, kẻ dẫn ngươi làm cho ngươi sai lạc, và họ phá hoại đường lối ngươi."));
visuperobject.biblemap.put("b23_3_13", new visuperobject(23,3,13,"Ðức Giê-hô-va dấy lên đặng biện luận, Ngài đứng mà xét đoán các dân."));
visuperobject.biblemap.put("b23_3_14", new visuperobject(23,3,14,"Ðức Giê-hô-va xét đoán các trưởng lão của dân Ngài với các quan trưởng nó: Ấy chính các ngươi là kẻ đã nuốt vườn nho; của cướp bóc kẻ nghèo đương ở trong nhà các ngươi!"));
visuperobject.biblemap.put("b23_3_15", new visuperobject(23,3,15,"Các ngươi có ý gì mà đè ép dân ta, giày vò mặt kẻ nghèo khó? Chúa, là Ðức Giê-hô-va vạn quân, phán vậy."));
visuperobject.biblemap.put("b23_3_16", new visuperobject(23,3,16,"Ðức Giê-hô-va lại phán rằng: Vì những con gái Si-ôn kiêu ngạo, ngóng cổ bước tới, liếc mắt trêu ngươi, vừa đi õng ẹo, vừa khua động tiếng dưới chơn,"));
visuperobject.biblemap.put("b23_3_17", new visuperobject(23,3,17,"nên Chúa sẽ làm cho đỉnh đầu con gái Si-ôn đóng vảy, và Ðức Giê-hô-va sẽ lột truồng chúng nó."));
visuperobject.biblemap.put("b23_3_18", new visuperobject(23,3,18,"Trong ngày đó, Chúa sẽ cất những vòng mắt cá họ trang sức đi, cái lưới và cái cài;"));
visuperobject.biblemap.put("b23_3_19", new visuperobject(23,3,19,"hoa tai, xuyến và lúp;"));
visuperobject.biblemap.put("b23_3_20", new visuperobject(23,3,20,"mão, chuyền mắt cá, nít lưng, hợp hương và bùa đeo;"));
visuperobject.biblemap.put("b23_3_21", new visuperobject(23,3,21,"cà rá và khoen đeo mũi;"));
visuperobject.biblemap.put("b23_3_22", new visuperobject(23,3,22,"áo lễ, áo lá rộng, áo choàng, túi nhỏ;"));
visuperobject.biblemap.put("b23_3_23", new visuperobject(23,3,23,"gương tay, vải mỏng, khăn bịt đầu và màn che mặt."));
visuperobject.biblemap.put("b23_3_24", new visuperobject(23,3,24,"Sẽ có mùi hôi hám thay vì thơm tho; dây tói thay vì nịt lưng; đầy sói thay vì tóc quăn; bao gai quấn mình thay vì áo dài phấp phới; dấu phỏng thay vì sắc đẹp."));
visuperobject.biblemap.put("b23_3_25", new visuperobject(23,3,25,"Lính chiến ngươi sẽ ngã dưới gươm, những kẻ anh hùng ngươi sẽ bị tử trận."));
visuperobject.biblemap.put("b23_3_26", new visuperobject(23,3,26,"Các cửa nó sẽ than khóc và thảm sầu; nó vắng vẻ ngồi trên đất."));
visuperobject.biblemap.put("b23_4_1", new visuperobject(23,4,1,"Trong ngày đó, bảy người nữ sẽ bắt lấy một người nam, và nói rằng: Chúng tôi ăn bánh của mình, mặc áo của mình; chỉ xin cho phép chúng tôi mang danh ngươi; hãy cất sự xấu hổ của chúng tôi đi."));
visuperobject.biblemap.put("b23_4_2", new visuperobject(23,4,2,"Trong ngày đó, chồi của Ðức Giê-hô-va sẽ làm đồ trang sức vinh hiển của những kẻ sót lại trong Y-sơ-ra-ên, sản vật dưới đất sẽ làm sự tốt đẹp cho chúng nó."));
visuperobject.biblemap.put("b23_4_3", new visuperobject(23,4,3,"Phàm những kẻ còn ở lại Si-ôn, những kẻ sót lại ở Giê-ru-sa-lem, tức là những kẻ ở Giê-ru-sa-lem được chép vào sổ người sống, thì sẽ được xưng là thánh;"));
visuperobject.biblemap.put("b23_4_4", new visuperobject(23,4,4,"khi Chúa đem thần công bình cùng thần thiêu đốt mà rửa sự ô uế của những con gái Si-ôn, tẩy sạch huyết Giê-ru-sa-lem khỏi giữa nó."));
visuperobject.biblemap.put("b23_4_5", new visuperobject(23,4,5,"Trên khắp nơi núi Si-ôn, trên khắp các hội nó, Ðức Giê-hô-va sẽ dựng lên một đám mây bọc khói trong ban ngày, và ngọn lửa soi sáng trong ban đêm. Vì một cái màn sẽ che cả sự vinh hiển:"));
visuperobject.biblemap.put("b23_4_6", new visuperobject(23,4,6,"sẽ có trại làm bóng mát tránh nắng ban ngày, và dùng làm nơi núp chổ đụp cho khỏi gió táp mưa sa."));
visuperobject.biblemap.put("b23_5_1", new visuperobject(23,5,1,"Ta sẽ hát cho bạn rất yêu dấu ta một bài ca của bạn yêu dấu ta về việc vườn nho người. Bạn rất yêu dấu ta có một vườn nho ở trên gò đất tốt."));
visuperobject.biblemap.put("b23_5_2", new visuperobject(23,5,2,"Người khai phá ra; cất bỏ những đá; trồng những gốc nho xinh tốt; dựng một cái tháp giữa vườn, và đào một nơi ép rượu. Vả, người mong rằng sẽ sanh trái nho; nhưng nó lại sanh trái nho hoang."));
visuperobject.biblemap.put("b23_5_3", new visuperobject(23,5,3,"Hỡi dân ở Giê-ru-sa-lem cùng người Giu-đa, vậy thì bây giờ ta xin các ngươi hãy đoán xét giữa ta với vườn nho ta."));
visuperobject.biblemap.put("b23_5_4", new visuperobject(23,5,4,"Có điều chi nên làm cho vườn nho ta mà ta không làm cho nó chăng? Cớ sao khi ta mong sanh trái nho, thì nó lại sanh trái nho hoang vậy?"));
visuperobject.biblemap.put("b23_5_5", new visuperobject(23,5,5,"Nầy, ta sẽ bảo các ngươi về điều ta định làm cho vườn nho ta: Ta phá rào, nó sẽ bị cắn nuốt; ta hạ tường xuống, nó sẽ bị giày đạp."));
visuperobject.biblemap.put("b23_5_6", new visuperobject(23,5,6,"Ta sẽ để nó hoang loạn, chẳng tỉa sửa, chẳng vun xới; nhưng tại đó sẽ mọc lên những gai gốc và chà chuôm; ta lại truyền cho mây đừng làm mưa xuống trên nó nữa."));
visuperobject.biblemap.put("b23_5_7", new visuperobject(23,5,7,"Vả, vườn nho của Ðức Giê-hô-va vạn quân, ấy là nhà Y-sơ-ra-ên, và những người của Giu-đa tức là cây mà Ngài ưa thích. Ngài trông họ được công chính, mà nầy, có sự bạo ngược; trông được công bình, mà nầy, có tiếng kêu la."));
visuperobject.biblemap.put("b23_5_8", new visuperobject(23,5,8,"Khốn thay cho những kẻ thêm nhà vào nhà, thêm ruộng vào ruộng, cho đến chừng nào chẳng còn chỗ hở nữa, và các ngươi ở một mình giữa xứ!"));
visuperobject.biblemap.put("b23_5_9", new visuperobject(23,5,9,"Ðức Giê-hô-va vạn quân có phán vào tai tôi những lời nầy: Thật nhiều nhà sẽ bỏ hoang, những nhà ấy lớn và đẹp mà chẳng có người ở!"));
visuperobject.biblemap.put("b23_5_10", new visuperobject(23,5,10,"Ấy vậy, mười mẫu vườn nho chỉ sanh được một bát rượu; một ô-me hột giống chỉ được một ê-pha."));
visuperobject.biblemap.put("b23_5_11", new visuperobject(23,5,11,"Khốn thay cho kẻ dậy sớm đặng tìm kiếm rượu, chầy đến ban đêm, phát nóng vì rượu!"));
visuperobject.biblemap.put("b23_5_12", new visuperobject(23,5,12,"Trong tiệc yến họ có những đờn cầm, đờn sắt, trống cơm, ống sáo và rượu nữa, những chẳng thiết đến công việc Ðức Giê-hô-va; họ chẳng xem xét công trình của tay Ngài."));
visuperobject.biblemap.put("b23_5_13", new visuperobject(23,5,13,"Vậy nên, dân ta phải bắt làm phu tù, vì không hiểu biết; kẻ sang nó chịu đói, chúng dân nó chịu khát."));
visuperobject.biblemap.put("b23_5_14", new visuperobject(23,5,14,"Vậy nên sự ham muốn của âm phủ đã rộng thêm, hả miệng vô ngần, sự vinh hiển nó, dân chúng nó, sự sang trọng nó đều sa xuống đó."));
visuperobject.biblemap.put("b23_5_15", new visuperobject(23,5,15,"Kẻ hèn bị khuất; kẻ tôn trọng bị thấp hèn, con mắt người cao ngạo bị hạ xuống;"));
visuperobject.biblemap.put("b23_5_16", new visuperobject(23,5,16,"song Ðức Giê-hô-va vạn quân lên cao trong sự công chính, Ðức Chúa Trời là Ðấng Thánh nên thánh trong sự công bình."));
visuperobject.biblemap.put("b23_5_17", new visuperobject(23,5,17,"Bấy giờ những chiên con sẽ ăn cỏ như trong đồng cỏ mình, người ngụ cư sẽ ăn ruộng hoang của kẻ giàu."));
visuperobject.biblemap.put("b23_5_18", new visuperobject(23,5,18,"Khốn thay cho kẻ lấy sự dối trá làm dây kéo sự gian ác theo sau, và như dùng đỏi xe kéo tội lỗi;"));
visuperobject.biblemap.put("b23_5_19", new visuperobject(23,5,19,"họ nói: Xin vội vã kíp làm nên công việc Ngài, hầu cho chúng tôi thấy! Nguyền xin mưu của Ðấng Thánh Y-sơ-ra-ên hãy lại gần, hãy tới đến, cho chúng tôi được biết!"));
visuperobject.biblemap.put("b23_5_20", new visuperobject(23,5,20,"Khốn thay cho kẻ gọi dữ là lành, gọi lành là dữ; lấy tối làm sáng, lấy sáng làm tối; vậy chi cay trở cho là ngọt, vật chi ngọt trở cho là cay."));
visuperobject.biblemap.put("b23_5_21", new visuperobject(23,5,21,"Khốn thay cho kẻ chính mắt mình coi mình là khôn ngoan, tự mình xét đoán mình là thông sáng!"));
visuperobject.biblemap.put("b23_5_22", new visuperobject(23,5,22,"Khốn thay cho kẻ mạnh uống rượu, có sức lực đặng pha các thức uống hay say;"));
visuperobject.biblemap.put("b23_5_23", new visuperobject(23,5,23,"vì hối lộ mà xưng kẻ có tội là công bình, và cướp lấy sự công bình của người nghĩa!"));
visuperobject.biblemap.put("b23_5_24", new visuperobject(23,5,24,"Vậy nên, như lửa đốt gốc rạ và rơm cháy thiêu trong ngọn lửa thể nào, thì rễ họ cùng mục nát và hoa họ cùng bay đi như bụi đất thể ấy; vì họ đã bỏ luật pháp của Ðức Giê-hô-va vạn quân, và khinh lời của Ðấng Thánh Y-sơ-ra-ên."));
visuperobject.biblemap.put("b23_5_25", new visuperobject(23,5,25,"Nhơn đó, cơn thạnh nộ của Ðức Giê-hô-va phừng lên nghịch cùng dân Ngài. Ngài đã giơ tay nghịch cùng nó và đánh nó; núi non đều rúng động: xác chết chúng nó như phân ngang ngửa giữa đường. Dầu vậy, cơn giận Ngài chẳng lánh khỏi, nhưng tay Ngài còn giơ ra."));
visuperobject.biblemap.put("b23_5_26", new visuperobject(23,5,26,"Ngài dựng cờ lên hướng về các dân tộc ở xa cách, và xuýt mà gọi họ từ nơi đầu cùng đất; và nầy, họ lật đật chạy đến, kíp và mau."));
visuperobject.biblemap.put("b23_5_27", new visuperobject(23,5,27,"Trong đám họ không một người nào mỏi mệt, chẳng ai vấp váp, chẳng ai ngủ gật hoặc ngủ mê; không ai có dây lưng tháo ra hoặc dây giày đứt."));
visuperobject.biblemap.put("b23_5_28", new visuperobject(23,5,28,"Tên của họ đã nhọn, cung đã giương; vó ngựa cứng dường đá, bánh xe như gió lốc."));
visuperobject.biblemap.put("b23_5_29", new visuperobject(23,5,29,"Tiếng gầm khác nào sư tử cái; rống như sư tử con; chúng nó sẽ rống và bắt mồi đem đi, không ai cứu được."));
visuperobject.biblemap.put("b23_5_30", new visuperobject(23,5,30,"Trong ngày đó, sẽ có tiếng gầm nghịch cùng nó khác nào tiếng sóng biển ầm ầm; nếu người ta nó vào xứ, chỉ thấy tối tăm và khốn nạn: sự sáng sẽ bị che tối bước các chòm mây."));
visuperobject.biblemap.put("b23_6_1", new visuperobject(23,6,1,"Về năm vua Ô-xia băng, tôi thấy Chúa ngồi trên ngôi cao sang, vạt áo của Ngài đầy dẫy đền thờ."));
visuperobject.biblemap.put("b23_6_2", new visuperobject(23,6,2,"Những sê-ra-phin đứng bên trên Ngài; mỗi sê-ra-phin có sáu cánh, hai cái che mặt, hai cái che chơn và hai cái dùng để bay."));
visuperobject.biblemap.put("b23_6_3", new visuperobject(23,6,3,"Các sê-ra-phin cùng nhau kêu lên rằng: Thánh thay, thánh thay, thánh thay là Ðức Giê-hô-va vạn quân! Khắp đất đầy dẫy sự vinh hiển Ngài!"));
visuperobject.biblemap.put("b23_6_4", new visuperobject(23,6,4,"Nhơn tiếng kêu đó, các nền ngạch cửa rúng động, và đền đầy những khói."));
visuperobject.biblemap.put("b23_6_5", new visuperobject(23,6,5,"Bấy giờ tôi nói: Khốn nạn cho tôi! Xong đời tôi rồi! Vì tôi là người có môi dơ dáy, ở giữa một dân có môi dơ dáy, bởi mắt tôi đã thấy Vua, tức là Ðức Giê-hô-va vạn quân!"));
visuperobject.biblemap.put("b23_6_6", new visuperobject(23,6,6,"Bấy giờ một sê-ra-phin bay đến tôi, tay cầm than lửa đỏ mà đã dùng kiềm gắp nơi bàn thờ,"));
visuperobject.biblemap.put("b23_6_7", new visuperobject(23,6,7,"để trên miệng ta, mà nói rằng: Nầy, cái nầy đã chạm đến môi ngươi; lỗi ngươi được bỏ rồi, tội ngươi được tha rồi."));
visuperobject.biblemap.put("b23_6_8", new visuperobject(23,6,8,"Ðoạn, tôi nghe tiếng Chúa phán rằng: Ta sẽ sai ai đi? Ai sẽ đi cho chúng ta? Tôi thưa rằng: Có tôi đây; xin hãy sai tôi."));
visuperobject.biblemap.put("b23_6_9", new visuperobject(23,6,9,"Ngài phán: Ði đi! nói với dân nầy rằng: Các ngươi hãy nghe, nhưng chẳng hiểu chi; hãy xem, nhưng chẳng thấy chi."));
visuperobject.biblemap.put("b23_6_10", new visuperobject(23,6,10,"Hãy làm cho dân ấy béo lòng, nặng tai, nhắm mắt, e rằng mắt nó thấy được, tai nó nghe được, lòng nó hiểu được, nó trở lại và được chữa lành chăng!"));
visuperobject.biblemap.put("b23_6_11", new visuperobject(23,6,11,"Tôi lại nói: Lạy Chúa, cho đến chừng nào? Ngài đáp rằng: Cho đến chừng các thành bị hoang vu, không có dân ở, nhà không có người, và đất đã nên hoang vu cả;"));
visuperobject.biblemap.put("b23_6_12", new visuperobject(23,6,12,"cho đến chừng Ðức Giê-hô-va dời dân đi xa, và trong xứ rất là tiêu điều."));
visuperobject.biblemap.put("b23_6_13", new visuperobject(23,6,13,"Giả sử còn lại một phần mười dân cư tại đó, họ sẽ lại bị thiêu nuốt. Nhưng, như khi người ta hạ cây thông cây dẽ, chừa gốc nó lại, thì cũng vậy, giống thánh là gốc của nó."));
visuperobject.biblemap.put("b23_7_1", new visuperobject(23,7,1,"Về đời A-cha, con trai Giô-tham, cháu Ô-xia, vua nước Giu-đa, xảy có Rê-xin, vua xứ Sy-ri, và Phê-ca, con trai Rê-ma-lia, vua Y-sơ-ra-ên, lên đánh thành Giê-ru-sa-lem; nhưng không thắng được."));
visuperobject.biblemap.put("b23_7_2", new visuperobject(23,7,2,"Có người váo tin đó cho nhà Ða-vít, mà rằng: Sy-ri kết minh cùng Ép-ra-im. Bấy giờ A-cha và dân sự người trong lòng kinh động, như cây trên rừng bị gió day."));
visuperobject.biblemap.put("b23_7_3", new visuperobject(23,7,3,"Ðức Giê-hô-va bèn phán cùng Ê-sai rằng: Ngươi cùng con trai ngươi là Sê-a-Gia-rúp hãy đi đón A-cha tại nơi cuối cống ao trên, trên đường cái ruộng thợ nện,"));
visuperobject.biblemap.put("b23_7_4", new visuperobject(23,7,4,"mà nói cùng người rằng: Hãy cẩn thận, ở yên lặng; đừng sợ chi, lòng ngươi chớ bủn rủn vì cớ hai đuôi đuốc có khói, tức là vì cơn giận dữ của Rê-xin và Sy-ri, và của con trai Rê-ma-lia."));
visuperobject.biblemap.put("b23_7_5", new visuperobject(23,7,5,"Vì Sy-ri với Ép-ra-im và con trai của Rê-ma-lia đồng mưu hại ngươi, nói rằng:"));
visuperobject.biblemap.put("b23_7_6", new visuperobject(23,7,6,"Chúng ta hãy lên nghịch cùng Giu-đa, khuấy rối nó, phá thành và lập một vua giữa nó, tức là con trai của Ta-bê-ên."));
visuperobject.biblemap.put("b23_7_7", new visuperobject(23,7,7,"Chúa là Ðức Giê-hô-va phán như vầy: Sự ngăm đe ấy không thành, điều đó không xảy ra!"));
visuperobject.biblemap.put("b23_7_8", new visuperobject(23,7,8,"Vì đầu của Sy-ri là Ða-mách, đầu của Ða-mách là Rê-xin. Còn trong sáu mươi lăm năm, Ép-ra-im sẽ bị hủy diệt, không được kể là dân nữa."));
visuperobject.biblemap.put("b23_7_9", new visuperobject(23,7,9,"Ðầu của Ép-ra-im là Sa-ma-ri, đầu Sa-ma-ri là con trai của Rê-ma-lia. Nếu các ngươi không tin, chắc sẽ không đứng vững được."));
visuperobject.biblemap.put("b23_7_10", new visuperobject(23,7,10,"Ðức Giê-hô-va lại phán cùng A-cha rằng:"));
visuperobject.biblemap.put("b23_7_11", new visuperobject(23,7,11,"Hãy xin Giê-hô-va Ðức Chúa Trời ngươi một điềm, hoặc dưới vực sâu, hoặc trên trời cao."));
visuperobject.biblemap.put("b23_7_12", new visuperobject(23,7,12,"A-cha thưa rằng: Tôi sẽ chẳng xin tôi cũng chẳng thử Ðức Giê-hô-va."));
visuperobject.biblemap.put("b23_7_13", new visuperobject(23,7,13,"Ê-sai bèn nói rằng: Hỡi nhà Ða-vít, hãy nghe! Các ngươi cho làm phiền người ta là nhỏ mọn, mà muốn làm phiền Ðức Chúa Trời ta nữa sao?"));
visuperobject.biblemap.put("b23_7_14", new visuperobject(23,7,14,"Vậy nên, chính Chúa sẽ ban một điềm cho các ngươi: nầy một gái đồng trinh sẽ chịu thai, sanh ra một trai, và đặt trên là Em-ma-nu-ên."));
visuperobject.biblemap.put("b23_7_15", new visuperobject(23,7,15,"Con trẻ ấy sẽ ăn mỡ sữa và mật, cho đến chừng nào biết bỏ điều dữ và chọn điều lành."));
visuperobject.biblemap.put("b23_7_16", new visuperobject(23,7,16,"Vả, trước khi con trẻ biết bỏ điều dữ chọn điều lành, thì nước của hai vua mà ngươi đương ghét sẽ bị bỏ hoang."));
visuperobject.biblemap.put("b23_7_17", new visuperobject(23,7,17,"Ðức Giê-hô-va sẽ giáng trên ngươi, dân ngươi, và nhà cho ngươi, bởi vua A-si-ri, những ngày mà từ ngày Ép-ra-im lìa bỏ Giu-đa chưa từng có giống như vậy."));
visuperobject.biblemap.put("b23_7_18", new visuperobject(23,7,18,"Xảy ra trong ngày đó, Ðức Giê-hô-va sẽ suýt mà gọi những ruồi ở cuối các sông Ê-díp-tô, và những ong ở xứ A-si-ri."));
visuperobject.biblemap.put("b23_7_19", new visuperobject(23,7,19,"Hai giống đó sẽ đến đậu trong các nơi trũng hoang loạn, trong các lỗ nẻ vầng đá, trên rào gai cùng các đồng cỏ."));
visuperobject.biblemap.put("b23_7_20", new visuperobject(23,7,20,"Trong ngày đó Ðức Giê-hô-va sẽ dùng dao cạo thuê tại bên kia sông, nghĩa là vua A-si-ri, mà cạo đầu cùng lông chơn, và bỏ cả râu nữa."));
visuperobject.biblemap.put("b23_7_21", new visuperobject(23,7,21,"Trong ngày đó mỗi người có thể nuôi một con bò cái tơ và hai con chiên,"));
visuperobject.biblemap.put("b23_7_22", new visuperobject(23,7,22,"bởi nó có sữa dư dật thì người sẽ ăn mỡ sữa; vì phàm ai sẽ còn sót lại giữa xứ, thì nuôi mình bằng mỡ sữa và mật."));
visuperobject.biblemap.put("b23_7_23", new visuperobject(23,7,23,"Xảy ra trong ngày đó, phàm chỗ đất trồng được một ngàn gốc nho, đáng giá một ngàn siếc-lơ bạc, sẽ mọc đầy những gai gốc và chà chuôm."));
visuperobject.biblemap.put("b23_7_24", new visuperobject(23,7,24,"Người ta sẽ đem cung tên đến đó, vì cả xứ chỉ là gai gốc và chà chuôm. vậy."));
visuperobject.biblemap.put("b23_7_25", new visuperobject(23,7,25,"Cũng không ai đến trên các gò là nơi mình đã cày cuốc nữa, vì sợ gai gốc và chà chuôm, nhưng sẽ cho bò ăn cỏ tại đó, và chiên giày đạp."));
visuperobject.biblemap.put("b23_8_1", new visuperobject(23,8,1,"Ðức Giê-hô-va phán cùng tôi rằng: Hãy lấy một cái bảng rộng và viết lên trên bằng bút loài người rằng: Ma-he-Sa-la-Hát-Bát."));
visuperobject.biblemap.put("b23_8_2", new visuperobject(23,8,2,"Tôi đem theo những kẻ làm chứng đáng tin, tức là U-ri, thầy tế lễ, và Xa-cha-ri, con trai của Giê-bê-rê-kia."));
visuperobject.biblemap.put("b23_8_3", new visuperobject(23,8,3,"Ðoạn, tôi đến nhà nữ tiên tri; người chịu thai và sanh một con trai. Ðức Giê-hô-va bèn phán cùng tôi rằng: Hãy đặt tên nó là Ma-he-Sa-la-Hát-Bát"));
visuperobject.biblemap.put("b23_8_4", new visuperobject(23,8,4,"Vì, trước khi con trẻ biết kêu: Cha ơi! mẹ ơi! thì người ta sẽ cất lấy sự giàu có Ða-mách và của cướp Sa-ma-ri trước mặt vua A-si-ri."));
visuperobject.biblemap.put("b23_8_5", new visuperobject(23,8,5,"Ðức Giê-hô-va lại phán cùng tôi rằng:"));
visuperobject.biblemap.put("b23_8_6", new visuperobject(23,8,6,"Vì dân nầy đã khinh bỏ các dòng nước Si-lô-ê chảy dịu, và ưa thích Rê-xin cùng con trai của Rê-ma-lia."));
visuperobject.biblemap.put("b23_8_7", new visuperobject(23,8,7,"Vì cớ đó, nầy, Chúa sẽ khiến nước Sông mạnh và nhiều đến trên họ, tức là vua A-si-ri và cả oai vinh người; nó sẽ dấy lên khắp trên lòng sông, khỏa lên các bực;"));
visuperobject.biblemap.put("b23_8_8", new visuperobject(23,8,8,"chảy vào Giu-đa, tràn lan chảy xói, ngập cho đến cổ; hỡi Em-ma-nu-ên, nó sè cánh ra che cả xứ ngươi."));
visuperobject.biblemap.put("b23_8_9", new visuperobject(23,8,9,"Hỡi các dân, hãy kêu la và bị tan nát! Hỡi các ngươi hết thảy, là dân các phương xa, hãy lắng tai! Hãy nịt lưng các ngươi, và bị tan nát!"));
visuperobject.biblemap.put("b23_8_10", new visuperobject(23,8,10,"Hãy toan mưu cùng nhau, nó sẽ nên hư không; hãy nói, lời các ngươi sẽ không đứng, vì Ðức Chúa Trời ở cùng chúng ta."));
visuperobject.biblemap.put("b23_8_11", new visuperobject(23,8,11,"Vì Ðức Giê-hô-va dùng tay mạnh phán cùng ta, và dạy ta đừng noi theo đường dân ấy,"));
visuperobject.biblemap.put("b23_8_12", new visuperobject(23,8,12,"rằng: Khi dân nầy nói rằng: Kết đảng! thì các ngươi chớ nói rằng: Kết đảng! Chớ sợ điều nó sợ, và đừng kinh hãi."));
visuperobject.biblemap.put("b23_8_13", new visuperobject(23,8,13,"Hãy tôn Ðức Giê-hô-va vạn quân là thánh; các ngươi chỉ nên sợ Ngài và kinh hãi Ngài."));
visuperobject.biblemap.put("b23_8_14", new visuperobject(23,8,14,"Ngài sẽ là nơi thánh, nhưng cũng là hòn đá vấp ngã, vầng đó vướng mắc cho cả hai nhà Y-sơ-ra-ên, và là bẫy cùng lưới cho dân cư thành Giê-ru-sa-lem vậy."));
visuperobject.biblemap.put("b23_8_15", new visuperobject(23,8,15,"Nhiều người trong bọn họ sẽ vấp chơn; sẽ té và giập nát; sẽ sa vào lưới và bị bắt."));
visuperobject.biblemap.put("b23_8_16", new visuperobject(23,8,16,"Ngươi hãy gói lời chứng nầy, niêm phong luật pháp nầy trong môn đồ ta!"));
visuperobject.biblemap.put("b23_8_17", new visuperobject(23,8,17,"Tôi trông đợi Ðức Giê-hô-va, là Ðấng ẩn mặt Ngài khỏi nhà Gia-cốp, tôi vẫn ngóng trông Ngài!"));
visuperobject.biblemap.put("b23_8_18", new visuperobject(23,8,18,"Nầy, tôi đây, với con cái mà Ðức Giê-hô-va đã ban cho tôi, là dấu và điềm trong Y-sơ-ra-ên, bởi Ðức Giê-hô-va vạn quân ngự trên núi Si-ôn."));
visuperobject.biblemap.put("b23_8_19", new visuperobject(23,8,19,"Nếu có ai bảo các ngươi: Hãy cầu hỏi đồng bóng và thầy bói, là kẻ nói rúi rít líu lo, thì hãy đáp rằng: Một dân tộc há chẳng nên cầu hỏi Ðức Chúa Trời mình sao? Há lại vì người sống mà hỏi kẻ chết sao?"));
visuperobject.biblemap.put("b23_8_20", new visuperobject(23,8,20,"Hãy theo luật pháp và lời chứng! Nếu dân chẳng nói như vậy, chắc sẽ chẳng có rạng đông cho nó."));
visuperobject.biblemap.put("b23_8_21", new visuperobject(23,8,21,"Nó sẽ đi lưu lạc trên đất, khốn khổ đói khát; trong cơn đói, nó bực mình, nguyền rủa vua và Ðức Chúa Trời mình. Nó sẽ ngước xem trên cao;"));
visuperobject.biblemap.put("b23_8_22", new visuperobject(23,8,22,"rồi cúi xem dưới đất, chỉ thấy sự hoạn nạn và mờ mịt, chỉ thấy bóng buồn rầu: nó sẽ bị đuổi vào nơi tối tăm mờ mịt."));
visuperobject.biblemap.put("b23_9_1", new visuperobject(23,9,1,"(8:23) Nhưng, cho người đã chịu buồn rầu thì sẽ không có sự mờ mịt. Trong đời xưa Ðức Chúa Trời đã hạ đất Sa-bu-lôn và đất Nép-ta-li xuống; nhưng trong kỳ sau Ngài đã làm cho đất gần mé biển, tức là nơi bên kia sông Giô-đanh, trong xứ Ga-li-lê của dân ngoại, được vinh hiển."));
visuperobject.biblemap.put("b23_9_2", new visuperobject(23,9,2,"(9:1) Dân đi trong nơi tối tăm đã thấy sự sáng lớn; và sự sáng đã chiếu trên những kẻ ở xứ thuộc về bóng của sự chết."));
visuperobject.biblemap.put("b23_9_3", new visuperobject(23,9,3,"(9:2) Chúa đã làm cho dân nầy thêm nhiều; và thêm sự vui cho họ. Mọi người đều vui mừng trước mặt Chúa, như vui mừng trong ngày mùa gặt, như người ta reo vui trong lúc chia của cướp."));
visuperobject.biblemap.put("b23_9_4", new visuperobject(23,9,4,"(9:3) Vì Chúa đã bẻ cái ách họ mang, cái roi đánh trên vai họ, cái gậy của kẻ hà hiếp, như trong ngày của Ma-đi-an."));
visuperobject.biblemap.put("b23_9_5", new visuperobject(23,9,5,"(9:4) Cả giày dép của kẻ đánh giặc trong khi giao chiến, cùng cả áo xống vấy máu, đều dùng để đốt và làm đồ chụm lửa."));
visuperobject.biblemap.put("b23_9_6", new visuperobject(23,9,6,"(9:5) Vì có một con trẻ sanh cho chúng ta, tức là một con trai ban cho chúng ta; quyền cai trị sẽ nấy trên vai Ngài. Ngài sẽ được xưng là Ðấng Lạ lùng, là Ðấng Mưu luận, là Ðức Chúa Trời Quyền năng, là Cha Ðời đời, là Chúa Bình an."));
visuperobject.biblemap.put("b23_9_7", new visuperobject(23,9,7,"(9:6) Quyền cai trị và sự bình an của Ngài cứ thêm mãi không thôi, ở trên ngôi Ða-vít và trên nước Ngài, đặng làm cho nước bền vững, và lập lên trong sự chánh trực công bình, từ nay cho đến đời đời. Thật, lòng sốt sắng của Ðức Giê-hô-va vạn quân sẽ làm nên sự ấy!"));
visuperobject.biblemap.put("b23_9_8", new visuperobject(23,9,8,"(9:7) Chúa đã giáng một lời trong Gia-cốp, và lời ấy đổ xuống trên Y-sơ-ra-ên."));
visuperobject.biblemap.put("b23_9_9", new visuperobject(23,9,9,"(9:8) Cả dân sẽ biết điều đó, tức là Ép-ra-im cùng dân cư Sa-ma-ri, họ đem lòng kiêu căng ỷ thị mà nói rằng:"));
visuperobject.biblemap.put("b23_9_10", new visuperobject(23,9,10,"(9:9) Gạch đã đổ, nhưng chúng ta sẽ xây bằng đá đẽo; cây sung đã bị đốn, nhưng chúng ta sẽ thay bằng cây hương bách."));
visuperobject.biblemap.put("b23_9_11", new visuperobject(23,9,11,"(9:10) Vậy nên, Ðức Giê-hô-va sẽ khiến kẻ đối địch ở Rê-xin dấy lên nghịch cùng dân sự, và khích chọc kẻ cừu thù,"));
visuperobject.biblemap.put("b23_9_12", new visuperobject(23,9,12,"(9:11) dân Sy-ri đằng trước, dân Phi-li-tin đằng sau, hả miệng nuốt Y-sơ-ra-ên. Dầu vậy, cơn giận Ngài chẳng lánh khỏi, nhưng tay Ngài còn giơ ra!"));
visuperobject.biblemap.put("b23_9_13", new visuperobject(23,9,13,"(9:12) Nhưng mà dân sự chẳng xây về Ðấng đánh mình, chẳng tin Ðức Giê-hô-va vạn quân."));
visuperobject.biblemap.put("b23_9_14", new visuperobject(23,9,14,"(9:13) Cho nên chỉ trong một ngày, Ðức Giê-hô-va sẽ dứt đầu và đuôi, cây kè và cây lác của Y-sơ-ra-ên."));
visuperobject.biblemap.put("b23_9_15", new visuperobject(23,9,15,"(9:14) Ðầu, tức là trưởng lão và kẻ tôn trọng; đuôi, tức là người tiên tri dạy sự nói dối."));
visuperobject.biblemap.put("b23_9_16", new visuperobject(23,9,16,"(9:15) Những kẻ dắt dân nầy làm cho họ sai lạc, còn những kẻ chịu dắt bị diệt mất."));
visuperobject.biblemap.put("b23_9_17", new visuperobject(23,9,17,"(9:16) Vậy nên, Chúa chẳng đẹp lòng về bọn trai trẻ của họ, và chẳng thương xót đến kẻ mồ côi góa bụa chút nào; vì họ đều là khinh lờn, gian ác, miệng nào cũng nói điều càn dỡ. Dầu vậy, cơn giận Ngài chẳng lánh khỏi, nhưng tay Ngài còn giơ ra!"));
visuperobject.biblemap.put("b23_9_18", new visuperobject(23,9,18,"(9:17) Vì sự hung ác hừng lên như lửa thiêu cháy gai gốc và chà chuôm, đốt các nơi rậm trong rừng, trụ khói cuộn lên."));
visuperobject.biblemap.put("b23_9_19", new visuperobject(23,9,19,"(9:18) Ðất bị thiêu đốt bởi cơn giận của Ðức Giê-hô-va vạn quân, dân sự trở nên mồi của lửa; chẳng ai thương tiếc anh em mình."));
visuperobject.biblemap.put("b23_9_20", new visuperobject(23,9,20,"(9:19) Có kẻ cướp bên hữu, mà vẫn cứ đói; có kẻ ăn bên tả, mà chẳng được no; ai nấy ăn thịt mình chính cánh tay mình."));
visuperobject.biblemap.put("b23_9_21", new visuperobject(23,9,21,"(9:20) Ma-na-se nghịch cùng Ép-ra-im, Ép-ra-im nghịch cùng Ma-na-se, và cả hai đều nghịch cùng Giu-đa! Dầu vậy, cơn giận của Ðức Giê-hô-va chẳng lánh khỏi, nhưng tay Ngài còn giơ ra."));
visuperobject.biblemap.put("b23_10_1", new visuperobject(23,10,1,"Kốn thay cho những kẻ lập luật không công bình, cho những kẻ chép lời trái lẽ,"));
visuperobject.biblemap.put("b23_10_2", new visuperobject(23,10,2,"cất mất sự công bình khỏi kẻ nghèo, cướp lẽ phải của kẻ khốn khó trong dân ta, lấy kẻ góa bụa làm miếng mồi, kẻ mồ côi làm của cướp!"));
visuperobject.biblemap.put("b23_10_3", new visuperobject(23,10,3,"Tới ngày thăm phạt, khi họa hoạn từ xa mà đến, các ngươi sẽ làm thể nào? Các ngươi trốn đến cùng ai để cầu cứu, và để sự vinh hiển mình ở nơi nào?"));
visuperobject.biblemap.put("b23_10_4", new visuperobject(23,10,4,"Chúng nó chỉ có khom lưng dưới những kẻ phu tù, và ngã dưới những kẻ bị giết! Dầu vậy, cơn giận của Chúa chẳng lánh khỏi, nhưng tay Ngài còn giơ ra."));
visuperobject.biblemap.put("b23_10_5", new visuperobject(23,10,5,"Hỡi A-si-ri, là cái roi của sự thạnh nộ ta! Cái gậy cầm trong tay nó là sự tức giận của ta vậy!"));
visuperobject.biblemap.put("b23_10_6", new visuperobject(23,10,6,"Ta sẽ sai nó nghịch cùng một nước chẳng tin kính; khiến nó đi đánh một dân mà ta nổi giận, để cướp lấy, bóc lột, và giày đạp chúng nó như bùn ngoài đường."));
visuperobject.biblemap.put("b23_10_7", new visuperobject(23,10,7,"Nhưng nó không có ý thế, trong lòng nó chẳng nghĩ như vậy: lại thích phá hại, và hủy diệt nhiều dân."));
visuperobject.biblemap.put("b23_10_8", new visuperobject(23,10,8,"Vì nó nói rằng: Các quan trưởng của ta há chẳng phải đều là vua sao?"));
visuperobject.biblemap.put("b23_10_9", new visuperobject(23,10,9,"Há lại Ca-nô chẳng như Cạt-kê-mít, Ha-mát chẳng như Aït-bát, Sa-ma-ri chẳng như Ða-mách sao?"));
visuperobject.biblemap.put("b23_10_10", new visuperobject(23,10,10,"Như tay ta đã tới đến các nước của thần tượng, tượng chạm của họ lại hơn của Giê-ru-sa-lem và Sa-ma-ri nữa,"));
visuperobject.biblemap.put("b23_10_11", new visuperobject(23,10,11,"thì ta há chẳng sẽ làm cho Giê-ru-sa-lem và thần tượng nó cũng như ta đã làm cho Sa-ma-ri và thần tượng nó sao?"));
visuperobject.biblemap.put("b23_10_12", new visuperobject(23,10,12,"Nên sẽ xảy ra khi Chúa đã làm xong cả việc trên núi Si-ôn và tại Giê-ru-sa-lem, thì chính ta sẽ hành phạt những bông trái bởi lòng kiêu ngạo và sự vinh hiển của con mắt ngó cao của vua A-si-ri."));
visuperobject.biblemap.put("b23_10_13", new visuperobject(23,10,13,"Vì người nói rằng: Ta đã làm điều đó bởi sức mạnh của tay ta, bởi sự khôn ngoan ta, và ta là thông minh. Ta đã dời đổi giới hạn các dân; đã cướp của quí họ, như một người mạnh đã làm cho những kẻ đương ngồi phải xuống."));
visuperobject.biblemap.put("b23_10_14", new visuperobject(23,10,14,"Tay ta đã tìm được của cải các dân như bắt ở chim, vơ vét cả đất như lượm trứng đã bỏ; chẳng có ai đập cánh, mở mỏ, hay là kêu hót!"));
visuperobject.biblemap.put("b23_10_15", new visuperobject(23,10,15,"Cái rìu há lại khoe mình cùng người cầm rìu ư? Cái cưa há lại dấy lên nghịch cùng kẻ cầm cưa ư? Thế cũng như cái roi vận động kẻ giơ roi, cái gậy nâng đỡ cánh tay cầm gậy!"));
visuperobject.biblemap.put("b23_10_16", new visuperobject(23,10,16,"Vậy nên, Chúa, là Ðức Giê-hô-va vạn quân, sẽ làm cho những người mập mạnh của nó trở nên gầy mòn, và dưới sự vinh hiển nó sẽ đốt cháy lên như lửa thiêu đốt."));
visuperobject.biblemap.put("b23_10_17", new visuperobject(23,10,17,"Sự sáng của Y-sơ-ra-ên sẽ trở nên lửa, Ðấng Thánh nó sẽ trở nên ngọn đèn, chỉ trong một ngày, đốt tiêu gai gốc và chà chuôm nó;"));
visuperobject.biblemap.put("b23_10_18", new visuperobject(23,10,18,"lại thiêu hủy vinh hoa của rừng cây và ruộng màu mỡ nó, các linh hồn và thân thể; như kẻ cầm cờ xí ngất đi vậy."));
visuperobject.biblemap.put("b23_10_19", new visuperobject(23,10,19,"Bấy giờ những cây trên rừng nó còn sót lại chẳng là bao, một đứa bé con có thể chép lấy được."));
visuperobject.biblemap.put("b23_10_20", new visuperobject(23,10,20,"Trong ngày đó, dân sót của Y-sơ-ra-ên, và người thoát nạn của nhà Gia-cốp, sẽ không cậy kẻ đánh mình nữa; nhưng thật lòng cậy Ðức Giê-hô-va, là Ðấng Thánh của Y-sơ-ra-ên."));
visuperobject.biblemap.put("b23_10_21", new visuperobject(23,10,21,"Một số dân sót, tức là dân sót của Gia-cốp, sẽ trở lại cùng Ðức Chúa Trời quyền năng."));
visuperobject.biblemap.put("b23_10_22", new visuperobject(23,10,22,"Hỡi Y-sơ-ra-ên, dầu dân ngươi như cát biển, chỉ một phần sót lại sẽ trở lại. Sự hủy diệt đã định, bởi vì sự công bình tràn khắp."));
visuperobject.biblemap.put("b23_10_23", new visuperobject(23,10,23,"Vì Chúa, là Ðức Giê-hô-va vạn quân, đã định làm trọn sự hủy diệt trong khắp đất."));
visuperobject.biblemap.put("b23_10_24", new visuperobject(23,10,24,"Vậy có lời Chúa, là Ðức Giê-hô-va vạn quân, phán như vầy: Hỡi dân ta, ngươi ở tại Si-ôn, chớ sợ người A-si-ri, dầu họ lấy roi đánh ngươi và giá gậy trên ngươi theo cách người Ê-díp-tô."));
visuperobject.biblemap.put("b23_10_25", new visuperobject(23,10,25,"Vì còn ít lâu nữa, ta sẽ hết giận các ngươi; cơn thạnh nộ ta trở nghịch cùng dân ấy đặng hủy diệt nó."));
visuperobject.biblemap.put("b23_10_26", new visuperobject(23,10,26,"Bấy giờ Ðức Giê-hô-va vạn quân sẽ giá roi trên họ, như Ngài đã đánh giết người Ma-đi-an nơi vầng đá Hô-rếp; lại sẽ giơ gậy trên biển, sẽ giơ lên như đã làm trong xứ Ê-díp-tô."));
visuperobject.biblemap.put("b23_10_27", new visuperobject(23,10,27,"Trong ngày đó, gánh nặng nó sẽ cất khỏi vai ngươi, ách nó cất khỏi cổ ngươi, ách sẽ bị gãy vì sự béo mập."));
visuperobject.biblemap.put("b23_10_28", new visuperobject(23,10,28,"Nó đi đến A-giát: trải qua Mi-gơ-rôn, để đồ đạc tại Mích-ma."));
visuperobject.biblemap.put("b23_10_29", new visuperobject(23,10,29,"Chúng nó vuợt qua ải, đóng trại tại Ghê-ba; Ra-ma run sợ; Ghi-bê-a của Sau-lơ chạy trốn."));
visuperobject.biblemap.put("b23_10_30", new visuperobject(23,10,30,"Hỡi con gái Ga-lim, hãy cất tiếng kêu to! Hỡi người La-ít, hãy ghé tai mà nghe! Thương thay cho người A-na-tốt!"));
visuperobject.biblemap.put("b23_10_31", new visuperobject(23,10,31,"Mát-mê-na trốn tránh; dân cư Ghê-bim tìm chỗ núp."));
visuperobject.biblemap.put("b23_10_32", new visuperobject(23,10,32,"Chính ngày đó chúng nó sẽ đóng tại Nóp, và vung tay nghịch cùng núi của con gái Si-ôn, nghịch cùng đồi của Giê-ru-sa-lem!"));
visuperobject.biblemap.put("b23_10_33", new visuperobject(23,10,33,"Nầy, Chúa, là Ðức Giê-hô-va vạn quân, sẽ dùng oai mạnh đốn các nhánh cây; những kẻ cao lớn đều bị chặt, những kẻ kiêu ngạo bị hạ xuống."));
visuperobject.biblemap.put("b23_10_34", new visuperobject(23,10,34,"Ngài dùng sắt chặt những bụi rậm trên rừng, và Li-ban bị ngã xuống bởi người mạnh sức."));
visuperobject.biblemap.put("b23_11_1", new visuperobject(23,11,1,"Có một chồi sẽ nứt lên từ gốc Y-sai, một nhành từ rễ nó sẽ ra trái."));
visuperobject.biblemap.put("b23_11_2", new visuperobject(23,11,2,"Thần của Ðức Giê-hô-va sẽ ngự trên Ngài, tức là thần khôn ngoan và thông sáng, thần mưu toan và mạnh sức, thần hiểu biết và kính sợ Ðức Giê-hô-va."));
visuperobject.biblemap.put("b23_11_3", new visuperobject(23,11,3,"Ngài lấy sự kính sợ Ðức Giê-hô-va làm vui; chẳng phán xét theo mắt mình thấy, và chẳng cứ sự tai nghe mà đoán định."));
visuperobject.biblemap.put("b23_11_4", new visuperobject(23,11,4,"Nhưng Ngài sẽ dùng sự công bình xét đoán kẻ nghèo, và xử lẽ ngay thẳng cho kẻ nhu mì trên đất. Ngài sẽ đánh thế gian bằng cái gậy của mình mình, và lấy hơi thở nơi môi mà giết kẻ ác."));
visuperobject.biblemap.put("b23_11_5", new visuperobject(23,11,5,"Sự công bình sẽ làm dây thắt lưng của Ngài, sự thành tín sẽ làm dây ràng hông."));
visuperobject.biblemap.put("b23_11_6", new visuperobject(23,11,6,"Bấy giờ muông sói sẽ ở với chiên con, beo nằm với dê con; bò con, sư tử con với bò nuôi mập cùng chung một chỗ, một đứa con trẻ sẽ dắt chúng nó đi."));
visuperobject.biblemap.put("b23_11_7", new visuperobject(23,11,7,"Bò cái sẽ ăn với gấu; các con nhỏ chúng nó nằm chung, sư tử ăn cỏ khô như bò."));
visuperobject.biblemap.put("b23_11_8", new visuperobject(23,11,8,"Trẻ con đương bú sẽ chơi kề ở rắn hổ mang, trẻ con thôi bú sẽ thò tay vào hang rắn lục."));
visuperobject.biblemap.put("b23_11_9", new visuperobject(23,11,9,"Nó chẳng làm hại, chẳng giết ai trong cả núi thánh của ta; vì thế gian sẽ đầy dẫy sự hiểu biết Ðức Giê-hô-va, như các dòng nước che lấp biển."));
visuperobject.biblemap.put("b23_11_10", new visuperobject(23,11,10,"Xảy ra trong ngày đó, rễ Y-sai đứng lên làm cờ cho muôn dân, các nước đều tìm đến Ngài, nơi an nghỉ của Ngài sẽ được vinh hiển."));
visuperobject.biblemap.put("b23_11_11", new visuperobject(23,11,11,"Trong ngày đó, Chúa còn đặt tay một lần nữa đặng chuộc dân sót của Ngài, tức là kẻ sót lại bởi A-si-ri, Ê-díp-tô, Pha-trô, Cút, Ê-lam, Si-nê-a, Ha-mát cùng các cù lao biển."));
visuperobject.biblemap.put("b23_11_12", new visuperobject(23,11,12,"Chúa sẽ dựng một cây cờ cho các nước; nhóm những người Y-sơ-ra-ên bị đuổi, thâu những người Giu-đa lưu lạc, từ bốn góc đất."));
visuperobject.biblemap.put("b23_11_13", new visuperobject(23,11,13,"Bấy giờ sự ghen tương của Ép-ra-im sẽ mất, kẻ khuấy rối Giu-đa sẽ bị trừ đi; Ép-ra-im không ghen ghét Giu-đa nữa, Giu-đa không khuấy rối Ép-ra-im nữa."));
visuperobject.biblemap.put("b23_11_14", new visuperobject(23,11,14,"Chúng nó sẽ bay trên vai đến Phi-li-tin về phía tây; cùng nhau cướp giựt con cái phương đông; tra tay trên Ê-đôm và Mô-áp; con cái Am-môn sẽ vâng phục họ."));
visuperobject.biblemap.put("b23_11_15", new visuperobject(23,11,15,"Ðức Giê-hô-va sẽ làm cạn tắt giải biển Ê-díp-tô; vung tay trên Sông cái bởi gió cháy của Ngài, đánh mà chia ra làm bảy suối, và khiến người ta có thể đi qua không ướt dép."));
visuperobject.biblemap.put("b23_11_16", new visuperobject(23,11,16,"Vậy, sẽ có một đường cái cho dân sót của Ngài, là những kẻ sót lại bởi A-si-ri, cũng như đã có một đường cái cho Y-sơ-ra-ên trong ngày họ ra khỏi xứ Ê-díp-tô vậy."));
visuperobject.biblemap.put("b23_12_1", new visuperobject(23,12,1,"Trong ngày đó, ngươi sẽ nói rằng: Hỡi Ðức Giê-hô-va, tôi cảm tạ Chúa; vì Chúa vốn giận tôi, nhưng cơn giận Chúa đã lành khỏi, và Chúa yên ủi tôi."));
visuperobject.biblemap.put("b23_12_2", new visuperobject(23,12,2,"Nầy, Ðức Chúa Trời là sự cứu rỗi tôi; tôi sẽ tin cậy và không sợ hãi. Vì Ðức Giê-hô-va, chính Ðức Giê-hô-va, là sức mạnh của tôi, lời ca tụng của tôi; Ngài đã nên sự cứu rỗi tôi."));
visuperobject.biblemap.put("b23_12_3", new visuperobject(23,12,3,"Vậy nên các ngươi sẽ vui vẻ mà múc nước nơi các nguồn sự cứu;"));
visuperobject.biblemap.put("b23_12_4", new visuperobject(23,12,4,"và trong ngày đó các ngươi sẽ nói rằng: Hãy cảm tạ Ðức Giê-hô-va; hãy kêu cầu danh Ngài; hãy rao mọi việc của Ngài ra trong các dân tộc! Hãy xưng danh Ngài là tôn trọng!"));
visuperobject.biblemap.put("b23_12_5", new visuperobject(23,12,5,"Hãy ca tụng Ðức Giê-hô-va, vì Ngài đã làm những công việc rực rỡ: nên phô cho thế gian đều biết!"));
visuperobject.biblemap.put("b23_12_6", new visuperobject(23,12,6,"Hỡi dân cư Si-ôn, hãy kêu lên to tiếng! Vì Ðấng Thánh của Y-sơ-ra-ên là tôn trọng giữa ngươi."));
visuperobject.biblemap.put("b23_13_1", new visuperobject(23,13,1,"Gánh nặng về Ba-by-lôn, mà Ê-sai, con trai A-mốt, đã thấy."));
visuperobject.biblemap.put("b23_13_2", new visuperobject(23,13,2,"Hãy dựng cờ trên núi trọi! Hãy cất tiếng kêu chúng nó, lấy tay mà ngoắt, cho người ta vào các cửa người sang trọng!"));
visuperobject.biblemap.put("b23_13_3", new visuperobject(23,13,3,"Chính ta đã truyền lịnh cho kẻ ta đã biệt riêng ra, và đã gọi những người mạnh mẽ của ta đến sự thạnh nộ, họ vui mừng vì cớ sự cao trọng của ta."));
visuperobject.biblemap.put("b23_13_4", new visuperobject(23,13,4,"Có tiếng xôn xao của đoàn lũ trên các núi, dường như tiếng xôn xao của một dân đông! Ấy là tiếng ồn áo các nước của các dân tộc nhóm lại! Ðức Giê-hô-va vạn quân điểm soát đạo quân mình để ra trận."));
visuperobject.biblemap.put("b23_13_5", new visuperobject(23,13,5,"Chính Ðức Giê-hô-va và những khí giới của sự thạnh nộ Ngài đến từ phương xa, từ nơi rất xa trên trời, để hủy diệt cả đất."));
visuperobject.biblemap.put("b23_13_6", new visuperobject(23,13,6,"Các ngươi khá than khóc! Vì ngày của Ðức Giê-hô-va đã gần: ngày đó đến như cơn tại nạn đến bởi Ðấng Toàn năng."));
visuperobject.biblemap.put("b23_13_7", new visuperobject(23,13,7,"Vậy nên mọi tay đều yếu đuối, lòng người đều tan chảy."));
visuperobject.biblemap.put("b23_13_8", new visuperobject(23,13,8,"Chúng đều kinh hoàng, bị sự đau đớn thảm sầu bắt lấy, quặn thắt như đờn bà đương đẻ; hắc hơ hắc hãi nhìn nhau, mặt như mặt ngọn lửa!"));
visuperobject.biblemap.put("b23_13_9", new visuperobject(23,13,9,"Nầy, ngày Ðức Giê-hô-va đến, là ngày hung dữ, có sự thạnh nộ và nóng giận để làm đất nầy nên hoang vu, và diệt những kẻ có tội khỏi đó."));
visuperobject.biblemap.put("b23_13_10", new visuperobject(23,13,10,"Vì các ngôi sao và các đám sao trên trời mọc lên thì mờ tối, mặt trăng không soi sáng đâu."));
visuperobject.biblemap.put("b23_13_11", new visuperobject(23,13,11,"Ta sẽ phạt thế gian vì sự độc ác nó, phạt kẻ dữ vì tội lỗi chúng nó, làm cho kẻ kiêu hết kiêu, hạ sự tự cao của kẻ bạo ngược."));
visuperobject.biblemap.put("b23_13_12", new visuperobject(23,13,12,"Ta sẽ làm cho loài người hiếm có hơn vàng quí, thật ta sẽ làm cho loài người hiếm có hơn vàng ròng xứ Ô-phia."));
visuperobject.biblemap.put("b23_13_13", new visuperobject(23,13,13,"Vậy nên, ta sẽ khiến các tầng trời rung rinh, đất bị day động lìa khỏi chỗ mình, vì cơn thạnh nộ của Ðức Giê-hô-va vạn quân, trong ngày Ngài nổi giận dữ."));
visuperobject.biblemap.put("b23_13_14", new visuperobject(23,13,14,"Bấy giờ, ai nấy như con hươu bị đuổi, như bầy chiên không người nhóm lại, sẽ trở lại cùng dân mình, sẽ trốn về xứ mình."));
visuperobject.biblemap.put("b23_13_15", new visuperobject(23,13,15,"Kẻ nào người ta gặp được sẽ bị đâm; kẻ nào bị bắt sẽ ngã dưới lưỡi gươm."));
visuperobject.biblemap.put("b23_13_16", new visuperobject(23,13,16,"Con trẻ họ sẽ bị đập chết trước mắt họ, nhà bị cướp, vợ bị dâm hãm."));
visuperobject.biblemap.put("b23_13_17", new visuperobject(23,13,17,"Nầy, ta sẽ xui người Mê-đi nghịch cùng họ, những người đó chẳng quí bạc, chẳng thích vàng;"));
visuperobject.biblemap.put("b23_13_18", new visuperobject(23,13,18,"dùng cung đập giập kẻ trai trẻ, chẳng thương xót đến thai trong lòng mẹ, mắt chẳng tiếc trẻ con."));
visuperobject.biblemap.put("b23_13_19", new visuperobject(23,13,19,"Ba-by-lôn, là sự vinh hiển các nước, sự hoa mĩ của lòng kiêu ngạo người Canh-đê, sẽ giống như Sô-đôm và Gô-mô-rơ mà Ðức Chúa Trời đã lật đổ."));
visuperobject.biblemap.put("b23_13_20", new visuperobject(23,13,20,"Nó sẽ chẳng hề có người ở nữa, trải đời nọ sang đời kia không ai ở đó; người A rạp không đóng trại tại đó, những kẻ chăn cũng chẳng cầm bầy mình ở đó."));
visuperobject.biblemap.put("b23_13_21", new visuperobject(23,13,21,"Song những thú rừng sẽ đến ở, và nhà cửa đầy những chim cú; chim đã choán làm chỗ mình, dê đực lấy làm nơi nhảy nhót."));
visuperobject.biblemap.put("b23_13_22", new visuperobject(23,13,22,"Sài lang sủa trong cung điện, có rừng tru trong đền đài vui sướng. Kỳ nó đã gần đến, ngày nó sẽ không được dài nữa."));
visuperobject.biblemap.put("b23_14_1", new visuperobject(23,14,1,"Thật, Ðức Giê-hô-va sẽ thương xót Gia-cốp; và còn lựa chọn Y-sơ-ra-ên. Ngài sẽ lập chúng nó lại trong bổn xứ; kẻ trú ngụ sẽ phụ về chúng nó và liên hiệp cùng nhà Gia-cốp."));
visuperobject.biblemap.put("b23_14_2", new visuperobject(23,14,2,"Các dân sẽ đem họ trở về bổn xứ; nhà Y-sơ-ra-ên sẽ được chúng nó làm tôi trai tớ gái trong đất Ðức Giê-hô-va. Chúng nó sẽ bắt làm phu tù những kẻ đã bắt mình làm phu tù, và quản trị kẻ đã hà hiếp mình."));
visuperobject.biblemap.put("b23_14_3", new visuperobject(23,14,3,"Ðến ngày mà Ðức Giê-hô-va sẽ cho ngươi ý nghĩ, khỏi cơn buồn bực bối rối, và sự phục dịch nặng nề mà người ta đã bắt ép ngươi,"));
visuperobject.biblemap.put("b23_14_4", new visuperobject(23,14,4,"thì ngươi sẽ dùng lời thí dụ nầy nói về vua Ba-by-lôn rằng: Sao kẻ bạo ngược đã tiệt đi, thành ức hiếp đã dứt đi!"));
visuperobject.biblemap.put("b23_14_5", new visuperobject(23,14,5,"Ðức Giê-hô-va đã bẻ gậy của người ác, và trượng của kẻ cai trị!"));
visuperobject.biblemap.put("b23_14_6", new visuperobject(23,14,6,"Ấy là kẻ đã nhơn giận đánh các dân tộc không thôi, dùng sự thạnh nộ cai trị các nước, và dùng sự bắt bớ chẳng ai ngăn lại."));
visuperobject.biblemap.put("b23_14_7", new visuperobject(23,14,7,"Nay cả đất được yên nghỉ bình tĩnh, trổi giọng hát mừng!"));
visuperobject.biblemap.put("b23_14_8", new visuperobject(23,14,8,"Dầu đến cây tùng cây bách ở Li-ban cũng nhơn cớ ngươi mà vui mừng, và nói rằng: Từ khi ngươi ngã xuống rồi, không ai trèo lên để đốn ta nữa."));
visuperobject.biblemap.put("b23_14_9", new visuperobject(23,14,9,"Nơi âm phủ sâu thăm thẳm đã rúng động vì cớ ngươi đặng rước ngươi đến. Nó đã vì ngươi đánh thức các âm hồn, tức là những kẻ cả trên đất; nó đã làm cho vua các nước đứng dậy khỏi ngai mình."));
visuperobject.biblemap.put("b23_14_10", new visuperobject(23,14,10,"Hết thảy chúng nó sẽ cất tiếng nói cùng ngươi rằng: Kìa ngươi cũng yếu đuối như chúng ta ư! Kìa ngươi cũng trở giống như chúng ta ư!"));
visuperobject.biblemap.put("b23_14_11", new visuperobject(23,14,11,"Sự sang trọng với tiếng đờn cầm của ngươi đều xuống nơi âm phủ. Một lớp dòi làm nệm cho ngươi, sâu bọ thì làm mền!"));
visuperobject.biblemap.put("b23_14_12", new visuperobject(23,14,12,"Hỡi sao mai, con trai của sáng sớm kia, sao ngươi từ trời sa xuống! Hỡi kẻ giày đạp các nước kia, ngươi bị chặt xuống đất là thể nào!"));
visuperobject.biblemap.put("b23_14_13", new visuperobject(23,14,13,"Ngươi vẫn bụng bảo dạ rằng: Ta sẽ lên trời, sẽ nhắc ngai ta lên trên các ngôi sao Ðức Chúa Trời. Ta sẽ ngồi trên núi hội về cuối cùng phương bắc."));
visuperobject.biblemap.put("b23_14_14", new visuperobject(23,14,14,"Ta sẽ lên trên cao những đám mây, làm ra mình bằng Ðấng Rất Cao."));
visuperobject.biblemap.put("b23_14_15", new visuperobject(23,14,15,"Nhưng ngươi phải xuống nơi âm phủ, sa vào nơi vực thẳm!"));
visuperobject.biblemap.put("b23_14_16", new visuperobject(23,14,16,"Những kẻ thấy ngươi sẽ ngó chăm ngươi; và nhìn ngươi cách ý tứ, mà rằng: Có phải người nầy là kẻ đã làm rung rinh đất, day động các nước,"));
visuperobject.biblemap.put("b23_14_17", new visuperobject(23,14,17,"làm cho thế gian thành đồng vắng, lật đổ các thành, và chẳng hề buông tha kẻ bị tù về nhà mình đó chăng?"));
visuperobject.biblemap.put("b23_14_18", new visuperobject(23,14,18,"Hết thảy vua các nước đều ngủ nơi lăng tẩm mình cách vinh hiển."));
visuperobject.biblemap.put("b23_14_19", new visuperobject(23,14,19,"Nhưng ngươi thì bị ném xa khỏi mồ mả, như nhánh cây thúi, như áo người bị giết, bị gươm đâm, bị xô xuống trong những đá nơi hố, khác nào thây chết bị giày đạp dưới chơn!"));
visuperobject.biblemap.put("b23_14_20", new visuperobject(23,14,20,"Ngươi sẽ không được hội hiệp cùng các vua trong lăng; vì ngươi đã diệt nước mình và giết dân mình! Dòng dõi kẻ hung ác sẽ không được xưng đến danh nó nữa."));
visuperobject.biblemap.put("b23_14_21", new visuperobject(23,14,21,"Hãy sắm sẳn sự chém giết cho con cháu vì tội ác của tổ phụ; hầu cho họ không dấy lên nữa đặng được đất làm cơ nghiệp, lập thành ấp khắp thế gian!"));
visuperobject.biblemap.put("b23_14_22", new visuperobject(23,14,22,"Ðức Giê-hô-va vạn quân phán: Ta sẽ dấy lên nghịch cùng chúng nó. Ta sẽ làm tiêu diệt tên Ba-by-lôn và dân sót lại, ca đến con và cháu nữa, Ðức Giê-hô-va phán vậy."));
visuperobject.biblemap.put("b23_14_23", new visuperobject(23,14,23,"Ta sẽ làm cho đất nó thuộc về con nhiếm, biến ra ao nước; và ta sẽ dùng chuổi hủy diệt mà quét nó, Ðức Giê-hô-va vạn quân phán vậy."));
visuperobject.biblemap.put("b23_14_24", new visuperobject(23,14,24,"Ðức Giê-hô-va vạn quân đã thề rằng: Chắc thật, sự ta đã định sẽ xảy đến, điều ta đã toan sẽ đứng vững."));
visuperobject.biblemap.put("b23_14_25", new visuperobject(23,14,25,"Ta sẽ đánh phá người A-si-ri trong đất ta; sẽ giày đạp nó trên núi ta. Ách nó sẽ cổi khỏi chúng nó; gánh nó sẽ cất khỏi vai chúng nó."));
visuperobject.biblemap.put("b23_14_26", new visuperobject(23,14,26,"Ðó là ý chỉ đã định nghịch cùng cả đất; đó là tay đã giá trên hết thảy các nước."));
visuperobject.biblemap.put("b23_14_27", new visuperobject(23,14,27,"Vì Ðức Giê-hô-va vạn quân đã toan định, thì ai bãi đi được? Tay Ngài đã giang ra, thì ai day lại được?"));
visuperobject.biblemap.put("b23_14_28", new visuperobject(23,14,28,"Nhằm năm vua A-cha băng, gánh nặng nầy đã được rao ra:"));
visuperobject.biblemap.put("b23_14_29", new visuperobject(23,14,29,"Hỡi cả đất Phi-li-tin, chớ vui mừng bởi cớ roi đánh ngươi đã gãy; vì từ giống rắn sẽ sanh ra thuồng luồng, thuồng luồng sẽ sanh ra rắn lửa bay."));
visuperobject.biblemap.put("b23_14_30", new visuperobject(23,14,30,"Bấy giṀ con cả của kẻ nghèo sẽ tìm được đồ ăn, kẻ túng sẽ nằm yên ổn; nhưng ta sẽ khiến dòng dõi ngươi chết đói, và kẻ thuộc về ngươi cón sót lại sẽ bị giết."));
visuperobject.biblemap.put("b23_14_31", new visuperobject(23,14,31,"Hỡi cửa, hãy than khóc! Hỡi thành, hãy kêu la! Hỡi cả đất Phi-li-tin, hết thảy hãy đều tan chảy! Vì có luồng khói từ phương bắc đến, chẳng ai lìa khỏi hàng ngũ mình."));
visuperobject.biblemap.put("b23_14_32", new visuperobject(23,14,32,"Lấy chi trả lời cho sứ giả của nước nầy? Trả lời rằng: Ðức Giê-hô-va đã lập Si-ôn, và kẻ sầu khổ trong dân Ngài sẽ được ẩn náu trong nó."));
visuperobject.biblemap.put("b23_15_1", new visuperobject(23,15,1,"Gánh nặng về Mô-áp. Ðương ban đêm, A-Mô-áp bị tàn phá và diệt mất; phải, trong ban đêm, Kiệt Mô-áp bị tàn phá và diệt mất!"));
visuperobject.biblemap.put("b23_15_2", new visuperobject(23,15,2,"Kìa, chúng lên nơi miếu các thần, đến Ði-hôn, trên các nơi cao mà khóc. Dân Mô-áp than vãn vì Nê-bô và Mê-đê-ba; mọi đầu đều sói, mọi râu đều cắt."));
visuperobject.biblemap.put("b23_15_3", new visuperobject(23,15,3,"Thảy đều thắt bao gai trong các đường phố; thảy đều khóc lóc, sa nhiều nước mắt, trên nóc nhà và nơi sân chợ."));
visuperobject.biblemap.put("b23_15_4", new visuperobject(23,15,4,"Hết-bôn và Ê-lê-a-lê kêu la, tiếng nghe thấu đến Gia-hát. Vậy nên linh chiến của Mô-áp đều than khóc, trong tâm thần run rẩy."));
visuperobject.biblemap.put("b23_15_5", new visuperobject(23,15,5,"Lòng ta ngậm ngùi vì Mô-áp; kẻ sang nó trốn chạy đến Xoa, và đến Ê-lát-Sê-li-sia. Vì họ vừa trèo lên đèo Lu-hít vừa khóc; vì bại hoại mà kêu van trên con đường Hô-rô-na-im."));
visuperobject.biblemap.put("b23_15_6", new visuperobject(23,15,6,"Các dòng nước ở Nim-rim cạn tắt; cỏ khô, cỏ non chẳng có, không còn một vật xanh nào."));
visuperobject.biblemap.put("b23_15_7", new visuperobject(23,15,7,"Vậy nên, những của cải đã được, và những đồ báu đã chứa, họ đều mang sang khe liêu."));
visuperobject.biblemap.put("b23_15_8", new visuperobject(23,15,8,"Tiếng kêu khóc thảm thương nghe khắp chung quanh bờ cõi Mô-áp; tiếng van la thấu đến Ê-la-im, và đến Bê -e-Ê-lim."));
visuperobject.biblemap.put("b23_15_9", new visuperobject(23,15,9,"Sông ngòi ở Ði-môn đầy những máu; vì ta sẽ còn thêm tai vạ cho Ði-môn, sai sư tử đuổi kịp những người Nô-áp thoát nạn, và những kẻ còn sót lại trong xứ."));
visuperobject.biblemap.put("b23_16_1", new visuperobject(23,16,1,"Hãy gởi chiên con phải dâng cho quan cai trị đất nầy, từ Sê-la sang đồng vắng, đến núi của con gái Si-ôn."));
visuperobject.biblemap.put("b23_16_2", new visuperobject(23,16,2,"Các con gái Mô-áp tại bến đò Aït-nôn sẽ giống như chim bay tán loạn, và như một lứa chim con bị quăng ra ngoài ổ."));
visuperobject.biblemap.put("b23_16_3", new visuperobject(23,16,3,"Ngươi hãy lập mưu, hãy làm sự công bình, đang lúc giữa trưa hãy phủ bóng ngươi như ban đêm; hãy che giấu kẻ bị đuổi; chớ bươi móc kẻ trốn tránh!"));
visuperobject.biblemap.put("b23_16_4", new visuperobject(23,16,4,"Hãy cho phép những kẻ bị đuổi của ta trú ngụ nơi ngươi! Hãy làm nơi ẩn náu cho Mô-áp khỏi mặt kẻ tàn hại! Vì kẻ cướp giựt đã mất, sự tàn hại đã hết, kẻ giày đạy đã bị diệt khỏi đất nầy."));
visuperobject.biblemap.put("b23_16_5", new visuperobject(23,16,5,"Ấy vậy, ngôi sẽ bởi sự nhơn từ mà bền lập; và trong trại Ða-vít sẽ có một Ðấng lấy lẽ thật ngồi lên, sẽ đoán xét, sẽ tìm sự ngay thẳng, và vội vàng làm sự công bình."));
visuperobject.biblemap.put("b23_16_6", new visuperobject(23,16,6,"Chúng ta có nghe sự kiêu ngạo của Mô-áp, nó kiêu ngạo lắm, cũng nghe nó xấc xược, kiêu căng, giận dữ; sự khoe khoang của nó là vô ích."));
visuperobject.biblemap.put("b23_16_7", new visuperobject(23,16,7,"Vậy nên Mô-áp sẽ than khóc vì Mô-áp, ai nấy đều than khóc; các ngươi hãy khóc, hãy thở than vì sự mất bánh trái nho của Kiệt-Ha-rê-sết!"));
visuperobject.biblemap.put("b23_16_8", new visuperobject(23,16,8,"Vì ruộng nương của Hết-bôn và cây nho của Síp-ma đều mòn mỏi; ngày trước nó giàng ra tới Gia-ê-xe, lan ra tới đồng vắng, chồi tược đâm ra tới bên kia biển, mà bây giờ vua chúa các nước đã bẻ gãy nhánh tốt."));
visuperobject.biblemap.put("b23_16_9", new visuperobject(23,16,9,"Vậy nên ta vì cây nho của Síp-ma mà khóc lóc như Gia-ê-xe. Hỡi Hết-bôn, hỡi Ê-lê-a-lê, ta sẽ tưới nước mắt ta trên các ngươi; vì đương lúc trái mùa hạ và màu gặt thì có tiếng reo của giặc xảy đến."));
visuperobject.biblemap.put("b23_16_10", new visuperobject(23,16,10,"Sự vui mừng hớn hở đã cất khỏi ruộng tốt; trong vườn nho chẳng còn hò hát, reo vui, kẻ đạp rượu chẳng còn đạp trong thùng; ta đã làm dứt tiếng reo vui."));
visuperobject.biblemap.put("b23_16_11", new visuperobject(23,16,11,"Cho nên lòng ta vì Mô-áp kêu vang như đờn cầm, ruột ta vì Kiệt-Hê-re cũng vậy."));
visuperobject.biblemap.put("b23_16_12", new visuperobject(23,16,12,"Vì dầu Mô-áp chịu mệt nhọc đặng đi đến nơi cao, dầu vào trong nơi thánh mình để cầu nguyện, cũng chẳng được nhậm!"));
visuperobject.biblemap.put("b23_16_13", new visuperobject(23,16,13,"Ấy là lời mà xua kia Ðức Giê-hô-va đã phán về Mô-áp."));
visuperobject.biblemap.put("b23_16_14", new visuperobject(23,16,14,"Bây giờ thì Ðức Giê-hô-va phán như vầy: Trong ba năm, kể như năm kẻ ở mướn, sự vinh hiển của Mô-áp với cả đoàn dân đông của nó sẽ bị khinh hèn; và số còn sót lại sẽ ít lắm, không kể ra gì."));
visuperobject.biblemap.put("b23_17_1", new visuperobject(23,17,1,"Gánh nặng về Ða-mách. Nầy, Ða-mách bị trừ ra, không kể làm thành nữa, và sẽ trở nên một đống đổ nát."));
visuperobject.biblemap.put("b23_17_2", new visuperobject(23,17,2,"Các thành A-rô -e bị bỏ hoang, sẽ làm chỗ cho bầy chiên nằm nghỉ, chẳng ai kinh động."));
visuperobject.biblemap.put("b23_17_3", new visuperobject(23,17,3,"Ðồn lũy của Ép-ra-im sẽ chẳng còn, ngôi nước của Ða-mách và dân sót của Sy-ri cũng vậy, khác nào sự vinh hiển của con cái Y-sơ-ra-ên, Ðức Giê-hô-va vạn quân phán vậy."));
visuperobject.biblemap.put("b23_17_4", new visuperobject(23,17,4,"Xảy ra trong ngày đó, sự vinh hiển của Gia-cốp sẽ giảm đi, xác thịt mập mạp đổi ra gầy ốm."));
visuperobject.biblemap.put("b23_17_5", new visuperobject(23,17,5,"Sẽ xảy ra như con gặt túm các ngọn lúa, rồi dùng cánh tay cắt lấy bông; lại như những gié lúa mót được ở nơi trũng Rê-pha-im vậy."));
visuperobject.biblemap.put("b23_17_6", new visuperobject(23,17,6,"Nhưng sẽ còn sót lại như là của mót, khác nào khi người ta rung cây ô-li-ve, còn hai ba trái trên chót nhánh rất cao, hoặc bốn năm trái trên những nhành xa của cây sai tríu. Giê-hô-va Ðức Chúa Trời của Y-sơ-ra-ên phán vậy."));
visuperobject.biblemap.put("b23_17_7", new visuperobject(23,17,7,"Trong ngày đó, người ta sẽ ngó về Ðấng tạo mình, và mắt trông thẳng đến Ðấng Thánh của Y-sơ-ra-ên."));
visuperobject.biblemap.put("b23_17_8", new visuperobject(23,17,8,"Họ sẽ không còn xây mắt về các bàn thờ, là công việc của tay mình; và không còn ngó các đồ vật bởi ngón tay mình chế ra, hoặc các tượng Át-tạt-tê, hoặc các trụ mặt trời."));
visuperobject.biblemap.put("b23_17_9", new visuperobject(23,17,9,"Trong ngày đó, các thành bền vững của nó sẽ giống như những nơi đổ nát, hoặc trong rừng, hoặc trên đỉnh núi, là những nơi đã bị bỏ hoang trước mặt con cái Y-sơ-ra-ên; xứ nầy sẽ bị bỏ hoang vậy."));
visuperobject.biblemap.put("b23_17_10", new visuperobject(23,17,10,"Vì ngươi đã quên Ðức Chúa Trời của sự cứu ngươi, chẳng nhớ đến vầng đá sức mạnh ngươi. Vậy nên ngươi trồng cây tốt, lại trồng những gốc nho khác giống."));
visuperobject.biblemap.put("b23_17_11", new visuperobject(23,17,11,"Ðang ngày ngươi trồng thì rào giậu, vừa lúc ban mai làm cho hột giống nở hoa; nhưng trong ngày buồn bực rầu rĩ thì mùa màng mất ráo!"));
visuperobject.biblemap.put("b23_17_12", new visuperobject(23,17,12,"Ôi! các dân đông đảo làm xôn xao là dường nào, khác nào như biển gầm! Các dân tộc chạy xông tới như nhiều nước đổ mạnh!"));
visuperobject.biblemap.put("b23_17_13", new visuperobject(23,17,13,"Thật, các dân tộc chạy xông tới như nhiều nước đổ ầm ạc; nhưng Ngài sẽ quở trách họ, thì đều trốn xa, bị đùa đi như rơm rác trên núi khi gió thổi, như luồng bụi gặp cơn bão."));
visuperobject.biblemap.put("b23_17_14", new visuperobject(23,17,14,"Ðương buổi chiều, nầy, có sự sợ hãi; kíp trước khi ban mai đã thành ra hư không. Ấy là phần của kẻ đã bóc lột chúng ta, là số phận xảy đến cho kẻ cướp chúng ta vậy."));
visuperobject.biblemap.put("b23_18_1", new visuperobject(23,18,1,"Ôi! đất đập cánh ào ào kia, là đất ở bên kia các sông của Cút;"));
visuperobject.biblemap.put("b23_18_2", new visuperobject(23,18,2,"sai sứ vượt biển, cỡi thuyền bằng lau đi trên mặt nước! Hỡi các sứ giả nhặm lẹ kia, hãy đi đến nước người cao da mởn, đến dân đáng kính sợ từ khi lập nước trở đi, đến dân hay cân lường và giày đạp, là nước mà có những sông chia đất nó."));
visuperobject.biblemap.put("b23_18_3", new visuperobject(23,18,3,"Hỡi các ngươi hết thảy, là dân thiên hạ ở trên đất kia, vừa khi cờ dựng trên núi, hãy xem; vừa khi kèn thổi, hãy nghe!"));
visuperobject.biblemap.put("b23_18_4", new visuperobject(23,18,4,"Vả, Ðức Giê-hô-va đã phán cùng ta như vầy: Ta sẽ ở yên lặng, và ở từ chỗ ta ngự mà xem, như ánh nắng sáng sủa, như mây ngút trong nắng mùa gặt."));
visuperobject.biblemap.put("b23_18_5", new visuperobject(23,18,5,"Nhưng, trước ngày gặt, khi mùa hoa đã cuối, và hoa nho thành trái hầu chín, thì Ngài sẽ dùng liềm cắt những nhánh, tỉa bỏ những nhánh rậm lá."));
visuperobject.biblemap.put("b23_18_6", new visuperobject(23,18,6,"Chúng nó sẽ cùng nhau bị bỏ lại cho chim ăn thịt trong núi, và cho cả thú vật trên đất sẽ dùng mà qua mùa đông."));
visuperobject.biblemap.put("b23_18_7", new visuperobject(23,18,7,"Trong lúc đó, dân người cao da mởn, tức là dân đáng kinh sợ từ khi lập nước trở đi, là dân hay cân lường và giày đạp, là nước mà có những sông chia đất nó, chính dân ấy sẽ đem lễ vật dâng cho Ðức Giê-hô-va vạn quân, trong nơi danh Ðức Giê-hô-va vạn quân ngự, là núi Si-ôn."));
visuperobject.biblemap.put("b23_19_1", new visuperobject(23,19,1,"Gánh nặng về Ê-díp-tô. Nầy, Ðức Giê-hô-va cỡi đám mây mau kíp đến Ê-díp-tô; các thần tượng của Ê-díp-tô đều run rẩy trước mặt Ngài, lòng Ê-díp-tô sẽ tan chảy giữa nó."));
visuperobject.biblemap.put("b23_19_2", new visuperobject(23,19,2,"Ta sẽ xui người Ê-díp-tô đánh người Ê-díp-tô: anh em nghịch cùng anh em, lân cận nghịch cùng lân cận, thành nghịch cùng thành, nước nghịch cùng nước."));
visuperobject.biblemap.put("b23_19_3", new visuperobject(23,19,3,"Tâm thần Ê-díp-tô sẽ lụn bại giữa nó, ta sẽ phá tan mưu chước nó. Bấy giờ chúng nó sẽ cầu hỏi các thần tượng cùng thuật sĩ, kẻ kêu hồn cùng thầy bói."));
visuperobject.biblemap.put("b23_19_4", new visuperobject(23,19,4,"Ta sẽ phó dân Ê-díp-tô trong tay chúa hung dữ, và một vua bạo ngược sẽ cai trị chúng nó; Chúa, là Ðức Giê-hô-va vạn quân, phán vậy."));
visuperobject.biblemap.put("b23_19_5", new visuperobject(23,19,5,"Nước biển sẽ tắt, sông cạn và khô."));
visuperobject.biblemap.put("b23_19_6", new visuperobject(23,19,6,"Các sông sẽ trở nên hôi thối; ngòi rạch rặc xuống và khan đi; sậy lau đều chết héo."));
visuperobject.biblemap.put("b23_19_7", new visuperobject(23,19,7,"Các đồng cỏ ở bờ sông Ni-lơ cùng hết thảy đồng ruộng đã gieo của nó, đều héo khô, bị thổi đi, không còn nữa."));
visuperobject.biblemap.put("b23_19_8", new visuperobject(23,19,8,"Những người đánh cá sẽ than vãn, mọi kẻ buông câu nơi sông Ni-lơ đều rên siết, và kẻ thả lưới trên các dòng nước đều âu sầu."));
visuperobject.biblemap.put("b23_19_9", new visuperobject(23,19,9,"Những kẻ làm vải gai mịn và những kẻ dệt vải trắng đều xấu hổ."));
visuperobject.biblemap.put("b23_19_10", new visuperobject(23,19,10,"Các trụ của Ê-díp-tô đều bị gãy, hết thảy thợ thầy đều có lòng lo."));
visuperobject.biblemap.put("b23_19_11", new visuperobject(23,19,11,"Các quan trưởng ở Xô-an rất đần độn; mưu sĩ rất khôn ngoan của Pha-ra-ôn, chỉ bày ra những kế dại khờ. Sao các ngươi nói cùng Pha-ra-ôn rằng: Tôi là con các bậc hiền triết, con các vua đời xưa?"));
visuperobject.biblemap.put("b23_19_12", new visuperobject(23,19,12,"Vậy thì các bậc hiền triết ngươi ở đâu? Các bật ấy hãy cho ngươi biết đi; các bậc ấy nên biết Ðức Giê-hô-va vạn quân đã định gì về Ê-díp-tô."));
visuperobject.biblemap.put("b23_19_13", new visuperobject(23,19,13,"Các quan trưởng ở Xô-an đã nên dại dột; các quan trưởng ở Nốp bị lừa dối; những người làm hòn đá góc của các chi phái Ê-díp-tô đã làm cho nó lầm lạc."));
visuperobject.biblemap.put("b23_19_14", new visuperobject(23,19,14,"Ðức Giê-hô-va đã xen sự trái ngược vào giữa nó, nên chúng nó làm cho Ê-díp-tô sai lầm trong mọi việc, như người say vừa mửa vừa đi xiêu tó."));
visuperobject.biblemap.put("b23_19_15", new visuperobject(23,19,15,"Chẳng có việc gì mà đầu và đuôi, cây kè và cây lau sẽ làm ra được cho Ê-díp-tô cả."));
visuperobject.biblemap.put("b23_19_16", new visuperobject(23,19,16,"Trong ngày đó, người Ê-díp-tô sẽ giống như đờn bà; thấy tay Ðức Giê-hô-va vạn quân vung lên trên mình, thì run rẩy sợ hãi."));
visuperobject.biblemap.put("b23_19_17", new visuperobject(23,19,17,"Bấy giờ đất Giu-đa sẽ nên sự kinh hoàng cho Ê-díp-tô; hễ khi người ta xưng tên ấy ra trước mặt ai thì nấy sợ khiếp, vì ý chỉ của Ðức Giê-hô-va vạn quân đã định nghịch cùng nó."));
visuperobject.biblemap.put("b23_19_18", new visuperobject(23,19,18,"Trong ngày đó, tại xứ Ê-díp-tô, sẽ có năm thành nói tiếng Ca-na-an, và chỉ Ðức Giê-hô-va vạn quân mà thề. Trong đó có một thành gọi là thành hủy diệt."));
visuperobject.biblemap.put("b23_19_19", new visuperobject(23,19,19,"Trong ngày đó, sẽ có một bàn thờ cho Ðức Giê-hô-va ở giữa xứ Ê-díp-tô, và có một trụ cho Ðức Giê-hô-va nơi bờ cõi nói."));
visuperobject.biblemap.put("b23_19_20", new visuperobject(23,19,20,"Ấy là dấu và chứng cho Ðức Giê-hô-va vạn quân tại xứ Ê-díp-tô; bởi chưng chúng sẽ kêu đến Ðức Giê-hô-va vì cứ kẻ bạo ngược, và Ngài sẽ sai một Ðấng cứu và binh vực để giải thoát họ."));
visuperobject.biblemap.put("b23_19_21", new visuperobject(23,19,21,"Ðức Giê-hô-va sẽ cho Ê-díp-tô biết Ngài; trong ngày đó người Ê-díp-tô sẽ biết Ðức Giê-hô-va; ắt họ sẽ dâng hi sinh và của lễ mà thờ phượng Ngài, lại khấn nguyện cùng Ðức Giê-hô-va, và hoàn nguyện."));
visuperobject.biblemap.put("b23_19_22", new visuperobject(23,19,22,"Ðức Giê-hô-va sẽ đánh Ê-díp-tô, đánh rồi lại chữa lành; họ sẽ trở về cùng Ðức Giê-hô-va, Ngài sẽ nghe lời họ cầu nguyện và chữa lành cho."));
visuperobject.biblemap.put("b23_19_23", new visuperobject(23,19,23,"Trong ngày đó, sẽ có con đường cái đi từ Ê-díp-tô đến A-si-ri; người A-si-ri sẽ đến Ê-díp-tô, người Ê-díp-tô sẽ đến A-si-ri; người Ê-díp-tô và người A-si-ri đều cùng nhau thờ phượng Ðức Giê-hô-va."));
visuperobject.biblemap.put("b23_19_24", new visuperobject(23,19,24,"Trong ngày đó, Y-sơ-ra-ên sẽ hiệp cùng Ê-díp-tô và A-si-ri làm ba, đặng làm nên nguồn phước giữa thiên hạ;"));
visuperobject.biblemap.put("b23_19_25", new visuperobject(23,19,25,"vì Ðức Giê-hô-va vạn quân đã chúc phước cho họ, mà rằng Ê-díp-tô dân ta, A-si-ri công trình của tay ta, Y-sơ-ra-ên gia tài ta, đều hãy được phước!"));
visuperobject.biblemap.put("b23_20_1", new visuperobject(23,20,1,"Nhằm năm mà Sa-gôn, vua A-si-ri, sai Ta-tân đến Ách-đốt, vây thành và chiếm lấy,"));
visuperobject.biblemap.put("b23_20_2", new visuperobject(23,20,2,"trong lúc đó, Ðức Giê-hô-va nhờ con trai A-mốt, là Ê-sai, mà phán rằng: Hãy đi, cổi bao gai khỏi lưng ngươi, và lột giày khỏi chơn ngươi. Ê-sai vâng lời, đi trần, và chơn không."));
visuperobject.biblemap.put("b23_20_3", new visuperobject(23,20,3,"Ðức Giê-hô-va bèn phán rằng: Như đầy tớ ta là Ê-sai đã đi trần và chơn không trong ba năm, làm dấu và điềm chỉ về Ê-díp-tô và Ê-thi-ô-bi thể nào,"));
visuperobject.biblemap.put("b23_20_4", new visuperobject(23,20,4,"thì những phu tù của Ê-díp-tô và lưu tù của Ê-thi-ô-bi, cả trẻ lẫn già, cũng bị vua A-si-ri giải đi trần và chơn không, bày mông ra thể ấy, để làm nhục nước Ê-díp-tô."));
visuperobject.biblemap.put("b23_20_5", new visuperobject(23,20,5,"Bấy giờ chúng nó sẽ sợ sệt và hổ thẹn vì cớ Ê-thi-ô-bi, là sự trông cậy mình, và Ê-díp-tô, là sự vinh hiển mình."));
visuperobject.biblemap.put("b23_20_6", new visuperobject(23,20,6,"Trong ngày đó, kẻ ở gần biển sẽ nói rằng: Kìa, dân tộc mà chúng ta vốn đem lòng trông cậy, và chạy đến cầu cứu để được giải thoát khỏi vua A-si-ri, thì nay đã trở nên thể ấy rồi! Chúng ta làm sao trốn khỏi được?"));
visuperobject.biblemap.put("b23_21_1", new visuperobject(23,21,1,"Gánh nặng về đồng vắng ở gần biển. Nó đến từ đồng vắng, là đất đáng kinh khiếp, khác nào cơn gió lốc vụt qua trong phương nam."));
visuperobject.biblemap.put("b23_21_2", new visuperobject(23,21,2,"Có sự hiện thấy hãi hùng đã tỏ ra cho tôi: kẻ gian dối ở gian dối, kẻ tàn hại làm tàn hại! Hỡi người Ê-lam, hãy lên; Hỡi người Mê-đi, hãy vây đi! Ta sẽ làm cho dứt mọi tiếng than thở nó."));
visuperobject.biblemap.put("b23_21_3", new visuperobject(23,21,3,"Vậy nên lưng ta đau lắm, quặn thắt như đờn bà đang sanh đẻ; sự đau đớn mà ta chịu, làm cho ta không thấy!"));
visuperobject.biblemap.put("b23_21_4", new visuperobject(23,21,4,"Lòng ta mê muội, rất nên khiếp sợ; vốn là chiều hôm ta ưa thích, đã đổi ra sự run rẩy cho ta."));
visuperobject.biblemap.put("b23_21_5", new visuperobject(23,21,5,"Người ta đặt bàn tiệc, cắt kẻ canh giữ; người ta ăn và uống. Hỡi các quan trưởng, hãy chờ dậy! Hãy thoa dầu cho cái thuẫn!"));
visuperobject.biblemap.put("b23_21_6", new visuperobject(23,21,6,"Vì Chúa phán cùng tôi như vầy: Hãy đi, sắp đặt vọng canh, thấy việc gì thì báo."));
visuperobject.biblemap.put("b23_21_7", new visuperobject(23,21,7,"Khi nó thấy quân lính cỡi ngựa đi từng cặp, và có lừa cùng lạc đà từng bầy, thì nó phải chăm chỉ mà nghe."));
visuperobject.biblemap.put("b23_21_8", new visuperobject(23,21,8,"Ðoạn nó kêu lên như sư tử, mà rằng: Hỡi Chúa, tôi đứng rình nơi chòi trông cả ngày, và đứng tại chỗ tôi canh cả đêm."));
visuperobject.biblemap.put("b23_21_9", new visuperobject(23,21,9,"Nầy, có quân lính cỡi ngựa đi từng cặp mà đến. Nó đáp rằng: Ba-by-lôn đổ xuống rồi, đổ xuống rồi! Mọi hình tượng tà thần của nó đã bể nát trên đất rồi!"));
visuperobject.biblemap.put("b23_21_10", new visuperobject(23,21,10,"Hỡi lúa bị đạp của ta, là thóc trên sân kia! sự ta đã nghe từ Ðức Giê-hô-va vạn quân, tức là Ðức Chúa Trời của Y-sơ-ra-ên, thì ta đã bảo cho ngươi biết."));
visuperobject.biblemap.put("b23_21_11", new visuperobject(23,21,11,"Gánh nặng về Ðu-ma. Có người từ Sê -i-rơ kêu tôi rằng: Hỡi người canh, đêm thể nào? Hỡi người canh, đêm thể nào?"));
visuperobject.biblemap.put("b23_21_12", new visuperobject(23,21,12,"Người canh đáp rằng: Buổi sáng đến; đêm cũng đến. Nếu muốn hỏi thì hỏi đi; hãy trở lại, hãy đến."));
visuperobject.biblemap.put("b23_21_13", new visuperobject(23,21,13,"Gánh nặng về A-ra-bi. Hỡi đoàn đi buôn ở Ðê-đan, sẽ ngủ trong rừng xứ A-ra-bi. Dân cư Thê-ma đã đem nước cho những kẻ khát;"));
visuperobject.biblemap.put("b23_21_14", new visuperobject(23,21,14,"đã đón những người trốn đặng cho họ bánh."));
visuperobject.biblemap.put("b23_21_15", new visuperobject(23,21,15,"Vì họ trốn gươm, trốn sự chiến tranh kinh khiếp."));
visuperobject.biblemap.put("b23_21_16", new visuperobject(23,21,16,"Chúa đã phán cùng tôi như vầy: Trong một năm nữa, kể như năm kẻ ở mướn, cả sự vinh hiển của Kê-đa sẽ hao mòn."));
visuperobject.biblemap.put("b23_21_17", new visuperobject(23,21,17,"Những kẻ cầm cung mạnh mẽ, là con cái của Kê-đa, sẽ sót lại chẳng bao lăm. Vì Giê-hô-va, Ðức Chúa Trời của Y-sơ-ra-ên, đã phán vậy."));
visuperobject.biblemap.put("b23_22_1", new visuperobject(23,22,1,"Gánh nặng về trũng của sự hiện thấy. Ngươi có sự gì mà cả dân trèo lên nóc nhà như vậy?"));
visuperobject.biblemap.put("b23_22_2", new visuperobject(23,22,2,"Hỡi thành xôn xao, đầy sự om sòm, là ấp vui vẻ kia; các kẻ chết của ngươi chẳng phải chết vì gươm, cũng không phải là tử trận."));
visuperobject.biblemap.put("b23_22_3", new visuperobject(23,22,3,"Các quan cai trị của ngươi thảy đều cùng nhau chạy trốn, bị trói bởi những kẻ cầm cung; còn trong dân ngươi, kẻ nào trốn xa mà người ta tìm thấy, thì đã bị trói làm một."));
visuperobject.biblemap.put("b23_22_4", new visuperobject(23,22,4,"Vậy nên ta phán rằng: Các ngươi chớ ngó ta, ta sẽ khóc lóc thảm thiết. Ðừng tìm cách yên ủi ta về sự hủy diệt của con gái dân ta!"));
visuperobject.biblemap.put("b23_22_5", new visuperobject(23,22,5,"Vì ấy là ngày bị rối loạn, giày đạp, và kinh hãi trong trũng của sự hiện thấy, đến bởi Chúa, là Ðức Giê-hô-va vạn quân; tường thành vỡ lở, tiếng kêu đến núi."));
visuperobject.biblemap.put("b23_22_6", new visuperobject(23,22,6,"Ê-lam đeo giỏ tên, có xe binh lính kỵ đi theo, và Ki-rơ để trần cái thuẫn,"));
visuperobject.biblemap.put("b23_22_7", new visuperobject(23,22,7,"các nơi trũng đẹp nhứt đầy những xe cộ, và lính kỵ dàn trận tại trước cửa thành."));
visuperobject.biblemap.put("b23_22_8", new visuperobject(23,22,8,"Màn che Giu-đa đã cất rồi; và trong ngày đó ngươi trông về khí giới của nhà rừng."));
visuperobject.biblemap.put("b23_22_9", new visuperobject(23,22,9,"Các ngươi thấy thành Ða-vít bị nhiều nơi sứt mẻ, bèn thâu chứa nước ao dưới lại."));
visuperobject.biblemap.put("b23_22_10", new visuperobject(23,22,10,"Các ngươi đếm nhà cửa Giê-ru-sa-lem, phá những nhà cửa để tu bổ tường thành;"));
visuperobject.biblemap.put("b23_22_11", new visuperobject(23,22,11,"lại đào hồ giữa khoảng hai tường thành đặng chứa nước ao cũ. Nhưng các ngươi chẳng trông lên Ðấng làm nên sự đó; chẳng nhìn xem Ðấng đã định sự đó từ xưa."));
visuperobject.biblemap.put("b23_22_12", new visuperobject(23,22,12,"Trong ngày đó, Chúa, là Ðức Giê-hô-va vạn quân, gọi các ngươi khóc lóc, thở than, cạo đầu, và thắt bao gai;"));
visuperobject.biblemap.put("b23_22_13", new visuperobject(23,22,13,"thế mà trong các ngươi có sự vui mừng hớn hở. Người ta mổ bò, giết chiên, ăn thịt, uống rượu: Hãy ăn uống đi, vì ngày mai chúng ta sẽ chết!"));
visuperobject.biblemap.put("b23_22_14", new visuperobject(23,22,14,"Vả, Ðức Giê-hô-va vạn quân tỏ mình trong tai tôi rằng: Tội ấy chắc sẽ chẳng hề được tha cho các ngươi cho đến giờ các ngươi chết, Chúa, là Ðức Giê-hô-va vạn quân, phán vậy."));
visuperobject.biblemap.put("b23_22_15", new visuperobject(23,22,15,"Chúa, là Ðức Giê-hô-va vạn quân, phán như vầy: Ngươi hãy đi, đến nhà kẻ giữ kho, tức Sép-na, làm chức giám cung, và bảo nó rằng:"));
visuperobject.biblemap.put("b23_22_16", new visuperobject(23,22,16,"Ngươi ở đây làm gì? có bà con chi đây, mà ngươi đã đục tại đây một huyệt mả? Thật người đục cho mình một huyệt mả trên nơi cao, khiến đào cho mình một chỗ ở trong vầng đá!"));
visuperobject.biblemap.put("b23_22_17", new visuperobject(23,22,17,"Nầy, Ðức Giê-hô-va, khác nào kẻ sức khỏe, sẽ quăng mạnh người ra nơi xa, và bọc chặt ngươi."));
visuperobject.biblemap.put("b23_22_18", new visuperobject(23,22,18,"Ngài ắt sẽ quấn ngươi, và ném ngươi như quả bóng vào xứ rộng và khoảng khoát. Tại đó ngươi sẽ chết, xe cộ sang trọng của ngươi cũng theo đến đó, ôi, ngươi là kẻ làm nhục cho nhà chủ mình!"));
visuperobject.biblemap.put("b23_22_19", new visuperobject(23,22,19,"Ta sẽ cách chức ngươi, ngươi sẽ bị truất khỏi ngôi mình."));
visuperobject.biblemap.put("b23_22_20", new visuperobject(23,22,20,"Trong ngày đó, ta sẽ gọi đầy tớ ta là Ê-li-a-kim, con trai Hinh-kia,"));
visuperobject.biblemap.put("b23_22_21", new visuperobject(23,22,21,"lấy áo ngươi mặc cho nó, lấy đai ngươi giúp sức nó, lấy chánh trị ngươi trao trong tay nó, nó sẽ làm cha cho dân cư Giê-ru-sa-lem và cho nhà Giu-đa."));
visuperobject.biblemap.put("b23_22_22", new visuperobject(23,22,22,"Ta sẽ đem chìa khóa nhà Ða-vít để trên vai nó; hễ nó mở, không ai đóng được; nó đóng, không ai mở được."));
visuperobject.biblemap.put("b23_22_23", new visuperobject(23,22,23,"Ta sẽ đóng nó xuống như đinh đóng nơi vững chãi; và nó sẽ trở nên một ngôi vinh hiển cho nhà cha mình."));
visuperobject.biblemap.put("b23_22_24", new visuperobject(23,22,24,"Người ta sẽ treo trên nó mọi sự vinh hiển của nhà cha mình, con cái dòng dõi, và hết thảy những đồ đựng bé nhỏ nữa, từ cái chén đến cái ve."));
visuperobject.biblemap.put("b23_22_25", new visuperobject(23,22,25,"Ðức Giê-hô-va vạn quân phán rằng: Trong ngày đó, cái đinh đóng nơi vững chãi sẽ lỏng xệch; nó sẽ bị đập và rớt xuống, và gánh nặng treo trên nó sẽ bị cắt đứt. Vì Ðức Giê-hô-va đã phán vậy."));
visuperobject.biblemap.put("b23_23_1", new visuperobject(23,23,1,"Gánh nặng về Ty-rơ. Hỡi tàu bè Ta-rê-si, hãy than khóc, vì nó đã bị hủy phá, đến nỗi chẳng còn nhà cửa nữa! chẳng còn lối vào nữa! Ấy là điều đã tỏ cho họ từ xứ Kít-tim."));
visuperobject.biblemap.put("b23_23_2", new visuperobject(23,23,2,"Hỡi dân cư bờ biển, là nơi những lái buôn Si-đôn, là những kẻ vượt biển chở đồ hàng đến, hãy nín lặng!"));
visuperobject.biblemap.put("b23_23_3", new visuperobject(23,23,3,"Hột giống của Si-ho, mùa màng của sông Ni-lơ, theo đường biển trở đến, ấy là phần lợi nó; nó vốn là chợ buôn của các nước."));
visuperobject.biblemap.put("b23_23_4", new visuperobject(23,23,4,"Hỡi Si-đôn, hãy hổ thẹn! Vì biển, nơi đồn lũy biển, có nói như vầy: Ta chưa ở cữ, chưa đẻ con, chưa nuôi trai trẻ, chưa nuôi gái bé."));
visuperobject.biblemap.put("b23_23_5", new visuperobject(23,23,5,"Khi tin nầy đồn đến Ê-díp-tô, người ta nghe tin về Ty-rơ, chắc sẽ đau thương lắm."));
visuperobject.biblemap.put("b23_23_6", new visuperobject(23,23,6,"Hãy dời qua Ta-rê-si; hỡi dân ở gần biển, hãy than khóc!"));
visuperobject.biblemap.put("b23_23_7", new visuperobject(23,23,7,"Ðây há chẳng phải là thành vui vẻ của các ngươi sao? Là thành có từ đời thượng cổ, mà chơn nó đã trải đến nơi xa đặng trú ngụ tại đó."));
visuperobject.biblemap.put("b23_23_8", new visuperobject(23,23,8,"Vậy, ai đã định sự ấy nghịch cùng Ty-rơ, là thành đã phân phát mão triều thiên, lái buôn nó vốn là hàng vương hầu, người mua bán nó vốn là tay quí hiển trong thiên hạ?"));
visuperobject.biblemap.put("b23_23_9", new visuperobject(23,23,9,"Ấy là Ðức Giê-hô-va vạn quân đã định sự nầy, để làm dơ dáy sự kiêu ngạo mọi vinh hiển, và làm nhục mọi kẻ sang trọng trong thế gian."));
visuperobject.biblemap.put("b23_23_10", new visuperobject(23,23,10,"Hỡi con gái Ta-rê-si, ngươi không còn bị trói buộc nữa, hãy đi qua khắp địa phận ngươi như sông Ni-lơ!"));
visuperobject.biblemap.put("b23_23_11", new visuperobject(23,23,11,"Ðức Giê-hô-va đã giang tay Ngài trên biển, làm cho các nước rung động. Ngài đã ban mạng lịnh về Ca-na-an, mà phá đồn lũy nó."));
visuperobject.biblemap.put("b23_23_12", new visuperobject(23,23,12,"Ngài có phán rằng: Hỡi con gái Si-đôn, là nữ đồng trinh bị ức hiếp kia, ngươi sẽ chẳng còn cứ vui mừng nữa! Vậy hãy chờ dậy, sang xứ Kít-tim, tại đó ngươi cũng sẽ không được an nghỉ đâu!"));
visuperobject.biblemap.put("b23_23_13", new visuperobject(23,23,13,"Hãy xem xứ của người Canh-đê, không có dân đó nữa; người A-si-ri lấy làm chỗ cho thú rừng; chúng nó dựng tháp, phá cung đền nó làm ra gò đống."));
visuperobject.biblemap.put("b23_23_14", new visuperobject(23,23,14,"Hỡi tàu bè Ta-rê-si, hãy than khóc! Vì đồn lũy các ngươi hủy diệt rồi."));
visuperobject.biblemap.put("b23_23_15", new visuperobject(23,23,15,"Trong lúc đó, thành Ty-rơ sẽ bị người ta quên đi trong bảy mươi năm, bằng một đời vua. Mãn bảy mươi năm rồi, Ty-rơ sẽ giống như bài ca của kỵ nữ hát."));
visuperobject.biblemap.put("b23_23_16", new visuperobject(23,23,16,"Hỡi kỵ nữ đã bị quên kia, hãy lấy đờn cầm, dạo chơi thành phố! Khảy cho hay, ca đi ca lại, hầu cho người ta nhớ đến mầy!"));
visuperobject.biblemap.put("b23_23_17", new visuperobject(23,23,17,"Mãn bảy mươi năm rồi, Ðức Giê-hô-va sẽ thăm viếng thành Ty-rơ, nó sẽ lại được lợi, sẽ hành dâm cùng mọi nước trong thế gian trên mặt đất."));
visuperobject.biblemap.put("b23_23_18", new visuperobject(23,23,18,"Nhưng hàng hóa lời lãi của nó sẽ nên thánh cho Ðức Giê-hô-va, mà không chứa, cũng không để dành lại. Vì lời lãi ấy sẽ phân phát cho những người ở trước mặt Ðức Giê-hô-va, để họ được ăn sung mặc sướng."));
visuperobject.biblemap.put("b23_24_1", new visuperobject(23,24,1,"Nầy, Ðức Giê-hô-va làm cho đất trống không và hoang vu; Ngài lật đổ mặt đất và làm tan lạc dân cư."));
visuperobject.biblemap.put("b23_24_2", new visuperobject(23,24,2,"Thầy tế lễ như chúng dân, ông chủ như đầy tớ trai, bà chủ như đầy tớ gái, kẻ bán như người mua, kẻ cho mượn như người mượn, kẻ lấy lợi như người nộp lợi, ai cũng đồng một thể."));
visuperobject.biblemap.put("b23_24_3", new visuperobject(23,24,3,"Trên đất sẽ đều trống không và hoang vu cả; vì Ðức Giê-hô-va đã phán lời ấy."));
visuperobject.biblemap.put("b23_24_4", new visuperobject(23,24,4,"Ðất thảm thương và tồi tàn; thế gian lụn bại và tồi tàn; những dân cao nhứt trên đất hao mòn đi."));
visuperobject.biblemap.put("b23_24_5", new visuperobject(23,24,5,"Ðất bị dân cư làm ô uế, vì họ đã phạm luật pháp, trái điều răn, dứt giao ước đời đời."));
visuperobject.biblemap.put("b23_24_6", new visuperobject(23,24,6,"Vậy nên sự rủa sả thiêu nuốt đất, những người ở trên nó mắc tội. Vì cớ đó dân trên đất bị đốt cháy, chỉ còn ít người sót lại."));
visuperobject.biblemap.put("b23_24_7", new visuperobject(23,24,7,"Rượu mới than vãn, cây nho tàn héo, mọi kẻ vốn có lòng vui đều than thở;"));
visuperobject.biblemap.put("b23_24_8", new visuperobject(23,24,8,"những kẻ đánh trống không đánh nữa, tiếng mừng rỡ hết rồi, dọng vui đờn cầm đã dứt."));
visuperobject.biblemap.put("b23_24_9", new visuperobject(23,24,9,"Chẳng còn vừa uống rượu vừa hát nữa; những tay ghiền rượu cho rượu mạnh là cay đắng."));
visuperobject.biblemap.put("b23_24_10", new visuperobject(23,24,10,"Thành hoang loạn đã đổ nát; nhà cửa đều đóng lại, không ai vào được."));
visuperobject.biblemap.put("b23_24_11", new visuperobject(23,24,11,"Người ta kêu la trong đường phố vì cớ rượu; mọi sự vui đều trở nên tăm tối; sự mừng rỡ trong xứ đi đâu rồi!"));
visuperobject.biblemap.put("b23_24_12", new visuperobject(23,24,12,"Trong thành vắng vẻ, cửa thành phá hoại!"));
visuperobject.biblemap.put("b23_24_13", new visuperobject(23,24,13,"Vì giữa các dân trên đất sẽ giống như lúc người ta rung cây ô-li-ve, và như khi mót trái nho sau mùa hái trái."));
visuperobject.biblemap.put("b23_24_14", new visuperobject(23,24,14,"Những kẻ nầy sẽ cất tiếng lên, kêu rao; từ nơi biển kêu lớn tiếng vui mừng vì sự uy nghiêm Ðức Giê-hô-va."));
visuperobject.biblemap.put("b23_24_15", new visuperobject(23,24,15,"Vậy hãy tôn vinh Ðức Giê-hô-va trong phương đông, hãy tôn vinh danh Ðức Giê-hô-va, Ðức Chúa Trời của Y-sơ-ra-ên, trong các cù lao biển!"));
visuperobject.biblemap.put("b23_24_16", new visuperobject(23,24,16,"Chúng ta đã nghe từ nơi đầu cùng đất hát rằng: Vinh hiển cho kẻ công bình! Nhưng tôi nói: Tôi bị gầy mòn, tôi bị gầy mòn! Những kẻ gian dối làm gian dối, phải, kẻ gian dối làm gian dối lắm!"));
visuperobject.biblemap.put("b23_24_17", new visuperobject(23,24,17,"Hỡi dân cư trên đất, sự kinh hãi, hầm và bẫy đến trên ngươi."));
visuperobject.biblemap.put("b23_24_18", new visuperobject(23,24,18,"Kẻ nào trốn khỏi tiếng kinh hãi, sẽ sa xuống hầm; nếu lên khỏi hầm, sẽ mắc vào lưới. Vì các cửa sổ trên trời đều mở ra, các nền dưới đất đều rung rinh."));
visuperobject.biblemap.put("b23_24_19", new visuperobject(23,24,19,"Ðất đều tan nát, đất đều vỡ lở, đất đều rúng động."));
visuperobject.biblemap.put("b23_24_20", new visuperobject(23,24,20,"Ðất lay động như người say; lỏng chỏng như cái võng, tội lỗi chất lên trên nặng trĩu, nó sẽ đổ xuống và không dậy được nữa!"));
visuperobject.biblemap.put("b23_24_21", new visuperobject(23,24,21,"Xảy ra trong ngày đó, Ðức Giê-hô-va sẽ phạt các cơ binh nơi cao ở trên trời, phạt các vua thế gian ở trên đất."));
visuperobject.biblemap.put("b23_24_22", new visuperobject(23,24,22,"Họ sẽ nhóm lại cùng nhau, bị cầm trong hầm như tù bị nhốt trong ngục; cách lâu ngày, sẽ đều bị phạt."));
visuperobject.biblemap.put("b23_24_23", new visuperobject(23,24,23,"Mặt trăng sẽ xấu hổ, mặt trời sẽ mắc cỡ; vì Ðức Giê-hô-va vạn quân sẽ trị vì trên núi Si-ôn, tại Giê-ru-sa-lem; và sự vinh hiển sẽ chói lói trước mặt các trưởng lão."));
visuperobject.biblemap.put("b23_25_1", new visuperobject(23,25,1,"Hỡi Ðức Giê-hô-va, Ngài là Ðức Chúa Trời tôi! Tôi tôn sùng Ngài, tôi ngợi khen danh Ngài; vì Ngài đã làm những sự mới lạ, là những mưu đã định từ xưa, cách thành tín chơn thật."));
visuperobject.biblemap.put("b23_25_2", new visuperobject(23,25,2,"Vì Ngài đã làm cho thành trở nên gò đống, thành bền vững trở nên nơi đổ nát. Ðền đài của dân ngoại chẳng phải là thành nữa, sẽ chẳng hề xây lại."));
visuperobject.biblemap.put("b23_25_3", new visuperobject(23,25,3,"Vậy nên một dân cường thạnh sẽ tôn vinh Ngài; thành của các dân tộc đáng kinh hãi sẽ kính sợ Ngài."));
visuperobject.biblemap.put("b23_25_4", new visuperobject(23,25,4,"Vì Ngài là nơi bền vững cho kẻ nghèo, đồn lũy cho kẻ thiếu thốn trong lúc khó khăn, chỗ đụt cho khỏi bão, chỗ bóng mát cho khỏi nắng, khi những kẻ cường bạo thổi hơi ra như bão, xông vào tường thành."));
visuperobject.biblemap.put("b23_25_5", new visuperobject(23,25,5,"Ngài sẽ dứt sự ồn ào của dân ngoại, như trừ khi nóng trong nơi đất hạn; ngừng tiếng hát nừng của kẻ cường bạo, như hơi nắng tan đi bởi bóng mây."));
visuperobject.biblemap.put("b23_25_6", new visuperobject(23,25,6,"Ðức Giê-hô-va vạn quân sẽ ban cho mọi dân tộc, tại trên núi nầy, một tiệc yến đồ béo, một diên rượu ngon, đồ béo có tủy, rượu ngon lọc sạch."));
visuperobject.biblemap.put("b23_25_7", new visuperobject(23,25,7,"Tại trên núi nầy Ngài sẽ trừ diệt mặt của đồ đắp trên muôn dân, và cái màn che phủ mọi dân tộc."));
visuperobject.biblemap.put("b23_25_8", new visuperobject(23,25,8,"Ngài đã nuốt sự chết đến đời đời. Chúa Giê-hô-va sẽ lau nước mắt khỏi mọi mặt, và trừ bỏ sự xấu hổ của đến Ngài khỏi cả thế gian; vì Ðức Giê-hô-va đã phán vậy."));
visuperobject.biblemap.put("b23_25_9", new visuperobject(23,25,9,"Trong ngày đó, người ta sẽ nói rằng: Kìa, ấy là Ðức Chúa Trời chúng ta; chúng ta đã mong đợi Ngài, và Ngài sẽ cứu chúng ta. Ấy là Ðức Giê-hô-va; chúng ta sẽ nức lòng mừng rỡ và đồng vui về sự cứu rỗi của Ngài!"));
visuperobject.biblemap.put("b23_25_10", new visuperobject(23,25,10,"Vì tay Ðức Giê-hô-va sẽ đặt trên núi nầy; nhưng Mô-áp sẽ bị giày đạp trong nơi nó, như rơm bị giày đạp trong ao phân."));
visuperobject.biblemap.put("b23_25_11", new visuperobject(23,25,11,"Nó sẽ giang tay trong đó, như người lội bơi giang tay ra để lội; nhưng Ðức Giê-hô-va sẽ dằn sự kiêu ngạo nó xuống, và cất bỏ mọi cơ mưu của tay nó."));
visuperobject.biblemap.put("b23_25_12", new visuperobject(23,25,12,"Ngài sẽ hạ lũy cao của tường thành ngươi xuống, đánh đổ, và ném xuống đất vào trong bụi bặm."));
visuperobject.biblemap.put("b23_26_1", new visuperobject(23,26,1,"Trong ngày đó, tại xứ Giu-đa, người ta sẽ hát bài nầy: Chúng ta có thành bền vững! Chúa lấy sự cứu làm tường làm lũy!"));
visuperobject.biblemap.put("b23_26_2", new visuperobject(23,26,2,"Hãy mở các cửa, cho dân công bình trung tín vào đó!"));
visuperobject.biblemap.put("b23_26_3", new visuperobject(23,26,3,"Người nào để trí mình nương dựa nơi Ngài, thì Ngài sẽ gìn giữ người trong sự bình yên trọn vẹn, vì người nhờ cậy Ngài."));
visuperobject.biblemap.put("b23_26_4", new visuperobject(23,26,4,"Hãy nhờ cậy Ðức Giê-hô-va đời đời, vì Ðức Giê-hô-va, chính Ðức Giê-hô-va, là vầng đá của các thời đại!"));
visuperobject.biblemap.put("b23_26_5", new visuperobject(23,26,5,"Ngài đã dằn những người ở nơi cao; đã phá đổ thành cao ngất, hạ xuống tới đất, sa vào bụi bặm,"));
visuperobject.biblemap.put("b23_26_6", new visuperobject(23,26,6,"bị giày đạp dưới chơn, bị bàn chơn kẻ nghèo nàn, thiếu thốn bước lên trên và nghiền nát."));
visuperobject.biblemap.put("b23_26_7", new visuperobject(23,26,7,"Ðường của người công bình là ngay thẳng; Ngài là Ðấng ngay thẳng ban bằng đường của người công bình."));
visuperobject.biblemap.put("b23_26_8", new visuperobject(23,26,8,"Hỡi Ðức Giê-hô-va, thật chúng tôi đứng trên đường phán xét mà trông đợi Ngài; danh Ngài, là sự ghi nhớ Ngài, vốn là sự ao ước của linh hồn chúng tôi."));
visuperobject.biblemap.put("b23_26_9", new visuperobject(23,26,9,"Ðương ban đêm, linh hồn tôi đã ao ước Ngài; phải, thần linh trong tôi sẽ thiết tha tìm Ngài; vì khi những sự phán xét Ngài làm ra trên đất, dân cư của thế gian đều học sự công bình."));
visuperobject.biblemap.put("b23_26_10", new visuperobject(23,26,10,"Kẻ ác dầu có được ơn, cũng chẳng học sự công bình; song nó sẽ làm điều ác trong đất ngay thẳng, và không nhìn xem uy nghiêm của Ðức Giê-hô-va."));
visuperobject.biblemap.put("b23_26_11", new visuperobject(23,26,11,"Hỡi Ðức Giê-hô-va, tay Ngài đã giá lên, mà chúng nó chẳng xem thấy, song chúng nó sẽ thấy, và sẽ xấu hổ bởi cớ lòng sốt sắng Ngài tỏ ra vì dân mình. Lửa sẽ đốt cháy kẻ thù nghịch Ngài."));
visuperobject.biblemap.put("b23_26_12", new visuperobject(23,26,12,"Hỡi Ðức Giê-hô-va, Ngài sẽ lập sự bình an cho chúng tôi; vì mọi điều chúng tôi làm, ấy là Ngài làm cho!"));
visuperobject.biblemap.put("b23_26_13", new visuperobject(23,26,13,"Hỡi Giê-hô-va Ðức Chúa Trời chúng tôi, ngoài Ngài từng có chủ khác cai trị chúng tôi, nay chúng tôi chỉ cậy một mình Ngài mà kêu cầu danh Ngài."));
visuperobject.biblemap.put("b23_26_14", new visuperobject(23,26,14,"Họ đã chết, sẽ không sống nữa, đã thành âm hồn không dậy nữa: vì Ngài đã phạt và diệt họ, cho đến sự ghi nhớ họ, Ngài cũng làm ra hư không."));
visuperobject.biblemap.put("b23_26_15", new visuperobject(23,26,15,"Hỡi Ðức Giê-hô-va, Ngài đã thêm dân nầy lên, Ngài đã thêm dân nầy lên. Ngài đã được vinh hiển, đã mở mang bờ cõi đất nầy."));
visuperobject.biblemap.put("b23_26_16", new visuperobject(23,26,16,"Hỡi Ðức Giê-hô-va, họ đã tìm kiếm Ngài trong lúc ngặt nghèo, kêu cầu Ngài khi Ngài sửa phạt họ."));
visuperobject.biblemap.put("b23_26_17", new visuperobject(23,26,17,"Hỡi Ðức Giê-hô-va, chúng tôi ở trước mặt Ngài khác nào như đờn bà có thai gần đẻ, đang đau đớn kêu rêu vì quặn thắt."));
visuperobject.biblemap.put("b23_26_18", new visuperobject(23,26,18,"Chúng tôi đã có mang, đã ở cữ, và đẻ ra như gió, chẳng làm sự cứu trong trái đất, cũng chẳng sanh ra dân ở trong thế gian."));
visuperobject.biblemap.put("b23_26_19", new visuperobject(23,26,19,"Những kẻ chết của Ngài sẽ sống, những xác chết của tôi đều chổi dậy! Hỡi kẻ ở trong bụi đất, hãy thức dậy, hãy hát! Vì hột móc của Ngài như hột móc lúc hừng đông, đất sẽ buông các âm hồn ra khỏi."));
visuperobject.biblemap.put("b23_26_20", new visuperobject(23,26,20,"Hỡi dân ta, hãy đến vào buồng và đóng cửa lại. Hãy ẩn mình một lát, cho đến chừng nào cơn giận đã qua."));
visuperobject.biblemap.put("b23_26_21", new visuperobject(23,26,21,"Vì nầy, Ðức Giê-hô-va từ nơi Ngài, đặng phạt dân cư trên đất vì tội ác họ. Bấy giờ đất sẽ tỏ bày huyết nó ra, và không che-đậy những kẻ đã bị giết nữa."));
visuperobject.biblemap.put("b23_27_1", new visuperobject(23,27,1,"Trong ngày đó, Ðức Giê-hô-va sẽ dùng gươm cứng, lớn, và mạnh mà phạt lê-vi-a-than, là con rắn lẹ làng, phạt lê-vi-a-than là con rắn cong queo; và Ngài sẽ giết con vật lớn dưới biển."));
visuperobject.biblemap.put("b23_27_2", new visuperobject(23,27,2,"Trong ngày đó, các ngươi khá hát bài nói về vườn nho sanh ra rượu nho!"));
visuperobject.biblemap.put("b23_27_3", new visuperobject(23,27,3,"Ấy chính ta, Ðức Giê-hô-va, là Ðấng giữ nó, sẽ chốc chốc tưới nó, và giữ nó đêm ngày, kẻo người ta phá hại chăng."));
visuperobject.biblemap.put("b23_27_4", new visuperobject(23,27,4,"Ta chẳng căm giận. Mặc ai đem chà chuôm gai gốc mà chống cự cùng ta! Ta sẽ đi đánh chúng nó, đốt chung làm một."));
visuperobject.biblemap.put("b23_27_5", new visuperobject(23,27,5,"Chẳng gì bằng nhờ sức ta, làm hòa với ta, phải, hãy làm hòa với ta!"));
visuperobject.biblemap.put("b23_27_6", new visuperobject(23,27,6,"Sau nầy, Gia-cốp đâm rễ, Y-sơ-ra-ên kết nụ và nở hoa, chắc sẽ ra trái đầy trên mặt đất."));
visuperobject.biblemap.put("b23_27_7", new visuperobject(23,27,7,"Ðức Chúa Trời đánh nó há như đánh kẻ đã đánh nó sao? Nó bị giết há như những kẻ giết nó đã bị giết sao?"));
visuperobject.biblemap.put("b23_27_8", new visuperobject(23,27,8,"Ấy là Ngài đã tranh nhau với nó cách chừng đỗi, mà dời nó đi, trong một ngày có gió đông, nó bị đùa đi bởi gió bão của Ngài."));
visuperobject.biblemap.put("b23_27_9", new visuperobject(23,27,9,"Vậy, nầy là cớ vì sao mà tội Gia-cốp sẽ được tha, và nầy là quả của sự trừ bỏ tội nó đi: nó sẽ làm cho hết thảy đá của bàn thờ ra như đó vôi bể nát; các hình tượng và các trụ mặt trời sẽ không dựng lên nữa."));
visuperobject.biblemap.put("b23_27_10", new visuperobject(23,27,10,"Vì thành bền vững đã trở nên tiêu điều, nhà ở sẽ bị bỏ và để hoang, dường như đồng vắng. Bò tơ sẽ đến ăn cỏ tại đó, nằm và nhá những nhánh cây."));
visuperobject.biblemap.put("b23_27_11", new visuperobject(23,27,11,"Khi nhánh khô và bị bẻ, đờn bà đến lấy mà nhen lửa. Vì dân nầy không có trí khôn, nên Ðấng đã làm nên nó chẳng thương xót đến, Ðấng tạo nên nó chẳng ban ơn cho."));
visuperobject.biblemap.put("b23_27_12", new visuperobject(23,27,12,"Xảy ra trong ngày đó, Ðức Giê-hô-va sẽ lung lay từ lòng Sông cái cho đến khe Ê-díp-tô; hỡi con cái Y-sơ-ra-ên, các ngươi đều sẽ bị lượm từng người một!"));
visuperobject.biblemap.put("b23_27_13", new visuperobject(23,27,13,"Trong ngày đó, kèn lớn sẽ thổi lên. Bấy giờ những kẻ bị thất lạc trong xứ A-si-ri, và những kẻ bị đày trong đất Ê-díp-tô, sẽ đến thờ lạy trước mặt Ðức Giê-hô-va, trên hòn núi thánh, tại Giê-ru-sa-lem."));
visuperobject.biblemap.put("b23_28_1", new visuperobject(23,28,1,"Khốn thay cho mão triều thiên kiêu ngạo của những bợm rượu Ép-ra-im, khốn thay cho hoa chóng tàn làm đồ trang sức đẹp nhứt dân ấy, đóng tại nơi trũng màu mỡ của những người say rượu?"));
visuperobject.biblemap.put("b23_28_2", new visuperobject(23,28,2,"Nầy, từ nơi Chúa có một người mạnh và có quyền, giống như cơn bão mưa đá, như trận gió phá hại, như nước lớn vỡ bờ. Người sẽ lấy tay ném cả xuống đất."));
visuperobject.biblemap.put("b23_28_3", new visuperobject(23,28,3,"Mão triều thiên kiêu ngạo của những bợm rượu Ép-ra-im sẽ bị giày đạp dưới chơn."));
visuperobject.biblemap.put("b23_28_4", new visuperobject(23,28,4,"Cái hoa chóng tàn làm đồ trang sức đẹp nhứt dân ấy, đóng tại nơi trũng màu mỡ, cũng sẽ giống như trái vả chín trước mùa hè: người ta thấy nó, vừa mới vào tay thì đã nuốt."));
visuperobject.biblemap.put("b23_28_5", new visuperobject(23,28,5,"Trong ngày đó, Ðức Giê-hô-va vạn quân sẽ trở nên mão triều thiên chói sáng và mão miện rực rỡ của dân sót Ngài;"));
visuperobject.biblemap.put("b23_28_6", new visuperobject(23,28,6,"sẽ trở nên thần công chính cho kẻ ngồi trên tòa xét đoán, và sức mạnh cho những kẻ đuổi giặc nơi cửa thành."));
visuperobject.biblemap.put("b23_28_7", new visuperobject(23,28,7,"Song những kẻ ấy cũng choáng váng vì rượu, xiêu tó vì các thứ rượu mạnh. Thầy tế lễ và đấng tiên tri đều choáng váng vì rượu mạnh, bị rượu nuốt đi, nhơn các thứ rượu mạnh mà xoàng ba; xem sự hiện thấy thì cắt nghĩa sai, xử kiện thì vấp ngã;"));
visuperobject.biblemap.put("b23_28_8", new visuperobject(23,28,8,"mửa ra ô uế đầy bàn tiệc, chẳng có chỗ nào sạch!"));
visuperobject.biblemap.put("b23_28_9", new visuperobject(23,28,9,"Vậy người sẽ dạy khôn cho ai, và khiến ai hiểu sẽ dạy dỗ mình? Có phải là dạy cho những trẻ con thôi bú, mới lìa khỏi vú chăng?"));
visuperobject.biblemap.put("b23_28_10", new visuperobject(23,28,10,"Vì, với họ phải giềng mối thêm giềng mối, giềng mối thêm giềng mối; hàng thêm hàng, hàng thêm hàng; một chút chỗ nầy, một chút chỗ kia!"));
visuperobject.biblemap.put("b23_28_11", new visuperobject(23,28,11,"Vậy nên Ðức Giê-hô-va sẽ dùng môi lạ lưỡi khác mà phán cùng dân nầy."));
visuperobject.biblemap.put("b23_28_12", new visuperobject(23,28,12,"Ngài đã phán cùng nó rằng: Ðây là nơi yên nghỉ; hãy để kẻ mệt nhọc được yên nghỉ. Nầy là lúc mát mẻ cho các ngươi. Thế mà họ chẳng chịu nghe."));
visuperobject.biblemap.put("b23_28_13", new visuperobject(23,28,13,"Lời Ðức Giê-hô-va đối với họ sẽ là giềng mối thêm giềng mối, giềng mối thêm giềng mối; hàng thêm hàng, hàng thêm hàng; một chút chỗ nầy, một chút chỗ kia; hầu cho họ bước tới, thì ngã nhào và giập nát, sập bẫy, và bị bắt!"));
visuperobject.biblemap.put("b23_28_14", new visuperobject(23,28,14,"Vậy nên, hỡi các ngươi là những người ngạo mạn, cai trị dân nầy ở Giê-ru-sa-lem, hãy nghe lời của Ðức Giê-hô-va."));
visuperobject.biblemap.put("b23_28_15", new visuperobject(23,28,15,"Vì các ngươi nói rằng: Chúng ta đã kết ước với sự chết, và giao ước cùng nơi âm phủ. Khi tai nạn hủy diệt trải qua, sẽ chẳng chạm đến ta đâu. Chúng ta lấy sự nói dối là nơi nương náu, sự gian lận làm nơi ẩn mình."));
visuperobject.biblemap.put("b23_28_16", new visuperobject(23,28,16,"Vậy nên, Chúa Giê-hô-va phán như vầy: Nầy, ta đặt tại Si-ôn một hòn đá để làm nền, là đá đã thử nghiệm, là đá góc quí báu, là nền bền vững: ai tin sẽ chẳng gấp rúc."));
visuperobject.biblemap.put("b23_28_17", new visuperobject(23,28,17,"Ta sẽ lấy sự chánh trực làm dây đo, sự công bình làm chuẩn mực; mưa đó sẽ hủy diệt nơi ẩn náu bằng sự nói dối, nước sẽ ngập chỗ nương náu."));
visuperobject.biblemap.put("b23_28_18", new visuperobject(23,28,18,"Bấy giờ ước các người kết với sự chết sẽ phế đi, ước các ngươi giao cùng nơi âm phủ sẽ không đứng được. Khi tai nạn hủy diệt trải qua, các ngươi đều bị giày đạp."));
visuperobject.biblemap.put("b23_28_19", new visuperobject(23,28,19,"Mỗi lần tai nạn trải qua, sẽ bắt lấy các ngươi, vì nó trải qua mọi buổi sớm mai, ngày và đêm; chỉ có sự kính sợ làm cho các ngươi hiểu biết lời dạy ấy."));
visuperobject.biblemap.put("b23_28_20", new visuperobject(23,28,20,"Giường ngắn quá không thể duỗi mình; nền hẹp quá không thể quấn mình."));
visuperobject.biblemap.put("b23_28_21", new visuperobject(23,28,21,"Vì Ðức Giê-hô-va sẽ dấy lên như ở núi Phê-ra-xim; Ngài nổi giận như tại trũng Ga-ba-ôn, đặng làm việc Ngài, là việc khác thường, và làm công Ngài, là công lạ lùng."));
visuperobject.biblemap.put("b23_28_22", new visuperobject(23,28,22,"Vậy bây giờ, đừng khinh lờn nữa, e dây tói càng buộc chặt các ngươi chẳng; vì ta có nghe từ nơi Chúa, là Ðức Giê-hô-va vạn quân, lịnh truyền hủy diệt cả đất."));
visuperobject.biblemap.put("b23_28_23", new visuperobject(23,28,23,"Hãy lắng tai, nghe tiếng ta; hãy để ý, nghe lời ta!"));
visuperobject.biblemap.put("b23_28_24", new visuperobject(23,28,24,"Kẻ cày để gieo, há hằng ngày cứ cày luôn sao? Há chẳng vỡ đất và bừa hay sao?"));
visuperobject.biblemap.put("b23_28_25", new visuperobject(23,28,25,"Khi đã bông bằng mặt đất rồi, há chẳng vải tiểu hồi, gieo đại hồi ư? Há chẳng tỉa lúa mì nơi rãnh, mạch nha nơi đã cắm dấu, và đại mạch trên bờ ư?"));
visuperobject.biblemap.put("b23_28_26", new visuperobject(23,28,26,"Ấy là Ðức Chúa Trời dạy và bảo nó cách phải làm."));
visuperobject.biblemap.put("b23_28_27", new visuperobject(23,28,27,"Vì người ta chẳng dùng trái lăn mà nghiền tiểu hồi, cũng chẳng cho bánh-xe lăn trên đại hồi; nhưng tiểu hồi thì đập bằng cái que, đại hồi thì đập bằng cái lẻ."));
visuperobject.biblemap.put("b23_28_28", new visuperobject(23,28,28,"Người ta vẫn xay lúa mì; những chẳng đập luôn dầu cho hột thóc qua dưới bánh xe hoặc chơn ngựa, song không giập nát."));
visuperobject.biblemap.put("b23_28_29", new visuperobject(23,28,29,"Ðiều đó cũng ra bởi Ðức Giê-hô-va vạn quân, mưu Ngài lạ lùng, sự khôn ngoan Ngài tốt lành."));
visuperobject.biblemap.put("b23_29_1", new visuperobject(23,29,1,"Khốn cho A-ri-ên, cho A-ri-ên, là thành Ða-vít đã đóng trại! Năm lại thêm năm, trải qua các kỳ lễ."));
visuperobject.biblemap.put("b23_29_2", new visuperobject(23,29,2,"Bấy giờ ta sẽ làm cho A-ri-ên thắt ngặt; tại đó sẽ có tang chế than vãn; nhưng nó vẫn còn làm A-ri-ên cho ta."));
visuperobject.biblemap.put("b23_29_3", new visuperobject(23,29,3,"Ta sẽ đóng trại vây ngươi, lập đồn hãm ngươi, đắp lũy nghịch cùng ngươi."));
visuperobject.biblemap.put("b23_29_4", new visuperobject(23,29,4,"Ngươi sẽ bị hạ xuống, nói ra từ dưới đất, giọng ngươi rầm rĩ từ bụi đất mà ra. Tiếng ngươi lên khỏi đất giống tiếng đồng bóng, lời nói ngươi thỏ thẻ ra từ bụi đất."));
visuperobject.biblemap.put("b23_29_5", new visuperobject(23,29,5,"Nhưng muôn vàn kẻ thù ngươi sẽ như bụi nhỏ, lũ cường bạo như trấu bay đi; sự đó sẽ xảy đến thình lình chỉ trong một lát."));
visuperobject.biblemap.put("b23_29_6", new visuperobject(23,29,6,"Ðức Giê-hô-va vạn quân sẽ dùng sấm sét, động đất, tiếng ầm, gió lốc, bão, và ngọn lửa thiêu nuốt mà thăm phạt nó."));
visuperobject.biblemap.put("b23_29_7", new visuperobject(23,29,7,"Như chiêm bao, như sự hiện thấy trong ban đêm thể nào, thì quân chúng của mọi nước đánh A-ri-ên, hết thảy đều hãm thành và đồn lũy, mà ép riết nó, cũng thể ấy."));
visuperobject.biblemap.put("b23_29_8", new visuperobject(23,29,8,"Như người đói chiêm bao thấy ăn, mà thức dậy, bụng vẫn trống; như người khát chiêm bao thấy uống, mà thức dậy, nghe trong mình kiệt đi và khát khao, cả quân chúng của mọi nước đánh núi Si-ôn khác nào như vậy."));
visuperobject.biblemap.put("b23_29_9", new visuperobject(23,29,9,"Hãy sững sờ và kinh hãi! Hãy mù mắt và quáng lòa! Họ say, nhưng không phải vì rượu; xoàng ba, nhưng không phải bởi rượu mạnh."));
visuperobject.biblemap.put("b23_29_10", new visuperobject(23,29,10,"Vì Ðức Giê-hô-va đã rải thần ngủ mê khắp trên các ngươi; đã bịt mắt các ngươi, tức là các kẻ tiên tri; đã trùm đầu các ngươi, tức là các kẻ tiên kiến."));
visuperobject.biblemap.put("b23_29_11", new visuperobject(23,29,11,"Vậy nên mọi sự hiện thấy đối với các ngươi đã nên như lời của quyển sách đóng ấn, đưa cho người biết đọc, mà rằng: Xin đọc sách nầy! thì nó trả lời rằng: Tôi không đọc được, vì sách nầy có đóng ấn;"));
visuperobject.biblemap.put("b23_29_12", new visuperobject(23,29,12,"hoặc đưa cho người không biết đọc, mà rằng: Xin đọc sách nầy! thì nó rằng: Tôi không biết đọc."));
visuperobject.biblemap.put("b23_29_13", new visuperobject(23,29,13,"Chúa có phán rằng: Vì dân nầy chỉ lấy miệng tới gần ta, lấy môi miếng tôn ta, mà lòng chúng nó thì cách xa ta lắm; sự chúng nó kính sợ ta chẳng qua là điều răn của loài người, bởi loài người dạy cho;"));
visuperobject.biblemap.put("b23_29_14", new visuperobject(23,29,14,"Vì cớ đó, ta sẽ cứ làm việc lạ lùng giữa dân nầy, sự lạ rất lạ, đến nỗi sự khôn ngoan của người khôn ngoan sẽ ra hư không, sự thông sáng của người thông sáng sẽ bị giấu."));
visuperobject.biblemap.put("b23_29_15", new visuperobject(23,29,15,"Khốn thay cho những kẻ giấu kín mưu mình cách thẳm sâu khỏi Ðức Giê-hô-va, làm việc mình trong xó tối, và nói rằng: Ai thấy ta, ai biết ta?"));
visuperobject.biblemap.put("b23_29_16", new visuperobject(23,29,16,"Các ngươi thật là trái ngược quá, há nên xem người thợ gốm như đất sét sao? Ðồ vật há được nói về kẻ làm nên mình rằng: Nó chẳng làm ra ta? Cái bình há được nói về kẻ tạo mình rằng: Nó chẳng có trí hiểu đâu."));
visuperobject.biblemap.put("b23_29_17", new visuperobject(23,29,17,"Chẳng phải còn ít lâu nữa, thì Li-ban sẽ đổi làm ruộng tốt, và ruộng tốt sẽ được kể như rừng rậm sao?"));
visuperobject.biblemap.put("b23_29_18", new visuperobject(23,29,18,"Trong ngày đó, kẻ điếc sẽ nghe những lời trong sách; con mắt của kẻ đui sẽ xem thấy từ sự tối tăm mù mịt."));
visuperobject.biblemap.put("b23_29_19", new visuperobject(23,29,19,"Những người nhu mì cũng sẽ được thêm sự vui vẻ trong Ðức Giê-hô-va, những kẻ nghèo khổ trong loài người sẽ nhơn Ðấng Thánh của Y-sơ-ra-ên mà mừng rỡ."));
visuperobject.biblemap.put("b23_29_20", new visuperobject(23,29,20,"Vì người bạo ngược đã bị diệt; kẻ khinh lờn đã mất đi, phàm những kẻ nhơn dịp làm sự ác đã bị trừ tiệt,"));
visuperobject.biblemap.put("b23_29_21", new visuperobject(23,29,21,"tức những kẻ lấy lời nói định tội người ta, gài bẫy cho những người kiện cáo trước cửa, lấy sự hư không làm khuất công bình."));
visuperobject.biblemap.put("b23_29_22", new visuperobject(23,29,22,"Vậy nên, Ðức Giê-hô-va, là Ðấng đã chuộc Áp-ra-ham, có phán về nhà Gia-cốp như vầy: Gia-cốp sẽ không còn hổ thẹn, và mặt nó sẽ chẳng tái nữa."));
visuperobject.biblemap.put("b23_29_23", new visuperobject(23,29,23,"Nhưng khi nó thấy các con trai mình là việc tay ta làm ra giữa họ, thì sẽ tôn danh ta là thánh; tôn sùng Ðấng Thánh của Gia-cốp, kính sợ Ðức Chúa Trời của Y-sơ-ra-ên."));
visuperobject.biblemap.put("b23_29_24", new visuperobject(23,29,24,"Những kẻ lòng vốn sai lầm sẽ trở nên khôn sáng, kẻ hay oán trách sẽ được dạy dỗ."));
visuperobject.biblemap.put("b23_30_1", new visuperobject(23,30,1,"Ðức Giê-hô-va phán: Khốn thay cho con cái bội nghịch, lập mưu chẳng bởi ý ta, kết ước chẳng cậy Thần ta, hầu cho thêm tội trên tội;"));
visuperobject.biblemap.put("b23_30_2", new visuperobject(23,30,2,"chúng nó chưa hỏi miệng ta, đã khởi đi xuống Ê-díp-tô, để cậy sức mạnh của Pha-ra-ôn mà thêm sức cho mình, và núp dưới bóng Ê-díp-tô!"));
visuperobject.biblemap.put("b23_30_3", new visuperobject(23,30,3,"Cho nên sức mạnh của Pha-ra-ôn sẽ làm sỉ nhục các ngươi, nơi ẩn náu dưới bóng Ê-díp-tô sẽ trở nên sự xấu hổ các ngươi."));
visuperobject.biblemap.put("b23_30_4", new visuperobject(23,30,4,"Các quan trưởng dân nó đã tới Xô-an, sứ giả đã đến Ha-ne;"));
visuperobject.biblemap.put("b23_30_5", new visuperobject(23,30,5,"hết thảy sẽ mắc cỡ vì một dân kia chẳng làm ích chi cho mình, chẳng cứu giúp cũng chẳng làm lợi, song làm cho mang hổ mang nhuốc."));
visuperobject.biblemap.put("b23_30_6", new visuperobject(23,30,6,"Gánh nặng về các thú vật phương nam. Trong xứ gian nan khốn khổ, tự xứ đó đến những sư tử đực, sư tử cái, rắn lục và rắn lửa bay. Chúng nó chở của cải mình trên vai lửa con, vật báu trên gu lạc đà, đặng dâng cho một dân tộc chẳng làm ích gì được hết."));
visuperobject.biblemap.put("b23_30_7", new visuperobject(23,30,7,"Sự cứu giúp của người Ê-díp-tô chẳng qua là hư không vô ích, nên ta đặt tên nó là Ra-háp, ngồi yên chẳng động."));
visuperobject.biblemap.put("b23_30_8", new visuperobject(23,30,8,"Bây giờ, ngươi hãy đi, chạm lời nầy trên bảng nhỏ ở trước mặt họ, hãy chép vào sách, để truyền lại đời sau, mãi mãi không cùng."));
visuperobject.biblemap.put("b23_30_9", new visuperobject(23,30,9,"Dân nầy là bội nghịch, là con cái nói dối, con cái không muốn nghe luật pháp Ðức Giê-hô-va;"));
visuperobject.biblemap.put("b23_30_10", new visuperobject(23,30,10,"nói với kẻ tiên kiến rằng: Ðừng tiên kiến làm chi! nói với kẻ tiên tri rằng: Ðừng nói tiên tri về lẽ thật! Hãy nói cho chúng tôi những đều vui thú, hãy lấy những sự huyễn hoặc bảo chúng tôi!"));
visuperobject.biblemap.put("b23_30_11", new visuperobject(23,30,11,"Hãy lìa khỏi đường, bỏ khỏi lối, cất Ðấng Thánh của Y-sơ-ra-ên khỏi mặt chúng tôi!"));
visuperobject.biblemap.put("b23_30_12", new visuperobject(23,30,12,"Vậy nên, Ðấng Thánh của Y-sơ-ra-ên phán như vầy: Vì các ngươi khinh bỏ lời nầy, cậy điều bạo ngược và trái nghịch, mà nương-nhờ những sự ấy,"));
visuperobject.biblemap.put("b23_30_13", new visuperobject(23,30,13,"bởi cớ đó, tội các ngươi như tường nẻ sắp vỡ, lồi ra trên cao, chỉ trong giây phút sẽ thình lình đổ xuống."));
visuperobject.biblemap.put("b23_30_14", new visuperobject(23,30,14,"Ngài sẽ đập bể nó như cái bình thợ gốm bị bể ra, bể ra từng miếng, chẳng tiếc gì; đến nỗi trong những miếng mẻ nó, sẽ chẳng tìm được một mảnh nào để lấy lửa nơi bếp hay là múc nước nơi hồ."));
visuperobject.biblemap.put("b23_30_15", new visuperobject(23,30,15,"Vả, Chúa Giê-hô-va, là Ðấng Thánh của Y-sơ-ra-ên, có phán như vầy: Các ngươi sẽ được rỗi, là tại trở lại và yên nghỉ; các ngươi sẽ được sức mạnh, là tại yên lặng và trông cậy. Nhưng các ngươi đã không muốn thế!"));
visuperobject.biblemap.put("b23_30_16", new visuperobject(23,30,16,"Các ngươi nói rằng: Không; chúng tôi sẽ cỡi ngựa chạy trốn! Ừ phải, các ngươi sẽ phải chạy trốn. Chúng tôi cỡi ngựa chạy mau! Ừ phải, những kẻ đuổi theo các ngươi cũng sẽ chạy mau!"));
visuperobject.biblemap.put("b23_30_17", new visuperobject(23,30,17,"Một người dọa, ngàn người đi trốn; năm người dọa, các ngươi đều đi trốn, cho đến chừng nào các ngươi còn sót lại như cây vọi trên đỉnh núi, như cờ xí trên gò."));
visuperobject.biblemap.put("b23_30_18", new visuperobject(23,30,18,"Dầu vậy, Ðức Giê-hô-va còn chờ đợi để làm ơn cho các ngươi; Ngài sẽ được tôn lên mà thương xót các ngươi; vì Giê-hô-va là Ðức Chúa Trời công nghĩa. Phước thay cho mọi kẻ trông đợi Ngài!"));
visuperobject.biblemap.put("b23_30_19", new visuperobject(23,30,19,"Vì dân nầy sẽ ở trong Si-ôn nơi Giê-ru-sa-lem; ngươi chẳng còn khóc lóc nữa. Khi ngươi kêu, Ngài chắc sẽ làm ơn; mới vừa nghe ngươi, Ngài liền nhậm lời."));
visuperobject.biblemap.put("b23_30_20", new visuperobject(23,30,20,"Dầu Chúa ban bánh hoạn nạn và nước khốn khó cho các ngươi, các thầy giáo ngươi sẽ chẳng lẩn khuất nữa, mắt ngươi sẽ được thấy các thầy giáo ngươi."));
visuperobject.biblemap.put("b23_30_21", new visuperobject(23,30,21,"Khi các ngươi xê qua bên hữu hoặc bên tả, tai các ngươi sẽ nghe có tiếng đằng sau mình rằng: Nầy là đường đây, hãy noi theo!"));
visuperobject.biblemap.put("b23_30_22", new visuperobject(23,30,22,"Các ngươi sẽ lấy bạc bọc tượng chạm, lấy vàng thếp tượng đúc của mình làm ô uế, mà ném ra xa như đồ dơ bẩn, và bảo nó rằng: Hãy ra khỏi đây!"));
visuperobject.biblemap.put("b23_30_23", new visuperobject(23,30,23,"Chúa sẽ khiến mưa xuống trên giống các ngươi gieo ở đất; bánh lương thực từ đất sanh sản sẽ màu mỡ và dư dật; trong ngày đó, súc vật các ngươi sẽ ăn cỏ nơi đồng rộng."));
visuperobject.biblemap.put("b23_30_24", new visuperobject(23,30,24,"Bò và lừa con cày ruộng sẽ ăn thóc ngon mà người ta dê bằng mủng với nia."));
visuperobject.biblemap.put("b23_30_25", new visuperobject(23,30,25,"Ðến ngày chém giết lớn, khi đồn lũy đổ xuống, thì trên mọi núi cao đồi cả sẽ có những suối nước chảy."));
visuperobject.biblemap.put("b23_30_26", new visuperobject(23,30,26,"Sáng mặt trăng sẽ chói lói như sáng mặt trời, và sự sáng mặt trời sẽ lớn hơn bảy lần, như vốn là sự sáng của bảy ngày, trong ngày Ðức Giê-hô-va buộc dấu vít của dân Ngài, và chữa lành vết thương nó."));
visuperobject.biblemap.put("b23_30_27", new visuperobject(23,30,27,"Nầy, danh Ðức Giê-hô-va từ xa mà đến; cơn giận phừng phừng, như khói đậm bay lên, môi đầy thạnh nộ, lưỡi giống như lửa nuốt."));
visuperobject.biblemap.put("b23_30_28", new visuperobject(23,30,28,"Hơi thở Ngài khác nào sông vỡ lở, nước lên ngập đến cổ. Ngài lấy cái sàng hủy diệt mà sáng các nước, và dùng cái khớp dẫn đi lạc đường mà khớp làm các dân."));
visuperobject.biblemap.put("b23_30_29", new visuperobject(23,30,29,"Bấy giờ các ngươi sẽ xướng ca như trong đêm giữ lễ thánh. Các ngươi sẽ có sự vui trong lòng, như kẻ thổi sáo mà lên núi Ðức Giê-hô-va, đến cùng vầng Ðá của Y-sơ-ra-ên."));
visuperobject.biblemap.put("b23_30_30", new visuperobject(23,30,30,"Ðức Giê-hô-va sẽ làm cho nghe tiếng oai nghiêm Ngài. Ngài sẽ tỏ cánh tay ngăm đe của mình ra, giữa cơn giận Ngài, những ngọn lửa nuốt, gió táp, bão, và mưa đá."));
visuperobject.biblemap.put("b23_30_31", new visuperobject(23,30,31,"Vì, A-si-ri sẽ nghe tiếng Ðức Giê-hô-va mà run sợ. Ngài sẽ lấy roi đánh A-si-ri;"));
visuperobject.biblemap.put("b23_30_32", new visuperobject(23,30,32,"và mỗi khi Ðức Giê-hô-va lấy roi định đánh nó, thì người ta sẽ nghe tiếng trống và đờn cầm; và trong những trận mạc Ðức Giê-hô-va sẽ vung cánh tay mà tranh chiến cùng nó."));
visuperobject.biblemap.put("b23_30_33", new visuperobject(23,30,33,"Vì Tô-phết đã sửa soạn từ xưa; sắm sẳn cho cho vua. Nó sâu và rộng, trên có lửa và củi thật nhiều; hơi thở của Ðức Giê-hô-va như suối lưu hoàng nhúm nó."));
visuperobject.biblemap.put("b23_31_1", new visuperobject(23,31,1,"Khốn thay cho những kẻ xuống Ê-díp-tô đặng cầu cứu, nhờ những ngựa, cậy những xe binh vì nó nhiều, và những lính kỵ vì nó mạnh, mà không ngó về Ðấng Thánh của Y-sơ-ra-ên, và chẳng tìm cầu Ðức Giê-hô-va!"));
visuperobject.biblemap.put("b23_31_2", new visuperobject(23,31,2,"Nhưng mà, Ngài cũng là khôn sáng! Ấy chính Ngài là Ðấng giáng tai vạ, chẳng hề ăn lời mình. Ngài sẽ dấy lên nghịch cùng nhà kẻ dữ, và nghịch cùng kẻ giúp bọn gian ác."));
visuperobject.biblemap.put("b23_31_3", new visuperobject(23,31,3,"Vả, người Ê-díp-tô là loài người, không phải là Ðức Chúa Trời; những ngựa nó chỉ là xác thịt, không phải là thần. Khi Ðức Giê-hô-va sẽ giang tay ra, tức thì kẻ giúp sẽ vấp, kẻ được giúp sẽ ngã, cả hai đều diệt mất."));
visuperobject.biblemap.put("b23_31_4", new visuperobject(23,31,4,"Vì Ðức Giê-hô-va đã phán cùng ta như vầy: Như sư tử hoặc sư tử con bắt được mồi và gầm thét, mặc dầu bọn chăn chiên nhóm lại thành đoàn, nó cũng chẳng thấy kêu mà rùng, thấy động mà sợ; đồng một thể ấy, Ðức Giê-hô-va vạn quân sẽ xuống đặng đánh trận trên núi Si-ôn và trên đồi nó."));
visuperobject.biblemap.put("b23_31_5", new visuperobject(23,31,5,"Như chim bay liệng thể nào, đồng một thể ấy, Ðức Giê-hô-va vạn quân sẽ che chở và cứu vớt cho, sẽ vuợt qua và gìn giữ cho."));
visuperobject.biblemap.put("b23_31_6", new visuperobject(23,31,6,"Hỡi con cái Y-sơ-ra-ên, vậy hãy trở lại cùng Ðấng mà các ngươi đã dấy loạn nghịch cùng,"));
visuperobject.biblemap.put("b23_31_7", new visuperobject(23,31,7,"vì trong ngày đó, ai nấy sẽ bỏ thần tượng mình bằng bạc bằng vàng, là đồ các ngươi đã dùng chính tay mình làm mà phạm tội."));
visuperobject.biblemap.put("b23_31_8", new visuperobject(23,31,8,"A-si-ri sẽ ngã dưới gươm, chẳng phải gươm của loài người; và cái gươm mà chẳng phải là gươm của người ta sẽ nuốt nó. Nó sẽ trốn khỏi gươm đó, và những kẻ trai trẻ nó sẽ phải chịu khổ dịch."));
visuperobject.biblemap.put("b23_31_9", new visuperobject(23,31,9,"Vầng đá nó sẽ qua đi vì sự kinh hãi, các quan trưởng nó sẽ kính sợ vì cờ xí, Ðức Giê-hô-va phán như vậy, tức là Ðấng có lửa tại Si-ôn, có lò tại Giê-ru-sa-lem."));
visuperobject.biblemap.put("b23_32_1", new visuperobject(23,32,1,"Nầy, sẽ có một vua lấy nghĩa trị vì, các quan trưởng lấy lẽ công bình mà cai trị."));
visuperobject.biblemap.put("b23_32_2", new visuperobject(23,32,2,"Sẽ có một người như nơi núp gió và chỗ che bão táp, như suối nước trong nơi đất khô, như bóng vầng đá lớn trong xứ mòn mỏi."));
visuperobject.biblemap.put("b23_32_3", new visuperobject(23,32,3,"Bấy giờ mắt của kẻ xem sẽ không mờ, tai của kẻ nghe sẽ chăm chỉ."));
visuperobject.biblemap.put("b23_32_4", new visuperobject(23,32,4,"Lòng của người hớp tớp sẽ hiểu sự khôn ngoan; lưỡi của người cà lăm sẽ nói dễ và rõ."));
visuperobject.biblemap.put("b23_32_5", new visuperobject(23,32,5,"Kẻ dại dột sẽ chẳng được xưng là cao sang, người rít róng chẳng được xưng là rộng rãi."));
visuperobject.biblemap.put("b23_32_6", new visuperobject(23,32,6,"Vì kẻ dại dột sẽ nói những lời dại dột, lòng họ xiêu về sự gian dối, đặng phạm các tội ác, đặng nói những sự sai lầm nghịch cùng Ðức Giê-hô-va, đặng làm cho kẻ đói trống bụng và kẻ khát hết đồ uống."));
visuperobject.biblemap.put("b23_32_7", new visuperobject(23,32,7,"Kẻ rít róng dùng những khí giới gian tà, toan những mưu gian, lấy sự nói dối đặng làm hại người nhu mì, và làm hại kẻ nghèo dầu nói lẽ công bình."));
visuperobject.biblemap.put("b23_32_8", new visuperobject(23,32,8,"Nhưng người cao sang chỉ lập những mưu cao sang, hằng làm sự cao sang."));
visuperobject.biblemap.put("b23_32_9", new visuperobject(23,32,9,"Hỡi những đờn bà sung sướng, hãy dậy mà nghe tiếng ta! Hỡi những con gái không hay lo, hãy để tai nghe lời ta!"));
visuperobject.biblemap.put("b23_32_10", new visuperobject(23,32,10,"Trong một năm và mấy ngày nữa, các ngươi là kẻ không lo lắng, sẽ đều run rẩy, vì mùa nho sẽ mất, và mùa gặt cũng không có nữa."));
visuperobject.biblemap.put("b23_32_11", new visuperobject(23,32,11,"Ðờn bà sung sướng, hãy run rẩy! con gái không hay lo, hãy bối rối! Hãy cổi áo xống, hãy ở trần, hãy thắt lưng bằng bao gai."));
visuperobject.biblemap.put("b23_32_12", new visuperobject(23,32,12,"Chúng nó sẽ đấm ngực vì đồng điền màu mỡ, vì cây nho sai trái."));
visuperobject.biblemap.put("b23_32_13", new visuperobject(23,32,13,"Chà chuôm gai gốc sẽ mọc lên trên đất dân ta, cùng trên mọi nhà chơi trong thành vui vẻ."));
visuperobject.biblemap.put("b23_32_14", new visuperobject(23,32,14,"Vì cung điện vắng tanh, thành đông đúc hoang loạn; đồi và tháp từ nay sẽ trở nên sào huyệt đến đời đời, cho những lừa rừng choán làm nơi nghỉ, và những bầy vật thả ăn,"));
visuperobject.biblemap.put("b23_32_15", new visuperobject(23,32,15,"cho đến chừng Thần từ nơi cao được rải xuống khắp trên chúng ta, đồng vắng biến thành ruộng tốt, ruộng tốt được kể như rừng rậm."));
visuperobject.biblemap.put("b23_32_16", new visuperobject(23,32,16,"Bấy giờ sự xét đoán sẽ ở nơi đồng vắng, sự công bình sẽ trú nơi ruộng tốt."));
visuperobject.biblemap.put("b23_32_17", new visuperobject(23,32,17,"Công bình sẽ sanh ra bình an, trái của sự công bình là yên lặng và an ổn mãi mãi."));
visuperobject.biblemap.put("b23_32_18", new visuperobject(23,32,18,"Dân ta sẽ trú trong chỗ bình an, trong nhà yên ổn và nơi nghỉ lặng lẽ."));
visuperobject.biblemap.put("b23_32_19", new visuperobject(23,32,19,"Nhưng mưa đá sẽ đổ xuống trên rừng, và thành sẽ bị hạ cả xuống."));
visuperobject.biblemap.put("b23_32_20", new visuperobject(23,32,20,"Phước thay cho các ngươi gieo giống ở bên mọi dòng nước, thả chơn bò lửa đi."));
visuperobject.biblemap.put("b23_33_1", new visuperobject(23,33,1,"Khốn thay cho ngươi chưa bị hại mà hại người, chưa bị dối mà dối người! Khi ngươi hại người vừa rồi, chính ngươi sẽ bị hại; khi ngươi dối người vừa rồi, đến phiên ngươi sẽ bị dối."));
visuperobject.biblemap.put("b23_33_2", new visuperobject(23,33,2,"Hỡi Ðức Giê-hô-va, hãy thương xót chúng tôi! Chúng tôi trông đợi nơi Chúa: xin Chúa làm cánh tay cho chúng tôi mỗi buổi sớm mai, giải cứu chúng tôi trong cơn hoạn nạn!"));
visuperobject.biblemap.put("b23_33_3", new visuperobject(23,33,3,"Nghe tiếng ồn ào, các dân đều trốn tránh; khi Chúa dấy lên, các nước đều vỡ tan."));
visuperobject.biblemap.put("b23_33_4", new visuperobject(23,33,4,"Của cải các ngươi sẽ bị thâu lại như con sâu thâu lại; người ta sấn đến trên nó như cào cào nhảy tới."));
visuperobject.biblemap.put("b23_33_5", new visuperobject(23,33,5,"Ðức Giê-hô-va là cao trọng, vì Ngài ở nơi cao. Ngài làm cho Si-ôn đầy dẫy sự công bình và chánh trực."));
visuperobject.biblemap.put("b23_33_6", new visuperobject(23,33,6,"Ngày giờ của ngươi sẽ được sự bền vững, sự giải cứu dư dật, sự khôn ngoan, sự thông biết, sự kính sợ Ðức Giê-hô-va sẽ là vật châu báu của ngươi."));
visuperobject.biblemap.put("b23_33_7", new visuperobject(23,33,7,"Nầy, những kẻ mạnh bạo đương kêu rêu ở ngoài; các sứ giả cầu hòa khóc lóc đắng cay."));
visuperobject.biblemap.put("b23_33_8", new visuperobject(23,33,8,"Ðường cái hoang loạn, người ta không qua lại nữa; kẻ thù đã dứt lời giao ước, khinh dể các thành ấp, chẳng thèm kể đến nhơn dân."));
visuperobject.biblemap.put("b23_33_9", new visuperobject(23,33,9,"Ðất đai thảm sầu hao mòn. Li-ban xấu hổ và khô héo. Sa-rôn giống như nơi sa mạc; Ba-san và Cạt-mên cây cối điêu tàn."));
visuperobject.biblemap.put("b23_33_10", new visuperobject(23,33,10,"Ðức Giê-hô-va phán: Bây giờ ta sẽ chỗi dậy; bây giờ ta sẽ dấy mình lên; bây giờ ta sẽ lên cao!"));
visuperobject.biblemap.put("b23_33_11", new visuperobject(23,33,11,"Các ngươi sẽ có nghén rơm rạ và sẽ đẻ ra cỏ rác. Hơi các ngươi thở ra tức là lửa thiêu nuốt mình."));
visuperobject.biblemap.put("b23_33_12", new visuperobject(23,33,12,"Các dân sự sẽ như vôi đương sôi nổi, như gai đã chặt bị đốt trong lửa."));
visuperobject.biblemap.put("b23_33_13", new visuperobject(23,33,13,"Các ngươi là kẻ ở xa, hãy nghe việc ta đã làm; còn các ngươi là kẻ ở gần, hãy nhận biết năng lực ta."));
visuperobject.biblemap.put("b23_33_14", new visuperobject(23,33,14,"Những kẻ phạm tội đã kinh hãi trong Si-ôn; bọn vô đạo đã run rẩy. Ai trong chúng ta ở được với đám lửa thiêu nuốt? Ai trong chúng ta ở được với sự thiêu đốt đời đời?"));
visuperobject.biblemap.put("b23_33_15", new visuperobject(23,33,15,"Ấy là kẻ bước theo công bình, nói ra chánh trực; khinh món lợi hoạch tài, vung tay chẳng lấy của hối lộ; bưng tai chẳng nghe chuyện đổ huyết, bịt mắt chẳng thấy điều ác."));
visuperobject.biblemap.put("b23_33_16", new visuperobject(23,33,16,"Kẻ đó sẽ ở trong nơi cao; các vầng đá bền vững sẽ là đồn lũy nó; bánh nó sẽ được ban cho; nước nó sẽ không bao giờ thiếu."));
visuperobject.biblemap.put("b23_33_17", new visuperobject(23,33,17,"Mắt ngươi sẽ ngắm xem vua trong sự tốt đẹp Ngài, sẽ thấy đất mở rộng."));
visuperobject.biblemap.put("b23_33_18", new visuperobject(23,33,18,"Lòng ngươi sẽ suy ngẫm sự kinh khiếp: Chớ nào kẻ đánh thuế ở đâu? Kẻ cầm cân ở đâu? Kẻ đến những đồn lũy ở đâu?"));
visuperobject.biblemap.put("b23_33_19", new visuperobject(23,33,19,"Ngươi sẽ chẳng còn thấy dân cường bạo nữa, là dân nói tiếng líu lo khó nghe, tiếng mọi rợ không hiểu được."));
visuperobject.biblemap.put("b23_33_20", new visuperobject(23,33,20,"Hãy nhìn xem Si-ôn, là thành của các kỳ lễ trọng thể chúng ta! Mắt ngươi sẽ thấy Giê-ru-sa-lem, là chỗ ở yên lặng, là trại sẽ không dời đi nữa, các nọc nó chẳng hề nhổ lên, chẳng có một cái dây nào đứt!"));
visuperobject.biblemap.put("b23_33_21", new visuperobject(23,33,21,"Vì Ðức Giê-hô-va sẽ ở đó với chúng ta trong sự oai nghi, là nơi có sông ngòi rất rộng, không thuyền chèo đi lại, chẳng tàu lớn vượt qua."));
visuperobject.biblemap.put("b23_33_22", new visuperobject(23,33,22,"Thật, Ðức Giê-hô-va là quan xét chúng ta, Ðức Giê-hô-va là Ðấng lập luật cho chúng ta, Ðức Giê-hô-va là vua chúng ta; chính Ngài sẽ cứu chúng ta!"));
visuperobject.biblemap.put("b23_33_23", new visuperobject(23,33,23,"Những dây của ngươi đã cổi ra, không thể chằng chơn cột và giương buồm được. Bấy giờ người ta sẽ chia của cướp bộn bề; kẻ què bắt lấy phần của đó."));
visuperobject.biblemap.put("b23_33_24", new visuperobject(23,33,24,"Dân cư sẽ không nói rằng: Tôi đau. Kẻ ở trong nó sẽ được tha tội."));
visuperobject.biblemap.put("b23_34_1", new visuperobject(23,34,1,"Hỡi các nước, hãy đến gần mà nghe; hỡi các dân, hãy để ý! Ðất và mọi vật trong nó thế gian và mọi vật sanh ra đó, hãy đều nghe!"));
visuperobject.biblemap.put("b23_34_2", new visuperobject(23,34,2,"Vì Ðức Giê-hô-va nổi giận nghịch cùng mọi nước; cơn thạnh nộ Ngài nghịch cùng cả đạo binh họ; Ngài đã giao họ cho sự diệt vong, phó cho sự đánh giết."));
visuperobject.biblemap.put("b23_34_3", new visuperobject(23,34,3,"Kẻ bị giết trong chúng nó sẽ phải ném bỏ, thây chết bay mùi hôi; các núi đầm đìa những máu."));
visuperobject.biblemap.put("b23_34_4", new visuperobject(23,34,4,"Cả cơ binh trên trời sẽ tan tác, các từng trời cuốn lại như cuốn sách; cả cơ binh điêu tàn như lá nho rụng, như lá vả khô rơi xuống."));
visuperobject.biblemap.put("b23_34_5", new visuperobject(23,34,5,"Vì gươm ta đã uống đủ ở trên trời; nầy, nó sẽ xuống trên Ê-đôm, và trên dân mà ta đã rủa sả, để làm sự đoán xét."));
visuperobject.biblemap.put("b23_34_6", new visuperobject(23,34,6,"Gươm của Ðức Giê-hô-va đầy những máu, vấy những mỡ, là máu của chiên con và dê đực, mỡ trái cật của chiên đực. Vì có sự tế tự Ðức Giê-hô-va ở Bốt-ra, và có sự đánh giết lớn ở đất Ê-đôm."));
visuperobject.biblemap.put("b23_34_7", new visuperobject(23,34,7,"Những bò rừng, bò, và bò đực đều ngã xuống; đất chúng nó say vì máu, và bụi chúng nó đượm nhuần vì mỡ."));
visuperobject.biblemap.put("b23_34_8", new visuperobject(23,34,8,"Vì Ðức Giê-hô-va có ngày báo thù, có năm báo trả vì có Si-ôn."));
visuperobject.biblemap.put("b23_34_9", new visuperobject(23,34,9,"Các khe suối Ê-đôm sẽ biến thành nhựa thông, bụi đất nó biến thành lưu hoàng, đất đai nó trở nên nhựa thông đương cháy."));
visuperobject.biblemap.put("b23_34_10", new visuperobject(23,34,10,"Ngày đêm chẳng tắt, hằng bay lên những luồng khỏi; đất sẽ hoang vu từ đời nọ qua đời kia, đời đời sẽ chẳng có người đi qua."));
visuperobject.biblemap.put("b23_34_11", new visuperobject(23,34,11,"Bồ nông và nhím sẽ chiếm lấy đó, chim cú chim quạ sẽ ở đó. Chúa sẽ giáng trên đó cái dây lộn lạo và thước thăng bằng trống không."));
visuperobject.biblemap.put("b23_34_12", new visuperobject(23,34,12,"Chúng nó sẽ gọi các kẻ sang trọng đến ngôi nước mà không có ai; hết thảy các quan trọng nó đều ra hư không."));
visuperobject.biblemap.put("b23_34_13", new visuperobject(23,34,13,"Gai gốc sẽ mọc lên trong lâu đài, chà chuôm cỏ rác mọc lên trong đồn lũy. Ấy sẽ là hang của chó đồng, và chỗ nghỉ của chim đà."));
visuperobject.biblemap.put("b23_34_14", new visuperobject(23,34,14,"Thú rừng và chó rừng sẽ gặp nhau tại đó; dê đực kêu nhau; yêu quái ban đêm loán làm chỗ ở, và làm nơi náu nương yên ổn."));
visuperobject.biblemap.put("b23_34_15", new visuperobject(23,34,15,"Rắn roi sẽ làm ở tại đó, đẻ trứng, ấp cho nở và nhóm con lại dưới bóng mình; sau nữa, những kên kên cùng nhau nhóm tại đó, trống và mái thành đôi."));
visuperobject.biblemap.put("b23_34_16", new visuperobject(23,34,16,"Hãy tìm trong sách Ðức Giê-hô-va và đọc lấy: trong những thú vật ấy chẳng một con nào thiếu, chẳng một con nào là không đủ đôi. Vì ấy là miệng Ðức Giê-hô-va đã truyền, và Thần Ngài đã nhóm chúng nó lại."));
visuperobject.biblemap.put("b23_34_17", new visuperobject(23,34,17,"Chính Chúa đã bắt thăm cho chúng nó, và tay Ngài dùng dây chia đất nầy cho. Những thú vật ấy sẽ được đất nầy làm kỉ vật luôn, và ở đó từ đời nọ qua đời kia."));
visuperobject.biblemap.put("b23_35_1", new visuperobject(23,35,1,"Ðồng vắng và đất khô hạn sẽ vui vẻ; nơi sa mạc sẽ mừng rỡ, và trổ hoa như bông hường."));
visuperobject.biblemap.put("b23_35_2", new visuperobject(23,35,2,"Nó trở hoa nhiều và vui mừng, cất tiếng hát hớn hở. Sự vinh hiển của Li-ban cùng sự tốt đẹp của Cạt-mên và Sa-rôn sẽ được ban cho nó. Chúng sẽ thấy sự vinh hiển Ðức Giê-hô-va, và sự tốt đẹp của Ðức Chúa Trời chúng ta."));
visuperobject.biblemap.put("b23_35_3", new visuperobject(23,35,3,"Hãy làm cho mạnh những tay yếu đuối, làm cho vững những gối run en!"));
visuperobject.biblemap.put("b23_35_4", new visuperobject(23,35,4,"Hãy bảo những kẻ có lòng sợ hãi rằng: Khá vững lòng, đừng sợ nữa! Ðây nầy, Ðức Chúa Trời các ngươi sẽ lấy sự báo thù mà đến, tức là sự báo trả của Ðức Chúa Trời. Chính Ngài sẽ đến và cứu các ngươi!"));
visuperobject.biblemap.put("b23_35_5", new visuperobject(23,35,5,"Bấy giờ, những kẻ mù sẽ mở mắt, những kẻ điếc sẽ rỗng tai."));
visuperobject.biblemap.put("b23_35_6", new visuperobject(23,35,6,"Bấy giờ, kẻ què sẽ nhảy như con nai, lưỡi kẻ câm sẽ hát. Vì có những dòng nước trào lên trong đồng vắng, và những suối chảy ra trong nơi sa mạc."));
visuperobject.biblemap.put("b23_35_7", new visuperobject(23,35,7,"Cát nóng sẽ biến ra hồ, ruộng khô sẽ biến thành suối nước. Hang chó đồng đã ở sẽ trở nên vùng sậy và lau."));
visuperobject.biblemap.put("b23_35_8", new visuperobject(23,35,8,"Tại đó sẽ có một đường cái, và một lối gọi là đường thánh. Kẻ nào ô uế sẽ không được đi qua; song nó sẽ dành cho những người được chuộc. Ai đi trong đường đó, dầu khờ dại cũng không lầm lạc."));
visuperobject.biblemap.put("b23_35_9", new visuperobject(23,35,9,"Trên đường đó không có sư tử, không có một thú dữ nào lên; chẳng ai gặp nó ở đó. Song những kẻ được chuộc sẽ bước đi đường ấy,"));
visuperobject.biblemap.put("b23_35_10", new visuperobject(23,35,10,"những kẻ Ðức Giê-hô-va đã chuộc sẽ về, ca hát mà đến Si-ôn; sự vui vẻ vô cùng sẽ ở trên đầu họ. Họ sẽ được sự vui vẻ mừng rỡ, mà sự buồn bực than vãn sẽ trốn đi."));
visuperobject.biblemap.put("b23_36_1", new visuperobject(23,36,1,"Năm thứ mười bốn đời vua Ê-xê-chia, thì San-chê-ríp, vua A-si-ri, đến đánh các thành bền vững của Giu-đa và chiếm lấy."));
visuperobject.biblemap.put("b23_36_2", new visuperobject(23,36,2,"Ðoạn, vua A-si-ri sai Ráp-sa-kê với đạo binh lớn từ La-ki đến Giê-ru-sa-lem, nghịch cùng vua Ê-xê-chia; Ráp-sa-kê đứng tại cống ao trên, nơi đường cái ruộng thợ nện."));
visuperobject.biblemap.put("b23_36_3", new visuperobject(23,36,3,"Bấy giờ, Ê-li-a-kim, con trai của Hinh-kia, làm chức cung giám, đi với thơ ký Sép-na, và sử quan Giô-a, con trai của A-sáp, mà đến cùng người."));
visuperobject.biblemap.put("b23_36_4", new visuperobject(23,36,4,"Ráp-sa-kê nói với ba người rằng: Hãy tâu cùng vua Ê-xê-chia rằng: Ðấng đại vương, tức là vua A-si-ri, phán như vầy: Sự trông cậy mà ngươi nương dựa là gì?"));
visuperobject.biblemap.put("b23_36_5", new visuperobject(23,36,5,"Ta nói rằng: mưu và sức ngươi dùng mà giao chiến chỉ là lời vô ích mà thôi! Vậy ngươi cậy ai mà dám làm phản ta?"));
visuperobject.biblemap.put("b23_36_6", new visuperobject(23,36,6,"Nầy, ngươi cậy Ê-díp-tô, ngươi lấy cây sậy đã gãy ấy làm gậy, là cây mà ai dựa vào thì nó đâm phủng tay. Pha-ra-ôn, vua Ê-díp-tô, đối với kẻ trông cậy mình cũng là thể ấy."));
visuperobject.biblemap.put("b23_36_7", new visuperobject(23,36,7,"Có lẽ các ngươi bảo ta rằng: Chúng ta cậy Giê-hô-va Ðức Chúa Trời chúng ta; nhưng ấy chẳng phải là Ðấng mà Ê-xê-chia đã bỏ các nơi cao và các bàn thờ Ngài, khi truyền cho Giu-đa và Giê-ru-sa-lem rằng: Các ngươi khá thờ lạy trước bàn thờ nầy hay sao?"));
visuperobject.biblemap.put("b23_36_8", new visuperobject(23,36,8,"Vậy bây giờ, hãy đưa con tin cho chủ ta, là vua A-si-ri. Nếu ngươi tìm được đủ lính kỵ, thì ta sẽ cấp cho hai ngàn con ngựa."));
visuperobject.biblemap.put("b23_36_9", new visuperobject(23,36,9,"Nhưng tài nào ngươi sẽ đuổi được lấy một quan tướng rất kém trong các tôi tớ chủ ta hay sao? Mà ngươi cậy Ê-díp-tô để tìm xe binh và lính kỵ!"));
visuperobject.biblemap.put("b23_36_10", new visuperobject(23,36,10,"Vả lại, há không có mạng của Ðức Giê-hô-va mà ta lên đánh xứ nầy để diệt nó sao? Ðức Giê-hô-va đã phán cùng ta rằng: Hãy lên đánh xứ nầy và diệt đi!"));
visuperobject.biblemap.put("b23_36_11", new visuperobject(23,36,11,"Ê-li-a-kim, cùng Sép-na và Giô-a bảo Ráp-sa-kê rằng: Xin nói với tôi tớ ông bằng tiếng A-ram, vì chúng tôi hiểu tiếng ấy. Song đừng lấy tiếng Giu-đa nói cùng chúng tôi, cho dân nầy đương ở trên tường thành nó nghe."));
visuperobject.biblemap.put("b23_36_12", new visuperobject(23,36,12,"Ráp-sa-kê nói: Chủ ta sai ta truyền mấy lời đó, nào những cho chủ ngươi và cho ngươi ư? Há chẳng cũng truyền cho những người ngồi trên tường thành, là kẻ sắp phải cùng với các ngươi ăn phân uống tiểu mình ư?"));
visuperobject.biblemap.put("b23_36_13", new visuperobject(23,36,13,"Ðoạn, Ráp-sa-kê đứng dậy, dùng tiếng Giu-đa mà nói to lên rằng: Hãy nghe lời phán của đại vương, là vua A-si-ri."));
visuperobject.biblemap.put("b23_36_14", new visuperobject(23,36,14,"Vua phán như vầy: Các ngươi chớ để vua Ê-xê-chia lừa dối mình; vì người chẳng có thể cứu các ngươi."));
visuperobject.biblemap.put("b23_36_15", new visuperobject(23,36,15,"Cũng chớ để vua Ê-xê-chia khuyên các ngươi trông cậy Ðức Giê-hô-va mà rằng: Ðức Giê-hô-va chắc sẽ cứu chúng ta, và thành nầy sẽ chẳng phó vào thay vua A-si-ri đâu!"));
visuperobject.biblemap.put("b23_36_16", new visuperobject(23,36,16,"Chớ nghe vua Ê-xê-chia; vì vua A-si-ri phán như vầy: Hãy hòa với ta, và ra hàng đi, thì các ngươi ai nấy sẽ được ăn trái nho trái vả của mình, uống nước giếng mình,"));
visuperobject.biblemap.put("b23_36_17", new visuperobject(23,36,17,"cho tới chừng ta đến, đặng đem các ngươi vào một xứ như xứ các ngươi, tức là xứ có mạch nha và rượu mới, lúa mì và nho."));
visuperobject.biblemap.put("b23_36_18", new visuperobject(23,36,18,"Vậy, hãy giữ mình kẻo vua Ê-xê-chia dỗ dành các ngươi mà rằng: Ðức Giê-hô-va sẽ giải cứu chúng ta! Vậy chớ các thần của các nước khác đã giải cứu xứ họ khỏi tay vua A-si-ri được chăng?"));
visuperobject.biblemap.put("b23_36_19", new visuperobject(23,36,19,"Nào các thần của Ha-mát và của Aït-bát đâu tá? Nào các thầy của Sê-phạt-va-im đâu tá? Các thần ấy đã cứu Sa-ma-ri ra khỏi tay ta chưa?"));
visuperobject.biblemap.put("b23_36_20", new visuperobject(23,36,20,"Trong những thầy của các xứ đó, có thầy nào là thầy đã cứu xứ mình ra khỏi tay ta? Mà Ðức Giê-hô-va có thể cứu Giê-ru-sa-lem khỏi tay ta được!"));
visuperobject.biblemap.put("b23_36_21", new visuperobject(23,36,21,"Chúng làm thinh, chẳng đáp lại một lời, vì vua có truyền lịnh rằng: Các ngươi đừng đáp lại."));
visuperobject.biblemap.put("b23_36_22", new visuperobject(23,36,22,"Ê-li-a-kim, con trai của Hinh-kia, làm chức cung giám, cùng thơ ký Sép-na, sử quan Giô-a, con trai của A-sáp, xé áo mình ra, trở về chầu vua Ê-xê-chia, và thuật lại những lời của Ráp-sa-kê."));
visuperobject.biblemap.put("b23_37_1", new visuperobject(23,37,1,"Nghe tin đó, vua Ê-xê-chia liền xé áo mình, quấn bao gai, vào đền Ðức Giê-hô-va."));
visuperobject.biblemap.put("b23_37_2", new visuperobject(23,37,2,"Ðoạn, sai quan cung giám Ê-li-a-kim, thơ ký Sép-na, và các trưởng lão trong hàng thầy tế lễ, đều quấn bao gai, đến cùng đấng tiên tri Ê-sai, con trai của A-mốt,"));
visuperobject.biblemap.put("b23_37_3", new visuperobject(23,37,3,"nói với người rằng: Vua Ê-xê-chia phán như vầy: Ngày nay là ngày hoạn nạn, quở phạt, và hổ nhuốc; vì con đã đến kỳ đẻ rồi, nhưng không có sức mà đẻ ra."));
visuperobject.biblemap.put("b23_37_4", new visuperobject(23,37,4,"Có lẽ Giê-hô-va Ðức Chúa Trời ngươi sẽ nghe những lời của Ráp-sa-kê mà chủ nó là vua A-si-ri đã sai đến để đố thách Ðức Chúa Trời hằng sống; và có lẽ Ngài sẽ quở phạt nó theo như những lời mà Giê-hô-va Ðức Chúa Trời ngươi đã nghe: vậy xin hãy vì những người còn lại đây mà cầu nguyện!"));
visuperobject.biblemap.put("b23_37_5", new visuperobject(23,37,5,"Các tôi tớ của vua Ê-xê-chia bèn đến cùng Ê-sai."));
visuperobject.biblemap.put("b23_37_6", new visuperobject(23,37,6,"Ê-sai bảo rằng: Nầy là lời các ngươi sẽ tâu lại cùng chủ mình: Ðức Giê-hô-va có phán như vầy: Chớ sợ về những lời ngươi đã nghe, là lời của tôi tớ vua A-si-ri dùng mà nói phạm ta."));
visuperobject.biblemap.put("b23_37_7", new visuperobject(23,37,7,"Nầy ta đặt thần linh trong nó, rồi nó sẽ nghe tin mà trở về xứ mình; tại đó, ta sẽ làm cho nó ngã dưới gươm."));
visuperobject.biblemap.put("b23_37_8", new visuperobject(23,37,8,"Vả, khi Ráp-sa-kê nghe chủ mình là vua A-si-ri đã đi khỏi La-ki, bèn trở về chầu người, gặp người đương đánh thành Líp-na."));
visuperobject.biblemap.put("b23_37_9", new visuperobject(23,37,9,"Bấy giờ vua A-si-ri có nghe tin báo về Tiệt-ha-ca là vua Ê-thi-ô-bi rằng: Người kéo ra để đánh cùng vua; bèn sai các sứ giả đến cùng Ê-xê-chia, và dặn rằng:"));
visuperobject.biblemap.put("b23_37_10", new visuperobject(23,37,10,"Các ngươi hãy nói cùng Ê-xê-chia, vua Giu-đa, rằng: Vua chớ để cho mình bị lừa bởi Ðức Chúa Trời mà mình tin cậy, nói rằng: Giê-ru-sa-lem sẽ chẳng phó vào tay vua A-si-ri đâu."));
visuperobject.biblemap.put("b23_37_11", new visuperobject(23,37,11,"Nầy, vua hẳn có nghe những sẽ các vua A-si-ri đã làm ra ở các nước. Các nước ấy đã bị hủy diệt cả rồi; còn vua, vua sẽ được cứu khỏi ư!"));
visuperobject.biblemap.put("b23_37_12", new visuperobject(23,37,12,"Các thần của các nước mà tổ tiên chúng ta đã diệt, tức là các thần của Gô-xan, của Cha-ran, của Rết-sép và của con cái Ê-đen ở Tê-la-sa, có cứu được họ không?"));
visuperobject.biblemap.put("b23_37_13", new visuperobject(23,37,13,"Chớ nào vua của Ha-mát, vua của Aït-bát, các vua của thành Sê-phạt va-im, thành Hê-ma và thành Y-va ở đâu?"));
visuperobject.biblemap.put("b23_37_14", new visuperobject(23,37,14,"Ê-xê-chia đã nhận thơ tại tay sứ giả và đọc rồi, thì lên nhà Ðức Giê-hô-va, mở thơ ra trước mặt Ðức Giê-hô-va,"));
visuperobject.biblemap.put("b23_37_15", new visuperobject(23,37,15,"cầu nguyện Ðức Giê-hô-va rằng:"));
visuperobject.biblemap.put("b23_37_16", new visuperobject(23,37,16,"Hỡi Ðức Giê-hô-va vạn quân, Ðức Chúa Trời của Y-sơ-ra-ên, là Ðấng ngự trên các chê-ru-bin, chỉ một mình Ngài là Ðức Chúa Trời của mọi nước trên đất, Ngài đã dựng nên trời và đất."));
visuperobject.biblemap.put("b23_37_17", new visuperobject(23,37,17,"Hỡi Ðức Giê-hô-va, xin nghiêng tai mà nghe! Hỡi Ðức Giê-hô-va, xin mở mắt mà xem! Xin nghe mọi lời mà San-chê-ríp đã khiến nói cùng tôi để đố thách Ðức Chúa Trời hằng sống!"));
visuperobject.biblemap.put("b23_37_18", new visuperobject(23,37,18,"Hỡi Ðức Giê-hô-va, thật rằng các vua nước A-si-ri đã hủy diệt các nước và phá hại đất đai,"));
visuperobject.biblemap.put("b23_37_19", new visuperobject(23,37,19,"ném các thần trong lửa. Nhưng ấy chẳng phải là các thần, bèn là việc của tay người ta, bằng gỗ và bằng đá, cho nên họ đã hủy diệt đi được."));
visuperobject.biblemap.put("b23_37_20", new visuperobject(23,37,20,"Hỡi Giê-hô-va Ðức Chúa Trời chúng tôi, vậy, bây giờ, xin Ngài cứu chúng tôi khỏi tay San-chê-ríp, hầu cho mọi nước trên đất đều biết chỉ một mình Ngài là Ðức Giê-hô-va!"));
visuperobject.biblemap.put("b23_37_21", new visuperobject(23,37,21,"Ê-sai, con trai của A-mốt, bèn khiến người tâu cùng vua Ê-xê-chia rằng: Giê-hô-va Ðức Chúa Trời của Y-sơ-ra-ên phán như vầy: Vì ngươi đã cầu xin ta nghịch cùng San-chê-ríp, vua A-si-ri;"));
visuperobject.biblemap.put("b23_37_22", new visuperobject(23,37,22,"nầy là lời Ðức Giê-hô-va đã phán về nó: Gái đồng trinh Si-ôn khinh dể ngươi, nhạo cười ngươi; gái Giê-ru-sa-lem lắc đầu sau ngươi!"));
visuperobject.biblemap.put("b23_37_23", new visuperobject(23,37,23,"Ngươi đã đố thách và nói phạm đến ai? Ngươi đã cất tiếng lên và ngước mắt lên cao nghịch cùng ai? Ấy là nghịch cùng Ðấng Thánh của Y-sơ-ra-ên."));
visuperobject.biblemap.put("b23_37_24", new visuperobject(23,37,24,"Ngươi đã dùng các tôi tớ mình mà đố thách Chúa, nói rằng: Ta đem muôn vàn cỗ xe lên trên chót núi, là nơi xa thẳm của Li-ban; ta sẽ đốn những cây bách rất cao, cây tùng rất xinh; vào đến trên đỉnh rất cao, trong rừng của ruộng tốt."));
visuperobject.biblemap.put("b23_37_25", new visuperobject(23,37,25,"Ta đã đào đất và uống nước; dùng bàn chơn làm cho cạn mọi sông Ê-díp-tô."));
visuperobject.biblemap.put("b23_37_26", new visuperobject(23,37,26,"Ngươi há chẳng nghe rằng ta đã làm sự đó từ lâu, đã định từ đời xưa hay sao? Hiện nay ta khiến xảy ra, hầu cho ngươi phá các thành bền vững nên gò đống đổ nát."));
visuperobject.biblemap.put("b23_37_27", new visuperobject(23,37,27,"Những dân cư các thành ấy đã kém sức, khiếp sợ, xấu hổ, trở nên như rau ngoài đồng và cỏ xanh, như cỏ trên mái nhà, như lúa mì chưa trồi đọt mà đã héo."));
visuperobject.biblemap.put("b23_37_28", new visuperobject(23,37,28,"Ta xem thấy ngươi khi ngồi, khi ra, khi vào và khi náo loạn nghịch cùng ta."));
visuperobject.biblemap.put("b23_37_29", new visuperobject(23,37,29,"Vì ngươi náo loạn nghịch cùng ta, lời xấc xược của ngươi đã thấu đến tai ta, nên ta tra cái khoen nơi lỗ mũi ngươi, và cái khớp nơi miệng ngươi, khiến ngươi theo con đường mình đã noi đến mà trở về."));
visuperobject.biblemap.put("b23_37_30", new visuperobject(23,37,30,"Hỡi Ê-xê-chia, điều nầy sẽ là dấu: năm nay sẽ ăn hoa lợi chính ruộng tự sanh ra, sang năm còn ăn lúa tự nhiên mọc lên không gieo giống. Nhưng, đến năm thứ ba, hãy gieo và gặt, hãy trồng vườn nho và ăn trái."));
visuperobject.biblemap.put("b23_37_31", new visuperobject(23,37,31,"Trong nhà Giu-đa kẻ nào tránh khỏi nạn và còn sót lại sẽ châm rễ mới ở dưới và ra trái ở trên."));
visuperobject.biblemap.put("b23_37_32", new visuperobject(23,37,32,"Vì sẽ có dân sót ra từ Giê-ru-sa-lem, và mấy kẻ trốn khỏi nạn ra từ núi Si-ôn: lòng sốt sắng của Ðức Giê-hô-va vạn quân sẽ làm điều đó."));
visuperobject.biblemap.put("b23_37_33", new visuperobject(23,37,33,"Vậy nên, Ðức Giê-hô-va phán về vua A-si-ri như vầy: Nó sẽ không vào thành nầy, chẳng bắn vào một mũi trên, cũng chẳng dùng một cái thuẫn mà nghịch với, và chẳng đắp lũy mà cự lại."));
visuperobject.biblemap.put("b23_37_34", new visuperobject(23,37,34,"Nó sẽ theo con đường mình đã noi đến mà trở về, không vào thành nầy đâu, Ðức Giê-hô-va phán vậy."));
visuperobject.biblemap.put("b23_37_35", new visuperobject(23,37,35,"Ta sẽ binh vực thành nầy, để giải cứu nó, vì cớ ta và vì cớ Ða-vít là tôi tớ ta."));
visuperobject.biblemap.put("b23_37_36", new visuperobject(23,37,36,"Bấy giờ, một thiên sứ của Ðức Giê-hô-va vào trại quân của người A-si-ri, và giết mười tám vạn năm ngàn người. Sáng hôm sau, người ta dậy sớm, thấy rặt những thây chết."));
visuperobject.biblemap.put("b23_37_37", new visuperobject(23,37,37,"San-chê-ríp, vua A-si-ri, bèn đi, trở về ở tại Ni-ni-ve."));
visuperobject.biblemap.put("b23_37_38", new visuperobject(23,37,38,"Một ngày kia người đương quì lạy trong đền Nít-róc, thần của mình, thì con trai người là A-tra-mê-léc và Sa-rết-se lấy gươm giết người. Ðoạn, hai người đó trốn trong xứ A-ra-rát. Con trai người là Ê-sạt-ha-đôn trị vì thay người."));
visuperobject.biblemap.put("b23_38_1", new visuperobject(23,38,1,"Trong lúc đó, Ê-xê-chia lâm bịnh hầu chết; đấng tiên tri Ê-sai, con trai A-mốt, đến chầu vua, và tâu rằng: Ðức Giê-hô-va phán như vầy: Hãy sắp đặt nhà ngươi, vì ngươi hầu chết, không sống được nữa."));
visuperobject.biblemap.put("b23_38_2", new visuperobject(23,38,2,"Ê-xê-chia bèn xây mặt vào tường, cầu nguyện Ðức Giê-hô-va rằng:"));
visuperobject.biblemap.put("b23_38_3", new visuperobject(23,38,3,"Hỡi Ðức Giê-hô-va, xin nhớ rằng tôi đã lấy lòng chơn thật trọn lành đi trước mặt Chúa, và đã làm điều lành trước mặt Ngài! Ðoạn, Ê-xê-chia khóc dầm dề."));
visuperobject.biblemap.put("b23_38_4", new visuperobject(23,38,4,"Bấy giờ, có lời Ðức Giê-hô-va phán cho Ê-sai rằng:"));
visuperobject.biblemap.put("b23_38_5", new visuperobject(23,38,5,"Hãy trở lại nói cùng Ê-xê-chia rằng: Giê-hô-va Ðức Chúa Trời của Ða-vít, tổ ngươi, phán như vầy: Ta đã nghe lời cầu nguyện của ngươi, đã thấy nước mắt ngươi. Nầy, ta sẽ thêm cho ngươi mười lăm tuổi."));
visuperobject.biblemap.put("b23_38_6", new visuperobject(23,38,6,"Ta sẽ giải cứu ngươi cùng thành nầy khỏi tay vua A-si-ri, và ta sẽ binh vực thành nầy."));
visuperobject.biblemap.put("b23_38_7", new visuperobject(23,38,7,"Nầy là dấu Ðức Giê-hô-va ban cho ngươi để nghiệm biết Ðức Giê-hô-va sẽ làm hoàn thành lời mình đã phán:"));
visuperobject.biblemap.put("b23_38_8", new visuperobject(23,38,8,"trên bàn trắc ảnh A-cha, ta sẽ làm cho bóng mặt trời đương đi và đã đi quá, lui lại mười độ. Vậy thì trên bàn trắc ảnh, bóng mặt trời đã lui lại mười độ mà nó đã đi quá rồi."));
visuperobject.biblemap.put("b23_38_9", new visuperobject(23,38,9,"Nầy là lời chép của Ê-xê-chia, vua Giu-đa, khi đã lâm bịnh và được lành bịnh:"));
visuperobject.biblemap.put("b23_38_10", new visuperobject(23,38,10,"Tôi từng nói: Vậy thì, đương khi giữa trưa của đời tôi, tôi hầu vào cửa âm phủ, còn mấy năm thừa bị cất mất!"));
visuperobject.biblemap.put("b23_38_11", new visuperobject(23,38,11,"Tôi từng nói: Tôi sẽ chẳng thấy Ðức Giê-hô-va nữa, là Ðức Giê-hô-va ở trên đất người sống. Tôi chẳng còn thấy loài người ở cùng dân cư thế gian."));
visuperobject.biblemap.put("b23_38_12", new visuperobject(23,38,12,"Nhà của tôi bị triệt đi, dời xa khỏi tôi như cái trại của người chăn chiên. Tôi cuốn đời tôi như thợ dệt cuốn vải nó; Chúa sẽ cắt tôi khỏi khung cửi; từ sáng đến tối, Chúa sẽ dứt mạng sống tôi!"));
visuperobject.biblemap.put("b23_38_13", new visuperobject(23,38,13,"Tôi làm thinh cho đến sáng mai: như Ngài đã xé cả xương tôi như sư tử vậy. Từ sáng đến tối, Chúa sẽ dứt mạng sống tôi!"));
visuperobject.biblemap.put("b23_38_14", new visuperobject(23,38,14,"Tôi rầm rì như chim hạc chim yến; gù như chim bò câu; mắt tôi nhìn trên cao nên nỗi mỏi. Hỡi Ðức Giê-hô-va, tôi đương cơn sầu khổ, xin Ngài bảo lãnh tôi."));
visuperobject.biblemap.put("b23_38_15", new visuperobject(23,38,15,"Tôi còn nói gì? Ngài đã phán cùng tôi thì Ngài cũng đã làm thành việc nầy. Trọn đời tôi sẽ ăn ở cách khiêm nhường vì cớ trong lòng cay đắng."));
visuperobject.biblemap.put("b23_38_16", new visuperobject(23,38,16,"Hỡi Chúa, người ta được sống là nhờ đó; thần linh tôi được sống cũng nhờ đó! Xin Chúa chữa lành tôi, khiến tôi được sống."));
visuperobject.biblemap.put("b23_38_17", new visuperobject(23,38,17,"Nầy, sự rất cay đắng của tôi đã trở nên sự bình an! Chúa đã yêu thương linh hồn tôi, đem nó ra khỏi hầm hư nát; vì Chúa đã ném mọi tội lỗi tôi ra sau lưng Ngài."));
visuperobject.biblemap.put("b23_38_18", new visuperobject(23,38,18,"Nơi âm phủ chẳng ngợi khen Ngài được, và sự chết chẳng tôn vinh Ngài được; những kẻ đã xuống mồ rồi chẳng còn trông cậy sự thành thật của Ngài nữa."));
visuperobject.biblemap.put("b23_38_19", new visuperobject(23,38,19,"Duy kẻ sống, kẻ sống mới hay tôn vinh Ngài, như tôi làm ngày nay; kẻ làm cha sẽ cho con cái mình biết sự thành thật của Ngài."));
visuperobject.biblemap.put("b23_38_20", new visuperobject(23,38,20,"Ðức Giê-hô-va là Ðấng cứu tôi! Trọn đời chúng tôi sẽ đờn và hát trong nhà Ðức Giê-hô-va!"));
visuperobject.biblemap.put("b23_38_21", new visuperobject(23,38,21,"Vả, Ê-sai có nói rằng: Hãy lấy một cái bánh trái vả đắp trên mụt ung độc, vua sẽ được lành."));
visuperobject.biblemap.put("b23_38_22", new visuperobject(23,38,22,"Ê-xê-chia đáp rằng: Có dấu nào cho ta biết rằng ta còn được lên nhà Ðức Giê-hô-va nữa chăng?"));
visuperobject.biblemap.put("b23_39_1", new visuperobject(23,39,1,"Trong lúc đó, vua Ba-by-lôn, là Mê-rô-đác-Ba-la-đan, con vua Ba-la-đan, sai đưa thơ và lễ vật dâng Ê-xê-chia; vì nghe người đau và được lành."));
visuperobject.biblemap.put("b23_39_2", new visuperobject(23,39,2,"Ê-xê-chia vui mừng vì các sứ thần, thì cho xem trong cung điện, nơi để những của quí, như bạc vàng, thuốc thơm, dầu báu, cùng kho khí giới, và mọi đồ trong phủ khố mình. Phàm vật chi trong cung và trong cả nước mình, chẳng có tí gì là Ê-xê-chia chẳng cho các sứ thần xem đến."));
visuperobject.biblemap.put("b23_39_3", new visuperobject(23,39,3,"Ðấng tiên tri Ê-sai bèn đến chầu vua Ê-xê-chia, và hỏi rằng: Các người đó nói chi, và từ đâu đến thăm vua? Ê-xê-chia đáp rằng: Họ đến từ phương xa, tức là từ nước Ba-by-lôn."));
visuperobject.biblemap.put("b23_39_4", new visuperobject(23,39,4,"Ê-sai tiếp rằng: Họ đã xem gì trong cung vua? Ê-xê-chia đáp rằng: Họ đã xem hết thảy mọi vật ở trong cung ta; chẳng có vật gì trong phủ khố ta mà ta chẳng chỉ cho họ."));
visuperobject.biblemap.put("b23_39_5", new visuperobject(23,39,5,"Ê-sai bèn tâu cùng Ê-xê-chia rằng: Xin nghe lời Ðức Giê-hô-va vạn quân phán:"));
visuperobject.biblemap.put("b23_39_6", new visuperobject(23,39,6,"Nầy, ngày đến, là khi mọi vật trong cung ngươi, mọi vật mà tổ tiên ngươi đã chứa trong kho cho đến ngày nay, sẽ dời sang Ba-by-lôn, chẳng còn tí nào hết, Ðức Giê-hô-va phán vậy."));
visuperobject.biblemap.put("b23_39_7", new visuperobject(23,39,7,"Các con trai ngươi, con cháu ra từ ngươi, sanh bởi ngươi, cũng có kẻ sẽ bị bắt để làm quan hoạn trong cung vua Ba-by-lôn."));
visuperobject.biblemap.put("b23_39_8", new visuperobject(23,39,8,"Ê-xê-chia đáp lại cùng Ê-sai rằng: Lời của Ðức Giê-hô-va mà ông vừa thuật cho ta nghe đó là tốt lắm! Lại rằng: Vì trong ngày ta còn sống, sẽ được bình yên và lẽ thật."));
visuperobject.biblemap.put("b23_40_1", new visuperobject(23,40,1,"Ðức Chúa Trời của các ngươi phán rằng: Hãy yên ủi, hãy yên ủi dân ta."));
visuperobject.biblemap.put("b23_40_2", new visuperobject(23,40,2,"Hãy nói cho thấu lòng Giê-ru-sa-lem, và rao rằng sự tranh chiến của nó đã trọn; tội lỗi nó đã được tha; nó đã chịu gấp hai từ tay Ðức Giê-hô-va vì các tội mình."));
visuperobject.biblemap.put("b23_40_3", new visuperobject(23,40,3,"Có tiếng kêu rằng: Hãy mở đường trong đồng vắng cho Ðức Giê-hô-va; hãy ban bằng đường cái trong đất hoang cho Ðức Chúa Trời chúng ta!"));
visuperobject.biblemap.put("b23_40_4", new visuperobject(23,40,4,"Mọi nơi sủng thấp sẽ được cao lên; mọi núi mọi gò sẽ bị hạ xuống; các nơi gập ghềnh sẽ làm cho bằng; các nơi dốc hãm sẽ làm thành đồng nội."));
visuperobject.biblemap.put("b23_40_5", new visuperobject(23,40,5,"Bấy giờ sự vinh hiển của Ðức Giê-hô-va sẽ hiện ra, và mọi xác thịt đều sẽ xem thấy; vì miệng Ðức Giê-hô-va đã phán vậy."));
visuperobject.biblemap.put("b23_40_6", new visuperobject(23,40,6,"Có tiếng kẻ rằng: Hãy kêu! Và có đáp rằng: Tôi sẽ kêu thế nào? Mọi xác thịt giống như cỏ, nhan sắc nó như hoa ngoài đồng."));
visuperobject.biblemap.put("b23_40_7", new visuperobject(23,40,7,"Cỏ khô, hoa rụng, vì hơi Ðức Giê-hô-va thổi qua. Phải, dân nầy khác nào cỏ ấy:"));
visuperobject.biblemap.put("b23_40_8", new visuperobject(23,40,8,"cỏ khô, hoa rụng; nhưng lời của Ðức Chúa Trời chúng ta còn mãi đời đời!"));
visuperobject.biblemap.put("b23_40_9", new visuperobject(23,40,9,"Ai rao tin lành cho Si-ôn, hãy lên trên núi cao! Ai rao tin lành cho Giê-ru-sa-lem, hãy ra sức cất tiếng lên! Hãy cất tiếng lên, đừng sợ! Hãy bảo các thành của Giu-đa rằng: Ðức Chúa Trời của các ngươi đây!"));
visuperobject.biblemap.put("b23_40_10", new visuperobject(23,40,10,"Nầy, Chúa Giê-hô-va sẽ lấy quyền năng mà đến; Ngài dùng cánh tay mình và cai trị. Nầy, sự ban thưởng Ngài ở nơi Ngài, sự báo trả Ngài ở trước mặt Ngài."));
visuperobject.biblemap.put("b23_40_11", new visuperobject(23,40,11,"Ngài sẽ chăn bầy mình như người chăn chiên; thâu các con chiên con vào cánh tay mình và ẳm vào lòng; từ từ dắt các chiên cái đương cho bú."));
visuperobject.biblemap.put("b23_40_12", new visuperobject(23,40,12,"Ai đã lấy lòng bàn tay mà lường biển, lấy gang tay mà đo các từng trời? Ai đã lấy đấu mà đong bụi đất? Ai đã dùng cân mà cân núi, và dùng thăng bằng mà cân gò?"));
visuperobject.biblemap.put("b23_40_13", new visuperobject(23,40,13,"Ai lường được Thần của Ðức Chúa Trời, và làm mưu sĩ Ngài, đặng dạy Ngài điều gì?"));
visuperobject.biblemap.put("b23_40_14", new visuperobject(23,40,14,"Nào Ngài đã bàn luận với ai? Ai đã dạy khôn cho Ngài và dạy Ngài đường công nghĩa? Nào ai đã đem sự thông biết dạy Ngài và chỉ cho Ngài lối khôn ngoan?"));
visuperobject.biblemap.put("b23_40_15", new visuperobject(23,40,15,"Kìa, các dân tộc khác nào một giọt nước nhỏ trong thùng, và kể như là một mảy bụi rơi trên cân; nầy, Ngài giở các cù lao lên như đồi vật nhỏ."));
visuperobject.biblemap.put("b23_40_16", new visuperobject(23,40,16,"Rừng Li-ban chẳng đủ đốt lửa, những thú vật ở đó chẳng đủ làm của lễ thiêu."));
visuperobject.biblemap.put("b23_40_17", new visuperobject(23,40,17,"Mọi dân tộc ở trước mặt Ðức Giê-hô-va thảy đều như không, Ngài xem như trống không và hư vô vậy."));
visuperobject.biblemap.put("b23_40_18", new visuperobject(23,40,18,"Vậy thì các ngươi có thể vì Ðức Chúa Trời với ai, lấy ảnh tượng nào mà so sánh với Ngài được?"));
visuperobject.biblemap.put("b23_40_19", new visuperobject(23,40,19,"Khi người thợ đúc tượng, thì thợ bạc lấy vàng mà bọc, và đúc cho những cái dây chuyền bằng bạc."));
visuperobject.biblemap.put("b23_40_20", new visuperobject(23,40,20,"Kẻ nghèo không dâng nổi vật đó, thì chọn gỗ không mục được, và tìn thợ khéo đặng trổ một tượng không lay đổ."));
visuperobject.biblemap.put("b23_40_21", new visuperobject(23,40,21,"Các ngươi không biết sao? Vậy thì các ngươi không nghe sao? Người ta há chưa dạy cho các ngươi từ lúc ban đầu? Các ngươi há chẳng hiểu từ khi dựng nền đất?"));
visuperobject.biblemap.put("b23_40_22", new visuperobject(23,40,22,"Ấy là Ðấng ngự trên vòng trái đất nầy, các dân cư trên đất như cào cào vậy. Chính Ngài là Ðấng giương các từng trời ra như cái màn, và giương ra như trại để ở."));
visuperobject.biblemap.put("b23_40_23", new visuperobject(23,40,23,"Chính Ngài là Ðấng làm tiêu diệt các quan trưởng và làm cho các quan xét trên đất ra hư không."));
visuperobject.biblemap.put("b23_40_24", new visuperobject(23,40,24,"Họ mới trồng, mới gieo, cây mới châm rễ trong đất, hơi Ðức Giê-hô-va thổi qua, thì khô héo; nầy, gió lốc đùa hết đi như rơm rác."));
visuperobject.biblemap.put("b23_40_25", new visuperobject(23,40,25,"Ðấng Thánh phán rằng: Vậy thì các ngươi sánh ta cùng ai? Ai sẽ bằng ta?"));
visuperobject.biblemap.put("b23_40_26", new visuperobject(23,40,26,"Hãy ngước mắt lên cao mà xem: Ai đã tạo những vật nầy? Ấy là Ðấng khiến các cơ binh ra theo số nó, và đặt tên hết thảy; chẳng một vật nào thiếu, vì sức mạnh Ngài lớn lắm, và quyền năng Ngài rất cao."));
visuperobject.biblemap.put("b23_40_27", new visuperobject(23,40,27,"Vậy thì, hỡi Gia-cốp, sao ngươi nói, hãy Y-sơ-ra-ên, sao ngươi nói như vầy: Ðường tôi đã khuất khỏi Ðức Giê-hô-va, lý đoán tôi qua khỏi Ðức Chúa Trời tôi?"));
visuperobject.biblemap.put("b23_40_28", new visuperobject(23,40,28,"Ngươi không biết sao, không nghe sao? Ðức Chúa Trời hằng sống, là Ðức Giê-hô-va, là Ðấng đã dựng nên các đầu cùng đất, chẳng mỏi chẳng mệt; sự khôn ngoan Ngài không thể dò."));
visuperobject.biblemap.put("b23_40_29", new visuperobject(23,40,29,"Ngài ban sức mạnh cho kẻ nhọc nhằn, thêm lực lượng cho kẻ chẳng có sức."));
visuperobject.biblemap.put("b23_40_30", new visuperobject(23,40,30,"Những kẻ trai trẻ cũng phải mòn mỏi mệt nhọc, người trai tráng cũng phải vấp ngã."));
visuperobject.biblemap.put("b23_40_31", new visuperobject(23,40,31,"Nhưng ai trông đợi Ðức Giê-hô-va thì chắc được sức mới, cất cánh bay cao như chim ưng; chạy mà không mệt nhọc, đi mà không mòn mỏi."));
visuperobject.biblemap.put("b23_41_1", new visuperobject(23,41,1,"Hỡi các cù lao, hãy nín lặng trước mặt ta; các dân tộc hãy lấy sức mới; hãy đến gần, thì mới nói! Chúng ta hãy đến gần nhau để xét đoán!"));
visuperobject.biblemap.put("b23_41_2", new visuperobject(23,41,2,"Ai đã khiến người kia dấy lên từ phương đông, lấy sự công bình gọi người đến kề chơn mình? Ngài phó các dân tộc trước mặt Người, khiến người cai trị các vua, phó họ như bụi cho gươm người, giống như rác rến bị gió thổi cho cung người."));
visuperobject.biblemap.put("b23_41_3", new visuperobject(23,41,3,"Người đuổi theo họ trên con đường chưa hề đặt chơn, mà vẫn vô sự lướt dặm."));
visuperobject.biblemap.put("b23_41_4", new visuperobject(23,41,4,"Ai đã làm và thành tựu việc nầy? Ấy là chính ta, Ðức Giê-hô-va, là đầu tiên, mà cũng sẽ ở với cuối cùng."));
visuperobject.biblemap.put("b23_41_5", new visuperobject(23,41,5,"Các cù lao thấy và thất kinh; các đầu cùng đất đều run rẩy. Chúng nó đều đến gần và nhóm lại,"));
visuperobject.biblemap.put("b23_41_6", new visuperobject(23,41,6,"ai nấy giúp đỡ kẻ lân cận mình, và bảo anh em mình rằng: Hãy phấn chí!"));
visuperobject.biblemap.put("b23_41_7", new visuperobject(23,41,7,"Thợ mộc giục lòng thợ vàng; kẻ dùng búa đánh bóng giục lòng kẻ đánh đe; luận về việc hàn rằng: Hàn vẫn tốt; đoạn lấy đinh đóng nó cho khỏi lung lay."));
visuperobject.biblemap.put("b23_41_8", new visuperobject(23,41,8,"Nhưng, hỡi Y-sơ-ra-ên, ngươi là tôi tớ ta, còn ngươi, Gia-cốp, là kẻ ta đã chọn, dòng giống của Áp-ra-ham, bạn ta;"));
visuperobject.biblemap.put("b23_41_9", new visuperobject(23,41,9,"ta đã cầm lấy ngươi từ đầu cùng đất, gọi ngươi từ các góc đất, mà bảo ngươi rằng: Ngươi là tôi tớ ta; ta đã lựa ngươi, chưa từng bỏ ngươi."));
visuperobject.biblemap.put("b23_41_10", new visuperobject(23,41,10,"Ðừng sợ, vì ta ở với ngươi; chớ kinh khiếp, vì ta là Ðức Chúa Trời ngươi! Ta sẽ bổ sức cho ngươi; phải, ta sẽ giúp đỡ ngươi, lấy tay hữu công bình ta mà nâng đỡ ngươi."));
visuperobject.biblemap.put("b23_41_11", new visuperobject(23,41,11,"Nầy, những kẻ nổi giận cùng ngươi sẽ hổ thẹn nhuốc nha. Những kẻ dấy lên nghịch cùng ngươi sẽ ra hư không và chết mất."));
visuperobject.biblemap.put("b23_41_12", new visuperobject(23,41,12,"Những kẻ công kích ngươi, ngươi sẽ tìm nó mà chẳng thấy; những kẻ giao chiến cùng ngươi sẽ bị diệt và thành không."));
visuperobject.biblemap.put("b23_41_13", new visuperobject(23,41,13,"Vì ta, là Giê-hô-va Ðức Chúa Trời ngươi, sẽ nắm tay hữu ngươi, và phán cùng ngươi rằng: Ðừng sợ, ta sẽ giúp đỡ ngươi;"));
visuperobject.biblemap.put("b23_41_14", new visuperobject(23,41,14,"hỡi sâu bọ Gia-cốp, và các ngươi của dân Y-sơ-ra-ên, đừng sợ chi hết, ta sẽ giúp ngươi, Ðức Giê-hô-va phán vậy, tức là Ðấng Thánh của Y-sơ-ra-ên, và là Ðấng chuộc ngươi."));
visuperobject.biblemap.put("b23_41_15", new visuperobject(23,41,15,"Nầy, ta sẽ lấy ngươi làm cái bừa nhọn, thật mới và có răng bén. Ngươi sẽ tán các núi và nghiền nhỏ ra, làm cho các gò nên giống như cám mịn."));
visuperobject.biblemap.put("b23_41_16", new visuperobject(23,41,16,"Ngươi sẽ dê nó, gió sẽ đùa đi, gió lốc sẽ làm tan lạc; nhưng ngươi sẽ vui mừng trong Ðức Giê-hô-va, khoe mình trong Ðấng Thánh của Y-sơ-ra-ên."));
visuperobject.biblemap.put("b23_41_17", new visuperobject(23,41,17,"Những kẻ nghèo nàn thiếu thốn tìm nước mà không được, lưỡi khô vì khát; nhưng ta, Ðức Giê-hô-va, sẽ nhậm lời họ; ta, Ðức Chúa Trời của Y-sơ-ra-ên, sẽ không lìa bỏ họ đâu."));
visuperobject.biblemap.put("b23_41_18", new visuperobject(23,41,18,"Ta sẽ khiến sông chảy ra trên đỉnh núi trọi, và suối trào lên giữa trũng. Ta sẽ biến sa mạc thành ao, và đất khô thành nguồn nước."));
visuperobject.biblemap.put("b23_41_19", new visuperobject(23,41,19,"Ta sẽ đặt trong đồng vắng những cây hương bách, cây tạo giáp, cây sim và cây dầu. Ta sẽ trồng chung nơi sa mạc những cây tùng, cây sam, cây hoàng dương,"));
visuperobject.biblemap.put("b23_41_20", new visuperobject(23,41,20,"hầu cho mọi người xem thấy và biết, ngẫm nghĩ và cùng nhau hiểu rằng tay Ðức Giê-hô-va đã làm sự đó, Ðấng Thánh của Y-sơ-ra-ên đã dựng nên sự đó."));
visuperobject.biblemap.put("b23_41_21", new visuperobject(23,41,21,"Ðức Giê-hô-va phán: Hãy trình đơn các ngươi; Vua của Gia-cốp phán: Hãy bày tỏ lẽ mạnh các ngươi."));
visuperobject.biblemap.put("b23_41_22", new visuperobject(23,41,22,"Phải, hãy thuật lại đi! Hãy rao cho chúng ta điều sẽ xảy đến! Hãy tỏ ra những điều đã có lúc trước, cho chúng ta để ý nghiệm sự cuối cùng nó là thế nào, hãy là bảo cho chúng ta biết những sự hầu đến."));
visuperobject.biblemap.put("b23_41_23", new visuperobject(23,41,23,"Hãy rao những việc sẽ xảy đến sau nầy, cho chúng ta biết các ngươi là thần, cũng hãy xuống phước hoặc xuống họa đi, hầu cho chúng ta cùng nhau xem thấy và lấy làm lạ."));
visuperobject.biblemap.put("b23_41_24", new visuperobject(23,41,24,"Nầy các ngươi chẳng ra gì, sự các ngươi làm cũng là vô ích; kẻ lựa chọn các ngươi là đáng gớm ghiếc!"));
visuperobject.biblemap.put("b23_41_25", new visuperobject(23,41,25,"Ta dấy lên một người từ phương bắc, và người đã đến; người kêu cầu danh ta từ phía mặt trời mọc, giẵm lên trên các quan trưởng như giẵm trên đất vôi, khác nào thự gốm nhồi đất sét."));
visuperobject.biblemap.put("b23_41_26", new visuperobject(23,41,26,"Ai đã tỏ ra điều đó từ lúc ban đầu cho chúng ta biết? Ai đã rao ra từ trước đặng chúng ta nói rằng, người là công bình? Nhưng chẳng ai rao hết, chẳng ai báo hết, chẳng ai từng nghe tiếng các ngươi."));
visuperobject.biblemap.put("b23_41_27", new visuperobject(23,41,27,"Ấy chính ta đã phán trước nhứt cùng Si-ôn rằng: Nầy, chúng nó đây! Ta sẽ sai một kẻ báo tin lành đến Giê-ru-sa-lem."));
visuperobject.biblemap.put("b23_41_28", new visuperobject(23,41,28,"Ta xem rồi, chẳng có ai hết; trong đám họ cũng chẳng có một người mưu sĩ, để mà khi ta hỏi họ, có thể đáp một lời."));
visuperobject.biblemap.put("b23_41_29", new visuperobject(23,41,29,"Thật, họ chỉ là hư vô hết thảy; công việc họ cũng thành không, tượng đúc của họ chẳng qua là gió và sự lộn lạo."));
visuperobject.biblemap.put("b23_42_1", new visuperobject(23,42,1,"Nầy, đầy tớ ta đây, là kẻ ta nâng đỡ; là kẻ ta chọn lựa, là kẻ mà linh hồn ta lấy làm đẹp lòng. Ta đã đặt Thần ta trên người, người sẽ tỏ ra sự công bình cho các dân ngoại."));
visuperobject.biblemap.put("b23_42_2", new visuperobject(23,42,2,"Người sẽ chẳng kêu la, chẳng lên tiếng, chẳng để ngoài đường phố nghe tiếng mình."));
visuperobject.biblemap.put("b23_42_3", new visuperobject(23,42,3,"Người sẽ chẳng bẻ cây sậy đã giập, và chẳng dụt tim đèn còn hơi cháy. Người sẽ lấy lẽ thật mà tỏ ra sự công bình."));
visuperobject.biblemap.put("b23_42_4", new visuperobject(23,42,4,"Người chẳng mòn mỏi, chẳng ngã lòng, cho đến chừng nào lập xong sự công bình trên đất; các cù lao sẽ trông đợi luật pháp người."));
visuperobject.biblemap.put("b23_42_5", new visuperobject(23,42,5,"Giê-hô-va Ðức Chúa Trời, là Ðấng đã dựng nên các từng trời và giương ra, đã trải đất với mọi sự ra bởi nó, đã ban hơi sống cho dân ở trên nó, và thần linh cho mọi loài đi trên nó, có phán như vầy:"));
visuperobject.biblemap.put("b23_42_6", new visuperobject(23,42,6,"Ta, Ðức Giê-hô-va, đã kêu gọi ngươi trong sự công bình; ta sẽ nắm tay ngươi và giữ lấy ngươi. Ta sẽ phó ngươi làm giao ước của dân nầy, làm sự sáng cho các dân ngoại,"));
visuperobject.biblemap.put("b23_42_7", new visuperobject(23,42,7,"để mở mắt kẻ mù, làm cho kẻ tù ra khỏi khám, kẻ ngồi trong tối tăm ra khỏi ngục."));
visuperobject.biblemap.put("b23_42_8", new visuperobject(23,42,8,"Ta là Ðức Giê-hô-va; ấy là danh ta. Ta chẳng nhường sự vinh hiển ta cho một đấng nào khác, cũng không nhường sự tôn trọng ta cho những tượng chạm!"));
visuperobject.biblemap.put("b23_42_9", new visuperobject(23,42,9,"Nầy, những sự đầu tiên đã ứng nghiệm rồi, nay ta lại rao cho các ngươi sự mới; ta làm cho các ngươi biết trước khi nó nổ ra."));
visuperobject.biblemap.put("b23_42_10", new visuperobject(23,42,10,"Các ngươi là kẻ đi biển cùng mọi vật ở trong biển, các cù lao cùng dân cư nó, hãy hát bài ca tụng mới cho Ðức Giê-hô-va, hãy từ nơi đầu cùng đất ngợi khen Ngài!"));
visuperobject.biblemap.put("b23_42_11", new visuperobject(23,42,11,"Ðồng vắng cùng các thành nó hãy cất tiếng lên; các làng Kê-đa ở cũng vậy; dân cư Sê-la hãy hát, hãy kêu lên từ trên chót núi!"));
visuperobject.biblemap.put("b23_42_12", new visuperobject(23,42,12,"Hãy dâng vinh hiển cho Ðức Giê-hô-va, và rao sự ngợi khen Chúa trong các cù lao!"));
visuperobject.biblemap.put("b23_42_13", new visuperobject(23,42,13,"Ðức Giê-hô-va sẽ ra như người mạnh dạn; giục lòng sốt sắng như người lính chiến; kêu la quát tháo; ra sức cự lại kẻ thù nghịch mình."));
visuperobject.biblemap.put("b23_42_14", new visuperobject(23,42,14,"Ta làm thinh đã lâu; ta đã nín lặng, ta đã cầm mình. Nhưng bây giờ, ta rên la, thở hào hễn, như đờn bà sanh đẻ."));
visuperobject.biblemap.put("b23_42_15", new visuperobject(23,42,15,"Ta sắp phá hoang núi và đồi, làm cho cỏ cây khô héo; biến sông thành cù lao, làm cho hồ ao cạn tắt."));
visuperobject.biblemap.put("b23_42_16", new visuperobject(23,42,16,"Ta sẽ khiến kẻ mù đi trên con đường mình không thuộc, dắt họ bởi các nẻo chẳng quen; ta sẽ đổi bóng tối trước mặt họ ra ánh sáng, nơi cong quẹo ra ngay thẳng. Thật, ta sẽ làm các sự đó, và không lìa bỏ chúng nó đâu."));
visuperobject.biblemap.put("b23_42_17", new visuperobject(23,42,17,"Song những kẻ cậy các tượng chạm, xưng các tượng đúc rằng: Các ngài là thần chúng tôi, thì sẽ đều giựt lùi và mang xấu hổ!"));
visuperobject.biblemap.put("b23_42_18", new visuperobject(23,42,18,"Hỡi những kẻ điếc, hãy nghe; còn các ngươi là kẻ mù, hãy mở mắt và thấy!"));
visuperobject.biblemap.put("b23_42_19", new visuperobject(23,42,19,"Ai là mù, há chẳng phải đầy tớ ta sao? Ai là điếc như sứ giả mà ta đã sai đi? Ai là mù như kẻ đã hòa thuận cùng ta! Ai là mù như đầy tớ của Ðức Giê-hô-va?"));
visuperobject.biblemap.put("b23_42_20", new visuperobject(23,42,20,"Ngươi thấy nhiều sự, mà không giữ chi hết; ngươi vận có lỗ tai ngỏ, mà không nghe chi hết."));
visuperobject.biblemap.put("b23_42_21", new visuperobject(23,42,21,"Ðức Giê-hô-va vui lòng vì sự công bình mình, làm cho luật pháp cả sáng và tôn trọng."));
visuperobject.biblemap.put("b23_42_22", new visuperobject(23,42,22,"Nhưng ấy là một dân bị cướp bị giựt: hết thảy đều bị mắc lưới trong hang và giam trong ngục; chúng nó làm của cướp mà chẳng ai giải cứu; làm mồi mà chẳng ai nói rằng: Hãy trả lại!"));
visuperobject.biblemap.put("b23_42_23", new visuperobject(23,42,23,"Trong vòng các ngươi ai là kẻ lắng tai nghe điều nầy, ai để ý đến và nghe sự xảy có về sau?"));
visuperobject.biblemap.put("b23_42_24", new visuperobject(23,42,24,"Ai đã phó Gia-cốp cho sự cướp bóc, và Y-sơ-ra-ên cho quân trộm cướp? Há chẳng phải Ðức Giê-hô-va, là Ðấng mà chúng ta phạm tội nghịch cùng, đường lối Ngài chúng nó chẳng noi theo, luật pháp Ngài chúng nó chẳng vâng giữ?"));
visuperobject.biblemap.put("b23_42_25", new visuperobject(23,42,25,"Cho nên Chúa đã trút cơn thạnh nộ và sự chiến tranh dữ dội trên nó; lửa đốt tứ bề mà nó không biết; lửa thiêu cháy nó, mà cũng chẳng lấy làm lo."));
visuperobject.biblemap.put("b23_43_1", new visuperobject(23,43,1,"Bây giờ, hỡi Gia-cốp! Ðức Giê-hô-va là Ðấng đã dựng nên ngươi, hỡi Y-sơ-ra-ên! Ngài là Ðấng đã tạo thành ngươi, phán như vầy: Ðừng sợ, vì ta đã chuộc ngươi. Ta đã lấy tên ngươi gọi ngươi; ngươi thuộc về ta."));
visuperobject.biblemap.put("b23_43_2", new visuperobject(23,43,2,"Khi ngươi vượt qua các dòng nước, ta sẽ ở cùng; khi ngươi lội qua sông, sẽ chẳng che lấp. Khi ngươi bước qua lửa, sẽ chẳng bị cháy, ngọn lửa chẳng đốt ngươi."));
visuperobject.biblemap.put("b23_43_3", new visuperobject(23,43,3,"Vì ta là Giê-hô-va Ðức Chúa Trời ngươi, Ðấng Thánh của Y-sơ-ra-ên, Cứu Chúa ngươi. Ta ban Ê-díp-tô làm giá chuộc ngươi, Ê-thi-ô-bi và Sê-ba làm của thay ngươi."));
visuperobject.biblemap.put("b23_43_4", new visuperobject(23,43,4,"Vì ta đã coi ngươi là quí báu, đáng chuộng, và ta đã yêu ngươi, nên ta sẽ ban những người thế ngươi, và các dân tộc thay mạng sống ngươi."));
visuperobject.biblemap.put("b23_43_5", new visuperobject(23,43,5,"Ðừng sợ, vì ta ở cùng ngươi: ta sẽ khiến dòng dõi ngươi đến từ phương tây."));
visuperobject.biblemap.put("b23_43_6", new visuperobject(23,43,6,"Ta sẽ bảo phương bắc rằng: Hãy buông ra! và bảo phương nam rằng: Chớ cầm lại làm chi! Hãy đem các con trai ta về từ nơi xa, đem các con gái ta về từ nơi đầu cùng đất,"));
visuperobject.biblemap.put("b23_43_7", new visuperobject(23,43,7,"tức là những kẻ xưng bằng tên ta, ta đã dựng nên họ vì vinh quang ta; ta đã tạo thành và đã làm nên họ."));
visuperobject.biblemap.put("b23_43_8", new visuperobject(23,43,8,"Hãy đem dân nầy ra, là dân có mắt mà đui, và những kẻ có tai mà điếc!"));
visuperobject.biblemap.put("b23_43_9", new visuperobject(23,43,9,"Các nước hãy nhóm lại, các dân tộc hãy hiệp lại! Trong vòng họ, ai có thể rao truyền sự nầy, và tỏ cho chúng ta những sự từ trước? Họ hãy dẫn người làm chứng, hầu cho mình được xưng công bình, và cho người ta nghe mà nói rằng: Ấy là thật!"));
visuperobject.biblemap.put("b23_43_10", new visuperobject(23,43,10,"Ðức Giê-hô-va phán: Các ngươi là kẻ làm chứng ta, và là đầy tớ ta đã chọn, hầu cho các ngươi được biết và tin ta, và hiểu rằng ta là Chúa! Chẳng có Ðức Chúa Trời nào tạo thành trước ta, và cũng chẳng có sau ta nữa."));
visuperobject.biblemap.put("b23_43_11", new visuperobject(23,43,11,"Ấy chính ta, chính ta là Ðức Giê-hô-va, ngoài ta không có cứu chúa nào khác."));
visuperobject.biblemap.put("b23_43_12", new visuperobject(23,43,12,"Ấy chính ta là Ðấng đã rao truyền, đã giải cứu, và đã chỉ bảo, chẳng có thần nào lạ giữa các ngươi. Ðức Giê-hô-va phán: Các ngươi đều là người làm chứng, và ta là Ðức Chúa Trời!"));
visuperobject.biblemap.put("b23_43_13", new visuperobject(23,43,13,"Thật, từ khi có ngày ta đã là Ðức Chúa Trời, chẳng ai có thể giải cứu khỏi tay ta. Ta làm ra, ai ngăn cấm ta được?"));
visuperobject.biblemap.put("b23_43_14", new visuperobject(23,43,14,"Ðức Giê-hô-va, Ðấng Cứu chuộc các ngươi, Ðấng Thánh của Y-sơ-ra-ên, phán như vầy: Vì cớ các ngươi, ta đã sai đến Ba-by-lôn, lên chúng nó, tức là người Canh-đê, như là người chạy trốn, xuống các tàu mình đã lấy làm vui mừng."));
visuperobject.biblemap.put("b23_43_15", new visuperobject(23,43,15,"Ta, Ðức Giê-hô-va, là Ðấng Thánh của các ngươi, Ðấng tạo thành Y-sơ-ra-ên, tức Vua các ngươi."));
visuperobject.biblemap.put("b23_43_16", new visuperobject(23,43,16,"Ðức Giê-hô-va, là Ðấng đã mở lối trong biển và đường vượt qua dòng nước mạnh,"));
visuperobject.biblemap.put("b23_43_17", new visuperobject(23,43,17,"khiến xe, ngựa, cơ binh và lính mạnh nhóm lại, thảy đều nằm cả tại đó, sẽ không dậy nữa, bị ngột và tắt đi như tim đèn, phán như vầy:"));
visuperobject.biblemap.put("b23_43_18", new visuperobject(23,43,18,"Ðừng nhớ lại sự đã qua, và chớ nghĩ đến sự đời trước."));
visuperobject.biblemap.put("b23_43_19", new visuperobject(23,43,19,"Nầy, ta sắp làm một việc mới, việc nầy sẽ hiện ra ngay; các ngươi há chẳng biết sao? Ấy là ta sẽ vạch một con đường trong đồng vắng, và khiến những sông chảy trong nơi sa mạc."));
visuperobject.biblemap.put("b23_43_20", new visuperobject(23,43,20,"Những thú đồng, với muông rừng và chim đà, sẽ tôn vinh ta; vì ta đặt các dòng nước trong đồng vắng và các sông trong sa mạc, đặng cho dân ta đã chọn được uống."));
visuperobject.biblemap.put("b23_43_21", new visuperobject(23,43,21,"Ta đã làm nên dân nầy cho ta, nó sẽ hát khen ngợi ta."));
visuperobject.biblemap.put("b23_43_22", new visuperobject(23,43,22,"Song, hỡi Gia-cốp, ngươi chẳng từng kêu cầu ta! Hỡi Y-sơ-ra-ên, ngươi đã chán ta!"));
visuperobject.biblemap.put("b23_43_23", new visuperobject(23,43,23,"Ngươi chẳng dùng chiên làm của lễ thiêu dâng cho ta, chẳng dùng hi sinh tôn kính ta. Ta chẳng làm phiền ngươi mà khiến dâng lễ vật, cũng chẳng làm nhọc ngươi mà khiến đốt hương."));
visuperobject.biblemap.put("b23_43_24", new visuperobject(23,43,24,"Ngươi chẳng lấy tiền mua xương bồ cho ta, và chẳng lấy mỡ của hi sinh làm cho ta no; nhưng lại lấy tội lỗi mình làm cho ta phiền, lấy sự gian ác làm cho ta nhọc."));
visuperobject.biblemap.put("b23_43_25", new visuperobject(23,43,25,"Ấy chính ta, là Ðấng vì mình ta mà xóa sự phạm tội ngươi; ta sẽ không nhớ đến tội lỗi ngươi nữa."));
visuperobject.biblemap.put("b23_43_26", new visuperobject(23,43,26,"Hãy nhắc lại cho ta nhớ; chúng ta hãy bình luận cùng nhau; hãy luận lẽ đi, hầu cho ngươi được xưng công bình."));
visuperobject.biblemap.put("b23_43_27", new visuperobject(23,43,27,"Thỉ tổ ngươi đã phạm tội, các thầy giáo ngươi lại phạm phép nghịch cùng ta."));
visuperobject.biblemap.put("b23_43_28", new visuperobject(23,43,28,"Vậy nên ta làm nhục các quan trưởng của nơi thánh; ta sẽ bắt Gia-cốp làm sự rủa sả, và Y-sơ-ra-ên làm sự gièm chê."));
visuperobject.biblemap.put("b23_44_1", new visuperobject(23,44,1,"Bây giờ, hỡi Gia-cốp, tôi tớ ta, hỡi Y-sơ-ra-ên mà ta đã chọn, hãy nghe!"));
visuperobject.biblemap.put("b23_44_2", new visuperobject(23,44,2,"Ðức Giê-hô-va, là Ðấng đã làm nên ngươi, đã gây nên ngươi từ trong lòng mẹ, và sẽ giúp đỡ ngươi, phán như vầy: Hỡi Gia-cốp, tôi tớ ta, và Giê-su-run mà ta đã chọn, đừng sợ chi;"));
visuperobject.biblemap.put("b23_44_3", new visuperobject(23,44,3,"vì ta sẽ rót nước trên kẻ khát, cho suối chảy nơi đất khô. Ta sẽ đổ Thần ta trên dòng dõi ngươi, và phước lành ta trên những kẻ ra từ ngươi."));
visuperobject.biblemap.put("b23_44_4", new visuperobject(23,44,4,"Chúng nó sẽ nẩy nở giữa đám cỏ, như cây liễu dưa dòng nước. Người nầy sẽ nói rằng: Ta thuộc về Ðức Giê-hô-va; người kia xưng"));
visuperobject.biblemap.put("b23_44_5", new visuperobject(23,44,5,"mình bằng danh của Gia-cốp, kẻ khác nữa lấy tay mình viết rằng: Ta thuộc về Ðức Giê-hô-va, và xưng mình bằng danh Y-sơ-ra-ên."));
visuperobject.biblemap.put("b23_44_6", new visuperobject(23,44,6,"Ðức Giê-hô-va, là Vua và Ðấng Cứu chuộc của Y-sơ-ra-ên, là Ðức Giê-hô-va vạn quân, phán như vầy: Ta là đầu tiên và cuối cùng; ngoài ta không có Ðức Chúa Trời nào khác."));
visuperobject.biblemap.put("b23_44_7", new visuperobject(23,44,7,"Ai là kẻ kêu gọi như ta, rao bảo và phán truyền điều đó từ khi ta đã lập dân tộc xưa nầy? thì hãy rao truyền sự mai sau và điều chi sẽ xảy đến!"));
visuperobject.biblemap.put("b23_44_8", new visuperobject(23,44,8,"Chớ kinh hãi và cũng đừng bối rối. Từ thuở đó ta há chẳng từng rao ra cho các ngươi biết sao? Các ngươi làm chứng cho ta. Ngoài ta có Ðức Chúa Trời nào khác không? Thật, chẳng có Vầng Ðá nào khác; ta không biết đến!"));
visuperobject.biblemap.put("b23_44_9", new visuperobject(23,44,9,"Những thợ chạm tượng đều là hư vô, việc họ rất ưa thích chẳng có ích gì. Những kẻ làm chứng của họ không thấy và không biết, để họ mang xấu hổ."));
visuperobject.biblemap.put("b23_44_10", new visuperobject(23,44,10,"Ai là kẻ tạo một vì thần, đúc một tượng, mà không có ích chi?"));
visuperobject.biblemap.put("b23_44_11", new visuperobject(23,44,11,"Nầy, mọi kẻ làm bạn về việc đó sẽ bị nhục, những thợ đó chẳng qua là loài người! Họ hãy nhóm lại hết thảy và đứng lên! Chắc sẽ cùng nhau bị kinh hãi và xấu hổ."));
visuperobject.biblemap.put("b23_44_12", new visuperobject(23,44,12,"Thợ rèn lấy một thỏi sắt nướng trên than lửa; dùng búa mà đập, dùng cánh tay mạnh mà làm; mặc dầu đói và kém sức; không uống nước và kiệt cả người."));
visuperobject.biblemap.put("b23_44_13", new visuperobject(23,44,13,"Thợ mộc giăng dây; dùng phấn mà gạch; đẽo bằng cái chàng, đo bằng cái nhíp; làm nên tượng người, giống hình người tốt đẹp, để ở trong một cái nhà."));
visuperobject.biblemap.put("b23_44_14", new visuperobject(23,44,14,"Lại đi đốn cây bách; lấy cây lật, cây dẽ, mà mình đã chọn trong các thứ cây trên rừng; trồng cây thông, mưa xuống làm cho lớn lên."));
visuperobject.biblemap.put("b23_44_15", new visuperobject(23,44,15,"Gỗ ấy người ta sẽ dùng mà chụm, lấy về mà sưởi. Cùng dùng để dun lửa hấp bánh; lại cũng lấy mà làm một vì thần, rồi thờ lạy nó, làm nó nên một tượng chạm mà quì lạy trước mặt nó."));
visuperobject.biblemap.put("b23_44_16", new visuperobject(23,44,16,"Họ chụm đi phân nửa trong lửa, còn phân nửa thì dùng mà nướng thịt, ăn cho no; hoặc sưởi cho ấm, và nói rằng: Hà, nầy ta sưởi đây; ta thấy ngọn lửa!"));
visuperobject.biblemap.put("b23_44_17", new visuperobject(23,44,17,"Ðoạn, gỗ còn thừa lại, làm một vì thần, tức là tượng chạm mình; cúi mình trước mặt nó mà thờ lạy, cầu nguyện nó mà rằng: Xin giải cứu tôi, vì ngài là thần của tôi!"));
visuperobject.biblemap.put("b23_44_18", new visuperobject(23,44,18,"Những người ấy không biết và không suy xét; vì mắt họ nhắm lại để đừng thấy, lòng họ lấp đi để đừng hiểu."));
visuperobject.biblemap.put("b23_44_19", new visuperobject(23,44,19,"Trong bọn họ chẳng ai suy đi nghĩ lại, chẳng ai có sự thông biết khôn sáng mà nói rằng: Ta đã lấy phân nửa mà chụm; đã hấp bánh trên lửa than; đã quay thịt và ăn rồi; còn thừa lại, ta dùng làm một vật gớm ghiếc sao? Ta lại đi cúi mình lạy một gốc cây sao?"));
visuperobject.biblemap.put("b23_44_20", new visuperobject(23,44,20,"Người đó ăn tro, lòng mê muội đã làm cho lìa bỏ đường, chẳng có thể tự cứu linh hồn mình, hay là nói rằng: Trong tay hữu ta há chẳng phải là đồ giả dối sao?"));
visuperobject.biblemap.put("b23_44_21", new visuperobject(23,44,21,"Hỡi Gia-cốp, hỡi Y-sơ-ra-ên, hãy nhớ các điều nầy! Vì ngươi là tôi tớ ta. Ta đã gây nên ngươi; hỡi Y-sơ-ra-ên, ngươi là tôi tớ ta, ta sẽ không quên ngươi!"));
visuperobject.biblemap.put("b23_44_22", new visuperobject(23,44,22,"Ta đã xóa sự phạm tội ngươi như mây đậm, và tội lỗi ngươi như đám nây. Hãy trở lại cùng ta, vì ta đã chuộc ngươi."));
visuperobject.biblemap.put("b23_44_23", new visuperobject(23,44,23,"Hỡi các từng trời, hãy hát vui mừng vì Ðức Giê-hô-va đã làm hoàn thành! Hỡi các nơi sâu dưới đất, hãy reo la! Hỡi các núi, rừng cũng mọi cây cối trên rừng, hãy cất tiếng mà hát! vì Ðức Giê-hô-va đã chuộc cứu giúp, và sẽ tỏ sự vinh hiển Ngài ra trong Y-sơ-ra-ên."));
visuperobject.biblemap.put("b23_44_24", new visuperobject(23,44,24,"Ðức Giê-hô-va, là Ðấng Cứu chuộc ngươi, đã gây nên ngươi từ trong lòng mẹ, phán như vầy: Ta là Ðức Giê-hô-va, đã làm nên mọi vật, một mình ta đã giương các từng trời và trải đất, ai ở cùng ta?"));
visuperobject.biblemap.put("b23_44_25", new visuperobject(23,44,25,"Ấy chính ta làm trật các điềm của kẻ nói dối, khiến thầy bói nên ngu muội, làm cho kẻ trí tháo lui, biến sự khôn ngoan nó ra dại khờ;"));
visuperobject.biblemap.put("b23_44_26", new visuperobject(23,44,26,"Chúa làm ứng nghiệm lời của tôi tớ Ngài; phán về thành Giê-ru-sa-lem rằng: Nó sẽ có người ở; và về các thành của Giu-đa rằng: Nó sẽ được lập lại, ta sẽ dựng lại các nơi hoang vu của nó;"));
visuperobject.biblemap.put("b23_44_27", new visuperobject(23,44,27,"phán cùng vực sâu rằng: Hãy khô đi, ta sẽ làm cạn các sông;"));
visuperobject.biblemap.put("b23_44_28", new visuperobject(23,44,28,"phán về Si-ru rằng: Nó là người chăn chiên của ta; nó sẽ làm nên mọi sự ta đẹp lòng; lại cũng phán về Giê-ru-sa-lem rằng: Nó sẽ được lập lại, và phán về đền thờ rằng: Nền ngươi sẽ lại lập."));
visuperobject.biblemap.put("b23_45_1", new visuperobject(23,45,1,"Ðức Giê-hô-va phán thể nầy cùng Si-ru, là người xức dầu của Ngài, ta đã cầm lấy tay hữu người, đặng hàng phục các nước trước mặt người, và ta sẽ tháo dây lưng các vua; đặng mỡ các cửa thành trước mặt người, cấm không được đóng lại:"));
visuperobject.biblemap.put("b23_45_2", new visuperobject(23,45,2,"Ta sẽ đi trước mặt ngươi, ban bằng các đường gập ghềnh; phá vỡ các cửa bằng đồng, bẻ gãy then gài bằng sắt;"));
visuperobject.biblemap.put("b23_45_3", new visuperobject(23,45,3,"ta sẽ ban vật báu chứa trong nơi tối, của cải chứa trong nơi kín cho ngươi, để ngươi biết rằng chính ta, là Ðức Giê-hô-va, Ðức Chúa Trời của Y-sơ-ra-ên, đã lấy tên ngươi gọi ngươi."));
visuperobject.biblemap.put("b23_45_4", new visuperobject(23,45,4,"Vì cớ Gia-cốp, tôi tớ ta, và Y-sơ-ra-ên, kẻ lựa chọn của ta, nên ta đã lấy tên ngươi gọi ngươi, dầu ngươi không biết ta."));
visuperobject.biblemap.put("b23_45_5", new visuperobject(23,45,5,"Ta là Ðức Giê-hô-va, không có Ðấng nào khác, ngoài ta không có Ðức Chúa Trời nào khác nữa. Dầu ngươi không biết ta, ta sẽ thắt lưng ngươi,"));
visuperobject.biblemap.put("b23_45_6", new visuperobject(23,45,6,"hầu cho từ phía mặt trời mọc đến phía mặt trời lặn họ được biết rằng không có Ðức Chúa Trời nào khác ngoài ta. Ta là Ðức Giê-hô-va, không có đấng nào khác."));
visuperobject.biblemap.put("b23_45_7", new visuperobject(23,45,7,"Ấy chính ta là Ðấng gây nên sự sáng và dựng nên sự tối tăm, làm ra sự bình an và dựng nên sự tai vạ; chính ta, là Ðức Giê-hô-va, là mọi sự đó."));
visuperobject.biblemap.put("b23_45_8", new visuperobject(23,45,8,"Hỡi các từng trời, hãy sa móc xuống từ trên cao, vòng khung hãy đổ sự công bình xuống! Ðất hãy tự nẻ ra, đặng sanh sự cứu rỗi, sự công bình mọc lên cả một lần! Ta, là Ðức Giê-hô-va, đã dựng nên sự đó."));
visuperobject.biblemap.put("b23_45_9", new visuperobject(23,45,9,"Khốn thay cho kẻ cãi lại với Ðấng tạo nên mình! một bình trong các bình bằng đất! Ðất sét há dễ nói cùng thợ gốm là người nhồi mình rằng: Ngươi làm chi? hoặc việc ngươi làm ra há nói rằng: Nó không có tay?"));
visuperobject.biblemap.put("b23_45_10", new visuperobject(23,45,10,"Khốn thay cho kẻ nói cùng cha mình rằng: Cha sinh ra gì? và cùng đờn bà rằng: Ngươi đẻ gì?"));
visuperobject.biblemap.put("b23_45_11", new visuperobject(23,45,11,"Ðức Giê-hô-va, Ðấng Thánh của Y-sơ-ra-ên, tức là Ðấng đã làm ra Y-sơ-ra-ên, phán như vầy: Các ngươi hãy hỏi ta về những sự sẽ đến; hãy bảo ta về sự các con trai ta, và về việc làm bởi tay ta."));
visuperobject.biblemap.put("b23_45_12", new visuperobject(23,45,12,"Ấy chính ta đã làm ra đất; chính ta, tức là tay ta, đã giương ra các từng trời, và đã truyền bảo các cơ binh nó."));
visuperobject.biblemap.put("b23_45_13", new visuperobject(23,45,13,"Ấy chính ta đã dấy người lên trong sự công bình, và sẽ ban bằng mọi đường lối người. Người sẽ lập lại thành ta, và thả kẻ bị đày của ta, không cần giá chuộc, cũng không cần phần thưởng, Ðức Giê-hô-va vạn quân phán vậy."));
visuperobject.biblemap.put("b23_45_14", new visuperobject(23,45,14,"Ðức Giê-hô-va phán như vầy: Lời lãi nước Ê-díp-tô, của cải nước Ê-thi-ô-bi, cùng người Sa-bê, người vóc giạc cao lớn, đều sẽ qua cùng ngươi và thuộc về ngươi. Họ sẽ bước theo ngươi; mang xiềng mà đến, quị lụy trước mặt ngươi và nài xin rằng: Thật Ðức Chúa Trời ở giữa ngươi; ngoài Ngài chẳng có ai khác, không có Ðức Chúa Trời nào nữa."));
visuperobject.biblemap.put("b23_45_15", new visuperobject(23,45,15,"Hỡi Ðức Chúa Trời của Y-sơ-ra-ên, là Cứu Chúa, Ngài thật là Ðức Chúa Trời ẩn mình!"));
visuperobject.biblemap.put("b23_45_16", new visuperobject(23,45,16,"Hết thảy chúng nó đều bị xấu hổ nhuốc nha; những thợ làm hình tượng cùng nhau trở về, rất là mắc cỡ."));
visuperobject.biblemap.put("b23_45_17", new visuperobject(23,45,17,"Duy Y-sơ-ra-ên đã được Ðức Giê-hô-va cứu cho, được sự cứu đời đời, đến đời đời vô cùng các ngươi không còn mang hổ mang nhơ!"));
visuperobject.biblemap.put("b23_45_18", new visuperobject(23,45,18,"Vì Ðức Giê-hô-va, là Ðấng đã dựng nên các từng trời, tức là Ðức Chúa Trời đã tạo thành đất và làm ra nó, đã lập nó cho bền vững, chẳng phải dựng nên là trống không, bèn đã làm nên để dân ở, phán như vầy: Ta là Ðức Giê-hô-va, không có Ðấng nào khác!"));
visuperobject.biblemap.put("b23_45_19", new visuperobject(23,45,19,"Ta chẳng hề nói cách kín giấu, chẳng nói trong nơi của đất tối tăm. Ta chẳng từng phán cùng dòng dõi Gia-cốp rằng: Các ngươi tìm ta là vô ích. Ta, là Ðức Giê-hô-va, phán sự công bình, truyền điều chơn thật."));
visuperobject.biblemap.put("b23_45_20", new visuperobject(23,45,20,"Hỡi dân thoát nạn của các nước, hãy nhóm lại và đến, thảy cùng nhau lại gần! Những kẻ khiên gổ của tượng chạm mình, cầu nguyện với thần không cứu được, thật là đồ vô thức."));
visuperobject.biblemap.put("b23_45_21", new visuperobject(23,45,21,"Vậy hãy truyền rao; hãy bảo chúng nó đến gần, và nghị luận cùng nhau! Từ đời xưa ai đã rao ra sự nầy? ai đã tỏ ra từ lúc thượng cổ? Há chẳng phải ta, là Ðức Giê-hô-va, sao? Ngoài ta chẳng có Ðức Chúa Trời nào khác! chẳng có Ðức Chúa Trời nào khác là công bình và là Cứu Chúa ngoài ta."));
visuperobject.biblemap.put("b23_45_22", new visuperobject(23,45,22,"Hỡi các ngươi hết thảy ở các nơi đầu cùng đất, hãy nhìn xem ta và được cứu! Vì ta là Ðức Chúa Trời, chẳng có Chúa nào khác."));
visuperobject.biblemap.put("b23_45_23", new visuperobject(23,45,23,"Ta đã chỉ mình mà thề, lời công bình ra từ miệng ta sẽ chẳng hề trở lại: mọi đầu gối sẽ quì trước mặt ta, mọi lưỡi sẽ chỉ ta ma thề."));
visuperobject.biblemap.put("b23_45_24", new visuperobject(23,45,24,"Người ta sẽ luận về ta rằng: sự công bình và sức mạnh chỉ ở trong Ðức Giê-hô-va, người ta sẽ đến cùng Ngài. Mọi kẻ giận Ngài sẽ bị hổ thẹn."));
visuperobject.biblemap.put("b23_45_25", new visuperobject(23,45,25,"Cả dòng dõi Y-sơ-ra-ên sẽ được xưng công bình trong Ðức Giê-hô-va, và nhờ Ngài mà được vinh hiển."));
visuperobject.biblemap.put("b23_46_1", new visuperobject(23,46,1,"Bên cúi xuống; Nê-bô-khom mình; tượng nó chở trên loài thú, trên súc vật. Những vật mà các ngươi vốn thường khiêng, đè nặng quá, làm cho súc vật mệt nhọc."));
visuperobject.biblemap.put("b23_46_2", new visuperobject(23,46,2,"Chúng nó cúi xuống, cùng nhau khom mình; đã chẳng cứu được gánh nặng của mình, và chính mình lại bị bắt làm phu tù."));
visuperobject.biblemap.put("b23_46_3", new visuperobject(23,46,3,"Hỡi nhà Gia-cốp, và hết thảy các ngươi là dân sót của nhà Y-sơ-ra-ên, hãy nghe ta, ta đã gánh vác các ngươi từ lúc mới sanh, bồng-ẵm các ngươi từ trong lòng mẹ."));
visuperobject.biblemap.put("b23_46_4", new visuperobject(23,46,4,"Cho đến chừng các ngươi già cả, đầu râu tóc bạc, ta cũng sẽ bồng-ẵm các ngươi. Ta đã làm ra, thì sẽ còn gánh vác các ngươi nữa. Ta sẽ bồng ẵm và giải cứu các ngươi."));
visuperobject.biblemap.put("b23_46_5", new visuperobject(23,46,5,"Các ngươi so sánh ta cùng ai, và coi ta bằng ai? Các ngươi lấy ai đọ với ta, đặng chúng ta được giống nhau?"));
visuperobject.biblemap.put("b23_46_6", new visuperobject(23,46,6,"Kìa, họ móc vàng trong túi ra, và lấy cân mà cân bạc, thuê thợ vàng dùng mà đúc một thần, với cúi mình thờ lạy."));
visuperobject.biblemap.put("b23_46_7", new visuperobject(23,46,7,"Họ vác tượng ấy trên vai, đem đi, đặt vào chỗ nó: thần cứ đứng đó mà không dời khỏi chỗ nó nữa. Nếu có ai kêu cầu, thì thần chẳng trả lời, và không cứu khỏi nạn được."));
visuperobject.biblemap.put("b23_46_8", new visuperobject(23,46,8,"Hãy nhớ mọi sự đó, hãy rõ ra là đấng trượng phu! Hỡi kẻ phạm tội, hãy nghĩ lại!"));
visuperobject.biblemap.put("b23_46_9", new visuperobject(23,46,9,"Hãy nhớ lại những sự ngày xưa; vì ta là Ðức Chúa Trời, và chẳng có Chúa nào khác; ta là Ðức Chúa Trời, chẳng có ai giống như ta."));
visuperobject.biblemap.put("b23_46_10", new visuperobject(23,46,10,"Ta đã rao sự cuối cùng từ buổi đầu tiên, và đã nói từ thuở xưa những sự chưa làm nên. Ta phán rằng: Mưu của ta sẽ lập, và ta sẽ làm ra mọi sự ta đẹp ý."));
visuperobject.biblemap.put("b23_46_11", new visuperobject(23,46,11,"Ta gọi chim ó đến từ phương đông, và gọi người làm mưu ta đến từ xứ xa. Ðiều ta đã rao ra, ta sẽ làm hoàn thành, điều ta đã định, ta cũng sẽ làm."));
visuperobject.biblemap.put("b23_46_12", new visuperobject(23,46,12,"Hỡi những người cứng lòng, xa cách sự công bình, hãy nghe ta phán."));
visuperobject.biblemap.put("b23_46_13", new visuperobject(23,46,13,"Ta làm cho sự công bình ta đến gần, nó chẳng xa chi, và sự cứu rỗi của ta sẽ chẳng chậm trễ. Ta sẽ đặt sự cứu rỗi trong Si-ôn cho Y-sơ-ra-ên, là sự vinh hiển ta."));
visuperobject.biblemap.put("b23_47_1", new visuperobject(23,47,1,"Hỡi con gái đồng trinh của Ba-by-lôn, hãy xuống ngồi trong bụi đất! Hỡi con gái người Canh-đê, hãy ngồi dưới đất, ngươi chẳng có ngôi nữa! Vì từ nay về sau, ngươi sẽ không được xưng là dịu dàng yểu điệu nữa đâu."));
visuperobject.biblemap.put("b23_47_2", new visuperobject(23,47,2,"Hãy lấy cối xay và đi xay bột; hãy bỏ lúp, vén vạt áo đi, để trần chơn, đặng lội qua sông."));
visuperobject.biblemap.put("b23_47_3", new visuperobject(23,47,3,"Sự lõa lồ của ngươi phải tỏ ra, sự sỉ nhục ngươi sẽ bị thấy! Ta sẽ làm sự báo cừu, không chừa ai hết."));
visuperobject.biblemap.put("b23_47_4", new visuperobject(23,47,4,"Ðấng Cứu chuộc của chúng ta, danh Ngài là Ðức Giê-hô-va vạn quân, là Ðấng Thánh của Y-sơ-ra-ên."));
visuperobject.biblemap.put("b23_47_5", new visuperobject(23,47,5,"Hỡi con gái của người Canh-đê, hãy ngồi làm thinh, trốn trong nơi tối tăm! Vì từ nay về sau, ngươi sẽ không được gọi là chủ mẫu của các nước nữa."));
visuperobject.biblemap.put("b23_47_6", new visuperobject(23,47,6,"Ta đã nổi giận nghịch cũng dân ta, làm ô uế sản nghiệp ta, và phó hết chúng nó trong tay ngươi. Nhưng ngươi chẳng từng dùng sự thương xót được chúng nó; đã tra ách nặng trên người già cả."));
visuperobject.biblemap.put("b23_47_7", new visuperobject(23,47,7,"Ngươi nói rằng: Ta sẽ làm những sự ấy vào lòng, và chẳng nghĩ đến cuối cùng sự đó."));
visuperobject.biblemap.put("b23_47_8", new visuperobject(23,47,8,"Hỡi nước sung sướng ở yên ổn, bụng bảo dạ rằng: Ta đây, ngoài ta chẳng còn ai! Ta sẽ chẳng góa bụa, chẳng biết mất con cái là gì; nầy, bây giờ hãy nghe đây."));
visuperobject.biblemap.put("b23_47_9", new visuperobject(23,47,9,"Cùng trong một giây phút trong một ngày, hai nạn ấy, là sự mất con cái và sự góa bụa, sẽ xảy đến cho ngươi. Hai nạn ấy sẽ đến đầy đủ trên ngươi, khi ngươi làm tà thuật rất nhiều, và có lắm quyền của phù chú!"));
visuperobject.biblemap.put("b23_47_10", new visuperobject(23,47,10,"Ngươi cậy sự gian ác của mình và nói rằng: Chẳng ai thấy ta. Sự khôn ngoan thông biết của ngươi đã phỉnh dổ ngươi, và ngươi tự nói trong lòng rằng: Ta đây, ngoài ta chẳng còn ai!"));
visuperobject.biblemap.put("b23_47_11", new visuperobject(23,47,11,"Vậy nên tai vạ sẽ làm trên ngươi, mà chẳng biết từ đâu; hoạn nạn sẽ đến cho ngươi, mà chẳng trừ được; sự hủy diệt xảy ra thình lình, ngươi không thể liệu trước."));
visuperobject.biblemap.put("b23_47_12", new visuperobject(23,47,12,"Vậy ngươi hãy đứng lên, dùng những tà thuật với vô số phù chú mà ngươi đã tập từ khi thơ ấu! Có lẽ ngươi sẽ được lợi ích; và có lẽ nhơn đó ngươi sẽ nên đáng sợ chăng?"));
visuperobject.biblemap.put("b23_47_13", new visuperobject(23,47,13,"Ngươi đã nhọc sức vì cớ nhiều mưu chước. Vậy những kẻ hỏi trời, xem sao, xem trăng mới mà đoán việc ngày sau, bây giờ hãy đứng lên và cứu ngươi cho khỏi những sự xảy đến trên ngươi."));
visuperobject.biblemap.put("b23_47_14", new visuperobject(23,47,14,"Kìa, họ sẽ trở nên như rơm rạ, bị lửa thiêu đốt; họ sẽ chẳng cứu mình được khỏi quyền ngọn lửa; lửa ấy chẳng phải lửa than để sưởi, hay là lửa để ngồi kề một bên."));
visuperobject.biblemap.put("b23_47_15", new visuperobject(23,47,15,"Kìa, những sự mà ngươi đã làm khó nhọc thì đã trở nên như vậy: những kẻ buôn bán với ngươi từ thuỡ nhỏ, ai về chỗ nấy, chẳng ai đến cứu ngươi hết!"));
visuperobject.biblemap.put("b23_48_1", new visuperobject(23,48,1,"Hỡi nhà Gia-cốp, là những ngươi được xưng bằng danh Y-sơ-ra-ên, ra từ nguồn Giu-đa, hãy nghe đây; các ngươi chỉ danh Ðức Giê-hô-va mà thề, kêu cầu Ðức Chúa Trời của Y-sơ-ra-ên, nhưng chẳng lấy lòng chơn thật và công bình."));
visuperobject.biblemap.put("b23_48_2", new visuperobject(23,48,2,"Vì họ lấy thành thánh làm danh mình, và cậy Ðức Chúa Trời của Y-sơ-ra-ên; danh Ngài là Ðức Giê-hô-va vạn quân."));
visuperobject.biblemap.put("b23_48_3", new visuperobject(23,48,3,"Ta đã phán từ xưa về những sự qua rồi; những sự ấy ra từ miệng ta, và ta đã rao ra: ta đã làm thình lình, và những sự đó đã xảy đến."));
visuperobject.biblemap.put("b23_48_4", new visuperobject(23,48,4,"Hỡi Y-sơ-ra-ên, vì ta biết ngươi cứng cỏi, gáy ngươi là gân sắt, trán ngươi là đồng,"));
visuperobject.biblemap.put("b23_48_5", new visuperobject(23,48,5,"nên ta đã phán những sự đó từ xưa, đã dạy ngươi về những sự đó trước khi chưa đến, kẻo ngươi nói rằng: Ấy là thần tượng ta đã làm nên, tượng chạm hoặc đúc của ta đã dạy biểu mọi điều đó."));
visuperobject.biblemap.put("b23_48_6", new visuperobject(23,48,6,"Ngươi đã nghe rồi, hãy thấy mọi điều đó, sao các ngươi không nói phô đi? Từ nay về sau, ta tỏ ra những sự mới, là sự kín nhiệm ngươi chưa biết."));
visuperobject.biblemap.put("b23_48_7", new visuperobject(23,48,7,"Bây giờ những sự đó mới dựng nên, không phải ngày xưa đã có; trước ngày nầy ngươi chưa nghe chi hết, kẻo ngươi nói rằng: Nầy, ta biết sự đó rồi!"));
visuperobject.biblemap.put("b23_48_8", new visuperobject(23,48,8,"Không, thật ngươi chưa từng nghe, chưa từng biết, tai ngươi chưa mở ra; vì ta biết rằng ngươi làm gian dối luôn, từ trong lòng mẹ đã được gọi là bội nghịch."));
visuperobject.biblemap.put("b23_48_9", new visuperobject(23,48,9,"Ta vì danh mình mà tạm nhịn giận ta; vì sự vinh hiển mình mà nhịn nhục đối với ngươi, đặng không hủy diệt ngươi."));
visuperobject.biblemap.put("b23_48_10", new visuperobject(23,48,10,"Nầy, ta luyện ngươi, nhưng không phải như luyện bạc; ta đã thử ngươi trong lò hoạn nạn."));
visuperobject.biblemap.put("b23_48_11", new visuperobject(23,48,11,"Ấy là vì ta, vì một mình ta, mà ta sẽ làm điều đó; vì ta há để nhục danh ta sao? Ta sẽ chẳng nhường sự vinh hiển ta cho thần nào khác."));
visuperobject.biblemap.put("b23_48_12", new visuperobject(23,48,12,"Hỡi Gia-cốp, và ngươi, Y-sơ-ra-ên ta đã gọi, hãy nghe lời ta: Ta là Ðấng đó; ta là đầu tiên và cùng là cuối cùng."));
visuperobject.biblemap.put("b23_48_13", new visuperobject(23,48,13,"Tay ta đã lập nền đất, tay hữu ta đã giương các từng trời; ta gọi đến, trời đất đều đứng lên."));
visuperobject.biblemap.put("b23_48_14", new visuperobject(23,48,14,"Các ngươi hãy nhóm lại hết thảy, và hãy nghe: Trong vòng họ có ai đã rao ra những sự nầy không? Người mà Ðức Giê-hô-va yêu, sẽ làm đều đẹp ý Ngài nghịch cùng Ba-by-lôn, và cánh tay người sẽ giá lên nghịch cùng người Canh-đê."));
visuperobject.biblemap.put("b23_48_15", new visuperobject(23,48,15,"Ta, chính ta đã phán, và đã gọi người; ta đã khiến người đến, người sẽ làm thạnh vượng đường lối mình."));
visuperobject.biblemap.put("b23_48_16", new visuperobject(23,48,16,"Các ngươi hãy đến gần ta, và nghe điều nầy: Từ lúc ban đầu ta chưa từng nói cách kín giấu. Vừa lúc có sự ấy, ta đã ở đó rồi. Vả, bây giờ, Chúa Giê-hô-va, và Thần của Ngài, sai ta đến."));
visuperobject.biblemap.put("b23_48_17", new visuperobject(23,48,17,"Ðức Giê-hô-va, Ðấng Cứu chuộc ngươi, là Ðấng Thánh của Y-sơ-ra-ên, phán như vầy: Ta là Ðức Giê-hô-va, Ðức Chúa Trời ngươi, là Ðấng dạy cho ngươi được ích, và dắt ngươi trong con đường ngươi phải đi."));
visuperobject.biblemap.put("b23_48_18", new visuperobject(23,48,18,"Than ôi! ước gì ngươi đã để ý đến các điều răn ta! thì sự bình an ngươi như sông, và sự công bình ngươi như sóng biển,"));
visuperobject.biblemap.put("b23_48_19", new visuperobject(23,48,19,"dòng dõi ngươi như cát, hoa trái của ruột già ngươi như sạn, danh ngươi chẳng bị diệt bị xóa trước mặt ta."));
visuperobject.biblemap.put("b23_48_20", new visuperobject(23,48,20,"Hãy ra khỏi Ba-by-lôn! Hãy tránh xa người Canh-đê! Hãy cất tiếng reo vui mà rao tin nầy, tuyên bố và truyền ra cho đến nơi cuối cùng đất! Hãy rằng: Ðức Giê-hô-va đã chuộc Gia-cốp, là tôi tớ Ngài."));
visuperobject.biblemap.put("b23_48_21", new visuperobject(23,48,21,"Khi Ngài dẫn họ đi ngang qua sa mạc thì họ không khát, vì Ngài đã khiến nước từ vầng đá, thì nước văng ra."));
visuperobject.biblemap.put("b23_48_22", new visuperobject(23,48,22,"Những người ác chẳng hưởng sự bình an bao giờ, Ðức Giê-hô-va phán vậy."));
visuperobject.biblemap.put("b23_49_1", new visuperobject(23,49,1,"Hỡi các cù lao, hãy nghe ta! Hỡi các dân xa lạ, hãy ghé tai! Ðức Giê-hô-va đã gọi ta từ trong bụng mẹ, đã nói đến danh ta từ lòng dạ mẹ ta."));
visuperobject.biblemap.put("b23_49_2", new visuperobject(23,49,2,"Ngài đã khiến miệng ta giống như gươm bén; lấy bóng tay Ngài mà che ta; làm cho ta như tên nhọn, và giấu ta trong bao tên."));
visuperobject.biblemap.put("b23_49_3", new visuperobject(23,49,3,"Ngài phán cùng ta rằng: Hỡi Y-sơ-ra-ên, ngươi là tôi tớ ta, ta sẽ được sáng danh bởi ngươi."));
visuperobject.biblemap.put("b23_49_4", new visuperobject(23,49,4,"Ta có nói rằng: Ta đã làm việc luống công, đã hao sức vô ích và không kết quả. Song lẽ ngay thẳng của ta ở nơi Ðức Giê-hô-va, sự ban thưởng cho ta ở nơi Ðức Chúa Trời ta."));
visuperobject.biblemap.put("b23_49_5", new visuperobject(23,49,5,"Bây giờ, Ðức Giê-hô-va là Ðấng đã lập ta làm tôi tớ Ngài từ trong bụng mẹ, có khiến ta dẫn Gia-cốp đến cùng Ngài, và nhóm Y-sơ-ra-ên về cùng Ngài; vì ta được tôn trọng trước mắt Ðức Giê-hô-va, và Ðức Chúa Trời ta là sức mạnh ta."));
visuperobject.biblemap.put("b23_49_6", new visuperobject(23,49,6,"Ngài có phán cùng ta rằng: Ngươi làm tôi tớ ta đặng lập lại các chi phái Gia-cốp, và làm cho những kẻ được gìn giữ của Y-sơ-ra-ên lại được trở về, còn là việc nhỏ; ta sẽ khiến ngươi làm sự sáng cho các dân ngoại, hầu cho ngươi làm sự cứu rỗi của ta đến nơi đầu cùng đất."));
visuperobject.biblemap.put("b23_49_7", new visuperobject(23,49,7,"Ðức Giê-hô-va, Ðấng Cứu chuộc Y-sơ-ra-ên, và Ðấng Thánh của Ngài, phán cùng người bị loài người khinh dể, bị dân nầy gớm ghiếc, là người làm tôi kẻ có quyền thế, như vầy: Các vua sẽ thấy và đứng đậy; các quan trưởng sẽ quì lạy, vì cớ Ðức Giê-hô-va là Ðấng thành tín, tức là Ðấng Thánh của Y-sơ-ra-ên, là Ðấng đã chọn ngươi."));
visuperobject.biblemap.put("b23_49_8", new visuperobject(23,49,8,"Ðức Giê-hô-va phán như vầy: Ta đã nhậm lời ngươi trong kỳ thuận tiện, đã vùa giúp ngươi trong ngày cứu rỗi. Ta sẽ gìn giữ ngươi, phó ngươi làm giao ước của dân, đặng lập lại xứ nầy và chia đất hoang vu làm sản nghiệp cho;"));
visuperobject.biblemap.put("b23_49_9", new visuperobject(23,49,9,"đặng bảo kẻ bị trói rằng: Hỡi ra, và bảo kẻ ở trong nơi tối tăm rằng: Hãy tỏ mình! Họ sẽ chăn thả trên đường, và đồng cỏ họ sẽ ở trên các núi trọi."));
visuperobject.biblemap.put("b23_49_10", new visuperobject(23,49,10,"Họ sẽ không đói không khát, hơi nắng chẳng đốt đến, khí nóng mặt trời chẳng phạm đến nữa; vì Ðấng thương xót họ sẽ dắt dẫn, đem họ đến gần suối nước."));
visuperobject.biblemap.put("b23_49_11", new visuperobject(23,49,11,"Ta sẽ làm cho mọi núi ta nên đường phẳng, và các đường cái ta sẽ được sửa sang."));
visuperobject.biblemap.put("b23_49_12", new visuperobject(23,49,12,"Nầy, những kẻ nầy sẽ đến từ nơi xa; kìa, những kẻ kia sẽ đến từ phương bắc, những kẻ nọ từ phương tây, và những kẻ khác từ xứ Si-ni."));
visuperobject.biblemap.put("b23_49_13", new visuperobject(23,49,13,"Hỡi trời, hãy hát! Hỡi đất, hãy vui mừng! Hỡi các núi, hãy lên tiếng hát xướng! Vì Ðức Giê-hô-va đã yên ủi dân Ngài, cũng thương xót kẻ khốn khó."));
visuperobject.biblemap.put("b23_49_14", new visuperobject(23,49,14,"Si-ôn từng nói rằng: Ðức Giê-hô-va đã lìa bỏ ta; Chúa đã quên ta."));
visuperobject.biblemap.put("b23_49_15", new visuperobject(23,49,15,"Ðờn bà há dễ quên con mình cho bú, không thương đến con trai ruột mình sao? Dầu đờn bà quên con mình, ta cũng chẳng quên ngươi."));
visuperobject.biblemap.put("b23_49_16", new visuperobject(23,49,16,"Nầy ta đã chạm ngươi trong lòng bàn tay ta; các tường thành ngươi thường ở trước mặt ta luôn."));
visuperobject.biblemap.put("b23_49_17", new visuperobject(23,49,17,"Con cái ngươi chóng về; kẻ phá hại hủy diệt ngươi sẽ ra khỏi giữa ngươi."));
visuperobject.biblemap.put("b23_49_18", new visuperobject(23,49,18,"Hãy liếc mắt xem xung quanh ngươi: những kẻ nầy đều nhóm lại và đến cùng ngươi! Ðức Giê-hô-va phán: Thật như ta hằng sống, ngươi sẽ mặc lấy những kẻ nầy như đồ trang sức, và dùng mà thắt đai như nàng dâu mới!"));
visuperobject.biblemap.put("b23_49_19", new visuperobject(23,49,19,"Thật, các nơi đổ nát, hoang vu, xứ bị phá hại của ngươi, nay có đông người ở thành ra chật hẹp quá. Những kẻ nuốt ngươi sẽ lánh xa ngươi."));
visuperobject.biblemap.put("b23_49_20", new visuperobject(23,49,20,"Con cái mà ngươi sẽ có, sau khi bị mất những đứa khác, sẽ nói vào tai ngươi rằng: Ðất đai chật hẹp quá cho tôi; hãy sắm chỗ cho tôi, đặng tôi ở đây."));
visuperobject.biblemap.put("b23_49_21", new visuperobject(23,49,21,"Chắc ngươi sẽ tự nói trong lòng rằng: Ai đã sanh sản cho ta những đứa nầy? Ta đã mất hết con cái; ta đã son sẻ, bị đày và lưu lạc? Vậy thì ai đã nuôi những đứa nầy? Nầy, ta đã ở một mình, những đứa nầy có bởi đâu?"));
visuperobject.biblemap.put("b23_49_22", new visuperobject(23,49,22,"Chúa Giê-hô-va phán như vầy: Nầy, ta sẽ giá tay trên các nước, dựng cờ ta trong các dân. Bấy giờ chúng sẽ bồng các con trai ngươi, vác các con gái ngươi trên vai mà đến."));
visuperobject.biblemap.put("b23_49_23", new visuperobject(23,49,23,"Các vua sẽ làm cha nuôi ngươi, các hoàng hậu sẽ làm mụ vú ngươi; họ sẽ cúi mặt sát đất ở trước mặt ngươi; liếm bụi dưới chơn ngươi; ngươi sẽ biết rằng ta là Ðức Giê-hô-va, và những kẻ trông cậy ta sẽ chẳng hổ thẹn."));
visuperobject.biblemap.put("b23_49_24", new visuperobject(23,49,24,"Vậy của cải bị người mạnh bạo cướp lấy, há ai giựt lại được sao? Người công bình bị bắt làm phu tù, há ai giải cứu được sao?"));
visuperobject.biblemap.put("b23_49_25", new visuperobject(23,49,25,"Song Ðức Giê-hô-va phán như vầy: Thật, những kẻ bị người mạnh bạo bắt lấy, sẽ được cứu thoát, mồi của người đáng sợ sẽ bị giựt lại. Vì ta sẽ chống cự kẻ đối địch ngươi, và chính ta sẽ giải cứu con cái ngươi."));
visuperobject.biblemap.put("b23_49_26", new visuperobject(23,49,26,"Ta sẽ làm cho kẻ hiếp đáp ngươi tự ăn thịt mình, say vì máu mình, như vì rượu mới; cả loài xác thịt sẽ biết ta, Ðức Giê-hô-va, là Ðấng Cứu ngươi, Ðấng Chuộc ngươi, tức là Ðấng Toàn năng của Gia-cốp!"));
visuperobject.biblemap.put("b23_50_1", new visuperobject(23,50,1,"Ðức Giê-hô-va phán như vầy: Chớ nào từ để mà ta đã để mẹ các ngươi ra ở đâu? Ai là người chủ nợ mà ta đã bán các ngươi cho? Nầy, các ngươi bị bán, là tại tội lỗi mình; mẹ các ngươi bị bỏ, là tại sự bội nghịch các ngươi."));
visuperobject.biblemap.put("b23_50_2", new visuperobject(23,50,2,"Cớ sao khi ta đến, chẳng thấy có người nào? Cớ sao khi ta gọi, chẳng có ai thưa lại? Tay ta há ngắn quá không chuộc được sao? hay là sức ta không đủ mà cứu được sao? Nầy, ta chỉ nạt một cái thì biển liền cạn. Ta biến sông thành đồng vắng; cá dưới sông vì khan nước phải chết khát, hóa ra hôi thối."));
visuperobject.biblemap.put("b23_50_3", new visuperobject(23,50,3,"Ta lấy sự tối tăm mặc cho các từng trời, và khoác cho một cái bao gai."));
visuperobject.biblemap.put("b23_50_4", new visuperobject(23,50,4,"Chúa Giê-hô-va đã ban cho ta cái lưỡi của người được dạy dỗ, hầu cho ta biết dùng lời nói nâng đỡ kẻ mệt mỏi. Ngài đánh thức tai ta để nghe lời Ngài dạy, như học trò vậy."));
visuperobject.biblemap.put("b23_50_5", new visuperobject(23,50,5,"Thật, Chúa Giê-hô-va đã mở tai ta, ta không trái nghịch, cũng không giựt lùi."));
visuperobject.biblemap.put("b23_50_6", new visuperobject(23,50,6,"Ta đã đưa lưng cho kẻ đánh ta, và đưa má cho kẻ nhổ râu ta; ai mắng hoặc giổ trên ta, ta chẳng hề che mặt."));
visuperobject.biblemap.put("b23_50_7", new visuperobject(23,50,7,"Chúa Giê-hô-va sẽ giúp ta, nên ta chẳng bị mắc cỡ; vậy ta làm cho mặt ta cứng như đá; vì biết mình sẽ chẳng có điều chi xấu hổ."));
visuperobject.biblemap.put("b23_50_8", new visuperobject(23,50,8,"Ðấng xưng ta công bình đã đến gần; ai dám kiện với ta? Hãy cùng ta đều đứng lên! Ai là kẻ đối địch ta? Hãy lại gần ta!"));
visuperobject.biblemap.put("b23_50_9", new visuperobject(23,50,9,"Thật, Chúa Giê-hô-va sẽ đến giúp ta: Ai định tội lỗi ta được? Nầy, hết thảy chúng nó sẽ cũ đi như áo, bị sâu cắn rách."));
visuperobject.biblemap.put("b23_50_10", new visuperobject(23,50,10,"Trong vòng các ngươi nào có ai kính sợ Ðức Giê-hô-va, và nghe tiếng của tôi tớ Ngài? Kẻ nào đi trong tối tăm và không có sự sáng thì hãy trông cậy danh Ðức Chúa Trời mình."));
visuperobject.biblemap.put("b23_50_11", new visuperobject(23,50,11,"Kìa, hết thảy các ngươi là kẻ thắp lửa và đốt đuốc vây lấy mình, thì hãy đi giữa ngọn lửa mình và giữa những đuốc mình đã đốt! Ấy là sự tay ta đã làm cho các ngươi, các ngươi sẽ nằm trong sự buồn bực!"));
visuperobject.biblemap.put("b23_51_1", new visuperobject(23,51,1,"Các ngươi là kẻ theo sự công bình, là kẻ tìm Ðức Giê-hô-va, hãy nghe ta! Khá nhìn xem vầng đá mà các ngươi đã được đục ra, và cái lỗ của hang mà các ngươi đã được đào lên!"));
visuperobject.biblemap.put("b23_51_2", new visuperobject(23,51,2,"Hãy nghĩ đến tổ ngươi là Áp-ra-ham, cùng Sa-ra, là người đã sanh sản các ngươi; ta đã gọi Áp-ra-ham khi người chỉ có một mình; ta đã ban phước cho người nên nhiều."));
visuperobject.biblemap.put("b23_51_3", new visuperobject(23,51,3,"Vì Ðức Giê-hô-va đã yên ủi Si-ôn; Ngài đã yên ủi mọi nơi đổ nát của nó. Ngài đã khiến đồng vắng nên như vườn Ê-đen, nơi sa mạc nên như vườn Ðức Giê-hô-va; giữa vườn ấy sẽ có sự vui vẻ, mừng rỡ, tạ ơn, và tiếng ca hát."));
visuperobject.biblemap.put("b23_51_4", new visuperobject(23,51,4,"Hỡi dân ta, hãy chăm chỉ mà nghe ta; hỡi nước ta, hãy lắng tai cho ta! Vì sẽ có luật pháp ra từ ta, và ta sẽ lập sự công bình làm sự sáng cho các dân."));
visuperobject.biblemap.put("b23_51_5", new visuperobject(23,51,5,"Sự công bình của ta đã gần; sự cứu rỗi của ta đã ra, cánh tay ta sẽ xét đoán các dân. Các cù lao sẽ trông đợi ta, và chúng nó sẽ nhờ cậy cánh tay ta."));
visuperobject.biblemap.put("b23_51_6", new visuperobject(23,51,6,"Hãy ngước mắt lên các từng trời và cúi xem dưới đất; vì các từng trời sẽ tan ra như khói, đất sẽ cũ như áo, dân cư trên đất sẽ chết như ruồi: nhưng sự cứu rỗi của ta còn đời đời, sự công bình của ta chẳng hề bị bỏ đi."));
visuperobject.biblemap.put("b23_51_7", new visuperobject(23,51,7,"Hỡi dân biết điều công nghĩa, ghi luật pháp ta trong lòng, hãy nghe ta! Chớ e người ta chê bai, đừng sợ họ nhiếc móc."));
visuperobject.biblemap.put("b23_51_8", new visuperobject(23,51,8,"Vì sâu sẽ cắn nuốt họ như cắn áo, mọt sẽ ăn họ như ăn lông chiên; nhưng sự công bình của ta sẽ còn mãi, sự cứu rỗi của ta còn đến muôn đời."));
visuperobject.biblemap.put("b23_51_9", new visuperobject(23,51,9,"Hỡi cánh tay Ðức Giê-hô-va, hãy thức dậy, thức dậy! Hãy mặc lấy sức mạnh đi! Hãy thức dậy như ngày xưa, như các đời thượng cổ! Há chẳng phải Ngài là Ðấng đã phân thây Ra-háp và đâm con quái vật lớn sao?"));
visuperobject.biblemap.put("b23_51_10", new visuperobject(23,51,10,"Há chẳng phải Ngài là Ðấng đã làm cạn biển, tắt nước vực lớn, làm cho biển sâu ra một con đường, hầu cho dân đã được chuộc đi qua sao?"));
visuperobject.biblemap.put("b23_51_11", new visuperobject(23,51,11,"Dân mà Ðức Giê-hô-va đã chuộc sẽ trở về, sẽ hát mà đến Si-ôn. Sự hỉ lạc vô cùng sẽ đội trên đầu họ, họ sẽ được vui vẻ mừng rỡ; sự buồn bực than vãn sẽ tránh đi."));
visuperobject.biblemap.put("b23_51_12", new visuperobject(23,51,12,"Ta, chính ta, là Ðấng yên ủi các ngươi. Ngươi là ai, mà sợ loài người hay chết, sợ con trai loài người là kẻ sẽ trở nên như cỏ?"));
visuperobject.biblemap.put("b23_51_13", new visuperobject(23,51,13,"mà lại quên Ðức Giê-hô-va, Ðấng tạo ngươi, đã giương các từng trời và lập nền đất, và trọn ngày cứ run sợ về cơn giận của kẻ ức hiếp khi nó toan hủy diệt ngươi? Vậy thì bây giờ chớ nào cơn giận của kẻ ức hiếp ấy ở đâu?"));
visuperobject.biblemap.put("b23_51_14", new visuperobject(23,51,14,"Kẻ bị bắt đem đi sẽ chóng được tha ra; sẽ chẳng chết trong hầm, và cũng không thiếu lương thực."));
visuperobject.biblemap.put("b23_51_15", new visuperobject(23,51,15,"Vì ta là Giê-hô-va Ðức Chúa Trời ngươi, tức là Ðấng làm cho biển động, sóng bổ ầm ầm. Ðức Giê-hô-va vạn quân là danh Ngài."));
visuperobject.biblemap.put("b23_51_16", new visuperobject(23,51,16,"Ta đã để lời nói ta trong miệng ngươi, và cho ngươi núp dưới bóng bàn tay ta, đặng dựng các từng trời và lập nên đất, cùng nói với Si-ôn rằng: Ngươi là dân ta!"));
visuperobject.biblemap.put("b23_51_17", new visuperobject(23,51,17,"Hỡi Giê-ru-sa-lem, hãy thức dậy, thức dậy, và đứng lên! Ngươi đã uống chén thạnh nộ từ tay Ðức Giê-hô-va; đã uống cạn chén xoàng-ba, uống cho tới cặn."));
visuperobject.biblemap.put("b23_51_18", new visuperobject(23,51,18,"Trong vòng các con trai mà Giê-ru-sa-lem đã sanh ra, chẳng một đứa nào để dắt nó; trong vòng các con trai nó đã nuôi, chẳng một đứa nào đến cầm lấy tay."));
visuperobject.biblemap.put("b23_51_19", new visuperobject(23,51,19,"Hai sự nầy, tức là diệt vong và hủy phá, đói kém và gươm đao, đã đến cho ngươi, mà ai là kẻ sầu não vì ngươi? Ta sẽ lấy gì yên ủi ngươi?"));
visuperobject.biblemap.put("b23_51_20", new visuperobject(23,51,20,"Các con trai ngươi đều hao mòn, nằm ngổn ngang nơi ngã ba đường phố, như con nai mắc trong lưới, đầy cơn giận của Ðức Giê-hô-va và sự quở trách của Ðức Chúa Trời ngươi!"));
visuperobject.biblemap.put("b23_51_21", new visuperobject(23,51,21,"Vậy, bây giờ, hỡi ngươi là kẻ khốn nạn, là kẻ say nhưng không vì rượu, hãy nghe điều nầy."));
visuperobject.biblemap.put("b23_51_22", new visuperobject(23,51,22,"Chúa Giê-hô-va ngươi, Ðức Chúa Trời ngươi, là Ðấng binh vực dân mình, phán như vầy: Nầy, ta đã lấy lại chén xoàng ba từ tay ngươi, tức là cặn của chén thạnh nộ ta, rày về sau ngươi sẽ không uống nó nữa."));
visuperobject.biblemap.put("b23_51_23", new visuperobject(23,51,23,"Ta sẽ để chén ấy trong tay các kẻ hà hiếp ngươi, tức là các kẻ bảo ngươi rằng: Hãy cúi xuống, đặng chúng ta bước ngang qua! Rồi ngươi cong lưng sát đất, như con đường cho người ta qua lại."));
visuperobject.biblemap.put("b23_52_1", new visuperobject(23,52,1,"Hỡi Si-ôn, hãy thức dậy, thức dậy, mặc lấy sức mạnh ngươi! Hỡi Giê-ru-sa-lem, là thành thánh, hãy mặc lấy áo đẹp! Vì rày về sau kẻ không chịu phép cắt bì và kẻ ô uế sẽ không vào nơi ngươi nữa."));
visuperobject.biblemap.put("b23_52_2", new visuperobject(23,52,2,"Hỡi Giê-ru-sa-lem, hãy chổi dậy, dũ bụi bặm đi, và ngồi lên; hỡi con gái Si-ôn, là kẻ phu tù, hãy cởi trói nơi cổ ngươi."));
visuperobject.biblemap.put("b23_52_3", new visuperobject(23,52,3,"Vì Ðức Giê-hô-va phán như vầy: Các ngươi đã bị bán những không; cũng sẽ được chuộc lại chẳng bằng tiền bạc."));
visuperobject.biblemap.put("b23_52_4", new visuperobject(23,52,4,"Chúa Giê-hô-va phán như vầy: Xưa kia dân ta xuống Ê-díp-tô đặng trú ngụ; song người A-si-ri vô-cớ mà hà hiếp nó."));
visuperobject.biblemap.put("b23_52_5", new visuperobject(23,52,5,"Ðức Giê-hô-va phán: Bây giờ, ta làm gì đây? Vì dân ta đã vô cớ bị đày đi. Ðức Giê-hô-va phán: Những kẻ cai trị nó cất tiếng van la cả ngày, nói phạm đến danh ta không thôi."));
visuperobject.biblemap.put("b23_52_6", new visuperobject(23,52,6,"Vậy nên dân ta sẽ biết danh ta; thật, trong ngày đó, nó sẽ biết rằng chính ta là Ðấng phán rằng: Nầy, ta đây!"));
visuperobject.biblemap.put("b23_52_7", new visuperobject(23,52,7,"Những kẻ đem tin tốt, rao sự bình an, đem tin tốt về phước lành, rao sự cứu chuộc, bảo Si-ôn rằng: Ðức Chúa Trời ngươi trị vì, chơn của những kẻ ấy trên các núi xinh đẹp là dường nào!"));
visuperobject.biblemap.put("b23_52_8", new visuperobject(23,52,8,"Tiếng những kẻ canh của ngươi! họ cất tiếng, cùng nhau hát xướng; vì họ sẽ thấy mắt đối mắt khi Ðức Giê-hô-va trở về Si-ôn."));
visuperobject.biblemap.put("b23_52_9", new visuperobject(23,52,9,"Hỡi những nơi hoang vu của Giê-ru-sa-lem, hãy trổi giọng hát mừng chung rập. Vì Ðức Giê-hô-va đã yên ủi dân Ngài, đã chuộc lại Giê-ru-sa-lem."));
visuperobject.biblemap.put("b23_52_10", new visuperobject(23,52,10,"Ðức Giê-hô-va đã tỏ trần cánh tay thánh Ngài trước mắt mọi nước; mọi đầu cùng đất đã thấy sự cứu rỗi của Ðức Chúa Trời chúng ta!"));
visuperobject.biblemap.put("b23_52_11", new visuperobject(23,52,11,"Các ngươi hãy đi, hãy đi, đi ra khỏi đó! Ðừng động đến đồ ô uế, hãy ra khỏi giữa nó. Các ngươi là kẻ mang khí dụng Ðức Giê-hô-va, hãy làm cho sạch mình!"));
visuperobject.biblemap.put("b23_52_12", new visuperobject(23,52,12,"Vả, các ngươi đi ra không cần phải vội vàng, cũng không cần phải trốn tránh; vì Ðức Giê-hô-va sẽ đi trước mặt các ngươi, và Ðức Chúa Trời của Y-sơ-ra-ên đi sau gìn giữ ngươi."));
visuperobject.biblemap.put("b23_52_13", new visuperobject(23,52,13,"Nầy, tôi tớ ta sẽ làm cách khôn ngoan; sẽ được tôn lên, dấy lên, và rất cao trọng."));
visuperobject.biblemap.put("b23_52_14", new visuperobject(23,52,14,"Như nhiều kẻ thấy ngươi mà lấy làm lạ (mặt mày người xài-xể lắm hơn kẻ nào khác, hình dung xài-xể hơn con trai loài người),"));
visuperobject.biblemap.put("b23_52_15", new visuperobject(23,52,15,"thì cũng vậy người sẽ vảy rửa nhiều dân, và các vua ngậm miệng lại vì người; vì họ sẽ thấy điều chưa ai nói cho mình, và sẽ hiểu biết điều mình chưa hề nghe."));
visuperobject.biblemap.put("b23_53_1", new visuperobject(23,53,1,"Ai tin điều đã rao truyền cho chúng ta, và cánh tay Ðức Giê-hô-va đã được tỏ ra cho ai?"));
visuperobject.biblemap.put("b23_53_2", new visuperobject(23,53,2,"Người đã lớn lên trước mặt Ngài như một cái chồi, như cái rễ ra từ đất khô. Người chẳng có hình dung, chẳng có sự đẹp đẽ; khi chúng ta thấy người, không có sự tốt đẹp cho chúng ta ưa thích được."));
visuperobject.biblemap.put("b23_53_3", new visuperobject(23,53,3,"Người đã bị người ta khinh dể và chán bỏ, từng trải sự buồn bực, biết sự đau ốm, bị khinh như kẻ mà người ta che mặt chẳng thèm xem; chúng ta cũng chẳng coi người ra gì."));
visuperobject.biblemap.put("b23_53_4", new visuperobject(23,53,4,"Thật người đã mang sự đau ốm của chúng ta, đã gánh sự buồn bực của chúng ta; mà chúng ta lại tưởng rằng người đã bị Ðức Chúa Trời đánh và đập, và làm cho khốn khổ."));
visuperobject.biblemap.put("b23_53_5", new visuperobject(23,53,5,"Nhưng người đã vì tội lỗi chúng ta mà bị vết, vì sự gian ác chúng ta mà bị thương. Bởi sự sửa phạt người chịu chúng ta được bình an, bởi lằn roi người chúng ta được lành bịnh."));
visuperobject.biblemap.put("b23_53_6", new visuperobject(23,53,6,"Chúng ta thảy đều như chiên đi lạc, ai theo đường nấy; Ðức Giê-hô-va đã làm cho tội lỗi của hết thảy chúng ta đều chất trên người."));
visuperobject.biblemap.put("b23_53_7", new visuperobject(23,53,7,"Người bị hiếp đáp, nhưng khi chịu sự khốn khổ chẳng hề mở miệng. Như chiên con bị dắt đến hàng làm thịt, như chiên câm ở trước mặt kẻ hớt lông, người chẳng từng mở miệng."));
visuperobject.biblemap.put("b23_53_8", new visuperobject(23,53,8,"Bởi sự ức hiếp, và xử đoán, nên người đã bị cất lấy; trong những kẻ đồng thời với người có ai suy xét rằng người đã bị dứt khỏi đất người sống, là vì cớ tội lỗi dân ta đáng chịu đánh phạt?"));
visuperobject.biblemap.put("b23_53_9", new visuperobject(23,53,9,"Người ta đã đặt mồ người với những kẻ ác, nhưng khi chết, người được chôn với kẻ giàu; dầu người chẳng hề làm điều hung dữ và chẳng có sự dối trá trong miệng."));
visuperobject.biblemap.put("b23_53_10", new visuperobject(23,53,10,"Ðức Giê-hô-va lấy làm vừa ý mà làm tổn thương người, và khiến gặp sự đau ốm. Sau khi đã dâng mạng sống người làm tế chuộc tội, người sẽ thấy dòng dõi mình; những ngày người sẽ thêm dài ra, và ý chỉ Ðức Giê-hô-va nhờ tay người được thạnh vượng."));
visuperobject.biblemap.put("b23_53_11", new visuperobject(23,53,11,"Người sẽ thấy kết quả của sự khốn khổ linh hồn mình, và lấy làm thỏa mãn. Tôi tớ công bình của ta sẽ lấy sự thông biết về mình làm cho nhiều người được xưng công bình; và người sẽ gánh lấy tội lỗi họ."));
visuperobject.biblemap.put("b23_53_12", new visuperobject(23,53,12,"Vậy nên ta sẽ chia phần cho người đồng với người lớn. Người sẽ chia của bắt với những kẻ mạnh, vì người đã đổ mạng sống mình cho đến chết, đã bị kể vào hàng kẻ dữ, đã mang lấy tội lỗi nhiều người, và cầu thay cho những kẻ phạm tội."));
visuperobject.biblemap.put("b23_54_1", new visuperobject(23,54,1,"Hỡi kẻ son sẻ, không sanh đẻ kia, hãy ca hát. Hỡi ngươi là kẻ chưa biết sự đau đớn sanh nở, hãy hát xướng reo mừng, vì con cái của vợ bị bỏ sẽ đông hơn con cái của đờn bà có chồng, Ðức Giê-hô-va phán vậy."));
visuperobject.biblemap.put("b23_54_2", new visuperobject(23,54,2,"Hãy mở rộng nơi trại ngươi, giương màn chỗ ngươi ở. Chớ để chật hẹp; hãy giăng dây cho dài, đóng nọc cho chắc!"));
visuperobject.biblemap.put("b23_54_3", new visuperobject(23,54,3,"Vì ngươi sẽ lan rộng ra bên hữu và bên tả; dòng dõi ngươi sẽ được các nước làm cơ nghiệp, và sẽ làm cho các thành hoang vu đông dân cư."));
visuperobject.biblemap.put("b23_54_4", new visuperobject(23,54,4,"Ðừng sợ chi; vì ngươi sẽ chẳng bị hổ thẹn. Chớ mắc cỡ; vì ngươi chẳng còn xấu hổ nữa. Ngươi sẽ quên điều sỉ nhục lúc còn thơ ấu, và đừng nhớ lại sự nhơ nhuốc trong khi mình đương góa bụi."));
visuperobject.biblemap.put("b23_54_5", new visuperobject(23,54,5,"Vì chồng ngươi tức là Ðấng đã tạo thành ngươi; danh Ngài là Ðức Giê-hô-va vạn quân. Ðấng chuộc ngươi tức là Ðấng Thánh của Y-sơ-ra-ên, sẽ được xưng là Ðức Chúa Trời của cả đất."));
visuperobject.biblemap.put("b23_54_6", new visuperobject(23,54,6,"Ðức Giê-hô-va đã gọi ngươi, như gọi vợ đã bị bỏ và phiền rầu trong lòng, như vợ cưới lúc còn trẻ và đã bị để, Ðức Chúa Trời ngươi phán vậy."));
visuperobject.biblemap.put("b23_54_7", new visuperobject(23,54,7,"Ta đã bỏ ngươi trong một lát; nhưng ta sẽ lấy lòng thương xót cả thể mà thâu ngươi lại."));
visuperobject.biblemap.put("b23_54_8", new visuperobject(23,54,8,"Trong cơn nóng giận, ta ẩn mặt với ngươi một lúc, những vì lòng nhơn từ vô cùng, ta sẽ thương đến ngươi, Ðấng Cứu chuộc ngươi là Ðức Giê-hô-va phán vậy."));
visuperobject.biblemap.put("b23_54_9", new visuperobject(23,54,9,"Ðiều đó cũng như nước lụt về đời Nô-ê, như khi ta thề rằng nước lụt của Nô-ê sẽ không tràn ra trên đất nữa; thì ta cũng thề rằng ta sẽ không giận ngươi nữa, và cũng không trách phạt ngươi."));
visuperobject.biblemap.put("b23_54_10", new visuperobject(23,54,10,"Dầu núi dời, dầu đồi chuyển, nhưng lòng nhơn từ ta đối với ngươi chẳng dời khỏi ngươi, lời giao ước bình an của ta chẳng chuyển, Ðức Giê-hô-va, là Ðấng thương xót ngươi, phán vậy."));
visuperobject.biblemap.put("b23_54_11", new visuperobject(23,54,11,"Hỡi ngươi đương cùng khốn, bị bão lung lay, mất sự yên ủi, nầy, ta sẽ lấy đơn sa lát đá ngươi, sẽ lập nên ngươi bằng thanh ngọc."));
visuperobject.biblemap.put("b23_54_12", new visuperobject(23,54,12,"Ta sẽ lấy hồng ngọc làm tường xép ngươi, lấy san hô làm các cửa ngươi, và cả bờ cõi ngươi đều làm bằng đá quí."));
visuperobject.biblemap.put("b23_54_13", new visuperobject(23,54,13,"Hết thảy con cái ngươi sẽ được Ðức Giê-hô-va dạy dỗ, và sự bình an của con cái ngươi sẽ lớn."));
visuperobject.biblemap.put("b23_54_14", new visuperobject(23,54,14,"Ngươi sẽ được đứng vững trong sự công bình, tránh khỏi sự hiếp đáp, vì chẳng sợ chi hết. Ngươi sẽ xa sự kinh hãi, vì nó chẳng hề đến gần ngươi."));
visuperobject.biblemap.put("b23_54_15", new visuperobject(23,54,15,"Nầy, nếu người ta lập mưu hại ngươi, ấy chẳng phải bởi ta. Kẻ nào lập mưu hại ngươi sẽ vì cớ ngươi mà sa ngã."));
visuperobject.biblemap.put("b23_54_16", new visuperobject(23,54,16,"Nầy, chính ta đã tạo thành người thợ rèn thổi lửa than, chế ra khí dụng để dùng; và cũng chính ta đã tạo thành kẻ phá diệt đặng làm sự phá diệt."));
visuperobject.biblemap.put("b23_54_17", new visuperobject(23,54,17,"Phàm binh khí chế ra nghịch cùng ngươi sẽ chẳng thạnh lợi, và ngươi sẽ định tội mọi lưỡi dấy lên để xét đoán ngươi. Ðức Giê-hô-va phán: Ấy là phần cơ nghiệp của các tôi tớ Ðức Giê-hô-va, và sự công bình bởi ta ban cho họ."));
visuperobject.biblemap.put("b23_55_1", new visuperobject(23,55,1,"Hỡi những kẻ nào khát, hãy đến suối nước! Và người nào không có tiền bạc, hãy đến, mua mà ăn! Hãy đến, mua rượu và sữa mà không cần tiền, không đòi giá."));
visuperobject.biblemap.put("b23_55_2", new visuperobject(23,55,2,"Sao các ngươi trả tiền để mua đồ không phải là bánh? Sao các ngươi đem công lao mình đổi lấy vật chẳng làm cho no? Hãy chăm chỉ nghe ta, hãy ăn của ngon, và cho linh hồn các ngươi vui thích trong của béo."));
visuperobject.biblemap.put("b23_55_3", new visuperobject(23,55,3,"Hãy nghiêng tai, và đến cùng ta; hãy nghe ta, thì linh hồn các ngươi được sống. Ta sẽ lập với các ngươi một giao ước đời đời, tức là sự nhơn từ chắc thật đã hứa cùng Ða-vít."));
visuperobject.biblemap.put("b23_55_4", new visuperobject(23,55,4,"Nầy, ta đã lập người lên làm chứng kiến cho các nước, làm quan trưởng và quan tướng cho muôn dân."));
visuperobject.biblemap.put("b23_55_5", new visuperobject(23,55,5,"Nầy, ngươi sẽ kêu gọi nước mà ngươi chưa hề biết, và nước chưa hề biết ngươi sẽ chạy đến cùng ngươi, vì cớ Giê-hô-va Ðức Chúa Trời ngươi, là Ðấng Thánh của Y-sơ-ra-ên, là Ðấng đã làm vinh hiển ngươi."));
visuperobject.biblemap.put("b23_55_6", new visuperobject(23,55,6,"Hãy tìm kiếm Ðức Giê-hô-va đang khi mình gặp được; hãy kêu cầu đang khi Ngài ở gần!"));
visuperobject.biblemap.put("b23_55_7", new visuperobject(23,55,7,"Kẻ ác khá bỏ đường mình, người bất nghĩa khá bỏ các ý tưởng; hãy trở lại cùng Ðức Giê-hô-va, Ngài sẽ thương xót cho, hãy đến cùng Ðức Chúa Trời chúng ta, vì Ngài tha thứ dồi dào."));
visuperobject.biblemap.put("b23_55_8", new visuperobject(23,55,8,"Ðức Giê-hô-va phán: Ý tưởng ta chẳng phải ý tưởng các ngươi, đường lối các ngươi chẳng phải đường lối ta."));
visuperobject.biblemap.put("b23_55_9", new visuperobject(23,55,9,"Vì các từng trời cao hơn đất bao nhiêu, thì đường lối ta cao hơn đường lối các ngươi, ý tưởng ta cao hơn ý tưởng các ngươi cũng bấy nhiêu."));
visuperobject.biblemap.put("b23_55_10", new visuperobject(23,55,10,"Vả, như mưa và tuyết xuống từ trên trời và không trở lại nữa, mà đượm nhuần đất đai, làm cho sanh ra và kết nụ, đặng có giống cho kẻ giao, có bánh cho kẻ ăn,"));
visuperobject.biblemap.put("b23_55_11", new visuperobject(23,55,11,"thì lời nói của ta cũng vậy, đã ra từ miệng ta, thì chẳng trở về luống nhưng, mà chắc sẽ làm trọn điều ta muốn, thuận lợi công việc ta đã sai khiến nó."));
visuperobject.biblemap.put("b23_55_12", new visuperobject(23,55,12,"Vì các ngươi sẽ đi ra vui vẻ, được đưa đi trong sự bình an. Trước mặt các ngươi, núi và đồi sẽ trổi tiếng ca hát, mọi cây cối ngoài đồng sẽ vỗ tay."));
visuperobject.biblemap.put("b23_55_13", new visuperobject(23,55,13,"Cây tùng sẽ mọc lên thay cho bụi gai, và cây sim sẽ lớn lên thay cho gai gốc; điều đó sẽ làm cho biết danh Ðức Giê-hô-va, và là một dấu đời đời không hề tiệt diệt."));
visuperobject.biblemap.put("b23_56_1", new visuperobject(23,56,1,"Ðức Giê-hô-va phán như vầy: Hãy giữ điều chánh trực, và làm sự công bình; vì sự cứu rỗi của ta gần đến, sự công bình của ta sắp được bày tỏ."));
visuperobject.biblemap.put("b23_56_2", new visuperobject(23,56,2,"Phước thay cho người làm điều đó, và con người cầm vững sự đó, giữ ngày Sa-bát đặng đừng làm ô uế, cấm tay mình không làm một điều ác nào!"));
visuperobject.biblemap.put("b23_56_3", new visuperobject(23,56,3,"Người dân ngoại liên hiệp cùng Ðức Giê-hô-va chớ nên nói rằng: Ðức Giê-hô-va chắc sẽ biệt ta ra khỏi dân Ngài. Kẻ hoạn cũng chớ nên nói rằng: Nầy, ta là cây khô."));
visuperobject.biblemap.put("b23_56_4", new visuperobject(23,56,4,"Vì Ðức Giê-hô-va phán như vầy: Những kẻ hoạn hay giữ các ngày Sa-bát ta, lựa điều đẹp lòng ta, cầm vững lời giao ước ta,"));
visuperobject.biblemap.put("b23_56_5", new visuperobject(23,56,5,"thì ta sẽ ban cho họ tại trong nhà ta và trong tường ta một chỗ, và một danh tốt hơn danh của con trai con gái; ta lại sẽ ban cho họ một danh đời đời chẳng hề dứt đi."));
visuperobject.biblemap.put("b23_56_6", new visuperobject(23,56,6,"Các người dân ngoại về cùng Ðức Giê-hô-va, đặng hầu việc Ngài, đặng yêu mến danh Ðức Giê-hô-va, đặng làm tôi tớ Ngài; tức là hết thảy những kẻ giữ ngày Sa-bát cho khỏi ô uế, và cầm vững lời giao ước ta,"));
visuperobject.biblemap.put("b23_56_7", new visuperobject(23,56,7,"thì ta sẽ đem họ vui mừng trong nhà cầu nguyện ta. Của lễ thiêu và hi sinh họ dâng trên bàn thờ ta, sẽ được nhận lấy; vì nhà ta sẽ gọi là nhà cầu nguyện cho mọi dân tộc."));
visuperobject.biblemap.put("b23_56_8", new visuperobject(23,56,8,"Chúa Giê-hô-va, Ðấng nhóm dân tan lạc của Y-sơ-ra-ên, phán như vầy: Dân nó đã được nhóm lại rồi, ta sẽ còn nhóm các dân khác lại về cùng nó."));
visuperobject.biblemap.put("b23_56_9", new visuperobject(23,56,9,"Hỡi loài thú đồng, loài thú rừng, hết thảy hãy đến mà cắn-nuốt."));
visuperobject.biblemap.put("b23_56_10", new visuperobject(23,56,10,"Những kẻ canh giữ của Y-sơ-ra-ên đều là đui mù, không biết chi hết. Chúng nó thảy đều là chó câm, chẳng biết sủa, đều chiêm bao, nằm sóng sượt, và ham ngủ;"));
visuperobject.biblemap.put("b23_56_11", new visuperobject(23,56,11,"lại là chó mê ăn, không biết no. Ấy là những kẻ chăn chiên chẳng khôn sáng; mọi người theo đường riêng mình, mỗi người tìm tư lợi mình, người nào cũng vậy."));
visuperobject.biblemap.put("b23_56_12", new visuperobject(23,56,12,"Nó nói rằng: Hãy đến, ta sẽ lấy rượu, chúng ta say vì rượu mạnh; ngày mai sẽ cũng như ngày nay, trọng thể quá bội."));
visuperobject.biblemap.put("b23_57_1", new visuperobject(23,57,1,"Người công bình chết, chẳng ai để ý đến; người nhơn đức bị cất đi, chẳng ai suy nghĩ rằng người công bình bị cất đi khỏi tai vạ hầu đến."));
visuperobject.biblemap.put("b23_57_2", new visuperobject(23,57,2,"Người vào trong sự bình an. Mỗi người đi trong đường ngay thẳng, thì được an nghỉ nơi giường mình."));
visuperobject.biblemap.put("b23_57_3", new visuperobject(23,57,3,"Nhưng các ngươi, là con trai của bà bóng, dòng dõi của kẻ gian dâm và người đĩ thõa kia, hãy lại gần đây!"));
visuperobject.biblemap.put("b23_57_4", new visuperobject(23,57,4,"Các ngươi nhạo cười ai? Các ngươi há miệng rộng và le lưỡi nghịch cùng ai? Các ngươi há chẳng phải con cái bội nghịch, dòng dõi nói dối sao?"));
visuperobject.biblemap.put("b23_57_5", new visuperobject(23,57,5,"Các ngươi hành dâm với nhau trong cây dẽ, dưới cây rậm, giết con cái nơi trũng, dưới lỗ nẻ vầng đá!"));
visuperobject.biblemap.put("b23_57_6", new visuperobject(23,57,6,"Những đá bóng láng nơi khe suối, tức là phần riêng ngươi; thật, đó là phần ngươi; nên ngươi làm lễ quán và dâng của lễ chay cho những đá đó! Ta há chẳng giận về những sự đó sao?"));
visuperobject.biblemap.put("b23_57_7", new visuperobject(23,57,7,"Ngươi đặt giường trên núi rất cao, và lên đó đặng dâng của lễ."));
visuperobject.biblemap.put("b23_57_8", new visuperobject(23,57,8,"Ngươi đặt dấu ghi đằng sau cửa và sau các trụ cửa; vì ngươi đã trần mình cho kẻ khác và leo lên; ngươi đã làm rộng giường mình và lập giao ước với chúng nó. Ngươi lại ngó xem giường chúng nó và ưa thích!"));
visuperobject.biblemap.put("b23_57_9", new visuperobject(23,57,9,"Ngươi đã đem dầu và gia thêm hương liệu dân cho vua kia; đã sai sứ giả mình đi phương xa; hạ mình xuống đến âm phủ!"));
visuperobject.biblemap.put("b23_57_10", new visuperobject(23,57,10,"Ngươi đã mệt nhọc vì đường dài; mà chưa từng nói rằng: Ấy là vô ích! Ngươi đã thấy sức lực mình được phấn chấn, cho nên ngươi chẳng mòn mỏi."));
visuperobject.biblemap.put("b23_57_11", new visuperobject(23,57,11,"Vậy thì ngươi kiêng ai? ngươi sợ ai, nên mới nói dối, nên nới không nhớ đến ta, và không lo đến sự đó? Có phải tại lâu nay ta làm thinh mãi, nên ngươi không kính sợ ta chăng?"));
visuperobject.biblemap.put("b23_57_12", new visuperobject(23,57,12,"Nầy, chính ta sẽ rao sự công bình ngươi, mọi việc ngươi làm đều là không ích cho ngươi."));
visuperobject.biblemap.put("b23_57_13", new visuperobject(23,57,13,"Khi ngươi sẽ kêu, hết thảy các thần mà ngươi đã nhóm họp hãy giải cứu ngươi! Gió sẽ đùa các thần ấy đi, một cái thở là làm mất hết thảy. Nhưng kẻ nào ẩn náu nơi ta, sẽ hưởng đất nầy, và được hòn núi thánh của ta làm cơ nghiệp."));
visuperobject.biblemap.put("b23_57_14", new visuperobject(23,57,14,"Người sẽ nói rằng: Hãy đắp đường, hãy đắp đường! hãy ban cho bằng! hãy cất lấy sự ngăn trở khỏi đường dân ta!"));
visuperobject.biblemap.put("b23_57_15", new visuperobject(23,57,15,"Ðấng cao cả, ở nơi đời đời vô cùng, danh Ngài là Thánh, có phán như vầy: Ta ngự trong nơi cao và thánh, với người có lòng ăn năn đau đớn và khiêm nhường, đặng làm tươi tỉnh thần linh của những kẻ khiêm nhường, và làm tươi tỉnh lòng người ăn năn đau đớn."));
visuperobject.biblemap.put("b23_57_16", new visuperobject(23,57,16,"Ta chẳng muốn cãi lẽ đời đời, cũng chẳng tức giận mãi mãi; vì thần linh sẽ mòn mỏi trước mặt ta, và các linh hồn mà ta đã dựng nên cũng vậy."));
visuperobject.biblemap.put("b23_57_17", new visuperobject(23,57,17,"Ấy là vì tội tham lam của nó mà ta giận, và đánh nó. Trong cơn giận, ta đã ẩn mặt ta với nó; nhưng nó cứ trở lui đi, theo con đường của lòng mình."));
visuperobject.biblemap.put("b23_57_18", new visuperobject(23,57,18,"Ta đã xem thấy đường lối nó, ta sẽ chữa lành cho; sẽ dắt đưa và thưởng cho nó sự yên ủi, cùng cho những kẻ lo buồn với nó."));
visuperobject.biblemap.put("b23_57_19", new visuperobject(23,57,19,"Ðức Giê-hô-va phán rằng: Ta dựng nên trái của môi miếng: bình an, bình an cho kẻ ở xa cùng cho kẻ ở gần; ta sẽ chữa lành kẻ ấy."));
visuperobject.biblemap.put("b23_57_20", new visuperobject(23,57,20,"Song những kẻ ác giống như biển đương động, không yên lặng được, thì nước nó chảy ra bùn lầy."));
visuperobject.biblemap.put("b23_57_21", new visuperobject(23,57,21,"Ðức Chúa Trời ta đã phán: Những kẻ gian ác chẳng hưởng sự bình an."));
visuperobject.biblemap.put("b23_58_1", new visuperobject(23,58,1,"Hãy kêu to lên, đừng dứt; hãy cất tiếng lên như cái loa; rao bảo tội lỗi dân ta cho nó, và rao bảo sự gian ác nhà Gia-cốp cho nhà ấy!"));
visuperobject.biblemap.put("b23_58_2", new visuperobject(23,58,2,"Ngày ngày họ tìm kiếm ta và muốn biết đường lối ta; như dân đã theo sự công bình và chưa từng bỏ luật pháp của Ðức Chúa Trời mình! Họ cầu hỏi ta sự đoán xét công bình; và vui lòng gần gủi Ðức Chúa Trời,"));
visuperobject.biblemap.put("b23_58_3", new visuperobject(23,58,3,"mà rằng: Sao chúng tôi kiêng ăn mà Chúa chẳng đoái xem? sao chúng tôi chịu dằn lòng mà Chúa chẳng biết đến? Nầy, trong ngày các ngươi kiêng ăn, cũng cứ tìm sự đẹp ý mình, và làm khổ cho kẻ làm thuê."));
visuperobject.biblemap.put("b23_58_4", new visuperobject(23,58,4,"Thật, các ngươi kiêng ăn để tìm sự cãi cọ tranh cạnh, đến nỗi nắm tay đánh nhau cộc cằn; các ngươi kiêng ăn trong ngày như vậy, thì tiếng các ngươi chẳng được nghe thấu nơi cao."));
visuperobject.biblemap.put("b23_58_5", new visuperobject(23,58,5,"Ðó há phải là sự kiêng ăn mà ta chọn lựa, há phải là ngày người ta dằn lòng mình đâu? Cúi đầu như cây sậy, nằm trên bao gai và trên tro, đó há phải điều ngươi gọi là kiêng ăn, là ngày đẹp lòng Ðức Giê-hô-va sao?"));
visuperobject.biblemap.put("b23_58_6", new visuperobject(23,58,6,"Sự kiêng ăn mà ta chọn lựa, há chẳng phải là bẻ những xiềng hung ác, mở những trói của ách, thả cho kẻ bị ức hiếp được tự do, bẻ gãy mọi ách, hay sao?"));
visuperobject.biblemap.put("b23_58_7", new visuperobject(23,58,7,"Há chẳng phải là chia bánh cho kẻ đói, đem những kẻ nghèo khổ đã bị đuổi đi về nhà mình, khi thấy kẻ trần truồng thì mặc cho, và chớ hề trớ trinh những kẻ cốt nhục mình, hay sao?"));
visuperobject.biblemap.put("b23_58_8", new visuperobject(23,58,8,"Bấy giờ sự sáng ngươi sẽ hừng lên như sự sáng ban mai, ngươi sẽ được chữa lành lập tức; sự công bình ngươi đi trước mặt ngươi, sự vinh hiển của Ðức Giê-hô-va sẽ gìn giữ sau ngươi."));
visuperobject.biblemap.put("b23_58_9", new visuperobject(23,58,9,"Bấy giờ ngươi cầu, Ðức Giê-hô-va sẽ ứng; ngươi kêu, Ngài sẽ phán rằng: Có ta đây! Nếu ngươi cất-bỏ cái ách khỏi giữa ngươi, không chỉ tay và không nói bậy;"));
visuperobject.biblemap.put("b23_58_10", new visuperobject(23,58,10,"nếu ngươi mở lòng cho kẻ đói, và làm no kẻ khốn khổ, thì sự sáng ngươi sẽ sáng ra trong tối tăm, và sự tối tăm ngươi sẽ như ban trưa."));
visuperobject.biblemap.put("b23_58_11", new visuperobject(23,58,11,"Ðức Giê-hô-va sẽ cứ dắt đưa ngươi; làm cho ngươi no lòng giữa nơi khô hạn lớn; làm cho cứng mạnh các xương ngươi, ngươi sẽ như vườn năng tưới, như nước suối chẳng hề khô vậy."));
visuperobject.biblemap.put("b23_58_12", new visuperobject(23,58,12,"Những kẻ ra từ ngươi sẽ dựng lại nơi đổ nát ngày xưa; ngươi sẽ lập lại các nần của nhiều đời trước. người ta sẽ xưng ngươi là Kẻ tu bổ sự hư hoại, và là Kẻ sửa đường lại cho người ở."));
visuperobject.biblemap.put("b23_58_13", new visuperobject(23,58,13,"Nếu ngươi ngừa giữ chơn mình trong ngày Sa-bát, không làm vừa ý mình trong ngày thánh của ta; nếu ngươi xưng ngày Sa-bát là ngày vui thích, coi người thánh của Ðức Giê-hô-va là đáng kính; nếu ngươi tôn trọng ngày đó, không đi đường riêng mình, không theo ý riêng mình, và không nói lời riêng mình,"));
visuperobject.biblemap.put("b23_58_14", new visuperobject(23,58,14,"bấy giờ ngươi sẽ lấy Ðức Giê-hô-va làm vui thích, ta sẽ làm cho ngươi cỡi lên các nơi cao trên đất, và lấy sản nghiệp của Gia-cốp, tổ phụ ngươi, mà nuôi ngươi; vì miệng Ðức Giê-hô-va đã phán vậy."));
visuperobject.biblemap.put("b23_59_1", new visuperobject(23,59,1,"Nầy, tay Ðức Giê-hô-va chẳng trở nên ngắn mà không cứu được; tai Ngài cũng chẳng nặng nề mà không nghe được đâu."));
visuperobject.biblemap.put("b23_59_2", new visuperobject(23,59,2,"Nhưng ấy là sự gian ác các ngươi làm xa cách mình với Ðức Chúa Trời; và tội lỗi các ngươi đã che khuất mặt Ngài khỏi các ngươi, đến nỗi Ngài không nghe các ngươi nữa."));
visuperobject.biblemap.put("b23_59_3", new visuperobject(23,59,3,"Vì tay các ngươi đã ô uế bởi tội ác; môi các ngươi nói dối, lưỡi các ngươi lằm bằm sự xấu xa."));
visuperobject.biblemap.put("b23_59_4", new visuperobject(23,59,4,"Trong các ngươi chẳng có ai lấy lẽ công bình mà kêu rêu; chẳng có ai lấy điều chơn thật mà đối nại. Hết thảy đều cậy sự hư không, nói lời dối trá, cưu mang điều ác và đẻ ra tội trọng."));
visuperobject.biblemap.put("b23_59_5", new visuperobject(23,59,5,"Họ ấp trứng hổ mang và dệt màng nhện; ai ăn những trứng đó sẽ chết và nếu một cái trứng giập ra, sẽ nở thành rắn lục."));
visuperobject.biblemap.put("b23_59_6", new visuperobject(23,59,6,"Những màng họ không dùng làm áo được, không thể mặc được hàng họ đã dệt ra; công việc họ là công việc đáng tội, tay họ làm những việc hung tàn."));
visuperobject.biblemap.put("b23_59_7", new visuperobject(23,59,7,"Chơn họ chạy đến điều ác, nôn nả làm đổ máu vô tội; tư tưởng họ là tư tưởng gian tà, sự phá hại diệt vong là ở trên đường lối họ."));
visuperobject.biblemap.put("b23_59_8", new visuperobject(23,59,8,"Họ không biết đường bình an, trong đường họ đi không có sự công nghĩa. Họ tự làm những lối quanh queo: ai đi trong đó thì chẳng biết sự bình an!"));
visuperobject.biblemap.put("b23_59_9", new visuperobject(23,59,9,"Vậy nên sự công bình đã cách xa chúng ta, sự nhơn nghĩa cũng không theo kịp chúng ta. Chúng ta trông mong sự sáng, mà sự tối tăm đây nầy, trông mong sự sáng láng, mà đi trong u ám."));
visuperobject.biblemap.put("b23_59_10", new visuperobject(23,59,10,"Chúng ta đi dọc theo tường như người mù; rờ rẫm như người không có mắt; đúng trưa mà vấp chơn như chạng vạng; giữa những kẻ mạnh mẽ mà mình như người chết."));
visuperobject.biblemap.put("b23_59_11", new visuperobject(23,59,11,"Chúng ta cứ rên siếc như con gấu, và rầm rì như chim bò câu; trông sự công bình, mà nó không đến, đợi sự cứu rỗi, mà nó cách xa!"));
visuperobject.biblemap.put("b23_59_12", new visuperobject(23,59,12,"Phải, sự phạm phép chúng tôi đã thêm nhiều trước mặt Ngài, tội lỗi chúng tôi làm chứng nghịch cùng chúng tôi. Vì sự phạm phép chúng tôi ở cùng chúng tôi, và chúng tôi biết sự gian ác mình."));
visuperobject.biblemap.put("b23_59_13", new visuperobject(23,59,13,"Chúng tôi đã bạn nghịch, chối Ðức Giê-hô-va, và trở lòng chẳng theo Ðức Chúa Trời mình. Chúng tôi nói sự bạo ngược và phản loạn, cưu mang những lời giả dối trong lòng và nói ra!"));
visuperobject.biblemap.put("b23_59_14", new visuperobject(23,59,14,"Cho nên sự công bình trở lui lại, sự nhơn nghĩa đứng xa; vì lẽ thật vấp ngã giữa đường phố, và sự ngay thẳng chẳng được vào."));
visuperobject.biblemap.put("b23_59_15", new visuperobject(23,59,15,"Lẽ thật đã không còn, ai lánh điều dữ thì phải cướp bắt. Ðức Giê-hô-va thấy không có sự công bình thì chẳng đẹp lòng,"));
visuperobject.biblemap.put("b23_59_16", new visuperobject(23,59,16,"thấy không có người, chẳng ai cầu thay, thì lấy làm lạ lùng. Ngài bèn dùng chính cánh tay mình đem sự cứu rỗi đến; lấy sự công bình mình mà nâng đỡ."));
visuperobject.biblemap.put("b23_59_17", new visuperobject(23,59,17,"Ngài mặc sự công bình làm giáp, đội sự cứu rỗi trên đầu làm mão trụ; lấy sự báo thù làm áo mà bận, lấy sự sốt sắng làm áo tơi mà choàng mình."));
visuperobject.biblemap.put("b23_59_18", new visuperobject(23,59,18,"Ngài báo trả cho người ta tùy việc họ làm: sự thạnh nộ cho kẻ đối địch Ngài, sự báo oán cho kẻ thù nghịch; Ngài sẽ báo trả các cù lao."));
visuperobject.biblemap.put("b23_59_19", new visuperobject(23,59,19,"Người ta sự kính sợ danh Ðức Giê-hô-va từ phương tây, và sự vinh hiển Ngài từ phương mặt trời mọc lên; vì Ngài sẽ đến như sông tràn dẫy bởi hơi Ðức Giê-hô-va thổi giục."));
visuperobject.biblemap.put("b23_59_20", new visuperobject(23,59,20,"Ðấng Cứu chuộc sẽ đến Si-ôn, đến cùng những kẻ thuộc về Gia-cốp, là kẻ bỏ tội lỗi, Ðức Giê-hô-va phán vậy."));
visuperobject.biblemap.put("b23_59_21", new visuperobject(23,59,21,"Ðức Giê-hô-va phán: Về phần ta, nầy là giao ước ta đã lập với họ. Thần ta ở trên ngươi, cả đến những lời ta đã đặt nơi miệng ngươi, sẽ chẳng lìa khỏi miệng ngươi, miệng dòng dõi ngươi, và miệng dòng dõi của dòng dõi ngươi, từ bây giờ cho đến đời đời! Ðức Giê-hô-va phán vậy."));
visuperobject.biblemap.put("b23_60_1", new visuperobject(23,60,1,"Hãy dấy lên, và sáng lòe ra! Vì sự sáng ngươi đã đến, và vinh quang Ðức Giê-hô-va vã mọc lên trên ngươi."));
visuperobject.biblemap.put("b23_60_2", new visuperobject(23,60,2,"Nầy, sự tối tăm vây phủ đất, sự u ám bao bọc các dân; song Ðức Giê-hô-va dấy lên trên ngươi, vinh quang Ngài tỏ rạng trên ngươi."));
visuperobject.biblemap.put("b23_60_3", new visuperobject(23,60,3,"Các dân tộc sẽ được nơi sự sáng ngươi, các vua sẽ đến nơi sự chói sáng đã mọc lên trên ngươi."));
visuperobject.biblemap.put("b23_60_4", new visuperobject(23,60,4,"Hãy ngước mắt lên xung quanh ngươi, và nhìn xem: họ nhóm lại hết thảy, và đến cùng ngươi. Con trai ngươi đến từ xa, con gái ngươi sẽ được bồng ẵm trong cánh tay."));
visuperobject.biblemap.put("b23_60_5", new visuperobject(23,60,5,"Bấy giờ ngươi sẽ thấy và được chói sáng, lòng ngươi vừa rung động vừa nở nang; vì sự dư dật dưới biển sẽ trở đến cùng ngươi, sự giàu có các nước sẽ đến với ngươi."));
visuperobject.biblemap.put("b23_60_6", new visuperobject(23,60,6,"Muôn vàn lạc đà sẽ che khắp xứ ngươi, cả đến lạc đà một gu ở Ma-đi-an và Ê-pha cũng vậy. Nầy, hết thảy những người Sê-ba đem vàng và nhũ hương đến, và rao truyền lời ngợi khen Ðức Giê-hô-va."));
visuperobject.biblemap.put("b23_60_7", new visuperobject(23,60,7,"Hết thảy bầy súc vật của Kê-đa sẽ nhóm lại nơi ngươi, những chiên đực xứ Nê-ba-giốt sẽ làm của ngươi dùng, dâng lên bàn thờ ta làm một của lễ đẹp ý, nên ta sẽ làm sáng nhà của sự vinh hiển ta."));
visuperobject.biblemap.put("b23_60_8", new visuperobject(23,60,8,"Những kẻ bay như mây, giống chim bò câu về cửa sổ mình, đó là ai?"));
visuperobject.biblemap.put("b23_60_9", new visuperobject(23,60,9,"Các cù lao chắc sẽ trông đợi ta, các tàu bè của Ta-rê-si trước nhứt đem con trai ngươi từ xa về, cùng với bạc vàng của họ, vì danh của Giê-hô-va Ðức Chúa Trời ngươi, và vì Ðấng Thánh của Y-sơ-ra-ên, bởi Ngài đã làm vinh hiển ngươi."));
visuperobject.biblemap.put("b23_60_10", new visuperobject(23,60,10,"Các ngươi dân ngoại sẽ sửa xây thành ngươi, các vua họ sẽ hầu việc ngươi; vì khi ta giận, có đánh ngươi, nhưng nay ta ra ơn thương xót ngươi."));
visuperobject.biblemap.put("b23_60_11", new visuperobject(23,60,11,"Các cửa ngươi sẽ mở luôn, ngày đêm đều không đóng, hầu cho người ta đem của báu các nước đến cho ngươi, và dẫn các vua đến làm phu tù."));
visuperobject.biblemap.put("b23_60_12", new visuperobject(23,60,12,"Vì dân và nước nào chẳng thần phục ngươi thì sẽ bị diệt vong. Những nước đó sẽ bị diệt vong."));
visuperobject.biblemap.put("b23_60_13", new visuperobject(23,60,13,"Những cây tùng, cây sam, cây hoàng dương vốn là sự vinh hiển của Li-ban, sẽ cùng nhau bị đem đến cho ngươi để trang hoàng nơi thánh ta. Ta sẽ làm cho chỗ ta đặt chơn được vinh hiển."));
visuperobject.biblemap.put("b23_60_14", new visuperobject(23,60,14,"Các con trai của những kẻ ức hiếp ngươi sẽ cúi đầu đến cùng ngươi; mọi kẻ vốn khinh dể ngươi sẽ quì lạy nơi bàn chơn ngươi. Ngươi sẽ được xưng là Thành của Ðức Giê-hô-va, là Si-ôn của Ðấng Thánh Y-sơ-ra-ên."));
visuperobject.biblemap.put("b23_60_15", new visuperobject(23,60,15,"Xưa kia ngươi đã bị bỏ, bị ghét, đến nỗi không ai đi qua giữa ngươi, ta sẽ làm cho ngươi nên cao trọng đời đời, nên sự vui mừng của nhiều đời."));
visuperobject.biblemap.put("b23_60_16", new visuperobject(23,60,16,"Ngươi sẽ hút sữa của các nước; bú vú của các vua; ngươi sẽ biết ta, Ðức Giê-hô-va, là Ðấng Cứu ngươi, Ðấng Chuộc ngươi, và là Ðấng Toàn năng của Gia-cốp."));
visuperobject.biblemap.put("b23_60_17", new visuperobject(23,60,17,"Ta sẽ ban vàng thay cho đồng, ban bạc thay cho sắt, ban đồng tay cho gỗ, ban sắt thay cho đá. Ta sẽ khiến sự bình an làm quan cai trị ngươi, và sự công bình làm quan xử đoán ngươi."));
visuperobject.biblemap.put("b23_60_18", new visuperobject(23,60,18,"Trong xứ ngươi sẽ chẳng nghe nói về sự hung dữ nữa, trong bờ cõi ngươi cũng không có sự hoang vu và phá hủy nữa; nhưng ngươi sẽ gọi tường mình là Cứu-rỗi, cửa mình là Ngợi khen."));
visuperobject.biblemap.put("b23_60_19", new visuperobject(23,60,19,"Ngươi sẽ chẳng nhờ mặt trời soi sáng ban ngày nữa, và cũng chẳng còn nhờ mặt trăng chiếu sáng ban đêm; nhưng Ðức Giê-hô-va sẽ làm sự sáng đời đời cho ngươi, Ðức Chúa Trời ngươi sẽ làm vinh quang cho ngươi."));
visuperobject.biblemap.put("b23_60_20", new visuperobject(23,60,20,"Mặt trời của ngươi không lặn nữa; mặt trăng chẳng còn khuyết, vì Ðức Giê-hô-va sẽ là sự sáng đời đời cho ngươi, những ngày sầu thảm của ngươi đã hết rồi."));
visuperobject.biblemap.put("b23_60_21", new visuperobject(23,60,21,"Còn dân ngươi, hết thảy sẽ là công bình. Họ sẽ hưởng được xứ nầy đời đời; ấy là nhánh ta đã trồng, việc tay ta làm để ta được vinh hiển."));
visuperobject.biblemap.put("b23_60_22", new visuperobject(23,60,22,"Kẻ rất nhỏ trong vòng họ sẽ nên một ngàn, còn kẻ rất hèn yếu sẽ trở nên một dân mạnh. Ta, Ðức Giê-hô-va, sẽ nôn nả làm điều ấy trong kỳ nó!"));
visuperobject.biblemap.put("b23_61_1", new visuperobject(23,61,1,"Thần của Chúa Giê-hô-va ngự trên ta; vì Ðức Giê-hô-va đã xức dầu cho ta, đặng giảng tin lành cho kẻ khiêm nhường. Ngài đã sai ta đến đặng rịt những kẻ vỡ lòng, đặng rao cho kẻ phu tù được tự do, kẻ bị cầm tù được ra khỏi ngục;"));
visuperobject.biblemap.put("b23_61_2", new visuperobject(23,61,2,"đặng rao năm ban ơn của Ðức Giê-hô-va, và ngày báo thù của Ðức Chúa Trời chúng ta; đặng yên ủi mọi kẻ buồn rầu;"));
visuperobject.biblemap.put("b23_61_3", new visuperobject(23,61,3,"đặng ban mão hoa cho kẻ buồn rầu ở Si-ôn thay vì tro bụi, ban dầu vui mừng thay vì tang chế, ban áo ngợi khen thay vì lòng nặng nề; hầu cho những kẻ ấy được xưng là cây của sự công bình, là cây Ðức Giê-hô-va đã trồng để được vinh hiển."));
visuperobject.biblemap.put("b23_61_4", new visuperobject(23,61,4,"Họ sẽ xây lại các nơi hoang vu ngày xưa, dựng lại các nơi hủy hoại lúc trước, lập lại thành bị hủy phá, là những nơi đổ nát lâu đời."));
visuperobject.biblemap.put("b23_61_5", new visuperobject(23,61,5,"Những người khách lạ sẽ đứng đặng chăn bầy chiên các ngươi, những người ngoại quốc sẽ làm kẻ cày ruộng và trồng nho của các ngươi;"));
visuperobject.biblemap.put("b23_61_6", new visuperobject(23,61,6,"nhưng các ngươi thì sẽ được gọi là thầy tế lễ của Ðức Giê-hô-va; sẽ được xưng là chức dịch của Ðức Chúa Trời chúng ta; sẽ ăn của báu các nước, và lấy sự vinh hoa của họ mà khoe mình."));
visuperobject.biblemap.put("b23_61_7", new visuperobject(23,61,7,"Các ngươi sẽ được gấp hai để thay vì sự xấu hổ; chúng nó sẽ có sản nghiệp bội phần trong xứ mình, và được sự vui mừng đời đời."));
visuperobject.biblemap.put("b23_61_8", new visuperobject(23,61,8,"Vì ta, Ðức Giê-hô-va, ưa sự chánh trực, ghét sự trộm cướp và sự bất nghĩa. Ta sẽ lấy điều thành tín báo trả lại, và lập giao ước nước chúng nó đời đời."));
visuperobject.biblemap.put("b23_61_9", new visuperobject(23,61,9,"Dòng-dõi chúng nó sẽ nổi tiếng trong các nước, con cháu sẽ nổi tiếng trong các dân; phàm ai thấy sẽ nhận là một dòng dõi mà Ðức Giê-hô-va đã ban phước."));
visuperobject.biblemap.put("b23_61_10", new visuperobject(23,61,10,"Ta sẽ rất vui vẻ trong Ðức Giê-hô-va, linh hồn ta mừng rỡ trong Ðức Chúa Trời ta; vì Ngài đã mặc áo cứu rỗi cho ta; khoác áo choàng công bình cho ta, như chàng rể mới diện mão hoa trên đầu mình, như cô dâu mới dồi mình bằng châu báu."));
visuperobject.biblemap.put("b23_61_11", new visuperobject(23,61,11,"Vả, như đất làm cho cây mọc lên, vườn làm cho hột giống nứt lên thể nào, thì Chúa Giê-hô-va cũng sẽ làm cho sự công bình và sự khen ngợi nứt ra trước mặt mọi dân tộc thể ấy."));
visuperobject.biblemap.put("b23_62_1", new visuperobject(23,62,1,"Ta vì cớ Si-ôn sẽ chẳng làm thình, vì cớ Giê-ru-sa-lem sẽ chẳng an nghỉ, cho đến chừng nào sự công bình nó rực rỡ như sự sáng, và sự cứu rỗi nó chói lòa như ngọn đèn."));
visuperobject.biblemap.put("b23_62_2", new visuperobject(23,62,2,"Bấy giờ các nước sẽ thấy sự công bình ngươi, các vua sẽ thấy sự vinh hiển ngươi, ngươi sẽ được xưng bằng tên mới mà miệng Ðức Giê-hô-va đặt cho."));
visuperobject.biblemap.put("b23_62_3", new visuperobject(23,62,3,"Ngươi sẽ làm bão triều thiên đẹp đẽ trong tay Ðức Giê-hô-va, làm mão miện vua trong tay Ðức Chúa Trời ngươi."));
visuperobject.biblemap.put("b23_62_4", new visuperobject(23,62,4,"Người ta sẽ chẳng gọi ngươi là Kẻ bị bỏ nữa, chẳng gọi đất ngươi là Ðất hoang vu nữa; nhưng sẽ gọi ngươi là Kẻ mà ta ưa thích; và đất ngươi sẽ được xưng là Kẻ có chồng; vì Ðức Giê-hô-va sẽ ưa thích ngươi, và đất ngươi sẽ có chồng."));
visuperobject.biblemap.put("b23_62_5", new visuperobject(23,62,5,"Như người trai tráng cưới người nữ đồng trinh, thì các con trai ngươi cũng sẽ cưới ngươi; chàng rể mới vui mừng vì vợ mới mình, Ðức Chúa Trời ngươi cũng vui mừng vì ngươi."));
visuperobject.biblemap.put("b23_62_6", new visuperobject(23,62,6,"Hỡi Giê-ru-sa-lem, ta đã đặt các vọng canh trên thành ngươi; cả ngày và đêm chúng nó chẳng hề im lặng. Hỡi các ngươi là kẻ nhắc nhở Ðức Giê-hô-va, chớ có nghỉ ngơi chút nào."));
visuperobject.biblemap.put("b23_62_7", new visuperobject(23,62,7,"Ðừng để Ngài an nghỉ cho đến chừng nào đã lập lại Giê-ru-sa-lem và dùng nó làm sự ngợi khen trong cả đất!"));
visuperobject.biblemap.put("b23_62_8", new visuperobject(23,62,8,"Ðức Giê-hô-va đã chỉ tay hữu mình và cánh tay quyền năng mình mà thề rằng: Ta chắc sẽ chẳng ban lúa mì ngươi cho kẻ thù ngươi ăn nữa, những ngươi dân ngoại sẽ chẳng còn uống rượu nho mới ngươi mà ngươi đã khó nhọc làm ra."));
visuperobject.biblemap.put("b23_62_9", new visuperobject(23,62,9,"Song những kẻ có gặt sẽ ăn hoa lợi mình, và khen ngợi Ðức Giê-hô-va; những kẻ có hái nho sẽ uống rượu mình trong hành lang của nơi thánh ta."));
visuperobject.biblemap.put("b23_62_10", new visuperobject(23,62,10,"Hãy đi qua, bởi cửa thành đi qua; hãy ban bằng đường của dân; hãy đắp đường, hãy đắp đường cái; bỏ hết đá; dựng một cờ xí cho các dân tộc."));
visuperobject.biblemap.put("b23_62_11", new visuperobject(23,62,11,"Nầy, Ðức Giê-hô-va đã rao truyền ra đến đầu cùng đất rằng: Hãy bảo con gái Si-ôn rằng: Nầy, sự cứu rỗi ngươi đến! Phần thưởng đi theo Ngài, và sự thưởng đi trước Ngài."));
visuperobject.biblemap.put("b23_62_12", new visuperobject(23,62,12,"Người ta sẽ gọi chúng nó là Dân thánh, tức những Kẻ được chuộc của Ðức Giê-hô-va; còn ngươi, sẽ được xưng là Thành hay tìm đến, tức Thành không bị bỏ."));
visuperobject.biblemap.put("b23_63_1", new visuperobject(23,63,1,"Ðấng từ Ê-đôm đến, từ Bốt-ra tới, mặc áo nhuộm, bận đồ hoa mĩ, cậy sức mạnh cả thể, kéo bộ cách oai nghiêm, là ai? Ấy, chính ta, là Ðấng dùng sự công bình mà nói, và có quyền lên để cứu rỗi!"));
visuperobject.biblemap.put("b23_63_2", new visuperobject(23,63,2,"Áo ngươi có màu đỏ, áo xống của ngươi như của người đạp bàn ép rượu là vì sao?"));
visuperobject.biblemap.put("b23_63_3", new visuperobject(23,63,3,"Chỉ một mình ta đạp bàn ép rượu, trong vòng các dân chẳng từng có ai với ta. Trong cơn giận ta đã đạp lên; đương khi thạnh nộ ta đã nghiền nát ra: máu tươi họ đã vảy ra trên áo ta, áo xống ta đã vấy hết."));
visuperobject.biblemap.put("b23_63_4", new visuperobject(23,63,4,"Vì ta đã định ngày báo thù trong lòng ta, và năm cứu chuộc của ta đã đến."));
visuperobject.biblemap.put("b23_63_5", new visuperobject(23,63,5,"Ta đã xem, chẳng có ai đến giúp ta; ta lấy làm lạ vì chẳng có ai nâng đỡ! Cánh tay ta bèn cứu ta, sự thạnh nộ ta bèn nâng đỡ ta."));
visuperobject.biblemap.put("b23_63_6", new visuperobject(23,63,6,"Ta đã giày đạp các dân trong cơn giận; đã khiến chúng nó say vì sự thạnh nộ ta, và đã đổ máu tươi chúng nó ra trên đất."));
visuperobject.biblemap.put("b23_63_7", new visuperobject(23,63,7,"Ta sẽ nói đến những sự nhơn từ của Ðức Giê-hô-va, và sự ngợi khen Ðức Giê-hô-va, y theo mọi sự Ðức Giê-hô-va đã ban cho chúng ta, và thuật lại phước lớn Ngài đã ban cho nhà Y-sơ-ra-ên, y theo những sự thương xót và sự nhơn từ vô số của Ngài."));
visuperobject.biblemap.put("b23_63_8", new visuperobject(23,63,8,"Vì Ngài có phán: Thật, chúng nó là dân ta, tức con cái sẽ chẳng làm dối; vậy Ngài đã làm Ðấng Cứu họ."));
visuperobject.biblemap.put("b23_63_9", new visuperobject(23,63,9,"Hễ khi dân Ngài bị khốn khổ, chính Ngài cũng khốn khổ, và thiên sứ trước mặt Ngài đã cứu họ rồi. Chính Ngài đã lấy lòng yêu đương thương xót mà chuộc họ. Ngài đã ẵm bồng, và mang họ trong các ngày thuở xưa."));
visuperobject.biblemap.put("b23_63_10", new visuperobject(23,63,10,"Song họ đã bội nghịch, đã làm cho Thần thánh Ngài buồn rầu, Ngài bèn trở làm cừu thù với họ, và chính mình Ngài chinh chiến cùng họ."));
visuperobject.biblemap.put("b23_63_11", new visuperobject(23,63,11,"Bấy giờ dân nhớ lại ngày xưa, về Môi-se và dân sự người, mà nói rằng: Nào Ðấng đã làm cho dân và người chăn bầy vật mình ra khỏi biển ở đâu? Nào Ðấng đã đặt Thần thánh mình giữa dân sự ở đâu?"));
visuperobject.biblemap.put("b23_63_12", new visuperobject(23,63,12,"là Ðấng lấy cánh tay vinh hiển đi bên tay hữu Môi-se; là Ðấng đã rẽ nước ra trước mặt họ, đặng rạng danh vô cùng;"));
visuperobject.biblemap.put("b23_63_13", new visuperobject(23,63,13,"là Ðấng đã dắt dân qua trên sóng đào, như ngựa chạy đồng bằng, và không sẩy bước."));
visuperobject.biblemap.put("b23_63_14", new visuperobject(23,63,14,"Thần của Ðức Giê-hô-va đã khiến dân sự yên nghỉ, như bầy vật xuống nơi trũng. Cũng vậy, Ngài đã dắt dân mình đi đường, để cho rạng danh vinh hiển mình."));
visuperobject.biblemap.put("b23_63_15", new visuperobject(23,63,15,"Xin Chúa từ trên trời ngó xuống, từ chỗ ở thánh và vinh hiển của Ngài mà nhìn xem! Chớ nào lòng nóng nảy và công việc lớn Ngài ở đâu? Lòng ước ao sốt sắng và sự thương xót của Ngài bị ngăn trở đến cùng tôi."));
visuperobject.biblemap.put("b23_63_16", new visuperobject(23,63,16,"Thật Ngài là Cha chúng tôi, dầu Áp-ra-ham chẳng biết chúng tôi, Y-sơ-ra-ên cũng chẳng nhận chúng tôi; hỡi Ðức Giê-hô-va, Ngài là Cha chúng tôi, danh Ngài là Ðấng Cứu chuộc chúng tôi từ trước đời đời."));
visuperobject.biblemap.put("b23_63_17", new visuperobject(23,63,17,"Hỡi Ðức Giê-hô-va, sao để chúng tôi lầm lạc xa đường Ngài? sao để lòng chúng tôi cứng cỏi đến nỗi chẳng kính sợ Ngài? Xin hãy vì cớ các tôi tớ Ngài và các chi phái của cơ nghiệp Ngài mà trở lại!"));
visuperobject.biblemap.put("b23_63_18", new visuperobject(23,63,18,"Dân thánh của Ngài được xứ nầy làm kỷ vật chưa bao lâu; mà kẻ thù chúng tôi đã giày đạp nơi thánh của Ngài."));
visuperobject.biblemap.put("b23_63_19", new visuperobject(23,63,19,"Từ lâu nay chúng tôi đã như một dân không hề được Ngài cai trị, và không còn đội danh Ngài nữa."));
visuperobject.biblemap.put("b23_64_1", new visuperobject(23,64,1,"ôi! ước gì Ngài xé rách các từng trời, Ngài ngự xuống và làm rúng động các núi trước mặt Ngài,"));
visuperobject.biblemap.put("b23_64_2", new visuperobject(23,64,2,"như lửa đốt củi khô, như lửa làm sôi nước, hầu cho kẻ thù nghịch biết danh Ngài, các dân tộc run rẩy trước mặt Ngài."));
visuperobject.biblemap.put("b23_64_3", new visuperobject(23,64,3,"Khi Ngài đã lam những sự đáng sợ mà chúng tôi không trông mong, thì Ngài ngự xuống, và các núi đều rúng động trước mặt Ngài."));
visuperobject.biblemap.put("b23_64_4", new visuperobject(23,64,4,"Từ xưa người ta chưa hề biết, tai chưa hề nghe, mắt chưa hề thấy ngoài Ngài có Ðức Chúa Trời nào khác, hay vì kẻ trông đợi mình mà làm những sự thể ấy."));
visuperobject.biblemap.put("b23_64_5", new visuperobject(23,64,5,"Ngài đã đón rước kẻ vui lòng làm sự công bình, kẻ đi trong đường lối Ngài và nhớ đến Ngài. Nầy, Ngài đã nổi giận vì chúng tôi phạm tội; đã lâu như vậy, thì còn được cứu sao?"));
visuperobject.biblemap.put("b23_64_6", new visuperobject(23,64,6,"Chúng tôi hết thảy đã trở nên như vật ô uế, mọi việc công bình của chúng tôi như áo nhớp; chúng tôi thảy đều héo như lá, và tội ác chúng tôi như gió đùa mình đi."));
visuperobject.biblemap.put("b23_64_7", new visuperobject(23,64,7,"Chẳng có ai kêu cầu danh Ngài, hay là gắng sức đặng cầm lấy Ngài; vì Ngài đã ẩn mặt khỏi chúng tôi, để chúng tôi bị tiêu mất bởi tội ác mình."));
visuperobject.biblemap.put("b23_64_8", new visuperobject(23,64,8,"Hỡi Ðức Giê-hô-va, dầu vậy, bây giờ Ngài là Cha chúng tôi! Chúng tôi là đất sét, Ngài là thợ gốm chúng tôi; chúng tôi thảy là việc của tay Ngài."));
visuperobject.biblemap.put("b23_64_9", new visuperobject(23,64,9,"Hỡi Ðức Giê-hô-va, xin Ngài chớ quá giận, đừng luôn luôn nhớ đến tội ác chúng tôi! Nầy, chúng tôi xin Ngài hãy đoái xem, chúng tôi đều là dân Ngài!"));
visuperobject.biblemap.put("b23_64_10", new visuperobject(23,64,10,"Các thành thánh của Ngài đã nên đồng vắng; Si-ôn đã trở nên đồng vắng, Giê-ru-sa-lem đã trở nên hoang vu."));
visuperobject.biblemap.put("b23_64_11", new visuperobject(23,64,11,"Nhà thánh và đẹp đẽ của chúng tôi, tức là nơi tổ phụ chúng tôi ngợi khen Ngài, thì đã bị lửa đốt cháy rồi; mọi nơi vui vẻ của chúng tôi đều đã bị hủy hoại."));
visuperobject.biblemap.put("b23_64_12", new visuperobject(23,64,12,"Hỡi Ðức Giê-hô-va, đã đến nỗi nầy, Ngài còn nín nhịn được sao? Có lẽ nào Ngài cứ làm thinh, khiến chúng tôi chịu khổ không ngần."));
visuperobject.biblemap.put("b23_65_1", new visuperobject(23,65,1,"Những kẻ vốn chẳng cầu hỏi ta thì đã hỏi thăm ta; những kẻ vốn chẳng tìm ta thì đã gặp được ta. Ta đã phán cùng một dân chưa kêu cầu danh ta, mà rằng: Ta đây, ta đây!"));
visuperobject.biblemap.put("b23_65_2", new visuperobject(23,65,2,"Ta đã giang tay ra trọn ngày hướng về một dân bội nghịch, là những kẻ đi trong đường không tốt, theo ý riêng mình,"));
visuperobject.biblemap.put("b23_65_3", new visuperobject(23,65,3,"là một dân kia hằng chọc giận ta trước mặt ta, tế trong vườn, đốt hương trên đống gạch,"));
visuperobject.biblemap.put("b23_65_4", new visuperobject(23,65,4,"ngồi trong mồ mả, trọ trong nơi kín, ăn thịt heo, đựng nước của vật gớm ghiếc trong khí mạnh nó,"));
visuperobject.biblemap.put("b23_65_5", new visuperobject(23,65,5,"và dám nói rằng: Hãy đứng riêng ra, đừng lại gần ta, vì ta thánh sạch hơn ngươi! Bọn đó là khói nơi lỗ mũi ta, như lửa cháy cả ngày."));
visuperobject.biblemap.put("b23_65_6", new visuperobject(23,65,6,"Nầy, đã ghi chép trước mặt ta rằng ta sẽ không làm thinh đâu, song ta sẽ báo trả, thật ta sẽ báo trả vào ngực nó,"));
visuperobject.biblemap.put("b23_65_7", new visuperobject(23,65,7,"tức là tội ác các ngươi, luôn với tội ác tổ phụ các ngươi, là những kẻ đã đốt hương trên các núi, và nói phạm đến ta trên các gò: nên ta sẽ đo lường việc trước chúng nó vào ngực chúng nó. Ðức Giê-hô-va phán vậy."));
visuperobject.biblemap.put("b23_65_8", new visuperobject(23,65,8,"Ðức Giê-hô-va phán như vầy: Khi người ta thấy rượu mới trong chùm nho, thì rằng: Ðừng làm hư đi, vì trong trái đó có phước lành; ta cũng sẽ làm như vậy vì cớ các tôi tớ ta, hầu cho ta không hủy diệt cả."));
visuperobject.biblemap.put("b23_65_9", new visuperobject(23,65,9,"Ta sẽ khiến một dòng dõi ra từ Gia-cốp, và một kẻ kế tự các núi ta ra từ Giu-đa; những kẻ lựa chọn của ta sẽ được xứ nầy làm kỷ vật, tôi tớ ta sẽ ở đó."));
visuperobject.biblemap.put("b23_65_10", new visuperobject(23,65,10,"Trong dân ta, những kẻ nào đã tìm kiếm ta, sẽ được đồng Sa-rôn dùng làm chỗ chăn chiên, nơi trũng A-cô dùng làm đồng cỏ thả bò."));
visuperobject.biblemap.put("b23_65_11", new visuperobject(23,65,11,"Còn như các ngươi, là kẻ đã bỏ Ðức Giê-hô-va, đã quên núi thánh ta, đã đặt một bàn cho Gát, và rót chén đầy kính Mê-ni,"));
visuperobject.biblemap.put("b23_65_12", new visuperobject(23,65,12,"nên ta định cho các ngươi phải gươm dao, các ngươi thảy đều cúi xuống và bị giết; vì ta đã kêu, các ngươi không đáp lại; ta đã nói, các ngươi không lắng nghe, nhưng các ngươi đã làm điều ta ghét, và đã chọn điều ta chẳng đẹp lòng."));
visuperobject.biblemap.put("b23_65_13", new visuperobject(23,65,13,"Vậy nên Chúa Giê-hô-va phán như vầy: Nầy, các tôi tớ ta sẽ ăn, còn các ngươi thì đói; nầy, tôi tớ ta sẽ uống, còn các ngươi thì khát; nầy, tôi tớ ta sẽ được vui vẻ, còn các ngươi thì bị nhuốc nha;"));
visuperobject.biblemap.put("b23_65_14", new visuperobject(23,65,14,"nầy, tôi tớ ta sẽ hát mừng vì lòng đầy vui vẻ, còn các ngươi thì khóc lóc vì lòng buồn bực, kêu than vì tâm thần phiền não."));
visuperobject.biblemap.put("b23_65_15", new visuperobject(23,65,15,"Danh các ngươi sẽ còn lại làm tiếng rủa sả cho những kẻ lựa chọn của ta; Chúa Giê-hô-va sẽ giết ngươi; những Ngài sẽ lấy danh khác đặt cho các tôi tớ mình."));
visuperobject.biblemap.put("b23_65_16", new visuperobject(23,65,16,"Vì vậy, phàm ai ở trên đất chúc phước cho mình thì sẽ cầu phước lành nơi danh Ðức Chúa Trời chơn thật; còn ai ở trên đất mà thề nguyền, thì sẽ chỉ Ðức Chúa Trời chơn thật mà thề nguyền; vì những sự khốn nạn trước đã quên hết, và đã khuất khỏi mặt ta."));
visuperobject.biblemap.put("b23_65_17", new visuperobject(23,65,17,"Ta sẽ dựng trời mới đất mới; những việc trước sẽ chẳng nhớ nữa, chẳng còn nhắc đến nữa."));
visuperobject.biblemap.put("b23_65_18", new visuperobject(23,65,18,"Thà các ngươi hãy mừng rỡ và vui vẻ đời đời trong sự ta dựng nên. ThḀ­t, ta dựng nên Giê-ru-sa-lem cho sự vui, và dân nó cho sự mừng rỡ."));
visuperobject.biblemap.put("b23_65_19", new visuperobject(23,65,19,"Ta sẽ vui vì Giê-ru-sa-lem, ta sẽ mừng vì dân ta; tại đó sẽ chẳng còn nghe tiếng khóc lóc kêu la nữa."));
visuperobject.biblemap.put("b23_65_20", new visuperobject(23,65,20,"Tại đó sẽ không có trẻ con chết non, cũng chẳng có ông già nào chẳng trọn đời mình; vì chết lúc trăm tuổi, còn là chết trẻ, và kẻ có tội lúc trăm tuổi, ấy là bị rủa sả."));
visuperobject.biblemap.put("b23_65_21", new visuperobject(23,65,21,"Dân ta sẽ xây nhà và ở, trồng vườn nho và ăn trái."));
visuperobject.biblemap.put("b23_65_22", new visuperobject(23,65,22,"Họ chẳng xây nhà cho người khác ở, chẳng trồng vườn nho cho người khác ăn; vì tuổi dân ta sẽ như tuổi cây, những kẻ lựa chọn của ta sẽ hằng hưởng công việc tay mình làm."));
visuperobject.biblemap.put("b23_65_23", new visuperobject(23,65,23,"Họ sẽ không nhọc mình vô ích nữa, không kẻ con ra để gặp sự họa, vì họ là dòng dõi của kẻ được ban phước bởi Ðức Giê-hô-va, con cháu họ cùng với họ nữa."));
visuperobject.biblemap.put("b23_65_24", new visuperobject(23,65,24,"Ta sẽ nhậm lời họ trước khi kêu cầu ta; họ còn nói, ta đã nghe rồi."));
visuperobject.biblemap.put("b23_65_25", new visuperobject(23,65,25,"Muông sói với chiên con sẽ ăn chung, sư tử ăn rơm như bò, rắn ăn bụi đất. Sẽ chẳng có ai làm tổn hại hay là hủy phá trong khắp núi thánh ta, Ðức Giê-hô-va đã phán vậy."));
visuperobject.biblemap.put("b23_66_1", new visuperobject(23,66,1,"Ðức Giê-hô-va phán như vầy: Trời là ngai ta, đất là bệ chơn ta. Các ngươi sẽ xây nhà thể nào cho ta? Các ngươi sẽ lấy nơi nào làm chỗ nghỉ ngơi cho ta?"));
visuperobject.biblemap.put("b23_66_2", new visuperobject(23,66,2,"Ðức Giê-hô-va phán: Mọi sự nầy đều bởi tay ta làm ra, và có như vậy. Nầy là kẻ mà ta đoái đến: tức là kẻ nghèo khó, có lòng ăn năn đau đớn, nghe lời nói ta mà run."));
visuperobject.biblemap.put("b23_66_3", new visuperobject(23,66,3,"Làm thịt một con bò, cũng như giết một người; tế bằng một con chiên con, cũng như bẻ cổ một con chó; dâng của lễ chay, cũng như chọc huyết heo; đốt hương cũng như ngợi khen thần tượng. Vì những kẻ ấy đã chọn đường mình, lòng ưa thích những việc gớm ghiếc,"));
visuperobject.biblemap.put("b23_66_4", new visuperobject(23,66,4,"thì ta cũng kén chọn những sự phỉnh dỗ, lấy những đều họ sợ mà giáng cho. Vì ta đã gọi mà chẳng ai thưa, ta đã phán mà họ không nghe, lại làm điều ác trước mắt ta, chọn điều ta không vừa ý."));
visuperobject.biblemap.put("b23_66_5", new visuperobject(23,66,5,"Hỡi các ngươi là kẻ nghe lời phán Ðức Giê-hô-va mà run, hãy nghe lời Ngài: Anh em các ngươi ghét các ngươi, vì cớ danh ta bỏ các ngươi, từng nói rằng: Xin Ðức Giê-hô-va được vinh hiển, đặng chúng ta thấy sự vui mừng các ngươi; nhưng chính họ sẽ bị hổ thẹn."));
visuperobject.biblemap.put("b23_66_6", new visuperobject(23,66,6,"Có tiếng om sòm nổi lên từ trong thành; có tiếng la lối vang ra từ đền thờ; là tiếng của Ðức Giê-hô-va, Ngài báo trả cho kẻ thù nghịch mình."));
visuperobject.biblemap.put("b23_66_7", new visuperobject(23,66,7,"Nó chưa ở cữ, đã sanh nở; chưa chịu đau đớn, đã đẻ một trai."));
visuperobject.biblemap.put("b23_66_8", new visuperobject(23,66,8,"Ai đã hề nghe một sự thể nầy? Ai đã hề thấy sự gì giống như vậy? Nước há dễ sanh ra trong một ngày, dân tộc há dễ sanh ra trong một chặp? Mà Si-ôn mới vừa nằm nơi, đã sanh con cái."));
visuperobject.biblemap.put("b23_66_9", new visuperobject(23,66,9,"Ðức Giê-hô-va phán: Ta là Ðấng đã mở lòng mẹ ra, há chẳng săn sóc sự sanh đẻ hay sao? Ðức Chúa Trời ngươi phán: Ta là Ðấng đã khiến sanh đẻ, há lại làm cho son sẻ hay sao?"));
visuperobject.biblemap.put("b23_66_10", new visuperobject(23,66,10,"Các ngươi là kẻ yêu Giê-ru-sa-lem, hãy vui với nó, hãy mừng vì nó! Các ngươi là kẻ đã khóc vì Giê-ru-sa-lem, hãy cùng nó hớn hở vui cười;"));
visuperobject.biblemap.put("b23_66_11", new visuperobject(23,66,11,"hầu cho các ngươi sẽ được bú và no bởi vú của sự yên ủi nó; được vắt sữa và lấy làm vui sướng bởi sự dư dật của vinh quang nó."));
visuperobject.biblemap.put("b23_66_12", new visuperobject(23,66,12,"Vì Ðức Giê-hô-va phán như vầy: Nầy, ta sẽ làm cho sự bình an chảy đến nó như một con sông, và sự vinh hiển của các dân như nước vỡ bờ; các ngươi sẽ được bú, được bồng trên hông, và mơn trớn trên đầu gối."));
visuperobject.biblemap.put("b23_66_13", new visuperobject(23,66,13,"Ta sẽ yên ủi các ngươi như mẹ yên ủi con, và ấy là tại trong Giê-ru-sa-lem mà các ngươi sẽ được yên ủi."));
visuperobject.biblemap.put("b23_66_14", new visuperobject(23,66,14,"Các ngươi sẽ thấy mọi điều đó, thì trong lòng được sự vui vẻ, và xương cốt các ngươi sẽ nẩy nở như cỏ non; tay Ðức Giê-hô-va sẽ tỏ ra cùng tôi tớ Ngài, và Ngài sẽ giận nghịch cùng kẻ cừu địch."));
visuperobject.biblemap.put("b23_66_15", new visuperobject(23,66,15,"Nầy, Ðức Giê-hô-va sẽ đến với lửa, và xe cộ Ngài giống như gió lốc, hầu cho cơn giận của Ngài thêm nóng nảy, và sự quở trách của Ngài biến thành ngọn lửa hừng."));
visuperobject.biblemap.put("b23_66_16", new visuperobject(23,66,16,"Ðức Giê-hô-va sẽ lấy lửa và gươm làm sự xét đoán Ngài trên mọi xác thịt, và số những kẻ phải chết bởi Ðức Giê-hô-va là nhiều lắm."));
visuperobject.biblemap.put("b23_66_17", new visuperobject(23,66,17,"Những kẻ biệt mình riêng ra và tự làm sạch mình để đến các vườn, ở đằng sau một cái cây giữa đó, ăn thịt heo, thịt chuột, và những đồ ăn gớm ghiếc, thì sẽ chết với nhau, Ðức Giê-hô-va phán vậy."));
visuperobject.biblemap.put("b23_66_18", new visuperobject(23,66,18,"Về phần ta, ta biết việc làm và ý tưởng chúng nó; kỳ đến mà ta sẽ nhóm các nước và các thứ tiếng; chúng nó sẽ được và thấy sự vinh hiển ta."));
visuperobject.biblemap.put("b23_66_19", new visuperobject(23,66,19,"Ta sẽ đặt một dấu hiệu giữa họ; những kẻ trong vòng họ đã tránh khỏi nạn, thì ta sẽ sai đến khắp các nước, tức là đến Ta-rẹ-si, Phun, Lút là dân có tài bắn cung, Tu-banh và Gia-van, cho đến trong các cù lao xa, là nơi chưa hề nghe danh tiếng ta, và chưa từng thấy sự vinh hiển ta. Bấy giờ chúng sẽ rao truyền sự vinh hiển ta ra trong các nước."));
visuperobject.biblemap.put("b23_66_20", new visuperobject(23,66,20,"Ðức Giê-hô-va phán: Các dân ngoại sẽ đem hết thảy anh em các ngươi cỡi ngựa, xe, kiệu, la, lạc đà một gu, từ các nước mà đem đến núi thánh ta, là Giê-ru-sa-lem, đặng dâng họ cho Ðức Giê-hô-va làm của lễ, cũng như con cái Y-sơ-ra-ên đem của lễ trong đồ-đựng sạch sẽ mà vào đền thờ Ðức Giê-hô-va vậy."));
visuperobject.biblemap.put("b23_66_21", new visuperobject(23,66,21,"Ðức Giê-hô-va phán: Ta lại từ trong đám họ lữa kẻ làm thầy tế lễ và làm người Lê-vi."));
visuperobject.biblemap.put("b23_66_22", new visuperobject(23,66,22,"Ðức Giê-hô-va phán: Vì như trời mới đất mới mà ta hầu dựng, sẽ cứ còn trước mặt ta thể nào, thì dòng giống và danh hiệu các ngươi cũng sẽ cứ còn thể ấy."));
visuperobject.biblemap.put("b23_66_23", new visuperobject(23,66,23,"Ðức Giê-hô-va phán: Từ ngày trăng mới nầy đến ngày trăng mới khác, từ ngày Sa-bát nọ đến ngày Sa-bát kia, mọi xác thịt sẽ đến thờ lạy trước mặt ta."));
visuperobject.biblemap.put("b23_66_24", new visuperobject(23,66,24,"Khi dân sự ra ngoài, sẽ thấy thây của những người đã bội nghịch cùng ta; vì sâu chúng nó chẳng hề chết, lửa chúng nó chẳng hề tắt; chúng nó sẽ làm sự gớm ghiếc cho mọi xác thịt."));

}

}