package vi.bible;

public class vibible34 {

public vibible34() {

putmapbible34();

}

private void putmapbible34() {

visuperobject.biblemap.put("b34_1_1", new visuperobject(34,1,1,"Gánh nặng của Ni-ni-ve. Sách sự hiện thấy của Na-hum, người Eân-cốt."));
visuperobject.biblemap.put("b34_1_2", new visuperobject(34,1,2,"Giê-hô-va là Ðức Chúa Trời ghen và báo thù; Ðức Giê-hô-va báo thù, và đầy sự thạnh nộ. Ðức Giê-hô-va báo thù kẻ cừu địch mình và cưu giận cho kẻ thù mình."));
visuperobject.biblemap.put("b34_1_3", new visuperobject(34,1,3,"Ðức Giê-hô-va chậm giận và có quyền lớn; nhưng Ngài chẳng cầm kẻ mắc tội là vô tội. Ðức Giê-hô-va đi trong gió lốc và bão tố, những đám mây là bụi dưới chơn Ngài."));
visuperobject.biblemap.put("b34_1_4", new visuperobject(34,1,4,"Ngài quở trách biển, làm cho nó khô; và làm cạn hết thảy các sông. Ba-san và Cạt-mên đều mòn mỏi, hoa của Li-ban đều héo rụng."));
visuperobject.biblemap.put("b34_1_5", new visuperobject(34,1,5,"Các núi run rẩy vì cớ Ngài, các đồi tan chảy; đất và thế gian cũng dân cư trên đất đều dậy lên trước mặt Ngài."));
visuperobject.biblemap.put("b34_1_6", new visuperobject(34,1,6,"Ai đứng được trước sự thạnh nộ Ngài? Ai đương nổi sự nóng giận Ngài? Sự tức giận của Ngài đổ ra như lửa, những vầng đá vỡ ra bởi Ngài."));
visuperobject.biblemap.put("b34_1_7", new visuperobject(34,1,7,"Ðức Giê-hô-va là tốt lành, làm đồn lũy trong ngày hoạn nạn, và biết những kẻ ẩn náu nơi Ngài."));
visuperobject.biblemap.put("b34_1_8", new visuperobject(34,1,8,"Ngài dùng nước lụt hủy diệt chỗ nó, và đuổi kẻ thù nghịch mình vào sự tối tăm."));
visuperobject.biblemap.put("b34_1_9", new visuperobject(34,1,9,"Các ngươi sẽ lập mưu gì nghịch cùng Ðức Giê-hô-va? Ngài sẽ diệt hết cả, sẽ chẳng có tai nạn dậy lên lần thứ hai."));
visuperobject.biblemap.put("b34_1_10", new visuperobject(34,1,10,"Vì chúng nó xỏ xen như gai gốc, và mê man như say rượu, thì sẽ bị thiêu hủy hết như rơm khô."));
visuperobject.biblemap.put("b34_1_11", new visuperobject(34,1,11,"Ấy là từ ngươi mà ra một kẻ mưu sự dữ nghịch cùng Ðức Giê-hô-va, và toan sự gian ác."));
visuperobject.biblemap.put("b34_1_12", new visuperobject(34,1,12,"Ðức Giê-hô-va phán như vầy: Dầu chúng nó sức mạnh đầy đủ và người đông cũng sẽ bị từ đi và trở nên hư không. Dầu ta đã làm khổ ngươi, song ta sẽ chẳng làm khổ ngươi nữa."));
visuperobject.biblemap.put("b34_1_13", new visuperobject(34,1,13,"Nhưng bấy giờ ta sẽ bẻ gãy ách nó khỏi ngươi, và bứt dứt dây ngươi."));
visuperobject.biblemap.put("b34_1_14", new visuperobject(34,1,14,"Ðức Giê-hô-va đã truyền lịnh về ngươi, từ danh ngươi chẳng sanh ra nữa. Ta sẽ trừ bỏ tượng chạm và tượng đúc khỏi nhà các thần ngươi; ta sẽ làm mồ mả cho ngươi, vì ngươi là hèn mạt."));
visuperobject.biblemap.put("b34_1_15", new visuperobject(34,1,15,"Nầy, trên các núi có chơn của kẻ đem tin lành và rao sự bình an! Hỡi Giu-đa, ngươi nữa, nó sẽ bị diệt sạch."));
visuperobject.biblemap.put("b34_2_1", new visuperobject(34,2,1,"Kẻ tàn phá đã đến nghịch cũng ngươi; hãy giữ đồn lũy, canh phòng đường sá, thắt chặt lưng ngươi, và thâu góp cả sức mạnh ngươi!"));
visuperobject.biblemap.put("b34_2_2", new visuperobject(34,2,2,"Vì Ðức Giê-hô-va khôi phục sự vinh hiển của Gia-cốp như sự vinh hiển của Y-sơ-ra-ên, bởi những kẻ bóc lột đã bóc lột chúng nó, và đã phá hại những nhành nho chúng nó."));
visuperobject.biblemap.put("b34_2_3", new visuperobject(34,2,3,"Thuẫn của những người mạnh là đỏ, những lính chiến nó mặc áo màu điều; trong ngày nó dàn trận, gang thép của xe cộ sẽ lòe ra như lửa, và gươm giáo đều lay động."));
visuperobject.biblemap.put("b34_2_4", new visuperobject(34,2,4,"Những xe đi hỗn độn trong đường phố, và chạy đi chạy lại trong các đường rộng, thấy nó như những ngọn đuốc, và chạy như chớp!"));
visuperobject.biblemap.put("b34_2_5", new visuperobject(34,2,5,"Nó nhớ lại những người mạnh dạn của mình, nhưng chúng nó bước đi vấp váp; chúng vội vả đến nơi vách thành nó, sắm sẵn để chống cự."));
visuperobject.biblemap.put("b34_2_6", new visuperobject(34,2,6,"Các cửa sông mở ra, điên dại tan mất?"));
visuperobject.biblemap.put("b34_2_7", new visuperobject(34,2,7,"Ðã nhứt định: hoàng hậu bị bóc lột và bị bắt đi; các nàng hầu hạ người đi theo, rên siếc như bò câu, và đấm ngực mình."));
visuperobject.biblemap.put("b34_2_8", new visuperobject(34,2,8,"Vả, Ni-ni-ve, từ lâu nay nó đã như ao chứa nước. Nhưng bây giờ chúng nó chạy trốn! Có kẻ kêu rằng: Hãy đứng, hãy đứng! Song không ai quay lại!"));
visuperobject.biblemap.put("b34_2_9", new visuperobject(34,2,9,"Hãy cướp bạc, cướp vàng; vì của báu nó vô ngần, mọi đồ đạc quí nhiều vô số."));
visuperobject.biblemap.put("b34_2_10", new visuperobject(34,2,10,"Nó đã bị tàn phá, trống không, và hủy hoại hết; lòng chúng nó tan chảy; đầu gối chúng nó chạm nhau; mọi lưng đều đau, hết thảy mặt đều xám ngắt."));
visuperobject.biblemap.put("b34_2_11", new visuperobject(34,2,11,"Nào cái hang của sư tử, và nơi mà sư tử con nuôi nấng, là nơi sư tử đực, sư tử cái và con nó bước đi, mà không ai làm cho chúng nó sợ hãi bây giờ ở đâu?"));
visuperobject.biblemap.put("b34_2_12", new visuperobject(34,2,12,"Sư tử đực xé đủ mồi cho con nó, và bóp mồi cho sư tử cái, hang nó đầy mồi, ổ nó đầy của đã bắt được."));
visuperobject.biblemap.put("b34_2_13", new visuperobject(34,2,13,"Ðức Giê-hô-va vạn quân phán rằng: Nầy, ta nghịch cùng ngươi, sẽ đốt xe cộ ngươi, và làm cho tan ra khỏi; gươm sẽ nuốt những sư tử con của ngươi; ta sẽ dứt mồi ngươi khỏi đất; và tiếng của những sứ giả ngươi sẽ không nghe nữa."));
visuperobject.biblemap.put("b34_3_1", new visuperobject(34,3,1,"Khốn thay cho thành đổ máu! Nó đầy dẫy những sự dối trá và cường bạo, cướp bóc không thôi."));
visuperobject.biblemap.put("b34_3_2", new visuperobject(34,3,2,"Người ta nghe tăm roi, tiếng ầm của bánh xe; ngựa thì phóng đại, xe thì chạy mau."));
visuperobject.biblemap.put("b34_3_3", new visuperobject(34,3,3,"Những lính kỵ sấn tới; gươm sáng lòe, giáo nhấp nháng. Có đoàn đông kẻ bị giết, có từng đống thây lớn, xác chết nhiều vô số! Người ta vấp ngã trên những thây!"));
visuperobject.biblemap.put("b34_3_4", new visuperobject(34,3,4,"Ðó là vì cớ rất nhiều sự dâm đãng của con đĩ tốt đẹp khéo làm tà thuật ấy; nó bán các nước bởi sự dâm đãng, và bán các họ hàng bởi sự tà thuật."));
visuperobject.biblemap.put("b34_3_5", new visuperobject(34,3,5,"Ðức Giê-hô-va vạn quân phán: Nầy, ta nghịch cũng ngươi; ta lột áo xống ngươi, phô bày sự trần truồng ngươi ra cho các dân tộc, và tỏ sự xấu hổ ngươi ra cho các nước."));
visuperobject.biblemap.put("b34_3_6", new visuperobject(34,3,6,"Ta sẽ ném sự ô uế gớm ghiếc của ngươi trên ngươi, làm cho ngươi nên khinh hèn, làm trò cho mọi ngươi xem."));
visuperobject.biblemap.put("b34_3_7", new visuperobject(34,3,7,"Xảy ra có ai thấy ngươi thì sẽ lánh xa và nói rằng: Ni-ni-ve đã hoang vu! Ai sẽ có lòng thương xót người? Ta bởi đâu tìm cho ngươi những kẻ yên ủi?"));
visuperobject.biblemap.put("b34_3_8", new visuperobject(34,3,8,"Ngươi muốn hơn Nô-a-môn ở giữa các sông, nước bọc chung quanh, lấy biển làm đồn lũy, lấy biển làm vách thành hay sao?"));
visuperobject.biblemap.put("b34_3_9", new visuperobject(34,3,9,"Ê-thi-ô-bi và Ê-díp-tô là sức mạnh vô cùng của nó. Phút và Li-by là kẻ cứu giúp ngươi."));
visuperobject.biblemap.put("b34_3_10", new visuperobject(34,3,10,"Dầu vậy, chính nó cũng đã bị đày khỏi đất mình; bị bắt đi làm phu tù; con cái nó cũng đã bị nghiền nát nơi ngã ba các đường phố. Người ta đã ném thăm trên các kẻ cả nó, và các quan trưởng nó đều bị mang xiềng."));
visuperobject.biblemap.put("b34_3_11", new visuperobject(34,3,11,"Ngươi cũng vậy, ngươi sẽ mê man vì say, sẽ được ẩn náu, và tìm nơi vững bền vì cớ kẻ thù."));
visuperobject.biblemap.put("b34_3_12", new visuperobject(34,3,12,"Hết thảy những đồn lũy ngươi như cây vả có trái chín đầu mùa, hễ lung lay thì rụng xuống trong miệng của kẻ muốn ăn nó."));
visuperobject.biblemap.put("b34_3_13", new visuperobject(34,3,13,"Nầy, dân sự ngươi là đờn bà ở giữa ngươi. Các cửa thành của đất ngươi sẽ mở rộng cho quân thù ngươi; lửa đã thiêu nuốt những then ngươi!"));
visuperobject.biblemap.put("b34_3_14", new visuperobject(34,3,14,"Ngươi khá múc nước để phòng cơn vây hãm, làm vững chắc đồn lũy ngươi. Khá đạp bùn, nhồi đất sét, và xây lại lò gạch!"));
visuperobject.biblemap.put("b34_3_15", new visuperobject(34,3,15,"Tại đó lửa sẽ thiêu ngươi, gươm sẽ diệt ngươi, nuốt ngươi như cào cào vậy, ngươi khá nhóm lại đông như cào cào, nhiều như châu chấu!"));
visuperobject.biblemap.put("b34_3_16", new visuperobject(34,3,16,"Ngươi đã thêm kẻ buôn bán ngươi nhiều ra như sao trên trời; cào cào đã cắn phá hết rồi trốn đi."));
visuperobject.biblemap.put("b34_3_17", new visuperobject(34,3,17,"Các quan trưởng ngươi như cào cào, các quan tướng ngươi như bầy châu chấu đậu trên hàng rào trong khi trời lạnh, đến chừng mặt trời mọc, nó đi mất, người ta không biết nó ở đâu."));
visuperobject.biblemap.put("b34_3_18", new visuperobject(34,3,18,"Hỡi vua A-si-ri, những kẻ chăn của ngươi ngủ rồi! Những kẻ sang trọng của ngươi đều yên nghỉ; dân sự ngươi tan lạc nơi các núi, không ai nhóm họp chúng nó lại."));
visuperobject.biblemap.put("b34_3_19", new visuperobject(34,3,19,"Vết thương ngươi không thuốc chữa, dấu vít ngươi rất hiểm nghèo; phàm kẻ nghe nói về ngươi đều vỗ tay trên ngươi; vì ai là kẻ chẳng từng chịu luôn sự hung ác của ngươi!"));

}

}