visuperobject.biblemap.put("b4_2_28", new visuperobject(4,2,28,"và quân đội người, cứ theo kê sổ, cộng bốn mươi mốt ngàn năm trăm người."));
visuperobject.biblemap.put("b4_2_29", new visuperobject(4,2,29,"Kế đến chi phái Nép-ta-li; quan trưởng của người Nép-ta-li là A-hi-ra, con trai của Ê-nan;"));
visuperobject.biblemap.put("b4_2_30", new visuperobject(4,2,30,"và quân đội người, cứ theo kê sổ, cộng năm mươi ba ngàn bốn trăm người."));
visuperobject.biblemap.put("b4_2_31", new visuperobject(4,2,31,"Vậy, các người đã kê sổ theo trại quân Ðan, cộng một trăm năm mươi bảy ngàn sáu trăm người. Họ theo ngọn cờ mình phải đi hậu binh."));
visuperobject.biblemap.put("b4_2_32", new visuperobject(4,2,32,"Ðó là những người trong dân Y-sơ-ra-ên mà người ta tu bộ tùy theo tông tộc mình. Trong các trại quân hết thảy người đã kê sổ từng đội ngũ là sáu trăm ba ngàn năm trăm năm mươi người."));
visuperobject.biblemap.put("b4_2_33", new visuperobject(4,2,33,"Nhưng người Lê-vi không nhập sổ chung với dân Y-sơ-ra-ên, tùy theo mạng của Ðức Giê-hô-va đã truyền cho Môi-se."));
visuperobject.biblemap.put("b4_2_34", new visuperobject(4,2,34,"Dân Y-sơ-ra-ên y theo mọi điều Ðức Giê-hô-va đã phán dặn Môi-se, bèn đóng trại tùy theo ngọn cờ mình, và mỗi người ra đi tùy theo họ hàng và tông tộc mình."));
visuperobject.biblemap.put("b4_3_1", new visuperobject(4,3,1,"Nầy là dòng dõi của A-rôn và Môi-se, về ngày Ðức Giê-hô-va phán cùng Môi-se tại trên núi Si-na -i."));
visuperobject.biblemap.put("b4_3_2", new visuperobject(4,3,2,"Nầy là tên các con trai A-rôn: Trưởng nam là Na-đáp, A-bi-hu, Ê-lê-a-sa, và Y-tha-ma."));
visuperobject.biblemap.put("b4_3_3", new visuperobject(4,3,3,"Ðó là tên các con trai A-rôn, tức những thầy tế lễ đã chịu phép xức dầu, và được lập lên để làm chức tế lễ."));
visuperobject.biblemap.put("b4_3_4", new visuperobject(4,3,4,"Vả, Na-đáp và A-bi-hu đã thác trước mặt Ðức Giê-hô-va khi hai người dâng một thứ lửa lạ trước mặt Ðức Giê-hô-va tại trong đồng vắng Si-na -i. Hai người nầy không có con. Còn Ê-lê-a-sa và Y-tha-ma làm chức tế lễ trước mặt A-rôn, là cha mình."));
visuperobject.biblemap.put("b4_3_5", new visuperobject(4,3,5,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_3_6", new visuperobject(4,3,6,"Hãy khiến chi phái Lê-vi lại gần, đặt trước thầy tế lễ A-rôn, để người Lê-vi phục sự người."));
visuperobject.biblemap.put("b4_3_7", new visuperobject(4,3,7,"Người Lê-vi sẽ giữ chức phận của người và chức phận của cả hội chúng về trước hội mạc mà làm những công việc của đền tạm."));
visuperobject.biblemap.put("b4_3_8", new visuperobject(4,3,8,"Cũng phải coi sóc hết thảy đồ đạc của hội mạc và mọi vật chi dân Y-sơ-ra-ên giao cho đặng dùng làm công việc của đền tạm."));
visuperobject.biblemap.put("b4_3_9", new visuperobject(4,3,9,"Vậy, ngươi phải giao người Lê-vi cho A-rôn và cho các con trai người; vì đã lấy họ từ trong vòng dân Y-sơ-ra-ên mà ban trọn cho A-rôn rồi."));
visuperobject.biblemap.put("b4_3_10", new visuperobject(4,3,10,"Vậy, ngươi sẽ lập A-rôn và các con trai người, để làm chức tế lễ; người ngoại đến gần sẽ bị xử tử."));
visuperobject.biblemap.put("b4_3_11", new visuperobject(4,3,11,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_3_12", new visuperobject(4,3,12,"Nầy, từ giữa dân Y-sơ-ra-ên ta chọn lấy người Lê-vi thế cho hết thảy con đầu lòng của dân Y-sơ-ra-ên; vậy, người Lê-vi sẽ thuộc về ta."));
visuperobject.biblemap.put("b4_3_13", new visuperobject(4,3,13,"Vì hết thảy con đầu lòng đều thuộc về ta; ngày xưa khi ta hành hại các con đầu lòng trong xứ Ê-díp-tô, ta đã biệt riêng ra thánh cho ta hết thảy con đầu lòng trong Y-sơ-ra-ên, bất luận loài người hay thú vật; chúng nó đều sẽ thuộc về ta. Ta là Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_3_14", new visuperobject(4,3,14,"Ðức Giê-hô-va lại phán cùng Môi-se tại trong đồng vắng Si-na -i mà rằng:"));
visuperobject.biblemap.put("b4_3_15", new visuperobject(4,3,15,"Hãy kê sổ con cháu Lê-vi, tùy theo tông tộc và họ hàng chúng nó, kê hết thảy nam đinh từ một tháng sắp lên."));
visuperobject.biblemap.put("b4_3_16", new visuperobject(4,3,16,"Môi-se bèn kê sổ người Lê-vi theo mạng của Ðức Giê-hô-va, y như Ngài đã phán dặn người vậy."));
visuperobject.biblemap.put("b4_3_17", new visuperobject(4,3,17,"Ðây là các con trai của Lê-vi kể từng tên: Ghẹt-sôn, Kê-hát, và Mê-ra-ri."));
visuperobject.biblemap.put("b4_3_18", new visuperobject(4,3,18,"Ðây là tên của các con trai Ghẹt-sôn, tùy theo họ hàng mình: Líp-ni và Si-mê -y."));
visuperobject.biblemap.put("b4_3_19", new visuperobject(4,3,19,"Ðây là các con trai của Kê-hát tùy theo họ hàng mình: Am-ram, Dít-sa, Hếp-rôn và U-xi-ên;"));
visuperobject.biblemap.put("b4_3_20", new visuperobject(4,3,20,"và các con trai của Mê-ra-ri, tùy theo họ hàng mình, là: Mách-li và Mu-si. Ðó là những họ hàng của người Lê-vi, tùy theo tông tộc của họ."));
visuperobject.biblemap.put("b4_3_21", new visuperobject(4,3,21,"Họ hàng Líp-ni và họ hàng Si-mê -y thuộc về Ghẹt-sôn. Ðó là hai họ hàng của Ghẹt-sôn vậy."));
visuperobject.biblemap.put("b4_3_22", new visuperobject(4,3,22,"Những người trong họ Ghẹt-sôn được tu bộ, kể mọi nam đinh từ một tháng sắp lên, số là bảy ngàn năm trăm người."));
visuperobject.biblemap.put("b4_3_23", new visuperobject(4,3,23,"Các họ hàng của Ghẹt-sôn đóng trại về phía tây, sau đền tạm."));
visuperobject.biblemap.put("b4_3_24", new visuperobject(4,3,24,"Quan trưởng của tông tộc Ghẹt-sôn là Ê-li-a-sáp, con trai của La-ên."));
visuperobject.biblemap.put("b4_3_25", new visuperobject(4,3,25,"Chức phận của con cháu Ghẹt-sôn trong hội mạc là coi sóc mọi vật chi can đến đền tạm và Trại, tức là tấm bong của Trại và bức màn của cửa hội mạc,"));
visuperobject.biblemap.put("b4_3_26", new visuperobject(4,3,26,"các bố vi của hành lang, và bức màn của cửa hành lang bao phủ chung quanh đền tạm và bàn thờ, cùng dây chạc về các việc của đền tạm."));
visuperobject.biblemap.put("b4_3_27", new visuperobject(4,3,27,"Họ hàng Am-ram, họ hàng Dít-sa, họ hàng Hếp-rôn và họ hàng U-xi-ên thuộc về Kê-hát. Ðó là những họ hàng của Kê-hát."));
visuperobject.biblemap.put("b4_3_28", new visuperobject(4,3,28,"Cứ kể mọi nam đinh từ một tháng sắp lên, số là tám ngàn sáu trăm người có chức phận săn sóc nơi thánh."));
visuperobject.biblemap.put("b4_3_29", new visuperobject(4,3,29,"Các họ hàng của con cháu Kê-hát đóng trại bên hông đền tạm về phía nam."));
visuperobject.biblemap.put("b4_3_30", new visuperobject(4,3,30,"Quan trưởng của tông tộc Kê-hát là Ê-li-sa-phan, con trai của U-xi-ên."));
visuperobject.biblemap.put("b4_3_31", new visuperobject(4,3,31,"Người Kê-hát coi sóc hòm bảng chứng, cái bàn, chân đèn, các bàn thờ, và những đồ đạc của nơi thánh để dùng về việc thờ phượng, luôn với bức màn và các vật chi can đến."));
visuperobject.biblemap.put("b4_3_32", new visuperobject(4,3,32,"Tổng trưởng của người Lê-vi là Ê-lê-a-sa, con trai của thầy tế lễ A-rôn, được đặt lên quản trị những người coi sóc nơi thánh."));
visuperobject.biblemap.put("b4_3_33", new visuperobject(4,3,33,"Họ hàng Mách-li và họ hàng Mu-si thuộc về Mê-ra-ri. Ðó là hai họ hàng của Mê-ra-ri vậy."));
visuperobject.biblemap.put("b4_3_34", new visuperobject(4,3,34,"Những người trong bọn Mê-ra-ri được tu bộ, kể mọi nam đinh từ một tháng sắp lên, số là sáu ngàn hai trăm người."));
visuperobject.biblemap.put("b4_3_35", new visuperobject(4,3,35,"Quan trưởng của tông tộc Mê-ra-ri là Xu-ri-ên, con trai của A-bi-hai. Con cháu Mê-ra-ri đóng trại bên hông đền tạm về phía bắc,"));
visuperobject.biblemap.put("b4_3_36", new visuperobject(4,3,36,"có phần coi sóc các tấm ván của đền tạm, cây xà ngang, trụ, lỗ trụ, những đồ đạc, và mọi việc thuộc về đó,"));
visuperobject.biblemap.put("b4_3_37", new visuperobject(4,3,37,"những trụ ở chung quanh hành lang và lỗ trụ, những nọc và dây chạc của trụ."));
visuperobject.biblemap.put("b4_3_38", new visuperobject(4,3,38,"Những người đóng trại trước đền tạm về phía đông, tức trước hội mạc về phía mặt trời mọc, là Môi-se, A-rôn và các con trai người, có chức phận săn sóc nơi thánh thế cho dân Y-sơ-ra-ên. Người ngoại đến gần sẽ bị xử tử."));
visuperobject.biblemap.put("b4_3_39", new visuperobject(4,3,39,"Hết thảy người Lê-vi mà Môi-se và A-rôn vâng mạng Ðức Giê-hô-va tu bộ, tùy theo họ hàng của họ, kể mọi nam đinh từ một tháng sắp lên, số là hai mươi hai ngàn người."));
visuperobject.biblemap.put("b4_3_40", new visuperobject(4,3,40,"Ðoạn, Ðức Giê-hô-va phán cùng Môi-se rằng: Hãy kê sổ hết thảy con trưởng nam của dân Y-sơ-ra-ên, từ một tháng sắp lên, và hãy dựng sổ tên chúng nó."));
visuperobject.biblemap.put("b4_3_41", new visuperobject(4,3,41,"Ta là Ðức Giê-hô-va. Ngươi phải chọn lấy cho ta người Lê-vi thế cho các con trưởng nam trong dân Y-sơ-ra-ên; cũng phải bắt súc vật của người Lê-vi thế cho mọi con súc vật đầu lòng của dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_3_42", new visuperobject(4,3,42,"Vậy, Môi-se kê sổ các con trưởng nam của dân Y-sơ-ra-ên, y như Ðức Giê-hô-va đã phán dặn người."));
visuperobject.biblemap.put("b4_3_43", new visuperobject(4,3,43,"Hết thảy những con trưởng nam kể từng tên, từ tuổi một tháng sắp lên, theo sự kê sổ họ, là hai mươi hai ngàn hai trăm bảy mươi ba người."));
visuperobject.biblemap.put("b4_3_44", new visuperobject(4,3,44,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_3_45", new visuperobject(4,3,45,"Hãy chọn lấy những người Lê-vi thế cho hết thảy con trưởng nam của dân Y-sơ-ra-ên, và súc vật của người Lê-vi thế cho súc vật của dân Y-sơ-ra-ên; vậy, các người Lê-vi sẽ thuộc về ta. Ta là Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_3_46", new visuperobject(4,3,46,"Còn về sự chuộc lại con trưởng nam của dân Y-sơ-ra-ên, kê sổ hơn số người Lê-vi hai trăm bảy mươi ba,"));
visuperobject.biblemap.put("b4_3_47", new visuperobject(4,3,47,"thì ngươi phải thâu mỗi người năm siếc-lơ, tùy theo siếc-lơ nơi thánh; một siếc-lơ là hai chục ghê-ra."));
visuperobject.biblemap.put("b4_3_48", new visuperobject(4,3,48,"Ngươi phải giao bạc cho A-rôn và các con trai người; ấy là bạc chuộc lại những con trưởng nam trổi hơn."));
visuperobject.biblemap.put("b4_3_49", new visuperobject(4,3,49,"Vậy, Môi-se thâu bạc chuộc lại những con trưởng nam trổi hơn số người Lê-vi đã chuộc lại."));
visuperobject.biblemap.put("b4_3_50", new visuperobject(4,3,50,"Bạc mà người thâu về những con trưởng nam của dân Y-sơ-ra-ên, cộng được một ngàn ba trăm sáu mươi lăm siếc-lơ, theo siếc-lơ của nơi thánh."));
visuperobject.biblemap.put("b4_3_51", new visuperobject(4,3,51,"Môi-se vâng mạng Ðức Giê-hô-va, giao bạc chuộc lại cho A-rôn và các con trai người, y như Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b4_4_1", new visuperobject(4,4,1,"Ðức Giê-hô-va phán cùng Môi-se và A-rôn rằng:"));
visuperobject.biblemap.put("b4_4_2", new visuperobject(4,4,2,"Trong người Lê-vi hãy dựng sổ các con cháu Kê-hát, tùy theo họ hàng và tông tộc của chúng nó,"));
visuperobject.biblemap.put("b4_4_3", new visuperobject(4,4,3,"từ ba mươi tuổi sắp lên cho đến năm mươi, tức là hết thảy người nào có thể giúp công vào hội mạc."));
visuperobject.biblemap.put("b4_4_4", new visuperobject(4,4,4,"Nầy sẽ là phần việc của con cháu Kê-hát tại trong hội mạc. Họ sẽ coi sóc nơi chí thánh."));
visuperobject.biblemap.put("b4_4_5", new visuperobject(4,4,5,"Khi nào dời trại quân đi, A-rôn và các con trai người sẽ đến gỡ bức màn mà bao lấy hòm bảng chứng;"));
visuperobject.biblemap.put("b4_4_6", new visuperobject(4,4,6,"đoạn đắp lên một tấm phủ bằng da cá nược, trải trên một tấm nỉ toàn bằng màu điều xanh và xỏ đòn khiêng vào."));
visuperobject.biblemap.put("b4_4_7", new visuperobject(4,4,7,"Cũng trải một tấm nỉ màu điều xanh trên cái bàn về bánh trần thiết; ở trên bàn thì sắp dĩa, chén, tộ và chậu về những lễ quán; bánh hằng trần thiết cũng sẽ có ở trên nữa."));
visuperobject.biblemap.put("b4_4_8", new visuperobject(4,4,8,"Rồi trải trên các vật đó một tấm nỉ màu đỏ sặm, đắp một tấm phủ bằng da cá nược và xỏ đòn khiêng vào."));
visuperobject.biblemap.put("b4_4_9", new visuperobject(4,4,9,"Cũng phải lấy một tấm nỉ màu điều xanh mà đậy chân đèn thắp, các thếp đèn, kéo hớt tim, đồ đựng tro, và các bình dầu mà người ta dùng về chân đèn."));
visuperobject.biblemap.put("b4_4_10", new visuperobject(4,4,10,"Rồi để chân đèn và hết thảy đồ phụ tùng trong một tấm phủ bằng da cá nược, để lên trên cái sề."));
visuperobject.biblemap.put("b4_4_11", new visuperobject(4,4,11,"Họ phải trải trên bàn thờ bằng vàng một tấm nỉ màu điều xanh và đắp một tấm phủ bằng da cá nược, rồi xỏ đòn khiêng vào."));
visuperobject.biblemap.put("b4_4_12", new visuperobject(4,4,12,"Lại, cũng phải lấy hết thảy đồ đạc dùng về công việc nơi thánh, để trong một tấm nỉ màu điều xanh, rồi đắp một tấm phủ bằng da cá nược và để lên trên cái sề."));
visuperobject.biblemap.put("b4_4_13", new visuperobject(4,4,13,"Phải hốt tro của bàn thờ, trải ở trên một tấm nỉ màu đỏ điều,"));
visuperobject.biblemap.put("b4_4_14", new visuperobject(4,4,14,"để lên trên hết thảy đồ đạc dùng về công việc của bàn thờ, tức là những lư hương, nĩa, vá, chén, cùng những đồ phụ tùng của bàn thờ. Rồi đắp trên bàn thờ một tấm phủ bằng da cá nược và xỏ đòn khiêng vào."));
visuperobject.biblemap.put("b4_4_15", new visuperobject(4,4,15,"Sau khi A-rôn và các con trai người đã đậy-điệm nơi thánh và các đồ đạc thánh xong, khi dời trại quân đi, thì các con cháu Kê-hát phải đến đặng khiêng các đồ đạc đó; họ chẳng nên đụng vào những vật thánh, e phải chết chăng. Trong hội mạc, ấy là đồ mà các con cháu Kê-hát phải khiêng đi."));
visuperobject.biblemap.put("b4_4_16", new visuperobject(4,4,16,"Ê-lê-a-sa, con trai thầy tế lễ A-rôn, sẽ coi về dầu thắp đèn, hương-liệu, của lễ chay hằng hiến, dầu xức, cả đền tạm, và hết thảy vật chi ở trong nơi thánh với đồ phụ tùng của nơi thánh."));
visuperobject.biblemap.put("b4_4_17", new visuperobject(4,4,17,"Ðức Giê-hô-va lại phán cùng Môi-se và A-rôn rằng:"));
visuperobject.biblemap.put("b4_4_18", new visuperobject(4,4,18,"Hãy làm thể nào cho tông tộc Kê-hát chớ bị truất khỏi chi phái Lê-vi,"));
visuperobject.biblemap.put("b4_4_19", new visuperobject(4,4,19,"và hãy làm điều nầy cho chúng nó, để khi đến gần nơi chí thánh, chúng nó được sống, chẳng phải chết: A-rôn và các con trai người phải đến cắt công việc cho mỗi người, và đồ chi họ phải khiêng đi."));
visuperobject.biblemap.put("b4_4_20", new visuperobject(4,4,20,"Dẫu một lát, chúng nó chớ nên vào đặng xem những vật thánh, e phải chết chăng."));
visuperobject.biblemap.put("b4_4_21", new visuperobject(4,4,21,"Ðức Giê-hô-va lại phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_4_22", new visuperobject(4,4,22,"Cũng hãy dựng sổ các con cháu Ghẹt-sôn, tùy theo tông tộc và họ hàng họ."));
visuperobject.biblemap.put("b4_4_23", new visuperobject(4,4,23,"Từ ba mươi tuổi sắp lên cho đến năm mươi, ngươi khá kê sổ hết thảy những người nào có thể làm công việc trong hội mạc."));
visuperobject.biblemap.put("b4_4_24", new visuperobject(4,4,24,"Nầy là chức việc của các họ hàng Ghẹt-sôn, hoặc phải làm hoặc phải khiêng đồ:"));
visuperobject.biblemap.put("b4_4_25", new visuperobject(4,4,25,"họ sẽ mang những tấm màn của đền tạm và hội mạc, tấm bong của hội mạc, tấm lá-phủ bằng da cá nược ở trên, và bức màn của cửa hội mạc;"));
visuperobject.biblemap.put("b4_4_26", new visuperobject(4,4,26,"các bố vi của hành lang ở chung quanh đền tạm và bàn thờ, bức màn của cửa hành lang, những dây chạc, và hết thảy đồ dùng về các vật đó, cùng làm các công việc về những món ấy."));
visuperobject.biblemap.put("b4_4_27", new visuperobject(4,4,27,"Hết thảy công việc của con cháu Ghẹt-sôn, món chi họ phải khiêng đi, và việc chi họ phải làm điều sẽ theo lịnh A-rôn và các con trai người chỉ biểu; người phải giao cho họ coi sóc mọi món chi phải khiêng đi."));
visuperobject.biblemap.put("b4_4_28", new visuperobject(4,4,28,"Trong hội mạc, ấy là công việc của các họ hàng con cháu Ghẹt-sôn. Họ sẽ giữ chức phận mình dưới quyền cai trị của Y-tha-ma, con trai thầy tế lễ A-rôn."));
visuperobject.biblemap.put("b4_4_29", new visuperobject(4,4,29,"Ngươi phải kê sổ con cháu Mê-ra-ri, tùy theo họ hàng và tông tộc của họ,"));
visuperobject.biblemap.put("b4_4_30", new visuperobject(4,4,30,"từ ba mươi tuổi sắp lên cho đến năm mươi, tức là hết thảy người nào có thể giúp công vào hội mạc."));
visuperobject.biblemap.put("b4_4_31", new visuperobject(4,4,31,"Về các công việc của con cháu Mê-ra-ri trong hội mạc, nầy là vật họ phải coi sóc và khiêng đi: những tấm ván của đền tạm, các xà ngang, trụ, lỗ trụ,"));
visuperobject.biblemap.put("b4_4_32", new visuperobject(4,4,32,"các trụ chung quanh hành lang và lỗ trụ, nọc, dây chạc, cùng hết thảy đồ đạc và các vật chi dùng về việc đó; các ngươi sẽ kê sổ từng tên những vật chi họ phải coi sóc và khiêng đi."));
visuperobject.biblemap.put("b4_4_33", new visuperobject(4,4,33,"Ðó là hết thảy việc của các họ hàng Mê-ra-ri, về những công việc trong hội mạc mà họ phải làm ở dưới quyền cai trị của Y-tha-ma, con trai thầy tế lễ A-rôn."));
visuperobject.biblemap.put("b4_4_34", new visuperobject(4,4,34,"Vậy, Môi-se, A-rôn và những hội trưởng kê sổ con cháu Kê-hát, tùy theo họ hàng và tông tộc của họ,"));
visuperobject.biblemap.put("b4_4_35", new visuperobject(4,4,35,"từ ba mươi tuổi sắp lên cho đến năm mươi, tức là hết thảy người nào có thể giúp công trong hội mạc;"));
visuperobject.biblemap.put("b4_4_36", new visuperobject(4,4,36,"và những người mà người ta kê sổ, tùy theo họ hàng mình, cộng được hai ngàn bảy trăm năm mươi."));
visuperobject.biblemap.put("b4_4_37", new visuperobject(4,4,37,"Ðó là những người của các họ hàng Kê-hát đã bị kê sổ, tức là hết thảy người hầu việc trong hội mạc mà Môi-se và A-rôn kê sổ, y theo mạng của Ðức Giê-hô-va đã cậy Môi-se truyền dạy."));
visuperobject.biblemap.put("b4_4_38", new visuperobject(4,4,38,"Còn con cháu của Ghẹt-sôn đã bị kê sổ, tùy theo họ hàng và tông tộc của họ,"));
visuperobject.biblemap.put("b4_4_39", new visuperobject(4,4,39,"từ ba mươi tuổi sắp lên cho đến năm mươi, tức là hết thảy người có thể giúp công trong hội mạc,"));
visuperobject.biblemap.put("b4_4_40", new visuperobject(4,4,40,"cộng được hai ngàn sáu trăm ba mươi."));
visuperobject.biblemap.put("b4_4_41", new visuperobject(4,4,41,"Ðó là những người về các họ hàng con cháu Ghẹt-sôn đã bị kê sổ, tức là mọi người hầu việc trong hội mạc mà Môi-se và A-rôn tu bộ y theo mạng của Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_4_42", new visuperobject(4,4,42,"Còn những người về họ hàng con cháu Mê-ra-ri đã bị kê sổ, tùy theo họ hàng và tông tộc của họ,"));
visuperobject.biblemap.put("b4_4_43", new visuperobject(4,4,43,"từ ba mươi tuổi sắp lên cho đến năm mươi, tức là mọi người có thể giúp công trong hội mạc,"));
visuperobject.biblemap.put("b4_4_44", new visuperobject(4,4,44,"cộng được ba ngàn hai trăm."));
visuperobject.biblemap.put("b4_4_45", new visuperobject(4,4,45,"Ðó là những người về các họ hàng con cháu Mê-ra-ri đã bị kê sổ. Môi-se và A-rôn lấy bộ dân, y theo mạng của Ðức Giê-hô-va đã cậy Môi-se truyền dạy."));
visuperobject.biblemap.put("b4_4_46", new visuperobject(4,4,46,"Hết thảy những kẻ trong người Lê-vi mà Môi-se, A-rôn, và các quan trưởng Y-sơ-ra-ên tu bộ, tùy theo họ hàng và tông tộc của họ,"));
visuperobject.biblemap.put("b4_4_47", new visuperobject(4,4,47,"từ ba mươi tuổi sắp lên cho đến năm mươi, tức là mọi người đến đặng hầu việc và khiêng đồ trong hội mạc,"));
visuperobject.biblemap.put("b4_4_48", new visuperobject(4,4,48,"cộng được tám ngàn năm trăm tám mươi."));
visuperobject.biblemap.put("b4_4_49", new visuperobject(4,4,49,"Người ta tu bộ người Lê-vi tùy theo mạng của Ðức Giê-hô-va đã cậy Môi-se truyền dạy, cắt phần cho mỗi người việc mình phải làm, và vật chi mình phải khiêng đi; người ta tu bộ dân tùy theo lời Ðức Giê-hô-va đã phán dặn Môi-se vậy."));
visuperobject.biblemap.put("b4_5_1", new visuperobject(4,5,1,"Ðoạn, Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_5_2", new visuperobject(4,5,2,"Hãy biểu dân Y-sơ-ra-ên đuổi ra ngoài trại quân hết thảy người phung, người có bịnh bạch trược, và người vì cớ đụng đến một xác chết nào đã bị ô uế."));
visuperobject.biblemap.put("b4_5_3", new visuperobject(4,5,3,"Bất luận nam hay nữ, các ngươi phải đuổi họ ra ngoài trại quân, hầu cho họ không làm cho trại quân bị ô uế, là nơi ta ngự ở trong."));
visuperobject.biblemap.put("b4_5_4", new visuperobject(4,5,4,"Dân Y-sơ-ra-ên bèn làm như vậy, đuổi họ ra khỏi trại quân, y như Ðức Giê-hô-va đã truyền lịnh cho Môi-se vậy."));
visuperobject.biblemap.put("b4_5_5", new visuperobject(4,5,5,"Ðức Giê-hô-va lại phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_5_6", new visuperobject(4,5,6,"Hãy nói cùng dân Y-sơ-ra-ên như vầy: Khi một người nam hay nữ phạm một trong những tội người ta thường phạm, cho đến can phạm cùng Ðức Giê-hô-va, và vì cớ đó phải mắc tội,"));
visuperobject.biblemap.put("b4_5_7", new visuperobject(4,5,7,"thì người ấy phải xưng tội mình đã phạm ra, và trả tang vật lại đủ, và thêm một phần năm giá vật mà giao cho người mình đã mắc tội cùng."));
visuperobject.biblemap.put("b4_5_8", new visuperobject(4,5,8,"Nếu người nầy không còn, và không bà con nào để lãnh tang vật đó lại, thì vật ấy sẽ về Ðức Giê-hô-va, tức là về thầy tế lễ, ngoại trừ con chiên đực về lễ chuộc tội mà thầy tế lễ sẽ dùng đặng chuộc tội cho người mắc tội đó."));
visuperobject.biblemap.put("b4_5_9", new visuperobject(4,5,9,"Phàm lễ vật chi biệt riêng ra thánh mà dân Y-sơ-ra-ên dâng cho thầy tế lễ, đều sẽ thuộc về người."));
visuperobject.biblemap.put("b4_5_10", new visuperobject(4,5,10,"Những vật biệt riêng ra thánh mà mỗi người dâng sẽ thuộc về người; vật chi mỗi người ban cho thầy tế lễ, chắc sẽ thuộc về người vậy."));
visuperobject.biblemap.put("b4_5_11", new visuperobject(4,5,11,"Ðức Giê-hô-va lại phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_5_12", new visuperobject(4,5,12,"Hãy nói cho dân Y-sơ-ra-ên rằng: Nếu một người đàn bà lỗi đạo và phạm tội bất chánh cùng chồng,"));
visuperobject.biblemap.put("b4_5_13", new visuperobject(4,5,13,"nếu có một người nam gian dâm cùng nàng mà việc nhẹm khuất mắt chồng; nếu nàng bị ô uế kín nhiệm, không có chứng cớ đối cùng nàng, và nàng không bị bắt tại trận;"));
visuperobject.biblemap.put("b4_5_14", new visuperobject(4,5,14,"nếu tánh ghen phát nơi người chồng ghen vợ mình, hoặc đã thật bị ô uế, hoặc không có bị ô uế,"));
visuperobject.biblemap.put("b4_5_15", new visuperobject(4,5,15,"thì người nầy phải dẫn vợ mình đến trước mặt thầy tế lễ, và vì nàng đem theo một phần mười ê-pha bột mạch nha dùng làm của tế lễ. Người chớ nên chế dầu vào, và cũng chẳng nên bỏ nhũ hương lên trên, vì là một của lễ chay về sự ghen tương, một của lễ chay kỷ niệm đặng nhắc tội gian ác."));
visuperobject.biblemap.put("b4_5_16", new visuperobject(4,5,16,"Thầy tế lễ sẽ biểu người nữ đến gần, đứng trước mặt Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_5_17", new visuperobject(4,5,17,"Kế đó thầy tế lễ lấy nước thánh đổ vào chậu đất, hốt bụi ở trên đất của đền tạm mà bỏ trong nước."));
visuperobject.biblemap.put("b4_5_18", new visuperobject(4,5,18,"Ðoạn, thầy tế lễ phải biểu người nữ đứng trước mặt Ðức Giê-hô-va, lột trần đầu nàng, để của lễ kỷ niệm trên lòng bàn tay nàng, nghĩa là của lễ chay về sự ghen tương; và thầy tế lễ phải có nước đắng giáng rủa sả trong tay mình."));
visuperobject.biblemap.put("b4_5_19", new visuperobject(4,5,19,"Thầy tế lễ phải bắt người nữ thề, và nói cùng nàng rằng: Nếu chẳng một người nam nào nằm cùng ngươi, và nếu đương dưới quyền chồng ngươi không có lỗi đạo và không bị ô uế, thì chẳng phải mắc một điều hại nào của nước đắng giáng rủa sả nầy."));
visuperobject.biblemap.put("b4_5_20", new visuperobject(4,5,20,"Còn nếu đương dưới quyền chồng, mà ngươi lỗi đạo; nếu ngươi bị ô uế, và nếu một người nào khác hơn chồng đã nằm cùng ngươi"));
visuperobject.biblemap.put("b4_5_21", new visuperobject(4,5,21,"bấy giờ thầy tế lễ phải bắt người nữ lấy một lời thề trù ẻo mà thề, và nói cùng nàng rằng: Cầu Ðức Giê-hô-va khiến cho ngươi trở nên một mầm rủa sả và trù ẻo giữa vòng dân sự ngươi, làm cho ngươi ốm lòi hông và phình bụng lên;"));
visuperobject.biblemap.put("b4_5_22", new visuperobject(4,5,22,"nước đắng giáng rủa sả nầy khá chun vào ruột gan làm cho bụng ngươi phình lên và ốm lòi hông. Người nữ sẽ nói rằng: A-men, a-men!"));
visuperobject.biblemap.put("b4_5_23", new visuperobject(4,5,23,"Kế đó, thầy tế lễ phải viết các lời trù ẻo nầy trong một cuốn sách, rồi lấy nước đắng bôi đi."));
visuperobject.biblemap.put("b4_5_24", new visuperobject(4,5,24,"Thầy tế lễ sẽ biểu người nữ uống nước đắng giáng rủa sả, nước đắng giáng rủa sả sẽ vào trong mình nàng đặng làm cay đắng cho nàng."));
visuperobject.biblemap.put("b4_5_25", new visuperobject(4,5,25,"Ðoạn, thầy tế lễ sẽ lấy khỏi tay người nữ của lễ chay về sự ghen tương, đưa qua đưa lại trước mặt Ðức Giê-hô-va và dâng lên trên bàn thờ."));
visuperobject.biblemap.put("b4_5_26", new visuperobject(4,5,26,"Rồi lấy một nắm của lễ chay kỷ niệm và xông trên bàn thờ; kế biểu người nữ uống nước đắng."));
visuperobject.biblemap.put("b4_5_27", new visuperobject(4,5,27,"Vả, khi nào thầy tế lễ biểu người uống nước đắng rồi, nếu quả người có bị ô uế phạm tội bất chánh cùng chồng mình, thì nước đắng giáng rủa sả sẽ vào mình làm cay đắng cho nàng, bụng nàng sẽ phình lên, hông nàng ốm lòi, và người nữ nầy sẽ làm một mầm rủa sả giữa vòng dân sự mình."));
visuperobject.biblemap.put("b4_5_28", new visuperobject(4,5,28,"Còn nếu người nữ không bị ô uế, vẫn thanh sạch, thì nàng sẽ chẳng bị một điều hại nào, và sẽ sanh con."));
visuperobject.biblemap.put("b4_5_29", new visuperobject(4,5,29,"Ðó là luật lệ về sự ghen tương khi một người đàn bà, ở dưới quyền chồng mình, lỗi đạo và bị ô uế;"));
visuperobject.biblemap.put("b4_5_30", new visuperobject(4,5,30,"hoặc khi nào tánh ghen phát sanh nơi người chồng mà ghen vợ mình: người phải đem vợ đến trước mặt Ðức Giê-hô-va, và thầy tế lễ sẽ làm cho nàng hết thảy điều chi luật lệ nầy truyền dạy."));
visuperobject.biblemap.put("b4_5_31", new visuperobject(4,5,31,"Người chồng sẽ vô tội, còn người đàn bà đó sẽ mang lấy tội mình."));
visuperobject.biblemap.put("b4_6_1", new visuperobject(4,6,1,"Ðức Giê-hô-va lại phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_6_2", new visuperobject(4,6,2,"Hãy nói cho dân Y-sơ-ra-ên rằng: Bất luận người nam hay nữ, khi hứa nguyện Na-xi-rê đặng biệt mình riêng ra cho Ðức Giê-hô-va,"));
visuperobject.biblemap.put("b4_6_3", new visuperobject(4,6,3,"thì phải kiêng cữ rượu và vật uống say; chẳng nên uống hoặc giấm rượu, hoặc giấm của vật uống say; lại cũng chẳng nên uống một thứ nước nào bằng trái nho, hay là ăn nho tươi, hoặc nho khô."));
visuperobject.biblemap.put("b4_6_4", new visuperobject(4,6,4,"Trọn trong lúc người biệt mình riêng ra, chớ ăn món chi của nho sanh sản hết, từ hột đến da."));
visuperobject.biblemap.put("b4_6_5", new visuperobject(4,6,5,"Trọn trong lúc hứa nguyện Na-xi-rê mình, chớ nên dùng dao cạo đầu; người sẽ nên thánh, và phải để tóc mọc cho đến chừng nào kỳ biệt mình riêng ra cho Ðức Giê-hô-va đã được mãn."));
visuperobject.biblemap.put("b4_6_6", new visuperobject(4,6,6,"Trọn trong lúc người biệt mình riêng ra cho Ðức Giê-hô-va chớ đến gần người chết."));
visuperobject.biblemap.put("b4_6_7", new visuperobject(4,6,7,"Người chớ vì cha hoặc vì mẹ, vì anh hoặc vì chị đã chết, mà làm cho mình bị ô uế, vì dấu chỉ sự biệt riêng ra cho Ðức Chúa Trời vẫn ở trên đầu người."));
visuperobject.biblemap.put("b4_6_8", new visuperobject(4,6,8,"Trọn trong lúc hứa nguyện Na-xi-rê, người được biệt ra thánh cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_6_9", new visuperobject(4,6,9,"Nếu ai chết thình lình gần bên người, làm cho ô uế đầu mình đã biệt riêng ra thánh, thì phải cạo đầu mình trong ngày được sạch, tức là ngày thứ bảy;"));
visuperobject.biblemap.put("b4_6_10", new visuperobject(4,6,10,"qua ngày thứ tám, người phải đem hai chim cu hay là hai bò câu con đến cho thầy tế lễ tại cửa hội mạc."));
visuperobject.biblemap.put("b4_6_11", new visuperobject(4,6,11,"Thầy tế lễ sẽ dâng con nầy làm của lễ chuộc tội, con kia làm của lễ thiêu, và chuộc tội cho người đã phạm, vì cớ xác chết. Vậy, trong ngày đó người sẽ biệt đầu mình riêng ra thánh."));
visuperobject.biblemap.put("b4_6_12", new visuperobject(4,6,12,"Thế thì, người phải biệt riêng ra cho Ðức Giê-hô-va những ngày về nguyện Na-xi-rê mình lại, và dâng một con chiên đực giáp năm làm của lễ chuộc sự mắc lỗi, và những ngày trước không kể nữa, vì sự Na-xi-rê mình đã bị ô uế."));
visuperobject.biblemap.put("b4_6_13", new visuperobject(4,6,13,"Nầy là luật lệ về người Na-xi-rê: Khi nào những ngày Na-xi-rê mình đã mãn, người ta phải đem người đến tại cửa hội mạc;"));
visuperobject.biblemap.put("b4_6_14", new visuperobject(4,6,14,"người phải dâng của lễ mình cho Ðức Giê-hô-va, tức là một con chiên con giáp năm không tì vít, làm của lễ thiêu, một con chiên cái giáp năm không tì vít, làm của lễ chuộc tội, và một con chiên đực không tì vít, làm của lễ thù ân;"));
visuperobject.biblemap.put("b4_6_15", new visuperobject(4,6,15,"một giỏ bánh không men bằng bột lọc, bánh nhỏ tưới dầu, bánh tráng không men thoa dầu, luôn với của lễ chay và lễ quán cặp theo các lễ kia."));
visuperobject.biblemap.put("b4_6_16", new visuperobject(4,6,16,"Thầy tế lễ sẽ đem những của lễ nầy đến trước mặt Ðức Giê-hô-va, và dâng của lễ chuộc tội cùng của lễ thiêu của người;"));
visuperobject.biblemap.put("b4_6_17", new visuperobject(4,6,17,"rồi ngoại trừ giỏ bánh không men, dâng luôn con chiên đực làm của lễ thù ân, cùng dâng của lễ chay và lễ quán của người."));
visuperobject.biblemap.put("b4_6_18", new visuperobject(4,6,18,"Tại cửa hội mạc, người Na-xi-rê phải cạo đầu mình đã biệt riêng ra, lấy tóc bỏ vào lửa dưới của lễ thù ân."));
visuperobject.biblemap.put("b4_6_19", new visuperobject(4,6,19,"Sau khi người Na-xi-rê cạo đầu rồi, thầy tế lễ sẽ lấy cái vai chín của chiên đực, một cái bánh nhỏ không men trong giỏ, một bánh tráng không men và để trong lòng hai bàn tay người,"));
visuperobject.biblemap.put("b4_6_20", new visuperobject(4,6,20,"rồi đưa qua đưa lại trước mặt Ðức Giê-hô-va: ấy là một vật thánh về thầy tế lễ với cái o đưa qua đưa lại và cái giò dâng giơ lên. Kế đó, người Na-xi-rê được phép uống rượu."));
visuperobject.biblemap.put("b4_6_21", new visuperobject(4,6,21,"Ðó là luật lệ về người Na-xi-rê đã hứa nguyện, và đây là của lễ người phải dâng cho Ðức Giê-hô-va về nguyện Na-xi-rê mình, ngoại trừ của lễ nào người có thể dâng thêm. Người phải theo luật lệ về sự Na-xi-rê mình mà làm theo lời hứa nguyện của mình vậy."));
visuperobject.biblemap.put("b4_6_22", new visuperobject(4,6,22,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_6_23", new visuperobject(4,6,23,"Hãy nói cùng A-rôn và các con trai người mà rằng: Các ngươi phải chúc phước cho dân Y-sơ-ra-ên như vầy:"));
visuperobject.biblemap.put("b4_6_24", new visuperobject(4,6,24,"Cầu xin Ðức Giê-hô-va ban phước cho ngươi và phù hộ ngươi!"));
visuperobject.biblemap.put("b4_6_25", new visuperobject(4,6,25,"Cầu xin Ðức Giê-hô-va chiếu sáng mặt Ngài trên ngươi, và làm ơn cho ngươi!"));
visuperobject.biblemap.put("b4_6_26", new visuperobject(4,6,26,"Cầu xin Ðức Giê-hô-va đoái xem ngươi và ban bình an cho ngươi!"));
visuperobject.biblemap.put("b4_6_27", new visuperobject(4,6,27,"Họ phải đặt danh ta trên dân Y-sơ-ra-ên như vầy, thì ta đây sẽ ban phước cho dân đó."));
visuperobject.biblemap.put("b4_7_1", new visuperobject(4,7,1,"Khi Môi-se đã dựng xong đền tạm, và đã xức dầu cùng biệt riêng ra thánh hết thảy đồ đạc của đền tạm, xức dầu và biệt riêng ra thánh bàn thờ và hết thảy đồ đạc của bàn thờ rồi,"));
visuperobject.biblemap.put("b4_7_2", new visuperobject(4,7,2,"thì các trưởng tộc của Y-sơ-ra-ên, tức là các quan trưởng về những chi phái mà đã cai trị cuộc tu bộ dân, đều đem dâng lễ vật của mình."));
visuperobject.biblemap.put("b4_7_3", new visuperobject(4,7,3,"Vậy, các quan trưởng dẫn lễ vật mình đến trước mặt Ðức Giê-hô-va: Sáu xe cộ, hình như cái kiệu, và mười hai con bò đực, nghĩa là mỗi hai quan trưởng đồng dâng một xe cộ, và mỗi một người dâng một con bò đực; họ dâng lễ vật đó trước đền tạm."));
visuperobject.biblemap.put("b4_7_4", new visuperobject(4,7,4,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng: Hãy thâu nhận các lễ vật của những người nầy, để dùng vào công việc hội mạc; và hãy giao cho người Lê-vi,"));
visuperobject.biblemap.put("b4_7_5", new visuperobject(4,7,5,"tức là cho mọi người tùy theo chức phận mình."));
visuperobject.biblemap.put("b4_7_6", new visuperobject(4,7,6,"Vậy, Môi-se bèn nhận lấy các xe cộ và những bò đực, giao cho người Lê-vi."));
visuperobject.biblemap.put("b4_7_7", new visuperobject(4,7,7,"Người giao cho con cháu Ghẹt-sôn hai xe cộ và bốn con bò đực, tùy theo chức phận của họ."));
visuperobject.biblemap.put("b4_7_8", new visuperobject(4,7,8,"Người giao cho con cháu Mê-ra-ri bốn xe cộ và tám con bò đực, tùy theo chức phận của họ, có Y-tha-ma, con trai thầy tế lễ A-rôn, làm quản lý."));
visuperobject.biblemap.put("b4_7_9", new visuperobject(4,7,9,"Nhưng người không có giao chi cho con cháu Kê-hát, vì họ mắc phần công việc về những vật thánh, và gánh vác những vật đó trên vai mình."));
visuperobject.biblemap.put("b4_7_10", new visuperobject(4,7,10,"Trong ngày bàn thờ được xức dầu, các quan trưởng dâng lễ vật mình trước bàn thờ về cuộc khánh thành nó."));
visuperobject.biblemap.put("b4_7_11", new visuperobject(4,7,11,"Ðức Giê-hô-va phán cùng Môi-se rằng: Các quan trưởng phải đem lễ vật mình về cuộc khánh thành bàn thờ, người nầy thì ngày nay, người kia thì ngày kia."));
visuperobject.biblemap.put("b4_7_12", new visuperobject(4,7,12,"Người dâng lễ vật mình ngày đầu, là Na-ha-sôn, con trai của A-mi-na-đáp, thuộc về chi phái Giu-đa."));
visuperobject.biblemap.put("b4_7_13", new visuperobject(4,7,13,"Lễ-vật người là một cái đĩa bạc nặng một trăm ba mươi siếc-lơ, một cái chậu bạc nặng bảy chục siếc-lơ, theo siếc-lơ của nơi thánh; cả hai cái đều đầy bột lọc nhồi dầu, dùng về của lễ chay;"));
visuperobject.biblemap.put("b4_7_14", new visuperobject(4,7,14,"một cái chén vàng nặng mười siếc-lơ, đầy thuốc thơm;"));
visuperobject.biblemap.put("b4_7_15", new visuperobject(4,7,15,"một con bò đực tơ, một con chiên đực, một con chiên con giáp năm, dùng về của lễ thiêu;"));
visuperobject.biblemap.put("b4_7_16", new visuperobject(4,7,16,"một con dê đực dùng về của lễ chuộc tội;"));
visuperobject.biblemap.put("b4_7_17", new visuperobject(4,7,17,"và về của lễ thù ân, hai con bò đực, năm con chiên đực, năm con dê đực, và năm con chiên con giáp năm. Ðó là lễ vật của Na-ha-sôn, con trai A-mi-na-đáp."));
visuperobject.biblemap.put("b4_7_18", new visuperobject(4,7,18,"Ngày thứ hai, Na-tha-na-ên, con trai của Xu-a, quan trưởng Y-sa-ca, dâng lễ vật mình."));
