visuperobject.biblemap.put("b4_7_83", new visuperobject(4,7,83,"và về của lễ thù ân, hai con bò đực, năm con chiên đực, năm con dê đực, và năm con chiên con giáp năm. Ðó là lễ vật của A-hi-ra, con trai Ê-nan."));
visuperobject.biblemap.put("b4_7_84", new visuperobject(4,7,84,"Ấy là những lễ vật của các quan trưởng dân Y-sơ-ra-ên dâng về cuộc khánh thành bàn thờ, khi bàn thờ được xức dầu: Mười hai đĩa bạc, mười hai chậu bạc, mười hai chén vàng;"));
visuperobject.biblemap.put("b4_7_85", new visuperobject(4,7,85,"mỗi cái đĩa bạc nặng một trăm ba mươi siếc-lơ, và mỗi cái chậu bạc nặng bảy chục siếc-lơ. Tổng cộng số bạc của các đĩa và chậu là hai ngàn bốn trăm siếc-lơ, theo siếc-lơ của nơi thánh."));
visuperobject.biblemap.put("b4_7_86", new visuperobject(4,7,86,"Mười hai cái chén vàng đầy thuốc thơm, mỗi cái mười siếc-lơ, theo siếc-lơ của nơi thánh; tổng cộng số vàng của chén nặng được một trăm hai mươi siếc-lơ."));
visuperobject.biblemap.put("b4_7_87", new visuperobject(4,7,87,"Tổng cộng số bò đực về của lễ thiêu là mười hai con, với mười hai con chiên đực, mười hai con chiên con giáp năm cùng những của lễ chay cặp theo, và mười hai con dê đực dùng về của lễ chuộc tội."));
visuperobject.biblemap.put("b4_7_88", new visuperobject(4,7,88,"Tổng cộng số bò đực về của lễ thù ân là hai mươi bốn con với sáu mươi con chiên đực, sáu mươi con dê đực, sáu mươi con chiên con giáp năm. Ấy là lễ vật dâng về cuộc khánh thành bàn thờ, sau khi bàn thờ được xức dầu rồi."));
visuperobject.biblemap.put("b4_7_89", new visuperobject(4,7,89,"Khi Môi-se vào hội mạc đặng hầu chuyện cùng Ðức Giê-hô-va, thì người nghe Tiếng nói cùng mình từ trên nắp thi ân để trên hòm bảng chứng, ở giữa hai chê-ru-bin, người hầu chuyện cùng Ðức Giê-hô-va vậy."));
visuperobject.biblemap.put("b4_8_1", new visuperobject(4,8,1,"Ðức Giê-hô-va cũng phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_8_2", new visuperobject(4,8,2,"Hãy nói cùng A-rôn: Khi nào người đặt các đèn, thì bảy ngọn đèn phải chiếu trên phía trước chân đèn."));
visuperobject.biblemap.put("b4_8_3", new visuperobject(4,8,3,"A-rôn bèn làm như vậy; đặt bảy ngọn đèn trên phía trước chân đèn, y như Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b4_8_4", new visuperobject(4,8,4,"Vả, chân đèn đã làm theo cách nầy; làm bằng vàng đánh giát, cho đến cái chân và những cái hoa đều đánh giát. Ấy vậy, người ta làm chân đèn theo như kiểu mà Ðức Giê-hô-va đã chỉ cho Môi-se xem."));
visuperobject.biblemap.put("b4_8_5", new visuperobject(4,8,5,"Ðoạn, Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_8_6", new visuperobject(4,8,6,"Hãy chọn lấy người Lê-vi ở trong vòng dân Y-sơ-ra-ên và dọn cho họ được sạch."));
visuperobject.biblemap.put("b4_8_7", new visuperobject(4,8,7,"Nầy là điều ngươi phải làm cho họ được sạch: Ngươi phải rảy nước rửa tội trên mình họ; họ phải cạo hết thảy cùng mình, giặt quần áo, và dọn cho mình được sạch."));
visuperobject.biblemap.put("b4_8_8", new visuperobject(4,8,8,"Ðoạn, họ phải bắt một con bò đực với của lễ chay bằng bột lọc nhồi dầu cặp theo; còn ngươi bắt một con bò đực tơ thứ nhì dùng về của lễ chuộc tội;"));
visuperobject.biblemap.put("b4_8_9", new visuperobject(4,8,9,"rồi biểu người Lê-vi đến gần trước hội mạc và nhóm cả hội chúng Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_8_10", new visuperobject(4,8,10,"Vậy, người phải biểu người Lê-vi đến gần trước mặt Ðức Giê-hô-va, rồi dân Y-sơ-ra-ên sẽ đặt tay trên mình người Lê-vi."));
visuperobject.biblemap.put("b4_8_11", new visuperobject(4,8,11,"A-rôn sẽ dâng người Lê-vi làm của lễ do phần dân Y-sơ-ra-ên mà đưa qua đưa lại trước mặt Ðức Giê-hô-va, và họ sẽ được phần làm công việc của Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_8_12", new visuperobject(4,8,12,"Ðoạn, người Lê-vi sẽ đặt tay trên đầu hai con bò đực, và ngươi phải dâng con nầy làm của lễ chuộc tội và con kia làm của lễ thiêu cho Ðức Giê-hô-va, để làm lễ chuộc tội cho người Lê-vi."));
visuperobject.biblemap.put("b4_8_13", new visuperobject(4,8,13,"Kế đó, ngươi phải biểu người Lê-vi đứng trước mặt A-rôn và các con trai người, rồi dâng họ như một của lễ đưa qua đưa lại cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_8_14", new visuperobject(4,8,14,"Như vậy ngươi sẽ biệt riêng ra người Lê-vi với dân Y-sơ-ra-ên, và người Lê-vi sẽ thuộc về ta."));
visuperobject.biblemap.put("b4_8_15", new visuperobject(4,8,15,"Sau việc ấy, người Lê-vi sẽ đến đặng làm công việc của hội mạc. Ấy, ngươi sẽ làm cho họ được sạch và dâng như của lễ đưa qua đưa lại vậy."));
visuperobject.biblemap.put("b4_8_16", new visuperobject(4,8,16,"Bởi vì, giữa dân Y-sơ-ra-ên người Lê-vi ban trọn cho ta; ta đã chọn lấy họ về ta thế cho hết thảy con đầu lòng của dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_8_17", new visuperobject(4,8,17,"Vì chưng hết thảy con đầu lòng trong dân Y-sơ-ra-ên đều thuộc về ta, bất luận người hay vật; ta đã biệt chúng nó riêng ra cho ta trong ngày ta hành hại mọi con đầu lòng tại xứ Ê-díp-tô,"));
visuperobject.biblemap.put("b4_8_18", new visuperobject(4,8,18,"và ta đã chọn lấy người Lê-vi thế cho mọi con đầu lòng của dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_8_19", new visuperobject(4,8,19,"Bởi trong dân Y-sơ-ra-ên ta đã chọn người Lê-vi ban trọn cho A-rôn và các con trai người, đặng làm công việc của dân Y-sơ-ra-ên ở trong hội mạc, cùng làm lễ chuộc tội cho dân Y-sơ-ra-ên, hầu cho khi họ đến gần nơi thánh, chẳng có tai hại chi trong dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_8_20", new visuperobject(4,8,20,"Vậy, Môi-se, A-rôn, và cả hội dân Y-sơ-ra-ên, đối cùng người Lê-vi làm hết mọi điều Ðức Giê-hô-va đã phán dặn Môi-se về họ; dân Y-sơ-ra-ên đối cùng người Lê-vi đều làm như vậy."));
visuperobject.biblemap.put("b4_8_21", new visuperobject(4,8,21,"Người Lê-vi bèn làm cho mình được sạch khỏi tội, và giặt áo xống mình; rồi A-rôn dâng họ như của lễ đưa qua đưa lại trước mặt Ðức Giê-hô-va, và làm lễ chuộc tội cho, để dọn họ được sạch."));
visuperobject.biblemap.put("b4_8_22", new visuperobject(4,8,22,"Sau rồi, người Lê-vi đến đặng làm công việc mình trong hội mạc trước mặt A-rôn và trước mặt các con trai người. Ðối cùng người Lê-vi, người ta làm y như Ðức Giê-hô-va đã phán dặn Môi-se về họ."));
visuperobject.biblemap.put("b4_8_23", new visuperobject(4,8,23,"Ðoạn, Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_8_24", new visuperobject(4,8,24,"Nầy là lệ định về người Lê-vi: Từ hai mươi lăm tuổi sắp lên, người Lê-vi phải đến nhập ban trong công việc của hội mạc."));
visuperobject.biblemap.put("b4_8_25", new visuperobject(4,8,25,"Nhưng đến năm mươi tuổi, người sẽ ra ban, chẳng làm công việc nữa;"));
visuperobject.biblemap.put("b4_8_26", new visuperobject(4,8,26,"người phải cứ giúp đỡ anh em mình trong hội mạc, coi sóc việc chi đã giao cho, nhưng không nên làm công việc nữa. Ngươi phải đối cùng người Lê-vi mà làm như vậy về chức phận của họ."));
visuperobject.biblemap.put("b4_9_1", new visuperobject(4,9,1,"Tháng giêng năm thứ hai, sau khi ra khỏi xứ Ê-díp-tô, Ðức Giê-hô-va lại phán cùng Môi-se trong đồng vắng Si-na -i rằng:"));
visuperobject.biblemap.put("b4_9_2", new visuperobject(4,9,2,"Dân Y-sơ-ra-ên phải giữ lễ Vượt-qua theo kỳ nhất định."));
visuperobject.biblemap.put("b4_9_3", new visuperobject(4,9,3,"Các ngươi phải giữ lễ đó theo kỳ nhất định, tức là ngày mười bốn tháng nầy, vào buổi chiều tối; các ngươi phải làm theo hết thảy lệ định và luật pháp về lễ đó."));
visuperobject.biblemap.put("b4_9_4", new visuperobject(4,9,4,"Vậy, Môi-se nói cùng dân Y-sơ-ra-ên để họ giữ lễ Vượt-qua."));
visuperobject.biblemap.put("b4_9_5", new visuperobject(4,9,5,"Dân Y-sơ-ra-ên giữ lễ Vượt-qua trong tuần tháng giêng, ngày mười bốn, vào buổi chiều tối, tại đồng vắng Si-na -i, làm y mọi điều Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b4_9_6", new visuperobject(4,9,6,"Vả, có mấy người vì cớ xác chết mà bị ô uế, không được giữ lễ Vượt-qua trong ngày đó, bèn đến trước mặt Môi-se và A-rôn,"));
visuperobject.biblemap.put("b4_9_7", new visuperobject(4,9,7,"mà thưa cùng Môi-se rằng: Chúng tôi bị lây ô uế vì xác chết của loài người; sao tại trong dân Y-sơ-ra-ên chúng tôi phải bị cất phần dâng của lễ cho Ðức Giê-hô-va theo kỳ đã chỉ?"));
visuperobject.biblemap.put("b4_9_8", new visuperobject(4,9,8,"Môi-se đáp rằng: Hãy đợi để ta biết điều Ðức Giê-hô-va sẽ truyền dạy về các ngươi."));
visuperobject.biblemap.put("b4_9_9", new visuperobject(4,9,9,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_9_10", new visuperobject(4,9,10,"Hãy nói cùng dân Y-sơ-ra-ên rằng: Khi một người trong vòng các ngươi hay là trong vòng hậu đại các ngươi bị ô uế vì cớ xác chết, hoặc mắc đi xa, thì cũng buộc phải giữ lễ Vượt-qua cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_9_11", new visuperobject(4,9,11,"Mấy người đó phải giữ lễ nầy ngày mười bốn tháng hai, vào buổi chiều tối, ăn bánh không men cùng rau đắng,"));
visuperobject.biblemap.put("b4_9_12", new visuperobject(4,9,12,"chớ nên để chi còn dư lại đến sáng lai, và cũng chẳng nên bẻ gãy những xương; phải giữ theo mọi luật lệ về lễ Vượt-qua vậy."));
visuperobject.biblemap.put("b4_9_13", new visuperobject(4,9,13,"Nhưng còn ai tinh sạch và không có đi xa, nếu chẳng giữ lễ Vượt-qua thì sẽ bị truất khỏi dân sự mình: vì người ấy chẳng dâng của lễ cho Ðức Giê-hô-va trong kỳ nhất định; người sẽ mang lấy tội mình."));
visuperobject.biblemap.put("b4_9_14", new visuperobject(4,9,14,"Khi một khách ngoại bang kiều ngụ giữa các ngươi mà muốn giữ lễ Vượt-qua cho Ðức Giê-hô-va, thì phải giữ theo lệ định và luật pháp về lễ Vượt-qua. Ðồng có một luật lệ cho các ngươi, cho khách ngoại bang, và cho người nào sanh tại trong xứ."));
visuperobject.biblemap.put("b4_9_15", new visuperobject(4,9,15,"Vả, ngày người ta dựng đền tạm, thì trụ mây bao phủ đền tạm và Trại chứng cớ; ban chiều dường có một vầng lửa ở trên đền tạm cho đến sáng mai."));
visuperobject.biblemap.put("b4_9_16", new visuperobject(4,9,16,"Hằng có như vậy; ban ngày trụ mây bao phủ đền tạm, và ban đêm giống như có lửa."));
visuperobject.biblemap.put("b4_9_17", new visuperobject(4,9,17,"Mỗi khi trụ mây cất lên khỏi Trại, thì dân Y-sơ-ra-ên ra đi; trong nơi nào trụ mây dừng lại, thì dân Y-sơ-ra-ên hạ trại ở đó."));
visuperobject.biblemap.put("b4_9_18", new visuperobject(4,9,18,"Dân Y-sơ-ra-ên ra đi theo mạng Ðức Giê-hô-va, và hạ trại theo mạng Ðức Giê-hô-va. Trọn trong lúc trụ mây ngự trên đền tạm, thì dân Y-sơ-ra-ên cứ đóng trại."));
visuperobject.biblemap.put("b4_9_19", new visuperobject(4,9,19,"Khi nào trụ mây ngự lâu trên đền tạm, thì dân Y-sơ-ra-ên vâng theo mạng Ðức Giê-hô-va, chẳng hề ra đi."));
visuperobject.biblemap.put("b4_9_20", new visuperobject(4,9,20,"Nhưng khi nào trụ mây ngự ít ngày trên đền tạm, thì dân Y-sơ-ra-ên cứ vâng theo mạng Ðức Giê-hô-va mà hạ trại và ra đi."));
visuperobject.biblemap.put("b4_9_21", new visuperobject(4,9,21,"Khi trụ mây ngự tại đó từ buổi chiều đến sáng mai, và khi đến sáng mai trụ mây cất lên, thì ra đi; không cứ ngày hay đêm trụ mây cất lên, thì họ ra đi."));
visuperobject.biblemap.put("b4_9_22", new visuperobject(4,9,22,"Trụ mây ngự trên đền tạm hoặc hai ngày, hoặc một tháng, hoặc lâu hơn nữa, thì dân Y-sơ-ra-ên cứ đóng trại, không hề ra đi; nhưng khi trụ mây cất lên, thì họ ra đi."));
visuperobject.biblemap.put("b4_9_23", new visuperobject(4,9,23,"Dân Y-sơ-ra-ên hạ trại và ra đi tùy theo mạng Ðức Giê-hô-va; họ theo chương trình của Ðức Giê-hô-va, tùy mạng Ngài đã cậy Môi-se mà truyền cho."));
visuperobject.biblemap.put("b4_10_1", new visuperobject(4,10,1,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_10_2", new visuperobject(4,10,2,"Hãy làm hai ống loa bằng bạc, đánh giát, dùng cho ngươi để nhóm hiệp hội chúng và truyền cho các trại quân ra đi."));
visuperobject.biblemap.put("b4_10_3", new visuperobject(4,10,3,"Khi nào người ta thổi loa, toàn hội chúng sẽ nhóm lại gần bên ngươi, tại cửa hội mạc."));
visuperobject.biblemap.put("b4_10_4", new visuperobject(4,10,4,"Nếu người ta thổi chỉ một ống loa, thì các quan trưởng, tức là các quan tướng đội-quân Y-sơ-ra-ên phải nhóm lại gần bên ngươi;"));
visuperobject.biblemap.put("b4_10_5", new visuperobject(4,10,5,"khi các ngươi thổi tiếng vang, thì những trại quân ở về hướng đông phải ra đi."));
visuperobject.biblemap.put("b4_10_6", new visuperobject(4,10,6,"Khi các ngươi thổi tiếng vang lần thứ nhì, thì những trại quân ở về hướng nam phải ra đi; người ta sẽ thổi tiếng vang khi nào các trại quân phải ra đi vậy."));
visuperobject.biblemap.put("b4_10_7", new visuperobject(4,10,7,"Khi nhóm hội chúng, các ngươi phải thổi, nhưng đừng thổi tiếng vang."));
visuperobject.biblemap.put("b4_10_8", new visuperobject(4,10,8,"Các con trai A-rôn, là những thầy tế lễ, sẽ thổi những ống loa. Từ đời nầy sang đời kia, ấy sẽ là một lệ định đời đời cho các ngươi."));
visuperobject.biblemap.put("b4_10_9", new visuperobject(4,10,9,"Khi nào trong xứ các ngươi phải ra chiến trận cùng kẻ thù nghịch xông đánh mình, thì phải thổi loa tiếng vang; Giê-hô-va, Ðức Chúa Trời của các ngươi sẽ nhớ lại các ngươi, và các ngươi sẽ được giải thoát khỏi kẻ thù nghịch."));
visuperobject.biblemap.put("b4_10_10", new visuperobject(4,10,10,"Trong những ngày vui mừng, trong các lễ trọng thể và đầu tháng của các ngươi, thì phải thổi loa khi dâng những của lễ thiêu và của lễ thù ân mình; loa sẽ dùng làm kỷ niệm trước mặt Ðức Chúa Trời của các ngươi. Ta là Giê-hô-va, Ðức Chúa Trời của các ngươi."));
visuperobject.biblemap.put("b4_10_11", new visuperobject(4,10,11,"Xảy trong ngày hai mươi tháng hai, năm thứ hai, thì trụ mây cất lên khỏi đền tạm chứng cớ."));
visuperobject.biblemap.put("b4_10_12", new visuperobject(4,10,12,"Dân Y-sơ-ra-ên, theo chương trình đã định, ra từ đồng vắng Si-na -i, và trụ mây dừng lại tại đồng vắng Pha-ran."));
visuperobject.biblemap.put("b4_10_13", new visuperobject(4,10,13,"Vậy, dân Y-sơ-ra-ên vâng mạng của Ðức Giê-hô-va cậy Môi-se truyền cho, mà ra đi lần thứ nhất."));
visuperobject.biblemap.put("b4_10_14", new visuperobject(4,10,14,"Ngọn cờ của trại quân Giu-đa, tùy theo các đội ngũ mình, đi trước. Na-ha-sôn, con trai A-mi-na-đáp, thống lãnh quân đội Giu-đa."));
visuperobject.biblemap.put("b4_10_15", new visuperobject(4,10,15,"Na-tha-na-ên, con trai Xu-a, thống lãnh quân đội của chi phái Y-sa-ca."));
visuperobject.biblemap.put("b4_10_16", new visuperobject(4,10,16,"Ê-li-áp, con trai Hê-lôn, thống lãnh quân đội của chi phái Sa-bu-lôn."));
visuperobject.biblemap.put("b4_10_17", new visuperobject(4,10,17,"Bấy giờ, đền tạm đã tháo dỡ, rồi con cháu Ghẹt-sôn và con cháu Mê-ra-ri khiêng đền tạm, bèn ra đi."));
visuperobject.biblemap.put("b4_10_18", new visuperobject(4,10,18,"Kế đó, ngọn cờ của trại quân Ru-bên ra đi, tùy theo các đội ngũ mình. Ê-lít-su, con trai Sê-đêu, thống lãnh quân đội Ru-bên."));
visuperobject.biblemap.put("b4_10_19", new visuperobject(4,10,19,"Sê-lu-mi-ên, con trai Xu-ri-ha-đai, thống lãnh quân đội của chi phái Si-mê-ôn."));
visuperobject.biblemap.put("b4_10_20", new visuperobject(4,10,20,"Ê-li-sáp, con trai Ðê -u-ên, thống lãnh quân đội của chi phái Gát."));
visuperobject.biblemap.put("b4_10_21", new visuperobject(4,10,21,"Bấy giờ, các người Kê-hát khiêng những vật thánh ra đi; người ta dựng đền tạm lên đương lúc đợi họ đến."));
visuperobject.biblemap.put("b4_10_22", new visuperobject(4,10,22,"Ðoạn, ngọn cờ của trại quân con cháu Ép-ra-im ra đi, tùy theo đội ngũ mình. Ê-li-sa-ma, con trai A-mi-hút, thống lãnh quân đội Ép-ra-im."));
visuperobject.biblemap.put("b4_10_23", new visuperobject(4,10,23,"Ga-ma-li-ên, con trai Phê-đát-su, thống lãnh quân đội của chi phái Ma-na-se."));
visuperobject.biblemap.put("b4_10_24", new visuperobject(4,10,24,"A-bi-đan, con trai Ghi-đeo-ni, thống lãnh quân đội của chi phái Bên-gia-min."));
visuperobject.biblemap.put("b4_10_25", new visuperobject(4,10,25,"Rốt lại, ngọn cờ của trại quân Ðan ra đi, tùy theo các đội ngũ mình; nó làm hậu binh của các trại quân khác. A-hi-ê-xe, con trai A-mi-sa-đai, thống lãnh quân đội Ðan."));
visuperobject.biblemap.put("b4_10_26", new visuperobject(4,10,26,"Pha-ghi-ên, con trai Oùc-ran, thống lãnh quân đội của chi phái A-se."));
visuperobject.biblemap.put("b4_10_27", new visuperobject(4,10,27,"A-hi-ra, con trai Ê-nan, thống lãnh quân đội của chi phái Nép-ta-li."));
visuperobject.biblemap.put("b4_10_28", new visuperobject(4,10,28,"Ðó là thứ tự về sự cất binh đi của dân Y-sơ-ra-ên, tùy theo các đội ngũ mình; ấy, họ ra đi là như vậy."));
visuperobject.biblemap.put("b4_10_29", new visuperobject(4,10,29,"Vả, Môi-se nói cùng Hô-báp, con trai Rê -u-ên, người Ma-đi-an, tức là anh vợ mình, mà rằng: Chúng tôi đi đến xứ mà Ðức Giê-hô-va có phán rằng: Ta sẽ ban xứ đó cho các ngươi. Xin anh hãy đi cùng chúng tôi, chúng tôi sẽ đãi anh tử tế; vì Ðức Giê-hô-va có hứa ban ơn cho dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_10_30", new visuperobject(4,10,30,"Hô-báp đáp rằng: Tôi không đi đâu, nhưng tôi sẽ đi về xứ sở tôi, là nơi bà con tôi."));
visuperobject.biblemap.put("b4_10_31", new visuperobject(4,10,31,"Môi-se lại nói rằng: Tôi xin anh chớ bỏ chúng tôi, vì anh biết nơi nào trong đồng vắng chúng tôi có thế hạ trại được; anh sẽ dường như con mắt cho chúng tôi vậy."));
visuperobject.biblemap.put("b4_10_32", new visuperobject(4,10,32,"Nếu anh đi cùng chúng tôi, thì chúng tôi sẽ làm cho anh hưởng ơn lành mà Ðức Giê-hô-va sẽ làm cho chúng tôi."));
visuperobject.biblemap.put("b4_10_33", new visuperobject(4,10,33,"Vậy, dân Y-sơ-ra-ên ra đi từ núi Ðức Giê-hô-va, đi trong ba ngày đường; hòm giao ước của Ðức Giê-hô-va đi trước trong lúc ba ngày đường nầy, đặng tìm cho dân một nơi an nghỉ."));
visuperobject.biblemap.put("b4_10_34", new visuperobject(4,10,34,"Trong lúc ban ngày, khi dân Y-sơ-ra-ên dời trại quân đi, thì trụ mây của Ðức Giê-hô-va ở trên họ."));
visuperobject.biblemap.put("b4_10_35", new visuperobject(4,10,35,"Khi hòm giao ước đi, thì Môi-se nói rằng: Hỡi Ðức Giê-hô-va, xin chổi dậy, để kẻ thù nghịch Ngài bị tản lạc, và kẻ nào ghét Ngài chạy trốn trước mặt Ngài!"));
visuperobject.biblemap.put("b4_10_36", new visuperobject(4,10,36,"Khi người ta để hòm giao ước xuống, thì Môi-se nói rằng: Hỡi Ðức Giê-hô-va, xin trở lại cùng muôn vàn của Y-sơ-ra-ên!"));
visuperobject.biblemap.put("b4_11_1", new visuperobject(4,11,1,"Vả, dân sự bèn lằm bằm, và điều đó chẳng đẹp tại Ðức Giê-hô-va. Ðức Giê-hô-va nghe, cơn thạnh nộ Ngài nổi lên, lửa của Ðức Giê-hô-va cháy phừng phừng giữa dân sự và thiêu hóa đầu cùng trại quân."));
visuperobject.biblemap.put("b4_11_2", new visuperobject(4,11,2,"Dân sự bèn kêu la cùng Môi-se; Môi-se cầu xin Ðức Giê-hô-va, thì lửa ngừng lại."));
visuperobject.biblemap.put("b4_11_3", new visuperobject(4,11,3,"Người ta đặt tên chỗ nầy là Tha-bê-ra, vì lửa của Ðức Giê-hô-va đã cháy giữa dân sự."));
visuperobject.biblemap.put("b4_11_4", new visuperobject(4,11,4,"Bọn dân tạp ở trong dân Y-sơ-ra-ên sanh lòng tham muốn, đến nỗi dân Y-sơ-ra-ên lại khóc nữa mà rằng: Ai sẽ cho chúng tôi ăn thịt?"));
visuperobject.biblemap.put("b4_11_5", new visuperobject(4,11,5,"Chúng tôi nhớ những cá chúng tôi ăn nhưng không tại xứ Ê-díp-tô, những dưa chuột, dưa gang, củ kiệu, hành, và tỏi."));
visuperobject.biblemap.put("b4_11_6", new visuperobject(4,11,6,"Bây giờ, linh hồn chúng tôi bị khô héo, không có chi hết! Mắt chỉ thấy ma-na mà thôi."));
visuperobject.biblemap.put("b4_11_7", new visuperobject(4,11,7,"Vả, Ma-na hình như hột ngò, sắc như trân châu."));
visuperobject.biblemap.put("b4_11_8", new visuperobject(4,11,8,"Dân sự đi tản ra và hốt lấy, rồi nghiền bằng cối xay, hay là giã bằng cối giã, nấu trong một cái nồi và làm bánh; nếm giống như bánh nhỏ có pha dầu."));
visuperobject.biblemap.put("b4_11_9", new visuperobject(4,11,9,"Khi ban tối mù sương xuống trên trại quân, thì ma-na cũng xuống nữa."));
visuperobject.biblemap.put("b4_11_10", new visuperobject(4,11,10,"Vậy, Môi-se nghe dân sự khóc, mỗi người ở trong nhà mình, tại nơi cửa trại mình: cơn thạnh nộ của Ðức Giê-hô-va nổi lên phừng phừng,"));
visuperobject.biblemap.put("b4_11_11", new visuperobject(4,11,11,"và Môi-se lấy làm buồn bực về điều đó. Môi-se bèn thưa cùng Ðức Giê-hô-va rằng: Sao Ngài làm cho tôi tớ Ngài buồn? Sao tôi chẳng được ơn trước mặt Ngài, và sao Ngài lại chất gánh nặng của hết thảy dân sự nầy trên mình tôi?"));
visuperobject.biblemap.put("b4_11_12", new visuperobject(4,11,12,"Tôi há có thọ thai dân nầy sao? há có sanh đẻ nó sao? mà Ngài lại phán biểu tôi rằng: Hãy ẵm dân nầy trong lòng người, như người cha nuôi bồng đứa trẻ bú, cho đến xứ mà Ngài có thề hứa ban cho tổ phụ của chúng nó."));
visuperobject.biblemap.put("b4_11_13", new visuperobject(4,11,13,"Tôi sẽ có thịt ở đâu đặng phát cho cả dân sự nầy? bởi vì họ khóc về tôi mà rằng: Hãy cho chúng tôi ăn thịt."));
visuperobject.biblemap.put("b4_11_14", new visuperobject(4,11,14,"Tôi không thế một mình gánh hết dân sự nầy, vì thật là rất nặng nề cho tôi quá!"));
visuperobject.biblemap.put("b4_11_15", new visuperobject(4,11,15,"Nếu Chúa đãi tôi như vậy, và nếu tôi được ơn trước mặt Chúa, xin thà giết tôi đi, để tôi không thấy sự khốn nạn tôi!"));
visuperobject.biblemap.put("b4_11_16", new visuperobject(4,11,16,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng: Hãy nhóm hiệp cho ta bảy mươi người trong bực trưởng lão Y-sơ-ra-ên, tức là những kẻ ngươi biết là trưởng lão và cai quản dân sự; hãy dẫn các người ấy đến hội mạc, đứng tại đó với ngươi."));
visuperobject.biblemap.put("b4_11_17", new visuperobject(4,11,17,"Ðoạn, ta sẽ ngự xuống, nói chuyện tại đó với ngươi; ta sẽ lấy Thần cảm ngươi mà ban cho những người đó, để họ chịu gánh nặng về dân sự với ngươi, và ngươi không phải gánh một mình."));
visuperobject.biblemap.put("b4_11_18", new visuperobject(4,11,18,"Ngươi phải truyền cho dân sự rằng: Ngày mai phải dọn mình ra thánh, thì các ngươi sẽ ăn thịt. Vì chưng lỗ tai Ðức Giê-hô-va có nghe các ngươi khóc mà rằng: Ai sẽ cho chúng tôi ăn thịt, vì chúng tôi lấy làm sung sướng tại xứ Ê-díp-tô, bởi cớ đó, Ðức Giê-hô-va sẽ ban thịt cho các ngươi, thì các ngươi sẽ ăn."));
visuperobject.biblemap.put("b4_11_19", new visuperobject(4,11,19,"Chẳng phải các ngươi sẽ ăn thịt một ngày, hai ngày, năm ngày, mười ngày hay là hai mươi ngày đâu;"));
visuperobject.biblemap.put("b4_11_20", new visuperobject(4,11,20,"nhưng cho đến một tháng trọn, cho đến chừng nào thịt tràn ra lỗ mũi, và các ngươi ngán đi, vì các ngươi đã bỏ Ðức Giê-hô-va ở giữa mình, và khóc trước mặt Ngài, mà rằng: Vì sao chúng tôi đã ra khỏi xứ Ê-díp-tô?"));
visuperobject.biblemap.put("b4_11_21", new visuperobject(4,11,21,"Môi-se thưa rằng: Trong dân sự tôi đương ở đây, có sáu trăm ngàn người đàn ông, mà Ngài có phán rằng: Ta sẽ phát thịt cho dân nầy ăn trong một tháng trọn."));
visuperobject.biblemap.put("b4_11_22", new visuperobject(4,11,22,"Người ta há sẽ giết chiên và bò đến đỗi được đủ cho họ sao? hay là người ta há sẽ gom hết thảy cá dưới biển đến nỗi được đủ cho họ sao?"));
visuperobject.biblemap.put("b4_11_23", new visuperobject(4,11,23,"Ðức Giê-hô-va phán cùng Môi-se rằng: Tay của Ðức Giê-hô-va đã vắn lại sao? Ngươi sẽ xem thử điều ta đã phán cùng ngươi có xảy đến hay chăng."));
visuperobject.biblemap.put("b4_11_24", new visuperobject(4,11,24,"Môi-se bèn đi ra, thuật lại các lời Ðức Giê-hô-va cho dân sự, rồi nhóm hiệp bảy mươi người trong bực trưởng lão của dân sự, đặt để xung quanh Trại."));
visuperobject.biblemap.put("b4_11_25", new visuperobject(4,11,25,"Ðức Giê-hô-va bèn ngự xuống trong mây, phán cùng Môi-se; Ngài lấy Thần cảm người và ban cho bảy mươi người trưởng lão. Vừa khi Thần ở trên bảy mươi người trưởng lão, bèn được cảm động nói tiên tri, nhưng không cứ nói mãi."));
visuperobject.biblemap.put("b4_11_26", new visuperobject(4,11,26,"Có hai người ở lại trong trại quân; một người tên là Eân-đát, một người tên là Mê-đát, được Thần cảm động, nói tiên tri tại trại quân. Hai người nầy thuộc về bọn được biên tên, nhưng không có đến hội mạc."));
visuperobject.biblemap.put("b4_11_27", new visuperobject(4,11,27,"Một đứa trai trẻ chạy thuật lại cùng Môi-se rằng: Eân-đát và Mê-đát nói tiên tri trong trại quân."));
visuperobject.biblemap.put("b4_11_28", new visuperobject(4,11,28,"Giô-suê, con trai của Nun, là người hầu việc Môi-se, đáp lại mà rằng: Hỡi Môi-se, chúa tôi, hãy cấm chúng nó!"));
visuperobject.biblemap.put("b4_11_29", new visuperobject(4,11,29,"Môi-se đáp rằng: Ngươi ganh cho ta chăng? Ôi! chớ chi cả dân sự của Ðức Giê-hô-va đều là người tiên tri, và chớ chi Ðức Giê-hô-va ban Thần của Ngài cho họ!"));
visuperobject.biblemap.put("b4_11_30", new visuperobject(4,11,30,"Môi-se và mấy người trưởng lão đều lui vào trại quân."));
visuperobject.biblemap.put("b4_11_31", new visuperobject(4,11,31,"Ðức Giê-hô-va bèn khiến một trận gió nổi từ biển, dẫn những chim cút đến vải trên trại quân và xung quanh, bên nầy trải ra dài chừng một ngày đường đi, và bên kia trải ra dài chừng một ngày đường đi, khỏi mặt đất chừng hai thước bề cao."));
visuperobject.biblemap.put("b4_11_32", new visuperobject(4,11,32,"Dân sự đứng dậy lượm những cút trong ngày đó, đêm đó và trọn ngày sau; ai lượm ít hơn hết có mười ô-me; người ta căng nó khắp chung quanh trại quân."));
visuperobject.biblemap.put("b4_11_33", new visuperobject(4,11,33,"Thịt vẫn còn nơi răng, chưa nhai, thì cơn giận của Ðức Giê-hô-va nổi lên cùng dân sự, hành dân sự một tai vạ rất nặng."));
visuperobject.biblemap.put("b4_11_34", new visuperobject(4,11,34,"Người ta đặt tên chỗ đó là Kíp-rốt Ha-tha-va; vì tại đó người ta chôn dân sự sanh lòng tham muốn."));
visuperobject.biblemap.put("b4_11_35", new visuperobject(4,11,35,"Từ Kíp-rốt Ha-tha-va dân sự đi đến Hát-sê-rốt, rồi ngừng lại tại đó."));
visuperobject.biblemap.put("b4_12_1", new visuperobject(4,12,1,"Mi-ri-am và A-rôn nói hành Môi-se về việc người nữ Ê-thi-ô-bi mà người đã lấy; vì người có lấy một người nữ Ê-thi-ô-bi làm vợ."));
visuperobject.biblemap.put("b4_12_2", new visuperobject(4,12,2,"Hai người nói rằng: Ðức Giê-hô-va chỉ dùng một mình Môi-se mà thôi sao? Ngài há không dùng chúng ta mà phán nữa sao? Ðức Giê-hô-va nghe điều đó."));
visuperobject.biblemap.put("b4_12_3", new visuperobject(4,12,3,"Vả, Môi-se là người rất khiêm hòa hơn mọi người trên thế gian."));
visuperobject.biblemap.put("b4_12_4", new visuperobject(4,12,4,"Thình lình Ðức Giê-hô-va phán cùng Môi-se, A-rôn và Mi-ri-am rằng: Cả ba hãy ra đến hội mạc. Cả ba đều đi."));
visuperobject.biblemap.put("b4_12_5", new visuperobject(4,12,5,"Ðức Giê-hô-va ngự xuống trong trụ mây đứng tại cửa hội mạc, rồi gọi A-rôn và Môi-se; hai người đều đến."));
visuperobject.biblemap.put("b4_12_6", new visuperobject(4,12,6,"Ngài phán cùng hai người rằng: Hãy nghe rõ lời ta. Nếu trong các ngươi có một tiên tri nào, ta là Ðức Giê-hô-va sẽ hiện ra cùng người trong sự hiện thấy, và nói với người trong cơn chiêm bao."));
visuperobject.biblemap.put("b4_12_7", new visuperobject(4,12,7,"Tôi tớ Môi-se ta không có như vậy, người thật trung tín trong cả nhà ta."));
visuperobject.biblemap.put("b4_12_8", new visuperobject(4,12,8,"Ta nói chuyện cùng người miệng đối miệng, một cách rõ ràng, không lời đố, và người thấy hình Ðức Giê-hô-va. Vậy, các ngươi không sợ mà nói hành kẻ tôi tớ ta, là Môi-se sao?"));
visuperobject.biblemap.put("b4_12_9", new visuperobject(4,12,9,"Như vậy, cơn thạnh nộ của Ðức Giê-hô-va nổi phừng phừng cùng hai người; Ngài ngự đi."));
visuperobject.biblemap.put("b4_12_10", new visuperobject(4,12,10,"Khi trụ mây rút khỏi trên đền tạm, thì kìa, Mi-ri-am đã bị phung trắng như tuyết. A-rôn xây lại phía Mi-ri-am, thấy người đã có bịnh phung."));
visuperobject.biblemap.put("b4_12_11", new visuperobject(4,12,11,"A-rôn bèn nói cùng Môi-se rằng: Ôi, lạy chúa tôi, xin chớ để trên chúng tôi tội mà chúng tôi đã dại phạm và đã mắc lấy."));
visuperobject.biblemap.put("b4_12_12", new visuperobject(4,12,12,"Xin chớ để nàng như một đứa con sảo thịt đã hư hết phân nửa khi lọt ra khỏi lòng mẹ."));
visuperobject.biblemap.put("b4_12_13", new visuperobject(4,12,13,"Môi-se bèn kêu van cùng Ðức Giê-hô-va rằng: Ôi, Ðức Chúa Trời! Tôi cầu khẩn Ngài chữa cho nàng."));
visuperobject.biblemap.put("b4_12_14", new visuperobject(4,12,14,"Ðức Giê-hô-va đáp cùng Môi-se rằng: Nếu cha nàng nhổ trên mặt nàng, nàng há không mang hổ trong bảy ngày sao? Nàng phải cầm riêng ngoài trại quân trong bảy ngày, rồi sau sẽ được nhận vào lại."));
visuperobject.biblemap.put("b4_12_15", new visuperobject(4,12,15,"Ấy vậy, Mi-ri-am bị cầm riêng ngoài trại quân trong bảy ngày, dân sự không ra đi cho đến chừng nào Mi-ri-am được nhận vào lại."));
visuperobject.biblemap.put("b4_12_16", new visuperobject(4,12,16,"Sau việc đó, dân sự ra đi từ Hát-sê-rốt, và hạ trại trong đồng vắng Pha-ran."));
visuperobject.biblemap.put("b4_13_1", new visuperobject(4,13,1,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_13_2", new visuperobject(4,13,2,"Hãy sai những người đi do thám xứ Ca-na-an, là xứ ta ban cho dân Y-sơ-ra-ên. Về mỗi một chi phái, các ngươi phải sai một người đi; mỗi người làm quan trưởng của chi phái mình."));
visuperobject.biblemap.put("b4_13_3", new visuperobject(4,13,3,"Tùy theo mạng Ðức Giê-hô-va, Môi-se từ đồng vắng Pha-ran sai các người nầy đi; hết thảy là quan trưởng của dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_13_4", new visuperobject(4,13,4,"Ðây là tên của các người ấy: Về chi phái Ru-bên, Sam-mua, con trai Xa-cu;"));
visuperobject.biblemap.put("b4_13_5", new visuperobject(4,13,5,"về chi phái Si-mê-ôn, Sa-phát, con trai của Hô-ri;"));
visuperobject.biblemap.put("b4_13_6", new visuperobject(4,13,6,"về chi phái Giu-đa, Ca-lép, con trai của Giê-phu-nê;"));
visuperobject.biblemap.put("b4_13_7", new visuperobject(4,13,7,"về chi phái Y-sa-ca, Di-ganh, con trai của Giô-sép;"));
visuperobject.biblemap.put("b4_13_8", new visuperobject(4,13,8,"về chi phái Ép-ra-im, Hô-sê, con trai của Nun;"));
visuperobject.biblemap.put("b4_13_9", new visuperobject(4,13,9,"về chi phái Bên-gia-min, Phan-thi, con trai của Ra-phu;"));
visuperobject.biblemap.put("b4_13_10", new visuperobject(4,13,10,"về chi phái Sa-bu-lôn, Gát-đi-ên, con trai của Sô-đi;"));
visuperobject.biblemap.put("b4_13_11", new visuperobject(4,13,11,"về chi phái Giô-sép, nghĩa là về chi phái Ma-na-se, Ga-đi, con trai của Su-si;"));
visuperobject.biblemap.put("b4_13_12", new visuperobject(4,13,12,"về chi phái Ðan, A-mi-ên, con trai của Ghê-ma-li;"));
visuperobject.biblemap.put("b4_13_13", new visuperobject(4,13,13,"về chi phái A-se, Sê-thu, con trai của Mi-ca-ên;"));
visuperobject.biblemap.put("b4_13_14", new visuperobject(4,13,14,"về chi phái Nép-ta-li, Nách-bi, con trai của Vấp-si;"));
visuperobject.biblemap.put("b4_13_15", new visuperobject(4,13,15,"về chi phái Gát, Gu-ên, con trai của Ma-ki."));
visuperobject.biblemap.put("b4_13_16", new visuperobject(4,13,16,"Ðó là tên những người mà Môi-se sai đi do thám xứ. Vả, Môi-se đặt tên cho Hô-sê, con trai Nun, là Giô-suê."));
visuperobject.biblemap.put("b4_13_17", new visuperobject(4,13,17,"Vậy, Môi-se sai đi do thám xứ Ca-na-an, và dặn rằng: Từ đây hãy lên Nam phương, và vào miền núi;"));
visuperobject.biblemap.put("b4_13_18", new visuperobject(4,13,18,"rồi sẽ thấy xứ ra sao, dân sự ở đó nếu mạnh yếu thể nào, nếu số ít hay nhiều;"));
visuperobject.biblemap.put("b4_13_19", new visuperobject(4,13,19,"xứ dân đó ở thế nào, nếu tốt hay xấu; các thành dân đó ở làm sao, hoặc chỉ những nơi đóng trại hay là đồn lũy;"));
visuperobject.biblemap.put("b4_13_20", new visuperobject(4,13,20,"đất ra làm sao, nếu màu mỡ hay là xấu, có cây cối hay là không. Hãy can đảm và hái lấy trái cây xứ đó. Bấy giờ nhằm mùa nho sớm."));
visuperobject.biblemap.put("b4_13_21", new visuperobject(4,13,21,"Vậy các người đó đi lên do thám xứ, từ đồng vắng Xin cho đến Rê-hốp, đi về Ha-mát."));
visuperobject.biblemap.put("b4_13_22", new visuperobject(4,13,22,"Họ bắt từ hướng Nam đi lên, đến thành Hếp-rôn, là nơi có A-hi-man, Sê-sai và Tanh-mai, con cái của A-nác. Vả, thành Hếp-rôn đã xây từ bảy năm trước Xô-an, là thành của xứ Ê-díp-tô."));
visuperobject.biblemap.put("b4_13_23", new visuperobject(4,13,23,"Các người đến khe Ếch-côn, cắt tại đó một nhành nho có một chùm nho; và hai người khiêng lấy bằng cây sào, luôn những trái lựu và trái vả."));
visuperobject.biblemap.put("b4_13_24", new visuperobject(4,13,24,"Người ta gọi chỗ nầy là khe Ếch-côn, vì có chùm nho mà dân Y-sơ-ra-ên đã cắt tại đó."));
visuperobject.biblemap.put("b4_13_25", new visuperobject(4,13,25,"Các người đi do thám xứ trong bốn mươi ngày trở về."));
visuperobject.biblemap.put("b4_13_26", new visuperobject(4,13,26,"Tới rồi, bèn đến cùng Môi-se, A-rôn và cả hội dân Y-sơ-ra-ên, trong đồng vắng Pha-ran, tại Ca-đe, mà thuật lại mọi sự cho hai người và cả hội chúng nghe, cùng đưa cho xem hoa quả của xứ."));
visuperobject.biblemap.put("b4_13_27", new visuperobject(4,13,27,"Vậy, các người ấy thuật cho Môi-se rằng: Chúng tôi đi đến xứ mà người đã sai chúng tôi đi; ấy quả thật một xứ đượm sữa và mật, nầy hoa quả xứ đó đây."));
visuperobject.biblemap.put("b4_13_28", new visuperobject(4,13,28,"Mà, dân sự ở trong xứ nầy vốn mạnh dạn, thành trì thật vững vàng và rất lớn; chúng tôi cũng có thấy con cái của A-nác ở đó."));
visuperobject.biblemap.put("b4_13_29", new visuperobject(4,13,29,"Dân A-ma-léc ở miền Nam, dân Hê-tít, dân Giê-bu-sít và dân A-mô-rít ở trong núi; dân Ca-na-an ở gần biển và dọc dài theo mé Giô-đanh."));
visuperobject.biblemap.put("b4_13_30", new visuperobject(4,13,30,"Ca-lép bèn làm cho dân sự, đương lằm bằm cùng Môi-se nín lặng đi, mà nói rằng: Chúng ta hãy đi lên và chiếm xứ đi, vì chúng ta thắng hơn được."));
visuperobject.biblemap.put("b4_13_31", new visuperobject(4,13,31,"Nhưng những người đi cùng Ca-lép nói rằng: Chúng ta không đi lên cự dân nầy được, vì chúng nó mạnh hơn chúng ta."));
visuperobject.biblemap.put("b4_13_32", new visuperobject(4,13,32,"Trước mặt dân Y-sơ-ra-ên, các người đó phao phản xứ mình đã do thám mà rằng: Xứ mà chúng tôi đã đi khắp đặng do thám, là một xứ nuốt dân sự mình; hết thảy những người chúng tôi đã thấy tại đó, đều là kẻ hình vóc cao lớn."));
visuperobject.biblemap.put("b4_13_33", new visuperobject(4,13,33,"Chúng tôi có thấy kẻ cao lớn, tức là con cháu của A-nác, thuộc về giống giềnh giàng; chúng tôi thấy mình khác nào con cào cào, và họ thấy chúng tôi cũng như vậy."));
visuperobject.biblemap.put("b4_14_1", new visuperobject(4,14,1,"Cả hội chúng bèn cất tiếng la lên, và dân sự khóc lóc trong đêm đó."));
visuperobject.biblemap.put("b4_14_2", new visuperobject(4,14,2,"Hết thảy dân Y-sơ-ra-ên lằm bằm cùng Môi-se và A-rôn; cả hội chúng nói cùng hai người rằng: Chớ chi chúng tôi đã chết trong xứ Ê-díp-tô, hay là đã chết trong đồng vắng nầy!"));
visuperobject.biblemap.put("b4_14_3", new visuperobject(4,14,3,"Vì cớ nào Ðức Giê-hô-va dẫn chúng tôi vào xứ nầy đặng bị gươm mà ngã? Vợ và con nhỏ chúng tôi sẽ bị làm một miếng mồi. Về phần chúng tôi, há chẳng khá trở về xứ Ê-díp-tô hơn sao?"));
visuperobject.biblemap.put("b4_14_4", new visuperobject(4,14,4,"Rồi dân sự nói với nhau rằng: Chúng ta hãy lập lên một quan trưởng, và trở về xứ Ê-díp-tô đi."));
visuperobject.biblemap.put("b4_14_5", new visuperobject(4,14,5,"Môi-se và A-rôn bèn sấp mình xuống trước mặt cả hội dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_14_6", new visuperobject(4,14,6,"Giô-suê, con trai của Nun, và Ca-lép, con trai của Giê-phu-nê, là hai người trong bọn đi do thám xứ, bèn xé áo quần mình."));
visuperobject.biblemap.put("b4_14_7", new visuperobject(4,14,7,"Hai người nói cùng cả hội dân Y-sơ-ra-ên rằng: Xứ mà chúng tôi đã đi khắp đặng do thám thật là một xứ rất tốt;"));
visuperobject.biblemap.put("b4_14_8", new visuperobject(4,14,8,"nếu Ðức Giê-hô-va đẹp lòng cùng chúng ta, ắt sẽ đem chúng ta vào xứ nầy mà ban cho; ấy là một xứ đượm sữa và mật."));
visuperobject.biblemap.put("b4_14_9", new visuperobject(4,14,9,"Chỉ các ngươi chớ dấy loạn cùng Ðức Giê-hô-va, và đừng sợ dân của xứ, vì dân đó sẽ là đồ nuôi chúng ta, bóng che chở họ đã rút đi khỏi họ rồi, và Ðức Giê-hô-va ở cùng ta. Chớ sợ chi."));
visuperobject.biblemap.put("b4_14_10", new visuperobject(4,14,10,"Bấy giờ cả hội chúng nói ném đá hai người nầy, nhưng sự vinh quang của Ðức Giê-hô-va hiện ra trên hội mạc, trước mặt dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_14_11", new visuperobject(4,14,11,"Ðức Giê-hô-va phán cùng Môi-se rằng: Dân nầy khinh ta và không tin ta cho đến chừng nào, mặc dầu các phép lạ ta làm giữa chúng nó?"));
visuperobject.biblemap.put("b4_14_12", new visuperobject(4,14,12,"Ta sẽ giáng cho dân sự nầy dịch lệ và tiêu diệt phần cơ nghiệp của nó đi; nhưng ta sẽ làm cho ngươi thành một dân lớn hơn và mạnh hơn nó."));
visuperobject.biblemap.put("b4_14_13", new visuperobject(4,14,13,"Môi-se thưa cùng Ðức Giê-hô-va rằng: Dân Ê-díp-tô có hay rằng Chúa cậy quyền năng Chúa đem dân nầy ra khỏi họ;"));
visuperobject.biblemap.put("b4_14_14", new visuperobject(4,14,14,"và có thuật điều đó cho dân xứ nầy. Ôi Ðức Giê-hô-va! người ta biết rằng Chúa ngự giữa dân nầy, Chúa hiện ra cho mắt chúng thấy, trụ mây Chúa ở trên dân nầy, và Chúa đi trước, ban ngày trong một trụ mây, ban đêm trong một trụ lửa."));
visuperobject.biblemap.put("b4_14_15", new visuperobject(4,14,15,"Nếu Chúa giết hết dân nầy như thể giết một người, thì các nước đã nghe nói về Chúa sẽ nói rằng:"));
visuperobject.biblemap.put("b4_14_16", new visuperobject(4,14,16,"Ðức Giê-hô-va không thể dẫn dân nầy vào xứ mà Ngài đã thề ban cho; bởi cớ đó, Ngài giết chết chúng nó trong đồng vắng."));
visuperobject.biblemap.put("b4_14_17", new visuperobject(4,14,17,"Vả, bây giờ, tôi xin quyền năng của Chúa hiện ra cách oai nghiêm như Chúa đã nói rằng:"));
visuperobject.biblemap.put("b4_14_18", new visuperobject(4,14,18,"Ðức Giê-hô-va vốn chậm nóng giận và đầy ơn; hay xá điều gian ác và tội lỗi; nhưng không kể kẻ có tội là vô tội, và nhơn tội tổ phụ phạt con cháu trải ba bốn đời."));
visuperobject.biblemap.put("b4_14_19", new visuperobject(4,14,19,"Tôi xin Chúa tha tội gian ác của dân nầy tùy theo ơn lớn của Chúa, như Chúa đã tha từ xứ Ê-díp-tô đến đây."));
visuperobject.biblemap.put("b4_14_20", new visuperobject(4,14,20,"Ðức Giê-hô-va đáp rằng: Ta đã tha như lời ngươi xin."));
visuperobject.biblemap.put("b4_14_21", new visuperobject(4,14,21,"Nhưng ta chỉ sự hằng sống ta mà quả quyết rằng, sự vinh quang của Ðức Giê-hô-va sẽ đầy dẫy khắp trái đất!"));
visuperobject.biblemap.put("b4_14_22", new visuperobject(4,14,22,"Trong mọi người đã thấy sự vinh quang ta, phép lạ ta đã làm tại xứ Ê-díp-tô và nơi đồng vắng, là các ngươi đã thử ta mười lần và đã không nghe lời ta,"));
visuperobject.biblemap.put("b4_14_23", new visuperobject(4,14,23,"thì chẳng một ai sẽ thấy xứ mà ta thề hứa cho tổ phụ chúng nó. Chẳng ai mà đã khinh ta sẽ thấy xứ đó đâu!"));
visuperobject.biblemap.put("b4_14_24", new visuperobject(4,14,24,"Nhưng vì kẻ tôi tớ Ca-lép ta không đồng lòng cùng chúng nó, theo ta một cách trung tín, thì ta sẽ đem ngươi vào xứ mà người đã có đi, và dòng dõi ngươi sẽ được xứ làm sản nghiệp."));
visuperobject.biblemap.put("b4_14_25", new visuperobject(4,14,25,"Dân A-ma-léc và dân Ca-na-an ở trong trũng; ngày mai các ngươi hãy trở lui lại sau, đi đến đồng vắng về hướng Biển đỏ."));
visuperobject.biblemap.put("b4_14_26", new visuperobject(4,14,26,"Ðức Giê-hô-va cũng phán cùng Môi-se và A-rôn, mà rằng:"));
visuperobject.biblemap.put("b4_14_27", new visuperobject(4,14,27,"Ta sẽ chịu hội chúng hung dữ nầy hay lằm bằm cùng ta cho đến chừng nào? Ta đã nghe lời lằm bằm của dân Y-sơ-ra-ên oán trách ta."));
visuperobject.biblemap.put("b4_14_28", new visuperobject(4,14,28,"Hãy nói với dân chúng: Ðức Giê-hô-va nói rằng: Ta chỉ sự hằng-sống ta mà thề, ta sẽ đãi các ngươi tùy theo lời ta đã nghe các ngươi nói;"));
visuperobject.biblemap.put("b4_14_29", new visuperobject(4,14,29,"những thây các ngươi sẽ ngã nằm trong đồng vắng nầy. Các ngươi mà người ta đã tu bộ, hết thảy bao nhiêu cũng vậy, từ hai mươi tuổi sắp lên, là những kẻ đã lằm bằm cùng ta,"));
visuperobject.biblemap.put("b4_14_30", new visuperobject(4,14,30,"thì chẳng hề được vào xứ mà ta đã thề cho các ngươi ở, ngoại trừ Ca-lép, con trai của Giê-phu-nê, và Giô-suê, con trai của Nun."));
visuperobject.biblemap.put("b4_14_31", new visuperobject(4,14,31,"Nhưng ta sẽ đem vào xứ những con trẻ của các ngươi mà các ngươi có nói rằng: Chúng nó sẽ bị làm một miếng mồi; rồi chúng nó sẽ biết xứ các ngươi đã chê bai."));
visuperobject.biblemap.put("b4_14_32", new visuperobject(4,14,32,"Còn những thây các ngươi sẽ ngã nằm trong đồng vắng nầy."));
visuperobject.biblemap.put("b4_14_33", new visuperobject(4,14,33,"Con cái các ngươi sẽ chăn chiên nơi đồng vắng trong bốn mươi năm, và sẽ mang hình phạt vì tội thông dâm của các ngươi, cho đến chừng nào thây của các ngươi đã ngã rạp hết trong đồng vắng."));
visuperobject.biblemap.put("b4_14_34", new visuperobject(4,14,34,"Các ngươi đi do thám xứ bao nhiêu ngày, nghĩa là bốn mươi ngày, thì các ngươi cũng sẽ mang hình phạt vì tội gian ác mình bấy nhiêu năm, nghĩa là bốn mươi năm, một năm đền cho một ngày; bấy giờ các ngươi sẽ biết ta đã xây khỏi các ngươi."));
visuperobject.biblemap.put("b4_14_35", new visuperobject(4,14,35,"Ta, Ðức Giê-hô-va, đã phán: Ta sẽ làm điều nầy cho cả hội chúng hung dữ nầy đã hiệp lại nghịch cùng ta; chúng nó sẽ bị hao mòn và chết tại trong đồng vắng nầy."));
visuperobject.biblemap.put("b4_14_36", new visuperobject(4,14,36,"Những người mà Môi-se đã sai đi do thám xứ, khi trở về có xui cho cả hội chúng lằm bằm cùng Môi-se,"));
visuperobject.biblemap.put("b4_14_37", new visuperobject(4,14,37,"và có phao phản xứ, những người nầy đều bị một tai vạ hành chết trước mặt Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_14_38", new visuperobject(4,14,38,"Nhưng trong bọn người đi do thám xứ, chỉ có Giô-suê, con trai của Nun, và Ca-lép, con trai của Giê-phu-nê, còn sống được."));
visuperobject.biblemap.put("b4_14_39", new visuperobject(4,14,39,"Môi-se thuật lại những lời nầy cho cả dân Y-sơ-ra-ên; rồi có một sự thảm sầu rất lớn trong dân sự."));
visuperobject.biblemap.put("b4_14_40", new visuperobject(4,14,40,"Ðoạn, dân sự dậy sớm đi lên chót núi mà nói rằng: Chúng tôi đây sẽ đi lên tới chỗ Ðức Giê-hô-va đã phán-hứa, vì chúng tôi có phạm tội."));
visuperobject.biblemap.put("b4_14_41", new visuperobject(4,14,41,"Song Môi-se nói rằng: Sao các ngươi trái mạng Ðức Giê-hô-va? Sự đó sẽ chẳng may mắn đâu."));
visuperobject.biblemap.put("b4_14_42", new visuperobject(4,14,42,"Chớ đi lên đó, e các ngươi bị quân nghịch đánh bại chăng; vì Ðức Giê-hô-va không còn ở giữa các ngươi nữa."));
visuperobject.biblemap.put("b4_14_43", new visuperobject(4,14,43,"Kìa, dân A-ma-léc và dân Ca-na-an ở đằng trước các ngươi, các ngươi sẽ bị gươm ngã rạp, bởi vì đã bội nghịch cùng Ðức Giê-hô-va, Ðức Giê-hô-va sẽ không ở cùng các ngươi."));
visuperobject.biblemap.put("b4_14_44", new visuperobject(4,14,44,"Nhưng dân sự cố ý đi lên chót núi; còn hòm giao ước của Ðức Giê-hô-va và Môi-se không đi ra khỏi trại quân."));
visuperobject.biblemap.put("b4_14_45", new visuperobject(4,14,45,"Dân A-ma-léc và dân Ca-na-an ở trong núi nầy đổ xuống, đánh bại và phân thây dân Y-sơ-ra-ên cho đến Họt-ma."));
visuperobject.biblemap.put("b4_15_1", new visuperobject(4,15,1,"Ðoạn, Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_15_2", new visuperobject(4,15,2,"Hãy nói cùng dân Y-sơ-ra-ên rằng: Khi các ngươi vào xứ mà ta ban cho các ngươi đặng ở,"));
visuperobject.biblemap.put("b4_15_3", new visuperobject(4,15,3,"và các ngươi dâng cho Ðức Giê-hô-va một của lễ dùng lửa đốt; hoặc một của lễ thiêu, hoặc một của lễ trả sự hứa nguyện, hoặc một của lễ lạc ý, hay là trong những lễ trọng thể, các ngươi muốn dâng cho Ðức Giê-hô-va một của lễ có mùi thơm bằng bò hay chiên,"));
visuperobject.biblemap.put("b4_15_4", new visuperobject(4,15,4,"thì ai dâng lễ vật mình phải dâng cho Ðức Giê-hô-va một của lễ chay bằng một phần mười ê-pha bột lọc nhồi với một phần tư hin dầu."));
visuperobject.biblemap.put("b4_15_5", new visuperobject(4,15,5,"Về mỗi con chiên con, ngươi phải dâng một lễ quán bằng một phần tư hin rượu với của lễ thiêu hay là với của lễ khác."));
visuperobject.biblemap.put("b4_15_6", new visuperobject(4,15,6,"Nhược bằng về một con chiên đực, thì ngươi phải dùng một của lễ chay bằng hai phần mười ê-pha bột lọc nhồi với một phần ba hin dầu,"));
visuperobject.biblemap.put("b4_15_7", new visuperobject(4,15,7,"và một phần ba hin rượu làm lễ quán, mà dâng có mùi thơm cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_15_8", new visuperobject(4,15,8,"Nhược bằng ngươi dâng một con bò tơ, hoặc làm của lễ thiêu, hoặc làm của lễ trả sự hứa nguyện hay là làm của lễ thù ân cho Ðức Giê-hô-va,"));
visuperobject.biblemap.put("b4_15_9", new visuperobject(4,15,9,"thì phải dâng chung với con bò tơ một của lễ chay bằng ba phần mười ê-pha bột lọc nhồi với nửa hin dầu,"));
visuperobject.biblemap.put("b4_15_10", new visuperobject(4,15,10,"và dâng nửa hin rượu làm lễ quán: ấy là một của lễ dùng lửa dâng lên có mùi thơm cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_15_11", new visuperobject(4,15,11,"Người ta cũng phải làm như vậy về mỗi con bò đực, mỗi con chiên đực, mỗi con chiên con, hay là mỗi con dê con."));
visuperobject.biblemap.put("b4_15_12", new visuperobject(4,15,12,"Tùy theo số con sinh mà các ngươi sẽ dâng, thì phải làm như vậy cho mỗi con."));
visuperobject.biblemap.put("b4_15_13", new visuperobject(4,15,13,"Phàm người sanh ra trong xứ phải làm như vậy, khi nào dâng một của lễ dùng lửa đốt, có mùi thơm cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_15_14", new visuperobject(4,15,14,"Nếu một khách ngoại bang kiều ngụ trong các ngươi hay là một người nào ở giữa các ngươi từ đời nầy sang đời kia, dùng lửa dâng một của lễ có mùi thơm cho Ðức Giê-hô-va, thì phải làm y như các ngươi làm vậy."));
visuperobject.biblemap.put("b4_15_15", new visuperobject(4,15,15,"Trong hội chúng chỉ đồng có một luật lệ cho các ngươi và cho khách ngoại bang kiều ngụ giữa các ngươi. Ấy sẽ là một lệ định đời đời cho dòng dõi các ngươi trước mặt Ðức Giê-hô-va; khách ngoại bang cũng đồng như các ngươi vậy."));
visuperobject.biblemap.put("b4_15_16", new visuperobject(4,15,16,"Sẽ có một luật và một lệ như nhau cho các ngươi và cho khách ngoại bang kiều ngụ giữa các ngươi."));
visuperobject.biblemap.put("b4_15_17", new visuperobject(4,15,17,"Ðức Giê-hô-va cũng phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_15_18", new visuperobject(4,15,18,"Hãy nói cho dân Y-sơ-ra-ên rằng: Khi nào các ngươi đã vào xứ mà ta sẽ dẫn các ngươi vào,"));
visuperobject.biblemap.put("b4_15_19", new visuperobject(4,15,19,"và ăn bánh của xứ đó, thì các ngươi phải lấy một lễ vật dâng giơ lên cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_15_20", new visuperobject(4,15,20,"Các ngươi phải lấy ra một cái bánh nhỏ làm lễ vật, tức là bánh đầu-tiên về bột nhồi của các ngươi; các ngươi phải dâng y một cách như dâng giơ lên lễ vật về sân đạp lúa vậy."));
visuperobject.biblemap.put("b4_15_21", new visuperobject(4,15,21,"Từ đời nầy sang đời kia, các ngươi phải dâng giơ lên cho Ðức Giê-hô-va một lễ vật về bột nhồi đầu tiên của các ngươi."));
visuperobject.biblemap.put("b4_15_22", new visuperobject(4,15,22,"Khi các ngươi lầm lỡ phạm tội, không giữ hết thảy các điều răn nầy mà Ðức Giê-hô-va đã truyền cho Môi-se,"));
visuperobject.biblemap.put("b4_15_23", new visuperobject(4,15,23,"nghĩa là mọi điều chi Ðức Giê-hô-va đã cậy Môi-se truyền cho các ngươi, từ ngày Ðức Giê-hô-va truyền những điều răn nầy cho và về ngày sau, trong những dòng dõi sẽ đến của các ngươi,"));
visuperobject.biblemap.put("b4_15_24", new visuperobject(4,15,24,"nếu là tội phạm vì lầm lỡ mà hội chúng không hay biết, thì cả hội chúng tùy theo mạng lịnh phải dùng một con bò đực tơ dâng làm của lễ thiêu có mùi thơm cho Ðức Giê-hô-va, luôn với của lễ chay cùng lễ quán cặp theo; và một con dê đực làm của lễ chuộc tội."));
visuperobject.biblemap.put("b4_15_25", new visuperobject(4,15,25,"Thầy tế lễ sẽ làm lễ chuộc tội cho cả hội dân Y-sơ-ra-ên, thì tội hội chúng sẽ được tha, vì là một tội phạm lầm lỡ và vì hội chúng đã đem đến trước mặt Ðức Giê-hô-va lễ vật mình, tức là một của lễ dùng lửa dâng cho Ðức Giê-hô-va, luôn với của lễ chuộc tội đặng chữa sự lầm lỡ mình."));
visuperobject.biblemap.put("b4_15_26", new visuperobject(4,15,26,"Cả hội dân Y-sơ-ra-ên và khách ngoại bang kiều ngụ trong dân đó sẽ được tha tội, vì là bởi lầm lỡ mà cả dân sự đã phạm tội."));
visuperobject.biblemap.put("b4_15_27", new visuperobject(4,15,27,"Nhược bằng chỉ một người lầm lỡ phạm tội, thì người đó phải dâng một con dê cái giáp năm làm của lễ chuộc tội;"));
visuperobject.biblemap.put("b4_15_28", new visuperobject(4,15,28,"thầy tế lễ sẽ làm lễ chuộc tội cho người đã lầm lỡ mà phạm tội như vậy trước mặt Ðức Giê-hô-va, và khi đã làm lễ chuộc tội cho người, thì người sẽ được tha."));
visuperobject.biblemap.put("b4_15_29", new visuperobject(4,15,29,"Khi lầm lỡ mà phạm tội, thì sẽ có đồng một luật lệ cho các ngươi, hoặc là sanh giữa dân Y-sơ-ra-ên hay là khách kiều ngụ trong dân đó."));
visuperobject.biblemap.put("b4_15_30", new visuperobject(4,15,30,"Nhưng ai cố ý phạm tội, hoặc người sanh trong xứ, hay là khách ngoại bang, thì ai đó khinh bỉ Ðức Giê-hô-va; người sẽ bị truất khỏi dân sự mình,"));
visuperobject.biblemap.put("b4_15_31", new visuperobject(4,15,31,"vì người đã khinh bỉ lời Ðức Giê-hô-va và trái mạng của Ngài: người hẳn sẽ bị truất diệt, tội gian ác người đổ lại trên mình người."));
visuperobject.biblemap.put("b4_15_32", new visuperobject(4,15,32,"Vả, dân Y-sơ-ra-ên đương ở tại đồng vắng, gặp một người lượm củi trong ngày sa-bát;"));
visuperobject.biblemap.put("b4_15_33", new visuperobject(4,15,33,"những kẻ gặp người đương lượm củi dẫn người đến Môi-se, A-rôn, và cả hội chúng."));
visuperobject.biblemap.put("b4_15_34", new visuperobject(4,15,34,"Họ bắt người giam tù, vì điều phải làm cho người chưa nhất định."));
visuperobject.biblemap.put("b4_15_35", new visuperobject(4,15,35,"Ðức Giê-hô-va bèn phán cùng Môi-se rằng: Người nầy phải phạt xử tử, cả hội chúng hãy ném đá người ngoài trại quân."));
visuperobject.biblemap.put("b4_15_36", new visuperobject(4,15,36,"Vậy, cả hội chúng đem người ra ngoài trại quân mà ném đá, và người chết, y như Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b4_15_37", new visuperobject(4,15,37,"Ðức Giê-hô-va lại phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_15_38", new visuperobject(4,15,38,"Hãy nói cùng dân Y-sơ-ra-ên và biểu họ từ đời nầy sang đời kia hãy làm một cái tua nơi các chéo áo mình, và trên mỗi tua kết một sợi dây màu điều."));
visuperobject.biblemap.put("b4_15_39", new visuperobject(4,15,39,"Các ngươi phải mang cái tua nầy; khi thấy nó các ngươi sẽ nhớ lại hết thảy điều răn của Ðức Giê-hô-va mà làm theo, không theo tư dục của lòng và mắt mình, là điều làm cho các ngươi sa vào sự thông dâm."));
visuperobject.biblemap.put("b4_15_40", new visuperobject(4,15,40,"Như vậy, các ngươi sẽ nhớ lại mà làm theo những điều răn của ta, và làm thánh cho Ðức Chúa Trời của các ngươi."));
visuperobject.biblemap.put("b4_15_41", new visuperobject(4,15,41,"Ta là Giê-hô-va, Ðức Chúa Trời của các ngươi, Ðấng đã đem các ngươi ra khỏi xứ Ê-díp-tô đặng làm Ðức Chúa Trời của các ngươi. Ta là Giê-hô-va, Ðức Chúa Trời của các ngươi."));
visuperobject.biblemap.put("b4_16_1", new visuperobject(4,16,1,"Vả, Cô-rê, con trai của Dít-sê-ha, cháu của Kê-hát, chít của Lê-vi, giục theo mình Ða-than và A-bi-ram, con trai của Ê-li-áp, cùng với Ôn, con trai của Phê-lết, cháu Ru-bên."));
visuperobject.biblemap.put("b4_16_2", new visuperobject(4,16,2,"Các người nầy với hai trăm năm mươi người trong dân Y-sơ-ra-ên, vốn là quan tướng của hội, nghị viên của hội đồng, có danh giá, đều hiệp nhau dấy nghịch cùng Môi-se và A-rôn,"));
visuperobject.biblemap.put("b4_16_3", new visuperobject(4,16,3,"mà rằng: Thôi đủ rồi! vì cả hội chúng đều là thánh, và Ðức Giê-hô-va ngự ở trong; vậy sao các ngươi tự cao trên hội chúng của Ðức Giê-hô-va?"));
visuperobject.biblemap.put("b4_16_4", new visuperobject(4,16,4,"Môi-se nghe điều đó, bèn sấp mặt xuống đất,"));
visuperobject.biblemap.put("b4_16_5", new visuperobject(4,16,5,"nói cùng Cô-rê và hết thảy kẻ hiệp với người mà rằng: Sáng mai, Ðức Giê-hô-va sẽ tỏ cho biết ai thuộc về Ngài và ai là thánh; Ngài sẽ khiến người đó lại gần Ngài, tức khiến người nào mà Ngài đã chọn lấy đến gần Ngài."));
visuperobject.biblemap.put("b4_16_6", new visuperobject(4,16,6,"Hỡi Cô-rê, hãy làm điều nầy: Ngươi và hết thảy kẻ hiệp với ngươi hãy cầm lư hương;"));
visuperobject.biblemap.put("b4_16_7", new visuperobject(4,16,7,"sáng mai hãy để lửa ở trong và bỏ hương ở trên trước mặt Ðức Giê-hô-va. Người nào mà Ðức Giê-hô-va sẽ chọn, ấy là người thánh. Hỡi con cháu Lê-vi, thôi đủ rồi!"));
visuperobject.biblemap.put("b4_16_8", new visuperobject(4,16,8,"Môi-se nói cùng Cô-rê rằng: Hỡi con cháu Lê-vi, bây giờ hãy nghe."));
visuperobject.biblemap.put("b4_16_9", new visuperobject(4,16,9,"Về phần các ngươi, Ðức Chúa Trời của Y-sơ-ra-ên đã biệt các ngươi riêng ra với hội chúng Y-sơ-ra-ên, và khiến các ngươi đến gần Ngài đặng làm công việc trong đền tạm của Ðức Giê-hô-va, đặng chực hầu việc trước mặt hội chúng, việc ấy há là nhỏ mọn sao?"));
visuperobject.biblemap.put("b4_16_10", new visuperobject(4,16,10,"Ngài biểu ngươi, hết thảy anh em ngươi với con cháu Lê-vi đến gần Ngài, các ngươi lại còn kiếm chức tế lễ nữa sao?"));
visuperobject.biblemap.put("b4_16_11", new visuperobject(4,16,11,"Vì vậy, ngươi và toàn bè đảng của ngươi đều hiệp lại nghịch cùng Ðức Giê-hô-va! Vì A-rôn là ai mà các ngươi lằm bằm cùng người?"));
visuperobject.biblemap.put("b4_16_12", new visuperobject(4,16,12,"Môi-se sai đòi Ða-than và A-bi-ram, con trai Ê-li-áp; nhưng hai người đáp rằng: Chúng tôi không đi lên đâu."));
visuperobject.biblemap.put("b4_16_13", new visuperobject(4,16,13,"Há là điều nhỏ mọn mà ngươi đã đem chúng ta ra khỏi xứ đượm sữa và mật, đặng làm cho chúng ta chết trong đồng vắng, mà ngươi lại còn muốn lấn lướt trên chúng ta nữa sao?"));
visuperobject.biblemap.put("b4_16_14", new visuperobject(4,16,14,"Quả thật, ngươi không có dẫn chúng ta đến trong một xứ đượm sữa và mật đâu, ngươi không cho chúng ta đồng ruộng hay là vườn nho làm sản nghiệp đâu! Ngươi toan khoét mắt của dân nầy ư? Chúng ta không đi lên đâu."));
visuperobject.biblemap.put("b4_16_15", new visuperobject(4,16,15,"Môi-se nổi giận lắm, thưa cùng Ðức Giê-hô-va rằng: Xin đừng kể đến của lễ chay chúng nó nữa; tôi không có bắt nơi chúng nó một con lừa nào, và tôi cũng không có làm hại một ai trong bọn chúng nó."));
visuperobject.biblemap.put("b4_16_16", new visuperobject(4,16,16,"Ðoạn, Môi-se nói cùng Cô-rê rằng: Ngày mai ngươi và toàn bè đảng ngươi với A-rôn hãy ra mắt Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_16_17", new visuperobject(4,16,17,"Mỗi người hãy lấy lư hương mình bỏ hương ở trong, rồi mỗi người dâng lư hương mình trước mặt Ðức Giê-hô-va, tức là hai trăm năm mươi cái lư hương. Ngươi và A-rôn, mỗi người cũng phải có lư hương mình."));
visuperobject.biblemap.put("b4_16_18", new visuperobject(4,16,18,"Vậy, mỗi người lấy lư hương mình để lửa vào, bỏ hương trên, rồi đứng tại cửa hội mạc luôn với Môi-se và A-rôn."));
visuperobject.biblemap.put("b4_16_19", new visuperobject(4,16,19,"Cô-rê hiệp cả hội chúng nghịch lại Môi-se và A-rôn tại cửa hội mạc; sự vinh quang của Ðức Giê-hô-va hiện ra cùng cả hội chúng."));
visuperobject.biblemap.put("b4_16_20", new visuperobject(4,16,20,"Ðoạn, Ðức Giê-hô-va phán cùng Môi-se và A-rôn rằng:"));
visuperobject.biblemap.put("b4_16_21", new visuperobject(4,16,21,"Hãy tách khỏi giữa hội chúng nầy, thì ta sẽ tiêu diệt nó trong một lát."));
visuperobject.biblemap.put("b4_16_22", new visuperobject(4,16,22,"Hai người bèn sấp mặt xuống mà rằng: Ôi Ðức Chúa Trời, là Chúa của thần linh mọi xác thịt! chỉ có một người phạm tội mà Chúa lại nổi giận cùng cả hội chúng sao?"));
visuperobject.biblemap.put("b4_16_23", new visuperobject(4,16,23,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_16_24", new visuperobject(4,16,24,"Hãy nói cùng hội chúng mà rằng: Hãy dan ra khỏi chung quanh chỗ ở của Cô-rê, Ða-than, và A-bi-ram."));
visuperobject.biblemap.put("b4_16_25", new visuperobject(4,16,25,"Môi-se đứng dậy, đi lại phía Ða-than và A-bi-ram; các trưởng lão Y-sơ-ra-ên đi theo người."));
visuperobject.biblemap.put("b4_16_26", new visuperobject(4,16,26,"Người nói cùng hội chúng rằng: Bây giờ, hãy dan ra khỏi trại của các người hung ác nầy, và chớ đụng đến vật chi thuộc về họ, e các ngươi sẽ chết vì những tội của họ chăng."));
visuperobject.biblemap.put("b4_16_27", new visuperobject(4,16,27,"Vậy, dân sự dan ra khỏi tứ phía chỗ ở của Cô-rê, Ða-than, và A-bi-ram. Ða-than và A-bi-ram ra đứng tại cửa trại với vợ, con trai và con nít mình."));
visuperobject.biblemap.put("b4_16_28", new visuperobject(4,16,28,"Môi-se bèn nói rằng: Nhờ điều nầy các ngươi sẽ biết rằng Ðức Giê-hô-va có sai ta đặng làm các điều nầy, và ta chẳng làm sự chi tự ta."));
visuperobject.biblemap.put("b4_16_29", new visuperobject(4,16,29,"Nếu các kẻ đó chết như mọi người khác chết, nếu họ đồng chịu số phận chung như mọi người, thì Ðức Giê-hô-va không có sai ta."));
visuperobject.biblemap.put("b4_16_30", new visuperobject(4,16,30,"Nhưng nếu Ðức Giê-hô-va làm một sự thật mới, nếu đất hả miệng ra nuốt họ và mọi món chi thuộc về họ, nếu họ còn đương sống mà xuống âm phủ, thì các ngươi sẽ biết rằng những người nầy có khinh bỉ Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_16_31", new visuperobject(4,16,31,"Vừa khi Môi-se nói dứt các lời nầy, đất ngay dưới chân họ bèn nứt ra;"));
visuperobject.biblemap.put("b4_16_32", new visuperobject(4,16,32,"hả miệng nuốt họ, gia quyến và hết thảy phe Cô-rê cùng tài sản của chúng nó."));
visuperobject.biblemap.put("b4_16_33", new visuperobject(4,16,33,"Các người đó còn đương sống và mọi vật chi thuộc về họ đều xuống âm phủ; đất lấp lại và chúng nó bị diệt khỏi hội chúng."));
visuperobject.biblemap.put("b4_16_34", new visuperobject(4,16,34,"Cả Y-sơ-ra-ên ở xung quanh nghe tiếng họ la, đều chạy trốn, vì nói rằng: Chúng ta hãy coi chừng, kẻo đất nuốt chúng ta chăng!"));
visuperobject.biblemap.put("b4_16_35", new visuperobject(4,16,35,"Rồi một ngọn lửa từ Ðức Giê-hô-va lòe ra thiêu hóa hai trăm năm mươi người đã dâng hương."));
visuperobject.biblemap.put("b4_16_36", new visuperobject(4,16,36,"Ðoạn, Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_16_37", new visuperobject(4,16,37,"Hãy nói với Ê-lê-a-sa, con trai thầy tế lễ A-rôn, biểu lượm các lư hương ra khỏi chỗ lửa cháy, và đổ lửa của các lư hương nơi xa, vì lư hương đã nên thánh."));
visuperobject.biblemap.put("b4_16_38", new visuperobject(4,16,38,"Còn những lư hương của các người đã phạm tội cùng sanh mạng mình, người ta phải lấy làm thành những tấm dát mỏng để bọc bàn thờ. Bởi các lư hương đã dâng trước mặt Ðức Giê-hô-va đã thành thánh; những tấm dát đó sẽ dùng làm một dấu cho dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_16_39", new visuperobject(4,16,39,"Vậy, Ê-lê-a-sa, thầy tế lễ, lấy những lư hương bằng đồng của các người bị thiêu đã dâng lên, và người ta làm thành những tấm dát mòng để bọc bàn thờ."));
visuperobject.biblemap.put("b4_16_40", new visuperobject(4,16,40,"Ấy là một kỷ niệm cho dân Y-sơ-ra-ên, để người nào ngoại dòng A-rôn chớ đến gần đặng xông hương trước mặt Ðức Giê-hô-va, kẻo e đồng số phận cùng Cô-rê và bè đảng người, theo lời Ðức Giê-hô-va đã cậy Môi-se truyền dạy."));
visuperobject.biblemap.put("b4_16_41", new visuperobject(4,16,41,"Ngày mai, cả hội dân Y-sơ-ra-ên lằm bằm cùng Môi-se và A-rôn mà rằng: Hai người đã làm chết dân của Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_16_42", new visuperobject(4,16,42,"Khi hội chúng đương hiệp nhau nghịch cùng Môi-se và A-rôn, thì ngó về hướng hội mạc, xảy thấy trụ mây bao phủ hội mạc, và sự vinh quang của Ðức Giê-hô-va hiện ra."));
visuperobject.biblemap.put("b4_16_43", new visuperobject(4,16,43,"Môi-se và A-rôn đến đứng trước hội mạc,"));
visuperobject.biblemap.put("b4_16_44", new visuperobject(4,16,44,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_16_45", new visuperobject(4,16,45,"Hãy dan ra khỏi giữa hội chúng nầy, thì ta sẽ tiêu diệt nó trong một lát. Nhưng hai người sấp mặt xuống đất,"));
visuperobject.biblemap.put("b4_16_46", new visuperobject(4,16,46,"rồi Môi-se nói cùng A-rôn rằng: Hãy cầm lấy lư hương để lửa từ trên bàn thờ vào, bỏ hương lên trên, mau mau đi đến hội chúng và làm lễ chuộc tội cho họ; vì sự thạnh nộ của Ðức Giê-hô-va đã nổi lên, và tai vạ đã phát khởi."));
visuperobject.biblemap.put("b4_16_47", new visuperobject(4,16,47,"A-rôn bèn cầm lấy lư hương y như Môi-se đã biểu, chạy đến giữa hội chúng; kìa, tai vạ đã phát khởi giữa dân sự. A-rôn bèn bỏ hương vào, và làm lễ chuộc tội cho dân sự."));
visuperobject.biblemap.put("b4_16_48", new visuperobject(4,16,48,"Người đứng giữa kẻ chết và kẻ sống, thì tai vạ bèn ngừng lại."));
visuperobject.biblemap.put("b4_16_49", new visuperobject(4,16,49,"Có mười bốn ngàn bảy trăm người chết vì tai vạ nầy, trừ ra những kẻ đã bị chết vì cớ Cô-rê."));
visuperobject.biblemap.put("b4_16_50", new visuperobject(4,16,50,"Ðoạn, A-rôn trở về cùng Môi-se tại cửa hội mạc, rồi tai vạ ngừng lại."));
visuperobject.biblemap.put("b4_17_1", new visuperobject(4,17,1,"Kế đó, Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_17_2", new visuperobject(4,17,2,"Hãy nói cùng dân Y-sơ-ra-ên và biểu rằng: Mỗi chi phái của họ phải đem nộp cho ngươi một cây gậy; nghĩa là mười hai cây gậy bởi các quan trưởng của mỗi chi phái."));
visuperobject.biblemap.put("b4_17_3", new visuperobject(4,17,3,"Ngươi phải đề tên mỗi người trên cây gậy của mình, và đề tên A-rôn trên cây gậy của Lê-vi; vì phải có một cây gậy cho mỗi trưởng tộc."));
visuperobject.biblemap.put("b4_17_4", new visuperobject(4,17,4,"Ngươi phải để các gậy đó trong hội mạc, trước hòm bảng chứng, là nơi ta gặp ngươi."));
visuperobject.biblemap.put("b4_17_5", new visuperobject(4,17,5,"Hễ người nào ta chọn lấy, thì cây gậy người đó sẽ trổ hoa; vậy ta sẽ làm cho nín đi trước mặt ta những lời lằm bằm của dân Y-sơ-ra-ên đã phát ra đối nghịch cùng các ngươi."));
visuperobject.biblemap.put("b4_17_6", new visuperobject(4,17,6,"Môi-se nói cùng dân Y-sơ-ra-ên, thì hết thảy trưởng tộc, tùy theo chi phái mình, đem nộp cho người một cây gậy, tức là mười hai cây. Gậy của A-rôn ở giữa các cây gậy khác."));
visuperobject.biblemap.put("b4_17_7", new visuperobject(4,17,7,"Môi-se để những gậy đó trong Trại bảng chứng trước mặt Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_17_8", new visuperobject(4,17,8,"Ngày mai, khi Môi-se vào Trại bảng chứng, xảy thấy cây gậy A-rôn về nhà Lê-vi đã trổ hoa: nó có nứt mụt, sanh hoa và trái hạnh nhơn chín."));
visuperobject.biblemap.put("b4_17_9", new visuperobject(4,17,9,"Môi-se bèn rút các cây gậy khỏi trước mặt Ðức Giê-hô-va, đem đưa cho cả dân Y-sơ-ra-ên; các người đều thấy và mỗi người lấy gậy mình lại."));
visuperobject.biblemap.put("b4_17_10", new visuperobject(4,17,10,"Ðức Giê-hô-va phán cùng Môi-se rằng: Hãy đem cây gậy của A-rôn để lại trước hòm bảng chứng, đặng giữ làm một dấu cho con cái phản nghịch; ngươi phải làm cho nín đi những lời lằm bằm đối nghịch cùng ta, hầu cho dân chúng chẳng chết."));
visuperobject.biblemap.put("b4_17_11", new visuperobject(4,17,11,"Môi-se làm như vậy, tức làm y như Ðức Giê-hô-va đã phán dặn mình."));
visuperobject.biblemap.put("b4_17_12", new visuperobject(4,17,12,"Dân Y-sơ-ra-ên nói cùng Môi-se rằng: Nầy, chúng tôi bị diệt, chúng tôi bị chết, chúng tôi chết mất hết thảy!"));
visuperobject.biblemap.put("b4_17_13", new visuperobject(4,17,13,"Phàm ai đến gần đền tạm của Ðức Giê-hô-va đều chết. Có lẽ chúng tôi phải chết hết sao?"));
visuperobject.biblemap.put("b4_18_1", new visuperobject(4,18,1,"Ðức Giê-hô-va bèn phán cùng A-rôn rằng: Ngươi, các con trai ngươi, và tông tộc ngươi phải mang lấy những tội lỗi phạm trong nơi thánh; còn ngươi và các con trai ngươi phải mang lấy những tội lỗi phạm trong chức tế lễ."));
visuperobject.biblemap.put("b4_18_2", new visuperobject(4,18,2,"Cũng hãy biểu đến gần ngươi các anh em mình, là chi phái Lê-vi, tức tông tộc ngươi, đặng các người đó hiệp với ngươi và hầu việc ngươi; nhưng ngươi và các con trai ngươi phải ở trước Trại bảng chứng."));
visuperobject.biblemap.put("b4_18_3", new visuperobject(4,18,3,"Các người đó sẽ gìn giữ điều chi ngươi truyền dạy, và điều nào thuộc về cả Trại; chỉ không nên lại gần những vật thánh, cũng đừng lại gần bàn thờ, e khi chúng nó phải chết, và các ngươi cũng chết luôn chăng."));
visuperobject.biblemap.put("b4_18_4", new visuperobject(4,18,4,"Vậy, các người đó sẽ hiệp với ngươi coi sóc điều chi thuộc về hội mạc, và giữ công việc của Trại; một người ngoại nào chẳng nên đến gần các ngươi."));
visuperobject.biblemap.put("b4_18_5", new visuperobject(4,18,5,"Các ngươi phải coi sóc điều chi thuộc về nơi thánh và bàn thờ để đừng có sự thạnh nộ cùng dân Y-sơ-ra-ên nữa."));
visuperobject.biblemap.put("b4_18_6", new visuperobject(4,18,6,"Còn ta đây, trong dân Y-sơ-ra-ên ta đã chọn lấy anh em các ngươi, là những người Lê-vi, mà ban cho các ngươi, vì đã dâng cho Ðức Giê-hô-va rồi, đặng làm công việc của hội mạc."));
visuperobject.biblemap.put("b4_18_7", new visuperobject(4,18,7,"Nhưng ngươi và các con trai ngươi phải kỹ lưỡng lo cho xong chức tế lễ, quản trị các điều chi thuộc về bàn thờ, và vật chi ở phía trong màn; các ngươi phải làm việc mình tại đó. Chức tế lễ của các ngươi là một việc ta phong cho như một của ban: người ngoại nào đến gần sẽ bị xử tử."));
visuperobject.biblemap.put("b4_18_8", new visuperobject(4,18,8,"Ðức Giê-hô-va lại phán cùng A-rôn rằng: Nầy ta cử ngươi coi sóc các lễ vật của ta thâu lấy về mọi-vật mà dân Y-sơ-ra-ên đã biệt riêng ra thánh; cứ một lệ định đời đời ta ban những lễ vật nầy cho ngươi và cho các con trai ngươi như một phần riêng mà phép xức dầu truyền cho."));
visuperobject.biblemap.put("b4_18_9", new visuperobject(4,18,9,"Trong các vật rất thánh không có thiêu hóa mà dân Y-sơ-ra-ên dâng cho ta, nầy là phần sẽ thuộc về ngươi: Hết thảy lễ vật của họ, nào của lễ chay, nào của lễ chuộc tội, nào của lễ chuộc sự mắc lỗi; những vật rất thánh nầy đều sẽ thuộc về ngươi và các con trai ngươi."));
visuperobject.biblemap.put("b4_18_10", new visuperobject(4,18,10,"Ngươi phải ăn nó trong nơi rất thánh; mọi người nam phải ăn lấy, ấy sẽ làm một vật thánh cho ngươi."));
visuperobject.biblemap.put("b4_18_11", new visuperobject(4,18,11,"Vật nầy cũng sẽ thuộc về ngươi: Phàm lễ vật nào mà dân Y-sơ-ra-ên dâng giơ lên và đưa qua đưa lại, thì ta cứ một lệ định đời đời mà ban cho ngươi và các con trai con gái ngươi. Ai trong nhà ngươi tinh sạch sẽ được ăn các món đó."));
visuperobject.biblemap.put("b4_18_12", new visuperobject(4,18,12,"Ta cũng đã ban cho ngươi những vật đầu mùa mà dân Y-sơ-ra-ên sẽ dâng cho Ðức Giê-hô-va, hết thảy phần dầu, rượu ngọt và lúa miến nhất hạng."));
visuperobject.biblemap.put("b4_18_13", new visuperobject(4,18,13,"Mọi hoa quả đầu mùa của đất sanh sản mà dân Y-sơ-ra-ên dâng cho Ðức Giê-hô-va, đều sẽ thuộc về ngươi; ai trong nhà ngươi tinh sạch sẽ được ăn lấy."));
visuperobject.biblemap.put("b4_18_14", new visuperobject(4,18,14,"Phàm vật chi mà dân Y-sơ-ra-ên phú dâng sẽ thuộc về ngươi."));
visuperobject.biblemap.put("b4_18_15", new visuperobject(4,18,15,"Mọi con đầu lòng của các xác thịt, hoặc ngươi, hoặc vật mà dân Y-sơ-ra-ên dâng cho Ðức Giê-hô-va, đều sẽ thuộc về ngươi; nhưng ngươi phải chuộc lại con đầu lòng của loài người, và cũng phải chuộc lại con đầu lòng của thú vật ô uế."));
visuperobject.biblemap.put("b4_18_16", new visuperobject(4,18,16,"Về sự chuộc lại, ngươi phải chuộc lại những con từ một tháng trở đi, theo giá định của ngươi, là năm siếc-lơ bạc, theo siếc-lơ của nơi thánh là hai mươi ghê-ra."));
visuperobject.biblemap.put("b4_18_17", new visuperobject(4,18,17,"Nhưng ngươi chớ chuộc lại con đầu lòng của bò cái, hoặc con đầu lòng của chiên cái hay là con đầu lòng của dê cái; ấy là những vật thánh. Ngươi phải rưới huyết nó trên bàn thờ, và xông mỡ nó làm của lễ dùng lửa dâng lên có mùi thơm cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_18_18", new visuperobject(4,18,18,"Thịt nó sẽ thuộc về ngươi như cái o dâng đưa qua đưa lại và cái giò hữu."));
visuperobject.biblemap.put("b4_18_19", new visuperobject(4,18,19,"Ta cứ lệ định đời đời ban cho ngươi, các con trai và các con gái ngươi, hết thảy lễ vật thánh mà dân Y-sơ-ra-ên dâng giơ lên cho Ðức Giê-hô-va: ấy là một giao ước bằng muối đời đời, không bỏ được, trước mặt Ðức Giê-hô-va cho ngươi và cho dòng dõi ngươi."));
visuperobject.biblemap.put("b4_18_20", new visuperobject(4,18,20,"Ðoạn, Ðức Giê-hô-va phán cùng A-rôn rằng: Ngươi sẽ không có cơ nghiệp trong xứ dân Y-sơ-ra-ên; và chẳng sẽ có phần cho ngươi giữa dân đó; ta là phần của ngươi, và là cơ nghiệp của ngươi ở giữa dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_18_21", new visuperobject(4,18,21,"Còn về con cháu Lê-vi, nầy ta đã ban cho làm cơ nghiệp, mọi thuế một phần mười của Y-sơ-ra-ên làm lương về công việc mình làm trong hội mạc."));
visuperobject.biblemap.put("b4_18_22", new visuperobject(4,18,22,"Dân Y-sơ-ra-ên chớ lại gần hội mạc nữa, e phải mắc tội và chết chăng."));
visuperobject.biblemap.put("b4_18_23", new visuperobject(4,18,23,"Nhưng ấy là người Lê-vi sẽ làm công việc của hội mạc; bằng có phạm tội, thì tự mang lấy; chúng nó sẽ không có phần cơ nghiệp giữa dân Y-sơ-ra-ên; ấy sẽ là một lệ định đời đời trải qua các thế đại."));
visuperobject.biblemap.put("b4_18_24", new visuperobject(4,18,24,"Vì ta đã ban cho người Lê-vi làm cơ nghiệp, những thuế một phần mười của dân Y-sơ-ra-ên dâng giơ lên cho Ðức Giê-hô-va. Bởi cớ đó, ta đã phán về họ rằng: Họ sẽ không có cơ nghiệp giữa dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_18_25", new visuperobject(4,18,25,"Ðoạn, Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_18_26", new visuperobject(4,18,26,"Ngươi cũng phải truyền cho người Lê-vi rằng: Khi nào các ngươi đã lãnh của dân Y-sơ-ra-ên thuế một phần mười mà ta đã ban về phần dân đó đặng làm cơ nghiệp của các ngươi, thì hãy lấy một phần mười của vật thuế một phần mười, làm của lễ dâng giơ lên cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_18_27", new visuperobject(4,18,27,"Lễ-vật dâng giơ lên của các ngươi sẽ kể như lúa mì lấy nơi sân đạp lúa, và như sự đầy dẫy của hầm rượu."));
visuperobject.biblemap.put("b4_18_28", new visuperobject(4,18,28,"Thế thì, trong những thuế một phần mười mà các ngươi lãnh nơi dân Y-sơ-ra-ên, thì cũng phải lấy ra một phần mười làm lễ vật dâng giơ lên cho Ðức Giê-hô-va, và giao lễ vật của Ðức Giê-hô-va đó cho A-rôn, thầy tế lễ."));
visuperobject.biblemap.put("b4_18_29", new visuperobject(4,18,29,"Trong những lễ vật lãnh được, các ngươi phải lấy ra phần tốt nhất, biệt riêng ra thánh mà dâng giơ lên cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_18_30", new visuperobject(4,18,30,"Ngươi phải nói cùng người Lê-vi rằng: Khi nào các ngươi đã dâng giơ lên phần tốt nhất của thuế một phần mười, thì sẽ kể cho người Lê-vi như huê lợi của sân đạp lúa, và như huê lợi của hầm rượu vậy."));
visuperobject.biblemap.put("b4_18_31", new visuperobject(4,18,31,"Các ngươi và gia quyến các ngươi phải ăn nó trong nơi nào cũng được, vì là công giá mình về việc các ngươi làm trong hội mạc."));
visuperobject.biblemap.put("b4_18_32", new visuperobject(4,18,32,"Bởi cớ ấy, khi các ngươi đã dâng giơ lên phần tốt nhất, các ngươi sẽ không mắc tội, sẽ không làm ô uế những vật thánh của dân Y-sơ-ra-ên, và các ngươi không chết đâu."));
visuperobject.biblemap.put("b4_19_1", new visuperobject(4,19,1,"Ðức Giê-hô-va lại phán cùng Môi-se và A-rôn rằng:"));
visuperobject.biblemap.put("b4_19_2", new visuperobject(4,19,2,"Nầy là lệ định của luật pháp mà Ðức Giê-hô-va có truyền rằng: Hãy nói cùng dân Y-sơ-ra-ên và biểu dẫn đến người một con bò cái tơ sắc hoe, không tật không vít, và chưa mang ách."));
visuperobject.biblemap.put("b4_19_3", new visuperobject(4,19,3,"Ðoạn phải giao nó cho Ê-lê-a-sa, thầy tế lễ, dẫn ra ngoài trại quân, rồi người ta giết nó trước mặt người."));
visuperobject.biblemap.put("b4_19_4", new visuperobject(4,19,4,"Ê-lê-a-sa, thầy tế lễ, sẽ dùng ngón tay nhúng huyết nó rảy bảy lần phía trước của hội mạc."));
visuperobject.biblemap.put("b4_19_5", new visuperobject(4,19,5,"Người ta sẽ thiêu con bò cái tơ đó trước mắt người, là thiêu da, thịt, và huyết với phẩn nó."));
visuperobject.biblemap.put("b4_19_6", new visuperobject(4,19,6,"Kế đó, thầy tế lễ sẽ lấy cây hương nam, chùm kinh giới, và màu đỏ sặm, liệng vào giữa đám lửa thiêu con bò cái tơ."));
visuperobject.biblemap.put("b4_19_7", new visuperobject(4,19,7,"Ðoạn, thầy tế lễ phải giặt áo xống và tắm mình trong nước, sau rồi vào trại quân, bị ô uế đến chiều tối."));
visuperobject.biblemap.put("b4_19_8", new visuperobject(4,19,8,"Kẻ nào thiêu con bò cái tơ phải giặt áo xống và tắm mình trong nước, bị ô uế đến chiều tối."));
visuperobject.biblemap.put("b4_19_9", new visuperobject(4,19,9,"Một người tinh sạch sẽ hốt tro con bò cái tơ đổ ngoài trại quân, trong một nơi tinh sạch; người ta phải giữ tro đó cho hội dân Y-sơ-ra-ên để dùng làm nước tẩy uế: ấy là một của lễ chuộc tội."));
visuperobject.biblemap.put("b4_19_10", new visuperobject(4,19,10,"Kẻ nào hốt tro con bò cái tơ phải giặt áo xống mình và bị ô uế đến chiều tối. Ðiều nầy sẽ làm một lệ định đời đời cho dân Y-sơ-ra-ên và cho khách ngoại bang kiều ngụ giữa dân đó."));
visuperobject.biblemap.put("b4_19_11", new visuperobject(4,19,11,"Ai đụng đến một xác chết của người nào sẽ bị ô uế trong bảy ngày."));
visuperobject.biblemap.put("b4_19_12", new visuperobject(4,19,12,"Ngày thứ ba và ngày thứ bảy, người ấy phải dùng nước nầy làm cho mình được sạch, thì sẽ được sạch; còn nếu ngày thứ ba và ngày thứ bảy không làm cho mình được sạch, thì người vẫn không tinh sạch."));
visuperobject.biblemap.put("b4_19_13", new visuperobject(4,19,13,"Phàm người nào đụng đến xác chết của người nào và không làm cho mình được sạch, thì sẽ gây cho đền tạm của Ðức Giê-hô-va bị ô uế. Người đó sẽ bị truất khỏi Y-sơ-ra-ên; vì nước tẩy uế không có rảy trên mình người, nên người vẫn ô uế; sự ô uế của người vẫn ở trên mình người vậy."));
visuperobject.biblemap.put("b4_19_14", new visuperobject(4,19,14,"Nầy là luật pháp khi có một người nào chết trong trại: bất kỳ ai vào trại và mọi vật chi ở trong đều sẽ bị ô uế trong bảy ngày."));
visuperobject.biblemap.put("b4_19_15", new visuperobject(4,19,15,"Phàm bình đựng nào để trống, không có nắp đậy buộc theo, sẽ bị ô uế."));
visuperobject.biblemap.put("b4_19_16", new visuperobject(4,19,16,"Ngoài đồng, ai đụng đến hoặc một người bị gươm giết, hoặc một xác chết, hoặc hài cốt loài người hay là một cái mả, thì sẽ bị ô uế trong bảy ngày."));
visuperobject.biblemap.put("b4_19_17", new visuperobject(4,19,17,"Về kẻ bị ô uế, người ta phải lấy tro của con sinh đã bị thiêu đặng chuộc tội, để trong một cái bình và đổ nước chảy lên trên."));
visuperobject.biblemap.put("b4_19_18", new visuperobject(4,19,18,"Ðoạn, một người tinh sạch sẽ lấy chùm kinh giới nhúng vào nước, rồi rảy trên trại, trên các bình, trên những người có tại đó, và trên người đã đụng, hoặc những hài cốt, hoặc một người bị giết, hoặc một xác chết hay là một cái mả."));
visuperobject.biblemap.put("b4_19_19", new visuperobject(4,19,19,"Ngày thứ ba và ngày thứ bảy, người tinh sạch phải rảy nước đó trên người bị ô uế, và ngày thứ bảy người tinh sạch sẽ làm cho người được sạch, Người đương được sạch phải giặt áo xống mình, tắm mình trong nước, và đến chiếu tối mới được tinh sạch."));
visuperobject.biblemap.put("b4_19_20", new visuperobject(4,19,20,"Còn người nào sẽ bị ô uế mà không làm cho mình được sạch, một người như vậy sẽ bị truất khỏi hội chúng; vì người đã làm cho nơi thánh của Ðức Giê-hô-va bị ô uế. Nước tẩy uế không có rưới trên mình người: người vẫn ô uế."));
visuperobject.biblemap.put("b4_19_21", new visuperobject(4,19,21,"Ấy sẽ là một lệ định đời đời cho dân sự. Người nào rảy nước tẩy uế sẽ giặt áo xống mình: kẻ nào đụng đến nước tẩy uế sẽ bị ô uế đến chiều tối."));
visuperobject.biblemap.put("b4_19_22", new visuperobject(4,19,22,"Phàm vật chi mà người ô uế đụng đến, đều sẽ bị lây ô uế; còn ai đụng đến người đó sẽ bị lây ô uế đến chiều tối."));
visuperobject.biblemap.put("b4_20_1", new visuperobject(4,20,1,"Tháng giêng, cả hội dân Y-sơ-ra-ên tới đồng vắng Xin; và dừng lại tại Ca-đe. Mi-ri-am qua đời và được chôn tại đó."));
visuperobject.biblemap.put("b4_20_2", new visuperobject(4,20,2,"Vả, không có nước cho hội chúng uống; chúng bèn dấy loạn cùng Môi-se và A-rôn."));
visuperobject.biblemap.put("b4_20_3", new visuperobject(4,20,3,"Dân sự cãi-lộn cùng Môi-se mà rằng: Anh em chúng tôi đã chết trước mặt Ðức Giê-hô-va, chớ chi chúng tôi cũng đồng chết luôn thể!"));
visuperobject.biblemap.put("b4_20_4", new visuperobject(4,20,4,"Sao người đem hội chúng của Ðức Giê-hô-va vào đồng vắng nầy, làm cho chúng tôi và súc vật của chúng tôi phải chết?"));
visuperobject.biblemap.put("b4_20_5", new visuperobject(4,20,5,"Sao người khiến chúng tôi đi lên khỏi xứ Ê-díp-tô đặng dẫn đến chỗ độc nầy, là nơi người ta không gieo mạ được, và chẳng có cây vả, cây nho, cây lựu, và cũng không có nước uống?"));
visuperobject.biblemap.put("b4_20_6", new visuperobject(4,20,6,"Môi-se và A-rôn bèn lui khỏi mặt hội chúng, đến tại cửa hội mạc, sấp mặt xuống đất; và sự vinh quang của Ðức Giê-hô-va cùng hai người."));
visuperobject.biblemap.put("b4_20_7", new visuperobject(4,20,7,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_20_8", new visuperobject(4,20,8,"Hãy cầm lấy cây gậy ngươi, rồi với A-rôn anh ngươi, hãy truyền nhóm hội chúng, và hai ngươi phải nói cùng hòn đá trước mặt hội chúng, thì hòn đá sẽ chảy nước ra; ngươi sẽ khiến nước từ hòn đá chảy ra cho hội chúng và súc vật của họ uống."));
visuperobject.biblemap.put("b4_20_9", new visuperobject(4,20,9,"Vậy, Môi-se cầm lấy cây gậy ở trước mặt Ðức Giê-hô-va, như Ngài đã phán dặn."));
visuperobject.biblemap.put("b4_20_10", new visuperobject(4,20,10,"Môi-se và A-rôn truyền nhóm hội chúng đến trước hòn đá mà nói rằng: Hỡi dân phản nghịch! hãy nghe, chúng ta há dễ khiến nước chảy từ hòn đá nầy ra cho các ngươi được sao?"));
visuperobject.biblemap.put("b4_20_11", new visuperobject(4,20,11,"Môi-se giơ tay lên, đập hòn đá hai lần bằng cây gậy mình. Nước bèn chảy tràn ra nhiều, hội chúng uống, và súc vật họ uống nữa."));
visuperobject.biblemap.put("b4_20_12", new visuperobject(4,20,12,"Ðoạn, Ðức Giê-hô-va phán cùng Môi-se và A-rôn rằng: Bởi vì hai ngươi không có tin đến ta, đặng tôn ta nên thánh trước mặt dân Y-sơ-ra-ên, vì cớ đó, hai ngươi sẽ không đem hội chúng nầy vào xứ mà ta đã cho nó đâu."));
visuperobject.biblemap.put("b4_20_13", new visuperobject(4,20,13,"Ấy đó là nước của Mê-ri-ba, tức là nơi dân Y-sơ-ra-ên cãi-trã cùng Ðức Giê-hô-va; Ngài tự nên thánh trước mặt dân đó."));
visuperobject.biblemap.put("b4_20_14", new visuperobject(4,20,14,"Ðoạn, từ Ca-đe, Môi-se sai sứ giả đi đến vua Ê-đôm đặng nói rằng: Y-sơ-ra-ên, là em vua, có nói như vầy: Vua biết các sự tai nạn đã xảy đến cho chúng tôi,"));
visuperobject.biblemap.put("b4_20_15", new visuperobject(4,20,15,"và tổ phụ chúng tôi đi xuống xứ Ê-díp-tô là làm sao? Chúng tôi đã ở lâu tại xứ Ê-díp-tô, nhưng dân xứ đó bạc đãi chúng tôi và tổ phụ chúng tôi."));
visuperobject.biblemap.put("b4_20_16", new visuperobject(4,20,16,"Chúng tôi có kêu la cùng Ðức Giê-hô-va; Ngài có nghe thấu tiếng kêu, bèn sai một thiên sứ dẫn chúng tôi ra khỏi xứ Ê-díp-tô. Nầy, chúng tôi hiện ở tại Ca-đe, là thành ở nơi đầu bờ cõi của vua."));
visuperobject.biblemap.put("b4_20_17", new visuperobject(4,20,17,"Xin cho phép chúng tôi đi ngang qua xứ của vua; chúng tôi sẽ chẳng đi ngang qua đồng ruộng, sẽ chẳng đi ngang vườn nho, và chúng tôi cũng sẽ chẳng uống nước các giếng; chúng tôi sẽ đi theo đường cái của vua, không xây qua bên hữu, cũng không xây qua bên tả cho đến chừng nào đã đi ngang khỏi bờ cõi của vua."));
visuperobject.biblemap.put("b4_20_18", new visuperobject(4,20,18,"Nhưng Ê-đôm đáp rằng: Ngươi chớ khá đi ngang ranh ta; bằng cượng, ta sẽ cầm gươm ra đón ngươi."));
visuperobject.biblemap.put("b4_20_19", new visuperobject(4,20,19,"Dân Y-sơ-ra-ên tâu rằng: Chúng tôi đi theo đường cái; nếu chúng tôi và bầy súc vật chúng tôi uống nước của vua, thì chúng tôi sẽ trả tiền. Xin chỉ để đi bộ ngang qua, không chi khác nữa."));
visuperobject.biblemap.put("b4_20_20", new visuperobject(4,20,20,"Nhưng Ê-đôm đáp rằng: Ngươi chẳng được qua đâu! Ê-đôm bèn đem quân chúng rất đông, tay cầm binh khí, ra đón Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_20_21", new visuperobject(4,20,21,"Ấy vậy, Ê-đôm không cho phép Y-sơ-ra-ên đi ngang bờ cõi mình; Y-sơ-ra-ên trở đi khỏi người."));
visuperobject.biblemap.put("b4_20_22", new visuperobject(4,20,22,"Cả hội dân Y-sơ-ra-ên đi từ Ca-đe đến núi Hô-rơ."));
visuperobject.biblemap.put("b4_20_23", new visuperobject(4,20,23,"Ðức Giê-hô-va phán cùng Môi-se và A-rôn trên núi Hô-rơ, tại bờ cõi xứ Ê-đôm, mà rằng:"));
visuperobject.biblemap.put("b4_20_24", new visuperobject(4,20,24,"A-rôn sẽ về nơi tổ phụ mình, vì người không được vào xứ mà ta đã ban cho dân Y-sơ-ra-ên đâu; bởi vì tại nước Mê-ri-ba, các ngươi đã bội nghịch điều răn ta."));
visuperobject.biblemap.put("b4_20_25", new visuperobject(4,20,25,"Hãy bắt A-rôn và Ê-lê-a-sa, con trai người, biểu đi lên núi Hô-rơ;"));
visuperobject.biblemap.put("b4_20_26", new visuperobject(4,20,26,"đoạn hãy lột áo xống A-rôn mặc cho Ê-lê-a-sa, con trai người. Ấy tại đó A-rôn sẽ được tiếp về và qua đời."));
visuperobject.biblemap.put("b4_20_27", new visuperobject(4,20,27,"Vậy, Môi-se làm y như Ðức Giê-hô-va đã phán dặn; ba người đi lên núi Hô-rơ, cả hội chúng đều thấy."));
visuperobject.biblemap.put("b4_20_28", new visuperobject(4,20,28,"Môi-se lột áo xống A-rôn, mặc cho Ê-lê-a-sa, con trai người. A-rôn chết tại đó, trên chót núi; rồi Môi-se và Ê-lê-a-sa xuống."));
visuperobject.biblemap.put("b4_20_29", new visuperobject(4,20,29,"Cả hội chúng thấy A-rôn đã tắt hơi rồi, bèn khóc người trong ba mươi ngày."));
visuperobject.biblemap.put("b4_21_1", new visuperobject(4,21,1,"Khi vua A-rát, người Ca-na-an, ở nơi Nam phương, hay rằng Y-sơ-ra-ên theo đường do thám đi đến, bèn hãm đánh Y-sơ-ra-ên, và bắt mấy người cầm tù."));
visuperobject.biblemap.put("b4_21_2", new visuperobject(4,21,2,"Y-sơ-ra-ên bèn hứa nguyện cùng Ðức Giê-hô-va rằng: Nếu Chúa phó dân nầy vào tay tôi, tất tôi sẽ diệt các thành của nó."));
visuperobject.biblemap.put("b4_21_3", new visuperobject(4,21,3,"Ðức Giê-hô-va nhậm lời dân Y-sơ-ra-ên và phó dân Ca-na-an cho. Người ta diệt hết dân đó và các thành của nó, đặt tên chỗ nầy là Họt-ma."));
visuperobject.biblemap.put("b4_21_4", new visuperobject(4,21,4,"Ðoạn, dân Y-sơ-ra-ên đi từ núi Hô-rơ về hướng Biển đỏ, đặng đi vòng theo xứ Ê-đôm; giữa đường dân sự ngã lòng."));
visuperobject.biblemap.put("b4_21_5", new visuperobject(4,21,5,"Vậy, dân sự nói nghịch cùng Ðức Chúa Trời và Môi-se mà rằng: Làm sao người khiến chúng tôi lên khỏi xứ Ê-díp-tô đặng chết trong đồng vắng? Vì đây không có bánh, cũng không có nước, và linh hồn chúng tôi đã ghê gớm thứ đồ ăn đạm bạc nầy."));
visuperobject.biblemap.put("b4_21_6", new visuperobject(4,21,6,"Ðức Giê-hô-va sai con rắn lửa đến trong dân sự, cắn dân đó đến đỗi người Y-sơ-ra-ên chết rất nhiều."));
visuperobject.biblemap.put("b4_21_7", new visuperobject(4,21,7,"Dân sự bèn đến cùng Môi-se mà nói rằng: Chúng tôi có phạm tội, vì đã nói nghịch Ðức Giê-hô-va và người. Hãy cầu xin Ðức Giê-hô-va để Ngài khiến rắn lìa xa chúng tôi. Môi-se cầu khẩn cho dân sự."));
visuperobject.biblemap.put("b4_21_8", new visuperobject(4,21,8,"Ðức Giê-hô-va phán cùng Môi-se rằng: Hãy làm lấy một con rắn lửa, rồi treo nó trên một cây sào. Nếu ai bị cắn và nhìn nó, thì sẽ được sống."));
visuperobject.biblemap.put("b4_21_9", new visuperobject(4,21,9,"Vậy, Môi-se làm một con rắn bằng đồng, rồi treo lên một cây sào; nếu người nào đã bị rắn cắn mà nhìn con rắn bằng đồng, thì được sống."));
visuperobject.biblemap.put("b4_21_10", new visuperobject(4,21,10,"Kế đó, dân Y-sơ-ra-ên đi và đóng trại tại Ô-bốt."));
visuperobject.biblemap.put("b4_21_11", new visuperobject(4,21,11,"Ðoạn, đi từ Ô-bốt và đóng trại tại Y-giê-a-ba-rim, tại đồng vắng, đối ngang Mô-áp về hướng mặt trời mọc."));
visuperobject.biblemap.put("b4_21_12", new visuperobject(4,21,12,"Họ đi từ đó đóng trại tại đèo Xê-rết."));
visuperobject.biblemap.put("b4_21_13", new visuperobject(4,21,13,"Rồi tự đó đi và đóng trại phía bên kia Aït-nôn, ở trong đồng vắng, vừa ra bờ cõi dân A-mô-rít; vì Aït-nôn là giáp-ranh của dân Mô-áp và dân A-mô-rít."));
visuperobject.biblemap.put("b4_21_14", new visuperobject(4,21,14,"Bởi cớ đó, trong sách chiến trận của Ðức Giê-hô-va có nói rằng: Va-hép ở tại Su-pha, và trũng Aït-nôn,"));
visuperobject.biblemap.put("b4_21_15", new visuperobject(4,21,15,"cùng những triền trũng chạy xuống phía A-rơ, và đụng ranh Mô-áp."));
visuperobject.biblemap.put("b4_21_16", new visuperobject(4,21,16,"Từ đó dân Y-sơ-ra-ên đến Bê-re, ấy đó là cái giếng mà Ðức Giê-hô-va đã phán cùng Môi-se rằng: Hãy nhóm hiệp dân sự, ta sẽ cho chúng nó nước."));
visuperobject.biblemap.put("b4_21_17", new visuperobject(4,21,17,"Y-sơ-ra-ên bèn hát bài nầy: Hỡi giếng, hãy tuôn lên! hãy ca hát cho nó!"));
visuperobject.biblemap.put("b4_21_18", new visuperobject(4,21,18,"Giếng mà các vua chúa đã khai, Các tước vị của dân sự đã đào Với phủ việt cùng cây gậy mình!"));
visuperobject.biblemap.put("b4_21_19", new visuperobject(4,21,19,"Kế đó, từ đồng vắng dân Y-sơ-ra-ên đến Ma-tha-na; rồi từ Ma-tha-na đến Na-ha-li-ên; từ Na-ha-li-ên đến Ba-mốt;"));
visuperobject.biblemap.put("b4_21_20", new visuperobject(4,21,20,"và từ Ba-mốt đến trũng ở trong bờ cõi Mô-áp, tại chót núi Phích-ga, là nơi người ta nhìn thấy đồng bằng của sa mạc."));
visuperobject.biblemap.put("b4_21_21", new visuperobject(4,21,21,"Vả, Y-sơ-ra-ên sai sứ giả đến Si-hôn, vua dân A-mô-rít, đặng nói rằng:"));
visuperobject.biblemap.put("b4_21_22", new visuperobject(4,21,22,"Xin cho phép chúng tôi đi ngang qua xứ vua, chúng tôi sẽ không xây vào đồng ruộng, hay là vào vườn nho, và chúng tôi cũng sẽ không uống nước các giếng, cứ đi đường cái của vua cho đến chừng nào qua khỏi bờ cõi vua."));
visuperobject.biblemap.put("b4_21_23", new visuperobject(4,21,23,"Nhưng Si-hôn không cho phép Y-sơ-ra-ên đi ngang qua bờ cõi mình, bèn hiệp hết dân sự mình đi ra đón Y-sơ-ra-ên về hướng đồng vắng; người đến Gia-hát, chiến trận cùng Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_21_24", new visuperobject(4,21,24,"Song Y-sơ-ra-ên dùng lưỡi gươm đánh bại người, và chiếm xứ người từ Aït-nôn đến Gia-bốc, đến dân Am-môn; vì bờ cõi dân Am-môn bền vững."));
visuperobject.biblemap.put("b4_21_25", new visuperobject(4,21,25,"Y-sơ-ra-ên chiếm hết các thành nầy, và ở trong các thành dân A-mô-rít, là ở tại Hết-bôn và trong các thành địa-hạt."));
visuperobject.biblemap.put("b4_21_26", new visuperobject(4,21,26,"Vì Hết-bôn là thành của Si-hôn, vua dân A-mô-rít, là vua đã giao chiến cùng vua trước của Mô-áp, chiếm lấy khắp hết xứ người đến Aït-nôn."));
visuperobject.biblemap.put("b4_21_27", new visuperobject(4,21,27,"Bởi cớ đó, những nhà văn sĩ nói rằng: Hãy đến Hết-bôn! Thành Si-hôn phải xây cất và lập vững!"));
visuperobject.biblemap.put("b4_21_28", new visuperobject(4,21,28,"Vì có một đám lửa bởi thành Hết-bôn, Một ngọn lửa bởi thành Si-hôn mà ra; Nó thiêu đốt A-rơ của Mô-áp, Các chúa của nơi cao Aït-nôn"));
visuperobject.biblemap.put("b4_21_29", new visuperobject(4,21,29,"Hỡi Mô-áp, khốn thay cho ngươi! Hỡi dân Kê-móc, ngươi đã diệt mất! Người đã nộp con trai mình làm kẻ trốn tránh, Và con gái mình làm phu tù cho Si-hôn, vua A-mô-rít."));
visuperobject.biblemap.put("b4_21_30", new visuperobject(4,21,30,"Chúng ta đã bắn tên nhằm chúng nó. Hết-bôn đã bị hủy diệt cho đến Ði-bôn. Chúng ta đã làm tàn hại đến Nô-phách, Lửa đã lan đến Mê-đê-ba."));
visuperobject.biblemap.put("b4_21_31", new visuperobject(4,21,31,"Vậy, Y-sơ-ra-ên ở trong xứ dân A-mô-rít."));
visuperobject.biblemap.put("b4_21_32", new visuperobject(4,21,32,"Môi-se sai đi do thám Gia-ê-xe, chiếm các thành bổn hạt và đuổi dân A-mô-rít ở tại đó."));
visuperobject.biblemap.put("b4_21_33", new visuperobject(4,21,33,"Ðoạn, dân Y-sơ-ra-ên trở lại đi lên về hướng Ba-san, Oùc, vua Ba-san, và cả dân sự người đi ra đón đặng chiến trận tại Ết-rê -i."));
visuperobject.biblemap.put("b4_21_34", new visuperobject(4,21,34,"Nhưng Ðức Giê-hô-va phán cùng Môi-se rằng: Ðừng sợ người, vì ta đã phó người, luôn dân sự và xứ người vào tay ngươi; ngươi khá làm cho người như đã làm cho Si-hôn, vua dân A-mô-rít ở tại Hết-bôn."));
visuperobject.biblemap.put("b4_21_35", new visuperobject(4,21,35,"Vậy, dân Y-sơ-ra-ên đánh bại Oùc, các con trai người và cả dân sự người, cho đến chừng không còn người nào ở lại; dân Y-sơ-ra-ên chiếm lấy xứ người vậy."));
visuperobject.biblemap.put("b4_22_1", new visuperobject(4,22,1,"Ðoạn dân Y-sơ-ra-ên đi, đến đóng trại trong đồng bắng Mô-áp, bên kia sông Giô-đanh, đối-diện Giê-ri-cô."));
visuperobject.biblemap.put("b4_22_2", new visuperobject(4,22,2,"Ba-lác, con trai Xếp-bô, thấy hết mọi điều Y-sơ-ra-ên đã làm cho dân A-mô-rít."));
visuperobject.biblemap.put("b4_22_3", new visuperobject(4,22,3,"Mô-áp lấy làm sợ sệt lắm và kinh khủng trước mặt dân Y-sơ-ra-ên, vì dân đó rất đông."));
visuperobject.biblemap.put("b4_22_4", new visuperobject(4,22,4,"Vậy, Mô-áp nói cùng các trưởng lão Ma-đi-an rằng: Bây giờ, đoàn dân đông nầy sẽ nuốt hết thảy chung quanh ta, khác nào con bò ăn cỏ trong đồng vậy. Ðương lúc đó, Ba-lác, con trai Xếp-bô, làm vua Mô-áp."));
visuperobject.biblemap.put("b4_22_5", new visuperobject(4,22,5,"Người sai sứ giả đến cùng Ba-la-am, con trai Bê-ô, ở Phê-thô-rơ, tại trên mé sông, trong xứ của con cái dân sự mình, đặng gọi người mà nói rằng: Nầy một dân đã ra khỏi xứ Ê-díp-tô, phủ khắp mặt đất, và đóng lại đối ngang ta."));
visuperobject.biblemap.put("b4_22_6", new visuperobject(4,22,6,"Vậy, ta xin ngươi hãy đến bây giờ, rủa sả dân nầy cho ta, vì nó mạnh hơn ta. Có lẽ ta sẽ đánh bại dân nầy và đuổi nó khỏi xứ được; vì ta biết rằng kẻ nào ngươi chúc phước cho, thì được phước; còn kẻ nào ngươi rủa sả, thì bị rủa sả."));
visuperobject.biblemap.put("b4_22_7", new visuperobject(4,22,7,"Vậy, các trưởng lão Mô-áp đi cùng các trưởng lão Ma-đi-an, trong tay có lễ vật cho thầy bói, đến cùng Ba-la-am mà thuật lại những lời của Ba-lác."));
visuperobject.biblemap.put("b4_22_8", new visuperobject(4,22,8,"Người đáp rằng: Hãy ở lại đây đêm nay thì ta sẽ trả lời theo như lời Ðức Giê-hô-va sẽ phán cùng ta. Các trưởng lão Mô-áp ở lại nhà Ba-la-am."));
visuperobject.biblemap.put("b4_22_9", new visuperobject(4,22,9,"Vả, Ðức Chúa Trời đến cùng Ba-la-am và phán rằng: Những người ngươi có trong nhà là ai?"));
visuperobject.biblemap.put("b4_22_10", new visuperobject(4,22,10,"Ba-la-am thưa cùng Ðức Chúa Trời rằng: Ba-lác, con trai Xếp-bô, vua Mô-áp, đã sai đến tôi đặng nói rằng:"));
visuperobject.biblemap.put("b4_22_11", new visuperobject(4,22,11,"Nầy, một dân đã ra khỏi xứ Ê-díp-tô, phủ khắp mặt đất; hãy đến bây giờ, rủa sả dân đó cho ta; có lẽ ta sẽ đánh bại dân nầy và đuổi nó được."));
visuperobject.biblemap.put("b4_22_12", new visuperobject(4,22,12,"Ðức Chúa Trời phán cùng Ba-la-am rằng: Ngươi chớ đi với chúng nó, chớ rủa sả dân nầy, vì dân nầy được ban phước."));
visuperobject.biblemap.put("b4_22_13", new visuperobject(4,22,13,"Sớm mai Ba-la-am dậy, nói cùng các sứ thần của Ba-lác rằng: Hãy trở về xứ các ngươi, vì Ðức Giê-hô-va không cho ta đi cùng các ngươi."));
visuperobject.biblemap.put("b4_22_14", new visuperobject(4,22,14,"Vậy, các sứ thần Mô-áp dậy, trở về Ba-lác mà tâu rằng: Ba-la-am từ chối đến cùng chúng tôi."));
visuperobject.biblemap.put("b4_22_15", new visuperobject(4,22,15,"Ba-lác lại sai nhiều sứ thần hơn và tôn trọng hơn những người trước,"));
visuperobject.biblemap.put("b4_22_16", new visuperobject(4,22,16,"đến cùng Ba-la-am mà rằng: Ba-lác, con trai Xếp-bô, có nói như vầy: Ta xin ngươi, chớ có chi ngăn cản ngươi đến cùng ta,"));
visuperobject.biblemap.put("b4_22_17", new visuperobject(4,22,17,"vì ta sẽ tôn ngươi rất vinh hiển, và làm theo mọi điều ngươi sẽ nói cùng ta. Ta xin ngươi hãy đến rủa sả dân nầy."));
visuperobject.biblemap.put("b4_22_18", new visuperobject(4,22,18,"Ba-la-am đáp cùng các sứ thần của Ba-lác rằng: Dầu Ba-lác sẽ cho ta nhà người đầy vàng và bạc, ta cũng chẳng được vượt qua mạng của Giê-hô-va, Ðức Chúa Trời của ta, đặng làm một việc hoặc nhỏ hay lớn."));
visuperobject.biblemap.put("b4_22_19", new visuperobject(4,22,19,"Song ta xin các ngươi cũng ở lại đêm nay, đặng ta biết Ðức Giê-hô-va sẽ phán điều chi cùng ta nữa."));
visuperobject.biblemap.put("b4_22_20", new visuperobject(4,22,20,"Trong lúc ban đêm, Ðức Chúa Trời giáng đến Ba-la-am mà phán rằng: Nếu các người ấy đến đặng gọi ngươi, hãy đứng dậy đi với chúng nó; nhưng chỉ hãy làm theo lời ta sẽ phán."));
visuperobject.biblemap.put("b4_22_21", new visuperobject(4,22,21,"Vậy, sớm mai, Ba-la-am dậy, thắng lừa cái mình và đi với các sứ thần Mô-áp."));
visuperobject.biblemap.put("b4_22_22", new visuperobject(4,22,22,"Nhưng cơn thạnh nộ của Ðức Chúa Trời nổi phừng lên, vì người ra đi; thiên sứ của Ðức Giê-hô-va đứng trên đường đặng cản người. Vả, người đã cỡi lừa cái mình, và có hai đứa đầy tớ đi theo."));
visuperobject.biblemap.put("b4_22_23", new visuperobject(4,22,23,"Lừa cái thấy thiên sứ của Ðức Giê-hô-va đứng trên đường có cây gươm trần nơi tay, bèn tẻ đường đi vào trong ruộng; Ba-la-am đánh nó đặng dẫn nó vào đường lại."));
visuperobject.biblemap.put("b4_22_24", new visuperobject(4,22,24,"Nhưng thiên sứ của Ðức Giê-hô-va đứng trong đường nhỏ của vườn nho có vách bên nầy và bên kia."));
visuperobject.biblemap.put("b4_22_25", new visuperobject(4,22,25,"Lừa thấy thiên sứ của Ðức Giê-hô-va thì nép vào vách và ép chân Ba-la-am; người đánh nó."));
visuperobject.biblemap.put("b4_22_26", new visuperobject(4,22,26,"Thiên sứ của Ðức Giê-hô-va đi tới xa hơn, đứng trong một ngả rất hẹp không chỗ nào trở qua bên hữu hay là bên tả;"));
visuperobject.biblemap.put("b4_22_27", new visuperobject(4,22,27,"lừa thấy thiên sứ của Ðức Giê-hô-va bèn nằm quị dưới Ba-la-am. Nhưng Ba-la-am nổi giận, đánh lừa bằng một cây gậy."));
visuperobject.biblemap.put("b4_22_28", new visuperobject(4,22,28,"Bấy giờ, Ðức Giê-hô-va mở miệng lừa ra, nó nói cùng Ba-la-am rằng: Tôi có làm chi cho người, mà người đã đánh tôi ba lần?"));
visuperobject.biblemap.put("b4_22_29", new visuperobject(4,22,29,"Ba-la-am đáp cùng lừa cái rằng: Ấy là tại mầy khinh nhạo ta. Chớ chi ta có cây gươm trong tay, ta đã giết mầy rồi!"));
visuperobject.biblemap.put("b4_22_30", new visuperobject(4,22,30,"Lừa nói cùng Ba-la-am rằng: Tôi há chẳng phải là lừa của người mà người đã cỡi luôn đến ngày nay sao? Tôi há có thói làm như vậy cùng người chăng? Người đáp: Không."));
visuperobject.biblemap.put("b4_22_31", new visuperobject(4,22,31,"Ðức Giê-hô-va bèn mở mắt Ba-la-am ra, thấy thiên sứ của Ðức Giê-hô-va đứng trong đường, tay cầm một cây gươm. Người cúi đầu và sấp mặt xuống đất."));
visuperobject.biblemap.put("b4_22_32", new visuperobject(4,22,32,"Thiên sứ của Ðức Giê-hô-va nói: Sao ngươi đã đánh lừa cái ngươi ba lần? Nầy, ta đi ra đặng cản ngươi, vì ta thấy ngươi đi theo một con đường dẫn mình đến nơi hư nát."));
visuperobject.biblemap.put("b4_22_33", new visuperobject(4,22,33,"Vả, con lừa cái có thấy ta, đi tẻ trước mặt ta ba lần, nếu nó không đi tẻ trước mặt ta, chắc ta đã giết ngươi rồi và để nó sống."));
visuperobject.biblemap.put("b4_22_34", new visuperobject(4,22,34,"Ba-la-am bèn thưa cùng thiên sứ của Ðức Giê-hô-va rằng: Tôi có phạm tội, vì chẳng biết người đã đứng trong đường đặng cản tôi, bây giờ, nếu người không bằng lòng tôi đi, tôi sẽ trở về."));
visuperobject.biblemap.put("b4_22_35", new visuperobject(4,22,35,"Thiên sứ của Ðức Giê-hô-va nói cùng Ba-la-am rằng: Hãy đi cùng các người đó, nhưng chỉ hãy nói điều chi ta sẽ phán dặn ngươi. Ba-la-am bèn đi cùng các sứ thần của Ba-lác."));
visuperobject.biblemap.put("b4_22_36", new visuperobject(4,22,36,"Khi Ba-lác hay rằng Ba-la-am đến, bèn đi ra tới thành Mô-áp ở gần khe Aït-nôn, tức là ở tận bờ cõi của xứ, mà nghinh tiếp người."));
visuperobject.biblemap.put("b4_22_37", new visuperobject(4,22,37,"Ba-lác nói cùng Ba-la-am rằng: Ta há chẳng sai sứ vời ngươi sao? Sao ngươi không đến ta? Ta há không tôn vinh hiển cho ngươi được sao?"));
visuperobject.biblemap.put("b4_22_38", new visuperobject(4,22,38,"Ba-la-am đáp cùng Ba-lác rằng: Nầy tôi đã đến cùng vua, bây giờ tôi há có thể nói ra lời gì chăng? Tôi sẽ nói điều chi Ðức Chúa Trời đặt trong miệng tôi vậy."));
visuperobject.biblemap.put("b4_22_39", new visuperobject(4,22,39,"Ba-la-am đi cùng Ba-lác, đến Ki-ri-át-Hút-sốt."));
visuperobject.biblemap.put("b4_22_40", new visuperobject(4,22,40,"Ba-lác giết những bò và chiên, sai dâng cho Ba-la-am và các sứ thần đã đi với người."));
visuperobject.biblemap.put("b4_22_41", new visuperobject(4,22,41,"Khi đến sáng mai, Ba-lác đem Ba-la-am lên Ba-mốt-Ba-anh, là nơi người thấy đầu cùng trại quân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_23_1", new visuperobject(4,23,1,"Ba-la-am nói cùng Ba-lác rằng: Hãy cất tại đây cho tôi bảy cái bàn thờ, cùng sắm sẵn cho tôi bảy con bò đực và bảy con chiên đực."));
visuperobject.biblemap.put("b4_23_2", new visuperobject(4,23,2,"Ba-lác làm như Ba-la-am đã nói; hai người dâng một con bò đực và một con chiên đực trên mỗi bàn thờ."));
visuperobject.biblemap.put("b4_23_3", new visuperobject(4,23,3,"Ðoạn, Ba-la-am nói cùng Ba-lác rằng: Vua hãy đứng gần của lễ thiêu của vua, tôi sẽ đi; có lẽ Ðức Giê-hô-va sẽ hiện ra cùng tôi chăng, tôi sẽ thuật lại cùng vua điều chi Ngài cho tôi thấy. Người đi lên một nơi cao trống trải."));
visuperobject.biblemap.put("b4_23_4", new visuperobject(4,23,4,"Ðức Chúa Trời hiện ra cùng Ba-la-am, và Ba-la-am thưa cùng Ngài rằng: Tôi có lập bảy cái bàn thờ, và trên mỗi bàn thờ tôi có dâng một con bò đực và một con chiên đực."));
visuperobject.biblemap.put("b4_23_5", new visuperobject(4,23,5,"Ðức Giê-hô-va để lời trong miệng Ba-la-am và phán rằng: Hãy trở về cùng Ba-lác và nói như vậy."));
visuperobject.biblemap.put("b4_23_6", new visuperobject(4,23,6,"Ba-la-am bèn trở về cùng Ba-lác; nầy, người và các sứ thần Mô-áp đứng gần của lễ thiêu vua."));
visuperobject.biblemap.put("b4_23_7", new visuperobject(4,23,7,"Ba-la-am bèn nói lời ca mình mà rằng: Ba-lác, vua Mô-áp, sai vời tôi từ A-ram, Từ những núi Ðông-phương, và nói: Hãy đến rủa sả Gia-cốp cho ta! Hãy đến, giận mắng Y-sơ-ra-ên!"));
visuperobject.biblemap.put("b4_23_8", new visuperobject(4,23,8,"Kẻ mà Ðức Chúa Trời không rủa sả, tôi sẽ rủa sả làm sao? Kẻ mà Ðức Chúa Trời không giận mắng, tôi sẽ giận mắng làm sao?"));
visuperobject.biblemap.put("b4_23_9", new visuperobject(4,23,9,"Vì từ đỉnh các núi, tôi thấy người, Từ đầu cao gò đống, tôi nhìn người: Kìa, là một dân ở riêng ra, Sẽ không nhập số các nước."));
visuperobject.biblemap.put("b4_23_10", new visuperobject(4,23,10,"Ai đếm được bụi cát của Gia-cốp, Ai tu bộ được phần tư của Y-sơ-ra-ên? Người công chánh thác thể nào, tôi nguyện thác thể ấy; Cuối cùng người nghĩa làm sao, tôi nguyện cuối cùng tôi làm vậy!"));
visuperobject.biblemap.put("b4_23_11", new visuperobject(4,23,11,"Bấy giờ, Ba-lác nói cùng Ba-la-am rằng: Ngươi đã làm chi với ta? Ta thỉnh ngươi để rủa sả những thù nghịch ta, kìa ngươi lại chúc phước cho!"));
visuperobject.biblemap.put("b4_23_12", new visuperobject(4,23,12,"Người đáp rằng: Tôi há không nên cẩn thận nói điều Giê-hô-va đặt vào miệng tôi sao?"));
visuperobject.biblemap.put("b4_23_13", new visuperobject(4,23,13,"Ba-lác bèn nói: Vậy, hãy đến cùng ta trong một chỗ khác, là nơi ngươi sẽ thấy dân nầy, vì ngươi chỉ đã thấy ở đầu cùng nó, chớ không thấy hết; ở đó hãy rủa sả nó cho ta."));
visuperobject.biblemap.put("b4_23_14", new visuperobject(4,23,14,"Vậy, người dẫn Ba-la-am đến đồng Xô-phim, trên chót núi Phích-ga, lập bảy cái bàn thờ, và trên mỗi cái dâng một con bò đực và một con chiên đực."));
visuperobject.biblemap.put("b4_23_15", new visuperobject(4,23,15,"Ba-la-am bèn nói cùng Ba-lác rằng: Hãy đứng đây gần của lễ thiêu của vua, còn tôi sẽ đi lại đó, đón Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_23_16", new visuperobject(4,23,16,"Ðức Giê-hô-va hiện ra cùng Ba-la-am, mà rằng: Hãy trở về cùng Ba-lác và nói như vậy."));
visuperobject.biblemap.put("b4_23_17", new visuperobject(4,23,17,"Ba-la-am trở về cùng Ba-lác; người và các sứ thần Mô-áp đứng gần của lễ thiêu của vua. Ba-lác hỏi: Ðức Giê-hô-va có phán chi?"));
visuperobject.biblemap.put("b4_23_18", new visuperobject(4,23,18,"Ba-la-am bèn nói lời ca mình mà rằng: Hỡi Ba-lác, hãy đứng dậy và nghe! Hỡi con trai Xếp-bô, hãy lắng tai!"));
visuperobject.biblemap.put("b4_23_19", new visuperobject(4,23,19,"Ðức Chúa Trời chẳng phải là người để nói dối, Cũng chẳng phải là con loài người đặng hối cải. Ðiều Ngài đã nói, Ngài há sẽ chẳng làm ư? Ðiều Ngài đã phán, Ngài há sẽ chẳng làm ứng nghiệm sao?"));
visuperobject.biblemap.put("b4_23_20", new visuperobject(4,23,20,"Nầy, tôi đã lãnh mạng chúc phước; Ngài đã ban phước, tôi chẳng cải đâu."));
visuperobject.biblemap.put("b4_23_21", new visuperobject(4,23,21,"Ngài chẳng xem tội ác nơi Gia-cốp, Và không thấy tà vạy trong Y-sơ-ra-@ªn; Giê-hô-va, Ðức Chúa Trời người, ở cùng người, Trong Y-sơ-ra-ên có tiếng reo mừng của vua."));
visuperobject.biblemap.put("b4_23_22", new visuperobject(4,23,22,"Ấy là Ðức Chúa Trời đã rút dân đó ra khỏi xứ Ê-díp-tô; Chúng có sức mạnh như bò rừng vậy"));
visuperobject.biblemap.put("b4_23_23", new visuperobject(4,23,23,"Không có phù chú nơi Gia-cốp, Cũng chẳng có bói-khoa trong Y-sơ-ra-ên, Vì đến kỳ định, sẽ nói về Gia-cốp và Y-sơ-ra-ên rằng: Việc Ðức Chúa Trời làm lạ là dường nào!"));
visuperobject.biblemap.put("b4_23_24", new visuperobject(4,23,24,"Kìa, một thứ dân hưng lên như sư tử cái, Và dược lên khác nào sư tử đực; Chỉ khi nào đã xé được mồi mới nằm xuống, Và uống huyết những kẻ bị thương."));
visuperobject.biblemap.put("b4_23_25", new visuperobject(4,23,25,"Bấy giờ, Ba-lác nói cùng Ba-la-am rằng: Chớ rủa sả họ, nhưng cũng đừng chúc phước cho nữa."));
visuperobject.biblemap.put("b4_23_26", new visuperobject(4,23,26,"Ba-la-am đáp cùng Ba-lác rằng: Tôi há chẳng có nói: Tôi phải làm mọi điều Ðức Giê-hô-va sẽ phán dặn sao?"));
visuperobject.biblemap.put("b4_23_27", new visuperobject(4,23,27,"Ba-lác lại nói cùng Ba-la-am rằng: Vậy, hãy lại, ta dẫn ngươi đến một chỗ khác; có lẽ Ðức Chúa Trời sẽ ưng cho ngươi rủa sả dân nầy tại đó chăng."));
visuperobject.biblemap.put("b4_23_28", new visuperobject(4,23,28,"Ba-lác bèn dẫn Ba-la-am đến chót núi Phê-ô, đối ngang đồng vắng."));
visuperobject.biblemap.put("b4_23_29", new visuperobject(4,23,29,"Ba-la-am nói cùng Ba-lác rằng: Tại đây, hãy lập cho tôi bảy cái bàn thờ, và sắm sẵn bảy con bò đực và bảy con chiên đực."));
visuperobject.biblemap.put("b4_23_30", new visuperobject(4,23,30,"Vậy, Ba-lác làm theo như Ba-la-am đã nói; trên mỗi bàn thờ, người dâng một con bò đực và một con chiên đực."));
visuperobject.biblemap.put("b4_24_1", new visuperobject(4,24,1,"Ba-la-am thấy rõ Ðức Giê-hô-va ưng ban phước cho Y-sơ-ra-ên, thì không cậy đến phù chú như những lần khác; nhưng người xây mặt về hướng đồng vắng,"));
visuperobject.biblemap.put("b4_24_2", new visuperobject(4,24,2,"nhướng mắt lên thấy Y-sơ-ra-ên đóng trại từng chi phái, và Thần Ðức Chúa Trời cảm động người,"));
visuperobject.biblemap.put("b4_24_3", new visuperobject(4,24,3,"bèn nói lời ca mình mà rằng: Lời ca của Ba-la-am, con trai Bê-ô, Lời ca của người có mắt mở ra,"));
visuperobject.biblemap.put("b4_24_4", new visuperobject(4,24,4,"Lời ca của người nghe lời Ðức Chúa Trời, Xem sự hiện thấy của Ðấng Toàn năng, Sấp mình xuống và mắt mở ra:"));
visuperobject.biblemap.put("b4_24_5", new visuperobject(4,24,5,"Hỡi Gia-cốp! trại ngươi tốt dường bao! Hỡi Y-sơ-ra-ên! nhà tạm ngươi đẹp biết mấy!"));
visuperobject.biblemap.put("b4_24_6", new visuperobject(4,24,6,"Nó trương ra như trũng núi, Khác nào cảnh vườn ở nơi mé sông, Tợ cây trầm hương mà Ðức Giê-hô-va đã trồng, Tỉ như cây hương nam ở nơi mé nước."));
visuperobject.biblemap.put("b4_24_7", new visuperobject(4,24,7,"Nước chảy tràn ngoài thùng chứa, Hột giống của người nhuần tưới dư dật, Vua người sẽ trổi cao hơn A-gát, Nước người được cao lên."));
visuperobject.biblemap.put("b4_24_8", new visuperobject(4,24,8,"Ðức Chúa Trời đã dẫn người ra khỏi xứ Ê-díp-tô, Người có sức mạnh như bò rừng, Sẽ nuốt các nước, tức kẻ thù nghịch mình, Bẻ gãy xương chúng nó, đánh chúng nó bằng mũi tên mình."));
visuperobject.biblemap.put("b4_24_9", new visuperobject(4,24,9,"Người sụm xuống, nằm như sư tử đực, khác nào sư tử cái: Ai dễ khiến ngồi lên? Phước cho kẻ nào chúc phước ngươi, Rủa sả kẻ nào rủa sả ngươi."));
visuperobject.biblemap.put("b4_24_10", new visuperobject(4,24,10,"Bấy giờ, Ba-lác nổi giận cùng Ba-la-am, vỗ tay mà nói cùng Ba-la-am rằng: Ta đã thỉnh ngươi đặng rủa sả kẻ thù nghịch ta; nầy, ngươi lại chúc phước cho chúng nó đã ba lần rồi!"));
visuperobject.biblemap.put("b4_24_11", new visuperobject(4,24,11,"Vậy, bây giờ, khá chạy trở về xứ ngươi! Ta đã nói ta sẽ tôn ngươi vinh hiển, nhưng nầy, Ðức Giê-hô-va đã cản ngươi nhận lãnh."));
visuperobject.biblemap.put("b4_24_12", new visuperobject(4,24,12,"Ba-la-am đáp cùng Ba-lác rằng: Tôi há chẳng có nói cùng các sứ giả vua đã sai đến tôi rằng:"));
visuperobject.biblemap.put("b4_24_13", new visuperobject(4,24,13,"Dầu Ba-lác cho tôi nhà người đầy bạc và vàng, tôi cũng chẳng được vượt qua mạng Ðức Giê-hô-va để làm tự ý điều phải hay là điều quấy; tôi phải nói điều chi Ðức Giê-hô-va sẽ phán!"));
visuperobject.biblemap.put("b4_24_14", new visuperobject(4,24,14,"Nầy, bây giờ, tôi trở cùng dân tôi, hãy lại, tôi sẽ cáo cho vua biết điều dân nầy ngày sau cùng sẽ làm cho dân sự vua."));
visuperobject.biblemap.put("b4_24_15", new visuperobject(4,24,15,"Người bèn nói lời ca mình mà rằng: Lời ca của Ba-la-am, con trai Bê-ô, Lời ca của người có mắt mở ra;"));
visuperobject.biblemap.put("b4_24_16", new visuperobject(4,24,16,"Lời ca của người nghe lời Ðức Chúa Trời, Biết sự tri thức của Ðấng Chí cao, Xem sự hiện thấy của Ðấng Toàn năng, Sấp mình xuống mà mắt tự mở ra:"));
visuperobject.biblemap.put("b4_24_17", new visuperobject(4,24,17,"Tôi thấy Người, nhưng chẳng phải bây giờ; Tôi xem Người, nhưng chẳng phải ở gần; Một ngôi sao hiện ra từ Gia-cốp, Một cây phủ việt trồi lên từ Y-sơ-ra-ên; Người sẽ đâm lủng Mô-áp từ đầu nầy tới đầu kia, Hủy diệt dân hay dấy giặc nầy."));
visuperobject.biblemap.put("b4_24_18", new visuperobject(4,24,18,"Người sẽ được Ê-đôm làm cơ nghiệp; Sẽ được Sê -i-rơ, là kẻ thù nghịch mình, làm sản nghiệp. Y-sơ-ra-ên sẽ tỏ ra sự cường thạnh mình."));
visuperobject.biblemap.put("b4_24_19", new visuperobject(4,24,19,"Ðấng ra từ Gia-cốp sẽ cầm quyền, Người sẽ diệt những dân sót của thành."));
visuperobject.biblemap.put("b4_24_20", new visuperobject(4,24,20,"Ba-la-am cũng thấy A-ma-léc, bèn nói lời ca mình rằng: A-ma-léc đứng đầu các nước; Nhưng sau cùng người sẽ bị diệt vong."));
visuperobject.biblemap.put("b4_24_21", new visuperobject(4,24,21,"Kế đó, Ba-la-am thấy người Kê-nít, bèn nói lời ca mình rằng: Chỗ ở ngươi là bền vững, Ổ ngươi đóng trong hòn đá."));
visuperobject.biblemap.put("b4_24_22", new visuperobject(4,24,22,"Nhưng Ca-in sẽ bị hư nát, Cho đến khi A-su-rơ bắt dẫn tù ngươi."));
visuperobject.biblemap.put("b4_24_23", new visuperobject(4,24,23,"Người còn nói lời ca mình rằng: Ôi! khi Ðức Chúa Trời đã làm các điều nầy, ai sẽ còn sống?"));
visuperobject.biblemap.put("b4_24_24", new visuperobject(4,24,24,"Nhưng sẽ có những tàu từ gành Kít-tim đến, Hà khắc A-su, hà khắc Hê-be. Rồi chính người cũng sẽ bị tuyệt diệt."));
visuperobject.biblemap.put("b4_24_25", new visuperobject(4,24,25,"Ðoạn, Ba-la-am đứng dậy đi về bổn xứ. Ba-lác cũng lên đường."));
visuperobject.biblemap.put("b4_25_1", new visuperobject(4,25,1,"Dân Y-sơ-ra-ên ở tại Si-tim, khởi thông dâm cùng những con gái Mô-áp."));
visuperobject.biblemap.put("b4_25_2", new visuperobject(4,25,2,"Con gái mời dân sự ăn sinh lễ cúng các thần mình; dân sự ăn và quì lạy trước các thần chúng nó."));
visuperobject.biblemap.put("b4_25_3", new visuperobject(4,25,3,"Y-sơ-ra-ên cũng thờ thần Ba-anh-Phê-ô, cơn giận của Ðức Giê-hô-va bèn nổi lên cùng Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_25_4", new visuperobject(4,25,4,"Ðức Giê-hô-va phán cùng Môi-se rằng: Hãy bắt các đầu trưởng của dân sự, và treo lên trước mặt Ðức Giê-hô-va, đối cùng mặt trời, để cơn giận của Ðức Giê-hô-va xây khỏi Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_25_5", new visuperobject(4,25,5,"Vậy, Môi-se nói cùng các quan án Y-sơ-ra-ên rằng: Mỗi người trong các ngươi phải giết những kẻ nào thuộc về bọn cúng thờ Ba-anh-Phê-ô."));
visuperobject.biblemap.put("b4_25_6", new visuperobject(4,25,6,"Nầy, một người trong dân Y-sơ-ra-ên dẫn một người nữ Ma-đi-an đến giữa anh em mình, hiện trước mắt Môi-se và cả hội dân Y-sơ-ra-ên, đương khi hội chúng khóc tại cửa hội mạc."));
visuperobject.biblemap.put("b4_25_7", new visuperobject(4,25,7,"Phi-nê-a, con trai Ê-lê-a-sa, cháu A-rôn, thầy tế lễ, thấy sự nầy, bèn đứng dậy giữa hội chúng, cầm một cây giáo,"));
visuperobject.biblemap.put("b4_25_8", new visuperobject(4,25,8,"đi theo người Y-sơ-ra-ên vào trong trại, đâm ngang dạ dưới của người Y-sơ-ra-ên và người nữ; tai vạ giữa dân Y-sơ-ra-ên bèn ngừng lại."));
visuperobject.biblemap.put("b4_25_9", new visuperobject(4,25,9,"Vả, có hai mươi bốn ngàn người chết về tai vạ nầy."));
visuperobject.biblemap.put("b4_25_10", new visuperobject(4,25,10,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_25_11", new visuperobject(4,25,11,"Phi-nê-a, con trai Ê-lê-a-sa, cháu A-rôn, thầy tế lễ, đã xây dân Y-sơ-ra-ên khỏi cơn giận ta, vì lòng kỵ tà thúc giục lòng người; vậy, ta không có diệt dân Y-sơ-ra-ên trong cơn kỵ tà của ta."));
visuperobject.biblemap.put("b4_25_12", new visuperobject(4,25,12,"Bởi cớ đó, hãy cáo cùng người rằng: Ta ưng cho người sự giao ước bình yên ta;"));
visuperobject.biblemap.put("b4_25_13", new visuperobject(4,25,13,"ấy về phần người và dòng dõi người sẽ là sự giao ước về một chức tế lễ đời đời, bởi vì người có lòng sốt sắng về Ðức Chúa Trời mình, và có chuộc tội cho dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_25_14", new visuperobject(4,25,14,"Vả, người Y-sơ-ra-ên bị giết chung với người nữ Ma-đi-an, tên là Xim-ri, con trai Sa-lu, quan trưởng của một tông tộc, người Si-mê-ôn."));
visuperobject.biblemap.put("b4_25_15", new visuperobject(4,25,15,"Tên người nữ Ma-đi-an bị giết là Cốt-bi, con gái Xu-rơ, đầu trưởng của một tông tộc Ma-đi-an."));
visuperobject.biblemap.put("b4_25_16", new visuperobject(4,25,16,"Ðức Giê-hô-va lại phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_25_17", new visuperobject(4,25,17,"Hãy khuấy rối người Ma-đi-an và hãm đánh chúng nó;"));
visuperobject.biblemap.put("b4_25_18", new visuperobject(4,25,18,"vì chúng nó đã lấy mưu chước mình mà khuấy rối các ngươi, dỗ dành các ngươi về việc cúng thờ Phê-ô, và trong việc của Cốt-bi, con gái quan trưởng Ma-đi-an, là chị chúng nó đã bị giết trong ngày tai vạ, vì cớ việc cúng thờ Phê-ô."));
visuperobject.biblemap.put("b4_26_1", new visuperobject(4,26,1,"Xảy sau tai vạ nầy, Ðức Giê-hô-va phán cùng Môi-se và Ê-lê-a-sa, con trai A-rôn, thầy tế lễ, mà rằng:"));
visuperobject.biblemap.put("b4_26_2", new visuperobject(4,26,2,"Hãy dựng sổ cả hội chúng Y-sơ-ra-ên, từ hai mươi tuổi sắp lên, tùy theo tông tộc của họ, tức là hết thảy người trong Y-sơ-ra-ên đi ra trận được."));
visuperobject.biblemap.put("b4_26_3", new visuperobject(4,26,3,"Vậy, Môi-se và Ê-lê-a-sa, thầy tế lễ, nói cùng dân Y-sơ-ra-ên trong đồng Mô-áp gần sông Giô-đanh, đối ngang Giê-ri-cô, mà rằng:"));
visuperobject.biblemap.put("b4_26_4", new visuperobject(4,26,4,"Hãy tu bộ dân sự từ hai mươi tuổi sắp lên, như Ðức Giê-hô-va đã phán dặn Môi-se và dân Y-sơ-ra-ên, mà đã ra khỏi xứ Ê-díp-tô."));
visuperobject.biblemap.put("b4_26_5", new visuperobject(4,26,5,"Ru-bên, con trưởng nam của Y-sơ-ra-ên. Con trai Ru-bên là Hê-nóc; do nơi người sanh ra họ Hê-nóc; do nơi Pha-lu sanh họ Pha-lu;"));
visuperobject.biblemap.put("b4_26_6", new visuperobject(4,26,6,"do nơi Hết-rôn sanh họ Hết-rôn; do nơi Cạt-mi sanh ra họ Cạt-mi."));
visuperobject.biblemap.put("b4_26_7", new visuperobject(4,26,7,"Ðó là các họ của Ru-bên, và những tên người mà người ta tu bộ, cộng được bốn mươi ba ngàn bảy trăm ba mươi."));
visuperobject.biblemap.put("b4_26_8", new visuperobject(4,26,8,"Con trai Pha-lu là Ê-li-áp."));
visuperobject.biblemap.put("b4_26_9", new visuperobject(4,26,9,"Các con trai của Ê-li-áp là Nê-mu-ên, Ða-than và A-bi-ram. Ấy là Ða-than và A-bi-ram nầy, trưởng của hội chúng dấy loạn cùng Môi-se và A-rôn, tại phe đảng Cô-rê, khi họ dấy loạn cùng Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_26_10", new visuperobject(4,26,10,"Ấy, khi đất hả miệng ra nuốt hai người và Cô-rê cùng những kẻ đã hiệp đảng chết luôn, lúc lửa thiêu nuốt hai trăm rưởi người; họ làm gương như vậy."));
visuperobject.biblemap.put("b4_26_11", new visuperobject(4,26,11,"Nhưng các con trai Cô-rê không chết."));
visuperobject.biblemap.put("b4_26_12", new visuperobject(4,26,12,"Các con trai Si-mê-ôn, tùy theo họ hàng mình: do nơi Nê-mu-ên sanh ra họ Nê-mu-ên; do nơi Gia-min sanh ra họ Gia-min; do nơi Gia-kin sanh ra họ Gia-kin;"));
visuperobject.biblemap.put("b4_26_13", new visuperobject(4,26,13,"do nơi Xê-rách sanh ra họ Xê-rách; do nơi Sau-lơ sanh ra họ Sau-lơ."));
visuperobject.biblemap.put("b4_26_14", new visuperobject(4,26,14,"Ðó là các họ của Si-mê-ôn: số là hai mươi hai ngàn hai trăm người."));
visuperobject.biblemap.put("b4_26_15", new visuperobject(4,26,15,"Các con trai của Gát, tùy theo họ hàng mình: do nơi Xê-phôn sanh ra họ Xê-phôn; do nơi Ha-ghi sanh ra họ Ha-ghi; do nơi Su-ni sanh ra họ Su-ni;"));
visuperobject.biblemap.put("b4_26_16", new visuperobject(4,26,16,"do nơi Oùc-ni sanh ra họ Oùc-ni; do nơi Ê-ri sanh ra họ Ê-ri;"));
visuperobject.biblemap.put("b4_26_17", new visuperobject(4,26,17,"do nơi A-rốt sanh ra họ A-rốt; do nơi A-rê-li sanh ra họ A-rê-li."));
visuperobject.biblemap.put("b4_26_18", new visuperobject(4,26,18,"Ðó là các họ của những con trai Gát, tùy theo tu bộ: số là bốn mươi ngàn năm trăm người."));
visuperobject.biblemap.put("b4_26_19", new visuperobject(4,26,19,"Các con trai Giu-đa: Ê-rơ và Ô-nan; nhưng Ê-rơ và Ô-nan đã qua đời tại xứ Ca-na-an."));
visuperobject.biblemap.put("b4_26_20", new visuperobject(4,26,20,"Các con trai Giu-đa, tùy theo họ hàng mình: do nơi Sê-la sanh ra họ Sê-la; do nơi Phê-rết sanh ra họ Phê-rết; do nơi Xê-rách sanh ra họ Xê-rách."));
visuperobject.biblemap.put("b4_26_21", new visuperobject(4,26,21,"Các con trai của Pha-rết: do nơi Hết-rôn sanh ra họ Hết-rôn; do nơi Ha-mun sanh ra họ Ha-mun."));
visuperobject.biblemap.put("b4_26_22", new visuperobject(4,26,22,"Ðó là các họ của Giu-đa, tùy theo tu bộ: số là bảy mươi sáu ngàn năm trăm người."));
visuperobject.biblemap.put("b4_26_23", new visuperobject(4,26,23,"Các con trai Y-sa-ca, tùy theo họ hàng mình: do nơi Thô-la sanh ra họ Thô-la; do nơi Phu-va sanh ra họ Phu-va;"));
visuperobject.biblemap.put("b4_26_24", new visuperobject(4,26,24,"do nơi Gia-súp sanh ra họ Gia-súp; do nơi Sim-rôn sanh ra họ Sim-rôn."));
visuperobject.biblemap.put("b4_26_25", new visuperobject(4,26,25,"Ðó là các họ Y-sa-ca, tùy theo tu bộ: số là sáu mươi bốn ngàn ba trăm người."));
visuperobject.biblemap.put("b4_26_26", new visuperobject(4,26,26,"Các con trai Sa-bu-lôn, tùy theo họ hàng mình: do nơi Sê-rết sanh ra họ Sê-rết; do nơi Ê-lôn sanh ra họ Ê-lôn; do nơi Gia-lê-ên sanh ra họ Gia-lê-ên."));
visuperobject.biblemap.put("b4_26_27", new visuperobject(4,26,27,"Ðó là các họ của Sa-bu-lôn tùy theo tu bộ: số là sáu mươi ngàn năm trăm người."));
visuperobject.biblemap.put("b4_26_28", new visuperobject(4,26,28,"Các con trai Giô-sép, tùy theo họ hàng mình, là Ma-na-se và Ép-ra-im."));
visuperobject.biblemap.put("b4_26_29", new visuperobject(4,26,29,"Các con trai Ma-na-se: do nơi Ma-ki sanh ra họ Ma-ki, Ma-ki sanh Ga-la-át: do nơi Ga-la-át sanh ra họ Ga-la-át."));
visuperobject.biblemap.put("b4_26_30", new visuperobject(4,26,30,"Nầy là các con trai Ga-la-át: do nơi Giê-xe sanh ra họ Giê-xe; do nơi Hê-léc sanh ra họ Hê-léc;"));
visuperobject.biblemap.put("b4_26_31", new visuperobject(4,26,31,"do nơi Ách-ri-ên sanh ra họ Ách-ri-ên; do nơi Si-chem sanh ra họ Si-chem;"));
visuperobject.biblemap.put("b4_26_32", new visuperobject(4,26,32,"do nơi Sê-mi-đa sanh ra họ Sê-mi-đa; do nơi Hê-phe sanh ra họ Hê-phe."));
visuperobject.biblemap.put("b4_26_33", new visuperobject(4,26,33,"Vả, Xê-lô-phát, con trai Hê-phe, không có con trai, nhưng có con gái. Tên các con gái Xê-lô-phát là Mách-la, Nô-a, Hốt-la, Minh-ca và Thiệt-sa."));
visuperobject.biblemap.put("b4_26_34", new visuperobject(4,26,34,"Ðó là các họ Ma-na-se, tùy theo tu bộ: số là năm mươi hai ngàn bảy trăm người."));
visuperobject.biblemap.put("b4_26_35", new visuperobject(4,26,35,"Nầy là các con trai Ép-ra-im, tùy theo họ hàng mình: do nơi Su-thê-lách sanh ra họ Su-thê-rách; do nơi Bê-ke sanh ra họ Bê-ke; do nơi Tha-chan sanh ra họ Tha-chan."));
visuperobject.biblemap.put("b4_26_36", new visuperobject(4,26,36,"Nầy là con trai của Su-thê-lách: do nơi Ê-ran sanh ra họ Ê-ran."));
visuperobject.biblemap.put("b4_26_37", new visuperobject(4,26,37,"Ðó là họ của các con trai Ép-ra-im, tùy theo tu bộ: số là ba mươi hai ngàn năm trăm người. Ấy là các con trai Giô-sép, tùy theo họ hàng mình vậy."));
visuperobject.biblemap.put("b4_26_38", new visuperobject(4,26,38,"Các con trai Bên gia-min, tùy theo họ hàng mình: do nơi Bê-la sanh ra họ Bê-la; do nơi Ách-bên sanh ra họ Ách-bên, do nơi A-chi-ram sanh ra họ A-chi-ram;"));
visuperobject.biblemap.put("b4_26_39", new visuperobject(4,26,39,"do nơi Sê-phu-pham sanh ra họ Sê-phu-pham; do nơi Hu-pham sanh ra họ Hu-pham."));
visuperobject.biblemap.put("b4_26_40", new visuperobject(4,26,40,"Các con trai Bê-la là A-rết và Na-a-man; do nơi A-rết sanh ra họ A-rết; do nơi Na-a-man sanh ra họ Na-a-man."));
visuperobject.biblemap.put("b4_26_41", new visuperobject(4,26,41,"Ðó là các con trai Bên-gia-min, tùy theo họ hàng và tùy theo tu bộ: số là bốn mươi lăm ngàn sáu trăm người."));
visuperobject.biblemap.put("b4_26_42", new visuperobject(4,26,42,"Nầy là các con trai của Ðan, tùy theo họ hàng mình: do nơi Su-cham sanh ra họ Su-cham. Ðó là họ của Ðan;"));
visuperobject.biblemap.put("b4_26_43", new visuperobject(4,26,43,"họ Su-cham, tùy theo tu bộ: số là sáu mươi bốn ngàn bốn trăm người."));
visuperobject.biblemap.put("b4_26_44", new visuperobject(4,26,44,"Các con trai A-se, tùy theo họ hàng mình: do nơi Di-ma sanh ra họ Di-ma; do nơi Dích-vi sanh ra họ Dích-vi; do nơi Bê-ri-a sanh ra họ Bê-ri-a."));
visuperobject.biblemap.put("b4_26_45", new visuperobject(4,26,45,"Các con trai Bê-ri-a: do nơi Hê-be sanh ra họ Hê-be; do nơi Manh-ki-ên sanh ra họ Manh-ki-ên."));
visuperobject.biblemap.put("b4_26_46", new visuperobject(4,26,46,"Tên của con gái A-se là Sê-rách."));
visuperobject.biblemap.put("b4_26_47", new visuperobject(4,26,47,"Ðó là những họ của các con trai A-se, tùy theo tu bộ: số là năm mươi ba ngàn bốn trăm người."));
visuperobject.biblemap.put("b4_26_48", new visuperobject(4,26,48,"Các con trai Nép-ta-li, tùy theo họ hàng mình: do nơi Giát-sê-ên sanh ra họ Giát-sê-ên; do nơi Gu-ni sanh ra họ Gu-ni;"));
visuperobject.biblemap.put("b4_26_49", new visuperobject(4,26,49,"do nơi Dít-se sanh ra họ Dít-se: do nơi Si-lem sanh ra họ Si-lem."));
visuperobject.biblemap.put("b4_26_50", new visuperobject(4,26,50,"Ðó là các họ của Nép-ta-li, tùy theo họ hàng và tu bộ mình: số là bốn mươi lăm ngàn bốn trăm người."));
visuperobject.biblemap.put("b4_26_51", new visuperobject(4,26,51,"Ðó là những người trong dân Y-sơ-ra-ên mà người ta tu bộ lại: số là sáu trăm một ngàn bảy trăm ba mươi người."));
visuperobject.biblemap.put("b4_26_52", new visuperobject(4,26,52,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_26_53", new visuperobject(4,26,53,"Phải tùy theo số các danh mà chia xứ ra cho những người nầy làm sản nghiệp;"));
visuperobject.biblemap.put("b4_26_54", new visuperobject(4,26,54,"chi phái nào số dân đông thì ngươi phải cho một sản nghiệp lớn hơn, chi phái nào số dân ít, thì phải cho một sản nghiệp nhỏ hơn, tức là phải cho mỗi chi phái sản nghiệp mình cân phân cùng số tu bộ."));
visuperobject.biblemap.put("b4_26_55", new visuperobject(4,26,55,"Nhưng phải bắt thăm mà chia xứ ra; dân Y-sơ-ra-ên sẽ lãnh phần sản nghiệp mình theo tên của các chi phái tổ tông."));
visuperobject.biblemap.put("b4_26_56", new visuperobject(4,26,56,"Phải tùy sự bắt thăm mà chia sản nghiệp ra cho mỗi chi phái, hoặc số dân đông hay ít."));
visuperobject.biblemap.put("b4_26_57", new visuperobject(4,26,57,"Nầy là người Lê-vi mà người ta tu bộ lại, tùy theo họ hàng mình: do nơi Ghẹt-sôn sanh ra họ Ghẹt-sôn; do nơi Kê-hát sanh ra họ Kê-hát; do nơi Mê-ra-ri sanh ra họ Mê-ra-ri."));
visuperobject.biblemap.put("b4_26_58", new visuperobject(4,26,58,"Nầy là các họ Lê-vi: họ Líp-ni, họ Hếp-rôn, họ Mách-li, họ Mu-si, họ Cô-rê. Vả, Kê-hát sanh Am-ram."));
visuperobject.biblemap.put("b4_26_59", new visuperobject(4,26,59,"Tên vợ Am-ram là Giô-kê-bết, con gái của Lê-vi, sanh trong xứ Ê-díp-tô; nàng sanh cho Am-ram, A-rôn, Môi-se và chị của hai người là Mi-ri-am."));
visuperobject.biblemap.put("b4_26_60", new visuperobject(4,26,60,"Còn A-rôn sanh Na-đáp và A-bi-hu, Ê-lê-a-sa và Y-tha-ma."));
visuperobject.biblemap.put("b4_26_61", new visuperobject(4,26,61,"Nhưng Na-đáp và A-bi-hu chết trong khi dâng một thứ lửa lạ trước mặt Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_26_62", new visuperobject(4,26,62,"Những nam đinh mà người ta tu bộ từ một tháng sắp lên, cộng được hai mươi ba ngàn; vì người Lê-vi không được kể vào số tu bộ của dân Y-sơ-ra-ên, bởi người Lê-vi không được phần sản nghiệp giữa dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_26_63", new visuperobject(4,26,63,"Ðó là sự tu bộ mà Môi-se và Ê-lê-a-sa, thầy tế lễ, làm về dân Y-sơ-ra-ên trong đồng bằng Mô-áp gần Giô-đanh, đối ngang Giê-ri-cô."));
visuperobject.biblemap.put("b4_26_64", new visuperobject(4,26,64,"Trong các người nầy không có một ai thuộc về bọn mà Môi-se và A-rôn, thầy tế lễ, đã kiểm soát khi hai người tu bộ dân Y-sơ-ra-ên tại đồng vắng Si-na -i."));
visuperobject.biblemap.put("b4_26_65", new visuperobject(4,26,65,"Vì Ðức Giê-hô-va có phán về bọn đó rằng: Chúng nó hẳn sẽ chết trong đồng vắng! Vậy, chẳng còn lại một tên, trừ ra Ca-lép, con trai Giê-phu-nê, và Giô-suê, con trai Nun."));
visuperobject.biblemap.put("b4_27_1", new visuperobject(4,27,1,"Các con gái của Xê-lô-phát, là con trai Hê-phe, cháu của Ga-la-át, chắt của Ma-ki, chít của Ma-na-se, thuộc về họ hàng Ma-na-se, là con trai của Giô-sép, đến gần; đây là tên của con gái người: Mách-la, Nô-a, Hốt-la, Minh-ca và Thiệt-sa."));
visuperobject.biblemap.put("b4_27_2", new visuperobject(4,27,2,"Các con gái đó đến ra mắt Môi-se, Ê-lê-a-sa thầy tế lễ, các quan trưởng, và cả hội chúng tại cửa hội mạc, mà nói rằng:"));
visuperobject.biblemap.put("b4_27_3", new visuperobject(4,27,3,"Cha chúng tôi đã chết trong đồng vắng; người chẳng phải về phe đảng của kẻ hiệp lại nghịch cùng Ðức Giê-hô-va tức là phe đảng Cô-rê; nhưng người chết vì tội lỗi mình, và không có con trai."));
visuperobject.biblemap.put("b4_27_4", new visuperobject(4,27,4,"Cớ sao danh cha chúng tôi bị trừ ra khỏi giữa họ người, bởi không có con trai? Hãy cho chúng tôi một phần sản nghiệp giữa anh em của cha chúng tôi."));
visuperobject.biblemap.put("b4_27_5", new visuperobject(4,27,5,"Môi-se bèn đem cớ sự của các con gái ấy đến trước mặt Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_27_6", new visuperobject(4,27,6,"Ðức Giê-hô-va phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_27_7", new visuperobject(4,27,7,"Các con gái Xê-lô-phát nói có lý; ngươi phải cho chúng nó một phần sản nghiệp giữa anh em của cha chúng nó, tức là phải giao cho chúng nó sản nghiệp của cha chúng nó."));
visuperobject.biblemap.put("b4_27_8", new visuperobject(4,27,8,"Ngươi cũng phải nói cùng dân Y-sơ-ra-ên rằng: Khi một người nào chết không có con trai, thì các ngươi phải giao sản nghiệp của người lại cho con gái người."));
visuperobject.biblemap.put("b4_27_9", new visuperobject(4,27,9,"Nhược bằng không có con gái, thì phải giao sản nghiệp cho anh em người."));
visuperobject.biblemap.put("b4_27_10", new visuperobject(4,27,10,"Ví bằng người không có anh em, thì phải giao sản nghiệp cho chú bác người."));
visuperobject.biblemap.put("b4_27_11", new visuperobject(4,27,11,"Nếu không có chú bác, thì phải giao sản nghiệp người cho người bà con gần hơn hết; và người ấy sẽ được lấy làm của. Ấy sẽ là một luật lệ để định cho dân Y-sơ-ra-ên, y như Ðức Giê-hô-va đã phán dặn Môi-se."));
visuperobject.biblemap.put("b4_27_12", new visuperobject(4,27,12,"Ðoạn, Ðức Giê-hô-va phán cùng Môi-se rằng: Hãy lên trên núi A-ba-rim nầy và nhìn xem xứ mà ta đã ban cho dân Y-sơ-ra-ên."));
visuperobject.biblemap.put("b4_27_13", new visuperobject(4,27,13,"Ngươi sẽ nhìn xem xứ đó, rồi ngươi cũng sẽ được về cùng tổ phụ, như A-rôn, anh ngươi, đã được về vậy;"));
visuperobject.biblemap.put("b4_27_14", new visuperobject(4,27,14,"bởi vì, tại đồng vắng Xin, các ngươi có bội nghịch mạng lịnh ta, trong lúc hội chúng cãi cọ, và vì trước mặt chúng nó, các ngươi không tôn ta nên thánh về việc nước. Ấy là nước về sự cãi cọ tại Ca-đe, trong đồng vắng Xin."));
visuperobject.biblemap.put("b4_27_15", new visuperobject(4,27,15,"Môi-se thưa cùng Ðức Giê-hô-va rằng:"));
visuperobject.biblemap.put("b4_27_16", new visuperobject(4,27,16,"Lạy Giê-hô-va là Ðức Chúa Trời của thần linh mọi xác thịt, xin Ngài lập trên hội chúng một người"));
visuperobject.biblemap.put("b4_27_17", new visuperobject(4,27,17,"để vào ra trước mặt chúng nó khiến chúng nó ra vào, hầu cho hội chúng của Ðức Giê-hô-va chớ như con chiên không người chăn."));
visuperobject.biblemap.put("b4_27_18", new visuperobject(4,27,18,"Ðức Giê-hô-va phán cùng Môi-se rằng: Hãy chọn lấy Giô-suê con trai của Nun, người có Thần cảm động; phải đặt tay trên mình người;"));
visuperobject.biblemap.put("b4_27_19", new visuperobject(4,27,19,"rồi đem người ra mắt Ê-lê-a-sa, thầy tế lễ, và cả hội chúng, truyền lịnh cho người trước mặt họ,"));
visuperobject.biblemap.put("b4_27_20", new visuperobject(4,27,20,"và trao phần vinh hiển ngươi lại cho người, hầu cho cả hội chúng Y-sơ-ra-ên nghe người."));
visuperobject.biblemap.put("b4_27_21", new visuperobject(4,27,21,"Người phải ra mắt Ê-lê-a-sa, thầy tế lễ, rồi người sẽ vì Giô-suê cầu hỏi sự xét đoán của u-rim trước mặt Ðức Giê-hô-va; theo lịnh Ê-lê-a-sa, người và cả hội chúng Y-sơ-ra-ên sẽ đi ra và đi vào."));
visuperobject.biblemap.put("b4_27_22", new visuperobject(4,27,22,"Vậy, Môi-se làm y như Ðức Giê-hô-va đã phán dặn mình, chọn lấy Giô-suê để trước mặt Ê-lê-a-sa, thầy tế lễ, và trước mặt cả hội chúng,"));
visuperobject.biblemap.put("b4_27_23", new visuperobject(4,27,23,"đặt tay trên mình người, và truyền lịnh cho, y như Ðức Giê-hô-va đã cậy Môi-se phán dặn vậy."));
visuperobject.biblemap.put("b4_28_1", new visuperobject(4,28,1,"Ðức Giê-hô-va lại phán cùng Môi-se rằng:"));
visuperobject.biblemap.put("b4_28_2", new visuperobject(4,28,2,"Hãy truyền lịnh nầy cho dân Y-sơ-ra-ên mà rằng: Các ngươi phải lo dâng cho ta trong kỳ định lễ vật và thực vật của ta, cùng các của lễ dùng lửa dâng lên có mùi thơm cho ta."));
visuperobject.biblemap.put("b4_28_3", new visuperobject(4,28,3,"Vậy, ngươi phải nói cùng dân Y-sơ-ra-ên rằng: Nầy là của lễ dùng lửa đốt mà các ngươi sẽ dâng cho Ðức Giê-hô-va: Mỗi ngày, hai con chiên con đực giáp năm, không tì vít, làm của lễ thiêu hằng hiến."));
visuperobject.biblemap.put("b4_28_4", new visuperobject(4,28,4,"Ngươi phải dâng con nầy vào buổi sớm mai và con kia vào buổi chiều tối;"));
visuperobject.biblemap.put("b4_28_5", new visuperobject(4,28,5,"còn về của lễ chay, thì phải dâng một phần mười ê-pha bột lọc nhồi với một phần tư hin dầu ô-li-ve ép."));
visuperobject.biblemap.put("b4_28_6", new visuperobject(4,28,6,"Ấy là của lễ thiêu hằng hiến đã lập tại núi Si-na -i. là một của lễ dùng lửa dâng lên có mùi thơm cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_28_7", new visuperobject(4,28,7,"Lễ quán sẽ bằng một phần tư hin rượu cho mỗi một chiên con. Ngươi phải dâng lễ quán bằng rượu cho Ðức Giê-hô-va trong nơi thánh."));
visuperobject.biblemap.put("b4_28_8", new visuperobject(4,28,8,"Ngươi phải dâng chiên con kia vào buổi chiều tối, và làm một của lễ chay và một lễ quán như buổi sớm mai; ấy là một của lễ dùng lửa dâng lên có mùi thơm cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_28_9", new visuperobject(4,28,9,"Ngày sa-bát, ngươi phải dâng hai chiên con đực giáp năm, không tì vít, và hai phần mười ê-pha bột lọc nhồi dầu làm của lễ chay với lễ quán cặp theo."));
visuperobject.biblemap.put("b4_28_10", new visuperobject(4,28,10,"Ấy là của lễ thiêu về mỗi ngày sa-bát, ngoại trừ của lễ thiêu hằng hiến và lễ quán cặp theo."));
visuperobject.biblemap.put("b4_28_11", new visuperobject(4,28,11,"Mỗi đầu tháng, các ngươi phải dâng cho Ðức Giê-hô-va hai con bò đực tơ, một con chiên đực, và bảy chiên con đực giáp năm, không tì vít, làm của lễ thiêu;"));
visuperobject.biblemap.put("b4_28_12", new visuperobject(4,28,12,"ba phần mười ê-pha bột lọc nhồi dầu dùng làm của lễ chay về mỗi con bò đực; hai phần mười ê-pha bột lọc nhồi dầu dùng làm của lễ chay về con chiên đực;"));
visuperobject.biblemap.put("b4_28_13", new visuperobject(4,28,13,"một phần mười ê-pha bột lọc nhồi dầu dùng làm của lễ chay về mỗi con chiên con. Ấy là một của lễ thiêu có mùi thơm, tức một của lễ dùng lửa dâng cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_28_14", new visuperobject(4,28,14,"Lễ quán sẽ bằng phân nửa hin rượu nho về mỗi con bò đực, một phần ba hin về con chiên đực và một phần tư hin về mỗi con chiên con. Ấy là của lễ thiêu về các đầu tháng trong năm."));
visuperobject.biblemap.put("b4_28_15", new visuperobject(4,28,15,"Ngoại trừ của lễ thiêu hằng hiến và lễ quán cặp theo, cũng phải dâng cho Ðức Giê-hô-va một con dê đực làm của lễ chuộc tội."));
visuperobject.biblemap.put("b4_28_16", new visuperobject(4,28,16,"Ngày mười bốn tháng giêng, phải giữ lễ Vượt-qua cho Ðức Giê-hô-va."));
visuperobject.biblemap.put("b4_28_17", new visuperobject(4,28,17,"Qua ngày mười lăm sẽ là ngày lễ; phải ăn bánh không men trong bảy ngày."));
visuperobject.biblemap.put("b4_28_18", new visuperobject(4,28,18,"Ngày thứ nhứt các ngươi sẽ có sự hội hiệp thánh: chớ nên làm một công việc xác thịt nào."));
visuperobject.biblemap.put("b4_28_19", new visuperobject(4,28,19,"Các ngươi phải dâng cho Ðức Giê-hô-va hai con bò đực tơ, và bảy chiên con đực giáp năm, đều không tì vít, làm của lễ thiêu."));
visuperobject.biblemap.put("b4_28_20", new visuperobject(4,28,20,"Của lễ chay sẽ bằng bột lọc nhồi dầu; các ngươi phải dâng ba phần mười ê-pha về một con bò đực, hai phần mười ê-pha về con chiên đực,"));
visuperobject.biblemap.put("b4_28_21", new visuperobject(4,28,21,"và một phần mười ê-pha về mỗi chiên con,"));
visuperobject.biblemap.put("b4_28_22", new visuperobject(4,28,22,"luôn một con dê đực làm của lễ chuộc tội, đặng làm lễ chuộc tội cho các ngươi."));
visuperobject.biblemap.put("b4_28_23", new visuperobject(4,28,23,"Các ngươi phải dâng các lễ vật nầy, ngoài của lễ thiêu buổi sớm mai, là một của lễ thiêu hằng hiến."));
visuperobject.biblemap.put("b4_28_24", new visuperobject(4,28,24,"Mỗi bữa trong bảy ngày, các ngươi phải dâng những lễ vật ngần ấy, như thực vật về của lễ dùng lửa dâng lên có mùi thơm cho Ðức Giê-hô-va. Người ta phải dâng lễ vật đó ngoài của lễ thiêu hằng hiến và lễ quán cặp theo."));
visuperobject.biblemap.put("b4_28_25", new visuperobject(4,28,25,"Ngày thứ bảy các ngươi sẽ có sự hội hiệp thánh, chớ nên làm một công việc xác thịt nào."));
visuperobject.biblemap.put("b4_28_26", new visuperobject(4,28,26,"Trong kỳ lễ của các tuần, nhằm ngày hoa quả đầu mùa, khi các ngươi dâng cho Ðức Giê-hô-va của lễ chay mới, thì phải có sự hội hiệp thánh; chớ nên làm một công việc xác thịt nào."));
visuperobject.biblemap.put("b4_28_27", new visuperobject(4,28,27,"Các ngươi sẽ dùng hai con bò đực tơ, một con chiên đực, và bảy chiên con đực giáp năm, đặng làm của lễ thiêu có mùi thơm cho Ðức Giê-hô-va;"));
visuperobject.biblemap.put("b4_28_28", new visuperobject(4,28,28,"của lễ chay sẽ bằng bột lọc nhồi dầu, ba phần mười ê-pha về mỗi con bò đực, hai phần mười ê-pha về con chiên đực,"));
visuperobject.biblemap.put("b4_28_29", new visuperobject(4,28,29,"một phần mười ê-pha về mỗi con chiên con;"));
