visuperobject.biblemap.put("b40_2_20", new visuperobject(40,2,20,"Hãy chờ dậy, đem con trẻ và mẹ Ngài trở về xứ Y-sơ-ra-ên, vì những kẻ muốn giết con trẻ đã chết rồi."));
visuperobject.biblemap.put("b40_2_21", new visuperobject(40,2,21,"Giô-sép bèn chờ dậy, đem con trẻ và mẹ Ngài trở về xứ Y-sơ-ra-ên."));
