visuperobject.biblemap.put("b40_3_12", new visuperobject(40,3,12,"Tay Ngài cầm nia mà dê thật sạch sân lúa mình và Ngài sẽ chứa lúa vào kho, còn rơm rạ thì đốt trong lửa chẳng hề tắt."));
visuperobject.biblemap.put("b40_3_13", new visuperobject(40,3,13,"Khi ấy, Ðức Chúa Jêsus từ xứ Ga-li-lê đến cùng Giăng tại sông Giô-đanh, đặng chịu người làm phép báp-tem."));
visuperobject.biblemap.put("b40_3_14", new visuperobject(40,3,14,"Song Giăng từ chối mà rằng: Chính tôi cần phải chịu Ngài làm phép báp-tem, mà Ngài lại trở đến cùng tôi sao! Ðức Chúa Jêsus đáp rằng:"));
visuperobject.biblemap.put("b40_3_15", new visuperobject(40,3,15,"Bây giờ cứ làm đi, vì chúng ta nên làm cho trọn mọi việc công bình như vậy. Giăng bèn vâng lời Ngài."));
visuperobject.biblemap.put("b40_3_16", new visuperobject(40,3,16,"Vừa khi chịu phép báp-tem rồi, Ðức Chúa Jêsus ra khỏi nước; bỗng chúc các từng trời mở ra, Ngài thấy Thánh Linh của Ðức Chúa Trời ngự xuống như chim bò câu, đậu trên Ngài."));
visuperobject.biblemap.put("b40_3_17", new visuperobject(40,3,17,"Tức thì có tiếng từ trên trời phán rằng: Nầy là Con yêu dấu của ta, đẹp lòng ta mọi đàng."));
