package vi.bible;

public class vibible41 {

public vibible41() {

putmapbible41();

}

private void putmapbible41() {

visuperobject.biblemap.put("b41_1_1", new visuperobject(41,1,1,"Ðầu Tin Lành của Ðức Chúa Jêsus Christ, là Con Ðức Chúa Trời."));
visuperobject.biblemap.put("b41_1_2", new visuperobject(41,1,2,"Như đã chép trong sách tiên tri Ê-sai rằng: Nầy, ta sai sứ ta đến trước mặt ngươi, Người sẽ dọn đường cho ngươi."));
visuperobject.biblemap.put("b41_1_3", new visuperobject(41,1,3,"Có tiếng kêu trong đồng vắng rằng: Hãy dọn đường Chúa, Ban bằng các nẻo Ngài;"));
visuperobject.biblemap.put("b41_1_4", new visuperobject(41,1,4,"Giăng đã tới, trong đồng vắng vừa làm vừa giảng phép báp-tem ăn năn, cho được tha tội."));
visuperobject.biblemap.put("b41_1_5", new visuperobject(41,1,5,"Cả xứ Giu-đê và hết thảy dân sự thành Giê-ru-sa-lem đều đến cùng người, xưng tội mình và chịu người làm phép báp tem dưới sông Giô-đanh."));
visuperobject.biblemap.put("b41_1_6", new visuperobject(41,1,6,"Giăng mặc áo lông lạc đà, buộc dây lưng da ngang hông; ăn những châu chấu và mật ong rừng."));
visuperobject.biblemap.put("b41_1_7", new visuperobject(41,1,7,"Người giảng dạy rằng: Có Ðấng quyền phép hơn ta đến sau ta; ta không đáng cúi xuống mở dây giày Ngài."));
visuperobject.biblemap.put("b41_1_8", new visuperobject(41,1,8,"Ta làm phép báp-tem cho các ngươi bằng nước; nhưng Ngài sẽ làm phép báp-tem cho các ngươi bằng Ðức Thánh-Linh."));
visuperobject.biblemap.put("b41_1_9", new visuperobject(41,1,9,"Vả, trong những ngày đó, Ðức Chúa Jêsus đến từ Na-xa-rét là thành xứ Ga-li-lê, và chịu Giăng làm phép báp-tem dưới sông Giô-đanh."));
visuperobject.biblemap.put("b41_1_10", new visuperobject(41,1,10,"Vừa khi lên khỏi nước, Ngài thấy các từng trời mở ra, và Ðức Thánh-Linh ngự xuống trên mình Ngài như chim bò câu."));
visuperobject.biblemap.put("b41_1_11", new visuperobject(41,1,11,"Lại có tiếng từ trên trời phán rằng: Ngươi là Con yêu dấu của ta, đẹp lòng ta mọi đường."));
visuperobject.biblemap.put("b41_1_12", new visuperobject(41,1,12,"Tức thì Ðức Thánh Linh giục Ngài đến nơi đồng vắng"));
visuperobject.biblemap.put("b41_1_13", new visuperobject(41,1,13,"Ngài ở nơi đồng vắng chịu quỉ Sa-tan cám dỗ bốn mươi ngày, ở chung với thú rừng, và có thiên sứ đến hầu việc Ngài."));
visuperobject.biblemap.put("b41_1_14", new visuperobject(41,1,14,"Sau khi Giăng bị tù, Ðức Chúa Jêsus đến xứ Ga-li-lê, giảng Tin-Lành của Ðức Chúa Trời,"));
visuperobject.biblemap.put("b41_1_15", new visuperobject(41,1,15,"mà rằng: Kỳ đã trọn, nước Ðức Chúa Trời đã đến gần; các ngươi hãy ăn năn và tin đạo Tin Lành."));
visuperobject.biblemap.put("b41_1_16", new visuperobject(41,1,16,"Ngài đi dọc theo mé biển Ga-li-lê, thấy Si-môn với Anh-rê, em người, đương thả lưới dưới biển; vì hai người vốn làm nghề chài."));
visuperobject.biblemap.put("b41_1_17", new visuperobject(41,1,17,"Ðức Chúa Jêsus bèn phán cùng họ rằng: Hãy theo ta, và ta sẽ khiến các ngươi trở nên tay đánh lưới người."));
visuperobject.biblemap.put("b41_1_18", new visuperobject(41,1,18,"Tức thì hai người bỏ chài lưới mà theo Ngài."));
visuperobject.biblemap.put("b41_1_19", new visuperobject(41,1,19,"Ði một đỗi xa xa, Ngài thấy Gia-cơ, con Xê-bê-đê, với em là Giăng, đương vá lưới trong thuyền."));
visuperobject.biblemap.put("b41_1_20", new visuperobject(41,1,20,"Ngài liền kêu hai người; thì họ để Xê-bê-đê, cho mình, ở lại trong thuyền với mấy người làm thuê, mà theo Ngài."));
visuperobject.biblemap.put("b41_1_21", new visuperobject(41,1,21,"Kế đó, đi đến thành Ca-bê-na-um; nhằm ngày Sa-bát, tức thì Ðức Chúa Jêsus vào nhà hội, khởi dạy dỗ tại đó."));
visuperobject.biblemap.put("b41_1_22", new visuperobject(41,1,22,"Chúng đều cảm động về sự dạy dỗ của Ngài, vì Ngài dạy như có quyền phép, chớ chẳng phải như các thầy thông giáo đâu."));
visuperobject.biblemap.put("b41_1_23", new visuperobject(41,1,23,"Vả, cũng một lúc ấy, trong nhà hội có người bị tà ma ám,"));
visuperobject.biblemap.put("b41_1_24", new visuperobject(41,1,24,"kêu lên rằng: Hỡi Jêsus, người Na-xa-rét, chúng tôi với Ngài có sự gì chăng? Ngài đến để diệt chúng tôi sao? Tôi biết Ngài là ai: là Ðấng Thánh của Ðức Chúa Trời."));
visuperobject.biblemap.put("b41_1_25", new visuperobject(41,1,25,"Nhưng Ðức Chúa Jêsus nghiêm trách nó rằng: Hãy nín đi, ra khỏi người."));
visuperobject.biblemap.put("b41_1_26", new visuperobject(41,1,26,"Tà ma bèn vật mạnh người ấy, cất tiếng kêu lớn, và ra khỏi người."));
visuperobject.biblemap.put("b41_1_27", new visuperobject(41,1,27,"Ai nấy đều lấy làm lạ, đến nỗi hỏi nhau rằng: Cái gì vậy? Sự dạy mới sao! Người nầy lấy quyền phép sai khiến đến tà ma, mà nói cũng phải vâng lời!"));
visuperobject.biblemap.put("b41_1_28", new visuperobject(41,1,28,"Danh tiếng Ðức Chúa Jêsus tức thì đồn ra khắp cả miền xung quanh xứ Ga-li-lê."));
visuperobject.biblemap.put("b41_1_29", new visuperobject(41,1,29,"Vừa ở nhà hội ra, Chúa và môn đồ đi với Gia-cơ và Giăng vào nhà Si-môn và Anh-rê."));
visuperobject.biblemap.put("b41_1_30", new visuperobject(41,1,30,"Và, bà gia Si-môn đang nằm trên giường, đau rét; tức thì chúng thưa với Ngài về chuyện người."));
visuperobject.biblemap.put("b41_1_31", new visuperobject(41,1,31,"Ngài bèn lại gần, cầm tay người đỡ dậy; bịnh rét dứt đi, và người bắt tay hầu hạ."));
visuperobject.biblemap.put("b41_1_32", new visuperobject(41,1,32,"Ðến chiều, mặt trời đã lặn, người ta đem mọi kẻ bịnh và kẻ bị quỉ ám đến cùng Ngài."));
visuperobject.biblemap.put("b41_1_33", new visuperobject(41,1,33,"Cả thành nhóm lại trước cửa."));
visuperobject.biblemap.put("b41_1_34", new visuperobject(41,1,34,"Ngài chữa lành nhiều kẻ đau các thứ bịnh, và đuổi nhiều quỉ, chẳng cho phép các quỉ nói ra, vì chúng nó biết Ngài."));
visuperobject.biblemap.put("b41_1_35", new visuperobject(41,1,35,"Sáng hôm sau trời còn mờ mờ, Ngài chờ dậy, bước ra, đi vào nơi vắng vẻ, và cầu nguyện tại đó."));
visuperobject.biblemap.put("b41_1_36", new visuperobject(41,1,36,"Si-môn cùng đồng bạn đi tìm Ngài."));
visuperobject.biblemap.put("b41_1_37", new visuperobject(41,1,37,"Khi kiếm được thì thưa rằng: Hết thảy đương tìm thầy."));
visuperobject.biblemap.put("b41_1_38", new visuperobject(41,1,38,"Ngài phán: Chúng ta đi nơi khác, trong những làng xung quanh đây, để ta cũng giảng đạo ở đó nữa; vì ấy là cốt tại việc đó mà ta đã đến."));
visuperobject.biblemap.put("b41_1_39", new visuperobject(41,1,39,"Ngài trải khắp xứ Ga-li-lê, giảng dạy trong các nhà hội và đuổi quỉ."));
visuperobject.biblemap.put("b41_1_40", new visuperobject(41,1,40,"Có một người phung đến cùng Ngài, quì xuống cầu xin rằng: Nếu Chúa khứng, có thể khiến tôi sạch được."));
visuperobject.biblemap.put("b41_1_41", new visuperobject(41,1,41,"Ðức Chúa Jêsus động lòng thương xót, giơ tay rờ người, mà phán rằng: Ta khứng, hãy sạch đi."));
visuperobject.biblemap.put("b41_1_42", new visuperobject(41,1,42,"Liền khi đó, phung lặn mất, người trở nên sạch."));
visuperobject.biblemap.put("b41_1_43", new visuperobject(41,1,43,"tức thì Ðức Chúa Jêsus cho người ấy ra, lấy giọng nghiêm phán rằng:"));
visuperobject.biblemap.put("b41_1_44", new visuperobject(41,1,44,"Hãy giữ chớ tỏ điều đó cùng ai; song khá đi tỏ mình cùng thầy tế lễ, và vì người được sạch, hãy dâng của lễ theo như Môi-se dạy, để điều đó làm chứng cho họ."));
visuperobject.biblemap.put("b41_1_45", new visuperobject(41,1,45,"Nhưng người ấy đi, đồn việc đó ra, tới đâu cũng thuật chuyện, đến nỗi Chúa Jêsus không vào thành cách rõ ràng được nữa; song Ngài ở ngoài, tại nơi vắng vẻ; và người ta từ bốn phương đều đến cùng Ngài."));
visuperobject.biblemap.put("b41_2_1", new visuperobject(41,2,1,"Khỏi một vài ngày, Ðức Chúa Jêsus trở lại thành Ca-bê-na-um, và chúng nghe nói Ngài ở trong nhà."));
visuperobject.biblemap.put("b41_2_2", new visuperobject(41,2,2,"Họ họp lại tại đó đông lắm, đến nỗi trước cửa cũng không còn chỗ trống; Ngài giảng đạo cho họ nghe."));
visuperobject.biblemap.put("b41_2_3", new visuperobject(41,2,3,"Bấy giờ có mấy kẻ đem tới cho Ngài một người đau bại, có bốn người khiêng."));
visuperobject.biblemap.put("b41_2_4", new visuperobject(41,2,4,"Nhưng, vì đông người, không lại gần Ngài được, nên dở mái nhà ngay chỗ Ngài ngồi, rồi do lỗ đó dòng giường người bại xuống."));
visuperobject.biblemap.put("b41_2_5", new visuperobject(41,2,5,"Ðức Chúa Jêsus thấy đức tin họ, bèn phán với kẻ bại rằng: Hỡi con ta, tội lỗi ngươi đã được tha."));
visuperobject.biblemap.put("b41_2_6", new visuperobject(41,2,6,"Vả, có mấy thầy thông giáo ngồi đó, nghĩ thầm rằng:"));
visuperobject.biblemap.put("b41_2_7", new visuperobject(41,2,7,"Sao người nầy nói như vậy? Người nói phạm thượng đó! Ngoài mình Ðức Chúa Trời, còn có ai tha tội được chăng?"));
visuperobject.biblemap.put("b41_2_8", new visuperobject(41,2,8,"Ðức Chúa Jêsus trong trí đã tự hiểu họ nghĩ như vậy, tức thì phán rằng: Sao các ngươi bàn luận trong lòng thể ấy?"));
visuperobject.biblemap.put("b41_2_9", new visuperobject(41,2,9,"Nay bảo kẻ bại rằng: Tội ngươi đã được tha; hay là bảo người rằng: Hãy đứng dậy vác giường mà đi; hai điều ấy điều nào dễ hơn?"));
visuperobject.biblemap.put("b41_2_10", new visuperobject(41,2,10,"Vả, để cho các ngươi biết rằng Con người ở thế gian có quyền tha tội, thì Ngài phán cùng kẻ bại rằng:"));
visuperobject.biblemap.put("b41_2_11", new visuperobject(41,2,11,"Ta biểu ngươi, hãy đứng dậy, vác giường đi về nhà."));
visuperobject.biblemap.put("b41_2_12", new visuperobject(41,2,12,"Kẻ bại đứng dậy, tức thì vác giường đi ra trước mặt thiên hạ; đến nỗi ai nấy làm lạ, ngợi khen Ðức Chúa Trời, mà rằng: Chúng tôi chưa hề thấy việc thể nầy."));
visuperobject.biblemap.put("b41_2_13", new visuperobject(41,2,13,"Bấy giờ, Ðức Chúa Jêsus trở lại về phía mé biển; cả dân chúng đến cùng Ngài, rồi Ngài dạy dỗ họ."));
visuperobject.biblemap.put("b41_2_14", new visuperobject(41,2,14,"Ngài vừa đi qua thấy Lê-vi, con A-phê, đương ngồi tại sở thâu thuế, thì phán cùng người rằng: Hãy theo ta. Người đứng dậy theo Ngài."));
visuperobject.biblemap.put("b41_2_15", new visuperobject(41,2,15,"Ðức Chúa Jêsus đương ngồi ăn tại nhà Lê-vi, có nhiều kẻ thâu thuế và người có tội đồng bàn với Ngài và môn đồ Ngài; vì trong bọn đó có nhiều kẻ theo Ngài rồi."));
visuperobject.biblemap.put("b41_2_16", new visuperobject(41,2,16,"Các thầy thông giáo và người Pha-ri-si thấy Ngài ăn với người thâu thuế và kẻ phạm tội, thì nói với môn đồ Ngài rằng: Người cùng ăn với kẻ thâu thuế và người có tội sao!"));
visuperobject.biblemap.put("b41_2_17", new visuperobject(41,2,17,"Ðức Chúa Jêsus nghe vậy, bèn phán cùng họ rằng: Chẳng phải kẻ mạnh khỏe cần thầy thuốc đâu, nhưng là kẻ có bịnh; ta chẳng phải đến gọi người công bình, nhưng gọi kẻ có tội."));
visuperobject.biblemap.put("b41_2_18", new visuperobject(41,2,18,"Vả, môn đồ của Giăng và người Pha-ri-si đều kiêng ăn. Có người đến thưa cùng Ðức Chúa Jêsus rằng: Bởi cớ nào môn đồ của Giăng và môn đồ của người Pha-ri-si đều kiêng ăn, còn môn đồ của thầy không kiêng ăn?"));
visuperobject.biblemap.put("b41_2_19", new visuperobject(41,2,19,"Ðức Chúa Jêsus phán rằng: Bạn hữu của chàng rể có kiêng ăn được trong khi chàng rể còn ở cùng mình chăng? Hễ chàng rể còn ở với họ đến chừng nào, thì họ không thể kiêng ăn được đến chừng nấy."));
visuperobject.biblemap.put("b41_2_20", new visuperobject(41,2,20,"Song tới kỳ chàng rể phải đem đi khỏi họ, trong ngày đó họ sẽ kiêng ăn."));
visuperobject.biblemap.put("b41_2_21", new visuperobject(41,2,21,"Không ai vá miếng nỉ mới vào cái áo cũ; nếu vậy thì miếng nỉ mới chằng rách áo cũ, mà đàng rách càng xấu thêm."));
visuperobject.biblemap.put("b41_2_22", new visuperobject(41,2,22,"Cũng không ai đổ rượu mới vào bầu da cũ; nếu vậy, rượu làm vỡ bầu, rượu mất và bầu cũng chẳng còn."));
visuperobject.biblemap.put("b41_2_23", new visuperobject(41,2,23,"Nhằm ngày Sa-bát, Ðức Chúa Jêsus đi qua đồng lúa mì; đang đi đường, môn đồ Ngài bứt bông lúa mì."));
visuperobject.biblemap.put("b41_2_24", new visuperobject(41,2,24,"Các người Pha-ri-si bèn nói cùng Ngài rằng: Coi kìa, sao môn đồ thầy làm điều không nên làm trong ngày Sa-bát?"));
visuperobject.biblemap.put("b41_2_25", new visuperobject(41,2,25,"Ngài đáp rằng: Các ngươi chưa đọc đến sự vua Ða-vít làm trong khi vua cùng những người đi theo bị túng đói hay sao?"));
visuperobject.biblemap.put("b41_2_26", new visuperobject(41,2,26,"Thể nào trong đời A-bi-tha làm thầy cả thượng phẩm, vua ấy vào đến Ðức Chúa Trời, ăn bánh bày ra, lại cũng cho những người đi theo ăn nữa, dầu bánh ấy chỉ những thầy tế lễ mới được phép ăn thôi?"));
visuperobject.biblemap.put("b41_2_27", new visuperobject(41,2,27,"Ðoạn, Ngài lại phán: Vì loài người mà lập ngày Sa-bát, chớ chẳng phải vì ngày Sa-bát mà dựng nên loài người."));
visuperobject.biblemap.put("b41_2_28", new visuperobject(41,2,28,"Vậy thì Con người cũng làm chủ ngày Sa-bát."));
visuperobject.biblemap.put("b41_3_1", new visuperobject(41,3,1,"Lần khác, Ðức Chúa Jêsus vào nhà hội, ở đó có một người nam teo bàn tay."));
visuperobject.biblemap.put("b41_3_2", new visuperobject(41,3,2,"Chúng rình xem thử Ngài có chữa người đó trong ngày Sa-bát chăng, đặng cáo Ngài."));
visuperobject.biblemap.put("b41_3_3", new visuperobject(41,3,3,"Ngài phán cùng người teo tay rằng: hãy dậy, đứng chính giữa đây."));
visuperobject.biblemap.put("b41_3_4", new visuperobject(41,3,4,"rồi Ngài hỏi họ rằng: trong ngày Sa-bát, nên làm việc lành hay việc dữ, cứu người hay giết người? Nhưng họ đều nín lặng."));
visuperobject.biblemap.put("b41_3_5", new visuperobject(41,3,5,"Bấy giờ, Ngài lấy mắt liếc họ, vừa giận vừa buồn vì lòng họ cứng cỏi, rồi phán cùng người nam rằng: hãy giơ tay ra. Người giơ ra, thì tay được lành."));
visuperobject.biblemap.put("b41_3_6", new visuperobject(41,3,6,"Các người Pha-ri-si đi ra, tức thì bàn luận với phe Hê-rốt nghịch cùng Ngài, đặng giết Ngài đi."));
visuperobject.biblemap.put("b41_3_7", new visuperobject(41,3,7,"Ðức Chúa Jêsus cùng môn đồ Ngài lánh ra nơi bờ biển, có đoàn dân đông lắm từ xứ Ga-li-lê đến theo Ngài. Từ xứ Giu-đê,"));
visuperobject.biblemap.put("b41_3_8", new visuperobject(41,3,8,"thành Giê-ru-sa-lem, xứ Y-đu-mê, xứ bên kia sông Giô-đanh, miền xung quanh thành Ty-rơ và thành Si-đôn cũng vậy, dân đông lắm, nghe nói mọi việc Ngài làm, thì đều đến cùng Ngài."));
visuperobject.biblemap.put("b41_3_9", new visuperobject(41,3,9,"Bởi cớ dân đông, nên Ngài biểu môn đồ sắm cho mình một chiếc thuyền nhỏ sẵn sàng, đặng cho khỏi bị chúng lấn ép Ngài quá."));
visuperobject.biblemap.put("b41_3_10", new visuperobject(41,3,10,"Vì Ngài đã chữa lành nhiều kẻ bịnh, nên nỗi ai nấy có bịnh chi cũng đều áp đến gần để rờ Ngài."));
visuperobject.biblemap.put("b41_3_11", new visuperobject(41,3,11,"Khi tà ma thấy Ngài, bèn sấp mình xuống nơi chơn Ngài mà kêu lên rằng: Thầy là Con Ðức Chúa Trời!"));
visuperobject.biblemap.put("b41_3_12", new visuperobject(41,3,12,"Song Ngài nghiêm cấm chúng nó chớ tỏ cho ai biết mình."));
visuperobject.biblemap.put("b41_3_13", new visuperobject(41,3,13,"Kế đó, Ðức Chúa Jêsus lên trên núi, đòi những người Ngài muốn, thì những người ấy đến kề Ngài."));
visuperobject.biblemap.put("b41_3_14", new visuperobject(41,3,14,"Ngài bèn lập mười hai người, gọi là sứ đồ, để ở cùng Ngài và sai đi giảng đạo,"));
visuperobject.biblemap.put("b41_3_15", new visuperobject(41,3,15,"lại ban cho quyền phép đuổi quỉ."));
visuperobject.biblemap.put("b41_3_16", new visuperobject(41,3,16,"Nầy là mười hai sứ đồ Ngài đã lập: Si-môn, Ngài đặt tên là Phi -e-rơ;"));
visuperobject.biblemap.put("b41_3_17", new visuperobject(41,3,17,"Gia-cơ con Xê-bê-đê, và Giăng em Gia-cơ, Ngài đặt tên hai người là Bô-a-nẹt, nghĩa là con trai của sấm sét;"));
visuperobject.biblemap.put("b41_3_18", new visuperobject(41,3,18,"Anh-rê, Phi-líp, Ba-thê-lê-my, Ma-thi -ơ, Thô-ma, Gia-cơ con A-phê, Tha-đê, Si-môn là người Ca-na-an,"));
visuperobject.biblemap.put("b41_3_19", new visuperobject(41,3,19,"và Giu-đa Ích-ca-ri-ốt, chính tên nầy đã phản Ngài."));
visuperobject.biblemap.put("b41_3_20", new visuperobject(41,3,20,"Ðức Chúa Jêsus cùng môn đồ vào trong một cái nhà; đoàn dân lại nhóm họp tại đó, đến nỗi Ngài và môn đồ không ăn được."));
visuperobject.biblemap.put("b41_3_21", new visuperobject(41,3,21,"Những bạn hữu Ngài nghe vậy, bèn đến để cầm giữ Ngài; vì người ta nói Ngài đã mất trí khôn."));
visuperobject.biblemap.put("b41_3_22", new visuperobject(41,3,22,"Có các thầy thông giáo ở thành Giê-ru-sa-lem xuống, nói rằng: Người bị quỉ Bê-ên-xê-bun ám, và nhờ phép chúa quỉ mà trừ quỉ."));
visuperobject.biblemap.put("b41_3_23", new visuperobject(41,3,23,"Song Ðức Chúa Jêsus kêu họ đến, dùng lời thí dụ mà phán rằng: Sao quỉ Sa-tan đuổi được quỉ Sa-tan?"));
visuperobject.biblemap.put("b41_3_24", new visuperobject(41,3,24,"Nếu một nước tự chia nhau, thì nước ấy không thể còn được;"));
visuperobject.biblemap.put("b41_3_25", new visuperobject(41,3,25,"lại nếu một nhà tự chia nhau, thì nhà ấy không thể còn được."));
visuperobject.biblemap.put("b41_3_26", new visuperobject(41,3,26,"Vậy, nếu quỉ Sa-tan tự dấy lên nghịch cùng mình, tất nó bị chia xẻ ra, không thể còn được; song nó đã gần đến cùng rồi."));
visuperobject.biblemap.put("b41_3_27", new visuperobject(41,3,27,"Chẳng ai có thể vào nhà một người mạnh sức cướp lấy của người, mà trước hết không trói người lại; phải trói người lại, rồi mới cướp nhà người được."));
visuperobject.biblemap.put("b41_3_28", new visuperobject(41,3,28,"Quả thật, ta nói cùng các ngươi, mọi tội lỗi sẽ tha cho con cái loài người, và hết thảy những lời phạm thượng họ sẽ nói ra cũng vậy;"));
visuperobject.biblemap.put("b41_3_29", new visuperobject(41,3,29,"nhưng ai sẽ nói phạm đến Ðức Thánh Linh thì chẳng hề được tha, phải mắc tội đời đời."));
visuperobject.biblemap.put("b41_3_30", new visuperobject(41,3,30,"Ngài phán như vậy, vì họ nói rằng: Người bị tà ma ám."));
visuperobject.biblemap.put("b41_3_31", new visuperobject(41,3,31,"Mẹ và anh em Ngài đến, đứng ngoài sai kêu Ngài. Ðoàn dân vẫn ngồi chung quanh Ngài."));
visuperobject.biblemap.put("b41_3_32", new visuperobject(41,3,32,"Có kẻ thưa rằng: Nầy, mẹ và anh em thầy ở ngoài kia đương tìm thầy."));
visuperobject.biblemap.put("b41_3_33", new visuperobject(41,3,33,"Song Ngài phán cùng họ rằng: Ai là mẹ ta, ai là anh em ta?"));
visuperobject.biblemap.put("b41_3_34", new visuperobject(41,3,34,"Ngài đưa mắt liếc những kẻ ngồi chung quanh mình mà phán rằng: Kìa là mẹ ta và anh em ta!"));
visuperobject.biblemap.put("b41_3_35", new visuperobject(41,3,35,"Ai làm theo ý muốn Ðức Chúa Trời, nấy là anh em, chị em, và mẹ ta vậy."));
visuperobject.biblemap.put("b41_4_1", new visuperobject(41,4,1,"Ðức Chúa Jêsus lại khởi sự giảng dạy nơi bờ biển; có một đoàn dân đông lắm nhóm lại xung quanh Ngài, Ngài bước lên trong một chiếc thuyền và ngồi đó, còn cả đoàn dân thì ở trên đất nơi mé biển."));
visuperobject.biblemap.put("b41_4_2", new visuperobject(41,4,2,"Ngài lấy thí dụ dạy dỗ họ nhiều điều, và trong khi dạy, Ngài phán rằng:"));
visuperobject.biblemap.put("b41_4_3", new visuperobject(41,4,3,"Hãy nghe. Có người gieo giống đi ra đặng gieo."));
visuperobject.biblemap.put("b41_4_4", new visuperobject(41,4,4,"Khi đương gieo, một phần giống rơi dọc đường, chim đến ăn hết."));
visuperobject.biblemap.put("b41_4_5", new visuperobject(41,4,5,"Một phần khác rơi nhằm nơi đất đá sỏi, chỉ có ít đất thịt, tức thì mọc lên, vì bị lấp không sâu;"));
visuperobject.biblemap.put("b41_4_6", new visuperobject(41,4,6,"nhưng khi mặt trời đã mọc, thì bị đốt, và bởi không có rễ, nên phải héo."));
visuperobject.biblemap.put("b41_4_7", new visuperobject(41,4,7,"Một phần khác rơi nhằm bụi gai; gai mọc lên làm cho nghẹt ngòi, và không kết quả."));
visuperobject.biblemap.put("b41_4_8", new visuperobject(41,4,8,"Một phần khác nữa rơi nhằm nơi đất tốt, thì kết quả, lớn lên và nẩy nở ra; một hột ra ba chục, hột khác sáu chục, hột khác một trăm."));
visuperobject.biblemap.put("b41_4_9", new visuperobject(41,4,9,"Ngài lại phán rằng: Ai có tai mà nghe, hãy nghe."));
visuperobject.biblemap.put("b41_4_10", new visuperobject(41,4,10,"Khi Ðức Chúa Jêsus ở một mình, những kẻ xung quanh Ngài cùng mười hai sứ đồ hỏi Ngài về các lời thí dụ."));
visuperobject.biblemap.put("b41_4_11", new visuperobject(41,4,11,"Ngài phán rằng: Sự mầu nhiệm của nước Ðức Chúa Trời đã tỏ ra cho các ngươi; nhưng về phần người ngoài, thì dùng thí dụ để dạy mọi sự,"));
visuperobject.biblemap.put("b41_4_12", new visuperobject(41,4,12,"hầu cho họ xem thì xem mà không thấy, nghe thì nghe mà không hiểu; e họ hối cải mà được tha tội chăng."));
visuperobject.biblemap.put("b41_4_13", new visuperobject(41,4,13,"Ðoạn, Ngài phán rằng: Các ngươi không hiểu thí dụ ấy sao? Vậy thì thế nào hiểu mọi thí dụ được?"));
visuperobject.biblemap.put("b41_4_14", new visuperobject(41,4,14,"Người gieo giống ấy là gieo đạo."));
visuperobject.biblemap.put("b41_4_15", new visuperobject(41,4,15,"Những kẻ ở dọc đường là kẻ đã chịu đạo gieo nơi mình; nhưng vừa mới nghe đạo, tức thì quỉ Sa-tan đến, cướp lấy đạo đã gieo trong lòng họ đi."));
visuperobject.biblemap.put("b41_4_16", new visuperobject(41,4,16,"Cũng vậy, những người chịu giống gieo nơi đất đá sỏi, là những kẻ nghe đạo, liền vui mừng chịu lấy;"));
visuperobject.biblemap.put("b41_4_17", new visuperobject(41,4,17,"song vì trong lòng họ không có rễ, chỉ tạm thời mà thôi, nên nỗi gặp khi vì cớ đạo mà xảy ra sự cực khổ, bắt bớ, thì liền vấp phạm."));
visuperobject.biblemap.put("b41_4_18", new visuperobject(41,4,18,"Còn những kẻ chịu giống gieo nơi bụi gai, là kẻ đã nghe đạo;"));
visuperobject.biblemap.put("b41_4_19", new visuperobject(41,4,19,"song sự lo lắng về đời nầy, sự mê đắm về giàu sang, và các sự tham muốn khác thấu vào lòng họ, làm cho nghẹt ngòi đạo, và trở nên không trái."));
visuperobject.biblemap.put("b41_4_20", new visuperobject(41,4,20,"Nhưng, những người chịu giống gieo vào nơi đất tốt, là kẻ nghe đạo, chịu lấy và kết quả, một hột ra ba chục, hột khác sáu chục, hột khác một trăm."));
visuperobject.biblemap.put("b41_4_21", new visuperobject(41,4,21,"Ngài lại phán cùng họ rằng: Có ai đem đèn để dưới cái thùng hoặc dưới cái giường chăng? Há chẳng phải để trên chơn đèn sao?"));
visuperobject.biblemap.put("b41_4_22", new visuperobject(41,4,22,"Vì chẳng có điều chi kín mà không phải lộ ra, chẳng có điều chi giấu mà không phải rõ ràng."));
visuperobject.biblemap.put("b41_4_23", new visuperobject(41,4,23,"Nếu ai có tai mà nghe, hãy nghe!"));
visuperobject.biblemap.put("b41_4_24", new visuperobject(41,4,24,"Ngài lại phán rằng: Hãy cẩn thận về điều mình nghe. Người ta sẽ đong cho các ngươi bằng lường các ngươi đã đong cho, và thêm vào đó nữa."));
visuperobject.biblemap.put("b41_4_25", new visuperobject(41,4,25,"Vì sẽ ban thêm cho kẻ nào có; nhưng kẻ nào không có, sẽ bị cất lấy điều mình đã có."));
visuperobject.biblemap.put("b41_4_26", new visuperobject(41,4,26,"Ngài lại phán rằng: Nước Ðức Chúa Trời cũng như một người vải giống xuống đất;"));
visuperobject.biblemap.put("b41_4_27", new visuperobject(41,4,27,"người ngủ hay dậy, đêm và ngày, giống cứ nẩy chồi mọc lên, mà người không biết thể nào."));
visuperobject.biblemap.put("b41_4_28", new visuperobject(41,4,28,"Vì đất tự sanh ra hoa lợi: ban đầu là cây, kế đến bông, đoạn bông kết thành hột."));
visuperobject.biblemap.put("b41_4_29", new visuperobject(41,4,29,"Khi hột đã chín, người ta liền tra lưỡi hái vào, vì mùa gặt đã đến."));
visuperobject.biblemap.put("b41_4_30", new visuperobject(41,4,30,"Ngài lại phán rằng: Chúng ta sánh nước Ðức Chúa Trời với chi, hay lấy thí dụ nào mà tỏ ra?"));
visuperobject.biblemap.put("b41_4_31", new visuperobject(41,4,31,"Nước ấy giống như một hột cải: khi người ta gieo, nó nhỏ hơn hết các hột giống trên đất;"));
visuperobject.biblemap.put("b41_4_32", new visuperobject(41,4,32,"song khi gieo rồi, nó mọc lên, trở lớn hơn mọi thứ rau, và nứt ra nhành lớn, đến nỗi chim trời núp dưới bóng nó được."));
visuperobject.biblemap.put("b41_4_33", new visuperobject(41,4,33,"Ấy bởi nhiều lời thí dụ như cách ấy mà Ngài giảng đạo cho họ, tùy theo sức họ nghe được."));
visuperobject.biblemap.put("b41_4_34", new visuperobject(41,4,34,"Ngài chẳng hề giảng cho chúng mà không dùng thí dụ; nhưng, khi ở riêng, Ngài cắt nghĩa hết cho môn đồ mình."));
visuperobject.biblemap.put("b41_4_35", new visuperobject(41,4,35,"Ðến chiều ngày ấy, Ngài phán cùng môn đồ rằng: Chúng ta hãy qua bờ bên kia."));
visuperobject.biblemap.put("b41_4_36", new visuperobject(41,4,36,"Khi cho dân chúng tan về rồi, Ðức Chúa Jêsus cứ ở trong thuyền, và môn đồ đưa Ngài đi; cũng có các thuyền khác cùng đi nữa."));
visuperobject.biblemap.put("b41_4_37", new visuperobject(41,4,37,"Vả, có cơn bão lớn nổi lên, sóng tạt vào thuyền, đến nỗi gần đầy nước;"));
visuperobject.biblemap.put("b41_4_38", new visuperobject(41,4,38,"nhưng Ngài đương ở đằng sau lái, dựa gối mà ngủ. Môn đồ thức Ngài dậy mà thưa rằng: Thầy ôi, thầy không lo chúng ta chết sao?"));
visuperobject.biblemap.put("b41_4_39", new visuperobject(41,4,39,"Ngài bèn thức dậy, quở gió và phán cùng biển rằng: Hãy êm đi, lặng đi! Gió liền dứt và đều yên lặng như tờ."));
visuperobject.biblemap.put("b41_4_40", new visuperobject(41,4,40,"Ðoạn, Ngài phán cùng môn đồ rằng: Sao các ngươi sợ? Chưa có đức tin sao?"));
visuperobject.biblemap.put("b41_4_41", new visuperobject(41,4,41,"Môn đồ kinh hãi lắm, nói với nhau rằng: Vậy thì người nầy là ai, mà gió và biển đều vâng lịnh người?"));
visuperobject.biblemap.put("b41_5_1", new visuperobject(41,5,1,"Ðức Chúa Jêsus cùng môn đồ qua đến bờ biển bên kia, trong miền Giê-ra-sê."));
visuperobject.biblemap.put("b41_5_2", new visuperobject(41,5,2,"Ngài mới ở trên thuyền bước xuống, tức thì có một người bị tà ma ám từ nơi mồ mả đi ra đến trước mặt Ngài."));
visuperobject.biblemap.put("b41_5_3", new visuperobject(41,5,3,"Người thường ở nơi mồ mả, dẫu dùng xiềng sắt cũng chẳng ai cột trói được nữa;"));
visuperobject.biblemap.put("b41_5_4", new visuperobject(41,5,4,"vì nhiều lần người bị cùm chơn hoặc bị xiềng, rồi bẻ xiềng tháo cùm, không ai có sức trị được."));
visuperobject.biblemap.put("b41_5_5", new visuperobject(41,5,5,"Người cứ ở nơi mồ mả và trên núi, ngày đêm kêu la và lấy đá đánh bầm mình."));
visuperobject.biblemap.put("b41_5_6", new visuperobject(41,5,6,"Người thấy Ðức Chúa Jêsus ở đằng xa, chạy lại sấp mình xuống trước mặt Ngài,"));
visuperobject.biblemap.put("b41_5_7", new visuperobject(41,5,7,"mà kêu lớn rằng: Hỡi Ðức Chúa Jêsus, Con Ðức Chúa Trời rất cao, tôi với Ngài có sự gì chăng? Tôi nhơn danh Ðức Chúa Trời mà khẩn cầu Ngài, xin đừng làm khổ tôi."));
visuperobject.biblemap.put("b41_5_8", new visuperobject(41,5,8,"Vì Ðức Chúa Jêsus vừa phán cùng nó rằng: Hỡi tà ma, phải ra khỏi người nầy."));
visuperobject.biblemap.put("b41_5_9", new visuperobject(41,5,9,"Ngài lại hỏi rằng: Mầy tên gì? Thưa rằng: Tên tôi là Quân đội; vì chúng tôi đông."));
visuperobject.biblemap.put("b41_5_10", new visuperobject(41,5,10,"Nó lại van xin Ngài đừng đuổi chúng nó ra khỏi miền đó."));
visuperobject.biblemap.put("b41_5_11", new visuperobject(41,5,11,"Vả, chỗ đó, tại trên núi, có một bầy heo đông đương ăn."));
visuperobject.biblemap.put("b41_5_12", new visuperobject(41,5,12,"Các quỉ cầu xin Ngài rằng: Xin khiến chúng tôi đến với bầy heo ấy, để chúng tôi nhập vào chúng nó. Ðức Chúa Jêsus cho phép."));
visuperobject.biblemap.put("b41_5_13", new visuperobject(41,5,13,"Các tà ma ra khỏi người đó, bèn nhập vào bầy heo từ trên bực cao vụt đâm đầu xuống biển. Có độ hai ngàn con heo chết chìm cả dưới biển."));
visuperobject.biblemap.put("b41_5_14", new visuperobject(41,5,14,"Nhưng kẻ chăn heo trốn, đồn tin nầy ra khắp trong thành và trong nhà quê;"));
visuperobject.biblemap.put("b41_5_15", new visuperobject(41,5,15,"dân sự đổ ra đặng xem điều đã xảy đến. Vậy, chúng đến cùng Ðức Chúa Jêsus, thấy người đã bị nhiều quỉ ám, đang ngồi, mặc quần áo, trí khôn bình tĩnh, thì sợ hãi lắm."));
visuperobject.biblemap.put("b41_5_16", new visuperobject(41,5,16,"Những người đã thấy việc đó, thuật cho họ nghe chuyện đã xảy đến cho kẻ bị quỉ ám và bầy heo."));
visuperobject.biblemap.put("b41_5_17", new visuperobject(41,5,17,"Chúng bèn xin Ngài ra khỏi địa phận mình."));
visuperobject.biblemap.put("b41_5_18", new visuperobject(41,5,18,"Lúc Ngài đương bước vào thuyền, người trước đã bị quỉ ám xin ở lại với Ngài."));
visuperobject.biblemap.put("b41_5_19", new visuperobject(41,5,19,"Nhưng Ðức Chúa Jêsus không cho, phán rằng: Hãy về nhà ngươi, nơi bạn hữu ngươi, mà thuật lại cho họ điều lớn lao thể nào Chúa đã làm cho ngươi, và Ngài đã thương xót ngươi cách nào."));
visuperobject.biblemap.put("b41_5_20", new visuperobject(41,5,20,"Vậy, người đi, đồn ra trong xứ Ðê-ca-bô-lơ những điều lớn lao thể nào mà Ðức Chúa Jêsus đã làm cho mình; ai nấy đều lấy làm lạ."));
visuperobject.biblemap.put("b41_5_21", new visuperobject(41,5,21,"Khi Ðức Chúa Jêsus lại xuống thuyền qua bờ bên kia, có đoàn dân đông nhóm họp chung quanh Ngài. Ngài đứng trên bờ biển."));
visuperobject.biblemap.put("b41_5_22", new visuperobject(41,5,22,"Bấy giờ, có một người trong những người cai nhà hội, tên là Giai ru, đến, thấy Ðức Chúa Jêsus, bèn gieo mình nơi chơn Ngài;"));
visuperobject.biblemap.put("b41_5_23", new visuperobject(41,5,23,"nài xin mà rằng: Con gái nhỏ tôi gần chết; xin Chúa đến, đặt tay trên nó, đặng nó lành mạnh và sống."));
visuperobject.biblemap.put("b41_5_24", new visuperobject(41,5,24,"Ðức Chúa Jêsus đi với người; đoàn dân đông cùng đi theo và lấn ép Ngài."));
visuperobject.biblemap.put("b41_5_25", new visuperobject(41,5,25,"vả, tại đó có một người đờn bà bị bịnh mất huyết đã mười hai năm,"));
visuperobject.biblemap.put("b41_5_26", new visuperobject(41,5,26,"bấy lâu chịu khổ sở trong tay nhiều thầy thuốc, hao tốn hết tiền của, mà không thấy đỡ gì; bịnh lại càng nặng thêm."));
visuperobject.biblemap.put("b41_5_27", new visuperobject(41,5,27,"Người đã nghe tin về Ðức Chúa Jêsus, bèn lẩn vào đằng sau giữa đám đông, mà rờ áo Ngài."));
visuperobject.biblemap.put("b41_5_28", new visuperobject(41,5,28,"Vì người nói rằng: Nếu ta chỉ rờ đến áo Ngài mà thôi, thì ta sẽ được lành."));
visuperobject.biblemap.put("b41_5_29", new visuperobject(41,5,29,"Cùng một lúc ấy, huyết lậu liền cầm lại; người nghe trong mình đã được lành bịnh."));
visuperobject.biblemap.put("b41_5_30", new visuperobject(41,5,30,"Tức thì Ðức Chúa Jêsus tự biết có sức mạnh đã ra từ mình, bèn xây lại giữa đám đông mà hỏi rằng: Ai đã rờ áo ta?"));
visuperobject.biblemap.put("b41_5_31", new visuperobject(41,5,31,"Môn đồ thưa rằng: Thầy thấy đám đông lấn ép thầy, thº§y còn hỏi rằng: Ai rṀ đến ta?"));
visuperobject.biblemap.put("b41_5_32", new visuperobject(41,5,32,"Ngài nhìn chung quanh mình để xem người đã làm điều đó."));
visuperobject.biblemap.put("b41_5_33", new visuperobject(41,5,33,"Người đờn bà biết sự đã xảy đến cho mình, bèn run sợ đến gieo mình dưới chơn Ngài, tỏ hết tình thật."));
visuperobject.biblemap.put("b41_5_34", new visuperobject(41,5,34,"Ðức Chúa Jêsus phán rằng: Hỡi con gái ta, đức tin con đã cứu con; hãy đi cho bình an và được lành bịnh."));
visuperobject.biblemap.put("b41_5_35", new visuperobject(41,5,35,"Ðương khi Ngài còn phán, có kẻ đến từ nhà người cai nhà hội mà nói với người rằng: Con gái ông đã chết rồi; còn phiền Thầy làm chi?"));
visuperobject.biblemap.put("b41_5_36", new visuperobject(41,5,36,"Nhưng Ðức Chúa Jêsus chẳng màng đến lời ấy, phán cùng cai nhà hội rằng: Ðừng sợ, chỉ tin mà thôi."));
visuperobject.biblemap.put("b41_5_37", new visuperobject(41,5,37,"Ngài chẳng cho ai theo mình, trừ ra Phi -e-rơ, Gia-cơ, và Giăng là em là Gia-cơ."));
visuperobject.biblemap.put("b41_5_38", new visuperobject(41,5,38,"Khi đã đến nhà người cai nhà hội, Ngài thấy chúng làm om sòm, kẻ khóc người kêu lớn tiếng."));
visuperobject.biblemap.put("b41_5_39", new visuperobject(41,5,39,"Vào nhà rồi, Ngài phán cùng chúng rằng: Sao các ngươi làm ồn ào và khóc lóc vậy? Ðứa trẻ chẳng phải chết, song nó ngủ."));
visuperobject.biblemap.put("b41_5_40", new visuperobject(41,5,40,"Chúng nhạo báng Ngài. Ngài bèn đuổi chúng ra hết, đem cha mẹ đứa trẻ với những kẻ theo Ngài, cùng vào chỗ nó nằm."));
visuperobject.biblemap.put("b41_5_41", new visuperobject(41,5,41,"Ngài nắm tay nó mà phán rằng: Ta-li-tha Cu-mi; nghĩa là: Hỡi con gái nhỏ, ta truyền cho mầy, hãy chờ dậy."));
visuperobject.biblemap.put("b41_5_42", new visuperobject(41,5,42,"Tức thì đứa gái chờ dậy mà bước đi, vì đã lên mười hai tuổi. Chúng rất lấy làm lạ."));
visuperobject.biblemap.put("b41_5_43", new visuperobject(41,5,43,"Ngài cấm ngặt chúng đừng cho ai biết sự ấy, và truyền cho đứa trẻ ăn."));
visuperobject.biblemap.put("b41_6_1", new visuperobject(41,6,1,"Ðức Chúa Jêsus đi khỏi đó, đến quê hương mình, có các môn đồ cùng đi theo."));
visuperobject.biblemap.put("b41_6_2", new visuperobject(41,6,2,"Ðến ngày Sa-bát, Ngài bắt đầu dạy dỗ trong nhà hội; thiên hạ nghe Ngài, lấy làm lạ mà nói rằng: Người bởi đâu được những điều nầy? Sự khôn sáng mà người được ban cho là gì, và thể nào tay người làm được những phép lạ dường ấy?"));
visuperobject.biblemap.put("b41_6_3", new visuperobject(41,6,3,"Có phải người là thợ mộc, con trai Ma-ri, anh em với Gia-cơ, Giô-sê, Giu-đê, và Si-môn chăng? Chị em người há chẳng phải ở giữa chúng ta đây ư? Chúng bèn vấp phạm vì cớ Ngài."));
visuperobject.biblemap.put("b41_6_4", new visuperobject(41,6,4,"Song Ðức Chúa Jêsus phán cùng họ rằng: Ðấng tiên tri chỉ bị quê hương mình, bà con mình và trong nhà mình khinh dể mà thôi."));
visuperobject.biblemap.put("b41_6_5", new visuperobject(41,6,5,"Ở đó, Ngài không làm phép lạ nào được, chỉ đặt tay chữa lành một vài người đau ốm;"));
visuperobject.biblemap.put("b41_6_6", new visuperobject(41,6,6,"và Ngài lấy làm lạ vì chúng chẳng tin. Rồi Ngài đi khắp các làng gần đó mà giảng dạy."));
visuperobject.biblemap.put("b41_6_7", new visuperobject(41,6,7,"Ngài bèn kêu mười hai sứ đồ, bắt đầu sai đi từng đôi, ban quyền phép trừ tà ma."));
visuperobject.biblemap.put("b41_6_8", new visuperobject(41,6,8,"Ngài truyền cho sứ đồ đi đường đừng đem chi theo hết, hoặc bánh, hoặc bao, hoặc tiền bạc trong dây lưng, chỉ đem một cây gậy mà thôi;"));
visuperobject.biblemap.put("b41_6_9", new visuperobject(41,6,9,"chỉ mang dép, đừng mặc hai áo."));
visuperobject.biblemap.put("b41_6_10", new visuperobject(41,6,10,"Ngài phán cùng sứ đồ rằng: Hễ nhà nào các ngươi sẽ vào, hãy cứ ở đó, cho đến khi đi."));
visuperobject.biblemap.put("b41_6_11", new visuperobject(41,6,11,"Nếu có chỗ nào, người ta không chịu tiếp các ngươi, và cũng không nghe các ngươi nữa, thì hãy đi khỏi đó, và phủi bụi đã dính chơn mình, để làm chứng cho họ."));
visuperobject.biblemap.put("b41_6_12", new visuperobject(41,6,12,"Vậy, các sứ đồ đi ra, giảng cho người ta phải ăn năn;"));
visuperobject.biblemap.put("b41_6_13", new visuperobject(41,6,13,"đuổi ma quỉ, xức dầu cho nhiều kẻ bịnh và chữa cho được lành."));
visuperobject.biblemap.put("b41_6_14", new visuperobject(41,6,14,"Vả, vua Hê-rốt nghe nói về Ðức Chúa Jêsus (vì danh tiếng Ngài đã trở nên lừng lẫy), thì nói rằng: Giăng, là người làm phép báp-tem, đã từ kẻ chết sống lại; cho nên tự người làm ra các phép lạ."));
visuperobject.biblemap.put("b41_6_15", new visuperobject(41,6,15,"Người thì nói: Ấy là Ê-li; kẻ thì nói: Ấy là đấng tiên tri, như một trong các đấng tiên tri đời xưa."));
visuperobject.biblemap.put("b41_6_16", new visuperobject(41,6,16,"Nhưng Hê-rốt nghe vậy, thì nói rằng: Ấy là Giăng mà ta đã truyền chém, người đã sống lại."));
visuperobject.biblemap.put("b41_6_17", new visuperobject(41,6,17,"Số là, Hê-rốt đã sai người bắt Giăng, và truyền xiềng lại cầm trong ngục, bởi cớ Hê-đô-đia, vợ Phi-líp em vua, vì vua đã cưới nàng,"));
visuperobject.biblemap.put("b41_6_18", new visuperobject(41,6,18,"và Giăng có can vua rằng: Vua không nên lấy vợ em mình."));
visuperobject.biblemap.put("b41_6_19", new visuperobject(41,6,19,"Nhơn đó, Hê-rô-đia căm Giăng, muốn giết đi."));
visuperobject.biblemap.put("b41_6_20", new visuperobject(41,6,20,"Nhưng không thể giết, vì Hê-rốt sợ Giăng, biết là một người công bình và thánh. Vua vẫn gìn giữ người, khi nghe lời người rồi, lòng hằng bối rối, mà vua bằng lòng nghe."));
visuperobject.biblemap.put("b41_6_21", new visuperobject(41,6,21,"Nhưng, vừa gặp dịp tiện, là ngày sanh nhựt Hê-rốt, vua đãi tiệc các quan lớn trong triều, các quan võ, cùng các ngươi tôn trưởng trong xứ Ga-li-lê."));
visuperobject.biblemap.put("b41_6_22", new visuperobject(41,6,22,"Chính con gái Hê-rô-đia vào, nhảy múa, đẹp lòng Hê-rốt và các ngươi dự tiệc nữa. Vua nói cùng người con gái ấy rằng: Hãy xin ta điều chi ngươi muốn, ta sẽ cho."));
visuperobject.biblemap.put("b41_6_23", new visuperobject(41,6,23,"Vua lại thề cùng nàng rằng: Bất kỳ ngươi xin điều chi, ta cũng sẽ cho, dầu xin phân nửa nước ta cũng vậy."));
visuperobject.biblemap.put("b41_6_24", new visuperobject(41,6,24,"Nàng ra khỏi đó, nói với mẹ rằng: Tôi nên xin điều chi? Mẹ rằng: Cái đầu của Giăng Báp-tít."));
visuperobject.biblemap.put("b41_6_25", new visuperobject(41,6,25,"Tức thì nàng vội trở vào nơi vua mà xin rằng: Tôi muốn vua lập tức cho tôi cái đầu Giăng Báp-tít để trên mâm."));
visuperobject.biblemap.put("b41_6_26", new visuperobject(41,6,26,"Vua lấy làm buồn rầu lắm; nhưng vì cớ lời thề mình và khách dự yến, thì không muốn từ chối nàng."));
visuperobject.biblemap.put("b41_6_27", new visuperobject(41,6,27,"Vua liền sai một người lính thị vệ truyền đem đầu Giăng đến."));
visuperobject.biblemap.put("b41_6_28", new visuperobject(41,6,28,"Người ấy đi chém Giăng trong ngục; rồi để đầu trên mâm đem cho người con gái, và người con gái đem cho mẹ mình."));
visuperobject.biblemap.put("b41_6_29", new visuperobject(41,6,29,"Môn đồ Giăng nghe tin, đến lấy xác người chôn trong mả."));
visuperobject.biblemap.put("b41_6_30", new visuperobject(41,6,30,"Các sứ đồ nhóm lại cùng Ðức Chúa Jêsus, thuật cho Ngài mọi điều mình đã làm và dạy."));
visuperobject.biblemap.put("b41_6_31", new visuperobject(41,6,31,"Ngài phán cùng sứ đồ rằng: Hãy đi tẻ ra trong nơi vắng vẻ, nghỉ ngơi một chút. Vì có kẻ đi người lại đông lắm, đến nỗi Ngài và sứ đồ không có thì giờ mà ăn."));
visuperobject.biblemap.put("b41_6_32", new visuperobject(41,6,32,"Vậy, Ngài và sứ đồ xuống thuyền đặng đi tẻ ra trong nơi vắng vẻ."));
visuperobject.biblemap.put("b41_6_33", new visuperobject(41,6,33,"Nhưng nhiều người thấy đi, thì nhận biết; dân chúng từ khắp các thành đều chạy bộ đến nơi Chúa cùng sứ đồ vừa đi, và đã tới đó trước."));
visuperobject.biblemap.put("b41_6_34", new visuperobject(41,6,34,"Bấy giờ Ðức Chúa Jêsus ở thuyền bước ra, thấy đoàn dân đông lắm, thì Ngài động lòng thương xót đến, vì như chiên không có người chăn; Ngài bèn khởi sự dạy dỗ họ nhiều điều."));
visuperobject.biblemap.put("b41_6_35", new visuperobject(41,6,35,"Trời đã chiều, môn đồ tới gần Ngài mà thưa rằng: Chỗ nầy là vắng vẻ, và trời đã chiều rồi;"));
visuperobject.biblemap.put("b41_6_36", new visuperobject(41,6,36,"xin cho dân chúng về, để họ đi các nơi nhà quê và các làng gần đây đặng mua đồ ăn."));
visuperobject.biblemap.put("b41_6_37", new visuperobject(41,6,37,"Ngài đáp rằng: Chính các ngươi phải cho họ ăn. Môn đồ thưa rằng: chúng tôi sẽ đi mua đến hai trăm đơ-ni-ê bánh để cho họ ăn hay sao?"));
visuperobject.biblemap.put("b41_6_38", new visuperobject(41,6,38,"Ngài hỏi rằng: Các ngươi có bao nhiêu bánh? Hãy đi xem thử. Môn đồ xem xét rồi, thưa rằng: Năm cái bánh và hai con cá."));
visuperobject.biblemap.put("b41_6_39", new visuperobject(41,6,39,"Ngài bèn truyền cho môn đồ biểu chúng ngồi xuống hết thảy từng chòm trên cỏ xanh."));
visuperobject.biblemap.put("b41_6_40", new visuperobject(41,6,40,"Chúng ngồi xuống từng hàng, hàng thì một trăm, hàng thì năm chục."));
visuperobject.biblemap.put("b41_6_41", new visuperobject(41,6,41,"Ðức Chúa Jêsus lấy năm cái bánh và hai con cá, ngước mắt lên trời, tạ ơn, rồi bẻ bánh ra mà trao cho môn đồ, đặng phát cho đoàn dân; lại cũng chia hai con cá cho họ nữa."));
visuperobject.biblemap.put("b41_6_42", new visuperobject(41,6,42,"Ai nấy ăn no nê rồi;"));
visuperobject.biblemap.put("b41_6_43", new visuperobject(41,6,43,"người ta lượm được mười hai giỏ đầy những miếng bánh và cá còn thừa lại."));
visuperobject.biblemap.put("b41_6_44", new visuperobject(41,6,44,"Vả, số những kẻ đã ăn chừng năm ngàn người."));
visuperobject.biblemap.put("b41_6_45", new visuperobject(41,6,45,"Rồi Ngài liền giục môn đồ vào thuyền, qua bờ bên kia trước mình, hướng đến thành Bết-sai-đa, trong khi Ngài cho dân chúng về."));
visuperobject.biblemap.put("b41_6_46", new visuperobject(41,6,46,"Ngài lìa khỏi chúng rồi, thì lên núi mà cầu nguyện."));
visuperobject.biblemap.put("b41_6_47", new visuperobject(41,6,47,"Ðến tối, chiếc thuyền đương ở giữa biển, còn Ngài thì ở một mình trên đất."));
visuperobject.biblemap.put("b41_6_48", new visuperobject(41,6,48,"Ngài thấy môn đồ chèo khó nhọc lắm, vì gió ngược. Lối canh tư đêm ấy, Ngài đi bộ trên biển mà đến cùng môn đồ; và muốn đi trước."));
visuperobject.biblemap.put("b41_6_49", new visuperobject(41,6,49,"Môn đồ thấy Ngài đi bộ trên mặt biển ngỡ là ma, nên la lên;"));
visuperobject.biblemap.put("b41_6_50", new visuperobject(41,6,50,"vì ai nấy đều thấy Ngài và sợ hoảng. nhưng Ngài liền nói chuyện với môn đồ và phán rằng: Hãy yên lòng, ta đây đừng sợ chi."));
visuperobject.biblemap.put("b41_6_51", new visuperobject(41,6,51,"Ngài bèn bước lên thuyền với môn đồ, thì gió lặng. Môn đồ càng lấy làm lạ hơn nữa;"));
visuperobject.biblemap.put("b41_6_52", new visuperobject(41,6,52,"vì chẳng hiểu phép lạ về mấy cái bánh, bởi lòng cứng cỏi."));
visuperobject.biblemap.put("b41_6_53", new visuperobject(41,6,53,"Khi Ngài và môn đồ đã qua khỏi biển, đến xứ Ghê-nê-xa-rết, thì ghé thuyền vào bờ."));
visuperobject.biblemap.put("b41_6_54", new visuperobject(41,6,54,"Vừa ở trong thuyền bước ra, có dân chúng nhận biết Ngài,"));
visuperobject.biblemap.put("b41_6_55", new visuperobject(41,6,55,"chạy khắp cả miền đó, khiêng những người đau nằm trên giường, hễ nghe Ngài ở đâu thì đem đến đó."));
visuperobject.biblemap.put("b41_6_56", new visuperobject(41,6,56,"Ngài đến nơi nào, hoặc làng, thành, hay chốn nhà quê, người ta đem người đau để tại các chợ, và xin Ngài cho mình ít nữa được rờ đến trôn áo Ngài; những kẻ đã rờ đều được lành bịnh cả."));
visuperobject.biblemap.put("b41_7_1", new visuperobject(41,7,1,"Bấy giờ các người Pha-ri-si và mấy thầy thông giáo từ thành Giê-ru-sa-lem đến, nhóm lại cùng Ðức Chúa Jêsus,"));
visuperobject.biblemap.put("b41_7_2", new visuperobject(41,7,2,"thấy một vài môn đồ Ngài dùng tay chưa tinh sạch mà ăn, nghĩa là không rửa tay."));
visuperobject.biblemap.put("b41_7_3", new visuperobject(41,7,3,"Vả, người Pha-ri-si và người Giu-đa thường giữ lời truyền khẩu của người xưa, nếu không rửa tay cẩn thận thì không ăn;"));
visuperobject.biblemap.put("b41_7_4", new visuperobject(41,7,4,"khi họ ở chợ về, nếu không rửa cũng không ăn. Họ lại còn theo lời truyền khẩu mà giữ nhiều sự khác nữa, như rửa chén và bình bằng đất hoặc bằng đồng."));
visuperobject.biblemap.put("b41_7_5", new visuperobject(41,7,5,"Vậy, các người Pha-ri-si và các thầy thông giáo hỏi Ngài rằng: Sao môn đồ thầy không giữ lời truyền khẩu của người xưa, dùng tay chưa tinh sạch mà ăn như vậy?"));
visuperobject.biblemap.put("b41_7_6", new visuperobject(41,7,6,"Ngài đáp rằng: Hỡi kẻ giả hình, Ê-sai đã nói tiên tri về việc các ngươi phải lắm, như có chép rằng: Dân nầy lấy môi miếng thờ kính ta; Nhưng lòng chúng nó xa ta lắm."));
visuperobject.biblemap.put("b41_7_7", new visuperobject(41,7,7,"Sự chúng nó thờ lạy ta là vô ích, Vì chúng nó dạy theo những điều răn mà chỉ bởi người ta đặt ra."));
visuperobject.biblemap.put("b41_7_8", new visuperobject(41,7,8,"Các ngươi bỏ điều răn của Ðức Chúa Trời, mà giữ lời truyền khẩu của loài người!"));
visuperobject.biblemap.put("b41_7_9", new visuperobject(41,7,9,"Ngài cũng phán cùng họ rằng: Các ngươi bỏ hẳn điều răn của Ðức Chúa Trời, đặng giữ theo lời truyền khẩu của mình."));
visuperobject.biblemap.put("b41_7_10", new visuperobject(41,7,10,"Vì Môi-se có nói: Hãy tôn kính cha mẹ ngươi; lại nói: Ai rủa sả cha mẹ, thì phải bị giết."));
visuperobject.biblemap.put("b41_7_11", new visuperobject(41,7,11,"Nhưng các ngươi nói rằng: Nếu ai nói với cha hay mẹ mình rằng: Mọi điều tôi sẽ giúp cha mẹ được, thì đã là co-ban (nghĩa là sự dâng cho Ðức Chúa Trời),"));
visuperobject.biblemap.put("b41_7_12", new visuperobject(41,7,12,"vậy người ấy không được phép giúp cha mẹ mình sự gì nữa;"));
visuperobject.biblemap.put("b41_7_13", new visuperobject(41,7,13,"dường ấy, các ngươi lấy lời truyền khẩu mình lập ra mà bỏ mất lời Ðức Chúa Trời. Các ngươi còn làm nhiều việc khác nữa giống như vậy."));
visuperobject.biblemap.put("b41_7_14", new visuperobject(41,7,14,"Bấy giờ, Ngài lại kêu đoàn dân mà phán rằng: Các ngươi ai nấy hãy nghe ta và hiểu:"));
visuperobject.biblemap.put("b41_7_15", new visuperobject(41,7,15,"Chẳng sự gì từ ngoài vào trong cơ thể làm dơ dáy người được; nhưng sự gì từ trong người ra, đó là sự làm dơ dáy người."));
visuperobject.biblemap.put("b41_7_16", new visuperobject(41,7,16,"Nếu ai có tai mà nghe, hãy nghe."));
visuperobject.biblemap.put("b41_7_17", new visuperobject(41,7,17,"Khi Ngài vào trong nhà, cách xa đoàn dân rồi, môn đồ hỏi Ngài về lời thí dụ ấy."));
visuperobject.biblemap.put("b41_7_18", new visuperobject(41,7,18,"Ngài phán rằng: Vậy chớ các ngươi cũng không có trí khôn sao? Chưa hiểu chẳng có sự gì ở ngoài vào trong người mà làm dơ dáy người được sao?"));
visuperobject.biblemap.put("b41_7_19", new visuperobject(41,7,19,"Vả, sự đó không vào lòng người, nhưng vào bụng, rồi bị bỏ ra nơi kín đáo, như vậy làm cho mọi đồ ăn được sạch."));
visuperobject.biblemap.put("b41_7_20", new visuperobject(41,7,20,"Vậy, Ngài phán: Hễ sự gì từ người ra, đó là sự làm dơ dáy người!"));
visuperobject.biblemap.put("b41_7_21", new visuperobject(41,7,21,"Vì thật là tự trong, tự lòng người mà ra những ác tưởng, sự dâm dục, trộm cướp, giết người,"));
visuperobject.biblemap.put("b41_7_22", new visuperobject(41,7,22,"tà dâm, tham lam, hung ác, gian dối, hoang đàng, con mắt ganh đố, lộng ngôn, kiêu ngạo, điên cuồng."));
visuperobject.biblemap.put("b41_7_23", new visuperobject(41,7,23,"Hết thảy những điều xấu ấy ra từ trong lòng thì làm cho dơ dáy người."));
visuperobject.biblemap.put("b41_7_24", new visuperobject(41,7,24,"Ðoạn, Ngài từ đó đi đến bờ cõi thành Ty-rơ và thành Si-đôn. Ngài vào một cái nhà, không muốn cho ai biết mình, song không thể ẩn được."));
visuperobject.biblemap.put("b41_7_25", new visuperobject(41,7,25,"Vì có một người đờn bà, con gái người bị tà ma ám, đã nghe nói về Ngài, tức thì vào gieo mình dưới chơn Ngài."));
visuperobject.biblemap.put("b41_7_26", new visuperobject(41,7,26,"Ðờn bà ấy là người Gờ-réc, dân Sy-rô-phê-ni-xi. Người xin Ngài đuổi quỉ ra khỏi con gái mình."));
visuperobject.biblemap.put("b41_7_27", new visuperobject(41,7,27,"Ðức Chúa Jêsus phán cùng người rằng: Hãy để con cái ăn no nê trước đã; vì không lẽ lấy bánh của con cái quăng cho chó."));
visuperobject.biblemap.put("b41_7_28", new visuperobject(41,7,28,"Song người đáp lại rằng: Lạy Chúa, hẳn vậy rồi; nhưng mà chó dưới bàn ăn mấy miếng bánh vụn của con cái."));
visuperobject.biblemap.put("b41_7_29", new visuperobject(41,7,29,"Ngài bèn phán: Hãy đi, vì lời ấy, quỉ đã ra khỏi con gái ngươi rồi."));
visuperobject.biblemap.put("b41_7_30", new visuperobject(41,7,30,"Khi người trở về nhà, thấy đứa trẻ nằm trên giường, và quỉ đã bị đuổi khỏi."));
visuperobject.biblemap.put("b41_7_31", new visuperobject(41,7,31,"Ðức Chúa Jêsus lìa bờ cõi thành Ty-rơ, qua thành Si-đôn và trải địa phận Ðê-ca-bô-lơ, đến biển Ga-li-lê."));
visuperobject.biblemap.put("b41_7_32", new visuperobject(41,7,32,"Người ta đem một người điếc và ngọng đến cùng Ngài, xin Ngài đặt tay trên người."));
visuperobject.biblemap.put("b41_7_33", new visuperobject(41,7,33,"Ngài đem riêng người ra, cách xa đám đông, rồi để ngón tay vào lỗ tai người, và thấm nước miếng xức lưỡi người."));
visuperobject.biblemap.put("b41_7_34", new visuperobject(41,7,34,"Ðoạn Ngài ngửa mặt lên trời, thở ra mà phán cùng người rằng: Ép-pha-ta! nghĩa là: Hãy mở ra!"));
visuperobject.biblemap.put("b41_7_35", new visuperobject(41,7,35,"Tức thì tai được mở ra, lưỡi được thong thả, người nói rõ ràng."));
visuperobject.biblemap.put("b41_7_36", new visuperobject(41,7,36,"Ðức Chúa Jêsus cấm chúng chớ nói điều đó cùng ai; song Ngài càng cấm người ta lại càng nói đến nữa."));
visuperobject.biblemap.put("b41_7_37", new visuperobject(41,7,37,"Chúng lại lấy làm lạ quá đỗi mà rằng: Ngài làm mọi việc được tốt lành: khiến kẻ điếc nghe, kẻ câm nói vậy!"));
visuperobject.biblemap.put("b41_8_1", new visuperobject(41,8,1,"Những ngày đó, lại có một đoàn dân đông theo Ðức Chúa Jêsus. Vì chúng không có chi ăn, nên Ngài kêu các môn đồ mà phán rằng:"));
visuperobject.biblemap.put("b41_8_2", new visuperobject(41,8,2,"Ta thương xót đoàn dân nầy: vì đã ba ngày nay, không rời bỏ ta, và chẳng có chi ăn."));
visuperobject.biblemap.put("b41_8_3", new visuperobject(41,8,3,"Nếu ta truyền cho nhịn đói trở về nơi họ ở, dọc đàng chắc sẽ kém sức, vì có mấy người đã đến từ nơi xa."));
visuperobject.biblemap.put("b41_8_4", new visuperobject(41,8,4,"Môn đồ thưa rằng: Có thể kiếm bánh đâu được trong đồng vắng nầy cho chúng ăn no ư?"));
visuperobject.biblemap.put("b41_8_5", new visuperobject(41,8,5,"Ngài nói: Các ngươi có bao nhiêu bánh? Thưa: Có bảy cái."));
visuperobject.biblemap.put("b41_8_6", new visuperobject(41,8,6,"Ngài bèn biểu đoàn dân ngồi xuống đất; đoạn lấy bảy cái bánh, tạ ơn, rồi bẻ ra trao cho môn đồ để phân phát cho đoàn dân."));
visuperobject.biblemap.put("b41_8_7", new visuperobject(41,8,7,"Môn đồ cũng có mấy con cá nhỏ; Ðức Chúa Jêsus tạ ơn rồi, cũng khiến đem phân phát như vậy."));
visuperobject.biblemap.put("b41_8_8", new visuperobject(41,8,8,"Ai nấy ăn no nê; rồi người ta thâu được bảy giỏ những miếng thừa."));
visuperobject.biblemap.put("b41_8_9", new visuperobject(41,8,9,"Và, những người ăn đó ước chừng bốn ngàn người. Ðức Chúa Jêsus cho chúng về;"));
visuperobject.biblemap.put("b41_8_10", new visuperobject(41,8,10,"rồi liền vào thuyền với môn đồ, sang xứ Ða-ma-nu-tha."));
visuperobject.biblemap.put("b41_8_11", new visuperobject(41,8,11,"Có mấy người Pha-ri-si đến đó, cãi lẽ với Ngài, muốn thử Ngài, thì xin một dấu lạ từ trên trời."));
visuperobject.biblemap.put("b41_8_12", new visuperobject(41,8,12,"Ðức Chúa Jêsus than thở trong lòng mà rằng: Làm sao dòng dõi nầy xin dấu lạ? Quả thật, ta nói cùng các ngươi, họ chẳng được ban cho một dấu lạ nào."));
visuperobject.biblemap.put("b41_8_13", new visuperobject(41,8,13,"Ngài bèn bỏ họ, trở vào thuyền mà qua bờ bên kia."));
visuperobject.biblemap.put("b41_8_14", new visuperobject(41,8,14,"Vả, môn đồ quên đem bánh theo, trong thuyền chỉ có một cái bánh mà thôi."));
visuperobject.biblemap.put("b41_8_15", new visuperobject(41,8,15,"Ngài răn môn đồ rằng: Hãy giữ mình cẩn thận về men người Pha-ri-si, và men đãng Hê-rốt."));
visuperobject.biblemap.put("b41_8_16", new visuperobject(41,8,16,"Môn-đồ nói cùng nhau rằng: Ấy là vì chúng ta không có bánh."));
visuperobject.biblemap.put("b41_8_17", new visuperobject(41,8,17,"Nhưng Ðức Chúa Jêsus biết điều đó, thì phán rằng: Sao các ngươi nói cùng nhau rằng đó là tại các ngươi không có bánh? Chớ các ngươi còn chưa nghe, chưa hiểu sao? Các ngươi có lòng cứng cỏi luôn ư?"));
visuperobject.biblemap.put("b41_8_18", new visuperobject(41,8,18,"Các ngươi lại không nhớ hay sao? Có tai mà sao không nghe? Các ngươi lại không nhớ hay sao?"));
visuperobject.biblemap.put("b41_8_19", new visuperobject(41,8,19,"Khi ta bẻ năm cái bánh cho năm ngàn người, các ngươi thâu được mấy giỏ đầy bánh vụn? Thưa: Mười hai giỏ."));
visuperobject.biblemap.put("b41_8_20", new visuperobject(41,8,20,"Còn khi ta bẻ bảy cái bánh cho bốn ngàn người, các ngươi thâu được bấy giỏ đầy bánh vụn? Thưa: Bảy giỏ."));
visuperobject.biblemap.put("b41_8_21", new visuperobject(41,8,21,"Ngài phán: Các ngươi còn chưa hiểu sao?"));
visuperobject.biblemap.put("b41_8_22", new visuperobject(41,8,22,"Kế đó, Ðức Chúa Jêsus và môn đồ đi đến làng Bết-sai-đa. Người ta đem cho Ngài một người mù và cầu xin Ngài rờ đến người."));
visuperobject.biblemap.put("b41_8_23", new visuperobject(41,8,23,"Ngài bèn nắm tay người mù, dắt ra ngoài làng, thấm nước miếng trên mắt người, đặt tay trên người, và hỏi, có thấy chi không?"));
visuperobject.biblemap.put("b41_8_24", new visuperobject(41,8,24,"Người mù ngó lên rồi thưa rằng: Tôi thấy người ta, và nhìn họ giống như cây."));
visuperobject.biblemap.put("b41_8_25", new visuperobject(41,8,25,"Rồi Ðức Chúa Jêsus lại đặt tay trên mắt người; người nhìn xem, được sáng mắt thấy rõ ràng cả thảy."));
visuperobject.biblemap.put("b41_8_26", new visuperobject(41,8,26,"Ðức Chúa Jêsus khiến người về nhà, và truyền rằng: Ðừng trở vào trong làng."));
visuperobject.biblemap.put("b41_8_27", new visuperobject(41,8,27,"Ðức Chúa Jêsus đi khỏi đó với môn đồ mình, đến trong các làng gần thành Sê-sa-rê phi-líp. Dọc đàng, Ngài hỏi môn đồ rằng: Người ta nói ta là ai?"));
visuperobject.biblemap.put("b41_8_28", new visuperobject(41,8,28,"Thưa rằng: Kẻ thì nói là Giăng Báp-tít; người thì nói là Ê-li; kẻ thì nói một trong các đấng tiên tri."));
visuperobject.biblemap.put("b41_8_29", new visuperobject(41,8,29,"Ngài hỏi: Nhưng các ngươi thì nói ta là ai? Phi -e-rơ thưa rằng: Thầy là Ðấng Christ."));
visuperobject.biblemap.put("b41_8_30", new visuperobject(41,8,30,"Ðức Chúa Jêsus bèn cấm ngặt môn đồ chớ nói sự mình cùng ai hết."));
visuperobject.biblemap.put("b41_8_31", new visuperobject(41,8,31,"Bấy giờ, nước khởi sự dạy môn đồ rằng Con người phải chịu khổ nhiều, phải bị các trưởng lão, các thầy tế lễ cả, và các thầy thông giáo loại ra, phải chịu giết, sau ba ngày phải sống lại."));
visuperobject.biblemap.put("b41_8_32", new visuperobject(41,8,32,"Ngài nói tỏ tường điều đó với môn đồ. Phi -e-rơ đem Ngài riêng ra, mà căn gián Ngài."));
visuperobject.biblemap.put("b41_8_33", new visuperobject(41,8,33,"Nhưng Ðức Chúa Jêsus xây lại ngó môn đồ, và quở Phi -e-rơ rằng: Hỡi quỉ Sa-tan, hãy lui ra đằng sau ta; vì người chẳng nghĩ đến việc Ðức Chúa Trời, song nghĩ đến việc người ta."));
visuperobject.biblemap.put("b41_8_34", new visuperobject(41,8,34,"Ðoạn, Ngài kêu dân chúng và môn đồ, mà phán rằng: Nếu ai muốn theo ta, phải liều mình, vác thập tự giá mình mà theo ta."));
visuperobject.biblemap.put("b41_8_35", new visuperobject(41,8,35,"Vì ai muốn cứu sự sống mình thì sẽ mất; còn ai vì cớ ta và đạo Tin Lành mà mất sự sống thì sẽ cứu."));
visuperobject.biblemap.put("b41_8_36", new visuperobject(41,8,36,"Người nào nếu được cả thiên hạ mà mất linh hồn mình, thì có ích gì?"));
visuperobject.biblemap.put("b41_8_37", new visuperobject(41,8,37,"Hay là có người nào lấy chi mà đổi linh hồn mình ư?"));
visuperobject.biblemap.put("b41_8_38", new visuperobject(41,8,38,"Vì giữa dòng dõi gian dâm tội lỗi nầy, hễ ai hổ thẹn về ta và đạo ta, thì Con người sẽ hổ thẹn về kẻ ấy, khi ngự trong sự vinh hiển của Cha Ngài mà đến với các thiên sứ thánh."));
visuperobject.biblemap.put("b41_9_1", new visuperobject(41,9,1,"Ngài lại phán cùng môn đồ rằng: Quả thật, ta nói cùng các ngươi, trong những người đứng đây, có mấy kẻ chẳng chết trước khi chưa thấy nước Ðức Chúa Trời lấy quyền phép mà đến."));
visuperobject.biblemap.put("b41_9_2", new visuperobject(41,9,2,"Khỏi sáu ngày, Ðức Chúa Jêsus đem Phi -e-rơ, Gia-cơ, và Giăng đi tẻ với Ngài lên núi cao; và Ngài hóa hình trước mặt ba người."));
visuperobject.biblemap.put("b41_9_3", new visuperobject(41,9,3,"Áo xống Ngài trở nên sáng rực và trắng tinh chói lòa, đến nỗi chẳng có thợ phiếu nào ở thế gian phiếu được trắng như vậy."));
visuperobject.biblemap.put("b41_9_4", new visuperobject(41,9,4,"Ê-li và Môi-se hiện ra, nói chuyện cùng Ðức Chúa Jêsus."));
visuperobject.biblemap.put("b41_9_5", new visuperobject(41,9,5,"Phi -e-rơ bèn cất tiếng thưa cùng Ðức Chúa Jêsus rằng: Lạy thầy, chúng ta ở đây tốt lắm; hãy đóng ba trại, một cái cho thầy, một cái cho Môi-se, và một cái cho Ê-li."));
visuperobject.biblemap.put("b41_9_6", new visuperobject(41,9,6,"Vì Phi -e-rơ không biết mình nói chi, tại cả ba đều sợ hãi."));
visuperobject.biblemap.put("b41_9_7", new visuperobject(41,9,7,"Lại có một đám mây bao phủ lấy; và từ đám mây có tiếng phán rằng: Người nầy là Con rất yêu dấu của ta, hãy vâng nghe người."));
visuperobject.biblemap.put("b41_9_8", new visuperobject(41,9,8,"Thình lình, các môn đồ ngó quanh quất, chẳng thấy ai nữa, chỉ còn một mình Ðức Chúa Jêsus ở với mình mà thôi."));
visuperobject.biblemap.put("b41_9_9", new visuperobject(41,9,9,"Khi từ trên núi xuống, Ngài cấm môn đồ đừng nói lại với ai những điều mình đã thấy, cho đến chừng nào Con người từ kẻ chết sống lại."));
visuperobject.biblemap.put("b41_9_10", new visuperobject(41,9,10,"Vậy, môn đồ ghi nhớ lời ấy, và hỏi nhau sự từ kẻ chết sống lại là gì."));
visuperobject.biblemap.put("b41_9_11", new visuperobject(41,9,11,"Lại hỏi nói Ngài rằng: Sao các thầy thông giáo nói rằng: Ê-li phải đến trước?"));
visuperobject.biblemap.put("b41_9_12", new visuperobject(41,9,12,"Ngài đáp rằng: Thật Ê-li phải đến trước sửa lại mọi việc. Vậy thì sao có chép về Con người rằng Ngài phải chịu khổ nhiều và bị khinh dể ư?"));
visuperobject.biblemap.put("b41_9_13", new visuperobject(41,9,13,"Vả, ta nói cùng các ngươi, Ê-li đã đến rồi, người ta đãi người theo ý muốn mình, y như lời đã chép về việc người vậy."));
visuperobject.biblemap.put("b41_9_14", new visuperobject(41,9,14,"Ðức Chúa Jêsus với ba người đến cùng các môn đồ khác, thì thấy đoàn dân rất đông vây chung quanh, và mấy thầy thông giáo đương cãi lẽ với các môn đồ ấy."));
visuperobject.biblemap.put("b41_9_15", new visuperobject(41,9,15,"Cả đoàn dân nầy vừa thấy Ngài, liền lấy làm lạ, thảy đều chạy đến chào Ngài."));
visuperobject.biblemap.put("b41_9_16", new visuperobject(41,9,16,"Ngài bèn hỏi rằng: Các ngươi cãi lẽ với môn đồ về việc gì?"));
visuperobject.biblemap.put("b41_9_17", new visuperobject(41,9,17,"Một người trong đám đông thưa rằng: Lạy thầy, tôi đã đem con trai tôi tới cho thầy; nó bị quỉ câm ám,"));
visuperobject.biblemap.put("b41_9_18", new visuperobject(41,9,18,"không cứ chỗ nào quỉ ám vào thì làm cho nổi kinh phong, sôi bọt mồm, nghiến răng, rồi nó mòn mỏi đi; tôi đã xin môn đồ thầy đuổi quỉ ấy, song đuổi không được."));
visuperobject.biblemap.put("b41_9_19", new visuperobject(41,9,19,"Ðức Chúa Jêsus bèn đáp rằng: Hỡi dòng dõi chẳng tin kia, ta sẽ ở cùng các ngươi cho đến chừng nào? Ta sẽ chịu các ngươi cho đến khi nào? Hãy đem con đến cho ta."));
visuperobject.biblemap.put("b41_9_20", new visuperobject(41,9,20,"Chúng bèn đem đứa trẻ cho Ngài. Ðứa trẻ vừa thấy Ðức Chúa Jêsus, tức thì ngã xuống đất, rồi lăn lóc sôi bọt miếng ra."));
visuperobject.biblemap.put("b41_9_21", new visuperobject(41,9,21,"Ðức Chúa Jêsus hỏi cha nó rằng: Ðiều đó xảy đến cho nó đã bao lâu? Người cha thưa rằng: Từ khi nó còn nhỏ."));
visuperobject.biblemap.put("b41_9_22", new visuperobject(41,9,22,"Quỉ đã lắm phen quăng nó trong lửa và dưới nước, để giết nó đi; nhưng nếu thầy làm được việc gì, xin thương xót chúng tôi và giúp cho!"));
visuperobject.biblemap.put("b41_9_23", new visuperobject(41,9,23,"Ðức Chúa Jêsus đáp rằng: Sao ngươi nói: Nếu thầy làm được? Kẻ nào tin thì mọi việc đều được cả."));
visuperobject.biblemap.put("b41_9_24", new visuperobject(41,9,24,"Tức thì cha đứa trẻ la lên rằng: Tôi tin; xin Chúa giúp đỡ trong sự không tin của tôi!"));
visuperobject.biblemap.put("b41_9_25", new visuperobject(41,9,25,"Khi Ðức Chúa Jêsus thấy dân chúng chạy đến đông, thì Ngài quở trách tà ma và phán cùng nó rằng: Hỡi quỉ câm và điếc, ta biểu mầy phải ra khỏi đứa trẻ nầy, đừng ám nó nữa."));
visuperobject.biblemap.put("b41_9_26", new visuperobject(41,9,26,"Quỉ bèn la lớn tiếng lên, vật đứa trẻ mạnh lắm mà ra khỏi; đứa trẻ trở như chết vậy, nên nỗi nhiều người nói rằng: Nó chết rồi."));
visuperobject.biblemap.put("b41_9_27", new visuperobject(41,9,27,"Nhưng Ðức Chúa Jêsus nắm tay nó, nâng lên; thì nó đứng dậy."));
visuperobject.biblemap.put("b41_9_28", new visuperobject(41,9,28,"Khi Ðức Chúa Jêsus vào nhà rồi, môn đồ hỏi riêng Ngài rằng: Sao chúng tôi đuổi quỉ ấy không được?"));
visuperobject.biblemap.put("b41_9_29", new visuperobject(41,9,29,"Ngài đáp rằng: Nếu không cầu nguyện, thì chẳng ai đuổi thứ quỉ ấy ra được."));
visuperobject.biblemap.put("b41_9_30", new visuperobject(41,9,30,"Ðoạn, đi khỏi đó, trải qua xứ Ga-li-lê, Ðức Chúa Jêsus không muốn cho ai biết."));
visuperobject.biblemap.put("b41_9_31", new visuperobject(41,9,31,"Vì Ngài dạy các môn đồ rằng: Con người sẽ bị nộp trong tay người ta, họ sẽ giết đi; Ngài bị giết đã ba ngày rồi, thì sẽ sống lại."));
visuperobject.biblemap.put("b41_9_32", new visuperobject(41,9,32,"Nhưng môn đồ không hiểu lời ấy, lại sợ không dám hỏi Ngài."));
visuperobject.biblemap.put("b41_9_33", new visuperobject(41,9,33,"Ðến thành Ca-bê-na-um, đang ở trong nhà, Ngài hỏi môn đồ rằng: Lúc đi đường, các ngươi nói chi với nhau?"));
visuperobject.biblemap.put("b41_9_34", new visuperobject(41,9,34,"Môn đồ đều làm thinh; vì dọc đường đã cãi nhau cho biết ai là lớn hơn trong bọn mình."));
visuperobject.biblemap.put("b41_9_35", new visuperobject(41,9,35,"Ngài bèn ngồi, kêu mười hai sứ đồ mà phán rằng: Nếu ai muốn làm đầu, thì phải làm rốt hết và làm tôi tớ mọi người."));
visuperobject.biblemap.put("b41_9_36", new visuperobject(41,9,36,"Ðoạn, Ngài bắt một đứa trẻ để ở giữa môn đồ; rồi ẵm nó trong tay, mà phán rằng:"));
visuperobject.biblemap.put("b41_9_37", new visuperobject(41,9,37,"Hễ ai vì danh ta tiếp một đứa trong những đứa trẻ nầy, tức là tiếp ta; còn ai tiếp ta, thì chẳng phải tiếp ta, bèn là tiếp Ðấng đã sai ta vậy."));
visuperobject.biblemap.put("b41_9_38", new visuperobject(41,9,38,"Giăng thưa cùng Ngài rằng: Lạy thầy, chúng tôi từng thấy có người lấy danh thầy mà trừ quỉ, thì chúng tôi đã cấm, vì họ không theo chúng ta."));
visuperobject.biblemap.put("b41_9_39", new visuperobject(41,9,39,"Ðức Chúa Jêsus phán rằng: Ðừng cấm làm chi; vì chẳng ai cùng trong một lúc, vừa nhơn danh ta làm phép lạ, vừa nói xấu ta được."));
visuperobject.biblemap.put("b41_9_40", new visuperobject(41,9,40,"Hễ ai không nghịch cùng ta là thuộc về ta."));
visuperobject.biblemap.put("b41_9_41", new visuperobject(41,9,41,"Còn ai nhơn danh ta mà cho các ngươi một chén nước, vì các ngươi thuộc về Ðấng Christ, quả thật, ta nói cùng các ngươi, người ấy sẽ không mất phần thưởng mình đâu."));
visuperobject.biblemap.put("b41_9_42", new visuperobject(41,9,42,"Nhưng hễ ai làm cho một đứa trong những đứa nhỏ nầy đã tin, phải sa vào tội lỗi, thì thà buộc cối đá lớn vào cổ nó mà bỏ xuống biển còn hơn."));
visuperobject.biblemap.put("b41_9_43", new visuperobject(41,9,43,"Nếu tay ngươi làm cho ngươi phạm tội, hãy chặt nó đi; thà rằng một tay mà vào sự sống, còn hơn đủ hai tay mà sa xuống địa ngục, trong lửa chẳng hề tắt."));
visuperobject.biblemap.put("b41_9_44", new visuperobject(41,9,44,"Ðó là nơi sâu bọ của chúng nó chẳng hề chết, và là nơi lửa chẳng hề tắt."));
visuperobject.biblemap.put("b41_9_45", new visuperobject(41,9,45,"Lại nếu chơn ngươi làm cho ngươi phạm tội, hãy chặt nó đi; thà rằng què chơn mà vào sự sống, còn hơn là đủ hai chơn mà bị quăng vào địa ngục."));
visuperobject.biblemap.put("b41_9_46", new visuperobject(41,9,46,"Ðó là nơi sâu bọ của chúng nó chẳng hề chết, và là nơi lửa chẳng hề tắt."));
visuperobject.biblemap.put("b41_9_47", new visuperobject(41,9,47,"Còn nếu mắt ngươi làm cho ngươi phạm tội, hãy móc nó đi; thà rằng chỉ một mắt mà vào nước Ðức Chúa Trời, còn hơn đủ hai mắt mà bị quăng vào địa ngục,"));
visuperobject.biblemap.put("b41_9_48", new visuperobject(41,9,48,"đó là nơi sâu bọ của chúng nó chẳng hề chết và là nơi lửa chẳng hề tắt."));
visuperobject.biblemap.put("b41_9_49", new visuperobject(41,9,49,"Vì mỗi người sẽ bị muối bằng lửa."));
visuperobject.biblemap.put("b41_9_50", new visuperobject(41,9,50,"Muối là vật tốt, nhưng nếu mất mặn đi, thì lấy chi làm cho mặn lại được? (9:51) Các ngươi phải có muối trong lòng mình, lại phải hòa thuận cùng nhau."));
visuperobject.biblemap.put("b41_10_1", new visuperobject(41,10,1,"Ðức Chúa Jêsus từ đó mà đi, qua bờ cõi xứ Giu-đê, bên kia sông Giô-đanh. Ðoàn dân đông lại nhóm họp cùng Ngài, Ngài dạy dỗ chúng y như lệ thường."));
visuperobject.biblemap.put("b41_10_2", new visuperobject(41,10,2,"Các người Pha-ri-si bèn đến gần hỏi để thử Ngài rằng: Người nam có phép để vợ mình chăng?"));
visuperobject.biblemap.put("b41_10_3", new visuperobject(41,10,3,"Ngài trả lời rằng: Vậy chớ Môi-se dạy các ngươi điều chi?"));
visuperobject.biblemap.put("b41_10_4", new visuperobject(41,10,4,"Họ thưa rằng: Môi-se có cho phép viết tờ để và cho phép để vợ."));
visuperobject.biblemap.put("b41_10_5", new visuperobject(41,10,5,"Ðức Chúa Jêsus phán rằng: Ấy vì cớ lòng các ngươi cứng cõi, nên người đã truyền mạng nầy cho."));
visuperobject.biblemap.put("b41_10_6", new visuperobject(41,10,6,"Nhưng từ lúc đầu sáng thế, Ðức Chúa Trời làm ra một người nam và một người nữ."));
visuperobject.biblemap.put("b41_10_7", new visuperobject(41,10,7,"Vì cớ đó, người nam lìa cha mẹ mình mà dính díu với vợ;"));
visuperobject.biblemap.put("b41_10_8", new visuperobject(41,10,8,"và hai người cùng nên một thịt mà thôi. Như thế, vợ chồng chẳng phải mà hai nữa, mà chỉ một thịt."));
visuperobject.biblemap.put("b41_10_9", new visuperobject(41,10,9,"Vậy, người ta không nên phân rẽ những kẻ mà Ðức Chúa Trời đã phối hiệp."));
visuperobject.biblemap.put("b41_10_10", new visuperobject(41,10,10,"Khi ở trong nhà, môn đồ lại hỏi Ngài về việc ấy;"));
visuperobject.biblemap.put("b41_10_11", new visuperobject(41,10,11,"Ngài phán rằng: Ai để vợ mình mà cưới vợ khác, thì phạm tội tà dâm với người;"));
visuperobject.biblemap.put("b41_10_12", new visuperobject(41,10,12,"còn nếu người đờn bà bỏ chồng mình mà lấy chồng khác, thì cũng phạm tội tà dâm."));
visuperobject.biblemap.put("b41_10_13", new visuperobject(41,10,13,"Người ta đem những con trẻ đến cùng Ngài, đặng Ngài rờ chúng nó; nhưng môn đồ trách những kẻ đem đến."));
visuperobject.biblemap.put("b41_10_14", new visuperobject(41,10,14,"Ðức Chúa Jêsus thấy vậy, bèn giận mà phán cùng môn đồ rằng: Hãy để con trẻ đến cùng ta, đừng cấm chúng nó; vì nước Ðức Chúa Trời thuộc về những kẻ giống như con trẻ ấy."));
visuperobject.biblemap.put("b41_10_15", new visuperobject(41,10,15,"Quả thật, ta nói cùng các ngươi, ai chẳng nhận lấy nước Ðức Chúa Trời như một đứa trẻ, thì chẳng được vào đó bao giờ."));
visuperobject.biblemap.put("b41_10_16", new visuperobject(41,10,16,"Ngài lại bồng những đứa trẻ ấy, đặt tay trên chúng nó mà chúc phước cho."));
visuperobject.biblemap.put("b41_10_17", new visuperobject(41,10,17,"Ngài đang ra đi, có một người chạy lại, quì trước mặt Ngài, mà hỏi rằng: Thưa thầy nhơn lành, tôi phải làm chi cho được hưởng sự sống đời đời?"));
visuperobject.biblemap.put("b41_10_18", new visuperobject(41,10,18,"Ðức Chúa Jêsus phán rằng: Sao ngươi gọi ta là nhơn lành? Chỉ có một Ðấng nhơn lành, là Ðức Chúa Trời."));
visuperobject.biblemap.put("b41_10_19", new visuperobject(41,10,19,"Ngươi biết các điều răn: Ðừng phạm tội tà dâm; đừng giết người; đừng trộm cướp; đừng làm chứng dối; đừng làm gian; hãy hiếu kính cha mẹ."));
visuperobject.biblemap.put("b41_10_20", new visuperobject(41,10,20,"Người thưa rằng: Lạy thầy, tôi đã giữ mọi điều đó từ khi còn nhỏ."));
visuperobject.biblemap.put("b41_10_21", new visuperobject(41,10,21,"Ðức Chúa Jêsus ngó người mà yêu, nên phán rằng: Nhưng ngươi còn thiếu một điều. Hãy đi, bán hết gia tài mình, đem thí cho kẻ nghèo khổ, chắc sẽ được của báu ở trên trời, rồi hãy đến mà theo ta."));
visuperobject.biblemap.put("b41_10_22", new visuperobject(41,10,22,"Song nét mặt người nầy rầu rĩ về lời đó, đi ra rất buồn bã, vì có nhiều của lắm."));
visuperobject.biblemap.put("b41_10_23", new visuperobject(41,10,23,"Bấy giờ Ðức Chúa Jêsus ngó xung quanh mình, mà phán cùng môn đồ rằng: Kẻ giàu vào nước Ðức Chúa Trời khó là dường nào!"));
visuperobject.biblemap.put("b41_10_24", new visuperobject(41,10,24,"Môn đồ lấy mấy lời đó làm lạ. Nhưng Ðức Chúa Jêsus lại phán rằng: Hỡi các con, những kẻ cậy sự giàu có vào nước Ðức Chúa Trời khó là dường nào!"));
visuperobject.biblemap.put("b41_10_25", new visuperobject(41,10,25,"Con lạc đà chui qua lỗ kim còn dễ hơn người giàu vào nước Ðức Chúa Trời."));
visuperobject.biblemap.put("b41_10_26", new visuperobject(41,10,26,"Môn đồ lại càng lấy làm lạ, nói cùng nhau rằng: Vậy thì ai được cứu?"));
visuperobject.biblemap.put("b41_10_27", new visuperobject(41,10,27,"Ðức Chúa Jêsus ngó môn đồ mà rằng: Sự đó loài người không thể làm được, nhưng Ðức Chúa Trời thì chẳng thế; vì Ðức Chúa Trời làm mọi sự được cả."));
visuperobject.biblemap.put("b41_10_28", new visuperobject(41,10,28,"Phi -e-rơ liền thưa cùng Ngài rằng: Nầy, chúng tôi đã bỏ hết mà đi theo thầy."));
visuperobject.biblemap.put("b41_10_29", new visuperobject(41,10,29,"Ðức Chúa Jêsus đáp rằng: Quả thật, ta nói cùng các ngươi chẳng một người nào vì ta và Tin Lành từ bỏ nhà cửa, anh em, chị em, cha mẹ, con cái, đất ruộng,"));
visuperobject.biblemap.put("b41_10_30", new visuperobject(41,10,30,"mà chẳng lãnh được đương bây giờ, trong đời nầy, trăm lần hơn về những nhà cửa, anh em, chị em, mẹ con, đất ruộng, với sự bắt bớ, và sự sống đời đời trong đời sau."));
visuperobject.biblemap.put("b41_10_31", new visuperobject(41,10,31,"Nhưng có nhiều kẻ đầu sẽ nên rốt; có kẻ rốt sẽ nên đầu."));
visuperobject.biblemap.put("b41_10_32", new visuperobject(41,10,32,"Ðức Chúa Trời và môn đồ đương đi đường lên thành Giê-ru-sa-lem, Ngài thì đi trước; các môn đồ thất kinh, và những người đi theo đều sợ hãi. Ngài lại kèm mười hai sứ đồ đi cùng mình mà phán về những việc phải xảy đến cho mình, rằng:"));
visuperobject.biblemap.put("b41_10_33", new visuperobject(41,10,33,"Nầy, chúng ta lên thành Giê-ru-sa-lem, Con người sẽ bị nộp cho các thầy tế lễ cả cùng các thầy thông giáo; họ sẽ định Ngài phải bị tử hình và giao Ngài cho dân ngoại."));
visuperobject.biblemap.put("b41_10_34", new visuperobject(41,10,34,"Người ta sẽ nhạo báng Ngài, nhổ trên Ngài, đánh đập Ngài mà giết đi; sau ba ngày, Ngài sẽ sống lại."));
visuperobject.biblemap.put("b41_10_35", new visuperobject(41,10,35,"Bấy giờ, Gia-cơ và Giăng, hai con trai Xê-bê-đê, đến gần Ngài mà thưa rằng: Lạy thầy, chúng tôi muốn thầy làm thành điều chúng tôi sẽ xin."));
visuperobject.biblemap.put("b41_10_36", new visuperobject(41,10,36,"Ngài hỏi rằng: Các ngươi muốn ta làm chi cho?"));
visuperobject.biblemap.put("b41_10_37", new visuperobject(41,10,37,"Thưa rằng: Khi thầy được vinh hiển, xin cho chúng tôi một đứa ngồi bên hữu, một đứa bên tả."));
visuperobject.biblemap.put("b41_10_38", new visuperobject(41,10,38,"Nhưng Ðức Chúa Trời phán rằng: Các ngươi không biết điều mình xin. Các ngươi có uống được chén ta uống, và chịu được phép báp-tem ta chịu chăng?"));
visuperobject.biblemap.put("b41_10_39", new visuperobject(41,10,39,"Thưa rằng: Ðược. Ðức Chúa Jêsus phán rằng: Các ngươi sẽ uống chén ta uống, và sẽ chịu phép báp-tem ta chịu;"));
visuperobject.biblemap.put("b41_10_40", new visuperobject(41,10,40,"nhưng chí như ngồi bên hữu và bên tả ta thì không phải tự ta cho được: ấy là thuộc về người nào mà điều đó đã dành cho."));
visuperobject.biblemap.put("b41_10_41", new visuperobject(41,10,41,"Mười sứ đồ kia nghe sự xin đó, thì giận Gia-cơ và Giăng."));
visuperobject.biblemap.put("b41_10_42", new visuperobject(41,10,42,"Nhưng Ðức Chúa Jêsus gọi họ mà phán rằng: Các ngươi biết những người được tôn làm đầu cai trị các dân ngoại, thì bắt dân phải phục mình, còn các quan lớn thì lấy quyền thế trị dân."));
visuperobject.biblemap.put("b41_10_43", new visuperobject(41,10,43,"Song trong các ngươi không như vậy; trái lại hễ ai muốn làm lớn trong các ngươi, thì sẽ làm đầy tớ;"));
visuperobject.biblemap.put("b41_10_44", new visuperobject(41,10,44,"còn ai trong các ngươi muốn làm đầu, thì sẽ làm tôi mọi mọi người."));
visuperobject.biblemap.put("b41_10_45", new visuperobject(41,10,45,"Vì Con người đã đến không phải để người ta hầu việc mình, song để hầu việc người ta, và phó sự sống mình làm giá chuộc cho nhiều người."));
visuperobject.biblemap.put("b41_10_46", new visuperobject(41,10,46,"Kế đó, Ðức Chúa Jêsus và môn đồ đến thành Giê-ri-cô. Ngài và môn đồ cùng một đoàn dân đông đang từ đó lại đi, thì có một người ăn mày mù tên là Ba-ti-mê, con trai của Ti-mê, ngồi bên đường."));
visuperobject.biblemap.put("b41_10_47", new visuperobject(41,10,47,"Vì đã nghe nói ấy là Ðức Chúa Jêsus, người Na-xa-rét, người vùng la lên mà rằng: Hỡi Ðức Chúa Jêsus, con vua Ða-vít, xin thương tôi cùng!"));
visuperobject.biblemap.put("b41_10_48", new visuperobject(41,10,48,"Có nhiều kẻ rầy người, biểu nín đi; song người lại kêu lớn hơn nữa rằng: Hỡi con vua Ða-vít, xin thương tôi cùng!"));
visuperobject.biblemap.put("b41_10_49", new visuperobject(41,10,49,"Ðức Chúa Jêsus dừng lại, phán rằng: Hãy kêu người đến. Chúng kêu người mù đến, mà nói rằng: Hãy vững lòng, đứng dậy, Ngài gọi ngươi."));
visuperobject.biblemap.put("b41_10_50", new visuperobject(41,10,50,"Người mù bỏ áo ngoài, bước tới đến cùng Ðức Chúa Jêsus."));
visuperobject.biblemap.put("b41_10_51", new visuperobject(41,10,51,"Ðức Chúa Jêsus bèn cất tiếng phán rằng: Ngươi muốn ta làm chi cho ngươi? Người mù thưa rằng: Lạy thầy, xin cho tôi được sáng mắt."));
visuperobject.biblemap.put("b41_10_52", new visuperobject(41,10,52,"Ðức Chúa Jêsus phán: Ði đi, đức tin ngươi đã chữa lành ngươi rồi. (10:53) Tức thì người mù được sáng mắt, và đi theo Ðức Chúa Jêsus trên đường."));
visuperobject.biblemap.put("b41_11_1", new visuperobject(41,11,1,"Khi tới gần thành Giê-ru-sa-lem, bên làng Bê-pha-giê và làng Bê-tha-ni, ngang núi ô-li-ve, Ðức Chúa Jêsus sai hai môn đồ đi,"));
visuperobject.biblemap.put("b41_11_2", new visuperobject(41,11,2,"và phán rằng: Hãy đi đến làng ngay trước mặt các ngươi; vừa khi vào đó, thấy một con lừa con đương buộc, chưa có ai cỡi; hãy mở nó ra, dắt về cho ta."));
visuperobject.biblemap.put("b41_11_3", new visuperobject(41,11,3,"Hoặc có ai hỏi các ngươi rằng: Vì sao làm điều đó? hãy đáp lại: Chúa cần dùng nó, tức thì họ sẽ cho dắt lừa về đây."));
visuperobject.biblemap.put("b41_11_4", new visuperobject(41,11,4,"Hai người đi, thì thấy lừa con đương buộc ở ngoài trước cửa, chỗ đường quẹo, bèn mở ra."));
visuperobject.biblemap.put("b41_11_5", new visuperobject(41,11,5,"Có mấy kẻ trong những người ở đó hỏi rằng: Các ngươi mở lừa con đó làm chi?"));
visuperobject.biblemap.put("b41_11_6", new visuperobject(41,11,6,"Hai người trả lời theo Ðức Chúa Jêsus đã dặn; thì họ để cho đi."));
visuperobject.biblemap.put("b41_11_7", new visuperobject(41,11,7,"Hai người dắt lừa con về cho Ðức Chúa Jêsus, lấy áo mình trải trên nó; rồi Ngài cỡi lên."));
visuperobject.biblemap.put("b41_11_8", new visuperobject(41,11,8,"Nhiều người trải áo mình trên đàng; lại có kẻ rải nhánh cây chặt trong đồng ruộng."));
visuperobject.biblemap.put("b41_11_9", new visuperobject(41,11,9,"Người đi trước kẻ theo sau đều la lên rằng: Hô-sa-na! Ðáng khen ngợi cho Ðấng nhơn danh Chúa mà đến!"));
visuperobject.biblemap.put("b41_11_10", new visuperobject(41,11,10,"Phước cho nước đến, là nước vua Ða-vít, tổ phụ chúng ta! Hô-sa-na ở trên nơi rất cao!"));
visuperobject.biblemap.put("b41_11_11", new visuperobject(41,11,11,"Ðức Chúa Jêsus vào thành Giê-ru-sa-lem, đến nơi đền thờ; đoạn, liếc mắt xem mọi sự chung quanh, thì trời đã tối, Ngài bèn ra, đặng đi tới làng Bê-tha-ni với mười hai sứ đồ."));
visuperobject.biblemap.put("b41_11_12", new visuperobject(41,11,12,"Sáng ngày, khi đã lìa làng Bê-tha-ni rồi, thì Ngài đói."));
visuperobject.biblemap.put("b41_11_13", new visuperobject(41,11,13,"Vừa thấy đàng xa một cây vả có lá, Ngài đến đó xem hoặc có chi chăng; song, tới gần rồi, chỉ thấy có lá mà thôi, vì bấy giờ không phải mùa vả."));
visuperobject.biblemap.put("b41_11_14", new visuperobject(41,11,14,"Ngài bèn cất tiếng phán cùng cây vả rằng: Chẳng hề có ai ăn trái của mầy nữa! Các môn đồ đều nghe lời ấy."));
visuperobject.biblemap.put("b41_11_15", new visuperobject(41,11,15,"Ðoạn, đến thành Giê-ru-sa-lem; Ðức Chúa Jêsus vào đền thờ, đuổi những kẻ buôn bán ở đó, lại lật đổ bàn những người đổi bạc, và ghế những kẻ bán bò câu."));
visuperobject.biblemap.put("b41_11_16", new visuperobject(41,11,16,"Ngài cấm không cho ai được đem đồ gì đi ngang qua đền thờ."));
visuperobject.biblemap.put("b41_11_17", new visuperobject(41,11,17,"Rồi Ngài dạy dỗ chúng mà rằng: há chẳng có lời chép: Nhà ta sẽ gọi là nhà cầu nguyện của muôn dân hay sao? Nhưng các ngươi đã làm thành cái hang trộm cướp."));
visuperobject.biblemap.put("b41_11_18", new visuperobject(41,11,18,"Các thầy tế lễ cả và các thầy thông giáo nghe mấy lời, bèn kiếm cách diệt Ngài; vì họ sợ Ngài, tại cả đoàn dân cảm động sự dạy dỗ của Ngài lắm."));
visuperobject.biblemap.put("b41_11_19", new visuperobject(41,11,19,"Ðến chiều, Ngài và môn đồ ra khỏi thành."));
visuperobject.biblemap.put("b41_11_20", new visuperobject(41,11,20,"Sáng hôm sau, khi đi ngang qua, thấy cây vả đã khô cho tới rễ;"));
visuperobject.biblemap.put("b41_11_21", new visuperobject(41,11,21,"bấy giờ Phi -e-rơ nhớ lại chuyện đã qua, thưa cùng Ngài rằng: Thầy, coi kìa! Cây vả thầy đã rủa nay khô đi rồi."));
visuperobject.biblemap.put("b41_11_22", new visuperobject(41,11,22,"Ðức Chúa Jêsus cất tiếng phán rằng: Hãy có đức tin đến Ðức Chúa Trời."));
visuperobject.biblemap.put("b41_11_23", new visuperobject(41,11,23,"Quả thật, ta nói cùng các ngươi, ai sẽ biểu hòn núi nầy rằng: Phải cất mình lên, và quăng xuống biển, nếu người chẳng nghi ngại trong lòng, nhưng tin chắc lời mình nói sẽ ứng nghiệm, thì điều đó sẽ thành cho."));
visuperobject.biblemap.put("b41_11_24", new visuperobject(41,11,24,"Bởi vậy ta nói cùng các ngươi: Mọi điều các ngươi xin trong lúc cầu nguyện, hãy tin đã được, tất điều đó sẽ ban cho các ngươi."));
visuperobject.biblemap.put("b41_11_25", new visuperobject(41,11,25,"Khi các ngươi đứng cầu nguyện, nếu có sự gì bất bình cùng ai, thì hãy tha thứ, để Cha các ngươi ở trên trời cũng tha lỗi cho các ngươi."));
visuperobject.biblemap.put("b41_11_26", new visuperobject(41,11,26,"Song nếu không tha lỗi cho người ta, thì Cha các ngươi ở trên trời cũng sẽ không tha lỗi cho các ngươi."));
visuperobject.biblemap.put("b41_11_27", new visuperobject(41,11,27,"Chúa và môn đồ trở lại thành Giê-ru-sa-lem. Ngài đang đi dạo trong đền thờ, thì các thầy tế lễ cả, các thầy thông giáo, và các trưởng lão tới gần Ngài,"));
visuperobject.biblemap.put("b41_11_28", new visuperobject(41,11,28,"mà hỏi rằng: Thầy lấy quyền phép nào làm những điều nầy, hoặc ai đã cho thầy quyền phép làm những điều ấy?"));
visuperobject.biblemap.put("b41_11_29", new visuperobject(41,11,29,"Ðức Chúa Jêsus phán rằng: Ta cũng hỏi các ngươi một câu; hãy trả lời cho ta, thì ta sẽ nói cho các ngươi biết ta làm những điều đó bởi quyền phép nào."));
visuperobject.biblemap.put("b41_11_30", new visuperobject(41,11,30,"Phép báp-tem của Giăng đến bởi trên trời hay là bởi người ta? Hãy trả lời cho ta đi."));
visuperobject.biblemap.put("b41_11_31", new visuperobject(41,11,31,"Vả, họ bàn với nhau như vầy: Nếu chúng ta nói: Bởi trên trời, thì người sẽ nói rằng: Vậy, sao các ngươi không tin lời Giăng?"));
visuperobject.biblemap.put("b41_11_32", new visuperobject(41,11,32,"Bằng chúng ta nói trái lại: Bởi người ta? Họ sợ dân chúng, vì ai nấy đều tin Giăng thật là Ðấng tiên tri."));
visuperobject.biblemap.put("b41_11_33", new visuperobject(41,11,33,"Nên họ thưa lại cùng Ðức Chúa Jêsus rằng: Chúng tôi không biết. Ðức Chúa Jêsus bèn phán cùng họ rằng: Ta cũng không nói cho các ngươi biết ta nhờ quyền phép nào mà làm những điều nầy."));
visuperobject.biblemap.put("b41_12_1", new visuperobject(41,12,1,"Ðức Chúa Jêsus khởi phán thí dụ cùng họ rằng: Một người kia trồng vườn nho, rào chung quanh, ở trong đào một cái hầm ép rượu, và dựng một cái tháp; đoạn cho mấy người làm nghề trồng nho thuê, mà lìa bổn xứ."));
visuperobject.biblemap.put("b41_12_2", new visuperobject(41,12,2,"Tới mùa, chủ vườn sai một đầy tớ đến cùng bọn trồng nho, đặng thâu lấy phần hoa lợi vườn nho nơi tay họ."));
visuperobject.biblemap.put("b41_12_3", new visuperobject(41,12,3,"Nhưng họ bắt đầy tớ mà đánh rồi đuổi về tay không."));
visuperobject.biblemap.put("b41_12_4", new visuperobject(41,12,4,"Người sai một đầy tớ khác đến; nhưng họ đánh đầu nó và chưởi rủa nữa."));
visuperobject.biblemap.put("b41_12_5", new visuperobject(41,12,5,"Người còn sai đứa khác đến, thì họ giết đi; lại sai nhiều đứa khác nữa, đứa thì bị họ đánh, đứa thì bị họ giết."));
visuperobject.biblemap.put("b41_12_6", new visuperobject(41,12,6,"Chủ vườn còn đứa con trai một rất yêu dấu, bèn sai đi lần cuối cùng mà nói rằng: Chúng nó sẽ kính vì con ta!"));
visuperobject.biblemap.put("b41_12_7", new visuperobject(41,12,7,"Nhưng bọn trồng nho đó bàn với nhau rằng: Thằng nầy là con kế tự; hè, ta hãy giết nó, thì phần gia tài nó sẽ về chúng ta."));
visuperobject.biblemap.put("b41_12_8", new visuperobject(41,12,8,"Họ bắt con trai ấy giết đi, ném ra ngoài vườn nho."));
visuperobject.biblemap.put("b41_12_9", new visuperobject(41,12,9,"Vậy thì chủ vườn nho sẽ làm thế nào? Người sẽ đến giết bọn trồng nho đó, rồi lấy vườn nho lại mà cho người khác."));
visuperobject.biblemap.put("b41_12_10", new visuperobject(41,12,10,"Các người há chưa đọc lời Kinh Thánh nầy: Hòn đá bị thợ xây nhà bỏ ra, Ðã trở nên đá góc nhà;"));
visuperobject.biblemap.put("b41_12_11", new visuperobject(41,12,11,"Ấy là công việc của Chúa, Và là việc rất lạ trước mắt chúng ta, hay sao?"));
visuperobject.biblemap.put("b41_12_12", new visuperobject(41,12,12,"Những người đó bèn tìm cách bắt Ngài, vì biết rõ rằng Ngài phán thí dụ ấy chỉ về mình; song sợ dân chúng, nên bỏ Ngài mà đi."));
visuperobject.biblemap.put("b41_12_13", new visuperobject(41,12,13,"Kế đó, họ sai mấy người thuộc về phe Pha-ri-si và đảng Hê-rốt đến, để bắt lỗi Ngài trong lời nói."));
visuperobject.biblemap.put("b41_12_14", new visuperobject(41,12,14,"Vậy, mấy người ấy đến, thưa Ngài rằng: Lạy thầy, chúng tôi biết thầy là người thật, không lo sợ gì ai; vì thầy không xem bề ngoài người ta, nhưng dạy đạo Ðức Chúa Trời theo mọi lẽ thật. Có nên nộp thuế cho Sê-sa hay không? Chúng tôi phải nộp hay là không nộp?"));
visuperobject.biblemap.put("b41_12_15", new visuperobject(41,12,15,"Nhưng Ngài biết sự giả hình của họ thì phán rằng: Các ngươi thử ta làm chi? Hãy đem cho ta xem một đồng đơ-ni-ê."));
visuperobject.biblemap.put("b41_12_16", new visuperobject(41,12,16,"Họ đem cho Ngài một đồng, Ngài bèn phán rằng: Hình và hiệu nầy của ai? Họ trả lời rằng: Của Sê-sa."));
visuperobject.biblemap.put("b41_12_17", new visuperobject(41,12,17,"Ðức Chúa Jêsus phán rằng: Vật chi của Sê-sa hãy trả lại cho Sê-sa, còn vật chi của Ðức Chúa Trời hãy trả lại cho Ðức Chúa Trời. Họ đều lấy làm lạ về Ngài."));
visuperobject.biblemap.put("b41_12_18", new visuperobject(41,12,18,"Các người Sa-đu-sê, là những kẻ vẫn nói rằng không có sự sống lại, đến gần mà hỏi Ngài rằng:"));
visuperobject.biblemap.put("b41_12_19", new visuperobject(41,12,19,"Thưa thầy, Môi-se đã ban cho chúng tôi luật nầy: Nếu một người kia có anh em qua đời, để vợ lại, không con, thì người kia phải lấy vợ góa đó đặng nối dòng dõi cho anh em mình."));
visuperobject.biblemap.put("b41_12_20", new visuperobject(41,12,20,"Vả, có bảy anh em. Người thứ nhứt cưới vợ, rồi chết, không có con."));
visuperobject.biblemap.put("b41_12_21", new visuperobject(41,12,21,"Người thứ hai lấy vợ góa ấy, sau chết, cũng không có con; rồi đến người thứ ba cũng vậy."));
visuperobject.biblemap.put("b41_12_22", new visuperobject(41,12,22,"Trong bảy người, chẳng ai để con lại. Sau hết, người đờn bà cũng chết."));
visuperobject.biblemap.put("b41_12_23", new visuperobject(41,12,23,"Lúc sống lại, đờn bà đó là vợ ai trong bảy anh em? vì hết thảy đều đã lấy người làm vợ."));
visuperobject.biblemap.put("b41_12_24", new visuperobject(41,12,24,"Ðức Chúa Jêsus đáp rằng: Há chẳng phải các ngươi lầm, vì không biết Kinh Thánh, cũng không hiểu quyền phép Ðức Chúa Trời sao?"));
visuperobject.biblemap.put("b41_12_25", new visuperobject(41,12,25,"Vì đến lúc người ta từ kẻ chết sống lại, thì chẳng cưới vợ, chẳng lấy chồng; nhưng các người sống lại đó ở như thiên sứ trên trời vậy."));
visuperobject.biblemap.put("b41_12_26", new visuperobject(41,12,26,"Còn về những người chết được sống lại, thì trong sách Môi-se có chép lời Ðức Chúa Trời phán cùng người trong bụi gai rằng: Ta là Ðức Chúa Trời của Áp-ra-ham, Ðức Chúa Trời của Y-sác, Ðức Chúa Trời của Gia-cốp, các ngươi há chưa đọc đến sao?"));
visuperobject.biblemap.put("b41_12_27", new visuperobject(41,12,27,"Ngài chẳng phải là Ðức Chúa Trời của kẻ chết, mà là của kẻ sống! Thật các ngươi lầm to."));
visuperobject.biblemap.put("b41_12_28", new visuperobject(41,12,28,"Bấy giờ, có một thầy thông giáo nghe Chúa và người Sa-đu-sê biện luận với nhau, biết Ðức Chúa Jêsus đã khéo đáp, bèn đến gần hỏi Ngài rằng: Trong các điều răn, điều nào là đầu hết?"));
visuperobject.biblemap.put("b41_12_29", new visuperobject(41,12,29,"Ðức Chúa Jêsus đáp rằng: Nầy là điều đầu nhứt: Hỡi Y-sơ-ra-ên, hãy nghe, Chúa, Ðức Chúa Trời chúng ta, là Chúa có một."));
visuperobject.biblemap.put("b41_12_30", new visuperobject(41,12,30,"Ngươi phải hết lòng, hết linh hồn, hết trí khôn, hết sức mà kính mến Chúa là Ðức Chúa Trời ngươi."));
visuperobject.biblemap.put("b41_12_31", new visuperobject(41,12,31,"Nầy là điều thứ hai: Ngươi phải yêu kẻ lân cận như mình. Chẳng có điều răn nào lớn hơn hai điều đó."));
visuperobject.biblemap.put("b41_12_32", new visuperobject(41,12,32,"Thầy thông giáo trả lời rằng: Thưa thầy, thầy nói phải, hiệp lý lắm, thật Ðức Chúa Trời là có một, ngoài Ngài chẳng có Chúa nào khác nữa;"));
visuperobject.biblemap.put("b41_12_33", new visuperobject(41,12,33,"thật phải kính mến Chúa hết lòng, hết trí, hết sức và yêu kẻ lân cận như mình, ấy là hơn mọi của lễ thiêu cùng hết thảy các của lễ."));
visuperobject.biblemap.put("b41_12_34", new visuperobject(41,12,34,"Ðức Chúa Jêsus thấy người trả lời như người khôn, thì phán rằng: Ngươi chẳng cách xa nước Ðức Chúa Trời đâu. Rồi không ai dám hỏi Ngài nữa."));
visuperobject.biblemap.put("b41_12_35", new visuperobject(41,12,35,"Ðức Chúa Jêsus đương dạy dỗ trong đền thờ, bèn cất tiếng phán những lời nầy: Sao các thầy thông giáo Ðấng Christ là con Ða-vít?"));
visuperobject.biblemap.put("b41_12_36", new visuperobject(41,12,36,"Chính Ða-vít đã cảm Ðức Thánh Linh mà nói rằng: Chúa phán cùng Chúa tôi; Hãy ngồi bên hữu ta, Cho đến chừng nào ta để kẻ thù nghịch ngươi làm bệ dưới chơn ngươi."));
visuperobject.biblemap.put("b41_12_37", new visuperobject(41,12,37,"Vì chính Ða-vít xưng Ngài bằng Chúa, thì lẽ nào Ngài là con vua ấy? Ðoàn dân đông vui lòng mà nghe Ngài."));
visuperobject.biblemap.put("b41_12_38", new visuperobject(41,12,38,"Trong lúc dạy dỗ, Ngài lại phán rằng: Hãy giữ mình về các thầy thông giáo, là kẻ ưa mặc áo dài đi chơi, thích người ta chào mình giữa chợ"));
visuperobject.biblemap.put("b41_12_39", new visuperobject(41,12,39,"muốn ngôi cao nhứt trong các nhà hội cùng trong các đám tiệc,"));
visuperobject.biblemap.put("b41_12_40", new visuperobject(41,12,40,"nuốt các nhà đờn bà góa, mà làm bộ đọc lời cầu nguyện dài. Họ sẽ bị phạt càng nặng hơn nữa."));
visuperobject.biblemap.put("b41_12_41", new visuperobject(41,12,41,"Ðức Chúa Jêsus ngồi đối ngang cái rương đựng tiền dâng, coi dân chúng bỏ tiền vào thể nào."));
visuperobject.biblemap.put("b41_12_42", new visuperobject(41,12,42,"Có lắm người giàu bỏ nhiều tiền; cũng có một mụ góa nghèo kia đến bỏ hai đồng tiền ăn một phần tư xu."));
visuperobject.biblemap.put("b41_12_43", new visuperobject(41,12,43,"Ngài bèn kêu môn đồ mà phán rằng: Quả thật, ta nói cùng các ngươi, mụ góa nghèo nầy đã bỏ tiền vào rương nhiều hơn hết thảy những người đã bỏ vào."));
visuperobject.biblemap.put("b41_12_44", new visuperobject(41,12,44,"Vì mọi kẻ khác lấy của dư mình bỏ vào, còn mụ nầy nghèo cực lắm, đã bỏ hết của mình có, là hết của có để nuôi mình."));
visuperobject.biblemap.put("b41_13_1", new visuperobject(41,13,1,"Ðức Chúa Jêsus vừa ra khỏi đền thờ, có một người môn đồ thưa cùng Ngài rằng: Thưa thầy, hãy xem đá và các nhà nầy là dường nào!"));
visuperobject.biblemap.put("b41_13_2", new visuperobject(41,13,2,"Ðức Chúa Jêsus đáp rằng: Ngươi thấy các nhà lớn nầy ư? Rồi ra sẽ không còn một hòn đá sót lại trên một hòn khác nữa; cả thảy đều đổ xuống."));
visuperobject.biblemap.put("b41_13_3", new visuperobject(41,13,3,"Ngài đương ngồi trên núi ô-li-ve, đối ngang đền thờ, thì Phi -e-rơ, Gia-cơ, Giăng và Anh-rê hỏi riêng Ngài rằng:"));
visuperobject.biblemap.put("b41_13_4", new visuperobject(41,13,4,"Xin thầy nói cho chúng tôi biết lúc nào các điều đó xảy đến, và có điềm chi cho người ta biết các sự đó sẽ hoàn thành?"));
visuperobject.biblemap.put("b41_13_5", new visuperobject(41,13,5,"Ðức Chúa Jêsus khởi phán rằng: Hãy giữ mình kẻo có ai lừa dối các ngươi chăng."));
visuperobject.biblemap.put("b41_13_6", new visuperobject(41,13,6,"Có nhiều kẻ sẽ lấy danh ta mà đến, xưng rằng: Chính ta là Ðấng Christ! Họ sẽ dỗ dành nhiều người."));
visuperobject.biblemap.put("b41_13_7", new visuperobject(41,13,7,"Khi các ngươi nghe nói về giặc và nghe tiếng đồn về giặc, thì đừng bối rối: những sự ấy phải xảy đến; nhưng chưa là cuối cùng."));
visuperobject.biblemap.put("b41_13_8", new visuperobject(41,13,8,"Vì dân nầy sẽ dấy lên nghịch cùng dân khác, nước nọ nghịch cùng nước kia; cũng sẽ có động đất nhiều nơi, và đói kém. Ðó chỉ là đầu sự khốn khổ mà thôi."));
visuperobject.biblemap.put("b41_13_9", new visuperobject(41,13,9,"Còn các ngươi, phải giữ mình; họ sẽ nộp các ngươi trước tòa án; các ngươi sẽ bị đánh trong các nhà hội, và vì cớ ta, sẽ đứng trước mặt các quan tổng đốc và các vua, để làm chứng trước mặt họ."));
visuperobject.biblemap.put("b41_13_10", new visuperobject(41,13,10,"Nhưng trước hết Tin Lành phải được giảng ra cho khắp muôn dân đã."));
visuperobject.biblemap.put("b41_13_11", new visuperobject(41,13,11,"Vả, khi họ điệu các ngươi đi nộp, chớ có ngại trước về điều mình sẽ nói, nhưng đến giờ đó, hãy nói theo lời sẽ ban cho; vì ấy không phải các ngươi nói, bèn là Ðức Thánh Linh vậy."));
visuperobject.biblemap.put("b41_13_12", new visuperobject(41,13,12,"Bấy giờ, anh sẽ nộp em cho phải chết, cha sẽ nộp con; con cái sẽ dấy lên nghịch cùng cha mẹ mình mà làm cho phải chết."));
visuperobject.biblemap.put("b41_13_13", new visuperobject(41,13,13,"Các ngươi sẽ bị mọi người ghen ghét vì danh ta; song ai cứ bền lòng đến cuối cùng, người ấy sẽ được cứu."));
visuperobject.biblemap.put("b41_13_14", new visuperobject(41,13,14,"Khi các ngươi sẽ xem thấy sự tàn nát gớm ghiếc lập ra nơi không nên lập (ai đọc phải để ý), bấy giờ những kẻ ở trong xứ Giu-đê hãy trốn lên núi;"));
visuperobject.biblemap.put("b41_13_15", new visuperobject(41,13,15,"ai ở trên mái nhà chớ xuống, và chớ vào trong nhà mà chuyên bất luận vật gì;"));
visuperobject.biblemap.put("b41_13_16", new visuperobject(41,13,16,"ai ở ngoài đồng chớ trở về lấy áo xống mình."));
visuperobject.biblemap.put("b41_13_17", new visuperobject(41,13,17,"Trong những ngày đó, khốn cho đờn bà có thai cùng đờn bà cho con bú!"));
visuperobject.biblemap.put("b41_13_18", new visuperobject(41,13,18,"Hãy cầu nguyện cho điều đó chớ xảy ra lúc mùa đông."));
visuperobject.biblemap.put("b41_13_19", new visuperobject(41,13,19,"Vì trong những ngày ấy có tại nạn, đến nỗi từ ban đầu Ðức Chúa Trời dựng nên trời đất cho đến bây giờ chưa hề có như vậy, và về sau cũng sẽ chẳng hề có nữa."));
visuperobject.biblemap.put("b41_13_20", new visuperobject(41,13,20,"Nếu Chúa chẳng giảm bớt các ngày ấy, thì không có sanh vật nào được cứu; song vì cớ những kẻ Ngài đã chọn, nên Ngài đã giảm bớt các ngày ấy."));
visuperobject.biblemap.put("b41_13_21", new visuperobject(41,13,21,"Khi ấy, dầu có ai bảo các ngươi rằng: Kìa, Ðấng Christ ở đây, hay là Ngài ở đó, thì chớ tin."));
visuperobject.biblemap.put("b41_13_22", new visuperobject(41,13,22,"Những christ giả, tiên tri giả sẽ nổi lên, làm những dấu lạ phép lạ, nếu có thể được thì họ cũng dỗ dành chính những người được chọn."));
visuperobject.biblemap.put("b41_13_23", new visuperobject(41,13,23,"Hãy giữ lấy, ta đã nói trước cho các ngươi cả."));
visuperobject.biblemap.put("b41_13_24", new visuperobject(41,13,24,"Trong những ngày ấy, sau kỳ tại nạn, mặt trời sẽ tối tăm, mặt trăng chẳng chiếu sáng nữa,"));
visuperobject.biblemap.put("b41_13_25", new visuperobject(41,13,25,"các ngôi sao sẽ tự trên trời rớt xuống, và thế lực các từng trời sẽ rúng động."));
visuperobject.biblemap.put("b41_13_26", new visuperobject(41,13,26,"Bấy giờ người ta sẽ thấy Con người lấy đại quyền đại vinh ngự đến trên đám mây;"));
visuperobject.biblemap.put("b41_13_27", new visuperobject(41,13,27,"Ngài sẽ sai các thiên sứ đặng nhóm những kẻ được chọn ở bốn phương, từ đầu cùng đất cho đến đầu cùng trời."));
visuperobject.biblemap.put("b41_13_28", new visuperobject(41,13,28,"Hãy nghe lời thí dụ về cây vả. Vừa khi nhánh nó trở nên non và nứt lộc, thì biết mùa hạ gần tới."));
visuperobject.biblemap.put("b41_13_29", new visuperobject(41,13,29,"Cũng một lẽ ấy, khi các ngươi thấy các điều đó xảy đến, hãy biết Con người đã tới gần, ở nơi cửa."));
visuperobject.biblemap.put("b41_13_30", new visuperobject(41,13,30,"Quả thật, ta nói cùng các ngươi, dòng dõi nầy chẳng qua trước khi mọi sự kia xảy tới."));
visuperobject.biblemap.put("b41_13_31", new visuperobject(41,13,31,"Trời đất sẽ qua đi, song lời ta không bao giờ qua đâu."));
visuperobject.biblemap.put("b41_13_32", new visuperobject(41,13,32,"về ngày và giờ đó, chẳng ai biết chi hết, các thiên sứ trên trời, hay là Con cũng chẳng biết nữa; song chỉ Cha mà thôi."));
visuperobject.biblemap.put("b41_13_33", new visuperobject(41,13,33,"Hãy giữ mình, tỉnh thức; vì các ngươi chẳng biết kỳ đó đến khi nào."));
visuperobject.biblemap.put("b41_13_34", new visuperobject(41,13,34,"Ấy cũng như một người kia đi đường xa, bỏ nhà, giao cho đầy tớ mỗi đứa cai quản một việc, và cũng biểu đứa canh cửa thức canh."));
visuperobject.biblemap.put("b41_13_35", new visuperobject(41,13,35,"Vậy, các ngươi hãy thức canh, vì không biết chủ nhà về lúc nào, hoặc chiều tối, nửa đêm, lúc gà gáy, hay là sớm mai,"));
visuperobject.biblemap.put("b41_13_36", new visuperobject(41,13,36,"e cho người về thình lình, gặp các ngươi ngủ chăng."));
visuperobject.biblemap.put("b41_13_37", new visuperobject(41,13,37,"Ðiều mà ta nói cùng các ngươi, ta cũng nói cho mọi người: Hãy tỉnh thức!"));
visuperobject.biblemap.put("b41_14_1", new visuperobject(41,14,1,"Hai ngày trước lễ Vượt Qua và lễ ăn bánh không men, các thầy tế lễ cả cùng các thầy thông giáo tìm mưu đặng bắt Ðức Chúa Jêsus và giết đi."));
visuperobject.biblemap.put("b41_14_2", new visuperobject(41,14,2,"Vì họ nói rằng: Chẳng nên làm việc nầy trong này lễ, sợ sanh sự xôn xao trong dân chúng."));
visuperobject.biblemap.put("b41_14_3", new visuperobject(41,14,3,"Ðức Chúa Jêsus ở tại làng Bê-tha-ni, trong nhà Si-môn là kẻ phung. Ngài đương ngồi bàn ăn, có một người đờn bà vào, đem một cái bình bằng ngọc, đựng đầy dầu cam tòng thật rất quí giá, đập bể ra mà đổ dầu thơm trên đầu Ðức Chúa Jêsus."));
visuperobject.biblemap.put("b41_14_4", new visuperobject(41,14,4,"Có vài người nổi giận nói cùng nhau rằng: Sao xài phí dầu thơm ấy như vậy?"));
visuperobject.biblemap.put("b41_14_5", new visuperobject(41,14,5,"Vì có thể bán dầu đó được hơn ba trăm đơ-ni-ê, mà bố thí cho kẻ khó khăn. Vậy, họ oán trách người."));
visuperobject.biblemap.put("b41_14_6", new visuperobject(41,14,6,"Nhưng Ðức Chúa Jêsus phán rằng: Hãy để mặc người; sao các ngươi làm rầy người mà chi? Người đã làm một việc tốt cho ta."));
visuperobject.biblemap.put("b41_14_7", new visuperobject(41,14,7,"Vì các ngươi hằng có kẻ khó khăn ở cùng mình, khi nào muốn làm phước cho họ cũng được; nhưng các ngươi chẳng có ta ở luôn với đâu."));
visuperobject.biblemap.put("b41_14_8", new visuperobject(41,14,8,"Người đã làm điều mình có thể làm được, đã xức xác cho ta trước để chôn."));
visuperobject.biblemap.put("b41_14_9", new visuperobject(41,14,9,"Quả thật, ta nói cùng các ngươi, trong khắp cả thế gian, hễ nơi nào Tin Lành nầy được giảng ra, việc người đã làm cũng sẽ được nhắc lại để nhớ đến người."));
visuperobject.biblemap.put("b41_14_10", new visuperobject(41,14,10,"Bấy giờ, Giu-đa Ích-ca-ri-ốt là một trong mười hai sứ đồ, đến nơi các thầy tế lễ cả, để nộp Ðức Chúa Jêsus cho."));
visuperobject.biblemap.put("b41_14_11", new visuperobject(41,14,11,"họ vui lòng mà nghe và hứa cho nó tiền bạc; rồi Giu-đa tìm dịp tiện để nộp Ngài."));
visuperobject.biblemap.put("b41_14_12", new visuperobject(41,14,12,"Ngày thứ nhứt về lễ ăn bánh không men, là ngày giết chiên con làm lễ Vượt Qua, các môn đồ thưa cùng Ðức Chúa Jêsus rằng: Thầy muốn chúng tôi đi dọn cho thầy ăn lễ Vượt Qua tại đâu?"));
visuperobject.biblemap.put("b41_14_13", new visuperobject(41,14,13,"Ngài sai hai môn đồ đi, và dặn rằng: Hãy vào thành, sẽ gặp một người xách vò nước; cứ theo sau,"));
visuperobject.biblemap.put("b41_14_14", new visuperobject(41,14,14,"hễ người vào nhà nào, các ngươi sẽ nói cùng chủ nhà ấy rằng: Thầy phán: Cái phòng ta sẽ dùng ăn lễ Vượt Qua với môn đồ ta ở đâu?"));
visuperobject.biblemap.put("b41_14_15", new visuperobject(41,14,15,"Chính kẻ đó sẽ chỉ cho các ngươi một cái phòng lớn trên lầu có đồ đạc sẵn sàng, hãy dọn tại đó cho chúng ta."));
visuperobject.biblemap.put("b41_14_16", new visuperobject(41,14,16,"Vậy, hai môn đồ đi ra mà vào thành, gặp mọi điều như lời Ngài đã phán, rồi dọn lễ Vượt Qua."));
visuperobject.biblemap.put("b41_14_17", new visuperobject(41,14,17,"Buổi chiều, Ngài đến với mười hai sứ đồ."));
visuperobject.biblemap.put("b41_14_18", new visuperobject(41,14,18,"Ðang ngồi ăn, Ðức Chúa Jêsus phán rằng: Quả thật, ta nói cùng các ngươi, trong vòng các ngươi có một kẻ ngồi ăn cùng ta, sẽ phản ta."));
visuperobject.biblemap.put("b41_14_19", new visuperobject(41,14,19,"Các môn đồ bèn buồn rầu lắm, cứ lần lượt mà thưa cùng Ngài rằng: Có phải tôi chăng?"));
visuperobject.biblemap.put("b41_14_20", new visuperobject(41,14,20,"Ngài đáp rằng: Ấy là một trong mười hai người, là người thò tay vào mâm cùng ta."));
visuperobject.biblemap.put("b41_14_21", new visuperobject(41,14,21,"Vì Con người đi, y như lời đã chép về Ngài; song khốn cho kẻ phản Con người! Thà nó chẳng sanh ra thì hơn."));
visuperobject.biblemap.put("b41_14_22", new visuperobject(41,14,22,"Khi đang ăn, Ðức Chúa Jêsus lấy bánh, tạ ơn, đoạn bẻ ra trao cho các môn đồ, mà phán rằng: Hãy lấy, nầy là thân thể ta."));
visuperobject.biblemap.put("b41_14_23", new visuperobject(41,14,23,"Ngài lại cầm chén, tạ ơn, rồi trao cho các môn đồ, và ai nấy đều uống."));
visuperobject.biblemap.put("b41_14_24", new visuperobject(41,14,24,"Ngài phán rằng: Nầy là huyết ta, huyết của sự giao ước đổ ra cho nhiều người."));
visuperobject.biblemap.put("b41_14_25", new visuperobject(41,14,25,"Quả thật, ta nói cùng các ngươi, ta không uống trái nho nầy nữa, cho đến ngày ta sẽ uống trái nho mới trong nước Ðức Chúa Trời."));
visuperobject.biblemap.put("b41_14_26", new visuperobject(41,14,26,"Khi đã hát thơ thánh rồi, Chúa và môn đồ đi ra đặng lên núi ô-li-ve."));
visuperobject.biblemap.put("b41_14_27", new visuperobject(41,14,27,"Ðức Chúa Jêsus phán cùng môn đồ rằng: Hết thảy các ngươi sẽ gặp dịp vấp phạm; vì có chép rằng: Ta sẽ đánh kẻ chăn chiên, thì bầy chiên sẽ tan lạc."));
visuperobject.biblemap.put("b41_14_28", new visuperobject(41,14,28,"Nhưng khi ta sống lại rồi, ta sẽ đi đến xứ Ga-li-lê trước các ngươi."));
visuperobject.biblemap.put("b41_14_29", new visuperobject(41,14,29,"Phi -e-rơ thưa rằng: Dầu nói người vấp phạm vì cớ thầy, nhưng tôi chẳng hề làm vậy."));
visuperobject.biblemap.put("b41_14_30", new visuperobject(41,14,30,"Ðức Chúa Jêsus đáp rằng: Quả thật, ta nói cùng ngươi, hôm nay, cũng trong đêm nay, trước khi gà gáy hai lượt, ngươi sẽ chối ta ba lần."));
visuperobject.biblemap.put("b41_14_31", new visuperobject(41,14,31,"Nhưng Phi -e-rơ lại thưa cách quả quyết hơn rằng: Dầu tôi phải chết cùng thầy, tôi cũng chẳng chối thầy đâu. Hết thảy các môn đồ khác cũng đều nói như vậy."));
visuperobject.biblemap.put("b41_14_32", new visuperobject(41,14,32,"Kế đó, đi đến một nơi kia, gọi là Ghết-sê-ma-nê, Ðức Chúa Jêsus phán cùng môn đồ rằng: Các ngươi hãy ngồi đây, đợi ta cầu nguyện."));
visuperobject.biblemap.put("b41_14_33", new visuperobject(41,14,33,"Ngài bèn đem Phi -e-rơ, Gia-cơ và Giăng đi, thì Ngài khởi sự kinh hãi và sầu não."));
visuperobject.biblemap.put("b41_14_34", new visuperobject(41,14,34,"Ngài phán cùng ba người rằng: Linh-hồn ta buồn rầu lắm cho đến chết; các ngươi hãy ở đây, và tỉnh thức."));
visuperobject.biblemap.put("b41_14_35", new visuperobject(41,14,35,"Rồi Ngài đi một đỗi xa hơn, sấp mình xuống đất mà cầu nguyện rằng: nếu có thể được, xin giờ nầy qua khỏi mình."));
visuperobject.biblemap.put("b41_14_36", new visuperobject(41,14,36,"Ngài rằng: A-ba lạy Cha, mọi việc Cha làm được cả; xin Cha cất chén nầy khỏi con; nhưng không theo điều con muốn, mà theo điều Cha muốn."));
visuperobject.biblemap.put("b41_14_37", new visuperobject(41,14,37,"Rồi Ngài trở lại, thấy ba người ngủ; bèn phán cùng Phi -e-rơ rằng: Si-môn, ngủ ư! Ngươi không thức được một giờ sao?"));
visuperobject.biblemap.put("b41_14_38", new visuperobject(41,14,38,"Hãy tỉnh thức và cầu nguyện, để các ngươi khỏi sa vào chước cám dỗ; tâm thần thì muốn lắm, mà xác thịt thì yếu đuối."));
visuperobject.biblemap.put("b41_14_39", new visuperobject(41,14,39,"Ngài lại đi lần nữa, và cầu nguyện, y như lời trước."));
visuperobject.biblemap.put("b41_14_40", new visuperobject(41,14,40,"Ngài trở lại, thấy môn đồ còn ngủ, vì con mắt đã đừ quá; và không biết trả lời cùng Ngài thể nào."));
visuperobject.biblemap.put("b41_14_41", new visuperobject(41,14,41,"Ngài trở lại lần thứ ba, phán cùng môn đồ rằng: Bây giờ các ngươi ngủ và nghỉ ngơi ư! thôi, giờ đã tới rồi; nầy, Con người hầu bị nộp trong tay kẻ có tội."));
visuperobject.biblemap.put("b41_14_42", new visuperobject(41,14,42,"Hãy chờ dậy, đi hè; kìa, đứa phản ta đã đến gần."));
visuperobject.biblemap.put("b41_14_43", new visuperobject(41,14,43,"Ngài đương còn phán, tức thì Giu-đa, là một trong mười hai sứ đồ thoạt đến với một toán đông cầm gươm và gậy, bởi các thầy tế lễ cả, các thầy thông giáo, và các trưởng lão phái đến."));
visuperobject.biblemap.put("b41_14_44", new visuperobject(41,14,44,"Và, đứa phản Ngài đã cho chúng dấu hiệu nầy: Hễ tôi hôn ai, ấy là người đó; hãy bắt lấy và điệu đi cho cẩn thận."));
visuperobject.biblemap.put("b41_14_45", new visuperobject(41,14,45,"Vậy, khi Giu-đa đến rồi, liền lại gần Ngài mà nói rằng: Lạy thầy! Rồi nó hôn Ngài."));
visuperobject.biblemap.put("b41_14_46", new visuperobject(41,14,46,"Chúng bèn tra tay bắt Ðức Chúa Jêsus."));
visuperobject.biblemap.put("b41_14_47", new visuperobject(41,14,47,"Có một người trong những kẻ ở đó rút gươm ra, đánh một đứa đầy tớ của thầy cả thượng phẩm, chém đứt tai đi."));
visuperobject.biblemap.put("b41_14_48", new visuperobject(41,14,48,"Ðức Chúa Jêsus cất tiếng phán cùng chúng rằng: Các ngươi đem gươm và gậy đến bắt ta như ta là kẻ trộm cướp."));
visuperobject.biblemap.put("b41_14_49", new visuperobject(41,14,49,"Ta hằng ngày ở giữa các ngươi, giảng dạy trong đền thờ, mà các ngươi không bắt ta; nhưng điều ấy xảy đến, để lời Kinh Thánh được ứng nghiệm."));
visuperobject.biblemap.put("b41_14_50", new visuperobject(41,14,50,"Bấy giờ, mọi người đều bỏ Ngài và trốn đi cả."));
visuperobject.biblemap.put("b41_14_51", new visuperobject(41,14,51,"Có một người trẻ tuổi kia theo Ngài, chỉ có cái khăn bằng gai trùm mình; chúng bắt người."));
visuperobject.biblemap.put("b41_14_52", new visuperobject(41,14,52,"Nhưng người bỏ khăn lại, ở truồng chạy trốn khỏi tay chúng."));
visuperobject.biblemap.put("b41_14_53", new visuperobject(41,14,53,"Chúng điệu Ðức Chúa Jêsus đến nơi thầy cả thượng phẩm, có hết thảy thầy tế lễ cả, trưởng lão, và thầy thông giáo nhóm họp tại đó."));
visuperobject.biblemap.put("b41_14_54", new visuperobject(41,14,54,"Phi -e-rơ theo sau Ngài xa xa, cho đến nơi sân trong của thầy cả thượng phẩm; rồi ngồi với quân lính gần đống lửa mà sưởi."));
visuperobject.biblemap.put("b41_14_55", new visuperobject(41,14,55,"Vả, các thầy tế lễ cả, cùng cả tòa công luận đều tìm chứng gì để nghịch cùng Ðức Chúa Jêsus đặng giết Ngài, song không kiếm được chi hết."));
visuperobject.biblemap.put("b41_14_56", new visuperobject(41,14,56,"Vì có nhiều kẻ làm chứng dối nghịch cùng Ngài; nhưng lời họ khai chẳng hiệp nhau."));
visuperobject.biblemap.put("b41_14_57", new visuperobject(41,14,57,"Bấy giờ có mấy người đứng lên làm chứng dối nghịch cùng Ng@ i rằng:"));
visuperobject.biblemap.put("b41_14_58", new visuperobject(41,14,58,"Chúng tôi có nghe người nói: Ta sẽ phá đền thờ nầy bởi tay người ta cất lên, khỏi ba ngày, ta sẽ cất một đền thờ khác không phải bởi tay người ta cất."));
visuperobject.biblemap.put("b41_14_59", new visuperobject(41,14,59,"Song về điều nầy, lời chứng của họ cũng chẳng hiệp nhau nữa."));
visuperobject.biblemap.put("b41_14_60", new visuperobject(41,14,60,"Khi ấy, thầy cả thượng phẩm đứng dậy giữa hội đồng, tra hỏi Ðức Chúa Jêsus mà rằng: Ngươi chẳng đối đáp chi hết về những điều các kẻ nầy cáo người sao?"));
visuperobject.biblemap.put("b41_14_61", new visuperobject(41,14,61,"Nhưng Ðức Chúa Jêsus làm thinh, không trả lời chi hết. Thầy cả thượng phẩm lại hỏi: Ấy chính ngươi là Ðấng Christ, Con Ðức Chúa Trời đáng ngợi khen phải không?"));
visuperobject.biblemap.put("b41_14_62", new visuperobject(41,14,62,"Ðức Chúa Jêsus phán rằng: Ta chính phải đó; các ngươi sẽ thấy Con người ngồi bên hữu quyền phép Ðức Chúa Trời, và ngự giữa đám mây trên trời mà đến."));
visuperobject.biblemap.put("b41_14_63", new visuperobject(41,14,63,"Bấy giờ thầy cả thượng phẩm xé áo mình ra mà rằng: Chúng ta có cần kiếm chứng cớ khác nữa làm chi?"));
visuperobject.biblemap.put("b41_14_64", new visuperobject(41,14,64,"Các ngươi có nghe lời lộng ngôn chăng? Các ngươi nghĩ thế nào: Ai nấy đều đoán Ngài đáng chết."));
visuperobject.biblemap.put("b41_14_65", new visuperobject(41,14,65,"Có kẻ nhổ trên Ngài, đậy mặt Ngài lại, đấm Ngài, và nói với Ngài rằng: Hãy nói tiên tri đi! Các lính canh lấy gậy đánh Ngài."));
visuperobject.biblemap.put("b41_14_66", new visuperobject(41,14,66,"Phi -e-rơ đương ở dưới nơi sân, có một đầy tớ gái của thầy cả thượng phẩm đến,"));
visuperobject.biblemap.put("b41_14_67", new visuperobject(41,14,67,"thấy Phi -e-rơ đương sưởi, ngó người mà rằng: ngươi trước cũng ở với Jêsus Na-xa-rét!"));
visuperobject.biblemap.put("b41_14_68", new visuperobject(41,14,68,"Nhưng người chối rằng: Ta không biết, ta không hiểu ngươi nói chi. Ðoạn, người bước ra tiền đàng, thì gà gáy."));
visuperobject.biblemap.put("b41_14_69", new visuperobject(41,14,69,"Ðầy tớ gái đó thầy người, lại nói cùng những người ở đó rằng: Người nầy cũng là bọn đó."));
visuperobject.biblemap.put("b41_14_70", new visuperobject(41,14,70,"Nhưng người lại chối một lần nữa. Khỏi một chặp, những kẻ đứng đó nói cùng Phi -e-rơ rằng: Chắc thật, ngươi cũng là bọn đó, vì ngươi là người Ga-li-lê."));
visuperobject.biblemap.put("b41_14_71", new visuperobject(41,14,71,"Người bèn rủa mà thề rằng: Ta chẳng hề quen biết với người mà các ngươi nói đó!"));
visuperobject.biblemap.put("b41_14_72", new visuperobject(41,14,72,"Tức thì gà gáy lần thứ hai; Phi -e-rơ bèn nhớ lại lời Ðức Chúa Jêsus đã phán rằng: Trước khi gà gáy hai lượt, ngươi sẽ chối ta ba lần. Người tưởng đến thì khóc."));
visuperobject.biblemap.put("b41_15_1", new visuperobject(41,15,1,"Vừa lúc ban mai, các thầy tế lễ cả bàn luận với các trưởng lão, các thầy thông giáo cùng cả tòa công luận; khi đã trói Ðức Chúa Jêsus rồi, thì giải nộp cho Phi-lát."));
visuperobject.biblemap.put("b41_15_2", new visuperobject(41,15,2,"Phi-lát hỏi Ngài rằng: Ấy chính ngươi là Vua dân Giu-đa phải không? Ðức Chúa Jêsus đáp rằng: Thật như lời."));
visuperobject.biblemap.put("b41_15_3", new visuperobject(41,15,3,"Các thầy tế lễ cả cáo Ngài nhiều điều."));
visuperobject.biblemap.put("b41_15_4", new visuperobject(41,15,4,"Phi-lát lại tra gạn Ngài, mà rằng: Ngươi chẳng đối đáp chi hết sao? Hãy coi họ cáo tội ngươi biết bao nhiêu!"));
visuperobject.biblemap.put("b41_15_5", new visuperobject(41,15,5,"Nhưng Ðức Chúa Jêsus chẳng trả lời chi nữa, nên mỗi Phi-lát lấy làm lạ."));
visuperobject.biblemap.put("b41_15_6", new visuperobject(41,15,6,"Vả, hễ đến ngày lễ Vượt Qua, quan vốn quen thói tha cho dân một tên tù, tùy theo dân xin."));
visuperobject.biblemap.put("b41_15_7", new visuperobject(41,15,7,"Bấy giờ có một đứa tên là Ba-ra-ba bị tù với kẻ làm loạn, vì trong khi nổi loạn, chúng nó phạm tội giết người."));
visuperobject.biblemap.put("b41_15_8", new visuperobject(41,15,8,"Ðoàn dân đã lên, bèn xin Phi-lát làm cho mình theo như lệ thường."));
visuperobject.biblemap.put("b41_15_9", new visuperobject(41,15,9,"Phi-lát trả lời rằng: Các ngươi muốn ta tha Vua dân Giu-đa cho chăng?"));
visuperobject.biblemap.put("b41_15_10", new visuperobject(41,15,10,"Vì người biết rõ các thầy tế lễ cả đã nộp Ngài bởi lòng ghen ghét."));
visuperobject.biblemap.put("b41_15_11", new visuperobject(41,15,11,"Nhưng các thầy tế lễ cả xui dân xin người tha tên Ba-ra-ba cho mình thì hơn."));
visuperobject.biblemap.put("b41_15_12", new visuperobject(41,15,12,"Phi-lát lại cất tiếng hỏi rằng: Vậy thì các ngươi muốn ta dùng cách nào xử người mà các ngươi gọi là Vua dân Giu-đa?"));
visuperobject.biblemap.put("b41_15_13", new visuperobject(41,15,13,"Chúng lại kêu lên rằng: Hãy đóng đinh nó vào thập tự giá!"));
visuperobject.biblemap.put("b41_15_14", new visuperobject(41,15,14,"Phi-lát nói cùng chúng rằng: Song người nầy đã làm điều ác gì? Chúng lại kêu lớn tiếng hơn rằng: Hãy đóng đinh nó vào thập tự giá!"));
visuperobject.biblemap.put("b41_15_15", new visuperobject(41,15,15,"Phi-lát muốn cho đẹp lòng dân, bèn tha tên Ba-ra-ba; và sai đánh đòn Ðức Chúa Jêsus, rồi giao Ngài cho chúng đem đóng đinh trên cây thập tự."));
visuperobject.biblemap.put("b41_15_16", new visuperobject(41,15,16,"Lính điệu Ðức Chúa Jêsus vào sân trong, tức là trong chỗ trường án; và nhóm cả cơ binh lại đó."));
visuperobject.biblemap.put("b41_15_17", new visuperobject(41,15,17,"Họ choàng cho Ngài một cái áo điều, và đội trên đầu Ngài một cái mão bằng gai họ đã đương,"));
visuperobject.biblemap.put("b41_15_18", new visuperobject(41,15,18,"rồi chào Ngài rằng: Kính lạy Vua dân Giu-đa!"));
visuperobject.biblemap.put("b41_15_19", new visuperobject(41,15,19,"Lại lấy cây sậy đánh đầu Ngài, nhổ trên Ngài, và quì xuống trước mặt Ngài mà lạy."));
visuperobject.biblemap.put("b41_15_20", new visuperobject(41,15,20,"Khi họ đã nhạo cười Ngài như vậy rồi, thì cổi áo điều ra, mặc áo của Ngài lại cho Ngài, và đem ra để đóng đinh trên cây thập tự."));
visuperobject.biblemap.put("b41_15_21", new visuperobject(41,15,21,"Có một tên Si-môn kia, người thành Sy-ren, là cha A-léc-xan-đơ và Ru-phu, ở ngoài ruộng về, đi ngang qua đó; họ bắt phải vác cây thập tự."));
visuperobject.biblemap.put("b41_15_22", new visuperobject(41,15,22,"Họ đem Ðức Chúa Jêsus đến nơi kia, gọi là Gô-gô-tha, nghĩa là chỗ Sọ."));
visuperobject.biblemap.put("b41_15_23", new visuperobject(41,15,23,"Họ cho Ngài uống rượu hòa với một dược; song Ngài không uống."));
visuperobject.biblemap.put("b41_15_24", new visuperobject(41,15,24,"Khi đóng đinh Ngài trên cây thập tự rồi, họ chia áo xống Ngài cho nhau, bắt thăm để biết ai được gì."));
visuperobject.biblemap.put("b41_15_25", new visuperobject(41,15,25,"Lúc đóng đinh Ngài, là giờ thứ ba."));
visuperobject.biblemap.put("b41_15_26", new visuperobject(41,15,26,"Án Ngài có ghi vào cái bảng rằng: Vua dân Giu-đa."));
visuperobject.biblemap.put("b41_15_27", new visuperobject(41,15,27,"Cũng có hai đứa ăn cướp bị đóng đinh với Ngài, một đứa tại cây thập tự bên hữu Ngài, một đứa tại cây thập tự bên tả."));
visuperobject.biblemap.put("b41_15_28", new visuperobject(41,15,28,"Như vậy được ứng nghiệm lời Kinh Thánh rằng: Ngài đã bị kể vào hàng kẻ dữ."));
visuperobject.biblemap.put("b41_15_29", new visuperobject(41,15,29,"Những kẻ đi ngang qua đó chế báng Ngài, lắc đầu mà nói rằng: Ê! ngươi là kẻ phá đền thờ, và cất lại trong ba ngày,"));
visuperobject.biblemap.put("b41_15_30", new visuperobject(41,15,30,"hãy cứu lấy mình, xuống khỏi cây thập tự đi!"));
visuperobject.biblemap.put("b41_15_31", new visuperobject(41,15,31,"Các thầy tế lễ cả và các thầy thông giáo cũng nói với nhau như vậy, mà nhạo cười Ngài rằng: Hắn đã cứu kẻ khác; mà cứu mình không được!"));
visuperobject.biblemap.put("b41_15_32", new visuperobject(41,15,32,"Hỡi Ðấng Christ, Vua dân Y-sơ-ra-ên, bây giờ hãy xuống khỏi cây thập tự, để cho chúng ta thấy và tin! Hai đứa bị đóng đinh cùng Ngài cũng nhiếc móc Ngài nữa."));
visuperobject.biblemap.put("b41_15_33", new visuperobject(41,15,33,"Ðến giờ thứ sáu, khắp đất đều tối tăm mù mịt cho tới giờ thứ chín."));
visuperobject.biblemap.put("b41_15_34", new visuperobject(41,15,34,"Ðến giờ thứ chín, Ðức Chúa Jêsus kêu lớn tiếng rằng: Ê-lô -i, Ê-lô -i, lam-ma-sa-bách-ta-ni? nghĩa là: Ðức Chúa Trời tôi ơi, Ðức Chúa Jêsus tôi ơi, sao Ngài lìa bỏ tôi?"));
visuperobject.biblemap.put("b41_15_35", new visuperobject(41,15,35,"Có mấy người đứng đó nghe vậy, thì nói rằng: Coi kìa, hắn kêu Ê-li."));
visuperobject.biblemap.put("b41_15_36", new visuperobject(41,15,36,"Có một người trong bọn họ chạy lấy bông đá thấm đầy giấm, để trên đầu cây sậy, đưa cho Ngài uống, mà nói rằng: Hãy để, chúng ta coi thử Ê-li có đến đem hắn xuống chăng!"));
visuperobject.biblemap.put("b41_15_37", new visuperobject(41,15,37,"Nhưng Ðức Chúa Jêsus kêu một tiếng lớn, rồi trút linh hồn."));
visuperobject.biblemap.put("b41_15_38", new visuperobject(41,15,38,"Màn trong đền thờ xé ra làm đôi từ trên chí dưới."));
visuperobject.biblemap.put("b41_15_39", new visuperobject(41,15,39,"Thầy đội đứng đối ngang Ngài, thấy Ngài trút linh hồn như vậy, thì nói rằng: Người nầy quả thật là Con Ðức Chúa Trời."));
visuperobject.biblemap.put("b41_15_40", new visuperobject(41,15,40,"Lại có những đờn bà đứng xa xa mà xem. Trong số ấy có Ma-ri, Ma-đơ-len, Ma-ri là mẹ Gia-cơ nhỏ, và Giô-sê, cùng Sa-lô-mê,"));
visuperobject.biblemap.put("b41_15_41", new visuperobject(41,15,41,"là ba người đã theo hầu việc Ngài khi Ngài ở xứ Ga-li-lê; cũng có nhiều đờn bà khác nữa, là những người đã theo Ngài lên thành Giê-ru-sa-lem."));
visuperobject.biblemap.put("b41_15_42", new visuperobject(41,15,42,"Bấy giờ là ngày sắm sửa, nghĩa là hôm trước ngày Sa-bát; khi đã chiều tối,"));
visuperobject.biblemap.put("b41_15_43", new visuperobject(41,15,43,"có một người tên là Giô-sép, Ở thành A-ri-ma-thê, là nghị viên tòa công luận có danh vọng, cũng trông đợi nước Ðức Chúa Trời. Người bạo gan đến Phi-lát mà xin xác Ðức Chúa Jêsus."));
visuperobject.biblemap.put("b41_15_44", new visuperobject(41,15,44,"Phi-lát lấy làm lạ về Ngài chết mau như vậy, bèn đòi thầy đội đến mà hỏi thử Ngài chết đã lâu chưa."));
visuperobject.biblemap.put("b41_15_45", new visuperobject(41,15,45,"Vừa nghe tin thầy đội trình, thì giao xác cho Giô-sép."));
visuperobject.biblemap.put("b41_15_46", new visuperobject(41,15,46,"Người đã mua vải liệm, cất xác Ðức Chúa Jêsus xuống khỏi cây thập tự, dùng vải liệm vấn lấy Ngài, rồi để xác trong huyệt đã đục trong hòn đá lớn, đoạn, lăn một hòn đá chận cửa huyệt lại."));
visuperobject.biblemap.put("b41_15_47", new visuperobject(41,15,47,"Vả, Ma-ri Ma-đơ-len cùng Ma-ri mẹ Giô-sê, đều thấy chỗ táng Ngài."));
visuperobject.biblemap.put("b41_16_1", new visuperobject(41,16,1,"Ngày Sa-bát qua rồi, Ma-ri ma-đơ-len, Ma-ri mẹ Gia-cơ, cùng Sa-lô-mê mua thuốc thơm đặng đi xức xác Ðức Chúa Jêsus."));
visuperobject.biblemap.put("b41_16_2", new visuperobject(41,16,2,"Ngày thứ nhứt trong tuần lễ, sáng sớm, mặt trời mới mọc, ba người đến nơi mộ,"));
visuperobject.biblemap.put("b41_16_3", new visuperobject(41,16,3,"nói cùng nhau rằng: Ai sẽ lăn hòn đá lấp cửa mộ ra cho chúng ta?"));
visuperobject.biblemap.put("b41_16_4", new visuperobject(41,16,4,"Khi ngó xem, thấy hòn đá đã lăn ra rồi; vả, hòn đá lớn lắm."));
visuperobject.biblemap.put("b41_16_5", new visuperobject(41,16,5,"Ðoạn, họ vào nơi mộ, thấy một người trẻ tuổi ngồi bên hữu, mặc áo dài trắng, thì thất kinh."));
visuperobject.biblemap.put("b41_16_6", new visuperobject(41,16,6,"Song người nói cùng họ rằng: Ðừng sợ chi, các ngươi tìm Ðức Chúa Jêsus Na-xa-rét, là Ðấng đã chịu đóng đinh; Ngài sống lại rồi, chẳng còn ở đây; hãy xem nơi đã táng xác Ngài."));
visuperobject.biblemap.put("b41_16_7", new visuperobject(41,16,7,"Nhưng hãy đi nói cho các môn đồ Ngài và cho Phi -e-rơ rằng: Ngài đi đến xứ Ga-li-lê trước các ngươi; các ngươi sẽ thấy Ngài tại đó, như Ngài đã phán cùng các ngươi vậy."));
visuperobject.biblemap.put("b41_16_8", new visuperobject(41,16,8,"Các bà ấy ra khỏi mồ, trốn đi, vì run sợ sửng sốt; chẳng dám nói cùng ai hết, bởi kinh khiếp lắm."));
visuperobject.biblemap.put("b41_16_9", new visuperobject(41,16,9,"Vả, Ðức Chúa Jêsus đã sống lại buổi sớm mai ngày thứ nhứt trong tuần lễ, thì trước hết hiện ra cho Ma-ri Ma-đơ-len, là người mà Ngài đã trừ cho khỏi bảy quỉ dữ."));
visuperobject.biblemap.put("b41_16_10", new visuperobject(41,16,10,"Người đi đem tin cho những kẻ theo Ngài khi trước, và nay đang tang chế khóc lóc."));
visuperobject.biblemap.put("b41_16_11", new visuperobject(41,16,11,"Nhưng các người ấy vừa nghe nói Ngài sống, và người từng thấy Ngài, thì không tin."));
visuperobject.biblemap.put("b41_16_12", new visuperobject(41,16,12,"Kế đó, Ðức Chúa Jêsus lấy hình khác hiện ra cho hai người trong bọn môn đồ đang đi đường về nhà quê."));
visuperobject.biblemap.put("b41_16_13", new visuperobject(41,16,13,"Hai người nầy đi báo tin cho các môn đồ khác, nhưng ai nấy cũng không tin."));
visuperobject.biblemap.put("b41_16_14", new visuperobject(41,16,14,"Sau nữa, Ngài hiện ra cho mười một sứ đồ đang khi ngồi ăn, mà quở trách về sự không tin và lòng cứng cỏi, vì chẳng tin những kẻ từng thấy Ngài đã sống lại."));
visuperobject.biblemap.put("b41_16_15", new visuperobject(41,16,15,"Ngài phán cùng các sứ đồ rằng: Hãy đi khắp thế gian, giảng Tin Lành cho mọi người."));
visuperobject.biblemap.put("b41_16_16", new visuperobject(41,16,16,"Ai tin và chịu phép báp-tem, sẽ được rỗi; nhưng ai chẳng tin, sẽ bị đoán phạt."));
visuperobject.biblemap.put("b41_16_17", new visuperobject(41,16,17,"Vậy những kẻ tin sẽ được các dấu lạ nầy: lấy danh ta mà trừ quỉ; dùng tiếng mới mà nói;"));
visuperobject.biblemap.put("b41_16_18", new visuperobject(41,16,18,"bắt rắn trong tay; nếu uống giống chi độc, cũng chẳng hại gì; hễ đặt tay trên kẻ đau, thì kẻ đau sẽ lành."));
visuperobject.biblemap.put("b41_16_19", new visuperobject(41,16,19,"Ðức Chúa Jêsus phán như vậy rồi, thì được đem lên trời, ngồi bên hữu Ðức Chúa Trời."));
visuperobject.biblemap.put("b41_16_20", new visuperobject(41,16,20,"Về phần các môn đồ, thì đi ra giảng đạo khắp mọi nơi, Chúa cùng làm với môn đồ, và lấy các phép lạ cặp theo lời giảng mà làm cho vững đạo."));

}

}