package vi.bible;

public class vibible50 {

public vibible50() {

putmapbible50();

}

private void putmapbible50() {

visuperobject.biblemap.put("b50_1_1", new visuperobject(50,1,1,"Phao-lô và Ti-mô-thê, tôi tớ của Ðức Chúa Jêsus Christ, gởi cho hết thảy các thánh đồ trong Ðức Chúa Jêsus Christ, ở thành Phi líp, cùng cho các giám mục và các chấp sự:"));
visuperobject.biblemap.put("b50_1_2", new visuperobject(50,1,2,"nguyền xin anh em được ân điển và sự bình an ban cho bởi Ðức Chúa Trời, Cha chúng ta và bởi Ðức Chúa Jêsus Christ!"));
visuperobject.biblemap.put("b50_1_3", new visuperobject(50,1,3,"Mỗi khi tôi nhớ đến anh em, thì cảm tạ Ðức Chúa Trời tôi,"));
visuperobject.biblemap.put("b50_1_4", new visuperobject(50,1,4,"và mỗi khi cầu nguyện, tôi hằng cầu cho hết thảy anh em cách hớn hở,"));
visuperobject.biblemap.put("b50_1_5", new visuperobject(50,1,5,"vì cớ từ buổi ban đầu cho đến bây giờ, anh em đã được thông công trong sự tấn tới của đạo Tin Lành;"));
visuperobject.biblemap.put("b50_1_6", new visuperobject(50,1,6,"tôi tin chắc rằng Ðấng đã khởi làm việc lành trong anh em, sẽ làm trọn hết cho đến ngày của Ðức Chúa Jêsus Christ."));
visuperobject.biblemap.put("b50_1_7", new visuperobject(50,1,7,"Tôi nghĩ đến hết thảy anh em dường ấy, là phải lắm; vì anh em ở trong lòng tôi, dầu trong vòng xiềng xích tôi, hoặc trong khi binh vực và làm chứng đạo Tin Lành, thì anh em cũng có dự phần ân điển đã ban cho tôi."));
visuperobject.biblemap.put("b50_1_8", new visuperobject(50,1,8,"Ðức Chúa Trời cũng chứng rằng: tôi lấy lòng yêu dấu của Ðức Chúa Jêsus Christ và tríu mến anh em."));
visuperobject.biblemap.put("b50_1_9", new visuperobject(50,1,9,"Lại, điều tôi xin trong khi cầu nguyện, ấy là lòng yêu thương của anh em càng ngày càng chan chứa hơn, trong sự thông biết và sự suy hiểu,"));
visuperobject.biblemap.put("b50_1_10", new visuperobject(50,1,10,"để nghiệm thử những sự tốt lành hơn, hầu cho anh em được tinh sạch không chỗ trách được, cho đến ngày Ðấng Christ,"));
visuperobject.biblemap.put("b50_1_11", new visuperobject(50,1,11,"được đầy trái công bình đến bởi Ðức Chúa Jêsus Christ, làm cho sáng danh và khen ngợi Ðức Chúa Trời."));
visuperobject.biblemap.put("b50_1_12", new visuperobject(50,1,12,"Hỡi anh em, tôi muốn anh em biết rằng điều xảy đến cho tôi đã giúp thêm sự tấn tới cho đạo Tin Lành,"));
visuperobject.biblemap.put("b50_1_13", new visuperobject(50,1,13,"đến nỗi chốn công đường và các nơi khác đều rõ tôi vì Ðấng Christ mà chịu xiềng xích."));
visuperobject.biblemap.put("b50_1_14", new visuperobject(50,1,14,"Phần nhiều trong anh em nhơn tôi bị xiềng xích mà có lòng tin rất lớn trong Ðấng Christ, dám truyền đạo Ðức Chúa Trời chẳng sợ hãi gì."));
visuperobject.biblemap.put("b50_1_15", new visuperobject(50,1,15,"Thật có một đôi người vì lòng ganh tị và cãi lẫy mà rao truyền Ðấng Christ, nhưng cũng có kẻ lấy ý tốt mà rao truyền."));
visuperobject.biblemap.put("b50_1_16", new visuperobject(50,1,16,"Những người nầy làm bởi lòng yêu mến, biết rằng: tôi được lập nên để binh vực đạo Tin Lành."));
visuperobject.biblemap.put("b50_1_17", new visuperobject(50,1,17,"Còn những người kia, vì ý phe đãng xui giục, có lòng rao truyền Ðấng Christ cách không ngay thật, tưởng thêm khổ não cho sự xiềng xích tôi."));
visuperobject.biblemap.put("b50_1_18", new visuperobject(50,1,18,"Song có hề gì! Dẫu thế nào đi nữa, hoặc làm bộ, hoặc thật thà, Ðấng Christ cũng được rao truyền; ấy vì đó tôi đương mừng rỡ, và sẽ còn mừng rỡ nữa."));
visuperobject.biblemap.put("b50_1_19", new visuperobject(50,1,19,"Vì tôi biết rằng nhờ anh em cầu nguyện, và nhờ Thánh Linh của Ðức Chúa Jêsus Christ giúp đỡ, thì điều nầy sẽ trở làm ích cho sự rỗi tôi."));
visuperobject.biblemap.put("b50_1_20", new visuperobject(50,1,20,"Tôi có lòng trông cậy chắc chắn nầy, việc chi tôi cũng chẳng hổ thẹn cả, nhưng bao giờ cũng thế, tôi nói cách tự do mọi bề, như vậy, dầu tôi sống hay chết, Ðấng Christ sẽ được cả sáng trong mình tôi."));
visuperobject.biblemap.put("b50_1_21", new visuperobject(50,1,21,"Vì Ðấng Christ là sự sống của tôi, và sự chết là điều ích lợi cho tôi vậy."));
visuperobject.biblemap.put("b50_1_22", new visuperobject(50,1,22,"Ví thử tôi cứ sống trong xác thịt, là ích lợi cho công khó tôi, thì tôi chẳng biết nên lựa điều chi."));
visuperobject.biblemap.put("b50_1_23", new visuperobject(50,1,23,"Tôi bị ép giữa hai bề muốn đi ở với Ðấng Christ, là điều rất tốt hơn;"));
visuperobject.biblemap.put("b50_1_24", new visuperobject(50,1,24,"nhưng tôi cứ ở trong xác thịt, ấy là sự cần hơn cho anh em."));
visuperobject.biblemap.put("b50_1_25", new visuperobject(50,1,25,"Trong sự tin chắc đó, tôi biết rằng tôi sẽ cứ còn lại và ở với hết thảy anh em, để giúp đức tin anh em được tấn tới và vui vẻ;"));
visuperobject.biblemap.put("b50_1_26", new visuperobject(50,1,26,"hầu cho khi tôi lại đến cùng anh em, anh em sẽ nhơn tôi mà có nhiều cứ khoe mình luôn trong Ðức Chúa Jêsus Christ."));
visuperobject.biblemap.put("b50_1_27", new visuperobject(50,1,27,"Duy anh em phải ăn ở một cách xứng đáng với đạo Tin Lành của Ðấng Christ, để hoặc khi đến thăm anh em, hoặc khi vắng mặt, tôi cũng biết rằng anh em một lòng đứng vững, đồng tâm chống cự vì đức tin của đạo Tin Lành, phàm sự chẳng để cho kẻ thù nghịch ngăm dọa mình;"));
visuperobject.biblemap.put("b50_1_28", new visuperobject(50,1,28,"điều đó là một chứng nghiệm chúng nó phải hư mất, còn anh em được cứu rỗi; và ấy là từ nơi Ðức Chúa Trời,"));
visuperobject.biblemap.put("b50_1_29", new visuperobject(50,1,29,"Ngài nhơn Ðấng Christ, ban ơn cho anh em, không những tin Ðấng Christ mà thôi, lại phải chịu khổ vì Ngài nữa,"));
visuperobject.biblemap.put("b50_1_30", new visuperobject(50,1,30,"là phải chịu cùng một cuộc chiến tranh như anh em đã thấy ở nơi tôi và hiện nay còn nghe nói có nơi tôi."));
visuperobject.biblemap.put("b50_2_1", new visuperobject(50,2,1,"Vậy nếu trong Ðấng Christ có điều yên ủi nào, nếu vì lòng yêu thương có điều cứu giúp nào, nếu có sự thông công nơi Thánh Linh, nếu có lòng yêu mến và lòng thương xót,"));
visuperobject.biblemap.put("b50_2_2", new visuperobject(50,2,2,"thì anh em hãy hiệp ý với nhau, đồng tình yêu thương, đồng tâm, đồng tư tưởng mà làm cho tôi vui mừng trọn vẹn."));
visuperobject.biblemap.put("b50_2_3", new visuperobject(50,2,3,"Chớ làm sự chi vì lòng tranh cạnh hoặc vì hư vinh, nhưng hãy khiêm nhường, coi người khác như tôn trọng hơn mình."));
visuperobject.biblemap.put("b50_2_4", new visuperobject(50,2,4,"Mỗi người trong anh em chớ chăm về lợi riêng mình, nhưng phải chăm về lợi kẻ khác nữa."));
visuperobject.biblemap.put("b50_2_5", new visuperobject(50,2,5,"Hãy có đồng một tâm tình như Ðấng Christ đã có,"));
visuperobject.biblemap.put("b50_2_6", new visuperobject(50,2,6,"Ngài vốn có hình Ðức Chúa Trời, song chẳng coi sự bình đẳng mình với Ðức Chúa Trời là sự nên nắm giữ;"));
visuperobject.biblemap.put("b50_2_7", new visuperobject(50,2,7,"chính Ngài đã tự bỏ mình đi, lấy hình tôi tớ và trở nên giống như loài người;"));
visuperobject.biblemap.put("b50_2_8", new visuperobject(50,2,8,"Ngài đã hiện ra như một người, tự hạ mình xuống, vâng phục cho đến chết, thậm chí chết trên cây thập tự."));
visuperobject.biblemap.put("b50_2_9", new visuperobject(50,2,9,"Cũng vì đó nên Ðức Chúa Trời đã đem Ngài lên rất cao, và ban cho Ngài danh trên hết mọi danh,"));
visuperobject.biblemap.put("b50_2_10", new visuperobject(50,2,10,"hầu cho nghe đến danh Ðức Chúa Jêsus, mọi đầu gối trên trời, dưới đất, bên dưới đất, thảy đều quì xuống,"));
visuperobject.biblemap.put("b50_2_11", new visuperobject(50,2,11,"và mọi lưỡi thảy đều xưng Jêsus Christ là Chúa, mà tôn vinh Ðức Chúa Trời, là Ðức Chúa Cha."));
visuperobject.biblemap.put("b50_2_12", new visuperobject(50,2,12,"Ấy vậy, hỡi những kẻ rất yêu dấu của tôi, như anh em đã vâng lời luôn luôn, chẳng những khi tôi có mặt mà thôi, lại bây giờ là lúc tôi vắng mặt, hãy cùng hơn nữa, mà lấy lòng sợ sệt run rẩy làm nên sự cứu chuộc mình."));
visuperobject.biblemap.put("b50_2_13", new visuperobject(50,2,13,"Vì ấy chính Ðức Chúa Trời cảm động lòng anh em vừa muốn vừa làm theo ý tốt Ngài."));
visuperobject.biblemap.put("b50_2_14", new visuperobject(50,2,14,"Phàm làm việc gì chớ nên lằm bằm và lưỡng lự,"));
visuperobject.biblemap.put("b50_2_15", new visuperobject(50,2,15,"hầu cho anh em ở giữa dòng dõi hung ác ngang nghịch, được nên con cái của Ðức Chúa Trời, không vít, không tì, không chỗ trách được, lại giữa dòng dõi đó, giữ lấy đạo sự sống, chiếu sáng như đuốc trong thế gian;"));
visuperobject.biblemap.put("b50_2_16", new visuperobject(50,2,16,"cho đến nỗi tới ngày của Ðấng Christ, tôi có thể khoe mình rằng chẳng đã chạy vô ích và khó nhọc luống công."));
visuperobject.biblemap.put("b50_2_17", new visuperobject(50,2,17,"Ví dầu huyết tôi phải dùng để tưới trên của tế lễ và của dâng đức tin anh em tôi cũng vui lòng, lại đồng vui với anh em hết thảy."));
visuperobject.biblemap.put("b50_2_18", new visuperobject(50,2,18,"anh em cũng vậy, hãy vui lòng về điều đó, và hãy cùng vui với tôi."));
visuperobject.biblemap.put("b50_2_19", new visuperobject(50,2,19,"Vả, tôi mong rằng nhờ ơn Ðức Chúa Jêsus, kíp sai Ti-mô-thê đến cùng anh em, để tới phiên tôi, tôi nghe tin anh em, mà được yên lòng."));
visuperobject.biblemap.put("b50_2_20", new visuperobject(50,2,20,"Thật vậy, tôi không có ai như người đồng tình với tôi để thật lòng lo về việc anh em:"));
visuperobject.biblemap.put("b50_2_21", new visuperobject(50,2,21,"ai nấy đều tìm lợi riêng của mình, chớ không tìm của Ðức Chúa Jêsus Christ."));
visuperobject.biblemap.put("b50_2_22", new visuperobject(50,2,22,"Nhưng anh em đã biết sự trung tín từng trải của người; và biết người là trung thành với tôi về việc Tin Lành, như con ở với cha vậy."));
visuperobject.biblemap.put("b50_2_23", new visuperobject(50,2,23,"Nên mong sai người đến nơi anh em liền, vừa khi tôi sẽ rõ sự tình tôi ra thể nào;"));
visuperobject.biblemap.put("b50_2_24", new visuperobject(50,2,24,"tôi lại có lòng trông cậy nầy trong Chúa, là chính mình tôi không bao lâu sẽ đến."));
visuperobject.biblemap.put("b50_2_25", new visuperobject(50,2,25,"trong khi chờ đợi, tôi tưởng cần phải sai Ép-ba-phô-đích, anh em tôi, bạn cùng làm việc và cùng chiến trận với tôi, đến cùng anh em, người cũng là ủy viên của anh em ở kề tôi, đặng cung cấp mọi sự cần dùng cho tôi vậy."));
visuperobject.biblemap.put("b50_2_26", new visuperobject(50,2,26,"Vì người rất ước ao thấy anh em hết thảy, và đã lo về anh em nghe mình bị đau ốm."));
visuperobject.biblemap.put("b50_2_27", new visuperobject(50,2,27,"Và, người mắc bịnh gần chết; nhưng Ðức Chúa Trời đã thương xót người, và chẳng những người mà thôi, cả đến tôi nữa, đặng tôi khỏi buồn rầu càng thêm buồn rầu."));
visuperobject.biblemap.put("b50_2_28", new visuperobject(50,2,28,"Vậy, tôi đã sai người đi rất kíp, hầu cho anh em lại thấy người thì mừng rỡ, và tôi cũng bớt buồn rầu."));
visuperobject.biblemap.put("b50_2_29", new visuperobject(50,2,29,"Thế thì, hãy lấy sự vui mừng trọn vẹn mà tiếp rước người trong Chúa, và tôn kính những người như vậy;"));
visuperobject.biblemap.put("b50_2_30", new visuperobject(50,2,30,"bởi, ấy là vì công việc của Ðấng Christ mà người đã gần chết, liều sự sống mình để bù lại các việc mà chính anh em không thể giúp tôi."));
visuperobject.biblemap.put("b50_3_1", new visuperobject(50,3,1,"Hỡi anh em, sau hết, anh em hãy vui mừng trong Chúa! Tôi chẳng hề sợ phiền mà lại viết những điều nầy cho anh em nữa, vì ấy là có ích cho anh em."));
visuperobject.biblemap.put("b50_3_2", new visuperobject(50,3,2,"Hãy coi chừng loài chó; hãy coi chừng kẻ làm công gian ác; hãy coi chừng phép cắt bì giả."));
visuperobject.biblemap.put("b50_3_3", new visuperobject(50,3,3,"Vì, ấy chính chúng ta là kẻ chịu phép cắt bì thật, là kẻ cậy Ðức Thánh Linh Ðức Chúa Trời mà hầu việc Ðức Chúa Trời, khoe mình trong Ðấng Christ, và không để lòng tin cậy trong xác thịt bao giờ."));
visuperobject.biblemap.put("b50_3_4", new visuperobject(50,3,4,"Ấy chẳng phải tôi không có thể cậy mình trong xác thịt đâu. Ví bằng kẻ khác tưởng rằng có thể cậy mình trong xác thịt, thì tôi lại có thể bội phần nữa,"));
visuperobject.biblemap.put("b50_3_5", new visuperobject(50,3,5,"tôi chịu phép cắt bì ngày thứ tám, về dòng Y-sơ-ra-ên, chi phái Bên-gia-min, người Hê-bơ-rơ, con của người Hê-bơ-rơ; về luật pháp, thì thuộc phe Pha-ra-si;"));
visuperobject.biblemap.put("b50_3_6", new visuperobject(50,3,6,"về lòng sốt sắng, thì là kẻ bắt bớ hội thánh; còn như về sự công bình của luật pháp, thì không chỗ trách được."));
visuperobject.biblemap.put("b50_3_7", new visuperobject(50,3,7,"Nhưng vì cớ Ðấng Christ, tôi đã coi sự lời cho tôi như là sự lỗ vậy."));
visuperobject.biblemap.put("b50_3_8", new visuperobject(50,3,8,"Tôi cũng coi hết thảy mọi sự như là sự lỗ, vì sự nhận biết Ðức Chúa Jêsus Christ là quí hơn hết, Ngài là Chúa tôi, và tôi vì Ngài mà liều bỏ mọi điều lợi đó. Thật, tôi xem những điều đó như rơm rác, hầu cho được Ðấng Christ"));
visuperobject.biblemap.put("b50_3_9", new visuperobject(50,3,9,"và được ở trong Ngài, được sự công bình, không phải của tôi bởi luật pháp mà đến, bèn là bởi tin đến Ðấng Christ mà được, tức là công bình đến bởi Ðức Chúa Trời và đã lập lên trên đức tin;"));
visuperobject.biblemap.put("b50_3_10", new visuperobject(50,3,10,"cho đến nỗi tôi được biết Ngài, và quyền phép sự sống lại của Ngài, và sự thông công thương khó của Ngài, làm cho tôi nên giống như Ngài trong sự chết Ngài,"));
visuperobject.biblemap.put("b50_3_11", new visuperobject(50,3,11,"mong cho tôi được đến sự sống lại từ trong kẻ chết."));
visuperobject.biblemap.put("b50_3_12", new visuperobject(50,3,12,"Ấy không phải tôi đã giựt giải rồi, hay là đã đến nơi trọn lành rồi đâu, nhưng tôi đương chạy hầu cho giựt được, vì chính tôi đã được Ðức Chúa Jêsus Christ giựt lấy rồi."));
visuperobject.biblemap.put("b50_3_13", new visuperobject(50,3,13,"Hỡi Anh em, về phần tôi, tôi không tưởng rằng đã đạt đến mục đích,"));
visuperobject.biblemap.put("b50_3_14", new visuperobject(50,3,14,"nhưng tôi cứ làm một điều: quên lửng sự ở đằng sau, mà bươn theo sự ở đằng trước, tôi nhắm mục đích mà chạy, để giựt giải về sự kêu gọi trên trời của Ðức Chúa Trời trong Ðức Chúa Jêsus Christ."));
visuperobject.biblemap.put("b50_3_15", new visuperobject(50,3,15,"Vậy, hễ những người trọn vẹn trong chúng ta, phải có ý tưởng đó; và nếu anh em tưởng đằng khác, Ðức Chúa Trời cũng sẽ lấy sự đó soi sáng cho anh em."));
visuperobject.biblemap.put("b50_3_16", new visuperobject(50,3,16,"Duy Chúng ta đã đạt đến bậc nào, thì chúng ta phải đồng đi vậy."));
visuperobject.biblemap.put("b50_3_17", new visuperobject(50,3,17,"Hỡi Anh em, hãy bắt chước tôi, lại xem những kẻ ăn ở theo mẫu mực mà anh em thấy trong chúng tôi."));
visuperobject.biblemap.put("b50_3_18", new visuperobject(50,3,18,"Vì tôi đã thường nói điều nầy cho anh em, nay tôi lại khóc mà nói nữa: lắm người có cách ăn ở như là kẻ thù nghịch thập tự giá của Ðấng Christ."));
visuperobject.biblemap.put("b50_3_19", new visuperobject(50,3,19,"Sự cuối cùng của họ là hư mất; họ lấy sự xấu hổ của mình làm vinh hiển, chỉ tư tưởng về các việc thế gian mà thôi."));
visuperobject.biblemap.put("b50_3_20", new visuperobject(50,3,20,"Nhưng chúng ta là công dân trên trời; ấy là từ nơi đó mà chúng ta trông đợi Cứu Chúa mình là Ðức Chúa Jêsus Christ,"));
visuperobject.biblemap.put("b50_3_21", new visuperobject(50,3,21,"Ngài sẽ biến hóa thân thể hèn mạt chúng ta ra giống như thân thể vinh hiển Ngài, y theo quyền phép Ngài có để phục muôn vật."));
visuperobject.biblemap.put("b50_4_1", new visuperobject(50,4,1,"Vậy thì, hỡi anh em rất yêu rất thiết, là sự vui mừng và mão triều thiên cho tôi, kẻ rất yêu dấu ơi, hãy đứng vững trong Chúa."));
visuperobject.biblemap.put("b50_4_2", new visuperobject(50,4,2,"Tôi khuyên Ê-yô-đi và khuyên Sin-ty-cơ phải hiệp một ý trong Chúa."));
visuperobject.biblemap.put("b50_4_3", new visuperobject(50,4,3,"Hỡi kẻ đồng liêu trung tín, tôi cũng xin anh giúp hai người đờn bà ấy, nhơn bai bà ấy cùng tôi đã vì đạo Tin Lành mà chiến đấu; Cơ-lê-măn và các bạn khác đồng làm việc với tôi cũng vậy, có tên những người đó biên vào sách sự sống rồi."));
visuperobject.biblemap.put("b50_4_4", new visuperobject(50,4,4,"Hãy vui mừng trong Chúa luôn luôn. Tôi lại còn nói nữa: hãy vui mừng đi."));
visuperobject.biblemap.put("b50_4_5", new visuperobject(50,4,5,"Hãy cho mọi người đều biết nết nhu mì của anh em. Chúa đã gần rồi."));
visuperobject.biblemap.put("b50_4_6", new visuperobject(50,4,6,"Chớ lo phiền chi hết, nhưng trong mọi sự hãy dùng lời cầu nguyện, nài xin, và sự tạ ơn và trình các sự cầu xin của mình cho Ðức Chúa Trời."));
visuperobject.biblemap.put("b50_4_7", new visuperobject(50,4,7,"Sự bình an của Ðức Chúa Trời vượt quá mọi sự hiểu biết, sẽ gìn giữ lòng và ý tưởng anh em trong Ðức Chúa Jêsus Christ."));
visuperobject.biblemap.put("b50_4_8", new visuperobject(50,4,8,"Rốt lại, hỡi anh em, phàm điều cho chơn thật, điều chi đáng tôn, điều chi công bình, điều chi thánh sạch, điều chi đáng yêu chuộng, điều chi có tiếng tốt, điều chi có nhân đức đáng khen, thì anh em phải nghĩ đến."));
visuperobject.biblemap.put("b50_4_9", new visuperobject(50,4,9,"Hễ sự gì anh em đã học, đã nhận, đã nghe ở nơi tôi và đã thấy trong tôi, hãy làm đi, thì Ðức Chúa Trời của sự bình an sẽ ở cùng anh em."));
visuperobject.biblemap.put("b50_4_10", new visuperobject(50,4,10,"Tôi được rất vui mừng trong Chúa, vì nay anh em lại lo tưởng về tôi lần nữa; anh em vẫn tưởng đến điều đó lắm, nhưng không có dịp tiện."));
visuperobject.biblemap.put("b50_4_11", new visuperobject(50,4,11,"Không phải tôi muốn nói đến sự cần dùng của tôi; vì tôi đã tập hễ gặp cảnh ngộ nào, cũng thỏa lòng ở vậy."));
visuperobject.biblemap.put("b50_4_12", new visuperobject(50,4,12,"Tôi biết chịu nghèo hèn, cũng biết được dư dật. Trong mọi sự và mọi nơi, tôi đã tập cả, dầu no hay đói, dầu dư hay thiếu cũng được."));
visuperobject.biblemap.put("b50_4_13", new visuperobject(50,4,13,"Tôi làm được mọi sự nhờ Ðấng ban thêm sức cho tôi."));
visuperobject.biblemap.put("b50_4_14", new visuperobject(50,4,14,"Nhưng anh em giúp tôi cơn hoạn nạn, thì đã làm điều thiện."));
visuperobject.biblemap.put("b50_4_15", new visuperobject(50,4,15,"Hỡi người Phi-líp, anh em cũng biết rằng lúc tôi khởi giảng Tin Lành trong khi lìa xứ Ma-xê-đoan, thi ngoài hội thánh của anh em, chẳng có hội nào khác hiệp với tôi để lập thành sự trao đổi trong chúng ta cả;"));
visuperobject.biblemap.put("b50_4_16", new visuperobject(50,4,16,"vì tại Tê-sa-lô-ni-ca, anh em đã một hai lần gởi đồ cung cấp về sự cần dùng cho tôi vậy."));
visuperobject.biblemap.put("b50_4_17", new visuperobject(50,4,17,"Ấy không phải tôi cầu lễ vật, nhưng cầu sự kết quả nhiều bởi lễ vật đến cho anh em."));
visuperobject.biblemap.put("b50_4_18", new visuperobject(50,4,18,"Vậy, tôi đã nhận được hết, và đương dư dật; tôi được đầy dẫy vì đã nhận đồ nơi Ép-ba-phô-đích mà anh em gởi cho tôi, như một thứ hương có mùi thơm, tức là một của lễ Ðức Chúa Trời đáng nhận, và đẹp lòng Ngài."));
visuperobject.biblemap.put("b50_4_19", new visuperobject(50,4,19,"Ðức Chúa Trời tôi sẽ làm cho đầy đủ mọi sự cần dùng của anh em y theo sự giàu có của Ngài ở nơi vinh hiển trong Ðức Chúa Jêsus Christ."));
visuperobject.biblemap.put("b50_4_20", new visuperobject(50,4,20,"Nguyền xin sự vinh hiển về nơi Ðức Chúa Trời, là Cha chúng ta, đời đời vô cùng! A-men."));
visuperobject.biblemap.put("b50_4_21", new visuperobject(50,4,21,"Hãy chào hết thảy các thánh đồ trong Ðức Chúa Jêsus Christ; các anh em ở cùng tôi chào anh em"));
visuperobject.biblemap.put("b50_4_22", new visuperobject(50,4,22,"Hết thảy các thánh đồ chào anh em, nhứt là về người nhà Sê-sa."));
visuperobject.biblemap.put("b50_4_23", new visuperobject(50,4,23,"Nguyền xin ân điển của Ðức Chúa Jêsus Christ ở trong lòng anh em!"));

}

}