package vi.bible;

public class vibible53 {

public vibible53() {

putmapbible53();

}

private void putmapbible53() {

visuperobject.biblemap.put("b53_1_1", new visuperobject(53,1,1,"Phao-lô, Sin-vanh và Ti-mô-thê gởi cho Hội thánh Tê-sa-lô-ni-ca, là Hội thánh ở trong Ðức Chúa Trời, Cha chúng ta, và trong Ðức Chúa Jêsus Christ, là Chúa:"));
visuperobject.biblemap.put("b53_1_2", new visuperobject(53,1,2,"nguyền xin anh em được ân điển và sự bình an ban cho bởi Ðức Chúa Jêsus Christ!"));
visuperobject.biblemap.put("b53_1_3", new visuperobject(53,1,3,"Hỡi anh em, chúng tôi phải vì anh em cảm tạ Ðức Chúa Trời luôn luôn; điều đó là phải lắm, vì đức tin anh em rất tấn tới, và lòng yêu thương của mỗi người trong hết thảy anh em đối với nhau càng ngày càng thêm."));
visuperobject.biblemap.put("b53_1_4", new visuperobject(53,1,4,"Chúng tôi cũng vì anh em mà khoe mình cùng các Hội thánh của Ðức Chúa Trời, vì lòng nhịn nhục và đức tin anh em trong mọi sự bắt bớ khốn khó đương chịu."));
visuperobject.biblemap.put("b53_1_5", new visuperobject(53,1,5,"Ðó đủ chứng cớ về sự đoán xét công bình của Ðức Chúa Trời, Ngài muốn khiến anh em nên xứng đáng cho nước Ngài, và vì nước đó mà anh em chịu khổ."));
visuperobject.biblemap.put("b53_1_6", new visuperobject(53,1,6,"Vả, theo sự công bình Ðức Chúa Trời, thì ắt là Ngài lấy điều khổ báo cho những kẻ làm khổ anh em,"));
visuperobject.biblemap.put("b53_1_7", new visuperobject(53,1,7,"và cho anh em, là kẻ chịu khổ, được nghỉ ngơi với chúng tôi, trong khi Ðức Chúa Jêsus từ trời hiện đến với các thiên sứ của quyền phép Ngài, giữa ngọn lửa hừng,"));
visuperobject.biblemap.put("b53_1_8", new visuperobject(53,1,8,"báo thù những kẻ chẳng hề nhận biết Ðức Chúa Trời, và không vâng phục Tin Lành của Ðức Chúa Jêsus Christ chúng ta."));
visuperobject.biblemap.put("b53_1_9", new visuperobject(53,1,9,"Họ sẽ bị hình phạt hư mất đời đời, xa cách mặt Chúa và sự vinh hiển của quyền phép Ngài,"));
visuperobject.biblemap.put("b53_1_10", new visuperobject(53,1,10,"tức là khi Ngài sẽ đến trong ngày đó, để được sáng danh trong các thánh đồ, được khen ngợi trong mọi kẻ tin; vì anh em đã tin lời chúng tôi làm chứng trước mặt anh em."));
visuperobject.biblemap.put("b53_1_11", new visuperobject(53,1,11,"Cho nên, chúng tôi vì anh em cầu nguyện không thôi, hầu cho Ðức Chúa Trời chúng ta khiến anh em xứng đáng với sự gọi của Ngài, và cho Ngài lấy quyền phép làm trọn trong anh em mọi ý định thương xót của lòng nhơn Ngài và công việc của đức tin;"));
visuperobject.biblemap.put("b53_1_12", new visuperobject(53,1,12,"đến nỗi làm sáng danh Ðức Chúa Jêsus chúng ta trong anh em, và anh em trong Ngài, tùy theo ân điển của Ðức Chúa Trời chúng ta và của Ðức Chúa Jêsus Christ."));
visuperobject.biblemap.put("b53_2_1", new visuperobject(53,2,1,"Luận về sự đến của Ðức Chúa Jêsus Christ chúng ta, và về sự chúng ta hội hiệp cùng Ngài,"));
visuperobject.biblemap.put("b53_2_2", new visuperobject(53,2,2,"thì, hỡi anh em, xin chớ vội bối rối và kinh hoàng hoặc bởi thần cảm giả mạo, hoặc bởi lời nói hay là bởi bức thơ nào tựa như chúng tôi đã gởi mà nói rằng ngày Chúa gần đến."));
visuperobject.biblemap.put("b53_2_3", new visuperobject(53,2,3,"Mặc ai dùng cách nào, cũng đừng để họ lừa dối mình. Vì phải có sự bỏ đạo đến trước, và có người tội ác, con của sự hư mất hiện ra,"));
visuperobject.biblemap.put("b53_2_4", new visuperobject(53,2,4,"tức là kẻ đối địch, tôn mình lên trên mọi sự mà người ta xưng là Ðức Chúa Trời hoặc người ta thờ lạy, rất đỗi ngồi trong đền Ðức Chúa Trời, chính mình tự xưng là Ðức Chúa Trời."));
visuperobject.biblemap.put("b53_2_5", new visuperobject(53,2,5,"Anh em há không nhớ khi tôi còn ở với anh em, thì đã nói về những sự đó sao?"));
visuperobject.biblemap.put("b53_2_6", new visuperobject(53,2,6,"Hiện nay anh em biết rõ điều làm ngăn trở nó, hầu cho nó chỉ hiện ra đúng kỳ nó thôi."));
visuperobject.biblemap.put("b53_2_7", new visuperobject(53,2,7,"Vì đã có sự mầu nhiệm của điều bội nghịch đương hành động rồi; song Ðấng còn ngăn trở cần phải cất đi."));
visuperobject.biblemap.put("b53_2_8", new visuperobject(53,2,8,"Bấy giờ kẻ nghịch cùng luật pháp kia sẽ hiện ra, Ðức Chúa Jêsus sẽ dùng hơi miệng Ngài mà hủy diệt nó, và trừ bỏ nó bởi sự chói sáng của sự Ngài đến."));
visuperobject.biblemap.put("b53_2_9", new visuperobject(53,2,9,"Kẻ đó sẽ lấy quyền của quỉ Sa-tan mà hiện đến, làm đủ mọi thứ phép lạ, dấu dị và việc kỳ dối giả;"));
visuperobject.biblemap.put("b53_2_10", new visuperobject(53,2,10,"dùng mọi cách phỉnh dổ không công bình mà dổ những kẻ hư mất, vì chúng nó đã không nhận lãnh sự yêu thương của lẽ thật để được cứu rỗi."));
visuperobject.biblemap.put("b53_2_11", new visuperobject(53,2,11,"Ấy vì thế mà Ðức Chúa Trời cho chúng nó mắc phải sự lầm lạc, là sự khiến chúng nó tin điều dối giả,"));
visuperobject.biblemap.put("b53_2_12", new visuperobject(53,2,12,"hầu cho hết thảy những người không tin lẽ thật, song chuộng sự không công bình, điều bị phục dưới sự phán xét của Ngài."));
visuperobject.biblemap.put("b53_2_13", new visuperobject(53,2,13,"Hỡi anh em yêu dấu của Chúa, còn như chúng tôi, phải vì anh em tạ ơn Ðức Chúa Trời không thôi, vì vừa lúc ban đầu, Ngài đã chọn anh em bởi sự nên thánh của Thánh Linh, và bởi tin lẽ thật, đặng ban sự cứu rỗi cho anh em."));
visuperobject.biblemap.put("b53_2_14", new visuperobject(53,2,14,"Ấy cũng là vì đó mà Ngài đã dùng Tin Lành chúng tôi gọi anh em, đặng anh em hưởng được sự vinh hiển của Ðức Chúa Jêsus Christ chúng ta."));
visuperobject.biblemap.put("b53_2_15", new visuperobject(53,2,15,"Hỡi anh em, vậy thì hãy đứng vững, hãy vâng giữ những điều dạy dỗ mà chúng tôi đã truyền cho anh em, hoặc bằng lời nói, hoặc bằng thơ từ của chúng tôi."));
visuperobject.biblemap.put("b53_2_16", new visuperobject(53,2,16,"Nguyền xin chính Ðức Chúa Jêsus Christ chúng ta, và Ðức Chúa Trời Cha chúng ta, là Ðấng đã yêu thương chúng ta, và đã lấy ân điển mà ban cho chúng ta sự yên ủi đời đời và sự trông cậy tốt lành,"));
visuperobject.biblemap.put("b53_2_17", new visuperobject(53,2,17,"hãy yên ủi lòng anh em, khiến anh em được bền vững trong mọi việc lành cùng mọi lời nói lành."));
visuperobject.biblemap.put("b53_3_1", new visuperobject(53,3,1,"Vả lại, hỡi anh em, hãy cầu nguyện cho chúng tôi, hầu cho đạo Chúa được đồn ra và được sáng danh, như thể ở trong anh em vậy,"));
visuperobject.biblemap.put("b53_3_2", new visuperobject(53,3,2,"lại hầu cho chúng tôi được giải thoát khỏi kẻ vô lý và người ác; vì chẳng phải hết thảy đều có đức tin."));
visuperobject.biblemap.put("b53_3_3", new visuperobject(53,3,3,"Chúa là thành tín, sẽ làm cho anh em bền vững và giữ cho khỏi Ác giả."));
visuperobject.biblemap.put("b53_3_4", new visuperobject(53,3,4,"Về phần anh em, chúng tôi có lòng tin cậy trong Chúa rằng anh em đương làm và sẽ làm những việc chúng tôi dặn biểu."));
visuperobject.biblemap.put("b53_3_5", new visuperobject(53,3,5,"Nguyền xin Chúa soi dẫn lòng anh em tới đến sự yêu mến Ðức Chúa Trời, và sự nhịn nhục của Ðấng Christ!"));
visuperobject.biblemap.put("b53_3_6", new visuperobject(53,3,6,"Hỡi anh em, nhơn danh Ðức Chúa Jêsus Christ chúng ta, chúng tôi khuyên anh em phải lánh người anh em nào không biết tu đức hạnh mình, và không bước theo các điều dạy dỗ mà anh em đã nhận lãnh nơi chúng tôi."));
visuperobject.biblemap.put("b53_3_7", new visuperobject(53,3,7,"Chính anh em biết điều mình phải làm để học đòi chúng tôi; vì chúng tôi không có ăn ở sái bậy giữa anh em,"));
visuperobject.biblemap.put("b53_3_8", new visuperobject(53,3,8,"chưa từng ăn dưng của ai, nhưng đêm ngày làm lụng khó nhọc, để khỏi lụy đến một người nào trong anh em hết."));
visuperobject.biblemap.put("b53_3_9", new visuperobject(53,3,9,"Chẳng phải chúng tôi không có quyền lợi được ăn dưng, nhưng muốn làm gương cho anh em, để anh em bắt chước."));
visuperobject.biblemap.put("b53_3_10", new visuperobject(53,3,10,"Khi chúng tôi ở cùng anh em, cũng đã rao bảo cho anh em rằng: nếu ai không khứng làm việc, thì cũng không nên ăn nữa."));
visuperobject.biblemap.put("b53_3_11", new visuperobject(53,3,11,"Vả, chúng tôi nghe trong anh em có kẻ ăn ở bậy bạ, chẳng hề làm lụng, trở chăm những sự vô ích thôi."));
visuperobject.biblemap.put("b53_3_12", new visuperobject(53,3,12,"Chúng tôi nhơn danh Ðức Chúa Jêsus Christ, bảo và khuyên những kẻ đó phải yên lặng mà làm việc, hầu cho ăn bánh của mình làm ra."));
visuperobject.biblemap.put("b53_3_13", new visuperobject(53,3,13,"Hỡi anh em, phần anh em chớ nên chán mệt làm sự lành."));
visuperobject.biblemap.put("b53_3_14", new visuperobject(53,3,14,"Ví bằng có ai không tuân theo lời chúng tôi nói trong thơ nầy, thì hãy ghi lấy, chớ giao thông với họ, hầu cho họ nhơn đó biết xấu hổ."));
visuperobject.biblemap.put("b53_3_15", new visuperobject(53,3,15,"Tuy vậy, chớ coi người đó như kẻ nghịch thù, nhưng hãy răn bảo người như anh em vậy."));
visuperobject.biblemap.put("b53_3_16", new visuperobject(53,3,16,"Nguyền xin chính Chúa bình an ban sự bình an cho anh em trong mọi khi, đủ mọi cách! Lại xin Chúa ở cùng anh em hết thảy!"));
visuperobject.biblemap.put("b53_3_17", new visuperobject(53,3,17,"Chính tay tôi, là Phao-lô, viết lời chào thăm nầy cho anh em: ấy đó là dấu ký của tôi trong mọi thơ từ của tôi, tôi viết như vậy."));
visuperobject.biblemap.put("b53_3_18", new visuperobject(53,3,18,"Nguyền xin ân điển của Ðức Chúa Jêsus Christ chúng ta ở cùng anh em hết thảy!"));

}

}