private void putmapbible56() {

visuperobject.biblemap.put("b56_1_1", new visuperobject(56,1,1,"Ta, Phao-lô, tôi tớ của Ðức Chúa Trời và sứ đồ của Ðức Chúa Jêsus Christ, để đưa các người được chọn của Ðức Chúa Trời đến đức tin và sự thông hiểu lẽ thật, là sự sanh lòng nhân đức,"));
visuperobject.biblemap.put("b56_1_2", new visuperobject(56,1,2,"trông cậy sự sống đời đời, là sự sống mà Ðức Chúa Trời không thể nói dối đã hứa từ muôn đời về trước,"));
visuperobject.biblemap.put("b56_1_3", new visuperobject(56,1,3,"tới kỳ chỉ định, Ngài đã bày tỏ lời của Ngài ra bởi sự giảng dạy, là sự ta đã chịu giao phó theo mạng lịnh Ðức Chúa Trời, là Cứu Chúa chúng ta,"));
visuperobject.biblemap.put("b56_1_4", new visuperobject(56,1,4,"gởi cho Tít, là con thật ta trong đức tin chung: nguyền xin con được ân điển và sự bình an ban cho bởi Ðức Chúa Trời, là Cha, và bởi Ðức Chúa Jêsus Christ, Cứu Chúa chúng ta!"));
visuperobject.biblemap.put("b56_1_5", new visuperobject(56,1,5,"Ta đã để con ở lại Cơ-rết, đặng sắp đặt mọi việc chưa thu xếp, và theo như ta đã răn bảo cho con mà lập những trưởng lão trong mỗi thành."));
visuperobject.biblemap.put("b56_1_6", new visuperobject(56,1,6,"Mỗi người trong vòng trưởng lão đó phải cho không chỗ trách được, chỉ chồng của một vợ; con cái phải tin Chúa, không được bị cáo là buông tuồng hoặc ngỗ nghịch."));
visuperobject.biblemap.put("b56_1_7", new visuperobject(56,1,7,"Vì người giám mục làm kẻ quản lý nhà Ðức Chúa Trời thì phải cho không chỗ trách được. Chẳng nên kiêu ngạo, giận dữ, ghiền rượu, hung tàn, tham lợi;"));
visuperobject.biblemap.put("b56_1_8", new visuperobject(56,1,8,"nhưng phải hay tiếp đãi khách, bạn với người hiền, khôn ngoan, công bình, thánh sạch, tiết độ,"));
visuperobject.biblemap.put("b56_1_9", new visuperobject(56,1,9,"hằng giữ đạo thật y như đã nghe dạy, hầu cho có thể theo đạo lành mà khuyên dổ người ta và bác lại kẻ chống trả."));
visuperobject.biblemap.put("b56_1_10", new visuperobject(56,1,10,"Vả, có nhiều người nhứt là trong những người chịu cắt bì, chẳng chịu vâng phục, hay nói hư không và phỉnh dỗ, đáng phải bịt miệng họ đi."));
visuperobject.biblemap.put("b56_1_11", new visuperobject(56,1,11,"Họ vì mối lợi đáng bỉ mà dạy điều không nên dạy, và phá đổ cả nhà người ta."));
visuperobject.biblemap.put("b56_1_12", new visuperobject(56,1,12,"Một người trong bọn họ, tức là bậc tiên tri của họ, có nói rằng: Người Cơ-rết thay nói dối, là thú dữ, ham ăn mà làm biếng."));
visuperobject.biblemap.put("b56_1_13", new visuperobject(56,1,13,"Lời chứng ấy quả thật lắm. Vậy hãy quở nặng họ, hầu cho họ có đức tin vẹn lành,"));
visuperobject.biblemap.put("b56_1_14", new visuperobject(56,1,14,"chớ nghe truyện huyễn của người Giu-đa, và điều răn của người ta trái với lẽ thật."));
visuperobject.biblemap.put("b56_1_15", new visuperobject(56,1,15,"Mọi sự là tinh sạch cho những người tinh sạch, nhưng, cho những kẻ dơ dáy và chẳng tin, thì không sự gì là tinh sạch cả; trái lại, tâm thần và lương tâm họ là dơ dáy nữa."));
visuperobject.biblemap.put("b56_1_16", new visuperobject(56,1,16,"Họ xưng mình biết Ðức Chúa Trời, nhưng theo những việc làm thì đều từ chối Ngài, thật là đáng ghét, trái nghịch và không thể làm một việc lành nào hết."));
visuperobject.biblemap.put("b56_2_1", new visuperobject(56,2,1,"Nhưng con hãy dạy điều hiệp với đạo lành."));
visuperobject.biblemap.put("b56_2_2", new visuperobject(56,2,2,"Khuyên những người già cả phải tiết độ, nghiêm trang, khôn ngoan, có đức tin, lòng yêu thương và tánh nhịn nhục vẹn lành."));
visuperobject.biblemap.put("b56_2_3", new visuperobject(56,2,3,"Các bà già cũng vậy, phải có thái độ hiệp với sự thánh; đừng nói xấu, đừng uống rượu quá độ; phải lấy điều khôn ngoan dạy bảo;"));
visuperobject.biblemap.put("b56_2_4", new visuperobject(56,2,4,"phải dạy đờn bà trẻ tuổi biết yêu chồng con mình,"));
visuperobject.biblemap.put("b56_2_5", new visuperobject(56,2,5,"có nết na, trinh chánh, trông nom việc nhà; lại biết ở lành, vâng phục chồng mình, hầu cho đạo Ðức Chúa Trời khỏi bị một lời chê bai nào."));
visuperobject.biblemap.put("b56_2_6", new visuperobject(56,2,6,"Cũng phải khuyên những người tuổi trẻ ở cho có tiết độ."));
visuperobject.biblemap.put("b56_2_7", new visuperobject(56,2,7,"Hãy lấy mình con làm gương về việc lành cho họ, trong sự dạy dỗ phải cho thanh sạch, nghiêm trang,"));
visuperobject.biblemap.put("b56_2_8", new visuperobject(56,2,8,"nói năng phải lời, không chỗ trách được, đặng kẻ nghịch hổ thẹn, không được nói xấu chúng ta điều chi."));
visuperobject.biblemap.put("b56_2_9", new visuperobject(56,2,9,"Hãy khuyên những tôi tớ phải vâng phục chủ mình, phải làm đẹp lòng chủ trong mọi việc, chớ cãi trả,"));
visuperobject.biblemap.put("b56_2_10", new visuperobject(56,2,10,"chớ ăn cắp vật chi, nhưng phải hằng tỏ lòng trung thành trọn vẹn, để làm cho tôn quí đạo Ðức Chúa Trời, là Cứu Chúa chúng ta, trong mọi đường."));
visuperobject.biblemap.put("b56_2_11", new visuperobject(56,2,11,"Vả, ân điển Ðức Chúa Trời hay cứu mọi người, đã được bày tỏ ra rồi."));
visuperobject.biblemap.put("b56_2_12", new visuperobject(56,2,12,"Aân ấy dạy chúng ta chừa bỏ sự không tôn kính và tài đức thế gian, phải sống ở đời nầy theo tiết độ, công bình, nhân đức,"));
visuperobject.biblemap.put("b56_2_13", new visuperobject(56,2,13,"đương chờ đợi sự trông cậy hạnh phước của chúng ta, và sự hiện ra của sự vinh hiển Ðức Chúa Trời lớn và Cứu Chúa chúng ta, là Ðức Chúa Jêsus Christ,"));
visuperobject.biblemap.put("b56_2_14", new visuperobject(56,2,14,"là Ðấng liều mình vì chúng ta, để chuộc chúng ta khỏi mọi tội và làm cho sạch, đặng lấy chúng ta khỏi mọi tội và làm một dân thuộc riêng về Ngài, là dân có lòng sốt sắng về các việc lành."));
visuperobject.biblemap.put("b56_2_15", new visuperobject(56,2,15,"Hãy dạy các điều đó, lấy quyền đầy đủ mà khuyên bảo quở trách. Chớ để ai khinh dể con."));
visuperobject.biblemap.put("b56_3_1", new visuperobject(56,3,1,"Hãy nhắc lại cho các tín đồ phải vâng phục những bậc cầm quyền chấp chánh, phải vâng lời các bậc ấy, sẵn sàng làm mọi việc lành,"));
visuperobject.biblemap.put("b56_3_2", new visuperobject(56,3,2,"chớ nói xấu ai, chớ tranh cạnh, hãy dong thứ, đối với mọi người tỏ ra một cách mềm mại trọn vẹn."));
visuperobject.biblemap.put("b56_3_3", new visuperobject(56,3,3,"Vì chưng chúng ta ngày trước cũng ngu muội, bội nghịch, bị lừa dối, bị đủ thứ tình dục dâm dật sai khiến, sống trong sự hung ác tham lam, đáng bị người ta ghét và tự chúng ta cũng ghét lẫn nhau."));
visuperobject.biblemap.put("b56_3_4", new visuperobject(56,3,4,"Nhưng từ khi lòng nhơn từ của Ðức Chúa Trời, là Cứu Chúa chúng ta, và tình thương yêu của Ngài đối với mọi người ta đã được bày ra, thì Ngài cứu chúng ta,"));
visuperobject.biblemap.put("b56_3_5", new visuperobject(56,3,5,"không phải cứu vì việc công bình chúng ta đã làm, nhưng cứ theo lòng thương xót Ngài, bởi sự rửa về sự lại sanh và sự đổi mới của Ðức Thánh Linh"));
visuperobject.biblemap.put("b56_3_6", new visuperobject(56,3,6,"mà Ngài đã rải ra trên chúng ta cách dư dật bởi Ðức Chúa Jêsus Christ, Cứu Chúa chúng ta;"));
visuperobject.biblemap.put("b56_3_7", new visuperobject(56,3,7,"hầu cho chúng ta nhờ ơn Ngài được xưng công bình, trở nên con kế tự của Ngài trong sự trông cậy của sự sống đời đời."));
visuperobject.biblemap.put("b56_3_8", new visuperobject(56,3,8,"Lời nầy là chắc chắn, ta muốn con nói quyết sự đó, hầu cho những kẻ đã tin Ðức Chúa Trời lo chăm chỉ làm việc lành: đó là điều tốt lành và có ích cho mọi người."));
visuperobject.biblemap.put("b56_3_9", new visuperobject(56,3,9,"Nhưng hãy lánh những điều cãi lẽ dại dột, những gia phổ, những sự cạnh tranh nghị luận về luật pháp, vì mấy sự đó đều là vô ích và hư không."));
visuperobject.biblemap.put("b56_3_10", new visuperobject(56,3,10,"Sau khi mình đã khuyên bảo kẻ theo tà giáo một hai lần rồi, thì hãy lánh họ,"));
visuperobject.biblemap.put("b56_3_11", new visuperobject(56,3,11,"vì biết rằng người như thế đã bội nghịch mà cứ phạm tội, thì tự đoán phạt lấy mình."));
visuperobject.biblemap.put("b56_3_12", new visuperobject(56,3,12,"Ðến chừng ta sẽ sai A-te-ma hoặc Ti-chi-cơ đến cùng con, thì hãy vội vả sang thành Ni-cô-bô-li nhập với ta; vì ta định qua mùa đông tại đó."));
visuperobject.biblemap.put("b56_3_13", new visuperobject(56,3,13,"Hãy lo liệu cho Xê-ma, là thầy dạy luật, và A-bô-lô, đừng thiếu đồ chi hết, trong khi họ ra đi."));
visuperobject.biblemap.put("b56_3_14", new visuperobject(56,3,14,"Những người bổn đạo ta cũng phải học tập chuyên làm việc lành, đặng giùm giúp khi túng ngặt, hầu cho họ khỏi đưng đi mà không ra trái."));
visuperobject.biblemap.put("b56_3_15", new visuperobject(56,3,15,"Hết thảy những người ở cùng ta gởi lời thăm con. Chào thăm những kẻ yêu chúng ta bởi lòng tin đạo. Nguyền xin ân điển ở với anh em hết thảy!"));

}

