package vi.bible;

public class vibible59 {

public vibible59() {

putmapbible59();

}

private void putmapbible59() {

visuperobject.biblemap.put("b59_1_1", new visuperobject(59,1,1,"Gia-cơ, tôi tớ Ðức Chúa Trời và Ðức Chúa Jêsus Christ, đạt cho mười hai chi phái ở tan lạc, chúc bình an!"));
visuperobject.biblemap.put("b59_1_2", new visuperobject(59,1,2,"Hỡi anh em, hãy coi sự thử thách trăm bề thoạt đến cho anh em như là điều vui mừng trọn vẹn,"));
visuperobject.biblemap.put("b59_1_3", new visuperobject(59,1,3,"vì biết rằng sự thử thách đức tin anh em sanh ra sự nhịn nhục."));
visuperobject.biblemap.put("b59_1_4", new visuperobject(59,1,4,"Nhưng sự nhịn nhục phải làm trọn việc nó, hầu cho chính mình anh em cũng trọn lành toàn vẹn, không thiếu thốn chút nào."));
visuperobject.biblemap.put("b59_1_5", new visuperobject(59,1,5,"Ví bằng trong anh em có kẻ kém khôn ngoan, hãy cầu xin Ðức Chúa Trời, là Ðấng ban cho mọi người cách rộng rãi, không trách móc ai, thì kẻ ấy sẽ được ban cho."));
visuperobject.biblemap.put("b59_1_6", new visuperobject(59,1,6,"Nhưng phải lấy đức tin mà cầu xin, chớ nghi ngờ; vì kẻ hay nghi ngờ giống như sóng biển, bị gió động và đưa đi đây đi đó."));
visuperobject.biblemap.put("b59_1_7", new visuperobject(59,1,7,"Người như thế chớ nên tưởng mình lãnh được vật chi từ nơi Chúa:"));
visuperobject.biblemap.put("b59_1_8", new visuperobject(59,1,8,"ấy là một người phân tâm, phàm làm việc gì đều không định."));
visuperobject.biblemap.put("b59_1_9", new visuperobject(59,1,9,"Anh em nào ở địa vị thấp hèn hãy khoe mình về phần cao trọng mình,"));
visuperobject.biblemap.put("b59_1_10", new visuperobject(59,1,10,"kẻ giàu cũng hãy khoe mình về phần đê hèn, vì người sẽ qua đi như hoa cỏ."));
visuperobject.biblemap.put("b59_1_11", new visuperobject(59,1,11,"Mặt trời mọc lên, nắng xẳng, cỏ khô, hoa rụng, sắc đẹp tồi tàn: kẻ giàu cũng sẽ khô héo như vậy trong những việc mình làm."));
visuperobject.biblemap.put("b59_1_12", new visuperobject(59,1,12,"Phước cho người bị cám dỗ; vì lúc đã chịu nổi sự thử thách rồi, thì sẽ lãnh mão triều thiên của sự sống mà Ðức Chúa Trời đã hứa cho kẻ kính mến Ngài."));
visuperobject.biblemap.put("b59_1_13", new visuperobject(59,1,13,"Chớ có ai đương bị cám dỗ mà nói rằng: Ấy là Ðức Chúa Trời cám dỗ tôi; vì Ðức Chúa Trời chẳng bị sự ác nào cám dỗ được, và chính Ngài cũng không cám dỗ ai."));
visuperobject.biblemap.put("b59_1_14", new visuperobject(59,1,14,"Nhưng mỗi người bị cám dỗ khi mắc tư dục xui giục mình."));
visuperobject.biblemap.put("b59_1_15", new visuperobject(59,1,15,"Ðoạn, lòng tư dục cưu mang, sanh ra tội ác; tội ác đã trọn, sanh ra sự chết."));
visuperobject.biblemap.put("b59_1_16", new visuperobject(59,1,16,"Hỡi anh em yêu dấu, chớ tự dối mình:"));
visuperobject.biblemap.put("b59_1_17", new visuperobject(59,1,17,"mọi ân điển tốt lành cùng sự ban cho trọn vẹn đều đến từ nơi cao và bởi Cha sáng láng mà xuống, trong Ngài chẳng có một sự thay đổi, cũng chẳng có bóng của sự biến cải nào."));
visuperobject.biblemap.put("b59_1_18", new visuperobject(59,1,18,"Ấy chính Ngài theo ý muốn mình, đã dùng lời chân thật sanh chúng ta, hầu cho chúng ta được nên như trái đầu mùa của những vật Ngài dựng nên."));
visuperobject.biblemap.put("b59_1_19", new visuperobject(59,1,19,"Hỡi anh em yêu dấu, anh em biết điều đó: người nào cũng phải mau nghe mà chậm nói, chậm giận;"));
visuperobject.biblemap.put("b59_1_20", new visuperobject(59,1,20,"vì cơn giận của người ta không làm nên sự công bình của Ðức Chúa Trời."));
visuperobject.biblemap.put("b59_1_21", new visuperobject(59,1,21,"Vậy, hãy bỏ đi mọi điều ô uế, và mọi điều gian ác còn lại, đem lòng nhu mì nhận lấy lời đã trồng trong anh em, là lời cứu được linh hồn của anh em."));
visuperobject.biblemap.put("b59_1_22", new visuperobject(59,1,22,"Hãy làm theo lời, chớ lấy nghe làm đủ mà lừa dối mình."));
visuperobject.biblemap.put("b59_1_23", new visuperobject(59,1,23,"Vì, nếu có kẻ nghe lời mà không làm theo thì khác nào người kia soi mặt mình trong gương,"));
visuperobject.biblemap.put("b59_1_24", new visuperobject(59,1,24,"thấy rồi thì đi, liền quên mặt ra thể nào."));
visuperobject.biblemap.put("b59_1_25", new visuperobject(59,1,25,"Nhưng kẻ nào xét kĩ luật pháp trọn vẹn, là luật pháp về sự tự do, lại bền lòng suy gẫm lấy, chẳng phải nghe rồi quên đi, nhưng hết lòng giữ theo phép tắc nó, thì kẻ đó sẽ tìm được phước trong sự mình vâng lời."));
visuperobject.biblemap.put("b59_1_26", new visuperobject(59,1,26,"Nhược bằng có ai tưởng mình là tin đạo, mà không cầm giữ lưỡi mình, nhưng lại lừa dối lòng mình, thì sự tin đạo của người hạng ấy là vô ích."));
visuperobject.biblemap.put("b59_1_27", new visuperobject(59,1,27,"Sự tin đạo thanh sạch không vết, trước mặt Ðức Chúa Trời, Cha chúng ta, là thăm viếng kẻ mồ côi, người góa bụa trong cơn khốn khó của nó, và giữ lấy mình cho khỏi sự ô uế của thế gian."));
visuperobject.biblemap.put("b59_2_1", new visuperobject(59,2,1,"Hỡi anh em, anh em đã tin đến Ðức Chúa Jêsus Christ, là Chúa vinh hiển chúng ta, thì chớ có tây vị người nào."));
visuperobject.biblemap.put("b59_2_2", new visuperobject(59,2,2,"Giả sử có người đeo nhẫn vàng, mặc áo đẹp, vào nơi hội anh em, lại có người nghèo, quần áo rách rưới, cũng vào nữa;"));
visuperobject.biblemap.put("b59_2_3", new visuperobject(59,2,3,"nếu anh em ngó kẻ mặc áo đẹp, mà nói rằng: Mời ngồi đây, là chỗ tử tế; lại nói với người nghèo rằng: Hãy đứng đó, hoặc ngồi dưới bệ chơn ta,"));
visuperobject.biblemap.put("b59_2_4", new visuperobject(59,2,4,"thế có phải anh em tự mình phân biệt ra và lấy ý xấu mà xét đoán không?"));
visuperobject.biblemap.put("b59_2_5", new visuperobject(59,2,5,"Hỡi anh em rất yêu dấu, hãy nghe nầy: Ðức Chúa Trời há chẳng lựa kẻ nghèo theo đời nầy đặng làm cho trở nên giàu trong đức tin, và kế tự nước Ngài đã hứa cho kẻ kính mến Ngài hay sao?"));
visuperobject.biblemap.put("b59_2_6", new visuperobject(59,2,6,"Mà anh em lại khinh dể kẻ nghèo! Há chẳng phải kẻ giàu đã hà hiếp anh em, kéo anh em đến trước tòa án sao?"));
visuperobject.biblemap.put("b59_2_7", new visuperobject(59,2,7,"Há chẳng phải họ phạm thượng đến danh tốt đã lấy đặt cho anh em sao?"));
visuperobject.biblemap.put("b59_2_8", new visuperobject(59,2,8,"Thật vậy, nếu anh em vâng giữ cho toàn vẹn luật pháp tôn trọng, theo như Kinh Thánh rằng: Hãy yêu người lân cận như mình, thì anh em ăn ở tốt lắm."));
visuperobject.biblemap.put("b59_2_9", new visuperobject(59,2,9,"Nhưng nếu anh em tây vị người ta, thì phạm tội, luật pháp bèn định tội anh em như kẻ phạm phép."));
visuperobject.biblemap.put("b59_2_10", new visuperobject(59,2,10,"Vì người nào giữ trọn luật pháp, mà phạm một điều răn, thì cũng đáng tội như đã phạm hết thảy."));
visuperobject.biblemap.put("b59_2_11", new visuperobject(59,2,11,"Vả, Ðấng đã phán rằng: Chớ phạm tội tà dâm, cũng có phán rằng: Chớ giết người. Vậy, nếu người không phạm tội tà dâm, nhưng phạm tội giết người, thì ngươi là kẻ phạm luật pháp."));
visuperobject.biblemap.put("b59_2_12", new visuperobject(59,2,12,"Hãy nói và làm dường như phải chịu luật pháp tự do đoán xét mình."));
visuperobject.biblemap.put("b59_2_13", new visuperobject(59,2,13,"Sự đoán xét không thương xót kẻ chẳng làm sự thương xót; nhưng sự thương xót thắng sự đoán xét."));
visuperobject.biblemap.put("b59_2_14", new visuperobject(59,2,14,"Hỡi anh em, nếu ai nói mình có đức tin, song không có việc làm, thì ích chi chăng? Ðức tin đó cứu người ấy được chăng?"));
visuperobject.biblemap.put("b59_2_15", new visuperobject(59,2,15,"Ví thử có anh em hoặc chị em nào không quần áo mặc, thiếu của ăn uống hằng ngày,"));
visuperobject.biblemap.put("b59_2_16", new visuperobject(59,2,16,"mà một kẻ trong anh em nói với họ rằng: Hãy đi cho bình an, hãy sưởi cho ấm và ăn cho no, nhưng không cho họ đồ cần dùng về phần xác, thì co ích gì chăng?"));
visuperobject.biblemap.put("b59_2_17", new visuperobject(59,2,17,"Về đức tin, cũng một lẽ ấy; nếu đức tin không sanh ra việc làm, thì tự mình nó chết."));
visuperobject.biblemap.put("b59_2_18", new visuperobject(59,2,18,"Hoặc có kẻ nói: Ngươi có đức tin, còn ta có việc làm. Hãy chỉ cho ta đức tin của ngươi không có việc làm, rồi ta sẽ chỉ cho ngươi đức tin bởi việc làm của ta."));
visuperobject.biblemap.put("b59_2_19", new visuperobject(59,2,19,"Ngươi tin rằng chỉ có một Ðức Chúa Trời mà thôi, ngươi tin phải; ma quỉ cũng tin như vậy và run sợ."));
visuperobject.biblemap.put("b59_2_20", new visuperobject(59,2,20,"Nhưng, hỡi người vô tri kia, ngươi muốn biết chắc rằng đức tin không có việc làm là vô ích chăng?"));
visuperobject.biblemap.put("b59_2_21", new visuperobject(59,2,21,"Áp-ra-ham, tổ phụ chúng ta, khi dâng con mình là Y-sác trên bàn thờ, há chẳng từng cậy việc làm được xưng công bình hay sao?"));
visuperobject.biblemap.put("b59_2_22", new visuperobject(59,2,22,"Thế thì, ngươi thấy đức tin đồng công với việc làm, và nhờ việc làm mà đức tin được trọn vẹn."));
visuperobject.biblemap.put("b59_2_23", new visuperobject(59,2,23,"Vậy được ứng nghiệm lời Thánh Kinh rằng: Áp-ra-ham tin Ðức Chúa Trời, và điều đó kể là công bình cho người; và người được gọi là bạn Ðức Chúa Trời."));
visuperobject.biblemap.put("b59_2_24", new visuperobject(59,2,24,"nhơn đó anh em biết người ta cậy việc làm được xưng công bình, chớ chẳng những là cậy đức tin mà thôi."));
visuperobject.biblemap.put("b59_2_25", new visuperobject(59,2,25,"Ðồng một thể ấy, kỵ nữ Ra-háp tiếp rước các sứ giả và khiến họ noi đường khác mà đi, người há chẳng phải cậy việc làm mà được xưng công bình sao?"));
visuperobject.biblemap.put("b59_2_26", new visuperobject(59,2,26,"Vả, xác chẳng có hồn thì chết, đức tin không có việc làm cũng chết như vậy."));
visuperobject.biblemap.put("b59_3_1", new visuperobject(59,3,1,"Hỡi anh em, trong vòng anh em chớ có nhiều người tự lập làm thầy, vì biết như vậy, mình sẽ phải chịu xét đoán càng nghiêm hơn."));
visuperobject.biblemap.put("b59_3_2", new visuperobject(59,3,2,"Chúng ta thảy đều vấp phạm nhiều cách lắm. Nếu có ai không vấp phạm trong lời nói mình, ấy là người trọn vẹn, hay hãm cầm cả mình."));
visuperobject.biblemap.put("b59_3_3", new visuperobject(59,3,3,"Chúng ta tra hàm thiếc vào miệng ngựa, cho nó chịu phục mình, nên mới sai khiến cả và mình nó được."));
visuperobject.biblemap.put("b59_3_4", new visuperobject(59,3,4,"Hãy xem những chiếc tàu: dầu cho lớn mấy mặc lòng, và bị gió mạnh đưa đi thây kệ, một bánh lái rất nhỏ cùng đủ cạy bát nó, tùy theo ý người cầm lái."));
visuperobject.biblemap.put("b59_3_5", new visuperobject(59,3,5,"Cũng vậy, cái lưỡi là một quan thể nhỏ, mà khoe được những việc lớn. Thử xem cái rừng lớn chừng nào mà một chút lửa có thể đốt cháy lên!"));
visuperobject.biblemap.put("b59_3_6", new visuperobject(59,3,6,"Cái lưỡi cũng như lửa; ấy là nơi đô hội của tội ác ở giữa các quan thể chúng ta, làm ô uế cả mình, đốt cháy cả đời người, chính mình nó đã bị lửa địa ngục đốt cháy."));
visuperobject.biblemap.put("b59_3_7", new visuperobject(59,3,7,"hết thảy loài muông thú, chim chóc, sâu bọ, loài dưới biển đều trị phục được và đã bị loài người trị phục rồi;"));
visuperobject.biblemap.put("b59_3_8", new visuperobject(59,3,8,"nhưng cái lưỡi, không ai trị phục được nó; ấy là một vật dữ người ta không thể hãm dẹp được: đầy dẫy những chất độc giết chết."));
visuperobject.biblemap.put("b59_3_9", new visuperobject(59,3,9,"Bởi cái lưỡi chúng ta khen ngợi Chúa, Cha chúng ta, và cũng bởi nó chúng ta rủa sả loài người, là loài tạo theo hình ảnh Ðức Chúa Trời."));
visuperobject.biblemap.put("b59_3_10", new visuperobject(59,3,10,"Ðồng một lỗ miệng mà ra cả sự khen ngợi và rủa sả! Hỡi anh em, không nên như vậy."));
visuperobject.biblemap.put("b59_3_11", new visuperobject(59,3,11,"Có lẽ nào một cái suối kia, đồng một mạch mà ra cả nước ngọt và nước đắng sao?"));
visuperobject.biblemap.put("b59_3_12", new visuperobject(59,3,12,"Hỡi anh em, cây vả có ra trái ô-li-ve được, cây nho có ra trái vả được chăng? Mạch nước mặn cũng không có thể chảy ra nước ngọt được nữa."));
visuperobject.biblemap.put("b59_3_13", new visuperobject(59,3,13,"Trong anh em có người nào khôn ngoan thông sáng chăng: Hãy lấy cách ăn ở tốt của mình mà bày tỏ việc mình làm bởi khôn ngoan nhu mì mà ra."));
visuperobject.biblemap.put("b59_3_14", new visuperobject(59,3,14,"Nhưng nếu anh em có sự ghen tương cay đắng và sự tranh cạnh trong lòng mình, thì chớ khoe mình và nói dối nghịch cùng lẽ thật."));
visuperobject.biblemap.put("b59_3_15", new visuperobject(59,3,15,"Sự khôn ngoan đó không phải từ trên mà xuống đâu; trái lại, nó thuộc về đất, về xác thịt và về ma quỉ."));
visuperobject.biblemap.put("b59_3_16", new visuperobject(59,3,16,"Vì ở đâu có những điều ghen tương tranh cạnh ấy, thì ở đó có sự lộn lạo và đủ mọi thứ ác."));
visuperobject.biblemap.put("b59_3_17", new visuperobject(59,3,17,"Nhưng sự khôn ngoan từ trên mà xuống thì trước hết là thanh sạch, sau lại hòa thuận, tiết độ, nhu mì, đầy dẫy lòng thương xót và bông trái lành, không có sự hai lòng và giả hình."));
visuperobject.biblemap.put("b59_3_18", new visuperobject(59,3,18,"Vả bông trái của điều công bình thì gieo trong sự hòa bình, cho những kẻ nào làm sự hòa bình vậy."));
visuperobject.biblemap.put("b59_4_1", new visuperobject(59,4,1,"Những điều chiến đấu tranh cạnh trong anh em bởi đâu mà đến? Há chẳng phải từ tình dục anh em vẫn hay tranh chiến trong quan thể mình sao?"));
visuperobject.biblemap.put("b59_4_2", new visuperobject(59,4,2,"Anh em tham muốn mà chẳng được chi; anh em giết người và ghen ghét mà chẳng được việc gì hết; anh em có sự tranh cạnh và chiến đấu; anh em chẳng được chi, vì không cầu xin."));
visuperobject.biblemap.put("b59_4_3", new visuperobject(59,4,3,"Anh em cầu xin mà không nhận lãnh được, vì cầu xin trái lẽ, để dùng trong tư dục mình."));
visuperobject.biblemap.put("b59_4_4", new visuperobject(59,4,4,"Hỡi bọn tà dâm kia, anh em há chẳng biết làm bạn với thế gian tức là thù nghịch với Ðức Chúa Trời sao? Cho nên, ai muốn làm bạn với thế gian, thì người ấy trở nên kẻ nghịch thù cùng Ðức Chúa Trời vậy."));
visuperobject.biblemap.put("b59_4_5", new visuperobject(59,4,5,"Hay là anh em tưởng Kinh Thánh nói vô ích sao? Ðức Thánh Linh mà Ðức Chúa Trời khiến ở trong lòng chúng ta, ham mến chúng ta đến nỗi ghen tương,"));
visuperobject.biblemap.put("b59_4_6", new visuperobject(59,4,6,"nhưng Ngài lại ban cho ta ơn lớn hơn nữa. Vì vậy, Thánh Kinh chép rằng: Ðức Chúa Trời chống cự kẻ kiêu ngạo, nhưng ban ơn cho kẻ khiêm nhường."));
visuperobject.biblemap.put("b59_4_7", new visuperobject(59,4,7,"Vậy hãy phục Ðức Chúa Trời; hãy chống trả ma quỉ, thì nó sẽ lánh xa anh em."));
visuperobject.biblemap.put("b59_4_8", new visuperobject(59,4,8,"Hãy đến gần Ðức Chúa Trời, thì Ngài sẽ đến gần anh em. Hỡi kẻ có tội, hãy lau tay mình, có ai hai lòng, hãy làm sạch lòng đi;"));
visuperobject.biblemap.put("b59_4_9", new visuperobject(59,4,9,"hãy cảm biết sự khốn nạn mình, hãy đau thương khóc lóc; hãy đổi cười ra khóc, đổi vui ra buồn."));
visuperobject.biblemap.put("b59_4_10", new visuperobject(59,4,10,"Hãy hạ mình xuống trước mặt Chúa, thì Ngài sẽ nhắc anh em lên."));
visuperobject.biblemap.put("b59_4_11", new visuperobject(59,4,11,"Hỡi anh em, chớ nói hành nhau. Ai nói hành anh em mình hoặc xét đoán anh em mình, tức là nói xấu luật pháp, và xét đoán luật pháp. Vả, nếu ngươi xét đoán luật pháp, thì ngươi chẳng phải là kẻ vâng giữ luật pháp, bèn là người xét đoán luật pháp vậy."));
visuperobject.biblemap.put("b59_4_12", new visuperobject(59,4,12,"Chỉ có một Ðấng lập ra luật pháp và một Ðấng xét đoán, tức là Ðấng cứu được và diệt được. Nhưng ngươi là ai, mà dám xét đoán kẻ lân cận mình?"));
visuperobject.biblemap.put("b59_4_13", new visuperobject(59,4,13,"Hỡi anh em, là kẻ nói rằng: Hôm nay hoặc ngày mai, ta sẽ đi đến thành kia, ở đó một năm, buôn bán và phát tài,"));
visuperobject.biblemap.put("b59_4_14", new visuperobject(59,4,14,"song ngày mai sẽ ra thế nao, anh em chẳng biết! Vì sự sống của anh em là chi? Chẳng qua như hơi nước, hiện ra một lát rồi lại tan ngay."));
visuperobject.biblemap.put("b59_4_15", new visuperobject(59,4,15,"Anh em phải nói trái lại: Ví bằng Chúa muốn, và ta còn sống, thì ta sẽ làm việc nọ việc kia."));
visuperobject.biblemap.put("b59_4_16", new visuperobject(59,4,16,"Kìa anh em lấy những lời kiêu ngạo mà khoe mình! Phàm khoe khoang như vậy là xấu."));
visuperobject.biblemap.put("b59_4_17", new visuperobject(59,4,17,"Cho nên, kẻ biết làm điều lành mà chẳng làm, thì phạm tội."));
visuperobject.biblemap.put("b59_5_1", new visuperobject(59,5,1,"Hỡi anh em là kẻ giàu có! Hãy khóc lóc, kêu la, vì cớ hoạn nạn sẽ đổ trên anh em."));
visuperobject.biblemap.put("b59_5_2", new visuperobject(59,5,2,"Của cải anh em bị mục nát, áo xống bị mối mọt ăn rồi."));
visuperobject.biblemap.put("b59_5_3", new visuperobject(59,5,3,"Vàng bạc anh em bị ten rét, ten rét đó sẽ làm chứng nghịch cùng anh em, nó cũng như lửa vậy, sẽ ăn thịt anh em. Anh em đã thâu trử tiền của trong những ngày sau rốt!"));
visuperobject.biblemap.put("b59_5_4", new visuperobject(59,5,4,"Kìa, tiền công con gặt gặt ruộng anh em, mà anh em đã ăn gian, nó kêu oan, và tiếng kêu của con gặt đã thấu đến tai Chúa các cơ binh."));
visuperobject.biblemap.put("b59_5_5", new visuperobject(59,5,5,"Anh em đã sống trên thế gian ăn uống vui sướng và xa xỉ, anh em đã làm cho lòng mình no nê trong ngày chém giết;"));
visuperobject.biblemap.put("b59_5_6", new visuperobject(59,5,6,"anh em đã luận tội và đã giết người công bình, mà người chẳng cự lại."));
visuperobject.biblemap.put("b59_5_7", new visuperobject(59,5,7,"Hỡi anh em, vậy hãy nhịn nhục cho tới kỳ Chúa đến. Hãy xem kẻ làm ruộng: họ bền lòng chờ đợi sản vật quí báu dưới đất cho đến chừng nào đã được mưa đầu mùa và cuối mùa."));
visuperobject.biblemap.put("b59_5_8", new visuperobject(59,5,8,"anh em cũng vậy, hãy nhịn nhục và bền lòng; vì kỳ Chúa đến gần rồi."));
visuperobject.biblemap.put("b59_5_9", new visuperobject(59,5,9,"Hỡi anh em, chớ oán trách nhau, hầu cho khỏi bị xét đoán; kìa, Ðấng xét đoán đứng trước cửa."));
visuperobject.biblemap.put("b59_5_10", new visuperobject(59,5,10,"Hỡi anh em, hãy lấy các đấng tiên tri đã nhơn danh Chúa mà nói, làm mẫu mực về sự chịu khổ và nhịn nhục cho mình."));
visuperobject.biblemap.put("b59_5_11", new visuperobject(59,5,11,"Anh em biết rằng những kẻ nhịn nhục chịu khổ thì chúng ta xưng là có phước. Anh em đã nghe nói về sự nhịn nhục của Gióp, và thấy cái kết cuộc mà Chúa ban cho người; vì Chúa đầy lòng thương xót và nhơn từ."));
visuperobject.biblemap.put("b59_5_12", new visuperobject(59,5,12,"Hỡi Anh em, trước hết chớ có thề, chớ chỉ trời, chỉ đất, cũng chớ chỉ vật khác mà thề; nhưng phải thì nói phải, không thì nói không, hầu cho khỏi bị xét đoán."));
visuperobject.biblemap.put("b59_5_13", new visuperobject(59,5,13,"Trong anh em có ai chịu khổ chăng? Người ấy hãy cầu nguyện. Có ai vui mừng chăng? hãy hát ngợi khen."));
visuperobject.biblemap.put("b59_5_14", new visuperobject(59,5,14,"Trong anh em có ai đau ốm chăng? hãy mời các trưởng lão hội thánh đến, sau khi nhơn danh Chúa xức dầu cho người bịnh đoạn, thì các trưởng lão hãy cầu nguyện cho người."));
visuperobject.biblemap.put("b59_5_15", new visuperobject(59,5,15,"Sự cầu nguyện bởi đức tin sẽ cứu kẻ bịnh, Chúa sẽ đỡ kẻ ấy dậy; nếu kẻ bịnh có phạm tội, cũng sẽ được tha."));
visuperobject.biblemap.put("b59_5_16", new visuperobject(59,5,16,"Vậy, hãy xưng tội cùng nhau, và cầu nguyện cho nhau, hầu cho anh em được lành bịnh: người công bình lấy lòng sốt sắng cầu nguyện, thật có linh nghiệm nhiều."));
visuperobject.biblemap.put("b59_5_17", new visuperobject(59,5,17,"Ê-li vốn là người yếu đuối như chúng ta. Người cầu nguyện, cố xin cho đừng mưa, thì không mưa xuống đất trong ba năm rưỡi."));
visuperobject.biblemap.put("b59_5_18", new visuperobject(59,5,18,"Ðoạn, người cầu nguyện lại, trời bèn mưa, và đất sanh sản hoa màu."));
visuperobject.biblemap.put("b59_5_19", new visuperobject(59,5,19,"Hỡi anh em, trong vòng anh em nếu có ai lầm lạc cách xa lẽ thật, mà có người khác làm cho nó trở lại,"));
visuperobject.biblemap.put("b59_5_20", new visuperobject(59,5,20,"thì phải biết rằng kẻ làm cho người có tội trở lại, bỏ đường lầm lạc, ấy là cứu linh hồn người khỏi sự chết và che đậy vô số tội lỗi."));

}

}