package vi.bible;

public class vibible60 {

public vibible60() {

putmapbible60();

}

private void putmapbible60() {

visuperobject.biblemap.put("b60_1_1", new visuperobject(60,1,1,"Phi -e-rơ, sứ đồ của Ðức Chúa Jêsus Christ, gởi cho những người kiều ngụ rải rác trong xứ Bông, Ga-la-ti, Cáp-ba-đốc, A-si và Bi-thi-ni, là những người được chọn,"));
visuperobject.biblemap.put("b60_1_2", new visuperobject(60,1,2,"theo sự biết trước của Ðức Chúa Trời, là Ðức Chúa Cha, và được nên thánh bởi Ðức Thánh Linh, đặng vâng phục Ðức Chúa Jêsus Christ và có phần trong sự rải huyết Ngài: nguyền xin ân điển và bình an thêm lên cho anh em!"));
visuperobject.biblemap.put("b60_1_3", new visuperobject(60,1,3,"Ngợi khen Ðức Chúa Trời, là Cha Ðức Chúa Jêsus Christ chúng ta, Ngài lấy lòng thương xót cả thể khiến chúng ta lại sanh, đặng chúng ta nhờ sự Ðức Chúa Jêsus Christ sống lại từ trong kẻ chết mà có sự trông cậy sống,"));
visuperobject.biblemap.put("b60_1_4", new visuperobject(60,1,4,"là cơ nghiệp không hư đi, không ô uế, không suy tàn, để dành trong các từng trời cho anh em,"));
visuperobject.biblemap.put("b60_1_5", new visuperobject(60,1,5,"là kẻ bởi đức tin nhờ quyền phép của Ðức Chúa Trời giữ cho, để được sự cứu rỗi gần hiện ra trong kỳ sau rốt!"));
visuperobject.biblemap.put("b60_1_6", new visuperobject(60,1,6,"Anh em vui mừng về điều đó, dầu hiện nay anh em vì sự thử thách trăm bề buộc phải buồn bã ít lâu;"));
visuperobject.biblemap.put("b60_1_7", new visuperobject(60,1,7,"hầu cho sự thử thách đức tin anh em quí hơn vàng hay hư nát, dầu đã bị thử lửa, sanh ra ngợi khen, tôn trọng, vinh hiển cho anh em khi Ðức Chúa Jêsus Christ hiện ra."));
visuperobject.biblemap.put("b60_1_8", new visuperobject(60,1,8,"Ngài là Ðấng anh em không thấy mà yêu mến; dầu bây giờ anh em không thấy Ngài, nhưng tin Ngài, và vui mừng lắm một cách không xiết kể và vinh hiển:"));
visuperobject.biblemap.put("b60_1_9", new visuperobject(60,1,9,"nhận được phần thưởng về đức tin anh em, là sự cứu rỗi linh hồn mình."));
visuperobject.biblemap.put("b60_1_10", new visuperobject(60,1,10,"Về sự cứu rỗi đó, các đấng tiên tri đã tìm tòi suy xét, và đã nói tiên tri về ân điển định sẵn cho anh em:"));
visuperobject.biblemap.put("b60_1_11", new visuperobject(60,1,11,"nghĩa là tìm cho biết thời kỳ nào và thời kỳ cách nào mà Thánh Linh Ðấng Christ ở trong lòng mình đã chỉ cho, là khi làm chứng trước về sự đau đớn của Ðấng Christ và về sự vinh hiển sẽ theo sau."));
visuperobject.biblemap.put("b60_1_12", new visuperobject(60,1,12,"Các đấng ấy đã được tỏ cho rằng chẳng phải vì mình, bèn là vì anh em mà truyền ra những điều đó, là những điều hiện nay đã rao cho anh em, bởi những người nhờ Ðức Thánh Linh từ trên trời sai xuống, mà giảng Tin Lành cho anh em; các thiên sứ cũng ước ao xem thấu những sự đó."));
visuperobject.biblemap.put("b60_1_13", new visuperobject(60,1,13,"Vậy, anh em hãy bền chí như thể thắt lưng, hãy tiết độ, lấy sự trông cậy trọn vẹn đợi chờ ơn sẽ ban cho mình trong khi Ðức Chúa Jêsus Christ hiện ra."));
visuperobject.biblemap.put("b60_1_14", new visuperobject(60,1,14,"Anh em đã nên như con cái hay vâng lời, thì chớ có làm theo sự dâm dục, là sự cai trị trong anh em ngày trước, về lúc anh em còn mê muội."));
visuperobject.biblemap.put("b60_1_15", new visuperobject(60,1,15,"Nhưng, như Ðấng gọi anh em là thánh, thì anh em cũng phải thánh trong mọi cách ăn ở mình,"));
visuperobject.biblemap.put("b60_1_16", new visuperobject(60,1,16,"bởi có chép rằng: Hãy nên thánh, vì ta là thánh."));
visuperobject.biblemap.put("b60_1_17", new visuperobject(60,1,17,"Nếu anh em xưng Ðấng không tây vị anh em, xét đoán từng người theo việc họ làm, bằng Cha, thì hãy lấy lòng kính sợ mà ăn ở trong thời kỳ ở trọ đời nầy,"));
visuperobject.biblemap.put("b60_1_18", new visuperobject(60,1,18,"vì biết rằng chẳng phải bởi vật hay hư nát như bạc hoặc vàng mà anh em đã được chuộc khỏi sự ăn ở không ra chi của tổ tiên truyền lại cho mình,"));
visuperobject.biblemap.put("b60_1_19", new visuperobject(60,1,19,"bèn là bởi huyết báu Ðấng Christ, dường như huyết của chiên con không lỗi không vít,"));
visuperobject.biblemap.put("b60_1_20", new visuperobject(60,1,20,"đã định sẵn trước buổi sáng thế, và hiện ra trong cuối các thời kỳ vì cớ anh em,"));
visuperobject.biblemap.put("b60_1_21", new visuperobject(60,1,21,"là kẻ nhơn Ðức Chúa Jêsus tin đến Ðức Chúa Trời, tức là Ðấng đã khiến Ngài từ kẻ chết sống lại, và ban sự vinh hiển cho Ngài đến nỗi đức tin và sự trông cậy anh em được nương nhờ Ðức Chúa Trời."));
visuperobject.biblemap.put("b60_1_22", new visuperobject(60,1,22,"Anh em đã vâng theo lẽ thật làm sạch lòng mình, đặng có lòng yêu thương anh em cách thật thà, nên hãy yêu nhau sốt sắng hết lòng;"));
visuperobject.biblemap.put("b60_1_23", new visuperobject(60,1,23,"anh em đã được lại sanh, chẳng phải bởi giống hay hư nát, nhưng bởi giống chẳng hay hư nát, là bởi lời hằng sống và bền vững của Ðức Chúa Trời."));
visuperobject.biblemap.put("b60_1_24", new visuperobject(60,1,24,"Vì, Mọi xác thịt vì như cỏ, Mọi sự vinh hiển của nó vì như hoa cỏ. Cỏ khô, hoa rụng,"));
visuperobject.biblemap.put("b60_1_25", new visuperobject(60,1,25,"Nhưng lời Chúa còn lại đời đời. Và lời đó là đạo Tin Lành đã giảng ra cho anh em."));
visuperobject.biblemap.put("b60_2_1", new visuperobject(60,2,1,"Vậy anh em đã từ bỏ mọi điều độc ác, mọi điều gian dảo, mọi thứ giả trá, lòng ghen ghét và sự nói hành,"));
visuperobject.biblemap.put("b60_2_2", new visuperobject(60,2,2,"thì hãy ham thích sữa thiêng liêng của đạo, như trẻ con mới đẻ vậy, hầu cho anh em nhờ đó lớn lên mà được rỗi linh hồn,"));
visuperobject.biblemap.put("b60_2_3", new visuperobject(60,2,3,"nếu anh em đã nếm biết Chúa là ngọt ngào."));
visuperobject.biblemap.put("b60_2_4", new visuperobject(60,2,4,"Hãy đến gần Ngài, là hòn đá sống, bị người ta loại ra, song được chọn và quí trước mặt Ðức Chúa Trời,"));
visuperobject.biblemap.put("b60_2_5", new visuperobject(60,2,5,"và anh em cũng như đá sống, được xây nên nhà thiêng liêng, làm chức tế lễ thánh, đặng dâng của tế lễ thiêng liêng, nhờ Ðức Chúa Jêsus Christ mà đẹp ý Ðức Chúa Trời."));
visuperobject.biblemap.put("b60_2_6", new visuperobject(60,2,6,"Vì trong Kinh Thánh có chép rằng: Nầy, ta đặt tại Si-ôn hòn đá góc nhà đã chọn lựa và quí báu; Ai tin đến đá ấy sẽ không bị xấu hổ."));
visuperobject.biblemap.put("b60_2_7", new visuperobject(60,2,7,"Vậy nên, cho anh em là kẻ đã tin, thì là đá quí; nhưng cho những kẻ không tin, thì Hòn đá mà bị thợ xây nhà loại ra, Bèn trở nên đá góc nhà, là Ðá gây cho vấp váp, là đá lớn làm cho sa ngã;"));
visuperobject.biblemap.put("b60_2_8", new visuperobject(60,2,8,"họ bị vấp đá đó, vì không vâng phục Ðạo, và điều ấy đã định sẵn cho họ rồi."));
visuperobject.biblemap.put("b60_2_9", new visuperobject(60,2,9,"Nhưng anh em là dòng giống được lựa chọn, là chức thầy tế lễ nhà vua, là dân thánh là dân thuộc về Ðức Chúa Trời, hầu cho anh em rao giảng nhơn đức của Ðấng đã gọi anh em ra khỏi nơi tối tăm, đến nơi sáng láng lạ lùng của Ngài;"));
visuperobject.biblemap.put("b60_2_10", new visuperobject(60,2,10,"anh em ngày trước không phải là một dân, mà bây giờ là dân Ðức Chúa Trời, trước không được thương xót, mà bây giờ được thương xót."));
visuperobject.biblemap.put("b60_2_11", new visuperobject(60,2,11,"Hỡi kẻ rất yêu dấu, anh em như người ở trọ, kẻ đi đường, tôi khuyên phải kiêng những điều xác thịt ưa thích, là điều chống trả với linh hồn."));
visuperobject.biblemap.put("b60_2_12", new visuperobject(60,2,12,"phải ăn ở ngay lành giữa dân ngoại, hầu cho họ là kẻ vẫn gièm chê anh em như người gian ác, đã thấy việc lành anh em, thì đến ngày Chúa thăm viếng, họ ngợi khen Ðức Chúa Trời."));
visuperobject.biblemap.put("b60_2_13", new visuperobject(60,2,13,"Vì cớ Chúa, hãy phục theo mọi phép tắc loài người lập lên, hoặc vua, như đấng rất cao,"));
visuperobject.biblemap.put("b60_2_14", new visuperobject(60,2,14,"hoặc các quan, như người vua sai ra để phạt kẻ làm dữ và khen người làm lành."));
visuperobject.biblemap.put("b60_2_15", new visuperobject(60,2,15,"Vì anh em làm điều lành để ngăn miệng những kẻ ngu muội dại dột, ấy là ý muốn của Ðức Chúa Trời."));
visuperobject.biblemap.put("b60_2_16", new visuperobject(60,2,16,"Hãy ăn ở như người tự do, nhưng chớ dùng tự do làm cái màn che sự hung ác, song phải coi mình là tôi mọi Ðức Chúa Trời."));
visuperobject.biblemap.put("b60_2_17", new visuperobject(60,2,17,"Hãy kính mọi người; yêu anh em; kính sợ Ðức Chúa Trời; tôn trọng vua."));
visuperobject.biblemap.put("b60_2_18", new visuperobject(60,2,18,"Hỡi kẻ làm tôi tớ, hãy lấy lòng rất kính sợ mà phục theo chủ mình, chẳng những phục những chủ hiền lành mà thôi, lại phải phục người chủ khó tánh nữa."));
visuperobject.biblemap.put("b60_2_19", new visuperobject(60,2,19,"Vì nhơn cớ lương tâm đối với Ðức Chúa Trời, mà chịu khốn nạn trong khi bị oan ức, ấy là một ơn phước."));
visuperobject.biblemap.put("b60_2_20", new visuperobject(60,2,20,"Vả, mình làm điều ác, bị đánh mà hay nhịn chịu, thì có đáng khoe gì: Nhưng nếu anh em làm lành, mà nhịn chịu sự khốn khó, ấy là một ơn phước trước mặt Ðức Chúa Trời."));
visuperobject.biblemap.put("b60_2_21", new visuperobject(60,2,21,"anh em đã được kêu gọi đến sự đó, vì Ðấng Christ cũng đã chịu khổ cho anh em, để lại cho anh em một gương, hầu cho anh em noi dấu chơn Ngài;"));
visuperobject.biblemap.put("b60_2_22", new visuperobject(60,2,22,"Ngài chưa hề phạm tội, trong miệng Ngài không thấy có chút chi dối trá;"));
visuperobject.biblemap.put("b60_2_23", new visuperobject(60,2,23,"Ngài bị rủa mà chẳng rủa lại, chịu nạn mà không hề ngăm dọa, nhưng cứ phó mình cho Ðấng xử đoán công bình;"));
visuperobject.biblemap.put("b60_2_24", new visuperobject(60,2,24,"Ngài gánh tội lỗi chúng ta trong thân thể Ngài trên cây gỗ, hầu cho chúng ta là kẻ đã chết về tội lỗi, được sống cho sự công bình; lại nhơn những lằn đòn của Ngài mà anh em đã được lành bịnh."));
visuperobject.biblemap.put("b60_2_25", new visuperobject(60,2,25,"Vì anh em vốn giống như con chiên lạc, mà bây giờ đã trở về cùng Ðấng chăn chiên và Giám mục của linh hồn mình."));
visuperobject.biblemap.put("b60_3_1", new visuperobject(60,3,1,"Hỡi người làm vợ, hãy phục chồng mình, hầu cho nếu có người chồng nào không vâng theo Ðạo, dẫu chẳng lấy lời khuyên bảo, chỉ bởi cách ăn ở của vợ, cũng đủ hóa theo,"));
visuperobject.biblemap.put("b60_3_2", new visuperobject(60,3,2,"vì thấy cách ăn ở của chị em là tinh sạch và cung kính."));
visuperobject.biblemap.put("b60_3_3", new visuperobject(60,3,3,"Chớ tìm kiếm sự trang sức bề ngoài, như gióc tóc, đeo đồ vàng, mặc áo quần lòa loẹt;"));
visuperobject.biblemap.put("b60_3_4", new visuperobject(60,3,4,"nhưng hãy tìm kiếm sự trang sức bề trong giấu ở trong lòng, tức là sự tinh sạch chẳng hư nát của tâm thần dịu dàng im lặng, ấy là giá quí trước mặt Ðức Chúa Trời."));
visuperobject.biblemap.put("b60_3_5", new visuperobject(60,3,5,"Vì các bà thánh xưa kia, trông cậy Ðức Chúa Trời, vâng phục chồng mình, đều trau giồi mình dường ấy;"));
visuperobject.biblemap.put("b60_3_6", new visuperobject(60,3,6,"như Sa-ra vâng phục Áp-ra-ham, gọi người là Chúa mình; nếu các chị em làm điều lành, không sợ chi hết mà rối trí, thì trở nên con gái của Sa-ra vậy."));
visuperobject.biblemap.put("b60_3_7", new visuperobject(60,3,7,"Hỡi người làm chồng, hãy tỏ điều khôn ngoan ra trong sự ăn ở với vợ mình, như là với giống yếu đuối hơn; vì họ sẽ cùng anh em hưởng phước sự sống, nên phải kính nể họ, hầu cho không điều gì làm rối loạn sự cầu nguyện của anh em."));
visuperobject.biblemap.put("b60_3_8", new visuperobject(60,3,8,"Rốt lại, hết thảy anh em phải đồng lòng đầy thương xót và tình yêu anh em, có lòng nhơn từ và đức khiêm nhường."));
visuperobject.biblemap.put("b60_3_9", new visuperobject(60,3,9,"Ðừng lấy ác trả ác, cũng đừng lấy rủa sả trả rủa sả; trái lại phải chúc phước, ấy vì điều đó mà anh em được gọi để hưởng phước lành."));
visuperobject.biblemap.put("b60_3_10", new visuperobject(60,3,10,"Vả, Ai muốn yêu sự sống Và thấy ngày tốt lành, Thì phải giữ gìn miệng lưỡi, Ðừng nói điều ác và lời gian dảo;"));
visuperobject.biblemap.put("b60_3_11", new visuperobject(60,3,11,"Phải lánh điều dữ, làm điều lành, Tìm sự hòa bình mà đuổi theo,"));
visuperobject.biblemap.put("b60_3_12", new visuperobject(60,3,12,"Vì mắt Chúa đoái trông người công bình, Tai Ngài lóng nghe lời cầu nguyện người, Nhưng mặt Chúa sấp lại nghịch với kẻ làm ác."));
visuperobject.biblemap.put("b60_3_13", new visuperobject(60,3,13,"Ví bằng anh em sốt sắng làm lành thì có ai làm dữ lại cho anh em?"));
visuperobject.biblemap.put("b60_3_14", new visuperobject(60,3,14,"Nếu anh em phải vì sự công bình mà chịu khổ, ấy thật là có phước. Chớ sợ điều họ sợ và đừng rối trí;"));
visuperobject.biblemap.put("b60_3_15", new visuperobject(60,3,15,"nhưng hãy tôn Ðấng Christ, là Chúa, làm thánh trong lòng mình. Hãy thường thường sẵn sàng để trả lời mọi kẻ hỏi lẽ về sự trông cậy trong anh em, song phải hiền hòa và kính sợ,"));
visuperobject.biblemap.put("b60_3_16", new visuperobject(60,3,16,"phải có lương tâm tốt, hầu cho những kẻ gièm chê cách ăn ở lành của anh em trong Ðấng Christ biết mình xấu hổ trong sự mà anh em đã bị nói hành;"));
visuperobject.biblemap.put("b60_3_17", new visuperobject(60,3,17,"vì nếu ý muốn Ðức Chúa Trời dường ấy, thì thà làm điều thiện mà chịu khổ, còn hơn làm điều ác mà chịu khổ vậy."));
visuperobject.biblemap.put("b60_3_18", new visuperobject(60,3,18,"Vả, Ðấng Christ cũng vì tội lỗi chịu chết một lần, là Ðấng công bình thay cho kẻ không công bình, để dẫn chúng ta đến cùng Ðức Chúa Trời; về phần xác thịt thì Ngài đã chịu chết, nhưng về phần linh hồn thì được sống."));
visuperobject.biblemap.put("b60_3_19", new visuperobject(60,3,19,"Ấy bởi đồng một linh hồn đó, Ngài đi giảng cho các linh hồn bị tù,"));
visuperobject.biblemap.put("b60_3_20", new visuperobject(60,3,20,"tức là kẻ bội nghịch thuở trước, về thời kỳ Nô-ê, khi Ðức Chúa Trời nhịn nhục chờ đợi, chiếc tàu đóng nên, trong đó có ít người được cứu bởi nước, là chỉ có tám người."));
visuperobject.biblemap.put("b60_3_21", new visuperobject(60,3,21,"Phép báp-tem bây giờ bèn là ảnh tượng của sự ấy để cứu anh em, phép ấy chẳng phải sự làm sạch ô uế của thân thể, nhưng một sự liên lạc lương tâm tốt với Ðức Chúa Trời, bởi sự sống lại của Ðức Chúa Jêsus Christ,"));
visuperobject.biblemap.put("b60_3_22", new visuperobject(60,3,22,"là Ðấng đã được lên trời, nay ngự bên hữu Ðức Chúa Trời, các thiên sứ, các vương hầu, các quyền thế thảy đều phục Ngài."));
visuperobject.biblemap.put("b60_4_1", new visuperobject(60,4,1,"Vậy, vì Ðấng Christ đã chịu khổ trong xác thịt, thì anh em cũng phải lấy sự đó làm giáp trụ, vì người nào đã chịu khổ trong xác thịt, thì đã dứt khỏi tội lỗi,"));
visuperobject.biblemap.put("b60_4_2", new visuperobject(60,4,2,"hầu cho còn sống trong xác thịt bao lâu thì chớ lại theo những sự người ta ưa thích, một phải theo ý muốn Ðức Chúa Trời."));
visuperobject.biblemap.put("b60_4_3", new visuperobject(60,4,3,"Ngày trước cũng đã đủ làm theo ý muốn người ngoại đạo rồi, mà ăn ở theo tà tịch, tư dục, say rượu, ăn uống quá độ, chơi bời, và thờ hình tượng đáng gớm ghiếc."));
visuperobject.biblemap.put("b60_4_4", new visuperobject(60,4,4,"Họ thấy anh em chẳng cùng họ theo sự dâm dật bậy bạ ấy, thì họ lấy làm lạ và gièm chê."));
visuperobject.biblemap.put("b60_4_5", new visuperobject(60,4,5,"Nhưng họ sẽ khai trình với Ðấng đã sẵn sàng xét đoán kẻ sống và kẻ chết."));
visuperobject.biblemap.put("b60_4_6", new visuperobject(60,4,6,"Vì ấy bởi điều đó mà Tin Lành cũng đã giảng ra cho kẻ chết, hầu cho sau khi chịu xét đoán theo loài người về phần xác, thì họ được sống theo Ðức Chúa Trời về phần hồn."));
visuperobject.biblemap.put("b60_4_7", new visuperobject(60,4,7,"Sự cuối cùng của muôn vật đã gần; vậy hãy khôn ngoan tỉnh thức mà cầu nguyện."));
visuperobject.biblemap.put("b60_4_8", new visuperobject(60,4,8,"Nhứt là trong vòng anh em phải có lòng yêu thương sốt sắng; vì sự yêu thương che đậy vô số tội lỗi."));
visuperobject.biblemap.put("b60_4_9", new visuperobject(60,4,9,"Người nầy người khác phải tiếp đãi nhau, chớ có cằn rằn."));
visuperobject.biblemap.put("b60_4_10", new visuperobject(60,4,10,"Mỗi người trong anh em hãy lấy ơn mình đã được mà giúp lẫn nhau, khác nào người quản lý trung tín giữ các thứ ơn của Ðức Chúa Trời."));
visuperobject.biblemap.put("b60_4_11", new visuperobject(60,4,11,"Ví bằng có người giảng luận, thì hãy giảng như rao lời sấm truyền của Ðức Chúa Trời; nếu có kẻ làm chức gì, thì hãy làm như nhờ sức Ðức Chúa Trời ban, hầu cho Ðức Chúa Trời được sáng danh trong mọi sự bởi Ðức Chúa Jêsus Christ; là Ðấng được sự vinh hiển quyền phép đời đời vô cùng. A-men."));
visuperobject.biblemap.put("b60_4_12", new visuperobject(60,4,12,"Hỡi kẻ rất yêu dấu, khi anh em bị trong lò lửa thử thách, chớ lấy làm lạ như mình gặp một việc khác thường."));
visuperobject.biblemap.put("b60_4_13", new visuperobject(60,4,13,"Nhưng anh em có phần trong sự thương khó của Ðấng Christ bao nhiêu, thì hãy vui mừng bấy nhiêu, hầu cho đến ngày vinh hiển của Ngài hiện ra, thì anh em cũng được vui mừng nhảy nhót."));
visuperobject.biblemap.put("b60_4_14", new visuperobject(60,4,14,"Ví bằng anh em vì cớ danh Ðấng Christ chịu sỉ nhục, thì anh em có phước; vì sự vinh hiển và Thánh Linh của Ðức Chúa Trời đậu trên anh em."));
visuperobject.biblemap.put("b60_4_15", new visuperobject(60,4,15,"Trong anh em chớ có ai chịu khổ như kẻ giết người, như kẻ trộm cướp, như kẻ hung ác, như kẻ thày lay việc người khác."));
visuperobject.biblemap.put("b60_4_16", new visuperobject(60,4,16,"Nhưng nếu có ai vì làm tín đồ Ðấng Christ mà chịu khổ, thì đừng hổ thẹn; thà hãy vì danh ấy ngợi khen Ðức Chúa Trời là hơn."));
visuperobject.biblemap.put("b60_4_17", new visuperobject(60,4,17,"Vì thời kỳ đã đến, là khi sự phán xét sẽ khởi từ nhà Ðức Chúa Trời; vả, nếu khởi từ chúng ta, thì sự cuối cùng của những kẻ chẳng vâng theo Tin Lành Ðức Chúa Trời sẽ ra thế nào?"));
visuperobject.biblemap.put("b60_4_18", new visuperobject(60,4,18,"Lại nếu người công bình còn khó được rỗi, thì những kẻ nghịch đạo và có tội sẽ trở nên thế nào?"));
visuperobject.biblemap.put("b60_4_19", new visuperobject(60,4,19,"Vậy những kẻ chịu khổ theo ý muốn Ðức Chúa Trời, hãy cứ làm lành mà phó linh hồn mình cho Ðấng Tạo hóa thành tín."));
visuperobject.biblemap.put("b60_5_1", new visuperobject(60,5,1,"Tôi gởi lời khuyên nhủ nầy cho các bậc trưởng lão trong anh em, tôi đây cũng là trưởng lão như họ, là người chứng kiến sự đau đớn của Ðấng Christ, và cũng có phần về sự vinh hiển sẽ hiện ra:"));
visuperobject.biblemap.put("b60_5_2", new visuperobject(60,5,2,"hãy chăn bầy của Ðức Chúa Trời đã giao phó cho anh em; làm việc đó chẳng phải bởi ép tình, bèn là bởi vui lòng, chẳng phải vì lợi dơ bẩn, bèn là hết lòng mà làm,"));
visuperobject.biblemap.put("b60_5_3", new visuperobject(60,5,3,"chẳng phải quản trị phần trách nhậm chia cho anh em, song để làm gương tốt cho cả bầy."));
visuperobject.biblemap.put("b60_5_4", new visuperobject(60,5,4,"Khi Ðấng làm đầu các kẻ chăn chiên hiện ra, anh em sẽ được mão triều thiên vinh hiển, chẳng hề tàn héo."));
visuperobject.biblemap.put("b60_5_5", new visuperobject(60,5,5,"Cũng khuyên bọn trẻ tuổi, hãy phục theo các trưởng lão. Hết thảy đối đãi với nhau phải trang sức bằng khiêm nhường; vì Ðức Chúa Trời chống cự kẻ kiêu ngạo, mà ban ơn cho kẻ khiêm nhường."));
visuperobject.biblemap.put("b60_5_6", new visuperobject(60,5,6,"Vậy, hãy hạ mình xuống dưới tay quyền phép của Ðức Chúa Trời, hầu cho đến ký thuận hiệp Ngài nhắc anh em lên;"));
visuperobject.biblemap.put("b60_5_7", new visuperobject(60,5,7,"lại hãy trao mọi điều lo lắng mình cho Ngài, vì Ngài hay săn sóc anh em."));
visuperobject.biblemap.put("b60_5_8", new visuperobject(60,5,8,"Hãy tiết độ và tỉnh thức: kẻ thù nghịch anh em là ma quỉ, như sư tử rống, đi rình mò chung quanh anh em, tìm kiếm người nào nó có thể nuốt được."));
visuperobject.biblemap.put("b60_5_9", new visuperobject(60,5,9,"Hãy đứng vững trong đức tin mà chống cự nó, vì biết rằng anh em mình ở rải khắp thế gian, cũng đồng chịu hoạn nạn như mình."));
visuperobject.biblemap.put("b60_5_10", new visuperobject(60,5,10,"Ðức Chúa Trời ban mọi ơn đã gọi anh em đến sự vinh hiển đời đời của Ngài trong Ðấng Christ, thì sau khi anh em tạm chịu khổ, chính Ngài sẽ làm cho anh em trọn vẹn, vững vàng, và thêm sức cho."));
visuperobject.biblemap.put("b60_5_11", new visuperobject(60,5,11,"Nguyền xin quyền phép về nơi Ngài, đời đời vô cùng! A-men."));
visuperobject.biblemap.put("b60_5_12", new visuperobject(60,5,12,"Tôi cậy Sin-vanh, là kẻ tôi coi như một người anh em trung tín, viết mấy chữ nầy đặng khuyên anh em, và làm chứng với anh em rằng ấy là ơn thật của Ðức Chúa Trời, anh em phải đứng vững trong đó."));
visuperobject.biblemap.put("b60_5_13", new visuperobject(60,5,13,"Hội thánh của các người được chọn, tại thành Ba-by-lôn, chào anh em, con tôi là Mác cũng vậy."));
visuperobject.biblemap.put("b60_5_14", new visuperobject(60,5,14,"Anh em hãy lấy cái hôn yêu thương mà chào nhau. Nguyền xin sự bình an ở cùng hết thảy anh em trong Ðấng Christ!"));

}

}