package vi.bible;

public class vibible61 {

public vibible61() {

putmapbible61();

}

private void putmapbible61() {

visuperobject.biblemap.put("b61_1_1", new visuperobject(61,1,1,"Si-môn Phi -e-rơ, làm tôi tớ và sứ đồ của Ðức Chúa Jêsus Christ, gởi cho những kẻ cậy sự công bình của Ðức Chúa Trời chúng ta và của Cứu Chúa là Ðức Chúa Jêsus Christ, đã lãnh phần đức tin đồng quí báu như của chúng tôi:"));
visuperobject.biblemap.put("b61_1_2", new visuperobject(61,1,2,"nguyền xin ân điển và sự bình an được gia thêm cho anh em bởi sự nhận biết Ðức Chúa Trời và Ðức Chúa Jêsus, là Chúa chúng ta!"));
visuperobject.biblemap.put("b61_1_3", new visuperobject(61,1,3,"Quyền phép Ðức Chúa Trời đã ban cho chúng ta mọi điều thuộc về sự sống và sự tôn kính, khiến chúng ta biết Ðấng lấy vinh hiển và nhơn đức mà gọi chúng ta,"));
visuperobject.biblemap.put("b61_1_4", new visuperobject(61,1,4,"và bởi vinh hiển nhơn đức ấy, Ngài lại ban lời hứa rất quí rất lớn cho chúng ta, hầu cho nhờ đó anh em được lánh khỏi sự hư nát của thế gian bởi tư dục đến, mà trở nên người dự phần bổn tánh Ðức Chúa Trời."));
visuperobject.biblemap.put("b61_1_5", new visuperobject(61,1,5,"Vậy nên, về phần anh em, phải gắng hết sức thêm cho đức tin mình sự nhơn đức, thêm cho nhơn đức sự học thức,"));
visuperobject.biblemap.put("b61_1_6", new visuperobject(61,1,6,"thêm cho học thức sự tiết độ, thêm cho tiết độ sự nhịn nhục, thêm cho nhịn nhục sự tôn kính,"));
visuperobject.biblemap.put("b61_1_7", new visuperobject(61,1,7,"thêm cho tôn kính tình yêu thương anh em, thêm cho tình yêu thương anh em lòng yêu mến."));
visuperobject.biblemap.put("b61_1_8", new visuperobject(61,1,8,"Vì nếu các điều đó có đủ trong anh em và đầy dẫy nữa, thì ắt chẳng để cho anh em ở dưng hoặc không kết quả trong sự nhận biết Ðức Chúa Jêsus Christ chúng ta đâu."));
visuperobject.biblemap.put("b61_1_9", new visuperobject(61,1,9,"Nhưng ai thiếu những điều đó, thì thành ra người cận thị, người mù; quên hẳn sự làm sạch tội mình ngày trước."));
visuperobject.biblemap.put("b61_1_10", new visuperobject(61,1,10,"Vậy, hỡi anh em, hãy chú ý cho chắc chắn về sự Chúa kêu gọi và chọn lựa mình. Làm điều đó anh em sẽ không hề vấp ngã;"));
visuperobject.biblemap.put("b61_1_11", new visuperobject(61,1,11,"dường ấy, anh em sẽ được cho vào cách rộng rãi trong nước đời đời của Ðức Chúa Jêsus Christ, là Chúa và Cứu Chúa của chúng ta."));
visuperobject.biblemap.put("b61_1_12", new visuperobject(61,1,12,"Bởi vậy cho nên, dầu anh em biết rõ ràng và chắc chắn trong lẽ thật hiện đây, tôi cũng sẽ nhắc lại những điều đó cho anh em chẳng thôi."));
visuperobject.biblemap.put("b61_1_13", new visuperobject(61,1,13,"Nhưng tôi còn ở trong nhà tạm nầy bao lâu, thì coi sự lấy lời rao bảo mà tỉnh thức anh em, là bổn phận của tôi vậy;"));
visuperobject.biblemap.put("b61_1_14", new visuperobject(61,1,14,"vì tôi biết tôi phải vội lìa nhà tạm nầy, như Ðức Chúa Jêsus Christ chúng ta đã bảo cho tôi."));
visuperobject.biblemap.put("b61_1_15", new visuperobject(61,1,15,"Nhưng tôi ân cần rằng sau khi tôi đi, anh em có thể hằng nhớ điều tôi đã nói."));
visuperobject.biblemap.put("b61_1_16", new visuperobject(61,1,16,"Vả, khi chúng tôi đã làm cho anh em biết quyền phép và sự đến của Ðức Chúa Jêsus Christ chúng ta, thì chẳng phải là theo những chuyện khéo đặt để, bèn là chính mắt chúng tôi đã ngó thấy sự oai nghiêm Ngài."));
visuperobject.biblemap.put("b61_1_17", new visuperobject(61,1,17,"Vì Ngài đã nhận lãnh sự tôn trọng vinh hiển từ nơi Ðức Chúa Trời, Cha Ngài, khi Ðấng tôn nghiêm rất cao phán cùng Ngài rằng: Nầy là Con yêu dấu của ta, đẹp lòng ta mọi đường."));
visuperobject.biblemap.put("b61_1_18", new visuperobject(61,1,18,"Chính chúng tôi cũng từng nghe tiếng ấy đến từ trời, lúc chúng tôi ở với Ngài trên hòn núi thánh."));
visuperobject.biblemap.put("b61_1_19", new visuperobject(61,1,19,"Nhơn đó, chúng tôi cùng tin lời các đấng tiên tri chắc chắn hơn, anh em nên chú ý lời đó, như cái đèn soi sáng trong nơi tối tăm, cho đến chừng nào ban ngày lộ ra, và sao mai mọc trong lòng anh em."));
visuperobject.biblemap.put("b61_1_20", new visuperobject(61,1,20,"Trước hết, phải biết rõ rằng chẳng có lời tiên tri nào trong Kinh Thánh lấy ý riêng giải nghĩa được."));
visuperobject.biblemap.put("b61_1_21", new visuperobject(61,1,21,"Vì chẳng hề có lời tiên tri nào là bởi ý một người nào mà ra, nhưng ấy là Ngài Ðức Thánh Linh cảm động mà người ta đã nói bởi Ðức Chúa Trời."));
visuperobject.biblemap.put("b61_2_1", new visuperobject(61,2,1,"Dầu vậy, trong dân chúng cũng đã có tiên tri giả, và cũng sẽ có giáo sư giả trong anh em; họ sẽ truyền những đạo dối làm hại, chối Chúa đã chuộc mình, tự mình chuốc lấy sự hủy phá thình lình."));
visuperobject.biblemap.put("b61_2_2", new visuperobject(61,2,2,"Có nhiều kẻ sẽ theo họ trong những sự buông tuồng, và đạo thật vì cứ họ sẽ bị gièm pha."));
visuperobject.biblemap.put("b61_2_3", new visuperobject(61,2,3,"Họ sẽ bởi lòng tham mình, lấy lời dối trá khoét anh em; nhưng sự kêu án nghịch cùng họ đã nghị định từ lâu nay, và sự hư mất của họ chẳng ngủ."));
visuperobject.biblemap.put("b61_2_4", new visuperobject(61,2,4,"Vả, nếu Ðức Chúa Trời chẳng tiếc các thiên sứ đã phạm tội, nhưng quăng vào trong vực sâu, tại đó họ bị trói buộc bằng xiềng nơi tối tăm để chờ sự phán xét;"));
visuperobject.biblemap.put("b61_2_5", new visuperobject(61,2,5,"nếu Ngài chẳng tiếc thế gian xưa, trong khi sai nước lụt phạt đời gian ác nầy, chỉ gìn giữ Nô-ê là thầy giảng đạo công bình, với bảy người khác mà thôi;"));
visuperobject.biblemap.put("b61_2_6", new visuperobject(61,2,6,"nếu Ngài đã đoán phạt thành Sô-đôm và Gô-mô-rơ, hủy phá đi khiến hóa ra tro, để làm gương cho người gian ác về sau;"));
visuperobject.biblemap.put("b61_2_7", new visuperobject(61,2,7,"nếu Ngài đã giải cứu người công bình là Lót, tức là kẻ quá lo vì cách ăn ở luông tuồng của bọn gian tà kia,"));
visuperobject.biblemap.put("b61_2_8", new visuperobject(61,2,8,"(vì người công bình nầy ở giữa họ, mỗi ngày nghe thấy việc trái phép của họ bèn cảm biết đau xót trong lòng công bình mình),"));
visuperobject.biblemap.put("b61_2_9", new visuperobject(61,2,9,"thì Chúa biết cứu chữa những người tôn kính khỏi cơn cám dỗ, và hành phạt kẻ không công bình, cầm chúng nó lại để chờ ngày phán xét,"));
visuperobject.biblemap.put("b61_2_10", new visuperobject(61,2,10,"nhứt là những kẻ theo lòng tư dục ô uế mình mà ham mê sự sung sướng xác thịt, khinh dể quyền phép rất cao. Bọn đó cả gan, tự đắc, nói hỗn đến các bậc tôn trọng mà không sợ,"));
visuperobject.biblemap.put("b61_2_11", new visuperobject(61,2,11,"dẫu các thiên sứ, là đấng có sức mạnh quyền phép hơn chúng nó, còn không hề lấy lời nguyền rủa mà xử đoán các bậc đó trước mặt Chúa."));
visuperobject.biblemap.put("b61_2_12", new visuperobject(61,2,12,"Nhưng chúng nó cũng như con vật không biết chi, sanh ra chỉ làm thú vật để bị bắt mà làm thịt, hay chê bai điều mình không biết, rồi chết đi như con vật, dường ấy chúng nó lãnh lấy tiền công về tội ác mình."));
visuperobject.biblemap.put("b61_2_13", new visuperobject(61,2,13,"Chúng nó lấy sự chơi bời giữa ban ngày làm sung sướng, là người xấu xa ô uế, ưa thích sự dối trá mình đang khi ăn tiệc với anh em;"));
visuperobject.biblemap.put("b61_2_14", new visuperobject(61,2,14,"cặp mắt chúng nó đầy sự gian dâm, chúng nó phạm tội không bao giờ chán, dỗ dành những người không vững lòng, chúng nó có lòng quen thói tham dục: ấy là những con cái đáng rủa sả."));
visuperobject.biblemap.put("b61_2_15", new visuperobject(61,2,15,"chúng nó đã bỏ đường thẳng mà đi sai lạc theo đường của Ba-la-am, con trai Bô-sô, là kẻ tham tiền công của tội ác; nhưng người bị trách về sự phạm tội của mình,"));
visuperobject.biblemap.put("b61_2_16", new visuperobject(61,2,16,"bởi có một con vật câm nói tiếng người ta, mà ngăn cấm sự điên cuồng của người tiên tri đó."));
visuperobject.biblemap.put("b61_2_17", new visuperobject(61,2,17,"Ấy là những suối nước, những đám mây bị luồng gió mạnh đưa đi và sự tối tăm mờ mịt đã để dành cho chúng nó."));
visuperobject.biblemap.put("b61_2_18", new visuperobject(61,2,18,"Chúng nó dùng những lời văn hoa hư đản, đem những điều ham muốn của xác thịt và điều gian dâm mà dỗ dành những kẻ mới vừa tránh khỏi các người theo đường lầm lạc;"));
visuperobject.biblemap.put("b61_2_19", new visuperobject(61,2,19,"chúng nó hứa sự tự do cho người, mà chính mình thì làm tôi mọi sự hư nát; vì có sự chi đã thắng hơn người, thì người là tôi mọi sự đó."));
visuperobject.biblemap.put("b61_2_20", new visuperobject(61,2,20,"Vả, chúng nó bởi sự nhận biết Chúa và Cứu Chúa chúng ta là Ðức Chúa Jêsus Christ, mà đã thoát khỏi sự ô uế của thế gian, rồi lại mắc phải và suy phục những sự đó, thì số phận sau cùng của chúng nó trở xấu hơn lúc đầu."));
visuperobject.biblemap.put("b61_2_21", new visuperobject(61,2,21,"chúng nó đã biết đường công bình, rồi lại lui đi về lời răn thánh đã truyền cho mình, thế thì thà rằng không biết là hơn."));
visuperobject.biblemap.put("b61_2_22", new visuperobject(61,2,22,"Ðã xảy đến cho chúng nó như lời t»¥c ngữ rằng: Chó liếm lại đồ nó đã mửa, heo đã rửa sách rồi, lại liên lạc trong vũng bùn."));
visuperobject.biblemap.put("b61_3_1", new visuperobject(61,3,1,"Hỡi kẻ rất yêu dấu, nầy là thơ thứ hai tôi viết cho anh em. trong thơ nầy và thơ kia, tôi tìm cách làm cho nhớ lại để giục lòng lành trong anh em,"));
visuperobject.biblemap.put("b61_3_2", new visuperobject(61,3,2,"hầu cho ghi lấy lời nói trước của Chúa và Cứu Chúa chúng ta, đã cậy các sứ đồ của anh em mà truyền lại."));
visuperobject.biblemap.put("b61_3_3", new visuperobject(61,3,3,"Trước hết phải biết rằng, trong những ngày sau rốt, sẽ có mấy kẻ hay gièm chê, dùng lời giễu cợt, ở theo tình dục riêng của mình,"));
visuperobject.biblemap.put("b61_3_4", new visuperobject(61,3,4,"đến mà nói rằng: Chớ nào lời hứa về sự Chúa đến ở đâu? Vì từ khi tổ phụ chúng ta qua đời rồi, muôn vật vẫn còn nguyên như lúc bắt đầu sáng thế."));
visuperobject.biblemap.put("b61_3_5", new visuperobject(61,3,5,"Chúng nó có ý quên lững đi rằng buổi xưa bởi lời Ðức Chúa Trời có các từng trời và trái đất, đất ra từ Nước và làm nên ở giữa nước,"));
visuperobject.biblemap.put("b61_3_6", new visuperobject(61,3,6,"thế gian bấy giờ cũng bị hủy phá như vậy, là bị chìm đắm bởi nước lụt."));
visuperobject.biblemap.put("b61_3_7", new visuperobject(61,3,7,"Nhưng trời đất thời bây giờ cũng là bởi lời ấy mà còn lại, và để dành cho lửa; lửa sẽ đốt nó đi trong ngày phán xét và hủy phá kẻ ác."));
visuperobject.biblemap.put("b61_3_8", new visuperobject(61,3,8,"Hỡi kẻ rất yêu dấu, chớ nên quên rằng ở trước mặt Chúa một ngày như ngàn năm, ngàn năm như một ngày."));
visuperobject.biblemap.put("b61_3_9", new visuperobject(61,3,9,"Chúa không chậm trễ về lời hứa của Ngài như mấy người kia tưởng đâu, nhưng Ngài lấy lòng nhịn nhục đối với anh em, không muốn cho một người nào chết mất, song muốn cho mọi người đều ăn năn."));
visuperobject.biblemap.put("b61_3_10", new visuperobject(61,3,10,"Song le, ngày của Chúa sẽ đến như kẻ trộm. Bấy giờ các từng trời sẽ có tiếng vang rầm mà qua đi, các thể chết bị đốt mà tiêu tán, đất cùng mọi công trình trên nó đều sẽ bị đốt cháy cả."));
visuperobject.biblemap.put("b61_3_11", new visuperobject(61,3,11,"Vì mọi vật đó phải tiêu tán thì anh em đáng nên thánh và tôn kính trong mọi sự ăn ở của mình là dường nào,"));
visuperobject.biblemap.put("b61_3_12", new visuperobject(61,3,12,"trong khi chờ đợi trông mong cho ngày Ðức Chúa Trời mau đến, là ngày các từng trời sẽ bị đốt mà tiêu tán, các thể chất sẽ bị thiêu mà tan chảy đi!"));
visuperobject.biblemap.put("b61_3_13", new visuperobject(61,3,13,"Vả, theo lời hứa của Chúa, chúng ta chờ đợi trời mới đất mới, là nơi sự công bình ăn ở."));
visuperobject.biblemap.put("b61_3_14", new visuperobject(61,3,14,"Vậy nên, hỡi kẻ rất yêu dấu, vì anh em trông đợi những sự đó, thì phải làm hết sức mình, hầu cho Chúa thấy anh em ở bình an, không dấu vít, chẳng chỗ trách được."));
visuperobject.biblemap.put("b61_3_15", new visuperobject(61,3,15,"Lại phải nhìn biết rằng sự nhịn nhục lâu dài của Chúa chúng ta cốt vì cứu chuộc anh em, cũng như Phao lô, anh rất yêu dấu của chúng ta, đã đem sự khôn ngoan được ban cho mình mà viết thơ cho anh em vậy."));
visuperobject.biblemap.put("b61_3_16", new visuperobject(61,3,16,"Ấy là điều người đã viết trong mọi bức thơ, nói về những sự đó, ở trong có mấy khúc khó hiểu, mà những kẻ dốt nát và tin không quyết đem giải sai ý nghĩa, cũng như họ giải sai về các phần Kinh Thánh khác, chuốc lấy sự hư mất riêng về mình."));
visuperobject.biblemap.put("b61_3_17", new visuperobject(61,3,17,"Hỡi kẻ rất yêu dấu, vì anh em đã được biết trước, vậy hãy giữ cho cẩn thận, e anh em cũng bị sự mê hoặc của những người ác ấy dẫn dụ, mất sự vững vàng của mình chăng."));
visuperobject.biblemap.put("b61_3_18", new visuperobject(61,3,18,"Hãy tấn tới trong ân điển và trong sự thông biết Chúa và Cứu Chúa chúng ta là Ðức Chúa Jêsus Christ. Nguyền xin vinh hiển về nơi Ngài, từ rày đến đời đời! A-men."));

}

}