package vi.bible;

public class vibible62 {

public vibible62() {

putmapbible62();

}

private void putmapbible62() {

visuperobject.biblemap.put("b62_1_1", new visuperobject(62,1,1,"Ðiều có từ trước hết, là điều chúng tôi đã nghe, điều mắt chúng tôi đã thấy, điều chúng tôi đã ngắm và tay chúng tôi đã rờ, về lời sự sống;"));
visuperobject.biblemap.put("b62_1_2", new visuperobject(62,1,2,"vì sự sống đã bày tỏ ra, chúng tôi có thấy, và đang làm chứng cho, chúng tôi rao truyền cho anh em sự sống đời đời vốn ở cùng Ðức Chúa Cha và đã bày tỏ ra cho chúng tôi rồi;"));
visuperobject.biblemap.put("b62_1_3", new visuperobject(62,1,3,"chúng tôi lấy điều đã thấy đã nghe mà truyền cho anh em, hầu cho anh em cũng được giao thông với chúng tôi. Vả, chúng tôi vẫn được giao thông với Ðức Chúa Cha, và với Con Ngài là Ðức Chúa Jêsus Christ."));
visuperobject.biblemap.put("b62_1_4", new visuperobject(62,1,4,"Chúng tôi viết những điều đó cho anh em, hầu cho sự vui mừng của chúng tôi được đầy dẫy."));
visuperobject.biblemap.put("b62_1_5", new visuperobject(62,1,5,"Nầy là lời truyền giảng mà chúng tôi đã nghe nơi Ngài và truyền lại cho anh em rằng Ðức Chúa Trời là sự sáng, trong Ngài chẳng có sự tối tăm đâu."));
visuperobject.biblemap.put("b62_1_6", new visuperobject(62,1,6,"Ví bằng chúng ta nói mình được giao thông với Ngài, mà còn đi trong sự tối tăm, ấy là chúng ta nói dối và không làm theo lẽ thật."));
visuperobject.biblemap.put("b62_1_7", new visuperobject(62,1,7,"Nhưng, nếu chúng ta đi trong sự sáng cũng như chính mình Ngài ở trong sự sáng, thì chúng ta giao thông cùng nhau; và huyết của Ðức Chúa Jêsus, Con Ngài, làm sạch mọi tội chúng ta."));
visuperobject.biblemap.put("b62_1_8", new visuperobject(62,1,8,"Ví bằng chúng ta nói mình không có tội chi hết, ấy là chính chúng ta lừa dối mình, và lẽ thật không ở trong chúng ta."));
visuperobject.biblemap.put("b62_1_9", new visuperobject(62,1,9,"Còn nếu chúng ta xưng tội mình, thì Ngài là thành tín công bình để tha tội cho chúng ta, và làm cho chúng ta sạch mọi điều gian ác."));
visuperobject.biblemap.put("b62_1_10", new visuperobject(62,1,10,"Nhược bằng chúng ta nói mình chẳng từng phạm tội, ấy là chúng ta cho Ngài là kẻ nói dối, lời Ngài không ở trong chúng ta."));
visuperobject.biblemap.put("b62_2_1", new visuperobject(62,2,1,"Hỡi con cái bé mọn ta, ta viết cho các con những điều nầy, hầu cho các con khỏi phạm tội. Nếu có ai phạm tội, thì chúng ta có Ðấng cầu thay ở nơi Ðức Chúa Cha, là Ðức Chúa Jêsus Christ, tức là Ðấng công bình."));
visuperobject.biblemap.put("b62_2_2", new visuperobject(62,2,2,"Ấy chính Ngài làm của lễ chuộc tội lỗi chúng ta, không những vì tội lỗi chúng ta thôi đâu, mà cũng vì tội lỗi cả thế gian nữa."));
visuperobject.biblemap.put("b62_2_3", new visuperobject(62,2,3,"Nầy tại sao chúng ta biết mình đã biết Ngài, ấy là tại chúng ta giữ các điều răn của Ngài."));
visuperobject.biblemap.put("b62_2_4", new visuperobject(62,2,4,"Kẻ nào nói: Ta biết Ngài, mà không giữ điều răn Ngài, là người nói dối, lẽ thật quyết không ở trong người."));
visuperobject.biblemap.put("b62_2_5", new visuperobject(62,2,5,"Nhưng ai giữ lời phán Ngài, thì lòng kính mến Ðức Chúa Trời thật là trọn vẹn trong người ấy. Bởi đó, chúng ta biết mình ở trong Ngài."));
visuperobject.biblemap.put("b62_2_6", new visuperobject(62,2,6,"Ai nói mình ở trong Ngài, thì cũng phải làm theo như chính Ngài đã làm."));
visuperobject.biblemap.put("b62_2_7", new visuperobject(62,2,7,"Hỡi kẻ rất yêu dấu, ấy chẳng phải là điều răn mới mà ta viết cho anh em, bèn là điều răn cũ anh em đã nhận lấy từ lúc ban đầu; điều răn cũ nầy tức là lời anh em đã nghe."));
visuperobject.biblemap.put("b62_2_8", new visuperobject(62,2,8,"Song le, ta cũng viết cho anh em điều răn mới, là điều chân thật trong Chúa và trong anh em, vì sự tối tăm đã trên rồi, và sự sáng thật đã soi sáng."));
visuperobject.biblemap.put("b62_2_9", new visuperobject(62,2,9,"Kẻ nào nói mình trong sự sáng, mà ghét anh em mình thì còn ở trong sự tối tăm."));
visuperobject.biblemap.put("b62_2_10", new visuperobject(62,2,10,"Ai yêu mến anh em mình, thì ở trong sự sáng, nơi người đó chẳng có điều chi gây cho vấp phạm."));
visuperobject.biblemap.put("b62_2_11", new visuperobject(62,2,11,"Nhưng ai ghét anh em mình, thì ở trong sự tối tăm, làm những việc tối tăm, và không biết mình đi đâu, vì bóng tối tăm đã làm mù mắt người."));
visuperobject.biblemap.put("b62_2_12", new visuperobject(62,2,12,"Hỡi các con cái bé mọn ta, ta viết cho các con, vì tội lỗi các con đã nhờ danh Chúa được tha cho."));
visuperobject.biblemap.put("b62_2_13", new visuperobject(62,2,13,"Hỡi các phụ lão, tôi viết cho các ông, vì các ông đã biết Ðấng có từ lúc ban đầu. Hỡi kẻ trẻ tuổi, ta viết cho các ngươi, vì các ngươi đã thắng được ma quỉ."));
visuperobject.biblemap.put("b62_2_14", new visuperobject(62,2,14,"Hỡi con trẻ, ta đã viết cho các con, vì các con đã biết Ðức Chúa Cha. Hỡi phụ lão, tôi đã viết cho các ông, vì các ông đã biết Ðấng có từ lúc ban đầu. Hỡi kẻ trẻ tuổi, ta đã viết cho các ngươi, vì các ngươi là mạnh mẽ, lời Ðức Chúa Trời ở trong các ngươi, và các ngươi đã thắng được ma quỉ."));
visuperobject.biblemap.put("b62_2_15", new visuperobject(62,2,15,"Chớ yêu thế gian, cũng đừng yêu các vật ở thế gian nữa; nếu ai yêu thế gian, thì sự kính mến Ðức Chúa Cha chẳng ở trong người ấy."));
visuperobject.biblemap.put("b62_2_16", new visuperobject(62,2,16,"Vì mọi sự trong thế gian, như sự mê tham của xác thịt, sự mê tham của mắt, và sự kiêu ngạo của đời, đều chẳng từ Cha mà đến, nhưng từ thế gian mà ra."));
visuperobject.biblemap.put("b62_2_17", new visuperobject(62,2,17,"Vả thế gian với sự tham dục nó đều qua đi, song ai làm theo ý muốn Ðức Chúa Trời thì còn lại đời đời."));
visuperobject.biblemap.put("b62_2_18", new visuperobject(62,2,18,"Hỡi các con cái ta, giờ cuối cùng là đây rồi, các con đã nghe nói rằng Kẻ địch lại Ðấng Christ phải đến. Vừa lúc bây giờ, có nhiều kẻ địch lại Ðấng Christ; bởi đó, chúng ta biết rằng ấy là giờ cuối cùng."));
visuperobject.biblemap.put("b62_2_19", new visuperobject(62,2,19,"Chúng nó đã từ giữa chúng ta mà ra, nhưng vốn chẳng phải thuộc về chúng ta; vì nếu chúng nó thuộc về chúng ta, thì đã ở cùng chúng ta; song điều đó đã xảy đến, hầu cho tỏ ra rằng mọi kẻ ấy chẳng thuộc về chúng ta vậy."));
visuperobject.biblemap.put("b62_2_20", new visuperobject(62,2,20,"Về phần các con, đã chịu xức dầu từ nơi Ðấng thánh, thì đã biết mọi sự rồi."));
visuperobject.biblemap.put("b62_2_21", new visuperobject(62,2,21,"Ta viết cho các con, chẳng phải vì các con không biết lẽ thật, nhưng vì các con biết lẽ thật, và hiểu rằng chẳng có sự dối trá nào bởi lẽ thật mà ra."));
visuperobject.biblemap.put("b62_2_22", new visuperobject(62,2,22,"Ai là kẻ nói dối, há chẳng phải kẻ chối Ðức Chúa Jêsus là Ðấng Christ sao? Ấy đó là Kẻ địch lại Ðấng Christ, tức là kẻ chối Ðức Chúa Cha và Ðức Chúa Con!"));
visuperobject.biblemap.put("b62_2_23", new visuperobject(62,2,23,"Ai chối Con, thì cũng không có Cha: ai xưng Con, thì cũng có Cha nữa."));
visuperobject.biblemap.put("b62_2_24", new visuperobject(62,2,24,"Còn như các con, điều mình đã nghe từ lúc ban đầu phải ở trong các con. Nếu điều các con đã nghe từ lúc ban đầu ở trong mình, thì các con cũng sẽ ở trong Con và trong Cha."));
visuperobject.biblemap.put("b62_2_25", new visuperobject(62,2,25,"Lời hứa mà chính Ngài đã hứa cùng chúng ta, ấy là sự sống đời đời."));
visuperobject.biblemap.put("b62_2_26", new visuperobject(62,2,26,"Ta đã viết cho các con những điều nầy, chỉ về những kẻ lừa dối các con."));
visuperobject.biblemap.put("b62_2_27", new visuperobject(62,2,27,"Về phần các con, sự xức dầu đã chịu từ nơi Chúa vẫn còn trong mình, thì không cần ai dạy cho hết; song vì sự xức dầu của Ngài dạy các con đủ mọi việc, mà sự ấy là thật, không phải dối, thì hãy ở trong Ngài, theo như sự dạy dỗ mà các con đã nhận."));
visuperobject.biblemap.put("b62_2_28", new visuperobject(62,2,28,"Vậy bây giờ, hỡi các con cái bé mọn ta, hãy ở trong Ngài, hầu cho, nếu Ngài hiện đến, chúng ta cũng đầy sự vững lòng, không bị hổ thẹn và quăng xa khỏi Ngài trong kỳ Ngài ngự đến."));
visuperobject.biblemap.put("b62_2_29", new visuperobject(62,2,29,"Ví bằng các con biết Ngài là công bình, thì hãy biết rằng người nào làm theo sự công bình, ấy là kẻ bởi Ngài mà sanh ra."));
visuperobject.biblemap.put("b62_3_1", new visuperobject(62,3,1,"Hãy xem Ðức Chúa Cha đã tỏ cho chúng ta sự yêu thương dường nào, mà cho chúng ta được xưng là con cái Ðức Chúa Trời; và chúng ta thật là con cái Ngài. Ấy là vì đó mà thế gian chẳng biết chúng ta, vì họ chẳng từng biết Ngài."));
visuperobject.biblemap.put("b62_3_2", new visuperobject(62,3,2,"Hỡi kẻ rất yêu dấu, chính lúc bây giờ chúng ta là con cái Ðức Chúa Trời, còn về sự chúng ta sẽ ra thể nào, thì điều đó chưa được bày tỏ. Chúng ta biết rằng khi Ngài hiện đến, chúng ta sẽ giống như Ngài, vì sẽ thấy Ngài như vốn có thật vậy."));
visuperobject.biblemap.put("b62_3_3", new visuperobject(62,3,3,"Ai có sự trông cậy đó trong lòng, thì tự mình làm nên thanh sạch, cũng như Ngài là thanh sạch."));
visuperobject.biblemap.put("b62_3_4", new visuperobject(62,3,4,"Còn ai phạm tội tức là trái luật pháp; và sự tội lỗi tức là sự trái luật pháp."));
visuperobject.biblemap.put("b62_3_5", new visuperobject(62,3,5,"Vả, các con biết Ðức Chúa Jêsus Christ đã hiện ra để cất tội lỗi đi, và biết trong Ngài không có tội lỗi."));
visuperobject.biblemap.put("b62_3_6", new visuperobject(62,3,6,"Ai ở trong Ngài thì không phạm tội; còn ai phạm tội, thì chẳng từng thấy Ngài và chẳng từng biết Ngài."));
visuperobject.biblemap.put("b62_3_7", new visuperobject(62,3,7,"Hỡi các con cái bé mọn, chớ để cho ai lừa dối mình: kẻ làm sự công bình là người công bình, như chính mình Chúa là công bình."));
visuperobject.biblemap.put("b62_3_8", new visuperobject(62,3,8,"Kẻ nào phạm tội là thuộc về ma quỉ; vì ma quỉ phạm tội từ lúc ban đầu. Vả, Con Ðức Chúa Trời đã hiện ra để hủy phá công việc của ma quỉ."));
visuperobject.biblemap.put("b62_3_9", new visuperobject(62,3,9,"Ai sanh bởi Ðức Chúa Trời, thì chẳng phạm tội, vì hột giống của Ðức Chúa Trời ở trong người, và người không thể phạm tội được, vì đã sanh bởi Ðức Chúa Trời."));
visuperobject.biblemap.put("b62_3_10", new visuperobject(62,3,10,"Bởi đó, người ta nhận biết con cái Ðức Chúa Trời và con cái ma quỉ: ai chẳng làm điều công bình là không thuộc về Ðức Chúa Trời, kẻ chẳng yêu anh em mình cũng vậy."));
visuperobject.biblemap.put("b62_3_11", new visuperobject(62,3,11,"Vả, lời rao truyền mà các con đã nghe từ lúc ban đầu, ấy là chúng ta phải yêu thương lẫn nhau."));
visuperobject.biblemap.put("b62_3_12", new visuperobject(62,3,12,"Chớ làm như Ca-in, là kẻ thuộc về ma quỉ, đã giết em mình. Vì sao người giết đi? Bởi việc làm của người là dữ, còn việc làm của em người là công bình."));
visuperobject.biblemap.put("b62_3_13", new visuperobject(62,3,13,"Hỡi anh em, nếu thế gian ghen ghét anh em, thì chớ lấy làm lạ."));
visuperobject.biblemap.put("b62_3_14", new visuperobject(62,3,14,"Chúng ta biết rằng mình đã vượt khỏi sự chết qua sự sống, vì chúng ta yêu anh em mình. Còn ai chẳng yêu thì ở trong sự chết."));
visuperobject.biblemap.put("b62_3_15", new visuperobject(62,3,15,"Ai ghét anh em mình, là kẻ giết người; anh em biết rằng chẳng một kẻ nào giết người có sự sống đời đời ở trong mình."));
visuperobject.biblemap.put("b62_3_16", new visuperobject(62,3,16,"Bởi đó chúng ta nhận biết lòng yêu thương, ấy là Chúa đã vì chúng ta bỏ sự sống; chúng ta cũng nên bỏ sự sống vì anh em mình vậy."));
visuperobject.biblemap.put("b62_3_17", new visuperobject(62,3,17,"Nếu ai có của cải đời nầy, thấy anh em mình đương cùng túng mà chặt dạ, thì lòng yêu mến Ðức Chúa Trời thể nào ở trong người ấy được!"));
visuperobject.biblemap.put("b62_3_18", new visuperobject(62,3,18,"Hỡi các con cái bé mọn, chớ yêu mến bằng lời nói và lưỡi, nhưng bằng việc làm và lẽ thật."));
visuperobject.biblemap.put("b62_3_19", new visuperobject(62,3,19,"Bởi đó, chúng ta biết mình là thuộc về lẽ thật, và giục lòng vững chắc ở trước mặt Ngài."));
visuperobject.biblemap.put("b62_3_20", new visuperobject(62,3,20,"Vì nếu lòng mình cáo trách mình, thì Ðức Chúa Trời lại lớn hơn lòng mình nữa, và biết cả mọi sự."));
visuperobject.biblemap.put("b62_3_21", new visuperobject(62,3,21,"Hỡi kẻ rất yêu dấu, ví bằng lòng mình không cáo trách, thì chúng ta có lòng rất dạn dĩ, đặng đến gần Ðức Chúa Trời:"));
visuperobject.biblemap.put("b62_3_22", new visuperobject(62,3,22,"và chúng ta xin điều gì mặc dầu, thì nhận được đều ấy, bởi chúng ta vâng giữ các điều răn của Ngài và làm những điều đẹp ý Ngài."));
visuperobject.biblemap.put("b62_3_23", new visuperobject(62,3,23,"Vả, nầy là điều răn của Ngài: là chúng ta phải tin đến danh Con Ngài, tức là Ðức Chúa Jêsus Christ, và chúng ta phải yêu mến lẫn nhau như Ngài đã truyền dạy ta."));
visuperobject.biblemap.put("b62_3_24", new visuperobject(62,3,24,"Ai vâng giữ các điều răn Ngài thì ở trong Ðức Chúa Trời, và Ðức Chúa Trời ở trong người ấy; chúng ta biết Ngài ở trong lòng chúng ta, là nhờ Ðức Thánh Linh mà Ngài đã ban cho chúng ta."));
visuperobject.biblemap.put("b62_4_1", new visuperobject(62,4,1,"Hỡi kẻ rất yêu dấu, chớ tin cậy mọi thần, nhưng hãy thử cho biết các thần có phải đến bởi Ðức Chúa Trời chăng; vì có nhiều tiên tri giả đã hiện ra trong thiên hạ."));
visuperobject.biblemap.put("b62_4_2", new visuperobject(62,4,2,"Bởi điều nầy, hãy nhận biết Thánh Linh của Ðức Chúa Trời: phàm thần nào xưng Ðức Chúa Jêsus Christ lấy xác thịt mà ra đời, thần đó là bởi Ðức Chúa Trời;"));
visuperobject.biblemap.put("b62_4_3", new visuperobject(62,4,3,"còn thần nào không xưng Ðức Chúa Jêsus, chẳng phải bởi Ðức Chúa Trời. Ðó là thần của Kẻ địch lại Ðấng Christ, mà các con đã nghe rằng hầu đến, và hiện nay đã ở trong thế gian rồi."));
visuperobject.biblemap.put("b62_4_4", new visuperobject(62,4,4,"Hỡi các con cái bé mọn, phần các con, là thuộc về Ðức Chúa Trời, đã thắng được họ rồi, vì Ðấng ở trong các con là lớn hơn kẻ ở trong thế gian."));
visuperobject.biblemap.put("b62_4_5", new visuperobject(62,4,5,"Họ thuộc về thế gian, cho nên nói theo như thế gian, và người thế gian nghe họ."));
visuperobject.biblemap.put("b62_4_6", new visuperobject(62,4,6,"Chúng ta thuộc về Ðức Chúa Trời: ai nhìn biết Ðức Chúa Trời thì nghe chúng ta; còn ai chẳng hề thuộc về Ðức Chúa Trời, thì chẳng nghe chúng ta. Ấy bởi đó chúng ta nhìn biết thần chân thật và thần sai lầm."));
visuperobject.biblemap.put("b62_4_7", new visuperobject(62,4,7,"Hỡi kẻ rất yêu dấu, chúng ta hãy yêu mến lẫn nhau; vì sự yêu thương đến từ Ðức Chúa Trời, kẻ nào yêu, thì sanh từ Ðức Chúa Trời và nhìn biết Ðức Chúa Trời."));
visuperobject.biblemap.put("b62_4_8", new visuperobject(62,4,8,"Ai chẳng yêu, thì không biết Ðức Chúa Trời; vì Ðức Chúa Trời là sự yêu thương."));
visuperobject.biblemap.put("b62_4_9", new visuperobject(62,4,9,"Lòng Ðức Chúa Trời yêu chúng ta đã bày tỏ ra trong điều nầy: Ðức Chúa Trời đã sai Con một Ngài đến thế gian, đặng chúng ta nhờ Con được sống."));
visuperobject.biblemap.put("b62_4_10", new visuperobject(62,4,10,"Nầy sự yêu thương ở tại đây: ấy chẳng phải chúng ta đã yêu Ðức Chúa Trời, nhưng Ngài đã yêu chúng ta, và sai Con Ngài làm của lễ chuộc tội chúng ta."));
visuperobject.biblemap.put("b62_4_11", new visuperobject(62,4,11,"Hỡi kẻ rất yêu dấu, nếu Ðức Chúa Trời đã yêu chúng ta dường ấy, thì chúng ta cũng phải yêu nhau."));
visuperobject.biblemap.put("b62_4_12", new visuperobject(62,4,12,"Chưa hề có ai thấy Ðức Chúa Trời; nếu chúng ta yêu nhau, thì Ðức Chúa Trời ở trong chúng ta, và sự yêu mến Ngài được trọn vẹn trong chúng ta."));
visuperobject.biblemap.put("b62_4_13", new visuperobject(62,4,13,"Bởi điều nầy chúng ta biết mình ở trong Ngài và Ngài ở trong chúng ta, là Ngài đã ban Thánh Linh Ngài cho chúng ta."));
visuperobject.biblemap.put("b62_4_14", new visuperobject(62,4,14,"Chúng ta lại đã thấy và làm chứng rằng Ðức Chúa Cha đã sai Ðức Chúa Con đặng làm Cứu Chúa thế gian."));
visuperobject.biblemap.put("b62_4_15", new visuperobject(62,4,15,"Ví bằng có ai xưng Ðức Chúa Jêsus là Con Ðức Chúa Trời, thì Ðức Chúa Trời ở trong người, và người ở trong Ðức Chúa Trời."));
visuperobject.biblemap.put("b62_4_16", new visuperobject(62,4,16,"Chúng ta đã biết và tin sự yêu thương của Ðức Chúa Trời đối với chúng ta. Ðức Chúa Trời tức là sự yêu thương, ai ở trong sự yêu thương, là ở trong Ðức Chúa Trời, và Ðức Chúa Trời ở trong người ấy."));
visuperobject.biblemap.put("b62_4_17", new visuperobject(62,4,17,"Nầy vì sao mà sự yêu thương được nên trọn vẹn trong chúng ta, hầu cho chúng ta được lòng mạnh bạo trong ngày xét đoán, ấy là Chúa thể nào thì chúng ta cũng thể ấy trong thế gian nầy."));
visuperobject.biblemap.put("b62_4_18", new visuperobject(62,4,18,"Quyết chẳng có điều sợ hãi trong sự yêu thương, nhưng sự yêu thương trọn vẹn thì cắt bỏ sự sợ hãi; vì sự sợ hãi có hình phạt, và kẻ đã sợ hãi thì không được trọn vẹn trong sự yêu thương."));
visuperobject.biblemap.put("b62_4_19", new visuperobject(62,4,19,"Chúng ta yêu, vì Chúa đã yêu chúng ta trước."));
visuperobject.biblemap.put("b62_4_20", new visuperobject(62,4,20,"Vì có ai nói rằng: Ta yêu Ðức Chúa Trời, mà lại ghét anh em mình, thì là kẻ nói dối; vì kẻ nào chẳng yêu anh em mình thấy, thì không thể yêu Ðức Chúa Trời mình chẳng thấy được."));
visuperobject.biblemap.put("b62_4_21", new visuperobject(62,4,21,"Chúng ta đã nhận nơi Ngài điều răn nầy: Ai yêu Ðức Chúa Trời, thì cũng phải yêu anh em mình."));
visuperobject.biblemap.put("b62_5_1", new visuperobject(62,5,1,"Ai tin Ðức Chúa Jêsus là Ðấng Christ, thì sanh bởi Ðức Chúa Trời; và ai yêu Ðức Chúa Trời là Ðấng đã sanh ra, thì cũng yêu kẻ đã sanh ra bởi Ngài."));
visuperobject.biblemap.put("b62_5_2", new visuperobject(62,5,2,"Chúng ta biết mình yêu con cái Ðức Chúa Trời, khi chúng ta yêu Ðức Chúa Trời và giữ vẹn các điều răn Ngài."));
visuperobject.biblemap.put("b62_5_3", new visuperobject(62,5,3,"Vì nầy là sự yêu mến Ðức Chúa Trời, tức là chúng ta vâng giữ điều răn Ngài. Ðiều răn của Ngài chẳng phải là nặng nề,"));
visuperobject.biblemap.put("b62_5_4", new visuperobject(62,5,4,"vì hễ sự gì sanh bởi Ðức Chúa Trời, thì thắng hơn thế gian; và sự thắng hơn thế gian, ấy là đức tin của chúng ta."));
visuperobject.biblemap.put("b62_5_5", new visuperobject(62,5,5,"Ai là người thắng hơn thế gian, há chẳng phải kẻ tin Ðức Chúa Jêsus là Con Ðức Chúa Trời hay sao?"));
visuperobject.biblemap.put("b62_5_6", new visuperobject(62,5,6,"Ấy chính Ðức Chúa Jêsus Christ đã lấy nước và huyết mà đến, chẳng những lấy nước mà thôi, bèn là lấy nước và huyết;"));
visuperobject.biblemap.put("b62_5_7", new visuperobject(62,5,7,"ấy là Ðức Thánh Linh đã làm chứng, vì Ðức Thánh Linh tức là lẽ thật."));
visuperobject.biblemap.put("b62_5_8", new visuperobject(62,5,8,"Vì có ba làm chứng: Ðức Thánh Linh, nước và huyết; ba ấy hiệp một."));
visuperobject.biblemap.put("b62_5_9", new visuperobject(62,5,9,"Ví bằng chúng ta nhận chứng của loài người, thì chứng của Ðức Thánh Linh trọng hơn; vả, chứng của Ðức Chúa Trời ấy là chứng mà Ngài làm về Con Ngài."));
visuperobject.biblemap.put("b62_5_10", new visuperobject(62,5,10,"Ai tin đến Con Ðức Chúa Trời, thì có chứng ấy trong mình; còn ai không tin Ðức Chúa Trời, thì cho Ngài là nói dối, vì chẳng tin đến chứng Ðức Chúa Trời đã làm về Con Ngài."));
visuperobject.biblemap.put("b62_5_11", new visuperobject(62,5,11,"Chừng ấy tức là Ðức Chúa Trời đã ban sự sống đời đời cho chúng ta, và sự sống ấy ở trong Con Ngài."));
visuperobject.biblemap.put("b62_5_12", new visuperobject(62,5,12,"Ai có Ðức Chúa Con thì có sự sống; ai không có Con Ðức Chúa Trời thì không có sự sống."));
visuperobject.biblemap.put("b62_5_13", new visuperobject(62,5,13,"Ta đã viết những điều nầy cho các con, hầu cho các con biết mình có sự sống đời đời, là kẻ nào tin đến danh Con Ðức Chúa Trời."));
visuperobject.biblemap.put("b62_5_14", new visuperobject(62,5,14,"Nầy là điều chúng ta dạn dĩ ở trước mặt Chúa, nếu chúng ta theo ý muốn Ngài mà cầu xin việc gì, thì Ngài nghe chúng ta."));
visuperobject.biblemap.put("b62_5_15", new visuperobject(62,5,15,"Nếu chúng ta biết không cứ mình xin điều gì, Ngài cũng nghe chúng ta, thì chúng ta biết mình đã nhận lãnh điều mình xin Ngài."));
visuperobject.biblemap.put("b62_5_16", new visuperobject(62,5,16,"Vì có kẻ thấy anh em mình phạm tội, mà tội không đến nỗi chết, thì hãy cầu xin, và Ðức Chúa Trời sẽ ban sự sống cho, tức là ban cho những kẻ phạm tội mà chưa đến nỗi chết. Cũng có tội đến nỗi chết; ấy chẳng phải vì tội đó mà ta nói nên cầu xin."));
visuperobject.biblemap.put("b62_5_17", new visuperobject(62,5,17,"Mọi sự không công bình đều là tội; mà cũng có tội không đến nỗi chết."));
visuperobject.biblemap.put("b62_5_18", new visuperobject(62,5,18,"Chúng ta biết rằng ai sanh bởi Ðức Chúa Trời, thì hẳn chẳng phạm tội; nhưng ai sanh bởi Ðức Chúa Trời, thì tự giữ lấy mình, ma quỉ chẳng làm hại người được."));
visuperobject.biblemap.put("b62_5_19", new visuperobject(62,5,19,"Chúng ta biết mình thuộc về Ðức Chúa Trời, còn cả thế gian đều phục dưới quyền ma quỉ."));
visuperobject.biblemap.put("b62_5_20", new visuperobject(62,5,20,"Nhưng chúng ta biết Con Ðức Chúa Trời đã đến, Ngài đã ban trí khôn cho chúng ta đặng chúng ta biết Ðấng chân thật, và chúng ta ở trong Ðấng chân thật, là ở trong Ðức Chúa Jêsus Christ, Con của Ngài. Ấy chính Ngài là Ðức Chúa Trời chân thật và là sự sống đời đời."));
visuperobject.biblemap.put("b62_5_21", new visuperobject(62,5,21,"Hỡi các con cái bé mọn, hãy giữ mình về hình tượng!"));

}

}