package vi.bible;

public class vibible65 {

public vibible65() {

putmapbible65();

}

private void putmapbible65() {

visuperobject.biblemap.put("b65_1_1", new visuperobject(65,1,1,"Giu-đe, tôi tớ của Ðức Chúa Jêsus Christ và em Gia-cơ, đạt cho những kẻ đã được kêu gọi, được Ðức Chúa Trời, là Cha yêu thương, và được Ðức Chúa Jêsus Christ giữ gìn:"));
visuperobject.biblemap.put("b65_1_2", new visuperobject(65,1,2,"nguyền xin sự thương xót, bình an, yêu mến thêm lên cho anh em!"));
visuperobject.biblemap.put("b65_1_3", new visuperobject(65,1,3,"Hỡi kẻ rất yêu dấu, vì tôi đã ân cần viết cho anh em về sự cứu rỗi chung của chúng ta, tôi tưởng phải làm điều đó, để khuyên anh em vì đạo mà tranh chiến, là đạo đã truyền cho các thánh một lần đủ rồi."));
visuperobject.biblemap.put("b65_1_4", new visuperobject(65,1,4,"Vì có mấy kẻ kia lẻn vào trong vòng chúng ta là những kẻ bị định đoán phạt từ lâu rồi, kẻ chẳng tin kính đổi ơn Ðức Chúa Trời chúng ta ra việc tà ác, chối Ðấng Chủ tể và Chúa có một của chúng ta, là Ðức Chúa Jêsus Christ."));
visuperobject.biblemap.put("b65_1_5", new visuperobject(65,1,5,"Dầu Anh em đã học những điều nầy rồi, tôi cũng muốn nhắc lại cho anh em rằng, xưa kia Chúa giải cứu dân mình ra khỏi xứ Ê-díp-tô, sau lại tiêu diệt những kẻ không tin;"));
visuperobject.biblemap.put("b65_1_6", new visuperobject(65,1,6,"còn các thiên sứ không giữ thứ bậc và bỏ chỗ riêng mình thì Ngài đã dùng dây xích họ trong nơi tối tăm đời đời, cầm giữ lại để chờ sự phán xét ngày lớn."));
visuperobject.biblemap.put("b65_1_7", new visuperobject(65,1,7,"Lại như thành Sô-đôm và Gô-mô-rơ cùng các thành lân cận cũng buông theo sự dâm dục và sắc lạ, thì đã chịu hình phạt bằng lửa đời đời, làm gương để trước mặt chúng ta."));
visuperobject.biblemap.put("b65_1_8", new visuperobject(65,1,8,"Nhưng mà chúng nó cũng như vậy, trong giấc mơ màng làm ô uế xác thịt mình, khinh dể quyền phép rất cao và nói hỗn các đấng tôn trọng."));
visuperobject.biblemap.put("b65_1_9", new visuperobject(65,1,9,"Vả, khi chính mình thiên sứ trưởng Mi-chen chống với ma quỉ giành xác Môi-se, còn chẳng dám lấy lời nhiếc móc mà đoán phạt; người chỉ nói rằng: Cầu Chúa phạt ngươi!"));
visuperobject.biblemap.put("b65_1_10", new visuperobject(65,1,10,"Song những kẻ nầy, hễ đều gì không biết thì khinh dể hết; và mọi điều chúng nó tự nhiên mà biết cũng như con thú vật vô tri, thì dùng mà làm hư mình."));
visuperobject.biblemap.put("b65_1_11", new visuperobject(65,1,11,"Khốn nạn thay cho chúng nó, vì đã theo đường của Ca-in, lấy lòng tham lợi mà gieo mình vào sự sai lạc của Ba-la-am; và bị hư mất về sự phản nghịch của Cơ-rê."));
visuperobject.biblemap.put("b65_1_12", new visuperobject(65,1,12,"Những kẻ đó là dấu vít trong đám tiệc anh em, như người chăn chiên chỉ tưởng nuôi mình cho no nê, không lo sợ gì; như đám mây không nước, theo gió đưa đi đây đi đó, như cây tàn mùa thu, không có trái, hai lần chết, trốc lên bựt rễ;"));
visuperobject.biblemap.put("b65_1_13", new visuperobject(65,1,13,"như sóng cuồng dưới biển, sôi bọt ô uế của mình; như sao đi lạc, sự tối tăm mù mịt đã dành cho chúng nó đời đời!"));
visuperobject.biblemap.put("b65_1_14", new visuperobject(65,1,14,"Ấy cũng vì họ mà Hê-nóc, là tổ bảy đời kể từ A-đam, đã nói tiên tri rằng:"));
visuperobject.biblemap.put("b65_1_15", new visuperobject(65,1,15,"Nầy, Chúa ngự đến với muôn vàn thánh, đặng phán xét mọi người, đặng trách hết thảy những người không tin kính về mọi việc không tin kính họ đã phạm, cùng mọi lời sỉ hổ mà người kẻ có tội không tin kính đó đã nói nghịch cùng Ngài."));
visuperobject.biblemap.put("b65_1_16", new visuperobject(65,1,16,"Ấy đều là người kẻ hay lằm bằm, hay phàn nàn luôn về số phận mình, làm theo sự ham muốn mình, miệng đầy những lời kiêu căng, và vì lợi mà nịnh hót người ta."));
visuperobject.biblemap.put("b65_1_17", new visuperobject(65,1,17,"Nhưng anh em, là kẻ rất yêu dấu, hãy nhớ lấy những lời mà các sứ đồ của Ðức Chúa Jêsus Christ chúng ta đã nói trước."));
visuperobject.biblemap.put("b65_1_18", new visuperobject(65,1,18,"Các sứ đồ đó nói với anh em rằng, trong các thời kỳ sau rốt, sẽ có mấy người hay nhạo báng làm theo lòng ham muốn không tin kính của mình."));
visuperobject.biblemap.put("b65_1_19", new visuperobject(65,1,19,"Ấy chính chúng nó là kẻ gây nên phe đảng, thuộc về tánh xác thịt, không có Ðức Thánh Linh."));
visuperobject.biblemap.put("b65_1_20", new visuperobject(65,1,20,"Hỡi kẻ rất yêu dấu, về phần anh em, hãy tự lập lấy trên nền đức tin rất thánh của mình, và nhơn Ðức Thánh Linh mà cầu nguyện,"));
visuperobject.biblemap.put("b65_1_21", new visuperobject(65,1,21,"hãy giữ mình trong sự yêu mến Ðức Chúa Trời, và trông đợi sự thương xót của Ðức Chúa Jêsus Christ chúng ta cho được sự sống đời đời."));
visuperobject.biblemap.put("b65_1_22", new visuperobject(65,1,22,"Hãy trách phạt những kẻ nấy, là kẻ trù trừ,"));
visuperobject.biblemap.put("b65_1_23", new visuperobject(65,1,23,"hãy cứu vớt những kẻ kia, rút họ ra khỏi lửa; còn đối với kẻ khác, hãy có lòng thương lẫn với sợ, ghét cả đến cái áo bị xác thịt làm ô uế."));
visuperobject.biblemap.put("b65_1_24", new visuperobject(65,1,24,"Vả, nguyền Ðấng có thể gìn giữ anh em khỏi vấp phạm và khiến anh em đứng trước mặt vinh hiển mình cách rất vui mừng, không chỗ trách được,"));
visuperobject.biblemap.put("b65_1_25", new visuperobject(65,1,25,"là Ðức Chúa Trời có một, là Cứu Chúa chúng ta, bởi Ðức Chúa Jêsus Christ là Chúa chúng ta, được sự vinh hiển, tôn trọng, thế lực, quyền năng thuộc về Ngài từ trước vô cùng và hiện nay cho đến đời đời! A-men."));

}

}